(window.webpackJsonp=window.webpackJsonp||[]).push([[300],{323:function(p){p.exports={chapterTitle:null,title:"\u7b2c181\u7ae0 \u8fdc\u5927\u7684\u76ee\u6807",content:"<article><h2>\u7b2c181\u7ae0 \u8fdc\u5927\u7684\u76ee\u6807</h2><p>\u3000\u3000\u5f20\u606a\u7ed9\u82cf\u6d25\u4e1c\u3001\u4e01\u69d0\u6253\u8fc7\u7535\u8bdd\u4e4b\u540e\uff0c\u5c31\u7ed9\u5b8b\u57f9\u660e\u6253\u7535\u8bdd\uff0c\u542c\u8bb8\u601d\u8bf4\u4eca\u5929\u6d77\u5dde\u8fce\u6765\u4eca\u5e74\u590f\u5929\u7684\u7b2c\u4e00\u4e2a\u9ad8\u6e29\u5929\u6c14\uff0c\u5b8b\u57f9\u660e\u7684\u58f0\u97f3\u6e05\u4eae\u800c\u6709\u529b\uff0c\u4e1d\u6beb\u4e0d\u53d7\u5230\u9ad8\u6e29\u5929\u6c14\u7684\u5f71\u54cd\u3002</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\uff0c\u7279\u522b\u662f\u4e5d\u4e8c\u5e74\u9093\u516c\u5357\u5de1\u4e4b\u540e\uff0c\u56fd\u5185\u9510\u610f\u7ecf\u6d4e\u6539\u9769\uff0c\u4fdd\u5b88\u52bf\u529b\u867d\u7136\u8fd8\u5728\u6000\u5ff5\u4e4b\u524d\u7684\u8ba1\u5212\u7ecf\u6d4e\u65f6\u4ee3\uff0c\u4f46\u662f\u653f\u754c\u7684\u5143\u8001\u4eec\u90fd\u6709\u6781\u5f3a\u7684\u515a\u6027\uff0c\u81ea\u5f8b\u7684\u7ef4\u6301\u515a\u5185\u7684\u7a33\u5b9a\u56e2\u7ed3\uff0c\u5927\u6279\u5e74\u5bcc\u529b\u5f3a\u3001\u5bcc\u6709\u6539\u9769\u7cbe\u795e\u4e0e\u5192\u9669\u7cbe\u795e\u7684\u7cbe\u82f1\u9752\u5e74\u8fd8\u662f\u987a\u5229\u7684\u4ece\u4e2d\u592e\u5230\u5730\u65b9\u7ed9\u63d0\u62d4\u5230\u91cd\u8981\u5c97\u4f4d\u4e0a\uff0c\u5510\u5b66\u8c26\u3001\u5b8b\u57f9\u660e\u5c31\u5c5e\u4e8e\u8fd9\u7c7b\u4eba\u3002\u4ece\u53e6\u4e00\u65b9\u9762\u8bf4\uff0c\u6539\u9769\u7cbe\u795e\u4e0e\u5192\u9669\u7cbe\u795e\u610f\u5473\u7740\u8981\u5728\u56fd\u5185\u8bb2\u7a76\u5e73\u8861\u3001\u5173\u7cfb\u76d8\u6839\u9519\u6742\u7684\u5b98\u573a\u4e0a\u66f4\u5bb9\u6613\u906d\u53d7\u632b\u6298\u3002\u867d\u7136\u4ed6\u4eec\u71ac\u8fc7\u4e86\u52ab\u96be\uff0c\u4e5f\u5e76\u4e0d\u610f\u5473\u7740\u4ed5\u9014\u4ece\u6b64\u5c31\u4f1a\u5766\u5766\u8361\u8361\uff0c\u4ed6\u4eec\u8fd8\u662f\u4f1a\u89e6\u78b0\u90a3\u4e9b\u654f\u611f\u7684\u95ee\u9898\uff0c\u867d\u7136\u4f1a\u5b66\u4f1a\u4e00\u4e9b\u59a5\u534f\uff0c\u4f46\u662f\u9510\u610f\u6539\u9769\u7684\u7cbe\u795e\u5374\u6ca1\u6709\u529e\u6cd5\u59a5\u534f\u3002</p><p>\u3000\u3000\u6709\u4eba\u6216\u8bb8\u89c9\u5f97\u5f20\u606a\u6ca1\u6709\u5fc5\u8981\u85cf\u5934\u7f29\u5c3e\uff0c\u65e0\u8bba\u662f\u9526\u6e56\u8fd8\u662f\u7231\u8fbe\u7535\u5b50\uff0c\u8d22\u52a1\u4e0a\u7edd\u6ca1\u6709\u4e1d\u6beb\u542b\u7cca\u7684\u5730\u65b9\uff0c\u4f46\u662f\u5728\u56fd\u5185\uff0c\u4e00\u4e2a\u4eba\u7684\u5149\u8292\u8000\u773c\uff0c\u80fd\u5f15\u6765\u522b\u4eba\u4ef0\u89c6\u4e0e\u8d5e\u53f9\u7684\u540c\u65f6\uff0c\u4e5f\u6ce8\u5b9a\u4f1a\u6742\u5939\u7740\u592a\u591a\u7684\u5992\u6068\u3002</p><p>\u3000\u3000\u4f17\u53e3\u94c4\u91d1\u3001\u79ef\u6bc1\u9500\u9aa8\uff0c\u5f20\u606a\u4e0d\u5f97\u4e0d\u8003\u8651\u6709\u4e9b\u4e8b\u53ef\u80fd\u5bf9\u7238\u7238\u3001\u5bf9\u5510\u5b66\u8c26\u3001\u5b8b\u57f9\u660e\u7b49\u4eba\u4f1a\u4ea7\u751f\u8d1f\u9762\u5f71\u54cd\u3002</p><p>\u3000\u3000\u5b8b\u57f9\u660e\u73b0\u5728\u5bf9\u7231\u8fbe\u7535\u5b50\u7684\u5fc3\u601d\u4e5f\u5f88\u91cd\uff0c\u57ce\u5357\u533a\u751a\u81f3\u6574\u4e2a\u6d77\u5dde\u5e02\u90fd\u6ca1\u6709\u5f15\u4ee5\u4e3a\u50b2\u7684\u4f18\u52bf\u4ea7\u4e1a\uff0c\u9526\u6e56\u76ee\u524d\u6574\u5408\u6d77\u5dde\u5e02\u7684\u4e2d\u5c0f\u578b\u56fd\u6709\u9020\u7eb8\u5382\u8ba9\u5b8b\u57f9\u660e\u770b\u5230\u597d\u7684\u5e0c\u671b\uff0c\u5f20\u606a\u5728\u7231\u8fbe\u7535\u5b50\u4e0a\u8ff8\u53d1\u51fa\u6765\u7684\u70ed\u60c5\u66f4\u5438\u5f15\u5b8b\u57f9\u660e\u7684\u6ce8\u610f\u529b\u3002</p><p>\u3000\u3000\u4ed6\u751a\u81f3\u8ba9\u533a\u59d4\u6210\u7acb\u4e13\u95e8\u7684\u529e\u516c\u5ba4\uff0c\u7d27\u76ef\u5f53\u524d\u7684\u5f71\u789f\u673a\u5e02\u573a\u8d8b\u52bf\uff0c\u4ed6\u867d\u7136\u6ca1\u53ef\u80fd\u627e\u5230\u6bd4\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u4ed6\u4eec\u66f4\u4e13\u4e1a\u7684\u4eba\uff0c\u4e5f\u8db3\u4ee5\u8ba9\u4ed6\u4e86\u89e3\u5f53\u524d\u5f71\u789f\u673a\u5e02\u573a\u7684\u6700\u65b0\u52a8\u6001\u3002</p><p>\u3000\u3000\u5373\u4f7f\u5b8b\u57f9\u660e\u6000\u7740\u8fd9\u4e48\u9ad8\u7684\u70ed\u60c5\u4e0e\u671f\u5f85\uff0c\u5f53\u4ed6\u542c\u5230\u5f20\u606a\u63d0\u51fa\u7231\u8fbe\u7535\u5b50\u5c06\u5f88\u5feb\u542f\u52a8\u7b79\u5efa\u5927\u578b\u751f\u4ea7\u57fa\u5730\u7684\u8ba1\u5212\u65f6\uff0c\u4ed6\u8fd8\u662f\u5f88\u60ca\u8bb6\uff1a\u201c\u4f1a\u4e0d\u4f1a\u592a\u6025\u4e86\u4e00\u4e9b\uff0c\u5f71\u789f\u673a\u5e02\u573a\u867d\u7136\u6b63\u5448\u4e95\u55b7\u5f0f\u7206\u53d1\uff0c\u534a\u5e74\u65f6\u95f4\u4ea7\u9500\u91cf\u589e\u6da8\u4e86\u5341\u500d\uff0c\u4f46\u662f\u8c01\u80fd\u51c6\u786e\u8ba1\u7b97\u6700\u7ec8\u7684\u5e02\u573a\u5bb9\u91cf\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ece\u56fd\u5185\u5e9e\u5927\u7684\u5e02\u573a\u6f5c\u529b\u4e0e\u56fd\u5185\u7ecf\u6d4e\u7684\u53d1\u5c55\u52bf\u6001\uff0c\u53ef\u4ee5\u5927\u81f4\u7684\u4f30\u7b97\u51fa\u4eca\u540e\u5341\u5e74\u5185\u7684\u5e02\u573a\u5bb9\u91cf\u3002\u4eca\u5e74\u5e74\u5e95\uff0c\u56fd\u5185\u5f71\u789f\u673a\u6708\u9500\u91cf\u5c31\u6709\u671b\u7a81\u7834\u5341\u4e07\u53f0\uff0c\u4eca\u5e74\u56fd\u5185\u7684\u5f55\u50cf\u673a\u5e02\u573a\u5168\u5e74\u9500\u91cf\u53ef\u80fd\u4f1a\u8fbe\u5230\u4e00\u767e\u4e94\u5341\u4e07\u53f0\uff0c\u201d\u5f20\u606a\u8f7b\u677e\u81ea\u82e5\u7684\u5206\u6790\u65e5\u540e\u7684\u5e02\u573a\u76db\u51b5\uff0c\u201c\u5f55\u50cf\u673a\u5236\u9020\u4e1a\u8fdb\u5165\u7684\u8d44\u91d1\u3001\u6280\u672f\u95e8\u69db\u5f88\u9ad8\uff0c\u6ca1\u6709\u56fd\u5bb6\u4ea7\u4e1a\u57fa\u91d1\u7684\u6276\u6301\uff0c\u5f88\u5c11\u6709\u6c11\u8425\u4f01\u4e1a\u6709\u80fd\u529b\u6324\u8fdb\u53bb\uff0c\u5bfc\u81f4\u5f55\u50cf\u673a\u7684\u4ef7\u683c\u59cb\u7ec8\u4f1a\u7ef4\u6301\u5728\u4e00\u4e2a\u9ad8\u4f4d\uff1b\u4f46\u662f\u5f71\u789f\u673a\u4e0d\u540c\uff0c\u4e24\u767e\u4e07\u8d44\u91d1\u5c31\u80fd\u529e\u5bb6\u5382\u5b50\uff0c\u6700\u5feb\u7684\u4e24\u4e2a\u6708\u5c31\u80fd\u63a8\u51fa\u4ea7\u54c1\uff0c\u968f\u7740\u8fdb\u5165\u5382\u5bb6\u7684\u6570\u91cf\u589e\u591a\uff0c\u4ef7\u683c\u4f1a\u8fc5\u901f\u4e0b\u964d\u3002\u8fd8\u6709\u4e00\u4e2a\u5f88\u5173\u952e\u7684\u56e0\u7d20\uff0c\u5f55\u50cf\u5e26\u7684\u5236\u4f5c\u4e0d\u6613\uff0c\u6210\u672c\u9ad8\uff0c\u4e0d\u6613\u7ed9\u5c0f\u4f5c\u574a\u6a21\u4eff\uff0c\u524d\u4e9b\u5929\u6211\u6258\u4eba\u7ed9\u5b8b\u53d4\u634e\u53bb\u7684\u51e0\u5f20\u789f\u7247\u6548\u679c\u5982\u4f55\uff1f\u4e0d\u8ba1\u7b97\u7248\u7a0e\uff0c\u8fd9\u4e48\u4e00\u5f20\u789f\u7247\u7684\u6210\u672c\u76ee\u524d\u4e0d\u5230\u4e24\u5143\uff0c\u4e00\u65e6\u8fd9\u79cd\u789f\u7247\u5927\u8086\u7684\u5145\u585e\u56fd\u5185\u5e02\u573a\uff0c\u5b8b\u53d4\u4ee5\u4e3a\u5f71\u789f\u673a\u5e02\u573a\u4f1a\u4ee5\u600e\u6837\u7684\u59ff\u6001\u72c2\u98da\u731b\u8fdb\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6700\u521d\u7785\u51c6\u5f71\u789f\u673a\u5e02\u573a\uff0c\u662f\u6253\u7740\u635e\u4e00\u628a\u5c31\u8d70\u7684\u5ff5\u5934\uff0c\u8003\u8651\u751f\u4ea7\u91c7\u53d6\u8bf7\u522b\u5bb6\u4ee3\u5de5\u7684\u6a21\u5f0f\u5c3d\u53ef\u80fd\u7f29\u51cf\u751f\u4ea7\u6295\u8d44\uff0c\u4f46\u662f\u4e0eTI\uff08\u5fb7\u5dde\u4eea\u5668\u4eea\u8868\uff09\u516c\u53f8\u7b7e\u7f72\u6709\u5173\u89e3\u7801\u82af\u7247\u4e0a\u7684\u5408\u4f5c\u534f\u8bae\u4ee5\u6765\uff0c\u5f20\u606a\u770b\u5230\u66f4\u5927\u7684\u673a\u9047\uff0c\u770b\u5230\u5728\u6d77\u5dde\u5e02\u8fdb\u884c\u4ea7\u4e1a\u5316\u53d1\u5c55\u7684\u53ef\u80fd\uff0c\u53ea\u8981\u71ac\u8fc7\u524d\u671f\u7684\u8d44\u91d1\u74f6\u9888\uff0c\u4e00\u65e6TI\u516c\u53f8\u5f00\u53d1\u65b0\u4e00\u4ee3\u89e3\u7801\u82af\u7247\u5f97\u6210\uff0c\u7231\u8fbe\u7535\u5b50\u5c31\u53ef\u4ee5\u5206\u4eab\u5de8\u989d\u7684\u5784\u65ad\u5229\u6da6\uff0c\u5f20\u606a\u5c31\u6709\u4fe1\u5fc3\u8ba9\u7231\u8fbe\u7535\u5b50\u5728\u540e\u671f\u6781\u7aef\u6b8b\u9177\u7684\u7fa4\u96c4\u4e71\u6218\u4e2d\u751f\u5b58\u4e0b\u53bb\uff0c\u5e76\u6700\u7ec8\u53d1\u5c55\u6210\u8ba9\u4e16\u754c\u77a9\u76ee\u7684\u5927\u578b\u4f01\u4e1a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e00\u65e6\u653e\u5f03\u635e\u4e00\u628a\u5c31\u8d70\u7684\u5ff5\u5934\uff0c\u5c31\u4e0d\u5f97\u4e0d\u8ba4\u771f\u5206\u6790\u65e5\u540e\u6ce8\u5b9a\u4f1a\u5d1b\u8d77\u7684\u5f3a\u5927\u7ade\u4e89\u5bf9\u624b\uff0c\u5386\u53f2\u7684\u8f68\u8ff9\u4e0d\u4f1a\u8f7b\u6613\u7684\u6539\u53d8\uff0c\u5f71\u789f\u673a\u4ea7\u4e1a\u4f1a\u4e0e\u7231\u8fbe\u4e89\u96c4\u7684\u524d\u4e09\u8005\u591a\u534a\u662f\u65b0\u79d1\u3001\u6b65\u6b65\u9ad8\u3001\u7231\u591a\u3002</p><p>\u3000\u3000\u65b0\u79d1\u96c6\u56e2\u672c\u5c31\u662f\u56fd\u5185\u7535\u5b50\u4ea7\u4e1a\u7684\u9f99\u5934\u4f01\u4e1a\uff0c\u751f\u4ea7\u6f5c\u529b\u5927\uff0c\u6280\u672f\u80fd\u91cf\u96c4\u539a\uff0c\u5bf9\u5e02\u573a\u7684\u8c28\u614e\u6001\u5ea6\u5374\u6ca1\u6709\u59a8\u788d\u4ed6\u4eec\u65e5\u540e\u6210\u4e3a\u5f71\u789f\u673a\u4ea7\u4e1a\u7684\u9738\u4e3b\uff1b\u6b65\u6b65\u9ad8\u505a\u5c0f\u9738\u738b\u5b66\u4e60\u673a\u51fa\u8eab\uff0c\u6b65\u6b65\u9ad8\u7684\u8001\u603b\u5bf9\u5e02\u573a\u8fd0\u4f5c\u6709\u7740\u72ec\u7279\u7684\u611f\u609f\uff0c\u5e76\u4e14\u5728\u8d44\u91d1\u3001\u6280\u672f\u4e0e\u751f\u4ea7\u6f5c\u529b\u4e0a\u6709\u4e00\u5b9a\u7684\u79ef\u7d2f\uff0c\u5728\u65b0\u79d1\u9010\u6b65\u653e\u5f03\u5bf9VCD\u5f71\u789f\u673a\u7684\u6295\u5165\u3001\u7231\u591a\u8f70\u7136\u5d29\u6e83\u4e4b\u540e\uff0c\u6b65\u6b65\u9ad8\u5f88\u5feb\u6210\u4e3a\u4e1a\u5185\u9f99\u5934\uff1b\u7231\u591a\u76ee\u524d\u7684\u60c5\u5f62\u4e0d\u6bd4\u7231\u8fbe\u7535\u5b50\u597d\uff0c\u5728\u6280\u672f\u3001\u8d44\u91d1\u4e0a\u7684\u79ef\u7d2f\u8fd8\u4e0d\u53ca\u7231\u8fbe\uff0c\u4f46\u662f\u7231\u591a\u7684\u8001\u603b\u80e1\u5fd7\u6807\u5bf9\u5e02\u573a\u8fd0\u4f5c\u6709\u7740\u504f\u6267\u72c2\u4e00\u6837\u7684\u6fc0\u60c5\uff0c\u8fd9\u4f1a\u8ba9\u7231\u591a\u5728\u4eca\u5e74\u5e74\u5e95\u5c31\u6a2a\u7a7a\u5d1b\u8d77\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u5728\u6280\u672f\u79ef\u7d2f\u6bd4\u4e0d\u4e0a\u65b0\u79d1\uff0c\u53ea\u4e0e\u6b65\u6b65\u9ad8\u76f8\u5f53\uff0c\u53ea\u662f\u5360\u7740\u4ece\u4e07\u71d5\u6316\u6398\u6280\u672f\u4eba\u624d\u7684\u4f18\u52bf\uff0c\u5e76\u4e14\u76f4\u63a5\u5728\u7b2c\u4e8c\u4ee3\u89e3\u7801\u82af\u7247\u4e0a\u7814\u53d1\u6837\u54c1\uff0c\u6bd4\u65b0\u79d1\u3001\u6b65\u6b65\u9ad8\u53d6\u5f97\u51e0\u4e2a\u6708\u7684\u65f6\u95f4\u4f18\u52bf\uff0c\u4f46\u662f\u8fd9\u4e2a\u4f18\u52bf\u5f88\u5bb9\u88ab\u7f3a\u4e4f\u8d44\u91d1\u4e0e\u6ca1\u6709\u751f\u4ea7\u57fa\u5730\u7684\u5f31\u52bf\u6240\u62b5\u6d88\uff0c\u800c\u4e14\u65b0\u79d1\u3001\u6b65\u6b65\u9ad8\u5df2\u7ecf\u5728\u5e02\u573a\u4e0a\u5efa\u7acb\u8d77\u77e5\u540d\u5ea6\uff0c\u8fd9\u662f\u6b64\u65f6\u8fd8\u9ed8\u9ed8\u65e0\u95fb\u7684\u7231\u8fbe\u6240\u65e0\u6cd5\u76f8\u63d0\u5e76\u8bba\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc5\u987b\u501f\u52a9\u60df\u4e00\u8ba9\u4ed6\u611f\u89c9\u597d\u53d7\u4e00\u4e9b\u7684\u65f6\u95f4\u4f18\u52bf\u505a\u597d\u6700\u7d27\u8981\u7684\u4e09\u4ef6\u4e8b\uff1a\u6253\u5f00\u5e02\u573a\u3001\u83b7\u53d6\u8d44\u91d1\u3001\u6269\u5927\u751f\u4ea7\u3002</p><p>\u3000\u3000\u8fd9\u4e09\u8005\u4e4b\u95f4\u662f\u76f8\u4e92\u5173\u8054\u7684\uff0c\u6253\u5f00\u5e02\u573a\uff0c\u6709\u4e86\u4e00\u5b9a\u7684\u77e5\u540d\u5ea6\u5c31\u53ef\u4ee5\u4ece\u4ee3\u7406\u5546\u90a3\u91cc\u83b7\u5f97\u4e00\u5b9a\u91cf\u7684\u4fdd\u8bc1\u91d1\uff0c\u5206\u4e00\u90e8\u5206\u7ee7\u7eed\u5e02\u573a\u8fd0\u4f5c\uff0c\u4e00\u90e8\u5206\u7528\u4e8e\u6269\u5927\u751f\u4ea7\u3002</p><p>\u3000\u3000\u4f46\u662f\uff0c\u8fd9\u4e09\u8005\u4e4b\u95f4\u73af\u73af\u76f8\u6263\u3001\u76f8\u4e92\u7ea0\u7f20\uff0c\u4f60\u8fd8\u5f97\u5c0f\u5fc3\u7ef4\u6301\u4e09\u8005\u7684\u5e73\u8861\u3002</p><p>\u3000\u3000\u5e02\u573a\u5f00\u53d1\u8d77\u6765\uff0c\u751f\u4ea7\u8ddf\u4e0d\u4e0a\uff0c\u767d\u767d\u4fbf\u5b9c\u90a3\u4e9b\u8d34\u724c\u5047\u5192\u7684\u5f71\u789f\u673a\u5236\u9020\u5382\u5546\uff0c\u4f60\u540c\u65f6\u8fd8\u5f97\u4e3a\u90a3\u4e9b\u8d28\u91cf\u4f4e\u52a3\u7684\u4ea7\u54c1\u80cc\u9ed1\u9505\uff1b\u5e02\u573a\u672a\u5145\u5206\u5f00\u53d1\uff0c\u5c31\u76f2\u76ee\u7684\u6269\u5927\u751f\u4ea7\uff0c\u4e0d\u4ec5\u65e0\u6cd5\u4ece\u4ee3\u7406\u5546\u90a3\u91cc\u4ee5\u4fdd\u8bc1\u91d1\u7684\u540d\u4e49\u5957\u53d6\u751f\u4ea7\u8d44\u4ea7\uff0c\u8fd8\u4f1a\u5bfc\u81f4\u6709\u9650\u7684\u8d44\u91d1\u88ab\u5360\u7528\u3002</p><p>\u3000\u3000\u5728\u5f71\u789f\u673a\u5e02\u573a\u6025\u5267\u7206\u53d1\u7684\u5e74\u4ee3\uff0c\u6700\u5927\u9650\u5ea6\u7684\u63a7\u5236\u597d\u5e02\u573a\u5f00\u53d1\u4e0e\u4ea7\u80fd\u6269\u5f20\u7684\u8282\u594f\uff0c\u624d\u80fd\u6700\u5927\u9650\u5ea6\u7684\u4ece\u5f71\u789f\u673a\u5e02\u573a\u652b\u53d6\u6700\u5927\u7684\u5229\u6da6\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u6269\u5927\u751f\u4ea7\u4e4b\u540e\uff0c\u8fd8\u8981\u5efa\u7acb\u4e25\u5bc6\u7684\u5de5\u4e1a\u4f53\u7cfb\uff0c\u4e25\u5bc6\u7684\u8d28\u91cf\u7ba1\u7406\u4e0e\u6210\u672c\u63a7\u5236\u4e5f\u662f\u5f20\u606a\u653e\u5f03\u4ee3\u5de5\u6a21\u5f0f\u81ea\u7b79\u751f\u4ea7\u57fa\u5730\u6700\u91cd\u8981\u7684\u8003\u8651\u3002\u76ee\u524d\u5df2\u7ecf\u59d4\u6258\u76db\u946b\u5728\u534e\u4e1c\u5730\u533a\u7b79\u5907\u5b8c\u5584\u7684\u552e\u540e\u670d\u52a1\u4f53\u7cfb\u3002</p><p>\u3000\u3000\u5f20\u606a\u9700\u8981\u4e00\u5f00\u59cb\u5c31\u8ddf\u5176\u4ed6\u5f71\u789f\u673a\u5236\u9020\u5546\u62c9\u5f00\u8ddd\u79bb\u3002</p><p>\u3000\u3000\u54ea\u4e2a\u5730\u65b9\u7684\u515a\u653f\u9886\u5bfc\u5e72\u90e8\u4e0d\u5e0c\u671b\u8f96\u533a\u5185\u7684\u5de5\u4e1a\u6295\u8d44\u89c4\u6a21\u8d8a\u5927\u8d8a\u597d\uff1f\u6709\u4e9b\u5730\u65b9\u751a\u81f3\u51fa\u73b0\u653f\u5e9c\u201c\u7ed1\u67b6\u5f0f\u201d\u7684\u8981\u6c42\u6c11\u8425\u4f01\u4e1a\u6269\u5927\u6295\u8d44\u89c4\u6a21\u3002</p><p>\u3000\u3000\u542c\u4e86\u5f20\u606a\u8bed\u6c14\u5e73\u6de1\u4f46\u662f\u5185\u542b\u6fc0\u60c5\u7684\u5e02\u573a\u5206\u6790\u4e4b\u540e\uff0c\u5b8b\u57f9\u660e\u95ee\u4ed6\uff1a\u201c\u7231\u8fbe\u7535\u5b50\u7b79\u5efa\u7684\u751f\u4ea7\u57fa\u5730\uff0c\u4f60\u5e0c\u671b\u8981\u6709\u591a\u5927\uff1f\u201d</p><p>\u3000\u3000\u201c\u7701\u8ba1\u59d4\u6279\u590d\u7684\u6743\u9650\u6709\u591a\u5927\uff0c\u6211\u4eec\u5c31\u7b79\u5efa\u591a\u5927\u3002\u201d\u73b0\u5728\u56fd\u5185\u6536\u7f29\u94f6\u6839\uff0c\u63a7\u5236\u5de5\u4e1a\u6295\u8d44\u89c4\u6a21\uff0c\u7528\u5730\u8d85\u8fc7\u516d\u767e\u4ea9\u7684\u5de5\u4e1a\u9879\u76ee\u5fc5\u987b\u8981\u4e2d\u592e\u6279\u590d\uff0c\u5728\u76ee\u524d\u7684\u60c5\u51b5\u4e0b\uff0c\u5f88\u96be\u5728\u77ed\u65f6\u95f4\u5185\u901a\u8fc7\u3002</p><p>\u3000\u3000\u5b8b\u57f9\u660e\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u7684\u5fc3\u8fd9\u4e48\u5927\uff0c\u9664\u4e86\u6d77\u5dde\u63a7\u80a1\u6b63\u7b79\u5907\u7684\u68c9\u7eba\u7ec7\u5de5\u4e1a\u56ed\u533a\uff0c\u6d77\u5dde\u5e02\u8fd8\u6ca1\u6709\u5355\u72ec\u7684\u9879\u76ee\u63a5\u8fd1\u516d\u767e\u4ea9\u7528\u5730\uff0c\u4ed6\u63a8\u52a8\u8fdb\u884c\u6280\u6539\u7684\u65b0\u5149\u5382\u65b0\u5382\u5b50\u624d\u4e00\u767e\u4e94\u5341\u4ea9\u7684\u89c4\u6a21\uff0c\u95ee\u9053\uff1a\u201c\u8d44\u91d1\u600e\u4e48\u89e3\u51b3\uff1f\u201d\u4e2d\u592e\u5bf9\u94f6\u8d37\u63a7\u5236\u6781\u4e25\uff0c\u9ad8\u8fbe\u6570\u4ebf\u7684\u6295\u8d44\u8d44\u91d1\u5df2\u7ecf\u8d85\u8fc7\u5b8b\u57f9\u660e\u7684\u80fd\u529b\u8303\u56f4\uff0c\u5c31\u7b97\u6345\u5230\u5e02\u91cc\u7684\u96be\u5ea6\u4e5f\u5f88\u5927\uff0c\u5173\u952e\u5510\u5b66\u8c26\u4f1a\u4e0d\u4f1a\u65d7\u5e1c\u9c9c\u660e\u7684\u652f\u6301\u5f20\u77e5\u884c\u8fd8\u5728\u8bfb\u9ad8\u4e2d\u7684\u513f\u5b50\uff0c\u5468\u5bcc\u660e\u7b49\u5176\u4ed6\u5e02\u59d4\u9886\u5bfc\u4f1a\u4e0d\u4f1a\u6709\u975e\u8bae\uff1f</p><p>\u3000\u3000\u201c\u524d\u671f\u8d44\u91d1\u81ea\u7b79\uff0c\u9996\u5148\u8003\u8651\u7ec4\u88c5\u5de5\u5382\uff0c\u8bbe\u5907\u6295\u8d44\u6709\u9650\uff0c\u4e3b\u8981\u662f\u57fa\u5efa\u6295\u8d44\uff0c\u6709\u4e24\u5343\u4e07\u5c31\u80fd\u8ba9\u9879\u76ee\u542f\u52a8\u8d77\u6765\uff0c\u540e\u671f\u8ffd\u52a0\u7684\u8d44\u91d1\u4e5f\u6709\u9650\uff1b\u76ee\u524d\u867d\u7136\u5df2\u7ecf\u5f00\u59cb\u673a\u82af\u7b49\u5143\u5668\u4ef6\u7684\u7814\u53d1\uff0c\u4f46\u662f\u8981\u62ff\u51fa\u6210\u719f\u7684\u4e1c\u897f\uff0c\u8fd8\u8981\u4e00\u4e24\u5e74\u7684\u65f6\u95f4\uff0c\u5230\u660e\u540e\u5e74\u7684\u6295\u8d44\u989d\u5ea6\u4f1a\u589e\u52a0\u8bb8\u591a\uff0c\u4f46\u662f\u7231\u8fbe\u7535\u5b50\u5f88\u53ef\u80fd\u5df2\u7ecf\u7f13\u8fc7\u52b2\u6765\uff0c\u4e5f\u8bf4\u4e0d\u5b9a\u56fd\u5185\u7684\u91d1\u878d\u73af\u5883\u4f1a\u5bbd\u677e\u4e00\u4e9b\uff0c\u518d\u8bf4\u7231\u8fbe\u7535\u5b50\u505a\u8d77\u6765\u4e4b\u540e\uff0c\u5411\u94f6\u884c\u501f\u8d37\u4e5f\u5bb9\u6613\u4e00\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u89c1\u5f20\u606a\u90fd\u6709\u5468\u5bc6\u7684\u8003\u8651\uff0c\u5b8b\u57f9\u660e\u81ea\u7136\u4f1a\u5168\u529b\u652f\u6301\u3002</p><p>\u3000\u3000\u5f20\u606a\u53c8\u8bf4\u9053\uff1a\u201c\u571f\u5730\u8f6c\u8ba9\u91d1\u7684\u95ee\u9898\u2026\u2026\u201d</p><p>\u3000\u3000\u5b8b\u57f9\u660e\u723d\u5229\u7684\u8bf4\u9053\uff1a\u201c\u53ea\u8981\u5728\u57ce\u5357\u4e00\u4ea9\u4e09\u5206\u5730\u91cc\uff0c\u6211\u8fd8\u80fd\u505a\u4e3b\uff0c\u533a\u91cc\u6240\u6536\u53d6\u7684\u571f\u5730\u8f6c\u8ba9\u91d1\u4f1a\u4ee5\u4ea7\u4e1a\u652f\u6301\u57fa\u91d1\u7684\u5f62\u5f0f\u5168\u90e8\u8fd4\u8fd8\u7ed9\u7231\u8fbe\u7535\u5b50\u2026\u2026\u201d</p><p>\u3000\u3000\u516d\u767e\u4ea9\u7684\u5de5\u4e1a\u7528\u5730\uff0c\u571f\u5730\u8f6c\u8ba9\u91d1\u7edd\u4e0d\u662f\u5c0f\u6570\u5b57\uff0c\u6ca1\u6709\u5b8b\u57f9\u660e\u8fd9\u53e5\u627f\u8bfa\uff0c\u5f20\u606a\u7684\u538b\u529b\u4f1a\u9aa4\u7136\u5927\u8bb8\u591a\u3002</p><p>\u3000\u3000\u5bf9\u5b8b\u57f9\u660e\u800c\u6765\uff0c\u72ec\u65ad\u4e13\u884c\u7684\u7ed9\u4e88\u7231\u8fbe\u7535\u5b50\u8fd9\u4e48\u5927\u7684\u652f\u6301\u529b\u5ea6\uff0c\u4e5f\u662f\u5192\u7740\u6781\u5927\u7684\u653f\u6cbb\u98ce\u9669\uff0c\u51e0\u4e4e\u5c06\u4ed6\u7684\u653f\u6cbb\u524d\u9014\u7ed1\u5230\u7231\u8fbe\u7535\u5b50\u7684\u8eab\u5b50\uff0c\u7231\u8fbe\u7535\u5b50\u4e0d\u80fd\u5728\u7a0e\u6536\u3001\u5c31\u4e1a\u3001\u589e\u521b\u3001\u4ea7\u4e1a\u53d1\u5c55\u4e0a\u7ed9\u57ce\u5357\u533a\u505a\u51fa\u5353\u8d8a\u7684\u8d21\u732e\uff0c\u5b8b\u57f9\u660e\u7684\u65e5\u5b50\u5c31\u96be\u8fc7\u4e86\u3002</p></article>"}}}]);
