(window.webpackJsonp=window.webpackJsonp||[]).push([[301],{324:function(p){p.exports={chapterTitle:null,title:"\u7b2c301\u7ae0 \u4e39\u4e95\u5df7\u7684\u5546\u4e1a\u4ef7\u503c",content:"<article><h2>\u7b2c301\u7ae0 \u4e39\u4e95\u5df7\u7684\u5546\u4e1a\u4ef7\u503c</h2><p>\u3000\u3000\u6c99\u7530\u4e1c\u7247\u5efa\u7b51\u7fa4\uff0c\u6700\u7ec8\u7684\u4fdd\u62a4\u65b9\u6848\u90fd\u8981\u4e0e\u65c5\u6e38\u3001\u5546\u4e1a\u5f00\u53d1\u7ed3\u5408\u8d77\u6765\u641e\uff0c\u524d\u8857\u3001\u4e39\u4e95\u8857\u5df2\u7ecf\u5177\u5907\u53d1\u6398\u5546\u4e1a\u4ef7\u503c\u7684\u5916\u90e8\u73af\u5883\uff0c\u524d\u8857\u5317\u4fa7\u7684\u6c11\u5b85\u4fee\u7f2e\u3001\u5546\u4e1a\u5f00\u53d1\uff0c\u7531\u5b8b\u57f9\u660e\u8d1f\u8d23\uff0c\u4ea4\u7ed9\u57ce\u5357\u533a\u4e0b\u5c5e\u7684\u516c\u53f8\u8fd0\u4f5c\uff0c\u57ce\u5357\u533a\u5c31\u7b49\u7740\u9526\u6e56\u5c06\u65b0\u5149\u5382\u4e70\u8fc7\u53bb\u62ff\u5230\u5fc5\u8981\u7684\u8d44\u91d1\u624d\u597d\u4e0b\u624b\u8fd0\u4f5c\u3002</p><p>\u3000\u3000\u524d\u8857\u3001\u4e39\u4e95\u5df7\u5546\u4e1a\u5f00\u53d1\u6210\u529f\u4e86\uff0c\u4f1a\u5bf9\u6c99\u7530\u4e1c\u7247\u7684\u5176\u4ed6\u5730\u65b9\u6709\u5f3a\u70c8\u7684\u793a\u8303\u4f5c\u7528\u3002</p><p>\u3000\u3000\u4f46\u662f\u6700\u5173\u952e\u7684\u4e00\u70b9\uff0c\u5e02\u91cc\u6ca1\u6709\u66f4\u6539\u5bf9\u6c99\u7530\u4e1c\u7247\u533a\u57df\u7684\u7528\u5730\u89c4\u5212\uff0c\u5728\u539f\u6765\u7684\u65b9\u6848\u4e2d\uff0c\u8fd9\u7247\u533a\u57df\u90fd\u662f\u8981\u62c6\u6389\u5efa\u5546\u4e1a\u5730\u4ea7\u7684\uff0c\u4ec5\u51ed\u8fd9\u4e00\u70b9\uff0c\u5c31\u62e6\u4f4f\u6c11\u95f4\u6295\u8d44\u8fd9\u91cc\uff0c\u8d38\u7136\u6295\u8d44\u5c31\u610f\u5473\u7740\u5de8\u5927\u7684\u98ce\u9669\uff0c\u800c\u4e14\u8fd9\u4e2a\u98ce\u9669\u90fd\u4e0d\u662f\u4e00\u822c\u4eba\u80fd\u627f\u53d7\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u8981\u76f4\u63a5\u8ddf\u9648\u5947\u5efa\u8bae\u5728\u4e39\u4e95\u5df7\u62ff\u4e00\u5ea7\u53e4\u5b85\u505a\u9910\u9986\uff0c\u9648\u5947\u662f\u7edd\u5bf9\u4e0d\u80af\u7684\uff0c\u4e07\u4e00\u4e24\u4e09\u5e74\u5c31\u62c6\u6389\uff0c\u6295\u5165\u7684\u88c5\u6f62\u8d39\u90fd\u672a\u5fc5\u80fd\u6536\u56de\u6765\uff0c\u4e00\u4e24\u5e74\u5c31\u62c6\u6389\uff0c\u4f1a\u76f4\u63a5\u5c06\u4ed6\u5bb6\u6253\u5165\u8d64\u8d2b\u7684\u5883\u5730\u3002\u5f20\u606a\u53c8\u4e0d\u80fd\u660e\u7740\u8ddf\u4ed6\u8bf4\uff0c\u6c99\u7530\u4e1c\u7247\u62c6\u4e0d\u4e86\uff0c\u5e02\u91cc\u8fdf\u65e9\u4f1a\u518d\u4e00\u6b65\u4fee\u6539\u89c4\u5212\u3002</p><p>\u3000\u3000\u9152\u5bb4\u4e4b\u524d\uff0c\u5f20\u606a\u5230\u9053\u89c2\u4e0e\u8bb8\u9e3f\u4f2f\u4e0b\u68cb\u65f6\uff0c\u5c31\u5c06\u610f\u601d\u8ddf\u4ed6\u8bf4\u4e86\uff0c\u4e70\u5b85\u5b50\u3001\u6539\u9020\u3001\u4fee\u65e7\u5982\u65e7\u7684\u4fee\u7f2e\uff0c\u6240\u6709\u7684\u8d44\u91d1\u90fd\u53ef\u4ee5\u7531\u4ed6\u6765\u51fa\uff0c\u7b49\u6574\u597d\u4e4b\u540e\u6574\u4f53\u51fa\u79df\u7ed9\u9648\u5947\u505a\u9910\u9986\uff0c\u4ed6\u73b0\u5728\u6bcf\u4e2a\u6708\u62bd\u4e24\u767e\u4e07\u51fa\u6765\u82b1\u82b1\uff0c\u5bf9\u7231\u8fbe\u7535\u5b50\u7684\u8425\u8fd0\u6ca1\u6709\u4e00\u70b9\u70b9\u7684\u5f71\u54cd\uff0c\u518d\u8bf4\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u7b49\u5176\u4ed6\u80a1\u4e1c\u4e5f\u8981\u62ff\u4e9b\u5206\u7ea2\u53bb\u6539\u5584\u751f\u6d3b\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u4e48\u505a\u4e0d\u662f\u56e0\u4e3a\u8ddf\u9648\u5983\u84c9\u5bb6\u5173\u7cfb\u719f\u7edc\uff0c\u4e3b\u8981\u8fd8\u662f\u8003\u8651\u5982\u4f55\u9010\u6b65\u7684\u53bb\u53d1\u6398\u6c99\u7530\u4e1c\u7247\u53e4\u5efa\u7b51\u7fa4\u7684\u5546\u4e1a\u4ef7\u503c\uff0c\u5f15\u5bfc\u6c11\u95f4\u8d44\u672c\u8fdb\u6765\u6295\u8d44\u53e4\u5b85\uff0c\u5f62\u6210\u4e00\u5b9a\u7684\u6c14\u5019\u4e4b\u540e\uff0c\u5e02\u91cc\u66f4\u6539\u8fd9\u7247\u533a\u57df\u7684\u7528\u5730\u89c4\u5212\u4e5f\u5c31\u6c34\u5230\u6e20\u6210\uff0c\u518d\u52a0\u9648\u5947\u7684\u201c\u516b\u9526\u73cd\u201d\u9910\u9986\u4e5f\u76f8\u5f53\u7684\u6709\u7279\u8272\u3002\u4e5f\u4e0d\u60f3\u8ba9\u9648\u5947\u4e00\u5bb6\u89c9\u5f97\u6b20\u4ed6\u591a\u5927\u7684\u4eba\u60c5\u4f3c\u7684\uff0c\u6240\u4ee5\u4ed6\u51b3\u5b9a\u4e0d\u51fa\u9762\uff0c\u8bdd\u90fd\u8ba9\u8bb8\u9e3f\u4f2f\u4ee3\u4ed6\u8bf4\u3002</p><p>\u3000\u3000\u9152\u684c\u4e0a\u63d0\u5230\u8fd9\u4e2a\u8bdd\u9898\uff0c\u8bb8\u9e3f\u4f2f\u5c31\u8bf4\u9053\uff1a\u201c\u8857\u5e02\u90a3\u513f\u7684\u5e9c\u5929\u5927\u53a6\u8981\u5efa\u6210\u4e86\uff0c\u5728\u90a3\u91cc\u505a\u9910\u9986\uff0c\u5e02\u53e3\u5f88\u4e0d\u9519\u2026\u2026\u201d</p><p>\u3000\u3000\u4e39\u4e95\u5df7\u4ee5\u897f\u7684\u623f\u5b50\u90fd\u5dee\u4e0d\u591a\u62c6\u5149\u4e86\uff0c\u9648\u5947\u8fd8\u6ca1\u6709\u5962\u671b\u5230\u80fd\u5c06\u9910\u9986\u5f00\u5230\u5e9c\u5929\u5927\u53a6\u91cc\u9762\u53bb\uff0c\u7591\u60d1\u7684\u95ee\uff1a\u201c\u4e39\u4e95\u5df7\u7684\u623f\u5b50\u9a6c\u4e0a\u4e5f\u8981\u62c6\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e5f\u4e0d\u89c1\u5f97\uff0c\u201d\u8bb8\u9e3f\u4f2f\u8fd9\u65f6\u4e5f\u53ea\u80fd\u8fd9\u4e48\u8bf4\uff0c\u6362\u6210\u662f\u5510\u5b66\u8c26\u66f4\u4e0d\u80fd\u516c\u5f00\u8868\u6001\u8bf4\u90a3\u91cc\u623f\u5b50\u62c6\u4e0d\u4e86\uff0c\u201c\u6211\u5012\u662f\u6653\u5f97\u6709\u4eba\u4e70\u4e0b\u90a3\u91cc\u7684\u51e0\u5904\u65e7\u5b85\u5b50\uff0c\u4fee\u7f2e\u3001\u88c5\u6f62\u4e4b\u540e\u51c6\u5907\u79df\u51fa\u53bb\uff0c\u65e7\u5b85\u5b50\u6536\u62fe\u6574\u9f50\uff0c\u53e4\u8272\u53e4\u9999\u7684\uff0c\u975e\u5e38\u6709\u7279\u8272\uff0c\u5f88\u9002\u5408\u505a\u79c1\u623f\u83dc\u9910\u9986\u2026\u2026\u201d</p><p>\u3000\u3000\u8bb8\u601d\u8138\u6709\u4e9b\u5fae\u7ea2\uff0c\u5176\u4e2d\u4e00\u5904\u5b85\u5b50\uff0c\u5979\u8fd8\u8ddf\u5f20\u606a\u5728\u91cc\u9762\u4e91\u96e8\u8fc7\uff0c\u597d\u5728\u522b\u4eba\u4e5f\u4e0d\u4f1a\u8054\u60f3\u522b\u5904\u53bb\u3002\u5012\u662f\u5979\u5c0f\u8205\u65bd\u536b\u5fe0\u80fd\u731c\u5230\uff0c\u6bd5\u7adf\u5f20\u606a\u8fd8\u901a\u8fc7\u90b5\u81f3\u521a\u8ddf\u4ed6\u5c06\u8bb8\u601d\u5bb6\u7684\u623f\u5b50\u4e70\u8fc7\u6765\uff0c\u5e2e\u52a9\u5979\u5bb6\u5c06\u503a\u8fd8\u6389\u4e86\u3002</p><p>\u3000\u3000\u9648\u5947\u8fd8\u662f\u72b9\u8c6b\uff0c\u5218\u82ac\u5012\u662f\u723d\u5feb\uff0c\u8bf4\u9053\uff1a\u201c\u4e39\u4e95\u5df7\u7684\u5e02\u53e3\u6307\u5b9a\u597d\uff0c\u79df\u4e0b\u88c5\u6f62\u597d\u7684\u5b85\u5b50\uff0c\u5c31\u7b97\u8fc7\u4e00\u4e24\u5e74\u8981\u62c6\uff0c\u5927\u4e0d\u4e86\u518d\u6362\u5730\u65b9\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u88c5\u6f62\u518d\u597d\uff0c\u4e5f\u4e0d\u4e00\u5b9a\u9002\u5408\u505a\u9910\u9986\uff0c\u79df\u4e0b\u6765\uff0c\u8fd8\u5f97\u6295\u4e9b\u94b1\u8fdb\u53bb\u6539\u9020\uff0c\u518d\u8bf4\u4e00\u4e2a\u9910\u9986\u505a\u8d77\u6765\uff0c\u6295\u5165\u7684\u7cbe\u529b\u90fd\u662f\u5de8\u5927\u7684\uff0c\u8fc7\u4e00\u4e24\u5e74\u5c31\u8981\u53e6\u6362\u5730\u65b9\uff0c\u8fd8\u8981\u91cd\u65b0\u6298\u817e\u4e00\u56de\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u5947\u4e0d\u6653\u5f97\u5185\u60c5\uff0c\u4ed6\u7684\u8fd9\u4e9b\u987e\u8651\u90fd\u662f\u6709\u9053\u7406\u7684\u3002</p><p>\u3000\u3000\u201c\u8981\u505a\u9910\u9986\u7684\u8bdd\uff0c\u65e9\u8ddf\u4eba\u5bb6\u8054\u7cfb\uff0c\u8ba9\u4ed6\u4eec\u6309\u7167\u505a\u9910\u9986\u7684\u8981\u6c42\u88c5\u6f62\uff0c\u81f3\u4e8e\u90a3\u91cc\u4ec0\u4e48\u65f6\u5019\u4f1a\u62c6\u2026\u2026\u201d\u8bb8\u9e3f\u4f2f\u626d\u8fc7\u5934\u6765\u95ee\u5f20\u606a\uff0c\u201c\u4f60\u77e5\u4e0d\u77e5\u9053\u4ec0\u4e48\u6d88\u606f\uff1f\u201d</p><p>\u3000\u3000\u5468\u590d\u3001\u5468\u6587\u658c\u90fd\u77e5\u9053\u5f20\u606a\u7684\u8eab\u4efd\uff0c\u65bd\u536b\u5fe0\u77e5\u9053\u5f20\u606a\u7684\u8001\u5b50\u662f\u5e02\u653f\u5e9c\u79d8\u4e66\u957f\u5f20\u77e5\u884c\uff0c\u63d0\u5230\u8fd9\u4e2a\u8bdd\u9898\u4ed6\u4eec\u90fd\u5f88\u611f\u5174\u8da3\uff0c\u8981\u6709\u4ec0\u4e48\u5185\u5e55\u6d88\u606f\uff0c\u5f20\u606a\u8bf4\u51fa\u6765\u7684\u8bdd\u90a3\u5c31\u662f\u5185\u5e55\u6d88\u606f\uff0c\u90fd\u8f6c\u5934\u770b\u5411\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4f4e\u5934\u559d\u9152\uff0c\u4e0d\u7406\u4f1a\u8bb8\u9e3f\u4f2f\u629b\u6765\u7684\u95ee\u9898\uff0c\u62ac\u5934\u770b\u5927\u5bb6\u8fd8\u770b\u7740\u4ed6\uff0c\u563f\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u778e\u732b\u518d\u80fd\uff0c\u4e5f\u4e0d\u80fd\u8fde\u7eed\u78b0\u5230\u4e24\u56de\u6b7b\u8017\u5b50\uff0c\u4e07\u4e00\u8bf4\u9519\u8bdd\uff0c\u90a3\u4ee5\u540e\u5c31\u4e0d\u6562\u518d\u767b\u9648\u53d4\u5bb6\u7684\u95e8\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u522b\u4eba\u5efa\u8bae\u90fd\u662f\u597d\u610f\uff0c\u4e3b\u610f\u603b\u662f\u81ea\u5df1\u62ff\u7684\uff0c\u8fd8\u80fd\u6028\u522b\u4eba\u2026\u2026\u201d\u5218\u82ac\u76f4\u723d\u7684\u8bf4\u3002</p><p>\u3000\u3000\u201c\u90a3\u6211\u8bf4\u778e\u8bf4\u4e86\uff0c\u9648\u53d4\u4f60\u8bf4\u968f\u4fbf\u542c\u542c\u2026\u2026\u201d\u5f20\u606a\u77a5\u773c\u770b\u89c1\u9648\u5983\u84c9\u3001\u8bb8\u7ef4\u4ece\u91cc\u5c4b\u51fa\u6765\uff0c\u8fd8\u662f\u653e\u5f03\u957f\u7bc7\u5927\u8bba\uff0c\u8bf4\u9053\uff0c\u201c\u8bb8\u8001\u5e08\u8ddf\u6211\u8bf4\u8fc7\u4e00\u4ef6\u4e8b\uff0c\u6709\u4eba\u4e70\u4e0b\u4e39\u4e95\u5df7\u7684\u4e00\u5ea7\u65b9\u697c\u4fee\u7f2e\u6210\u539f\u6765\u7684\u6a21\u6837\uff0c\u5dee\u4e0d\u591a\u8981\u6295\u4e24\u4e09\u767e\u4e07\u8fdb\u53bb\uff0c\u73b0\u5728\u4fee\u4e00\u680b\u65e7\u5b85\u5b50\u6bd4\u62c6\u6389\u91cd\u5efa\u53ef\u8d39\u94b1\u591a\u4e86\uff0c\u6211\u60f3\u8fd9\u4eba\u5927\u6982\u4e0d\u5e0c\u671b\u8fc7\u4e00\u4e24\u5e74\u5c31\u7ed9\u62c6\u6389\uff0c\u603b\u5f52\u6709\u4e00\u4e9b\u628a\u63e1\u7684\u5427\uff1f\u201d</p><p>\u3000\u3000\u5468\u590d\u542c\u51fa\u6765\u4e86\uff0c\u5f20\u606a\u662f\u9f13\u52b1\u9648\u5947\u5728\u90a3\u91cc\u5f00\u9910\u9986\uff0c\u53ea\u662f\u4ed6\u7684\u8eab\u4efd\u654f\u611f\uff0c\u4e0d\u65b9\u4fbf\u5c06\u8bdd\u8bf4\u5f97\u592a\u900f\uff0c\u4ed6\u5e2e\u8154\u8bf4\u9053\uff1a\u201c\u5c31\u662f\u554a\uff0c\u522b\u4eba\u6295\u8d44\u88c5\u6f62\uff0c\u90a3\u6307\u4e0d\u5b9a\u662f\u6709\u4e9b\u5185\u5e55\u6d88\u606f\uff0c\u5c31\u7b97\u8fc7\u4e00\u4e24\u5e74\u8981\u62c6\uff0c\u4ed6\u4eec\u7684\u635f\u5931\uff0c\u80af\u5b9a\u8981\u6bd4\u4f60\u4ec5\u4ec5\u53ea\u505a\u9910\u9986\u8981\u5927\u5f97\u591a\u2026\u2026\u6211\u770b\u8981\u5f00\u4e5f\u5f00\u5927\u4e00\u70b9\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54ea\u6709\u90a3\u4e48\u94b1\u6295\u6765\uff1f\u8981\u4eba\u94b1\uff0c\u76f4\u63a5\u8fdb\u56db\u51e4\u6865\u4e86\u2026\u2026\u201d\u9648\u5947\u7b11\u4e86\u7b11\uff0c\u201c\u5c31\u6253\u7b97\u642c\u51fa\u53bb\uff0c\u8fd9\u91cc\u8981\u6709\u4eba\u5f00\u8f66\u5403\u996d\uff0c\u505c\u8f66\u90fd\u6ca1\u6709\u5730\u65b9\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u56db\u51e4\u6865\u90a3\u91cc\u4e5f\u6ca1\u5730\u65b9\u4e86\uff0c\u201d\u56db\u51e4\u6865\u9910\u996e\u5e7f\u573a\u662f\u5728\u65b0\u5149\u5382\u65e7\u5382\u7684\u57fa\u7840\u4e0a\u641e\u8d77\u6765\u7684\uff0c\u66fe\u662f\u65b0\u5149\u5382\u5382\u957f\u7684\u5468\u590d\u5bf9\u90a3\u91cc\u60c5\u51b5\u6700\u6e05\u695a\u4e86\uff0c\u201c\u539f\u6765\u65e7\u5382\u4e0e\u758f\u6e2f\u6cb3\u4e4b\u95f4\u8fd8\u6709\u4e00\u5757\u5730\uff0c\u758f\u6e2f\u6cb3\u758f\u6d5a\u4e4b\u540e\uff0c\u90a3\u5757\u5730\u4e00\u5206\u4e3a\u4e8c\uff0c\u4e34\u6cb3\u7684\u90e8\u5206\u505a\u6c34\u5cb8\u7eff\u5730\uff0c\u7ee7\u7eed\u5b8c\u5584\u90a3\u91cc\u7684\u73af\u5883\uff0c\u53e6\u4e00\u534a\u5730\u7ed9\u9910\u996e\u5e7f\u573a\u5efa\u4e8c\u671f\uff0c\u4e0d\u8fc7\u90a3\u5757\u5730\u4e0d\u5927\uff0c\u65b9\u6848\u521a\u62ff\u51fa\u6765\uff0c\u5c31\u7ed9\u4eba\u5bb6\u8ba2\u8d70\u4e86\uff0c\u6709\u4e24\u5bb6\u65e5\u5f0f\u9910\u9986\u3001\u8fd8\u6709\u4e00\u5bb6\u5df4\u897f\u70e7\u70e4\uff0c\u73b0\u5728\u5c31\u7b97\u6709\u94b1\uff0c\u4e5f\u8f6e\u4e0d\u5230\u4f60\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u4ee5\u56db\u51e4\u6865\u9910\u996e\u5e7f\u573a\u4e3a\u4e2d\u5fc3\uff0c\u4e00\u5e74\u7684\u65f6\u95f4\uff0c\u6574\u4e2a\u524d\u95e8\u7684\u9910\u996e\u5a31\u4e50\u91cd\u70b9\u5c31\u5411\u524d\u7aef\u7684\u56db\u51e4\u6865\u8f6c\u79fb\u4e86\uff0c\u4e0d\u662f\u9910\u996e\u5e97\uff0cKTV\u3001\u9152\u5427\u3001\u821e\u5385\u7b49\u6bd4\u8f83\u6709\u540d\u7684\u591c\u5e97\u3001\u5a31\u4e50\u573a\u90fd\u9646\u7eed\u5efa\u6210\uff0c\u76ee\u524d\u57ce\u5357\u533a\u53c8\u5f15\u8fdb\u4e00\u5bb6\u6e2f\u8d44\u4f01\u4e1a\u5728\u90a3\u91cc\u6295\u8d44\u5efa\u4e00\u5ea7\u4e09\u661f\u7ea7\u7684\u5bbe\u9986\uff0c\u591c\u95f4\u7684\u7e41\u534e\u7a0b\u4e0d\u6bd4\u524d\u95e8\u4e2d\u5fc3\u533a\u5dee\u534a\u5206\uff0c\u4e5f\u5c06\u4e2d\u5fc3\u5546\u8d38\u533a\u7684\u8303\u56f4\u6269\u5927\u5230\u56db\u51e4\u6865\u4ee5\u5916\uff0c\u4e0d\u8fc7\u5b8b\u57f9\u660e\u7684\u6001\u5ea6\u5f88\u575a\u51b3\uff0c\u8ddf\u675c\u5c0f\u5c71\u79c1\u4eba\u4ea4\u60c5\u5f52\u4ea4\u60c5\uff0c\u5374\u6b7b\u6d3b\u4e0d\u80af\u5c06\u56db\u51e4\u6865\u90a3\u91cc\u5f52\u5165\u897f\u57ce\u533a\u3002</p><p>\u3000\u3000\u5468\u590d\u8bf4\u9053\uff1a\u201c\u524d\u95e8\u5f80\u5357\uff0c\u5f80\u4e1c\u53d1\u5c55\u662f\u5927\u8d8b\u52bf\uff0c\u4e0d\u4e00\u5b9a\u8981\u6324\u8fdb\u56db\u51e4\u6865\u53bb\uff0c\u4f55\u51b5\u73b0\u5728\u56db\u51e4\u6865\u505a\u8d77\u6765\uff0c\u6324\u8fdb\u53bb\uff0c\u79df\u91d1\u4e0d\u4fbf\u5b9c\uff0c\u6211\u770b\u5728\u4e39\u4e95\u5df7\u505a\u9910\u996e\u5c31\u5f88\u6709\u524d\u9014\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u5947\u8ba9\u5927\u5bb6\u8bf4\u5f97\u6709\u4e9b\u5fc3\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u4e39\u4e95\u5df7\u90a3\u91cc\u90fd\u662f\u4e00\u4e9b\u5927\u5b85\u5b50\uff0c\u6211\u8fd9\u5bb6\u9152\u9986\u52a0\u4e86\u4e24\u95f4\u5c4b\u5b50\uff0c\u624d\u4e00\u767e\u591a\u4e2a\u5e73\u65b9\uff0c\u505a\u592a\u5927\u7684\u9910\u9986\uff0c\u5c31\u7b97\u88c5\u6f62\u90fd\u4e0d\u7528\u7ba1\uff0c\u4e5f\u62ff\u4e0d\u51fa\u90a3\u4e48\u591a\u94b1\u6765\u6295\u2026\u2026\u201d</p><p>\u3000\u3000\u5218\u82ac\u8bf4\u9053\uff1a\u201c\u5468\u603b\u9f13\u52a8\u6211\u5bb6\u8001\u9648\u505a\u5927\u4e00\u70b9\u7684\u9910\u9986\uff0c\u8981\u4e0d\u4f60\u5e2e\u6211\u5bb6\u8001\u9648\u60f3\u60f3\u8d44\u91d1\u7684\u95ee\u9898\u2026\u2026\u6211\u770b\u4f60\u5012\u662f\u5f88\u8d2a\u5403\uff0c\u8981\u4e0d\u4f60\u4e5f\u53c2\u80a1\u5f97\u4e86\uff1f\u5468\u6587\u658c\u4e5f\u7b97\u4e00\u4efd\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u6e38\u65e9\u542c\u51fa\u5f20\u606a\u7684\u8bdd\u5916\u97f3\uff0c\u4e39\u4e95\u5df7\u591a\u534a\u62c6\u4e0d\u4e86\uff0c\u4e39\u4e95\u5df7\u62c6\u4e0d\u4e86\uff0c\u524d\u8857\u62d3\u5bbd\u4e86\uff0c\u5e9c\u5929\u5927\u53a6\u53c8\u5c06\u843d\u6210\uff0c\u90a3\u91cc\u5e02\u53e3\u771f\u662f\u597d\u5f97\u6ca1\u8bdd\u8bf4\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u662f\u7b2c\u4e8c\u4e2a\u56db\u51e4\u6865\u3002\u56db\u51e4\u6865\u90a3\u91cc\u7684\u79df\u91d1\u5728\u4e24\u5e74\u5185\u6da8\u4e86\u591a\u5c11\uff1f\u5dee\u4e0d\u591a\u6709\u4e03\u516b\u500d\u3002\u5468\u6e38\u8fd8\u62ff\u4e0d\u94b1\u6765\u6295\u8d44\u7269\u4e1a\uff0c\u90a3\u91cc\u968f\u4fbf\u4e00\u680b\u5b85\u5b50\uff0c\u90fd\u5728\u4e03\u767e\u5e73\u7c73\u4ee5\u4e0a\uff0c\u4ee5\u62c6\u8fc1\u4ef7\u4e70\u4e0b\u6765\uff0c\u518d\u4fee\u7f2e\u6539\u9020\uff0c\u5c11\u8bf4\u4e5f\u8981\u6295\u4e00\u767e\u4e07\u8fdb\u53bb\u624d\u591f\uff0c\u800c\u4e14\u62c6\u4e0e\u4e0d\u62c6\uff0c\u6bd5\u7adf\u662f\u60ac\u800c\u672a\u51b3\u7684\u4e8b\u60c5\uff0c\u98ce\u9669\u5f88\u5927\u3002\u6295\u8d44\u505a\u9910\u996e\uff0c\u5c31\u7b97\u4e00\u4e24\u5e74\u62c6\u6389\uff0c\u635f\u5931\u4e5f\u5f88\u6709\u9650\uff0c\u4e07\u4e00\u4e0d\u62c6\uff0c\u51ed\u90a3\u91cc\u7684\u5e02\u53e3\uff0c\u7edd\u5bf9\u662f\u4e00\u4e2a\u805a\u5b9d\u76c6\u3002\u60f3\u60f3\u5434\u5929\u5b9d\uff0c\u7a9d\u6c99\u7530\u5317\u9762\u641e\u5efa\u90ba\u9152\u5e97\uff0c\u51e0\u5e74\u90fd\u6ca1\u6709\u5927\u51fa\u606f\uff0c\u8fdb\u4e86\u56db\u51e4\u6865\uff0c\u5374\u7529\u5f00\u624b\u505a\u5176\u4ed6\u4e8b\u60c5\uff0c\u5c31\u662f\u56e0\u4e3a\u4ed6\u5728\u56db\u51e4\u6865\u7684\u5e97\u7ed9\u4ed6\u575a\u5f3a\u7684\u540e\u76fe\u3002</p><p>\u3000\u3000\u5927\u5bcc\u5929\u6210\u5c31\uff0c\u5c0f\u5bcc\u52e4\u52b3\u51fa\uff0c\u5f20\u606a\u90a3\u6837\u7684\u624b\u7b14\uff0c\u5468\u590d\u81ea\u8ba4\u4e3a\u73a9\u4e0d\u51fa\u6765\uff0c\u4f46\u662f\u4e00\u4e9b\u6709\u6f5c\u529b\u7684\u5c0f\u6295\u8d44\uff0c\u4ed6\u8fd8\u662f\u5f88\u5173\u5fc3\u3002\u4e0d\u8fc7\u4ed6\u773c\u4e0b\u6709\u987e\u8651\u5462\uff0c\u4ed6\u662f\u9526\u6e56\u8d1f\u8d23\u7eb8\u4e1a\u751f\u4ea7\u8425\u8fd0\u7684\u526f\u603b\uff0c\u603b\u4e0d\u80fd\u5f53\u7740\u8001\u677f\u7684\u9762\u8868\u9732\u51fa\u6709\u505a\u526f\u4e1a\u7684\u5fc3\u601d\uff0c\u5c31\u7b97\u4ed6\u624b\u91cc\u7684\u6709\u4e9b\u8d44\u91d1\uff0c\u90a3\u4e5f\u662f\u9526\u6e56\u7ed9\u4ed6\u4e30\u539a\u7684\u85aa\u8d44\u79ef\u7d2f\u6240\u81f4\uff0c\u66f4\u4f55\u51b5\u516c\u53f8\u773c\u4e0b\u5df2\u7ecf\u8003\u8651\u4e70\u4e0b\u65b0\u5149\u5382\u7684\u4e8b\u5b9c\uff0c\u4ed6\u8fd9\u6b21\u4e5f\u53ef\u4ee5\u6301\u80a1\u7684\u3002</p><p>\u3000\u3000\u5218\u82ac\u4e0d\u63d0\u8fd9\u4e8b\uff0c\u5468\u590d\u7edd\u5bf9\u4e0d\u4f1a\u4e3b\u52a8\u63d0\u8d77\uff0c\u5218\u82ac\u63d0\u8d77\u8fd9\u4e8b\uff0c\u4ed6\u5c31\u62ff\u773c\u775b\u53bb\u770b\u5f20\u606a\uff0c\u5f20\u606a\u53ea\u662f\u6de1\u7136\u7b11\u7740\uff0c\u8138\u4e0a\u6ca1\u6709\u592a\u591a\u7684\u8868\u60c5\u3002\u65e0\u8bba\u662f\u8c01\uff0c\u8981\u6c42\u4ed6\u4eec\u5168\u8eab\u5fc3\u7684\u6295\u5165\u5230\u516c\u53f8\u7684\u4e8b\u4e1a\u4e0a\uff0c\u90fd\u662f\u82db\u6c42\u7684\uff0c\u4f55\u51b5\u4e2a\u4eba\u8d44\u4ea7\u7684\u518d\u6295\u8d44\u672c\u8eab\u5c31\u662f\u5e94\u8be5\u8ba4\u771f\u8003\u8651\u7684\u4e8b\u60c5\uff0c\u4e5f\u662f\u79c1\u4eba\u7684\u95ee\u9898\uff0c\u5f20\u606a\u90fd\u4e0d\u4f1a\u8fc7\u95ee\u7684\u3002</p><p>\u3000\u3000\u89c1\u5f20\u606a\u7684\u8868\u60c5\u6ca1\u6709\u5f02\u6837\uff0c\u5468\u590d\u72b9\u8c6b\u7684\u56de\u590d\u5218\u82ac\uff0c\u8bf4\u9053\uff1a\u201c\u5012\u4e0d\u662f\u4e0d\u80fd\u8003\u8651\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd8\u8003\u8651\u5565\uff1f\u201d\u5218\u82ac\u76f4\u723d\u7684\u8bf4\u9053\uff0c\u201c\u4f60\u4eca\u5e74\u7684\u62ff\u4e0d\u5c11\u94b1\u5427\uff0c\u50cf\u5468\u603b\u8fd9\u4e48\u6709\u7ecf\u6d4e\u5934\u8111\u7684\u4eba\uff0c\u62ff\u4e86\u5de5\u8d44\u96be\u9053\u653e\u5728\u94f6\u884c\u91cc\u5403\u5229\u606f\uff1f\u201d</p></article>"}}}]);
