(window.webpackJsonp=window.webpackJsonp||[]).push([[301],{323:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e03\u8282 \u9009\u6c11",content:"<article><h2>\u7b2c\u5341\u4e03\u8282 \u9009\u6c11</h2><p>\u743c\u6069\u662f\u4e2a\u5bbd\u5bb9\u7684\u4eba\u3002</p><p>\u6240\u8c13\u5bbd\u5bb9\uff0c\u610f\u601d\u5c31\u662f\u8bf4\uff0c\u4f5c\u4e3a\u4e00\u4e2a\u59d0\u63a7\uff0c\u4ed6\u4e0d\u6b67\u89c6\u90a3\u79cd\u6709\u604b\u59b9\u60c5\u7ed3\u7684\u5bb6\u4f19\uff0c\u6bd4\u5982\u773c\u524d\u7684\u8fd9\u4f4d\u5438\u8840\u9b3c\u3002</p><p>\u201c\u8389\u8389\u4e1d\u5c0f\u59d0\u4e5f\u662f\u7d2b\u8272\u773c\u775b\u5417\uff1f\u201d\u4ed6\u9887\u6709\u5174\u8da3\u5730\u95ee\u3002</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u65e2\u7136\u8ba4\u4e3a\u7d2b\u8272\u773c\u775b\u662f\u5584\u826f\u7684\u8c61\u5f81\uff0c\u90a3\u4e3a\u4ec0\u4e48\u4e0d\u76f4\u63a5\u5728\u5a74\u513f\u7684\u65f6\u5019\u5c31\u6740\u6b7b\u5462\uff1f\u201d</p><p>\u201c\u90a3\u662f\u4eba\u7c7b\u7684\u8c23\u4f20\u800c\u5df2\uff0c\u201d\u5fb7\u53e4\u62c9\u6447\u6447\u5934\uff0c\u201c\u6211\u4eec\u5353\u5c14\u7684\u773c\u775b\u989c\u8272\u672c\u6765\u5c31\u591a\uff0c\u94f6\u8272\u7684\uff0c\u7c89\u8272\u7684\uff0c\u84dd\u8272\u7684\uff0c\u7ea2\u8272\u7684\uff0c\u90fd\u5f88\u5e38\u89c1\uff0c\u7d2b\u8272\u867d\u7136\u7a00\u5c11\uff0c\u4f46\u4e5f\u4e0d\u7b97\u4ec0\u4e48\u532a\u5937\u6240\u601d\uff0c\u8ddf\u5584\u826f\u90aa\u6076\u66f4\u626f\u4e0d\u4e0a\u5173\u7cfb\u2014\u2014\u4f60\u4e0d\u4f1a\u771f\u76f8\u4fe1\u8fd9\u79cd\u65e0\u7a3d\u4e4b\u8c08\u5427\u3002\u201d</p><p>\u201c\u5514\uff0c\u6211\u6709\u70b9\u597d\u5947\uff0c\u95ee\u95ee\u7f62\u4e86\u3002\u201d</p><p>\u201c\u6ca1\u90a3\u4e48\u56de\u4e8b\uff0c\u201d\u5fb7\u53e4\u62c9\u6447\u5934\uff0c\u201c\u81f3\u5c11\u4ee5\u4f60\u4eec\u4eba\u7c7b\u7684\u6807\u51c6\u6765\u8bf4\uff0c\u8389\u8389\u4e1d\u5c31\u7edd\u4e0d\u7b97\u5584\u826f\u3002\u201d</p><p>\u201c\u4f60\u8fd9\u4e48\u8bf4\u4f60\u59b9\u59b9\uff1f\u201d</p><p>\u201c\u5728\u6211\u4eec\u7684\u8f9e\u5178\u91cc\uff0c\u5584\u826f\u662f\u8d2c\u4e49\u8bcd\u3002\u201d</p><p>\u201c\u90a3\u4f60\u4eec\u7684\u8f9e\u5178\u91cc\u6709\u6ca1\u6709\u4e71\u4f26\u8fd9\u4e2a\u8bcd\u3002\u201d</p><p>\u201c\u6ca1\u6709\u3002\u201d</p><p>\u201c\u4e5f\u662f\uff0c\u4f60\u4eec\u6ca1\u6709\u5a5a\u59fb\u5236\u5ea6\uff0c\u201d\u743c\u6069\u62cd\u4e86\u62cd\u5438\u8840\u9b3c\u7684\u80a9\u8180\uff0c\u201c\u52a0\u6cb9\u5427\uff0c\u4e2d\u5c09\uff0c\u6211\u770b\u597d\u4f60\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u610f\u601d\u5c31\u662f\u8bf4\u4f60\u53ef\u4ee5\u653e\u5fc3\u5927\u80c6\u5730\u8ffd\u6c42\u4f60\u7684\u59b9\u59b9\u2014\u2014\u6211\u662f\u8bf4\uff0c\u4f60\u53ef\u4ee5\u628a\u5979\u5f53\u4f5c\u4f60\u7684\u59b9\u59b9\u3002\u201d</p><p>\u5438\u8840\u9b3c\u7ffb\u4e86\u7ffb\u767d\u773c\uff0c\u201c\u6211\u6ca1\u8fd9\u4e2a\u610f\u601d\u3002\u201d</p><p>\u201c\u4e0d\u7528\u4e0d\u597d\u610f\u601d\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u80fd\u7406\u89e3\u3002\u201d</p><p>\u5fb7\u53e4\u62c9\u95ed\u7740\u5634\uff0c\u4e0d\u80af\u518d\u8bf4\u8bdd\u3002</p><p>\u743c\u6069\u8038\u8038\u80a9\uff0c\u6ca1\u6709\u7ee7\u7eed\u8fd9\u4e2a\u8bdd\u9898\u3002\u4f17\u4eba\u4e00\u8def\u56de\u5230\u6668\u70bc\u57ce\uff0c\u5176\u4ed6\u4e24\u961f\u4eba\u4e5f\u9646\u7eed\u8fd4\u56de\u3002\u8fd9\u6b21\u62a2\u52ab\u6536\u83b7\u4e0d\u5c0f\uff0c\u5305\u62ec\u516b\u5341\u526f\u7cbe\u5236\u9501\u7532\u3001\u4e94\u5341\u67c4\u957f\u5251\u548c\u4e94\u5341\u9762\u5706\u76fe\u4ee5\u53ca\u5927\u91cf\u7684\u4e0a\u7b49\u8611\u83c7\u9152\u3002\u6668\u70bc\u57ce\u5982\u4eca\u6b63\u7f3a\u5175\u7532\uff0c\u867d\u7136\u4ece\u4f0a\u5361\u6c99\u57ce\u83b7\u5f97\u4e00\u6279\uff0c\u4f46\u8fd9\u6bb5\u65f6\u95f4\u6765\u4e5f\u635f\u8017\u5927\u534a\u4e86\uff0c\u6b63\u9700\u8981\u8865\u5145\u3002\u81f3\u4e8e\u8611\u83c7\u9152\uff0c\u867d\u7136\u4e0d\u662f\u6025\u9700\u4e4b\u7269\uff0c\u81f3\u5c11\u53ef\u4ee5\u4f5c\u4e3a\u798f\u5229\u53d1\u4e0b\u53bb\u3002</p><p>\u743c\u6069\u5728\u8fd9\u8fb9\u5fd9\u7740\u4ea4\u6d89\u6218\u5229\u54c1\u5206\u914d\u7684\u95ee\u9898\uff0c\u6885\u83f2\u65af\u548c\u5fb7\u53e4\u62c9\u5219\u5728\u8be2\u95ee\u7d2b\u8272\u773c\u775b\u5c0f\u5973\u5b69\u7684\u6765\u5386\u3002\u8def\u4e0a\u5306\u5fd9\uff0c\u4e5f\u6ca1\u987e\u5f97\u4e0a\u7ec6\u770b\uff0c\u5982\u4eca\u628a\u8138\u4e0a\u6ce5\u6c61\u6d17\u6389\uff0c\u6362\u4e86\u8863\u670d\uff0c\u53d1\u73b0\u5c45\u7136\u8fd8\u662f\u4e2a\u5c0f\u7f8e\u4eba\uff0c\u9887\u4e3a\u79c0\u4e3d\u3002\u4e00\u95ee\u4e4b\u4e0b\uff0c\u5f97\u77e5\u5979\u53eb\u4f0a\u8389\u96c5\uff0c\u539f\u672c\u4e5f\u662f\u8d35\u65cf\uff0c\u5979\u7684\u5bb6\u65cf\u5728\u524d\u6bb5\u65f6\u95f4\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u5974\u96b6\u53db\u4e71\u4e2d\u88ab\u6bc1\u706d\uff0c\u81ea\u5df1\u5219\u843d\u5230\u4e86\u5546\u4eba\u624b\u4e2d\u3002\u548c\u4eba\u7c7b\u4e16\u754c\u4e00\u6837\uff0c\u5353\u5c14\u5f53\u4e2d\u4e5f\u6709\u5974\u96b6\u8d38\u6613\uff0c\u800c\u4e14\u56e0\u4e3a\u5973\u5c0a\u7537\u5351\u7684\u793e\u4f1a\u4f53\u5236\uff0c\u5bfc\u81f4\u5973\u6027\u5974\u96b6\u7684\u4ef7\u503c\u6781\u9ad8\u3002\u50cf\u4f0a\u8389\u96c5\u8fd9\u79cd\u8d35\u65cf\u51fa\u8eab\u7684\u5c0f\u5973\u5b69\uff0c\u53ea\u8981\u6ca1\u6709\u6b8b\u75be\uff0c\u4e0d\u662f\u76f8\u8c8c\u4e11\u964b\uff0c\u5176\u4ef7\u503c\u80dc\u8fc7\u767e\u540d\u7537\u6027\u5974\u96b6\u3002\u5018\u82e5\u4e0d\u662f\u56e0\u4e3a\u5e74\u9f84\u5c1a\u5e7c\uff0c\u53ea\u6015\u73b0\u5728\u65e9\u5c31\u5df2\u7ecf\u9001\u8fdb\u6c89\u9ed8\u4e4b\u6b4c\u91cc\u53bb\u4e86\u3002\u6b64\u6b21\u539f\u672c\u662f\u8981\u524d\u5f80\u827e\u745e\u5fb7\u6797\u57ce\uff0c\u6ca1\u60f3\u5230\u8def\u4e0a\u649e\u5230\u743c\u6069\u7b49\u4eba\uff0c\u5176\u4ed6\u4eba\u90fd\u6b7b\u4e86\uff0c\u5979\u5374\u673a\u7075\uff0c\u65e9\u65e9\u5c31\u8eb2\u5728\u5c38\u4f53\u4e2d\u88c5\u6b7b\uff0c\u9003\u8fc7\u4e00\u52ab\u3002</p><p>\u5728\u5982\u4f55\u5b89\u6392\u4f0a\u8389\u96c5\u7684\u95ee\u9898\u4e0a\uff0c\u8d77\u4e86\u70b9\u5c0f\u5c0f\u7684\u5206\u6b67\u3002\u743c\u6069\u89c9\u5f97\u65e2\u7136\u5979\u50cf\u5fb7\u53e4\u62c9\u7684\u59b9\u59b9\uff0c\u90a3\u5c31\u76f4\u63a5\u8ba9\u5438\u8840\u9b3c\u53bb\u5b89\u6392\u64cd\u5fc3\u597d\u4e86\uff0c\u4f46\u8fd9\u906d\u5230\u4e86\u6885\u83f2\u65af\u53cd\u5bf9\u3002\u5979\u90fd\u8ba4\u4e3a\u5e94\u8be5\u65e9\u65e9\u57f9\u517b\u8d77\u4f0a\u8389\u96c5\u6b63\u786e\u7684\u4eba\u751f\u89c2\u4e16\u754c\u89c2\u2014\u2014\u4e5f\u5c31\u662f\u5b97\u6559\u4fe1\u4ef0\u4e86\uff0c\u800c\u5fb7\u53e4\u62c9\u663e\u7136\u4e0d\u662f\u4e2a\u5408\u9002\u7684\u76d1\u62a4\u4eba\u3002</p><p>\u201c\u96be\u9053\u4f60\u8fd8\u60f3\u57f9\u517b\u51fa\u4e00\u4e2a\u5353\u5c14\u5723\u6b66\u58eb\u51fa\u6765\uff1f\u201d</p><p>\u201c\u4e5f\u672a\u5c1d\u4e0d\u53ef\u554a\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u4f60\u662f\u5728\u5f00\u73a9\u7b11\u5427\u3002\u201d</p><p>\u201c\u786e\u5b9e\u662f\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u201c\u6211\u662f\u6ca1\u7a7a\uff0c\u4e0d\u8fc7\u4e0d\u662f\u8fd8\u6709\u522b\u4eba\u4e48\u3002\u201d</p><p>\u201c\u8c01\uff1f\u201d</p><p>\u6885\u83f2\u65af\u4f38\u624b\u5f80\u95e8\u53e3\u4e00\u6307\uff0c\u743c\u6069\u3001\u5fb7\u53e4\u62c9\u548c\u4f0a\u8389\u96c5\u5c0f\u841d\u8389\u90fd\u987a\u7740\u770b\u8fc7\u53bb\uff0c\u6b64\u65f6\u6708\u7cbe\u7075\u963f\u5fd2\u59ae\u6b63\u597d\u63a8\u5f00\u95e8\u8d70\u8fdb\u6765\uff0c\u8fce\u4e0a\u4e86\u6240\u6709\u4eba\u7684\u76ee\u5149\uff0c\u4e00\u65f6\u6709\u4e9b\u83ab\u540d\u5176\u5999\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u201d\u6885\u83f2\u65af\u7b11\u7740\uff0c\u201c\u6b63\u8bf4\u5230\u4f60\u3002\u201d</p><p>\u628a\u4e8b\u60c5\u7ecf\u8fc7\u89e3\u91ca\u4e86\u4e00\u904d\uff0c\u6885\u83f2\u65af\u7684\u610f\u601d\u662f\u4ece\u4f0a\u8389\u4e1d\u7fe0\u6559\u4f1a\u4e2d\u8bf7\u4e00\u4f4d\u7267\u5e08\u6765\u4f5c\u4e3a\u4f0a\u8389\u96c5\u7684\u5bfc\u5e08\uff0c\u5bf9\u6b64\u5927\u5bb6\u90fd\u6ca1\u4ec0\u4e48\u53cd\u5bf9\u610f\u89c1\uff0c\u9664\u4e86\u5fb7\u53e4\u62c9\u7565\u6709\u4e0d\u6ee1\uff0c\u4f46\u6700\u7ec8\u4e5f\u6ca1\u8bf4\u4ec0\u4e48\u3002</p><p>\u8fd9\u4ef6\u4e8b\u60c5\u5c31\u4ea4\u7ed9\u963f\u5fd2\u59ae\u5b89\u6392\uff0c\u5979\u8fd9\u6b21\u8fc7\u6765\u5012\u662f\u4e3a\u4e86\u901a\u77e5\u53e6\u5916\u4e00\u4ef6\u4e8b\uff1a\u201c\u8475\u9732\u5c06\u8981\u62b5\u8fbe\u6668\u70bc\u57ce\u4e86\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u6668\u70bc\u57ce\u5982\u4eca\u5df2\u7ecf\u6210\u4e3a\u4e86\u4f0a\u8389\u4e1d\u7fe0\u6559\u4f1a\u7684\u5927\u672c\u8425\uff0c\u743c\u6069\u7b49\u4eba\u867d\u7136\u4e5f\u5360\u4e86\u4e00\u5757\u5730\u76d8\uff0c\u7ec8\u7a76\u7b97\u662f\u5ba2\u537f\u8eab\u4efd\uff0c\u4e0d\u662f\u4e3b\u4eba\u3002\u8475\u9732\u662f\u4f0a\u8389\u4e1d\u7fe0\u6559\u4f1a\u7684\u9886\u8896\uff0c\u5979\u6765\u4e86\u4e4b\u540e\uff0c\u96be\u514d\u4f1a\u6253\u4ea4\u9053\uff0c\u51fa\u4e8e\u77e5\u5df1\u77e5\u5f7c\u7684\u8003\u8651\uff0c\u743c\u6069\u4e8e\u662f\u79c1\u4e0b\u5411\u6885\u83f2\u65af\u8bf7\u6559\u8fd9\u4f4d\u8475\u9732\u5c0f\u59d0\u7684\u5177\u4f53\u8d44\u6599\u3002</p><p>\u201c\u8475\u9732\u662f\u4e24\u4f4d\u5973\u58eb\u5171\u540c\u6307\u5b9a\u7684\u9009\u6c11\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u6240\u8c13\u4e24\u4f4d\u5973\u58eb\uff0c\u662f\u6307\u9b54\u6cd5\u5973\u795e\u5bc6\u65af\u62c9\u548c\u5e7d\u6697\u5c11\u5973\u4f0a\u8389\u4e1d\u7fe0\uff0c\u8475\u9732\u662f\u5979\u4eec\u7684\u5171\u540c\u9009\u6c11\uff0c\u8fd9\u662f\u5f88\u7f55\u89c1\u7684\u3002\u5b9e\u9645\u4e0a\uff0c\u8475\u9732\u4e0d\u4ec5\u4ec5\u662f\u9009\u6c11\uff0c\u800c\u4e14\u8fd8\u662f\u795e\u5b50\u3002</p><p>\u5728\u5386\u53f2\u4e0a\uff0c\u9b54\u6cd5\u5973\u795e\u5bc6\u65af\u62c9\u66fe\u7ecf\u4ee5\u5723\u8005\u5f62\u6001\u9644\u4f53\u5979\u7684\u5973\u4fe1\u5f92\u827e\u5c14\xb7\u6851\u8fbe\uff0c\u548c\u51e1\u4eba\u591a\u8499\xb7\u94f6\u624b\u751f\u80b2\u4e86\u4e03\u4e2a\u5973\u513f\uff0c\u8fd9\u5c31\u662f\u8457\u540d\u7684\u201c\u4e03\u59d0\u59b9\u201d\u3002\u51db\u7684\u8001\u5e08\uff0c\u963f\u683c\u62c9\u9686\u5973\u738b\u6b23\u5e03\uff0c\u4ee5\u53ca\u5965\u6c83\u66fe\u7ecf\u6253\u8fc7\u4e3b\u610f\u7684\u83b1\u62c9\uff0c\u73b0\u5728\u662f\u6df1\u6c34\u57ce\u4e3b\u51ef\u5c14\u672c\u7684\u592b\u4eba\uff0c\u90fd\u662f\u4e03\u59d0\u59b9\u4e4b\u4e00\u3002\u5bc6\u65af\u62c9\u8d4b\u4e88\u5979\u7684\u4e03\u4e2a\u5973\u513f\u795e\u529b\uff0c\u8ba9\u5979\u4eec\u6210\u4e3a\u81ea\u5df1\u7684\u9009\u6c11\u3002\u8475\u9732\u4fbf\u662f\u201c\u4e03\u59d0\u59b9\u201d\u4e2d\u6700\u5c0f\u7684\u4e00\u4f4d\u3002</p><p>\u201c\u7b49\u4e00\u4e0b\uff0c\u201d\u743c\u6069\u4e3e\u624b\u6253\u65ad\uff0c\u201c\u5982\u679c\u6211\u6ca1\u542c\u9519\u7684\u8bdd\uff0c\u4f60\u662f\u8bf4\u9b54\u6cd5\u5973\u795e\u4ee5\u5723\u8005\u5f62\u6001\u964d\u4e34\uff0c\u9644\u4f53\u51e1\u4eba\uff0c\u751f\u4e0b\u4e86\u4e03\u59d0\u59b9\u3002\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u5973\u795e\u9644\u4f53\u7684\u90a3\u4f4d\u827e\u5c14\xb7\u6851\u8fbe\u5c0f\u59d0\uff0c\u662f\u4eba\u7c7b\uff1f\u201d</p><p>\u201c\u4eba\u7c7b\u3002\u201d</p><p>\u201c\u5979\u7684\u4e08\u592b\u2014\u2014\u6211\u662f\u8bf4\uff0c\u5979\u9644\u4f53\u7684\u51e1\u4eba\u7684\u4e08\u592b\uff0c\u591a\u8499\xb7\u94f6\u624b\uff0c\u662f\u4eba\u7c7b\uff1f\u201d</p><p>\u201c\u4eba\u7c7b\u3002\u201d</p><p>\u201c\u6240\u4ee5\u6b23\u5e03\u662f\u4eba\u7c7b\uff0c\u8fd9\u5f88\u5408\u7406\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f46\u8475\u9732\u4f3c\u4e4e\u662f\u5353\u5c14\u5427\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u5728\u5973\u795e\u751f\u4e0b\u7b2c\u516d\u4e2a\u5973\u513f\u4e4b\u540e\uff0c\u827e\u5c14\xb7\u6851\u8fbe\u53bb\u4e16\u4e86\u3002\u201d</p><p>\u795e\u7947\u51fa\u73b0\u5728\u7269\u8d28\u754c\uff0c\u6709\u4e24\u79cd\u5e38\u89c1\u7684\u5f62\u5f0f\uff0c\u7b2c\u4e00\u79cd\u662f\u521b\u9020\u5316\u8eab\uff0c\u6709\u4e9b\u7c7b\u4f3c\u4e8e\u6076\u9b54\u7684\u201c\u6295\u5f71\u201d\uff0c\u5f53\u7136\u8981\u5f3a\u5f97\u591a\uff0c\u76f8\u5f53\u4e8e\u5728\u7269\u8d28\u754c\u51fa\u73b0\u4e00\u4e2a\u5f31\u5316\u7248\u672c\u7684\u201c\u795e\u7947\u201d\uff0c\u4f46\u5b58\u5728\u65f6\u95f4\u5f88\u77ed\u3002\u5982\u679c\u5316\u8eab\u88ab\u6467\u6bc1\uff0c\u795e\u7947\u4f1a\u53d7\u91cd\u521b\u3002\u7b2c\u4e8c\u79cd\u5c31\u662f\u5723\u8005\u964d\u4e34\uff0c\u7b80\u5355\u6765\u8bf4\u5c31\u662f\u795e\u7947\u9644\u4f53\u5728\u67d0\u4e2a\u51e1\u4eba\u8eab\u4e0a\uff0c\u80fd\u591f\u957f\u4e45\u5730\u505c\u7559\u5728\u7269\u8d28\u754c\uff0c\u4f46\u4e00\u65e6\u88ab\u6467\u6bc1\uff0c\u795e\u7947\u4e5f\u5c31\u6b7b\u4ea1\u3002</p><p>\u5bc6\u65af\u62c9\u4f7f\u7528\u7684\u65b9\u5f0f\u663e\u7136\u4fbf\u662f\u5723\u8005\u964d\u4e34\uff0c\u501f\u7528\u4fe1\u5f92\u7684\u8eab\u4f53\u53d7\u5b55\u751f\u80b2\uff0c\u4f46\u8fd9\u79cd\u65b9\u5f0f\u7ec8\u7a76\u5bf9\u51e1\u4eba\u6709\u7740\u5de8\u5927\u7684\u635f\u5bb3\u3002\u827e\u5c14\xb7\u6851\u8fbe\u6025\u901f\u5730\u82cd\u8001\u3001\u8870\u673d\uff0c\u53d8\u5f97\u4eff\u4f5b\u4e00\u5177\u5e72\u5c38\uff0c\u5728\u751f\u80b2\u4e86\u516d\u4e2a\u5973\u513f\u4e4b\u540e\uff0c\u5979\u7ec8\u4e8e\u56de\u5f52\u957f\u7720\uff0c\u4f46\u5728\u6b64\u65f6\uff0c\u5979\u7684\u8179\u4e2d\u5df2\u7ecf\u6709\u4e86\u7b2c\u4e03\u4e2a\u5a74\u513f\u7684\u80da\u80ce\u3002\u5973\u795e\u7528\u795e\u529b\u5c06\u80ce\u513f\u8f6c\u79fb\u5230\u4e00\u4f4d\u4f0a\u8389\u4e1d\u7fe0\u7267\u5e08\uff08\u5f53\u7136\u662f\u5353\u5c14\uff09\u7684\u8179\u4e2d\uff0c\u501f\u52a9\u5979\u7684\u8eab\u4f53\u751f\u4e0b\u4e86\u7b2c\u4e03\u4e2a\u5973\u513f\uff0c\u8fd9\u5c31\u662f\u8475\u9732\u4e3a\u4ec0\u4e48\u662f\u5353\u5c14\u7684\u7f18\u6545\u3002\u8475\u9732\u88ab\u4f0a\u8389\u4e1d\u7fe0\u7267\u5e08\u629a\u517b\u957f\u5927\uff0c\u540e\u6765\u540c\u65f6\u88ab\u4e24\u4f4d\u5973\u795e\u8d4b\u4e88\u795e\u529b\uff0c\u6210\u4e3a\u9009\u6c11\uff0c\u5982\u4eca\u662f\u5e7d\u6697\u5c11\u5973\u6559\u4f1a\u7684\u9886\u8896\u3002</p><p>\u201c\u4e16\u754c\u4e0a\u9009\u6c11\u5f88\u591a\u4e48\u3002\u201d\u743c\u6069\u987a\u53e3\u95ee\u3002</p><p>\u201c\u4e0d\u5c11\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5f88\u591a\u795e\u7947\u90fd\u6709\u9009\u6c11\uff0c\u4e0d\u8fc7\u5927\u591a\u53ea\u6709\u4e00\u4f4d\u2014\u2014\u9b54\u6cd5\u5973\u795e\u662f\u4f8b\u5916\uff0c\u5979\u7684\u9009\u6c11\u81f3\u5c11\u8d85\u8fc7\u5341\u4f4d\u3002\u201d</p><p>\u201c\u63d0\u5c14\u2026\u2026\u4f60\u7684\u795e\u7947\u6709\u9009\u6c11\u5417\uff1f\u6211\u662f\u8bf4\uff0c\u4f60\u7b97\u4e0d\u7b97\uff1f\u201d</p><p>\u6885\u83f2\u65af\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u201c\u6211\u60f3\u4e0d\u7b97\u5427\u3002\u201d\u5979\u8bf4\u3002</p><p>\u6c14\u6c1b\u6709\u4e9b\u6c89\u95f7\uff0c\u8fd9\u4e2a\u8bdd\u9898\u4fbf\u6ca1\u6709\u7ee7\u7eed\u8fdb\u884c\u4e0b\u53bb\u3002\u743c\u6069\u5f00\u59cb\u7814\u7a76\u4ece\u6b7b\u6389\u7684\u5353\u5c14\u5deb\u5e08\u8eab\u4e0a\u641c\u51fa\u7684\u83f2\u5c14\u677e\u62a4\u7b26\u3002\u4ed6\u76f8\u4fe1\u8fd9\u4e1c\u897f\u4e0d\u81f3\u4e8e\u53ea\u6709\u706d\u706b\u5668\u4e00\u79cd\u529f\u80fd\uff0c\u603b\u8be5\u8fd8\u6709\u4e9b\u5176\u4ed6\u7684\u4f5c\u7528\uff0c\u53ea\u662f\u4e00\u65f6\u7422\u78e8\u4e0d\u51fa\u4ec0\u4e48\u5934\u7eea\u3002\u5353\u5c14\u9b54\u6cd5\u662f\u4e2a\u6df7\u5408\u4ea7\u7269\uff0c\u548c\u4eba\u7c7b\u9b54\u6cd5\u4e0d\u540c\uff0c\u5236\u4f5c\u51fa\u6765\u7684\u9b54\u6cd5\u7269\u54c1\u4e0d\u662f\u90a3\u4e48\u5bb9\u6613\u5206\u6790\u6e05\u695a\u7684\uff0c\u5c24\u5176\u662f\u5728\u6ca1\u6709\u4efb\u4f55\u63d0\u793a\u548c\u6307\u70b9\u7684\u60c5\u51b5\u4e0b\uff0c\u6bd4\u8f83\u9ebb\u70e6\u3002</p><p>\u56db\u5929\u4e4b\u540e\uff0c\u8475\u9732\u62b5\u8fbe\u4e86\u6668\u70bc\u3002</p><p>\u203b\u203b\u203b</p><p>\u8475\u9732\u662f\u4f0a\u8389\u4e1d\u7fe0\u6559\u4f1a\u7684\u9886\u8896\uff0c\u5728\u5982\u4eca\u5973\u795e\u6c89\u7720\u7684\u60c5\u51b5\u4e0b\uff0c\u5979\u4fbf\u662f\u7075\u9b42\u548c\u652f\u67f1\uff0c\u5979\u7684\u5230\u6765\u8ba9\u5168\u57ce\u7684\u5353\u5c14\u4eec\u6b22\u6b23\u9f13\u821e\uff0c\u4e3e\u529e\u4e86\u6b22\u8fce\u5bb4\u4f1a\u3002\u743c\u6069\u4f5c\u4e3a\u5982\u4eca\u6668\u70bc\u57ce\u4e2d\u7684\u9996\u9886\u4eba\u7269\u4e4b\u4e00\uff0c\u81ea\u7136\u4e5f\u5217\u5e2d\u5176\u4e2d\uff0c\u867d\u7136\u4ed6\u5f88\u4e0d\u559c\u6b22\u8fd9\u79cd\u793e\u4ea4\u5de5\u4f5c\uff0c\u4f46\u6ca1\u6709\u529e\u6cd5\u3002</p><p>\u867d\u7136\u5df2\u7ecf\u4ece\u6885\u83f2\u65af\u548c\u963f\u5fd2\u59ae\u53e3\u4e2d\u5f97\u77e5\u4e86\u8475\u9732\u7684\u5f88\u591a\u8d44\u6599\uff0c\u4f46\u771f\u6b63\u89c1\u5230\u672c\u4eba\uff0c\u743c\u6069\u8fd8\u662f\u5403\u4e86\u4e00\u60ca\u3002</p><p>\u8475\u9732\u5f88\u6f02\u4eae\uff0c\u4e94\u5b98\u7cbe\u81f4\uff0c\u6709\u4e00\u5934\u8c61\u7011\u5e03\u4e00\u6837\u5782\u5230\u811a\u8e1d\u7684\u94f6\u53d1\uff0c\u7a7f\u7740\u4e00\u4ef6\u95ea\u70c1\u7740\u53d8\u5e7b\u94f6\u5149\u7684\u888d\u5b50\uff0c\u8d64\u7740\u53cc\u8db3\u3002\u5f53\u7136\uff0c\u4ec5\u4ec5\u8fd9\u4e9b\u8fd8\u6ca1\u4ec0\u4e48\u53ef\u60ca\u8bb6\u7684\uff0c\u743c\u6069\u771f\u6b63\u88ab\u9707\u64bc\u5230\u7684\uff0c\u662f\u5979\u7684\u4e2a\u5934\u3002</p><p>\u7c97\u7565\u76ee\u6d4b\u7684\u8bdd\uff0c\u8475\u9732\u5927\u7ea6\u6709\u516d\u82f1\u5c3a\u9ad8\uff0c\u6298\u5408\u8d77\u6765\u8d85\u8fc7\u4e00\u7c73\u516b\u4e8c\uff0c\u5982\u679c\u653e\u5728\u4eba\u7c7b\u4e2d\uff0c\u8fd9\u4e2a\u8eab\u9ad8\u8fd8\u4e0d\u7b97\u7279\u522b\u79bb\u8c31\uff0c\u4f46\u5bf9\u4e8e\u4e00\u540d\u5353\u5c14\u6765\u8bf4\uff0c\u5374\u5b9e\u5728\u662f\u6709\u4e9b\u532a\u5937\u6240\u601d\u4e86\u3002\u901a\u5e38\u6765\u8bf4\uff0c\u7cbe\u7075\u7684\u4f53\u683c\u6bd4\u4eba\u7c7b\u660e\u663e\u77ee\u5c0f\u7626\u5f31\u4e00\u4e9b\uff0c\u800c\u5353\u5c14\u867d\u7136\u8f83\u4e3a\u4e30\u6ee1\uff0c\u5374\u662f\u7cbe\u7075\u516d\u5927\u4e9a\u79cd\u4e2d\u6700\u77ee\u5c0f\u7684\u4e00\u79cd\u3002\u7ef4\u5eb7\u5c3c\u4e9a\u548c\u743c\u6069\u5728\u4e00\u8d77\u7684\u65f6\u5019\uff0c\u5373\u4fbf\u7a7f\u4e0a\u9ad8\u8ddf\u978b\uff0c\u4e5f\u53ea\u80fd\u5230\u743c\u6069\u7684\u80f8\u53e3\u3002\u8475\u9732\u7684\u8fd9\u79cd\u4f53\u683c\uff0c\u7ad9\u5728\u4e00\u7fa4\u5353\u5c14\u5f53\u4e2d\uff0c\u5927\u6709\u9e64\u7acb\u9e21\u7fa4\u4e4b\u611f\uff0c\u89c6\u89c9\u6548\u679c\u7740\u5b9e\u60ca\u4eba\u3002</p><p>\u201c\u4f60\u6ca1\u544a\u8bc9\u6211\u5979\u662f\u4e2a\u5de8\u4eba\u7f8e\u5973\u3002\u201d\u743c\u6069\u4f4e\u58f0\u5bf9\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u201c\u4e5f\u4e0d\u7b97\u5f88\u9ad8\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u548c\u4f60\u5dee\u4e0d\u591a\u3002\u201d</p><p>\u201c\u53ef\u5979\u662f\u4e2a\u5353\u5c14\u554a\u3002\u201d</p><p>\u201c\u6211\u53ef\u4ee5\u628a\u8fd9\u53e5\u8bdd\u7406\u89e3\u4e3a\u79cd\u65cf\u6b67\u89c6\u5417\uff1f\u201d</p><p>\u8fd9\u79cd\u79c1\u4e0b\u7684\u8c08\u8bdd\u5f88\u5feb\u88ab\u6253\u65ad\uff0c\u8475\u9732\u671d\u8fd9\u8fb9\u8d70\u4e86\u8fc7\u6765\uff0c\u5979\u4f18\u96c5\u5730\u5411\u6885\u83f2\u65af\u5fae\u7b11\u7740\uff0c\u201c\u6211\u542c\u8fc7\u4f60\u7684\u4e8b\u8ff9\uff0c\u6885\u83f2\u65af\u5c0f\u59d0\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6709\u53f2\u4ee5\u6765\u6700\u5e74\u8f7b\u7684\u63d0\u5c14\u5723\u6b66\u58eb\u3002\u201d</p><p>\u5979\u63a5\u7740\u5c06\u76ee\u5149\u8f6c\u5411\u6885\u83f2\u65af\u8eab\u65c1\u7684\u743c\u6069\uff0c\u7a81\u7136\u50cf\u662f\u6014\u4e86\u4e00\u6014\uff0c\u539f\u672c\u5fae\u7b11\u7684\u9762\u5e9e\u4e0a\u9732\u51fa\u4e00\u4e1d\u8bb6\u5f02\u548c\u8b66\u60d5\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u654c\u610f\uff0c\u4f46\u968f\u5373\u63a9\u9970\u8fc7\u53bb\u3002\u201c\u8fd9\u4f4d\u4e00\u5b9a\u662f\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u627f\u8499\u60a8\u6b64\u524d\u7684\u6177\u6168\u63f4\u624b\uff0c\u8ba9\u6211\u4eec\u62e5\u6709\u8fd9\u7acb\u8db3\u4e4b\u5730\u3002\u201d</p><p>\u8475\u9732\u7684\u8bdd\u8bf4\u5f97\u5f88\u5ba2\u6c14\uff0c\u4f46\u743c\u6069\u660e\u663e\u611f\u89c9\u5230\u4e86\u90a3\u9690\u9690\u7ea6\u7ea6\u7684\u758f\u8fdc\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u4e5f\u6ce8\u610f\u5230\u4e86\u5bf9\u65b9\u770b\u5230\u81ea\u5df1\u65f6\u4e0d\u7ecf\u610f\u95f4\u6d41\u9732\u51fa\u7684\u654c\u610f\u3002\u8fd9\u8ba9\u4ed6\u6709\u4e9b\u8be7\u5f02\uff0c\u81ea\u5ea6\u81ea\u5df1\u4f3c\u4e4e\u4e0d\u66fe\u6709\u4ec0\u4e48\u5730\u65b9\u5f97\u7f6a\u8fc7\u8fd9\u4f4d\u9009\u6c11\uff0c\u4f55\u81f3\u4e8e\u6b64\u3002</p><p>\u8fd9\u4e2a\u7591\u60d1\u6ca1\u6709\u5f97\u5230\u89e3\u91ca\uff0c\u4e8b\u540e\u6885\u83f2\u65af\u4e5f\u79c1\u4e0b\u8be2\u95ee\u963f\u5fd2\u59ae\uff0c\u4f46\u5bf9\u65b9\u540c\u6837\u4e5f\u83ab\u540d\u5176\u5999\u3002\u53ea\u77e5\u9053\u8475\u9732\u5728\u5bb4\u4f1a\u540e\u4fbf\u53ec\u96c6\u4f17\u4eba\uff0c\u7279\u522b\u5f3a\u8c03\u8981\u63d0\u9632\u743c\u6069\u3002\u7ecf\u8fc7\u8fd9\u4e9b\u5929\u6765\u7684\u76f8\u5904\u4e0e\u5e76\u80a9\u4f5c\u6218\uff0c\u4f0a\u8389\u4e1d\u7fe0\u7684\u4fe1\u5f92\u548c\u743c\u6069\u5df2\u7ecf\u7b97\u662f\u5173\u7cfb\u9887\u4e3a\u878d\u6d3d\uff0c\u5f00\u59cb\u65f6\u7684\u6212\u5907\u4e5f\u65e9\u5c31\u6de1\u53bb\uff0c\u5982\u4eca\u542c\u8475\u9732\u7a81\u7136\u8fd9\u4e48\u8bf4\uff0c\u4e0d\u7981\u90fd\u6709\u4e9b\u8be7\u5f02\uff0c\u4f46\u4e5f\u4e0d\u6562\u63d0\u51fa\u5f02\u8bae\uff0c\u53ea\u80fd\u552f\u552f\u542c\u547d\u3002</p><p>\u201c\u539f\u56e0\u5462\uff1f\u201d\u6885\u83f2\u65af\u95ee\u3002</p><p>\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\u662f\u838e\u5c14\u7684\u4fe1\u5f92\u3002\u201d</p><p>\u201c\u5c31\u56e0\u4e3a\u8fd9\u4e2a\uff1f\u201d</p><p>\u201c\u8475\u9732\u53ea\u8bf4\u4e86\u8fd9\u70b9\u3002\u201d</p><p>\u743c\u6069\u8038\u8038\u80a9\uff0c\u4ed6\u6765\u81ea\u9634\u9b42\u57ce\uff0c\u800c\u9634\u9b42\u57ce\u5168\u90fd\u662f\u838e\u5c14\u7684\u4fe1\u5f92\uff0c\u8fd9\u6839\u672c\u5c31\u4e0d\u662f\u79d8\u5bc6\uff0c\u6668\u70bc\u57ce\u4e2d\u5730\u4f4d\u7a0d\u9ad8\u7684\u4eba\u5168\u90fd\u6e05\u695a\uff0c\u800c\u4e00\u76f4\u4ee5\u6765\u5979\u4eec\u4e5f\u4e0d\u66fe\u5728\u610f\u8fc7\u8fd9\u70b9\u3002\u6ca1\u60f3\u5230\u8475\u9732\u5c45\u7136\u4f1a\u56e0\u4e3a\u8fd9\u4ef6\u4e8b\u800c\u5927\u60ca\u5c0f\u602a\uff0c\u5b9e\u5728\u8ba9\u4eba\u96be\u4ee5\u7406\u89e3\u3002</p><p>\u201c\u8475\u9732\u4e0d\u4ec5\u4ec5\u662f\u821e\u8e48\u5973\u58eb\u7684\u9009\u6c11\uff0c\u5979\u540c\u65f6\u8fd8\u662f\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\uff0c\u201d\u6885\u83f2\u65af\u60f3\u4e86\u60f3\uff0c\u8f7b\u58f0\u8bf4\uff0c\u201c\u800c\u9b54\u6cd5\u5973\u795e\u548c\u591c\u5973\u58eb\u662f\u6c38\u8fdc\u7684\u6b7b\u654c\u3002\u201d</p><p>\u201c\u8fd9\u548c\u6211\u6709\u4ec0\u4e48\u5173\u7cfb\u3002\u201d\u743c\u6069\u644a\u5f00\u624b\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u538b\u6839\u5c31\u6ca1\u6709\u4e00\u4f4d\u201c\u4fe1\u5f92\u201d\u7684\u81ea\u89c9\u3002</p><p>\u201c\u5173\u4e8e\u8fd9\u4e2a\uff0c\u6211\u4eec\u5176\u5b9e\u4e5f\u5f88\u4e0d\u660e\u767d\uff0c\u201d\u963f\u5fd2\u59ae\u5728\u65c1\u8fb9\u8bf4\uff0c\u201c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u4eec\u65e9\u5c31\u5df2\u7ecf\u5c06\u5170\u5c3c\u65af\u7279\u5148\u751f\u7684\u8eab\u4efd\u544a\u77e5\u4e86\u8475\u9732\uff0c\u5979\u4e5f\u5e76\u6ca1\u6709\u5f88\u5728\u610f\u2026\u2026\u7ed9\u6211\u7684\u611f\u89c9\uff0c\u4f3c\u4e4e\u662f\u5bb4\u4f1a\u4e0a\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6539\u53d8\u4e86\u5979\u7684\u770b\u6cd5\u3002\u201d</p><p>\u743c\u6069\u770b\u7740\u6885\u83f2\u65af\uff0c\u7136\u540e\u644a\u5f00\u624b\u3002\u201c\u4e8b\u5b9e\u4e0a\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u5bb4\u4f1a\u4e2d\u53d1\u751f\u7684\u552f\u4e00\u4e8b\u60c5\uff0c\u5c31\u662f\u5979\u770b\u89c1\u6211\u4e86\u3002\u201d</p><p>\u5173\u4e8e\u8fd9\u4e2a\u95ee\u9898\u5c31\u6b64\u6401\u4e0b\uff0c\u743c\u6069\u662f\u4e2a\u61d2\u6563\u7684\u4eba\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u4ed6\u5e76\u4e0d\u5f88\u5728\u610f\u522b\u4eba\u5bf9\u81ea\u5df1\u7684\u6001\u5ea6\u3002\u6216\u8bb8\u8475\u9732\u56e0\u4e3a\u67d0\u79cd\u539f\u56e0\u800c\u6392\u65a5\u4ed6\uff0c\u4f46\u90a3\u53c8\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\uff0c\u8fd9\u662f\u4e2a\u4eba\u81ea\u7531\u3002</p></article>"}}}]);
