(window.webpackJsonp=window.webpackJsonp||[]).push([[303],{325:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e5d\u8282 \u6df1\u6e0a",content:"<article><h2>\u7b2c\u5341\u4e5d\u8282 \u6df1\u6e0a</h2><p>\u5f53\u743c\u6069\u518d\u6b21\u9192\u6765\u7684\u65f6\u5019\uff0c\u4ed6\u53d1\u73b0\u81ea\u5df1\u6b63\u8eba\u5728\u4e00\u7247\u8352\u5730\u4e0a\uff0c\u592a\u9633\u50cf\u5e9e\u5927\u7684\u706b\u7403\u4e00\u6837\u60ac\u6302\u5728\u5934\u9876\uff0c\u53d1\u51fa\u4ee4\u4eba\u7a92\u606f\u7684\u9ad8\u6e29\u548c\u523a\u76ee\u7684\u5149\u8292\uff0c\u4f46\u5374\u4f9d\u65e7\u65e0\u6cd5\u9a71\u6563\u904d\u5e03\u6574\u4e2a\u5929\u7a7a\u7684\u9634\u973e\u3002\u952f\u9f7f\u72b6\u7684\u9ed1\u8272\u5c71\u8109\u5728\u8fdc\u5904\u7ef5\u5ef6\u8d77\u4f0f\uff0c\u4e0a\u9762\u4f3c\u4e4e\u6709\u5927\u7fa4\u7684\u9e1f\u7c7b\u5728\u98de\u7fd4\u3002\u72c2\u98ce\u547c\u5578\u800c\u8fc7\uff0c\u5c06\u78e8\u76d8\u5927\u5c0f\u7684\u77f3\u5934\u5439\u5f97\u5495\u565c\u6eda\u52a8\u4f5c\u54cd\uff0c\u4e00\u9635\u9635\u7684\u98ce\u6c99\u5f25\u6f2b\u3002</p><p>\u6765\u5230\u5730\u8868\u4e86\uff1f</p><p>\u8fd9\u4e2a\u5ff5\u5934\u8ba9\u743c\u6069\u7565\u7565\u5b9a\u4e0b\u5fc3\u6765\uff0c\u867d\u7136\u4e0d\u77e5\u9053\u600e\u4e48\u4f1a\u51fa\u73b0\u8fd9\u79cd\u7ed3\u679c\uff0c\u4f46\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u5730\u8868\u603b\u662f\u6bd4\u5e7d\u6697\u5730\u57df\u5b89\u5168\u767e\u500d\u7684\u5730\u65b9\uff0c\u6ca1\u6709\u90a3\u4e48\u591a\u5c42\u51fa\u4e0d\u7a77\u7684\u51f6\u6076\u602a\u7269\u548c\u654c\u4eba\uff0c\u800c\u4e14\u81f3\u5c11\u53ef\u4ee5\u4f7f\u7528\u4f20\u9001\u6cd5\u672f\u3002\u4ed6\u8bd5\u7740\u52a8\u5f39\u4e86\u4e00\u4e0b\uff0c\u611f\u89c9\u81ea\u5df1\u7684\u9aa8\u67b6\u4eff\u4f5b\u90fd\u6563\u4e86\u4f3c\u7684\uff0c\u5168\u8eab\u9178\u75db\uff0c\u8c03\u606f\u534a\u5929\u53c8\u704c\u4e86\u81ea\u5df1\u4e00\u74f6\u6cbb\u7597\u836f\u6c34\uff0c\u603b\u7b97\u52c9\u5f3a\u80fd\u6447\u6447\u6643\u6643\u7ad9\u8d77\u6765\uff0c\u68c0\u67e5\u4e00\u904d\u81ea\u8eab\uff0c\u53d1\u73b0\u6ca1\u7f3a\u80f3\u818a\u6ca1\u7f3a\u817f\uff0c\u8fde\u76ae\u90fd\u6ca1\u522e\u7834\u534a\u70b9\uff0c\u6240\u6709\u7684\u88c5\u5907\u7269\u54c1\u57fa\u672c\u90fd\u5728\uff0c\u9664\u4e86\u90a3\u526f\u8428\u74e6\u68cb\u4e0d\u77e5\u6240\u8e2a\uff0c\u53ea\u5f97\u7b97\u4e86\uff0c\u5f00\u59cb\u56db\u5904\u5f20\u671b\uff0c\u5bfb\u627e\u540c\u4f34\u3002</p><p>\u5728\u8ddd\u79bb\u81ea\u5df1\u5927\u7ea6\u4e24\u767e\u82f1\u5c3a\u7684\u4e00\u5757\u5ca9\u77f3\u4e4b\u540e\uff0c\u743c\u6069\u53d1\u73b0\u4e86\u6885\u83f2\u65af\uff0c\u8fd9\u8ba9\u4ed6\u5927\u5927\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u522b\u4eba\u6b7b\u5149\u4e86\u6216\u8005\u5931\u8e2a\u4e86\u65e0\u6240\u8c13\uff0c\u53ea\u8981\u5979\u6ca1\u4e8b\u5c31\u884c\u3002\u5723\u6b66\u58eb\u8fd8\u5728\u6655\u8ff7\u4e4b\u4e2d\uff0c\u4f46\u547c\u5438\u5e73\u7f13\uff0c\u5e94\u8be5\u6ca1\u6709\u4ec0\u4e48\u5927\u788d\uff0c\u8eab\u4e0a\u4e5f\u6ca1\u4ec0\u4e48\u4f24\u75d5\uff0c\u5979\u7684\u94f6\u5251\u4e22\u5728\u4e0d\u8fdc\u5904\u3002\u743c\u6069\u5c06\u5979\u62b1\u8d77\u6765\uff0c\u501a\u5728\u6000\u4e2d\uff0c\u5c06\u8863\u670d\u4e0a\u6cbe\u5230\u7684\u7070\u5c18\u62c2\u53bb\uff0c\u8f7b\u8f7b\u62cd\u5979\u7684\u8138\uff0c\u201c\u9192\u9192\uff0c\u827e\u5f25\u8587\uff0c\u201d\u4ed6\u8f7b\u58f0\u547c\u5524\uff0c\u201c\u9192\u9192\u3002\u201d</p><p>\u6885\u83f2\u65af\u60a0\u60a0\u9192\u8f6c\u8fc7\u6765\uff0c\u62ac\u773c\u770b\u89c1\u743c\u6069\uff0c\u5fae\u5fae\u9732\u51fa\u7b11\u610f\uff0c\u201c\u4f60\u6ca1\u4e8b\u5427\u3002\u201d\u5979\u95ee\uff0c\u58f0\u97f3\u5f88\u865a\u5f31\u3002</p><p>\u201c\u6211\u6ca1\u4e8b\uff0c\u4f60\u600e\u4e48\u4e86\uff0c\u201d\u743c\u6069\u6709\u4e9b\u7740\u6025\uff0c\u4ed6\u521a\u53d1\u73b0\u6885\u83f2\u65af\u7684\u8138\u8272\u82cd\u767d\u5f97\u5413\u4eba\uff0c\u5634\u5507\u90fd\u5b8c\u5168\u5931\u53bb\u4e86\u8840\u8272\uff0c\u201c\u54ea\u91cc\u53d7\u4f24\u4e86\uff1f\u201d</p><p>\u201c\u6ca1\u53d7\u4f24\uff0c\u201d\u6885\u83f2\u65af\u8f7b\u58f0\u8bf4\uff0c\u201c\u53ea\u662f\u96be\u53d7\uff0c\u5934\u6655\u5f97\u5389\u5bb3\uff0c\u5168\u8eab\u8f6f\u7ef5\u7ef5\u7684\uff0c\u63d0\u4e0d\u8d77\u534a\u70b9\u529b\u6c14\u3002\u201d</p><p>\u743c\u6069\u81ea\u4ece\u8ba4\u8bc6\u5979\u4ee5\u6765\uff0c\u8fd8\u4ece\u4e0d\u66fe\u89c1\u8fc7\u5979\u8fd9\u6837\u865a\u5f31\u7684\u6a21\u6837\uff0c\u4e00\u65f6\u95f4\u51e0\u4e4e\u90fd\u6709\u4e9b\u624b\u8db3\u65e0\u63aa\uff0c\u629a\u6478\u5979\u7684\u989d\u5934\uff0c\u6e29\u5ea6\u6b63\u5e38\uff0c\u4e1d\u6beb\u6ca1\u6709\u53d1\u70e7\u7684\u8ff9\u8c61\u3002\u4ed6\u5728\u6668\u70bc\u7684\u65f6\u5019\u6ca1\u4e8b\u91c7\u96c6\u690d\u7269\uff0c\u5236\u4f5c\u4e86\u51e0\u74f6\u6709\u6cbb\u7597\u6062\u590d\u4f5c\u7528\u7684\u836f\u6c34\uff0c\u6070\u597d\u90fd\u5e26\u5728\u8eab\u4e0a\uff0c\u8d76\u7d27\u53d6\u51fa\u6765\u7ed9\u6885\u83f2\u65af\u5582\u4e86\u4e00\u70b9\u3002\u201c\u611f\u89c9\u597d\u4e9b\u6ca1\u6709\u3002\u201d\u4ed6\u95ee\u3002</p><p>\u6885\u83f2\u65af\u8f7b\u8f7b\u70b9\u4e86\u70b9\u5934\uff0c\u4f46\u4f9d\u65e7\u975e\u5e38\u865a\u5f31\u7684\u6837\u5b50\uff0c\u201c\u8fd9\u662f\u54ea\u91cc\uff1f\u201d\u5979\u95ee\uff0c\u201c\u6211\u770b\u5230\u592a\u9633\u4e86\u3002\u201d</p><p>\u201c\u5e94\u8be5\u662f\u56de\u5230\u5730\u8868\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u5c06\u5979\u62b1\u4e86\u8d77\u6765\uff0c\u201c\u6ca1\u4e8b\uff0c\u827e\u5f25\u8587\uff0c\u4f60\u5927\u6982\u662f\u7d2f\u4e86\u3002\u6211\u4eec\u73b0\u5728\u5c31\u53bb\u627e\u4e2a\u795e\u6bbf\uff0c\u8bf7\u7267\u5e08\u7ed9\u4f60\u6cbb\u7597\u3002\u201d</p><p>\u8bf4\u662f\u8fd9\u4e48\u8bf4\uff0c\u53ea\u662f\u4e5f\u4e0d\u77e5\u9053\u8eab\u5728\u4f55\u5904\uff0c\u5374\u8981\u5f80\u54ea\u91cc\u8d70\u3002\u743c\u6069\u628a\u5730\u4e0a\u7684\u94f6\u5251\u62e3\u8d77\u6765\uff0c\u6302\u5728\u81ea\u5df1\u8170\u95f4\uff0c\u5de6\u53f3\u770b\u770b\uff0c\u51b3\u5b9a\u5148\u4e0a\u65c1\u8fb9\u7684\u4e00\u5ea7\u5c71\u5cf0\u9876\u4e0a\uff0c\u770b\u770b\u56db\u5468\u60c5\u51b5\u518d\u8bf4\uff0c\u5018\u82e5\u80fd\u53d1\u73b0\u57ce\u9547\u90a3\u66f4\u662f\u4e0a\u4e0a\u5927\u5409\u3002\u8fd9\u91cc\u98ce\u6c99\u592a\u5927\uff0c\u5f97\u627e\u4f4f\u5904\u5148\u5b89\u987f\u4e0b\u6765\uff0c\u867d\u7136\u770b\u592a\u9633\u8fd8\u5728\u6b63\u4e2d\uff0c\u8ddd\u79bb\u508d\u665a\u8fd8\u65e9\uff0c\u4f46\u8c01\u77e5\u9053\u8fd9\u91cc\u4f1a\u4e0d\u4f1a\u662f\u6e3a\u65e0\u4eba\u70df\u7684\u5927\u6208\u58c1\u5462\uff0c\u770b\u8fd9\u5730\u5f62\u8fd8\u771f\u6709\u51e0\u5206\u50cf\u3002</p><p>\u53ea\u662f\uff0c\u73b0\u5728\u5e94\u8be5\u8fd8\u6ca1\u5230\u76db\u590f\u5427\uff0c\u7eff\u8349\u8282\u521a\u8fc7\uff0c\u4e3a\u4ec0\u4e48\u592a\u9633\u5c31\u5df2\u7ecf\u8fd9\u4e48\u731b\u70c8\u6bd2\u8fa3\u4e86\u5462\u3002</p><p>\u5e7d\u6697\u5730\u57df\u4e4b\u4e2d\uff0c\u56e0\u4e3a\u5730\u8109\u8f90\u5c04\u7684\u5f71\u54cd\uff0c\u6ca1\u4eba\u6562\u4e71\u7528\u4f20\u9001\uff0c\u743c\u6069\u4e5f\u5df2\u7ecf\u5f88\u4e45\u4e0d\u51c6\u5907\u8fd9\u4e2a\u6cd5\u672f\u4e86\u3002\u5982\u4eca\u8981\u4e0a\u5c71\u9876\uff0c\u6050\u6015\u8fd8\u53ea\u80fd\u6162\u6162\u722c\u4e0a\u53bb\u3002\u8fd9\u5c71\u5cf0\u5374\u9661\u5ced\u5f97\u5f88\uff0c\u81ea\u5df1\u4e00\u4e2a\u4eba\u5012\u7f62\u4e86\uff0c\u6000\u91cc\u8fd8\u62b1\u7740\u4eba\u90a3\u662f\u80af\u5b9a\u4e0d\u6210\u3002\u6e38\u76ee\u56db\u987e\uff0c\u53d1\u73b0\u4e00\u5904\u7a81\u51fa\u7684\u77f3\u58c1\uff0c\u80fd\u591f\u6321\u4f4f\u72c2\u98ce\uff0c\u4fbf\u62b1\u7740\u6885\u83f2\u65af\u8d70\u8fc7\u53bb\uff0c\u6b63\u8981\u627e\u4e00\u5904\u5e73\u5766\u5730\u65b9\u8ba9\u5979\u4f11\u606f\uff0c\u4e00\u773c\u5374\u770b\u89c1\u5730\u4e0a\u5df2\u7ecf\u8eba\u4e86\u4e00\u4e2a\u4eba\u2014\u2014\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u4e2a\u6708\u7cbe\u7075\u3002</p><p>\u201c\u963f\u5fd2\u59ae\uff1f\u201d</p><p>\u867d\u7136\u4e0d\u7b97\u5f88\u4eb2\u8fd1\uff0c\u4f46\u5728\u964c\u751f\u73af\u5883\u91cc\uff0c\u591a\u4e00\u4e2a\u719f\u4eba\u603b\u662f\u597d\uff0c\u4f55\u51b5\u8fd8\u662f\u6885\u83f2\u65af\u7684\u670b\u53cb\u3002\u743c\u6069\u53c8\u662f\u4e00\u756a\u6298\u817e\uff0c\u628a\u963f\u5fd2\u59ae\u5f04\u9192\uff0c\u9012\u4e86\u4e00\u74f6\u836f\u6c34\u8fc7\u53bb\uff0c\u4ee4\u4eba\u6b23\u6170\u7684\u662f\uff0c\u5979\u6ca1\u6709\u51fa\u73b0\u7c7b\u4f3c\u6885\u83f2\u65af\u7684\u865a\u5f31\u75c7\u72b6\u3002\u5979\u7684\u77ed\u5f13\u3001\u7bad\u56ca\u4e5f\u90fd\u5728\u624b\u8fb9\uff0c\u6ca1\u6709\u4e22\u5931\u3002</p><p>\u201c\u827e\u5f25\u8587\u600e\u4e48\u4e86\uff1f\u201d\u963f\u5fd2\u59ae\u95ee\uff0c\u5979\u4e5f\u53d1\u73b0\u6885\u83f2\u65af\u660e\u663e\u72b6\u6001\u4e0d\u5bf9\u3002</p><p>\u201c\u4e0d\u77e5\u9053\uff0c\u201d\u743c\u6069\u76b1\u7709\uff0c\u201c\u53ef\u80fd\u662f\u751f\u75c5\u4e86\u2026\u2026\u5e2e\u6211\u7167\u987e\u5979\u4e00\u4f1a\uff0c\u6211\u4e0a\u53bb\u770b\u770b\u8fd9\u5230\u5e95\u662f\u4ec0\u4e48\u5730\u65b9\u3002\u201d</p><p>\u201c\u4e0d\u7528\u4e86\u3002\u201d\u4e00\u4e2a\u58f0\u97f3\u4ece\u5934\u9876\u4e0a\u4f20\u6765\u3002</p><p>\u743c\u6069\u5413\u4e86\u4e00\u8df3\uff0c\u62ac\u5934\u53bb\u770b\uff0c\u53d1\u73b0\u8475\u9732\u6b63\u7ad9\u5728\u5c71\u5cf0\u7684\u534a\u8170\u4e0a\uff0c\u94f6\u8272\u7684\u957f\u53d1\u968f\u98ce\u98d8\u821e\uff0c\u7136\u540e\u8f7b\u6613\u5730\u8df3\u4e86\u4e0b\u6765\uff0c\u8d70\u5230\u8fd1\u524d\u3002\u201c\u8fd9\u91cc\u662f\u6df1\u6e0a\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u8475\u9732\u4f5c\u4e3a\u9b54\u6cd5\u5973\u795e\u548c\u5e7d\u6697\u5c11\u5973\u7684\u53cc\u6599\u9009\u6c11\uff0c\u8d1f\u76db\u540d\u5782\u6570\u767e\u5e74\uff0c\u662f\u51e1\u4eba\u4e2d\u7684\u9876\u5c16\u7cbe\u82f1\u4e4b\u4e00\u3002\u4e0d\u4ec5\u4ec5\u662f\u529b\u91cf\u5f3a\u5927\uff0c\u89c1\u8bc6\u540c\u6837\u5e7f\u535a\uff0c\u5979\u65e2\u7136\u8bf4\u8fd9\u91cc\u662f\u6df1\u6e0a\uff0c\u90a3\u4e48\u901a\u5e38\u662f\u4e0d\u4f1a\u6709\u4eba\u6000\u7591\u7684\u2014\u2014\u7136\u800c\u743c\u6069\u504f\u504f\u662f\u4f8b\u5916\u3002</p><p>\u201c\u4f60\u600e\u4e48\u5224\u65ad\u8fd9\u91cc\u662f\u6df1\u6e0a\uff1f\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8fd9\u91cc\u548c\u7269\u8d28\u754c\u4e5f\u6ca1\u4ec0\u4e48\u533a\u522b\u3002\u201d</p><p>\u201c\u591c\u5973\u58eb\u7684\u9009\u6c11\u96be\u9053\u8fde\u8fd9\u70b9\u89c1\u8bc6\u90fd\u6ca1\u6709\u5417\uff1f\u201d\u8475\u9732\u51b7\u51b7\u8bf4\u3002</p><p>\u201c\u6211\u4e0d\u662f\u4ec0\u4e48\u9009\u6c11\u2026\u2026\u201d</p><p>\u8475\u9732\u6ca1\u6709\u542c\u743c\u6069\u7684\u5206\u8fa9\uff0c\u5979\u4f38\u624b\u5c06\u963f\u5fd2\u59ae\u7684\u77ed\u5f13\u8981\u8fc7\u6765\uff0c\u4ece\u7bad\u56ca\u4e2d\u53d6\u51fa\u4e00\u652f\u94f6\u8272\u957f\u5251\u642d\u4e0a\uff0c\u6ee1\u6ee1\u62c9\u5f00\uff0c\u7167\u7740\u7a7a\u4e2d\u4e00\u7bad\u5c04\u53bb\u3002\u98d5\u5730\u4e00\u58f0\uff0c\u4e00\u5177\u5e9e\u5927\u7684\u5c38\u4f53\u6389\u843d\u4e0b\u6765\u3002</p><p>\u5b83\u7c97\u770b\u50cf\u4e2a\u9e1f\u7c7b\uff0c\u518d\u4ed4\u7ec6\u770b\u5374\u662f\u79c3\u9e70\u548c\u4eba\u7c7b\u7684\u6df7\u5408\u4f53\uff0c\u6709\u53d1\u8fbe\u7684\u808c\u8089\u548c\u5de8\u5927\u7684\u7070\u8272\u7fc5\u8180\uff0c\u8fd8\u6709\u957f\u957f\u7684\u8116\u9888\uff0c\u4ee5\u53ca\u4e00\u9897\u79c3\u9e70\u5934\u9885\u3002\u201c\u5f17\u6d1b\u9b54\u3002\u201d\u743c\u6069\u4f4e\u58f0\u8bf4\uff0c\u4ed6\u8ba4\u8bc6\u8fd9\u79cd\u6076\u9b54\uff0c\u74dc\u7406\u5fb7\u65af\u7b2c\u56db\u5bb6\u65cf\u7684\u57ce\u5899\u4e0a\u5c31\u6709\u5f88\u591a\u5b83\u7684\u96d5\u5851\u3002</p><p>\u5f17\u6d1b\u9b54\u662f\u4e00\u79cd\u4f4e\u9636\u6076\u9b54\uff0c\u5728\u6df1\u6e0a\u4e2d\u5f88\u5e38\u89c1\u2014\u2014\u4f46\u5b83\u518d\u5e38\u89c1\u4e5f\u4e0d\u53ef\u80fd\u5728\u7269\u8d28\u754c\u7684\u5929\u7a7a\u4e2d\u98de\u6765\u98de\u53bb\uff0c\u7136\u540e\u88ab\u8475\u9732\u4e00\u7bad\u5c04\u4e0b\u6765\u3002</p><p>\u552f\u4e00\u7684\u89e3\u91ca\uff0c\u5c31\u662f\u8fd9\u91cc\u6839\u672c\u4e0d\u662f\u7269\u8d28\u754c\uff0c\u800c\u662f\u6df1\u6e0a\u3002</p><p>\u201c\u6211\u4eec\u600e\u4e48\u4f1a\u5230\u6df1\u6e0a\uff1f\u201d\u6885\u83f2\u65af\u95ee\u3002</p><p>\u76f8\u6bd4\u8d77\u5bf9\u743c\u6069\u7684\u654c\u610f\uff0c\u8475\u9732\u5bf9\u6885\u83f2\u65af\u4fbf\u5ba2\u6c14\u5f97\u591a\uff0c\u201c\u6211\u4eec\u5e72\u6270\u4e86\u7f57\u4e1d\u7684\u5f3a\u5236\u4f20\u9001\uff0c\u201d\u5979\u89e3\u91ca\u8bf4\uff0c\u201c\u6cd5\u672f\u9519\u4e71\u4e4b\u4e0b\uff0c\u4fbf\u5230\u4e86\u8fd9\u91cc\u3002\u201d</p><p>\u5728\u573a\u8bf8\u4eba\u90fd\u4e0d\u662f\u6cdb\u6cdb\u4e4b\u8f88\uff0c\u6b64\u524d\u7684\u4e8b\u60c5\u7565\u4e00\u601d\u7d22\uff0c\u4e5f\u4fbf\u5927\u4f53\u660e\u767d\u662f\u600e\u4e48\u56de\u4e8b\u3002\u7f57\u4e1d\u8bbe\u4e86\u4e00\u4e2a\u5c40\uff0c\u8ba9\u4f0a\u8389\u96c5\u6df7\u8fdb\u4e86\u6668\u70bc\u57ce\u91cc\uff0c\u56e0\u4e3a\u5979\u662f\u4e2a\u5c0f\u5b69\u5b50\uff0c\u5927\u5bb6\u4e5f\u90fd\u4e0d\u653e\u5728\u5fc3\u4e0a\uff0c\u672c\u80fd\u5730\u4f1a\u653e\u677e\u8b66\u60d5\u3002\u540c\u65f6\u7f57\u4e1d\u5728\u90a3\u679a\u83f2\u5c14\u677e\u62a4\u7b26\u4e0a\u4e5f\u505a\u4e86\u624b\u811a\uff0c\u5979\u6599\u5230\u743c\u6069\u80af\u5b9a\u4e0d\u4f1a\u653e\u8fc7\u8fd9\u79cd\u73cd\u7a00\u7684\u9b54\u6cd5\u7269\u54c1\uff0c\u5fc5\u5b9a\u8981\u5e26\u56de\u53bb\u4ed4\u7ec6\u7814\u7a76\u3002\u5bfb\u627e\u5230\u6070\u5f53\u7684\u65f6\u673a\uff0c\u7f57\u4e1d\u5723\u8005\u964d\u4e34\uff0c\u9644\u4f53\u5728\u4f0a\u8389\u96c5\u8eab\u4e0a\uff0c\u501f\u52a9\u62a4\u7b26\u53d1\u52a8\u5f3a\u5236\u4f20\u9001\uff0c\u4fbf\u53ef\u4ee5\u628a\u4f17\u4eba\u90fd\u9001\u8fdb\u5979\u7684\u795e\u57df\u4e4b\u4e2d\u3002</p><p>\u8fd9\u662f\u4e00\u4e2a\u5f88\u5b8c\u7f8e\u7684\u9677\u9631\uff0c\u4f46\u8fd9\u4e2a\u4e16\u754c\u4e4b\u6240\u4ee5\u6709\u8da3\uff0c\u5c31\u5728\u4e8e\u5b83\u6c38\u8fdc\u90fd\u6709\u610f\u60f3\u4e0d\u5230\u7684\u53d8\u6570\uff0c\u8fde\u795e\u660e\u4e5f\u4e0d\u53ef\u80fd\u5b8c\u5168\u638c\u63e1\u3002\u7f57\u4e1d\u6210\u529f\u53d1\u52a8\u4e86\u5f3a\u5236\u4f20\u9001\uff0c\u4f46\u5374\u88ab\u4e09\u79cd\u53e6\u5916\u7684\u529b\u91cf\u7a81\u7136\u6740\u51fa\u6765\u6405\u5c40\u2014\u2014\u9996\u5148\u662f\u743c\u6069\u7684\u8428\u74e6\u68cb\u9b54\u50cf\u3002</p><p>\u201c\u6211\u5c31\u77e5\u9053\u90a3\u4e2a\u6076\u9b54\u662f\u4e2a\u65e0\u826f\u8d29\u5b50\uff0c\u201d\u743c\u6069\u62b1\u6028\uff0c\u201c\u679c\u7136\u8fd9\u526f\u68cb\u4e0a\u6709\u95ee\u9898\u3002\u201d</p><p>\u201c\u4f46\u5b83\u6551\u4e86\u6211\u4eec\u7684\u547d\u3002\u201d\u6885\u83f2\u65af\u8f7b\u58f0\u8bf4\u3002</p><p>\u201c\u4f60\u90a3\u526f\u8428\u74e6\u68cb\u5e2e\u6211\u4eec\u4e89\u53d6\u5230\u4e86\u5598\u606f\u4e4b\u673a\uff0c\u201d\u8475\u9732\u8bf4\uff0c\u201c\u7136\u540e\u6211\u7684\u94f6\u706b\u548c\u4f60\u7684\u5f71\u706b\u78b0\u649e\uff0c\u8fd9\u4e24\u79cd\u6027\u8d28\u5b8c\u5168\u9006\u53cd\u7684\u795e\u529b\u76f8\u51b2\u7a81\uff0c\u5f88\u5bb9\u6613\u4ea7\u751f\u96be\u4ee5\u63a7\u5236\u7684\u9b54\u6cd5\u7206\u70b8\uff0c\u6700\u7ec8\u7834\u574f\u4e86\u7f57\u4e1d\u7684\u5f3a\u5236\u4f20\u9001\u3002\u201d</p><p>\u201c\u90a3\u4e3a\u4ec0\u4e48\u6211\u4eec\u4f1a\u6070\u597d\u6389\u5230\u65e0\u5c3d\u6df1\u6e0a\u6765\uff1f\u201d</p><p>\u201c\u6df1\u6e0a\u662f\u6240\u6709\u6df7\u4e71\u7684\u96c6\u5408\uff0c\u662f\u4e00\u5207\u5076\u7136\u548c\u610f\u5916\u7684\u53d1\u751f\u4e0e\u7ec8\u7ed3\u4e4b\u6240\uff0c\u8de8\u4f4d\u9762\u4f20\u9001\u5982\u679c\u906d\u9047\u610f\u5916\u7684\u8bdd\uff0c\u5f80\u5f80\u90fd\u4f1a\u88ab\u9001\u5230\u6df1\u6e0a\u6765\u2014\u2014\u6211\u4eec\u5f88\u5e78\u8fd0\u3002\u201d</p><p>\u201c\u90fd\u5230\u4e86\u6df1\u6e0a\u8fd8\u6709\u4ec0\u4e48\u53ef\u5e78\u8fd0\u7684\u3002\u201d</p><p>\u201c\u603b\u6bd4\u7f57\u4e1d\u7684\u8718\u86db\u9b54\u7f51\u597d\u5f97\u591a\u5427\u3002\u201d</p><p>\u8fd9\u5012\u662f\u4e8b\u5b9e\uff0c\u6240\u4ee5\u743c\u6069\u4e5f\u6ca1\u4ec0\u4e48\u8bdd\u597d\u8bf4\uff0c\u95ee\u9898\u662f\u8fd9\u6837\u4e00\u6765\uff0c\u5374\u5230\u54ea\u91cc\u627e\u7267\u5e08\u7ed9\u6885\u83f2\u65af\u6cbb\u75c5\u3002\u8475\u9732\u867d\u7136\u662f\u7267\u5e08\uff0c\u4f46\u5979\u7684\u795e\u7947\u8fd8\u5728\u6c89\u7720\u4e2d\u5462\u2026\u2026\u5bf9\u4e86\uff0c\u94f6\u706b\u4e5f\u662f\u6709\u6cbb\u7597\u4f5c\u7528\u7684\u3002</p><p>\u201c\u6885\u83f2\u65af\u5c0f\u59d0\u5176\u5b9e\u6ca1\u751f\u75c5\uff0c\u201d\u8475\u9732\u6de1\u6de1\u8bf4\uff0c\u201c\u5979\u53ea\u662f\u548c\u8fd9\u4e2a\u4f4d\u9762\u7684\u89c4\u5219\u62b5\u89e6\u5f97\u592a\u5389\u5bb3\uff0c\u53d7\u5230\u538b\u5236\u7f62\u4e86\uff0c\u5bf9\u6b64\u6211\u4e5f\u7231\u83ab\u80fd\u52a9\u3002\u201d</p><p>\u201c\u548c\u8fd9\u4e2a\u4f4d\u9762\u7684\u89c4\u5219\u62b5\u89e6\uff1f\u201d\u743c\u6069\u6709\u4e9b\u4e0d\u89e3\u3002</p><p>\u8475\u9732\u5fae\u5fae\u76b1\u8d77\u7709\u5934\uff0c\u663e\u7136\u5bf9\u743c\u6069\u5982\u6b64\u89c1\u8bc6\u6d45\u8584\u800c\u611f\u5230\u532a\u5937\u6240\u601d\u3002\u6885\u83f2\u65af\u7b11\u4e86\u4e00\u7b11\uff0c\u8f7b\u58f0\u89e3\u91ca\uff0c\u201c\u5916\u5c42\u4f4d\u9762\u548c\u7269\u8d28\u754c\u4e0d\u540c\uff0c\u5b83\u4eec\u5b9e\u9645\u4e0a\u662f\u6982\u5ff5\u548c\u610f\u5fd7\u5316\u7684\u5b58\u5728\u3002\u4e0d\u540c\u7684\u5916\u5c42\u4f4d\u9762\uff0c\u81ea\u6709\u5404\u81ea\u76f8\u5e94\u7684\u89c4\u5219\uff0c\u662f\u4e3a\u4f4d\u9762\u7684\u81f3\u9ad8\u6cd5\u5219\uff0c\u987a\u5e94\u8005\u5f3a\uff0c\u9006\u53cd\u8005\u5f31\uff0c\u5f97\u4e4b\u8005\u751f\uff0c\u5931\u4e4b\u8005\u6b7b\u3002\u65e0\u5c3d\u6df1\u6e0a\u7684\u6cd5\u5219\uff0c\u5c31\u662f\u6df7\u4e71\u548c\u90aa\u6076\uff0c\u800c\u6211\u662f\u5723\u6b66\u58eb\uff0c\u5b8c\u5168\u4e0d\u80fd\u89c1\u5bb9\uff0c\u6240\u4ee5\u6211\u786e\u5b9e\u6ca1\u751f\u75c5\u2014\u2014\u6211\u662f\u5728\u88ab\u6574\u4e2a\u4f4d\u9762\u89c4\u5219\u6240\u538b\u5236\u3002\u201d</p><p>\u201c\u90a3\u73b0\u5728\u8be5\u600e\u4e48\u529e\uff1f\u201d</p><p>\u9976\u662f\u743c\u6069\u7d20\u6765\u9547\u5b9a\uff0c\u6b64\u65f6\u770b\u7740\u6885\u83f2\u65af\u82cd\u767d\u7684\u8138\u988a\uff0c\u4e5f\u4e0d\u514d\u5173\u5fc3\u5219\u4e71\uff0c\u6709\u4e9b\u4e0d\u77e5\u6240\u63aa\u8d77\u6765\u3002\u8475\u9732\u5728\u65c1\u8fb9\u770b\u4e86\uff0c\u4e0d\u7531\u5f97\u53c8\u51b7\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u201c\u8fd8\u80fd\u600e\u4e48\u529e\uff0c\u8d76\u5feb\u56de\u7269\u8d28\u754c\u3002\u5979\u5728\u8fd9\u91cc\u591a\u505c\u7559\u4e00\u523b\uff0c\u5c31\u662f\u591a\u4e00\u5206\u5371\u9669\uff0c\u867d\u7136\u73b0\u5728\u8fd8\u6ca1\u5927\u788d\uff0c\u53ea\u662f\u865a\u5f31\uff0c\u65f6\u95f4\u957f\u4e86\u5c31\u6709\u6027\u547d\u4e4b\u5fe7\u3002\u201d</p><p>\u8bdd\u867d\u5982\u6b64\u8bf4\uff0c\u4f46\u600e\u4e48\u56de\u7269\u8d28\u754c\uff0c\u8fd9\u4e5f\u662f\u4e2a\u5927\u96be\u9898\u3002\u8fd9\u5df2\u7ecf\u662f\u4e24\u4e2a\u4f4d\u9762\uff0c\u4e24\u4e2a\u4e16\u754c\uff0c\u4e24\u4e2a\u65f6\u7a7a\uff0c\u4e0d\u662f\u5355\u9760\u8d70\u8def\u5c31\u80fd\u8d70\u5230\u7684\u3002\u8de8\u4f4d\u9762\u4f20\u9001\u7684\u9b54\u6cd5\u662f\u6709\uff0c\u4f46\u90a3\u662f\u6700\u9ad8\u9636\u7684\u6cd5\u672f\u4e4b\u4e00\uff0c\u548c\u65f6\u95f4\u505c\u6b62\u3001\u5927\u88c2\u89e3\u5e76\u5217\uff0c\u743c\u6069\u662f\u4e0d\u4f1a\u7684\uff0c\u8475\u9732\u540c\u6837\u4e5f\u4e0d\u4f1a\u2014\u2014\u5c31\u7b97\u4ed6\u4eec\u4f1a\u4e5f\u6ca1\u7528\uff0c\u6df1\u6e0a\u4e4b\u6240\u4ee5\u662f\u6df1\u6e0a\uff0c\u6700\u672c\u8d28\u7684\u7279\u5f81\u5c31\u662f\u6df7\u4e71\u3002\u9664\u975e\u80fd\u591f\u8fbe\u5230\u5404\u5927\u6076\u9b54\u9886\u4e3b\u90a3\u79cd\u7a0b\u5ea6\uff0c\u81ea\u8eab\u548c\u4f4d\u9762\u89c4\u5219\u9ad8\u5ea6\u534f\u8c03\uff0c\u80fd\u591f\u987a\u5229\u5b8c\u6210\u4f4d\u9762\u4f20\u9001\uff0c\u5426\u5219\u5341\u6709\u516b\u4e5d\u6cd5\u672f\u4f1a\u51fa\u9519\uff0c\u4e0d\u77e5\u9053\u628a\u4eba\u9001\u5230\u4ec0\u4e48\u9b3c\u5730\u65b9\u53bb\u3002</p><p>\u201c\u6211\u4e5f\u6ca1\u4ec0\u4e48\u597d\u529e\u6cd5\uff0c\u53ea\u80fd\u89c1\u673a\u884c\u4e8b\uff0c\u201d\u8475\u9732\u8bf4\uff0c\u201c\u597d\u5728\u6df1\u6e0a\u91cc\u5371\u9669\u591a\uff0c\u673a\u4f1a\u540c\u6837\u4e5f\u591a\uff0c\u672a\u5fc5\u4e0d\u80fd\u627e\u5230\u8db3\u591f\u5f3a\u5927\u7684\u6076\u9b54\u9001\u6211\u4eec\u56de\u53bb\u3002\u5c31\u76ee\u524d\u6765\u8bf4\uff0c\u4e5f\u53ea\u80fd\u8d70\u4e00\u6b65\u770b\u4e00\u6b65\u4e86\u3002\u201d</p><p>\u8475\u9732\u7684\u8bdd\u7a81\u7136\u63d0\u9192\u4e86\u743c\u6069\u3002</p><p>\u627e\u5230\u8db3\u591f\u5f3a\u5927\u7684\u6076\u9b54\uff1f\u81ea\u5df1\u4e0d\u5c31\u6b63\u597d\u8ba4\u8bc6\u4e00\u4e2a\u4e48\u3002</p><p>\u4ed6\u5306\u5fd9\u4ece\u6000\u4e2d\u53d6\u51fa\u6b21\u5143\u888b\uff0c\u5728\u91cc\u9762\u5bfb\u627e\u7740\uff0c\u6700\u540e\u7ffb\u51fa\u4e00\u5f20\u79d8\u94f6\u540d\u7247\u6765\u3002\u4e0d\u7406\u4f1a\u8475\u9732\u548c\u963f\u5fd2\u59ae\u8be7\u5f02\u7684\u76ee\u5149\uff0c\u743c\u6069\u6e05\u4e86\u6e05\u55d3\u5b50\uff0c\u201c\u6211\u7231\u5973\u795e\u201d\uff0c\u4ed6\u5bf9\u7740\u540d\u7247\u8bf4\u3002</p><p>\u5728\u8bf4\u51fa\u53e3\u4ee4\u7684\u65f6\u5019\uff0c\u4ed6\u5fc3\u4e2d\u8fd8\u9887\u6709\u4e9b\u5fd0\u5fd1\u4e0d\u5b89\uff0c\u4e0d\u77e5\u9053\u8fd9\u4e1c\u897f\u8de8\u8d8a\u4f4d\u9762\u80fd\u4e0d\u80fd\u8fd8\u6709\u6548\u3002\u5e78\u597d\u51e0\u79d2\u949f\u540e\uff0c\u4e00\u56e2\u865a\u50cf\u4ece\u540d\u7247\u4e0a\u5347\u8d77\uff0c\u6b63\u662f\u6b27\u51ef\u3002\u201c\u5514\uff0c\u597d\u4e45\u4e0d\u89c1\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u6709\u4ec0\u4e48\u6211\u53ef\u4ee5\u6548\u52b3\u7684\u5417\uff1f\u201d</p><p>\u201c\u5f53\u7136\u6709\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u8d76\u5feb\u5f00\u4e00\u9053\u4f4d\u9762\u4f20\u9001\u95e8\uff0c\u8ba9\u6211\u80fd\u591f\u56de\u7269\u8d28\u754c\u3002\u201d</p><p>\u6b27\u51ef\u4f3c\u4e4e\u5413\u4e86\u4e00\u8df3\uff0c\u201c\u56de\u7269\u8d28\u754c\uff1f\u4f60\u73b0\u5728\u5728\u54ea\u91cc\uff1f\u201d</p><p>\u201c\u5728\u4f60\u7684\u8001\u5bb6\uff0c\u65e0\u5c3d\u6df1\u6e0a\u3002\u201d</p><p>\u201c\u4f60\u600e\u4e48\u8dd1\u5230\u90a3\u91cc\u53bb\u4e86\uff1f\u201d</p><p>\u201c\u8bf4\u6765\u8bdd\u957f\uff0c\u4ee5\u540e\u518d\u89e3\u91ca\uff0c\u201d\u743c\u6069\u4e0d\u8010\u70e6\u5730\u6253\u65ad\uff0c\u201c\u5148\u8d76\u5feb\u628a\u6211\u548c\u6211\u7684\u670b\u53cb\u5f04\u56de\u53bb\u518d\u8bf4\u3002\u201d</p><p>\u201c\u53ef\u662f\u8fd9\u4e2a\u6211\u4e5f\u6ca1\u529e\u6cd5\u5440\uff0c\u201d\u6b27\u51ef\u644a\u5f00\u624b\uff0c\u8868\u793a\u65e0\u5948\uff0c\u201c\u4f4d\u9762\u4f20\u9001\u7684\u6cd5\u672f\u6211\u662f\u4f1a\uff0c\u4f46\u95ee\u9898\u662f\u8fd9\u4e2a\u6cd5\u672f\u662f\u5355\u5411\u4f20\u9001\u7684\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u6211\u5f97\u5148\u56de\u6df1\u6e0a\uff0c\u627e\u5230\u4f60\uff0c\u7136\u540e\u518d\u5f00\u4e00\u9053\u901a\u5f80\u7269\u8d28\u754c\u7684\u4f20\u9001\u95e8\u3002\u201d</p><p>\u201c\u90a3\u4f60\u8d76\u5feb\u56de\u6765\u5c31\u662f\u4e86\uff01\u201d</p><p>\u201c\u95ee\u9898\u5c31\u5728\u4e8e\u6211\u4e0d\u80fd\u56de\u53bb\u554a\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u662f\u6076\u9b54\u5417\uff0c\u6df1\u6e0a\u5c31\u662f\u4f60\u5bb6\uff0c\u4f60\u600e\u4e48\u56de\u4e0d\u6765\u3002\u201d</p><p>\u201c\u4e0d\u662f\u6211\u56de\u4e0d\u53bb\uff0c\u662f\u6211\u4e0d\u80fd\u56de\u53bb\uff0c\u201d\u6b27\u51ef\u8d39\u52b2\u5730\u89e3\u91ca\uff0c\u201c\u8981\u56de\u6df1\u6e0a\u662f\u53ef\u4ee5\uff0c\u4f46\u56de\u53bb\u4e4b\u540e\u6211\u5c31\u6ca1\u529e\u6cd5\u518d\u6765\u7269\u8d28\u754c\u4e86\u3002\u6211\u5de5\u4f5c\u8fd8\u6ca1\u505a\u5b8c\uff0c\u8fd9\u6837\u4f1a\u88ab\u8001\u677f\u5f00\u9664\u7684\u3002\u201d</p><p>\u201c\u4f60\u8fd9\u6b21\u600e\u4e48\u5230\u7269\u8d28\u754c\u7684\uff0c\u4e0b\u6b21\u4e5f\u7167\u65e7\u5c31\u662f\u4e86\uff0c\u6709\u4ec0\u4e48\u4e0d\u884c\u7684\u3002\u201d\u743c\u6069\u51e0\u4e4e\u6709\u4e9b\u60f3\u53d1\u706b\u4e86\u3002</p><p>\u201c\u6ca1\u90a3\u4e48\u7b80\u5355\uff0c\u4f60\u77e5\u9053\u6211\u662f\u6076\u9b54\uff0c\u6076\u9b54\u5b9e\u9645\u4e0a\u5c31\u662f\u6df1\u6e0a\u672c\u8d28\u7684\u51dd\u7ed3\uff0c\u662f\u4f4d\u9762\u89c4\u5219\u7684\u5177\u73b0\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u6076\u9b54\u548c\u6df1\u6e0a\u5176\u5b9e\u662f\u4e00\u4f53\u7684\u3002\u4f60\u4eec\u662f\u51e1\u4eba\uff0c\u53ea\u8981\u6709\u4f20\u9001\u95e8\uff0c\u6709\u901a\u9053\uff0c\u5c31\u80fd\u76f4\u63a5\u79bb\u5f00\u6df1\u6e0a\uff0c\u4f46\u6211\u4eec\u662f\u88ab\u6574\u4e2a\u4f4d\u9762\u6240\u675f\u7f1a\u7684\uff0c\u8f7b\u6613\u4e0d\u80fd\u79bb\u5f00\u3002\u6240\u4ee5\u51e1\u4eba\u60f3\u8981\u53ec\u5524\u6076\u9b54\uff0c\u5f80\u5f80\u90fd\u9700\u8981\u4ed8\u51fa\u5404\u79cd\u5de8\u5927\u4ee3\u4ef7\uff0c\u6682\u65f6\u7f13\u89e3\u6076\u9b54\u548c\u6df1\u6e0a\u4e4b\u95f4\u7684\u8054\u7ed3\u3002\u6211\u8fd9\u6b21\u80fd\u6765\u7269\u8d28\u754c\uff0c\u662f\u6d88\u8017\u4e86\u5965\u5580\u65af\u56db\u5206\u4e4b\u4e09\u7684\u51e1\u4eba\u4fe1\u5f92\u6765\u8840\u796d\uff0c\u4e0b\u6b21\u5374\u5230\u54ea\u91cc\u518d\u5f04\u8fd9\u4e48\u591a\u4eba\u2014\u2014\u5c31\u7b97\u771f\u6709\u8fd9\u4e48\u591a\u4eba\uff0c\u5965\u5580\u65af\u4e5f\u80af\u5b9a\u4f1a\u628a\u6211\u5f00\u9664\u4e86\uff0c\u8bf4\u8d77\u6765\u4ed6\u4eca\u5e74\u7684\u5de5\u8d44\u4e00\u76f4\u8fd8\u6ca1\u4ed8\u5462\u2026\u2026\u201d</p><p>\u542c\u4ed6\u8bf4\u5f97\u632f\u632f\u6709\u8bcd\uff0c\u743c\u6069\u4e00\u65f6\u4e5f\u6709\u4e9b\u65e0\u8bed\u3002\u5e78\u597d\u6b27\u51ef\u53d1\u4e86\u51e0\u53e5\u7262\u9a9a\uff0c\u505c\u4f4f\u8bdd\u5934\uff0c\u201c\u5148\u522b\u7740\u6025\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4e8b\u60c5\u6162\u6162\u6765\uff0c\u4f60\u5728\u6df1\u6e0a\u54ea\u4e00\u5c42\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u6211\u600e\u4e48\u77e5\u9053\uff0c\u6211\u53c8\u4e0d\u719f\u3002\u201d</p><p>\u201c\u90a3\u4f60\u544a\u8bc9\u6211\u4f60\u90fd\u770b\u5230\u4ec0\u4e48\u4e86\u3002\u201d</p><p>\u201c\u5934\u4e0a\u6709\u4e2a\u706b\u7ea2\u7684\u5927\u592a\u9633\uff0c\u4e00\u76f4\u6302\u5728\u6b63\u4e2d\u6ca1\u504f\u8fc7\u534a\u70b9\uff1b\u6ee1\u5929\u7684\u4e4c\u4e91\u9634\u973e\u50cf\u8981\u4e0b\u96e8\uff0c\u5230\u73b0\u5728\u534a\u70b9\u6c34\u6ef4\u6ca1\u6709\uff1b\u5f17\u6d1b\u9b54\u50cf\u9e1f\u4e00\u6837\u6210\u7fa4\u7ed3\u961f\u5730\u98de\u6765\u98de\u53bb\u2026\u2026\u201d</p><p>\u201c\u90a3\u662f\u7b2c\u4e00\u5c42\uff0c\u201d\u6b27\u51ef\u6253\u65ad\uff0c\u201c\u5e15\u7956\u5c3c\u4e9a\uff0c\u5728\u7269\u8d28\u754c\u4e00\u822c\u79f0\u4f5c\u4e07\u6e0a\u5e73\u539f\u3002\u201d</p><p>\u201c\u6211\u7ba1\u5b83\u53eb\u4ec0\u4e48\uff0c\u95ee\u9898\u662f\u600e\u4e48\u624d\u80fd\u56de\u53bb\uff1f\u201d</p><p>\u201c\u522b\u6025\uff0c\u65e2\u7136\u662f\u4e07\u6e0a\u5e73\u539f\uff0c\u90a3\u4e8b\u60c5\u5c31\u597d\u529e\u591a\u4e86\uff0c\u201d\u6b27\u51ef\u8bf4\uff0c\u201c\u76f8\u5bf9\u6765\u8bf4\uff0c\u4e07\u6e0a\u5e73\u539f\u662f\u6df1\u6e0a\u8bf8\u5c42\u4e2d\u6700\u5b89\u5168\u7684\u6240\u5728\u4e86\uff0c\u53ea\u8981\u4e0d\u548c\u9b54\u9b3c\u4eec\u5f00\u6218\u2026\u2026\u4f60\u5148\u627e\u5230\u65ad\u57df\u9547\u3002\u201d</p><p>\u201c\u65ad\u57df\u9547\u662f\u4ec0\u4e48\u5730\u65b9\uff1f\u201d</p><p>\u201c\u4e00\u4e2a\u53eb\u7ea2\u8272\u5bff\u8863\u7684\u9b45\u9b54\u5efa\u7acb\u7684\u5c0f\u9547\uff0c\u662f\u4e07\u6e0a\u5e73\u539f\u91cc\u6700\u5b89\u5168\u7684\u5730\u65b9\u4e86\uff0c\u56e0\u4e3a\u6700\u6709\u79e9\u5e8f\u3002\u53ea\u8981\u8fdb\u4e86\u65ad\u57df\u9547\uff0c\u5c31\u53ef\u4ee5\u907f\u514d\u88ab\u6076\u9b54\u968f\u610f\u653b\u51fb\u3002\u5728\u90a3\u91cc\u4f60\u5e94\u8be5\u80fd\u83b7\u5f97\u4e00\u4e9b\u5e2e\u52a9\uff0c\u751a\u81f3\u76f4\u63a5\u627e\u7ea2\u8272\u5bff\u8863\u5e2e\u5fd9\uff0c\u5979\u76f8\u5bf9\u6bd4\u8f83\u5bb9\u6613\u6253\u4ea4\u9053\u3002\u201d</p><p>\u201c\u65ad\u57df\u9547\u5728\u54ea\u91cc\u3002\u201d</p><p>\u6b27\u51ef\u505a\u4e86\u4e2a\u65e0\u53ef\u5948\u4f55\u7684\u624b\u52bf\uff0c\u201c\u8fd9\u4e2a\u6211\u5c31\u6ca1\u529e\u6cd5\u8bf4\u4e86\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u4f60\u73b0\u5728\u5728\u4e07\u6e0a\u5e73\u539f\u7684\u54ea\u4e2a\u4f4d\u7f6e\u2026\u2026\u4f55\u51b5\u6df1\u6e0a\u4e4b\u6240\u4ee5\u662f\u6df1\u6e0a\uff0c\u5c31\u5728\u4e8e\u5b83\u6839\u672c\u5c31\u662f\u4f1a\u968f\u65f6\u53d8\u52a8\u7684\uff0c\u4f60\u53ea\u80fd\u81ea\u5df1\u78b0\u8fd0\u6c14\u4e86\u3002\u597d\u4e86\u5c31\u8fd9\u4e48\u591a\uff0c\u6211\u73b0\u5728\u8fd8\u6709\u522b\u7684\u4e8b\u60c5\uff0c\u4e0b\u6b21\u518d\u89c1\u3002\u201d</p><p>\u6b27\u51ef\u6ca1\u6709\u5e2e\u4e0a\u592a\u591a\u5fd9\uff0c\u4f46\u81f3\u5c11\u6307\u51fa\u4e86\u4e00\u4e2a\u76ee\u6807\uff1a\u65ad\u57df\u9547\u3002\u53ea\u662f\u5149\u6709\u5730\u540d\u6ca1\u6709\u8def\u5f84\uff0c\u5bf9\u4e8e\u743c\u6069\u8fd9\u4e9b\u5916\u6765\u4eba\u800c\u8a00\u4f9d\u65e7\u662f\u65e0\u7528\u3002</p><p>\u201c\u73b0\u5728\u53ea\u6709\u8d70\u4e00\u6b65\u770b\u4e00\u6b65\uff0c\u201d\u8475\u9732\u4e00\u76f4\u5728\u51b7\u8a00\u65c1\u89c2\uff0c\u6b64\u65f6\u5f00\u53e3\u8bf4\uff0c\u201c\u6211\u4eec\u4e0d\u77e5\u9053\u65ad\u57df\u9547\u7684\u65b9\u4f4d\uff0c\u4f46\u6076\u9b54\u4eec\u603b\u8be5\u77e5\u9053\u3002\u5e0c\u671b\u80fd\u6293\u4f4f\u4e00\u4e2a\u9ad8\u9636\u6076\u9b54\uff0c\u8ba9\u5b83\u7ed9\u6211\u4eec\u5e26\u8def\u3002\u6211\u4eec\u8fd9\u91cc\u6ca1\u6709\u8c01\u61c2\u6076\u9b54\u8bed\u7684\uff0c\u4f46\u5982\u679c\u662f\u9ad8\u9636\u7684\u6076\u9b54\uff0c\u5e94\u8be5\u4e5f\u4f1a\u7269\u8d28\u754c\u7684\u8bed\u8a00\uff0c\u90a3\u4e48\u5c31\u53ef\u4ee5\u4ea4\u6d41\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u4e5f\u53ea\u80fd\u5982\u6b64\uff0c\u6ca1\u6709\u522b\u7684\u65b9\u6cd5\u4e86\u3002</p><p>\u201c\u5927\u5bb6\u73b0\u5728\u5904\u5883\u76f8\u540c\uff0c\u76ee\u6807\u4e5f\u76f8\u540c\uff0c\u90fd\u662f\u8981\u56de\u7269\u8d28\u754c\u53bb\uff0c\u201d\u8475\u9732\u63a5\u7740\u8bf4\uff0c\u201c\u6df1\u6e0a\u53ef\u4e0d\u662f\u4ec0\u4e48\u548c\u5e73\u5b89\u8be6\u7684\u5730\u65b9\uff0c\u5c31\u51ed\u6211\u4eec\u8fd9\u51e0\u4e2a\u4eba\uff0c\u5982\u679c\u5404\u81ea\u4e3a\u6218\u751a\u81f3\u76f8\u4e92\u62c6\u53f0\u7684\u8bdd\uff0c\u8c01\u90fd\u6d3b\u4e0d\u8fc7\u4eca\u665a\uff0c\u53ea\u6709\u8054\u624b\u624d\u6709\u51fa\u8def\u3002\u5bf9\u4e8e\u8fd9\u4e00\u70b9\uff0c\u4f60\u6ca1\u4ec0\u4e48\u5f02\u8bae\u5427\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\u3002\u201d</p><p>\u201c\u6ca1\u6709\u3002\u201d</p><p>\u201c\u90a3\u5f88\u597d\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u6211\u4eec\u662f\u4e0d\u662f\u5e94\u8be5\u5f00\u8bda\u5e03\u516c\u4e00\u4e9b\uff0c\u201d\u8475\u9732\u8bf4\uff0c\u201c\u81f3\u5c11\uff0c\u4f60\u5e94\u8be5\u627f\u8ba4\u81ea\u5df1\u7684\u9009\u6c11\u8eab\u4efd\u5427\u3002\u201d</p></article>"}}}]);
