(window.webpackJsonp=window.webpackJsonp||[]).push([[304],{326:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u8282 \u5076\u9047",content:"<article><h2>\u7b2c\u4e8c\u5341\u8282 \u5076\u9047</h2><p>\u743c\u6069\u5fae\u5fae\u6709\u4e9b\u607c\u6012\u3002\u4ed6\u867d\u7136\u540d\u4e49\u4e0a\u7b97\u838e\u5c14\u7684\u4fe1\u5f92\uff0c\u5176\u5b9e\u6700\u591a\u7b97\u662f\u4e2a\u6cdb\u4fe1\u8005\uff0c\u6839\u672c\u5c31\u4e0d\u8654\u8bda\uff0c\u8bf4\u4ec0\u4e48\u9009\u6c11\u66f4\u662f\u65e0\u7a3d\u4e4b\u8c08\uff0c\u4e0d\u77e5\u9053\u4ece\u4f55\u8bf4\u8d77\u3002\u8475\u9732\u4e0d\u77e5\u9053\u56e0\u4e3a\u4ec0\u4e48\u7f18\u6545\uff0c\u5c31\u8ba4\u51c6\u4e86\u4ed6\u662f\u838e\u5c14\u9009\u6c11\uff0c\u5bfc\u81f4\u83ab\u540d\u5176\u5999\u7684\u654c\u610f\u3002\u743c\u6069\u539f\u672c\u61d2\u5f97\u89e3\u91ca\uff0c\u4f55\u51b5\u8fd9\u6b21\u8fd8\u7b97\u662f\u88ab\u5bf9\u65b9\u6551\u4e86\u4e00\u547d\uff0c\u6bd5\u7adf\u7f57\u4e1d\u7684\u9996\u8981\u76ee\u6807\u9488\u5bf9\u7684\u5e94\u8be5\u662f\u4ed6\uff0c\u4f0a\u8389\u96c5\u51fa\u73b0\u7684\u65f6\u5019\uff0c\u8475\u9732\u90fd\u8fd8\u6ca1\u5230\u6668\u70bc\u57ce\uff0c\u5c5e\u4e8e\u88ab\u6b83\u53ca\u7684\u3002\u8fd9\u6b21\u80fd\u9003\u8131\u7f57\u4e1d\u7684\u5f3a\u5236\u4f20\u9001\uff0c\u6ca1\u6709\u843d\u5230\u8718\u86db\u9b54\u7f51\u53bb\uff0c\u4e5f\u591a\u8d56\u8475\u9732\u4e4b\u529b\uff0c\u63a5\u4e0b\u6765\u8981\u56de\u7269\u8d28\u754c\uff0c\u4e5f\u9700\u8981\u53cc\u65b9\u5408\u4f5c\u3002\u56e0\u4e3a\u8fd9\u4e9b\u7f18\u6545\uff0c\u4ed6\u539f\u672c\u5b58\u4e86\u5bb9\u8ba9\u4e09\u5206\u7684\u5fc3\u601d\uff0c\u4e0d\u60f3\u591a\u8ba1\u8f83\uff0c\u5374\u6ca1\u60f3\u5230\u5bf9\u65b9\u4e00\u800c\u518d\u518d\u800c\u4e09\u5730\u63d0\u8d77\uff0c\u4e0d\u7531\u5f97\u4e5f\u5fc3\u5934\u706b\u8d77\u3002</p><p>\u201c\u6211\u4e0d\u662f\u4ec0\u4e48\u9009\u6c11\uff01\u201d</p><p>\u8475\u9732\u51b7\u7b11\uff0c\u201c\u521a\u624d\u4e0d\u662f\u8bf4\u8981\u5f00\u8bda\u5e03\u516c\u5408\u4f5c\u5417\uff0c\u96be\u9053\u8fde\u627f\u8ba4\u81ea\u5df1\u7684\u8eab\u4efd\u90fd\u4e0d\u6562\uff1f\u201d</p><p>\u201c\u6211\u4e0d\u77e5\u9053\u4f60\u4ece\u54ea\u91cc\u5f97\u6765\u7684\u8fd9\u79cd\u8352\u8bde\u60f3\u6cd5\uff0c\u201d\u743c\u6069\u4e5f\u51b7\u51b7\u56de\u5e94\uff0c\u201c\u4f46\u65e2\u7136\u4f60\u53ea\u613f\u610f\u542c\u5230\u81ea\u5df1\u559c\u6b22\u7684\u7b54\u6848\uff0c\u90a3\u6839\u672c\u5c31\u6ca1\u5fc5\u8981\u95ee\u6211\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u627f\u8ba4\u81ea\u5df1\u662f\u838e\u5c14\u7684\u9009\u6c11\uff1f\u201d</p><p>\u201c\u6839\u672c\u5c31\u4e0d\u5b58\u5728\u7684\u4e8b\u60c5\uff0c\u6211\u600e\u4e48\u627f\u8ba4\uff1f\u201d\u743c\u6069\u53cd\u95ee\uff0c\u201c\u96be\u9053\u6240\u8c13\u7684\u9b54\u6cd5\u5973\u795e\u9009\u6c11\uff0c\u5c31\u662f\u8fd9\u6837\u504f\u6267\u548c\u4e0d\u53ef\u7406\u55bb\uff1f\u65e2\u7136\u4f60\u4e00\u76f4\u58f0\u79f0\u6211\u662f\u9009\u6c11\uff0c\u90a3\u4e48\u5c31\u8bf7\u62ff\u51fa\u8bc1\u636e\u51fa\u6765\u3002\u201d</p><p>\u201c\u8bc1\u636e\uff1f\u5f71\u706b\u5c31\u662f\u6700\u660e\u663e\u7684\u8bc1\u636e\u3002\u201d</p><p>\u201c\u5f71\u706b\uff1f\u201d</p><p>\u201c\u5f71\u706b\u662f\u838e\u5c14\u7684\u795e\u529b\uff0c\u4f60\u6709\u5f71\u706b\uff0c\u4f60\u5f53\u7136\u662f\u5979\u7684\u9009\u6c11\uff0c\u4f60\u80fd\u5426\u8ba4\u8fd9\u70b9\uff1f\u201d</p><p>\u201c\u6211\u4e0d\u77e5\u9053\u4ec0\u4e48\u5f71\u706b\u3002\u201d</p><p>\u201c\u4f60\u660e\u660e\u7528\u5f71\u706b\u5851\u6210\u4e86\u9634\u5f71\u6597\u7bf7\uff0c\u96be\u9053\u8fd8\u4e0d\u627f\u8ba4\uff1f\u201d\u8475\u9732\u4e5f\u6709\u4e9b\u53d1\u6012\u8d77\u6765\uff0c\u201c\u4f60\u7684\u5f71\u706b\u548c\u6211\u7684\u94f6\u706b\u76f8\u78b0\u649e\uff0c\u6240\u4ee5\u624d\u7834\u574f\u4e86\u7f57\u4e1d\u7684\u5f3a\u5236\u4f20\u9001\uff0c\u8fd9\u96be\u9053\u4e0d\u662f\u4e8b\u5b9e\uff1f\u201d</p><p>\u743c\u6069\u5fae\u5fae\u76b1\u7709\uff0c\u7f57\u4e1d\u9644\u4f53\u4f0a\u8389\u96c5\uff0c\u53d1\u52a8\u5f3a\u5236\u53ec\u5524\u7684\u65f6\u5019\uff0c\u60c5\u51b5\u867d\u7136\u5371\u6025\uff0c\u5927\u5bb6\u610f\u8bc6\u5374\u8fd8\u90fd\u6e05\u9192\uff0c\u5f53\u65f6\u53d1\u751f\u7684\u4e8b\u60c5\u81ea\u7136\u4e5f\u90fd\u8bb0\u5f97\u3002\u8981\u8bf4\u8d77\u6765\uff0c\u743c\u6069\u786e\u5b9e\u8bb0\u5f97\u81ea\u5df1\u4f53\u5185\u90a3\u80a1\u51b0\u5bd2\u6c14\u606f\u4e0a\u6d8c\uff0c\u6ea2\u51fa\u4f53\u5916\u5f62\u6210\u4e86\u9634\u5f71\u6597\u7bf7\uff0c\u800c\u4e14\u4e5f\u786e\u5b9e\u662f\u5728\u548c\u8475\u9732\u53d1\u51fa\u7684\u90a3\u9053\u84dd\u767d\u95ea\u7535\u76f8\u649e\u4e4b\u540e\u53d1\u751f\u4e86\u5927\u7206\u70b8\u2026\u2026\u4f46\u90a3\u662f\u5f71\u706b\u4e48\uff1f</p><p>\u201c\u4f60\u8bf4\u6211\u53d1\u51fa\u7684\u90a3\u662f\u5f71\u706b\uff1f\u201d</p><p>\u201c\u5f53\u7136\u662f\u5f71\u706b\u3002\u94f6\u706b\u662f\u795e\u79d8\u5973\u58eb\u7684\u795e\u529b\u5177\u73b0\uff0c\u94f6\u706b\u662f\u838e\u5c14\u7684\u795e\u529b\u5177\u73b0\uff0c\u5b83\u4eec\u7c7b\u578b\u76f8\u540c\u5374\u53c8\u5b8c\u5168\u9006\u53cd\u3002\u94f6\u706b\u552f\u6709\u548c\u5f71\u706b\u5f3a\u70c8\u78b0\u649e\uff0c\u624d\u6709\u53ef\u80fd\u4ea7\u751f\u8fd9\u79cd\u6548\u679c\u2014\u2014\u96be\u9053\u4f60\u8fd9\u4e5f\u8981\u5426\u8ba4\u5417\uff1f\u201d</p><p>\u743c\u6069\u6c89\u9ed8\u4e86\u7247\u523b\u3002</p><p>\u201c\u6216\u8bb8\u90a3\u662f\u5f71\u706b\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f46\u6211\u4e0d\u77e5\u9053\u5b83\u7684\u6765\u5386\uff0c\u66f4\u4e0d\u662f\u4ec0\u4e48\u9009\u6c11\u3002\u201d</p><p>\u8475\u9732\u6012\u6781\u53cd\u7b11\uff0c\u201c\u83ab\u975e\u4f60\u662f\u60f3\u544a\u8bc9\u6211\uff0c\u838e\u5c14\u6388\u4e88\u4f60\u795e\u529b\uff0c\u800c\u4f60\u81ea\u5df1\u5c45\u7136\u4e0d\u77e5\u60c5\uff1f\u201d</p><p>\u201c\u542c\u8d77\u6765\u4f3c\u4e4e\u786e\u5b9e\u6709\u4e9b\u4e0d\u53ef\u601d\u8bae\uff0c\u4f46\u8fd9\u662f\u4e8b\u5b9e\uff0c\u4e0d\u7ba1\u4f60\u76f8\u4fe1\u4e0d\u76f8\u4fe1\uff0c\u201d\u743c\u6069\u6de1\u6de1\u8bf4\uff0c\u201c\u524d\u63d0\u662f\u5b83\u771f\u7684\u662f\u5f71\u706b\uff0c\u6bd5\u7adf\u8fd9\u5b8c\u5168\u662f\u4f60\u7684\u4e00\u9762\u4e4b\u8bcd\u3002\u201d</p><p>\u8475\u9732\u52c3\u7136\u53d8\u8272\uff0c\u4fbf\u8981\u53d1\u4f5c\uff0c\u6885\u83f2\u65af\u8f7b\u8f7b\u62ac\u4e86\u62ac\u624b\uff0c\u201c\u4ed6\u8bf4\u5f97\u662f\u771f\u8bdd\uff0c\u7ef4\u62c9\u591a\u4f26\u5973\u58eb\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4ed6\u5e76\u975e\u591c\u5973\u58eb\u7684\u9009\u6c11\uff0c\u8fd9\u70b9\u6211\u53ef\u4ee5\u4fdd\u8bc1\u3002\u201d</p><p>\u8475\u9732\u671d\u5979\u770b\u4e86\u770b\uff0c\u201c\u5f53\u7136\uff0c\u6885\u83f2\u65af\u5c0f\u59d0\uff0c\u4f60\u662f\u5723\u6b66\u58eb\uff0c\u6211\u5e94\u8be5\u76f8\u4fe1\u4f60\uff0c\u201d\u5979\u8c28\u614e\u9009\u62e9\u7740\u63aa\u8f9e\uff0c\u201c\u6211\u542c\u8fc7\u4e00\u4e9b\u6709\u5173\u4f60\u7684\u4e8b\u8ff9\u3002\u6beb\u65e0\u7591\u95ee\uff0c\u4f60\u662f\u4e00\u4f4d\u4f18\u79c0\u7684\u6218\u58eb\uff0c\u806a\u660e\u3001\u82f1\u52c7\uff0c\u79c9\u6301\u5584\u826f\u2026\u2026\u4f46\u4f3c\u4e4e\u5e76\u4e0d\u4ee5\u8bda\u5b9e\u8457\u79f0\u3002\u201d</p><p>\u201c\u5728\u5e94\u8be5\u8bda\u5b9e\u7684\u65f6\u5019\uff0c\u6211\u4f1a\u975e\u5e38\u8bda\u5b9e\u3002\u201d</p><p>\u201c\u4f46\u6211\u600e\u4e48\u77e5\u9053\u4f60\u4f1a\u5728\u4ec0\u4e48\u65f6\u5019\u8bda\u5b9e\uff1f\u201d</p><p>\u201c\u662f\u4e48\uff0c\u201d\u6885\u83f2\u65af\u51b7\u51b7\u8bf4\uff0c\u201c\u90a3\u4e48\u4f60\u53ef\u591f\u611a\u8822\u7684\u3002\u201d</p><p>\u6240\u6709\u4eba\u90fd\u6014\u4f4f\uff0c\u6ca1\u60f3\u5230\u5979\u4f1a\u8fd9\u4e48\u8bf4\uff0c\u8475\u9732\u77aa\u7740\u6885\u83f2\u65af\uff0c\u963f\u5fd2\u59ae\u5728\u4e00\u65c1\u4e0d\u77e5\u6240\u63aa\uff0c\u4e0d\u77e5\u9053\u5728\u6559\u4f1a\u9886\u8896\u548c\u670b\u53cb\u4e4b\u95f4\u5e94\u8be5\u5982\u4f55\u9009\u62e9\u3002\u743c\u6069\u5c06\u6885\u83f2\u65af\u62b1\u7d27\uff0c\u540c\u65f6\u6697\u4e2d\u6212\u5907\u7740\uff0c\u6c14\u6c1b\u4e00\u65f6\u53d8\u52a8\u5341\u5206\u7d27\u5f20\u3002\u4f46\u6700\u7ec8\uff0c\u8475\u9732\u7684\u5634\u89d2\u5fae\u5fae\u626f\u52a8\uff0c\u9732\u51fa\u4e00\u4e1d\u7b11\u610f\u3002</p><p>\u201c\u90a3\u597d\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u76f8\u4fe1\u4f60\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u4e00\u573a\u7ea0\u7eb7\u901a\u8fc7\u8fd9\u79cd\u51fa\u4e4e\u610f\u6599\u7684\u65b9\u5f0f\u6682\u65f6\u5316\u89e3\uff0c\u5927\u5bb6\u4f11\u606f\u7247\u523b\uff0c\u51b3\u5b9a\u542f\u7a0b\u3002\u56e0\u4e3a\u6ca1\u6709\u660e\u786e\u7684\u76ee\u6807\uff0c\u53ea\u80fd\u968f\u673a\u9009\u62e9\u4e86\u4e00\u4e2a\u65b9\u5411\u3002\u66f4\u9ebb\u70e6\u7684\u662f\uff0c\u4e07\u6e0a\u5e73\u539f\u7684\u592a\u9633\u662f\u6c38\u8fdc\u60ac\u6302\u6b63\u4e2d\u7684\uff0c\u6240\u4ee5\u8fde\u4e1c\u5357\u897f\u5317\u90fd\u6ca1\u529e\u6cd5\u5206\u6e05\u695a\uff0c\u5f53\u7136\u8fd9\u4e5f\u5df2\u7ecf\u6ca1\u6709\u610f\u4e49\u4e86\u3002</p><p>\u8475\u9732\u8d70\u5728\u6700\u524d\u9762\uff0c\u963f\u5fd2\u59ae\u968f\u540e\uff0c\u743c\u6069\u62b1\u7740\u6885\u83f2\u65af\u8d70\u5728\u6700\u540e\u9762\u3002\u5929\u6c14\u5f88\u70ed\uff0c\u5934\u9876\u7684\u592a\u9633\u70bd\u70c8\uff0c\u6ca1\u4e00\u4f1a\u6240\u6709\u4eba\u90fd\u5df2\u7ecf\u5927\u6c57\u6dcb\u6f13\uff0c\u5168\u8eab\u6e7f\u900f\uff0c\u743c\u6069\u4ece\u6b21\u5143\u888b\u91cc\u628a\u6885\u83f2\u65af\u7684\u94f6\u7532\u53d6\u4e86\u51fa\u6765\uff0c\u66ff\u5979\u7a7f\u4e0a\uff0c\u53ef\u4ee5\u963b\u9694\u9ad8\u6e29\u3002\u884c\u8d70\u4e86\u5927\u534a\u4e2a\u5c0f\u65f6\uff0c\u8def\u4e0a\u770b\u5230\u591a\u5904\u6df1\u4e0d\u89c1\u5e95\u7684\u5de8\u5751\uff0c\u5192\u7740\u6d53\u70df\uff0c\u8475\u9732\u8bf4\u8fd9\u5176\u5b9e\u5c31\u662f\u901a\u5f80\u6df1\u6e0a\u5176\u4ed6\u5c42\u9762\u7684\u4f20\u9001\u95e8\u2014\u2014\u95ee\u9898\u5728\u4e8e\u8fd9\u4e9b\u4f20\u9001\u95e8\u7684\u76ee\u7684\u5730\u90fd\u662f\u968f\u673a\u7684\uff0c\u6216\u8bb8\u6b64\u523b\u901a\u5f80\u6df1\u6e0a\u7b2c\u516b\u5341\u516b\u5c42\uff0c\u4e0b\u4e00\u77ac\u95f4\u5b83\u5c31\u6539\u901a\u5f80\u7b2c\u4e5d\u5341\u4e5d\u5c42\u4e86\u3002\u6210\u7fa4\u7ed3\u961f\u7684\u5f17\u6d1b\u9b54\u5728\u5929\u7a7a\u4e2d\u98de\u7fd4\u7740\uff0c\u6216\u8005\u6816\u5c45\u5728\u5200\u5203\u822c\u5c16\u9510\u7684\u5c71\u5cf0\u9876\u4e0a\uff0c\u4fef\u89c6\u7740\u5730\u4e0a\u7684\u8fd9\u4e00\u884c\u4eba\u3002\u9887\u6709\u51e0\u4e2a\u6253\u7b97\u6362\u6362\u80c3\u53e3\uff0c\u6251\u4e0b\u6765\u6355\u98df\uff0c\u7ed3\u679c\u88ab\u963f\u5fd2\u59ae\u8fde\u8fde\u53d1\u7bad\u5c04\u4e86\u4e0b\u6765\uff0c\u8475\u9732\u4ea4\u66ff\u4f7f\u7528\u4e24\u4f4d\u5973\u795e\u7684\u795e\u529b\uff0c\u6240\u6709\u88ab\u94f6\u706b\u63a0\u8fc7\u7684\u6076\u9b54\u5168\u90fd\u7acb\u523b\u5316\u4f5c\u7c89\u672b\uff0c\u51e0\u6b21\u4e0b\u6765\u4e5f\u5c31\u6ca1\u6709\u8c01\u8fd8\u6562\u4e0a\u524d\u4e86\u3002</p><p>\u53ea\u662f\u8fd9\u6837\u6f2b\u65e0\u76ee\u7684\uff0c\u7ec8\u7a76\u4e0d\u662f\u4e2a\u529e\u6cd5\uff0c\u4eba\u529b\u7ec8\u7a76\u6709\u65f6\u800c\u7a77\uff0c\u6076\u9b54\u5374\u662f\u8fd1\u4e4e\u65e0\u7a77\u65e0\u5c3d\u7684\u3002\u743c\u6069\u3001\u8475\u9732\u548c\u963f\u5fd2\u59ae\u867d\u7136\u4e0d\u50cf\u6885\u83f2\u65af\u90a3\u6837\uff0c\u53d7\u5230\u4f4d\u9762\u89c4\u5219\u7684\u4e25\u91cd\u538b\u5236\uff0c\u4f46\u6216\u591a\u6216\u5c11\u4e5f\u4f1a\u53d7\u5f71\u54cd\uff0c\u5728\u8fd9\u79cd\u72c2\u98ce\u70c8\u65e5\u7684\u6076\u52a3\u73af\u5883\u91cc\uff0c\u4f53\u529b\u6d88\u8017\u4e5f\u4e25\u91cd\u52a0\u5feb\u3002\u518d\u8fd9\u6837\u4e0b\u53bb\u7684\u8bdd\uff0c\u53ea\u6015\u627e\u4e0d\u5230\u65ad\u57df\u9547\uff0c\u81ea\u5df1\u5148\u8981\u7d2f\u6b7b\u4e86\u3002</p><p>\u800c\u4e14\u4ed6\u4eec\u8fd8\u6ca1\u643a\u5e26\u4efb\u4f55\u98df\u7269\u548c\u6c34\u3002</p><p>\u6b63\u5f77\u5fa8\u65e0\u7b56\u4e4b\u9645\uff0c\u963f\u5fd2\u59ae\u7a81\u7136\u6307\u7740\u8fdc\u5904\uff0c\u201c\u90a3\u91cc\u6709\u4e2a\u4eba\uff01\u201d</p><p>\u4f17\u4eba\u987a\u7740\u5979\u6240\u6307\u7684\u65b9\u5411\u770b\u53bb\uff0c\u786e\u5b9e\u662f\u6709\u4e2a\u4eba\u8eba\u5728\u5730\u4e0a\uff0c\u51e0\u53ea\u5f17\u6d1b\u9b54\u6b63\u5728\u4e0a\u7a7a\u76d8\u65cb\u7740\uff0c\u4f3c\u4e4e\u5728\u8003\u8651\u5982\u4f55\u4e0b\u53e3\u3002\u8475\u9732\u5389\u559d\u4e00\u58f0\uff0c\u84dd\u767d\u8272\u706b\u7130\u5728\u53cc\u81c2\u7f20\u7ed5\u7740\uff0c\u5316\u4f5c\u4e24\u652f\u957f\u77db\u7535\u5c04\u800c\u51fa\uff0c\u51fb\u4e2d\u4e24\u53ea\u5f17\u6d1b\u9b54\uff0c\u963f\u5fd2\u59ae\u4e5f\u53d1\u51fa\u4e86\u4e00\u7bad\u3002\u5f17\u6d1b\u9b54\u4eec\u89c1\u6765\u8005\u4e0d\u5584\uff0c\u7eb7\u7eb7\u632f\u7ffc\u800c\u8d77\uff0c\u98de\u4e0a\u4e86\u9ad8\u7a7a\uff0c\u8fdc\u8fdc\u9041\u5f00\u3002</p><p>\u8475\u9732\u5feb\u6b65\u8d70\u8fd1\uff0c\u4ed4\u7ec6\u770b\u6e05\u5730\u4e0a\u7684\u4eba\uff0c\u8138\u8272\u5fae\u5fae\u4e00\u53d8\uff0c\u963f\u5fd2\u59ae\u4e5f\u662f\u4e00\u60ca\uff0c\u624b\u5f80\u8170\u95f4\u4e00\u6309\uff0c\u5df2\u7ecf\u62d4\u51fa\u4e86\u77ed\u5251\u3002\u743c\u6069\u62b1\u7740\u6885\u83f2\u65af\u8ddf\u5728\u540e\u9762\uff0c\u6765\u5f97\u8fdf\u4e9b\uff0c\u8fd8\u4e0d\u77e5\u9053\u600e\u4e48\u56de\u4e8b\uff0c\u518d\u4e00\u770b\u4e5f\u4fbf\u660e\u767d\u8fc7\u6765\u3002</p><p>\u201c\u4f0a\u8389\u96c5\uff01\u201d</p><p>\u521a\u521a\u4ece\u5f17\u6d1b\u9b54\u722a\u4e0b\u6551\u51fa\u7684\u8fd9\u4eba\uff0c\u5c45\u7136\u5c31\u662f\u5bb3\u5f97\u4ed6\u4eec\u6d41\u843d\u5230\u6df1\u6e0a\u7684\u7f6a\u9b41\u7978\u9996\u2014\u2014\u81f3\u5c11\u662f\u5e2e\u51f6\u3002\u795e\u7947\u5723\u8005\u964d\u4e34\uff0c\u4e0d\u662f\u968f\u4fbf\u5728\u5927\u8def\u4e0a\u627e\u4e2a\u4eba\u5c31\u53ef\u4ee5\u7684\uff0c\u201c\u5bb9\u5668\u201d\u5fc5\u987b\u662f\u7cbe\u6311\u7ec6\u9009\uff0c\u5426\u5219\u6839\u672c\u4e0d\u53ef\u80fd\u201c\u627f\u53d7\u201d\u795e\u7947\uff0c\u54ea\u6015\u662f\u6682\u65f6\u7684\u9644\u4f53\u3002\u5c31\u4ee5\u5f80\u7684\u5148\u4f8b\u6765\u8bf4\uff0c\u5723\u8005\u964d\u4e34\u90fd\u662f\u9009\u62e9\u81ea\u5df1\u7684\u4fe1\u5f92\u505a\u9644\u4f53\u5bf9\u8c61\uff0c\u4e24\u8005\u7684\u201c\u5951\u5408\u5ea6\u201d\u6bd4\u8f83\u9ad8\uff0c\u8d8a\u8654\u8bda\u7684\u4fe1\u5f92\u81ea\u7136\u8d8a\u597d\uff0c\u9b54\u6cd5\u5973\u795e\u751f\u80b2\u4e03\u59d0\u59b9\uff0c\u9644\u4f53\u5bf9\u8c61\u5c31\u662f\u81ea\u5df1\u7684\u4fe1\u5f92\u3002\u4f0a\u8389\u96c5\u80fd\u591f\u88ab\u7f57\u4e1d\u9644\u4f53\uff0c\u8fd9\u672c\u8eab\u5c31\u5df2\u7ecf\u610f\u5473\u7740\u5f88\u591a\u4e1c\u897f\u4e86\u3002</p><p>\u53ea\u662f\u73b0\u5728\u9047\u4e0a\uff0c\u8981\u5982\u4f55\u5904\u7f6e\uff0c\u8fd9\u5374\u662f\u4e2a\u5927\u95ee\u9898\u3002\u963f\u5fd2\u59ae\u5bdf\u770b\u4e86\u4e00\u4e0b\uff0c\u4f0a\u8389\u96c5\u8eab\u4e0a\u53d7\u4e86\u4e9b\u4f24\uff0c\u5012\u5e76\u4e0d\u91cd\uff0c\u73b0\u5728\u53ea\u662f\u6655\u8ff7\u8fc7\u53bb\u3002\u5982\u679c\u653e\u4efb\u4e0d\u7406\u7684\u8bdd\uff0c\u90a3\u5979\u81ea\u7136\u662f\u6b7b\u8def\u4e00\u6761\uff0c\u5929\u7a7a\u4e2d\u90a3\u4e9b\u5f17\u6d1b\u9b54\u8fd8\u6b63\u7b49\u7740\u665a\u9910\u5462\u3002</p><p>\u201c\u600e\u4e48\u529e\uff1f\u201d\u8475\u9732\u770b\u770b\u4e09\u4f4d\u540c\u4f34\uff0c\u5f81\u6c42\u610f\u89c1\u3002</p><p>\u82e5\u8bba\u8d77\u6765\uff0c\u8fd9\u51e0\u4eba\u5c31\u7b97\u8bf4\u4e0d\u4e0a\u6740\u4f10\u679c\u65ad\uff0c\u5374\u4e5f\u7edd\u4e0d\u662f\u4f18\u67d4\u8f6f\u5f31\u4e4b\u8f88\uff0c\u5018\u82e5\u662f\u522b\u7684\u5bf9\u624b\uff0c\u76f4\u63a5\u4e00\u5251\u780d\u4e86\u4fbf\u662f\uff0c\u6216\u8005\u7f6e\u4e4b\u4e0d\u7406\u5c31\u597d\uff0c\u65e0\u9700\u591a\u60f3\u3002\u4f46\u4f0a\u8389\u96c5\u6bd5\u7adf\u5728\u6668\u70bc\u5446\u4e86\u8fd9\u4e9b\u65f6\u65e5\uff0c\u548c\u5927\u5bb6\u76f8\u719f\uff0c\u867d\u7136\u8c03\u76ae\u5f97\u5389\u5bb3\uff0c\u603b\u662f\u6076\u4f5c\u5267\uff0c\u5374\u4e5f\u662f\u5b69\u5b50\u5929\u6027\uff0c\u5e76\u4e0d\u5f53\u771f\u60f9\u4eba\u538c\u70e6\u3002\u867d\u7136\u8f6c\u773c\u95f4\u6210\u4e86\u4ec7\u654c\uff0c\u4f46\u770b\u5979\u73b0\u5728\u5b89\u5b89\u9759\u9759\u7761\u7740\uff0c\u94f6\u53d1\u5782\u4e0b\u534a\u906e\u7740\u8138\uff0c\u4f9d\u65e7\u89c9\u5f97\u4e00\u6d3e\u5929\u771f\u7a1a\u6c14\uff0c\u8ba9\u4eba\u96be\u4ee5\u72e0\u4e0b\u5fc3\u6765\u3002</p><p>\u201c\u8981\u4e48\u2026\u2026\u5e26\u4e0a\u5979\u597d\u4e86\uff0c\u201d\u963f\u5fd2\u59ae\u6709\u4e9b\u72b9\u8c6b\u5730\u8bf4\uff0c\u201c\u53cd\u6b63\u4e5f\u5c31\u4e00\u4e2a\u5c0f\u5b69\u5b50\u3002\u201d</p><p>\u201c\u7f57\u4e1d\u4e0d\u4f1a\u518d\u7528\u5979\u73a9\u4e00\u6b21\u5723\u8005\u964d\u4e34\u5427\u3002\u201d\u743c\u6069\u4e0d\u65e0\u62c5\u5fe7\u3002</p><p>\u201c\u90a3\u5012\u53ef\u4ee5\u653e\u5fc3\uff0c\u5723\u8005\u964d\u4e34\u53ea\u53ef\u80fd\u5728\u7269\u8d28\u754c\u53d1\u751f\u3002\u201d\u8475\u9732\u8bf4\u3002</p><p>\u743c\u6069\u8038\u8038\u80a9\uff0c\u201c\u90a3\u5c31\u5e26\u4e0a\u5427\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u5c0f\u5fc3\u770b\u7740\u5979\u5c31\u662f\u3002\u201d</p><p>\u201c\u6216\u8bb8\u5979\u8fd8\u80fd\u5e2e\u4e0a\u5fd9\u3002\u201d\u6885\u83f2\u65af\u7a81\u7136\u8bf4\u3002</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u5979\u5e74\u9f84\u5f88\u5c0f\uff0c\u6700\u591a\u4e5f\u4e0d\u4f1a\u8d85\u8fc7\u4e8c\u5341\u5c81\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u800c\u6309\u7167\u5353\u5c14\u7684\u89c4\u77e9\uff0c\u5973\u6027\u8981\u5728\u56db\u5341\u5c81\u4e4b\u540e\u624d\u80fd\u63a5\u53d7\u796d\u53f8\u6559\u80b2\uff0c\u6b63\u5e38\u60c5\u51b5\u4e0b\u4e5d\u5341\u5c81\u65f6\u6bd5\u4e1a\u2014\u2014\u662f\u8fd9\u6837\u7684\u5427\u3002\u201d</p><p>\u201c\u662f\u3002\u201d\u8475\u9732\u70b9\u5934\u3002</p><p>\u201c\u4e5f\u5c31\u662f\u8bf4\uff0c\u4ee5\u5e38\u7406\u63a8\u6d4b\uff0c\u5979\u80af\u5b9a\u8fd8\u4e0d\u662f\u7f57\u4e1d\u7684\u796d\u7940\uff0c\u6240\u4ee5\u6211\u4eec\u5f53\u65f6\u4e5f\u5c31\u758f\u5ffd\u4e86\uff0c\u4f46\u73b0\u5728\u770b\u6765\uff0c\u5979\u5fc5\u5b9a\u662f\u7f57\u4e1d\u7684\u4fe1\u5f92\uff0c\u800c\u4e14\u4e0d\u662f\u90a3\u79cd\u6cdb\u4fe1\u5f92\u3002\u201d</p><p>\u201c\u662f\u8fd9\u6837\u6ca1\u9519\u3002\u201d</p><p>\u201c\u5176\u5b9e\u6211\u4eec\u5ffd\u7565\u4e86\u4e00\u4ef6\u4e8b\uff0c\u5979\u786e\u5b9e\u4e0d\u662f\u796d\u53f8\uff0c\u4f46\u7f57\u4e1d\u6559\u4f1a\u4e2d\u7684\u5973\u6027\uff0c\u5e76\u975e\u4e00\u5b9a\u90fd\u662f\u796d\u53f8\uff0c\u201d\u6885\u83f2\u65af\u8f7b\u58f0\u8bf4\uff0c\u201c\u8fd8\u6709\u2018\u6bd2\u523a\u2019\u3002\u201d</p><p>\u7f57\u4e1d\u796d\u53f8\u5168\u90fd\u662f\u5973\u6027\uff0c\u4f46\u53cd\u8fc7\u6765\u5e76\u4e0d\u662f\u6240\u6709\u5973\u6027\u90fd\u4f1a\u6210\u4e3a\u796d\u53f8\uff0c\u4e5f\u4f1a\u6709\u6781\u5c11\u6570\u7279\u4f8b\uff0c\u9009\u62e9\u53e6\u5916\u4e00\u79cd\u6295\u8eab\u4fe1\u4ef0\u7684\u65b9\u5f0f\uff0c\u6210\u4e3a\u9ad8\u660e\u7684\u6740\u624b\u548c\u7a83\u8d3c\uff0c\u9690\u6ca1\u5728\u9ed1\u6697\u4e2d\uff0c\u4e3a\u795e\u540e\u548c\u4e3b\u6bcd\u4eec\u6548\u52b3\uff0c\u5979\u4eec\u88ab\u79f0\u4e3a\u201c\u6bd2\u523a\u201d\u3002\u796d\u53f8\u662f\u8981\u5728\u5fc3\u667a\u8db3\u591f\u6210\u719f\u4e4b\u540e\u624d\u5f00\u59cb\u57f9\u517b\u7684\uff0c\u4f46\u6bd2\u523a\u5374\u4e0d\u540c\uff0c\u5f80\u5f80\u4ece\u5b69\u63d0\u65f6\u4ee3\uff0c\u8eab\u4f53\u548c\u7075\u9b42\u4e24\u65b9\u9762\u7684\u6559\u80b2\u5c31\u5df2\u7ecf\u5f00\u59cb\u3002</p><p>\u5982\u679c\u6ca1\u731c\u9519\u7684\u8bdd\uff0c\u4f0a\u8389\u96c5\u5c31\u662f\u4e00\u679a\u201c\u6bd2\u523a\u201d\u3002</p><p>\u94f6\u94c3\u822c\u7684\u6e05\u8106\u7b11\u58f0\u7a81\u7136\u54cd\u8d77\uff0c\u539f\u672c\u4e00\u76f4\u201c\u6655\u8ff7\u201d\u7684\u5c0f\u5973\u5b69\u5750\u8d77\u8eab\u6765\uff0c\u8138\u4e0a\u6302\u7740\u751c\u751c\u7b11\u5bb9\uff0c\u770b\u7740\u88ab\u743c\u6069\u62b1\u5728\u6000\u91cc\u7684\u6885\u83f2\u65af\uff0c\u201c\u827e\u5f25\u8587\u59d0\u59d0\u771f\u806a\u660e\u5462\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4e00\u4e0b\u5b50\u5c31\u731c\u51fa\u4eba\u5bb6\u8eab\u4efd\u4e86\u3002\u201d</p><p>\u6885\u83f2\u65af\u5fae\u5fae\u4e00\u7b11\uff0c\u201c\u59d0\u59d0\u5982\u679c\u771f\u806a\u660e\uff0c\u65e9\u5c31\u5e94\u8be5\u60f3\u5230\u4e86\uff0c\u4e5f\u4e0d\u7528\u7b49\u5230\u73b0\u5728\u3002\u4f60\u600e\u4e48\u5728\u8fd9\u91cc\uff1f\u201d</p><p>\u201c\u4f60\u4eec\u90fd\u9003\u4e86\uff0c\u795e\u540e\u4e5f\u5c31\u56de\u53bb\u4e86\uff0c\u6211\u6655\u8fc7\u53bb\u4e86\uff0c\u201d\u4f0a\u8389\u96c5\u6241\u6241\u5634\uff0c\u201c\u7b49\u9192\u8fc7\u6765\u5c31\u53d1\u73b0\u5230\u4e86\u8fd9\u91cc\uff0c\u7136\u540e\u5929\u4e0a\u4e00\u7fa4\u6076\u9b54\u6251\u4e0b\u6765\uff0c\u6211\u8ddf\u5b83\u4eec\u6253\u554a\u6253\u2026\u2026\u7136\u540e\u4f60\u4eec\u5c31\u6765\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u91cc\u662f\u65e0\u5c3d\u6df1\u6e0a\u7684\u7b2c\u4e00\u5c42\uff0c\u4e07\u6e0a\u5e73\u539f\u3002\u201d</p><p>\u201c\u54e6\u3002\u201d</p><p>\u4f0a\u8389\u96c5\u6709\u4e9b\u604d\u7136\u5730\u70b9\u70b9\u5934\uff0c\u201c\u602a\u4e0d\u5f97\u5462\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6211\u6b63\u5947\u602a\u8fd9\u5929\u4e0a\u7684\u706b\u7403\u597d\u50cf\u4f24\u5bb3\u4e0d\u5230\u6211\u3002\u201d</p><p>\u5353\u5c14\u4f1a\u88ab\u660e\u4eae\u7684\u9633\u5149\u4f24\u5bb3\uff0c\u7136\u800c\u4e07\u6e0a\u5e73\u539f\u8fd9\u4e2a\u201c\u592a\u9633\u201d\u548c\u7269\u8d28\u754c\u7684\u592a\u9633\u5b8c\u5168\u4e24\u7801\u4e8b\uff0c\u5bf9\u8475\u9732\u548c\u4f0a\u8389\u96c5\u90fd\u6ca1\u4ec0\u4e48\u5f71\u54cd\u3002\u4e5f\u5e78\u4e8f\u5982\u6b64\uff0c\u5426\u5219\u5c11\u4e86\u8475\u9732\u8fd9\u4e2a\u5f3a\u52a9\uff0c\u4ee5\u540e\u7684\u8def\u5c31\u66f4\u96be\u8d70\u4e86\u3002</p><p>\u201c\u743c\u6069\u54e5\u54e5\uff0c\u4f60\u4eec\u662f\u8981\u56de\u5bb6\u5417\uff0c\u5e26\u4e0a\u6211\u597d\u4e0d\u597d\u554a\uff0c\u201d\u4f0a\u8389\u96c5\u8e66\u8e66\u8df3\u8df3\u5730\u8d34\u8fc7\u6765\uff0c\u201c\u6211\u4fdd\u8bc1\u4e56\u4e56\u7684\uff0c\u4fdd\u8bc1\u4e0d\u6dd8\u6c14\u2026\u2026\u201d</p><p>\u743c\u6069\u628a\u8138\u677f\u8d77\u6765\uff0c\u201c\u4f60\u5148\u4fdd\u8bc1\u4e56\u4e56\u542c\u8bdd\u518d\u8bf4\u3002\u201d</p><p>\u201c\u55ef\uff01\u201d\u5c0f\u841d\u8389\u7528\u529b\u70b9\u5934\uff0c\u4ece\u80cc\u540e\u53d8\u9b54\u672f\u4e00\u822c\u62ff\u51fa\u4e00\u4e2a\u65b9\u65b9\u6b63\u6b63\u7684\u4e1c\u897f\u6765\uff0c\u201c\u54e5\u54e5\uff0c\u8fd9\u662f\u4f60\u7684\u8428\u74e6\u68cb\u54e6\uff0c\u6211\u5e2e\u4f60\u62e3\u8d77\u6765\u4e86\uff0c\u8fd8\u7ed9\u4f60\u3002\u201d</p><p>\u8fd9\u526f\u8428\u74e6\u68cb\u867d\u7136\u9887\u6709\u95ee\u9898\uff0c\u6b27\u51ef\u660e\u663e\u4e5f\u522b\u6709\u7528\u610f\uff0c\u4f46\u4e0d\u7ba1\u600e\u4e48\u8bf4\u603b\u662f\u6551\u4e86\u81ea\u5df1\u4e00\u547d\uff0c\u800c\u4e14\u68cb\u5b50\u9b54\u50cf\u4e5f\u5f88\u597d\u7528\uff0c\u80fd\u591f\u5931\u800c\u590d\u5f97\uff0c\u603b\u662f\u4ee4\u4eba\u9ad8\u5174\u7684\u3002\u65e2\u7136\u5982\u6b64\uff0c\u743c\u6069\u4e5f\u5c31\u4e0d\u518d\u591a\u8bf4\u4ec0\u4e48\uff0c\u4e00\u884c\u4eba\u7ee7\u7eed\u4e0a\u8def\u3002</p><p>\u867d\u7136\u4eba\u6570\u589e\u52a0\u5230\u4e86\u4e94\u4e2a\uff0c\u4f46\u6548\u7387\u5e76\u6ca1\u6709\u56e0\u6b64\u800c\u63d0\u9ad8\uff0c\u4f9d\u65e7\u662f\u8475\u9732\u8d70\u5728\u6700\u524d\u9762\uff0c\u5176\u4ed6\u4eba\u968f\u540e\uff0c\u4f0a\u8389\u96c5\u8e51\u624b\u8e51\u811a\u5730\u5c06\u8eab\u4f53\u65f6\u523b\u9690\u85cf\u5728\u743c\u6069\u7684\u5f71\u5b50\u91cc\u3002\u201c\u4f60\u8fd9\u662f\u5e72\u4ec0\u4e48\uff1f\u201d\u963f\u5fd2\u59ae\u5947\u602a\u5730\u95ee\u3002</p><p>\u201c\u8fd9\u6837\u6bd4\u8f83\u51c9\u5feb\u554a\uff0c\u201d\u4f0a\u8389\u96c5\u7406\u76f4\u6c14\u58ee\u5730\u56de\u7b54\uff0c\u201c\u5929\u6c14\u5f88\u70ed\u561b\u3002\u201d</p><p>\u53bb\u6389\u8fd9\u79cd\u65e0\u610f\u4e49\u7684\u5bf9\u8bdd\uff0c\u5728\u63a5\u4e0b\u6765\u7684\u4e09\u4e2a\u5c0f\u65f6\u91cc\u662f\u4e00\u65e0\u6240\u83b7\u3002\u539f\u672c\u7684\u6253\u7b97\u662f\u80fd\u649e\u4e0a\u4e00\u4f4d\u9ad8\u9636\u6076\u9b54\uff0c\u80fd\u591f\u4ea4\u6d41\uff0c\u903c\u8feb\u6216\u8005\u529d\u8bf4\u5b83\u5e26\u9886\u5927\u5bb6\u53bb\u65ad\u57df\u9547\u3002\u7136\u800c\u8d70\u4e86\u8fd9\u4e48\u4e45\uff0c\u9664\u4e86\u5f17\u6d1b\u9b54\u8fd8\u662f\u5f17\u6d1b\u9b54\uff0c\u5176\u4ed6\u6076\u9b54\u534a\u4e2a\u90fd\u6ca1\u89c1\u5230\u2014\u2014\u800c\u5f17\u6d1b\u9b54\u662f\u4e0d\u4f1a\u7269\u8d28\u754c\u8bed\u8a00\u7684\u3002</p><p>\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u201d\u8475\u9732\u4e5f\u5fcd\u4e0d\u4f4f\u62b1\u6028\u8d77\u6765\uff0c\u201c\u4e0d\u662f\u8bf4\u65e0\u5c3d\u6df1\u6e0a\u91cc\u7684\u6076\u9b54\u591a\u5f97\u6570\u4e0d\u6e05\u5417\uff1f\u201d</p><p>\u201c\u6216\u8bb8\u4ed6\u4eec\u805a\u4f17\u8d4c\u535a\u53bb\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u7b11\u8bdd\u5e76\u4e0d\u597d\u7b11\u3002\u201d\u8475\u9732\u6709\u4e9b\u4e0d\u9ad8\u5174\u3002</p><p>\u201c\u672a\u5fc5\u662f\u7b11\u8bdd\uff0c\u201d\u6885\u83f2\u65af\u8f7b\u58f0\u6253\u65ad\uff0c\u201c\u8d4c\u535a\u81ea\u7136\u672a\u5fc5\uff0c\u4f46\u6076\u9b54\u4eec\u88ab\u53ec\u96c6\u5230\u67d0\u4e2a\u5730\u65b9\uff0c\u8fd9\u662f\u5f88\u6709\u53ef\u80fd\u7684\u4e8b\u60c5\u3002\u201d</p><p>\u8475\u9732\u6014\u4e86\u6014\uff0c\u968f\u5373\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u8840\u6218\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u6b27\u51ef\u4e0d\u662f\u8bf4\uff0c\u4e07\u6e0a\u5e73\u539f\u76f8\u5bf9\u6765\u8bf4\u662f\u6df1\u6e0a\u4e2d\u6700\u5b89\u5168\u7684\u5730\u65b9\u2014\u2014\u524d\u63d0\u662f\u6076\u9b54\u6ca1\u6709\u548c\u9b54\u9b3c\u4eec\u5f00\u6218\u3002\u201d</p><p>\u201c\u6211\u4eec\u4e0d\u4f1a\u8fd9\u4e48\u4e0d\u5e78\u5427\u3002\u201d\u963f\u5fd2\u59ae\u4f4e\u58f0\u5600\u5495\u3002</p><p>\u201c\u8fd9\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u4ece\u6765\u90fd\u662f\u8d8a\u5bb3\u6015\u4ec0\u4e48\uff0c\u8d8a\u53d1\u751f\u4ec0\u4e48\u554a\u3002\u201d</p><p>\u8bf8\u4eba\u7684\u60c5\u7eea\u90fd\u6709\u4e9b\u4f4e\u843d\uff0c\u6885\u83f2\u65af\u867d\u7136\u662f\u731c\u6d4b\uff0c\u5374\u662f\u5f88\u6709\u53ef\u80fd\u53d1\u751f\u7684\u4e8b\u60c5\uff0c\u53ea\u80fd\u5e0c\u671b\u522b\u8fd9\u4e48\u5012\u9709\u4e86\u3002\u8d70\u4e86\u534a\u5929\uff0c\u5927\u5bb6\u4e5f\u90fd\u6709\u4e9b\u75b2\u5026\uff0c\u627e\u4e86\u4e2a\u907f\u98ce\u7684\u5730\u65b9\u4f11\u606f\u3002\u8475\u9732\u9760\u7740\u77f3\u58c1\u95ed\u76ee\u9ed8\u5750\uff0c\u6062\u590d\u7cbe\u795e\uff0c\u963f\u5fd2\u59ae\u64e6\u62ed\u7740\u81ea\u5df1\u7684\u77ed\u5f13\uff0c\u743c\u6069\u4f9d\u65e7\u628a\u6885\u83f2\u65af\u62b1\u5728\u6000\u91cc\uff0c\u66ff\u5979\u64e6\u53bb\u989d\u5934\u4e0a\u7684\u6c57\u6c34\u3002\u4f0a\u8389\u96c5\u5012\u662f\u4ecd\u65e7\u6d3b\u6cfc\u5f97\u5f88\uff0c\u5143\u6c14\u5341\u8db3\uff0c\u770b\u8d77\u6765\u4e94\u4e2a\u4eba\u4e2d\uff0c\u5979\u662f\u53d7\u4f4d\u9762\u89c4\u5219\u538b\u5236\u6700\u5c0f\u7684\u2014\u2014\u751a\u81f3\u6839\u672c\u5c31\u6ca1\u6709\u88ab\u538b\u5236\u3002</p><p>\u201c\u827e\u5f25\u8587\u59d0\u59d0\uff0c\u4f60\u4e3a\u4ec0\u4e48\u4e00\u76f4\u8ba9\u743c\u6069\u54e5\u54e5\u62b1\u7740\u5440\u3002\u201d\u5979\u95ee\u3002</p><p>\u6885\u83f2\u65af\u52c9\u5f3a\u7b11\u7b11\uff0c\u201c\u59d0\u59d0\u4e0d\u592a\u8212\u670d\u3002\u201d</p><p>\u201c\u751f\u75c5\u4e86\uff1f\u201d</p><p>\u201c\u5dee\u4e0d\u591a\u5427\u3002\u201d</p><p>\u201c\u90a3\u8d76\u5feb\u627e\u4eba\u6cbb\u7597\u554a\u3002\u201d</p><p>\u743c\u6069\u77aa\u4e86\u5979\u4e00\u773c\uff0c\u5018\u82e5\u4e0d\u662f\u4f0a\u8389\u96c5\uff0c\u4ed6\u4eec\u4f55\u81f3\u4e8e\u843d\u5230\u8fd9\u79cd\u5730\u6b65\uff0c\u6885\u83f2\u65af\u66f4\u4e0d\u4f1a\u5982\u6b64\u865a\u5f31\u3002\u4f0a\u8389\u96c5\u4f3c\u4e4e\u4e5f\u77e5\u9053\u81ea\u5df1\u8bf4\u9519\u4e86\u8bdd\uff0c\u5410\u4e86\u5410\u820c\u5934\uff0c\u4e0d\u6562\u518d\u591a\u95ee\u3002\u201c\u54e5\u54e5\uff0c\u628a\u4f60\u90a3\u526f\u671b\u8fdc\u955c\u501f\u6211\u73a9\u73a9\u3002\u201d</p><p>\u201c\u4f60\u8981\u5b83\u5e72\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6211\u5230\u9ad8\u5904\u770b\u770b\u98ce\u666f\u3002\u201d</p><p>\u743c\u6069\u968f\u624b\u628a\u671b\u8fdc\u955c\u4e22\u8fc7\u53bb\uff0c\u4f0a\u8389\u96c5\u722c\u5230\u65c1\u8fb9\u7684\u5c71\u5cf0\u4e0a\u53bb\u4e86\u3002\u201c\u4f60\u5bf9\u5979\u522b\u90a3\u4e48\u51f6\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u53ea\u662f\u4e2a\u5b69\u5b50\u7f62\u4e86\u3002\u201d</p><p>\u201c\u5b69\u5b50\uff1f\u5982\u679c\u5929\u4e0b\u7684\u5b69\u5b50\u90fd\u8fd9\u4e48\u5371\u9669\uff0c\u90a3\u4e16\u754c\u65e9\u5c31\u5d29\u6e83\u4e86\u3002\u201d</p><p>\u201c\u4ee5\u5353\u5c14\u7684\u6807\u51c6\u6765\u770b\uff0c\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\u3002\u201d</p><p>\u743c\u6069\u4e0d\u60f3\u5c31\u8fd9\u4e2a\u8bdd\u9898\u548c\u5979\u4e89\u6267\uff0c\u201c\u6211\u77e5\u9053\u4e86\uff0c\u201d\u4ed6\u67d4\u58f0\u8bf4\uff0c\u201c\u4f60\u5148\u4f11\u606f\uff0c\u7761\u4e00\u4f1a\u5427\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u6885\u83f2\u65af\u75b2\u5026\u5730\u95ed\u4e0a\u773c\u775b\uff0c\u6b63\u5feb\u8981\u5165\u7761\uff0c\u4f0a\u8389\u96c5\u7a81\u7136\u6025\u5306\u5306\u5730\u4ece\u5c71\u5cf0\u4e0a\u4e00\u8def\u8e66\u8df3\u4e0b\u6765\uff0c\u8dd1\u5230\u8ddf\u524d\uff0c\u201c\u54e5\u54e5\uff01\u54e5\u54e5\uff01\u201d</p><p>\u5979\u5927\u547c\u5c0f\u53eb\u7740\uff0c\u628a\u6885\u83f2\u65af\u90fd\u5f04\u9192\u4e86\uff0c\u743c\u6069\u6709\u4e9b\u4e0d\u5feb\uff0c\u201c\u4ec0\u4e48\u4e8b\uff1f\u201d\u4ed6\u95ee\u3002</p><p>\u201c\u6211\u770b\u5230\u6709\u51e0\u4e2a\u4eba\u5f80\u8fd9\u8fb9\u8fc7\u6765\u4e86\u3002\u201d</p><p>\u743c\u6069\u6014\u4e86\u6014\uff0c\u65c1\u8fb9\u7684\u8475\u9732\u5df2\u7ecf\u7ad9\u8d77\u8eab\u6765\uff0c\u4ece\u4f0a\u8389\u96c5\u624b\u4e2d\u53d6\u8fc7\u671b\u8fdc\u955c\uff0c\u5feb\u6b65\u8dc3\u4e0a\u65c1\u8fb9\u7684\u5c0f\u5c71\u5cf0\uff0c\u201c\u662f\u6709\u51e0\u4e2a\u4eba\u2026\u2026\u4e0d\u5bf9\uff0c\u662f\u51e0\u4e2a\u6076\u9b54\u3002\u201d</p></article>"}}}]);
