(window.webpackJsonp=window.webpackJsonp||[]).push([[307],{330:function(p){p.exports={chapterTitle:null,title:"\u7b2c188\u7ae0 \u5c0f\u9152\u9986\u5927\u4f5c\u4e3a",content:"<article><h2>\u7b2c188\u7ae0 \u5c0f\u9152\u9986\u5927\u4f5c\u4e3a</h2><p>\u3000\u3000\u65e0\u8bba\u4ee4\u5c0f\u71d5\u3001\u6797\u51b0\u5979\u4eec\u600e\u6837\u53d6\u7b11\uff0c\u6c5f\u9edb\u513f\u8fd8\u662f\u63a5\u53d7\u5f20\u606a\u7684\u5b89\u6392\uff0c\u5148\u5b89\u5fc3\u5728\u98de\u9e3f\u5e7f\u544a\u5e2e\u7231\u8fbe\u7535\u5b50\u62cd\u6444\u5e73\u9762\u5ba3\u4f20\u56fe\u7247\uff0c\u6691\u671f\u91cc\u914d\u5408\u7231\u8fbe\u7535\u8fbe\u5546\u4e1a\u5ba3\u4f20\u6d3b\u52a8\u4e4b\u4f59\u518d\u8fdb\u884c\u4e00\u4e9b\u97f3\u4e50\u4e0e\u5f62\u4f53\u4e0a\u7684\u57f9\u8bad\uff0c\u8bb8\u7ef4\u6700\u7ec8\u53ea\u80fd\u63a5\u53d7\u9edb\u513f\u79c1\u4eba\u52a9\u7406\u7684\u89d2\u8272\u3002</p><p>\u3000\u3000\u603b\u4e4b\uff0c\u4ee3\u8a00\u7684\u8d39\u7528\u867d\u7136\u6709\u516b\u4e07\u5143\uff0c\u4f46\u662f\u5f20\u606a\u4e00\u53e5\u201c\u7ecf\u7eaa\u4eba\u8981\u5bf9\u5f00\u652f\u7528\u5ea6\u6709\u7edf\u7b79\u201d\u7684\u8bdd\uff0c\u5c06\u6c5f\u9edb\u513f\u3001\u8bb8\u7ef4\u7684\u751f\u6d3b\u524a\u51cf\u5230\u6bcf\u6708\u516d\u767e\u5143\uff0c\u4e00\u53e5\u201c\u9edb\u513f\u59d0\u8fd8\u6ca1\u6709\u51fa\u9053\u201d\u7684\u8bdd\uff0c\u5c06\u670d\u88c5\u8d39\u524a\u51cf\u6389\uff0c\u8bf4\u4ec0\u4e48\u201c\u672a\u6210\u540d\u5c31\u4e0d\u80fd\u4eab\u53d7\u77e5\u540d\u827a\u4eba\u7684\u5f85\u9047\u201d\uff0c\u8ba9\u6c5f\u9edb\u513f\u5148\u53bb\u62a5\u540d\u53c2\u52a0\u90a3\u4e9b\u5927\u4f17\u5316\u7684\u57f9\u8bad\u73ed\uff0c\u57f9\u8bad\u8d39\u8282\u7ea6\u4e86\u4e00\u5927\u622a\uff0c\u8ba9\u4eba\u6000\u7591\u4e00\u5e74\u80fd\u4e0d\u80fd\u7528\u6389\u516b\u4e07\u5143\u4ee3\u8a00\u8d39\u7684\u4e09\u5206\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b2c\u4e09\u65e5\u624d\u4e0e\u5085\u4fca\u5982\u671f\u8fd4\u56de\u6d77\u5dde\uff0c\u4e3a\u4e86\u88c5\u6a21\u4f5c\u6837\u7ed9\u5bb6\u91cc\u4ea4\u5f85\uff0c\u8fd4\u56de\u6d77\u5dde\u5c31\u7559\u5728\u5bcc\u8d35\u56ed\u5c0f\u533a\u7684\u4f4f\u5904\u6e29\u4e66\uff0c\u867d\u7136\u8bfe\u672c\u7684\u5185\u5bb9\u90fd\u6709\u5b66\u4e60\uff0c\u4f46\u662f\u53ea\u82b1\u4e24\u4e09\u5929\u7684\u65f6\u95f4\u6e29\u4e60\u8fdc\u8fdc\u4e0d\u591f\uff0c\u516d\u6708\u4e09\u5341\u65e5\u8003\u5b8c\u8bd5\uff0c\u5510\u5a67\u95ee\u4ed6\u7684\u8003\u8bd5\u611f\u89c9\u5982\u4f55\uff0c\u5f20\u606a\u53ea\u80fd\u56de\u7b54\u8bf4\uff1a\u201c\u8fd8\u884c\uff0c\u611f\u89c9\u4e0d\u6bd4\u671f\u4e2d\u8003\u8bd5\u96be\u3002\u201d\u4ed6\u5fd8\u8bb0\u8fd9\u5b66\u671f\u7684\u671f\u4e2d\u8003\u8bd5\u4ed6\u7f3a\u5e2d\u4ed6\u6700\u64c5\u957f\u7684\u82f1\u8bed\u79d1\u8003\u8bd5\uff0c\u8003\u5206\u5f53\u4ec1\u4e0d\u8ba9\u7684\u8749\u8054\u6587\u79d1\u5e74\u7ea7\u5012\u6570\u7b2c\u4e00\uff0c\u5012\u4e0d\u5760\u4ed6\u56db\u5927\u516c\u5b50\u7684\u540d\u5934\u3002</p><p>\u3000\u3000\u5510\u5a67\u89c1\u4ed6\u8fd9\u6837\u56de\u7b54\uff0c\u5c31\u5927\u611f\u5934\u75bc\uff0c\u8fd8\u4e0d\u5fd8\u529d\u6170\u4ed6\u8bf4\uff1a\u201c\u53cd\u6b63\u5927\u5bb6\u5bf9\u4f60\u7684\u6210\u7ee9\u4e5f\u6ca1\u4ec0\u4e48\u671f\u5f85\uff0c\u4f60\u4e0d\u8981\u592a\u62c5\u5fc3\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u6691\u5047\u600e\u4e48\u5b89\u6392\uff1f\u201d\u5e73\u65e5\u91cd\u70b9\u9ad8\u4e2d\u7684\u5b66\u4e60\u4efb\u52a1\u7e41\u91cd\uff0c\u6bcf\u4e2a\u5b66\u751f\u7684\u4e1a\u4f59\u65f6\u95f4\u5dee\u4e0d\u591a\u90fd\u7ed9\u6324\u5360\u4e86\u7528\u53bb\u5b66\u4e60\uff0c\u7279\u522b\u662f\u5510\u5a67\u8fd9\u6837\u8ba9\u5927\u5bb6\u671f\u5f85\u7740\u3001\u6027\u5b50\u53c8\u597d\u5f3a\u3001\u52a0\u4e0a\u53c8\u662f\u73ed\u957f\u7684\u597d\u5b69\u5b50\uff0c\u51e0\u4e4e\u6ca1\u6709\u65f6\u95f4\u7f20\u7740\u5f20\u606a\uff0c\u4f46\u662f\u6f2b\u957f\u7684\u6691\u5047\u603b\u4e0d\u80fd\u5c06\u5510\u5a67\u5b8c\u5168\u4e22\u4e0b\u4e0d\u7ba1\u3002</p><p>\u3000\u3000\u201c\u5509\uff0c\u9664\u4e86\u6691\u671f\u8865\u8bfe\u4e4b\u5916\uff0c\u6211\u5988\u8fd8\u7ed9\u6211\u62a5\u4e86\u5f88\u591a\u57f9\u8bad\u73ed\uff0c\u5979\u5012\u4e0d\u8003\u8651\u5979\u5973\u513f\u80fd\u4e0d\u80fd\u627f\u53d7\uff01\u201d</p><p>\u3000\u3000\u8fd9\u4e2a\u590f\u5929\u4ee5\u534e\u4e1c\u3001\u534e\u5357\u5730\u533a\u7684\u5e02\u573a\u4e3a\u91cd\uff0c\u5f20\u606a\u6ce8\u5b9a\u8981\u5954\u6ce2\u4e0d\u6b47\uff0c\u634e\u4e0a\u5510\u5a67\u5012\u4e0d\u662f\u4e0d\u53ef\u4ee5\uff0c\u5c31\u6015\u5979\u5403\u4e0d\u4e86\u8fd9\u82e6\uff0c\u8ba9\u987e\u5efa\u840d\u627e\u6765\u8fd9\u4e48\u591a\u57f9\u8bad\u73ed\u7f20\u7740\u5979\u4e5f\u597d\uff0c\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u6ca1\u6709\u591a\u8bf4\u4ec0\u4e48\uff0c\u95ee\u5510\u5a67\uff1a\u201c\u4e0a\u6b21\u8ba9\u4f60\u5e2e\u5fd9\u641e\u7684\u90a3\u4e2a\u65b0\u4ea7\u54c1\u65b0\u95fb\u53d1\u5e03\u4f1a\u7684\u7b56\u5212\u7a3f\uff0c\u6574\u51fa\u6765\u6ca1\u6709\uff1f\u201d</p><p>\u3000\u3000\u5510\u5a67\u5c06\u968f\u8eab\u7684\u630e\u5305\u6253\u5f00\uff0c\u62ff\u51fa\u4e00\u53e0\u7eb8\u9012\u7ed9\u5f20\u606a\uff1a\u201c\u56e0\u4e3a\u641e\u8fd9\u4e2a\u8981\u662f\u8003\u8bd5\u6709\u9000\u6b65\uff0c\u90a3\u5c31\u60e8\u4e86\u3002\u201d</p><p>\u3000\u3000\u7231\u8fbe\u63a8\u51fa\u7684\u7b2c\u4e00\u6b3e\u5f71\u789f\u673a\u4e0a\u5e02\uff0c\u7531\u76db\u946b\u90a3\u8fb9\u8d1f\u8d23\u7ec4\u7ec7\u65b0\u4ea7\u54c1\u4e0a\u5e02\u65b0\u95fb\u53d1\u5e03\u4f1a\uff0c\u76db\u946b\u6709\u7231\u7acb\u4fe1398\u4e0a\u5e02\u63a8\u4ecb\u7684\u6210\u529f\u7ecf\u9a8c\uff0c\u7701\u57ce\u90a3\u8fb9\u7684\u5a92\u4f53\u90fd\u4e50\u610f\u8ddf\u4ed6\u4eec\u5408\u4f5c\uff0c\u6d3b\u52a8\u4ea4\u7ed9\u76db\u946b\u8d1f\u8d23\uff0c\u8ba9\u4eba\u653e\u5fc3\uff0c\u5f20\u606a\u53ea\u5b89\u6392\u5218\u660e\u8f89\u914d\u5408\u4ed6\u4eec\uff0c\u6b64\u65f6\u53ea\u9700\u8981\u5c3d\u91cf\u591a\u7684\u8054\u7cfb\u6f5c\u5728\u7684\u7ecf\u9500\u5546\u5230\u4f1a\u5c31\u53ef\u4ee5\u4e86\u3002</p><p>\u3000\u3000\u8ba9\u5510\u5a67\u505a\u540c\u6837\u7684\u7b56\u5212\u6848\uff0c\u53ea\u662f\u8ba9\u5979\u6709\u673a\u4f1a\u953b\u70bc\u4e00\u4e0b\uff0c\u4e0d\u7136\u65e0\u7f18\u65e0\u6545\u7684\u5c06\u5979\u4e22\u5230\u4e00\u8fb9\uff0c\u8c01\u6653\u5f97\u5979\u5fc3\u91cc\u6709\u591a\u5c11\u6028\u8a00\uff1f\u5f20\u606a\u63a5\u8fc7\u90a3\u53e0\u7eb8\uff0c\u7ffb\u770b\u4e86\u4e00\u904d\uff0c\u5510\u5a67\u5bf9\u8fd9\u4e8b\u8fd8\u76f8\u5f53\u4e0a\u5fc3\uff0c\u8fd8\u771f\u62c5\u5fc3\u4f1a\u803d\u6401\u5979\u7684\u5b66\u4e60\uff1a\u201c\u55ef\uff0c\u6bd4\u516c\u53f8\u90a3\u4e9b\u4e13\u95e8\u505a\u8fd9\u4e2a\u7684\u4eba\u8fd8\u5468\u5168\uff0c\u6211\u5148\u770b\u4e00\u4e0b\uff0c\u518d\u8ba9\u4ed6\u4eec\u62ff\u53bb\u53c2\u8003\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u80fd\u53c2\u52a0\u65b0\u95fb\u53d1\u5e03\u4f1a\u5417\uff1f\u201d</p><p>\u3000\u3000\u5510\u5a67\u957f\u7709\u4fee\u76f4\uff0c\u773c\u7738\u5b50\u53c8\u957f\u53c8\u5a9a\uff0c\u6e05\u6f88\u7684\u773c\u795e\u5374\u5929\u7136\u6709\u80a1\u5b50\u9b45\u60d1\u52b2\u513f\uff0c\u8ba9\u5979\u5b9a\u775b\u770b\u7740\uff0c\u5f20\u606a\u54ea\u5fcd\u5fc3\u62d2\u7edd\uff1a\u201c\u53d1\u5e03\u4f1a\u5728\u7701\u57ce\u4e3e\u884c\uff0c\u4e5f\u4e0d\u662f\u5f88\u8fdc\uff0c\u5230\u65f6\u5019\u6211\u8ba9\u4eba\u5f00\u8f66\u6765\u63a5\u4f60\u3002\u4f60\u4e0d\u662f\u4e5f\u8981\u770b\u770b\u82b7\u5f64\u5417\uff1f\u5230\u65f6\u5019\u5c31\u4f4f\u5f90\u4f2f\u4f2f\u5bb6\u91cc\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u5c31\u8fd9\u6837\u8bf4\u5b9a\u4e86\uff0c\u4f60\u53ef\u4e0d\u8981\u723d\u7ea6\u554a\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u54ea\u6562\u723d\u5510\u5a67\u7684\u7ea6\uff0c\u4e0d\u8fc7\u5fc3\u91cc\u60f3\u7740\u665a\u4e0a\u8fd8\u8981\u53bb\u89c1\u8bb8\u601d\uff0c\u8ddf\u5510\u5a67\u8bf4\u8bdd\u7684\u5e95\u6c14\u5c31\u6ca1\u6709\u90a3\u4e48\u5145\u8db3\u3002\u4eca\u5929\u8bb8\u601d\u5bb6\u91cc\u642c\u5bb6\uff0c\u8bb8\u7ef4\u8fd8\u5f97\u610f\u4ece\u7701\u57ce\u8d76\u56de\u6765\u3002</p><p>\u3000\u3000\u8bb8\u601d\u5bb6\u623f\u5b50\u7684\u4e8b\u60c5\uff0c\u7531\u5979\u5c0f\u8205\u5728\u4e2d\u95f4\u7b79\u8c0b\uff0c\u4e09\u56db\u5929\u5c31\u529e\u59a5\u5f53\uff0c\u5f20\u606a\u6258\u90b5\u81f3\u521a\u4ee3\u8868\u516c\u53f8\u51fa\u9762\uff0c\u5c06\u8bb8\u601d\u5bb6\u90a3\u680b\u5b85\u5b50\u4e70\u4e0b\u6765\u3002\u5f53\u65f6\u5e02\u91cc\u7ed9\u51fa\u7684\u62c6\u8fc1\u5b89\u7f6e\u6807\u51c6\u662f\u6709\u6548\u5efa\u7b51\u9762\u79ef\u4ee5\u6bcf\u5e73\u65b9\u7c73\u516b\u767e\u5143\u8865\u507f\uff0c\u8bb8\u601d\u5bb6\u7684\u6709\u6548\u5efa\u7b51\u9762\u79ef\u662f\u4e09\u95f4\u6b63\u5c4b\u52a0\u4e0a\u4e00\u95f4\u4f5c\u53a8\u623f\u7684\u8033\u623f\uff0c\u5dee\u4e0d\u591a\u4e00\u767e\u4e2a\u65b9\uff0c\u65e2\u7136\u8981\u5e2e\u8bb8\u601d\u5bb6\uff0c\u5c31\u8981\u7b97\u7cca\u6d82\u8d26\uff0c\u8bb8\u601d\u5c0f\u8205\u5c06\u8bb8\u601d\u5bb6\u9662\u5b50\u4e5f\u8ba1\u7b97\u5728\u5185\uff0c\u6298\u7b97\u51fa\u4e00\u767e\u516b\u5341\u516d\u4e2a\u65b9\uff0c\u90b5\u81f3\u521a\u88c5\u51fa\u7126\u6025\u7684\u6837\u5b50\u8981\u8ba9\u8bb8\u601d\u4e00\u5bb6\u4eba\u5c3d\u5feb\u642c\u51fa\uff0c\u5c06\u8d2d\u7f6e\u4ef7\u63d0\u5230\u6bcf\u5e73\u65b9\u7c73\u4e00\u5343\u5143\uff0c\u6700\u7ec8\u5c06\u5341\u516b\u4e07\u516d\u5343\u5143\u4ea4\u5230\u8bb8\u6d77\u5c71\u624b\u91cc\u3002</p><p>\u3000\u3000\u81f3\u5c11\u4eca\u540e\u4f1a\u4e0d\u4f1a\u88ab\u62c6\u7a7f\uff0c\u5f20\u606a\u4e5f\u7ba1\u4e0d\u4e86\u592a\u591a\uff0c\u5f53\u7136\uff0c\u4e8b\u60c5\u80fd\u6309\u7167\u5f20\u606a\u7684\u8bbe\u60f3\u53d1\u5c55\uff0c\u53ea\u8981\u5bf9\u6c99\u7530\u4e1c\u7247\u8fdb\u884c\u5546\u4e1a\u5316\u8fd0\u4f5c\u4e0e\u4fee\u7f2e\uff0c\u8fd9\u91cc\u7684\u623f\u4ea7\u4ef7\u503c\u4e24\u4e09\u5e74\u540e\u4f1a\u4e0a\u5347\u76f8\u5f53\u5927\u7684\u4e00\u4e2a\u53f0\u9636\uff0c\u4e5f\u5c31\u662f\u53ea\u9700\u8981\u7792\u7740\u8bb8\u601d\u5bb6\u4eba\u4e24\u4e09\u5e74\u7684\u65f6\u95f4\u5c31\u53ef\u4ee5\u4e86\u3002\u8fd9\u65f6\u5019\u4e0d\u80fd\u7ed9\u8bb8\u7ef4\u592a\u5927\u7684\u538b\u529b\uff0c\u6709\u4e86\u8fd9\u5341\u516b\u4e07\uff0c\u8bb8\u601d\u5bb6\u5c31\u5dee\u4e0d\u591a\u80fd\u5c06\u5916\u503a\u8fd8\u6e05\u5927\u90e8\u5206\uff0c\u867d\u7136\u8fd8\u77ed\u7f3a\u51e0\u4e07\u5143\uff0c\u4f46\u4ee5\u8bb8\u601d\u5bb6\u7684\u6536\u5165\uff0c\u4e5f\u6ca1\u6709\u5fc5\u8981\u592a\u8feb\u5207\u3002</p><p>\u3000\u3000\u65e0\u8bba\u662f\u8bb8\u601d\u8fd8\u662f\u8bb8\u601d\u7236\u6bcd\uff0c\u90fd\u60f3\u4ece\u6c99\u7530\u642c\u51fa\u53bb\uff0c\u867d\u7136\u5979\u4eec\u4e00\u5bb6\u4eba\u751f\u4e8e\u65af\u957f\u4e8e\u65af\uff0c\u4f46\u662f\u5f80\u4e8b\u4e2d\u63ba\u6742\u7740\u592a\u591a\u6c89\u91cd\u7684\u4e1c\u897f\uff0c\u9887\u6709\u8ba9\u4eba\u4e0d\u582a\u56de\u9053\u7684\u611f\u89e6\u3002</p><p>\u3000\u3000\u8bb8\u601d\u5bb6\u5728\u542f\u79c0\u4e2d\u5b66\u9644\u8fd1\u7684\u8001\u5e72\u4f11\u6240\u79df\u4e0b\u4e24\u5ba4\u534a\u7684\u4e2d\u5957\u623f\u5b50\uff0c\u623f\u4e3b\u662f\u9000\u4f11\u7684\u8001\u519b\u5b98\uff0c\u4ed6\u9000\u4f11\u540e\u968f\u513f\u5b50\u3001\u5ab3\u5987\u4f4f\u5728\u5317\u4eac\uff0c\u6d77\u5dde\u7684\u623f\u5b50\u5c31\u7a7a\u4e86\u4e0b\u6765\u3002\u867d\u7136\u542c\u8001\u519b\u5b98\u5439\u5618\u662f\u5927\u6821\u8854\u9000\u4f11\uff0c\u4f46\u662f\u5bf9\u623f\u79df\u65a4\u65a4\u8ba1\u8f83\uff0c\u6bcf\u4e2a\u6708\u4e09\u767e\u4e8c\u5341\u5143\u4e00\u6b65\u4e0d\u8ba9\u3002\u867d\u7136\u623f\u5b50\u6ca1\u6709\u88c5\u4fee\u8fc7\uff0c\u4f46\u662f\u6bd5\u7adf\u662f\u7ed9\u9000\u4f11\u8001\u5e72\u90e8\u5b89\u6392\u7684\u623f\u5b50\uff0c\u623f\u5b50\u8d28\u91cf\u4ee5\u53ca\u5bbf\u820d\u533a\u7684\u73af\u5883\u90fd\u76f8\u5f53\u7406\u60f3\uff0c\u8bb8\u601d\u7236\u6bcd\u89c9\u5f97\u79df\u91d1\u8fd8\u662f\u8d35\u4e86\u4e9b\uff0c\u4f46\u662f\u8bb8\u601d\u575a\u6301\u8981\u79df\u4e0b\u6765\uff1b\u4ed4\u7ec6\u8861\u91cf\u4e00\u4e0b\u5bb6\u91cc\u7684\u6536\u5165\uff0c\u5012\u4e5f\u6ca1\u4ec0\u4e48\uff0c\u8bb8\u601d\u7236\u6bcd\u6700\u7ec8\u6ca1\u6709\u575a\u6301\u3002</p><p>\u3000\u3000\u642c\u5bb6\u7684\u8fd9\u5929\uff0c\u5012\u6ca1\u820d\u5f97\u8bf7\u4eba\uff0c\u53ea\u8bf7\u4e86\u4e24\u540d\u5de5\u53cb\u5e2e\u5fd9\uff0c\u65bd\u536b\u5fe0\u5f53\u4e0a\u5382\u957f\uff0c\u4e5f\u6ca1\u6709\u7a7a\u95f2\u8fc7\u6765\u5e2e\u5fd9\uff0c\u8bb8\u6d77\u5c71\u81ea\u5df1\u7528\u5e73\u677f\u8f66\u5c06\u6574\u4e2a\u5bb6\u79fb\u5230\u65b0\u79df\u7684\u623f\u5b50\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u5510\u5a67\u9001\u56de\u5e08\u9662\uff0c\u5bfb\u7740\u5730\u5740\u8d76\u5230\u79bb\u4e00\u4e2d\u4e0d\u7b97\u8fdc\u7684\u5e72\u4f11\u6240\uff0c\u8d76\u7740\u770b\u89c1\u9648\u5983\u84c9\u9a91\u8f66\u5f80\u91cc\u8d70\uff0c\u60f3\u5fc5\u5979\u7238\u5988\u4e5f\u8fc7\u6765\u5e2e\u5fd9\u642c\u5bb6\uff0c\u8f66\u5b50\u7ed5\u8fc7\u9648\u5983\u84c9\uff0c\u5f20\u606a\u6309\u4e0b\u8f66\u7a97\uff0c\u8ddf\u5979\u6253\u62db\u547c\uff1a\u201c\u4f60\u4e5f\u53bb\u8bb8\u601d\u59d0\u7684\u65b0\u5bb6\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u9648\u5983\u84c9\u60ca\u8bb6\u7684\u8f7b\u547c\u4e86\u4e00\u58f0\uff0c\u51fa\u4e4e\u5f20\u606a\u610f\u6599\u7684\uff0c\u5979\u70b9\u5934\u5e94\u4e86\u4e00\u58f0\uff0c\u201c\u55ef\uff01\u201d</p><p>\u3000\u3000\u8f66\u5b50\u8f6c\u773c\u5c31\u8d85\u4e86\u8fc7\u53bb\uff0c\u6ca1\u770b\u5230\u9648\u5983\u84c9\u4e00\u8d2f\u51b7\u6de1\u7684\u8868\u60c5\uff0c\u5f20\u606a\u5012\u6709\u4e9b\u610f\u5916\uff0c\u6478\u4e86\u6478\u8111\u888b\uff0c\u5fc3\u91cc\u60f3\uff1a\u96be\u9053\u6700\u8fd1\u9b45\u529b\u53c8\u589e\u52a0\u4e86\uff1f\u81ea\u5632\u7684\u7b11\u4e86\u7b11\uff0c\u8d76\u5230\u8bb8\u601d\u65b0\u5bb6\uff0c\u6ca1\u6709\u7b49\u9648\u5983\u84c9\u8fc7\u6765\uff0c\u5148\u8ddf\u5085\u4fca\u4e0a\u4e86\u697c\u3002</p><p>\u3000\u3000\u867d\u8bf4\u662f\u4e2d\u5957\uff0c\u623f\u5b50\u4f9d\u65e7\u5f88\u5c0f\uff0c\u9760\u7740\u5899\u89d2\u6446\u7740\u4e00\u5f20\u7ea2\u6f06\u516b\u4ed9\u684c\uff0c\u5927\u5bb6\u90fd\u56f4\u7740\u684c\u5b50\u6b47\u5750\uff0c\u5218\u82ac\u770b\u7740\u5f20\u606a\u8fc7\u6765\uff0c\u5c31\u7b11\u7740\u8bf4\uff1a\u201c\u5f20\u606a\u8111\u5b50\u679c\u7136\u7075\u5149\uff0c\u6d41\u6c34\u5e2d\u624d\u529e\u4e86\u8fd9\u51e0\u5929\uff0c\u6bcf\u5929\u90fd\u8981\u4e24\u9910\u90fd\u8981\u6709\u516b\u4e5d\u684c\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u5947\u4e0b\u5348\u8fc7\u6765\u5e2e\u5fd9\u4e86\u4e00\u4f1a\u513f\uff0c\u8fd9\u65f6\u5019\u8d76\u56de\u9910\u9986\u3002\u5f20\u606a\u4e4b\u524d\u5efa\u8bae\u9648\u5947\u5728\u9152\u9986\u91cc\u6446\u6d41\u6c34\u5e2d\uff0c\u6bcf\u5929\u53ea\u9700\u8981\u51c6\u5907\u516b\u79cd\u5927\u7897\u83dc\uff0c\u51d1\u8d77\u5341\u4eba\u5c31\u5f00\u4e00\u684c\u6d41\u6c34\u5e2d\u3002\u9648\u5947\u72e0\u72e0\u5fc3\uff0c\u9694\u5929\u5c31\u62ff\u4e94\u767e\u5143\u5230\u6d77\u5dde\u65e5\u62a5\u53d1\u4e86\u4e24\u5929\u7684\u4e2d\u7f1d\u6d88\u606f\uff0c\u6309\u4eba\u5934\u6bcf\u4eba\u6536\u5341\u4e94\u5757\u94b1\uff0c\u4e00\u5929\u516b\u4e5d\u684c\uff0c\u52a0\u4e0a\u9152\u6c34\uff0c\u5dee\u4e0d\u591a\u4e00\u5343\u516d\u4e03\u767e\u5143\u7684\u8425\u4e1a\u989d\uff0c\u8fd9\u662f\u53ea\u80fd\u6446\u4e0b\u56db\u5f20\u684c\u5b50\u7684\u5c0f\u9152\u9986\u6240\u65e0\u6cd5\u60f3\u8c61\u7684\u3002</p><p>\u3000\u3000\u542c\u5218\u82ac\u4ecb\u7ecd\uff0c\u5979\u5bb6\u9152\u9986\u529e\u6d41\u6c34\u5e2d\u65f6\u8bf7\u4e86\u4e00\u4e2a\u4eba\u624b\uff0c\u6ca1\u4e24\u5929\u89c1\u8fd9\u4e48\u53d7\u6b22\u8fce\uff0c\u6ca1\u4e24\u5929\u8fc7\uff0c\u53c8\u589e\u52a0\u4e86\u4e00\u540d\u4eba\u624b\u3002</p><p>\u3000\u3000\u672c\u6765\u5218\u82ac\u89c1\u5f20\u606a\u5c31\u70ed\u60c5\uff0c\u773c\u7740\u5f20\u606a\u8fdb\u95e8\u6765\uff0c\u773c\u775b\u90fd\u7b11\u772f\u4e86\uff0c\u5634\u91cc\u8fd8\u8bf4\uff1a\u201c\u4eca\u5929\u8003\u8bd5\u554a\uff0c\u5983\u84c9\u8fd8\u8bf4\u8003\u8fc7\u8bd5\u8981\u8fc7\u6765\u73a9\u5462\uff0c\u5f20\u606a\u8fc7\u6765\uff0c\u6709\u6ca1\u6709\u770b\u5230\u5983\u84c9\uff1f\u201d\u7ec8\u662f\u5fcd\u7740\u6ca1\u95ee\u5f20\u606a\u8003\u8bd5\u600e\u4e48\u6837\uff0c\u542c\u8bf4\u4e0d\u5927\u7075\u5149\uff0c\u4e07\u4e00\u95ee\u607c\u4e86\uff0c\u53ef\u4e0d\u6210\u4e86\u5f04\u5de7\u6210\u62d9\uff1f</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u6ca1\u63d0\u5728\u8def\u4e0a\u9047\u5230\u9648\u5983\u84c9\u7684\u4e8b\u60c5\uff0c\u6ca1\u770b\u89c1\u8bb8\u601d\u3001\u8bb8\u7ef4\u5979\u4eec\u59d0\u59b9\u4fe9\uff0c\u8bb8\u601d\u7236\u6bcd\u5728\u623f\u95f4\u91cc\u6536\u62fe\uff0c\u8fd8\u6ca1\u6709\u610f\u8bc6\u4ed6\u8fc7\u6765\uff0c\u95ee\u5218\u82ac\uff1a\u201c\u8bb8\u601d\u59d0\u3001\u8bb8\u7ef4\u59d0\u4e24\u4eba\u5462\uff01\u201d</p><p>\u3000\u3000\u201c\u5979\u4eec\u53bb\u5e97\u91cc\u4e86\uff0c\u542c\u7740\u4f60\u8981\u8fc7\u6765\uff0c\u8ba9\u9648\u5947\u70e7\u51e0\u6837\u62ff\u624b\u7684\u83dc\u8ba9\u5979\u4eec\u62ff\u56de\u6765\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u3001\u8bb8\u7ef4\u5c06\u51e0\u6837\u83dc\u62ff\u8fc7\u6765\uff0c\u7b49\u8bb8\u601d\u5979\u5c0f\u8205\u65bd\u536b\u5fe0\u4eba\u8fc7\u6765\u624d\u5f00\u5e2d\u3002\u516b\u70b9\u949f\u4e0d\u5230\uff0c\u9648\u5947\u4e5f\u8d76\u4e86\u8fc7\u6765\uff0c\u4ed6\u770b\u7740\u5f20\u606a\u7591\u60d1\u7684\u773c\u795e\uff0c\u89e3\u91ca\u8bf4\uff1a\u201c\u665a\u4e0a\u53ea\u505a\u516d\u684c\u83dc\uff0c\u8fd9\u91cc\u7528\u53bb\u4e00\u684c\uff0c\u8fd8\u7ed9\u4e00\u5bb6\u5e86\u751f\u7684\u8ba2\u8d70\u4e00\u684c\uff0c\u5e97\u91cc\u56db\u684c\u83dc\uff0c\u4e00\u6279\u5934\u5c31\u6e05\u7a7a\u4e86\uff0c\u73b0\u5728\u6bcf\u5929\u589e\u52a0\u5230\u5341\u684c\u5de6\u53f3\uff0c\u518d\u4e0d\u80fd\u589e\u52a0\u4e86\uff0c\u5730\u65b9\u5c0f\uff0c\u4eba\u5403\u4e0d\u6d88\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u9648\u53d4\u867d\u7136\u53ea\u505a\u516b\u6837\u5927\u7897\u83dc\uff0c\u53ea\u8981\u5728\u8fd9\u516b\u6837\u83dc\u5f0f\u4e0a\u4e0b\u529f\u592b\uff0c\u518d\u575a\u6301\u6bcf\u4e2a\u6708\u5728\u6d77\u5dde\u65e5\u62a5\u505a\u4e2d\u7f1d\u3001\u5c0f\u7248\u5e7f\u544a\uff0c\u6307\u4e0d\u5b9a\u4e00\u4e24\u5e74\u5c31\u6709\u5927\u4f5c\u4e3a\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u5947\u4e5f\u4e0d\u662f\u6ca1\u6709\u60f3\u6cd5\u7684\u4eba\uff0c\u53ea\u662f\u4eba\u751f\u53d7\u632b\u592a\u591a\uff0c\u4e00\u76f4\u4e0d\u6562\u8f7b\u6613\u5984\u52a8\uff0c\u59bb\u5b50\u5218\u82ac\u5de5\u4f5c\u7a33\u5b9a\u4e0b\u6765\uff0c\u624d\u4f7f\u8ba9\u72e0\u72e0\u542c\u4e86\u5f20\u606a\u7684\u4e3b\u610f\u529e\u6d41\u6c34\u5e2d\uff0c\u5341\u5929\u7684\u5de5\u592b\u80fd\u9876\u4ee5\u5f80\u5c0f\u534a\u5e74\u7684\u6536\u5165\uff0c\u8fd9\u662f\u4ed6\u4e4b\u524d\u6240\u4e0d\u6562\u60f3\u8c61\u7684\uff0c\u4ed6\u4e5f\u53cd\u601d\u8fc7\u4e3a\u4ec0\u4e48\u4f1a\u8fd9\u4e48\u6210\u529f\uff0c\u4e3b\u8981\u8fd8\u5728\u4e8e\u4e24\u70b9\uff0c\u516b\u6837\u5927\u7897\u83dc\u7cbe\u81f4\u5b9e\u60e0\uff0c\u6d41\u6c34\u5e2d\u7684\u5f62\u5f0f\u65b0\u5947\u5f15\u4eba\u3002\u5f20\u606a\u4e4b\u524d\u7ed9\u4ed6\u603b\u6709\u4e9b\u7ea8\u7ed4\u5b50\u5f1f\u7684\u5370\u8c61\uff0c\u8fd9\u4e0b\u6765\uff0c\u5370\u8c61\u5c31\u8fe5\u7136\u4e0d\u540c\u4e86\u3002</p></article>"}}}]);
