(window.webpackJsonp=window.webpackJsonp||[]).push([[308],{330:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u56db\u8282 \u653e\u5f03",content:"<article><h2>\u7b2c\u4e8c\u5341\u56db\u8282 \u653e\u5f03</h2><p>\u743c\u6069\u51e0\u4e4e\u4ee5\u4e3a\u81ea\u5df1\u51fa\u73b0\u4e86\u5e7b\u542c\uff0c\u201c\u4ec0\u4e48\uff1f\u201d\u4ed6\u4e0d\u7531\u81ea\u4e3b\u5730\u53cd\u95ee\uff0c\u201c\u7075\u9b42\u5951\u7ea6\uff1f\u201d</p><p>\u201c\u7075\u9b42\u5951\u7ea6\u3002\u201d\u838e\u73de\u514b\u91cd\u590d\u3002</p><p>\u743c\u6069\u786e\u5b9a\u81ea\u5df1\u8033\u6735\u6ca1\u51fa\u95ee\u9898\uff0c\u4ed6\u4f4e\u5934\u770b\u770b\u6885\u83f2\u65af\uff0c\u53d1\u73b0\u5723\u6b66\u58eb\u4e5f\u662f\u4e00\u8138\u60ca\u8bb6\u3002\u201c\u4f60\u521a\u624d\u8bf4\u4f60\u8981\u81ea\u7531\uff0c\u201d\u743c\u6069\u4e0d\u89e3\u5730\u95ee\uff0c\u201c\u7136\u540e\u4f60\u53c8\u8bf4\u8981\u548c\u6211\u7b7e\u8ba2\u7075\u9b42\u5951\u7ea6\uff1f\u201d</p><p>\u201c\u6ca1\u9519\u3002\u201d</p><p>\u201c\u4f46\u8fd9\u4e24\u8005\u538b\u6839\u5c31\u662f\u77db\u76fe\u7684\u5427\u3002\u201d</p><p>\u7075\u9b42\u5951\u7ea6\u96be\u9053\u662f\u80fd\u968f\u4fbf\u7b7e\u7684\u4e1c\u897f\u4e48\u3002</p><p>\u6240\u8c13\u7075\u9b42\u5951\u7ea6\uff0c\u6700\u521d\u662f\u6e90\u81ea\u4e5d\u5c42\u5730\u72f1\u7684\u5927\u9b54\u9b3c\u4eec\u53d1\u660e\u7684\u4e00\u79cd\u9b54\u6cd5\u2014\u2014\u751a\u81f3\u90fd\u5df2\u7ecf\u8d85\u51fa\u9b54\u6cd5\u7684\u8303\u7574\u4e86\u2014\u2014\u4e13\u95e8\u7528\u4e8e\u6536\u53d6\u7269\u8d28\u754c\u7684\u51e1\u4eba\u7075\u9b42\u6240\u7528\u3002\u5728\u4e00\u822c\u4eba\u773c\u4e2d\uff0c\u6076\u9b54\u548c\u9b54\u9b3c\u6ca1\u4ec0\u4e48\u533a\u522b\uff0c\u7edf\u79f0\u4e3a\u90aa\u9b54\uff0c\u90fd\u4f4f\u5728\u4e0b\u5c42\u754c\uff0c\u4f46\u5176\u5b9e\u4ed6\u4eec\u7684\u5dee\u8ddd\u5c31\u50cf\u5929\u5802\u5c71\u548c\u8718\u86db\u9b54\u7f51\u4e00\u6837\u9065\u8fdc\u3002\u6076\u9b54\u662f\u6df7\u4e71\u65e0\u5e8f\u7684\uff0c\u5c45\u4f4f\u5728\u65e0\u5c3d\u6df1\u6e0a\uff0c\u9b54\u9b3c\u662f\u4e25\u683c\u9075\u5b88\u89c4\u5219\u7684\uff0c\u5c45\u4f4f\u5728\u4e5d\u5c42\u5730\u72f1\u3002\u4ed6\u4eec\u90fd\u559c\u6b22\u51e1\u4eba\u7684\u7075\u9b42\uff0c\u4f46\u6076\u9b54\u7528\u7684\u65b9\u6cd5\u662f\u5f15\u8bf1\u3001\u6b3a\u8bc8\u751a\u81f3\u76f4\u63a5\u6740\u4eba\u593a\u9b42\uff0c\u800c\u9b54\u9b3c\u5374\u662f\u901a\u8fc7\u5951\u7ea6\u3002</p><p>\u51e1\u4eba\u548c\u9b54\u9b3c\u7b7e\u8ba2\u4e00\u4efd\u7075\u9b42\u5951\u7ea6\uff0c\u53ea\u8981\u6210\u7acb\uff0c\u7acb\u523b\u6709\u6548\u3002\u4e3e\u4e2a\u7b80\u5355\u4f8b\u5b50\uff0c\u5951\u7ea6\u4e2d\u5199\u201c\u9b54\u9b3c\u5411\u51e1\u4eba\u63d0\u4f9b\u4e00\u4e07\u679a\u91d1\u5e01\uff0c\u51e1\u4eba\u6b7b\u540e\u7075\u9b42\u4fbf\u5f52\u9b54\u9b3c\u6240\u6709\u201d\uff0c\u90a3\u4e48\u53ea\u8981\u9b54\u9b3c\u63d0\u4f9b\u4e86\u8fd9\u4e00\u4e07\u679a\u91d1\u5e01\uff0c\u51e1\u4eba\u6b7b\u540e\uff0c\u7075\u9b42\u81ea\u7136\u5c31\u843d\u5230\u9b54\u9b3c\u624b\u4e2d\uff0c\u65e0\u9700\u518d\u901a\u8fc7\u5176\u4ed6\u65b9\u5f0f\uff0c\u8fd9\u662f\u53d7\u5951\u7ea6\u672c\u8eab\u4fdd\u8bc1\u7684\u3002</p><p>\u7075\u9b42\u5951\u7ea6\u7684\u5f3a\u5927\u4e4b\u5904\u5728\u4e8e\uff0c\u5b83\u501f\u52a9\u7684\u662f\u8fd9\u4e2a\u591a\u5143\u5b87\u5b99\u4e2d\u6700\u672c\u6e90\u7684\u79e9\u5e8f\u529b\u91cf\u3002\u54ea\u6015\u662f\u6700\u4f4e\u9636\u6700\u5f31\u5c0f\u7684\u5c0f\u9b54\u9b3c\uff0c\u53ea\u8981\u4ed6\u80fd\u62ff\u51fa\u7075\u9b42\u5951\u7ea6\u6765\uff0c\u548c\u51e1\u4eba\u7b7e\u8ba2\u6210\u529f\uff0c\u90a3\u4e48\u8fd9\u4efd\u5951\u7ea6\u5c31\u662f\u6c38\u8fdc\u6709\u6548\u7684\uff0c\u5c31\u7b97\u662f\u8bf8\u795e\u4e5f\u4e0d\u53ef\u80fd\u63d2\u624b\u64a4\u9500\u3002\u795e\u7947\u53ef\u4ee5\u628a\u7b7e\u7ea6\u53cc\u65b9\u90fd\u6740\u4e86\uff0c\u8ba9\u5951\u7ea6\u5185\u5bb9\u6c38\u8fdc\u4e5f\u65e0\u6cd5\u5f97\u5230\u5b9e\u9645\u5c65\u884c\uff0c\u4f46\u8fd9\u4efd\u5951\u7ea6\u672c\u8eab\u4f9d\u65e7\u662f\u6709\u6548\u7684\u3002</p><p>\u5f53\u7136\uff0c\u8fd9\u79cd\u6700\u539f\u59cb\u7684\u7075\u9b42\u5951\u7ea6\u4e5f\u6709\u5b83\u7684\u9650\u5236\u4e4b\u5904\uff0c\u4fbf\u5728\u4e8e\u53cc\u65b9\u5f53\u4e8b\u4eba\u53ea\u53ef\u80fd\u662f\u51e1\u4eba\u548c\u9b54\u9b3c\u3002\u53ea\u6709\u9b54\u9b3c\u624d\u80fd\u62ff\u51fa\u7075\u9b42\u5951\u7ea6\uff0c\u4e5f\u53ea\u6709\u9b54\u9b3c\u7684\u5b88\u5e8f\u672c\u8d28\uff0c\u624d\u80fd\u4fdd\u8bc1\u5951\u7ea6\u7684\u6548\u529b\uff0c\u6362\u4e86\u4ee5\u6df7\u4e71\u8457\u79f0\u7684\u6076\u9b54\u662f\u538b\u6839\u5c31\u6ca1\u7528\u7684\u3002\u800c\u4e14\u5185\u5bb9\u4e5f\u9650\u5236\u5f97\u5f88\u4e25\u683c\uff0c\u9b54\u9b3c\u63d0\u4f9b\u5404\u79cd\u670d\u52a1\uff0c\u6362\u53d6\u51e1\u4eba\u7684\u7075\u9b42\uff0c\u4e0d\u80fd\u504f\u79bb\u8fd9\u4e2a\u4e3b\u9898\u3002</p><p>\u5728\u6f2b\u957f\u7684\u5c81\u6708\u4e2d\uff0c\u8fd9\u79cd\u9b54\u9b3c\u7684\u9b54\u6cd5\u6280\u672f\u6d41\u4f20\u5230\u4e86\u7269\u8d28\u754c\uff0c\u7ecf\u8fc7\u4e00\u4ee3\u4ee3\u5deb\u5e08\u7684\u7814\u7a76\uff0c\u53c8\u5f04\u51fa\u4e86\u4e00\u4e9b\u65b0\u7684\u53d8\u79cd\u3002\u6bd4\u5982\u6700\u5e38\u7528\u7684\u9b54\u5ba0\u5951\u7ea6\uff0c\u4e13\u4f9b\u5deb\u5e08\u53ec\u5524\u9b54\u5ba0\u4e4b\u7528\uff0c\u4ece\u672c\u8d28\u4e0a\u8bf4\u4f9d\u65e7\u662f\u7075\u9b42\u5951\u7ea6\uff0c\u5f53\u7136\u743c\u6069\u61d2\u5f97\u53ec\u5524\u9b54\u5ba0\uff0c\u6240\u4ee5\u8fd8\u6ca1\u5c1d\u8bd5\u8fc7\u3002\u800c\u6240\u6709\u53d8\u79cd\u4e4b\u4e2d\uff0c\u6700\u5f3a\u5927\u4e5f\u6700\u795e\u79d8\u7684\uff0c\u4fbf\u662f\u771f\u540d\u5951\u7ea6\u3002</p><p>\u771f\u540d\u5951\u7ea6\u53d1\u660e\u51fa\u6765\u7684\u6700\u521d\u672c\u610f\uff0c\u662f\u4e3a\u5deb\u5e08\u5bfb\u627e\u5f97\u529b\u53ef\u9760\u7684\u90aa\u9b54\u52a9\u624b\u3002\u5b83\u7a81\u7834\u4e86\u539f\u59cb\u7075\u9b42\u5951\u7ea6\u7684\u5f88\u591a\u9650\u5236\uff0c\u5374\u4e5f\u589e\u52a0\u4e86\u65b0\u7684\u6761\u4ef6\u3002\u7b7e\u7ea6\u8005\u7684\u4e00\u65b9\u4ecd\u65e7\u5fc5\u987b\u662f\u51e1\u4eba\uff0c\u4f46\u53e6\u4e00\u65b9\u5374\u4e0d\u518d\u9650\u5b9a\u5fc5\u987b\u662f\u9b54\u9b3c\uff0c\u5b8c\u5168\u53ef\u4ee5\u662f\u6076\u9b54\uff0c\u751a\u81f3\u53ef\u4ee5\u662f\u5929\u4f7f\u2014\u2014\u4f46\u540c\u65f6\u5374\u8981\u6c42\u51e1\u4eba\u5fc5\u987b\u662f\u9ad8\u9636\u5deb\u5e08\uff0c\u4e5f\u5c31\u662f\u8bf4\u5fc5\u987b\u62e5\u6709\u201c\u771f\u540d\u201d\u3002</p><p>\u56e0\u4e3a\u8fd9\u672c\u6765\u5c31\u662f\u4e00\u4efd\u4ee5\u201c\u771f\u540d\u201d\u4f5c\u4e3a\u6548\u529b\u4fdd\u969c\u7684\u5951\u7ea6\u3002</p><p>\u6240\u8c13\u79e9\u5e8f\uff0c\u6700\u6839\u672c\u7684\u89c4\u5219\uff0c\u9996\u5148\u662f\u81ea\u7531\u610f\u5fd7\uff0c\u5176\u6b21\u662f\u5951\u7ea6\u5fc5\u5b88\uff0c\u5176\u4f59\u4e00\u5207\u89c4\u5219\u90fd\u5728\u5176\u4e0b\u3002\u800c\u4ee5\u6548\u529b\u800c\u8bba\uff0c\u4e66\u9762\u5951\u7ea6\u53c8\u8fdc\u8fdc\u5f3a\u4e8e\u53e3\u5934\u5951\u7ea6\u3002\u771f\u540d\u5951\u7ea6\u662f\u4e66\u9762\u5951\u7ea6\uff0c\u4ee5\u53cc\u65b9\u7684\u201c\u771f\u540d\u201d\u7b7e\u7f72\uff0c\u4ee3\u8868\u4e86\u5b8c\u5168\u7684\u81ea\u7531\u610f\u5fd7\uff0c\u4e5f\u56e0\u6b64\u62e5\u6709\u4e86\u7edd\u5bf9\u6548\u529b\u3002\u4efb\u4f55\u5199\u5728\u5951\u7ea6\u4e2d\u7684\u5185\u5bb9\uff0c\u53ea\u8981\u7ecf\u8fc7\u786e\u8ba4\uff0c\u5bf9\u4e8e\u7b7e\u7ea6\u53cc\u65b9\u800c\u8a00\u90a3\u5c31\u662f\u81f3\u9ad8\u5f8b\u6cd5\uff0c\u4e0d\u80fd\u8fdd\u80cc\u3002\u5373\u4fbf\u662f\u518d\u72e1\u733e\u591a\u667a\u7684\u51e1\u4eba\u548c\u518d\u6df7\u4e71\u53db\u9006\u7684\u6076\u9b54\uff0c\u4e5f\u5fc5\u987b\u8001\u8001\u5b9e\u5b9e\u9075\u5faa\u3002</p><p>\u6700\u9ebb\u70e6\u7684\u5173\u952e\u5728\u4e8e\uff0c\u771f\u540d\u5951\u7ea6\u7684\u5185\u5bb9\u662f\u53ef\u4ee5\u7531\u53cc\u65b9\u81ea\u7531\u5546\u5b9a\uff0c\u4f46\u6709\u4e00\u6761\u5374\u662f\u7cfb\u7edf\u9ed8\u8ba4\uff0c\u65e0\u6cd5\u66f4\u6539\uff0c\u4e5f\u4e0d\u80fd\u901a\u8fc7\u5176\u4ed6\u6761\u6b3e\u5e9f\u9664\u6216\u8005\u89c4\u907f\u7684\uff1a\u751f\u547d\u7ed1\u5b9a\u3002</p><p>\u751f\u547d\u7ed1\u5b9a\u7684\u610f\u601d\u5b8c\u5168\u53ef\u4ee5\u4ece\u5b57\u9762\u7406\u89e3\uff0c\u6362\u53e5\u8bdd\u8bf4\uff0c\u53ea\u8981\u771f\u540d\u5951\u7ea6\u7b7e\u8ba2\uff0c\u5176\u4e2d\u6709\u4e00\u65b9\u6b7b\u4ea1\uff0c\u90a3\u53e6\u4e00\u65b9\u4e5f\u4f1a\u8ddf\u7740\u6b7b\u4ea1\uff0c\u7edd\u65e0\u610f\u5916\uff0c\u76f8\u5f53\u4e8e\u662f\u628a\u4e24\u4e2a\u4eba\u7684\u547d\u8fd0\u7ed1\u5728\u4e00\u8d77\uff0c\u4e00\u65e6\u51fa\u4e8b\u5c31\u540c\u65f6\u5b8c\u86cb\u3002\u4e16\u754c\u4e0a\u7684\u4eba\uff0c\u53ea\u6709\u5acc\u547d\u592a\u77ed\u7684\uff0c\u6ca1\u6709\u6d3b\u5f97\u4e0d\u8010\u70e6\u7684\uff0c\u8fd9\u79cd\u53d8\u6001\u7684\u5951\u7ea6\uff0c\u81ea\u7136\u6ca1\u6709\u51e0\u4e2a\u4eba\u613f\u610f\u7b7e\uff0c\u867d\u7136\u8d39\u4e86\u8bb8\u591a\u529f\u592b\u53d1\u660e\u51fa\u6765\uff0c\u5374\u88ab\u675f\u4e4b\u9ad8\u9601\uff0c\u6210\u4e86\u53ea\u5177\u6709\u201c\u7406\u8bba\u7814\u7a76\u4ef7\u503c\u201d\u7684\u5c60\u9f99\u4e4b\u6280\u3002</p><p>\u838e\u73de\u514b\u73b0\u5728\u63d0\u51fa\u8981\u7b7e\u7075\u9b42\u5951\u7ea6\uff0c\u9996\u5148\u5979\u662f\u6076\u9b54\u4e0d\u662f\u9b54\u9b3c\uff0c\u539f\u59cb\u7684\u90a3\u79cd\u7075\u9b42\u5951\u7ea6\u4e0d\u53ef\u80fd\u9002\u7528\uff0c\u5979\u4e5f\u62ff\u4e0d\u51fa\u6765\uff1b\u9b54\u5ba0\u5951\u7ea6\u66f4\u4e0d\u53ef\u80fd\uff0c\u73b0\u6709\u7684\u9b54\u6cd5\u6280\u672f\uff0c\u8fd8\u4e0d\u8db3\u4ee5\u628a\u4e00\u4e2a\u9b45\u9b54\u53d8\u6210\u9b54\u5ba0\uff0c\u5426\u5219\u5deb\u5e08\u4eec\u53ea\u6015\u4eba\u624b\u4e00\u4e2a\u4e86\uff1b\u552f\u4e00\u53ef\u80fd\u7684\uff0c\u5c31\u662f\u771f\u540d\u5951\u7ea6\u3002</p><p>\u4f46\u771f\u540d\u5951\u7ea6\u548c\u201c\u81ea\u7531\u201d\uff0c\u538b\u6839\u5c31\u662f\u5357\u8f95\u5317\u8f99\u7684\u5427\u3002</p><p>\u201c\u4e0d\u77db\u76fe\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u6211\u9700\u8981\u7684\u81ea\u7531\uff0c\u662f\u79bb\u5f00\u8fd9\u91cc\uff0c\u56de\u5230\u7269\u8d28\u754c\u53bb\u3002\u201d</p><p>\u201c\u8fd9\u5730\u65b9\u4f3c\u4e4e\u4e5f\u6ca1\u4ec0\u4e48\u4e0d\u597d\u2026\u2026\u6211\u7684\u610f\u601d\u662f\u8bf4\uff0c\u4f60\u73b0\u5728\u5df2\u7ecf\u662f\u6076\u9b54\u4e86\uff0c\u8fd9\u91cc\u6b63\u662f\u6700\u9002\u5408\u4f60\u7684\u4f4d\u9762\u2026\u2026\u201d</p><p>\u201c\u9002\u5408\u4e0d\u9002\u5408\uff0c\u662f\u6211\u81ea\u5df1\u8bf4\u4e86\u7b97\u7684\uff0c\u201d\u838e\u73de\u514b\u51b7\u51b7\u77a5\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u6211\u867d\u7136\u6362\u4e86\u526f\u8eab\u4f53\uff0c\u4f46\u6211\u7684\u8bb0\u5fc6\uff0c\u6211\u7684\u7075\u9b42\uff0c\u5b8c\u5168\u8fd8\u548c\u51e1\u4eba\u65f6\u4e00\u6837\uff0c\u90a3\u4e48\u6211\u4e3a\u4ec0\u4e48\u8981\u4f4f\u5728\u8fd9\u79cd\u80ae\u810f\u3001\u8352\u51c9\uff0c\u6c38\u8fdc\u900f\u7740\u8150\u81ed\u548c\u8840\u8165\u6b7b\u6c14\uff0c\u65e0\u4f11\u6b62\u65e0\u7406\u7531\u5730\u4e92\u76f8\u6b8b\u6740\u7684\u9b3c\u5730\u65b9\u2014\u2014\u4f60\u662f\u4e0d\u662f\u5f88\u60f3\u5c1d\u8bd5\uff1f\u201d</p><p>\u201c\u4e0d\uff0c\u8c22\u4e86\u3002\u201d\u743c\u6069\u8d76\u5feb\u63a8\u6258\uff0c\u201c\u4f46\u5982\u679c\u4f60\u53ea\u662f\u60f3\u56de\u5230\u7269\u8d28\u754c\uff0c\u90a3\u5b8c\u5168\u4e0d\u9700\u8981\u8fd9\u4e48\u5927\u8d39\u5468\u7ae0\uff0c\u201d\u4ed6\u5efa\u8bae\u8bf4\uff0c\u201c\u53ea\u8981\u4f60\u80fd\u5e2e\u52a9\u6211\u4eec\u56de\u5230\u7269\u8d28\u754c\uff0c\u6211\u4fbf\u53ef\u4ee5\u7528\u9b54\u6cd5\u5c06\u4f60\u53ec\u5524\u8fc7\u6765\uff0c\u8fd9\u5e76\u4e0d\u96be\u3002\u201d</p><p>\u786e\u5b9e\u4e0d\u96be\uff0c\u6076\u9b54\u548c\u6df1\u6e0a\u7684\u8054\u7cfb\u7d27\u5bc6\uff0c\u4f46\u7a0b\u5ea6\u9ad8\u4f4e\u4e5f\u662f\u6709\u533a\u522b\u7684\u3002\u529b\u91cf\u8d8a\u5f3a\u7684\u6076\u9b54\uff0c\u548c\u6df1\u6e0a\u7684\u8054\u7cfb\u8d8a\u5bc6\u5207\uff0c\u529b\u91cf\u8d8a\u5f31\u7684\u6076\u9b54\uff0c\u8054\u7cfb\u5219\u8d8a\u8584\u5f31\u3002\u5bf9\u4e8e\u51e1\u4eba\u5deb\u5e08\u6765\u8bf4\uff0c\u8fd9\u5c31\u610f\u5473\u7740\u4f60\u53ec\u5524\u4e00\u53ea\u5938\u8d5b\u9b54\uff0c\u548c\u53ec\u5524\u4e00\u53ea\u708e\u9b54\uff0c\u96be\u5ea6\u76f8\u5dee\u4e0d\u53ef\u4ee5\u9053\u7406\u8ba1\uff0c\u524d\u8005\u662f\u521d\u51fa\u8305\u5e90\u7684\u5deb\u5e08\u5c31\u80fd\u529e\u5230\u7684\uff0c\u540e\u8005\u975e\u7b2c\u4e00\u6d41\u5927\u5deb\u5e08\u4e0d\u53ef\uff0c\u5f80\u5f80\u8fd8\u6709\u751f\u547d\u5371\u9669\u3002\u838e\u73de\u514b\u73b0\u5728\u662f\u9b45\u9b54\uff0c\u6bd4\u5938\u8d5b\u9b54\u81ea\u7136\u9ad8\u7ea7\u4e9b\uff0c\u4f46\u4e5f\u4e0d\u7b97\u5f88\u5f3a\uff0c\u4f55\u51b5\u5979\u6210\u4e3a\u6076\u9b54\u624d\u4e0d\u5230\u4e00\u5e74\uff0c\u672c\u8d28\u4e0a\u8fd8\u6ca1\u6709\u88ab\u5b8c\u5168\u540c\u5316\uff0c\u53ec\u5524\u8d77\u6765\u96be\u5ea6\u5f88\u4f4e\u3002</p><p>\u53ea\u4e0d\u8fc7\uff0c\u53ec\u5524\u6076\u9b54\u4e5f\u662f\u6709\u4e00\u5b9a\u6761\u4ef6\u7684\u2026\u2026</p><p>\u201c\u6211\u600e\u4e48\u80fd\u4fdd\u8bc1\u4f60\u56de\u5230\u7269\u8d28\u754c\uff0c\u5c31\u4e00\u5b9a\u4f1a\u5c65\u884c\u8bfa\u8a00\u5462\uff1f\u201d\u838e\u73de\u514b\u53cd\u95ee\uff0c\u201c\u83ab\u975e\u6211\u4eec\u4e3a\u6b64\u518d\u7b7e\u4e00\u4efd\u7075\u9b42\u5951\u7ea6\uff1f\u201d</p><p>\u5e9f\u8bdd\uff0c\u8fd8\u7b7e\u7075\u9b42\u5951\u7ea6\uff0c\u90a3\u4e8b\u60c5\u4e0d\u53c8\u7ed5\u56de\u6765\u4e86\u4e48\u3002\u201c\u4f60\u53ef\u4ee5\u4fe1\u4efb\u6211\u4eec\u2026\u2026\u201d</p><p>\u201c\u4f60\u8981\u6c42\u4e00\u4e2a\u6076\u9b54\u4fe1\u4efb\u4f60\uff1f\u201d\u838e\u73de\u514b\u7528\u4e0d\u53ef\u601d\u8bae\u7684\u8bed\u6c14\u8bf4\uff0c\u201c\u4f60\u89c9\u5f97\u6076\u9b54\u7684\u8f9e\u5178\u91cc\u4f1a\u6709\u2018\u4fe1\u4efb\u2019\u8fd9\u79cd\u6982\u5ff5\uff1f\u201d</p><p>\u743c\u6069\u95ed\u4e0a\u5634\u3002</p><p>\u201c\u800c\u4e14\u4f60\u600e\u6837\u624d\u80fd\u53ec\u5524\u6211\uff1f\u4f60\u5fc5\u987b\u8981\u62ff\u5230\u6211\u7684\u771f\u540d\u5427\uff0c\u201d\u838e\u73de\u514b\u53c8\u8bf4\uff0c\u201c\u7136\u540e\u5462\uff1f\u4f60\u662f\u4e2a\u5deb\u5e08\uff0c\u4f60\u6709\u6211\u7684\u771f\u540d\uff0c\u4f60\u5c31\u53ef\u4ee5\u968f\u5fc3\u6240\u6b32\u5730\u5dee\u9063\u6211\u3001\u6307\u4f7f\u6211\u3001\u547d\u4ee4\u6211\u2014\u2014\u4f60\u89c9\u5f97\u6211\u5f88\u4e50\u610f\u63a5\u53d7\u8fd9\u6837\u7684\u5904\u5883\uff1f\u867d\u7136\u6211\u73b0\u5728\u80f8\u90e8\u662f\u6bd4\u4ee5\u524d\u66f4\u5927\u4e86\u70b9\uff0c\u4f46\u667a\u529b\u4f3c\u4e4e\u5e76\u6ca1\u6709\u4e0b\u964d\u5462\u3002\u201d</p><p>\u743c\u6069\u7ee7\u7eed\u95ed\u7740\u5634\u3002</p><p>\u201c\u6700\u540e\u4e00\u70b9\uff0c\u5c31\u7b97\u4f60\u9075\u5b88\u7ea6\u5b9a\uff0c\u5c31\u7b97\u4f60\u4e0d\u7528\u771f\u540d\u6765\u8981\u631f\u6211\uff0c\u5bf9\u6211\u800c\u8a00\u4e5f\u6ca1\u6709\u4ec0\u4e48\u610f\u4e49\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u683c\u62c9\u5179\u7279\u540c\u6837\u4e5f\u6709\u6211\u7684\u771f\u540d\uff0c\u4f60\u53ef\u4ee5\u628a\u6211\u53ec\u5524\u5230\u7269\u8d28\u754c\uff0c\u4ed6\u4e5f\u540c\u6837\u53ef\u4ee5\u518d\u628a\u6211\u53ec\u56de\u6df1\u6e0a\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u89c4\u5219\u53ea\u4f1a\u88ab\u66f4\u9ad8\u7684\u89c4\u5219\u6240\u6539\u53d8\uff0c\u7075\u9b42\u5951\u7ea6\u5c31\u662f\u6700\u9ad8\u7684\u89c4\u5219\u3002\u201d</p><p>\u201c\u4f60\u7684\u53e3\u6c14\u771f\u4e0d\u50cf\u662f\u4e2a\u6076\u9b54\uff0c\u201d\u6885\u83f2\u65af\u5728\u65c1\u8fb9\u8bc4\u4ef7\uff0c\u201c\u5012\u50cf\u662f\u4e2a\u9b54\u9b3c\u3002\u201d</p><p>\u9b45\u9b54\u5ae3\u7136\u7b11\u4e86\u7b11\uff0c\u201c\u8ddf\u683c\u62c9\u5179\u7279\u5b66\u7684\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u522b\u5fd8\u4e86\u90a3\u5bb6\u4f19\u7684\u5916\u53f7\u3002\u201d</p><p>\u201c\u6211\u53ef\u4ee5\u4ee3\u66ff\u4ed6\u548c\u4f60\u7b7e\u8fd9\u4efd\u5951\u7ea6\u5417\uff1f\u201d\u6885\u83f2\u65af\u95ee\u3002</p><p>\u201c\u4f60\u6ca1\u6709\u771f\u540d\uff0c\u201d\u838e\u73de\u514b\u56de\u7b54\uff0c\u201c\u800c\u4e14\u6211\u4e5f\u7edd\u4e0d\u5e0c\u671b\u548c\u4e00\u4f4d\u5723\u6b66\u58eb\u7b7e\u7075\u9b42\u5951\u7ea6\u3002\u201d</p><p>\u6885\u83f2\u65af\u9ed8\u7136\uff0c\u838e\u73de\u514b\u6240\u8bf4\u662f\u4e8b\u5b9e\u3002\u5982\u679c\u7b7e\u8ba2\u7075\u9b42\u5951\u7ea6\uff08\u5979\u6307\u7684\u663e\u7136\u662f\u771f\u540d\u5951\u7ea6\uff09\uff0c\u5c31\u610f\u5473\u7740\u7b7e\u7ea6\u53cc\u65b9\u4ece\u6b64\u7d27\u7d27\u7ed1\u5728\u4e00\u8d77\uff0c\u8fd9\u662f\u81ea\u7531\u610f\u5fd7\u8fbe\u6210\u7684\u5408\u610f\uff0c\u518d\u4e5f\u4e0d\u80fd\u6539\u53d8\uff0c\u4e5f\u4e0d\u53ef\u80fd\u88ab\u4ee5\u540e\u7684\u5951\u7ea6\u6240\u4fee\u6b63\u6216\u5e9f\u6b62\u3002\u8fd9\u79cd\u4ee3\u4ef7\uff0c\u5e76\u4e0d\u662f\u4eba\u4eba\u90fd\u613f\u610f\u627f\u53d7\u7684\uff0c\u4f46\u6b64\u65f6\u6b64\u523b\uff0c\u4e5f\u552f\u6709\u743c\u6069\u80fd\u4f5c\u4e3a\u7b7e\u7ea6\u8005\u3002</p><p>\u771f\u540d\u5951\u7ea6\u88ab\u53d1\u660e\u51fa\u6765\uff0c\u539f\u672c\u7684\u7528\u610f\u5c31\u662f\u8ba9\u5deb\u5e08\u83b7\u5f97\u53ef\u9760\u7684\u90aa\u9b54\u52a9\u624b\u3002\u5982\u679c\u743c\u6069\u548c\u838e\u73de\u514b\u7b7e\u7ea6\u6210\u529f\uff0c\u90a3\u4e48\u53ea\u8981\u743c\u6069\u5728\u7269\u8d28\u754c\uff0c\u838e\u73de\u514b\u81ea\u7136\u5c31\u53ef\u4ee5\u81ea\u7531\u524d\u5f80\u4ed6\u8eab\u8fb9\uff0c\u8fde\u901a\u5e38\u7684\u4eea\u5f0f\u8840\u796d\u90fd\u4e0d\u9700\u8981\uff0c\u4e5f\u4e0d\u518d\u4f1a\u88ab\u5176\u4ed6\u5b58\u5728\u6240\u53ec\u5524\u63a7\u5236\uff0c\u56e0\u4e3a\u771f\u540d\u5951\u7ea6\u672c\u8eab\u5c31\u662f\u6700\u9ad8\u89c4\u5219\uff0c\u5728\u5b83\u7684\u9886\u57df\u4e4b\u5185\u51cc\u9a7e\u4e8e\u4e00\u5207\u89c4\u5219\u4e4b\u4e0a\u3002\u800c\u4e14\u771f\u540d\u5951\u7ea6\u9020\u5c31\u7684\u662f\u201c\u52a9\u624b\u201d\u800c\u4e0d\u662f\u201c\u5974\u96b6\u201d\uff0c\u5730\u4f4d\u76f8\u5bf9\u5e73\u7b49\uff0c\u62e5\u6709\u66f4\u591a\u7684\u81ea\u7531\uff0c\u52a0\u4e0a\u201c\u751f\u547d\u7ed1\u5b9a\u201d\u7684\u94c1\u5f8b\u9650\u5236\uff0c\u5f7c\u6b64\u4e4b\u95f4\u4e5f\u5fc5\u987b\u4fdd\u6301\u76f8\u5e94\u7684\u5c0a\u91cd\u3002</p><p>\u4ece\u838e\u73de\u514b\u7684\u89d2\u5ea6\u6765\u8bf4\uff0c\u5979\u5df2\u7ecf\u53d8\u6210\u4e86\u9b45\u9b54\uff0c\u4f46\u5374\u4f9d\u65e7\u4fdd\u6709\u4eba\u7c7b\u7684\u8bb0\u5fc6\u548c\u7075\u9b42\uff0c\u5e0c\u671b\u8fd4\u56de\u7269\u8d28\u754c\uff0c\u8fd9\u662f\u7406\u6240\u5f53\u7136\u7684\u4e8b\u60c5\u3002\u6362\u4e86\u743c\u6069\uff0c\u4ed6\u4e5f\u4e0d\u80af\u5728\u8fd9\u9b3c\u5730\u65b9\u591a\u5446\u534a\u79d2\u3002\u4f55\u51b5\u5728\u6df1\u6e0a\u65e0\u6570\u6076\u9b54\u4e4b\u4e2d\uff0c\u9b45\u9b54\u4e0d\u8fc7\u662f\u5730\u4f4d\u5f88\u4f4e\u7684\u4e00\u79cd\uff0c\u5b9e\u529b\u5f31\u5c0f\uff0c\u5904\u5904\u53d7\u5230\u6b3a\u538b\uff0c\u4f46\u5230\u4e86\u7269\u8d28\u754c\uff0c\u90a3\u60c5\u51b5\u4fbf\u53c8\u5b8c\u5168\u4e0d\u540c\u3002\u4e3a\u4e86\u8fd9\u4e9b\u597d\u5904\uff0c\u4e00\u4efd\u7075\u9b42\u5951\u7ea6\u538b\u6839\u4e0d\u7b97\u4ec0\u4e48\uff0c\u867d\u7136\u4f5c\u4e3a\u201c\u52a9\u624b\u201d\uff0c\u4f1a\u56e0\u6b64\u53d7\u5230\u743c\u6069\u7684\u4e00\u5b9a\u5236\u7ea6\uff0c\u4f46\u603b\u6bd4\u53d7\u683c\u62c9\u5179\u7279\u63a7\u5236\u597d\u5f97\u591a\u3002</p><p>\u95ee\u9898\u662f\u8fd9\u5bf9\u743c\u6069\u6765\u8bf4\uff0c\u5374\u6ca1\u591a\u5c11\u597d\u5904\u3002\u8bda\u7136\uff0c\u591a\u4e00\u4e2a\u6f02\u4eae\u7684\u9b45\u9b54\u52a9\u624b\u5728\u8eab\u8fb9\uff0c\u662f\u4ee4\u4eba\u6109\u5feb\u7684\u4e8b\u60c5\uff0c\u800c\u4e14\u4e5f\u786e\u5b9e\u80fd\u5e2e\u4e0a\u4e0d\u5c11\u5fd9\uff0c\u4f46\u4e07\u4e00\u5979\u51fa\u4e8b\u4e86\uff0c\u81ea\u5df1\u8ddf\u7740\u5b8c\u86cb\uff0c\u8fd9\u53ef\u5c31\u7cdf\u7cd5\u5f97\u5f88\uff0c\u7b80\u76f4\u50cf\u662f\u628a\u6027\u547d\u653e\u5728\u522b\u4eba\u624b\u91cc\uff0c\u592a\u7f3a\u4e4f\u5b89\u5168\u611f\u4e86\u3002</p><p>\u201c\u8fd9\u662f\u552f\u4e00\u6211\u80fd\u63a5\u53d7\u7684\u65b9\u6cd5\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u201c\u6211\u5fc5\u987b\u4fdd\u8bc1\u6211\u4e0d\u4f1a\u88ab\u6b3a\u9a97\uff0c\u4e0d\u4f1a\u88ab\u4f24\u5bb3\uff0c\u4e0d\u4f1a\u88ab\u8fc7\u6cb3\u62c6\u6865\uff0c\u4e0d\u4f1a\u5f92\u52b3\u4e00\u573a\u3002\u6211\u5fc5\u987b\u80fd\u786e\u5b9a\u8fd9\u4e00\u70b9\uff0c\u800c\u4e0d\u662f\u8981\u7a7a\u6d1e\u7684\u627f\u8bfa\u3002\u201d</p><p>\u201c\u6211\u662f\u4e2a\u51e1\u4eba\uff0c\u201d\u743c\u6069\u8bd5\u56fe\u6253\u6d88\u5bf9\u65b9\u7684\u5ff5\u5934\uff0c\u201c\u5bff\u547d\u4e0d\u8fc7\u767e\u5c81\uff0c\u5306\u5306\u4fbf\u8fc7\u3002\u5018\u82e5\u771f\u7b7e\u4e86\u7075\u9b42\u5951\u7ea6\uff0c\u4e00\u65e6\u6211\u6b7b\u53bb\uff0c\u4f60\u4e5f\u4f1a\u6b7b\u53bb\uff0c\u8fde\u590d\u6d3b\u90fd\u4e0d\u53ef\u80fd\u3002\u201d</p><p>\u201c\u65e0\u6240\u8c13\uff0c\u53cd\u6b63\u6211\u672c\u6765\u4e5f\u662f\u51e1\u4eba\uff0c\u4e5f\u4e0d\u8fc7\u80fd\u6d3b\u767e\u5c81\u7f62\u4e86\uff0c\u201d\u838e\u73de\u514b\u56de\u7b54\uff0c\u201c\u4e8b\u5b9e\u4e0a\uff0c\u6211\u5df2\u7ecf\u6b7b\u8fc7\uff0c\u73b0\u5728\u7684\u751f\u547d\u4e0d\u8fc7\u662f\u989d\u5916\u5f97\u6765\u7684\u3002\u4f55\u51b5\u4f5c\u4e3a\u5deb\u5e08\uff0c\u4f60\u6709\u65e0\u6570\u79cd\u65b9\u6cd5\u53ef\u4ee5\u5ef6\u957f\u81ea\u5df1\u7684\u5bff\u547d\u2014\u2014\u6211\u77e5\u9053\u4f60\u80fd\u529e\u5f97\u5230\uff0c\u6211\u8ba4\u8bc6\u5f88\u591a\u5deb\u5e08\uff0c\u4f46\u6ca1\u6709\u54ea\u4e00\u4e2a\u80fd\u50cf\u4f60\u8fd9\u6837\u5929\u8d4b\u4f18\u79c0\u7684\u3002\u201d</p><p>\u201c\u4f60\u4eec\u53ef\u4ee5\u6162\u6162\u8003\u8651\uff0c\u4f46\u6211\u8981\u63d0\u9192\u7684\u662f\uff0c\u65f6\u95f4\u5df2\u7ecf\u4e0d\u591a\u4e86\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u5979\u626c\u8d77\u4e0b\u5df4\uff0c\u6307\u4e86\u6307\u6885\u83f2\u65af\uff0c\u201c\u5979\u5728\u8fd9\u91cc\u505c\u7559\u65f6\u95f4\u8d8a\u4e45\uff0c\u5c31\u4f1a\u8d8a\u865a\u5f31\uff0c\u5982\u679c\u518d\u62d6\u5ef6\u4e0b\u53bb\uff0c\u5c31\u7b97\u4ee5\u540e\u56de\u5230\u7269\u8d28\u754c\uff0c\u5979\u6240\u53d7\u5230\u7684\u4f24\u5bb3\u4e5f\u4e0d\u80fd\u5b8c\u5168\u6062\u590d\u4e86\u3002\u5f53\u7136\uff0c\u5982\u679c\u8d76\u5feb\u5230\u65ad\u57df\u9547\uff0c\u90a3\u4e48\u60c5\u51b5\u4f1a\u597d\u8f6c\u4e00\u4e9b\u3002\u201d</p><p>\u201c\u5728\u65ad\u57df\u9547\u5979\u5c31\u4e0d\u4f1a\u88ab\u4f4d\u9762\u89c4\u5219\u538b\u5236\uff1f\u201d</p><p>\u201c\u76f8\u5bf9\u4f1a\u597d\u4e00\u4e9b\uff0c\u90a3\u5730\u65b9\u6bd5\u7adf\u662f\u4e07\u6e0a\u5e73\u539f\u4e2d\u6700\u6709\u2018\u79e9\u5e8f\u2019\u7684\u6240\u5728\u3002\u201d</p><p>\u743c\u6069\u4f4e\u5934\u770b\u4e86\u770b\u6885\u83f2\u65af\uff0c\u89c1\u5979\u8138\u8272\u5df2\u7ecf\u8d8a\u52a0\u82cd\u767d\uff0c\u201c\u597d\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u7b7e\u7075\u9b42\u5951\u7ea6\u3002\u201d</p><p>\u201c\u743c\u6069\u2026\u2026\u201d</p><p>\u201c\u4e00\u4efd\u5951\u7ea6\u7f62\u4e86\uff0c\u6211\u4ee5\u524d\u7b7e\u5f97\u591a\u4e86\uff0c\u201d\u743c\u6069\u7b11\u7740\u5bf9\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u522b\u62c5\u5fc3\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u7075\u9b42\u5951\u7ea6\u7684\u7a0b\u5e8f\u975e\u5e38\u7b80\u5355\uff0c\u968f\u4fbf\u53d6\u4e00\u5f20\u7eb8\u6765\uff0c\u54ea\u6015\u662f\u5df2\u7ecf\u5199\u8fc7\u5b57\u7684\u5e9f\u7eb8\u80cc\u9762\u90fd\u6ca1\u95ee\u9898\uff0c\u5199\u4e0a\u6761\u6b3e\uff0c\u5b57\u8ff9\u4e0d\u5de5\u6574\u90fd\u65e0\u6240\u8c13\uff0c\u5b83\u771f\u6b63\u7684\u5173\u952e\uff0c\u53ea\u5728\u4e8e\u53cc\u65b9\u6700\u540e\u7b7e\u7f72\u7684\u201c\u771f\u540d\u201d\u3002</p><p>\u743c\u6069\u4ece\u6000\u91cc\u53d6\u51fa\u4e00\u5f20\u7a7a\u767d\u7684\u7f8a\u76ae\u7eb8\u6765\uff0c\u53c8\u638f\u51fa\u7fbd\u7b14\u58a8\u6c34\uff0c\u8fd9\u4e9b\u90fd\u662f\u5deb\u5e08\u6284\u5f55\u5377\u8f74\u7684\u5fc5\u5907\u5de5\u5177\uff0c\u867d\u7136\u4ed6\u4e0d\u5e38\u7528\uff0c\u4e5f\u662f\u968f\u8eab\u643a\u5e26\u7684\u3002\u4ed6\u5237\u5237\u51e0\u7b14\uff0c\u5c06\u7075\u9b42\u5951\u7ea6\u7684\u4e00\u4e9b\u56fa\u5b9a\u6761\u6b3e\u5148\u5199\u4e0a\u2014\u2014\u5176\u5b9e\u4e0d\u5199\u4e5f\u6ca1\u5173\u7cfb\uff0c\u5951\u7ea6\u9ed8\u8ba4\u7684\uff0c\u53ea\u662f\u51fa\u4e8e\u7a33\u59a5\u8d77\u89c1\u7f62\u4e86\u3002\u201c\u4f60\u8fd8\u6709\u4ec0\u4e48\u8865\u5145\u5efa\u8bae\u5417\uff1f\u201d\u4ed6\u95ee\u838e\u73de\u514b\u3002</p><p>\u201c\u6211\u4e0d\u662f\u9b54\u9b3c\uff0c\u8fd9\u79cd\u54ac\u6587\u56bc\u5b57\u63a8\u6572\u6761\u6b3e\u7684\u4e8b\u60c5\u6211\u53ef\u4e0d\u64c5\u957f\uff0c\u201d\u9b45\u9b54\u5a07\u7b11\u7740\uff0c\u201c\u53cd\u6b63\u6211\u5e26\u9886\u4f60\u4eec\u53bb\u65ad\u57df\u9547\u5c31\u662f\u2014\u2014\u4f46\u6709\u4e00\u70b9\u6211\u5fc5\u987b\u8bf4\u6e05\u695a\uff0c\u8fd9\u4e2a\u2018\u4f60\u4eec\u2019\uff0c\u4ec5\u4ec5\u662f\u6307\u4f60\u548c\u4f60\u7684\u5c0f\u60c5\u4eba\uff0c\u4e0d\u5305\u62ec\u90a3\u8fb9\u4e09\u4e2a\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d\u743c\u6069\u4e00\u6014\uff0c\u9690\u9690\u6709\u4e9b\u607c\u706b\uff0c\u201c\u8fd9\u662f\u4ec0\u4e48\u610f\u601d\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\u610f\u601d\uff0c\u4f60\u4eec\u662f\u6211\u8ba4\u8bc6\u7684\u4eba\uff0c\u6240\u4ee5\u6211\u613f\u610f\u5e2e\u5fd9\uff0c\u4f55\u51b5\u8981\u8bba\u8d77\u6765\uff0c\u8fd9\u4f4d\u5723\u6b66\u58eb\u5c0f\u59d0\u8fd8\u662f\u6211\u7684\u59d0\u59b9\uff0c\u81f3\u4e8e\u4f60\u4e48\uff0c\u6211\u8bb0\u5f97\u6211\u4eec\u5728\u70db\u5821\u7684\u65f6\u5019\uff0c\u4e5f\u66fe\u7ecf\u6709\u8fc7\u5f88\u7f8e\u5999\u2026\u2026\u201d</p><p>\u201c\u597d\u4e86\uff0c\u201d\u743c\u6069\u6253\u65ad\u5979\u7684\u8bdd\uff0c\u201c\u5979\u4eec\u4e09\u4eba\u662f\u6211\u7684\u670b\u53cb\uff0c\u751a\u81f3\u662f\u88ab\u6211\u8fde\u7d2f\u624d\u6765\u5230\u8fd9\u91cc\u3002\u6211\u4e0d\u53ef\u80fd\u4e22\u4e0b\u5979\u4eec\u4e0d\u7ba1\uff0c\u8fd9\u529e\u4e0d\u5230\u3002\u201d</p><p>\u201c\u8fd9\u548c\u6211\u65e0\u5173\uff0c\u201d\u9b45\u9b54\u7684\u5634\u89d2\u4f18\u7f8e\u5730\u7fd8\u8d77\uff0c\u201c\u5979\u4eec\u53c8\u4e0d\u662f\u6211\u670b\u53cb\uff0c\u800c\u4e14\u5979\u4eec\u521a\u521a\u6740\u4e86\u6211\u7684\u56db\u4e2a\u540c\u4f34\u3002\u201d</p><p>\u743c\u6069\u76b1\u7740\u7709\u5934\uff0c\u6076\u9b54\u4e5f\u4f1a\u6709\u4ec0\u4e48\u201c\u540c\u4f34\u201d\u7684\u6982\u5ff5\uff1f\u522b\u5f00\u73a9\u7b11\u4e86\u3002\u4f46\u838e\u73de\u514b\u8fd9\u4e48\u8bf4\uff0c\u5206\u660e\u5c31\u662f\u6545\u610f\u5201\u96be\uff0c\u201c\u4f60\u5230\u5e95\u60f3\u600e\u4e48\u6837\uff1f\u201d\u4ed6\u95ee\uff0c\u201c\u76f4\u8bf4\u5427\u3002\u201d</p><p>\u201c\u5982\u679c\u8981\u5e26\u4e0a\u5979\u4eec\u4e09\u4e2a\uff0c\u90a3\u4e48\u663e\u7136\u6211\u5e94\u8be5\u8981\u6c42\u66f4\u591a\u7684\u56de\u62a5\uff0c\u4ee5\u4fbf\u5e73\u590d\u6211\u4e27\u5931\u540c\u4f34\u7684\u5fe7\u4f24\uff0c\u5b89\u6170\u6211\u53d7\u60ca\u7684\u5fc3\u7075\uff0c\u4ee5\u53ca\u4f5c\u4e3a\u589e\u52a0\u5de5\u4f5c\u91cf\u7684\u916c\u52b3\u2014\u2014\u8fd9\u662f\u5f88\u5408\u7406\u7684\u8981\u6c42\u5427\u3002\u201d</p><p>\u5408\u7406\u4e2a\u5934\uff0c\u743c\u6069\u5fc3\u60f3\uff0c\u4f46\u538b\u6291\u7740\u607c\u6012\uff0c\u201c\u8bf7\u8bf4\u5427\u3002\u201d</p><p>\u201c\u522b\u62c5\u5fc3\uff0c\u522b\u7d27\u5f20\uff0c\u201d\u9b45\u9b54\u4e0d\u7d27\u4e0d\u6162\u5730\u8bf4\uff0c\u201c\u6211\u7684\u8981\u6c42\u5f88\u7b80\u5355\uff0c\u6beb\u65e0\u534a\u70b9\u96be\u5ea6\uff1a\u6211\u8981\u6210\u4e3a\u4f60\u7684\u60c5\u4eba\u3002\u201d</p><p>\u743c\u6069\u6115\u7136\uff0c\u600e\u4e48\u90fd\u6ca1\u60f3\u5230\u5979\u63d0\u51fa\u7684\u662f\u8fd9\u79cd\u8981\u6c42\uff0c\u201c\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6211\u76f8\u4fe1\u6211\u7684\u610f\u601d\u5df2\u7ecf\u8868\u8fbe\u5f97\u5f88\u6e05\u695a\uff0c\u6211\u8981\u6210\u4e3a\u4f60\u7684\u60c5\u4eba\uff0c\u5f53\u7136\u4e5f\u56e0\u6b64\u62e5\u6709\u4e00\u5207\u60c5\u4eba\u5e94\u6709\u7684\u6743\u5229\u548c\u5f85\u9047\u2014\u2014\u6211\u4eec\u90fd\u77e5\u9053\u662f\u54ea\u4e9b\uff0c\u201d\u5979\u7728\u4e86\u7728\u773c\u775b\uff0c\u7ea2\u5b9d\u77f3\u822c\u6676\u83b9\u5254\u900f\u7684\u53cc\u7738\u4e2d\u6c34\u6c14\u6c6a\u6c6a\uff0c\u542b\u60c5\u8109\u8109\uff0c\u201c\u6211\u76f8\u4fe1\u5f88\u591a\u4eba\u5bf9\u6b64\u90fd\u6c42\u4e4b\u4e0d\u5f97\u5427\uff0c\u6bd5\u7adf\u9b45\u9b54\u53ef\u662f\u4e16\u754c\u4e0a\u6700\u5b8c\u7f8e\u7684\u60c5\u4eba\u5462\uff0c\u800c\u4e14\u56e0\u4e3a\u5951\u7ea6\u7684\u9650\u5236\uff0c\u6211\u8fd8\u4e0d\u4f1a\u4f24\u5bb3\u4f60\u3002\u201d</p><p>\u743c\u6069\u6447\u5934\uff0c\u62b1\u7d27\u6000\u4e2d\u7684\u5c11\u5973\uff0c\u201c\u4e0d\u53ef\u80fd\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4e0d\u4f1a\u653e\u5f03\u827e\u5f25\u8587\u3002\u201d</p><p>\u201c\u4e0d\uff0c\u4e0d\uff0c\u4f60\u8bef\u4f1a\u4e86\uff0c\u201d\u9b45\u9b54\u8fde\u8fde\u6447\u5934\uff0c\u201c\u6070\u6070\u76f8\u53cd\uff0c\u5f53\u7136\u4e5f\u662f\u6211\u7684\u8868\u8ff0\u4e0d\u591f\u6e05\u695a\u3002\u6211\u7684\u8981\u6c42\u4ec5\u4ec5\u662f\u4f60\u7684\u60c5\u4eba\u4e4b\u4e00\uff0c\u800c\u5e76\u4e0d\u662f\u552f\u4e00\u7684\u60c5\u4eba\u3002\u4e8b\u5b9e\u4e0a\uff0c\u5982\u679c\u4f60\u653e\u5f03\u4e86\u5723\u6b66\u58eb\u5c0f\u59d0\uff0c\u90a3\u4e48\u6211\u53cd\u800c\u4f1a\u5f88\u9057\u61be\u7684\u3002\u201d</p><p>\u201c\u8fd9\u53c8\u662f\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u6211\u77e5\u9053\u5979\u4e0d\u559c\u6b22\u548c\u4eba\u5206\u4eab\uff0c\u201d\u838e\u73de\u514b\u5fae\u7b11\uff0c\u9732\u51fa\u6d01\u767d\u6574\u9f50\u7684\u8d1d\u9f7f\uff0c\u201c\u5979\u610f\u5fd7\u575a\u5b9a\uff0c\u6267\u7740\u672c\u5fc3\uff0c\u4e0d\u60e7\u6015\u6b7b\u4ea1\uff0c\u4e0d\u60e7\u6015\u4f24\u5bb3\uff0c\u4e0d\u62b1\u5e0c\u671b\uff0c\u4e0d\u6c42\u56de\u62a5\uff0c\u51e0\u4e4e\u662f\u65e0\u61c8\u53ef\u51fb\uff0c\u5e78\u597d\u5979\u8fd8\u662f\u6709\u5f31\u70b9\u7684\u3002\u5979\u5df2\u7ecf\u6e10\u6e10\u7231\u4e0a\u4f60\uff0c\u5979\u4e0d\u4f1a\u63a5\u53d7\u548c\u4eba\u5206\u4eab\u2014\u2014\u4f46\u662f\u5979\u73b0\u5728\u5fc5\u987b\u63a5\u53d7\uff0c\u90a3\u4e48\u6211\u60f3\u8fd9\u4e00\u5b9a\u662f\u5f88\u6709\u8da3\u7684\u4e8b\u60c5\u3002\u201d</p><p>\u5982\u679c\u662f\u522b\u4eba\uff0c\u6885\u83f2\u65af\u8fd8\u53ef\u4ee5\u50cf\u5979\u534a\u5f00\u73a9\u7b11\u5a01\u80c1\u7684\u90a3\u6837\uff0c\u76f4\u63a5\u4e00\u5251\u6740\u4e86\u3002\u4f46\u6709\u7075\u9b42\u5951\u7ea6\u5728\uff0c\u838e\u73de\u514b\u6b7b\uff0c\u743c\u6069\u4e5f\u5c31\u6b7b\uff0c\u8fd9\u79cd\u4f5c\u6cd5\u5c31\u5b8c\u5168\u884c\u4e0d\u901a\u3002</p><p>\u201c\u522b\u90a3\u4e48\u4e25\u8083\uff0c\u6211\u77e5\u9053\u4f60\u5176\u5b9e\u5f88\u4e50\u89c1\u5176\u6210\uff0c\u201d\u9b45\u9b54\u683c\u683c\u7b11\u7740\uff0c\u201c\u6ca1\u6709\u54ea\u4e2a\u7537\u6027\u4f1a\u62d2\u7edd\u8fd9\u79cd\u63d0\u8bae\uff0c\u6240\u4ee5\u51b3\u5b9a\u6743\u73b0\u5728\u8f6c\u79fb\u5230\u6885\u83f2\u65af\u5c0f\u59d0\u624b\u4e2d\u3002\u90a3\u4e48\uff0c\u7ed9\u6211\u4eec\u4e00\u4e2a\u7b54\u6848\u5427\uff0c\u662f\u653e\u5f03\u4f60\u7684\u4e09\u4f4d\u540c\u4f34\uff0c\u8fd8\u662f\u653e\u5f03\u4f60\u7684\u60c5\u4eba\u7684\u4e00\u90e8\u5206\uff0c\u5723\u6b66\u58eb\uff1f\u201d</p></article>"}}}]);
