(window.webpackJsonp=window.webpackJsonp||[]).push([[310],{333:function(p){p.exports={chapterTitle:null,title:"\u7b2c190\u7ae0 \u6c11\u4e0d\u754f\u6b7b",content:"<article><h2>\u7b2c190\u7ae0 \u6c11\u4e0d\u754f\u6b7b</h2><p>\u3000\u3000\u5f20\u606a\u8fd9\u51e0\u5929\u7559\u5728\u5b66\u6821\u4e00\u5fc3\u51c6\u5907\u671f\u672b\u8003\u8bd5\u7684\u4e8b\u60c5\uff0c\u8fde\u9526\u6e56\u90fd\u6ca1\u6709\u53bb\uff0c\u6ca1\u6709\u60f3\u5230\u8fd9\u91cc\u95f9\u51fa\u8fd9\u4e48\u5927\u7684\u52a8\u9759\u6765\u3002\u5f20\u606a\u6ca1\u6709\u542c\u5230\u6d88\u606f\uff0c\u4ece\u53e6\u4e00\u65b9\u9762\uff0c\u4e5f\u8bf4\u660e\u5e02\u91cc\u5bf9\u8fd9\u79cd\u6027\u8d28\u7684\u4e8b\u60c5\u4fdd\u6301\u6cf0\u7136\u5904\u4e4b\u7684\u6001\u5ea6\uff0c\u5927\u6982\u8bb8\u591a\u4eba\u90fd\u8ba4\u4e3a\u4e3a\u4e86\u5feb\u901f\u63a8\u8fdb\u57ce\u5e02\u5316\u5efa\u8bbe\uff0c\u90a3\u56db\u5341\u6237\u88ab\u5f3a\u62c6\u7684\u5bb6\u5ead\u7684\u5408\u6cd5\u6743\u76ca\u662f\u53ef\u4ee5\u5ffd\u7565\u7684\uff0c\u662f\u53ef\u4ee5\u727a\u7272\u7684\u3002</p><p>\u3000\u3000\u65bd\u536b\u5fe0\u8bf4\u201c\u5728\u524d\u51e0\u5929\u6240\u8c13\u7684\u62c6\u8fc1\u52a8\u5458\u4f1a\u4e0a\u3002\u4e07\u526f\u5e02\u957f\u653e\u51fa\u8bdd\u6765\uff1a\u2018\u8c01\u6562\u62d6\u62c6\u8fc1\u7684\u540e\u817f\uff0c\u6211\u5c31\u6253\u65ad\u4ed6\u7684\u540e\u817f\uff0c\u8c01\u5f71\u54cd\u6c99\u7530\u53d1\u5c55\u4e00\u9635\uff0c\u6211\u5c31\u5f71\u54cd\u4ed6\u4e00\u8f88\u5b50\u2026\u2026\u2019\u201d</p><p>\u3000\u3000\u5f20\u606a\u5b8c\u5168\u80fd\u60f3\u8c61\u51fa\u4e07\u52c7\u56a3\u5f20\u8dcb\u6248\u7684\u6837\u5b50\uff0c\u542c\u7740\u62c6\u8fc1\u73b0\u573a\u91cc\u9664\u4e86\u673a\u5668\u7684\u8f70\u9e23\u58f0\uff0c\u4f3c\u4e4e\u8fd8\u6709\u5176\u4ed6\u7684\u52a8\u9759\u3002\u62cd\u4e86\u62cd\u5085\u4fca\u7684\u80a9\u8180\uff0c\u8ba9\u4ed6\u5c06\u8f66\u5b50\u505c\u8fc7\u53bb\u3002\u62c6\u8fc1\u573a\u5730\u8fb9\u4e0a\u90fd\u6709\u62c9\u8d77\u8b66\u6212\u7ebf\uff0c\u8f66\u5b50\u5c31\u505c\u5728\u8b66\u6212\u7ebf\u8fb9\u4e0a\uff0c\u4ece\u8fd9\u8fb9\u770b\u8fc7\u53bb\uff0c\u91cc\u9762\u6709\u9ad8\u538b\u6c34\u67aa\u51b2\u51fb\u7684\u75d5\u8ff9\u3002\u5386\u53f2\u5e76\u6ca1\u6709\u66f4\u6539\uff0c\u53ea\u662f\u5c06\u4e5d\u4e03\u5e74\u66b4\u529b\u62c6\u8fc1\u7684\u4e00\u5e55\u63d0\u524d\u5230\u4e5d\u4e94\u5e74\u800c\u5df2\uff0c\u5f20\u606a\u751a\u81f3\u80fd\u60f3\u8c61\u51fa\u5f31\u5c0f\u7684\u62c6\u8fc1\u6237\u60f3\u56e2\u7ed3\u8d77\u6765\u5236\u6b62\u62c6\u8fc1\u516c\u53f8\u7ec4\u7ec7\u7684\u6d41\u6c13\u5730\u75de\u51b2\u8fdb\u4ed6\u4eec\u7684\u5bb6\u56ed\u5374\u7ed9\u9ad8\u538b\u6c34\u67aa\u51b2\u51fb\u3001\u51b2\u6563\u7684\u60c5\u5f62\u3002</p><p>\u3000\u3000\u8b66\u6212\u7ebf\u91cc\u8fd8\u6709\u51e0\u680b\u5c0f\u697c\u6ca1\u6709\u7ed9\u62c6\u6389\uff0c\u4f46\u662f\u9662\u5899\u90fd\u7ed9\u6252\u6389\u4e86\uff0c\u5b64\u96f6\u96f6\u7684\u7ad6\u7acb\u5728\u5e9f\u589f\u91cc\uff0c\u5341\u5206\u7684\u523a\u773c\uff0c\u5f02\u5e38\u7684\u58f0\u97f3\u5c31\u662f\u4ece\u4e00\u680b\u697c\u91cc\u4f20\u51fa\u6765\u7684\uff0c\u5e95\u5c4b\u91cc\u9762\u4eae\u7740\u706f\uff0c\u6709\u4eba\u5f71\u5728\u6643\u52a8\u3002</p><p>\u3000\u3000\u4e24\u540d\u9752\u5e74\u4ece\u90a3\u91cc\u94bb\u51fa\u6765\uff0c\u4ed6\u4eec\u770b\u89c1\u505c\u5728\u8b66\u6212\u7ebf\u5916\u505c\u7740\u7684\u5c0f\u8f66\uff0c\u5c0f\u6b65\u6e9c\u8fc7\u6765\uff0c\u5634\u91cc\u56b7\u56b7\u7740\uff0c\u6325\u624b\u8d76\u5f20\u606a\u4ed6\u4eec\u79bb\u5f00\uff1a\u201c\u770b\u4ec0\u4e48\u770b\uff01\u6709\u9e21\u5df4\u597d\u770b\u7684\uff01\u201d\u7a7f\u7740\u9ed1\u8272\u5f39\u529b\u80cc\u5fc3\uff0c\u8180\u5927\u8170\u5706\uff0c\u5243\u7740\u53ef\u89c1\u5934\u76ae\u7684\u5e73\u5934\uff0c\u80a9\u8180\u8fd8\u7eb9\u7740\u523a\u8eab\uff0c\u751f\u6015\u522b\u4eba\u4e0d\u77e5\u9053\u4ed6\u4eec\u662f\u5730\u75de\u6d41\u6c13\u3002</p><p>\u3000\u3000\u5f20\u606a\u80f3\u818a\u538b\u7740\u8f66\u7a97\uff0c\u770b\u89c1\u4e00\u4e2a\u4eba\u8dcc\u8dcc\u649e\u649e\u51b2\u51fa\u5c4b\u5b50\uff0c\u540e\u9762\u8d70\u51fa\u4e24\u4e2a\u7a7f\u9ed1\u8272\u5f39\u529b\u80cc\u5fc3\u7684\u5730\u75e6\u51b2\u7740\u4ed6\u7684\u540e\u80cc\u5c31\u662f\u4e24\u811a\uff0c\u8ba9\u90a3\u4eba\u4e00\u5934\u683d\u8fdb\u788e\u7816\u5c51\u5806\u91cc\u53bb\u3002\u90a3\u4e24\u4eba\u8fd9\u65f6\u624d\u770b\u8b66\u6212\u7ebf\u505c\u7740\u4e00\u90e8\u8f66\uff0c\u624d\u6536\u655b\u4e86\u4e00\u4e9b\uff0c\u4ece\u540e\u9762\u5c06\u90a3\u540d\u6c49\u5b50\u6293\u4f4f\u3002\u90a3\u540d\u6c49\u5b50\u60f3\u53cd\u6297\u6765\u7740\uff0c\u624b\u817f\u5374\u7ed9\u6293\u7d27\uff0c\u6323\u624e\u4e0d\u5f97\u3002\u7d27\u63a5\u7740\u53c8\u4ece\u5b85\u5b50\u91cc\u8d70\u51fa\u4e24\u4eba\uff0c\u62c6\u8fc1\u73b0\u573a\u9ad8\u529f\u7387\u7684\u7167\u5c04\u706f\u5c06\u8fd9\u4e00\u5c0f\u7247\u533a\u57df\u7167\u5f97\u7ea4\u6beb\u6bd5\u5448\uff0c\u5f20\u606a\u8ba4\u51fa\u5176\u4e2d\u4e00\u4eba\u5c31\u662f\u9526\u57ce\u96c6\u56e2\u7684\u8001\u677f\u8d75\u9526\u8363\uff0c\u4ed6\u65c1\u8fb9\u7684\u4e00\u4eba\u7a7f\u6234\u4e5f\u4e0d\u5dee\uff0c\u4e0d\u8ba4\u5f97\u4ed6\u662f\u8c01\u3002</p><p>\u3000\u3000\u8d75\u9526\u8363\u6beb\u4e0d\u907f\u5acc\u7684\u671d\u8fd9\u8fb9\u8d70\u8fc7\u6765\uff0c\u76b1\u7740\u7709\u5934\u50ac\u4fc3\u8b66\u6212\u7ebf\u8fb9\u7684\u4e24\u4e2a\u5730\u75de\uff1a\u201c\u4ec0\u4e48\u4eba\u5728\u90a3\u91cc\uff0c\u8fd8\u4e0d\u8d76\u8d70\uff1f\u201d\u5728\u4ed6\u770b\u6765\uff0c\u6d77\u5dde\u5e02\u5df2\u7ecf\u65e0\u4eba\u80fd\u5236\u6b62\u4ed6\u6a2a\u884c\u9738\u9053\u4e86\u3002</p><p>\u3000\u3000\u4e09\u4eba\u62bc\u7740\u88ab\u6253\u7684\u90a3\u4eba\u4e5f\u8ddf\u7740\u8d70\u8fc7\u6765\uff0c\u90a3\u4eba\u9f3b\u9752\u8138\u80bf\uff0c\u90fd\u662f\u8840\u8ff9\uff0c\u624b\u81c2\u65e0\u529b\u7684\u5782\u6302\u7740\uff0c\u770b\u4e0a\u53bb\u50cf\u9aa8\u6298\u4e86\uff0c\u770b\u6765\u5728\u5c4b\u5b50\u91cc\u53d7\u8fc7\u4e00\u756a\u6298\u78e8\u3002\u4ed6\u7684\u5934\u53d1\u7ed9\u540e\u9762\u4eba\u63ea\u7d27\uff0c\u4e0d\u5f97\u4e0d\u4ef0\u7740\u5934\uff0c\u67d3\u7740\u8840\u8ff9\u7684\u8840\u770b\u4e0a\u53bb\u989d\u5916\u7684\u72f0\u72de\uff0c\u95ed\u7740\u773c\u775b\u7ed9\u63a8\u7740\u5f80\u524d\uff0c\u5076\u5c14\u7741\u5f00\u6765\uff0c\u5374\u662f\u51f6\u608d\u7684\u76ee\u5149\uff0c\u6beb\u65e0\u5c48\u670d\u7684\u575a\u5b9a\u3002</p><p>\u3000\u3000\u201c\u6d77\u9f99\u2026\u2026\u201d\u5085\u4fca\u8f7b\u547c\u4e86\u4e00\u58f0\uff0c\u4ed6\u8ba4\u51fa\u7ed9\u626d\u6253\u7684\u90a3\u540d\u6c49\u5b50\u662f\u8c01\uff0c\u624b\u6293\u4f4f\u95e8\u628a\u624b\uff0c\u624b\u80cc\u4e0a\u9752\u7b4b\u66b4\u8df3\uff0c\u5374\u5fcd\u4f4f\u6ca1\u6709\u63a8\u5f00\u8f66\u95e8\u51b2\u51fa\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u89c1\u5085\u4fca\u7684\u5f02\u72b6\uff0c\u95ee\u4ed6\uff1a\u201c\u4f60\u8ba4\u8bc6\u8fd9\u4eba\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ee5\u524d\u7684\u6218\u53cb\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u90a3\u5c31\u4e0d\u80fd\u5750\u89c6\u4e0d\u7ba1\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u5230\u5f20\u606a\u8fd9\u53e5\u8bdd\uff0c\u5085\u4fca\u63a8\u51fa\u8f66\u95e8\uff0c\u4eff\u4f5b\u4e00\u5934\u730e\u8c79\u4e00\u6837\u7a9c\u4e86\u51fa\u53bb\uff0c\u51b2\u7740\u4e24\u4e2a\u7ad9\u5728\u8f66\u95e8\u5916\u7684\u5730\u75e6\u63aa\u624b\u4e0d\u53ca\u4e4b\u9645\uff0c\u5f53\u80f8\u4e00\u817f\u8e6c\u7ffb\u4e00\u4eba\uff0c\u97ad\u817f\u626b\u5012\u4e00\u4eba\uff0c\u51e0\u4e4e\u6ca1\u6709\u505c\u987f\uff0c\u5085\u4fca\u5355\u624b\u76f4\u6293\u8d75\u9526\u8363\u9762\u95e8\uff0c\u5728\u5176\u4ed6\u7684\u5730\u75e6\u8fd8\u6ca1\u6709\u53cd\u5e94\u4e4b\u65f6\uff0c\u5de6\u624b\u5df2\u7ecf\u9501\u4f4f\u8d75\u9526\u8363\u7684\u54bd\u5589\u3002</p><p>\u3000\u3000\u771f\u662f\u7cbe\u5f69\uff0c\u5085\u4fca\u4e5f\u77e5\u9053\u5148\u9707\u6151\u4f4f\u8d75\u9526\u8363\u8fd9\u4e2a\u5173\u952e\u4eba\u7269\u3002\u5f20\u606a\u5567\u5567\u53eb\u4e86\u4e24\u58f0\uff0c\u624d\u63a8\u95e8\u4e0b\u8f66\uff0c\u6c89\u58f0\u8bad\u65a5\u5085\u4fca\uff1a\u201c\u653e\u624b\uff0c\u600e\u4e48\u5bf9\u8d75\u603b\u8fd9\u4e48\u65e0\u793c\uff1f\u201d</p><p>\u3000\u3000\u5085\u4fca\u677e\u5f00\u624b\uff0c\u5c06\u8d75\u9526\u8363\u63a8\u5411\u4ed6\u8eab\u540e\u90a3\u4e09\u4e2a\u5730\u75e6\u3002</p><p>\u3000\u3000\u8d75\u9526\u8363\u7ed9\u5085\u4fca\u66b4\u8d77\u88ad\u51fb\uff0c\u60ca\u614c\u5931\u63aa\u4e4b\u4e0b\u5012\u5fd8\u4e86\u6323\u624e\uff0c\u5f85\u5085\u4fca\u8131\u5f00\u624b\uff0c\u624d\u770b\u6e05\u5f20\u77e5\u884c\u7684\u513f\u5b50\u5f20\u606a\u7b11\u76c8\u76c8\u7684\u7ad9\u5728\u524d\u9762\uff0c\u5fc3\u91cc\u607c\u706b\uff0c\u4f46\u662f\u5f20\u5bb6\u540e\u9762\u7ad9\u7740\u7701\u957f\u5f90\u5b66\u5e73\uff0c\u4e0d\u5bb9\u4ed6\u4e0d\u987e\u5fcc\uff0c\u76ee\u5149\u9634\u51b7\u7684\u76ef\u7740\u5f20\u606a\uff1a\u201c\u539f\u6765\u662f\u53f8\u660e\u7684\u540c\u5b66\uff0c\u4f60\u5728\u8fd9\u91cc\u505a\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u957f\u591c\u6f2b\u6f2b\uff0c\u65e0\u5fc3\u7761\u773c\uff0c\u542c\u5230\u91cc\u9762\u6709\u52a8\u9759\uff0c\u60f3\u505c\u4e0b\u8f66\u6765\u770b\u770b\u70ed\u95f9\uff0c\u6ca1\u60f3\u5230\u8d75\u603b\u5c48\u5c0a\u5728\u8fd9\u91cc\u529e\u4e8b\u3002\u6211\u5012\u662f\u89c9\u5f97\u5947\u602a\u4e86\uff0c\u6211\u5bb6\u53f8\u673a\u7684\u8fd9\u4f4d\u6218\u53cb\uff0c\u5230\u5e95\u6709\u591a\u5927\u7684\u80fd\u8010\uff0c\u8981\u52b3\u8d75\u603b\u4eb2\u81ea\u51fa\u624b\u6536\u62fe\uff1f\u201d\u5f20\u606a\u6487\u5634\u6307\u7740\u90a3\u4e2a\u4e0d\u6210\u4eba\u5f62\u7684\u9a6c\u6d77\u9f99\uff0c\u5012\u4e0d\u6653\u5f97\u5085\u4fca\u600e\u4e48\u4ece\u8fd9\u5f20\u88ab\u6253\u5f97\u4e0d\u6210\u5f62\u7684\u8138\u4e0a\u8ba4\u51fa\u662f\u81ea\u5df1\u7684\u6218\u53cb\u3002</p><p>\u3000\u3000\u8bdd\u8bf4\u201c\u957f\u591c\u6f2b\u6f2b\uff0c\u65e0\u5fc3\u7761\u7720\u201d\u8fd9\u53e5\u8bdd\u8fd8\u662f\u51fa\u81ea\u300a\u5927\u8bdd\u897f\u6e38\u300b\u4e2d\u81f3\u5c0a\u5b9d\u4e4b\u53e3\uff0c\u53ea\u662f\u8fd9\u90e8\u4e5d\u56db\u5e74\u5e95\u62cd\u6210\u7684\u7ecf\u5178\u7535\u5f71\u5728\u4e5d\u4e94\u5e74\u5e02\u573a\u53cd\u5e94\u5e73\u6de1\uff0c\u5728\u56fd\u5185\u9ed8\u9ed8\u65e0\u95fb\uff0c\u4e00\u76f4\u5230\u4e5d\u4e03\u3001\u4e5d\u516b\u5e74\u76d7\u7248\u789f\u5728\u9ad8\u6821\u5b66\u751f\u4e2d\u95f4\u6d41\u884c\u8d77\u6765\uff0c\u624d\u98ce\u9761\u534e\u8bed\u5f71\u575b\uff0c\u6210\u4e3a\u540e\u4eba\u65e0\u6cd5\u8d85\u8d8a\u7684\u7ecf\u5178\u4e4b\u4f5c\u3002</p><p>\u3000\u3000\u201c\u8ddf\u8d75\u603b\u6c42\u4e2a\u4eba\u60c5\uff0c\u8fd9\u4eba\u6211\u5e26\u8d70\u3002\u201d\u5f20\u606a\u6307\u7740\u8d75\u9526\u8363\uff0c\u53c8\u6307\u4e86\u6307\u4ed6\u8eab\u540e\u7ed9\u67b6\u4f4f\u65e0\u6cd5\u52a8\u5f39\u7684\u9a6c\u6d77\u9f99\u3002</p><p>\u3000\u3000\u8d75\u9526\u8363\u9f3b\u5b50\u90fd\u5dee\u70b9\u6c14\u6b6a\u4e86\uff0c\u5f20\u5bb6\u80cc\u666f\u5728\u6d77\u5dde\u80cc\u666f\u6df1\u539a\uff0c\u8c01\u90fd\u8981\u8ba9\u4ed6\u4eec\u4e09\u5206\u4e0d\u5047\uff0c\u8d75\u9526\u8363\u5374\u600e\u4e48\u80fd\u5fcd\u53d7\u5341\u4e03\u516b\u5c81\u7684\u534a\u5927\u5c11\u5e74\u6307\u7740\u4ed6\u7684\u9f3b\u5b50\u8981\u4eba\u3002</p><p>\u3000\u3000\u201c\u8d75\u603b\u89c9\u5f97\u59d4\u5c48\u4e86\uff0c\u4e0d\u628a\u4eba\u4ea4\u51fa\u6765\u4e5f\u884c\uff0c\u6211\u4eec\u603b\u4e0d\u81f3\u4e8e\u6015\u8d75\u603b\u6709\u672c\u4e8b\u5c06\u8fd9\u4eba\u53d8\u6ca1\u6709\u4e86\u3002\u53ea\u662f\u62d6\u5230\u660e\u5929\uff0c\u8d75\u603b\u8fd8\u8981\u4e3a\u4ed6\u8eab\u4e0a\u7684\u4f24\u75d5\u89e3\u91ca\uff0c\u591a\u4e48\u8d39\u5fc3\u52b3\u547d\u7684\u4e8b\u60c5\u2026\u2026\u201d\u5f20\u606a\u4ece\u5bb9\u4e0d\u8feb\u7684\u76ef\u7740\u8d75\u9526\u8363\u3002</p><p>\u3000\u3000\u65f6\u5e74\u56db\u5341\u516d\u5c81\u7684\u8d75\u9526\u8363\u5728\u6d77\u5dde\u5e02\u7684\u786e\u79f0\u5f97\u4e0a\u67ad\u96c4\uff0c\u5728\u4ed6\u7684\u624b\u91cc\uff0c\u9526\u57ce\u96c6\u56e2\u53d1\u5c55\u6210\u6d77\u5dde\u5e02\u6700\u5927\u7684\u6c11\u8425\u4f01\u4e1a\uff0c\u4f46\u662f\u725f\u53d6\u66b4\u5229\u4e0d\u62e9\u624b\u6bb5\u7684\u4f5c\u98ce\u8ba9\u4eba\u618e\u6068\u3002</p><p>\u3000\u3000\u8d75\u9526\u8363\u8138\u5fae\u5fae\u626d\u66f2\u7740\uff0c\u4eca\u5929\u8fd9\u53e3\u6c14\u4ed6\u8fd8\u5fc5\u987b\u8981\u5fcd\u4e0b\u6765\uff0c\u5f20\u606a\u94c1\u7740\u5fc3\u8981\u63d2\u624b\u8fd9\u4ef6\u4e8b\uff0c\u4ed6\u96be\u9053\u80fd\u5c06\u4eba\u53d8\u6ca1\u4e86\uff1f\u8d70\u5230\u9a6c\u6d77\u9f99\u7684\u9762\u524d\uff0c\u63ea\u7740\u4ed6\u7684\u9886\u53e3\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u7b97\u4f60\u8d70\u8fd0\uff0c\u4f60\u5c0f\u5b50\u7ed9\u8001\u5b50\u8bb0\u4f4f\uff0c\u8d75\u5bb6\u4eba\u53ef\u4e0d\u662f\u90a3\u4e48\u597d\u5a01\u80c1\u7684\uff01\u201d\u62fd\u7740\u9a6c\u6d77\u9f99\u7684\u9886\u53e3\uff0c\u731b\u7684\u5c06\u4ed6\u63a8\u4e86\u8fc7\u6765\uff0c\u624d\u62cd\u62cd\u624b\u5bf9\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u4eca\u5929\u7684\u4e8b\u60c5\u4f60\u4e0d\u61c2\uff0c\u6211\u4e0d\u602a\u4f60\uff0c\u4eba\u4f60\u5e26\u8d70\uff0c\u4e0b\u56de\u5c31\u4e0d\u4f1a\u51d1\u5de7\u8ba9\u4f60\u770b\u5230\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u563f\u563f\u4e00\u7b11\uff0c\u4e00\u8138\u865a\u4f2a\u7684\u5173\u5fc3\u8d75\u9526\u8363\uff1a\u201c\u8fd9\u5bb6\u4f19\u96be\u9053\u5a01\u80c1\u8981\u52a8\u8d75\u603b\u7684\u5bb6\u91cc\u4eba\uff1f\u771f\u662f\u4e0d\u53ef\u7406\u55bb\u7684\u66b4\u6c11\uff0c\u62c6\u51e0\u680b\u623f\u5b50\u800c\u5df2\uff0c\u5c31\u503c\u5f97\u4ed6\u4eec\u8fd9\u6837\u94e4\u800c\u8d70\u9669\uff1f\u4e0d\u8fc7\u8bdd\u8bf4\u56de\u6765\uff0c\u6c11\u4e0d\u754f\u6b7b\uff0c\u5f00\u53d1\u5546\u7684\u65e5\u5b50\u5c31\u96be\u8fc7\u4e86\uff0c\u96be\u602a\u9526\u6e56\u4e00\u76f4\u6ca1\u5fc3\u601d\u505a\u65e7\u57ce\u6539\u9020\u7684\u9879\u76ee\uff0c\u5c31\u6015\u9047\u5230\u4e00\u4e24\u4e2a\u4e0d\u6015\u6b7b\u7684\u4eba\u8ddf\u4f60\u73a9\u547d\u2026\u2026\u201d\u770b\u7740\u5085\u4fca\u5c06\u6ee1\u8eab\u8840\u8ff9\u7684\u9a6c\u6d77\u9f99\u88c5\u8fdb\u8f66\u91cc\uff0c\u53c8\u5bf9\u8138\u8272\u5f88\u5dee\u7684\u8d75\u9526\u8363\u8bf4\u9053\uff0c\u201c\u8bdd\u8bf4\u56de\u6765\uff0c\u9526\u57ce\u53ea\u662f\u5f00\u53d1\u5546\uff0c\u53c8\u4e0d\u662f\u9526\u57ce\u62c6\u4ed6\u4eec\u7684\u623f\u5b50\uff0c\u8fd9\u5bb6\u4f19\u600e\u4e48\u627e\u5230\u8d75\u603b\u7684\u5934\u4e0a\u53bb\u4e86\uff1f\u201d</p><p>\u3000\u3000\u8d75\u9526\u8363\u51b7\u54fc\u4e86\u4e00\u58f0\uff0c\u53ef\u6ca1\u6709\u5fc3\u60c5\u89e3\u91ca\u7ed9\u5f20\u606a\u542c\uff0c\u626d\u5934\u5c31\u8d70\u4e86\uff0c\u5f20\u606a\u8fd9\u65f6\u5019\u624d\u770b\u623f\u5b50\u53c8\u8d70\u51fa\u56db\u4e2a\u4eba\u6765\uff0c\u770b\u7a7f\u7740\u8ddf\u8d75\u9526\u8363\u4ed6\u4eec\u662f\u4e00\u4f19\u7684\uff0c\u90fd\u53d7\u4e86\u4e9b\u4f24\uff0c\u76f8\u4e92\u6400\u6276\u7740\uff0c\u539f\u6765\u9a6c\u6d77\u9f99\u4e5f\u6ca1\u6709\u5403\u591a\u5927\u4e8f\u3002\u5f20\u606a\u4e0d\u518d\u51b7\u5632\u70ed\u8bbd\uff0c\u8981\u8d76\u7d27\u5c06\u9a6c\u6d77\u9f99\u9001\u533b\u9662\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0a\u4e86\u8f66\uff0c\u770b\u7740\u9a6c\u6d77\u9f99\u8eba\u65bd\u536b\u5fe0\u7684\u819d\u76d6\u4e0a\uff0c\u6ca1\u6709\u52a8\u9759\uff0c\u770b\u60c5\u5f62\u662f\u660f\u6b7b\u8fc7\u53bb\u4e86\u3002</p><p>\u3000\u3000\u201c\u628a\u606a\u5c11\u7275\u6d89\u8fdb\u6765\u2026\u2026\u201d\u5085\u4fca\u4e00\u8fb9\u53d1\u52a8\u8f66\u5b50\uff0c\u4e00\u8fb9\u62b1\u6b49\u7684\u8bf4\uff0c\u4ed6\u770b\u5230\u8d75\u9526\u8363\u79bb\u5f00\u65f6\u7684\u773c\u795e\u5f88\u4e0d\u5584\u3002</p><p>\u3000\u3000\u201c\u8d75\u9526\u8363\u5728\u6d77\u5dde\u6a2a\u884c\u60ef\u4e86\uff0c\u603b\u8981\u6709\u4eba\u6765\u6536\u62fe\u4ed6\u4eec\uff0c\u201d\u5f20\u606a\u6487\u5634\u7b11\u4e86\u7b11\uff0c\u6beb\u4e0d\u5728\u610f\u7684\u8bf4\uff0c\u201c\u8d76\u7d27\u5148\u9001\u533b\u9662\uff0c\u518d\u770b\u80fd\u4e0d\u80fd\u8054\u7cfb\u4e0a\u4ed6\u5bb6\u4eba\u3002\u201d\u53c8\u8f6c\u56de\u8eab\u5bf9\u65bd\u536b\u5fe0\u8bf4\uff0c\u201c\u5c0f\u8205\u5e2e\u4e0b\u5fd9\uff0c\u5c06\u4ed6\u7684\u9886\u5b50\u89e3\u5f00\u6765\uff0c\u518d\u5c06\u4ed6\u7684\u5934\u4fa7\u8fc7\u6765\uff0c\u4ed6\u5634\u91cc\u6709\u6de4\u8840\uff0c\u4e0d\u8ba9\u6de4\u8840\u545b\u5230\u4ed6\u5589\u5499\u91cc\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u7b80\u5355\u7684\u6025\u6551\u77e5\u8bc6\uff0c\u5927\u5bb6\u6216\u4ece\u7535\u89c6\u6216\u4ece\u62a5\u520a\u90fd\u6709\u8fc7\u63a5\u89e6\uff0c\u5374\u4e0d\u662f\u4ec0\u4e48\u4eba\u90fd\u80fd\u4ece\u5bb9\u4e0d\u8feb\u7684\u5904\u7f6e\uff0c\u65bd\u536b\u5fe0\u770b\u5230\u8d75\u9526\u8363\u65f6\uff0c\u8fd8\u771f\u6709\u4e9b\u5fc3\u865a\uff0c\u6ca1\u60f3\u5230\u5341\u4e03\u516b\u5c81\u7684\u5f20\u606a\u6123\u662f\u6307\u7740\u8d75\u9526\u8363\u7684\u9f3b\u5b50\u5c06\u4eba\u8981\u4e86\u8fc7\u6765\uff0c\u5c31\u7b97\u4ed6\u5bb6\u5ead\u80cc\u666f\u5982\u4f55\u7684\u6df1\u539a\uff0c\u8fd9\u5206\u5904\u4e8b\u7684\u51b7\u9759\uff0c\u5374\u4e0d\u662f\u4e00\u822c\u4eba\u80fd\u505a\u5230\u7684\uff0c\u66f4\u4e0d\u8981\u8bf4\u4e00\u540d\u5c11\u5e74\u3002</p><p>\u3000\u3000\u65bd\u536b\u5fe0\u6309\u7740\u5f20\u606a\u8bf4\u7684\uff0c\u5c06\u9a6c\u6d77\u9f99\u7684\u9886\u53e3\u89e3\u5f00\uff0c\u5c06\u4ed6\u7684\u5934\u4fa7\u8fc7\u6765\uff0c\u4e5f\u987e\u4e0d\u4e0a\u8840\u8ff9\u6c61\u810f\uff0c\u7528\u624b\u6307\u5c06\u4ed6\u5634\u91cc\u7684\u6de4\u8840\u6e05\u51fa\u6765\uff0c\u8d76\u5230\u5e02\u7b2c\u4e00\u533b\u9662\uff0c\u7acb\u5373\u9001\u6025\u8bca\u5ba4\u6551\u6cbb\u3002</p><p>\u3000\u3000\u65bd\u536b\u5fe0\u4e0d\u4e5f\u6025\u7740\u8d70\uff0c\u5085\u4fca\u62ff\u7740\u5f20\u606a\u7684\u624b\u673a\u6253\u7535\u8bdd\uff0c\u7535\u8bdd\u8fd8\u6ca1\u6709\u901a\uff0c\u8ddf\u5f20\u606a\u89e3\u91ca\u8bf4\uff1a\u201c\u6d77\u9f99\u662f\u53c2\u52a079\u5e74\u5bf9\u8d8a\u81ea\u9645\u53cd\u51fb\u6218\u7684\u8001\u5fd7\u613f\u5175\uff0c\u6211\u5165\u4f0d\u65f6\uff0c\u4ed6\u662f\u4fa6\u5bdf\u8fde\u7684\u73ed\u957f\uff0c\u56e0\u4e3a\u662f\u8001\u4e61\uff0c\u5e73\u65f6\u90fd\u5f88\u7167\u5e94\uff0c\u8fd9\u4e24\u5e74\u4ed6\u8ddf\u5979\u7231\u4eba\u90fd\u4e0b\u4e86\u5c97\uff0c\u4e24\u4e2a\u5c0f\u5b69\u4e0a\u5c0f\u5b66\u4e86\uff0c\u76db\u9752\u5728\u90e8\u961f\u65f6\u5c31\u8ddf\u4ed6\u8ba4\u8bc6\u4e86\uff0c\u4e5f\u8bf7\u4ed6\u5e2e\u5fd9\u53bb\u770b\u573a\u5b50\uff0c\u4ed6\u8bf4\u81ea\u5df1\u662f\u515a\u5458\uff0c\u4e0d\u505a\u90a3\u4e9b\u4e8b\uff0c\u5c31\u5728\u5bb6\u95e8\u53e3\u644a\u4e86\u4e2a\u4fee\u8f66\u644a\u2026\u2026\u201d</p></article>"}}}]);
