(window.webpackJsonp=window.webpackJsonp||[]).push([[322],{345:function(p){p.exports={chapterTitle:null,title:"\u7b2c322\u7ae0 \u5e9c\u5929\u5927\u53a6\u7684\u51fa\u8def",content:"<article><h2>\u7b2c322\u7ae0 \u5e9c\u5929\u5927\u53a6\u7684\u51fa\u8def</h2><p>\u3000\u3000\u7b2c\u4e8c\u5929\uff0c3\u67089\u65e5\uff0c\u8c22\u5251\u5357\u524d\u5f80\u6d77\u5dde\u8c08\u5224\u4ece\u9526\u6e56\u64a4\u80a1\u4e00\u4e8b\uff0c\u4ed6\u7684\u5fc3\u60c5\u5e76\u4e0d\u597d\uff0c\u8fd9\u5927\u6982\u662f\u4ed6\u4ece\u5b66\u751f\u65f6\u4ee3\u5c31\u7ecf\u5546\u4ee5\u6765\u9996\u6b21\u8ba9\u5916\u4eba\u903c\u5165\u8fdb\u9000\u5931\u636e\u7684\u5883\u5730\uff0c\u6b63\u6cf0\u96c6\u56e2\u8fd9\u6b21\u4e5f\u989c\u9762\u5927\u5931\u3002</p><p>\u3000\u3000\u4e00\u5207\u90fd\u5728\u4e8e\u7231\u8fbe\u7535\u5b50\u950b\u8292\u592a\u76db\uff0c\u53bb\u5e74\u590f\u5929\u4e4b\u524d\u8fd8\u9ed8\u9ed8\u65e0\u95fb\u7684\u7231\u8fbe\u7535\u5b50\u4e00\u8dc3\u800c\u6210\u4e3a\u56fd\u5185\u7535\u5b50\u7c7b\u6700\u77e5\u540d\u7684\u4f01\u4e1a\u4e4b\u4e00\uff0c\u592e\u89c6\u6807\u738b\uff0c\u5360\u636e\u65b0\u5174\u7684\u54c1\u724c\u5f71\u789f\u673a\u5e02\u573a40\uff05\u7684\u4efd\u989d\uff0c\u6709\u8fd9\u6837\u7684\u9a84\u7ee9\uff0c\u6b63\u6cf0\u96c6\u56e2\u4e5f\u4e0d\u5f97\u4e0d\u907f\u950b\u8292\u3002</p><p>\u3000\u3000\u201c\u4e0d\u5f97\u4e0d\u6682\u907f\u950b\u8292\u554a\u2026\u2026\u201d\u51fa\u4e86\u9ad8\u901f\u8def\u53e3\uff0c\u8c22\u5251\u5357\u5355\u624b\u6276\u7740\u65b9\u5411\u76d8\uff0c\u5de6\u624b\u6401\u5728\u8f66\u7a97\u4e0a\uff0c\u5480\u56bc\u7740\u7236\u4eb2\u8c22\u6c49\u660e\u5206\u6790\u7684\u8bdd\uff0c\u201c\u7701\u91cc\u6709\u5f90\u5b66\u5e73\u7f69\u7740\uff0c\u6d77\u5dde\u5e02\u8ba9\u4ed6\u4eec\u7ecf\u8425\u5f97\u5982\u94c1\u6876\u4e00\u4e2a\uff0c\u76f4\u63a5\u6405\u8fdb\u53bb\uff0c\u662f\u6709\u4e9b\u5931\u7b56\uff0c\u8d75\u9526\u8363\u7684\u9526\u57ce\u96c6\u56e2\u539f\u6765\u662f\u8001\u4e66\u8bb0\u4e07\u5411\u524d\u3001\u526f\u5e02\u957f\u4e07\u52c7\u4e00\u7cfb\uff0c\u6216\u8bb8\u8ddf\u4ed6\u4eec\u6709\u4e9b\u9f8c\u9f8a\uff0c\u53e6\u5916\uff0c\u9526\u57ce\u7684\u90a3\u680b\u5927\u697c\u5e94\u8be5\u5feb\u5b8c\u5de5\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u51b3\u5b9a\u5148\u53bb\u627e\u8d75\u9526\u8363\uff0c\u4fa7\u5934\u77a5\u4e86\u4e00\u773c\u5750\u5728\u526f\u9a7e\u9a76\u5e2d\u4e0a\u7684\u79d8\u4e66\u95ed\u7740\u773c\u775b\u4f3c\u4e4e\u7761\u7740\u4e86\uff0c\u9ad8\u6311\u7684\u776b\u6bdb\u5fae\u5fae\u98a4\u6296\u7740\uff0c\u867d\u7136\u5bb9\u8c8c\u6bd4\u9648\u9759\u7684\u7edd\u7f8e\u7a0d\u900a\u4e00\u4e9b\uff0c\u4f46\u662f\u80dc\u5728\u5a07\u67d4\u4e56\u5de7\uff0c\u5468\u8eab\u808c\u80a4\u80dc\u96ea\uff0c\u80f8\u5927\u8170\u7ec6\uff0c\u4f38\u624b\u5728\u5979\u7684\u5927\u817f\u5185\u4fa7\u8f7b\u8f7b\u6390\u4e86\u4e00\u628a\uff0c\u5c06\u5979\u60ca\u9192\uff1a\u201c\u5a49\u79c0\uff0c\u4f60\u5e2e\u6211\u8054\u7cfb\u4e00\u4e0b\u9526\u57ce\u96c6\u56e2\u7684\u8d75\u603b\uff0c\u6211\u5148\u53bb\u62dc\u8bbf\u4ed6\uff0c\u5927\u4e2d\u5348\u7684\uff0c\u5927\u6982\u4e5f\u5c31\u4ed6\u4eec\u4f1a\u597d\u597d\u5bb4\u8bf7\u54b1\u4fe9\u2026\u2026\u201d\u5fc3\u91cc\u60f3\u7740\uff1a\u9648\u9759\u4e0d\u8fdb\u6d77\u6cf0\u4e5f\u597d\uff0c\u4e24\u4e2a\u5973\u4eba\u5728\u624b\u8fb9\uff0c\u901a\u5e38\u662f\u4e00\u4e2a\u90fd\u5403\u4e0d\u7740\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8d75\u53f8\u660e\u542c\u4ed6\u8001\u5b50\u8bf4\u8c22\u5251\u5357\u5c11\u5e74\u5c31\u5f88\u5f97\u5fd7\uff0c\u5e74\u7eaa\u8f7b\u8f7b\u5c31\u8d5a\u4e0b\u4ebf\u8d2f\u5bb6\u8d22\uff0c\u5b8c\u5168\u51ed\u501f\u7684\u662f\u81ea\u5df1\u7684\u672c\u4e8b\uff0c\u4ed6\u4e5f\u80fd\u4ece\u4ed6\u8001\u5b50\u90a3\u91cc\u7ee7\u7eed\u4ebf\u8d2f\u5bb6\u8d22\uff0c\u4ed6\u5bf9\u8c22\u5251\u5357\u7fa1\u6155\u8c08\u4e0d\u4e0a\u5341\u5206\uff0c\u800c\u662f\u5341\u5206\u7fa1\u6155\u8c22\u5251\u5357\u8eab\u8fb9\u90a3\u4f4d\u6e29\u5a49\u67d4\u7f8e\u7684\u5973\u79d8\u4e66\u3002\u5728\u4ed6\u6240\u8ba4\u8bc6\u7684\u5973\u5b69\u5b50\u91cc\uff0c\u597d\u50cf\u4e5f\u53ea\u6709\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u6bd4\u8fd9\u4f4d\u8c22\u5251\u5357\u7684\u5973\u79d8\u4e66\u6f02\u4eae\u3002\u53ef\u60dc\uff0c\u60f3\u5230\u5f20\u606a\u90a3\u72fc\u6bd2\u4e00\u6837\u7684\u773c\u795e\uff0c\u8fd8\u771f\u8ba9\u4eba\u4e0d\u5bd2\u800c\u6817\u3002</p><p>\u3000\u3000\u8d75\u53f8\u660e\u522b\u7684\u6ca1\u6709\uff0c\u63e3\u6469\u5973\u4eba\u7684\u795e\u6001\u5012\u6709\u4e00\u624b\uff0c\u5750\u5230\u5e2d\u4e0a\u4e0d\u4e00\u4f1a\u513f\uff0c\u5c31\u770b\u51fa\u8c22\u5251\u5357\u8ddf\u4ed6\u7684\u79d8\u4e66\u6709\u4e00\u817f\uff0c\u6697\u611f\u53ef\u60dc\uff0c\u53ef\u60dc\u4ed6\u8001\u5b50\u4e0d\u8bb8\u4ed6\u4e5f\u7269\u8272\u4e00\u4e2a\u5c0f\u79d8\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5230\u6d77\u5dde\u7b2c\u4e00\u4e2a\u627e\u4ed6\uff0c\u8d75\u9526\u8363\u5fc3\u91cc\u6b23\u559c\uff0c\u5012\u4e0d\u662f\u8868\u9762\u7684\u865a\u8363\uff0c\u800c\u662f\u51d1\u5de7\u6709\u4e8b\u3002</p><p>\u3000\u3000\u5f53\u521d\u4e0e\u53f6\u5bb6\u7684\u76db\u946b\u73af\u7403\u7535\u5668\u96c6\u56e2\u8349\u7b7e\u5408\u4f5c\u534f\u8bae\u4ee5\u53ca\u53d7\u5230\u5e02\u59d4\u3001\u5e02\u653f\u5e9c\u7684\u538b\u529b\uff0c\u9526\u57ce\u5730\u4ea7\u624d\u52a0\u5feb\u5e9c\u5929\u5927\u53a6\u7684\u5efa\u8bbe\u8fdb\u5ea6\uff0c\u773c\u89c1\u5e9c\u5929\u5927\u53a6\u5373\u5c06\u843d\u6210\uff0c\u5374\u88ab\u544a\u4e4b\u76db\u946b\u53d1\u5c55\u5bb6\u7535\u8fde\u9501\u7ecf\u8425\u7684\u6218\u7565\u91cd\u70b9\u4e0d\u5728\u7701\u5185\uff0c\u4e4b\u524d\u8349\u7b7e\u7684\u534f\u8bae\u6210\u4e86\u4e00\u53e0\u5e9f\u7eb8\uff0c\u4ed6\u5fc5\u987b\u7ed9\u5e9c\u5929\u5927\u53a6\u53e6\u627e\u4e70\u5bb6\uff0c\u8c22\u5bb6\u5728\u7701\u5185\u53d1\u5c55\u5bb6\u7535\u8fde\u9501\u95e8\u5e97\u7684\u6d77\u6cf0\u7535\u5668\u96c6\u56e2\u6709\u9650\u516c\u53f8\u5c31\u6210\u4e3a\u6700\u4f73\u7684\u76ee\u6807\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u5c31\u7b97\u5728\u4e0e\u53f6\u5bb6\u5e9f\u5f03\u5408\u4f5c\u4e4b\u524d\uff0c\u8d75\u9526\u8363\u4e5f\u66fe\u4e0e\u8c22\u6c49\u9756\u8c08\u8d77\u6b64\u7c7b\u5408\u4f5c\u7684\u4e8b\u60c5\uff0c\u53ea\u662f\u6ca1\u6709\u6df1\u5165\u7684\u8c08\u4e0b\u53bb\uff0c\u5e9c\u5929\u5927\u53a6\u518d\u8fc7\u4e24\u4e09\u4e2a\u6708\u5373\u5c06\u843d\u6210\uff0c\u8d75\u9526\u8363\u5e0c\u671b\u4e0e\u8c22\u5bb6\u5c3d\u5feb\u6572\u5b9a\u8fd9\u4ef6\u4e8b\uff0c\u662f\u79df\u662f\u5356\uff0c\u90fd\u8ba9\u4ed6\u597d\u62bd\u51fa\u8d44\u91d1\u505a\u5176\u4ed6\u7684\u9879\u76ee\u3002</p><p>\u3000\u3000\u8c2f\u697c\u4e1c\u4fa7\u3001\u56e0\u5b8b\u4ee3\u77f3\u7891\u88ab\u6bc1\u800c\u7ed9\u5e02\u653f\u5e9c\u5c01\u505c\u5f00\u53d1\u7684\u90a3\u5757\u5730\uff0c\u95ee\u9898\u4f9d\u65e7\u6ca1\u6709\u89e3\u51b3\uff0c\u4e5f\u4e0d\u6653\u5f97\u4ec0\u4e48\u65f6\u5019\u624d\u80fd\u89e3\u51b3\uff0c\u9526\u57ce\u5f80\u90a3\u5757\u5730\u91cc\u524d\u540e\u6295\u5165\u4e09\u5343\u4e07\uff0c\u4e09\u5343\u4e07\u5c31\u7ed9\u6b7b\u6b7b\u7684\u5957\u5728\u91cc\u9762\uff0c\u8fd9\u4e8b\u4e5f\u8ba9\u8d75\u9526\u8363\u6ca1\u813e\u6c14\uff0c\u6587\u7269\u5c40\u7684\u51e0\u4e2a\u4eba\u90fd\u56e0\u8fd9\u4e8b\u8eab\u9677\u7262\u72f1\u3002</p><p>\u3000\u3000\u5403\u8fc7\u4e2d\u996d\uff0c\u8c22\u5251\u5357\u4e0e\u79d8\u4e66\u5a49\u79c0\u5728\u5bbe\u9986\u623f\u95f4\u91cc\u7a0d\u4f5c\u4f11\u606f\uff0c\u5c31\u4e0e\u8d75\u9526\u8363\u7236\u5b50\u5230\u5e9c\u5929\u5927\u53a6\u73b0\u573a\u53bb\uff0c\u5e9c\u5929\u5927\u53a6\u7684\u5927\u7406\u77f3\u5916\u9970\u9762\u5df2\u7ecf\u505a\u597d\uff0c\u786e\u5b9e\u662f\u6d77\u5dde\u5e02\u5f53\u524d\u6700\u6c14\u6d3e\u7684\u9ad8\u5c42\u5efa\u7b51\uff0c\u524d\u8857\u5927\u9053\u62d3\u5bbd\u4e4b\u540e\uff0c\u5e9c\u5929\u5927\u53a6\u7684\u4f4d\u7f6e\u4e5f\u53d8\u5f97\u76f8\u5f53\u4f18\u8d8a\uff0c\u53ea\u662f\u5468\u56f4\u7684\u5efa\u7b51\u90fd\u8fd8\u6ca1\u6709\u5efa\u8d77\u6765\uff0c\u770b\u4e0a\u53bb\u6709\u7a81\u5140\u3002</p><p>\u3000\u3000\u8f66\u5b50\u505c\u5230\u5e9c\u5929\u5927\u53a6\u540e\u9762\u7684\u505c\u8f66\u573a\uff0c\u8c22\u5251\u5357\u770b\u5230\u4e1c\u8fb9\u7684\u53e4\u8857\u6709\u51e0\u680b\u5b85\u5b50\u5728\u88c5\u4fee\u6539\u9020\uff0c\u5df7\u5b50\u53e3\u6709\u4e00\u680b\u5efa\u7b51\u6574\u4e2a\u7684\u7ed9\u5305\u5728\u4fdd\u62a4\u5e10\u91cc\uff0c\u4f3c\u4e4e\u52a8\u4f5c\u8fd8\u4e0d\u5c0f\uff0c\u4ed6\u95ee\u8d75\u9526\u8363\uff1a\u201c\u4e1c\u8fb9\u4e0d\u62c6\uff1f\u201d</p><p>\u3000\u3000\u201c\u5e02\u91cc\u6ca1\u6709\u8bf4\u4e0d\u62c6\uff0c\u4e0d\u8fc7\u4e00\u65f6\u534a\u4f1a\u4e5f\u62c6\u4e0d\u4e86\u2026\u2026\u201d\u8d75\u9526\u8363\u4e0d\u77e5\u9053\u592a\u8be6\u7ec6\u7684\u4e1c\u897f\uff0c\u4f46\u662f\u4e5f\u80fd\u770b\u5230\u4e9b\u7aef\u502a\uff0c\u201c\u5e02\u91cc\u7684\u610f\u601d\uff0c\u897f\u7247\u7684\u62c6\u8fc1\uff0c\u4ea7\u751f\u5927\u91cf\u7684\u62c6\u8fc1\u6237\u8fd8\u6ca1\u6709\u5b89\u7f6e\u597d\uff0c\u7b49\u897f\u7247\u7684\u62c6\u8fc1\u6237\u5b89\u7f6e\u5b8c\uff0c\u518d\u8003\u8651\u62c6\u4e1c\u7247\uff0c\u611f\u89c9\u4e0a\u597d\u50cf\u62c6\u4e0d\u4e0b\u53bb\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8d75\u53f8\u660e\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u53ef\u80fd\u4e0d\u62c6\uff0c\u7834\u7834\u843d\u843d\u7684\uff0c\u6709\u4ec0\u4e48\u597d\u770b\u7684\uff0c\u518d\u8bf4\u5e02\u91cc\u65e9\u5c31\u5b9a\u4e0b\u7684\u89c4\u5212\uff0c\u4e0d\u80fd\u8bf4\u6539\u5c31\u6539\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7834\u7834\u843d\u843d\uff0c\u6211\u770b\u5012\u4e0d\u4f1a\uff0c\u201d\u8c22\u5251\u5357\u89c1\u8bc6\u5f53\u7136\u6bd4\u8d75\u53f8\u660e\u8981\u9ad8\uff0c\u201c\u6709\u4eba\u60f3\u501f\u5e9c\u5929\u5927\u53a6\u7684\u4e1c\u98ce\u5462\u3002\u201d</p><p>\u3000\u3000\u8d75\u9526\u8363\u70b9\u70b9\u5934\uff0c\u6709\u4eba\u60f3\u501f\u5e9c\u5929\u5927\u53a6\u7684\u4e1c\u98ce\uff0c\u4e0d\u8fc7\u4ed6\u4eec\u5bf9\u65c1\u8fb9\u7684\u65e7\u5b85\u5b50\u8fdb\u884c\u6539\u9020\u4fee\u7f2e\uff0c\u672a\u5c1d\u5bf9\u5e9c\u5929\u5927\u53a6\u6ca1\u6709\u597d\u5904\uff0c\u4ed6\u8bf4\u9053\uff1a\u201c\u5e02\u91cc\u65e9\u5c31\u5b9a\u4e0b\u7684\u89c4\u5212\uff0c\u6b63\u56e0\u4e3a\u65e0\u6cd5\u8bf4\u6539\u5c31\u6539\uff0c\u6240\u4ee5\u5e02\u91cc\u4e5f\u6ca1\u6709\u9a6c\u4e0a\u5c31\u8981\u6539\uff0c\u8fd9\u53ef\u80fd\u662f\u62d6\u5b57\u8bc0\u800c\u5df2\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u89c1\u8d75\u9526\u8363\u5012\u770b\u5f97\u660e\u767d\uff0c\u95ee\u4ed6\uff1a\u201c\u8d75\u603b\u77e5\u9053\u90a3\u51e0\u680b\u8001\u5b85\u5b50\u662f\u8c01\u62ff\u4e0b\u6765\u7684\uff0c\u4ed6\u4eec\u53ef\u80fd\u6bd4\u8d75\u603b\u77e5\u9053\u66f4\u591a\u7684\u5185\u5e55\u6d88\u606f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u67e5\u8fc7\uff0c\u6ca1\u67e5\u51fa\u80cc\u540e\u7684\u4eba\uff0c\u4e4b\u524d\u4e39\u4e95\u5df7\u6709\u4e24\u680b\u5b85\u5b50\u662f\u9526\u6e56\u4e70\u4e0b\u6765\u7684\uff0c\u8fd8\u662f\u5728\u5e9c\u5929\u5927\u53a6\u52a8\u5de5\u4e4b\u524d\uff0c\u90a3\u65f6\u5019\u95f9\u5f97\u6cb8\u6cb8\u626c\u626c\uff0c\u90fd\u8bf4\u6c99\u7530\u8981\u62c6\u8fc1\u4e86\uff0c\u540e\u6765\u6c99\u7530\u897f\u7247\u679c\u7136\u62c6\u8fc1\u641e\u5f00\u53d1\uff0c\u6b63\u6cf0\u96c6\u56e2\u662f\u9526\u6e56\u7684\u5927\u80a1\u4e1c\uff0c\u8c22\u603b\u4e0d\u6e05\u695a\uff1f\u201d</p><p>\u3000\u3000\u4e39\u4e95\u5df7\u7684\u4e24\u680b\u5b85\u5b50\u65e9\u5728\u6b63\u6cf0\u63d2\u624b\u9526\u6e56\u4e4b\u524d\u5c31\u8f6c\u4e86\u51fa\u53bb\uff0c\u8c22\u5251\u5357\u5f53\u7136\u4e0d\u6e05\u695a\uff0c\u4ed6\u8fd9\u65f6\u5012\u7559\u4e86\u5fc3\uff0c\u8bf4\u9053\uff1a\u201c\u6d77\u5dde\u8981\u8bf4\u8c01\u80fd\u6bd4\u8d75\u603b\u66f4\u65e9\u77e5\u9053\u4e9b\u6d88\u606f\uff0c\u6211\u770b\u4e5f\u53ea\u6709\u9526\u6e56\u7684\u90a3\u4e9b\u4eba\u2026\u2026\u201d\u4e0d\u8fc7\u6295\u8d44\u4fee\u7f2e\u53e4\u5b85\uff0c\u663e\u7136\u4e0d\u53ca\u62c6\u6389\u76f4\u63a5\u5f00\u53d1\u5546\u4e1a\u5730\u4ea7\u6765\u94b1\u5feb\uff0c\u8fd9\u70b9\u5374\u662f\u4ed6\u4e0d\u80fd\u7406\u89e3\u7684\uff0c\u5c31\u50cf\u7ed9\u65b0\u5149\u5382\u65b0\u5382\u5f00\u51fa\u90a3\u4e48\u9ad8\u7684\u5e76\u8d2d\u4ef7\u4e00\u6837\uff0c\u85cf\u5728\u8d8a\u79c0\u80cc\u540e\u7684\u90a3\u4e9b\u4eba\u7684\u5fc3\u601d\uff0c\u8c22\u5251\u5357\u59cb\u7ec8\u7422\u78e8\u4e0d\u900f\uff0c\u8fd9\u4e5f\u662f\u4ed6\u5fc3\u91cc\u867d\u7136\u4e0d\u7518\u5fc3\u5374\u540c\u610f\u5148\u4ece\u9526\u6e56\u64a4\u51fa\u6765\u7684\u539f\u56e0\u3002</p><p>\u3000\u3000\u8d75\u9526\u8363\u70b9\u70b9\u5934\uff0c\u4ed6\u4e5f\u8fd9\u4e48\u60f3\uff0c\u4e0d\u8fc7\u9526\u6e56\u4e00\u76f4\u6ca1\u6709\u53c2\u4e0e\u6d77\u5dde\u5e02\u7684\u5546\u4e1a\u5730\u4ea7\u5f00\u53d1\uff0c\u90b5\u81f3\u521a\u867d\u7136\u5728\u9526\u6e56\u6302\u7740\u8463\u4e8b\u4e00\u804c\uff0c\u4f46\u662f\u5728\u6c99\u7530\u897f\u7247\u62ff\u5730\u7684\u4e16\u7eaa\u5730\u4ea7\u5374\u662f\u90b5\u81f3\u521a\u7684\u79c1\u4eba\u516c\u53f8\uff0c\u4e0e\u9526\u6e56\u6ca1\u6709\u4efb\u4f55\u5173\u7cfb\u3002</p><p>\u3000\u3000\u5bf9\u5e9c\u5929\u5927\u53a6\u7684\u79cd\u79cd\u6761\u4ef6\uff0c\u8c22\u5251\u5357\u5fc3\u91cc\u65e9\u5c31\u6709\u6570\uff0c\u8fc7\u6765\u8d70\u4e86\u4e00\u5708\uff0c\u4e5f\u57fa\u672c\u4e0a\u80fd\u62ff\u5b9a\u4e3b\u610f\uff0c\u51fa\u6765\u65f6\uff0c\u4ed6\u56de\u5934\u5bf9\u8d75\u9526\u8363\u8bf4\uff1a\u201c\u5730\u4e0a\u4e09\u5c42\u516b\u5343\u5e73\u7c73\u7684\u5730\u65b9\uff0c\u6d77\u6cf0\u53ef\u4ee5\u79df\u4e0b\u6765\uff0c\u5927\u697c\u5efa\u5f97\u5dee\u4e0d\u591a\u4e86\uff0c\u4e5f\u53ef\u4ee5\u5f80\u7ec6\u91cc\u8c08\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8d75\u9526\u8363\u6709\u4e9b\u5931\u671b\uff0c\u4ed6\u662f\u5e0c\u671b\u6d77\u6cf0\u76f4\u63a5\u4e70\u4e0b\u6765\uff0c\u9526\u57ce\u96c6\u56e2\u7684\u8d44\u91d1\u5468\u8f6c\u6709\u4e9b\u5403\u7d27\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5c06\u8d75\u9526\u8363\u8138\u4e0a\u7684\u96be\u8272\u770b\u5728\u773c\u91cc\uff0c\u95ee\u9053\uff1a\u201c\u600e\u4e48\u4e86\uff0c\u8d75\u603b\u6709\u4ec0\u4e48\u96be\u5904\uff1f\u201d</p><p>\u3000\u3000\u8d75\u9526\u8363\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0e\u4f60\u4e8c\u53d4\u8ba4\u8bc6\u5feb\u5341\u5e74\u4e86\uff0c\u6709\u4e9b\u4e8b\u4e5f\u4e0d\u7792\u4f60\uff0c\u867d\u7136\u8fd9\u91cc\u7684\u5347\u503c\u6f5c\u529b\u5f88\u5927\uff0c\u4f46\u662f\u9526\u57ce\u8fd8\u6ca1\u6709\u5230\u624b\u91cc\u6512\u7269\u4e1a\u7684\u65f6\u5019\uff0c\u73b0\u5728\u94f6\u884c\u5bf9\u653e\u8d37\u6b3e\u63a7\u5236\u53c8\u662f\u6781\u7d27\u2026\u2026\u201d</p><p>\u3000\u3000\u8d75\u9526\u8363\u8fd9\u4e48\u8bf4\uff0c\u662f\u627f\u8ba4\u9526\u57ce\u7684\u8d44\u91d1\u5403\u7d27\u4e86\uff0c\u4f46\u662f\u6d77\u6cf0\u505a\u5bb6\u7535\u8fde\u9501\uff0c\u5bf9\u8d44\u91d1\u7684\u5360\u7528\u91cf\u76f8\u5f53\u5927\uff0c\u95e8\u5e97\u80fd\u79df\u5219\u79df\uff0c\u4e0d\u4f1a\u8f7b\u6613\u8d2d\u7f6e\u7269\u4e1a\u7684\uff0c\u4e07\u4e00\u9009\u5740\u5931\u5f53\uff0c\u8fde\u9000\u51fa\u90fd\u4f1a\u53d8\u5f97\u6781\u5176\u56f0\u96be\u3002\u8c22\u5251\u5357\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8d75\u603b\u6709\u6ca1\u6709\u542c\u8bf4\u8fc7\u4ea7\u6743\u5f0f\u5546\u94fa\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8c22\u603b\u5728\u5916\u9762\u7559\u5b66\u5f52\u6765\uff0c\u89c1\u8bc6\u6bd4\u6211\u591a\uff0c\u613f\u95fb\u5176\u8be6\u2026\u2026\u201d</p><p>\u3000\u3000\u8981\u60f3\u4e0e\u9526\u6e56\u3001\u8d8a\u79c0\u5236\u8861\uff0c\u9526\u57ce\u96c6\u56e2\u5012\u662f\u4e0d\u9519\u7684\u4f19\u4f34\uff0c\u8c22\u5251\u5357\u4e5f\u4e50\u610f\u5e2e\u4ed6\u4eec\u89e3\u51b3\u8d22\u52a1\u4e0a\u7684\u7a98\u5883\uff0c\u4e5d\u516d\u5e74\u521d\u4ea7\u6743\u5f0f\u5546\u94fa\u5728\u56fd\u5185\u8fd8\u6781\u4e3a\u7f55\u89c1\uff0c\u4ed6\u89e3\u91ca\u9053\uff1a\u201c\u5e9c\u5929\u5927\u53a6\u5730\u4e0a\u4e09\u5c42\u662f\u6574\u4f53\u5f0f\u5546\u94fa\uff0c\u8981\u60f3\u6574\u4f53\u51fa\u552e\u7684\u96be\u5ea6\u5f88\u5927\uff0c\u5c31\u7b97\u9526\u57ce\u8981\u5356\u7ed9\u6211\u4eec\u6d77\u6cf0\uff0c\u6211\u4e5f\u4e0d\u4f1a\u7ed9\u4f60\u4eec\u597d\u4ef7\u94b1\uff0c\u201d\u8d75\u9526\u8363\u542c\u5230\u8fd9\u91cc\u5c31\u7b11\u4e86\u8d77\u6765\uff0c\u9526\u57ce\u6025\u4e8e\u8131\u624b\uff0c\u7ed9\u538b\u4ef7\u662f\u5fc5\u7136\u7684\uff0c\u4ed6\u542c\u8c22\u5251\u5357\u7ee7\u7eed\u8bf4\u4e0b\u53bb\uff0c\u201c\u56fd\u5916\u5bf9\u8fd9\u79cd\u6574\u4f53\u5546\u94fa\u6709\u79cd\u65b0\u7684\u9500\u552e\u6a21\u5f0f\uff0c\u5373\u5546\u94fa\u6574\u4f53\u59d4\u6258\u7ed9\u7ecf\u8425\u5546\u8fdb\u884c\u7edf\u4e00\u7ecf\u8425\uff0c\u6bd4\u5982\u8bf4\u8fd9\u4e09\u5c42\u5546\u94fa\u51fa\u79df\u7ed9\u6211\u4eec\u6d77\u6cf0\u5f00\u5bb6\u7535\u8fde\u9501\u5e97\uff0c\u4f46\u662f\u4e09\u5c42\u5546\u94fa\u7684\u4ea7\u6743\u8fdb\u884c\u5206\u5272\u6210\u51e0\u767e\u4efd\u72ec\u7acb\u7684\u4ea7\u6743\u5206\u522b\u51fa\u552e\uff0c\u6295\u8d44\u8005\u4e0d\u76f4\u63a5\u7ecf\u8425\uff0c\u53ea\u662f\u4ee5\u4e00\u90e8\u5206\u4ea7\u6743\u6765\u83b7\u5f97\u5b9a\u671f\u7684\u79df\u91d1\u4e0e\u4ea7\u6743\u589e\u503c\uff0c\u4e5f\u662f\u6240\u8c13\u7684\u5305\u79df\u51fa\u552e\u2026\u2026\u6211\u770b\u4f60\u4eec\u8981\u65b9\u4fbf\u51fa\u552e\u7684\u8bdd\uff0c\u53ef\u4ee5\u8ddf\u6295\u8d44\u8005\u7ea6\u5b9a\u4e00\u4e2a\u6bd4\u8f83\u8bf1\u4eba\u7684\u79df\u91d1\u56de\u62a5\u7387\uff0c\u6bd4\u5982\u8bf410\uff05\uff0c\u6216\u800512\uff05\u2026\u2026\u5bf9\u4e8e\u4e00\u4e9b\u6bd4\u8f83\u4fdd\u5b88\u7684\u6295\u8d44\u4eba\uff0c\u65e2\u7136\u770b\u5230\u5546\u4e1a\u5730\u4ea7\u7684\u589e\u503c\uff0c\u53c8\u53ef\u4ee5\u62ff\u5230\u6bd4\u94f6\u884c\u5229\u606f\u9ad8\u5f97\u591a\u7684\u79df\u91d1\uff0c\u53ea\u8981\u5ba3\u4f20\u5230\u4f4d\uff0c\u8fd8\u662f\u5f88\u6709\u8bf1\u60d1\u529b\u7684\u3002\u201d</p><p>\u3000\u3000\u8d75\u9526\u8363\u773c\u754c\u4e0d\u53ca\u8c22\u5251\u5357\u5f00\u9614\uff0c\u4f46\u662f\u5f88\u6709\u89c1\u8bc6\uff0c\u542c\u8c22\u5251\u5357\u8fd9\u4e48\u8bf4\uff0c\u773c\u775b\u4e00\u4eae\uff0c\u786e\u5b9e\u662f\u597d\u4e3b\u610f\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u552e\u4ef7\u62ac\u9ad8\u4e00\u4e9b\uff0c\u53ef\u4ee5\u5728\u8d2d\u4e70\u65f6\u5148\u8fd4\u56de\u4e24\u5e74\u7684\u79df\u91d1\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u7b11\u4e86\u7b11\uff0c\u8d75\u9526\u8363\u5012\u662f\u77e5\u9053\u505a\u751f\u610f\u5751\u4eba\u7684\u4e09\u6627\u3002</p></article>"}}}]);
