(window.webpackJsonp=window.webpackJsonp||[]).push([[325],{348:function(p){p.exports={chapterTitle:null,title:"\u7b2c203\u7ae0 \u7838\u9519\u8f66\u4e86",content:"<article><h2>\u7b2c203\u7ae0 \u7838\u9519\u8f66\u4e86</h2><p>\u3000\u3000\u76f4\u5230\u65e9\u6668\u503c\u73ed\u8b66\u5458\u63a5\u5230\u516c\u5b89\u90e8\u8b66\u52a1\u7763\u5bdf\u5c40\u526f\u5c40\u957f\u7eaa\u6c38\u594e\u7684\u7535\u8bdd\uff0c\u60e0\u5c71\u5e02\u5c40\u624d\u77e5\u9053\u6628\u5929\u6df1\u591c\u5728\u949f\u9e93\u5bbe\u9986\u53d1\u751f\u7684\u4e8b\u60c5\u3002\u949f\u9e93\u8857\u9053\u6d3e\u51fa\u6240\u8fdd\u53cd\u5de5\u4f5c\u7a0b\u5e8f\uff0c\u62d2\u7edd\u7ed9\u6bc1\u8f66\u4e8b\u4ef6\u7acb\u6848\uff0c\u7ed9\u4e86\u53f6\u5c0f\u6850\u7238\u7238\u53f6\u795d\u6c11\u901a\u8fc7\u8001\u6218\u53cb\u7eaa\u6c38\u594e\u76f4\u63a5\u8fc7\u95ee\u6b64\u4e8b\u7684\u65b9\u4fbf\u3002</p><p>\u3000\u3000\u81ea\u4ece\u60e0\u5c71\u5e02\u516c\u5b89\u7cfb\u7edf\u53d1\u751f\u4e25\u91cd\u6c61\u804c\u4e8b\u4ef6\uff0c\u81f4\u4f7f\u5e74\u521d\u60e0\u5c71\u5927\u8f66\u7978\u672a\u5f97\u5230\u53ca\u65f6\u63a7\u5236\uff0c\u751a\u81f3\u8fde\u524d\u5c40\u957f\u3001\u653f\u6cd5\u59d4\u526f\u4e66\u8bb0\u4e5f\u6b7b\u4e8e\u6b64\u6b21\u8f66\u7978\uff0c\u7701\u91cc\u4e4b\u540e\u76f4\u63a5\u5bf9\u60e0\u5c71\u5e02\u7684\u6574\u4e2a\u653f\u6cd5\u7cfb\u7edf\u8fdb\u884c\u5927\u8c03\u6574\uff0c\u4e0d\u53ca\u60e0\u5c71\u5e02\u653f\u6cd5\u59d4\u4e66\u8bb0\u9000\u5c45\u4e8c\u7ebf\uff0c\u7531\u6d77\u5dde\u5e02\u653f\u6cd5\u59d4\u4e66\u8bb0\u960e\u771f\u79d1\u7ee7\u4efb\uff0c\u5e02\u516c\u5b89\u5c40\u597d\u51e0\u540d\u8001\u8d44\u683c\u7684\u526f\u5c40\u957f\u88ab\u95f2\u7f6e\uff0c\u6392\u540d\u6700\u672b\u7684\u526f\u5c40\u957f\u5b8b\u6653\u610f\u5916\u7684\u51fa\u4efb\u5e02\u5c40\u5c40\u957f\u3002</p><p>\u3000\u3000\u88ab\u516c\u5b89\u90e8\u76f4\u63a5\u95ee\u8d23\u7684\u6ecb\u5473\u5e76\u4e0d\u597d\u53d7\uff0c\u7279\u522b\u662f\u5e74\u521d\u521a\u521a\u53d1\u751f\u90a3\u6837\u7684\u4e8b\u60c5\uff0c\u5404\u65b9\u9762\u5bf9\u60e0\u5c71\u5e02\u7684\u516c\u5b89\u7cfb\u7edf\u90fd\u5f88\u654f\u611f\uff0c\u4e0d\u7ba1\u4e8b\u60c5\u662f\u4e0d\u662f\u7275\u6d89\u5230\u5e02\u957f\u5468\u747e\u745c\uff0c\u5b8b\u6653\u90fd\u4e0d\u60f3\u7531\u60e0\u5c71\u5e02\u8b66\u65b9\u627f\u62c5\u76f8\u5173\u8d23\u4efb\uff0c\u8d23\u4ee4\u6628\u591c\u51fa\u8b66\u5374\u6ca1\u6709\u4f9d\u6b63\u5e38\u7a0b\u5e8f\u7acb\u6848\u7684\u949f\u9e93\u8857\u9053\u6d3e\u51fa\u6240\u6240\u957f\u505c\u804c\u68c0\u67e5\uff0c\u7136\u540e\u5175\u5206\u4e24\u8def\uff0c\u4ed6\u4eb2\u81ea\u5411\u5e02\u957f\u5468\u747e\u745c\u6c47\u62a5\u6b64\u4e8b\uff0c\u7531\u526f\u5c40\u957f\u7530\u6d77\u971e\u7387\u961f\u5230\u949f\u9e93\u5bbe\u9986\u7acb\u6848\u4fa6\u67e5\u7838\u8f66\u6848\u3002</p><p>\u3000\u3000\u81f3\u4e8e\u7838\u8f66\u6848\u5982\u4f55\u8c03\u67e5\u3001\u5982\u4f55\u7ed3\u6848\uff0c\u8fd8\u662f\u8ba9\u4e0a\u9762\u4eba\u53bb\u73a9\u592a\u6781\u62f3\uff0c\u60e0\u5c71\u5e02\u8b66\u65b9\u7684\u8868\u9762\u5de5\u592b\u8981\u505a\u8db3\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u7ed9\u4ed6\u5988\u5468\u747e\u745c\u53eb\u5230\u5e02\u653f\u5e9c\u624d\u77e5\u9053\u6628\u591c\u7838\u9519\u4e86\u8f66\uff0c\u6ee1\u76ee\u8be7\u7136\uff0c\u4e5f\u4e0d\u907f\u5b8b\u6653\u5728\u573a\uff0c\u96be\u4ee5\u7f6e\u4fe1\u7684\u95ee\uff1a\u201c\u600e\u4e48\u53ef\u80fd\u4f1a\u641e\u9519\uff1f\u201d</p><p>\u3000\u3000\u5b8b\u6653\u5c06\u8f66\u8f86\u8d44\u6599\u9012\u7ed9\u8c22\u5251\u5357\uff1a\u201c\u8f66\u724c\u53f7\u4e3a\u6d77A-B8097\u7684\u94f6\u7070\u8272VOLVO\u7684\u767b\u8bb0\u8f66\u4e3b\u662f\u53f6\u5c0f\u6850\uff0c\u53f6\u5c0f\u6850\u662f\u76db\u946b\u8d38\u6613\u6709\u9650\u516c\u53f8\u603b\u7ecf\u7406\u52a9\u7406\u2026\u2026\u201d\u4ed6\u6ca1\u6709\u5fc5\u8981\u4ecb\u7ecd\u53f6\u5c0f\u6850\u80cc\u540e\u7684\u8eab\u4efd\uff0c\u65e0\u8bba\u662f\u524d\u7ec4\u7ec7\u90e8\u526f\u90e8\u957f\u3001\u653f\u534f\u526f\u4e3b\u5e2d\u53f6\u7956\u8303\u7684\u5b59\u5973\uff0c\u8fd8\u662f\u56fd\u9632\u5175\u5668\u5de5\u4e1a\u603b\u516c\u53f8\u526f\u603b\u7ecf\u7406\u53f6\u795d\u6c11\u7684\u5973\u513f\uff0c\u90fd\u4e0d\u4f1a\u5fcd\u53d7\u88ab\u4eba\u65e0\u7f18\u65e0\u6545\u7838\u8f66\u7684\u5f85\u9047\uff0c\u4f55\u51b5\u53f6\u5c0f\u6850\u62b5\u8fbe\u60e0\u5c71\u5e02\u662f\u4ee3\u8868\u53f6\u5bb6\u7684\u76db\u946b\u8d38\u6613\u8fdb\u884c\u6b63\u5e38\u7684\u5546\u8d38\u6d3b\u52a8\uff0c\u8981\u662f\u6d88\u606f\u4f20\u51fa\u53bb\uff0c\u5bf9\u60e0\u5c71\u4f1a\u9020\u6210\u5f88\u4e0d\u597d\u7684\u5f71\u54cd\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u82f1\u4fca\u7684\u8138\u5fae\u5fae\u626d\u66f2\u7740\uff0c\u63a5\u8fc7\u5b8b\u6653\u9012\u8fc7\u6765\u7684\u8d44\u6599\uff0c\u8fd8\u6ca1\u6709\u611a\u8822\u5230\u4e0d\u6562\u9762\u5bf9\u73b0\u5b9e\uff0c\u53ea\u662f\u60f3\u4e0d\u901a\u5f20\u606a\u4e3a\u4ec0\u4e48\u4f1a\u8ddf\u53f6\u5bb6\u642d\u4e0a\u5173\u7cfb\uff1a\u201c\u53f6\u5bb6\u4e3a\u4ec0\u4e48\u4f1a\u6709\u4eba\u6765\u60e0\u5c71\u53c2\u52a0\u7231\u8fbe\u7535\u5b50\u7684\u4ea7\u54c1\u53d1\u5e03\u4f1a\uff1f\u201d</p><p>\u3000\u3000\u60e0\u5c71\u5e02\u8b66\u65b9\u7684\u5de5\u4f5c\u6548\u7387\u8fd8\u662f\u503c\u5f97\u8868\u626c\uff0c\u5b8b\u6653\u5728\u77ed\u77ed\u7684\u65f6\u95f4\u5c31\u5c06\u57fa\u672c\u60c5\u51b5\u6478\u6e05\u695a\u4e86\uff0c\u77a5\u4e86\u5750\u5728\u6905\u5b50\u4e0a\u7684\u5468\u747e\u745c\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u76db\u946b\u516c\u53f8\u662f\u6d77\u5dde\u5e02\u7231\u8fbe\u7535\u5b50\u6709\u9650\u516c\u53f8\u7684\u534e\u4e1c\u5730\u533a\u603b\u4ee3\u7406\uff0c\u6628\u5929\u7684\u4ea7\u54c1\u53d1\u5e03\u4f1a\u662f\u7531\u7231\u8fbe\u7535\u5b50\u3001\u76db\u946b\u8d38\u6613\u8054\u5408\u4e3b\u529e\uff0c\u53f6\u5c0f\u6850\u4ee3\u8868\u76db\u946b\u8d38\u6613\u51fa\u5e2d\u6628\u5929\u7684\u4ea7\u54c1\u53d1\u5e03\u4f1a\uff0c\u7231\u8fbe\u65b9\u9762\u7531\u7231\u8fbe\u516c\u53f8\u5e02\u573a\u90e8\u603b\u76d1\u5468\u6e38\u8d1f\u8d23\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u6291\u4f4f\u5c06\u624b\u4e2d\u8d44\u6599\u6495\u788e\u7684\u51b2\u52a8\uff0c\u6628\u5929\u665a\u4e0a\uff0c\u770b\u5230\u5f20\u606a\u51fa\u73b0\u5728\u4ea7\u54c1\u53d1\u5e03\u4f1a\u73b0\u573a\u5df2\u7ecf\u591f\u4ed6\u60ca\u8bb6\u4e86\uff0c\u5e76\u6ca1\u6709\u6ce8\u610f\u5230\u7ad9\u5728\u89d2\u843d\u91cc\u7684\u5468\u6e38\u3002\u5468\u6e38\u624d\u4ece\u6d77\u6cf0\u8c03\u5f80\u9526\u6e56\u62c5\u4efb\u526f\u603b\uff0c\u600e\u4e48\u8f6c\u773c\u53c8\u53bb\u62c5\u4efb\u7231\u8fbe\u7535\u5b50\u7684\u5e02\u573a\u90e8\u603b\u76d1\uff1f</p><p>\u3000\u3000\u8c22\u5251\u5357\u4e00\u8138\u9634\u6c89\uff0c\u8fd9\u4e9b\u5929\u5df2\u7ecf\u7ed9\u4e88\u9526\u6e56\u8db3\u591f\u7684\u5173\u6ce8\u4e86\uff0c\u6ca1\u6709\u60f3\u5230\u7a81\u7136\u5192\u51fa\u7231\u8fbe\u7535\u5b50\u6765\u3002\u65e2\u7136\u5468\u6e38\u5728\u9526\u6e56\u4e0e\u7231\u8fbe\u7535\u5b50\u4e4b\u95f4\u4efb\u610f\u8c03\u52a8\uff0c\u8bf4\u660e\u9526\u6e56\u4e0e\u7231\u8fbe\u7535\u5b50\u6709\u7740\u5bc6\u5207\u7684\u5173\u7cfb\uff0c\u8c22\u5251\u5357\u773c\u775b\u76ef\u7740\u684c\u5b50\u89d2\u7684\u7535\u8bdd\u673a\uff0c\u60f3\u5230\u4e8c\u59d0\u8c22\u665a\u6674\u4e0a\u6b21\u5bf9\u4ed6\u7684\u6001\u5ea6\uff0c\u653e\u5f03\u76f4\u63a5\u6253\u7535\u8bdd\u5411\u5979\u8be2\u95ee\u8fd9\u4e8b\u7684\u5ff5\u5934\u3002</p><p>\u3000\u3000\u5b8b\u6653\u8f7b\u8f7b\u54b3\u55fd\u4e86\u4e00\u58f0\uff0c\u4e00\u76f4\u6c89\u9ed8\u4e0b\u53bb\uff0c\u4e5f\u4e0d\u662f\u529e\u6cd5\uff0c\u53f6\u5bb6\u90a3\u8fb9\u5fc5\u987b\u8981\u6709\u4ea4\u5f85\u624d\u884c\uff1a\u201c\u5468\u5e02\u957f\uff0c\u6211\u662f\u4e0d\u662f\u5230\u949f\u9e93\u5bbe\u9986\u4eb2\u81ea\u8d70\u4e00\u8d9f\uff1f\u201d</p><p>\u3000\u3000\u5468\u747e\u745c\u957f\u671f\u8eab\u5c45\u9ad8\u4f4d\uff0c\u8bf4\u8bdd\u65f6\u4e60\u60ef\u7528\u88c5\u8154\u4f5c\u52bf\u7684\u58f0\u97f3\uff1a\u201c\u5251\u5357\uff0c\u4ee5\u4e3a\u4f60\u4ece\u56fd\u5916\u56de\u6765\u80fd\u7a33\u91cd\u4e00\u4e9b\uff0c\u8fd8\u662f\u5230\u5904\u60f9\u662f\u751f\u975e\uff0c\u7ed9\u4f60\u5b8b\u53d4\u53d4\u60f9\u6765\u8fd9\u4e48\u5927\u9ebb\u70e6\uff0c\u4f60\u770b\u600e\u4e48\u89e3\u51b3\uff1f\u662f\u4e0d\u662f\u8ba9\u4f60\u5b8b\u53d4\u8c03\u89e3\u4e00\u4e0b\uff0c\u4f60\u51fa\u9762\u9053\u4e2a\u6b49\uff0c\u8d54\u4eba\u5bb6\u4e00\u8f86\u65b0\u8f66\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u4e8b\u626f\u4e0a\u53f6\u5bb6\uff0c\u4e0d\u670d\u8f6f\u4e5f\u4e0d\u884c\uff0c\u4f46\u662f\u60f3\u5230\u5f20\u606a\u53ef\u80fd\u6709\u7684\u5f97\u610f\u6a21\u6837\uff0c\u8c22\u5251\u5357\u54ea\u7518\u5fc3\u51fa\u9762\u9053\u6b49\uff1f\u6897\u7740\u8116\u5b50\u8bf4\uff1a\u201c\u600e\u4e48\u89e3\u51b3\u90fd\u597d\uff0c\u51fa\u9762\u9053\u6b49\u514d\u8c08\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u747e\u745c\u6447\u5934\u53f9\u606f\uff0c\u5bf9\u5b8b\u6653\u8bf4\uff1a\u201c\u8fd9\u4e8b\u5c31\u9ebb\u70e6\u8001\u5b8b\u4e86\uff0c\u90fd\u662f\u5c0f\u5b69\u5b50\u95f9\u610f\u6c14\uff0c\u5934\u75bc\uff01\u201d</p><p>\u3000\u3000\u5b8b\u6653\u8d70\u540e\uff0c\u8c22\u5251\u5357\u8d77\u8eab\u4e5f\u8981\u8d70\uff0c\u5468\u747e\u745c\u4e0d\u7531\u52a0\u91cd\u8bed\u6c14\u8bf4\uff1a\u201c\u4f60\u6700\u597d\u5b89\u5206\u5b88\u5df1\u4e9b\uff0c\u5f20\u606a\u4ed6\u5230\u5e95\u957f\u4ec0\u4e48\u6837\u5b50\uff0c\u80fd\u4e09\u756a\u6570\u6b21\u8ba9\u4f60\u5931\u4e86\u5206\u5bf8\uff1f\u4e0b\u6b21\u518d\u60f9\u662f\u751f\u975e\u4e4b\u524d\uff0c\u6700\u597d\u628a\u5bf9\u65b9\u7684\u6765\u5934\u6478\u6e05\u695a\u518d\u8bf4\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u77e5\u9053\u4e86\uff0c\u201d\u8c22\u5251\u5357\u7f13\u7f13\u7684\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u201c\u8fd9\u6b21\u5fcd\u6c14\u541e\u58f0\u5c31\u7b97\u4e86\uff0c\u603b\u4e0d\u4f1a\u518d\u5403\u7b2c\u4e8c\u6b21\u4e8f\u3002\u201d</p><p>\u3000\u3000\u60e0\u5c71\u5e02\u5c40\u526f\u5c40\u957f\u7530\u6d77\u971e\u9886\u961f\u8d76\u5230\u949f\u9e93\u5bbe\u9986\u7acb\u6848\u4fa6\u67e5\u4e0d\u8fc7\u662f\u505a\u8868\u9762\u4e0a\u7684\u5de5\u592b\uff0c\u6ca1\u89c1\u5230\u5f53\u4e8b\u4eba\uff0c\u600e\u4e48\u8868\u8fbe\u60e0\u5c71\u5e02\u8b66\u65b9\u7684\u6b49\u610f\uff1f\u6309\u7167\u53f6\u5c0f\u6850\u7559\u4e0b\u7684\u8054\u7cfb\u5730\u5740\u8d76\u5f80\u6c5f\u9edb\u513f\u5bb6\u3002</p><p>\u3000\u3000\u5f20\u606a\u6b63\u5e2e\u7740\u6c5f\u9edb\u513f\u5979\u5988\u5988\u8865\u5145\u4ee3\u8a00\u534f\u8bae\u7684\u7ec6\u8282\uff0c\u8fd8\u5bf9\u5979\u5988\u5988\u8bf4\uff1a\u201c\u674e\u59e8\uff0c\u4f60\u6709\u6ca1\u6709\u8ba4\u8bc6\u5f8b\u5e08\u670b\u53cb\uff0c\u8fd9\u4efd\u534f\u8bae\u8bf7\u5f8b\u5e08\u770b\u4e00\u4e0b\uff0c\u4f1a\u66f4\u597d\u4e00\u4e9b\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5403\u91cc\u722c\u5916\u7684\u4e3e\u52a8\u7acb\u5373\u8d62\u5f97\u6c5f\u9edb\u513f\u5979\u5988\u5988\u674e\u6708\u8339\u7684\u597d\u611f\uff0c\u5979\u56de\u5934\u558a\u6c5f\u9edb\u513f\u5979\u7238\uff1a\u201c\u8001\u6c5f\uff0c\u4f60\u8d76\u7d27\u7ed9\u8001\u5468\u6253\u7535\u8bdd\u95ee\u4e00\u4e0b\uff0c\u8fd9\u534f\u8bae\u8fd9\u4e48\u5199\u4f1a\u4e0d\u4f1a\u6709\u4ec0\u4e48\u9057\u6f0f\uff1f\u201d</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u4e00\u76f4\u6ca1\u6709\u6b63\u5f0f\u4ecb\u7ecd\u8fc7\u5979\u7238\u5988\uff0c\u5f00\u53e3\u95ee\u957f\u8f88\u7684\u59d3\u540d\u603b\u662f\u5f88\u5931\u793c\u7684\u884c\u4e3a\uff0c\u8981\u4e0d\u662f\u62df\u5199\u4ee3\u8a00\u534f\u8bae\uff0c\u5f20\u606a\u4e5f\u4e0d\u77e5\u9053\u6c5f\u9edb\u513f\u5979\u7238\u5988\u7684\u59d3\u540d\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u5979\u7238\u6c5f\u6d9b\u5f88\u65e0\u5948\u7684\u7b11\u4e86\u7b11\uff0c\u53ea\u5f97\u6309\u59bb\u5b50\u7684\u5429\u5490\u7ed9\u4ed6\u7684\u5f8b\u5e08\u670b\u53cb\u62e8\u7535\u8bdd\uff0c\u7ad9\u5728\u7535\u8bdd\u673a\u65c1\u8fb9\uff0c\u770b\u89c1\u8b66\u8f66\u505c\u5728\u7a97\u5916\u3001\u7530\u6d77\u971e\u6b63\u4e0e\u4e24\u540d\u8b66\u5458\u4ece\u8f66\u91cc\u94bb\u51fa\u6765\uff0c\u6307\u4e86\u6307\u7a97\u5916\uff0c\u5bf9\u5f20\u606a\u4ed6\u4eec\u8bf4\uff1a\u201c\u516c\u5b89\u5c40\u7684\u627e\u4e0a\u95e8\u6765\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u5979\u5988\u674e\u6708\u8339\u4e0d\u77e5\u9053\u5f20\u606a\u4ed6\u4eec\u88ab\u7838\u8f66\u7684\u4e8b\u60c5\uff0c\u4e0d\u6653\u5f97\u53d1\u751f\u4ec0\u4e48\u4e8b\u8ba9\u8b66\u5bdf\u627e\u4e0a\u95e8\u6765\uff0c\u5fc3\u91cc\u53d1\u614c\uff0c\u53f6\u5c0f\u6850\u3001\u8bb8\u7ef4\u3001\u6c5f\u9edb\u513f\u5979\u4eec\u7eb7\u7eb7\u7ad9\u8d77\u6765\uff0c\u5f20\u606a\u5374\u5750\u5728\u90a3\u91cc\u5cbf\u7136\u4e0d\u52a8\uff0c\u7b49\u5230\u5916\u9762\u4eba\u6309\u54cd\u95e8\u94c3\uff0c\u624d\u6b20\u8eab\u7ad9\u8d77\u6765\uff0c\u770b\u7740\u6c5f\u9edb\u513f\u5979\u7238\u7238\u6c5f\u6d9b\u8fc7\u53bb\u5f00\u95e8\u3002</p><p>\u3000\u3000\u7530\u6d77\u971e\u671b\u8fdb\u6765\uff0c\u9996\u5148\u8981\u627e\u53f6\u5c0f\u6850\uff0c\u8fd9\u4e8b\u80fd\u4e0d\u80fd\u6084\u65e0\u58f0\u606f\u7684\u89e3\u51b3\u6389\uff0c\u8fd8\u8981\u8fd9\u4f4d\u5927\u5c0f\u59d0\u4e0d\u95f9\u813e\u6c14\u624d\u884c\uff0c\u76ee\u5149\u5728\u5c4b\u91cc\u4eba\u8138\u4e0a\u8f6c\u4e86\u4e00\u5708\uff0c\u6e9c\u8fc7\u5f20\u606a\u7684\u8138\uff0c\u6123\u4e86\u4e00\u4e0b\uff1a\u201c\u554a\uff0c\u662f\u4f60\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u6ca1\u6709\u60f3\u5230\u60e0\u5c71\u8b66\u65b9\u8fc7\u6765\u8c03\u89e3\u7684\u4eba\u4f1a\u662f\u5e74\u521d\u5728\u60e0\u5c71\u5927\u8f66\u7978\u73b0\u573a\u8ba4\u8bc6\u7684\u90a3\u4f4d\u5973\u8b66\u5b98\uff0c\u5f53\u65f6\u7530\u6d77\u971e\u4e0d\u4fbf\u8868\u660e\u8eab\u4efd\uff0c\u53ea\u77e5\u9053\u5979\u53eb\u7530\u6d77\u971e\uff0c\u5f20\u606a\u62cd\u4e86\u62cd\u8111\u888b\uff0c\u5fcd\u4e0d\u4f4f\u8981\u5927\u7b11\uff1a\u201c\u4e8b\u60c5\u8fd8\u662f\u5de7\u5462\uff0c\u5468\u5e02\u957f\u4e0d\u4f1a\u8ba9\u4f60\u51fa\u9762\u8c03\u89e3\u8f66\u5b50\u7684\u4e8b\u60c5\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5f53\u65f6\u624b\u5fd9\u811a\u4e71\u7684\uff0c\u4f60\u4eec\u7559\u4e0b\u7684\u540d\u7247\u4e0d\u6653\u5f97\u4ec0\u4e48\u65f6\u5019\u641e\u4e22\u4e86\uff0c\u7535\u8bdd\u3001\u5730\u5740\u90fd\u4e0d\u8bb0\u5f97\uff0c\u4e13\u95e8\u5230\u6d77\u5dde\u6253\u542c\u8fc7\uff0c\u5c31\u662f\u6ca1\u542c\u8fc7\u8bf4\u6d77\u5dde\u5e02\u91cc\u6709\u4ec0\u4e48\u8d8a\u79c0\u63a7\u80a1\u516c\u53f8\uff0c\u5bb3\u5f97\u6211\u4eec\u5b8b\u5c40\u957f\u603b\u662f\u57cb\u6028\u6211\uff0c\u5f53\u521d\u8981\u4e0d\u662f\u4f60\u53ca\u65f6\u5c06\u6211\u4eec\u5b8b\u5c40\u957f\u4ece\u8f66\u5b50\u91cc\u62c9\u51fa\u6765\uff0c\u540e\u679c\u771f\u4e0d\u582a\u8bbe\u60f3\u3002\u4f60\u4eec\u4e0d\u4f1a\u7559\u4e0b\u5047\u59d3\u540d\u5417\uff1f\u201d\u7530\u6d77\u971e\u4e00\u8138\u60ca\u559c\uff0c\u54ea\u987e\u5f97\u4e0a\u4ec0\u4e48\u7838\u8f66\u7684\u4e8b\u60c5\uff0c\u201c\u4f60\u7684\u5973\u670b\u53cb\u5462\uff0c\u8bb8\u601d\u8fd9\u6b21\u6709\u6ca1\u6709\u5230\u60e0\u5c71\u6765\uff0c\u8ba9\u6211\u8ddf\u5b8b\u5c40\u957f\u597d\u597d\u5c3d\u4e00\u5c3d\u5730\u4e3b\u4e4b\u8c0a\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u2026\u2026\u201d\u5f20\u606a\u6ca1\u60f3\u5230\u7530\u6d77\u971e\u5634\u8fd9\u4e48\u5229\u7d22\uff0c\u8ba9\u5979\u8fd9\u4e00\u8bf4\uff0c\u5fc3\u810f\u5dee\u70b9\u5c31\u505c\u5728\u90a3\u91cc\uff0c\u773c\u770b\u7740\u5176\u4ed6\u4eba\u90fd\u4e00\u8138\u7684\u60ca\u7591\uff0c\u8d76\u5fd9\u8bf4\uff0c\u201c\u7530\u8b66\u5b98\u95ee\u6211\u8bb8\u601d\u59d0\u554a\uff0c\u5979\u4eba\u5728\u6d77\u5dde\uff0c\u5f53\u65f6\u5728\u9ad8\u901f\u8def\u4e0a\u9047\u5230\u8f66\u7978\uff0c\u90fd\u60ca\u614c\u5931\u63aa\u7684\uff0c\u56de\u5bb6\u4e5f\u6ca1\u6562\u8ddf\u5bb6\u91cc\u4eba\u8bf4\uff0c\u201d\u6307\u7740\u8bb8\u7ef4\u7b49\u4eba\uff0c\u201c\u4ed6\u4eec\u90fd\u4e0d\u77e5\u9053\u6211\u4eec\u5728\u60e0\u5c71\u9047\u5230\u8f66\u7978\u7684\u4e8b\u60c5\uff0c\u90fd\u8ba9\u7530\u8b66\u5b98\u4f60\u8bf4\u7834\u4e86\u3002\u201d\u53c8\u4ecb\u7ecd\u8bb8\u7ef4\u7ed9\u5979\u8ba4\u8bc6\uff0c\u201c\u7530\u8b66\u5b98\uff0c\u8bb8\u7ef4\u59d0\u662f\u8bb8\u601d\u7684\u59b9\u59b9\uff0c\u8fd9\u6b21\u8ddf\u7740\u5230\u60e0\u5c71\u6765\u73a9\uff0c\u6ca1\u60f3\u5230\u53c8\u9047\u4e0a\u8fd9\u644a\u5b50\u4e8b\u2026\u2026\u201d</p><p>\u3000\u3000\u5f53\u521d\u5f20\u606a\u4e0e\u8bb8\u601d\u7edf\u4e00\u53e3\u5f84\uff0c\u53ea\u8bf4\u7ed9\u60e0\u5c71\u5927\u8f66\u7978\u5835\u5728\u60e0\u5c71\u5e02\u91cc\u51e0\u5929\uff0c\u6ca1\u6709\u63d0\u4ed6\u4eec\u7ecf\u5386\u60e0\u5c71\u5927\u8f66\u7978\u7684\u4e8b\u60c5\uff0c\u5927\u5bb6\u7684\u6ce8\u610f\u529b\u90fd\u7ed9\u5438\u5f15\u5230\u5927\u8f66\u7978\u4e0a\u9762\u53bb\u4e86\uff0c\u7530\u6d77\u971e\u8ba9\u5f20\u606a\u8fd9\u4e48\u4e00\u89e3\u91ca\uff0c\u8fd8\u4ee5\u4e3a\u5979\u641e\u9519\u4ed6\u4e0e\u8bb8\u601d\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u5374\u6ca1\u6709\u60f3\u5230\u90a3\u573a\u5927\u8f66\u7978\u8ba9\u5f20\u606a\u4e0e\u8bb8\u601d\u4e4b\u95f4\u6700\u7ec8\u6345\u7834\u90a3\u5c42\u7a97\u6237\u7eb8\u3002</p><p>\u3000\u3000\u4e00\u65f6\u95f4\u6ca1\u4eba\u7406\u4f1a\u8f66\u5b50\u88ab\u7838\u7684\u4e8b\u60c5\uff0c\u90fd\u8ba9\u5f20\u606a\u8bf4\u60e0\u5c71\u5927\u8f66\u7978\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u7530\u6d77\u971e\u5bf9\u5f20\u606a\u8f66\u7978\u53d1\u751f\u540e\u9547\u5b9a\u7684\u7ec4\u7ec7\u5927\u5bb6\u8fdb\u884c\u81ea\u6551\u7684\u5370\u8c61\u5f88\u6df1\u523b\uff0c\u5f20\u606a\u4e0d\u80af\u591a\u8bf4\u4ec0\u4e48\uff0c\u7530\u6d77\u971e\u4fbf\u5c06\u4ed6\u5f53\u65f6\u7ec4\u7ec7\u5927\u5bb6\u81ea\u6551\u7684\u4e8b\u60c5\u8bf4\u4e86\u4e00\u901a\uff0c\u53c8\u5c06\u8f66\u7978\u73b0\u573a\u7684\u60e8\u72b6\u63cf\u8ff0\u4e86\u4e00\u756a\u3002</p><p>\u3000\u3000\u5927\u5bb6\u542c\u4e86\u7a00\u5618\u4e0d\u5df2\uff0c\u6c5f\u9edb\u513f\u6342\u7740\u80f8\u53e3\uff0c\u5fc3\u91cc\u6709\u4e9b\u53d1\u7d27\uff0c\u66ff\u4ed6\u4eec\u60f3\u60f3\u90fd\u611f\u5230\u540e\u6015\uff0c\u8bf4\u9053\uff1a\u201c\u554a\uff0c\u90a3\u5929\u665a\u4e0a\u6211\u8fd8\u62c9\u7740\u4f60\u4eec\u901b\u918b\u574a\u6865\uff0c\u8981\u662f\u6211\u9047\u5230\u8fd9\u79cd\u4e8b\uff0c\u53ea\u6015\u8fc7\u534a\u5e74\u60f3\u60f3\u811a\u8fd8\u53d1\u6296\uff0c\u4f60\u4eec\u600e\u4e48\u5c31\u8ddf\u6ca1\u4e8b\u4eba\u4f3c\u7684\uff1f\u201d</p></article>"}}}]);
