(window.webpackJsonp=window.webpackJsonp||[]).push([[325],{347:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e00\u8282 \u8a93\u7ea6",content:"<article><h2>\u7b2c\u56db\u5341\u4e00\u8282 \u8a93\u7ea6</h2><p>\u5982\u679c\u8bf4\u5728\u6b64\u4e4b\u524d\uff0c\u743c\u6069\u8fd8\u8ba4\u4e3a\u8fd9\u4e2a\u4e16\u754c\u4e0a\u5904\u5904\u5371\u673a\uff0c\u6b65\u6b65\u9677\u9631\uff0c\u6beb\u65e0\u534a\u70b9\u5b89\u5168\u611f\u53ef\u8a00\u7684\u8bdd\uff0c\u73b0\u5728\u4ed6\u5df2\u7ecf\u5b8c\u5168\u6539\u53d8\u770b\u6cd5\u4e86\uff0c\u8fd9\u771f\u662f\u4e00\u4e2a\u5149\u660e\u7684\u793e\u4f1a\uff0c\u548c\u8c10\u4e4b\u5149\u904d\u7167\u5927\u5730\u3002</p><p>\u56e0\u4e3a\u5c31\u8fde\u51db\u8fd9\u6837\u5192\u5192\u5931\u5931\u7684\u5973\u5b69\u5b50\uff0c\u5c45\u7136\u90fd\u80fd\u5b89\u7136\u65e0\u6059\u5730\u6d3b\u5230\u4eca\u5929\u3002</p><p>\u4e0a\u6b21\u5728\u963f\u683c\u62c9\u9686\uff0c\u5979\u5df2\u7ecf\u6709\u8fc7\u4e00\u6b21\u8bef\u670d\u5a9a\u836f\u7684\u7ecf\u5386\uff0c\u7ed3\u679c\u628a\u81ea\u5df1\u90fd\u8d54\u4e86\u8fdb\u53bb\uff1b\u4e8b\u60c5\u624d\u8fc7\u53bb\u534a\u5e74\uff0c\u5386\u53f2\u5c45\u7136\u53c8\u4e00\u6b21\u5982\u6b64\u8fd1\u4f3c\u5730\u4e0a\u6f14\u4e86\u3002\u8fd9\u4f4d\u51db\u5927\u5c0f\u59d0\u679c\u7136\u662f\u4ece\u6765\u4e0d\u4f1a\u5438\u53d6\u6559\u8bad\u7684\u554a\u3002</p><p>\u5176\u5b9e\u8fd9\u4ef6\u4e8b\u60c5\u5f88\u5947\u602a\uff0c\u51db\u4e5f\u5e76\u975e\u771f\u662f\u90a3\u79cd\u51fa\u8eab\u8c6a\u95e8\u4e0d\u901a\u4e16\u4e8b\u7684\u5927\u5c0f\u59d0\uff0c\u4e0d\u77e5\u9053\u4e3a\u4ec0\u4e48\u6027\u683c\u4f1a\u5982\u6b64\u4e0d\u8c28\u614e\u3002\u5979\u6bcd\u4eb2\u5f88\u65e9\u5c31\u53bb\u4e16\uff0c\u7b97\u662f\u88ab\u6885\u83f2\u65af\u5bb6\u6536\u517b\uff0c\u540e\u6765\u8eb2\u907f\u4ec7\u4eba\u8ffd\u6740\uff0c\u548c\u6885\u83f2\u65af\u4e00\u8d77\u5728\u5916\u6d41\u6d6a\uff0c\u4e5f\u7b97\u662f\u5386\u5c3d\u8270\u96be\u3002\u5c31\u7b97\u540e\u6765\u88ab\u6b23\u5e03\u770b\u4e2d\uff0c\u6536\u4e3a\u5b66\u751f\u2014\u2014\u4f46\u4e00\u770b\u6b23\u5e03\u81ea\u5df1\u7684\u908b\u9062\u6837\u5b50\u5c31\u77e5\u9053\uff0c\u5979\u4e5f\u4e0d\u662f\u4e2a\u4f1a\u7167\u987e\u4eba\u7684\uff0c\u6216\u8005\u8bf4\uff0c\u8fd9\u5bf9\u5e08\u5f92\u5176\u5b9e\u6027\u60c5\u90fd\u662f\u4e00\u7c7b\uff0c\u51db\u4e5f\u672a\u89c1\u5f97\u5c31\u80fd\u8fc7\u5f97\u517b\u5c0a\u5904\u4f18\u4e86\uff0c\u53ea\u6015\u662f\u8ddf\u7740\u5979\u5230\u5904\u6e38\u8361\u5954\u6ce2\u8fd8\u5dee\u4e0d\u591a\u3002\u5979\u5c45\u7136\u8fd8\u4e00\u76f4\u4fdd\u6301\u8fd9\u79cd\u6027\u683c\uff0c\u53ea\u80fd\u8bf4\u662f\u5929\u6027\u4e86\u3002</p><p>\u66f4\u4f55\u51b5\uff0c\u51db\u8fd8\u5728\u7ea2\u888d\u5deb\u5e08\u4f1a\u91cc\u5f85\u8fc7\uff0c\u88ab\u5949\u4e3a\u7b2c\u4e00\u7f8e\u4eba\uff0c\u6709\u4e00\u5927\u7968\u503e\u6155\u8005\u548c\u89ca\u89ce\u8005\u3002\u7ea2\u888d\u5deb\u5e08\u4f1a\u90a3\u662f\u4ec0\u4e48\u5730\u65b9\uff0c\u5927\u9646\u4e0a\u6700\u8457\u540d\u7684\u9ed1\u9053\u7ec4\u7ec7\u4e4b\u4e00\uff0c\u6740\u4eba\u653e\u706b\u8d29\u5356\u6bd2\u54c1\uff0c\u65e0\u6240\u4e0d\u4e3a\u7684\uff0c\u91cc\u9762\u57f9\u517b\u51fa\u6765\u7684\u5deb\u5e08\u53c8\u600e\u4e48\u53ef\u80fd\u662f\u5584\u826f\u4eba\u58eb\uff0c\u5341\u6709\u516b\u4e5d\u90fd\u662f\u90aa\u6076\u5f97\u5168\u8eab\u5192\u7ea2\u5149\u7684\u89d2\u8272\u3002\u51db\u5728\u8fd9\u79cd\u7ec4\u7ec7\u91cc\u5f85\u4e86\u5927\u6982\u4e09\u56db\u5e74\uff0c\u5c45\u7136\u8fd8\u4e00\u76f4\u662f\u5b8c\u74a7\u4e4b\u8eab\uff0c\u76f4\u5230\u88ab\u743c\u6069\u63a8\u5012\uff0c\u8fd9\u5b9e\u5728\u662f\u8ba9\u4eba\u6709\u4e9b\u532a\u5937\u6240\u601d\u3002</p><p>\u201c\u636e\u827e\u5f25\u8587\u8bf4\uff0c\u51db\u4e00\u76f4\u5c31\u662f\u8fd9\u574f\u6bdb\u75c5\u90fd\u6ca1\u6539\u8fc7\u3002\u7136\u800c\u90a3\u4e9b\u7ea2\u888d\u5deb\u5e08\u4eec\u5c45\u7136\u90fd\u6ca1\u4e00\u4e2a\u5bf9\u5979\u4e0b\u836f\u7684\uff1f\u96be\u9053\u7ea2\u888d\u5deb\u5e08\u4f1a\u7684\u8bad\u7ec3\u8bfe\u7a0b\u4e2d\u6ca1\u6709\u5236\u836f\u5b66\u4e48\uff0c\u867d\u8bf4\u5a9a\u836f\u4e0d\u7b97\u4e3b\u8981\u5185\u5bb9\uff0c\u603b\u4e5f\u6709\u6240\u6d89\u730e\u5427\u2026\u2026\u662f\u7ea2\u888d\u5deb\u5e08\u4f1a\u592a\u5584\u826f\u4e86\uff0c\u8fd8\u662f\u6211\u592a\u90aa\u6076\u4e86\u2026\u2026\u201d</p><p>\u81ea\u5df1\u592a\u90aa\u6076\u8fd8\u662f\u7ea2\u888d\u5deb\u5e08\u592a\u5584\u826f\uff0c\u6291\u6216\u771f\u7684\u662f\u81ea\u5df1\u4eba\u54c1\u8d85\u597d\uff0c\u4e3b\u89d2\u6a21\u677f\uff0c\u8fd9\u4e9b\u6682\u65f6\u90fd\u4e0d\u7528\u8003\u8651\u4e86\u3002\u73b0\u5728\u7684\u95ee\u9898\u662f\uff1a\u8fd9\u6b21\u53c8\u600e\u4e48\u89e3\u51b3\u3002</p><p>\u4e8b\u51fa\u7a81\u7136\uff0c\u6885\u83f2\u65af\u4e5f\u6ca1\u60f3\u5230\u4f1a\u51fa\u73b0\u8fd9\u79cd\u5c40\u9762\uff0c\u63d0\u9192\u7a0d\u8fdf\uff0c\u51db\u5df2\u7ecf\u628a\u6574\u676f\u7684\u5a9a\u836f\u90fd\u559d\u4e0b\u53bb\u4e86\uff0c\u8fd8\u5728\u4e00\u672c\u6b63\u7ecf\u5730\u8bc4\u4ef7\u8bf4\u5473\u9053\u4e0d\u9519\u3002\u53ea\u662f\u8bdd\u521a\u8bf4\u51fa\uff0c\u5979\u4f3c\u4e4e\u5c31\u5f00\u59cb\u6709\u4e9b\u5934\u6655\u7684\u6837\u5b50\uff0c\u8eab\u4f53\u6447\u6447\u6643\u6643\uff0c\u7ad9\u7acb\u4e0d\u7a33\uff0c\u6885\u83f2\u65af\u671d\u743c\u6069\u77aa\u4e86\u4e00\u773c\uff0c\u8d70\u8fc7\u6765\u6276\u7740\u5979\u3002</p><p>\u201c\u73b0\u5728\u4ec0\u4e48\u611f\u89c9\uff1f\u201d\u5979\u95ee\u3002</p><p>\u201c\u5934\u6709\u70b9\u6655\u4e4e\u4e4e\u7684\uff0c\u201d\u51db\u4f4e\u58f0\u8bf4\uff0c\u501a\u9760\u5728\u6885\u83f2\u65af\u80a9\u4e0a\uff0c\u201c\u8eab\u4e0a\u7a81\u7136\u597d\u70ed\u2026\u2026\u201d</p><p>\u51db\u662f\u9f99\u5973\uff0c\u7528\u6885\u83f2\u65af\u7684\u8bdd\u8bf4\u5c31\u662f\u51b7\u8840\u52a8\u7269\uff0c\u6ca1\u6709\u51b7\u70ed\u6982\u5ff5\u7684\uff0c\u54ea\u6015\u5728\u590f\u5929\u5979\u4e5f\u7167\u6837\u80fd\u7a7f\u5927\u8863\uff0c\u5979\u4f1a\u89c9\u5f97\u7a81\u7136\u597d\u70ed\uff0c\u90a3\u53ea\u6709\u4e00\u79cd\u89e3\u91ca\uff0c\u5c31\u662f\u5a9a\u836f\u5728\u8d77\u4f5c\u7528\u4e86\u2026\u2026\u751f\u6548\u5f97\u597d\u5feb\u3002</p><p>\u201c\u62b1\u7740\u6211\uff0c\u827e\u5f25\u8587\uff0c\u201d\u51db\u6073\u6c42\u7740\uff0c\u6574\u4e2a\u4eba\u5df2\u7ecf\u57cb\u8fdb\u6885\u83f2\u65af\u6000\u91cc\u3002\u5979\u8eab\u9ad8\u8fdc\u900a\uff0c\u53c8\u662f\u7a7f\u7740\u88d9\u88c5\uff0c\u770b\u8d77\u6765\u8fd8\u771f\u50cf\u5c0f\u5973\u5b69\u5728\u5411\u60c5\u4eba\u6492\u5a07\u4f3c\u7684\uff0c\u201c\u8eab\u4e0a\u597d\u96be\u53d7\u2026\u2026\u201d</p><p>\u6885\u83f2\u65af\u521a\u521a\u6d17\u6d74\u51fa\u6765\uff0c\u8eab\u4e0a\u539f\u672c\u6b63\u6e05\u51c9\uff0c\u88ab\u5979\u8fd9\u4e48\u4e00\u8d34\u8fc7\u6765\uff0c\u5c31\u50cf\u662f\u6000\u91cc\u7a81\u7136\u591a\u4e86\u5c0f\u706b\u7089\u4f3c\u7684\uff0c\u4f38\u624b\u629a\u6478\u5979\u7684\u76ae\u80a4\uff0c\u611f\u89c9\u6307\u5c16\u90fd\u5fae\u5fae\u6709\u4e9b\u53d1\u70eb\u3002\u4e0d\u8fc7\u7247\u523b\u529f\u592b\uff0c\u51db\u7684\u4fcf\u8138\u4e0a\u5df2\u7ecf\u6ee1\u662f\u7ea2\u971e\uff0c\u547c\u5438\u6025\u4fc3\uff0c\u53cc\u773c\u534a\u5f00\u534a\u5408\uff0c\u7565\u5e26\u6e56\u84dd\u8272\u7684\u78a7\u7eff\u53cc\u7738\u8499\u4e0a\u4e86\u4e00\u5c42\u96fe\u6c14\uff0c\u77b3\u5b54\u4e2d\u5fae\u5fae\u8df3\u8dc3\u7684\u707f\u70c2\u706b\u5149\u4e5f\u90fd\u6682\u65f6\u7184\u706d\uff0c\u6c34\u6c6a\u6c6a\u7684\u52a8\u4eba\u81f3\u6781\u3002</p><p>\u838e\u73de\u514b\u679c\u7136\u662f\u6709\u81ea\u4fe1\u7684\u8d44\u672c\uff0c\u5979\u914d\u5f97\u8fd9\u526f\u5a9a\u836f\u751f\u6548\u6781\u5feb\uff0c\u8fd9\u624d\u534a\u5206\u949f\u4e0d\u5230\uff0c\u51db\u5c31\u5df2\u7ecf\u610f\u4e71\u60c5\u8ff7\uff0c\u4e0d\u80fd\u81ea\u5236\u4e86\u3002\u6885\u83f2\u65af\u6273\u7740\u5979\u7684\u80a9\uff0c\u60f3\u8ba9\u5979\u6682\u65f6\u51b7\u9759\u4e0b\u6765\uff0c\u5374\u88ab\u51db\u201c\u5514\u201d\u4e86\u4e00\u58f0\uff0c\u53cc\u81c2\u73af\u7ed5\u6402\u4f4f\u8116\u9888\uff0c\u6a31\u5507\u4fbf\u5728\u6885\u83f2\u65af\u7684\u9501\u9aa8\u4e0a\u4eb2\u543b\u8d77\u6765\u3002</p><p>\u6885\u83f2\u65af\u521a\u8d70\u51fa\u6d74\u5ba4\uff0c\u8eab\u4e0a\u53ea\u88f9\u7740\u4e00\u4ef6\u6d74\u5dfe\uff0c\u5979\u8eab\u6750\u53c8\u6781\u9ad8\uff0c\u6d74\u5dfe\u867d\u7136\u5bbd\u5927\uff0c\u5374\u4e5f\u53ea\u80fd\u52c9\u5f3a\u906e\u4f4f\u80f8\u8179\uff0c\u80a9\u90e8\u4ee5\u4e0a\u548c\u4e00\u53cc\u7389\u817f\u51e0\u4e4e\u5c3d\u6570\u66b4\u9732\u5728\u5916\u3002\u51db\u7684\u5634\u5507\u706b\u70ed\u6eda\u70eb\uff0c\u67d4\u8f6f\u4e2d\u7565\u5e26\u6c99\u6c99\u6469\u64e6\u611f\uff0c\u867d\u7136\u662f\u540c\u6027\uff0c\u88ab\u543b\u5728\u8eab\u4e0a\u4e5f\u6709\u79cd\u8bf4\u4e0d\u51fa\u7684\u6109\u60a6\uff0c\u4e00\u9635\u4e00\u9635\u7684\u8f7b\u5fae\u9165\u75d2\uff0c\u4eff\u4f5b\u7535\u51fb\u3002\u6885\u83f2\u65af\u53f9\u4e86\u53e3\u6c14\uff0c\u4efb\u5979\u62b1\u7740\uff0c\u56de\u8fc7\u5934\u770b\u7740\u743c\u6069\uff0c\u201c\u4f60\u4e0d\u4f1a\u662f\u6545\u610f\u7684\u5427\u3002\u201d</p><p>\u743c\u6069\u65e0\u8f9c\u5730\u644a\u5f00\u624b\uff0c\u201c\u6211\u5c31\u7b97\u518d\u6545\u610f\uff0c\u4e5f\u4e0d\u53ef\u80fd\u9884\u6d4b\u5230\u5979\u4f1a\u7a81\u7136\u51b2\u56de\u6765\u554a\u3002\u201d</p><p>\u201c\u6709\u89e3\u836f\u4e48\uff1f\u201d</p><p>\u201c\u5a9a\u836f\u53c8\u4e0d\u662f\u6bd2\u836f\uff0c\u53ea\u662f\u50ac\u60c5\u52a9\u5174\u7528\u7684\uff0c\u54ea\u6765\u7684\u89e3\u836f\uff0c\u800c\u4e14\u8fd9\u662f\u838e\u73de\u514b\u914d\u7684\uff0c\u73b0\u5728\u5979\u4e5f\u4e0d\u5728\u554a\u3002\u201d</p><p>\u743c\u6069\u548c\u6885\u83f2\u65af\u662f\u6bd4\u8d5b\u5b8c\u4e4b\u540e\u76f4\u63a5\u5c31\u56de\u4e86\u8001\u5934\u9aa8\u65c5\u5e97\uff0c\u6b23\u5e03\u548c\u8475\u9732\u7b49\u4eba\u53bb\u641c\u5bfb\u8428\u9a6c\u65af\u7279\uff0c\u838e\u73de\u514b\u5374\u662f\u56e0\u4e3a\u5206\u5230\u4e86\u5956\u91d1\uff0c\u53c8\u53bb\u5546\u5e97\u8d2d\u7269\u4e86\u3002\u201c\u53cd\u6b63\u5c06\u6765\u56de\u4e86\u7269\u8d28\u754c\uff0c\u8fd9\u4e9b\u7075\u9b42\u5b9d\u77f3\u5c31\u6ca1\u7528\u4e86\uff0c\u4e0d\u5982\u6362\u6210\u7528\u5f97\u4e0a\u7684\u4e1c\u897f\u3002\u201d\u9b45\u9b54\u5982\u662f\u8bf4\u3002</p><p>\u8fd9\u51e0\u5929\u7684\u6bd4\u8d5b\uff0c\u838e\u73de\u514b\u4e00\u573a\u6ca1\u4e0a\uff0c\u57fa\u672c\u5c31\u53ea\u662f\u4f5c\u4e3a\u8d44\u6599\u5e93\u7684\u610f\u4e49\u5b58\u5728\uff0c\u8981\u8bf4\u5979\u4e00\u70b9\u529b\u6c14\u6ca1\u51fa\u90a3\u81ea\u7136\u662f\u8fc7\u5206\uff0c\u4f46\u771f\u6b63\u4e0a\u573a\u6253\u751f\u6253\u6b7b\u7684\u786e\u5b9e\u90fd\u4e0d\u662f\u5979\u3002\u800c\u4e14\u968f\u7740\u53c2\u8d5b\u9009\u624b\u8d8a\u6765\u8d8a\u5343\u5947\u767e\u602a\uff0c\u5404\u79cd\u5f02\u4f4d\u9762\u602a\u7269\u90fd\u6405\u5408\u8fdb\u6765\uff0c\u838e\u73de\u514b\u5f80\u5f80\u4e5f\u65e0\u6cd5\u53ca\u65f6\u63d0\u4f9b\u4ec0\u4e48\u6709\u4ef7\u503c\u7684\u60c5\u62a5\u4e86\u3002\u6bcf\u8f6e\u6bd4\u8d5b\u4e4b\u540e\u90fd\u4f1a\u6709\u4e00\u7b14\u5956\u91d1\uff0c\u539f\u672c\u5982\u679c\u6309\u7167\u51fa\u529b\u5927\u5c0f\u6765\u5206\u7684\u8bdd\uff0c\u838e\u73de\u514b\u62ff\u4e0d\u5230\u591a\u5c11\uff0c\u4f46\u56e0\u4e3a\u5176\u4ed6\u4eba\u53cd\u6b63\u4e5f\u6ca1\u4ec0\u4e48\u8d2d\u7269\u6b32\u671b\uff0c\u800c\u4e14\u8fd9\u91cc\u662f\u65ad\u57df\u9547\uff0c\u5546\u5e97\u91cc\u51fa\u552e\u7684\u4e1c\u897f\u57fa\u672c\u90fd\u662f\u4e3a\u6076\u9b54\u91cf\u8eab\u5b9a\u505a\u7684\uff0c\u9664\u4e86\u838e\u73de\u514b\u4e5f\u6ca1\u4eba\u80fd\u7528\u3002\u6240\u4ee5\u5230\u6700\u540e\uff0c\u9664\u4e86\u8865\u5145\u5fc5\u5907\u7684\u7bad\u77e2\u3001\u836f\u6c34\u4e4b\u5916\uff0c\u5269\u4e0b\u7684\u7075\u9b42\u5b9d\u77f3\u4e09\u5206\u4e4b\u4e8c\u90fd\u7ed9\u4e86\u838e\u73de\u514b\uff0c\u8fd8\u6709\u4e09\u5206\u4e4b\u4e00\u88ab\u51db\u548c\u4f0a\u8389\u96c5\u74dc\u5206\u4e86\uff0c\u7528\u53bb\u4e70\u9970\u54c1\u548c\u5404\u79cd\u5973\u5b69\u5b50\u559c\u6b22\u7684\u5c0f\u73a9\u610f\u3002\u838e\u73de\u514b\u62ff\u4e86\u5b9d\u77f3\u4fbf\u6574\u65e5\u5728\u5916\u8d2d\u7269\uff0c\u5982\u4eca\u5df2\u7ecf\u628a\u5168\u8eab\u4e0a\u4e0b\u90fd\u6b66\u88c5\u4e86\u4e00\u904d\uff0c\u5c31\u8fde\u5979\u6700\u65e9\u7528\u7684\u90a3\u6761\u706b\u7ea2\u8272\u957f\u97ad\u90fd\u6362\u4e86\u65b0\u7684\u3002</p><p>\u65ad\u57df\u9547\u5982\u6b64\u5927\uff0c\u4e00\u65f6\u534a\u523b\u4e4b\u95f4\u54ea\u91cc\u53bb\u627e\u5979\u56de\u6765\uff0c\u5c31\u7b97\u627e\u5230\u4e5f\u672a\u5fc5\u6709\u89e3\u836f\u2014\u2014\u5c31\u7b97\u6709\u89e3\u836f\uff0c\u770b\u51db\u73b0\u5728\u7684\u6a21\u6837\u4e5f\u6765\u4e0d\u53ca\u914d\u5236\u4e86\u3002</p><p>\u6885\u83f2\u65af\u5fae\u5fae\u76b1\u7709\uff0c\u6b64\u65f6\u51db\u5df2\u7ecf\u628a\u5979\u62b1\u5f97\u66f4\u7d27\u4e86\uff0c\u8138\u4e0a\u8d8a\u52a0\u53d1\u70eb\uff0c\u6d53\u90c1\u9187\u539a\u7684\u9999\u6c14\u4ece\u9f99\u5973\u4f53\u5185\u900f\u51fa\u6765\uff0c\u662f\u73ab\u7470\u82b1\u7684\u5473\u9053\uff0c\u5145\u6ee1\u60c5\u6b32\u7684\u6eda\u70ed\u5598\u606f\u547c\u5728\u76ae\u80a4\u4e0a\uff0c\u5f04\u5f97\u5723\u6b66\u58eb\u81ea\u5df1\u90fd\u6709\u4e9b\u52a8\u60c5\u3002\u6df1\u6df1\u547c\u4e86\u53e3\u6c14\uff0c\u91d1\u53d1\u5c11\u5973\u52c9\u5f3a\u8ba9\u81ea\u5df1\u9547\u5b9a\u4e0b\u6765\uff0c\u201c\u743c\u6069\uff0c\u628a\u6d74\u7f38\u653e\u6ee1\u51b7\u6c34\uff0c\u201d\u5979\u60f3\u4e86\u60f3\u8bf4\uff0c\u201c\u628a\u5979\u6254\u8fdb\u53bb\u6ce1\u4e0a\u4e94\u5206\u949f\u3002\u201d</p><p>\u5a9a\u836f\u4e0d\u662f\u6bd2\u836f\uff0c\u901a\u5e38\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u7684\u89e3\u836f\u4e4b\u7c7b\uff0c\u56e0\u4e3a\u5b83\u5176\u5b9e\u4e5f\u5bb9\u6613\u89e3\uff0c\u62ff\u51e0\u76c6\u51b7\u6c34\u4ece\u5934\u6d47\u4e0b\u6765\uff0c\u57fa\u672c\u4e5f\u5c31\u5dee\u4e0d\u591a\u4e86\u3002\u53ea\u662f\u838e\u73de\u514b\u914d\u7684\u5a9a\u836f\u660e\u663e\u6548\u529b\u975e\u51e1\uff0c\u53ea\u6015\u8fd9\u79cd\u65b9\u6cd5\u90fd\u672a\u5fc5\u7ba1\u7528\uff0c\u5982\u4eca\u4e5f\u53ea\u80fd\u8bd5\u8bd5\u4e86\u3002</p><p>\u743c\u6069\u539f\u672c\u4e5f\u5df2\u7ecf\u60f3\u5230\u4e86\u8fd9\u79cd\u65b9\u6cd5\uff0c\u53ea\u662f\u6545\u610f\u88c5\u4f5c\u4e0d\u77e5\uff0c\u5982\u4eca\u6885\u83f2\u65af\u5df2\u7ecf\u8bf4\u8bdd\uff0c\u4ed6\u4e5f\u53ea\u80fd\u9887\u4e3a\u9057\u61be\u5730\u8038\u8038\u80a9\uff0c\u8f6c\u8eab\u8fdb\u4e86\u6d74\u5ba4\uff0c\u5fc3\u4e2d\u6697\u81ea\u60cb\u60dc\uff0c\u8fd9\u4e48\u597d\u7684\u673a\u4f1a\u5c31\u767d\u767d\u9519\u5931\u4e86\u3002\u6885\u83f2\u65af\u548c\u51db\u90fd\u79f0\u5f97\u4e0a\u662f\u7edd\u8272\uff0c\u4e00\u4e2a\u575a\u6bc5\u82f1\u6b66\uff0c\u6027\u60c5\u6c89\u7a33\uff0c\u4e00\u4e2a\u98de\u626c\u8df3\u8131\uff0c\u4fcf\u4e3d\u53ef\u7231\uff0c\u800c\u4e14\u4e24\u4eba\u53c8\u662f\u631a\u53cb\uff0c\u60c5\u5982\u59d0\u59b9\u4e00\u822c\uff0c\u5b9e\u5728\u662f\u7537\u6027\u68a6\u5bd0\u4ee5\u6c42\u7684\u6069\u7269\u3002\u4e0a\u6b21\u5728\u963f\u683c\u62c9\u9686\u7684\u65f6\u5019\uff0c\u548c\u8fd9\u59d0\u59b9\u4e24\u4eba\u73a9\u4e86\u4e00\u6b21\u53cc\u98de\uff0c\u90a3\u7f8e\u5999\u6ecb\u5473\u4ee4\u743c\u6069\u81f3\u4eca\u96be\u5fd8\uff0c\u51db\u7684\u4f53\u5185\u706b\u70ed\u7d27\u81f4\uff0c\u800c\u4e14\u56e0\u4e3a\u662f\u6b63\u5e38\u4ea4\u5408\uff0c\u4ec5\u8bba\u8089\u4f53\u6109\u60a6\u7684\u8bdd\uff0c\u6bd4\u8d77\u548c\u6885\u83f2\u65af\u6b22\u597d\u8fd8\u8981\u80dc\u8fc7\u4e00\u7b79\uff0c\u54c1\u5c1d\u8fc7\u4e00\u6b21\u4e4b\u540e\u4fbf\u518d\u4e5f\u96be\u4ee5\u5fd8\u6000\u4e86\u3002</p><p>\u201c\u771f\u53ef\u60dc\uff0c\u524d\u5929\u8fd8\u5f00\u73a9\u7b11\u8bf4\u80fd\u518d\u6765\u4e00\u6b21\u5c31\u597d\uff0c\u6ca1\u60f3\u5230\u4eca\u5929\u771f\u5b9e\u73b0\u4e86\u2026\u2026\u773c\u770b\u5c31\u8981\u5b9e\u73b0\u4e86\uff0c\u53c8\u6ca1\u4e86\u3002\u201d</p><p>\u743c\u6069\u53f9\u6c14\u7740\uff0c\u5c06\u6d74\u7f38\u653e\u6ee1\u6c34\uff0c\u51fa\u4e86\u6d74\u5ba4\uff0c\u89c1\u51db\u8fd8\u662f\u8d56\u5728\u6885\u83f2\u65af\u6000\u91cc\u3002\u201c\u6c34\u653e\u597d\u4e86\u3002\u201d\u4ed6\u8bf4\uff0c\u6b63\u8981\u5148\u56de\u907f\uff0c\u6885\u83f2\u65af\u5374\u6447\u6447\u5934\uff0c\u201c\u7528\u4e0d\u7740\u4e86\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u5979\u8bf4\u5979\u60f3\u518d\u8bd5\u8bd5\u4e0a\u6b21\u90a3\u79cd\u611f\u89c9\u3002\u201d</p><p>\u201c\u554a\uff1f\u201d</p><p>\u5cf0\u56de\u8def\u8f6c\uff0c\u539f\u672c\u7684\u610f\u5916\u60ca\u559c\u53d8\u6210\u4e86\u5931\u671b\uff0c\u7a81\u7136\u53c8\u5728\u5931\u671b\u4e2d\u7838\u4e0b\u4e00\u4e2a\u60ca\u559c\u6765\uff0c\u743c\u6069\u8111\u5b50\u4e00\u65f6\u90fd\u6ca1\u8f6c\u8fc7\u5f2f\u6765\u3002\u201c\u554a\u4ec0\u4e48\uff1f\u8fc7\u6765\u5e2e\u5fd9\uff0c\u201d\u6885\u83f2\u65af\u4f4e\u5934\u770b\u4e86\u770b\u6000\u91cc\u7684\u670b\u53cb\uff0c\u53f9\u4e86\u53e3\u6c14\uff0c\u201c\u6211\u73b0\u5728\u4e00\u4e2a\u4eba\u53ef\u62b1\u4e0d\u52a8\u5979\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u838e\u73de\u514b\u914d\u5236\u7684\u5a9a\u836f\u679c\u7136\u6548\u529b\u60ca\u4eba\uff0c\u51db\u5728\u5e8a\u4e0a\u7684\u8868\u73b0\u53ea\u80fd\u7528\u706b\u8fa3\u6765\u5f62\u5bb9\uff0c\u5979\u5c31\u50cf\u4e00\u56e2\u718a\u718a\u70c8\u7130\u4f3c\u7684\uff0c\u51e0\u4e4e\u628a\u743c\u6069\u7ed9\u5b8c\u5168\u69a8\u5e72\u4e86\u3002\u5e78\u597d\u6709\u6885\u83f2\u65af\u5728\u65c1\u8fb9\uff0c\u5206\u6563\u4e86\u51db\u5f88\u5927\u4e00\u90e8\u5206\u6ce8\u610f\u529b\uff0c\u5426\u5219\u743c\u6069\u771f\u8981\u8003\u8651\u7528\u4e0d\u7528\u70b9\u9738\u9053\u7684\u91c7\u8865\u529f\u592b\u4e86\u3002\u90a3\u4e9b\u4e1c\u897f\u5bf9\u5973\u5b69\u5b50\u7684\u8eab\u4f53\u6709\u5bb3\u65e0\u76ca\uff0c\u4ed6\u539f\u672c\u4e0d\u60f3\u5bf9\u51db\u7528\uff0c\u4f46\u73b0\u5728\u770b\u6765\u4f3c\u4e4e\u8fd8\u662f\u81ea\u5df1\u7684\u6027\u547d\u66f4\u8981\u7d27\u3002</p><p>\u603b\u7b97\u51db\u611f\u5174\u8da3\u7684\u5bf9\u8c61\u4e0d\u4ec5\u4ec5\u662f\u743c\u6069\uff0c\u8fd8\u5305\u62ec\u6885\u83f2\u65af\uff0c\u8fd9\u624d\u8ba9\u5deb\u5e08\u6709\u4e86\u5598\u606f\u4e4b\u673a\u3002\u4e09\u4eba\u8db3\u8db3\u6298\u817e\u4e86\u534a\u65e5\uff0c\u743c\u6069\u7ec8\u4e8e\u7b4b\u75b2\u529b\u5c3d\u5730\u6c89\u6c89\u7761\u53bb\uff0c\u7559\u4e0b\u65c1\u8fb9\u4e24\u4f4d\u8fd8\u5728\u80a2\u4f53\u4ea4\u7f20\u4e2d\u7684\u5c11\u5973\u3002\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u4ed6\u7ec8\u4e8e\u8ff7\u8ff7\u7cca\u7cca\u5730\u9192\u6765\uff0c\u773c\u775b\u6682\u65f6\u8fd8\u7741\u4e0d\u5f00\uff0c\u5168\u8eab\u9178\u8f6f\u4e5f\u4e0d\u60f3\u52a8\u5f39\uff0c\u53ea\u662f\u9759\u9759\u8eba\u7740\uff0c\u4e00\u65f6\u95f4\u8111\u5b50\u91cc\u7a7a\u7a7a\u7684\uff0c\u4ec0\u4e48\u90fd\u4e0d\u60f3\uff0c\u751a\u81f3\u90fd\u4e0d\u77e5\u9053\u81ea\u5df1\u8eab\u5728\u4f55\u5904\u3002\u623f\u95f4\u91cc\u9759\u6084\u6084\u7684\uff0c\u5f25\u6f2b\u7740\u4e00\u79cd\u751c\u4e1d\u4e1d\u7684\u9999\u6c14\uff0c\u662f\u4e24\u4e2a\u5973\u5b69\u5b50\u8eab\u4e0a\u900f\u51fa\u6765\u7684\uff0c\u51db\u7684\u4f53\u9999\u50cf\u73ab\u7470\uff0c\u6d53\u90c1\u9187\u539a\uff0c\u6885\u83f2\u65af\u7684\u4f53\u9999\u5374\u50cf\u767e\u5408\uff0c\u6e05\u5e7d\u6de1\u96c5\uff0c\u82e5\u6709\u82e5\u65e0\uff0c\u7565\u5e26\u51b0\u51b7\uff0c\u6df7\u5408\u8d77\u6765\u522b\u6709\u4e00\u756a\u6ecb\u5473\u3002</p><p>\u5e8a\u5934\u6446\u653e\u7684\u9b54\u6cd5\u6c99\u6f0f\u53d1\u51fa\u8f7b\u5fae\u7684\u6c99\u6c99\u58f0\uff0c\u63d0\u9192\u7740\u65f6\u95f4\u5728\u4e00\u5206\u4e00\u79d2\u8fc7\u53bb\uff0c\u8fd9\u662f\u623f\u95f4\u91cc\u552f\u4e00\u7684\u52a8\u9759\u3002\u743c\u6069\u6162\u6162\u6536\u655b\u5fc3\u795e\uff0c\u5934\u8111\u6e10\u6e10\u6e05\u9192\u8fc7\u6765\uff0c\u7761\u524d\u53d1\u751f\u7684\u4e8b\u60c5\u4e5f\u4e00\u4e00\u8bb0\u8d77\u3002\u4ed6\u6b63\u8003\u8651\u8981\u4e0d\u8981\u8d77\u5e8a\u8fd8\u662f\u518d\u8eba\u4e0a\u7247\u523b\uff0c\u7a81\u7136\u611f\u89c9\u8eab\u65c1\u53f3\u4fa7\u6709\u4eba\u7a0d\u7a0d\u52a8\u4e86\u4e00\u52a8\uff0c\u63a5\u7740\u6162\u6162\u5750\u8d77\u8eab\u6765\u3002</p><p>\u662f\u6885\u83f2\u65af\u3002</p><p>\u4e0d\u7528\u770b\uff0c\u5355\u51ed\u52a8\u9759\u743c\u6069\u4fbf\u80fd\u5224\u65ad\uff0c\u6bd5\u7adf\u4e24\u4eba\u540c\u5e8a\u5171\u6795\u4e5f\u8fd9\u4e48\u4e45\u4e86\uff0c\u5f7c\u6b64\u975e\u5e38\u719f\u6089\u3002\u6885\u83f2\u65af\u5750\u8d77\u8eab\u6765\uff0c\u5374\u5e76\u6ca1\u6709\u4e0b\u5e8a\u7a7f\u8863\uff0c\u800c\u662f\u9760\u7740\u5e8a\u5934\uff0c\u4e00\u8a00\u4e0d\u53d1\uff0c\u4f3c\u4e4e\u4e5f\u5728\u9759\u9759\u5730\u60f3\u7740\u4ec0\u4e48\u3002</p><p>\u743c\u6069\u95ed\u4e0a\u773c\u775b\uff0c\u7ee7\u7eed\u88c5\u7761\u3002\u6ca1\u8fc7\u591a\u4e45\uff0c\u4ed6\u5de6\u4fa7\u4e5f\u6709\u52a8\u9759\uff0c\u8fd9\u6b21\u5374\u5927\u5f97\u591a\uff0c\u662f\u51db\u4e00\u9aa8\u788c\u4ece\u5e8a\u4e0a\u5750\u8d77\uff0c\u8212\u5c55\u53cc\u81c2\u5927\u5927\u5730\u4f38\u4e86\u4e2a\u61d2\u8170\uff0c\u201c\u554a\uff0c\u597d\u7d2f\u3002\u201d\u5979\u610f\u72b9\u672a\u5c3d\u5730\u8bf4\uff0c\u4f3c\u4e4e\u8fd8\u60f3\u5012\u4e0b\u53bb\u518d\u7761\u4e00\u4f1a\uff0c\u4e00\u773c\u770b\u89c1\u6885\u83f2\u65af\u6b63\u5750\u5728\u5e8a\u5934\u6c89\u601d\uff0c\u5979\u4e0a\u534a\u8eab\u8d64\u88f8\u7740\uff0c\u6ca1\u76d6\u6bef\u5b50\uff0c\u957f\u957f\u7684\u91d1\u53d1\u6563\u5728\u6d01\u767d\u808c\u80a4\u4e0a\uff0c\u5728\u9ed1\u6697\u7684\u623f\u95f4\u91cc\u6cdb\u7740\u71a0\u71a0\u5fae\u5149\uff0c\u4eff\u4f5b\u4e00\u5177\u5b8c\u7f8e\u65e0\u6687\u7684\u5973\u795e\u5851\u50cf\u3002</p><p>\u201c\u4f60\u9192\u4e86\u554a\uff0c\u827e\u5f25\u8587\uff0c\u201d\u51db\u6253\u4e86\u4e2a\u5475\u6b20\uff0c\u201c\u8fd8\u65e9\u5427\uff0c\u600e\u4e48\u4e0d\u518d\u7761\u4f1a\u3002\u201d</p><p>\u201c\u7761\u4e0d\u7740\uff0c\u201d\u6885\u83f2\u65af\u671d\u5979\u5fae\u5fae\u7b11\u7b11\uff0c\u4f38\u624b\u8f7b\u62db\uff0c\u201c\u8fc7\u6765\uff0c\u51db\uff0c\u966a\u966a\u6211\u3002\u201d</p><p>\u51db\u4ece\u743c\u6069\u8eab\u4e0a\u722c\u8fc7\uff0c\u6eda\u843d\u5230\u53f3\u4fa7\uff0c\u4e5f\u5750\u8d77\u8eab\u6765\u3002\u5979\u7684\u80f8\u90e8\u7855\u5927\u67d4\u8f6f\uff0c\u4fef\u4e0b\u8eab\u7684\u65f6\u5019\u66f4\u52a0\u60ca\u4eba\uff0c\u5728\u743c\u6069\u8138\u4e0a\u76f4\u63a5\u8e6d\u8fc7\u53bb\uff0c\u8ba9\u4ed6\u90fd\u6709\u79cd\u51b2\u52a8\u60f3\u5f20\u5634\u542b\u4f4f\uff0c\u603b\u7b97\u662f\u5fcd\u4f4f\u4e86\u3002\u743c\u6069\u5c06\u773c\u775b\u772f\u5f00\u4e00\u6761\u7f1d\uff0c\u6084\u6084\u671b\u53bb\uff0c\u53ea\u89c1\u4e24\u4f4d\u8d64\u88f8\u7684\u5c11\u5973\u80a9\u5e76\u80a9\u5750\u5728\u4e00\u8d77\uff0c\u540c\u6837\u7cbe\u81f4\u7edd\u7f8e\u7684\u5bb9\u989c\uff0c\u6de1\u91d1\u957f\u53d1\u548c\u4e4c\u9ed1\u957f\u53d1\u6563\u6563\u6d12\u5728\u80a9\u80cc\uff0c\u6620\u886c\u5f97\u808c\u80a4\u683c\u5916\u5149\u6d01\u83b9\u6da6\uff0c\u767d\u7389\u65e0\u7455\uff0c\u5979\u4eec\u7684\u773c\u775b\u90fd\u662f\u78a7\u7eff\u8272\u7684\uff0c\u6e05\u6f88\u900f\u660e\uff0c\u4f46\u6885\u83f2\u65af\u8f83\u4e3a\u6df1\u4e9b\uff0c\u4eff\u4f5b\u5e7d\u9759\u6e56\u6c34\uff0c\u6e29\u96c5\u6c89\u90c1\uff0c\u51db\u8f83\u4e3a\u6d45\u4e9b\uff0c\u7565\u5e26\u4e9b\u6e56\u84dd\u8272\uff0c\u77b3\u5b54\u4e2d\u9690\u9690\u6709\u5fae\u5fae\u7684\u8df3\u8dc3\u706b\u5149\u3002</p><p>\u4e24\u4eba\u5e76\u80a9\u5750\u7740\uff0c\u4e00\u65f6\u90fd\u6ca1\u8bf4\u8bdd\uff0c\u8fc7\u4e86\u534a\u5929\uff0c\u6885\u83f2\u65af\u624d\u8f7b\u8f7b\u5f00\u53e3\u3002</p><p>\u201c\u51db\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d</p><p>\u201c\u5e72\u561b\u8981\u548c\u6211\u62a2\u5462\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u4f60\u53c8\u4e0d\u771f\u7684\u559c\u6b22\u4ed6\u3002\u201d</p><p>\u201c\u6211\u4e5f\u4e0d\u8ba8\u538c\u4ed6\u554a\uff0c\u8981\u8bf4\u559c\u6b22\u4e5f\u52c9\u5f3a\u80fd\u7b97\u3002\u201d</p><p>\u201c\u90a3\u662f\u4e24\u4e2a\u6982\u5ff5\uff0c\u201d\u6885\u83f2\u65af\u8f7b\u58f0\u8bf4\uff0c\u201c\u6211\u8bf4\u7684\u559c\u6b22\uff0c\u548c\u4f60\u8bf4\u7684\u559c\u6b22\uff0c\u4e0d\u662f\u4e00\u56de\u4e8b\uff0c\u4f60\u5e94\u8be5\u660e\u767d\u7684\u3002\u5982\u679c\u4f60\u771f\u559c\u6b22\u4ed6\uff0c\u90a3\u6211\u4e5f\u4e0d\u4f1a\u8bf4\u4ec0\u4e48\uff0c\u6211\u8ba9\u4f60\u5c31\u662f\u3002\u6211\u4eec\u662f\u4ece\u5c0f\u4e00\u8d77\u957f\u5927\uff0c\u591a\u5c11\u6b21\u751f\u6b7b\u60a3\u96be\u5171\u540c\u7ecf\u5386\u8fc7\u6765\u7684\u670b\u53cb\uff0c\u6211\u628a\u4f60\u5f53\u4eb2\u59d0\u59b9\u4e00\u6837\u770b\u5f85\u3002\u65e0\u8bba\u4ec0\u4e48\uff0c\u53ea\u8981\u662f\u6211\u6709\u7684\uff0c\u5982\u679c\u4f60\u771f\u60f3\u8981\uff0c\u6211\u7ed9\u4f60\u4e5f\u65e0\u59a8\uff0c\u4f46\u4f60\u660e\u660e\u6ca1\u90a3\u4e48\u559c\u6b22\uff0c\u4f55\u5fc5\u53c8\u975e\u8981\u6765\u548c\u6211\u62a2\u5462\u3002\u201d</p><p>\u201c\u6211\u4e5f\u6ca1\u60f3\u548c\u4f60\u62a2\u561b\uff0c\u201d\u51db\u5fae\u5fae\u5782\u4e0b\u773c\u8138\uff0c\u201c\u4f60\u8fd8\u662f\u4ed6\u60c5\u4eba\uff0c\u4ed6\u8fd8\u662f\u4f60\u7684\uff0c\u6211\u53c8\u6ca1\u593a\u8d70\u3002\u6211\u53ea\u662f\u559c\u6b22\u8fd9\u79cd\u2026\u2026\u559c\u6b22\u548c\u4f60\uff0c\u548c\u4ed6\u505a\u8fd9\u79cd\u4e8b\u60c5\u7684\u611f\u89c9\u561b\u3002\u4f60\u5c31\u5f53\u662f\u4f60\u6709\u4e2a\u559c\u6b22\u7684\u4e1c\u897f\uff0c\u5076\u5c14\u548c\u6211\u5206\u4eab\u4e00\u4e0b\u800c\u5df2\uff0c\u4e1c\u897f\u4e0d\u8fd8\u662f\u4f60\u7684\u3002\u201d</p><p>\u201c\u4f60\u8981\u559c\u6b22\u90a3\u6837\u4e1c\u897f\uff0c\u90a3\u6211\u76f4\u63a5\u8ba9\u7ed9\u4f60\uff0c\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u201c\u4e0d\u597d\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u56e0\u4e3a\u662f\u4f60\u7684\u4e1c\u897f\uff0c\u56e0\u4e3a\u5728\u4f60\u624b\u91cc\uff0c\u6240\u4ee5\u6211\u624d\u559c\u6b22\u5462\u3002\u56e0\u4e3a\u4ed6\u662f\u4f60\u7684\u60c5\u4eba\uff0c\u6240\u4ee5\u6211\u624d\u613f\u610f\u5462\uff0c\u4e0d\u7136\u6211\u624d\u4e0d\u5e72\u3002\u201d</p><p>\u201c\u90a3\u4f60\u7684\u610f\u601d\u4e0d\u5c31\u662f\u8bf4\u8981\u548c\u6211\u62a2\uff1f\u201d\u6885\u83f2\u65af\u4e5f\u7565\u7565\u6709\u4e9b\u4e0d\u5feb\uff0c\u201c\u4e0d\u662f\u6211\u7684\uff0c\u4f60\u5c31\u4e0d\u8981\uff0c\u5c31\u56e0\u4e3a\u662f\u6211\u7684\uff0c\u6240\u4ee5\u4f60\u5c31\u8981\u6765\u63d2\u624b\u3002\u6211\u8ba9\u7ed9\u4f60\uff0c\u4f60\u53c8\u8bf4\u4e0d\u597d\uff0c\u90a3\u4f60\u5230\u5e95\u60f3\u5e72\u4ec0\u4e48\u5462\u3002\u201d</p><p>\u201c\u6211\u771f\u7684\u6ca1\u548c\u4f60\u62a2\u7684\u610f\u601d\uff0c\u827e\u5f25\u8587\uff0c\u201d\u51db\u6709\u4e9b\u59d4\u5c48\u5730\u8bf4\uff0c\u201c\u6211\u4e5f\u8bf4\u4e0d\u6e05\u695a\u5566\uff0c\u53cd\u6b63\u6211\u662f\u771f\u6ca1\u60f3\u548c\u4f60\u62a2\uff0c\u4ed6\u662f\u4f60\u7684\u60c5\u4eba\uff0c\u90a3\u5c31\u662f\u4f60\u7684\uff0c\u6211\u4ece\u6765\u5c31\u6ca1\u60f3\u8fc7\u8981\u593a\u8d70\u3002\u4f46\u662f\u6211\u770b\u5230\u4f60\u4eec\u5728\u4e00\u8d77\uff0c\u6211\u5c31\u60f3\u53c2\u52a0\u8fdb\u6765\u561b\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u89c9\u5f97\u4f60\u8fd9\u79cd\u60f3\u6cd5\u5f88\u5947\u602a\u4e48\u3002\u201d</p><p>\u201c\u597d\u50cf\u662f\u6709\u70b9\u5947\u602a\u554a\uff0c\u201d\u51db\u601d\u8003\u4e86\u4e00\u4f1a\uff0c\u201c\u4f46\u6211\u4eec\u4e5f\u4ece\u6765\u90fd\u4e0d\u662f\u6b63\u5e38\u4eba\u554a\u3002\u201d</p><p>\u6885\u83f2\u65af\u4f3c\u4e4e\u6709\u70b9\u751f\u6c14\uff0c\u9759\u9759\u9760\u7740\u5e8a\u5934\u9ed8\u5750\u7740\uff0c\u4e0d\u518d\u8bf4\u8bdd\u3002\u51db\u4e5f\u6ca1\u8bf4\u8bdd\uff0c\u743c\u6069\u7ee7\u7eed\u88c5\u7761\u3002\u623f\u95f4\u91cc\u6062\u590d\u4e86\u9759\u6084\u6084\u7684\uff0c\u53ea\u6709\u8f7b\u5fae\u7684\u547c\u5438\u58f0\uff0c\u8fc7\u4e86\u4e00\u4f1a\uff0c\u51db\u5148\u5f00\u53e3\u4e86\u3002</p><p>\u201c\u6211\u89c9\u5f97\uff0c\u827e\u5f25\u8587\uff0c\u201d\u5979\u6709\u4e9b\u72b9\u8c6b\u5730\u8bf4\uff0c\u201c\u6211\u5176\u5b9e\u53ef\u80fd\u662f\u559c\u6b22\u4f60\u5427\u3002\u201d</p><p>\u201c\u6211\u4e5f\u559c\u6b22\u4f60\u554a\u3002\u201d</p><p>\u201c\u4e0d\u662f\u90a3\u79cd\u559c\u6b22\uff0c\u4f60\u77e5\u9053\u6211\u7684\u610f\u601d\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u4f60\u8bf4\u5f97\u5bf9\uff0c\u6211\u662f\u6765\u62a2\u4f60\u7684\uff0c\u4f46\u6211\u5176\u5b9e\u53c8\u4e0d\u662f\u771f\u7684\u591a\u4e48\u559c\u6b22\u2026\u2026\u5176\u5b9e\u6211\u559c\u6b22\u7684\uff0c\u662f\u80fd\u5728\u4f60\u8eab\u8fb9\u5427\u3002\u6211\u559c\u6b22\u6211\u4eec\u5c0f\u65f6\u5019\uff0c\u751a\u81f3\u559c\u6b22\u6211\u4eec\u90a3\u4e00\u5e74\u591a\u56db\u5904\u6d41\u6d6a\uff0c\u9003\u907f\u8ffd\u6740\u7684\u65e5\u5b50\uff0c\u867d\u7136\u662f\u5f88\u8f9b\u82e6\uff0c\u5f88\u5371\u9669\uff0c\u4f46\u6211\u6bcf\u6b21\u56de\u60f3\u8d77\u6765\u5c31\u771f\u7684\u5f88\u559c\u6b22\u554a\uff0c\u90a3\u65f6\u5019\u6211\u4eec\u5929\u5929\u5728\u4e00\u8d77\uff0c\u4f60\u603b\u5728\u6211\u8eab\u8fb9\uff0c\u7761\u89c9\u966a\u7740\u6211\uff0c\u5e2e\u6211\u8d76\u90a3\u4e9b\u72d0\u72f8\uff0c\u6211\u4eec\u4e00\u8d77\u8d70\u8def\uff0c\u4e00\u8d77\u4f11\u606f\uff0c\u4e00\u8d77\u4e70\u4e1c\u897f\uff0c\u8fd8\u4e00\u8d77\u6740\u4eba\u3002\u6211\u2026\u2026\u6211\u771f\u7684\u5f88\u60f3\u4f60\u6c38\u8fdc\u90fd\u5728\u6211\u8eab\u8fb9\u7684\u5462\u3002\u201d</p><p>\u6885\u83f2\u65af\u6c89\u9ed8\u4e86\u4e00\u4f1a\uff0c\u5fae\u5fae\u53f9\u6c14\uff0c\u7275\u7740\u5979\u7684\u624b\uff0c\u63e1\u5728\u638c\u5fc3\u3002</p><p>\u201c\u6211\u4e5f\u5f88\u60f3\u554a\uff0c\u6211\u4e5f\u559c\u6b22\u4f60\u80fd\u4e00\u76f4\u5728\u6211\u8eab\u8fb9\uff0c\u6211\u4eec\u662f\u597d\u670b\u53cb\uff0c\u662f\u597d\u59d0\u59b9\uff0c\u6211\u4eec\u66fe\u7ecf\u53d1\u8fc7\u8a93\u7684\uff0c\u6c38\u8fdc\u90fd\u5728\u4e00\u8d77\uff0c\u6c38\u8fdc\u90fd\u4e0d\u5206\u5f00\u2026\u2026\u53ef\u662f\u2026\u2026\u201d</p><p>\u201c\u53ef\u662f\u4f60\u5148\u8fdd\u80cc\u6211\u4eec\u7684\u7ea6\u5b9a\u4e86\u554a\uff0c\u201d\u51db\u54ac\u7740\u5634\u5507\uff0c\u201c\u6211\u4eec\u8bf4\u597d\u76f8\u4e92\u6276\u6301\uff0c\u76f8\u4e92\u501a\u9760\uff0c\u6c38\u8fdc\u90fd\u5728\u4e00\u8d77\u7684\u3002\u53ef\u662f\u4f60\u540e\u6765\u88ab\u90a3\u4e2a\u8001\u5934\u5b50\u5e26\u53bb\u4e86\u8ff7\u65af\u5353\u8bfa\uff0c\u5f53\u4e86\u4ec0\u4e48\u5723\u6b66\u58eb\uff0c\u4e22\u4e0b\u6211\u4e00\u4e2a\u4eba\uff0c\u8ba9\u6211\u5728\u7edd\u5883\u4e1c\u57df\u5b64\u96f6\u96f6\u5730\u6d41\u6d6a\u2026\u2026\u662f\u4f60\u5148\u4e22\u4e0b\u6211\u7684\u554a\uff01\u201d</p><p>\u201c\u4f60\u77e5\u9053\u6211\u7684\u60c5\u5f62\uff0c\u51db\uff0c\u201d\u6885\u83f2\u65af\u4f4e\u58f0\u89e3\u91ca\uff0c\u201c\u6211\u5f53\u65f6\u8eab\u4f53\u91cc\u7684\u6740\u622e\u795e\u529b\u5df2\u7ecf\u5230\u4e86\u6781\u9650\uff0c\u5982\u679c\u4e0d\u53bb\u8ff7\u65af\u5353\u8bfa\uff0c\u6211\u5c31\u2026\u2026\u6211\u4e5f\u9080\u8bf7\u4f60\u4e00\u8d77\u53bb\u554a\uff0c\u5927\u4e3b\u6559\u4e5f\u7b54\u5e94\u4e86\uff0c\u53ef\u662f\u4f60\u4e0d\u613f\u610f\u53bb\u3002\u201d</p><p>\u201c\u6211\u5f53\u7136\u4e0d\u613f\u610f\u53bb\uff0c\u6211\u5e72\u561b\u8981\u548c\u4e00\u7fa4\u7b28\u86cb\u52a0\u767d\u75f4\u6253\u4ea4\u9053\u554a\uff0c\u6211\u770b\u7740\u4ed6\u4eec\u5c31\u6709\u6c14\uff0c\u201d\u51db\u6485\u7740\u5634\uff0c\u6c14\u547c\u547c\u5730\uff0c\u201c\u800c\u4e14\u4f60\u8fd8\u662f\u8981\u53bb\u5f53\u5723\u6b66\u58eb\uff0c\u4f60\u53c8\u4e0d\u662f\u4e0d\u77e5\u9053\u6211\u7236\u4eb2\u600e\u4e48\u6b7b\u7684\uff0c\u4f60\u53c8\u4e0d\u662f\u4e0d\u77e5\u9053\u6211\u6bcd\u4eb2\u600e\u4e48\u90a3\u4e48\u65e9\u5c31\u53bb\u4e16\u4e86\uff0c\u4f60\u660e\u660e\u77e5\u9053\u6211\u4e0d\u559c\u6b22\u7684\uff0c\u4f60\u8fd8\u975e\u8981\u53bb\uff0c\u4f60\u5c31\u662f\u6545\u610f\u7684\u2026\u2026\u201d</p><p>\u6885\u83f2\u65af\u53f9\u53e3\u6c14\uff0c\u628a\u5979\u63fd\u5230\u81ea\u5df1\u6000\u91cc\uff0c\u201c\u662f\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u662f\u6211\u4e0d\u5bf9\uff0c\u662f\u6211\u5148\u8fdd\u80cc\u4e86\u6211\u4eec\u7684\u8a93\u7ea6\uff0c\u662f\u6211\u4e0d\u8be5\u53bb\u505a\u5723\u6b66\u58eb\u2026\u2026\u53ef\u662f\u6211\u4e5f\u6ca1\u529e\u6cd5\u554a\uff0c\u6709\u4e9b\u4e8b\u60c5\uff0c\u6211\u4e5f\u8eab\u4e0d\u7531\u5df1\u2026\u2026\u201d</p><p>\u51db\u4f0f\u5728\u5979\u7684\u817f\u4e0a\uff0c\u957f\u957f\u7684\u9ed1\u53d1\u50cf\u7ef8\u7f0e\u4e00\u6837\u5782\u4e0b\u6765\uff0c\u7f8e\u4e3d\u5f97\u8ba9\u4eba\u5fc3\u75db\uff0c\u201c\u6211\u77e5\u9053\uff0c\u827e\u5f25\u8587\uff0c\u8fd9\u4e9b\u9053\u7406\u6211\u4e5f\u660e\u767d\u3002\u4eba\u751f\u5728\u4e16\uff0c\u603b\u6709\u4e9b\u4e8b\u60c5\u8eab\u4e0d\u7531\u5df1\uff0c\u4f60\u53bb\u505a\u4e86\u5723\u6b66\u58eb\uff0c\u90a3\u5c31\u5723\u6b66\u58eb\u5427\uff0c\u6211\u662f\u4e0d\u559c\u6b22\u5723\u6b66\u58eb\uff0c\u4f46\u6211\u8ba4\u4f60\u662f\u6211\u7684\u670b\u53cb\u3002\u540e\u6765\u6211\u4eec\u5728\u585e\u5c14\u53c8\u9047\u5230\u4e86\uff0c\u6211\u4e0d\u4e5f\u518d\u6ca1\u63d0\u8fc7\u534a\u53e5\uff0c\u518d\u6ca1\u8bf4\u8fc7\u534a\u70b9\u4e86\u3002\u8fc7\u53bb\u7684\u4e8b\u60c5\u5c31\u8fc7\u53bb\uff0c\u4e5f\u4e0d\u7528\u8bf4\u4ec0\u4e48\u5bf9\u9519\uff0c\u6211\u53ea\u662f\u2026\u2026\u6211\u53ea\u662f\u771f\u7684\u5f88\u60f3\u5f88\u60f3\u548c\u4f60\u6c38\u8fdc\u90fd\u5728\u4e00\u8d77\u554a\u3002\u201d</p><p>\u201c\u53ef\u662f\u6211\u4eec\u7ec8\u7a76\u4f1a\u957f\u5927\u7684\u5440\uff0c\u201d\u6885\u83f2\u65af\u629a\u6478\u7740\u5979\u7684\u79c0\u53d1\uff0c\u201c\u6211\u4eec\u7ec8\u7a76\u90fd\u4f1a\u957f\u5927\uff0c\u800c\u4e14\u6211\u4eec\u73b0\u5728\u4e5f\u5df2\u7ecf\u4e0d\u662f\u5c0f\u5b69\u5b50\u4e86\u3002\u6211\u4eec\u603b\u6709\u5404\u81ea\u8981\u505a\u7684\u4e8b\u60c5\uff0c\u603b\u6709\u5404\u81ea\u8981\u627f\u62c5\u7684\u8d23\u4efb\uff0c\u603b\u6709\u5404\u81ea\u7684\u751f\u6d3b\uff0c\u4e5f\u603b\u4f1a\u5404\u81ea\u7231\u4e0a\u4e0d\u540c\u7684\u7537\u4eba\u2026\u2026\u201d</p><p>\u201c\u6211\u4e0d\u7ba1\uff0c\u201d\u51db\u575a\u51b3\u5730\u8bf4\uff0c\u201c\u6211\u53ea\u559c\u6b22\u4f60\uff0c\u53cd\u6b63\u6211\u4e00\u5b9a\u8981\u548c\u4f60\u5728\u4e00\u8d77\u3002\u201d</p><p>\u201c\u51db\u2026\u2026\u201d</p><p>\u201c\u6211\u4e0d\u8981\u6c42\u4f60\u5bf9\u6211\u548c\u6211\u5bf9\u4f60\u4e00\u6837\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u4f60\u53ef\u4ee5\u505a\u4f60\u7684\u4e8b\u60c5\uff0c\u627f\u62c5\u4f60\u7684\u8d23\u4efb\uff0c\u6709\u4f60\u7684\u751f\u6d3b\uff0c\u7231\u4e0a\u4f60\u7684\u60c5\u4eba\uff0c\u8fd9\u4e9b\u6211\u90fd\u4e0d\u7ba1\uff0c\u4f46\u81f3\u5c11\u4f60\u4e0d\u80fd\u518d\u50cf\u4ee5\u524d\u4e00\u6837\u4e22\u4e0b\u6211\uff0c\u81ea\u5df1\u53bb\u8d70\u81ea\u5df1\u7684\u9633\u5149\u5927\u9053\uff0c\u4e0a\u81ea\u5df1\u7684\u5929\u5802\uff0c\u8ba9\u6211\u4e00\u4e2a\u4eba\u5728\u5730\u4e0a\u5b64\u96f6\u96f6\u5730\u6d41\u6d6a\u3002\u201d</p><p>\u201c\u51db\uff0c\u4f60\u522b\u8fd9\u4e48\u8bf4\u3002\u201d</p><p>\u201c\u4f46\u6211\u5c31\u662f\u60f3\u8fd9\u4e48\u8bf4\uff0c\u201d\u51db\u5014\u5f3a\uff0c\u201c\u6211\u5c31\u662f\u8fd9\u4e2a\u610f\u601d\u3002\u4f60\u6709\u60c5\u4eba\u6211\u4e0d\u7ba1\uff0c\u6211\u4e5f\u4e0d\u4f1a\u8ddf\u4f60\u62a2\uff0c\u4f46\u4f60\u4e5f\u4e0d\u80fd\u629b\u4e0b\u6211\u4e0d\u7ba1\uff0c\u81ea\u987e\u81ea\u5730\u8fc7\u751c\u871c\u751f\u6d3b\u3002\u6211\u4eec\u6709\u8fc7\u7ea6\u5b9a\uff0c\u53d1\u8fc7\u8a93\u7684\uff0c\u6211\u4eec\u6c38\u8fdc\u90fd\u4e0d\u5206\u5f00\u3002\u201d</p><p>\u201c\u90a3\u65f6\u5019\u6211\u4eec\u8fd8\u662f\u5c0f\u5b69\u5b50\uff0c\u5b69\u5b50\u7684\u8bdd\u2026\u2026\u201d</p><p>\u201c\u96be\u9053\u56e0\u4e3a\u5f53\u65f6\u662f\u5c0f\u5b69\u5b50\uff0c\u8bf4\u8fc7\u7684\u8bdd\u5c31\u80fd\u4e0d\u7b97\u6570\u4e86\uff1f\u201d\u51db\u53cd\u95ee\uff0c\u201c\u96be\u9053\u6211\u4eec\u5f53\u65f6\u8a93\u7ea6\u7684\u65f6\u5019\u4e0d\u662f\u771f\u5fc3\u8bda\u610f\u7684\uff1f\u4f60\u662f\u5723\u6b66\u58eb\uff0c\u4f60\u81ea\u5df1\u66fe\u7ecf\u8bf4\u8fc7\u7684\u8bdd\uff0c\u53d1\u8fc7\u7684\u8a93\uff0c\u771f\u5fc3\u8bda\u610f\u5bf9\u6211\u7684\u8bb8\u8bfa\uff0c\u5c31\u4e00\u53e5\u2018\u90a3\u65f6\u5019\u6211\u4eec\u8fd8\u662f\u5c0f\u5b69\u5b50\u2019\uff0c\u5c31\u53ef\u4ee5\u90fd\u4e0d\u7b97\u4e86\uff1f\u201d</p><p>\u201c\u51db\uff0c\u4f60\u5f97\u8bb2\u70b9\u9053\u7406\u3002\u201d</p><p>\u201c\u6211\u5f88\u8bb2\u9053\u7406\uff0c\u827e\u5f25\u8587\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u6240\u4ee5\u6211\u5df2\u7ecf\u8bf4\u4e86\uff0c\u4f60\u53ef\u4ee5\u6709\u4f60\u7684\u751f\u6d3b\uff0c\u8d70\u4f60\u81ea\u5df1\u7684\u8def\uff0c\u6709\u4f60\u7684\u60c5\u4eba\uff0c\u505a\u4f60\u8be5\u505a\u7684\u4e8b\u60c5\uff0c\u8fd9\u4e9b\u6211\u90fd\u65e0\u6240\u8c13\u3002\u6211\u6240\u8981\u7684\uff0c\u53ea\u662f\u80fd\u8ba9\u6211\u53c2\u4e0e\u4e00\u4efd\u8fdb\u6765\uff0c\u4ec5\u6b64\u800c\u5df2\u3002\u201d</p><p>\u201c\u6211\u8981\u6c38\u8fdc\u548c\u4f60\u5728\u4e00\u8d77\u3002\u201d\u5979\u62ac\u8d77\u5934\uff0c\u51dd\u89c6\u7740\u81ea\u5df1\u7684\u7ae5\u5e74\u4f19\u4f34\uff0c\u65a9\u9489\u622a\u94c1\u5730\u8bf4\u3002</p></article>"}}}]);
