(window.webpackJsonp=window.webpackJsonp||[]).push([[326],{348:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u4e8c\u8282 \u53d8\u6001",content:"<article><h2>\u7b2c\u56db\u5341\u4e8c\u8282 \u53d8\u6001</h2><p>\u610f\u5916\u542c\u5230\u8fd9\u79cd\u5bf9\u8bdd\uff0c\u743c\u6069\u5b9e\u5728\u6709\u4e9b\u65e0\u8bed\uff0c\u751a\u81f3\u90fd\u4e0d\u77e5\u9053\u81ea\u5df1\u7684\u7acb\u573a\u5e94\u8be5\u5f80\u54ea\u91cc\u6446\uff0c\u6216\u8005\u8bf4\uff0c\u611f\u89c9\u81ea\u5df1\u4eff\u4f5b\u5c31\u662f\u591a\u4f59\u4f3c\u7684\u3002\u5982\u679c\u8fd9\u4e16\u754c\u4e0a\u4e0d\u5b58\u5728\u743c\u6069\xb7\u5170\u5c3c\u65af\u7279\u8fd9\u4e2a\u4eba\uff0c\u90a3\u4e48\u65c1\u8fb9\u8fd9\u4e24\u4f4d\u5c11\u5973\u6216\u8bb8\u5c31\u4ec0\u4e48\u70e6\u607c\u90fd\u6ca1\u6709\uff0c\u4ece\u6b64\u5e78\u798f\u5feb\u4e50\u5730\u751f\u6d3b\u5728\u4e00\u8d77\u5427\u2026\u2026\u600e\u4e48\u542c\u8d77\u6765\u5979\u4eec\u4e24\u4e2a\u50cf\u662f\u7ae5\u8bdd\u6545\u4e8b\u91cc\u7684\u738b\u5b50\u548c\u516c\u4e3b\uff0c\u81ea\u5df1\u53cd\u800c\u662f\u6ce8\u5b9a\u8981\u9eef\u7136\u9000\u573a\u7684\u90aa\u6076\u5deb\u5e08\u6216\u8005\u5927\u9b54\u738b\u4f3c\u7684\u3002</p><p>\u4e0d\u5bf9\u4e0d\u5bf9\uff0c\u743c\u6069\u5728\u5fc3\u91cc\u6447\u5934\uff0c\u7136\u540e\u5207\u6362\u4eba\u683c\uff0c\u6362\u4e86\u4e00\u79cd\u903b\u8f91\uff1a\u9996\u5148\uff0c\u7537\u5973\u624d\u662f\u6b63\u9053\uff0c\u5973\u5973\u5206\u660e\u662f\u90aa\u9053\uff0c\u6240\u4ee5\u6211\u7684\u5b58\u5728\u663e\u7136\u662f\u6b63\u786e\u7684\uff0c\u800c\u51db\u548c\u6885\u83f2\u65af\u7684\u5173\u7cfb\u5219\u5206\u660e\u662f\u9519\u8bef\u7684\u2014\u2014\u4e3a\u4e86\u7ea0\u6b63\u8fd9\u79cd\u9519\u8bef\uff0c\u6240\u4ee5\u6211\u5fc5\u987b\u575a\u6301\u7acb\u573a\uff0c\u4e0d\u80fd\u9000\u6b65\uff0c\u800c\u4e14\u5e94\u8be5\u5bf9\u51db\u7684\u9519\u8bef\u6027\u53d6\u5411\u8fdb\u884c\u6b63\u786e\u7684\u6559\u80b2\u3001\u5f15\u5bfc\u548c\u6539\u9020\uff0c\u4ee5\u4fbf\u8fbe\u5230\u6700\u7ec8\u7684\u548c\u8c10\u793e\u4f1a\u2026\u2026\u4e0d\uff0c\u662f\u548c\u8c10\u540e\u5bab\u7684\u76ee\u7684\u3002\u5514\uff0c\u8fd9\u4e48\u60f3\u5c31\u5b8c\u5168\u6b63\u786e\u4e86\u3002</p><p>\u4e0d\u8fc7\u60f3\u5f52\u60f3\uff0c\u5982\u4eca\u623f\u95f4\u91cc\u6c14\u6c1b\u5fae\u5999\uff0c\u81ea\u5df1\u8fd8\u662f\u7ee7\u7eed\u88c5\u6b7b\u6bd4\u8f83\u597d\uff0c\u5148\u4e0d\u8981\u5377\u8fdb\u53bb\uff0c\u9759\u89c2\u5176\u53d8\u518d\u8bf4\u3002\u53cd\u6b63\u4ee5\u6885\u83f2\u65af\u7684\u4e86\u89e3\uff0c\u5979\u81f3\u5c11\u4e0d\u662f\u51db\u8fd9\u6837\u7684\u767e\u5408\uff0c\u90a3\u4e48\u81f3\u5c11\u4e0d\u6015\u5979\u4eec\u4e24\u4eba\u8fbe\u6210\u534f\u8bae\uff0c\u628a\u81ea\u5df1\u8fdc\u8fdc\u8e22\u98de\u3002</p><p>\u6885\u83f2\u65af\u4f3c\u4e4e\u4e5f\u88ab\u51db\u7684\u8bdd\u7ed9\u9707\u4f4f\u4e86\uff0c\u6216\u8005\u8bf4\u5979\u6ca1\u60f3\u5230\u5bf9\u65b9\u4f1a\u5728\u8fd9\u79cd\u65f6\u5019\u4ee5\u8fd9\u79cd\u65b9\u5f0f\u5bf9\u81ea\u5df1\u8bf4\u51fa\u6765\uff0c\u5c31\u7b97\u662f\u573a\u5408\u4e5f\u4e0d\u5bf9\u5427\u3002\u5c31\u5728\u51e0\u5c0f\u65f6\u524d\uff0c\u5728\u8fd9\u95f4\u5367\u5ba4\u91cc\uff0c\u5728\u8fd9\u5f20\u5e8a\u4e0a\uff0c\u5979\u4eec\u624d\u521a\u521a\u8fdb\u884c\u4e86\u4e00\u573a\u6fc0\u70c8\u7684\u6027\u7231\uff0c\u7a7a\u6c14\u4e2d\u8fd8\u5f25\u6f2b\u7740\u6deb\u7cdc\u7684\u6c14\u606f\uff0c\u51db\u7684\u4f53\u5185\u6b64\u65f6\u8fd8\u6b8b\u7559\u7740\u743c\u6069\u7684\u4e73\u767d\u8272\u7cbe\u534e\uff0c\u5979\u7684\u5507\u74e3\u4e0a\u8fd8\u6709\u6885\u83f2\u65af\u7565\u5e26\u9178\u751c\u7684\u5473\u9053\uff0c\u7136\u540e\u73b0\u5728\u5979\u8d64\u88f8\u7740\u5730\u5750\u5728\u8fd9\u91cc\uff0c\u51dd\u89c6\u7740\u6885\u83f2\u65af\uff0c\u4e25\u8083\u5730\u3001\u8ba4\u771f\u5730\u3001\u65a9\u9489\u622a\u94c1\u5730\u8bf4\uff1a\u201c\u6211\u8981\u6c38\u8fdc\u548c\u4f60\u5728\u4e00\u8d77\u201d\u3002</p><p>\u8fd9\u573a\u9762\u600e\u4e48\u60f3\u600e\u4e48\u89c9\u5f97\u8352\u8bde\u2026\u2026</p><p>\u5f53\u7136\uff0c\u5b58\u5728\u7684\u5c31\u662f\u5408\u7406\u7684\uff0c\u73b0\u5b9e\u7684\u5c31\u662f\u6b63\u786e\u7684\uff0c\u800c\u4e14\u6b63\u5982\u51db\u8bf4\u7684\uff1a\u6211\u4eec\u90fd\u4e0d\u662f\u6b63\u5e38\u4eba\u2014\u2014\u5176\u5b9e\u8fd9\u91cc\u5c31\u6ca1\u6709\u6b63\u5e38\u4eba\uff0c\u4e25\u683c\u6765\u8bf4\u5168\u90fd\u662f\u5fc3\u7406\u53d8\u6001\u3002\u6240\u4ee5\u4e94\u79d2\u949f\u4e4b\u540e\uff0c\u6885\u83f2\u65af\u51b7\u9759\u5730\u505a\u51fa\u4e86\u56de\u7b54\u3002</p><p>\u201c\u597d\u3002\u201d</p><p>\u201c\u4e00\u8a00\u4e3a\u5b9a\uff1f\u201d</p><p>\u201c\u4e00\u8a00\u4e3a\u5b9a\u3002\u201d</p><p>\u51db\u683c\u683c\u7b11\u4e86\u8d77\u6765\uff0c\u4f38\u624b\u62cd\u4e86\u62cd\u65c1\u8fb9\u7684\u743c\u6069\uff0c\u201c\u8d77\u6765\u5566\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u522b\u7761\u4e86\u3002\u201d</p><p>\u201c\u8ba9\u4ed6\u518d\u7761\u4f1a\u3002\u201d</p><p>\u201c\u5fc3\u75bc\u60c5\u4eba\u4e86\uff1f\u201d\u51db\u7b11\u7740\uff0c\u201c\u653e\u5fc3\u597d\u4e86\uff0c\u4ed6\u65e9\u5c31\u5df2\u7ecf\u9192\u4e86\u3002\u201d</p><p>\u743c\u6069\u53ea\u597d\u7ed3\u675f\u88c5\u7761\u7684\u72b6\u6001\uff0c\u201c\u4f60\u600e\u4e48\u77e5\u9053\uff1f\u201d</p><p>\u201c\u7761\u7740\u7684\u4eba\u548c\u6e05\u9192\u7684\u4eba\uff0c\u547c\u5438\u662f\u4e0d\u540c\u7684\uff0c\u4e00\u542c\u5c31\u77e5\u9053\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u4f60\u4f2a\u88c5\u5f97\u592a\u4e0d\u591f\u4e13\u4e1a\u4e86\uff0c\u4e0b\u6b21\u6211\u6765\u6559\u4f60\u3002\u827e\u5f25\u8587\u662f\u72b6\u6001\u4e0d\u597d\uff0c\u5426\u5219\u4e5f\u65e9\u5c31\u53d1\u73b0\u4e86\u3002\u201d</p><p>\u201c\u96be\u9053\u4f60\u8fd8\u4e13\u95e8\u8bad\u7ec3\u8fc7\u5982\u4f55\u88c5\u7761\uff1f\u201d</p><p>\u201c\u4ee5\u524d\u6211\u6bcd\u4eb2\u7763\u4fc3\u6211\u4eec\u7ec3\u5251\u672f\uff0c\u5979\u5c31\u6bcf\u6b21\u90fd\u88c5\u7761\u4e0d\u53bb\uff0c\u201d\u6885\u83f2\u65af\u66ff\u51db\u89e3\u91ca\uff0c\u201c\u4e00\u5f00\u59cb\u603b\u88ab\u6211\u6bcd\u4eb2\u53d1\u73b0\uff0c\u540e\u6765\u5979\u5c31\u62ff\u6211\u5f53\u5b9e\u9a8c\u54c1\uff0c\u7814\u7a76\u771f\u7761\u7740\u548c\u88c5\u7761\u7684\u533a\u522b\uff0c\u6700\u540e\u5979\u81ea\u5df1\u7684\u88c5\u7761\u6280\u672f\u51fa\u795e\u5165\u5316\uff0c\u8fde\u6211\u6bcd\u4eb2\u90fd\u5206\u4e0d\u51fa\u4e86\u3002\u201d</p><p>\u201c\u4f60\u6bcd\u4eb2\u96be\u9053\u4e0d\u4f1a\u76f4\u63a5\u628a\u5979\u53eb\u9192\u4e48\uff1f\u201d</p><p>\u201c\u4e0d\u4f1a\uff0c\u963f\u59e8\u5f88\u8bb2\u4eba\u6743\u7684\uff0c\u201d\u51db\u62a2\u7740\u8bf4\uff0c\u201c\u800c\u4e14\u5979\u5176\u5b9e\u6700\u559c\u6b22\u770b\u5230\u522b\u4eba\u7761\u89c9\u4e86\uff0c\u7761\u5f97\u8d8a\u6c89\u5979\u5c31\u8d8a\u9ad8\u5174\uff0c\u7edd\u5bf9\u4e0d\u4f1a\u53bb\u5435\u9192\u3002\u201d</p><p>\u201c\u8fd9\u662f\u4e3a\u4ec0\u4e48\u2026\u2026\u5443\uff0c\u6211\u77e5\u9053\u4e86\u3002\u201d</p><p>\u4e16\u754c\u4e0a\u559c\u6b22\u522b\u4eba\u7761\u7740\uff0c\u800c\u4e14\u7761\u5f97\u8d8a\u6c89\u5c31\u8d8a\u9ad8\u5174\u7684\uff0c\u65e0\u975e\u662f\u7a83\u8d3c\u548c\u6740\u624b\uff0c\u5df4\u5c14\u6b63\u662f\u6740\u622e\u4e4b\u795e\uff0c\u6559\u4f1a\u91cc\u6700\u591a\u7684\u5c31\u662f\u6740\u624b\uff0c\u6885\u83f2\u65af\u7684\u6bcd\u4eb2\u65e2\u7136\u662f\u9ad8\u9636\u796d\u53f8\uff0c\u8fd8\u662f\u9009\u6c11\uff0c\u60f3\u5fc5\u4e5f\u64c5\u957f\u6b64\u9053\u2014\u2014\u4e5f\u5c31\u662f\u8bf4\uff0c\u8fd9\u5176\u5b9e\u662f\u804c\u4e1a\u4e60\u60ef\u3002</p><p>\u201c\u660e\u767d\u4e86\u5427\u3002\u201d</p><p>\u201c\u660e\u767d\u4e86\uff0c\u96be\u602a\u4f60\u7684\u5251\u672f\u4e0d\u884c\uff0c\u8fdc\u8fdc\u4e0d\u5982\u827e\u5f25\u8587\u3002\u201d</p><p>\u201c\u5207\uff0c\u5251\u672f\u6211\u662f\u4e0d\u5982\u5979\uff0c\u7a7a\u624b\u640f\u51fb\u53ef\u662f\u6211\u6bd4\u8f83\u5f3a\u3002\u201d</p><p>\u201c\u90a3\u81ea\u7136\uff0c\u4f60\u662f\u9f99\u5973\u2026\u2026\u8fd9\u4e48\u8bf4\u8d77\u6765\uff0c\u6709\u4ef6\u4e8b\u60c5\u6211\u5f88\u597d\u5947\u554a\uff0c\u4f2f\u6bcd\u65e2\u7136\u662f\u53eb\u4f60\u4eec\u8d77\u5e8a\u7ec3\u5251\uff0c\u90a3\u81ea\u5df1\u624b\u91cc\u4f1a\u4e0d\u4f1a\u4e5f\u63d0\u7740\u5251\u554a\u3002\u201d</p><p>\u201c\u5f53\u7136\u554a\u3002\u201d</p><p>\u201c\u90a3\u5979\u770b\u89c1\u4f60\u88c5\u7761\u88c5\u5f97\u90a3\u4e48\u50cf\uff0c\u7761\u5f97\u90a3\u4e48\u6c89\uff0c\u4f1a\u4e0d\u4f1a\u5f88\u9ad8\u5174\uff0c\u7136\u540e\u804c\u4e1a\u4e60\u60ef\u53d1\u4f5c\uff0c\u4e00\u5251\u780d\u4e0b\u6765\uff1f\u201d</p><p>\u201c\u5f53\u7136\u4f1a\u554a\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u6240\u4ee5\u827e\u5f25\u8587\u5c31\u8981\u65e9\u65e9\u8d77\u5e8a\u4e86\uff0c\u65f6\u523b\u6ce8\u610f\u963f\u59e8\u7684\u52a8\u5411\uff0c\u62e6\u7740\u5979\uff0c\u4e0d\u7136\u6211\u5c31\u5f88\u5371\u9669\u4e86\u3002\u540e\u6765\u6211\u88c5\u7761\u7684\u6280\u672f\u8d8a\u6765\u8d8a\u597d\uff0c\u6240\u4ee5\u827e\u5f25\u8587\u8d77\u5e8a\u5c31\u5fc5\u987b\u8d8a\u6765\u8d8a\u65e9\uff0c\u53cd\u5e94\u5c31\u8bad\u7ec3\u5f97\u8d8a\u6765\u8d8a\u5feb\uff0c\u5426\u5219\u4e00\u4e0d\u7559\u795e\u963f\u59e8\u5c31\u628a\u6211\u780d\u4e86\uff0c\u201d\u5979\u5410\u4e86\u5410\u820c\u5934\uff0c\u201c\u8bdd\u8bf4\u6709\u51e0\u6b21\u771f\u7684\u662f\u597d\u9669\u5462\uff0c\u5343\u94a7\u4e00\u53d1\uff0c\u5dee\u70b9\u5c31\u88ab\u780d\u5230\u4e86\u3002\u201d</p><p>\u201c\u6211\u592a\u4f69\u670d\u4f60\u4e86\uff0c\u51db\u3002\u201d</p><p>\u201c\u662f\u5417\uff0c\u4f60\u4f69\u670d\u6211\u54ea\u4e00\u70b9\uff1f\u201d</p><p>\u201c\u4f60\u5c45\u7136\u8fd9\u79cd\u60c5\u51b5\u4e0b\u90fd\u8fd8\u80fd\u7ee7\u7eed\u88c5\u7761\uff0c\u7b80\u76f4\u662f\u628a\u751f\u6b7b\u7f6e\u4e4b\u5ea6\u5916\uff0c\u8fd9\u6837\u5927\u65e0\u754f\u7684\u52c7\u6c14\u6211\u600e\u4e48\u80fd\u4e0d\u4f69\u670d\u5462\u3002\u201d</p><p>\u201c\u8fc7\u5956\u8fc7\u5956\uff0c\u5176\u5b9e\u6211\u7eaf\u7cb9\u53ea\u662f\u5077\u61d2\uff0c\u5b9e\u5728\u4e0d\u60f3\u4e00\u5927\u65e9\u8d77\u5e8a\u7ec3\u5251\u7f62\u4e86\uff0c\u4f60\u8fd9\u4e48\u8bf4\u6211\u4f1a\u5f88\u4e0d\u597d\u610f\u601d\u7684\u3002\u201d</p><p>\u201c\u6211\u4e0d\u662f\u5728\u5938\u5956\u4f60\uff0c\u6211\u662f\u5728\u8bf4\u4f60\u5fc3\u7406\u53d8\u6001\u554a\uff0c\u5c31\u4e3a\u4e86\u65e9\u4e0a\u7761\u4f1a\u61d2\u89c9\u8fde\u547d\u90fd\u6562\u4e0d\u8981\uff0c\u4f60\u5c31\u4e0d\u62c5\u5fc3\u4e07\u4e00\u827e\u5f25\u8587\u6ca1\u62e6\u4f4f\u600e\u4e48\u529e\uff1f\u201d</p><p>\u201c\u4e0d\u62c5\u5fc3\uff0c\u6211\u4fe1\u4efb\u827e\u5f25\u8587\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u800c\u4e14\u8981\u8bf4\u5fc3\u7406\u53d8\u6001\uff0c\u8fd9\u91cc\u4e09\u4e2a\u4eba\u600e\u4e48\u4e5f\u8f6e\u4e0d\u5230\u6211\u5427\u3002\u201d</p><p>\u201c\u5514\uff0c\u4f60\u8bf4\u6211\uff1f\u6211\u5fc3\u7406\u53ef\u662f\u975e\u5e38\u975e\u5e38\u7684\u6b63\u5e38\u554a\u3002\u201d</p><p>\u201c\u4f60\u4e5f\u7b97\u4e0d\u4e0a\u6b63\u5e38\uff0c\u53ea\u662f\u53d8\u6001\u7a0b\u5ea6\u8f7b\u4e9b\u7f62\u4e86\uff0c\u6211\u8bf4\u7684\u662f\u827e\u5f25\u8587\uff0c\u201d\u51db\u6307\u4e86\u6307\u65c1\u8fb9\u7684\u91d1\u53d1\u5c11\u5973\uff0c\u201c\u5979\u662f\u90aa\u795e\u7684\u5973\u513f\uff0c\u6709\u4e00\u4e2a\u8fd9\u4e48\u53d8\u6001\u7684\u6bcd\u4eb2\uff0c\u51fa\u751f\u5728\u8fd9\u6837\u53d8\u6001\u7684\u5bb6\u5ead\uff0c\u8fd8\u6709\u6211\u8fd9\u6837\u53d8\u6001\u7684\u670b\u53cb\u2014\u2014\u4f46\u5979\u5c45\u7136\u8fd8\u80fd\u8fd9\u4e48\u6b63\u5e38\uff0c\u8dd1\u53bb\u5f53\u5723\u6b66\u58eb\uff0c\u4f60\u8bf4\u5979\u662f\u4e0d\u662f\u6700\u53d8\u6001\uff1f\u201d</p><p>\u743c\u6069\u7acb\u523b\u5c31\u6ca1\u8bdd\u8bf4\u4e86\u3002</p><p>\u203b\u203b\u203b</p><p>\u901a\u5e38\u6765\u8bf4\uff0c\u4ec0\u4e48\u6837\u7684\u5bb6\u5ead\u5c31\u4f1a\u51fa\u4ec0\u4e48\u6837\u7684\u5b69\u5b50\uff0c\u4ec0\u4e48\u6837\u7684\u7236\u6bcd\u5c31\u4f1a\u6559\u80b2\u51fa\u4ec0\u4e48\u6837\u7684\u5b50\u5973\uff0c\u4eba\u603b\u662f\u4f1a\u53d7\u5230\u73af\u5883\u5f71\u54cd\u7684\u3002\u6885\u83f2\u65af\u6709\u8fd9\u79cd\u9ed1\u6697\u53d8\u6001\u7684\u7236\u6bcd\u548c\u670b\u53cb\uff0c\u81ea\u5df1\u5374\u6210\u4e3a\u6b63\u4e49\u4e4b\u795e\u7684\u5723\u6b66\u58eb\u2026\u2026\u5982\u679c\u8981\u8fd9\u4e48\u8bf4\u7684\u8bdd\uff0c\u5979\u786e\u5b9e\u662f\u6709\u591f\u53d8\u6001\u7684\u3002</p><p>\u53d1\u73b0\u8fd9\u4e00\u70b9\u5e76\u65e0\u610f\u4e49\uff0c\u56e0\u4e3a\u743c\u6069\u65e9\u5c31\u5df2\u7ecf\u4e60\u60ef\u4e86\u88ab\u4e00\u7fa4\u5fc3\u7406\u53d8\u6001\u5305\u56f4\uff0c\u5df2\u7ecf\u5dee\u4e0d\u591a\u90fd\u8981\u9ebb\u6728\u4e86\uff0c\u591a\u4e00\u4e2a\u5c11\u4e00\u4e2a\uff0c\u5bf9\u4ed6\u6765\u8bf4\u4e1d\u6beb\u6ca1\u6709\u5f71\u54cd\uff0c\u5168\u65e0\u6240\u8c13\u3002</p><p>\u6b64\u65f6\u4e09\u4e2a\u4eba\u90fd\u8fd8\u8d64\u8eab\u88f8\u4f53\u5730\u5750\u5728\u5e8a\u4e0a\uff0c\u6885\u83f2\u65af\u9760\u7740\u5e8a\u5934\uff0c\u5fae\u5fae\u7b11\u7740\u770b\u4ed6\u4eec\u6597\u5634\uff0c\u51db\u50cf\u53ea\u6e29\u987a\u7684\u6ce2\u65af\u732b\uff0c\u8db4\u5728\u6885\u83f2\u65af\u817f\u4e0a\uff0c\u5927\u534a\u4e2a\u8eab\u4f53\u90fd\u88f8\u9732\u5728\u5916\uff0c\u9732\u51fa\u4f18\u7f8e\u5706\u6ed1\u7684\u80a9\u80cc\u548c\u8170\u81c0\u66f2\u7ebf\uff0c\u743c\u6069\u80cc\u9760\u7740\u5e8a\u5185\u4fa7\u7684\u5899\u58c1\uff0c\u9762\u5bf9\u7740\u5979\u4eec\u3002\u8fd9\u79cd\u683c\u5c40\u4f3c\u4e4e\u6709\u4e9b\u602a\u5f02\uff0c\u96be\u9053\u4e00\u573a\u6b63\u5e38\u7684\u53cc\u98de\u4e4b\u540e\u4e0d\u5e94\u8be5\u662f\u7537\u6027\u5de6\u62e5\u53f3\u62b1\uff0c\u4e00\u8fb9\u4e00\u4e2a\u4e48\u3002</p><p>\u4e8e\u662f\u743c\u6069\u51b3\u5b9a\u6253\u7834\u8fd9\u79cd\u4e0d\u5408\u7406\u7684\u5c40\u9762\u3002</p><p>\u201c\u521a\u624d\u4f60\u4eec\u8bf4\u8bdd\u6211\u6ca1\u5b8c\u5168\u542c\u61c2\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u51db\uff0c\u827e\u5f25\u8587\u6700\u540e\u7b54\u5e94\u4f60\u4ec0\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u7b54\u5e94\u6211\u6c38\u8fdc\u548c\u5979\u5728\u4e00\u8d77\u554a\u3002\u201d</p><p>\u201c\u6211\u662f\u8bf4\uff0c\u8fd9\u53e5\u8bdd\u7684\u5177\u4f53\u6240\u6307\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\u5177\u4f53\u6240\u6307\uff0c\u5927\u6982\u610f\u601d\u5c31\u662f\u8bf4\uff0c\u4ee5\u540e\u4f60\u4eec\u7167\u6837\u8be5\u5e72\u561b\u5e72\u561b\uff0c\u4e00\u5207\u7167\u5e38\u3002\u201d</p><p>\u201c\u4e00\u5207\u7167\u5e38\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u4f60\u4eec\u8be5\u5403\u996d\u7167\u6837\u5403\u996d\uff0c\u8be5\u51fa\u95e8\u7167\u6837\u51fa\u95e8\uff0c\u8be5\u6253\u67b6\u7167\u6837\u6253\u67b6\uff0c\u8be5\u4e0a\u5e8a\u7167\u6837\u4e0a\u5e8a\u2026\u2026\u201d</p><p>\u201c\u90a3\u4f60\u5462\uff1f\u201d</p><p>\u201c\u5403\u996d\u7684\u65f6\u5019\u66ff\u6211\u52a0\u4e00\u526f\u5200\u53c9\uff0c\u51fa\u95e8\u7684\u65f6\u5019\u66ff\u6211\u53eb\u4e00\u8f86\u9a6c\u8f66\uff0c\u6253\u67b6\u7684\u65f6\u5019\u66ff\u6211\u7559\u4e2a\u540d\u989d\uff0c\u4e0a\u5e8a\u4e48\uff0c\u5c31\u50cf\u521a\u624d\u90a3\u6837\u597d\u4e86\uff0c\u6ca1\u610f\u89c1\u5427\u3002\u201d</p><p>\u610f\u89c1\u5012\u662f\u6709\u4e00\u70b9\u7684\uff0c\u5c31\u662f\u4f60\u80fd\u4e0d\u80fd\u628a\u5bf9\u827e\u5f25\u8587\u7684\u4e0d\u826f\u5174\u8da3\u8f6c\u79fb\u5230\u6211\u8eab\u4e0a\u6765\u2026\u2026\u4e0d\u8fc7\u8fd9\u4e2a\u95ee\u9898\u4ee5\u540e\u518d\u8bf4\uff0c\u53cd\u6b63\u65f6\u95f4\u591a\u5f97\u662f\u3002\u201c\u4f60\u8001\u5e08\u5462\uff1f\u201d\u743c\u6069\u95ee\uff0c\u201c\u5979\u4f1a\u540c\u610f\u5417\uff1f\u201d</p><p>\u201c\u5979\u662f\u6211\u8001\u5e08\uff0c\u53c8\u4e0d\u662f\u6211\u8001\u5988\uff0c\u6211\u7684\u79c1\u4eba\u4e8b\u52a1\u8f6e\u4e0d\u5230\u5979\u6765\u5e72\u6d89\u3002\u201d</p><p>\u8fd9\u53e5\u8bdd\u8bf4\u5f97\u5e72\u8106\u5229\u843d\uff0c\u987f\u65f6\u8ba9\u743c\u6069\u653e\u4e0b\u5fc3\u6765\uff0c\u5426\u5219\u4ed6\u5bf9\u6b23\u5e03\u90a3\u4e2a\u706b\u7206\u813e\u6c14\u8fd8\u662f\u5f88\u6709\u5fcc\u60ee\u7684\uff0c\u6bd5\u7adf\u662f\u628a\u5979\u7684\u5b66\u751f\u5f04\u4e0a\u5e8a\u3002\u65e2\u7136\u51db\u81ea\u5df1\u7684\u6001\u5ea6\u90fd\u8fd9\u4e48\u660e\u786e\u4e86\uff0c\u90a3\u4e48\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\u3002</p><p>\u8981\u8bf4\u8d77\u6765\uff0c\u51db\u548c\u6b23\u5e03\u771f\u662f\u6027\u683c\u76f8\u8fd1\uff0c\u96be\u602a\u4f1a\u88ab\u770b\u4e2d\uff0c\u4e3b\u52a8\u6536\u4e3a\u5b66\u751f\u2026\u2026\u7b49\u7b49\uff0c\u4e0d\u80fd\u8fd9\u4e48\u63a8\u8bba\uff0c\u5426\u5219\u5c82\u4e0d\u662f\u8bf4\u6211\u548c\u6211\u90a3\u6b7b\u80d6\u5b50\u8001\u5e08\u6027\u683c\u76f8\u8fd1\u4e86\uff0c\u90a3\u662f\u4e07\u4e07\u8981\u4e0d\u5f97\u7684\u3002</p><p>\u743c\u6069\u4fa7\u8eab\u79fb\u5230\u6885\u83f2\u65af\u8eab\u65c1\uff0c\u5c06\u5979\u63fd\u5165\u6000\u4e2d\uff0c\u6b63\u51c6\u5907\u8d81\u673a\u628a\u51db\u4e5f\u62c9\u8fc7\u6765\uff0c\u4eab\u53d7\u4e00\u4e0b\u5de6\u62e5\u53f3\u62b1\u7684\u611f\u89c9\uff0c\u51db\u8f7b\u8f7b\u5de7\u5de7\u5730\u907f\u5f00\u4e86\uff0c\u501f\u52bf\u4e00\u4e2a\u7ffb\u8eab\u4e0b\u4e86\u5e8a\u3002\u201c\u6211\u997f\u4e86\uff01\u201d\u5979\u5ba3\u5e03\uff0c\u201c\u5148\u53bb\u5403\u996d\u3002\u201d</p><p>\u51db\u7a7f\u4e0a\u5979\u90a3\u4e00\u5957\u81ea\u79f0\u4e3a\u201c\u51db\u5f0f\u5deb\u5e08\u670d\u201d\u7684\u8d85\u77ed\u88d9\u88c5\uff0c\u8e66\u8e66\u8df3\u8df3\u51fa\u95e8\u53bb\u4e86\uff0c\u5fc5\u987b\u8bf4\u4f5c\u4e3a\u9f99\u8109\u8005\uff0c\u5979\u7684\u6062\u590d\u80fd\u529b\u771f\u662f\u60ca\u4eba\u3002\u7761\u524d\u90a3\u4e00\u573a\u6fc0\u6218\uff0c\u743c\u6069\u5230\u73b0\u5728\u90fd\u89c9\u5f97\u8170\u80cc\u9178\u75bc\uff0c\u5979\u5c45\u7136\u82e5\u65e0\u5176\u4e8b\uff0c\u4eff\u4f5b\u4ec0\u4e48\u90fd\u6ca1\u53d1\u751f\u8fc7\u4e00\u822c\u3002</p><p>\u743c\u6069\u4f4e\u5934\uff0c\u89c1\u6000\u4e2d\u7684\u5c11\u5973\u6b63\u5728\u9759\u9759\u6c89\u601d\uff0c\u4e5f\u4e0d\u6253\u6270\uff0c\u53ea\u662f\u8f7b\u8f7b\u62b1\u7740\u5979\uff0c\u623f\u95f4\u91cc\u518d\u5ea6\u5f52\u4e8e\u5bc2\u9759\u3002\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u9b54\u6cd5\u6c99\u6f0f\u7a81\u7136\u53d1\u51fa\u4e00\u58f0\u6e05\u8106\u7684\u201c\u5494\u201d\u58f0\uff0c\u81ea\u52a8\u7ffb\u8f6c\uff0c\u7136\u540e\u7ee7\u7eed\u6c99\u6c99\u4f5c\u54cd\u3002</p><p>\u201c\u51e0\u70b9\u4e86\uff1f\u201d</p><p>\u743c\u6069\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u201c\u4e0b\u5348\u516d\u70b9\u949f\u4e86\u3002\u201d</p><p>\u201c\u8be5\u5403\u665a\u9910\u4e86\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u743c\u6069\u5e2e\u6885\u83f2\u65af\u7a7f\u597d\u8863\u670d\uff0c\u63a5\u7740\u81ea\u5df1\u4e09\u4e0b\u4e94\u9664\u4e8c\u641e\u5b9a\uff0c\u4e24\u4eba\u5230\u4e86\u9910\u5385\uff0c\u53d1\u73b0\u9664\u4e86\u4f0a\u8389\u96c5\u4e4b\u5916\uff0c\u5176\u4ed6\u4eba\u90fd\u5728\uff0c\u6b63\u5728\u5546\u8bae\u7740\u4ec0\u4e48\u3002\u963f\u5fd2\u59ae\u89c1\u743c\u6069\u4e24\u4eba\u5230\u6765\uff0c\u9012\u7ed9\u4ed6\u4e00\u4efd\u540d\u5355\u3002</p><p>\u743c\u6069\u63a5\u8fc7\uff0c\u5750\u4e0b\u7ec6\u770b\uff0c\u53d1\u73b0\u539f\u6765\u662f\u4e0b\u9762\u51e0\u8f6e\u6bd4\u8d5b\u7684\u6d41\u7a0b\u5b89\u6392\u3002\u201c\u55ef\uff0c\u6309\u7167\u8fd9\u6837\u6253\u4e0b\u6765\u7684\u8bdd\uff0c\u6211\u4eec\u5e94\u8be5\u662f\u5728\u6700\u540e\u51b3\u8d5b\u4e2d\u78b0\u5230\u8428\u9a6c\u65af\u7279\u3002\u201d</p><p>\u201c\u524d\u63d0\u662f\u6211\u4eec\u80fd\u591f\u4e00\u8def\u6253\u5230\u51b3\u8d5b\uff0c\u800c\u4e0d\u662f\u534a\u8def\u5c31\u88ab\u6dd8\u6c70\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u4e0d\u81f3\u4e8e\u5427\u3002\u201d\u743c\u6069\u6709\u4e9b\u4e0d\u4ee5\u4e3a\u7136\u3002\u6bd5\u7adf\u8fd9\u652f\u961f\u4f0d\u7684\u6574\u4f53\u5b9e\u529b\u4e0d\u5dee\uff0c\u66f4\u6709\u4e24\u4e2a\u9009\u6c11\u538b\u9635\uff0c\u6b64\u524d\u7684\u6bd4\u8d5b\u90fd\u662f\u4e00\u5e06\u98ce\u987a\u5730\u6253\u4e0b\u6765\uff0c\u6b23\u5e03\u90fd\u57fa\u672c\u6ca1\u600e\u4e48\u9700\u8981\u4e0a\u573a\u8fc7\u3002\u65e0\u5f62\u4e4b\u95f4\uff0c\u4ed6\u4e5f\u6709\u4e9b\u4fe1\u5fc3\u81a8\u80c0\u8d77\u6765\u3002</p><p>\u963f\u5fd2\u59ae\u793a\u610f\u4ed6\u4ed4\u7ec6\u770b\u540d\u5355\uff0c\u201c\u6211\u4eec\u63a5\u4e0b\u6765\u7684\u5bf9\u624b\u53ef\u90fd\u5f88\u96be\u7f20\u554a\uff0c\u5c31\u8bf4\u4e0b\u4e00\u573a\uff0c\u4fbf\u4e0d\u592a\u5bb9\u6613\u5bf9\u4ed8\u4e86\u3002\u201d</p><p>\u743c\u6069\u4ed4\u7ec6\u518d\u770b\uff0c\u53d1\u73b0\u4e0b\u4e00\u8f6e\u8981\u9762\u5bf9\u7684\u516d\u4e2a\u5bf9\u624b\u540d\u5b57\u90fd\u5f88\u53e4\u602a\uff0c\u800c\u4e14\u660e\u663e\u662f\u540c\u4e00\u4e2a\u5bb6\u65cf\u51fa\u6765\u7684\uff0c\u59d3\u6c0f\u90fd\u76f8\u540c\uff0c\u4ed6\u518d\u770b\u770b\u540e\u9762\u7684\u201c\u79cd\u65cf\u201d\u4e00\u680f\uff0c\u53d1\u73b0\u6ce8\u660e\u662f\u201c\u5409\u65af\u6d0b\u57fa\u4eba\u201d\u3002</p><p>\u201c\u5409\u65af\u6d0b\u57fa\u4eba\uff1f\u201d\u743c\u6069\u601d\u7d22\u7740\uff0c\u201c\u8fd9\u540d\u5b57\u6211\u542c\u8fc7\u2026\u2026\u4ed6\u4eec\u662f\u5c45\u4f4f\u5728\u661f\u754c\u7684\u5427\u3002\u201d</p><p>\u201c\u5bf9\uff0c\u661f\u754c\u6b66\u58eb\u5409\u65af\u6d0b\u57fa\u4eba\uff0c\u201d\u963f\u5fd2\u59ae\u8bf4\uff0c\u201c\u7075\u5438\u602a\u7684\u6b7b\u654c\u3002\u201d</p><p>\u5409\u65af\u6d0b\u57fa\u4eba\u548c\u7075\u5438\u602a\u4e00\u6837\uff0c\u90fd\u64c5\u957f\u5fc3\u7075\u5f02\u80fd\uff0c\u5018\u82e5\u4ec5\u4ec5\u5982\u6b64\u5012\u4e5f\u7f62\u4e86\uff0c\u7075\u80fd\u6cd5\u672f\u5927\u591a\u90fd\u662f\u7cbe\u795e\u653b\u51fb\u7c7b\u578b\u7684\uff0c\u743c\u6069\u538b\u6839\u4e0d\u60e7\uff0c\u4ed6\u4e00\u4e2a\u4eba\u4e0a\u53bb\u5c31\u80fd\u641e\u5b9a\u2014\u2014\u4f46\u5409\u65af\u6d0b\u57fa\u4eba\u4e0d\u4f46\u64c5\u957f\u7075\u80fd\uff0c\u800c\u4e14\u4e2a\u4e2a\u90fd\u662f\u683c\u6597\u9ad8\u624b\uff0c\u8fd9\u5c31\u5f88\u9ebb\u70e6\u4e86\u3002\u6216\u8005\u66f4\u901a\u4fd7\u70b9\u8bf4\uff0c\u8fd9\u5409\u65af\u6d0b\u57fa\u4eba\u662f\u771f\u6b63\u9b54\u6b66\u53cc\u4fee\u7684\u3002</p><p>\u201c\u518d\u4e0b\u4e00\u8f6e\uff0c\u6839\u636e\u63a8\u6d4b\u6211\u4eec\u6781\u6709\u53ef\u80fd\u662f\u649e\u4e0a\u8fd9\u4e00\u652f\u961f\u4f0d\uff0c\u201d\u963f\u5fd2\u59ae\u5728\u540d\u5355\u4e0a\u6307\u4e86\u4e00\u6307\uff0c\u201c\u5982\u679c\u4e0d\u51fa\u610f\u5916\u7684\u8bdd\u3002\u201d</p><p>\u8fd9\u652f\u961f\u4f0d\u90fd\u662f\u6df1\u6e0a\u571f\u8457\u4eec\u7ec4\u6210\u7684\uff0c\u5e76\u975e\u5409\u65af\u6d0b\u57fa\u4eba\u8fd9\u79cd\u5f02\u4f4d\u9762\u6765\u5ba2\uff0c\u5176\u4e2d\u4e94\u4e2a\u5012\u7f62\u4e86\uff0c\u4f46\u6709\u4e00\u53ea\u6b7b\u4ea1\u5de1\u730e\u8005\uff0c\u5f88\u662f\u4ee4\u4eba\u5934\u75bc\u3002</p><p>\u743c\u6069\u4e00\u8fb9\u770b\u8d44\u6599\uff0c\u838e\u73de\u514b\u5750\u5728\u65c1\u8fb9\u4e3a\u4ed6\u89e3\u8bf4\u3002\u6b7b\u4ea1\u5de1\u730e\u8005\u662f\u6df1\u6e0a\u4e09\u5927\u6076\u9b54\u541b\u4e3b\u4e4b\u4e00\u7684\u5965\u5580\u65af\u53d1\u660e\u51fa\u6765\u7684\u4ea1\u7075\u602a\u7269\uff0c\u7c97\u7565\u5f62\u5bb9\u7684\u8bdd\uff0c\u5c31\u662f\u201c\u4e00\u5927\u5806\u5c38\u4f53\u548c\u9ab7\u9ac5\u5806\u79ef\u8d77\u6765\u7684\u5de8\u65e0\u9738\u201d\uff0c\u667a\u529b\u6bd4\u50f5\u5c38\u9ab7\u9ac5\u4e4b\u7c7b\u9ad8\u4e9b\uff0c\u6709\u57fa\u7840\u7684\u81ea\u6211\u610f\u8bc6\u548c\u5224\u65ad\u80fd\u529b\uff0c\u4ee5\u4eba\u7c7b\u7684\u6807\u51c6\u6765\u770b\u662f\u5f31\u667a\u3002\u81f3\u4e8e\u8bf4\u672c\u9886\uff0c\u522b\u7684\u5012\u4e5f\u65e0\u6240\u8c13\uff0c\u6700\u9ebb\u70e6\u7684\u5728\u4e8e\u5b83\u80fd\u591f\u541e\u566c\u654c\u4eba\u3002\u6bcf\u541e\u566c\u4e00\u4e2a\uff0c\u628a\u654c\u4eba\u7684\u5c38\u4f53\u878d\u5165\u81ea\u8eab\uff0c\u5b83\u5c31\u66f4\u5f3a\u5927\u4e00\u5206\uff0c\u662f\u90a3\u79cd\u521d\u59cb\u65f6\u5f88\u5f31\uff0c\u4f46\u8d8a\u6218\u8d8a\u5f3a\u7684\u7c7b\u578b\u3002</p><p>\u743c\u6069\u7b49\u4eba\u660e\u5929\u8981\u9762\u5bf9\u7684\u8fd9\u53ea\u6b7b\u4ea1\u5de1\u730e\u8005\uff0c\u539f\u672c\u5c31\u5df2\u7ecf\u5f88\u5f3a\uff0c\u4e00\u8def\u6bd4\u8d5b\u8fd9\u6837\u6253\u4e0b\u6765\uff0c\u88ab\u5b83\u541e\u566c\u7684\u5bf9\u624b\u4e5f\u4e0d\u5c11\uff0c\u786e\u5b9e\u662f\u52b2\u654c\u3002</p><p>\u201c\u6700\u5173\u952e\u7684\uff0c\u5728\u4e8e\u5b83\u4eec\u662f\u4ea1\u7075\uff0c\u201d\u963f\u5fd2\u59ae\u8bf4\uff0c\u201c\u5bf9\u4ed8\u4ea1\u7075\uff0c\u7267\u5e08\u7684\u795e\u672f\u539f\u672c\u662f\u6700\u6709\u6548\uff0c\u53ea\u662f\u6211\u4eec\u73b0\u5728\u2026\u2026\u201d</p><p>\u73b0\u5728\u961f\u4f0d\u4e4b\u4e2d\uff0c\u6709\u4e09\u4e2a\u5deb\u5e08\uff08\u743c\u6069\u3001\u6b23\u5e03\u548c\u51db\uff09\uff0c\u4e00\u4e2a\u5f13\u7bad\u624b\uff08\u963f\u5fd2\u59ae\uff09\uff0c\u4e00\u4e2a\u9b45\u9b54\uff08\u838e\u73de\u514b\uff09\uff0c\u8fd8\u6709\u4e00\u4e2a\u7267\u5e08\uff08\u8475\u9732\uff09\u2014\u2014\u4f46\u5374\u662f\u4e2a\u795e\u7947\u6c89\u7720\u7684\u7267\u5e08\uff0c\u6839\u672c\u6ca1\u6709\u534a\u70b9\u795e\u672f\u53ef\u7528\uff0c\u8fd9\u5c31\u6bd4\u8f83\u9ebb\u70e6\u3002</p><p>\u201c\u6211\u7684\u5f13\u7bad\u5bf9\u4ea1\u7075\u6548\u679c\u4e0d\u5927\uff0c\u800c\u4e14\u8fd9\u53ea\u6b7b\u4ea1\u5de1\u730e\u8005\u57fa\u672c\u662f\u9ab7\u9ac5\u7ed3\u6784\uff0c\u7bad\u77e2\u53ea\u6015\u90fd\u76f4\u63a5\u7a7f\u8fc7\u53bb\u4e86\u3002\u4ece\u4ee5\u5f80\u7684\u51e0\u6b21\u6bd4\u8d5b\u89c2\u6d4b\u7ed3\u679c\u6765\u770b\uff0c\u4e5f\u4e0d\u600e\u4e48\u754f\u60e7\u706b\u7130\u9b54\u6cd5\uff0c\u5b83\u81ea\u5df1\u8eab\u4e0a\u5c31\u80fd\u55b7\u706b\uff0c\u201d\u963f\u5fd2\u59ae\u5411\u743c\u6069\u5206\u6790\u7740\uff0c\u201c\u81f3\u4e8e\u838e\u73de\u514b\u5c0f\u59d0\u2026\u2026\u201d</p><p>\u90a3\u5c31\u7b97\u4e86\u5427\uff0c\u9b45\u9b54\u7684\u5a9a\u60d1\u80fd\u529b\u518d\u63d0\u9ad8\u5341\u500d\uff0c\u5bf9\u4e00\u5806\u9ab7\u9ac5\u9aa8\u67b6\u53c8\u6709\u4ec0\u4e48\u4f5c\u7528\u5462\u3002</p><p>\u743c\u6069\u867d\u7136\u6ca1\u6709\u900f\u9732\u81ea\u5df1\u653e\u5f03\u4e86\u54ea\u4e9b\u5b66\u6d3e\uff0c\u4f46\u5927\u5bb6\u76f8\u5904\u65f6\u95f4\u957f\u4e86\uff0c\u5927\u81f4\u4e5f\u80fd\u731c\u6d4b\uff0c\u4ed6\u4ece\u6765\u4e0d\u7528\u4efb\u4f55\u4ea1\u7075\u6cd5\u672f\uff0c\u53ef\u89c1\u5bf9\u8fd9\u65b9\u9762\u5f88\u4e0d\u64c5\u957f\uff0c\u81f3\u4e8e\u6b23\u5e03\u548c\u8475\u9732\u4e48\u2026\u2026</p><p>\u201c\u6211\u7684\u6cd5\u672f\u4e5f\u672a\u5fc5\u80fd\u6709\u591a\u5c11\u6548\u679c\uff0c\u201d\u6b23\u5e03\u96be\u5f97\u5730\u4f4e\u8c03\u4e00\u6b21\uff0c\u201c\u6050\u6015\u53ea\u80fd\u7528\u94f6\u706b\u4e86\u3002\u201d</p><p>\u94f6\u706b\u662f\u5973\u795e\u7684\u795e\u529b\uff0c\u6548\u679c\u6bd4\u7267\u5e08\u7684\u795e\u672f\u66f4\u597d\uff0c\u7528\u6765\u5bf9\u4ed8\u4ea1\u7075\u662f\u6ca1\u95ee\u9898\uff0c\u4f46\u94f6\u706b\u4e5f\u662f\u6709\u9650\u5ea6\u7684\uff0c\u6216\u8005\u8bf4\u795e\u7947\u4e0b\u653e\u7ed9\u9009\u6c11\u7684\u201c\u6d41\u91cf\u201d\u662f\u6709\u9650\u7684\uff0c\u6d88\u8017\u4e86\u5c31\u9700\u8981\u6709\u8db3\u591f\u7684\u65f6\u95f4\u6765\u81ea\u52a8\u6062\u590d\u3002\u6b23\u5e03\u4f30\u7b97\u4e86\u4e00\u4e0b\uff0c\u6bc1\u6389\u8fd9\u4e48\u5927\u7684\u4e00\u53ea\u6b7b\u4ea1\u5de1\u730e\u8005\uff0c\u53ea\u6015\u8981\u6d88\u8017\u6389\u5979\u5c06\u8fd1\u4e09\u5206\u4e4b\u4e8c\u7684\u94f6\u706b\uff0c\u800c\u8fd9\u5927\u6982\u9700\u8981\u4e24\u5929\u7684\u65f6\u95f4\u624d\u80fd\u5b8c\u5168\u6062\u590d\u2014\u2014\u4f46\u7d27\u63a5\u7740\u5c31\u662f\u51b3\u8d5b\u4e86\u3002</p><p>\u201c\u53ef\u60dc\u6211\u65e0\u6cd5\u4f7f\u7528\u795e\u672f\uff0c\u201d\u8475\u9732\u53f9\u606f\uff0c\u201c\u5426\u5219\u867d\u7136\u662f\u5728\u6df1\u6e0a\u4f4d\u9762\uff0c\u795e\u672f\u7684\u6548\u679c\u4f1a\u5927\u6253\u6298\u6263\uff0c\u4f46\u81f3\u5c11\u80fd\u591f\u5927\u5927\u6d88\u8017\u8fd9\u53ea\u6b7b\u4ea1\u5de1\u730e\u8005\u7684\u5b9e\u529b\u3002\u201d</p><p>\u201c\u6216\u8005\u6211\u4eec\u660e\u5929\u4e0a\u5348\uff0c\u7b2c\u4e00\u8f6e\u6bd4\u8d5b\u5b8c\u4e86\u5c31\u8981\u6c42\u9000\u51fa\u4e00\u4eba\uff0c\u53e6\u5916\u518d\u53bb\u627e\u4e2a\u6076\u9b54\u796d\u53f8\u6765\u66ff\u8865\uff1f\u201d</p><p>\u8fd9\u662f\u963f\u5fd2\u59ae\u60f3\u51fa\u7684\u4e3b\u610f\uff0c\u5012\u4e5f\u4e0d\u65e0\u53ef\u80fd\u3002\u56e0\u4e3a\u51b3\u6597\u5927\u8d5b\u6bd4\u8f83\u6fc0\u70c8\uff0c\u6b7b\u4f24\u751a\u91cd\uff0c\u5982\u679c\u4e00\u652f\u961f\u4f0d\u88ab\u6dd8\u6c70\u4e86\uff0c\u90a3\u81ea\u7136\u6ca1\u4ec0\u4e48\u53ef\u8bf4\uff0c\u4f46\u5982\u679c\u6253\u8d62\u4e86\uff0c\u80fd\u591f\u664b\u7ea7\u4e0b\u4e00\u8f6e\uff0c\u53ea\u662f\u672c\u961f\u6210\u5458\u6b7b\u4f24\u4e00\u7247\uff0c\u90a3\u4e8b\u5b9e\u4e0a\u4e5f\u6ca1\u6cd5\u7ee7\u7eed\u4e86\u3002\u9274\u4e8e\u6b64\u6545\uff0c\u6bd4\u8d5b\u89c4\u5219\u6709\u4e00\u6761\uff0c\u6bcf\u8f6e\u6bd4\u8d5b\u4e4b\u540e\uff0c\u80dc\u5229\u961f\u4f0d\u53ef\u4ee5\u7533\u8bf7\u9000\u51fa\u67d0\u51e0\u540d\u6210\u5458\uff0c\u53ea\u8981\u4f60\u80fd\u5728\u4e0b\u4e00\u573a\u6bd4\u8d5b\u4e4b\u524d\u627e\u5230\u4eba\u6765\u66ff\u8865\u5c31\u884c\u3002</p><p>\u201c\u4f46\u4ed3\u4fc3\u4e4b\u95f4\uff0c\u6211\u4eec\u4ece\u54ea\u91cc\u53bb\u627e\u4e2a\u6076\u9b54\u796d\u53f8\u6765\uff0c\u90a3\u79cd\u5bb6\u4f19\u5c11\u5f97\u7b80\u76f4\u5c31\u50cf\u6d77\u65af\u62c9\u8482\u7684\u7406\u6027\uff0c\u201d\u838e\u73de\u514b\u53cd\u9a73\uff0c\u201c\u5c31\u7b97\u80fd\u627e\u5230\uff0c\u96be\u9053\u5c31\u80fd\u4fe1\u4efb\uff1f\u201d</p><p>\u5728\u6709\u5173\u6076\u9b54\u7684\u4e8b\u60c5\u4e0a\uff0c\u838e\u73de\u514b\u7684\u53d1\u8a00\u81ea\u7136\u6709\u76f8\u5f53\u6743\u5a01\uff0c\u963f\u5fd2\u59ae\u4e5f\u53ea\u662f\u60c5\u6025\u4e4b\u4e0b\u4e71\u60f3\uff0c\u95fb\u8a00\u4fbf\u95ed\u53e3\u4e0d\u8bf4\u3002\u6c14\u6c1b\u4e00\u65f6\u6709\u4e9b\u6c89\u95f7\uff0c\u743c\u6069\u628a\u73a9\u7740\u540d\u5355\uff0c\u7a81\u7136\u5fc3\u5ff5\u4e00\u52a8\uff0c\u60f3\u8d77\u4ef6\u4e8b\u60c5\u6765\u3002</p><p>\u201c\u90a3\u53ea\u6b7b\u4ea1\u5de1\u730e\u8005\u5c31\u4ea4\u7ed9\u6211\u5427\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u5e94\u8be5\u80fd\u8bf7\u5230\u7267\u5e08\u5e2e\u5fd9\u3002\u201d</p></article>"}}}]);
