(window.webpackJsonp=window.webpackJsonp||[]).push([[326],{349:function(p){p.exports={chapterTitle:null,title:"\u7b2c326\u7ae0 \u516d\u5341\u4e07\u5e74\u85aa",content:"<article><h2>\u7b2c326\u7ae0 \u516d\u5341\u4e07\u5e74\u85aa</h2><p>\u3000\u3000\u7d20\u51c0\u7684\u6df1\u8272\u5c0f\u95e8\uff0c\u95e8\u5916\u53ea\u6709\u95e8\u724c\u53f7\uff0c\u7528\u6960\u6728\u96b6\u4e66\u6d45\u96d5\u4e0a\u6df1\u8272\u6f06\uff1a\u201c\u4e39\u4e95\u5df7\u4e09\u53f7\u201d\uff0c\u8fd9\u4e00\u5757\u6728\u523b\u95e8\u724c\u97f5\u5473\u5341\u8db3\uff0c\u63a8\u95e8\u800c\u5165\u624d\u770b\u5f97\u89c1\u5f71\u58c1\u4e0a\u5c0f\u7bc6\u523b\u4e66\u201c\u516b\u9526\u73cd\u80b4\u5c45\u201d\u4e94\u5b57\uff0c\u8d34\u7c89\u5899\u9752\u74e6\u7684\u9662\u5899\u662f\u8377\u82b1\u6c34\u6c60\uff0c\u7cbc\u7cbc\u6c34\u6ce2\uff0c\u65b0\u8377\u624d\u9732\u5c16\u5c16\u89d2\uff0c\u4e1d\u6761\u6c34\u8349\u8361\u6f3e\u3002\u7ed5\u8fc7\u5f71\u58c1\uff0c\u539f\u5148\u7684\u524d\u5ead\u5730\u9762\u5b8c\u5168\u88ab\u534a\u900f\u660e\u7684\u73bb\u7483\u66ff\u4ee3\uff0c\u539f\u6765\u6574\u4e2a\u524d\u5ead\u90fd\u6316\u6210\u8377\u82b1\u6c34\u6c60\uff0c\u53ea\u662f\u8d34\u7740\u9662\u5899\u7684\u56de\u5eca\u4f3c\u7684\u6c34\u9762\u9732\u51fa\u6765\uff0c\u900f\u8fc7\u73bb\u7483\u4e5f\u80fd\u770b\u89c1\u4e0b\u9762\u7684\u4e1d\u6761\u6c34\u8349\u4e0e\u5a09\u5a77\u8377\u830e\uff0c\u4e0e\u65e7\u5f0f\u5eca\u67f1\u3001\u7a97\u683c\u76f8\u6620\uff0c\u4fe8\u7136\u4e00\u5e45\u73b0\u4ee3\u6d3e\u8001\u6d77\u5dde\u7684\u98ce\u60c5\u753b\u5377\u3002</p><p>\u3000\u3000\u8fd9\u91cc\u539f\u6765\u68cb\u9986\u7684\u540e\u9662\uff0c\u4e0e\u68cb\u9986\u8fd8\u6709\u4e00\u5904\u5c0f\u95e8\u76f8\u901a\u3002</p><p>\u3000\u3000\u5386\u7ecf\u5c0f\u534a\u5e74\u7684\u4fee\u7f2e\u3001\u6574\u56fa\uff0c\u68cb\u9986\u4e8e\u4e09\u6708\u5e95\u84e6\u7136\u63ed\u5f00\u9762\u7eb1\u7684\u90a3\u5929\uff0c\u5438\u5f15\u5728\u524d\u8857\u9a7b\u8db3\u89c2\u770b\u7684\u884c\u4eba\u5c06\u6574\u6761\u8857\u90fd\u62e5\u5835\u4e86\u534a\u5929\uff0c\u7070\u6734\u7834\u65e7\u7684\u53e4\u697c\u7f8e\u8f6e\u7f8e\u5942\u7684\u8038\u7acb\u5728\u4eba\u4eec\u7684\u9762\u524d\uff0c\u624d\u4fc3\u4f7f\u4eba\u4eec\u53bb\u601d\u8003\u6c99\u7530\u7684\u5efa\u7b51\u7fa4\u7684\u4ef7\u503c\u3002</p><p>\u3000\u3000\u8981\u7b79\u5907\u77404\u67088\u65e5\u90a3\u4e00\u5929\u6b63\u5f0f\u8425\u4e1a\uff0c\u9648\u5947\u8fd9\u4e9b\u5929\u8f9b\u82e6\u5f97\u7d27\uff0c\u6b63\u8d76\u4e0a\u65b0\u5149\u7eb8\u4e1a\u6539\u5236\uff0c\u5408\u4f19\u4eba\u5468\u590d\u5b8c\u5168\u632a\u4e0d\u51fa\u624b\u6765\uff0c\u4ed6\u59bb\u5b50\u662f\u65b0\u5149\u5382\u7684\u4f1a\u8ba1\uff0c\u4e5f\u5b8c\u5168\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u7b49\u8fd9\u8fb9\u7b79\u63aa\u505c\u5f53\uff0c\u65b0\u5149\u7eb8\u4e1a\u4e5f\u8fc7\u53bb\u6700\u5fd9\u4e71\u7684\u4e00\u7ae0\uff0c\u5468\u590d\u624d\u6709\u7a7a\u8fc7\u6765\u4ed4\u7ec6\u7684\u7785\u4e00\u773c\u3002</p><p>\u3000\u3000\u5468\u590d\u8fdb\u6765\uff0c\u8bb8\u9e3f\u4f2f\u7684\u4e8c\u513f\u5b50\u8bb8\u745e\u5e73\u770b\u89c1\u4ed6\uff0c\u8bf4\uff1a\u201c\u5927\u8001\u677f\u6765\u4e86\uff0c\u53d1\u70df\u6765\u62bd\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u590d\u638f\u51fa\u70df\u6765\u53d1\uff0c\u8bb8\u745e\u5e73\u7b11\u4ed6\uff1a\u201c\u5e74\u85aa\u516d\u5341\u4e07\uff0c\u600e\u4e48\u4e5f\u8ddf\u6211\u4eec\u4e00\u6837\u62bd\u9526\u6e56\uff1f\u201d</p><p>\u3000\u3000\u5468\u590d\u82e6\u7b11\uff0c\u65b0\u5149\u7eb8\u4e1a\u82b1\u516d\u5341\u4e07\u5e74\u85aa\u8058\u8bf7\u526f\u603b\u88c1\u5b8b\u5fd7\u4f51\u76ee\u524d\u5df2\u6210\u6d77\u5dde\u7684\u5927\u65b0\u95fb\uff0c\u7701\u5185\u5a92\u4f53\u4e5f\u591a\u6709\u7092\u4f5c\uff0c\u9526\u6e56\u8fd9\u6b21\u662f\u4e00\u6539\u4ee5\u524d\u7684\u4f4e\u8c03\u4f5c\u98ce\u3002\u516d\u5341\u4e07\u5e74\u85aa\u90a3\u662f\u7ed9\u5b8b\u5fd7\u4f51\u7684\uff0c\u5468\u590d\u867d\u7136\u4e5f\u662f\u65b0\u5149\u7eb8\u4e1a\u526f\u603b\uff0c\u85aa\u6c34\u53ef\u8fdc\u8fdc\u6ca1\u6709\u8fd9\u4e48\u9ad8\uff0c\u65b0\u5149\u7eb8\u4e1a\u8fd9\u4e00\u6b21\u4ece\u8058\u8bf7\u7684\u51e0\u540d\u9ad8\u7ea7\u804c\u5458\uff0c\u5e74\u85aa\u4e5f\u5c31\u5728\u5341\u4e07\u5230\u5341\u4e94\u4e07\u4e4b\u95f4\uff0c\u4fbf\u662f\u516c\u53f8\u603b\u88c1\u5468\u6e38\u7684\u5e74\u85aa\u4e5f\u4e0d\u8fc7\u4e09\u5341\u4e07\uff0c\u53ea\u662f\u5468\u6e38\u53e6\u6709\u80a1\u6743\u7ea2\u5229\u53ef\u4eab\u90a3\u662f\u4e24\u8bf4\u3002</p><p>\u3000\u3000\u522b\u4eba\u7684\u4e0d\u77e5\u9053\u5176\u4e2d\u7684\u8be6\u7ec6\uff0c\u53ea\u5f53\u65b0\u5149\u7eb8\u4e1a\u540c\u4e00\u7ea7\u7684\u7ba1\u7406\u5c42\u85aa\u6c34\u603b\u4e0d\u4f1a\u5dee\u592a\u8fdc\uff0c\u77e5\u9053\u5468\u590d\u5728\u65b0\u5149\u7eb8\u4e1a\u4efb\u526f\u603b\u7684\u4eba\uff0c\u90fd\u4ee5\u4e3a\u4ed6\u7684\u5e74\u85aa\u4e5f\u6709\u516d\u5341\u4e07\uff0c\u8fd9\u5728\u6d77\u5dde\u80fd\u628a\u4eba\u7684\u4e0b\u5df4\u5413\u8131\u4e86\uff0c\u66fe\u51e0\u4f55\u65f6\uff0c\u81ea\u5df1\u4e0d\u5f53\u8001\u677f\u80fd\u62ff\u8fd9\u4e48\u9ad8\u7684\u5de5\u8d44\uff1f\u8981\u77e5\u9053\u6d77\u5dde\u5e02\u5e73\u5747\u5de5\u8d44\u7ecf\u8fc7\u4e5d\u4e94\u5e74\u5927\u5e45\u5ea6\u7684\u63d0\u5347\u4e4b\u540e\uff0c\u8fd8\u53ea\u505c\u7559\u5728\u4e09\u767e\u56db\u5341\u5143\u5de6\u53f3\uff0c\u4fbf\u662f\u4e00\u5e74\u80fd\u6709\u516d\u5341\u4e07\u5229\u6da6\u7684\u5382\u5b50\u5728\u6d77\u5dde\u4e5f\u7b97\u662f\u6709\u5934\u6709\u8138\u3002\u5468\u590d\u89e3\u91ca\u8fc7\u51e0\u6b21\uff0c\u6ca1\u4eba\u4fe1\u4ed6\uff0c\u4ed6\u4e5f\u5c31\u4e0d\u518d\u89e3\u91ca\uff0c\u5fc3\u60f3\u5b8b\u5fd7\u4f51\u62ff\u516d\u5341\u4e07\u5e74\u85aa\uff0c\u65e5\u5b50\u4e5f\u4e0d\u597d\u8fc7\uff0c\u65b0\u95fb\u8fd9\u4e00\u7092\u4f5c\uff0c\u4ed6\u4eba\u5c31\u7ed9\u5f20\u606a\u63a8\u5230\u706b\u7089\u4e0a\u53bb\u70e4\uff0c\u96c6\u56e2\u91cc\u5176\u4ed6\u4eba\u90fd\u76ef\u7740\u4ed6\uff0c\u516d\u5341\u4e07\u5e74\u85aa\u4e5f\u7684\u786e\u8bf1\u4eba\uff0c\u800c\u4e14\u5b8b\u5fd7\u4f51\u8fd8\u53ea\u662f\u5916\u6765\u6237\uff0c\u4e00\u6765\u5c31\u62ff\u6bd4\u516c\u53f8\u603b\u88c1\u8fd8\u9ad8\u7684\u5e74\u85aa\uff0c\u4efb\u662f\u8c01\u5fc3\u91cc\u90fd\u6709\u4e9b\u4e0d\u670d\u6c14\u3001\u4e0d\u723d\u5feb\uff0c\u770b\u4ed6\u600e\u6837\u5c06\u96c6\u56e2\u6240\u4ea4\u7ed9\u4ed6\u7684\u76ee\u6807\u8fbe\u6210\u3002</p><p>\u3000\u3000\u4e24\u5e74\u5185\u8425\u6797\u767e\u4e07\u4ea9\uff0c\u7b79\u5907\u4e94\u5341\u4ebf\u6295\u8d44\u7684\u5236\u6d46\u9879\u76ee\uff0c\u80fd\u628a\u8fd9\u4e24\u4ef6\u4e8b\u505a\u6210\uff0c\u4e5f\u503c\u516d\u5341\u4e07\u7684\u5e74\u85aa\u3002</p><p>\u3000\u3000\u5468\u590d\u4f38\u624b\u8981\u5c06\u8bb8\u745e\u5e73\u624b\u91cc\u7684\u70df\u62a2\u56de\u6765\uff1a\u201c\u4e0d\u62bd\u62c9\u5012\u2026\u2026\u4f60\u7ed9\u4f60\u81ea\u5df1\u5f00\u591a\u5c11\u85aa\u6c34\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\u90a3\u7834\u5382\uff0c\u4e00\u5e74\u90fd\u6323\u4e0d\u4e86\u516d\u5341\u4e07\u2026\u2026\u201d\u8bb8\u745e\u5e73\u563f\u7136\u4e00\u7b11\uff0c\u4ed6\u5927\u4e13\u6bd5\u4e1a\u8fdb\u5e02\u74f7\u5668\u5382\u5de5\u4f5c\u5341\u4e8c\u5e74\uff0c\u53bb\u5e74\u58ee\u7740\u80c6\u5b50\u4e0e\u4e09\u540d\u540c\u4e8b\u4e00\u8d77\u5c06\u5e02\u74f7\u5668\u5382\u627f\u5305\u4e0b\u6765\uff0c\u9648\u5947\u8fd9\u6b21\u5411\u4ed6\u4eec\u74f7\u5668\u5382\u5b9a\u5236\u4e86\u4e00\u6279\u9ad8\u6863\u9aa8\u74f7\u5668\u76bf\uff0c\u4ed6\u8fd9\u662f\u9001\u8d27\u8fc7\u6765\u3002</p><p>\u3000\u3000\u5468\u590d\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u4eec\u8981\u80fd\u5c06\u5386\u53f2\u4e0a\u7684\u6d77\u5dde\u74f7\u9020\u51fa\u6765\uff0c\u60f3\u53d1\u8d22\u8fd8\u4e0d\u7b80\u5355\uff01\u8bb8\u8001\u5e08\u4e0d\u662f\u5bf9\u8fd9\u4e2a\u6709\u7814\u7a76\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u5bb6\u8001\u5934\u5b50\u53ea\u662f\u5174\u8da3\u6240\u81f4\uff0c\u5199\u51e0\u7bc7\u6587\u7ae0\u800c\u5df2\uff0c\u5bf9\u751f\u4ea7\u5de5\u827a\u6539\u826f\u6ca1\u4ec0\u4e48\u5e2e\u52a9\u7684\uff0c\u6211\u8981\u8bf7\u4ed6\u5e2e\u52a9\u770b\u770b\uff0c\u4ed6\u8fd8\u53d1\u813e\u6c14\u3002\u4ed6\u8981\u6709\u5fc3\uff0c\u5f53\u5e74\u5c31\u5728\u5e02\u91cc\u4e0d\u8981\u51fa\u6765\uff0c\u6211\u4eec\u5144\u5f1f\u5c31\u4e0d\u7528\u8fd9\u4e48\u62fc\u547d\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u53bb\uff0c\u8bf4\u8bdd\u4e0d\u8981\u6321\u95e8\u53e3\u2026\u2026\u201d\u8bb8\u9e3f\u4f2f\u8fdb\u6765\u65a5\u4e86\u4ed6\u4e8c\u5b50\u4e00\u58f0\uff0c\u8fd9\u516b\u9526\u73cd\u80b4\u5c45\u5c31\u662f\u68cb\u9986\u7684\u540e\u9662\uff0c\u68cb\u9986\u4fee\u7f2e\u6574\u56fa\u5b8c\u6bd5\uff0c\u8bb8\u9e3f\u4f2f\u5c31\u6b63\u5f0f\u5c06\u68cb\u9662\u642c\u5230\u8fd9\u91cc\u6765\uff0c\u95ee\u5468\u590d\uff0c\u201c\u8fd9\u6b21\u641e\u8fd9\u4e48\u5927\u52a8\u9759\uff0c\u751f\u6015\u522b\u4eba\u4e0d\u6653\u5f97\u4f60\u4eec\u516c\u53f8\u8d22\u5927\u6c14\u7c97\u4f3c\u7684\uff0c\u51c6\u5907\u505a\u4ec0\u4e48\uff0c\u8981\u5148\u641e\u5f97\u58f0\u52bf\u593a\u4eba\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f20\u606a\u8fd8\u6ca1\u6709\u627e\u8bb8\u8001\u5e08\uff1f\u4e0a\u56de\u5f00\u4f1a\u65f6\u8fd8\u8981\u8bf4\u8bf7\u8bb8\u8001\u5e08\u7ed9\u6211\u4eec\u516c\u53f8\u5f53\u987e\u95ee\uff0c\u5927\u6982\u4ed6\u4e5f\u5feb\u627e\u8bb8\u8001\u5e08\u4f60\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u5bf9\u9020\u7eb8\u4e00\u7a8d\u4e0d\u901a\uff0c\u4f1a\u70b9\u8003\u636e\u6587\u7ae0\uff0c\u96be\u9053\u73b0\u5728\u7684\u9020\u7eb8\u5de5\u827a\u8fd8\u8981\u501f\u9274\u53e4\u4ee3\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fdb\u53bb\u8bf4\u8bdd\u2026\u2026\u201d\u95e8\u53e3\u4eba\u6765\u4eba\u5f80\uff0c\u6d88\u606f\u8fd9\u65f6\u5019\u4f20\u51fa\u53bb\u4e5f\u4e0d\u5927\u597d\uff0c\u5468\u590d\u62c9\u7740\u8bb8\u9e3f\u4f2f\u5f80\u91cc\u8d70\uff0c\u8bb8\u745e\u5e73\u4e5f\u8bc6\u8da3\u6ca1\u6709\u8ddf\u7740\uff0c\u62db\u547c\u4e86\u4e00\u58f0\uff0c\u5e26\u7740\u4eba\u5f00\u7740\u74f7\u5668\u5382\u7684\u9762\u5305\u8f66\u56de\u53bb\u4e86\u3002</p><p>\u3000\u3000\u5df2\u7ecf\u6709\u623f\u95f4\u5e03\u7f6e\u597d\u4e86\uff0c\u6311\u4e86\u4e00\u95f4\uff0c\u90fd\u662f\u4eff\u53e4\u7684\u5bb6\u4ff1\uff0c\u660e\u5f0f\u4e66\u6a71\u91cc\u6709\u4e66\u7c4d\u4e0e\u4eff\u9752\u82b1\u74f7\u5668\u88c5\u9970\u3002</p><p>\u3000\u3000\u201c\u8ddf\u524d\u4e9b\u5929\u7701\u653f\u5e9c\u9881\u53d1\u7684\u300a\u4fdd\u62a4\u5c0f\u6c5f\u4e2d\u4e0a\u6e38\u5929\u7136\u6797\u8d44\u6e90\u300b\u7684\u6587\u6709\u5173\uff1f\u201d\u8bb8\u9e3f\u4f2f\u8ddf\u9648\u5947\u719f\u7edc\uff0c\u81ea\u5df1\u5012\u6c34\u6c8f\u8336\uff0c\u7aef\u8d77\u8336\u676f\uff0c\u5439\u6389\u6c34\u9762\u4e0a\u7684\u8336\u6cab\u95ee\u5468\u590d\u3002\u8fd9\u662f\u5f20\u606a\u5728\u6d2a\u81f4\u6797\u573a\u53d1\u73b0\u7684\u5c71\u8336\uff0c\u9664\u4e86\u5f62\u788e\u6cab\u591a\u4e4b\u5916\uff0c\u5374\u662f\u597d\u8336\uff0c\u9001\u4e86\u4e00\u4e9b\u7ed9\u8bb8\u9e3f\u4f2f\uff0c\u9648\u5947\u559d\u8fc7\u51e0\u6b21\uff0c\u9648\u5947\u6253\u7b97\u5728\u516b\u9526\u73cd\u6253\u7b97\u7528\u8fd9\u79cd\u8336\u53f6\u6c8f\u8336\uff0c\u6ee4\u53bb\u788e\u53f6\u8336\u6cab\u5c31\u53ef\u4ee5\u3002</p><p>\u3000\u3000\u201c\u55ef\uff0c\u7701\u91cc\u7684\u90a3\u7bc7\u6587\uff0c\u7b49\u540c\u4e8e\u7981\u4f10\u4ee4\u3002\u4f46\u662f\u5149\u6709\u4fdd\u62a4\u8fd8\u4e0d\u591f\uff0c\u6211\u53bb\u770b\u8fc7\uff0c\u5927\u90e8\u5206\u7684\u6797\u5730\u90fd\u4f10\u7a7a\u4e86\uff0c\u8352\u4e86\uff0c\u7701\u6797\u4e1a\u5385\u7684\u5934\u5934\u8111\u8111\u7ed9\u62ff\u6389\u4e00\u5927\u6279\uff0c\u53ef\u60dc\u6ca1\u7528\uff0c\u7701\u91cc\u53ea\u6709\u529b\u6c14\u6062\u590d\u4e00\u90e8\u5206\u516c\u76ca\u6797\uff0c\u8fd8\u5f97\u63a8\u52a8\u539f\u6599\u6797\u7684\u5efa\u8bbe\u3002\u867d\u7136\u56fd\u52a1\u966287\u5e74\u5c31\u51fa\u53f0\u300a\u5173\u4e8e\u52a0\u5feb\u9020\u7eb8\u5de5\u4e1a\u539f\u6599\u6797\u57fa\u5730\u5efa\u8bbe\u82e5\u5e72\u610f\u89c1\u7684\u901a\u77e5\u300b\uff0c\u4f46\u662f\u5feb\u5341\u5e74\u4e86\uff0c\u56fd\u5185\u6709\u591a\u5927\u8fdb\u5c55\uff0c\u524d\u4e9b\u5e74\uff0c\u641e\u8349\u6d46\uff0c\u6210\u672c\u4f4e\uff0c\u9879\u76ee\u6295\u8d44\u5c11\uff0c\u8fd9\u51e0\u5e74\u610f\u8bc6\u5230\u641e\u8349\u6d46\uff0c\u73af\u5883\u6210\u672c\u592a\u5927\uff0c\u6211\u4eec\u56fd\u5bb6\u5df2\u7ecf\u652f\u4ed8\u4e0d\u8d77\u8fd9\u4e48\u9ad8\u7684\u73af\u5883\u6210\u672c\uff0c\u624d\u5f00\u59cb\u6dd8\u6c70\u8349\u6d46\uff0c\u4e0a\u6728\u6d46\uff0c\u6728\u6d46\u6295\u8d44\u592a\u9ad8\uff0c\u5bf9\u539f\u6599\u6797\u8fd8\u6709\u4e25\u683c\u7684\u8981\u6c42\uff0c\u52a0\u4e0a\u56fd\u5916\u6728\u6d46\u4f9b\u5e94\u60c5\u51b5\u8fd8\u597d\uff0c\u56fd\u5185\u7684\u51e0\u5bb6\u5927\u5382\uff0c\u5c11\u6709\u6253\u7b97\u81ea\u5df1\u4e0a\u5236\u6d46\u9879\u76ee\u7684\uff0c\u4e0a\u5236\u6d46\u9879\u76ee\u6700\u5173\u952e\u7684\u662f\u8981\u6709\u539f\u6599\u6797\u57fa\u5730\uff0c\u9760\u5929\u7136\u6797\u4e0d\u884c\uff0c\u4e0d\u8fc7\uff0c\u6211\u4eec\u8fd9\u6b21\u53ef\u4e0d\u662f\u641e\u4ec0\u4e48\u539f\u6599\u6797\u57fa\u5730\uff0c\u800c\u662f\u4e00\u4e0b\u5b50\u5c31\u5f3a\u4e0a\u767e\u4e07\u4ea9\u7ea7\u8425\u6797\u9879\u76ee\uff0c\u800c\u4e14\u8981\u540c\u65f6\u5728\u5c0f\u6c5f\u4e2d\u4e0a\u6e38\u7684\u4e5d\u4e2a\u53bf\u641e\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u542c\u5f97\u51fa\uff0c\u4f60\u5bf9\u8fd9\u4e2a\u9879\u76ee\u6709\u4e9b\u610f\u89c1\uff1f\u201d\u8bb8\u9e3f\u4f2f\u95ee\u9053\u3002</p><p>\u3000\u3000\u5468\u590d\u7b11\u7740\u8bf4\uff1a\u201c\u65b0\u5149\u7eb8\u4e1a\u5c06\u57ce\u5357\u533a\u9020\u7eb8\u5de5\u4e1a\u56ed\u91cc\u7684\u5382\u5b50\u90fd\u4e70\u4e0b\u6765\uff0c\u603b\u8d44\u4ea7\u4e5f\u4e0d\u8fc7\u56db\u4e2a\u4ebf\uff0c\u505a\u4e8b\u603b\u8981\u6309\u6b65\u9aa4\u6765\uff0c\u6211\u662f\u6709\u4e9b\u610f\u89c1\uff0c\u8ddf\u606a\u5c11\u4e5f\u5f53\u9762\u63d0\u8fc7\u4e86\uff0c\u4ed6\u6ca1\u6709\u542c\uff0c\u6211\u53ea\u6709\u8001\u8001\u5b9e\u5b9e\u505a\u81ea\u5df1\u4efd\u5185\u7684\u4e8b\u60c5\u2026\u2026\u201d</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u77e5\u9053\u5f20\u606a\u4e3a\u4ec0\u4e48\u8981\u627e\u4ed6\u5f53\u987e\u95ee\u4e86\uff0c\u5206\u6563\u5230\u4e5d\u4e2a\u53bf\u540c\u65f6\u8425\u9020\u539f\u6599\u6797\uff0c\u52bf\u5fc5\u8981\u4f9d\u8d56\u5f53\u5730\u653f\u5e9c\u53d1\u5c55\u519c\u6237\u4e0e\u539f\u6765\u7684\u68ee\u5de5\u4f01\u4e1a\u4e00\u8d77\u5206\u96be\u8425\u6797\u7684\u4efb\u52a1\uff0c\u8fd9\u5176\u4e2d\u7684\u73af\u8282\u53ef\u6bd4\u7531\u65b0\u5149\u7eb8\u4e1a\u76f4\u63a5\u627f\u63fd\u4e00\u5927\u7247\u6797\u5730\u6765\u690d\u6797\u590d\u6742\u5f97\u591a\uff0c\u60df\u4e00\u7684\u597d\u5904\u5c31\u662f\u5904\u7406\u5f97\u5f53\u8425\u6797\u901f\u5ea6\u4f1a\u5f02\u5e38\u7684\u5feb\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u638f\u516d\u5341\u4e07\u5e74\u85aa\u8bf7\u7684\u8fd9\u4e2a\u4eba\u5c31\u662f\u4e3a\u4e86\u505a\u8fd9\u4e2a\uff1f\u201d\u8bb8\u9e3f\u4f2f\u4f4e\u5934\u601d\u91cf\uff0c\u53c8\u62ac\u5934\u770b\u7740\u5468\u590d\uff0c\u201c\u4f60\u4eca\u6253\u7b97\u51e0\u5e74\u80fd\u6709\u6210\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4eca\u5e74\u5c31\u8981\u6295\u4e24\u4e2a\u4ebf\u8fdb\u53bb\uff0c\u8ba1\u5212\u662f\u660e\u5e74\u5e95\u8981\u8425\u9020\u4e00\u767e\u4e07\u4ea9\u2026\u2026\u201d</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u6025\u662f\u6025\u4e86\u4e9b\uff0c\u770b\u5f97\u51fa\uff0c\u65b0\u5149\u7eb8\u4e1a\u7684\u8fd9\u4e2a\u9879\u76ee\u53ef\u662f\u8ddf\u7701\u91cc\u7684\u90a3\u7bc7\u53d1\u6587\u4e92\u4e3a\u8865\u5145\u554a\uff0c\u5c0f\u6c5f\u4e2d\u4e0a\u6e38\u4ec0\u4e48\u72b6\u51b5\u6211\u6ca1\u6709\u4eb2\u773c\u89c1\u8fc7\uff0c\u53ef\u80fd\u771f\u5230\u4e86\u975e\u5e38\u7d27\u8feb\u7684\u65f6\u5019\uff0c\u5f20\u606a\u5012\u4e0d\u662f\u4ece\u4f01\u4e1a\u7684\u89d2\u5ea6\u53bb\u770b\u8fd9\u4e2a\u95ee\u9898\u2026\u2026\u5f20\u606a\u4e0e\u4ed6\u8001\u5b50\u3001\u4e0e\u5510\u5b66\u8c26\u8bf4\u8fc7\u8fd9\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u5468\u590d\u82e6\u7b11\u7740\u8bf4\uff1a\u201c\u53ef\u80fd\u8fd8\u6ca1\u6709\uff0c\u524d\u671f\u7684\u8425\u6797\u8fd8\u662f\u5176\u6b21\uff0c\u540e\u671f\u7684\u5236\u6d46\u9879\u76ee\u624d\u662f\u91cd\u5934\u620f\uff0c\u5927\u7ea6\u6709\u56db\u4e94\u5341\u4ebf\u5de6\u53f3\u7684\u6295\u8d44\uff0c\u8981\u6709\u53ef\u80fd\uff0c\u5510\u5b66\u8c26\u600e\u4e48\u4f1a\u540c\u610f\u8fd9\u4e2a\u5927\u7684\u6295\u8d44\u843d\u5230\u522b\u4eba\u5634\u91cc\uff1f\u6211\u60f3\u5f20\u606a\u8fd8\u6ca1\u6709\u8ddf\u5510\u5b66\u8c26\u63d0\u53ca\u8fd9\u4e8b\uff0c\u4e0d\u7136\u4e0d\u4f1a\u6ca1\u6709\u4e00\u70b9\u52a8\u9759\u3002\u201d</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u62ff\u516d\u5341\u4e07\u5e74\u85aa\u8bf7\u6765\u7684\u90a3\u4e2a\u4eba\uff0c\u5bf9\u8fd9\u4e2a\u9879\u76ee\u6709\u4ec0\u4e48\u610f\u89c1\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u4eba\u53eb\u5b8b\u5fd7\u4f51\uff0c\u6c34\u5e73\u5f88\u9ad8\uff0c\u5728\u56fd\u5185\u662f\u6700\u65e9\u7814\u7a76\u901f\u4e30\u6797\u9057\u4f20\u79cd\u690d\u7684\u90a3\u6279\u4eba\uff0c\u516b\u516d\u5e74\u51fa\u56fd\u7559\u5b66\uff0c\u540e\u6765\u7559\u5728\u91d1\u4f70\u5229\u7eb8\u4e1a\u4efb\u804c\uff0c\u4e0d\u8fc7\u4e0d\u662f\u518d\u505a\u7814\u7a76\uff0c\u800c\u662f\u8f6c\u884c\u505a\u4e86\u7ba1\u7406\uff0c\u5bf9\u4e8e\u641e\u6797\u7eb8\u4e00\u4f53\u5316\uff0c\u4ed6\u5012\u662f\u652f\u6301\u5148\u5efa\u539f\u6599\u6797\u57fa\u5730\u518d\u5efa\u5236\u6d46\u5382\uff0c\u82e5\u662f\u5148\u5efa\u5236\u6d46\u5382\u662f\u5b8c\u5168\u6ca1\u6709\u793e\u4f1a\u8d23\u4efb\u611f\u7684\u6295\u8d44\u3002\u4e0d\u8fc7\u554a\uff0c\u901f\u4e30\u6797\u4ece\u8425\u9020\u5230\u53ef\u4ee5\u780d\u4f10\uff0c\u5c11\u8bf4\u4e5f\u8981\u4e94\u516d\u5e74\uff0c\u8fd9\u4e48\u5927\u7684\u4e00\u7b14\u6295\u8d44\uff0c\u4e94\u516d\u5e74\u4e4b\u540e\u624d\u51fa\u6548\u76ca\uff0c\u76ee\u524d\u9664\u4e86\u9526\u6e56\uff0c\u56fd\u5185\u8fd8\u6709\u6ca1\u6709\u4eba\u4f1a\u8fd9\u4e48\u505a\u2026\u2026\u201d</p><p>\u3000\u3000\u8bb8\u9e3f\u4f2f\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u6709\u4e9b\u4eba\u6025\u5207\u7684\uff0c\u6068\u4e0d\u5f97\u4e09\u56db\u5e74\u5c31\u6536\u56de\u6295\u8d44\u6210\u672c\uff0c\u4e94\u516d\u5e74\u4e4b\u540e\u624d\u51fa\u6548\u76ca\uff0c\u7edd\u5927\u591a\u6570\u7684\u6295\u8d44\u4eba\u90fd\u7b49\u4e0d\u53ca\u3002\u6295\u8d44\u8fd9\u4e2a\u9879\u76ee\uff0c\u771f\u8981\u975e\u5e38\u5927\u7684\u9b44\u529b\u624d\u884c\u2026\u2026\u201d</p></article>"}}}]);
