(window.webpackJsonp=window.webpackJsonp||[]).push([[327],{350:function(p){p.exports={chapterTitle:null,title:"\u7b2c327\u7ae0 \u4e00\u63fd\u5b50\u89e3\u51b3\u65b9\u6848",content:"<article><h2>\u7b2c327\u7ae0 \u4e00\u63fd\u5b50\u89e3\u51b3\u65b9\u6848</h2><p>\u3000\u3000\u4f4d\u4e8e\u57ce\u5357\u533a\u5357\u90ca\u3001\u6bd7\u90bb\u5f00\u53d1\u533a\u7684\u5357\u90ca\u5de5\u4e1a\u56ed\u4e24\u5e74\u524d\u8fd8\u53ea\u6709\u4e24\u4e09\u5bb6\u5382\uff0c\u4e5d\u4e94\u5e74\u521d\uff0c\u57ce\u5357\u533a\u5c31\u5c06\u8fd9\u91cc\u66f4\u6539\u4e3a\u9020\u7eb8\u5de5\u4e1a\u56ed\u533a\uff0c\u9526\u6e56\u6240\u627f\u5305\u7684\u9020\u7eb8\u5382\u9646\u7eed\u8fc1\u5165\u5de5\u4e1a\u56ed\uff0c\u7531\u9526\u6e56\u6295\u8d44\u5efa\u8bbe\u516c\u7528\u7cfb\u7edf\u3001\u4ed3\u50a8\u7269\u6d41\u4e0e\u6c61\u6c34\u5904\u7406\u7cfb\u7edf\uff0c\u800c\u5404\u5bb6\u5382\u53ea\u8d1f\u8d23\u627f\u5efa\u751f\u4ea7\u7ebf\uff0c\u5f62\u6210\u5b8c\u6574\u7684\u751f\u4ea7\u4f53\u7cfb\uff0c\u6240\u7528\u8d44\u91d1\u5927\u591a\u6765\u81ea\u4e8e\u56fd\u5bb6\u5bf9\u9020\u7eb8\u4f01\u4e1a\u6280\u6539\u9879\u76ee\u7684\u6276\u6301\u8d44\u91d1\u3002</p><p>\u3000\u3000\u65b0\u5149\u7eb8\u4e1a\u96c6\u56e2\u7b79\u5efa\u4e4b\u540e\uff0c\u5f20\u606a\u63a8\u7ffb\u4e4b\u524d\u4e0e\u6b63\u6cf0\u96c6\u56e2\u7ea6\u5b9a\u7684\u5e76\u8d2d\u8fdb\u5ea6\u8868\uff0c\u6253\u7b97\u4e00\u63fd\u5b50\u5e76\u8d2d\u6240\u6709\u7684\u9020\u7eb8\u5382\u3002</p><p>\u3000\u3000\u4e0e\u5404\u5bb6\u9020\u7eb8\u5382\u5546\u8c08\u7684\u5e76\u8d2d\u4ef7\u90fd\u5305\u62ec\u4e24\u90e8\u5206\uff0c\u4e00\u4e3a\u51c0\u8d44\u4ea7\u90e8\u5206\uff0c\u7531\u4e8e\u65b0\u5382\u591a\u4e3a\u8d37\u6b3e\u7b79\u5efa\uff0c\u800c\u8001\u5382\u7684\u571f\u5730\u3001\u5382\u623f\u7b49\u8d44\u4ea7\u4e0d\u5728\u5e76\u8d2d\u8303\u56f4\u4e4b\u5185\uff0c\u5305\u62ec\u65b0\u5382\u9020\u7eb8\u5382\u65b0\u5382\u5728\u5185\u7684\u5341\u4e8c\u5bb6\u9020\u7eb8\u5382\u7684\u51c0\u8d44\u4ea7\u4ec5\u4e3a\u516d\u5343\u4e07\uff1b\u4e8c\u4e3a\u9526\u6e56\u4e3a\u5e76\u8d2d\u6240\u652f\u4ed8\u7684\u6ea2\u4ef7\u90e8\u5206\uff0c\u5305\u62ec\u5bf9\u539f\u9020\u7eb8\u5382\u6280\u672f\u50a8\u5907\u3001\u653f\u7b56\u652f\u6301\u3001\u7ea6\u5b9a\u7684\u7a0e\u6536\u4f18\u60e0\u7b49\u65e0\u5f62\u4ef7\u503c\u6240\u652f\u4ed8\u7684\u6ea2\u4ef7\uff0c\u8fd9\u4e00\u90e8\u5206\u6b63\u8bf7\u56fd\u5185\u4f1a\u8ba1\u4e8b\u52a1\u6240\u8fdb\u884c\u516c\u6b63\u7684\u8bc4\u4f30\uff0c\u6309\u7167\u65b0\u5149\u5382\u7684\u6807\u51c6\u8ba1\u7b97\uff0c\u5dee\u4e0d\u591a\u4e00\u5171\u8981\u652f\u4ed8\u4e00\u4ebf\u56db\u5343\u4e07\u7684\u6ea2\u4ef7\u3002</p><p>\u3000\u3000\u4e00\u63fd\u5b50\u89e3\u51b3\u65b9\u6848\u5c31\u662f\uff0c\u9526\u6e56\u5411\u5305\u62ec\u57ce\u5357\u533a\u653f\u5e9c\u5728\u5185\u7684\u5730\u65b9\u8d22\u653f\uff0c\u4e00\u5e74\u5185\u652f\u4ed8\u6e05\u4e0e\u5e76\u8d2d\u8d44\u4ea7\u51c0\u503c\u76f8\u7b49\u7684\u516d\u5343\u4e07\uff0c\u6ea2\u4ef7\u90e8\u5206\u7531\u8f6c\u4e3a\u5341\u5e74\u671f\u7684\u516c\u53f8\u503a\uff0c\u4ee5\u6b64\u4e00\u4e3e\u5f7b\u5e95\u89e3\u51b3\u4ea7\u6743\u4e0a\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u8fd9\u5341\u4e8c\u5bb6\u9020\u7eb8\u5382\u591a\u4e3a\u533a\u5c5e\u4f01\u4e1a\uff0c\u6700\u7ec8\u7684\u5ba1\u6279\u6743\u5728\u5e02\u91cc\uff0c\u4e00\u5bb6\u4e00\u5bb6\u7684\u5e76\u8d2d\uff0c\u4e0d\u4f1a\u5f15\u4eba\u6ce8\u610f\uff0c\u5c31\u50cf\u6536\u8d2d\u65b0\u5149\u5382\u90a3\u6837\uff0c\u53ef\u662f\u8981\u4e00\u4e0b\u5b50\u6536\u8d2d\u5176\u4ed6\u5341\u4e00\u5bb6\u5382\uff0c\u800c\u4e14\u65b0\u5149\u7eb8\u4e1a\u96c6\u56e2\u8fd9\u6bb5\u65f6\u6b63\u7092\u4f5c\u516d\u5341\u4e07\u5e74\u85aa\u7684\u4e8b\u60c5\uff0c\u7701\u5185\u6709\u5f88\u591a\u5a92\u4f53\u90fd\u76ef\u8fc7\u6765\u3002\u65b0\u5149\u7eb8\u4e1a\u96c6\u56e2\u63d0\u51fa\u4e00\u63fd\u5b50\u89e3\u51b3\u65b9\u6848\uff0c\u5510\u5b66\u8c26\u4e5f\u53ea\u80fd\u5c06\u8fd9\u4e8b\u62ff\u5230\u5e02\u5e38\u59d4\u4f1a\u8bae\u4e0a\u8ba8\u8bba\u3002</p><p>\u3000\u3000\u5468\u590d\u5728\u516b\u9526\u73cd\u80b4\u5c45\u4e0e\u8bb8\u9e3f\u4f2f\u8c08\u8bba\u6797\u7eb8\u4e00\u4f53\u5316\u9879\u76ee\u7684\u65f6\u5019\uff0c\u6587\u5c71\u996d\u5e97\u516b\u53f7\u697c\u4e8c\u697c\u7684\u4f1a\u8bae\u5ba4\u91cc\uff0c\u4f1a\u8bae\u5df2\u7ecf\u8fdb\u884c\u4e86\u597d\u51e0\u4e2a\u5c0f\u65f6\u3002\u8fd9\u6b21\u7684\u4f1a\u8bae\uff0c\u5e02\u59d4\u4e5d\u540d\u5e38\u59d4\u4e00\u4e2a\u90fd\u4e0d\u843d\uff0c\u5b8b\u57f9\u660e\u3001\u5f20\u77e5\u884c\u4ee5\u53ca\u9526\u6e56\u63a7\u80a1\u603b\u88c1\u8521\u88d5\u5a1f\u3001\u65b0\u5149\u7eb8\u4e1a\u96c6\u56e2\u603b\u88c1\u5468\u6e38\uff0c\u8fd8\u6709\u521a\u521a\u52a0\u5165\u65b0\u5149\u7eb8\u4e1a\u624d\u534a\u4e2a\u6708\u65f6\u95f4\u7684\u526f\u603b\u88c1\u5b8b\u5fd7\u4f51\u5217\u5e2d\u3002</p><p>\u3000\u3000\u6bcf\u4e2a\u4eba\u7684\u9762\u524d\u90fd\u6446\u7740\u4e09\u4efd\u6587\u4ef6\uff0c\u5176\u4e00\u662f\u91cd\u6574\u540e\u9526\u6e56\u63a7\u80a1\u4e0e\u4e0b\u5c5e\u65b0\u5149\u7eb8\u4e1a\u3001\u7231\u8fbe\u7535\u5b50\u7684\u57fa\u672c\u8d22\u52a1\u60c5\u51b5\u4e0e\u80a1\u6743\u7ed3\u6784\u7684\u4ecb\u7ecd\uff0c\u7b2c\u4e8c\u4efd\u5c31\u662f\u5f20\u606a\u901a\u8fc7\u65b0\u5149\u7eb8\u4e1a\u63d0\u51fa\u7684\u4e00\u63fd\u5b50\u89e3\u51b3\u65b9\u6848\uff0c\u5176\u4e8c\u5c31\u662f\u5468\u6e38\u7ec4\u7ec7\u4eba\u624b\u62df\u5b9a\u3001\u540e\u7531\u5b8b\u5fd7\u4f51\u4fee\u6539\u7684\u6797\u7eb8\u4e00\u4f53\u5316\u9879\u76ee\u4e66\u3002</p><p>\u3000\u3000\u8ba9\u5927\u5bb6\u96be\u4ee5\u63a5\u53d7\u7684\uff0c\u4e00\u4e2a\u5341\u516b\u5c81\u7684\u5c11\u5e74\uff0c\u5e02\u653f\u5e9c\u79d8\u4e66\u957f\u5f20\u77e5\u884c\u7684\u516c\u5b50\uff0c\u7adf\u7136\u63e1\u6709\u4f17\u4eba\u96be\u4ee5\u60f3\u8c61\u7684\u8d22\u5bcc\uff0c\u800c\u4e14\u8fd9\u4efd\u8d22\u5bcc\u5374\u662f\u5728\u8fd1\u4e24\u5e74\u65f6\u95f4\u91cc\u7d2f\u79ef\u8d77\u6765\u7684\u3002</p><p>\u3000\u3000\u770b\u5230\u6587\u4ef6\u4e4b\u521d\uff0c\u5468\u5bcc\u660e\u5c31\u8bf4\u4e86\u4e24\u53e5\uff1a\u201c\u5c31\u77e5\u9053\u5f20\u79d8\u4e66\u957f\u5bb6\u7684\u5c0f\u5b50\u4e0d\u7b80\u5355\uff0c\u4e0d\u7b80\u5355\u554a\u2026\u2026\u201d\u63a5\u4e0b\u6765\uff0c\u4ed6\u5c31\u5f88\u5c11\u8bf4\u8bdd\uff0c\u5173\u4e8e\u5f20\u606a\u53ef\u80fd\u662f\u5510\u5b66\u8c26\u3001\u5f90\u5b66\u5e73\u4e00\u7cfb\u6700\u6838\u5fc3\u7684\u4eba\u7269\u4e4b\u4e00\uff0c\u65e9\u5728\u8c22\u5251\u5357\u4e0a\u56de\u6765\u6d77\u5dde\u62dc\u8bbf\u4ed6\u65f6\uff0c\u5c31\u542c\u8c22\u5251\u5357\u63d0\u8d77\u4e86\u2026\u2026\u53bb\u5e74\u5341\u4e00\u6708\u521d\uff0c\u7231\u8fbe\u7535\u5b50\u7ade\u593a\u592e\u89c6\u6807\u738b\u7684\u5f53\u665a\u6d88\u606f\u4f20\u56de\u6d77\uff0c\u8c22\u665a\u6674\u5c31\u4ee3\u8868\u7231\u8fbe\u7535\u5b50\u5411\u5e02\u91cc\u901a\u62a5\u4e86\u4e00\u4e9b\u60c5\u51b5\uff0c\u5468\u5bcc\u660e\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u6570\uff0c\u4f46\u662f\u4e5f\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u4f1a\u662f\u7231\u8fbe\u7535\u5b50\u7684\u4e3b\u8981\u521b\u59cb\u4eba\uff0c\u6b64\u65f6\u53c8\u6210\u4e3a\u9526\u6e56\u7684\u5355\u4e00\u80a1\u4e1c\u3002</p><p>\u3000\u3000\u675c\u5c0f\u5c71\u3001\u91d1\u56fd\u6d77\u77e5\u9053\u7684\u60c5\u51b5\u66f4\u591a\u4e00\u4e9b\uff0c\u4e0d\u8fc7\u4e5f\u662f\u8fd9\u6b21\u624d\u77e5\u9053\u8fd9\u4e48\u5177\u4f53\u3002</p><p>\u3000\u3000\u4f1a\u8bae\u5ba4\u91cc\u7684\u6c14\u6c1b\u6709\u4e9b\u51dd\u91cd\uff0c\u8521\u88d5\u5a1f\u3001\u5468\u6e38\u4e0e\u5b8b\u5fd7\u4f51\u5750\u5728\u91cc\u9762\uff0c\u611f\u89c9\u4e0a\u5e76\u4e0d\u8f7b\u677e\u3002\u5468\u6e38\u5fc3\u91cc\u4e0d\u7531\u7684\u60f3\u5f20\u606a\u8fd9\u65f6\u5019\u5728\u505a\u4ec0\u4e48\uff1f\u62ac\u5934\u770b\u4e86\u4e00\u773c\u5468\u5bcc\u660e\uff0c\u4ed6\u8138\u4e0a\u7684\u795e\u60c5\u8fd8\u7b97\u5e73\u9759\uff0c\u770b\u4e0d\u51fa\u4ed6\u5fc3\u91cc\u5728\u60f3\u4ec0\u4e48\uff0c\u60f3\u5fc5\u4ee5\u9526\u5929\u4eca\u5929\u7684\u89c4\u6a21\uff0c\u5468\u5bcc\u660e\u5373\u4f7f\u60f3\u7ed9\u5c0f\u978b\u7a7f\uff0c\u4e5f\u8981\u614e\u91cd\u7684\u8003\u8651\u9526\u6e56\u7684\u53cd\u5f39\u4e86\u3002</p><p>\u3000\u3000\u5468\u5bcc\u660e\u4e0d\u8bf4\u8bdd\uff0c\u5176\u4ed6\u4eba\u4e5f\u90fd\u6c89\u9ed8\u7740\uff0c\u6587\u4ef6\u90fd\u4e0d\u539a\uff0c\u5468\u6e38\u3001\u5b8b\u5fd7\u4f51\u53ea\u89e3\u8bf4\u4e86\u4e0d\u5230\u4e00\u4e2a\u5c0f\u65f6\u7684\u65f6\u95f4\uff0c\u5269\u4e0b\u7684\u65f6\u95f4\uff0c\u5927\u5bb6\u90fd\u5728\u7ffb\u770b\u624b\u91cc\u7684\u6587\u4ef6\uff0c\u8c01\u4e5f\u6ca1\u60f3\u7740\u8981\u4e3b\u52a8\u53d1\u8a00\uff0c\u603b\u8981\u5c06\u8fd9\u4e9b\u6d88\u606f\u597d\u597d\u6d88\u5316\u4e00\u4e0b\u624d\u884c\u3002</p><p>\u3000\u3000\u5468\u5bcc\u660e\u5185\u5fc3\u91cc\u6ce2\u6f9c\u4e0d\u5b9a\uff0c\u60f3\u8d77\u597d\u591a\u4ee5\u5f80\u6ce8\u610f\u4e0d\u5230\u7684\u7ec6\u8282\uff0c\u8981\u8bf4\u5bf9\u5f20\u606a\u6709\u591a\u5c11\u7684\u597d\u611f\uff0c\u90a3\u7edd\u6ca1\u6709\u53ef\u80fd\uff0c\u611f\u89c9\u5f20\u606a\u6709\u7740\u8fd9\u4e48\u5389\u5bb3\u7684\u624b\u6bb5\uff0c\u8fd8\u662f\u8ba9\u4eba\u8be7\u5f02\uff0c\u7adf\u7136\u8fd8\u6709\u8fd9\u6837\u8ba9\u4e16\u4eba\u77a9\u76ee\u7684\u6210\u5c31\u3002\u4eba\u603b\u662f\u6709\u5dee\u8ddd\u7684\uff0c\u6ca1\u6709\u5dee\u8ddd\uff0c\u4eba\u7684\u793e\u4f1a\u5730\u4f4d\u5c31\u6ca1\u6709\u8fd9\u4e48\u5927\u7684\u5dee\u5f02\u6027\u4e86\uff1b\u53ea\u662f\u5f20\u606a\u8ba9\u522b\u4eba\u5dee\u8ddd\u4ed6\u592a\u591a\u4e86\u3002</p><p>\u3000\u3000\u201c\u597d\u5427\uff0c\u6587\u4ef6\u5927\u5bb6\u90fd\u770b\u4e86\u5f88\u957f\u65f6\u95f4\u4e86\uff0c\u60f3\u5fc5\u90fd\u8ba4\u771f\u94bb\u7814\u4e86\u597d\u4e9b\u904d\uff0c\u4e0d\u7ba1\u6709\u6ca1\u6709\u94bb\u7814\u900f\uff0c\u5927\u5bb6\u90fd\u53d1\u8868\u4e00\u4e0b\u5404\u81ea\u7684\u89c2\u70b9\u2026\u2026\u201d</p><p>\u3000\u3000\u4e09\u4efd\u6587\u4ef6\uff0c\u5e02\u91cc\u80fd\u8ba4\u771f\u8ba8\u8bba\u7684\u4e5f\u53ea\u6709\u7b2c\u4e8c\u4efd\uff0c\u5f20\u606a\u901a\u8fc7\u65b0\u5149\u7eb8\u4e1a\u63d0\u51fa\u7684\u4e00\u63fd\u5b50\u89e3\u51b3\u65b9\u6848\u3002</p><p>\u3000\u3000\u5468\u5bcc\u660e\u89c1\u6ca1\u6709\u4eba\u8981\u8bf4\u8bdd\u7684\u610f\u601d\uff0c\u4fa7\u5934\u5bf9\u5510\u5b66\u8c26\u8bf4\uff1a\u201c\u5b66\u8c26\uff0c\u4f60\u662f\u5e02\u957f\uff0c\u4f60\u5148\u53d1\u8868\u610f\u89c1\u2026\u2026\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u76f4\u4e86\u76f4\u8eab\u5b50\uff0c\u5750\u4e86\u8fd9\u4e48\u4e45\u4e0d\u8bf4\u8bdd\uff0c\u4eba\u8fd8\u662f\u611f\u89c9\u6709\u4e9b\u4e4f\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u51e0\u5e74\u6765\uff0c\u8d8a\u6765\u8d8a\u591a\u7684\u4eba\u610f\u8bc6\u653f\u5e9c\u5f88\u96be\u80dc\u4efb\u4f01\u4e1a\u7ba1\u7406\u8005\u7684\u89d2\u8272\uff0c\u56fd\u6709\u4f01\u4e1a\u4f53\u5236\u6539\u9769\u662f\u5927\u52bf\u6240\u8d8b\uff0c\u73b0\u5728\u5404\u5730\u90fd\u5728\u6478\u7d22\u4e0d\u540c\u7684\u9053\u8def\uff0c\u6d77\u5dde\u63a7\u80a1\u8d70\u7684\u662f\u4e00\u6761\u8def\u5b50\uff0c\u65b0\u5149\u7eb8\u4e1a\u63d0\u51fa\u7684\u4e00\u63fd\u5b50\u89e3\u51b3\u65b9\u6848\u4e5f\u662f\u4e00\u6761\u8def\u5b50\uff0c\u53ea\u8981\u6709\u53ef\u80fd\u66f4\u597d\u7684\u4ece\u6839\u672c\u6027\u4e0a\u89e3\u51b3\u56fd\u6709\u4f01\u4e1a\u5df2\u7ecf\u66b4\u9732\u51fa\u6765\u7684\u95ee\u9898\uff0c\u5c31\u503c\u5f97\u5c1d\u8bd5\u2026\u2026\u201d</p><p>\u3000\u3000\u9526\u6e56\u627f\u5305\u7ecf\u8425\u9020\u7eb8\u4e1a\u5382\uff0c\u4e89\u593a\u56fd\u5bb6\u6276\u6301\u8d44\u91d1\u8fdb\u884c\u6280\u6539\uff0c\u5174\u5efa\u65b0\u5382\uff0c\u6295\u8d44\u6c61\u6c34\u5904\u7406\u5382\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u7f13\u89e3\u4e86\u6d77\u5dde\u5e02\u6240\u9762\u4e34\u7684\u73af\u5883\u538b\u529b\uff0c\u8fd9\u4e00\u63fd\u5b50\u65b9\u6848\u53c8\u5e72\u51c0\u5229\u7d22\u7684\u89e3\u51b3\u9057\u7559\u4e0b\u6765\u7684\u4ea7\u6743\u95ee\u9898\u3002</p><p>\u3000\u3000\u5e02\u59d4\u526f\u4e66\u8bb0\u9646\u6ea2\u5149\u89c1\u5510\u5b66\u8c26\u8bf4\u5230\u8fd9\u91cc\u5c31\u628a\u76ee\u5149\u8f6c\u5411\u522b\u4eba\uff0c\u60f3\u5fc5\u60f3\u8ba9\u63a5\u4e0b\u6765\u7684\u8bdd\u7531\u522b\u4eba\u4ee3\u4ed6\u8bf4\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u4e00\u4e2a\u65b9\u6848\uff0c\u5f88\u96be\u8ba9\u4eba\u62d2\u7edd\uff0c\u4f46\u662f\u5b9e\u65bd\u8fd9\u4e00\u65b9\u6848\uff0c\u65b0\u5149\u7eb8\u4e1a\u96c6\u56e2\u5c06\u8d70\u9ad8\u8d1f\u503a\u8fd0\u8425\u8fd9\u6761\u8def\uff0c\u8fd9\u8ddf\u6587\u4ef6\u4e0a\u4ecb\u7ecd\u7684\u7231\u8fbe\u7535\u5b50\u5f53\u521d\u7684\u8fd0\u8425\u6a21\u5f0f\u4e00\u4e9b\uff0c\u5c31\u7b97\u9526\u6e56\u5411\u65b0\u5149\u7eb8\u4e1a\u6ce8\u5165\u7b2c\u4e00\u7b14\u516d\u5343\u4e07\u7684\u6536\u8d2d\u94b1\u6b3e\uff0c\u65b0\u5149\u7eb8\u4e1a\u7684\u51c0\u8d44\u4ea7\u4e5f\u53ea\u5728\u4e00\u4ebf\u4e24\u4e09\u5343\u4e07\u5de6\u53f3\uff0c\u4f46\u662f\u8d1f\u8d23\u5374\u9ad8\u8fbe\u56db\u4ebf\u4e94\u5343\u4e07\u4e4b\u591a\uff0c\u5176\u4e2d\u5730\u65b9\u653f\u5e9c\u624b\u91cc\u63e1\u6709\u4e00\u4ebf\u56db\u5343\u4e07\u7684\u516c\u53f8\u503a\uff0c\u9ad8\u8fbe\u4e09\u4ebf\u4e00\u5343\u4e07\u7684\u56fd\u5bb6\u9020\u7eb8\u5de5\u4e1a\u6280\u6539\u6276\u6301\u8d37\u6b3e\uff0c\u5c06\u8fd180\uff05\u7684\u8d1f\u503a\u7387\uff0c\u65b0\u5149\u7eb8\u4e1a\u8fd8\u80fd\u50cf\u7231\u8fbe\u7535\u5b50\u90a3\u4e48\u5e78\u8fd0\uff0c\u6b63\u597d\u8d76\u4e0a\u884c\u4e1a\u7684\u597d\u65f6\u673a\uff1f\u201d</p><p>\u3000\u3000\u5b9e\u65bd\u8fd9\u4e00\u65b9\u6848\uff0c\u65b0\u5149\u7eb8\u4e1a\u9ad8\u8d1f\u503a\u8fd0\u8425\u4e2d\u5b58\u5728\u7684\u8d22\u52a1\u98ce\u9669\uff0c\u4f1a\u7ed9\u5730\u65b9\u653f\u5e9c\u624b\u91cc\u7684\u90a3\u90e8\u5206\u516c\u53f8\u503a\u5e26\u6765\u4e00\u5b9a\u7684\u98ce\u9669\u3002</p><p>\u3000\u3000\u9646\u6ea2\u5149\u8bdd\u91cc\u7684\u610f\u601d\uff0c\u5927\u5bb6\u90fd\u660e\u767d\uff0c\u53ea\u662f\u6ca1\u6709\u4eba\u63a5\u4ed6\u7684\u8bdd\u3002</p><p>\u3000\u3000\u4e5d\u516d\u5e74\u56fd\u52a1\u9662\u5bf9\u56fd\u6709\u4e2d\u5c0f\u4f01\u4e1a\u8f6c\u8ba9\u5728\u8d44\u4ea7\u4f30\u503c\u65b9\u9762\u6709\u4e00\u6761\u786c\u6027\u89c4\u5b9a\uff0c\u5e76\u8d2d\u4ef7\u4e0d\u5f97\u4f4e\u4e8e\u8f6c\u8ba9\u56fd\u4f01\u7684\u51c0\u8d44\u4ea7\u503c\uff0c\u9526\u6e56\u5e76\u8d2d\u8fd9\u4e9b\u9020\u7eb8\u4f01\u4e1a\uff0c\u9664\u4e86\u5148\u671f\u652f\u4ed8\u7b49\u540c\u4e8e\u51c0\u8d44\u4ea7\u503c\u7684\u516d\u5343\u4e07\u73b0\u91d1\u4e4b\u5916\uff0c\u8fd8\u5411\u5730\u65b9\u8d22\u653f\u652f\u4ed8\u4e00\u4ebf\u56db\u5343\u4e07\u7684\u516c\u53f8\u503a\u4f5c\u4e3a\u5e76\u8d2d\u6ea2\u4ef7\uff0c\u5728\u5458\u5de5\u5b89\u7f6e\u95ee\u9898\u4e0a\u4e5f\u4e00\u70b9\u4e0d\u7ed9\u653f\u5e9c\u7559\u4e0b\u9057\u60a3\uff0c\u5373\u4f7f\u516c\u53f8\u503a\u5b58\u5728\u4e00\u5b9a\u7684\u98ce\u9669\uff0c\u4f46\u662f\u8fd9\u770b\u8d77\u6765\u4f9d\u65e7\u662f\u5728\u6574\u4e2a\u4e5d\u5341\u5e74\u4ee3\uff0c\u6d77\u5dde\u5e02\u6240\u9047\u5230\u4e86\u4e00\u680b\u6700\u8f7b\u677e\u5408\u610f\u7684\u4e70\u5356\u3002</p><p>\u3000\u3000\u675c\u5c0f\u5c71\u8bf4\u9053\uff1a\u201c\u6211\u770b\u53ea\u8981\u524d\u9762\u7684\u516d\u5343\u4e07\u62ff\u5230\u624b\u5c31\u4e0d\u80fd\u7b97\u4e8f\uff0c\u518d\u8bf4\u90a3\u4e00\u4ebf\u56db\u5343\u4e07\u7684\u516c\u53f8\u503a\uff0c\u65b0\u5149\u7eb8\u4e1a\u4e5f\u662f\u8981\u652f\u4ed8\u5e74\u606f\u7684\uff0c\u4e00\u5e74\u4e00\u5343\u591a\u4e07\u7684\u5e74\u606f\u8fd8\u662f\u5f88\u53ef\u89c2\u7684\uff0c\u5373\u4f7f\u65b0\u5149\u7eb8\u4e1a\u7ecf\u8425\u4e0a\u6709\u4ec0\u4e48\u95ea\u5931\uff0c\u516c\u53f8\u503a\u4e5f\u662f\u4f18\u5148\u4fdd\u8bc1\u507f\u8fd8\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u9646\u6ea2\u5149\u7684\u76ee\u5149\u843d\u5728\u7b2c\u4e09\u4efd\u6587\u4ef6\u4e0a\uff0c\u65b0\u5149\u7eb8\u4e1a\u7adf\u7136\u629b\u51fa\u516d\u5341\u4ebf\u7684\u6295\u8d44\u8ba1\u5212\uff0c\u8bf4\u9053\uff1a\u201c\u516c\u53f8\u503a\u7684\u5f62\u5f0f\uff0c\u4e5f\u662f\u4e00\u79cd\u65b0\u7684\u5c1d\u8bd5\uff0c\u6211\u4eec\u90fd\u4e0d\u5e94\u8be5\u56fa\u6b65\u81ea\u5c01\uff0c\u8981\u662f\u7b2c\u4e00\u7b14\u516d\u5343\u4e07\u7684\u6b3e\u5b50\u80fd\u5728\u5b9e\u65bd\u6797\u7eb8\u4e00\u4f53\u5316\u9879\u76ee\u524d\u4ed8\u6e05\uff0c\u8fd9\u4e00\u63fd\u5b50\u65b9\u6848\uff0c\u5927\u6982\u5c31\u6311\u4e0d\u51fa\u4ec0\u4e48\u95ee\u9898\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u76ee\u524d\u7684\u7231\u8fbe\u7535\u5b50\u662f\u8d85\u7ea7\u73b0\u91d1\u5976\u725b\u4e0d\u5047\uff0c\u4f46\u662f\u6324\u5976\u4e5f\u9700\u8981\u65f6\u95f4\uff0c\u7231\u8fbe\u7535\u5b50\u81ea\u8eab\u7684\u53d1\u5c55\u5c31\u8981\u7528\u53bb\u5927\u534a\u7684\u8d44\u91d1\uff0c\u65b0\u5149\u7eb8\u4e1a\u4e5f\u8981\u589e\u52a0\u4e00\u90e8\u5206\u7684\u6d41\u52a8\u8d44\u91d1\uff0c\u6797\u7eb8\u4e00\u4f53\u5316\u4e00\u7ecf\u542f\u52a8\uff0c\u5c31\u8981\u6301\u7eed\u4e0d\u65ad\u62ff\u94b1\u51fa\u53bb\uff0c\u8c22\u665a\u6674\u7b79\u529e\u79c1\u7acb\u5b66\u6821\u4e5f\u8981\u6e90\u6e90\u4e0d\u65ad\u7684\u62bd\u53d6\u8d44\u91d1\uff0c\u8fd9\u4e48\u591a\u4e8b\u60c5\u52a0\u5728\u4e00\u8d77\uff0c\u5373\u4f7f\u7231\u8fbe\u7535\u5b50\u662f\u5934\u8d85\u7ea7\u73b0\u91d1\u5976\u725b\uff0c\u4e5f\u4f1a\u611f\u5230\u83ab\u5927\u7684\u538b\u529b\u3002</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u770b\u4e86\u770b\u8521\u88d5\u5a1f\uff0c\u53c8\u770b\u4e86\u770b\u5f20\u77e5\u884c\uff0c\u5f20\u606a\u7f29\u7740\u8111\u888b\u4e0d\u9732\u9762\uff0c\u8521\u88d5\u5a1f\u662f\u5f20\u606a\u7684\u6b63\u5f0f\u4ee3\u8868\uff0c\u4e0d\u8fc7\u5927\u5bb6\u53c8\u89c9\u5f97\u5f20\u77e5\u884c\u6216\u8bb8\u66f4\u80fd\u4ee3\u8868\u4ed6\u513f\u5b50\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u5bf9\u4f17\u4eba\u671b\u6765\u7684\u76ee\u5149\u89c6\u82e5\u65e0\u7779\uff0c\u773c\u5e18\u4f4e\u5782\u7740\uff0c\u5e73\u9759\u7684\u6ce8\u89c6\u7740\u684c\u4e0a\u7684\u6587\u4ef6\u3002\u5f20\u606a\u540d\u4e0b\u7684\u8d22\u5bcc\u9732\u51fa\u6c34\u9762\u8fd8\u771f\u5413\u4e86\u4eba\u4e00\u8df3\uff0c\u6362\u4f5c\u522b\u4eba\uff0c\u5bf9\u8fd9\u7b14\u8d22\u5bcc\u4f1a\u4e0d\u4f1a\u6240\u6258\u975e\u4eba\u90fd\u4f1a\u6709\u7740\u8bb8\u591a\u7684\u5fe7\u8651\uff0c\u5f20\u77e5\u884c\u4e5f\u4e0d\u4f8b\u5916\uff0c\u4f46\u662f\u4ed6\u7406\u667a\u7684\u514b\u5236\u7740\u4e0d\u53bb\u95ee\u516c\u53f8\u7684\u4e8b\u60c5\uff0c\u4e5f\u4e0e\u59bb\u5b50\u6881\u683c\u73cd\u7ea6\u5b9a\uff0c\u4e0d\u8981\u63d2\u624b\u516c\u53f8\u7684\u4e8b\u60c5\uff0c\u90fd\u7531\u5f20\u606a\u4e00\u4eba\u53bb\u5904\u7f6e\uff0c\u597d\u4e5f\u7f62\uff0c\u574f\u4e5f\u7f62\uff0c\u63d2\u624b\u7684\u7ed3\u679c\u901a\u5e38\u8bf4\u6765\u53ea\u4f1a\u66f4\u7cdf\u7cd5\u3002</p></article>"}}}]);
