(window.webpackJsonp=window.webpackJsonp||[]).push([[328],{351:function(p){p.exports={chapterTitle:null,title:"\u7b2c328\u7ae0 \u89d2\u529b",content:"<article><h2>\u7b2c328\u7ae0 \u89d2\u529b</h2><p>\u3000\u3000\u5f20\u606a\u4eca\u5929\u4e0d\u51fa\u5e2d\uff0c\u53ea\u662f\u4e0d\u60f3\u7ed9\u5e02\u91cc\u7684\u8fd9\u4e9b\u4eba\u592a\u5f3a\u70c8\u7684\u523a\u6fc0\uff0c\u7eb8\u4e0a\u7684\u6570\u636e\u6bd4\u8f83\u865a\u5316\u4e00\u4e9b\uff0c\u5f20\u606a\u771f\u8981\u5750\u5728\u8fd9\u91cc\uff0c\u5468\u5bcc\u660e\u3001\u9646\u6ea2\u5149\u8fd9\u4e9b\u7ecf\u8fc7\u5927\u534a\u8f88\u5b50\u6323\u624e\u624d\u722c\u5230\u6b64\u9ad8\u4f4d\u7684\u4eba\uff0c\u5fc3\u91cc\u94c1\u5b9a\u522b\u626d\u6b7b\uff0c\u66f4\u4e0d\u7528\u8bf4\uff0c\u5f20\u77e5\u884c\u5728\u8fd9\u91cc\u4e5f\u6ca1\u6709\u968f\u4fbf\u8bf4\u8bdd\u7684\u6743\u5229\u3002\u5c31\u5f20\u606a\u6765\u8bf4\uff0c\u65e2\u7136\u7ad9\u4e86\u51fa\u6765\uff0c\u81ea\u7136\u4e0d\u7518\u5fc3\u518d\u5728\u5468\u5bcc\u660e\u3001\u9646\u6ea2\u5149\u8fd9\u4e9b\u4eba\u9762\u524d\u7ee7\u7eed\u88c5\u5b59\u5b50\uff0c\u6240\u4ee5\u65e0\u8bba\u662f\u8ba8\u4ef7\u8fd8\u4ef7\u4e5f\u7f62\uff0c\u5546\u91cf\u5408\u8bae\u4e5f\u7f62\uff0c\u8fd8\u662f\u8ba9\u522b\u4eba\u5728\u4e2d\u95f4\u4f20\u8bdd\u6700\u597d\u3002</p><p>\u3000\u3000\u5e76\u8d2d\u4ef7\u7684\u6ea2\u4ef7\u90e8\u5206\u8f6c\u4e3a\u5341\u5e74\u671f\u7684\u516c\u53f8\u503a\uff0c\u5e02\u91cc\u5e76\u6ca1\u6709\u610f\u89c1\uff0c\u4f46\u662f\u526f\u4e66\u8bb0\u9646\u6ea2\u5149\u5bf9\u65b0\u5149\u7eb8\u4e1a\u8fd9\u65f6\u5019\u4e0a\u516d\u5341\u4ebf\u89c4\u6a21\u7684\u6797\u7eb8\u4e00\u4f53\u5316\u9879\u76ee\u6709\u6240\u7591\u8651\uff0c\u5e0c\u671b\u65b0\u5149\u7eb8\u4e1a\u5728\u542f\u52a8\u6797\u7eb8\u4e00\u4f53\u5316\u9879\u76ee\u4e4b\u524d\u652f\u4ed8\u5b8c\u6bd5\uff0c\u800c\u4e0d\u662f\u65b0\u5149\u7eb8\u4e1a\u6240\u671f\u5f85\u7684\u90a3\u6837\u7ed9\u4e88\u4e00\u5e74\u7684\u7f13\u51b2\u671f\u3002</p><p>\u3000\u3000\u5176\u4ed6\u4eba\u4e5f\u6709\u8ddf\u9646\u6ea2\u5149\u4e00\u6837\u60f3\u6cd5\u7684\uff0c\u4f46\u662f\u660e\u773c\u4eba\u80fd\u770b\u51fa\u6797\u7eb8\u4e00\u4f53\u5316\u9879\u76ee\u4e0e\u7701\u91cc\u6700\u8fd1\u6574\u987f\u6797\u4e1a\u7cfb\u7edf\uff0c\u53d1\u6587\u7981\u4f10\u5929\u7136\u6797\u6709\u7740\u5bc6\u5207\u7684\u5173\u7cfb\uff0c\u65e2\u7136\u9646\u6ea2\u5149\u63d0\u51fa\u6765\uff0c\u4e5f\u624d\u8ddf\u7740\u5e94\u548c\u4e00\u58f0\u3002</p><p>\u3000\u3000\u8521\u88d5\u5a1f\u8bf4\u9053\uff1a\u201c\u7231\u8fbe\u7535\u5b50\u6b63\u4e0e\u7701\u53d1\u5c55\u94f6\u884c\u8c08\u4e00\u7b14\u8d37\u6b3e\uff0c\u5f88\u5feb\u5c31\u6709\u7ed3\u679c\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u5bcc\u660e\u7ffb\u4e86\u4e00\u7ffb\u9526\u6e56\u7684\u8d22\u52a1\u8d44\u6599\uff0c\u95ee\u9053\uff1a\u201c\u7231\u8fbe\u7535\u5b50\u8fd8\u9700\u8981\u8d37\u6b3e\uff1f\u201d</p><p>\u3000\u3000\u201c\u7231\u8fbe\u7535\u5b50\u8d62\u5229\u80fd\u529b\u6bd4\u8f83\u9ad8\uff0c\u4f46\u4e5f\u8981\u65f6\u95f4\u624d\u80fd\u5c06\u8d62\u5229\u80fd\u529b\u8f6c\u5316\u4e3a\u5b9e\u5b9e\u5728\u5728\u7684\u73b0\u91d1\uff0c\u201d\u8521\u88d5\u5a1f\u7b11\u4e86\u7b11\uff0c\u5468\u5bcc\u660e\u867d\u7136\u5e72\u8fc7\u4e00\u4efb\u5e02\u957f\uff0c\u5bf9\u8d22\u52a1\u7684\u4e8b\u60c5\u8fd8\u8c08\u4e0d\u4e0a\u7cbe\u901a\uff0c\u201c\u5e02\u91cc\u6025\u7740\u8981\u9526\u6e56\u652f\u4ed8\u8fd9\u516d\u5343\u4e07\uff0c\u8fd9\u7b14\u94b1\uff0c\u9526\u6e56\u4e5f\u53ea\u80fd\u4ece\u7231\u8fbe\u7535\u5b50\u62bd\u53d6\u2026\u2026\u201d</p><p>\u3000\u3000\u4e0e\u4e00\u5e74\u524d\u76f8\u6bd4\uff0c\u4eca\u5929\u7684\u7231\u8fbe\u7535\u5b50\u6709\u7740\u5929\u58e4\u4e4b\u522b\uff0c\u53bb\u5e74\u8981\u8d37\u4e2a\u4e94\u767e\u4e07\uff0c\u8fd8\u8981\u5b8b\u57f9\u660e\u641e\u4e9b\u7279\u6b8a\u653f\u7b56\uff0c\u8fd9\u6b21\u76f4\u63a5\u5411\u7701\u53d1\u5c55\u94f6\u884c\u7533\u8bf7\u4e24\u4e2a\u4ebf\u7684\u8d37\u6b3e\uff0c\u7528\u4ee5\u8865\u5145\u6d41\u52a8\u8d44\u91d1\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u4f5c\u4e3a\u9ad8\u65b0\u79d1\u6280\u4f01\u4e1a\uff0c\u53c8\u6709\u5e02\u91cc\u76f4\u63a5\u6279\u7ed9\u7684\u4f18\u60e0\u653f\u7b56\uff0c\u4e0e\u90a3\u4e9b\u4e09\u8d44\u4f01\u4e1a\u540c\u6837\u80fd\u4eab\u53d7\u201c\u4e24\u514d\u4e09\u51cf\u534a\u201d\u7684\u7a0e\u6536\u4f18\u60e0\u3002\u6839\u636e\u901a\u7528\u7684\u4f1a\u8ba1\u539f\u5219\uff0c\u53bb\u5e74\u53ea\u6709\u56db\u4e2a\u6708\u7684\u8d62\u5229\u671f\uff0c\u6240\u4ee5\u8d62\u5229\u5e74\u5ea6\u53ef\u4ee5\u63a8\u8fdf\u5230\u4eca\u5e74\uff0c\u4e5f\u5c31\u8bf4\u662f\uff0c\u7231\u8fbe\u7535\u5b50\u8981\u5c3d\u53ef\u80fd\u5c06\u5229\u6da6\u653e\u5230\u4e5d\u516d\u3001\u4e5d\u4e03\u5e74\uff0c\u624d\u53ef\u4ee5\u6700\u5927\u9650\u5ea6\u7684\u4eab\u53d7\u4f01\u4e1a\u6240\u5f97\u7a0e\u5168\u514d\u7684\u4f18\u60e0\u653f\u7b56\u3002\u8fd9\u65f6\u5019\u63d0\u51fa\u5229\u6da6\u6295\u8d44\u5176\u4ed6\u9886\u57df\uff0c\u5c31\u65e0\u9700\u8003\u8651\u5176\u4ed6\u76f8\u5f53\u4f18\u60e0\u653f\u7b56\u7684\u914d\u5408\u2014\u2014\u4f01\u4e1a\u8d62\u5229\u8f6c\u6295\u8d44\uff0c\u653f\u5e9c\u4f1a\u6839\u636e\u884c\u4e1a\u4e0e\u5730\u57df\u7684\u533a\u522b\u8fd4\u8fd8\u4e0d\u540c\u6bd4\u4f8b\u7684\u4f01\u4e1a\u6240\u5f97\u7a0e\u3002</p><p>\u3000\u3000\u5373\u4f7f\u7231\u8fbe\u7535\u5b50\u6709\u7740\u6781\u9ad8\u7684\u8d62\u5229\u80fd\u529b\uff0c\u4f46\u662f\u4e0d\u4f1a\u6b20\u503a\u7684\u516c\u53f8\u6c38\u8fdc\u7b97\u4e0a\u662f\u4e00\u5bb6\u597d\u516c\u53f8\uff0c\u8fd9\u65f6\u5019\u4e5f\u5e94\u8be5\u5c3d\u53ef\u80fd\u7684\u5229\u7528\u4e2d\u957f\u671f\u7684\u94f6\u884c\u8d37\u6b3e\u7a33\u5b9a\u516c\u53f8\u7684\u8d22\u52a1\u7ed3\u6784\u3002</p><p>\u3000\u3000\u9646\u6ea2\u5149\u6ca1\u6709\u518d\u591a\u8bf4\u4ec0\u4e48\uff0c\u5468\u5bcc\u660e\u89c1\u8521\u88d5\u5a1f\u4ee3\u8868\u9526\u6e56\u7ed9\u4e88\u627f\u8bfa\uff0c\u8bf4\u9053\uff1a\u201c\u5927\u5bb6\u90fd\u6ca1\u6709\u53cd\u5bf9\u610f\u89c1\uff1f\u90a3\u65b0\u5149\u7eb8\u4e1a\u63d0\u51fa\u7684\u4e00\u63fd\u5b50\u89e3\u51b3\u65b9\u6848\u7b97\u662f\u539f\u5219\u4e0a\u901a\u8fc7\uff0c\u201d\u53c8\u4fa7\u5934\u95ee\u5510\u5b66\u8c26\uff0c\u201c\u5177\u4f53\u7684\u4e8b\u60c5\u662f\u4e0d\u662f\u90fd\u4ea4\u7ed9\u5206\u7ba1\u6539\u5236\u7684\u526f\u5e02\u957f\u65b9\u5b8f\u6e05\u8d1f\u8d23\u534f\u8c03\uff1f\u201d</p><p>\u3000\u3000\u8fd8\u5269\u4e0b\u7684\u5341\u4e00\u5bb6\u9020\u7eb8\u5382\u5206\u5c5e\u4e0d\u540c\u7684\u533a\u53bf\uff0c\u8ba9\u65b9\u5b8f\u6e05\u51fa\u9762\u534f\u8c03\uff0c\u5176\u5b9e\u5c31\u662f\u8ba9\u5e02\u91cc\u7edf\u4e00\u7275\u5934\u5904\u7f6e\u6b64\u4e8b\uff0c\u4e5f\u65b9\u4fbf\u4e8b\u60c5\u8fc5\u901f\u89e3\u51b3\uff0c\u5468\u5bcc\u660e\u8fd9\u4e48\u5efa\u8bae\uff0c\u5012\u4e0d\u662f\u62d6\u540e\u817f\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u70b9\u5934\u5e94\u5141\uff0c\u8bf4\u9053\uff1a\u201c\u5e02\u653f\u5e9c\u4f1a\u9a6c\u4e0a\u5b89\u6392\uff0c\u4e5f\u4e89\u53d6\u4eca\u5e74\u5c31\u628a\u6d77\u5dde\u6240\u6709\u9020\u7eb8\u5382\u5b58\u5728\u7684\u95ee\u9898\u90fd\u89e3\u51b3\u6389\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u5bcc\u660e\u5012\u662f\u60f3\u62d6\u4e00\u62d6\u540e\u817f\uff0c\u5728\u522b\u4eba\u773c\u91cc\uff0c\u65b0\u5149\u7eb8\u4e1a\u4e0a\u516d\u5341\u4ebf\u7684\u6797\u7eb8\u4e00\u4f53\u5316\u9879\u76ee\u6709\u4e9b\u86c7\u541e\u5927\u8c61\u7684\u5938\u5f20\u611f\u89c9\uff0c\u4f46\u662f\u5468\u5bcc\u660e\u5374\u80fd\u60e6\u91cf\u51fa\u8fd9\u4e2a\u9879\u76ee\u7684\u4efd\u91cf\uff0c\u4e0d\u662f\u4ece\u6295\u8d44\u7684\u89d2\u5ea6\uff0c\u800c\u662f\u4ece\u653f\u6cbb\u7684\u89d2\u5ea6\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u4e0e\u7701\u53d1\u5c55\u94f6\u884c\u8c08\u7684\u90a3\u7b14\u8d37\u6b3e\uff0c\u7701\u91cc\u6216\u8bb8\u4f1a\u76f4\u63a5\u5e72\u9884\u4e00\u4e0b\uff0c\u6700\u7ec8\u4f1a\u901a\u8fc7\u9526\u6e56\u6d41\u5165\u65b0\u5149\u7eb8\u4e1a\uff0c\u4ece\u7231\u8fbe\u7535\u5b50\u7684\u8d26\u9762\u4e0a\uff0c\u7231\u8fbe\u7535\u5b50\u5e76\u4e0d\u7f3a\u94b1\uff0c\u7f3a\u94b1\u7684\u53ea\u662f\u65b0\u5149\u7eb8\u4e1a\u800c\u5df2\u3002</p><p>\u3000\u3000\u5468\u5bcc\u660e\u4e3e\u8d77\u7b2c\u4e09\u4efd\u6587\u4ef6\uff0c\u90a3\u672c\u65b0\u5149\u7eb8\u4e1a\u7684\u6797\u7eb8\u4e00\u4f53\u5316\u9879\u76ee\u4e66\uff0c\u95ee\u5468\u6e38\uff1a\u201c\u65b0\u5149\u7eb8\u4e1a\u641e\u6797\u7eb8\u4e00\u4f53\u5316\u9879\u76ee\uff0c\u662f\u597d\u4e8b\uff0c\u6211\u4f5c\u4e3a\u5e02\u59d4\u4e66\u8bb0\uff0c\u5927\u529b\u652f\u6301\uff0c\u4e0d\u8fc7\uff0c\u8fd9\u4e2a\u9879\u76ee\u80fd\u4e0d\u80fd\u8003\u8651\u5411\u6d77\u5dde\u7a0d\u7a0d\u503e\u659c\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u5bcc\u660e\u63d0\u5230\u8fd9\u4e00\u70b9\uff0c\u4e5f\u662f\u5176\u4ed6\u5e38\u59d4\u6700\u5173\u5fc3\u7684\uff0c\u516d\u5341\u4ebf\u7684\u5927\u9879\u76ee\uff0c\u662f\u6d77\u5dde\u5e02\u7684\u4f01\u4e1a\u641e\u51fa\u6765\u7684\uff0c\u7ed3\u679c\u6d77\u5dde\u5e02\u6cbe\u4e0d\u5230\u4e00\u70b9\u5149\uff0c\u603b\u6709\u80a5\u6c34\u6d41\u5165\u5916\u4eba\u7530\u7684\u522b\u626d\uff0c\u5373\u4f7f\u662f\u5510\u5b66\u8c26\u4e5f\u6709\u8fd9\u6837\u7684\u611f\u89c9\uff0c\u6bd5\u7adf\u5176\u4ed6\u4eba\u8c01\u611f\u53d7\u4e0d\u5230\u5728\u5c0f\u6c5f\u4e2d\u4e0a\u6e38\u8425\u6797\u9020\u6797\u7684\u8feb\u5207\u6027\uff1b\u4e0d\u8fc7\u5510\u5b66\u8c26\u81f3\u5c11\u77e5\u9053\u7ef4\u62a4\u5927\u5c40\uff0c\u603b\u4e0d\u80fd\u73b0\u5728\u7ed9\u5f90\u5b66\u5e73\u6dfb\u9ebb\u70e6\u3002</p><p>\u3000\u3000\u5468\u6e38\u8bf4\u9053\uff1a\u201c\u6797\u7eb8\u4e00\u4f53\u5316\uff0c\u9664\u4e86\u8425\u6797\u3001\u5236\u6d46\u4e4b\u5916\uff0c\u8fd8\u5305\u62ec\u6700\u7ec8\u7684\u9020\u7eb8\u3002\u5173\u4e8e\u9020\u7eb8\u4e00\u73af\uff0c\u5e76\u6ca1\u6709\u5728\u8fd9\u4e2a\u9879\u76ee\u4e66\u5f97\u5230\u4f53\u73b0\uff0c\u56e0\u4e3a\u8981\u7b49\u4e00\u63fd\u5b50\u89e3\u51b3\u65b9\u6848\u901a\u8fc7\u4e4b\u540e\uff0c\u5c06\u73b0\u5728\u7684\u9020\u7eb8\u751f\u4ea7\u6574\u5408\u597d\uff0c\u624d\u53ef\u4ee5\u5236\u8ba2\u4e00\u4e2a\u8be6\u5b9e\u7684\u65b9\u6848\uff0c\u81f3\u5c11\u5728\u4e09\u56db\u5e74\u5185\uff0c\u65b0\u5149\u7eb8\u4e1a\u4e0d\u4f1a\u8003\u8651\u5728\u5176\u4ed6\u5730\u533a\u6295\u8d44\u53e6\u5efa\u9020\u7eb8\u5de5\u5382\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u65b0\u5149\u7eb8\u4e1a\u4e09\u56db\u5e74\u95f4\u4f1a\u4e0d\u65ad\u7684\u8ffd\u52a0\u6295\u8d44\uff1f\u201d\u5468\u5bcc\u660e\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u8fd9\u662f\u80af\u5b9a\u7684\uff0c\u6211\u4eec\u4e5f\u5e0c\u671b\u8425\u6797\u3001\u5236\u6d46\u6240\u751f\u4ea7\u7684\u539f\u6599\u7531\u5185\u90e8\u5de5\u5382\u6d88\u5316\uff0c\u8fd9\u6837\u624d\u7b26\u5408\u65b0\u5149\u7eb8\u4e1a\u6700\u5927\u7684\u5229\u76ca\u3002\u201d</p><p>\u3000\u3000\u5468\u6e38\u77e5\u9053\u81ea\u5df1\u5728\u4fe1\u53e3\u80e1\u8a00\uff0c\u8fd9\u4efd\u9879\u76ee\u4e66\u76ee\u524d\u53ea\u6709\u8425\u9020\u6df7\u751f\u578b\u901f\u4e30\u6797\u7684\u4e00\u73af\u7a0d\u6709\u53ef\u6267\u884c\u6027\uff0c\u5176\u540e\u7684\u56db\u4e94\u5341\u4ebf\u89c4\u6a21\u7684\u5236\u6d46\u9879\u76ee\uff0c\u8d44\u91d1\u6765\u6e90\u90fd\u6ca1\u6709\u8bf4\u660e\uff0c\u53ea\u662f\u7a7a\u8bb8\u4e86\u4e00\u4e2a\u5929\u5927\u7684\u751c\u997c\u8ba9\u5927\u5bb6\u53bb\u4e89\u3002\u867d\u7136\u5bf9\u9020\u7eb8\u5de5\u4e1a\u56ed\u867d\u6709\u4e2a\u4e2d\u671f\u7684\u53d1\u5c55\u89c4\u5212\uff0c\u4f46\u662f\u89c4\u6a21\u8fdc\u6bd4\u522b\u4eba\u60f3\u8c61\u7684\u5c0f\uff0c\u9020\u7eb8\u5de5\u4e1a\u56ed\u7684\u4ea7\u91cf\u8981\u8fbe\u5230\u4e0e\u8425\u6797\u3001\u5236\u6d46\u9879\u76ee\u76f8\u5339\u914d\u7684\u7a0b\u5ea6\uff0c\u81f3\u5c11\u8fd8\u8981\u6295\u8fdb\u53bb\u4e8c\u4e09\u5341\u4ebf\uff0c\u52a0\u4e0a\u5236\u6d46\u9879\u76ee\u7684\u94b1\uff0c\u4ece\u54ea\u91cc\u7b79\u53bb\uff1f</p><p>\u3000\u3000\u6797\u7eb8\u4e00\u4f53\u5316\u662f\u5927\u4ea7\u4e1a\u5faa\u73af\uff0c\u56fd\u5185\u5148\u5929\u4e0d\u8db3\uff0c\u56fd\u5185\u63d0\u53ca\u8fd9\u4e2a\u6982\u5ff5\u4e5f\u8fd1\u5341\u5e74\uff0c\u4e5f\u53d1\u5c55\u4e86\u8fd1\u5341\u5e74\uff0c\u4f46\u662f\u6839\u672c\u4e0d\u6210\u6c14\u5019\uff0c\u65b0\u5149\u7eb8\u4e1a\u8981\u8fce\u96be\u800c\u4e0a\uff0c\u96be\u5ea6\u5374\u51fa\u4e4e\u610f\u6599\u7684\u5927\u3002</p><p>\u3000\u3000\u5468\u5bcc\u660e\u4e0d\u5927\u613f\u610f\u76f4\u63a5\u4e0e\u5f20\u606a\u89c1\u9762\uff0c\u611f\u89c9\u522b\u626d\uff0c\u4f46\u662f\u4e0d\u4e3b\u52a8\u4e00\u4e9b\uff0c\u6240\u6709\u7684\u653f\u7ee9\u90fd\u4f1a\u8ba9\u5510\u5b66\u8c26\u635e\u53bb\uff0c\u5468\u5bcc\u660e\u77a5\u4e86\u5510\u5b66\u8c26\u4e00\u773c\uff0c\u5510\u5b66\u8c26\u773c\u6709\u4e4f\u6001\uff0c\u4f3c\u4e4e\u5bf9\u4ed6\u63d0\u8d77\u6295\u8d44\u7684\u95ee\u9898\u5f88\u4e0d\u4e3a\u610f\uff0c\u5468\u5bcc\u660e\u5fc3\u91cc\u7ea0\u7ed3\u7740\uff0c\u5510\u5b66\u8c26\u7684\u795e\u6001\u65e0\u7591\u662f\u544a\u8bc9\u4ed6\uff1a\u518d\u4e3b\u52a8\u4e5f\u4e0d\u8d77\u4ec0\u4e48\u5927\u4f5c\u7528\uff0c\u5fc3\u91cc\u7ea0\u7ed3\u5f88\u96be\u5316\u89e3\u3002</p><p>\u3000\u3000\u4f1a\u8bae\u7ed3\u675f\u4e4b\u540e\uff0c\u5468\u5bcc\u660e\u6ca1\u6709\u7acb\u5373\u79bb\u5f00\uff0c\u6587\u5c71\u996d\u5e97\u4e13\u95e8\u7ed9\u4ed6\u51c6\u5907\u4e86\u5957\u623f\u4f9b\u4ed6\u968f\u65f6\u4f11\u606f\uff0c\u6709\u4e00\u4e2a\u4e13\u95e8\u4ed6\u5dee\u4f7f\u7684\u670d\u52a1\u5458\u5c0f\u90c1\uff0c\u5e02\u59d4\u79d8\u4e66\u957f\u5f20\u6653\u5065\u8ddf\u4ed6\u8d70\u8fdb\u623f\u95f4\uff0c\u670d\u52a1\u5458\u5c0f\u90c1\u5c31\u63d0\u4e86\u74f6\u70ed\u6c34\u58f6\u8fc7\u6765\uff0c\u5f20\u6653\u5065\u7aa5\u4e86\u4e00\u773c\uff0c\u867d\u7136\u7b97\u4e0d\u4e0a\u975e\u5e38\u7f55\u89c1\u7684\u7f8e\u4eba\uff0c\u4f46\u662f\u8eab\u6750\u505c\u5300\u5408\u5ea6\uff0c\u7709\u773c\u64a9\u4eba\uff0c\u4e8c\u5341\u56db\u4e94\u5c81\u7684\u7f8e\u4eba\u4e0d\u4f1a\u53ea\u7518\u5fc3\u5f53\u4e00\u540d\u666e\u901a\u7684\u670d\u52a1\u5458\uff0c\u5927\u6982\u5c31\u662f\u51b2\u5468\u5bcc\u660e\u6765\u7684\u3002</p><p>\u3000\u3000\u201c\u90c1\u840d\u554a\uff0c\u201d\u5468\u5bcc\u660e\u76f4\u547c\u5c0f\u90c1\u7684\u540d\u5b57\uff0c\u5728\u5f20\u6653\u5065\u9762\u524d\u4e5f\u4e0d\u63a9\u9970\u4e24\u4eba\u719f\u7edc\u7684\u5173\u7cfb\uff0c\u201c\u4f60\u5b89\u6392\u4e00\u4e0b\u665a\u996d\uff0c\u6211\u8ddf\u5f20\u6653\u5065\u7559\u4e0b\u6765\u5403\u996d\u2026\u2026\u201d</p><p>\u3000\u3000\u90c1\u840d\u5e2e\u4e24\u4eba\u6c8f\u4e86\u8336\u6c34\u518d\u8d70\u51fa\u53bb\u5b89\u6392\u665a\u996d\uff0c\u5468\u5bcc\u660e\u5bf9\u5f20\u6653\u5065\u8bf4\uff1a\u201c\u5e02\u91cc\u8fd8\u6709\u51e0\u4e2a\u4eba\u4f1a\u60df\u6211\u9a6c\u9996\u662f\u77bb\uff1f\u201d</p><p>\u3000\u3000\u201c\u8ba9\u9526\u6e56\u63d0\u524d\u652f\u4ed8\u5e76\u8d2d\u6b3e\u53ef\u662f\u9646\u4e66\u8bb0\u5148\u63d0\u51fa\u6765\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u9646\u6ea2\u5149\uff0c\u5475\u5475\uff0c\u201d\u5468\u5bcc\u660e\u51b7\u7b11\u4e86\u4e0b\uff0c\u201c\u4ed6\u662f\u4e0d\u75db\u5feb\u5510\u5b66\u8c26\u5f53\u5e02\u957f\uff0c\u8981\u662f\u9526\u6e56\u7684\u6001\u5ea6\u518d\u575a\u51b3\u4e00\u4e9b\uff0c\u4ed6\u7684\u6001\u5ea6\u9a6c\u4e0a\u5c31\u4f1a\u8f6f\u4e0b\u6765\uff0c\u8ddf\u5176\u4ed6\u4eba\u4e00\u6837\uff0c\u662f\u5899\u5934\u8349\uff0c\u6211\u4eec\u5b64\u5355\u4e86\u2026\u2026\u201d\u8fd9\u4e48\u8bf4\u8bdd\uff0c\u5468\u5bcc\u660e\u7ed9\u4eba\u6709\u4e9b\u51c4\u51c9\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u5f20\u6653\u5065\u8fdf\u7591\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u9526\u57ce\u7684\u8d75\u9526\u8363\u524d\u4e24\u5929\u8bf7\u6211\u5403\u996d\uff0c\u6211\u63a8\u6258\u4e8b\u60c5\u591a\uff0c\u6ca1\u6709\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u5bcc\u660e\u6447\u5934\u82e6\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5b98\u573a\u554a\uff0c\u6ca1\u6709\u4ec0\u4e48\u4ece\u4e0b\u5411\u4e0a\u7684\u6548\u5fe0\uff0c\u5411\u6765\u53ea\u662f\u5229\u76ca\u7684\u7ea0\u7f20\u3002\u8d75\u9526\u8363\u65e2\u7136\u8bf7\u4f60\u5403\u996d\uff0c\u4f60\u5c31\u53bb\u5403\u5f97\u4e86\uff0c\u6211\u8fd8\u80fd\u62e6\u7740\u4f60\u4e0d\u8ba9\u4f60\u5403\u996d\uff1f\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u4e0d\u53ef\u80fd\u5bb9\u4ed6\u518d\u5e72\u4e00\u5c4a\u5e02\u59d4\u4e66\u8bb0\uff0c\u6240\u4ee5\u4ed6\u4e0e\u5510\u5b66\u8c26\u4e4b\u95f4\u603b\u4e0d\u80fd\u6cf0\u7136\u65e0\u4e8b\u7684\u76f8\u5904\u3002\u5973\u513f\u7ea2\u6885\u5728\u65b0\u6cf0\u5f00\u516c\u53f8\u9887\u53d7\u8c22\u5bb6\u7684\u7167\u987e\uff0c\u5982\u4eca\u8c22\u5bb6\u8981\u8ddf\u8d75\u9526\u8363\u5728\u6d77\u5dde\u5c55\u5f00\u5e7f\u6cdb\u7684\u5408\u4f5c\uff0c\u8bba\u53ca\u79c1\u60c5\uff0c\u90fd\u8981\u628a\u4ee5\u524d\u7684\u5c0f\u6069\u6028\u653e\u5f00\uff1b\u8d75\u9526\u8363\u884c\u4e8b\u867d\u7136\u6709\u4e9b\u504f\u9887\uff0c\u4f46\u4e0d\u4e0e\u76f4\u63a5\u53d1\u751f\u91d1\u94b1\u4e0a\u7684\u5173\u7cfb\uff0c\u4e0d\u6101\u4ed6\u65e5\u540e\u7ffb\u8239\u4f1a\u628a\u6c34\u6e85\u81ea\u5df1\u8eab\u4e0a\u6765\u3002\u6700\u4e3b\u8981\u7684\uff0c\u5e02\u91cc\u7684\u90a3\u4e9b\u5bb6\u4f19\u9a91\u5899\u89c2\u671b\u7740\uff0c\u8f6f\u5f31\u65e0\u58f0\uff0c\u5c31\u50cf\u9646\u6ea2\u5149\uff0c\u4f46\u4ed6\u4eec\u90fd\u662f\u4e07\u5411\u524d\u63d0\u62d4\u8d77\u6765\u7684\uff0c\u81f3\u5c11\u8ddf\u540c\u51fa\u4e00\u7cfb\u7684\u8d75\u9526\u8363\u6709\u51e0\u5206\u9999\u706b\u60c5\u4e49\uff0c\u8fd9\u4e48\u60f3\uff0c\u66f4\u5e94\u8be5\u653e\u5f00\u4ee5\u524d\u7684\u5c0f\u7599\u7629\u4e86\u3002</p><p>\u3000\u3000\u5468\u5bcc\u660e\u6c89\u541f\u601d\u91cf\u7740\uff0c\u8981\u538b\u5236\u5510\u5b66\u8c26\uff0c\u79bb\u5f00\u8d75\u9526\u8363\u8fd8\u771f\u662f\u4e0d\u884c\u3002\u5f20\u6653\u5065\u7684\u624b\u673a\u54cd\u4e86\uff0c\u4ed6\u5f53\u7740\u5468\u5bcc\u660e\u7684\u9762\u63a5\u4e86\u7535\u8bdd\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u6342\u7740\u901a\u8bdd\u5b54\u5bf9\u5468\u5bcc\u660e\u8bf4\uff1a\u201c\u5468\u4e66\u8bb0\uff0c\u8d75\u9526\u8363\u7684\u7535\u8bdd\uff0c\u8bf4\u662f\u8c22\u5bb6\u6709\u4eba\u8fc7\u6765\uff0c\u9080\u6211\u8fc7\u53bb\u5403\u996d\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8c22\u5bb6\u6765\u4eba\u554a\uff0c\u201d\u5468\u5bcc\u660e\u72b9\u8c6b\u4e86\u4e00\u4f1a\u513f\uff0c\u8bf4\u9053\uff0c\u201c\u7ea2\u6885\u592b\u59bb\u5728\u65b0\u6cf0\u5f00\u516c\u53f8\u591a\u53d7\u8c22\u5bb6\u7167\u987e\uff0c\u8c22\u5bb6\u6709\u4eba\u8fc7\u6765\uff0c\u5e94\u8be5\u662f\u6211\u8bf7\u4ed6\u5403\u996d\u2026\u2026\u201d</p></article>"}}}]);
