(window.webpackJsonp=window.webpackJsonp||[]).push([[330],{352:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u516d\u8282 \u6b63\u4e8b",content:"<article><h2>\u7b2c\u56db\u5341\u516d\u8282 \u6b63\u4e8b</h2><p>\u70c8\u7130\u9152\u662f\u6df1\u6e0a\u4e2d\u7684\u4e00\u79cd\u7279\u4ea7\uff0c\u7528\u751f\u957f\u5728\u8840\u4e4b\u6cb3\u6cbf\u5cb8\u7684\u706b\u660e\u8349\u917f\u5236\u800c\u6210\uff0c\u559d\u4e0b\u53bb\u540e\u80f8\u819b\u91cc\u4eff\u4f5b\u6709\u4e00\u56e2\u706b\u7130\u5728\u71c3\u70e7\uff0c\u6700\u662f\u80fd\u591f\u632f\u594b\u60c5\u7eea\uff0c\u4e43\u662f\u6076\u9b54\u4eec\u7684\u6700\u7231\u3002\u743c\u6069\u4e0d\u64c5\u996e\u9152\uff0c\u662f\u538b\u6839\u5c31\u6ca1\u60f3\u8fc7\u53bb\u5c1d\u8bd5\u7684\u3002\u4e0d\u8fc7\u65e2\u7136\u9762\u524d\u8fd9\u4f4d\u8001\u8005\u5f00\u53e3\u4e86\uff0c\u4ed6\u4fbf\u6325\u624b\u53ec\u6765\u5938\u8d5b\u9b54\u4f8d\u8005\u3002</p><p>\u201c\u4e24\u676f\u70c8\u7130\u9152\uff0c\u7ed9\u8fd9\u4f4d\u5148\u751f\u3002\u201d</p><p>\u5938\u8d5b\u9b54\u542c\u660e\u767d\u4e86\u524d\u9762\u534a\u53e5\u8bdd\uff0c\u4f46\u5bf9\u540e\u9762\u534a\u53e5\u611f\u5230\u6115\u7136\uff0c\u4e0d\u77e5\u9053\u743c\u6069\u6240\u6307\u7684\u201c\u8fd9\u4f4d\u5148\u751f\u201d\u5728\u54ea\u91cc\u3002\u743c\u6069\u89c1\u5b83\u6a21\u6837\uff0c\u987f\u65f6\u660e\u767d\u4e86\u600e\u4e48\u56de\u4e8b\uff0c\u201c\u9001\u4e24\u676f\u70c8\u7130\u9152\u6765\u5c31\u662f\u3002\u201d\u4ed6\u6539\u53e3\u8bf4\u3002</p><p>\u4f8d\u8005\u8ff7\u60d1\u5730\u9000\u4e0b\uff0c\u8fc7\u4e86\u7247\u523b\u7aef\u4e0a\u4e24\u676f\u70c8\u7130\u9152\uff0c\u706b\u7ea2\u8272\u7684\u6db2\u4f53\u5728\u6c34\u6676\u676f\u4e2d\u8f7b\u5fae\u8361\u6f3e\u7740\uff0c\u771f\u50cf\u662f\u4e00\u56e2\u56e2\u7ec6\u5c0f\u7684\u706b\u7130\u5728\u8df3\u8dc3\u71c3\u70e7\u3002\u201c\u591a\u8c22\u3002\u201d\u7070\u888d\u8001\u8005\u8bf4\uff0c\u6296\u6296\u7d22\u7d22\u5730\u4f38\u624b\u62ff\u8d77\u4e00\u676f\uff0c\u6162\u6162\u9001\u5230\u5507\u8fb9\u3002</p><p>\u201c\u53ea\u6709\u6211\u80fd\u770b\u89c1\u60a8\u5417\u3002\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u6211\u53ea\u6253\u7b97\u8ba9\u4f60\u770b\u89c1\u3002\u201d\u8001\u8005\u56de\u7b54\u3002</p><p>\u743c\u6069\u770b\u4e86\u770b\u56db\u5468\uff0c\u8fd9\u65f6\u5019\u6b63\u662f\u8001\u5934\u9aa8\u91cc\u5ba2\u4eba\u6700\u5c11\u7684\u65f6\u5019\uff0c\u697c\u4e0b\u5927\u5385\u91cc\u7a7a\u7a7a\u8361\u8361\uff0c\u57fa\u672c\u6ca1\u4ec0\u4e48\u4eba\uff0c\u53ea\u6709\u51e0\u53ea\u5938\u8d5b\u9b54\u4f8d\u8005\u5728\u8fdc\u5904\u7b49\u5f85\u62db\u547c\uff0c\u975e\u5e38\u5b89\u9759\u3002\u201c\u90a3\u6211\u548c\u60a8\u8bf4\u8bdd\u5462\uff0c\u201d\u743c\u6069\u53c8\u95ee\uff0c\u201c\u4ed6\u4eec\u53ea\u770b\u5230\u662f\u6211\u5728\u5bf9\u7740\u7a7a\u6c14\u81ea\u8a00\u81ea\u8bed\uff1f\u8fd8\u6709\u8fd9\u4e2a\u676f\u5b50\uff0c\u4ed6\u4eec\u4f1a\u770b\u5230\u5b83\u81ea\u5df1\u98d8\u6d6e\u8d77\u6765\uff0c\u7136\u540e\u91cc\u9762\u7684\u9152\u4e00\u70b9\u70b9\u53d8\u5c11\uff1f\u201d</p><p>\u201c\u4e0d\uff0c\u4ed6\u4eec\u6839\u672c\u5c31\u6ca1\u770b\u89c1\u4f60\u5728\u4ea4\u8c08\uff0c\u4e5f\u6ca1\u542c\u89c1\u4f60\u5728\u8bf4\u8bdd\uff0c\u8fd9\u53ea\u676f\u5b50\u5728\u4ed6\u4eec\u773c\u4e2d\u4e5f\u4e00\u76f4\u6446\u5728\u684c\u5b50\u4e0a\u6ca1\u52a8\u8fc7\uff0c\u7b80\u5355\u6765\u8bf4\uff0c\u6240\u6709\u6d89\u53ca\u5230\u6211\u7684\u5730\u65b9\uff0c\u5bf9\u4e8e\u4ed6\u4eec\u6765\u8bf4\u90fd\u662f\u4ece\u672a\u53d1\u751f\uff0c\u6839\u672c\u4e0d\u5b58\u5728\u7684\u3002\u201d</p><p>\u201c\u771f\u795e\u5947\u3002\u201d</p><p>\u201c\u8fd9\u5c31\u662f\u9b54\u6cd5\u3002\u201d</p><p>\u4e24\u4f4d\u5deb\u5e08\u90fd\u4e0d\u518d\u8bf4\u8bdd\uff0c\u8001\u4eba\u4e00\u8fb9\u54b3\u55fd\uff0c\u4e00\u8fb9\u6162\u6162\u54c1\u5c1d\u7740\u70c8\u7130\u9152\uff0c\u4ed6\u539f\u672c\u7070\u6697\u7684\u8138\u8272\u6e10\u6e10\u53d8\u5f97\u6709\u51e0\u5206\u7ea2\u6da6\u8d77\u6765\uff0c\u6df7\u6d4a\u7684\u773c\u775b\u91cc\u4e5f\u5f00\u59cb\u6709\u4e86\u4e9b\u5149\u91c7\u3002\u201c\u9152\u4e0d\u9519\uff0c\u201d\u4ed6\u6700\u540e\u8bf4\uff0c\u5c06\u7a7a\u676f\u5b50\u653e\u5728\u684c\u4e0a\uff0c\u201c\u8fd9\u662f\u6076\u9b54\u4eec\u552f\u4e00\u52c9\u5f3a\u503c\u5f97\u79f0\u9053\u7684\u5730\u65b9\uff0c\u4f60\u5e94\u8be5\u8bd5\u8bd5\u3002\u201d</p><p>\u201c\u592a\u70c8\u4e86\uff0c\u6211\u4e0d\u559c\u6b22\u3002\u201d</p><p>\u201c\u4f46\u80fd\u6cbb\u54b3\u55fd\uff0c\u201d\u8001\u4eba\u8bf4\uff0c\u201c\u4f60\u770b\u6211\u73b0\u5728\uff0c\u54b3\u55fd\u5c31\u597d\u5f97\u591a\u4e86\u3002\u201d</p><p>\u743c\u6069\u5fc3\u60f3\u6211\u53c8\u4e0d\u54b3\u55fd\uff0c\u518d\u8bf4\u4f60\u5176\u5b9e\u4e5f\u4e0d\u54b3\u55fd\uff0c\u4f60\u7eaf\u7cb9\u662f\u5fc3\u7406\u4f5c\u7528\u3002\u201c\u8fd8\u6709\u4e00\u676f\u3002\u201d\u4ed6\u63d0\u9192\u3002</p><p>\u8001\u4eba\u70b9\u70b9\u5934\uff0c\u5374\u6ca1\u6709\u7acb\u523b\u7aef\u8d77\u53e6\u5916\u4e00\u676f\uff0c\u201c\u53c8\u89c1\u9762\u4e86\uff0c\u5c0f\u5bb6\u4f19\uff0c\u201d\u4ed6\u6162\u6162\u5f00\u53e3\uff0c\u201c\u770b\u5230\u6211\u4e0d\u4f1a\u5bb3\u6015\u5417\uff1f\u201d</p><p>\u201c\u5f53\u7136\u5bb3\u6015\u3002\u201d</p><p>\u201c\u867d\u7136\u8fd9\u91cc\u7981\u6b62\u79c1\u4e0b\u6597\u6bb4\uff0c\u4f46\u6211\u8981\u6740\u4f60\u7684\u8bdd\uff0c\u8fd8\u662f\u8f7b\u800c\u6613\u4e3e\u7684\uff0c\u201d\u8001\u4eba\u8bf4\uff0c\u521a\u624d\u90a3\u676f\u70c8\u7130\u9152\u4f3c\u4e4e\u771f\u7684\u6709\u4e86\u6548\u679c\uff0c\u4ed6\u8bf4\u8bdd\u987a\u7545\u6d41\u5229\u5f97\u591a\u4e86\uff0c\u201c\u5c31\u4f60\u7684\u5e74\u9f84\u548c\u9b54\u6cd5\u9020\u8be3\u6765\u770b\uff0c\u8db3\u4ee5\u7b97\u5f97\u4e0a\u662f\u5929\u624d\uff0c\u4f46\u5929\u624d\u6211\u89c1\u5f97\u591a\u4e86\uff0c\u4e5f\u6ca1\u4ec0\u4e48\u4e86\u4e0d\u8d77\u3002\u4f60\u7684\u6cd5\u672f\u5728\u6211\u773c\u91cc\u4e0d\u8fc7\u662f\u8857\u5934\u9a97\u5b50\u7684\u620f\u6cd5\uff0c\u80fd\u501a\u4ed7\u7684\u65e0\u975e\u4e5f\u5c31\u662f\u4f60\u8001\u5e08\u7ed9\u4f60\u7684\u6cd5\u672f\u9006\u8f6c\u6212\u6307\u3002\u5982\u679c\u6211\u5168\u529b\u4ee5\u8d74\u7684\u8bdd\uff0c\u4f60\u5927\u6982\u80fd\u652f\u6491\u5341\u79d2\u949f\uff0c\u201d\u4ed6\u504f\u7740\u5934\uff0c\u4f3c\u4e4e\u53c8\u91cd\u65b0\u4f30\u7b97\u4e86\u4e00\u4e0b\uff0c\u201c\u6700\u591a\u5341\u4e94\u79d2\u949f\uff0c\u8fd9\u662f\u5728\u8003\u8651\u5230\u6709\u6076\u9b54\u58eb\u5175\u6765\u6363\u4e71\u7684\u60c5\u51b5\u4e0b\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u4ed6\u77e5\u9053\u5bf9\u65b9\u6240\u8bf4\u662f\u4e8b\u5b9e\u3002</p><p>\u201c\u4f46\u4f60\u770b\u8d77\u6765\u4f3c\u4e4e\u5f88\u9547\u5b9a\u3002\u201d</p><p>\u201c\u4e0d\uff0c\u6211\u53ea\u662f\u4e0d\u559c\u6b22\u505a\u6ca1\u6709\u610f\u4e49\u7684\u4e8b\u60c5\u800c\u5df2\u3002\u201d</p><p>\u201c\u6ca1\u6709\u610f\u4e49\u7684\u4e8b\u60c5\uff1f\u201d</p><p>\u201c\u6bd4\u5982\u8bf4\u8868\u73b0\u5f97\u5f88\u5bb3\u6015\uff0c\u201d\u743c\u6069\u89e3\u91ca\uff0c\u201c\u5982\u679c\u60a8\u544a\u8bc9\u6211\u8bf4\uff0c\u53ea\u8981\u6211\u5413\u5f97\u745f\u745f\u53d1\u6296\uff0c\u60a8\u5c31\u76f4\u63a5\u65e0\u89c6\u6211\u7684\u5b58\u5728\uff0c\u90a3\u4e48\u6211\u4fdd\u8bc1\u7acb\u523b\u6296\u5f97\u5c31\u50cf\u89c1\u5230\u4e86\u72d0\u72f8\u7684\u5c0f\u9e21\u2014\u2014\u5982\u679c\u6a21\u4eff\u5f97\u4e0d\u50cf\u8bf7\u6307\u6b63\uff0c\u6211\u5b66\u5f97\u5f88\u5feb\u7684\u3002\u4f46\u6211\u60f3\u60a8\u5750\u5728\u8fd9\u91cc\uff0c\u603b\u4e0d\u4f1a\u5c31\u662f\u4e3a\u4e86\u770b\u6211\u5bb3\u6015\u7684\u6837\u5b50\u5427\uff0c\u6240\u4ee5\u6211\u5c31\u61d2\u5f97\u8868\u73b0\u4e86\u3002\u201d</p><p>\u201c\u6709\u70b9\u9053\u7406\uff0c\u201d\u8001\u4eba\u8bf4\uff0c\u201c\u90a3\u4f60\u51c6\u5907\u600e\u4e48\u505a\u5462\uff1f\u80fd\u591f\u5728\u4efb\u4f55\u65f6\u5019\u90fd\u4fdd\u6301\u9547\u5b9a\u81ea\u82e5\uff0c\u662f\u826f\u597d\u7684\u54c1\u8d28\uff0c\u4f46\u7ec8\u7a76\u662f\u4e0d\u80fd\u89e3\u51b3\u5b9e\u9645\u95ee\u9898\u7684\u3002\u201d</p><p>\u201c\u4e0d\u51c6\u5907\u600e\u4e48\u505a\uff0c\u4ec5\u4ec5\u7b49\u5f85\u800c\u5df2\uff0c\u201d\u743c\u6069\u56de\u7b54\uff0c\u201c\u7b49\u5f85\u60a8\u8bf4\u660e\u6765\u610f\u3002\u201d</p><p>\u8001\u4eba\u6c89\u9ed8\u4e86\u4e00\u4f1a\uff0c\u201c\u4f60\u771f\u65e0\u8da3\u3002\u201d\u4ed6\u8bc4\u4ef7\u3002</p><p>\u201c\u5f88\u591a\u4eba\u90fd\u8fd9\u4e48\u8bf4\u3002\u201d</p><p>\u518d\u4e00\u6b21\u6c89\u9ed8\uff0c\u7136\u540e\u662f\u743c\u6069\u5148\u5f00\u53e3\u6253\u7834\u4e86\u5bc2\u9759\u3002</p><p>\u201c\u6709\u4ec0\u4e48\u6307\u6559\u5462\uff0c\u8428\u9a6c\u65af\u7279\u5148\u751f\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u5982\u679c\u6ca1\u4ec0\u4e48\u4e8b\u60c5\u7684\u8bdd\uff0c\u6211\u60f3\u5148\u56de\u53bb\u4f11\u606f\u4e86\uff0c\u6bd5\u7adf\u660e\u5929\u8fd8\u8981\u5728\u8d5b\u573a\u4e0a\u4ea4\u624b\u5462\u3002\u201d</p><p>\u8428\u9a6c\u65af\u7279\u6447\u6447\u5934\uff0c\u201c\u6362\u4e2a\u79f0\u547c\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4e0d\u559c\u6b22\u542c\u5230\u4f60\u53eb\u6211\u7684\u540d\u5b57\u3002\u201d</p><p>\u201c\u5514\uff1f\u201d</p><p>\u201c\u4f60\u79f0\u547c\u6211\u4e3a\u8428\u9a6c\u65af\u7279\u5148\u751f\uff0c\u90a3\u6309\u9053\u7406\u6211\u5c31\u5f97\u53eb\u4f60\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u53ef\u662f\u6211\u4e0d\u60f3\u8fd9\u4e48\u79f0\u547c\uff0c\u4f60\u592a\u5dee\u4e86\uff0c\u8fd9\u4f1a\u8ba9\u6211\u89c9\u5f97\u81ea\u964d\u8eab\u5206\u3002\u201d</p><p>\u743c\u6069\u5fae\u7b11\uff0c\u201c\u60a8\u5c3d\u53ef\u4ee5\u968f\u610f\u79f0\u547c\uff0c\u6211\u4e0d\u4ecb\u610f\uff0c\u6216\u8005\u53eb\u6211\u743c\u6069\u5c31\u884c\u3002\u201d</p><p>\u201c\u4e0d\u884c\uff0c\u6211\u4ecb\u610f\uff0c\u8fd9\u4e0d\u5408\u6211\u7684\u89c4\u77e9\u2026\u2026\u8fd9\u6837\u5427\uff0c\u6211\u53eb\u4f60\u5c0f\u5bb6\u4f19\uff0c\u4f60\u53eb\u6211\u8001\u5bb6\u4f19\u5c31\u884c\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u6211\u770b\u6211\u79f0\u547c\u60a8\u4e3a\u524d\u8f88\u5982\u4f55\u3002\u201d</p><p>\u201c\u968f\u4fbf\u5427\uff0c\u201d\u8428\u9a6c\u65af\u7279\u6709\u4e9b\u610f\u5174\u8427\u7d22\u5730\u6325\u6325\u624b\uff0c\u201c\u90a3\u5c31\u8fd9\u6837\u597d\u4e86\u3002\u8bdd\u8bf4\uff0c\u4f60\u597d\u50cf\u6709\u70b9\u95f7\u95f7\u4e0d\u4e50\u7684\u6837\u5b50\uff0c\u6709\u5fc3\u4e8b\uff1f\u201d</p><p>\u201c\u7b97\u662f\u5427\u3002\u201d</p><p>\u201c\u5728\u62c5\u5fc3\u660e\u5929\u7684\u51b3\u8d5b\uff1f\u201d</p><p>\u201c\u5514\uff0c\u8fd9\u4e2a\u5176\u5b9e\u5012\u4e0d\u600e\u4e48\u62c5\u5fc3\uff0c\u201d\u743c\u6069\u5766\u767d\u627f\u8ba4\uff0c\u201c\u53cd\u6b63\u6211\u662f\u6253\u7740\u4e0d\u884c\u5c31\u5f03\u6743\u7684\u4e3b\u610f\uff0c\u770b\u5728\u6211\u8001\u5e08\u7684\u4efd\u4e0a\uff0c\u60f3\u5fc5\u524d\u8f88\u60a8\u4e5f\u4e0d\u4f1a\u600e\u4e48\u4e3a\u96be\u6211\u5427\u3002\u201d</p><p>\u8428\u9a6c\u65af\u7279\u4f4e\u6c89\u5730\u7b11\u8d77\u6765\uff0c\u201c\u4f60\u771f\u5766\u767d\u3002\u201d</p><p>\u201c\u6211\u4e00\u5411\u90fd\u5f88\u5766\u767d\u7684\uff0c\u5728\u5e94\u8be5\u5766\u767d\u5e76\u4e14\u5fc5\u987b\u5766\u767d\u7684\u65f6\u5019\u3002\u201d</p><p>\u201c\u597d\u4e60\u60ef\uff0c\u201d\u8428\u9a6c\u65af\u7279\u8bf4\uff0c\u201c\u7136\u800c\u6211\u65e9\u5c31\u8b66\u544a\u8fc7\u4f60\uff0c\u8ba9\u4f60\u9000\u51fa\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80af\u542c\u6211\u7684\u5efa\u8bae\u5462\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u6211\u5728\u6b64\u4e4b\u524d\u5df2\u7ecf\u7b54\u5e94\u4e86\u6211\u7684\u670b\u53cb\uff0c\u6211\u4e0d\u80fd\u53cd\u6094\u3002\u201d</p><p>\u201c\u4fe1\u5b88\u627f\u8bfa\u662f\u4e2a\u597d\u54c1\u8d28\u2026\u2026\u4e0d\u8fc7\u4f60\u7684\u670b\u53cb\u4f3c\u4e4e\u5168\u90fd\u662f\u5973\u5b69\u5b50\u554a\u3002\u201d</p><p>\u201c\u5514\uff0c\u60a8\u8fd9\u4e48\u4e00\u8bf4\u6211\u624d\u521a\u53d1\u89c9\u2026\u2026\u6211\u8eab\u8fb9\u4e03\u4e2a\u4eba\u5c45\u7136\u5168\u90fd\u662f\u5973\u6027\u5462\u3002\u201d</p><p>\u201c\u559c\u6b22\u8fd9\u79cd\u72b6\u6001\uff1f\u201d</p><p>\u201c\u5f53\u7136\uff0c\u4e00\u7fa4\u6f02\u4eae\u5973\u5b69\u5b50\u5728\u773c\u524d\u8d70\u6765\u8d70\u53bb\uff0c\u603b\u6bd4\u7537\u4eba\u770b\u7740\u8212\u670d\u3002\u201d</p><p>\u201c\u6700\u5173\u952e\u7684\u5730\u65b9\u5728\u4e8e\u5973\u5b69\u5b50\u53ef\u4ee5\u63a8\u5012\u4e0a\u5e8a\uff0c\u7537\u4eba\u4f60\u6ca1\u5174\u8da3\u5bf9\u5427\u3002\u201d</p><p>\u201c\u5443\uff0c\u524d\u8f88\uff0c\u60a8\u80fd\u4e0d\u80fd\u8bf4\u8bdd\u4e0d\u8981\u8fd9\u4e48\u76f4\u63a5\u2026\u2026\u867d\u7136\u6211\u627f\u8ba4\u8fd9\u4e5f\u6ca1\u9519\u5c31\u662f\u4e86\u3002\u201d</p><p>\u201c\u6211\u8bf4\u8bdd\u5f88\u76f4\u63a5\u5417\uff1f\u6211\u53ea\u662f\u5728\u9648\u8ff0\u4e8b\u5b9e\u800c\u5df2\u3002\u4e0a\u6b21\u89c1\u5230\u4f60\u7684\u65f6\u5019\uff0c\u90a3\u4e2a\u8d85\u77ed\u88d9\u5c0f\u5973\u5deb\u8fd8\u53ea\u662f\u670b\u53cb\u5427\uff0c\u73b0\u5728\u90fd\u5df2\u7ecf\u76f4\u63a5\u8eba\u5728\u4f60\u5e8a\u4e0a\u3002\u4f60\u5e72\u90fd\u5e72\u4e86\uff0c\u96be\u9053\u8fd8\u6015\u522b\u4eba\u8bc4\u4ef7\u5417\uff1f\u201d</p><p>\u63d0\u8d77\u51db\uff0c\u4e8e\u662f\u743c\u6069\u53c8\u5f00\u59cb\u90c1\u95f7\u3002</p><p>\u201c\u55ef\uff0c\u600e\u4e48\u770b\u4f60\u7684\u795e\u60c5\u597d\u50cf\u4e0d\u592a\u6ee1\u610f\u4f3c\u7684\uff0c\u96be\u9053\u4e24\u4e2a\u8fd8\u4e0d\u591f\uff1f\u6211\u770b\u5979\u4eec\u4f53\u529b\u5e94\u8be5\u4e0d\u9519\u554a\uff0c\u4f60\u4e00\u4e2a\u4eba\u53ea\u6015\u90fd\u5e94\u4ed8\u4e0d\u4e0b\u6765\u3002\u800c\u4e14\u4f60\u9694\u58c1\u4e0d\u8fd8\u4f4f\u7740\u4e2a\u9b45\u9b54\u5417\uff0c\u867d\u7136\u4e0d\u7b97\u7279\u522b\u6f02\u4eae\uff0c\u8eab\u6750\u5012\u662f\u4e00\u6d41\uff0c\u53ef\u662f\u4f60\u597d\u50cf\u6ca1\u4ec0\u4e48\u5174\u8da3\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u95ee\u9898\u6bd4\u8f83\u590d\u6742\uff0c\u524d\u8f88\uff0c\u201d\u743c\u6069\u53f9\u6c14\uff0c\u201c\u4e00\u65f6\u6211\u4e5f\u8bf4\u4e0d\u6e05\u695a\u2026\u2026\u201d</p><p>\u201c\u90a3\u5c31\u7b80\u5355\u70b9\u8bf4\u5427\u3002\u201d</p><p>\u201c\u2026\u2026\u7b80\u5355\u70b9\u8bf4\u4e48\uff0c\u5c31\u662f\u60a8\u8bf4\u7684\u90a3\u4f4d\u8d85\u77ed\u88d9\u5c0f\u5973\u5deb\u2014\u2014\u5979\u53eb\u51db\u2014\u2014\u4e0d\u592a\u4e56\u5de7\u542c\u8bdd\u3002\u201d</p><p>\u201c\u4e0d\u592a\u542c\u8bdd\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u66f4\u9ebb\u70e6\u7684\u662f\u5979\u8fd8\u6709\u70b9\u767e\u5408\u503e\u5411\u2014\u2014\u767e\u5408\u662f\u4e2a\u4e13\u4e1a\u672f\u8bed\uff0c\u610f\u601d\u5c31\u662f\u8bf4\u2026\u2026\u201d</p><p>\u201c\u767e\u5408\u7684\u610f\u601d\u6211\u61c2\uff0c\u201d\u8428\u9a6c\u65af\u7279\u6325\u6325\u624b\uff0c\u201c\u7ee7\u7eed\u3002\u201d</p><p>\u201c\u6240\u4ee5\u5979\u662f\u52a0\u8fdb\u6765\u4e86\uff0c\u5374\u628a\u6211\u539f\u6765\u7684\u60c5\u4eba\u7ed9\u5360\u4f4f\u4e86\uff0c\u800c\u4e14\u5979\u4eec\u662f\u631a\u53cb\uff0c\u4ece\u5c0f\u4e00\u8d77\u73a9\u5230\u5927\u7684\u4f19\u4f34\uff0c\u5173\u7cfb\u5176\u5b9e\u6bd4\u6211\u5bc6\u5207\u5f97\u591a\u3002\u60a8\u770b\uff0c\u8fd9\u5c31\u662f\u6211\u90c1\u95f7\u7684\u6839\u6e90\u4e86\u3002\u201d</p><p>\u201c\u660e\u767d\u4e86\uff0c\u201d\u8428\u9a6c\u65af\u7279\u70b9\u70b9\u5934\uff0c\u201c\u672c\u6765\u4f60\u6709\u4e00\u4e2a\uff0c\u73b0\u5728\u65b0\u52a0\u8fdb\u6765\u4e00\u4e2a\uff0c\u8868\u9762\u4e0a\u770b\u8d77\u6765\u4f60\u662f\u6709\u4e24\u4e2a\uff0c\u5176\u5b9e\u8fd8\u662f\u53ea\u6709\u4e00\u4e2a\uff0c\u4e0d\u8fc7\u662f\u6362\u4e86\u4eba\u800c\u5df2\u3002\u201d</p><p>\u201c\u6ca1\u9519\u3002\u201d</p><p>\u201c\u53ef\u662f\u8fd9\u542c\u8d77\u6765\u4e5f\u4e0d\u574f\u554a\uff0c\u5c1d\u5c1d\u65b0\u9c9c\u7684\u6709\u4ec0\u4e48\u4e0d\u597d\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u4e48\uff0c\u524d\u8f88\uff0c\u6211\u662f\u6bd4\u8f83\u8d2a\u5fc3\u2026\u2026\u4e0d\uff0c\u6211\u662f\u6bd4\u8f83\u8d1f\u8d23\u7684\u7537\u4eba\uff0c\u6240\u4ee5\u6211\u4e0d\u80fd\u559c\u65b0\u538c\u65e7\u561b\u3002\u201d</p><p>\u201c\u8fd9\u6837\u554a\uff0c\u201d\u8428\u9a6c\u65af\u7279\u601d\u7d22\u7740\uff0c\u7a81\u7136\u53c8\u662f\u4e00\u9635\u731b\u70c8\u54b3\u55fd\uff0c\u6574\u4e2a\u4e0a\u534a\u8eab\u8db4\u5728\u684c\u4e0a\uff0c\u51e0\u4e4e\u8fde\u6c14\u90fd\u8981\u5598\u4e0d\u8fc7\u6765\u4f3c\u7684\uff0c\u201c\u9152\uff0c\u201d\u4ed6\u5636\u54d1\u7740\u58f0\u97f3\u8bf4\uff0c\u201c\u62ff\u7ed9\u6211\u3002\u201d</p><p>\u743c\u6069\u8d76\u5feb\u628a\u684c\u4e0a\u53e6\u5916\u4e00\u676f\u70c8\u7130\u9152\u9012\u5230\u4ed6\u624b\u4e2d\uff0c\u8428\u9a6c\u65af\u7279\u52c9\u5f3a\u6491\u8d77\u8eab\uff0c\u704c\u4e86\u51e0\u53e3\u3002\u4ed6\u559d\u5f97\u592a\u5feb\uff0c\u7ed3\u679c\u88ab\u545b\u5230\u4e86\uff0c\u54b3\u55fd\u66f4\u52a0\u5267\u70c8\uff0c\u743c\u6069\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u7136\u540e\u8d77\u8eab\u7ed5\u8fc7\u684c\u8fb9\uff0c\u66ff\u4ed6\u8f7b\u8f7b\u6376\u80cc\u3002</p><p>\u8fc7\u4e86\u597d\u534a\u5929\uff0c\u8428\u9a6c\u65af\u7279\u624d\u7ec8\u4e8e\u53c8\u7f13\u8fc7\u52b2\u6765\uff0c\u201c\u591a\u8c22\uff0c\u201d\u4ed6\u4f4e\u58f0\u8bf4\uff0c\u201c\u4eba\u8001\u4e86\uff0c\u8eab\u4f53\u5c31\u8d8a\u6765\u8d8a\u5dee\u3002\u201d</p><p>\u201c\u90a3\u662f\u3002\u201d\u743c\u6069\u8d76\u5feb\u9644\u548c\u3002</p><p>\u4ed6\u56de\u5230\u5ea7\u4f4d\uff0c\u62db\u624b\u53eb\u6765\u4f8d\u8005\uff0c\u53c8\u8981\u4e86\u4e24\u676f\u70c8\u7130\u9152\u653e\u5728\u65c1\u8fb9\u5907\u7528\u3002\u8428\u9a6c\u65af\u7279\u6162\u6162\u628a\u4ed6\u624b\u4e2d\u7684\u4e00\u676f\u559d\u5b8c\uff0c\u8138\u8272\u6e10\u6e10\u6062\u590d\u8fc7\u6765\uff0c\u201c\u5982\u679c\u4f60\u662f\u4e3a\u8fd9\u4e2a\u70e6\u607c\u4e48\uff0c\u6211\u89c9\u5f97\u5b8c\u5168\u6ca1\u5fc5\u8981\u554a\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u5f88\u5bb9\u6613\u89e3\u51b3\u7684\u561b\u3002\u201d</p><p>\u201c\u5f88\u5bb9\u6613\uff1f\u201d\u743c\u6069\u7cbe\u795e\u4e00\u632f\uff0c\u201c\u8bf7\u6307\u70b9\u3002\u201d</p><p>\u201c\u7b80\u5355\u554a\uff0c\u5979\u4e0d\u592a\u542c\u8bdd\u662f\u5427\uff0c\u4f60\u76f4\u63a5\u628a\u5979\u7ed1\u8d77\u6765\u6254\u4e00\u8fb9\uff0c\u81ea\u5df1\u7167\u6837\u548c\u4f60\u90a3\u4e2a\u5723\u6b66\u58eb\u60c5\u4eba\u505a\uff0c\u7b49\u723d\u591f\u4e86\u518d\u6765\u73a9\u5979\uff0c\u5148\u5978\u540e\u6740\u2026\u2026\u54e6\uff0c\u9519\u4e86\uff0c\u4e0d\u80fd\u6740\uff0c\u90a3\u5c31\u5148\u5978\u518d\u5978\uff0c\u4e0d\u5c31\u4e00\u5207\u641e\u5b9a\u4e86\uff0c\u6709\u4ec0\u4e48\u95ee\u9898\u3002\u5982\u679c\u4f60\u5bf9\u5982\u4f55\u6346\u7ed1\u5973\u6027\u4e0d\u592a\u4e86\u89e3\u7684\u8bdd\uff0c\u6211\u53ef\u4ee5\u5411\u4f60\u63a8\u8350\u672c\u4e66\uff0c\u4ee5\u4f60\u7684\u806a\u660e\u4e00\u5b9a\u5b66\u5f97\u5f88\u5feb\u3002\u201d</p><p>\u201c\u524d\u8f88\uff0c\u60a8\u2026\u2026\u201d</p><p>\u201c\u5f88\u60ca\u8bb6\uff1f\u6211\u5e74\u8f7b\u7684\u65f6\u5019\u4e5f\u662f\u66fe\u7ecf\u98ce\u6d41\u8fc7\u7684\u561b\uff0c\u201d\u8428\u9a6c\u65af\u7279\u53f9\u6c14\u7740\uff0c\u201c\u53ef\u60dc\u90a3\u65f6\u5019\u592a\u5e74\u8f7b\uff0c\u4e0d\u61c2\u4ec0\u4e48\u662f\u771f\u6b63\u7684\u7231\u60c5\u554a\u2026\u2026\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u524d\u8f88\uff0c\u60a8\u80fd\u4e0d\u80fd\u518d\u63d0\u4f9b\u70b9\u5176\u4ed6\u7684\u521b\u610f\u3002\u201d</p><p>\u201c\u6ca1\u95ee\u9898\u554a\uff0c\u5982\u679c\u4f60\u5acc\u6346\u7ed1\u592a\u96be\u5b66\uff0c\u90a3\u76f4\u63a5\u4e00\u4e2a\u5b9a\u8eab\u672f\u5c31\u662f\u4e86\uff0c\u4f60\u662f\u5deb\u5e08\u561b\uff0c\u8fd9\u8fd8\u9700\u8981\u6211\u6559\u4f60\uff1f\u5f53\u7136\uff0c\u5b9a\u8eab\u672f\u6301\u7eed\u65f6\u95f4\u592a\u77ed\u4e86\uff0c\u4ee5\u4f60\u7684\u9020\u8be3\u9876\u591a\u5b9a\u4f4f\u5979\u51e0\u5206\u949f\uff0c\u53ea\u6015\u8fd8\u6765\u4e0d\u53ca\u723d\u591f\u2026\u2026\u6709\u4e86\uff0c\u6211\u4ee5\u524d\u66fe\u7ecf\u53d1\u660e\u8fc7\u4e00\u4e2a\u6cd5\u672f\uff0c\u53eb\u505a\u9ebb\u75f9\u4e4b\u89e6\uff0c\u4e00\u78b0\u5c31\u80fd\u8ba9\u4eba\u9ebb\u75f9\u5f88\u4e45\uff0c\u81f3\u5c11\u534a\u5c0f\u65f6\u6ca1\u95ee\u9898\uff0c\u4f60\u8981\u4e0d\u8981\u5b66\uff1f\u201d</p><p>\u201c\u8fd9\u540d\u5b57\u2026\u2026\u662f\u4ea1\u7075\u672f\u5427\u3002\u201d</p><p>\u201c\u5f53\u7136\uff0c\u6211\u5c31\u662f\u4ea1\u7075\u5e08\u561b\u3002\u201d</p><p>\u201c\u90a3\u7b97\u4e86\uff0c\u201d\u743c\u6069\u8d76\u5feb\u63a8\u6258\uff0c\u201c\u6211\u5728\u4ea1\u7075\u672f\u4e0a\u5b8c\u5168\u6ca1\u6709\u534a\u70b9\u5929\u8d4b\u7684\uff0c\u80af\u5b9a\u8f9c\u8d1f\u60a8\u7684\u671f\u671b\u3002\u201d</p><p>\u201c\u4f60\u4e0d\u559c\u6b22\u4ea1\u7075\u672f\uff1f\u90a3\u4f60\u600e\u4e48\u62dc\u5965\u6c83\u5f53\u8001\u5e08\uff0c\u90a3\u6b7b\u80d6\u5b50\u4e5f\u662f\u4e13\u95e8\u73a9\u4ea1\u7075\u672f\u7684\u554a\u3002\u201d</p><p>\u201c\u524d\u8f88\uff0c\u60a8\u5f04\u9519\u4e86\uff0c\u4e0d\u662f\u6211\u8981\u62dc\u4ed6\u5f53\u8001\u5e08\uff0c\u662f\u4ed6\u6c42\u6211\u5f53\u5b66\u751f\u554a\u3002\u201d</p><p>\u201c\u662f\u5417\uff1f\u201d\u8428\u9a6c\u65af\u7279\u4e0a\u4e0a\u4e0b\u4e0b\u628a\u743c\u6069\u6253\u91cf\u4e86\u4e00\u904d\uff0c\u201c\u770b\u4f60\u4e5f\u4e0d\u50cf\u662f\u8bf4\u8c0e\uff0c\u4e0b\u6b21\u89c1\u5230\u6211\u53bb\u95ee\u95ee\u4ed6\u3002\u597d\u5427\uff0c\u5148\u4e0d\u8bf4\u8fd9\u4e2a\uff0c\u90a3\u4f60\u6700\u64c5\u957f\u4ec0\u4e48\u2026\u2026\u53d8\u5316\u672f\uff1f\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u201c\u90a3\u4e5f\u5f88\u5bb9\u6613\u554a\uff0c\u53d8\u5316\u672f\u4e2d\u4e0d\u662f\u6709\u77f3\u5316\u672f\u5417\uff0c\u4f60\u5148\u628a\u5979\u53d8\u6210\u77f3\u50cf\u653e\u4e00\u8fb9\uff0c\u7b49\u8981\u5e72\u5979\u65f6\u5c31\u518d\u89e3\u9664\u77f3\u5316\u53d8\u56de\u6765\uff0c\u591a\u7b80\u5355\u3002\u201d</p><p>\u201c\u524d\u8f88\uff0c\u60a8\u5e94\u8be5\u770b\u5f97\u51fa\u6765\uff0c\u77f3\u5316\u672f\u548c\u89e3\u9664\u77f3\u5316\uff0c\u8fd9\u5df2\u7ecf\u662f\u6211\u76ee\u524d\u80fd\u638c\u63e1\u7684\u6700\u5f3a\u6cd5\u672f\u4e86\uff0c\u60a8\u8981\u6211\u6d6a\u8d39\u4e24\u4e2a\u6700\u5f3a\u7684\u6cd5\u672f\uff0c\u5c31\u4e3a\u4e86\u8fd9\u4e8b\u2026\u2026\u8fd9\u662f\u4e0d\u662f\u592a\u532a\u5937\u6240\u601d\u4e86\u70b9\u3002\u201d</p><p>\u201c\u8fd9\u6709\u4ec0\u4e48\u532a\u5937\u6240\u601d\u7684\uff0c\u662f\u4f60\u7684\u6027\u798f\u751f\u6d3b\u91cd\u8981\u8fd8\u662f\u4e24\u4e2a\u6cd5\u672f\u91cd\u8981\uff1f\u201d</p><p>\u201c\u5f53\u7136\u662f\u6027\u798f\u91cd\u8981\uff0c\u4f46\u6ca1\u4e86\u6cd5\u672f\u6211\u8fde\u6027\u547d\u5c31\u4f1a\u6709\u5371\u9669\u4e86\u2026\u2026\u800c\u4e14\u95ee\u9898\u4e0d\u5728\u4e8e\u8fd9\u91cc\u554a\u524d\u8f88\uff0c\u60a8\u4e0d\u89c9\u5f97\u60a8\u7684\u8fd9\u4e9b\u65b9\u6cd5\uff0c\u6346\u7ed1\u3001\u5b9a\u8eab\u3001\u9ebb\u75f9\u3001\u77f3\u5316\u2026\u2026\u90fd\u592a\u8fc7\u5206\u4e86\u5417\uff1f\u201d</p><p>\u201c\u8fc7\u5206\uff1f\u8fd9\u6709\u4ec0\u4e48\u8fc7\u5206\u7684\uff0c\u5979\u662f\u5973\u4eba\uff0c\u4f60\u662f\u7537\u4eba\uff0c\u4f60\u4e0a\u5979\u5929\u7ecf\u5730\u4e49\u3002\u5982\u679c\u8fd9\u90fd\u7b97\u8fc7\u5206\uff0c\u96be\u9053\u5979\u6765\u62a2\u4f60\u7684\u60c5\u4eba\u5c31\u4e0d\u8fc7\u5206\u4e86\uff1f\u201d</p><p>\u201c\u8bdd\u4e0d\u662f\u8fd9\u4e48\u8bf4\u5427\u2026\u2026\u201d</p><p>\u201c\u8bdd\u5c31\u662f\u8fd9\u4e48\u8bf4\u7684\uff0c\u201d\u8428\u9a6c\u65af\u7279\u5f88\u80af\u5b9a\u5730\u70b9\u5934\uff0c\u201c\u800c\u4e14\u8fd9\u538b\u6839\u5c31\u4e0d\u8fc7\u5206\u3002\u56e0\u4e3a\u4f60\u592a\u5e74\u8f7b\uff0c\u5fc3\u7075\u6bd4\u8f83\u8106\u5f31\uff0c\u6240\u4ee5\u6211\u5df2\u7ecf\u662f\u5c3d\u91cf\u6311\u9009\u6bd4\u8f83\u6e29\u548c\u7684\u65b9\u6cd5\u4e86\u3002\u5982\u679c\u6362\u4e86\u6211\u7684\u8bdd\uff0c\u6211\u5c31\u76f4\u63a5\u7ed9\u5979\u6d17\u8111\uff0c\u628a\u5979\u53d8\u6210\u610f\u8bc6\u5080\u5121\uff0c\u6216\u8005\u8bad\u7ec3\u6210\u7f8e\u5973\u72ac\uff0c\u5230\u65f6\u5019\u81ea\u7136\u4f60\u60f3\u600e\u4e48\u73a9\u5c31\u600e\u4e48\u73a9\u2014\u2014\u8fd9\u4e2a\u4e3b\u610f\u5982\u4f55\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u2026\u2026\u60a8\u7684\u53e3\u5473\u662f\u4e0d\u662f\u592a\u91cd\u4e86\u3002\u201d</p><p>\u201c\u4e0d\u91cd\u4e0d\u91cd\uff0c\u201d\u8428\u9a6c\u65af\u7279\u8fde\u8fde\u6447\u5934\uff0c\u201c\u6211\u8fd8\u6ca1\u8bf4\u5b8c\u5462\u3002\u65e2\u7136\u5979\u4e0d\u591f\u4e56\u5de7\u542c\u8bdd\uff0c\u8ba9\u6211\u4e0d\u9ad8\u5174\uff0c\u90a3\u8bf4\u660e\u5979\u7684\u5bb6\u5ead\u6ca1\u6709\u5c3d\u5230\u6559\u80b2\u7684\u8d23\u4efb\uff0c\u6240\u4ee5\u6211\u4f1a\u53bb\u628a\u5979\u7236\u6bcd\u5144\u5f1f\u59d0\u59b9\u4e00\u8d77\u90fd\u6293\u6765\uff0c\u7537\u7684\u6740\u4e86\uff0c\u5973\u7684\u8c03\u6559\u6210\u6027\u5974\uff0c\u5230\u65f6\u5019\u6bcd\u5973\u59d0\u59b9\u4e00\u8d77\u4e0a\u624b\u2014\u2014\u8fd9\u4e2a\u4e3b\u610f\u5982\u4f55\uff0c\u8981\u4e0d\u8981\u5c1d\u8bd5\uff1f\u201d</p><p>\u201c\u4e0d\u7528\u4e86\uff0c\u5979\u7236\u6bcd\u65e9\u53bb\u4e16\u4e86\uff0c\u4e5f\u6839\u672c\u6ca1\u59d0\u59b9\uff0c\u552f\u4e00\u4e00\u4e2a\u60c5\u540c\u59d0\u59b9\u7684\u2026\u2026\u5df2\u7ecf\u88ab\u6211\u63a8\u5012\u4e86\u3002\u201d</p><p>\u201c\u90a3\u6837\u554a\uff0c\u771f\u53ef\u60dc\uff0c\u201d\u8428\u9a6c\u65af\u7279\u8bf4\uff0c\u201c\u6211\u770b\u5979\u633a\u6f02\u4eae\uff0c\u5982\u679c\u6bcd\u4eb2\u8fd8\u5728\u7684\u8bdd\u4e00\u5b9a\u4e5f\u662f\u4e2a\u5927\u7f8e\u4eba\uff0c\u53ef\u60dc\u4f60\u6ca1\u8fd9\u4e2a\u798f\u6c14\u2026\u2026\u4e0d\u8fc7\u6ca1\u5173\u7cfb\u554a\uff0c\u5979\u6ca1\u6bcd\u4eb2\uff0c\u5374\u6709\u8001\u5e08\u554a\u3002\u201d</p><p>\u743c\u6069\u4e00\u53e3\u545b\u4f4f\u4e86\u3002</p><p>\u201c\u8001\u5e08\uff1f\u524d\u8f88\u60a8\u662f\u8bf4\u6b23\u5e03\u965b\u4e0b\u2026\u2026\u201d</p><p>\u201c\u662f\u554a\uff0c\u6b23\u5e03\u4e0d\u662f\u5979\u8001\u5e08\u5417\uff0c\u6211\u770b\u5979\u4eec\u4e24\u4eba\u5176\u5b9e\u6027\u683c\u633a\u50cf\u7684\uff0c\u4f60\u5c31\u5f53\u662f\u6bcd\u5973\u4e5f\u65e0\u4e0d\u53ef\u554a\u3002\u55ef\uff0c\u8bf4\u8d77\u6765\uff0c\u53e6\u5916\u90a3\u4e2a\u8475\u9732\u5012\u4e5f\u4e0d\u9519\uff0c\u800c\u4e14\u662f\u4e2a\u5353\u5c14\uff0c\u5982\u679c\u4f60\u4e0d\u6311\u5254\u7684\u8bdd\u4e5f\u53ef\u4ee5\u5c1d\u5c1d\uff0c\u542c\u8bf4\u522b\u6709\u6ecb\u5473\u3002\u6211\u5bf9\u9ed1\u76ae\u80a4\u7684\u5973\u4eba\u6bd4\u8f83\u8fc7\u654f\uff0c\u5c31\u6ca1\u8bd5\u8fc7\u4e86\u3002\u201d</p><p>\u743c\u6069\u53f9\u6c14\uff0c\u201c\u597d\u5427\uff0c\u522b\u62ff\u6211\u5f00\u73a9\u7b11\u4e86\uff0c\u524d\u8f88\uff0c\u8fd8\u662f\u8bf4\u6b63\u4e8b\u5427\u3002\u201d</p><p>\u201c\u4f60\u4e3a\u4ec0\u4e48\u89c9\u5f97\u6211\u4e0d\u662f\u5728\u8bf4\u6b63\u4e8b\u5462\uff1f\u4f60\u770b\u6211\u7684\u773c\u795e\uff0c\u96be\u9053\u4e0d\u662f\u5f88\u8bda\u6073\u5f88\u4e25\u8083\u5f88\u8ba4\u771f\u5417\uff1f\u201d</p><p>\u201c\u53ef\u662f\u60a8\u8bf4\u7684\u6839\u672c\u5c31\u662f\u80e1\u626f\u2026\u2026\u6839\u672c\u5c31\u4e0d\u53ef\u80fd\u554a\uff0c\u201d\u743c\u6069\u644a\u624b\uff0c\u201c\u9996\u5148\uff0c\u6211\u662f\u4e2a\u6b63\u4eba\u541b\u5b50\uff0c\u6240\u4ee5\u6211\u600e\u4e48\u80fd\u50cf\u60a8\u90a3\u6837\u5fc3\u7406\u53d8\u6001\u5462\uff0c\u4ec0\u4e48\u610f\u8bc6\u5080\u5121\u7f8e\u5973\u72ac\u4ec0\u4e48\u6bcd\u5973\u59d0\u59b9\u2026\u2026\u597d\u5427\uff0c\u6211\u627f\u8ba4\u6211\u5012\u662f\u633a\u559c\u6b22\u59d0\u59b9\u82b1\u7684\uff0c\u4f46\u5176\u4ed6\u5c31\u7b97\u4e86\uff1b\u5176\u6b21\uff0c\u6211\u662f\u4e2a\u667a\u5546\u6b63\u5e38\u7684\u4eba\uff0c\u4f60\u8981\u6211\u53bb\u6253\u6b23\u5e03\u965b\u4e0b\u7684\u4e3b\u610f\uff0c\u90a3\u6211\u4e0d\u5982\u76f4\u63a5\u81ea\u6740\u7b97\u4e86\u2026\u2026\u201d</p><p>\u201c\u4e5f\u5c31\u662f\u8bf4\u4f60\u5176\u5b9e\u662f\u5bb3\u6015\uff1f\u201d</p><p>\u201c\u524d\u8f88\uff0c\u6211\u4e0d\u662f\u60a8\u554a\uff0c\u6211\u5f53\u7136\u5bb3\u6015\uff0c\u8fd9\u662f\u7406\u6240\u5f53\u7136\u7684\u5427\u3002\u751f\u6d3b\u5982\u6b64\u7f8e\u597d\uff0c\u6211\u8fd8\u6ca1\u6d3b\u5f97\u4e0d\u8010\u70e6\u5462\u2026\u2026\u201d</p><p>\u201c\u522b\u90a3\u4e48\u591a\u5e9f\u8bdd\uff0c\u201d\u8428\u9a6c\u65af\u7279\u4e00\u53e3\u622a\u65ad\uff0c\u201c\u4e00\u53e5\u8bdd\uff1a\u5982\u679c\u73b0\u5728\u6211\u544a\u8bc9\u4f60\uff0c\u6211\u53ef\u4ee5\u5e2e\u4f60\u628a\u90a3\u4e24\u4e2a\u9009\u6c11\u641e\u5b9a\uff0c\u4f60\u6709\u6ca1\u6709\u5174\u8da3\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u641e\u5b9a\u7684\u610f\u601d\u662f\u2026\u2026\u201d</p><p>\u201c\u5c31\u662f\u4f60\u73b0\u5728\u5fc3\u91cc\u6b63\u5728\u60f3\u7684\u610f\u601d\u4e86\uff0c\u6211\u80fd\u5e2e\u4f60\u628a\u5979\u4eec\u5236\u4f4f\uff0c\u8ba9\u5979\u4eec\u4e27\u5931\u53cd\u6297\u80fd\u529b\u3002\u81f3\u4e8e\u4f60\u662f\u60f3\u4fdd\u9669\u8d77\u89c1\uff0c\u5148\u5978\u540e\u6740\uff0c\u5c1d\u8fc7\u5c31\u7b97\uff1b\u6216\u8005\u662f\u60f3\u957f\u671f\u4eab\u7528\uff0c\u8c03\u6559\u6210\u6027\u5974\u7f8e\u5973\u72ac\uff1b\u6216\u8005\u662f\u73a9\u73a9\u611f\u60c5\u6e38\u620f\uff0c\u56e0\u5978\u751f\u7231\uff0c\u65e5\u4e45\u751f\u60c5\uff0c\u5168\u90fd\u968f\u4f60\u4fbf\u3002\u600e\u4e48\u6837\uff0c\u6709\u6ca1\u6709\u5174\u8da3\u3002\u201d</p><p>\u743c\u6069\u6014\u4e86\u4e09\u79d2\u949f\uff0c\u7136\u540e\u7a81\u7136\u7b11\u4e86\u8d77\u6765\u3002\u201c\u60a8\u786e\u5b9e\u662f\u5728\u548c\u6211\u8c08\u6b63\u4e8b\uff0c\u201d\u4ed6\u8f7b\u58f0\u8bf4\uff0c\u201c\u6211\u73b0\u5728\u660e\u767d\u4e86\u3002\u201d</p></article>"}}}]);
