(window.webpackJsonp=window.webpackJsonp||[]).push([[331],{354:function(p){p.exports={chapterTitle:null,title:"\u7b2c209\u7ae0 \u76db\u946b\u7684\u65b0\u654c",content:"<article><h2>\u7b2c209\u7ae0 \u76db\u946b\u7684\u65b0\u654c</h2><p>\u3000\u3000\u4e0d\u7ba1\u600e\u4e48\u6765\u8bf4\uff0c\u5468\u5bcc\u660e\u624d\u662f\u5bf9\u6d77\u5dde\u5e02\u7684\u5927\u5b97\u5546\u4e1a\u7528\u5730\u62e5\u6709\u6700\u7ec8\u51b3\u5b9a\u6743\u7684\u4eba\uff0c\u4f46\u662f\u6b64\u65f6\u7684\u4e07\u52c7\u4f3c\u4e4e\u7b3c\u7f69\u7740\u8fc7\u591a\u7684\u5149\u8292\uff0c\u9526\u6e56\u4e5f\u6d41\u9732\u51fa\u6d89\u8db3\u5546\u4e1a\u5730\u4ea7\u7684\u610f\u613f\uff0c\u9526\u6e56\u7684\u8463\u4e8b\u90b5\u81f3\u521a\u76f8\u5f53\u6d3b\u8dc3\uff0c\u5b8f\u8fdc\u66f4\u662f\u76f4\u63a5\u5c31\u8981\u4e0a\u9a6c\u4e0e\u6c99\u7530\u65e7\u57ce\u6539\u9020\u9879\u76ee\u914d\u5957\u7684\u5b89\u7f6e\u623f\u9879\u76ee\uff0c\u8fd9\u4e9b\u90fd\u5e94\u8be5\u7531\u4ed6\u5468\u5bcc\u660e\u6765\u62cd\u677f\u51b3\u7b56\uff0c\u4f46\u662f\u73b0\u5728\u5c31\u8fde\u53f6\u5bb6\u90fd\u8981\u53bb\u8ba8\u597d\u4e07\u52c7\uff0c\u5468\u5bcc\u660e\u5fc3\u91cc\u591a\u5c11\u4e0d\u662f\u6ecb\u5473\u3002</p><p>\u3000\u3000\u81ea\u4ece\u51b3\u5b9a\u79bb\u95f4\u4e07\u52c7\u4e0e\u5468\u5bcc\u660e\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u5510\u5b66\u8c26\u5bf9\u4e07\u52c7\u7684\u6001\u5ea6\u6709\u7740\u5fae\u5999\u7684\u8f6c\u53d8\uff0c\u9996\u5148\u5728\u571f\u5730\u51b3\u7b56\u4e8b\u60c5\u4e0a\u6539\u53d8\u6d88\u6781\u5e94\u5bf9\u7684\u6001\u5ea6\u3002\u5728\u6b64\u4e4b\u524d\uff0c\u6d77\u5dde\u5e02\u5546\u4e1a\u7528\u5730\uff0c\u5c0f\u4e8b\u7531\u4e07\u52c7\u51b3\u5b9a\uff0c\u5510\u5b66\u8c26\u4e0d\u8fc7\u95ee\uff0c\u5927\u4e8b\u5219\u62ff\u5230\u5e38\u59d4\u4f1a\u8bae\u4e0a\u8ba8\u8bba\uff0c\u7531\u5468\u5bcc\u660e\u95f4\u63a5\u63a7\u5236\u3002\u73b0\u5728\uff0c\u5510\u5b66\u8c26\u90fd\u4f1a\u5728\u4e07\u52c7\u63d0\u4ea4\u7684\u5546\u4e1a\u7528\u5730\u9879\u76ee\u5ba1\u6279\u6587\u4ef6\u4e0a\u79ef\u6781\u7684\u9644\u7b7e\uff0c\u5546\u4e1a\u7528\u5730\u9879\u76ee\u5ba1\u6279\u4e0a\u4e00\u65e6\u6709\u5510\u5b66\u8c26\u7684\u9644\u7b7e\uff0c\u5f88\u591a\u9879\u76ee\u5c31\u4e0d\u9700\u8981\u518d\u62ff\u5230\u5e38\u59d4\u4f1a\u8bae\u4e0a\u8ba8\u8bba\uff0c\u53d8\u76f8\u7684\u8ba9\u4e07\u52c7\u62e5\u6709\u66f4\u5927\u7684\u51b3\u7b56\u6743\u9650\u3002</p><p>\u3000\u3000\u76f8\u5e94\u7684\u7ed9\u5468\u5bcc\u660e\u9020\u6210\u4e07\u52c7\u8d8a\u6765\u8d8a\u4e0d\u91cd\u89c6\u4ed6\u7684\u60f3\u6cd5\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u4e0e\u4e07\u52c7\u5173\u7cfb\u7684\u5fae\u5999\u8f6c\u53d8\uff0c\u5982\u6897\u5728\u54bd\u5589\u7684\u4e00\u6839\u523a\uff0c\u8ba9\u5468\u5bcc\u660e\u5fc3\u91cc\u5f88\u4e0d\u8212\u670d\uff0c\u8fd9\u624d\u662f\u8ba9\u5468\u5bcc\u660e\u6700\u5728\u610f\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5468\u5bcc\u660e\u60f3\u5230\u81ea\u5df1\u4eca\u5e74\u90fd\u4e94\u5341\u516d\u4e86\uff0c\u5e72\u5b8c\u8fd9\u4e00\u5c4a\uff0c\u7701\u91cc\u8981\u6ca1\u6709\u7279\u522b\u5f3a\u786c\u7684\u652f\u6301\uff0c\u4ed6\u5c31\u8981\u9000\u4e8c\u7ebf\u4e86\uff1b\u5c31\u7b97\u6ca1\u6709\u5f90\u5b66\u5e73\u8fd9\u4e00\u5c42\u56e0\u7d20\uff0c\u5468\u5bcc\u660e\u90fd\u4e0d\u60f3\u4e0e\u5510\u5b66\u8c26\u76f4\u63a5\u8d77\u51b2\u7a81\uff0c\u5510\u5b66\u8c26\u624d\u56db\u5341\u56db\u5c81\uff0c\u5c31\u7b97\u81ea\u5df1\u80fd\u518d\u5e72\u4e00\u5c4a\uff0c\u524d\u540e\u4e5f\u5c31\u538b\u5236\u5510\u5b66\u8c26\u4e5d\u5e74\uff0c\u8fc7\u4e86\u8fd9\u4e5d\u5e74\uff0c\u4ed6\u4e5f\u624d\u4e94\u5341\u4e09\u5c81\uff0c\u6b63\u503c\u5f97\u5e74\u5bcc\u529b\u5f3a\u7684\u58ee\u5e74\uff0c\u8981\u8003\u8651\u5230\u65e5\u540e\u597d\u76f8\u89c1\u3002</p><p>\u3000\u3000\u5728\u81ea\u5df1\u653f\u6cbb\u524d\u9014\u8d8a\u8d70\u8d8a\u7a84\uff0c\u800c\u5510\u5b66\u8c26\u7684\u524d\u7a0b\u5374\u65e5\u76ca\u5149\u660e\uff0c\u5f90\u5b66\u5e73\u5012\u4e5f\u7f62\u4e86\uff0c\u73b0\u5728\u7adf\u8ddf\u53f6\u5bb6\u4eb2\u70ed\u8d77\u6765\uff0c\u4e07\u52c7\u4e0e\u5510\u5b66\u8c26\u4e4b\u95f4\u7684\u5173\u7cfb\u53d8\u5316\uff0c\u8ba9\u5468\u5bcc\u660e\u60f3\u5230\u53e6\u4e00\u79cd\u53ef\u6015\u7684\u53ef\u80fd\u3002</p><p>\u3000\u3000\u5229\u76ca\u6240\u5f62\u6210\u7684\u94fe\u6761\u5f88\u5bb9\u6613\u56e0\u4e3a\u5229\u76ca\u800c\u65ad\u88c2\uff0c\u975e\u5e38\u7684\u8106\u5f31\uff0c\u5510\u5b66\u8c26\u7684\u653f\u6cbb\u524d\u7a0b\u53c8\u662f\u90a3\u6837\u7684\u5149\u660e\uff0c\u5c31\u7b97\u4e07\u52c7\u6709\u8f6c\u6295\u9635\u8425\u7684\u5fc3\u601d\uff0c\u4e5f\u4e0d\u662f\u5f88\u5947\u602a\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u770b\u5230\u53f6\u5efa\u658c\u3001\u5510\u5b66\u8c26\u3001\u4e07\u52c7\u7ad9\u5728\u4e00\u8d77\u548c\u8a00\u60a6\u8272\u7684\u4ea4\u8c08\uff0c\u5468\u5bcc\u660e\u5fc3\u91cc\u8d8a\u6765\u8d8a\u4e0d\u662f\u6ecb\u5473\uff0c\u52a0\u4e0a\u53f6\u5efa\u658c\u523b\u610f\u5bf9\u4ed6\u7684\u51b7\u843d\uff0c\u6574\u4e2a\u665a\u5bb4\uff0c\u5468\u5bcc\u660e\u7684\u8138\u8272\u4e00\u76f4\u9634\u6092\u4e0d\u6674\u3002</p><p>\u3000\u3000\u5728\u8fd9\u79cd\u573a\u5408\uff0c\u53ea\u8981\u53f6\u5efa\u658c\u4e0d\u5174\u594b\u7684\u7ea0\u7f20\u4ed6\uff0c\u5f20\u606a\u81ea\u7136\u4e0d\u4f1a\u5f15\u4eba\u6ce8\u76ee\uff0c\u4ed6\u9759\u9759\u7684\u89c2\u5bdf\u7740\u665a\u5bb4\u4e0a\u7684\u6bcf\u4e00\u5904\u7ec6\u8282\u3002</p><p>\u3000\u3000\u76db\u946b\u8fd9\u6b21\u4e3e\u529e\u7684\u7b54\u8c22\u5bb4\u9080\u8bf7\u6d77\u5dde\u5e02\u7684\u8bb8\u591a\u653f\u5546\u540d\u6d41\uff0c\u5c31\u662f\u8981\u7ed9\u67d0\u4e9b\u4eba\u9519\u8bef\u7684\u5370\u8c61\uff1a\u76db\u946b\u8981\u5728\u6d77\u5dde\u6301\u7eed\u6709\u5927\u52a8\u4f5c\uff0c\u8fd9\u624d\u5e7f\u7ed3\u6d77\u5dde\u653f\u5546\u540d\u6d41\u3002</p><p>\u3000\u3000\u897f\u57ce\u5356\u573a\u7684\u6210\u529f\uff0c\u5f53\u7136\u4f1a\u4fc3\u4f7f\u76db\u946b\u63a5\u4e0b\u6765\u4f1a\u6709\u5927\u7684\u52a8\u4f5c\uff0c\u4f46\u662f\u76db\u946b\u7684\u6b65\u4f10\u6bd4\u522b\u4eba\u9884\u60f3\u7684\u8981\u5927\u5f97\u591a\u3002</p><p>\u3000\u3000\u5f20\u606a\u56de\u5230\u6d77\u5dde\u7684\u8fd9\u51e0\u5929\uff0c\u9664\u4e86\u8981\u5904\u7406\u516c\u53f8\u7684\u4e8b\u52a1\uff0c\u8fd8\u8981\u7ed9\u53f6\u5efa\u658c\u7f20\u7740\u542c\u4ed6\u7684\u5b8f\u5927\u8bbe\u60f3\uff0c\u897f\u57ce\u5356\u573a\u7684\u6210\u529f\uff0c\u8ba9\u53f6\u5efa\u658c\u7684\u4fe1\u5fc3\u5f88\u53d7\u9f13\u821e\uff0c\u4ed6\u8ba1\u5212\u4e09\u5230\u56db\u4e2a\u6708\u65f6\u95f4\u76f4\u63a5\u5728\u7701\u57ce\u5f00\u8bbe\u76db\u946b\u73af\u7403\u7535\u5668\u7684\u65d7\u8230\u5356\u573a\uff0c\u5360\u636e\u7701\u5185\u5546\u4e1a\u9886\u57df\u7684\u5236\u9ad8\u70b9\uff0c\u7ec4\u5efa\u5b8c\u5584\u7684\u7269\u6d41\u3001\u5ba2\u670d\u4f53\u7cfb\uff0c\u8ffd\u6c42\u76db\u946b\u5728\u7ec8\u7aef\u5546\u4e1a\u7f51\u7edc\u7684\u5730\u4f4d\uff0c\u7136\u540e\u5728\u7701\u57ce\u5927\u91cf\u590d\u5236\u6d77\u5dde\u897f\u57ce\u5356\u573a\u6a21\u5f0f\u7684\u540c\u65f6\uff0c\u518d\u5411\u6574\u4e2a\u534e\u4e1c\u5730\u533a\u6269\u5f20\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u6e05\u695a\u76db\u946b\u7684\u4e3e\u52a8\u4f1a\u4e0d\u4f1a\u6fc0\u53d1\u82cf\u5b81\u7535\u5668\u63d0\u524d\u5168\u9762\u8fdb\u5165\u5bb6\u7535\u8fde\u9501\u4e1a\uff0c\u4f46\u662f\u73b0\u5728\u8003\u8651\u8fd9\u4e9b\u8fd8\u592a\u65e9\uff0c\u5bb6\u7535\u8fde\u9501\u5728\u56fd\u5185\u51e0\u4e4e\u8fd8\u662f\u7a7a\u767d\uff0c\u4ee5\u9ad8\u6b4c\u731b\u8fdb\u7684\u8c6a\u60c5\u653b\u57ce\u7565\u5730\u624d\u662f\u76db\u946b\u8981\u505a\u7684\u4e8b\u4e1a\uff0c\u5f20\u606a\u53ea\u60f3\u77e5\u9053\u53f6\u5bb6\u6240\u638c\u63e1\u7684\u8d44\u91d1\u94fe\u4f1a\u652f\u6491\u4ed6\u4eec\u8d70\u5230\u54ea\u4e00\u6b65\u624d\u4f1a\u6682\u65f6\u505c\u4e0b\u6765\u6b47\u4e00\u6b47\u3002</p><p>\u3000\u3000\u5f20\u606a\u8981\u53f6\u5efa\u658c\u914d\u5408\u4ed6\u6316\u5751\u63a9\u57cb\u8d75\u9526\u8363\u3001\u4e07\u52c7\uff0c\u5bf9\u76db\u946b\u7684\u5e9e\u5927\u8ba1\u5212\u81ea\u7136\u5f88\u79ef\u6781\u7684\u51fa\u8c0b\u5212\u7b56\uff0c\u4e0d\u8fc7\u4ed6\u8fd9\u65f6\u5019\u8eb2\u5728\u89d2\u843d\u91cc\uff0c\u5634\u89d2\u6302\u7740\u6d45\u7b11\u4e0d\u514d\u6d41\u9732\u51fa\u592a\u591a\u9634\u8c0b\u7684\u610f\u5473\u3002</p><p>\u3000\u3000\u201c\u5f20\u606a\u7b11\u8d77\u6765\u603b\u8ba9\u4eba\u6709\u4e0d\u5b89\u5fc3\u7684\u611f\u89c9\u2026\u2026\u201d\u5510\u5a67\u3001\u8c22\u665a\u6674\u3001\u53f6\u5c0f\u6850\u7ad9\u5728\u89d2\u843d\u5bf9\u5f20\u606a\u8138\u4e0a\u7684\u7b11\u5bb9\u8bc4\u5934\u8bba\u8db3\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u90a3\u5929\u5728\u60e0\u5c71\uff0c\u4ed6\u4e5f\u8fd9\u4e48\u7b11\u6765\u7740\uff0c\u4e0d\u6653\u5f97\uff0c\u8fd9\u6b21\u6362\u8c01\u5012\u9709\uff1f\u201d\u53f6\u5c0f\u6850\u51b3\u5b9a\u79bb\u5f20\u606a\u8fdc\u4e9b\u597d\uff0c\u5374\u4e0d\u77e5\u9053\u53f6\u5efa\u658c\u5df2\u7ecf\u5fc3\u7518\u60c5\u613f\u7684\u7ed9\u5f20\u606a\u5f53\u67aa\u4f7f\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u77e5\u9053\u5f20\u606a\u7684\u8be6\u7ec6\u8ba1\u5212\uff0c\u77a5\u773c\u770b\u4e86\u8fdc\u5904\u5206\u5f97\u5f00\u5f00\u7684\u4e24\u5806\u4eba\uff0c\u4e07\u52c7\u3001\u53f6\u5efa\u658c\u3001\u5510\u5b66\u8c26\u8c08\u7b11\u98ce\u751f\uff0c\u5468\u5bcc\u660e\u867d\u7136\u4e5f\u7ed9\u4eba\u56f4\u7740\uff0c\u4f46\u662f\u8138\u8272\u9634\u6c89\uff0c\u65f6\u4e0d\u65f6\u4f1a\u671b\u5411\u5510\u5b66\u8c26\u90a3\u8fb9\uff0c\u800c\u5f20\u606a\u4eff\u4f5b\u6f5c\u85cf\u7684\u8770\u86c7\u76ee\u4e0d\u8f6c\u775b\u7684\u6ce8\u89c6\u7740\u4e24\u5806\u4eba\u3002\u5927\u6982\u518d\u6ca1\u6709\u5982\u6b64\u751f\u52a8\u9c9c\u660e\u7684\u653f\u6cbb\u751f\u6001\u56fe\u4e86\u5427\uff1f</p><p>\u3000\u3000\u201c\u5582\uff01\u201d\u60f3\u5230\u5f20\u606a\u8fd8\u53ea\u6709\u5341\u4e03\u5c81\u7684\u5e74\u9f84\uff0c\u8c22\u665a\u6674\u90fd\u514d\u4e0d\u4e86\u6709\u54d1\u7136\u5931\u7b11\u7684\u611f\u89c9\uff0c\u867d\u7136\u5fc3\u7406\u4f1a\u5f88\u4f9d\u8d56\uff0c\u4f46\u65f6\u4e0d\u65f6\u4ecd\u8981\u60f3\u8d77\u6765\u4ed6\u8fd8\u662f\u4e00\u4e2a\u534a\u5927\u4e0d\u5927\u7684\u5c11\u5e74\uff0c\u8c22\u665a\u6674\u8f7b\u58f0\u5524\u8d77\u5f20\u606a\u7684\u6ce8\u610f\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u201d\u5f20\u606a\u8fd9\u624d\u6ce8\u610f\u5230\u665a\u6674\u8ddf\u5510\u5a67\u3001\u53f6\u5c0f\u6850\u5979\u4eec\u6b63\u5bf9\u81ea\u5df1\u8bc4\u5934\u8bba\u8db3\uff0c\u8d70\u8fc7\u6765\u95ee\u9053\uff0c\u201c\u6709\u4ec0\u4e48\u4e8b\uff0c\u788e\u788e\u5ff5\u4e2a\u4e0d\u505c\uff1f\u201d</p><p>\u3000\u3000\u201c\u5c0f\u516d\u4f1a\u62ff\u81ea\u5df1\u7684\u94b1\u4e70\u4e0b\u9526\u6e56\u3001\u5609\u4fe1\u5728\u6d77\u6cf0\u7684\u80a1\u4efd\uff0c\u5609\u4fe1\u90a3\u8fb9\u5df2\u7ecf\u8c08\u59a5\u4e86\uff0c\u5177\u4f53\u7684\u4ef7\u683c\u4e0d\u77e5\u9053\uff0c\u53ea\u6015\u4e0d\u4f1a\u592a\u9ad8\uff0c\u4f60\u8981\u662f\u4e0d\u613f\u610f\uff0c\u90a3\u5c31\u7b97\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u5728\u8c22\u5bb6\u8fd9\u4e00\u4ee3\u4eba\u4e2d\u6392\u884c\u7b2c\u516d\u3002\u8c22\u5251\u5357\u5728\u8bfb\u5927\u5b66\u671f\u95f4\u641e\u4e39\u6d25\u53e3\u670d\u6db2\uff0c\u540e\u6765\u51fa\u56fd\u7559\u5b66\u5c06\u4e39\u6d25\u516c\u53f8\u5e76\u5165\u6b63\u6cf0\u96c6\u56e2\u6362\u53d6\u4ed6\u5728\u5bb6\u65cf\u4f01\u4e1a\u4e2d\u7684\u5730\u4f4d\uff0c\u6b63\u6cf0\u96c6\u56e2\u4ee5\u4e39\u6d25\u516c\u53f8\u4e3a\u57fa\u7840\uff0c\u9664\u4e86\u7ee7\u7eed\u505a\u5f3a\u539f\u6765\u7684\u4fdd\u5065\u54c1\u4e1a\u52a1\uff0c\u8fd8\u5411\u751f\u7269\u5236\u836f\u65b9\u9762\u6269\u5f20\uff1b\u770b\u6765\u8c22\u5251\u5357\u90a3\u6b21\u9664\u4e86\u83b7\u5f97\u6b63\u6cf0\u96c6\u56e2\u7684\u80a1\u4efd\u4e4b\u5916\uff0c\u8fd8\u4ece\u4e39\u6d25\u516c\u53f8\u989d\u5916\u635e\u4e86\u4e0d\u5c11\u94b1\u3002</p><p>\u3000\u3000\u4ed6\u8fd9\u65f6\u5019\u62ff\u51fa\u81ea\u5df1\u7684\u94b1\u5927\u4e3e\u6536\u8d2d\u6d77\u6cf0\u516c\u53f8\u5728\u6b63\u6cf0\u96c6\u56e2\u63a7\u5236\u4e4b\u5916\u7684\u80a1\u4efd\uff0c\u96be\u9053\u4e5f\u770b\u51fa\u6e20\u9053\u8d44\u6e90\u5411\u7ec8\u7aef\u5546\u4e1a\u7f51\u7edc\u8f6c\u53d8\u7684\u5546\u673a\uff0c\u8fd8\u662f\u5355\u7eaf\u7684\u770b\u4e2d\u6570\u5b57\u624b\u673a\u672c\u8eab\u5c31\u5b58\u5728\u5de8\u5927\u673a\u9047\uff1f</p><p>\u3000\u3000\u867d\u7136\u8c01\u90fd\u4f1a\u4e00\u65f6\u7684\u5931\u53bb\u7406\u667a\uff0c\u4f46\u662f\u8c22\u5251\u5357\u7684\u884c\u4e3a\u4e0d\u4f1a\u662f\u5355\u7eaf\u5bf9\u81ea\u5df1\u7684\u53cd\u51fb\uff0c\u8981\u60f3\u77e5\u9053\u8c22\u5251\u5357\u7684\u610f\u56fe\uff0c\u53ea\u6709\u8ba9\u4ed6\u653e\u624b\u53bb\u505a\uff0c\u5f20\u606a\u6487\u4e86\u6487\u5634\uff0c\u8bf4\u9053\uff1a\u201c\u4e3a\u4ec0\u4e48\u7b97\u4e86\uff1f\u5bb3\u4ed6\u635f\u5931\u4e24\u90e8\u5954\u9a70\uff0c\u6211\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u8fc7\u610f\u4e0d\u53bb\uff0c\u4ed6\u8981\u80fd\u51fa\u5f97\u8d77\u4ef7\u683c\uff0c\u6d77\u6cf0\u7684\u80a1\u4efd\uff0c\u9526\u6e56\u4e5f\u4e0d\u4e00\u5b9a\u8981\u6293\u5728\u624b\u91cc\u3002\u201d</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u7728\u4e86\u7728\u773c\u775b\uff0c\u5979\u624d\u4e0d\u4fe1\u5f20\u606a\u6709\u5185\u759a\u7684\u65f6\u5019\uff0c\u4e0d\u8fc7\u5bf9\u8c22\u5251\u5357\u7684\u4e3e\u52a8\u4e5f\u5f88\u5947\u602a\uff1a\u201c\u4ed6\u81ea\u5df1\u62ff\u94b1\u5356\u4e0b\u6d77\u6cf0\u7684\u80a1\u4efd\u505a\u4ec0\u4e48\uff0c\u4ed6\u672c\u4eba\u4e0d\u5c31\u662f\u6b63\u6cf0\u7684\u7b2c\u4e8c\u5927\u4e2a\u4eba\u80a1\u4e1c\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u8c01\u77e5\u9053\uff1f\u8981\u77e5\u9053\u5bb6\u7535\u8fde\u9501\u4e1a\u5728\u56fd\u5916\u5e76\u4e0d\u662f\u4ec0\u4e48\u7a00\u7f55\u4e8b\uff0c\u4f46\u662f\u5173\u952e\u8981\u914d\u5408\u56fd\u5185\u5bb6\u7528\u7535\u5668\u5e02\u573a\u4f9b\u6c42\u5173\u7cfb\u7684\u53d8\u5316\uff0c\u624d\u662f\u5bb6\u7535\u9501\u4e1a\u80fd\u5426\u4e00\u4e3e\u6210\u529f\u7684\u5173\u952e\uff0c\u4e00\u65e6\u5bb6\u7528\u7535\u5668\u4f9b\u7ed9\u8fc7\u5269\uff0c\u638c\u63e1\u7ec8\u7aef\u5546\u4e1a\u7f51\u7edc\u7684\u52bf\u529b\uff0c\u5c06\u62e5\u6709\u65e0\u6bd4\u5f3a\u52bf\u7684\u5546\u4e1a\u5730\u4f4d\uff0c\u8bf4\u4e0d\u5b9a\u8c22\u5251\u5357\u4e5f\u770b\u5230\u8fd9\u4e00\u70b9\u3002\u201d</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u72d0\u7591\u7684\u76ef\u7740\u5f20\u606a\uff0c\u5f20\u606a\u8bf4\u5f97\u8f7b\u677e\uff0c\u4f46\u662f\u53f6\u5c0f\u6850\u7684\u611f\u89c9\u5374\u4e0d\u8f7b\u677e\uff0c\u771f\u8981\u50cf\u5f20\u606a\u6240\u8bf4\u7684\u8fd9\u6837\uff0c\u76db\u946b\u8fd8\u6ca1\u6709\u5927\u4e3e\u6269\u5f20\u4e4b\u524d\uff0c\u5c31\u5728\u7701\u5185\u51fa\u73b0\u4e86\u4e00\u4e2a\u5f3a\u654c\u3002\u4f46\u662f\u5f20\u606a\u7684\u8f7b\u677e\u591a\u5c11\u6709\u4e9b\u5e78\u707e\u4e50\u7978\u7684\u610f\u5473\uff0c\u53f6\u5c0f\u6850\u5403\u8fc7\u5f20\u606a\u7684\u4e8f\uff0c\u8c01\u77e5\u9053\u4ed6\u8fd9\u6b21\u662f\u4e0d\u662f\u6545\u610f\u8bef\u5bfc\u81ea\u5df1\uff1f</p><p>\u3000\u3000\u201c\u771f\u662f\u8fd9\u6837\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u8c01\u77e5\u9053\u5462\uff0c\u53ea\u662f\u731c\u6d4b\u800c\u5df2\uff0c\u4f46\u662f\u4ed6\u8981\u6ca1\u6709\u5927\u7684\u8ba1\u5212\uff0c\u4e0d\u4f1a\u8f7b\u6613\u5c06\u79c1\u623f\u94b1\u90fd\u62ff\u51fa\u6765\u3002\u5f88\u663e\u7136\uff0c\u4ed6\u5728\u6b63\u6cf0\u96c6\u56e2\u6240\u5360\u7684\u4e2a\u4eba\u80a1\u4efd\uff0c\u5c31\u7b97\u80fd\u4ece\u8fd9\u4e2a\u8ba1\u5212\u5206\u5f97\u5e94\u6709\u7684\u5229\u6da6\uff0c\u4f46\u662f\u8fd8\u4e0d\u80fd\u8ba9\u4ed6\u6ee1\u8db3\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u53e5\u8bdd\uff0c\u53f6\u5c0f\u6850\u4fe1\uff0c\u8c22\u5251\u5357\u4e00\u5b9a\u4f1a\u6709\u4ec0\u4e48\u5e9e\u5927\u7684\u5546\u4e1a\u8ba1\u5212\u8981\u501f\u52a9\u6d77\u6cf0\u7684\u8d44\u6e90\u3002</p><p>\u3000\u3000\u6d77\u6cf0\u76ee\u524d\u6700\u6709\u4ef7\u503c\u7684\u8d44\u6e90\u5c31\u662f\u5728\u4e1c\u6d77\u7701\u7684\u6e20\u9053\u8d44\u6e90\u4e0e\u7231\u7acb\u4fe1\u7684\u4e1c\u6d77\u7701\u4ee3\u7406\u6743\u3002\u76db\u946b\u4f5c\u4e3a\u5730\u533a\u603b\u4ee3\uff0c\u6e20\u9053\u8d44\u6e90\u662f\u7c97\u7ebf\u6761\u7684\uff0c\u4ee3\u7406\u7684\u4ea7\u54c1\u5f88\u5355\u4e00\uff1b\u6d77\u6cf0\u4f5c\u4e3a\u4e0b\u4e00\u7ea7\u7684\u4ee3\u7406\u5546\uff0c\u5728\u4e1c\u6d77\u7701\u62e5\u6709\u66f4\u7ec6\u5bc6\u7684\u5e02\u573a\u6e20\u9053\uff0c\u8ddd\u7ec8\u7aef\u5e02\u573a\u4ec5\u4e00\u6b65\u4e4b\u9065\uff0c\u5728\u8c22\u665a\u5c71\u7684\u7ecf\u8425\u4e0b\uff0c\u6d77\u6cf0\u4e0d\u4ec5\u4ee3\u7406\u7231\u7acb\u4fe1\u624b\u673a\uff0c\u8fd8\u6d89\u53ca\u5176\u4ed6\u4f17\u591a\u7684\u4ea7\u54c1\u7ecf\u9500\uff0c\u662f\u9999\u96ea\u6d77\u51b0\u7bb1\u3001\u5c0f\u5929\u9e45\u6d17\u8863\u673a\u7b49\u5bb6\u7528\u7535\u5668\u5728\u4e1c\u6d77\u7701\u7684\u603b\u4ee3\u7406\uff0c\u7269\u6d41\u3001\u5ba2\u670d\u4f53\u7cfb\u5df2\u7ecf\u521d\u5177\u96cf\u5f62\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u4e0d\u4f1a\u5236\u6b62\u6d77\u6cf0\u76f2\u76ee\u7684\u8ddf\u76db\u946b\u7ade\u4e89\uff1f\u201d\u53f6\u5c0f\u6850\u771f\u6709\u4e9b\u76f8\u4fe1\u5f20\u606a\u7684\u5224\u65ad\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u53ea\u662f\u5c0f\u80a1\u4e1c\uff0c\u5f71\u54cd\u4e0d\u4e86\u6d77\u6cf0\u7684\u7ecf\u8425\u51b3\u7b56\uff0c\u4e0e\u5176\u65e5\u540e\u5728\u6d77\u6cf0\u4e0e\u76db\u946b\u4e4b\u95f4\u96be\u4ee5\u53d6\u820d\uff0c\u8fd8\u4e0d\u5982\u73b0\u5728\u5c31\u9000\u51fa\u6765\uff0c\u4ee5\u540e\u53ef\u4ee5\u575a\u5b9a\u7684\u7ad9\u5728\u76db\u946b\u7684\u8fd9\u4e00\u8fb9\u6447\u65d7\u5450\u558a\u3002\u201d\u5f20\u606a\u8146\u7740\u8138\u8bf4\uff0c\u660e\u660e\u8981\u4ece\u6d77\u6cf0\u9000\u51fa\u662f\u4ed6\u81ea\u5df1\u7684\u610f\u601d\uff0c\u5374\u786c\u8981\u7275\u6d89\u4e0a\u76db\u946b\u3002</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u6a2a\u4e86\u4ed6\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u9526\u6e56\u5728\u6d77\u6cf0\u7684\u80a1\u4efd\u6709\u6ca1\u6709\u53ef\u80fd\u8f6c\u8ba9\u7ed9\u76db\u946b\uff1f\u201d\u8bdd\u521a\u51fa\u53e3\uff0c\u5c31\u89c9\u5f97\u6709\u4e9b\u5984\u60f3\u4e86\uff0c\u4e14\u4e0d\u8bf4\u6b63\u6cf0\u96c6\u56e2\u6709\u4f18\u5148\u8ba4\u8d2d\u6743\uff0c\u8c22\u665a\u6674\u6bd5\u7adf\u662f\u8c22\u5bb6\u7684\u4eba\uff0c\u600e\u4e48\u53ef\u80fd\u660e\u76ee\u5f20\u80c6\u7684\u80cc\u5f03\u6574\u4e2a\u5bb6\u65cf\uff1f</p><p>\u3000\u3000\u5f20\u606a\u66f4\u60f3\u6b63\u6cf0\u96c6\u56e2\u80fd\u771f\u6b63\u7684\u6210\u4e3a\u76db\u946b\u7684\u5546\u4e1a\u5f3a\u654c\uff0c\u4e5f\u65b9\u4fbf\u4ed6\u8ddf\u76db\u946b\u66f4\u52a0\u7684\u540c\u4ec7\u654c\u5ffe\uff0c\u81f3\u4e8e\u8c22\u5251\u5357\u7684\u610f\u56fe\u662f\u4ec0\u4e48\uff0c\u4ed6\u4e5f\u6ca1\u6709\u592a\u591a\u7684\u628a\u63e1\u3002</p></article>"}}}]);
