(window.webpackJsonp=window.webpackJsonp||[]).push([[332],{355:function(p){p.exports={chapterTitle:null,title:"\u7b2c21\u7ae0 \u8bb0\u4e8b\u672c\u80cc\u540e\u7684\u4e8b\u60c5",content:"<article><h2>\u7b2c21\u7ae0 \u8bb0\u4e8b\u672c\u80cc\u540e\u7684\u4e8b\u60c5</h2><p>\u3000\u3000\u9910\u4f1a\u7ed3\u675f\uff0c\u5df2\u7ecf\u662f\u51cc\u6668\u4e00\u70b9\uff0c\u5927\u5bb6\u90fd\u5f88\u5174\u594b\uff0c\u5510\u5b66\u8c26\u63d0\u51fa\u8981\u89c1\u4e00\u89c1\u53f6\u65b0\u660e\u3002</p><p>\u3000\u3000\u5728\u6b64\u4e4b\u524d\uff0c\u5510\u5b66\u8c26\u56de\u5fc6\u8d77\u90a3\u672c\u81f3\u5173\u91cd\u8981\u7684\u8bb0\u4e8b\u672c\u5982\u4f55\u5230\u4ed6\u624b\u91cc\u7684\u60c5\u5f62\u3002\u51c6\u786e\u8bf4\u6765\uff0c\u5728\u516d\u6708\u4e8c\u5341\u56db\u65e5\uff0c\u59dc\u660e\u8bda\u7a81\u7136\u5411\u4ed6\u6c47\u62a5\u66fe\u5efa\u534e\u5377\u6b3e\u5916\u9003\uff0c\u4e3a\u4e0d\u5f71\u54cd\u6539\u5236\u8fdb\u7a0b\uff0c\u59dc\u660e\u8bda\u8981\u6c42\u516c\u5b89\u90e8\u95e8\u79d8\u5bc6\u4fa6\u67e5\uff0c\u5185\u90e8\u53ea\u9650\u4e8e\u6539\u5236\u5c0f\u7ec4\u6210\u5458\u77e5\u9053\u3002\u5510\u5b66\u8c26\u5bf9\u66fe\u5efa\u534e\u5377\u6b3e\u5916\u9003\u8fd9\u4e8b\uff0c\u4e5f\u89c9\u5f97\u5341\u5206\u8e4a\u8df7\uff0c\u6d77\u5dde\u5e02\u4eba\u4eba\u90fd\u77e5\u9053\u65b0\u4e30\u96c6\u56e2\u6709\u95ee\u9898\uff0c\u4f46\u5c31\u662f\u67e5\u4e0d\u51fa\u6765\uff0c\u5510\u5b66\u8c26\u5728\u66fe\u5efa\u534e\u5931\u8e2a\u4e4b\u524d\uff0c\u5c31\u627e\u4ed6\u8c08\u8fc7\u51e0\u56de\u8bdd\uff0c\u60f3\u5728\u4ed6\u8eab\u4e0a\u627e\u51fa\u7a81\u7834\u53e3\uff0c\u5c06\u65b0\u4e30\u96c6\u56e2\u7684\u95ee\u9898\u5728\u6539\u5236\u524d\u67e5\u6e05\u695a\u3002</p><p>\u3000\u3000\u5728\u59dc\u660e\u8bda\u62a5\u6848\u7684\u5f53\u5929\uff0c\u5510\u5b66\u8c26\u4e0e\u516c\u5b89\u90e8\u95e8\u4e00\u8d77\u641c\u67e5\u4e86\u66fe\u5efa\u534e\u7684\u529e\u516c\u5ba4\uff0c\u5728\u8b66\u5458\u79bb\u5f00\u4e4b\u540e\uff0c\u5510\u5b66\u8c26\u5728\u66fe\u5efa\u534e\u529e\u516c\u684c\u7684\u62bd\u5c49\u4e0e\u684c\u5b50\u5185\u58c1\u4e4b\u95f4\uff0c\u65e0\u610f\u53d1\u73b0\u8fd9\u672c\u8bb0\u4e8b\u672c\u3002\u4e0d\u6653\u5f97\u66fe\u5efa\u534e\u6545\u610f\u85cf\u7684\uff0c\u8fd8\u662f\u62c9\u62bd\u5c49\u65f6\u65e0\u610f\u95f4\u6389\u8fdb\u53bb\u7684\u3002\u5510\u5b66\u8c26\u4e00\u5f00\u59cb\u4ee5\u4e3a\u662f\u66fe\u5efa\u534e\u6545\u610f\u85cf\u7684\uff0c\u5f88\u5174\u594b\uff0c\u6ca1\u6709\u7ec6\u770b\u5c31\u8ba4\u4e3a\u627e\u5230\u89e3\u5f00\u65b0\u4e30\u96c6\u56e2\u8d22\u52a1\u8ff7\u56e2\u7684\u5173\u952e\u8bc1\u636e\uff0c\u8ddf\u79d8\u4e66\u53f6\u65b0\u660e\u63d0\u4e86\u4e00\u4e0b\uff0c\u6ca1\u6709\u7ec6\u8bf4\u3002\u63a5\u4e0b\u6765\uff0c\u4ed6\u5c31\u88ab\u68c0\u4e3e\u53d7\u8d3f\uff0c\u5148\u662f\u4e01\u5411\u5c71\u8ba9\u4ed6\u6682\u65f6\u505c\u6b62\u65b0\u4e30\u96c6\u56e2\u7684\u6539\u5236\u5de5\u4f5c\uff0c\u7d27\u63a5\u7740\u7701\u68c0\u67e5\u7ec4\u5230\u4e86\u6d77\u5dde\uff0c\u5bf9\u4ed6\u8fdb\u884c\u9694\u79bb\u5ba1\u67e5\u3002\u5510\u5b66\u8c26\u7559\u4e86\u4e00\u4e2a\u5fc3\u773c\uff0c\u5c06\u8bb0\u4e8b\u672c\u7559\u5728\u6446\u653e\u65e7\u4e66\u4e0e\u74f7\u5668\u7684\u67b6\u5b50\u4e0a\uff0c\u6ca1\u60f3\u5230\u8fd9\u662f\u5e2e\u4ed6\u6d17\u6e05\u5acc\u7591\u6700\u4e3a\u5173\u952e\u7684\u8bc1\u636e\u3002</p><p>\u3000\u3000\u79cd\u79cd\u8ff9\u8c61\u8868\u660e\uff0c\u5728\u5510\u5b66\u8c26\u53d6\u5f97\u66fe\u5efa\u534e\u7684\u7b14\u8bb0\u672c\u4e4b\u540e\uff0c\u662f\u53f6\u65b0\u660e\u5411\u59dc\u660e\u8bda\u3001\u4e01\u5411\u5c71\u901a\u98ce\u62a5\u4fe1\u3002</p><p>\u3000\u3000\u201c\u4ece\u4ed6\u8fdb\u79d8\u4e66\u4e8c\u5904\u5f00\u59cb\uff0c\u5c31\u8ddf\u7740\u6211\uff0c\u6709\u4e24\u5e74\u65f6\u95f4\u4e86\uff0c\u6211\u5012\u89c9\u5f97\u4ed6\u86ee\u503c\u5f97\u4fe1\u4efb\u7684\u3002\u201d\u5510\u5b66\u8c26\u7709\u5934\u8e59\u7740\uff0c\u5bf9\u53f6\u65b0\u660e\u7684\u80cc\u53db\u4e0e\u5815\u843d\uff0c\u72b9\u4e3a\u60cb\u60dc\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u611f\u6168\u9053\uff1a\u201c\u4ed6\u968f\u5510\u5e02\u957f\u4f60\u53c2\u52a0\u65b0\u4e30\u96c6\u56e2\u7684\u6539\u5236\u5de5\u4f5c\uff0c\u5c31\u8ddf\u4ee5\u524d\u6709\u4e9b\u53d8\u5316\uff0c\u6ca1\u6709\u60f3\u5230\u53d8\u5316\u8fd9\u4e48\u5927\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u53f6\u79d8\u4e66\u6d89\u6848\u53ef\u80fd\u4e0d\u6df1\uff0c\u66fe\u5efa\u534e\u7684\u5931\u8e2a\u6848\u53ef\u80fd\u8ddf\u4ed6\u6ca1\u6709\u592a\u5927\u7684\u5173\u7cfb\uff0c\u8fd9\u6837\u7684\u8bdd\uff0c\u4ed6\u90a3\u91cc\u5c31\u5bb9\u6613\u7a81\u7834\u3002\u201d</p><p>\u3000\u3000\u4ece\u8bb8\u601d\u53cd\u5e94\u7684\u60c5\u51b5\u6765\u770b\uff0c\u53f6\u65b0\u660e\u5e94\u8be5\u662f\u968f\u5510\u5b66\u8c26\u5230\u65b0\u4e30\u96c6\u56e2\u4e3b\u6301\u6539\u5236\u5de5\u4f5c\u4e4b\u540e\uff0c\u7ed9\u59dc\u660e\u8bda\u62c9\u4e0b\u6c34\u7684\uff0c\u53c2\u4e0e\u9677\u5bb3\u5510\u5b66\u8c26\u8ba1\u5212\u7684\u7b56\u5212\u4e0e\u5b9e\u65bd\uff0c\u5305\u62ec\u5510\u5b66\u8c26\u4e0e\u8bb8\u601d\u7684\u90a3\u4e9b\u7167\u7247\uff0c\u4e5f\u662f\u53f6\u65b0\u660e\u6697\u4e2d\u6d3e\u4eba\u5077\u62cd\u7684\u3002</p><p>\u3000\u3000\u8fd9\u4e2a\u65f6\u5019\uff0c\u4e13\u6848\u7ec4\u7684\u6210\u5458\u81ea\u7136\u4e0d\u4f1a\u518d\u53bb\u8ffd\u7a76\u4e3a\u4ec0\u4e48\u6bcf\u4e00\u5f20\u7167\u7247\u4e0a\u5510\u5b66\u8c26\u7684\u76ee\u5149\u90fd\u4f1a\u843d\u5728\u8bb8\u601d\u7684\u8eab\u4e0a\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u770b\u8fc7\u8bb8\u601d\u7684\u7b14\u5f55\u4e4b\u540e\uff0c\u5728\u51b7\u6c14\u6253\u8db3\u7684\u7a7a\u8c03\u623f\u91cc\uff0c\u60ca\u4e86\u4e00\u8eab\u51b7\u6c57\uff0c\u8981\u6ca1\u6709\u5f20\u77e5\u884c\u7236\u5b50\u53d1\u73b0\u8fd9\u4e9b\u7591\u8bc1\uff0c\u53ea\u6015\u6d17\u4e0d\u6e05\u51a4\u5c48\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ddf\u7740\u4e00\u8d77\u8fdb\u62d8\u7559\u5ba4\u89c1\u53f6\u65b0\u660e\uff0c\u8fd9\u662f\u56de\u5230\u4e5d\u56db\u5e74\u7b2c\u4e00\u6b21\u8fd1\u8ddd\u79bb\u7684\u89c1\u53f6\u65b0\u660e\uff0c\u4e4b\u524d\uff0c\u542c\u5230\u8fc7\u4ed6\u7684\u58f0\u97f3\uff0c\u5728\u65b0\u4e30\u96c6\u56e2\u914d\u7ed9\u8bb8\u601d\u7684\u516c\u5bd3\u91cc\u8fdc\u8fdc\u7684\u89c1\u8fc7\u4ed6\u3002</p><p>\u3000\u3000\u53f6\u65b0\u660e\u662f\u4e13\u6848\u7ec4\u4ee5\u534f\u52a9\u8c03\u67e5\u7684\u540d\u4e49\u63a5\u5230\u7701\u57ce\u7684\uff0c\u4ed6\u8fd8\u6ca1\u6709\u610f\u8bc6\u81ea\u5df1\u662f\u5ea7\u4e0b\u56da\uff0c\u770b\u7740\u91d1\u56fd\u6d77\u4e0e\u4e13\u6848\u7ec4\u6210\u5458\u63a8\u95e8\u8fdb\u6765\uff0c\u62ff\u8d77\u684c\u4e0a\u7684\u70df\uff0c\u62d4\u51fa\u51e0\u652f\u6b63\u8981\u6309\u4eba\u5934\u6563\u70df\uff0c\u770b\u89c1\u5510\u5b66\u8c26\u3001\u5f20\u77e5\u884c\u3001\u5f20\u606a\u968f\u540e\u8d70\u8fdb\u6765\uff0c\u7b11\u5bb9\u51dd\u56fa\u5728\u8138\u4e0a\u3002</p><p>\u3000\u3000\u5728\u4e13\u6848\u7ec4\u76ee\u524d\u6240\u638c\u63e1\u7684\u6750\u6599\u9762\u524d\uff0c\u53f6\u65b0\u660e\u6ca1\u6709\u505a\u65e0\u8c13\u7684\u62b5\u8c13\uff0c\u5bf9\u8bb8\u601d\u4e3b\u52a8\u4ea4\u5f85\u7684\u95ee\u9898\u90fd\u4f9b\u8ba4\u4e0d\u8bb3\uff0c\u4ea4\u5f85\u4ed6\u968f\u5510\u5b66\u8c26\u4e3b\u6301\u65b0\u4e30\u96c6\u56e2\u6539\u5236\u5de5\u4f5c\u4e4b\u521d\uff0c\u5c31\u63a5\u53d7\u59dc\u660e\u8bda\u4e0d\u83f2\u7684\u9988\u8d60\uff0c\u7531\u59dc\u660e\u8bda\u5f15\u8350\uff0c\u642d\u4e0a\u5e02\u59d4\u4e66\u8bb0\u4e01\u5411\u5c71\u4e4b\u540e\uff0c\u53f6\u65b0\u660e\u5c31\u5f7b\u5e95\u629b\u5f03\u5510\u5b66\u8c26\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u51b7\u7b11\uff1a\u5728\u5e02\u59d4\u4e66\u8bb0\u4e0e\u5e38\u52a1\u526f\u5e02\u957f\u4e4b\u95f4\uff0c\u53f6\u65b0\u660e\u7684\u9009\u62e9\u5012\u4e0d\u80fd\u8bf4\u4ed6\u9519\u4e86\uff0c\u5ba6\u6d77\u6c89\u6d6e\u4e2d\uff0c\u80fd\u575a\u6301\u6e05\u8005\u81ea\u6e05\u7684\u5b98\u5458\u80fd\u6709\u51e0\u4e2a\uff1f</p><p>\u3000\u3000\u53f6\u65b0\u660e\u6240\u4ea4\u5f85\u7684\u95ee\u9898\uff0c\u5bf9\u65b0\u4e30\u96c6\u56e2\u56fd\u6709\u8d44\u4ea7\u6d41\u5931\u3001\u66fe\u5efa\u534e\u5931\u8e2a\u7b49\u6848\u6ca1\u6709\u592a\u660e\u663e\u7684\u5e2e\u52a9\uff0c\u4f46\u662f\u8db3\u4ee5\u652f\u6301\u7701\u5e38\u59d4\u5bf9\u4e01\u5411\u5c71\u91c7\u53d6\u63aa\u65bd\u3002</p><p>\u3000\u3000\u4e13\u6848\u7ec4\u53c8\u8fde\u7eed\u5bf9\u59dc\u660e\u8bda\u8fdb\u884c\u5ba1\u8baf\uff0c\u59dc\u660e\u8bda\u5230\u7701\u57ce\u4e4b\u540e\u5c31\u5bdf\u89c9\u5f02\u5e38\u6765\uff0c\u4e13\u6848\u7ec4\u7684\u5fc3\u7406\u653b\u52bf\u5bf9\u4ed6\u6ca1\u6709\u8d77\u4efb\u4f55\u4f5c\u7528\u3002</p><p>\u3000\u3000\u4e3a\u6700\u5927\u9650\u5ea6\u7684\u5e2e\u8bb8\u601d\u51cf\u8f7b\u7f6a\u8d23\uff0c\u5f20\u606a\u8865\u8ff0\u4e86\u4e00\u4efd\u7b14\u5f55\uff0c\u56e0\u4e3a\u65e0\u6cd5\u4e0e\u8bb8\u601d\u5bf9\u53e3\u4f9b\uff0c\u8fd9\u4efd\u7b14\u5f55\u9887\u82b1\u4e86\u4ed6\u4e00\u756a\u5fc3\u601d\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u4e0d\u5e0c\u671b\u4e0e\u8bb8\u601d\u5bf9\u53e3\u4f9b\uff0c\u5b81\u53ef\u7a7f\u7ed1\uff0c\u4e5f\u4e0d\u60f3\u7ed9\u8bb8\u601d\u7559\u4e0b\u4ec0\u4e48\u5fc3\u7ed3\u3002</p><p>\u3000\u3000\u8865\u5f55\u5b8c\u7b14\u5f55\uff0c\u5f20\u606a\u4fbf\u56de\u5230\u81ea\u5df1\u7684\u623f\u95f4\u4f11\u606f\uff0c\u4e13\u6848\u7ec4\u8981\u6574\u7406\u62ff\u5230\u7701\u5e38\u59d4\u4f1a\u8bae\u4e0a\u62a5\u544a\u7528\u7684\u8d44\u6599\uff0c\u7238\u7238\u7ed9\u5510\u5b66\u8c26\u62c9\u8fc7\u53bb\u7ee7\u7eed\u8c08\u8bdd\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u4e00\u89c9\u9192\u8fc7\u6765\u65f6\uff0c\u5916\u9762\u7684\u592a\u9633\u8001\u9ad8\uff0c\u5df2\u7ecf\u8fc7\u5341\u70b9\u4e86\uff0c\u597d\u4e45\u6ca1\u7761\u8fd9\u4e48\u8212\u670d\uff0c\u52a0\u4e0a\u524d\u4e16\u7684\u65f6\u95f4\uff0c\u90a3\u771f\u662f\u592a\u4e45\u4e86\uff0c\u6253\u5f00\u95e8\u770b\u89c1\u5510\u5a67\u7ad9\u5728\u5916\u9762\uff0c\u4e00\u8138\u7684\u8e1f\u8e87\u4e0d\u5b9a\uff0c\u7ed9\u5413\u4e86\u4e00\u8df3\uff1a\u201c\u4e0d\u8981\u7a81\u7136\u5c31\u6253\u5f00\u95e8\uff0c\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7728\u4e86\u7728\u773c\u775b\uff0c\u597d\u50cf\u4e0d\u662f\u81ea\u5df1\u7684\u9519\uff1a\u201c\u62dc\u6258\u4f60\u4e0d\u8981\u7a81\u7136\u7ad9\u5728\u95e8\u53e3\uff0c\u597d\u4e0d\u597d\uff1f\u4ec0\u4e48\u4e8b\uff0c\u4e0d\u76f4\u63a5\u8fdb\u6765\u53eb\u9192\u6211\uff1f\u201d</p><p>\u3000\u3000\u5510\u5a67\u795e\u60c5\u5ff8\u6029\u4e86\u4e00\u4f1a\u513f\uff0c\u7a81\u7136\u8bf4\u4e86\u4e00\u58f0\uff1a\u201c\u8c22\u8c22\u4f60\u3002\u201d\u8bf4\u5b8c\u5c31\u626c\u5934\u8d70\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u83ab\u540d\u5176\u5999\u7684\u53d1\u4e86\u4e00\u4f1a\u513f\u6123\uff0c\u8d76\u5230\u529e\u516c\u5ba4\uff0c\u770b\u89c1\u4f17\u4eba\u56f4\u7740\u5f90\u5b66\u5e73\u3001\u5510\u5b66\u8c26\uff0c\u5f90\u5b66\u5e73\u63e1\u7740\u5510\u5b66\u8c26\u7684\u624b\u5728\u8bf4\u8bdd\uff0c\u539f\u6765\u7701\u5e38\u59d4\u4f1a\u8bae\u5df2\u7ecf\u5f00\u5b8c\u4e86\uff0c\u5f90\u5b66\u5e73\u4ee3\u8868\u7701\u59d4\u6765\u6170\u95ee\u5510\u5b66\u8c26\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u770b\u89c1\u5f20\u606a\uff0c\u62db\u624b\u8ba9\u4ed6\u8fc7\u6765\uff1a\u201c\u6765\uff0c\u6765\uff0c\u6765\uff0c\u542c\u8bf4\u4f60\u6628\u5929\u53c8\u7acb\u529f\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5e38\u59d4\u4f1a\u8bae\u901a\u8fc7\u5bf9\u4e01\u5411\u5c71\u91c7\u53d6\u5fc5\u8981\u63aa\u65bd\u7684\u51b3\u5b9a\uff0c\u5e76\u51b3\u5b9a\u7531\u5468\u5bcc\u660e\u6682\u4ee3\u6d77\u5dde\u5e02\u59d4\u4e66\u8bb0\u3001\u4e3b\u6301\u5de5\u4f5c\uff0c\u5510\u5b66\u8c26\u6062\u590d\u5de5\u4f5c\uff0c\u7238\u7238\u5219\u6b63\u5f0f\u53c2\u4e0e\u4e13\u6848\u7ec4\u7684\u5ba1\u7406\u5de5\u4f5c\u3002\u4ece\u5373\u65e5\u8d77\uff0c\u4e13\u6848\u7ec4\u5c06\u8fdb\u9a7b\u6d77\u5dde\uff0c\u5bf9\u4e01\u5411\u5c71\u53d7\u8d3f\u3001\u4fb5\u541e\u56fd\u6709\u8d44\u4ea7\u6848\u8fdb\u884c\u6df1\u5165\u8c03\u67e5\uff0c\u7531\u91d1\u56fd\u6d77\u5148\u5e26\u4eba\u53bb\u6d77\u5dde\u5bf9\u76f8\u5173\u5acc\u7591\u4eba\u91c7\u53d6\u63aa\u65bd\u3002</p><p>\u3000\u3000\u7701\u59d4\u8fd9\u4e48\u5feb\u505a\u51fa\u51b3\u8bae\uff0c\u662f\u5bf9\u4e13\u6848\u7ec4\u5de5\u4f5c\u7684\u80af\u5b9a\u3002</p><p>\u3000\u3000\u7ecf\u5f20\u606a\u6240\u89c1\u3001\u8bb8\u601d\u6240\u8ff0\uff0c\u8c61\u5c71\u5317\u9e93\u7684\u7ea2\u7816\u522b\u5885\u5185\u5e94\u85cf\u7740\u4e01\u5411\u5c71\u65e0\u6570\u7684\u7f6a\u8bc1\uff0c\u4e01\u5411\u5c71\u6b64\u65f6\u5bf9\u7701\u57ce\u8fd9\u8fb9\u7684\u52a8\u4f5c\u6beb\u65e0\u5bdf\u89c9\uff0c\u4e13\u6848\u7ec4\u6210\u5458\u4f3c\u4e4e\u90fd\u76f8\u4fe1\u76d6\u4f4f\u771f\u76f8\u7684\u906e\u63a9\u5982\u6c64\u6c83\u96ea\u4e00\u822c\u4f1a\u8fc5\u901f\u6d88\u878d\u3002</p><p>\u3000\u3000\u5bf9\u539f\u68c0\u67e5\u7ec4\u6210\u5458\u8fdb\u884c\u9010\u4e00\u5ba1\u67e5\uff0c\u4e5f\u53d1\u73b0\u4e86\u7591\u70b9\uff0c\u68c0\u67e5\u7ec4\u526f\u7ec4\u957f\u627f\u8ba4\u5728\u4fa6\u529e\u5510\u5b66\u8c26\u6848\u7684\u8fc7\u7a0b\u4e2d\uff0c\u90fd\u5c06\u60c5\u51b5\u5411\u4e01\u5411\u5c71\u505a\u4e86\u6c47\u62a5\u3002\u4e0d\u662f\u4ed6\u4e0e\u4e01\u5411\u5c71\u540c\u6d41\u5408\u6c61\uff0c\u800c\u662f\u4e01\u5411\u5c71\u4e3b\u52a8\u5411\u4ed6\u4e86\u89e3\u8fdb\u5c55\uff0c\u4e01\u5411\u5c71\u662f\u6d77\u5dde\u5e02\u7684\u4e00\u628a\u624b\uff0c\u5411\u4ed6\u6c47\u62a5\uff0c\u4e5f\u4e0d\u662f\u8fc7\u5206\u8fdd\u89c4\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u7559\u4e0b\u6765\u966a\u4e13\u6848\u7ec4\u5403\u5348\u996d\uff0c\u7136\u540e\u9001\u91d1\u56fd\u6d77\u5148\u53bb\u6d77\u5dde\uff0c\u5176\u4ed6\u4e13\u6848\u7ec4\u6210\u5458\u4e0e\u5acc\u7591\u72af\u7559\u5230\u660e\u5929\u518d\u53bb\u6d77\u5dde\u3002\u5f20\u606a\u627e\u4e0d\u5230\u7406\u7531\u518d\u89c1\u8bb8\u601d\uff0c\u4e00\u76f4\u4e3a\u8fd9\u4e8b\u803f\u803f\u4e8e\u6000\uff0c\u53c8\u62c5\u5fc3\u90a3\u4efd\u7b14\u5f55\u672a\u5fc5\u4f1a\u8d77\u5230\u81ea\u5df1\u6240\u8bbe\u60f3\u7684\u6548\u679c\u3002\u60f3\u5230\u4e5d\u4e5d\u5e74\u8bb8\u601d\u5165\u72f1\u540e\u4e00\u5e74\u5c31\u81ea\u6740\u7684\u4e8b\u60c5\uff0c\u5f20\u606a\u771f\u6015\u8bb8\u601d\u627f\u53d7\u4e0d\u4e86\u592a\u591a\u3002</p><p>\u3000\u3000\u8bb8\u601d\u6d89\u6848\u5e76\u4e0d\u6df1\uff0c\u4e0e\u4e01\u5411\u5c71\u4fdd\u6301\u60c5\u4eba\u5173\u7cfb\u534a\u5e74\u4e4b\u4e45\uff0c\u5206\u4e09\u6b21\u4ece\u59dc\u660e\u8bda\u90a3\u91cc\u652f\u53d6\u4e8c\u5341\u4e03\u4e07\uff0c\u90fd\u7ed9\u59dc\u660e\u8bda\u5199\u4e0b\u501f\u6761\uff0c\u53ea\u8981\u80fd\u627e\u5230\u8fd9\u5f20\u501f\u6761\uff0c\u6216\u8005\u59dc\u660e\u8bda\u80fd\u627f\u8ba4\u8fd9\u70b9\uff0c\u90a3\u5c31\u597d\u4e86\uff0c\u5173\u952e\u8fd8\u662f\u8bec\u9677\u5510\u5b66\u8c26\u7684\u95ee\u9898\u6bd4\u8f83\u4e25\u91cd\uff0c\u867d\u7136\u6709\u88ab\u80c1\u8feb\u7684\u6210\u5206\uff0c\u4f46\u662f\u95ee\u9898\u8fd8\u662f\u6709\u4e9b\u5927\uff0c\u53ea\u5e0c\u671b\u81ea\u5df1\u7684\u7b14\u5f55\u80fd\u5e2e\u4e0a\u5fd9\u3002</p></article>"}}}]);
