(window.webpackJsonp=window.webpackJsonp||[]).push([[333],{356:function(p){p.exports={chapterTitle:null,title:"\u7b2c333\u7ae0 \u65b0\u4efb\u5385\u957f",content:"<article><h2>\u7b2c333\u7ae0 \u65b0\u4efb\u5385\u957f</h2><p>\u3000\u3000\u4e5d\u4e8c\u5e74\u5efa\u6210\u7684\u666f\u6797\u5927\u53a6\u7b2c\u5341\u4e00\u3001\u5341\u4e8c\u5c42\u697c\uff0c\u662f\u5f90\u5fd7\u660e\u521b\u529e\u6d77\u88d5\u516c\u53f8\u4e09\u5e74\u540e\u5c31\u7f6e\u529e\u4e0b\u7684\u7269\u4e1a\uff0c\u6d77\u88d5\u516c\u53f8\u6700\u56f0\u96be\u7684\u65f6\u5019\uff0c\u8c22\u665a\u6674\u66fe\u60f3\u5c06\u8fd9\u4e24\u5c42\u697c\u51fa\u552e\u5957\u53d6\u4e00\u4e9b\u8d44\u91d1\u6765\u7f13\u89e3\u516c\u53f8\u7684\u56f0\u5883\uff0c\u4e0d\u8fc7\u540e\u6765\u4e00\u76f4\u90fd\u6ca1\u6709\u7528\u5f97\u7740\u5c06\u8fd9\u4e24\u5c42\u697c\u51fa\u552e\u6389\u3002</p><p>\u3000\u3000\u966a\u6797\u4e1a\u5385\u5385\u957f\u6c5f\u6d9b\u53ca\u5176\u4ed6\u5b98\u5458\u53c2\u89c2\u516c\u53f8\u5b8c\u4e4b\u540e\uff0c\u5f20\u606a\u6b49\u7136\u7684\u8bf4\uff1a\u201c\u8d76\u7740\u5f90\u7701\u957f\u6709\u7a7a\uff0c\u8981\u53bb\u542c\u5f90\u7701\u957f\u9762\u6388\u673a\u5b9c\uff0c\u8fd9\u8fb9\u5c31\u7531\u5468\u603b\u5177\u4f53\u5411\u6c5f\u5385\u957f\u4ecb\u7ecd\u516c\u53f8\u7684\u60c5\u51b5\u2026\u2026\u201d</p><p>\u3000\u3000\u6c5f\u6d9b\u8bf4\u9053\uff1a\u201c\u6211\u6b63\u597d\u8981\u53bb\u7701\u653f\u5e9c\uff0c\u5177\u4f53\u7684\u4e8b\u60c5\u7531\u4e0b\u9762\u4eba\u8c08\u5c31\u53ef\u4ee5\u4e86\u3002\u201d\u4fbf\u4e0e\u5f20\u77e5\u884c\u3001\u5f20\u606a\u4e00\u8d77\u4e0b\u4e86\u697c\uff0c\u6797\u4e1a\u5385\u53ea\u5f00\u4e86\u4e00\u8f86\u516c\u52a1\u8f66\u8fc7\u6765\uff0c\u6c5f\u6d9b\u8bf4\u7559\u7ed9\u7559\u4e0b\u6765\u8c08\u4e8b\u7684\u5176\u4ed6\u5b98\u5458\u56de\u6797\u4e1a\u5385\u7528\uff0c\u4ed6\u4fbf\u4e0e\u79d8\u4e66\u5750\u65b0\u5149\u7eb8\u4e1a\u7684\u5546\u52a1\u8f66\u4e00\u540c\u53bb\u7701\u653f\u5e9c\u3002</p><p>\u3000\u3000\u6c5f\u6d9b\u4eca\u5e74\u624d\u56db\u5341\u4e03\u5c81\uff0c\u5728\u6b63\u5385\u7ea7\u5e72\u90e8\u91cc\u7b97\u662f\u6709\u4e3a\u4e4b\u4eba\u3002\u5728\u6b64\u4e4b\u524d\uff0c\u6c5f\u6d9b\u4e0e\u5f20\u606a\u63a5\u89e6\u8fc7\u4e00\u6b21\uff0c\u662f\u5f90\u5b66\u5e73\u7684\u79d8\u4e66\u674e\u4e49\u6c5f\u5f15\u8350\u7684\uff0c\u90a3\u6b21\u89c1\u9762\uff0c\u6c5f\u6d9b\u5012\u4e0d\u662f\u5f88\u91cd\u89c6\u3002\u7701\u59d4\u529e\u516c\u5385\u4e0e\u7701\u653f\u5e9c\u529e\u516c\u5ba4\u5927\u6982\u662f\u7701\u91cc\u6d88\u606f\u6700\u96c6\u4e2d\u7684\u8859\u95e8\uff0c\u6c5f\u6d9b\u5012\u662f\u77e5\u9053\u6d77\u5dde\u7684\u9526\u6e56\u516c\u53f8\u4e0e\u5f90\u5b66\u5e73\u4e4b\u5b50\u7684\u672a\u4ea1\u4eba\u8c22\u665a\u6674\u6709\u5173\uff0c\u5f20\u606a\u51fa\u9762\u8bf7\u4ed6\u5403\u996d\uff0c\u4ed6\u8d77\u521d\u8fd8\u4ee5\u4e3a\u8c22\u665a\u6674\u662f\u60f3\u501f\u7740\u516c\u516c\u5f90\u5b66\u5e73\u7684\u5173\u7cfb\u6765\u94bb\u653f\u7b56\u7684\u7a7a\u5b50\u3002</p><p>\u3000\u3000\u4e00\u4ef6\u4e8b\u60c5\uff0c\u770b\u8d77\u6765\u662f\u4ef6\u5f71\u54cd\u6781\u5176\u6076\u52a3\u7684\u4e8b\u60c5\uff0c\u5728\u67d0\u4e9b\u4eba\u773c\u91cc\uff0c\u5374\u662f\u610f\u5473\u7740\u6781\u5927\u7684\u5546\u4f1a\u3002\u65e0\u8bba\u662f\u7701\u91cc\u76f4\u63a5\u8865\u8d34\u79cd\u690d\u516c\u76ca\u6797\u4e5f\u597d\uff0c\u8fd8\u662f\u6797\u6728\u91c7\u4f10\u9650\u989d\uff0c\u8fd8\u662f\u6797\u5730\u8bb8\u53ef\u8bc1\u5236\u5ea6\u6539\u9769\uff0c\u8981\u60f3\u5728\u77ed\u65f6\u95f4\u91cc\u89e3\u51b3\u597d\u95ee\u9898\uff0c\u6253\u5f00\u4e00\u4e2a\u65b0\u7684\u5c40\u9762\uff0c\u90fd\u4f1a\u6709\u5f88\u591a\u7167\u987e\u4e0d\u5230\u7684\u5730\u65b9\uff0c\u90fd\u4f1a\u5f88\u591a\u53ef\u4ee5\u94bb\u7684\u653f\u7b56\u7a7a\u5b50\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u4e0e\u653f\u5e9c\u9ad8\u7ea7\u5b98\u5458\u6cbe\u4eb2\u5e26\u6545\u7684\u5546\u4eba\uff0c\u662f\u6700\u5584\u4e8e\u5bfb\u627e\u8fd9\u79cd\u673a\u4f1a\u7684\uff0c\u901a\u5e38\u4e00\u58f0\u62db\u547c\u3001\u4e00\u5f20\u7eb8\u6761\uff0c\u5c31\u610f\u5473\u7740\u51e0\u767e\u4e07\u3001\u4e0a\u5343\u4e07\u7684\u8d22\u5bcc\u843d\u5165\u79c1\u4eba\u7684\u8170\u5305\u3002</p><p>\u3000\u3000\u6c5f\u6d9b\u5bf9\u8fd9\u4e9b\u884c\u4e3a\u5f88\u4e0d\u5f85\u89c1\uff0c\u4f46\u4e5f\u4e0d\u662f\u4e0d\u80fd\u5bb9\u5fcd\u3002\u4ed6\u662f\u9676\u664b\u4e66\u8bb0\u63a8\u8350\u5230\u6797\u4e1a\u5385\u7684\uff0c\u8fd9\u4e9b\u4e8b\uff0c\u5728\u4ed6\u770b\u6765\uff0c\u4e8b\u65e0\u5de8\u7ec6\u7684\u5411\u9676\u664b\u4e66\u8bb0\u6c47\u62a5\u4e5f\u672a\u5fc5\u80fd\u8ba8\u597d\uff0c\u5374\u4f1a\u5f88\u60f9\u7701\u957f\u5f90\u5b66\u5e73\u538c\u3002</p><p>\u3000\u3000\u5728\u65b0\u5149\u7eb8\u4e1a\u91cd\u7ec4\u3001\u6d77\u88d5\u80a1\u6743\u8131\u79bb\u4e4b\u524d\uff0c\u5f20\u606a\u8fd8\u4e0d\u80fd\u5411\u6c5f\u6d9b\u3001\u5411\u7701\u6797\u4e1a\u5385\u629b\u51fa\u4ed6\u5b8c\u6574\u7684\u8ba1\u5212\uff0c\u7b49\u4e8b\u60c5\u9010\u6e10\u5b89\u6392\u59a5\u5f53\uff0c\u5f20\u606a\u5df2\u7ecf\u6ca1\u6709\u4e0e\u6c5f\u6d9b\u76f4\u63a5\u63a5\u89e6\u7684\u673a\u4f1a\u3002\u6c5f\u6d9b\u8981\u6574\u987f\u7701\u6797\u4e1a\u7cfb\u7edf\uff0c\u603b\u80fd\u627e\u5230\u5404\u79cd\u501f\u53e3\u53bb\u63a8\u8131\u674e\u4e49\u6c5f\u7684\u518d\u6b21\u5f15\u8350\u4e0d\u8ddf\u5f20\u606a\u89c1\u9762\u3002\u4e0d\u8fc7\u4ed6\u4e5f\u4e0d\u662f\u90a3\u79cd\u5c06\u4e8b\u60c5\u505a\u7edd\u7684\u5149\u68cd\uff0c\u867d\u7136\u4e0d\u559c\u6b22\u8fd9\u4e9b\u4e8b\uff0c\u5e76\u4e0d\u89c1\u5f97\u4ed6\u4f1a\u575a\u51b3\u5236\u6b62\u8fd9\u4e9b\u4e8b\uff0c\u4ed6\u8ba9\u5385\u91cc\u7684\u5176\u4ed6\u4eba\u4e0e\u9526\u6e56\u4fdd\u6301\u8054\u7cfb\uff0c\u9526\u6e56\u6709\u4ec0\u4e48\u9700\u8981\u7167\u987e\u7684\u5730\u65b9\uff0c\u4ed6\u5c3d\u53ef\u80fd\u6ee1\u8db3\u5c31\u662f\uff0c\u514d\u5f97\u5f90\u5b66\u5e73\u7684\u7535\u8bdd\u6253\u5230\u4ed6\u529e\u516c\u5ba4\u6216\u8005\u5f90\u5b66\u5e73\u7b7e\u5b57\u7684\u6761\u5b50\u9001\u5230\u4ed6\u9762\u524d\u6765\uff0c\u53ea\u4f1a\u8ba9\u4ed6\u7684\u5de5\u4f5c\u66f4\u52a0\u88ab\u52a8\u800c\u5df2\u3002</p><p>\u3000\u3000\u65b0\u5149\u7eb8\u4e1a\u91cd\u7ec4\u7684\u90a3\u6bb5\u65f6\u95f4\u91cc\uff0c\u5468\u6e38\u7ec4\u7ec7\u4eba\u624b\u62df\u5b9a\u9879\u76ee\u4e66\uff0c\u6709\u4ec0\u4e48\u8d44\u6599\u76f4\u63a5\u8ddf\u6797\u4e1a\u5927\u5b66\u7684\u5468\u798f\u745e\u6559\u6388\u8981\uff0c\u6700\u521d\u62df\u5199\u9879\u76ee\u4e66\u7684\u8d1f\u8d23\u4eba\u4e4b\u4e00\u5c31\u662f\u5468\u798f\u745e\u6559\u6388\u5e26\u51fa\u6765\u7684\u535a\u58eb\u7814\u7a76\u751f\u675c\u65b0\u6052\uff0c\u65b0\u5149\u7eb8\u4e1a\u4e0e\u7701\u6797\u4e1a\u5385\u51e0\u4e4e\u6ca1\u6709\u63a5\u89e6\uff0c\u5012\u662f\u5b8b\u5fd7\u4f51\u52a0\u5165\u65b0\u5149\u7eb8\u4e1a\uff0c\u5f88\u5feb\u5c06\u9879\u76ee\u4e66\u5b8c\u5584\u8d77\u6765\uff0c\u800c\u7701\u57ce\u8fd9\u8fb9\u7684\u5206\u516c\u53f8\u5f00\u59cb\u7b79\u5efa\uff0c\u5468\u6e38\u4ed6\u4eec\u624d\u6b63\u5f0f\u4e0e\u7701\u6797\u4e1a\u5385\u7684\u5b98\u5458\u63a5\u89e6\u3002</p><p>\u3000\u3000\u4e5d\u516d\u5e74\uff0c\u4e00\u5bb6\u6ce8\u518c\u8d44\u91d1\u9ad8\u8fbe\u4e00\u4e2a\u4ebf\u7684\u6797\u4e1a\u516c\u53f8\u662f\u7701\u6797\u4e1a\u5385\u65e0\u6cd5\u5ffd\u89c6\u7684\u3002</p><p>\u3000\u3000\u7701\u6797\u4e1a\u7cfb\u7edf\u95f9\u51fa\u8fd9\u4e48\u5927\u7684\u7eb0\u6f0f\uff0c\u5f90\u5b66\u5e73\u51b3\u5b9a\u8ffd\u52a0\u516c\u76ca\u6797\u7684\u5efa\u8bbe\u8d44\u91d1\uff0c\u54ac\u54ac\u7259\u624d\u4ece\u7701\u8d22\u653f\u91cc\u6324\u51fa\u4e24\u4e2a\u4ebf\u800c\u5df2\uff0c\u7528\u4e8e\u63a8\u52a8\u7701\u68ee\u5de5\u4f01\u4e1a\u6539\u5236\u3001\u804c\u5de5\u5206\u6d41\u7684\u8d44\u91d1\u624d\u516b\u5343\u591a\u4e07\u3002</p><p>\u3000\u3000\u8981\u5c3d\u5feb\u5c06\u7eb0\u6f0f\u5f25\u8865\u597d\uff0c\u4ec5\u9760\u7701\u91cc\u62e8\u4ed8\u7684\u8d44\u91d1\u529b\u6709\u4e0d\u8db3\uff0c\u5438\u5f15\u8d44\u91d1\u627f\u5305\u5b9c\u6797\u8352\u5c71\uff0c\u5927\u9762\u79ef\u7684\u79cd\u690d\u5de5\u4e1a\u539f\u6599\u6797\uff0c\u624d\u662f\u6839\u672c\u89e3\u51b3\u4e4b\u9053\u3002\u8fd9\u4e9b\u9053\u7406\uff0c\u5373\u4f7f\u662f\u4e0a\u4e00\u4efb\u5385\u957f\u5218\u534e\u5c71\u4e5f\u660e\u767d\uff0c\u660e\u767d\u672a\u5fc5\u80fd\u505a\u597d\uff0c\u505a\u597d\u4e00\u4ef6\u4e8b\u9700\u8981\u65f6\u673a\u3002\u56fd\u5bb6\u63d0\u51fa\u6797\u7eb8\u4e00\u4f53\u5316\u6982\u5ff5\u8fd1\u5341\u5e74\uff0c\u53ef\u60dc\u5e94\u8005\u5ed6\u5ed6\u3002</p><p>\u3000\u3000\u9996\u5148\u662f\u8fd1\u51e0\u5e74\u624d\u63a8\u52a8\u8349\u6d46\u53d6\u4ee3\u6728\u6d46\u7684\u4e8b\u60c5\uff0c\u53d6\u7f14\u8349\u6d46\u751f\u4ea7\u7ebf\u7684\u6001\u5ea6\u8fd8\u4e0d\u591f\u575a\u51b3\uff0c\u6267\u884c\u8fc7\u7a0b\u4e2d\u4e5f\u5b58\u5728\u79cd\u79cd\u5730\u65b9\u4fdd\u62a4\u4e3b\u4e49\u7684\u9634\u529b\uff0c\u5176\u6b21\uff0c\u56fd\u5185\u7684\u9020\u7eb8\u4f01\u4e1a\u666e\u904d\u5b9e\u529b\u504f\u5f31\uff0c\u65e0\u6cd5\u63a8\u52a8\u6797\u7eb8\u4e00\u4f53\u5316\u7684\u5927\u4ea7\u4e1a\u5faa\u73af\uff0c\u6797\u7eb8\u4e00\u4f53\u5316\u52a8\u5219\u4e0a\u5341\u4ebf\uff0c\u800c\u4e14\u6536\u76ca\u671f\u592a\u957f\uff0c\u4e0d\u7b26\u5408\u56fd\u5185\u8d44\u672c\u7684\u8ffd\u9010\u77ed\u671f\u9ad8\u8425\u5229\u7684\u7279\u8d28\u3002</p><p>\u3000\u3000\u5f15\u5165\u5927\u8d44\u672c\u5927\u9762\u79ef\u7684\u690d\u6797\uff0c\u5728\u4e5d\u516d\u5e74\uff0c\u770b\u8d77\u6765\u50cf\u662f\u4e00\u4e2a\u4e0d\u53ef\u80fd\u7684\u4efb\u52a1\u3002</p><p>\u3000\u3000\u65b0\u5149\u7eb8\u4e1a\u4e00\u4e0b\u5b50\u6210\u4e86\u6c5f\u6d9b\u89e3\u51b3\u7701\u6797\u4e1a\u7cfb\u7edf\u773c\u4e0b\u5371\u673a\u6700\u91cd\u8981\u7684\u5e0c\u671b\uff0c\u91cd\u7ec4\u540e\u7684\u65b0\u5149\u7eb8\u4e1a\u53c8\u4e0e\u8c22\u665a\u6674\u8131\u5f00\u5173\u7cfb\uff0c\u5f90\u5b66\u5e73\u8fd9\u65f6\u5019\u624d\u7ad9\u51fa\u6765\u66ff\u65b0\u5149\u7eb8\u4e1a\u8bf4\u8bdd\uff0c\u4e13\u95e8\u4e3a\u65b0\u5149\u7eb8\u4e1a\u7684\u627e\u8fc7\u6c5f\u6d9b\u8c08\u8bdd\uff0c\u63d0\u5230\u65b0\u5149\u7eb8\u4e1a\u89c4\u6a21\u4e0a\u516d\u5341\u4ebf\u7684\u6295\u8d44\u8ba1\u5212\uff0c\u6c5f\u6d9b\u524d\u540e\u7684\u6001\u5ea6\u624d\u9661\u7136\u8f6c\u53d8\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u6bb5\u65f6\u95f4\u6ede\u7559\u5728\u6d77\u5dde\u5fd9\u65b0\u5149\u7eb8\u4e1a\u91cd\u7ec4\u7684\u4e8b\u60c5\uff0c\u65e0\u6687\u5206\u8eab\u5230\u7701\u91cc\u6765\uff0c\u6c5f\u6d9b\u4e0d\u5f97\u4e0d\u5c48\u5c31\u7740\u8ddf\u5468\u6e38\u3001\u5b8b\u5fd7\u4f51\u5546\u8c08\u5177\u4f53\u7684\u4e8b\u60c5\uff0c\u4eca\u5929\u624d\u7b2c\u4e8c\u6b21\u4e0e\u5f20\u606a\u63a5\u89e6\u3002</p><p>\u3000\u3000\u7531\u4e8e\u8d77\u521d\u7684\u4e0d\u91cd\u89c6\uff0c\u5f90\u5b66\u5e73\u3001\u674e\u4e49\u6c5f\u4ed6\u4eec\u53c8\u4e0d\u4f1a\u65e0\u4e8b\u4e13\u95e8\u8ddf\u522b\u4eba\u70ab\u8000\u5f20\u606a\u8eab\u4e3a\u9ad8\u4e2d\u751f\u5374\u521b\u4e0b\u4ebf\u4e07\u8d22\u5bcc\u7684\u5947\u8ff9\uff0c\u6c5f\u6d9b\u5bf9\u5f20\u606a\u7684\u4e86\u89e3\u751a\u5c11\uff0c\u5012\u662f\u5f20\u77e5\u884c\u7684\u4e8b\u60c5\uff0c\u4ed6\u7565\u6709\u8033\u95fb\uff0c\u5728\u516c\u53f8\u53c2\u89c2\u65f6\uff0c\u4ed6\u89c9\u5f97\u5f20\u606a\u4e0e\u5f20\u77e5\u884c\u76f8\u8c8c\u5f88\u50cf\uff0c\u5750\u5230\u8f66\u91cc\uff0c\u624d\u65b9\u4fbf\u95ee\u8d77\u6765\uff1a\u201c\u5f20\u603b\u4e0e\u5f20\u79d8\u4e66\u957f\u662f\u4ec0\u4e48\u5173\u7cfb\uff0c\u6211\u770b\u4f60\u957f\u5f97\u5f88\u50cf\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4eec\uff1f\u201d\u5f20\u606a\u624d\u60f3\u8d77\u6765\u5230\u516c\u53f8\u5f53\u7740\u6797\u4e1a\u5385\u5b98\u5458\u7684\u9762\uff0c\u4ed6\u7238\u7238\u51e0\u4e4e\u6ca1\u5f00\u53e3\u8bf4\u8fc7\u8bdd\uff0c\u89c1\u6c5f\u6d9b\u4e0d\u6562\u63a8\u6d4b\u4ed6\u4eec\u7684\u5173\u7cfb\uff0c\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u8fd8\u6ca1\u6709\u90d1\u91cd\u7684\u8ddf\u6c5f\u5385\u957f\u4ecb\u7ecd\u5462\uff0c\u8fd9\u4f4d\u5f20\u79d8\u4e66\u957f\u662f\u6211\u7238\u7238\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u6c5f\u6d9b\u96be\u4ee5\u7f6e\u4fe1\u7684\u5438\u4e86\u4e00\u53e3\u51c9\u6c14\uff0c\u5012\u4e0d\u662f\u8bf4\u5f20\u606a\u4e0e\u5f20\u77e5\u884c\u4e00\u5546\u4e00\u5b98\u7684\u8eab\u4efd\uff0c\u201c\u5f20\u79d8\u4e66\u957f\u770b\u8d77\u6765\u6bd4\u6211\u5e74\u8f7b\uff0c\u6211\u4eca\u5e74\u56db\u5341\u516b\uff0c\u5f20\u79d8\u4e66\u957f\u8d35\u5e9a\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u56db\u5341\u516d\u2026\u2026\u201d\u5f20\u77e5\u884c\u7b11\u4e86\u7b11\uff0c\u6362\u4f5c\u4e0d\u77e5\u5e95\u7ec6\u7684\u4eba\uff0c\u8c01\u90fd\u96be\u4ee5\u76f8\u4fe1\u773c\u524d\u7684\u4e8b\u5b9e\uff0c\u201c\u5f20\u606a\u4eca\u5e74\u624d\u5341\u516b\u5c81\uff0c\u770b\u4e0a\u53bb\u6210\u719f\u4e00\u4e9b\uff0c\u8fd8\u4ee5\u4e3a\u5f90\u7701\u957f\u65e9\u8ddf\u6c5f\u5385\u957f\u63d0\u8d77\u8fc7\u5462\u3002\u201d</p><p>\u3000\u3000\u8bb8\u591a\u8bdd\u5361\u5728\u55d3\u5b50\u773c\u91cc\uff0c\u4e00\u65f6\u90fd\u4e0d\u89c9\u5f97\u5408\u9002\u8bf4\u51fa\u53e3\u3002\u4efb\u6c5f\u6d9b\u5728\u7701\u59d4\u529e\u516c\u5385\u5c06\u4eba\u4e8b\u7422\u78e8\u900f\u5f7b\uff0c\u8fd8\u662f\u96be\u4ee5\u6d88\u5316\u542c\u5230\u7684\u4e8b\u5b9e\u3002\u5f20\u77e5\u884c\u7684\u513f\u5b50\u4ece\u8f66\u8f6e\u4e0b\u6551\u51fa\u82b7\u5f64\u7684\u4e8b\u60c5\uff0c\u6c5f\u6d9b\u542c\u8bf4\u8fc7\uff0c\u53ea\u662f\u4ed6\u4e00\u76f4\u672a\u80fd\u5c06\u6551\u4eba\u7684\u5c11\u5e74\u4e0e\u5f20\u606a\u8054\u7cfb\u8d77\u6765\uff0c\u5373\u4f7f\u5728\u516c\u53f8\u89c9\u5f97\u4e24\u4eba\u5f88\u50cf\uff0c\u4e5f\u6ca1\u6709\u8054\u7cfb\u5230\u8fd9\u4ef6\u4e8b\u4e0a\uff0c\u6bd5\u7adf\u4e00\u4e2a\u521a\u6ee1\u5341\u516b\u5c81\u7684\u5c11\u5e74\uff0c\u5c31\u62e5\u6709\u4e0a\u4ebf\u7684\u8d22\u5bcc\u662f\u90a3\u6837\u7684\u4e0d\u53ef\u601d\u8bae\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u53bb\u5e74\u65e0\u610f\u6210\u7acb\u7684\u4e00\u5bb6\u751f\u4ea7\u5f71\u789f\u673a\u7684\u516c\u53f8\uff0c\u504f\u504f\u773c\u4e0b\u5f71\u789f\u673a\u8fd8\u6bd4\u8f83\u8d5a\u94b1\uff0c\u4e00\u4e0d\u5c0f\u5fc3\u5c31\u6210\u4e86\u4ebf\u4e07\u5bcc\u7fc1\uff0c\u6211\u81ea\u5df1\u5b8c\u5168\u9884\u6599\u4e0d\u5230\u7684\u2026\u2026\u201d\u5f20\u606a\u5c3d\u53ef\u80fd\u8bf4\u5f97\u8f7b\u63cf\u6de1\u5199\u4e00\u4e9b\uff0c\u8fd9\u6b21\u5230\u7701\u91cc\u6765\uff0c\u4e5f\u4f1a\u8ddf\u7238\u7238\u5230\u7701\u7eaa\u68c0\u90e8\u95e8\u5c06\u8fd9\u4e9b\u60c5\u51b5\u89e3\u91ca\u6e05\u695a\u3002</p><p>\u3000\u3000\u201c\u4f60\u662f\u8bf4\u6d77\u5dde\u5e02\u7684\u7231\u8fbe\u7535\u5b50\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u7684\uff0c\u56e0\u4e3a\u6211\u8fd8\u6ca1\u6ee1\u5341\u516b\u5c81\u7684\u7f18\u6545\uff0c\u6240\u4ee5\u6cd5\u4eba\u4ee3\u8868\u4ee5\u53ca\u516c\u53f8\u7684\u7ba1\u7406\u4eba\u90fd\u662f\u8bf7\u522b\u4eba\u4ee3\u66ff\u3002\u54e6\uff0c\u5373\u4f7f\u73b0\u5728\uff0c\u6211\u8fd8\u662f\u4ee5\u5b66\u4e60\u4e3a\u4e3b\uff0c\u516c\u53f8\u7684\u4e8b\u60c5\uff0c\u6211\u4e5f\u4e0d\u5927\u61c2\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u5f97\u8c26\u865a\uff0c\u6c5f\u6d9b\u5374\u5b8c\u5168\u4e0d\u8fd9\u4e48\u8ba4\u4e3a\uff0c\u8981\u4e0d\u662f\u5f20\u606a\u4e3b\u52a8\u8bf4\u7834\uff0c\u4ed6\u4e07\u4e07\u60f3\u4e0d\u5230\u5f20\u606a\u4f1a\u662f\u6d77\u5dde\u5e02\u653f\u5e9c\u79d8\u4e66\u957f\u5f20\u77e5\u884c\u7684\u513f\u5b50\uff0c\u800c\u4e14\u8fd8\u53ea\u6709\u5341\u516b\u5c81\u3002</p><p>\u3000\u3000\u5c11\u5e74\u5f97\u5fd7\u7684\u4eba\uff0c\u6c5f\u6d9b\u4e5f\u6ca1\u6709\u5c11\u542c\u8bf4\u8fc7\uff0c\u53ea\u662f\u4eb2\u773c\u770b\u5230\uff0c\u5f20\u606a\u662f\u7b2c\u4e00\u4e2a\uff0c\u4e0d\u80fd\u4e0d\u4ee4\u4ed6\u8be7\u5f02\u4e07\u5206\u3002\u4e0d\u6e05\u695a\u5f90\u5b66\u5e73\u6709\u6ca1\u6709\u8ddf\u9676\u664b\u4e66\u8bb0\u63d0\u8d77\u8fd9\u4e8b\uff0c\u60f3\u5fc5\u9676\u664b\u4e66\u8bb0\u4e5f\u4f1a\u8be7\u5f02\u4e07\u5206\u7684\u3002</p><p>\u3000\u3000\u201c\u54b3\u54b3\u2026\u2026\u201d\u6c5f\u6d9b\u6e05\u54b3\u4e86\u4e24\u58f0\u6765\u63a9\u9970\u521a\u624d\u7684\u5931\u6001\uff0c\u201c\u5f20\u2026\u2026\u603b\uff0c\u5e74\u5c11\u6709\u4e3a\uff0c\u6211\u8ddf\u8d35\u516c\u53f8\u7684\u5468\u6e38\u8c08\u8fc7\u51e0\u6b21\uff0c\u5468\u6e38\u53ef\u5bf9\u4f60\u8d5e\u4e0d\u7edd\u53e3\uff0c\u5468\u6e38\u8fd8\u8bf4\u5f20\u603b\u4e00\u5b9a\u4f1a\u8ba9\u6211\u5927\u5403\u4e00\u60ca\u7684\uff0c\u8fd8\u771f\u662f\u5927\u5403\u4e00\u60ca\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u6ca1\u5fc5\u8981\u4e92\u76f8\u5439\u6367\uff0c\u9676\u664b\u8fd9\u65f6\u5019\u5c06\u6c5f\u6d9b\u63a8\u5230\u7701\u6797\u4e1a\u7cfb\u7edf\u7684\u65cb\u6da1\u91cc\uff0c\u8981\u4e48\u6c5f\u6d9b\u7684\u80fd\u529b\u51fa\u4f17\uff0c\u8981\u4e48\u9676\u664b\u5f88\u4e0d\u5f85\u89c1\u6c5f\u6d9b\u624d\u628a\u4ed6\u5f80\u706b\u5751\u91cc\u63a8\uff0c\u7b54\u6848\u663e\u7136\u662f\u524d\u8005\uff0c\u4ed6\u8bf4\u9053\uff1a\u201c\u65b0\u5149\u7eb8\u4e1a\u7684\u524d\u8eab\uff0c\u60f3\u5fc5\u6c5f\u5385\u957f\u4e5f\u6e05\u695a\uff0c\u5373\u4f7f\u5728\u5c0f\u6c5f\u4e2d\u4e0a\u6e38\u5927\u9762\u79ef\u7684\u8425\u6797\uff0c\u5bf9\u5404\u65b9\u9762\u90fd\u662f\u4e00\u4ef6\u6709\u76ca\u7684\u4e8b\u60c5\uff0c\u5f90\u7701\u957f\u4e5f\u4e0d\u65b9\u4fbf\u76f4\u63a5\u7ad9\u51fa\u6765\u652f\u6301\uff0c\u6240\u4ee5\u6d77\u88d5\u516c\u53f8\u8fd9\u6b21\u624d\u4f1a\u5b8c\u5168\u64a4\u51fa\u65b0\u5149\u7eb8\u4e1a\u2026\u2026\u201d\u5f20\u606a\u5c06\u9526\u6e56\u3001\u7231\u8fbe\u7535\u5b50\u3001\u65b0\u5149\u7eb8\u4e1a\u4e0e\u6d77\u88d5\u516c\u53f8\u4e4b\u95f4\u7684\u5173\u7cfb\u4ecb\u7ecd\u7ed9\u6c5f\u6d9b\u542c\uff0c\u514d\u5f97\u65e5\u540e\u5408\u4f5c\u65f6\u6c5f\u6d9b\u6709\u4ec0\u4e48\u987e\u5fcc\uff0c\u7834\u574f\u65e2\u6709\u7684\u5de5\u4f5c\u6b65\u9aa4\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u4e8b\u60c5\u4f1a\u5411\u7701\u7eaa\u68c0\u90e8\u95e8\u8bf4\u660e\u60c5\u51b5\uff0c\u81ea\u7136\u6ca1\u6709\u5fc5\u8981\u7792\u7740\u6c5f\u6d9b\uff0c\u5230\u7701\u653f\u5e9c\u624d\u4e8c\u5341\u591a\u5206\u949f\u7684\u8f66\u7a0b\uff0c\u4e0d\u8fc7\u5bf9\u6c5f\u6d9b\u6765\u8bf4\uff0c\u8fd9\u77ed\u77ed\u4e8c\u5341\u591a\u5206\u949f\u7684\u8f66\u8def\u91cc\u77e5\u9053\u7684\u4e8b\u60c5\u5374\u8ba9\u4ed6\u77a0\u76ee\u7ed3\u820c\u3002</p></article>"}}}]);
