(window.webpackJsonp=window.webpackJsonp||[]).push([[336],{358:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u4e8c\u8282 \u7be1\u593a",content:"<article><h2>\u7b2c\u4e94\u5341\u4e8c\u8282 \u7be1\u593a</h2><p>\u6b23\u5e03\u4e2d\u4e86\u9ad8\u7b49\u964d\u5492\uff0c\u5168\u8eab\u762b\u75ea\u4e0d\u80fd\u52a8\u5f39\uff0c\u5e78\u597d\u6709\u8475\u9732\u5728\uff0c\u53ca\u65f6\u7528\u94f6\u706b\u66ff\u5979\u6cbb\u7597\uff0c\u9a71\u9000\u8bc5\u5492\uff0c\u7ec8\u4e8e\u6e10\u6e10\u6062\u590d\u8fc7\u6765\u3002\u8428\u9a6c\u65af\u7279\u7684\u6cd5\u672f\u53ea\u662f\u8ba9\u5979\u529b\u6c14\u5168\u5931\uff0c\u795e\u667a\u4f9d\u65e7\u6e05\u9192\uff0c\u6240\u4ee5\u540e\u6765\u53d1\u751f\u7684\u4e00\u5207\u5979\u5168\u90fd\u77e5\u9053\uff0c\u53c8\u542c\u743c\u6069\u628a\u524d\u56e0\u540e\u679c\u5927\u81f4\u8bb2\u8ff0\u4e86\u4e00\u904d\uff0c\u57fa\u672c\u660e\u767d\u4e86\u662f\u600e\u4e48\u56de\u4e8b\u3002\u5f53\u7136\uff0c\u743c\u6069\u6240\u8bf4\u7684\u4e5f\u53ea\u662f\u201c\u5927\u81f4\u201d\uff0c\u7ec6\u8282\u57fa\u672c\u90fd\u542b\u7cca\u8fc7\u53bb\uff0c\u4ed6\u603b\u4e0d\u80fd\u5f53\u7740\u8fd9\u4e48\u591a\u4eba\u7684\u9762\u5927\u8c08\u7279\u8c08\u8428\u9a6c\u65af\u7279\u7684\u7f8e\u5973\u72ac\u548c\u5c38\u5993\u6784\u60f3\u5427\uff0c\u81f3\u4e8e\u8bf4\u628a\u4e24\u4e2a\u9009\u6c11\u8c03\u6559\u6210\u6027\u5974\u4e4b\u7c7b\u7684\u8bf4\u6cd5\u4e5f\u76f4\u63a5\u7565\u8fc7\uff0c\u800c\u4e14\u6709\u4e9b\u5730\u65b9\uff0c\u6d89\u53ca\u743c\u6069\u81ea\u5df1\u7684\u79d8\u5bc6\uff0c\u4f8b\u5982\u8428\u9a6c\u65af\u7279\u53d1\u660e\u7684\u90a3\u79cd\u4ee5\u900f\u652f\u751f\u547d\u4e3a\u4ee3\u4ef7\u7684\u6df1\u5ea6\u6697\u793a\u672f\uff0c\u81ea\u7136\u662f\u66f4\u52a0\u4e0d\u4f1a\u8bf4\u4e86\u3002</p><p>\u6b23\u5e03\u4ece\u5e8a\u4e0a\u6162\u6162\u5750\u8d77\uff0c\u9760\u7740\u5899\u58c1\uff0c\u773c\u5149\u7f13\u7f13\u4ece\u623f\u95f4\u91cc\u626b\u8fc7\uff0c\u6700\u540e\u843d\u5230\u743c\u6069\u8138\u4e0a\u3002\u201c\u8c22\u8c22\u3002\u201d\u5979\u7b80\u5355\u5730\u8bf4\uff0c\u70b9\u4e86\u70b9\u5934\u3002</p><p>\u743c\u6069\u7b11\u4e86\u4e00\u7b11\uff0c\u201c\u4f60\u8bf4\u4ed6\u6ca1\u90a3\u4e48\u5bb9\u6613\u6b7b\uff1f\u201d\u4ed6\u76f4\u63a5\u8f6c\u56de\u521a\u624d\u7684\u8bdd\u9898\uff0c\u201c\u96be\u9053\u90a3\u8bb0\u711a\u8eab\u7206\u8fd8\u6740\u4e0d\u6b7b\u4ed6\uff1f\u201d</p><p>\u708e\u9b54\u88ab\u9522\u9b42\u672f\u51fb\u4e2d\uff0c\u968f\u5373\u5b9d\u77f3\u7206\u70b8\u7684\u65f6\u5019\uff0c\u743c\u6069\u8fd8\u5728\u5854\u4e0a\uff0c\u4eb2\u773c\u89c1\u8bc6\u5230\u90a3\u5de8\u5927\u5a01\u52bf\u3002\u867d\u7136\u81ea\u5df1\u8ddd\u79bb\u8f83\u8fdc\uff0c\u800c\u4e14\u65e9\u6709\u51c6\u5907\uff0c\u4e8b\u5148\u5728\u8eab\u4e0a\u53e0\u52a0\u4e86\u5404\u79cd\u9632\u62a4\u6cd5\u672f\uff0c\u4f9d\u65e7\u88ab\u9707\u5f97\u5934\u6655\u76ee\u7729\uff0c\u5185\u8151\u7ffb\u817e\uff0c\u51e0\u4e4e\u8981\u5f53\u573a\u5410\u51fa\u8840\u6765\uff0c\u5426\u5219\u4e5f\u4e0d\u81f3\u4e8e\u5760\u5854\u4e4b\u540e\u8981\u8db3\u8db3\u82b1\u4e24\u79d2\u949f\u624d\u80fd\u5b9a\u5fc3\u56de\u795e\uff0c\u6fc0\u6d3b\u98de\u884c\u672f\u3002\u8fd9\u79cd\u81ea\u7206\u7c7b\u578b\u7684\u62db\u6570\uff0c\u4ed6\u4e5f\u4e0d\u662f\u6ca1\u89c1\u8bc6\u8fc7\uff0c\u5f53\u65e5\u5728\u5e7d\u6697\u5730\u57df\u91cc\u9047\u5230\u7684\u90a3\u53ea\u7075\u5438\u602a\u5deb\u5996\u65af\u5170\u666e\uff0c\u5c31\u6709\u7c7b\u4f3c\u7684\u6cd5\u672f\uff0c\u636e\u8bf4\u662f\u4ece\u5357\u65b9\u4e00\u4e2a\u53eb\u505a\u54c8\u9c81\u963f\u7684\u5730\u65b9\u5b66\u6765\u7684\uff0c\u540d\u4e3a\u201c\u9ea6\u5eb7\u63d0\u5c14\u4e4b\u6700\u540e\u4e00\u51fb\u201d\uff0c\u5c06\u5deb\u5e08\u6240\u6709\u51c6\u5907\u7684\u6cd5\u672f\u8fd8\u539f\u6210\u7eaf\u7cb9\u7684\u9b54\u6cd5\u80fd\u91cf\u7206\u70b8\u5f00\u6765\uff0c\u4e3a\u7684\u5c31\u662f\u548c\u5bf9\u624b\u540c\u5f52\u4e8e\u5c3d\uff0c\u786e\u5b9e\u5389\u5bb3\u65e0\u6bd4\u3002\u4eca\u5929\u8fd9\u708e\u9b54\u7684\u711a\u8eab\u7206\uff0c\u8f83\u4e4b\u65af\u5170\u666e\u7684\u201c\u9ea6\u5eb7\u63d0\u5c14\u4e4b\u6700\u540e\u4e00\u51fb\u201d\uff0c\u5a01\u529b\u53ea\u6015\u8981\u80dc\u8fc7\u4e24\u500d\u4e0d\u6b62\uff0c\u53c8\u662f\u201c\u96f6\u8ddd\u79bb\u201d\u653b\u51fb\uff0c\u4ee5\u743c\u6069\u7684\u4f30\u7b97\uff0c\u522b\u8bf4\u8428\u9a6c\u65af\u7279\u7ec8\u7a76\u53ea\u662f\u4e00\u526f\u9ab7\u9ac5\u9aa8\u67b6\uff0c\u54ea\u6015\u4ed6\u5c31\u662f\u4e2a\u7eaf\u7cbe\u91d1\u94f8\u9020\u7684\u9b54\u50cf\uff0c\u5e94\u8be5\u4e5f\u88ab\u70b8\u6210\u788e\u5757\u4e86\u3002</p><p>\u201c\u6050\u6015\u4e0d\u884c\uff0c\u201d\u6b23\u5e03\u53f9\u6c14\uff0c\u201c\u4ed6\u548c\u666e\u901a\u7684\u5deb\u5996\u4e0d\u540c\u3002\u201d</p><p>\u201c\u600e\u4e48\u4e0d\u540c\uff1f\u201d</p><p>\u6b23\u5e03\u770b\u770b\u5468\u56f4\uff0c\u6b64\u65f6\u623f\u95f4\u91cc\u53ea\u6709\u4e24\u4f4d\u9009\u6c11\u3001\u743c\u6069\u3001\u6885\u83f2\u65af\u548c\u51db\uff0c\u838e\u73de\u514b\u88ab\u6d3e\u51fa\u53bb\u6253\u63a2\u6d88\u606f\u4e86\uff0c\u963f\u5fd2\u59ae\u548c\u4f0a\u8389\u96c5\u5c1a\u672a\u5f52\u6765\u3002\u5979\u548c\u8475\u9732\u5bf9\u89c6\u4e00\u773c\uff0c\u5fae\u5fae\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u8428\u9a6c\u65af\u7279\u4e5f\u6709\u795e\u529b\uff0c\u201d\u6b23\u5e03\u8f7b\u58f0\u8bf4\uff0c\u201c\u708e\u9b54\u7684\u711a\u8eab\u7206\u80fd\u91cd\u4f24\u4ed6\uff0c\u4f46\u60f3\u6740\u6b7b\u5374\u4e0d\u5bb9\u6613\u3002\u201d</p><p>\u8428\u9a6c\u65af\u7279\u4e5f\u6709\u795e\u529b\uff1f</p><p>\u743c\u6069\u4e00\u65f6\u9519\u6115\uff0c\u6ca1\u60f3\u5230\u4f1a\u662f\u8fd9\u79cd\u56de\u7b54\uff0c\u201c\u4ed6\u4e3a\u4ec0\u4e48\u4f1a\u6709\u795e\u529b\uff0c\u201d\u4ed6\u4e0d\u89e3\uff0c\u201c\u4ed6\u4e0d\u662f\u65e9\u5c31\u88ab\u5265\u593a\u9009\u6c11\u8d44\u683c\u4e86\u4e48\u3002\u201d</p><p>\u6b23\u5e03\u72b9\u8c6b\u7740\uff0c\u4f3c\u4e4e\u4e0d\u77e5\u9053\u5982\u4f55\u63aa\u8f9e\u89e3\u91ca\uff0c\u201c\u4f60\u77e5\u9053\uff0c\u8428\u9a6c\u65af\u7279\u662f\u4e00\u4e2a\u5929\u624d\uff0c\u201d\u5979\u6700\u540e\u8bf4\uff0c\u201c\u4ed6\u66fe\u7ecf\u5728\u5f88\u957f\u4e00\u6bb5\u65f6\u95f4\u91cc\u62c5\u4efb\u9009\u6c11\uff0c\u5bf9\u94f6\u706b\u6709\u7740\u975e\u5e38\u6df1\u5165\u7684\u7814\u7a76\uff0c\u591a\u6709\u521b\u9020\u2026\u2026\u751a\u81f3\uff0c\u5c31\u50cf\u6211\u4eca\u5929\u4f7f\u7528\u7684\u96f7\u7535\u67aa\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u4e5f\u6709\u4ed6\u7684\u8d21\u732e\u5728\u5185\u3002\u201d</p><p>\u201c\u6211\u542c\u8bf4\u8fc7\u3002\u201d\u743c\u6069\u8bf4\u3002</p><p>\u8428\u9a6c\u65af\u7279\u5728\u62c5\u4efb\u9009\u6c11\u7684\u4e8c\u5341\u591a\u5e74\u4e2d\uff0c\u53d1\u660e\u51fa\u4e86\u94f6\u706b\u7403\u3001\u94f6\u706b\u95ea\u7535\u3001\u94f6\u706b\u62a4\u76fe\u3001\u94f6\u706b\u722a\u7b49\u5404\u79cd\u8fd0\u7528\u65b9\u6cd5\uff0c\u5c06\u94f6\u706b\u7684\u5a01\u529b\u771f\u6b63\u53d1\u6325\u51fa\u6765\u3002\u800c\u8fd9\u4e00\u5207\u7684\u80cc\u540e\uff0c\u5219\u610f\u5473\u7740\u4e00\u4e2a\u4e8b\u5b9e\uff1a\u8428\u9a6c\u65af\u7279\u5bf9\u94f6\u706b\u672c\u8d28\u7684\u8ba4\u8bc6\uff0c\u5df2\u7ecf\u6e05\u695a\u900f\u5f7b\u5230\u4e86\u4e00\u4e2a\u60ca\u4eba\u7684\u5730\u6b65\u3002\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u4ed6\u624d\u80fd\u9ad8\u5c4b\u5efa\u74f4\uff0c\u540e\u6765\u5c45\u4e0a\uff0c\u80dc\u8fc7\u90a3\u4e9b\u8001\u724c\u9009\u6c11\u4eec\uff0c\u6709\u8fd9\u6837\u591a\u7684\u521b\u65b0\u548c\u8d21\u732e\u3002\u4ed6\u5bf9\u743c\u6069\u81ea\u79f0\u201c\u6709\u53f2\u4ee5\u6765\u5bf9\u795e\u529b\u6700\u6709\u7814\u7a76\u7684\u51e1\u4eba\u201d\uff0c\u5e76\u4e0d\u662f\u5439\u5618\uff0c\u800c\u662f\u540d\u526f\u5176\u5b9e\u3002</p><p>\u8428\u9a6c\u65af\u7279\u5bf9\u94f6\u706b\uff08\u6216\u8005\u8bf4\u795e\u529b\uff09\u7684\u6df1\u5165\u7814\u7a76\uff0c\u5176\u76ee\u7684\u5e76\u4e0d\u4ec5\u4ec5\u662f\u4e3a\u4e86\u63a2\u5bfb\u5404\u79cd\u53d8\u5316\u8fd0\u7528\u65b9\u6cd5\u2014\u2014\u8fd9\u53ea\u662f\u9644\u5e26\u4ea7\u54c1\uff0c\u4ed6\u771f\u6b63\u6240\u8ffd\u6c42\u7684\uff0c\u662f\u201c\u6c38\u751f\u201d\u3002</p><p>\u201c\u6c38\u751f\uff1f\u201d</p><p>\u201c\u6c38\u751f\uff0c\u201d\u6b23\u5e03\u8bf4\uff0c\u201c\u4e5f\u5c31\u662f\u8bf4\u957f\u547d\u4e0d\u6b7b\uff0c\u9752\u6625\u6c38\u9a7b\u3002\u201d</p><p>\u201c\u7136\u800c\u4ed6\u4e0d\u5df2\u7ecf\u662f\u9009\u6c11\u4e86\u5417\uff1f\u201d\u743c\u6069\u6307\u51fa\uff0c\u201c\u4f60\u4eec\u9009\u6c11\u4e0d\u662f\u5df2\u7ecf\u53ef\u4ee5\u957f\u751f\u4e0d\u6b7b\u4e86\u5417\uff1f\u201d</p><p>\u201c\u4f46\u8fd9\u662f\u795e\u7947\u7684\u8d50\u4e88\uff0c\u800c\u8428\u9a6c\u65af\u7279\u6240\u671f\u671b\u7684\uff0c\u662f\u4e0d\u4f9d\u8d56\u4e8e\u795e\u7947\u800c\u83b7\u5f97\u6c38\u751f\u3002\u201d</p><p>\u743c\u6069\u76b1\u7709\uff0c\u9690\u7ea6\u89c9\u5f97\u4ec0\u4e48\u5730\u65b9\u4f3c\u4e4e\u4e0d\u592a\u5bf9\u52b2\uff0c\u201c\u90a3\u4ed6\u600e\u4e48\u505a\u5462\uff1f\u201d</p><p>\u201c\u4ed6\u5c45\u7136\u8bd5\u56fe\u6253\u7b97\u7a83\u53d6\u5973\u795e\u8d50\u4e88\u7684\u94f6\u706b\uff0c\u628a\u5b83\u53d8\u6210\u771f\u6b63\u5c5e\u4e8e\u81ea\u8eab\u7684\u80fd\u529b\u3002\u201d</p><p>\u743c\u6069\u5fc3\u4e2d\u6697\u81ea\u4e00\u51db\uff0c\u4eff\u4f5b\u628a\u63e1\u5230\u4e86\u4ec0\u4e48\uff0c\u201c\u4ed6\u6210\u529f\u4e86\uff1f\u201d</p><p>\u201c\u5e94\u8be5\u662f\uff0c\u201d\u6b23\u5e03\u8bf4\uff0c\u201c\u867d\u7136\u5f88\u4e0d\u53ef\u601d\u8bae\uff0c\u4f46\u4ed6\u4f3c\u4e4e\u771f\u7684\u505a\u5230\u4e86\u3002875DR\uff0c\u4ed6\u53d1\u4e86\u75af\uff0c\u5c45\u7136\u653b\u51fb\u827e\u62c9\u65af\u5353\uff0c\u56e0\u6b64\u88ab\u5265\u593a\u4e86\u9009\u6c11\u8eab\u5206\uff0c\u6536\u56de\u94f6\u706b\u2014\u2014\u4f46\u5f53\u65f6\u963f\u7956\u65af\u5374\u53d1\u73b0\u4ed6\u4f53\u5185\u5c45\u7136\u6709\u4e00\u4e1d\u94f6\u706b\u65e0\u6cd5\u6536\u56de\uff0c\u5df2\u7ecf\u548c\u4ed6\u878d\u5408\u4e3a\u4e00\u4e86\u3002\u201d</p><p>\u201c\u4ed6\u600e\u4e48\u505a\u5230\u7684\uff1f\u201d\u743c\u6069\u95ee\uff0c\u8bed\u6c14\u4e2d\u9690\u9690\u900f\u51fa\u4e00\u4e1d\u6025\u5207\u3002</p><p>\u6b23\u5e03\u6709\u4e9b\u5947\u602a\u5730\u770b\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u4ed6\u5230\u5e95\u662f\u600e\u4e48\u505a\u5230\u7684\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u6ca1\u6709\u4eba\u77e5\u9053\uff0c\u8fd9\u662f\u79d8\u5bc6\u3002\u4f46\u4ed6\u548c\u827e\u62c9\u65af\u5353\u5728\u4e00\u8d77\u7684\u65f6\u5019\uff0c\u66fe\u7ecf\u8bed\u7109\u4e0d\u8be6\u5730\u900f\u9732\u8fc7\u4e00\u4e9b\uff0c\u4ed6\u4f3c\u4e4e\u662f\u53d1\u660e\u4e86\u67d0\u79cd\u5947\u602a\u7684\u9644\u9b54\u672f\uff0c\u4e00\u79cd\u81ea\u6211\u6697\u793a\u6216\u8005\u8bf4\u5f3a\u5236\u50ac\u7720\u7684\u65b9\u6cd5\uff0c\u80fd\u591f\u8ba9\u4ed6\u505a\u5230\u79cd\u79cd\u4e0d\u53ef\u601d\u8bae\u7684\u4e8b\u60c5\u2014\u2014\u8fd9\u6beb\u65e0\u7591\u95ee\u662f\u4ed6\u6700\u540e\u9677\u5165\u75af\u72c2\u7684\u539f\u56e0\u4e4b\u4e00\uff0c\u4f46\u6211\u4eec\u6000\u7591\u4ed6\u6700\u7ec8\u6210\u529f\u7a83\u53d6\u94f6\u706b\uff0c\u548c\u8fd9\u79cd\u9644\u9b54\u672f\u4e5f\u4e0d\u65e0\u5173\u7cfb\u3002\u53e6\u5916\uff0c\u4f0a\u5c14\u660e\u65af\u7279\u8ba4\u4e3a\uff0c\u8428\u9a6c\u65af\u7279\u5f88\u6709\u53ef\u80fd\u83b7\u5f97\u4e86\u67d0\u4e9b\u53e4\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u7684\u9b54\u6cd5\u77e5\u8bc6\u3002\u201d</p><p>\u53e4\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u7684\u9b54\u6cd5\u77e5\u8bc6\uff1f</p><p>\u743c\u6069\u5fc3\u4e2d\u72c2\u8df3\u4e0d\u6b62\uff0c\u4ed6\u611f\u89c9\u81ea\u5df1\u4f3c\u4e4e\u5df2\u7ecf\u5c06\u8981\u89e6\u53ca\u79d8\u5bc6\u7684\u6838\u5fc3\uff0c\u201c\u8010\u745f\u745e\u5c14\u5e1d\u56fd\u7684\u4ec0\u4e48\u9b54\u6cd5\u77e5\u8bc6\uff1f\u201d\u4ed6\u5c3d\u53ef\u80fd\u5e73\u9759\u5730\u95ee\uff0c\u201c\u6211\u6216\u8bb8\u542c\u8fc7\u3002\u201d</p><p>\u201c\u90a3\u6211\u5c31\u786e\u5b9e\u4e0d\u6e05\u695a\u4e86\u3002\u201d\u6b23\u5e03\u6447\u5934\uff0c\u4e0d\u80af\u5c31\u8fd9\u4e2a\u8bdd\u9898\u518d\u591a\u8bf4\u3002</p><p>\u203b\u203b\u203b</p><p>\u8428\u9a6c\u65af\u7279\u7684\u4f5c\u6cd5\u786e\u5b9e\u60ca\u4eba\uff0c\u9009\u6c11\u7684\u795e\u529b\uff0c\u6765\u81ea\u4e8e\u795e\u7947\u7684\u6388\u4e88\uff0c\u76f8\u5f53\u4e8e\u4e00\u79cd\u6743\u529b\u6682\u65f6\u4e0b\u653e\uff0c\u4ed6\u5c45\u7136\u80fd\u591f\u4ece\u4e2d\u7be1\u593a\uff0c\u5316\u5f52\u81ea\u8eab\uff0c\u8fd9\u4efd\u672c\u9886\u548c\u80c6\u8bc6\u5b9e\u5728\u662f\u975e\u540c\u5c0f\u53ef\u3002\u53ea\u662f\u8fd9\u65b9\u6cd5\u7684\u9002\u7528\u6027\u4e5f\u5f88\u5dee\uff0c\u56e0\u4e3a\u4f60\u9996\u5148\u5f97\u662f\u9009\u6c11\u624d\u884c\uff0c\u5426\u5219\u8fde\u4e0b\u653e\u7684\u795e\u529b\u90fd\u6ca1\u6709\uff0c\u53c8\u54ea\u91cc\u8fd8\u8c08\u5176\u4ed6\u3002\u8428\u9a6c\u65af\u7279\u521a\u521a\u878d\u5408\u4e00\u4e1d\u795e\u529b\uff0c\u5c31\u88ab\u5265\u593a\u4e86\u9009\u6c11\u8d44\u683c\uff0c\u4ed6\u7684\u795e\u5947\u65b9\u6cd5\u4ece\u6b64\u4e5f\u5c31\u6ca1\u6709\u4e86\u7528\u6b66\u4e4b\u5730\uff0c\u4e0d\u673d\u7684\u68a6\u60f3\u6210\u7a7a\uff0c\u53ea\u597d\u8001\u8001\u5b9e\u5b9e\u8f6c\u5316\u5deb\u5996\u53bb\u4e86\u3002</p><p>\u5c3d\u7ba1\u5982\u6b64\uff0c\u8fd9\u4e00\u4e1d\u795e\u529b\uff0c\u4f9d\u65e7\u8db3\u4ee5\u8ba9\u4ed6\u9762\u5bf9\u9009\u6c11\u4eec\u7684\u5c61\u6b21\u56f4\u653b\u800c\u5c79\u7acb\u4e0d\u5012\uff0c\u603b\u80fd\u591f\u6b7b\u91cc\u9003\u751f\uff0c\u4e1c\u5c71\u518d\u8d77\uff0c\u76f4\u81f3\u521b\u7acb\u9f99\u5deb\u6559\uff0c\u5728\u6574\u4e2a\u5927\u9646\u6380\u8d77\u4e00\u573a\u8165\u98ce\u8840\u96e8\u3002\u5018\u82e5\u4e0d\u662f\u540e\u6765\u6668\u66e6\u4e4b\u795e\u5170\u68ee\u5fb7\u5c14\u4eb2\u81ea\u964d\u4e34\uff0c\u5c06\u4ed6\u51fb\u6740\uff0c\u4e8b\u60c5\u8fd8\u4e0d\u77e5\u9053\u5982\u4f55\u4e86\u5c40\u3002</p><p>\u5373\u4fbf\u662f\u51e0\u6b21\u6b7b\u800c\u590d\u6d3b\uff0c\u5b9e\u529b\u9887\u6709\u524a\u5f31\uff0c\u4f46\u90a3\u4e00\u4e1d\u795e\u529b\u4f9d\u65e7\u5b58\u7559\u3002\u56e0\u4e3a\u592a\u8fc7\u5fae\u5f31\uff0c\u8428\u9a6c\u65af\u7279\u65e0\u6cd5\u7528\u6765\u5851\u6210\u94f6\u706b\u7403\u3001\u94f6\u706b\u62a4\u76fe\u3001\u96f7\u7535\u67aa\u4e4b\u7c7b\u7684\u6cd5\u672f\uff0c\u4f46\u6709\u4e86\u5b83\uff0c\u8001\u5deb\u5996\u6b63\u9762\u786c\u625b\u4e00\u51fb\u711a\u8eab\u7206\uff0c\u4f1a\u4e0d\u4f1a\u5c31\u6b64\u5b8c\u86cb\u8fd8\u771f\u4e0d\u597d\u8bf4\u3002</p><p>\u8fd9\u53ef\u5b9e\u5728\u4e0d\u662f\u4e2a\u597d\u6d88\u606f\u3002</p><p>\u743c\u6069\u76b1\u7740\u7709\u5934\uff0c\u6709\u4e9b\u5934\u75bc\u8d77\u6765\u3002\u4ed6\u4e4b\u6240\u4ee5\u9009\u62e9\u5bf9\u8428\u9a6c\u65af\u7279\u4e0b\u624b\u800c\u4e0d\u662f\u8001\u8001\u5b9e\u5b9e\u5408\u4f5c\uff0c\u539f\u56e0\u81ea\u7136\u6709\u5f88\u591a\uff0c\u4f46\u5176\u4e2d\u5f88\u91cd\u8981\u7684\u4e00\u70b9\uff0c\u5c31\u5728\u4e8e\u8428\u9a6c\u65af\u7279\u53ea\u662f\u4e00\u4e2a\u4eba\uff0c\u800c\u9b54\u6cd5\u5973\u795e\u7684\u9009\u6c11\u4eec\u5374\u662f\u4e00\u5927\u7968\u3002\u5f97\u7f6a\u4e86\u6b23\u5e03\u548c\u8475\u9732\uff0c\u90a3\u5c31\u662f\u6811\u4e86\u4e00\u7fa4\u5f3a\u5927\u7684\u6b7b\u654c\uff0c\u800c\u5f97\u7f6a\u4e86\u8428\u9a6c\u65af\u7279\uff0c\u90a3\u4e5f\u53ea\u662f\u4e00\u4e2a\u8001\u5deb\u5996\u2014\u2014\u743c\u6069\u76d8\u7b97\u4e86\u534a\u5929\uff0c\u89c9\u5f97\u81ea\u5df1\u5728\u53ef\u9884\u89c1\u7684\u5c06\u6765\uff0c\u7edd\u65e0\u5e0c\u671b\u628a\u9634\u5f71\u8c37\u5927\u8d24\u8005\u3001\u6df1\u6c34\u57ce\u4e3b\u3001\u4e03\u59d0\u59b9\u8fd9\u4e00\u7fa4\u9009\u6c11\u90fd\u5c3d\u6570\u6740\u5149\uff0c\u4f46\u53ea\u5e72\u6389\u8428\u9a6c\u65af\u7279\u4e00\u4e2a\uff0c\u90a3\u53ef\u884c\u6027\u5c31\u9ad8\u5f88\u591a\u4e86\u3002</p><p>\u5f53\u7136\uff0c\u8428\u9a6c\u65af\u7279\u662f\u5deb\u5996\uff0c\u53ea\u8981\u547d\u5323\u4e0d\u6bc1\uff0c\u4ed6\u5c31\u80fd\u590d\u6d3b\u2014\u2014\u4f46\u603b\u662f\u9700\u8981\u4e00\u5b9a\u7684\u65f6\u95f4\u7684\u3002\u660e\u5929\u51b3\u8d5b\u4e00\u8fc7\uff0c\u743c\u6069\u62ff\u5230\u5370\u7ae0\uff0c\u5927\u5bb6\u76f4\u63a5\u5f00\u6e9c\u56de\u7269\u8d28\u754c\uff0c\u8428\u9a6c\u65af\u7279\u8fd8\u53bb\u54ea\u91cc\u627e\u4ed6\uff1f\u743c\u6069\u751a\u81f3\u5df2\u7ecf\u60f3\u597d\uff0c\u56de\u5230\u7269\u8d28\u754c\uff0c\u76f4\u5954\u9634\u9b42\u57ce\uff0c\u8bf7\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u9884\u8a00\u5b9a\u4f4d\u8428\u9a6c\u65af\u7279\u7684\u547d\u5323\uff0c\u65a9\u8349\u9664\u6839\uff0c\u6216\u8005\u627e\u6b7b\u80d6\u5b50\u8001\u5e08\u5965\u6c83\u51fa\u9762\uff0c\u603b\u6709\u529e\u6cd5\u5e94\u4ed8\u3002</p><p>\u7b97\u76d8\u867d\u7136\u6253\u5f97\u5982\u610f\uff0c\u4e00\u5207\u5374\u90fd\u662f\u5efa\u7acb\u5728\u80fd\u591f\u6740\u6b7b\u2014\u2014\u81f3\u5c11\u662f\u6682\u65f6\u6740\u6b7b\u2014\u2014\u8428\u9a6c\u65af\u7279\u7684\u524d\u63d0\u4e0a\u3002\u743c\u6069\u539f\u672c\u4ee5\u4e3a\u4e00\u51fb\u201c\u96f6\u8ddd\u79bb\u201d\u711a\u8eab\u7206\u518d\u52a0\u4e09\u4e2a\u5df4\u6d1b\u708e\u9b54\uff0c\u600e\u4e48\u4e5f\u80fd\u5e72\u6389\u8428\u9a6c\u65af\u7279\u4e86\uff0c\u4e00\u65f6\u9690\u9690\u6709\u4e9b\u5f97\u610f\uff0c\u5982\u4eca\u542c\u6b23\u5e03\u8fd9\u4e48\u4e00\u8bf4\uff0c\u4e8b\u60c5\u53ea\u6015\u5012\u8fd8\u6ca1\u9884\u60f3\u5f97\u90a3\u4e48\u987a\u5229\u3002</p><p>\u201c\u5012\u4e5f\u65e0\u9700\u62c5\u5fc3\uff0c\u201d\u8475\u9732\u5728\u65c1\u8fb9\u8bf4\uff0c\u201c\u8428\u9a6c\u65af\u7279\u5c31\u7b97\u4e0d\u6b7b\uff0c\u81f3\u5c11\u4e5f\u4f1a\u53d7\u91cd\u521b\uff0c\u4ed6\u53ef\u662f\u4ea1\u7075\uff0c\u60f3\u6062\u590d\u6ca1\u6709\u90a3\u4e48\u5bb9\u6613\u3002\u201d</p><p>\u6d3b\u4eba\u53d7\u4f24\uff0c\u53ea\u8981\u7267\u5e08\u62ff\u6cbb\u7597\u6cd5\u672f\u7838\u4e0a\u53bb\uff0c\u52a0\u901f\u8eab\u4f53\u7684\u81ea\u6108\u80fd\u529b\uff0c\u4fbf\u80fd\u5feb\u901f\u5eb7\u590d\u3002\u743c\u6069\u5728\u7b2c\u4e8c\u8fdc\u5f81\u5e08\u7684\u65f6\u5019\uff0c\u4e5f\u66fe\u7ecf\u89c1\u8fc7\u9ad8\u660e\u7684\u7267\u5e08\u4e3a\u53d7\u4f24\u7684\u6218\u58eb\u6cbb\u7597\uff0c\u4e00\u9053\u6cbb\u7597\u672f\u91ca\u653e\u51fa\u6765\uff0c\u4f24\u53e3\u8840\u8089\u5f53\u771f\u662f\u201c\u4ee5\u8089\u773c\u770b\u5f97\u89c1\u7684\u901f\u5ea6\u201d\u6108\u5408\uff0c\u795e\u5999\u65e0\u6bd4\u3002\u7136\u800c\u4ea1\u7075\u4fbf\u6ca1\u6709\u8fd9\u4e48\u5e78\u8fd0\uff0c\u5b83\u4eec\u5df2\u7ecf\u4e0d\u662f\u8840\u8089\u4e4b\u8eaf\uff0c\u5e72\u67af\u50f5\u6b7b\u7684\u76ae\u8089\u9aa8\u9abc\u4e5f\u4e0d\u4f1a\u518d\u5ea6\u751f\u957f\uff0c\u6cbb\u7597\u672f\u62cd\u4e0a\u53bb\u53cd\u800c\u4f1a\u4e25\u91cd\u53d7\u521b\uff0c\u5c31\u50cf\u88ab\u743c\u6069\u5e72\u6389\u7684\u90a3\u53ea\u6b7b\u4ea1\u5de1\u730e\u8005\u3002\u4ea1\u7075\u5018\u82e5\u53d7\u4e86\u4f24\uff0c\u4f8b\u5982\u817f\u9aa8\u88ab\u6253\u788e\u4e86\uff0c\u4fbf\u5f97\u5bfb\u627e\u5408\u9002\u7684\u9aa8\u9abc\u6765\u66ff\u6362\u4e0a\uff0c\u540c\u65f6\u7528\u8d1f\u80fd\u91cf\u6cd5\u672f\uff08\u4f8b\u5982\u4f24\u5bb3\u672f\uff09\u8fde\u7f00\u3001\u878d\u5408\u3001\u5316\u5165\u8eaf\u4f53\u3001\u91cd\u65b0\u9a71\u52a8\uff0c\u9ebb\u70e6\u5f97\u5f88\u3002</p><p>\u8428\u9a6c\u65af\u7279\u7528\u9522\u9b42\u672f\u56da\u7981\u708e\u9b54\uff0c\u5bfc\u81f4\u711a\u8eab\u7206\u5c31\u5728\u638c\u5fc3\u8ff8\u53d1\uff0c\u5c31\u7b97\u4ed6\u771f\u80fd\u4e0d\u6b7b\uff0c\u81f3\u5c11\u90a3\u53ea\u80f3\u818a\u4e5f\u80af\u5b9a\u70b8\u788e\u4e86\uff0c\u7f3a\u4e86\u624b\u81c2\uff0c\u5374\u8981\u770b\u4ed6\u5982\u4f55\u65bd\u6cd5\u3002\u5982\u4eca\u8ddd\u79bb\u51b3\u8d5b\u53ea\u6709\u4e0d\u5230\u5341\u4e2a\u5c0f\u65f6\uff0c\u5373\u4fbf\u4ed6\u65e9\u6709\u51c6\u5907\uff0c\u968f\u8eab\u643a\u5e26\u4e86\u4e00\u5806\u9aa8\u9abc\u7528\u4e8e\u66ff\u6362\uff0c\u771f\u6b63\u201c\u5b89\u88c5\u201d\u5b8c\u6210\u4e5f\u5f97\u8d39\u4e0a\u534a\u5929\u529f\u592b\uff0c\u90a3\u5c31\u6ca1\u591a\u5c11\u65f6\u95f4\u91cd\u65b0\u51c6\u5907\u6cd5\u672f\u4e86\u3002</p><p>\u6b23\u5e03\u53ea\u662f\u4e2d\u4e86\u8bc5\u5492\uff0c\u5e76\u672a\u53d7\u4f24\uff0c\u5982\u4eca\u7ecf\u8fc7\u6cbb\u7597\u4e5f\u5df2\u7ecf\u6062\u590d\uff0c\u94f6\u706b\u867d\u7136\u6d88\u8017\u6b86\u5c3d\uff0c\u4e00\u65f6\u65e0\u6cd5\u8865\u5145\uff0c\u4f46\u6293\u7d27\u65f6\u95f4\u4f11\u606f\uff0c\u91cd\u65b0\u51c6\u5907\u6cd5\u672f\uff0c\u660e\u5929\u6bd4\u8d5b\u65f6\u7167\u6837\u8fd8\u662f\u8d27\u771f\u4ef7\u5b9e\u7684\u5927\u5deb\u5e08\u3002\u8475\u9732\u4e3a\u4e86\u66ff\u6b23\u5e03\u6cbb\u7597\uff0c\u94f6\u706b\u6d88\u8017\u4e0d\u5c11\uff0c\u4f46\u5230\u4e86\u660e\u5929\u6bd4\u8d5b\u65f6\uff0c\u5e94\u8be5\u4e5f\u80fd\u6062\u590d\u8fc7\u534a\uff0c\u5bf9\u8428\u9a6c\u65af\u7279\u4f9d\u65e7\u8fd8\u662f\u6709\u4e00\u5b9a\u5a01\u80c1\u3002\u52a0\u4e0a\u743c\u6069\u548c\u963f\u5fd2\u59ae\uff0c\u5b9e\u529b\u5e76\u672a\u6253\u592a\u591a\u6298\u6263\u3002\u53cd\u89c2\u8428\u9a6c\u65af\u7279\uff0c\u4ed6\u5374\u6ca1\u4ec0\u4e48\u5f3a\u529b\u961f\u53cb\uff0c\u53ea\u662f\u4e34\u65f6\u6293\u4e86\u51e0\u53ea\u5f17\u6d1b\u9b54\u51d1\u6570\uff0c\u80fd\u4e00\u8def\u6253\u8fdb\u51b3\u8d5b\u9760\u7684\u5168\u662f\u4ed6\u4e00\u4e2a\u4eba\u3002\u53ea\u8981\u5e72\u6389\u8428\u9a6c\u65af\u7279\uff0c\u5176\u4ed6\u4e5f\u5c31\u4e0d\u8db3\u4e3a\u60e7\u3002</p><p>\u8fd9\u6837\u4e00\u7b97\uff0c\u5927\u5bb6\u4e5f\u5c31\u653e\u4e0b\u5fc3\u6765\uff0c\u4f55\u51b5\u8fd9\u8fd8\u53ea\u662f\u505a\u8f83\u574f\u7684\u6253\u7b97\uff0c\u8bf4\u4e0d\u5b9a\u8428\u9a6c\u65af\u7279\u8fd0\u6c14\u4e0d\u4f73\uff0c\u771f\u7684\u88ab\u5f53\u573a\u70b8\u4e86\u4e2a\u7c89\u8eab\u788e\u9aa8\u4e5f\u672a\u53ef\u77e5\u3002\u201c\u5bf9\u4e86\uff0c\u201d\u743c\u6069\u7a81\u7136\u60f3\u8d77\u4e00\u4ef6\u4e8b\u60c5\u6765\uff0c\u201c\u4ed6\u4eca\u5929\u62db\u6765\u7684\u90a3\u79cd\u4e09\u4e2a\u8111\u888b\u7684\u602a\u7269\u662f\u4ec0\u4e48\uff1f\u201d</p><p>\u8428\u9a6c\u65af\u7279\u4e3a\u4e86\u9632\u6b62\u6b23\u5e03\u9003\u8131\uff0c\u5728\u9ad8\u5854\u5468\u56f4\u5e03\u7f6e\u4e86\u516b\u53ea\u5f17\u6d1b\u9b54\u548c\u56db\u53ea\u4e09\u9996\u7ffc\u9f99\uff0c\u6700\u540e\u88ab\u5e72\u6389\u4e86\u4e5d\u53ea\u3002\u6709\u5f17\u6d1b\u9b54\u5f88\u6b63\u5e38\uff0c\u8428\u9a6c\u65af\u7279\u53c2\u52a0\u51b3\u6597\u5927\u8d5b\uff0c\u5c31\u662f\u628a\u81ea\u5df1\u4f2a\u88c5\u6210\u4e00\u53ea\u5f17\u6d1b\u9b54\uff0c\u53c8\u53e6\u5916\u6293\u4e86\u4e94\u4e2a\u5f17\u6d1b\u9b54\u505a\u961f\u53cb\uff0c\u6240\u4ee5\u4e00\u76f4\u7792\u8fc7\u4e86\u6b23\u5e03\u7b49\u4eba\uff0c\u76f4\u5230\u516b\u5f3a\u664b\u7ea7\u4e4b\u524d\u624d\u88ab\u53d1\u89c9\u3002\u5f53\u65f6\u51db\u5306\u5306\u5fd9\u5fd9\u8dd1\u6765\u62a5\u4fe1\uff0c\u7ed3\u679c\u559d\u4e86\u5a9a\u836f\u88ab\u743c\u6069\u987a\u52bf\u63a8\u5012\u4e86\u3002\u81f3\u4e8e\u4e09\u9996\u7ffc\u9f99\uff0c\u743c\u6069\u5c31\u4e0d\u8ba4\u8bc6\u4e86\uff0c\u4e5f\u4ece\u672a\u542c\u8bf4\uff0c\u4e0d\u7531\u5f97\u6709\u4e9b\u597d\u5947\u3002</p><p>\u201c\u4e09\u9996\u7ffc\u9f99\uff0c\u8428\u9a6c\u65af\u7279\u7684\u90aa\u6076\u7814\u7a76\u6210\u679c\u4e4b\u4e00\u3002\u201d\u6b23\u5e03\u8bf4\u3002</p><p>\u8428\u9a6c\u65af\u7279\u4e0d\u4f46\u662f\u4e2a\u4ea1\u7075\u5e08\uff0c\u800c\u4e14\u5bf9\u201c\u9f99\u201d\u6709\u7740\u8d85\u4e4e\u5bfb\u5e38\u7684\u70ed\u7231\uff0c\u6240\u4ee5\u624d\u4f1a\u6298\u817e\u51fa\u9f99\u5deb\u5996\u8fd9\u79cd\u60ca\u4e16\u9a87\u4fd7\u7684\u4e1c\u897f\u3002\u4f46\u6b63\u56e0\u4e3a\u9f99\u5deb\u5996\u592a\u8fc7\u8457\u540d\uff0c\u5927\u5bb6\u90fd\u53ea\u8bb0\u5f97\u8fd9\u4e2a\uff0c\u5374\u4e0d\u77e5\u9053\u4ed6\u5176\u5b9e\u8fd8\u6709\u4e00\u5927\u5806\u53d1\u660e\u521b\u9020\uff0c\u8fd9\u4e09\u9996\u7ffc\u9f99\u4fbf\u662f\u5176\u4e2d\u4e4b\u4e00\uff0c\u4e09\u4e2a\u8111\u888b\u80fd\u591f\u540c\u65f6\u55b7\u51fa\u706b\u7130\u3001\u5bd2\u51b0\u548c\u6bd2\u6c14\uff0c\u98de\u884c\u901f\u5ea6\u66f4\u662f\u5feb\u82e5\u95ea\u7535\uff0c\u867d\u7136\u4e0d\u7b97\u7279\u522b\u5389\u5bb3\uff0c\u5374\u4e5f\u4e0d\u80fd\u5c0f\u770b\u3002</p><p>\u6b64\u65f6\u838e\u73de\u514b\u4ece\u5916\u9762\u8fd4\u56de\uff0c\u8bf4\u90a3\u5ea7\u9ad8\u5854\u5df2\u7ecf\u5d29\u6e83\u5012\u584c\uff0c\u5316\u4f5c\u5e9f\u589f\u4e00\u7247\uff0c\u65e0\u8bba\u662f\u8428\u9a6c\u65af\u7279\u8fd8\u662f\u6076\u9b54\u536b\u5175\u90fd\u4e0d\u89c1\u8e2a\u5f71\uff0c\u83ab\u77e5\u53bb\u5411\u3002\u5979\u5efa\u8bae\u53bb\u7ea2\u8272\u5bff\u8863\u90a3\u91cc\u63a2\u542c\u6d88\u606f\uff0c\u4f46\u88ab\u743c\u6069\u5236\u6b62\u4e86\uff0c\u201c\u4e0d\u7528\u4e86\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u73b0\u5728\u8fd8\u662f\u5148\u522b\u5206\u6563\uff0c\u514d\u5f97\u51fa\u4e8b\uff0c\u8fc7\u4e86\u51b3\u8d5b\u518d\u8bf4\u3002\u201d</p><p>\u963f\u5fd2\u59ae\u548c\u4f0a\u8389\u96c5\u4e5f\u5148\u540e\u8fd4\u56de\uff0c\u8bf8\u4eba\u5404\u81ea\u56de\u623f\u95f4\u4f11\u606f\u3002</p></article>"}}}]);
