(window.webpackJsonp=window.webpackJsonp||[]).push([[340],{362:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e94\u5341\u516d\u8282 \u8865\u507f",content:"<article><h2>\u7b2c\u4e94\u5341\u516d\u8282 \u8865\u507f</h2><p>\u6709\u5173\u62dc\u5c14\u7684\u8d44\u6599\uff0c\u838e\u73de\u514b\u5012\u662f\u4e86\u89e3\u4e0d\u5c11\uff0c\u539f\u56e0\u65e0\u4ed6\uff0c\u8fd9\u5bb6\u4f19\u5728\u6076\u9b54\u5f53\u4e2d\u7684\u540d\u58f0\u592a\u6076\u52a3\u4e86\u3002\u5982\u679c\u5728\u6df1\u6e0a\u505a\u8c03\u67e5\u95ee\u5377\uff0c\u95ee\u201c\u8c01\u662f\u4f60\u6700\u60f3\u75db\u6241\u4e00\u987f\u7684\u9b54\u9b3c\u201d\uff0c\u62dc\u5c14\u7edd\u5bf9\u9ad8\u5c45\u699c\u9996\uff0c\u8fdc\u8d85\u5176\u4ed6\u3002</p><p>\u201c\u6bcf\u4e2a\u9b54\u9b3c\u9886\u4e3b\u90fd\u6709\u81ea\u5df1\u7684\u79f0\u53f7\uff0c\u81f3\u4e8e\u62dc\u5c14\u7684\u79f0\u53f7\uff0c\u201d\u838e\u73de\u514b\u987f\u4e86\u987f\uff0c\u201c\u80cc\u53db\u8005\u201d\u3002</p><p>\u201c\u80cc\u53db\u8005\u201d\u8fd9\u4e2a\u79f0\u53f7\uff0c\u662f\u6076\u9b54\u548c\u9b54\u9b3c\u5171\u540c\u7ed9\u4e88\u7684\uff0c\u5b83\u4eec\u96be\u5f97\u5730\u5728\u8fd9\u4ef6\u4e8b\u60c5\u4e0a\u8fbe\u6210\u4e86\u4e00\u81f4\u610f\u89c1\uff0c\u800c\u62dc\u5c14\u5bf9\u6b64\u4e5f\u7b11\u7eb3\u4e0d\u8f9e\uff0c\u56e0\u4e3a\u4ed6\u5f53\u4e4b\u65e0\u6127\uff0c\u540d\u526f\u5176\u5b9e\uff0c\u4ed6\u786e\u786e\u5b9e\u5b9e\u5c31\u662f\u9760\u7740\u4e0d\u65ad\u7684\u80cc\u53db\u800c\u8d77\u5bb6\uff0c\u6700\u7ec8\u722c\u4e0a\u4eca\u5929\u7684\u4f4d\u7f6e\u7684\u3002</p><p>\u636e\u838e\u73de\u514b\u8bf4\uff0c\u62dc\u5c14\u662f\u4e00\u4f4d\u771f\u6b63\u4ece\u6700\u5e95\u5c42\u722c\u4e0a\u6765\u7684\u5e73\u6c11\u82f1\u96c4\uff0c\u4ed6\u539f\u672c\u53ea\u662f\u4e2a\u9ed8\u9ed8\u65e0\u95fb\u7684\u5c0f\u89d2\u8272\uff0c\u5728\u6210\u4e07\u4e0a\u4ebf\u7684\u9b54\u9b3c\u91cc\u6beb\u4e0d\u8d77\u773c\uff0c\u7ecf\u8fc7\u51e0\u4e07\u5e74\u7684\u8840\u706b\u53ae\u6740\u3001\u541e\u566c\u3001\u8fdb\u5316\uff0c\u6700\u7ec8\u6210\u4e3a\u6df1\u72f1\u70bc\u9b54\uff08\u9b54\u9b3c\u4e2d\u6700\u5f3a\u5927\u7684\u4e00\u79cd\uff0c\u5730\u4f4d\u7b49\u540c\u4e8e\u6076\u9b54\u4e2d\u7684\u5df4\u6d1b\u708e\u9b54\uff09\uff0c\u5728\u624e\u745e\u5c14\uff08\u963f\u5f17\u7eb3\u65af\u7684\u524d\u4efb\u7edf\u6cbb\u8005\uff09\u7684\u9ebe\u4e0b\u5f53\u4e00\u540d\u5c06\u519b\u3002</p><p>\u5728\u51e0\u767e\u5e74\u524d\u7684\u67d0\u6b21\u8840\u6218\u4e2d\uff0c\u62dc\u5c14\u7a81\u7136\u4e34\u9635\u5012\u6208\uff0c\u7387\u90e8\u6295\u964d\u6076\u9b54\uff0c\u5e76\u4e14\u7528\u4e00\u7cfb\u5217\u7684\u624b\u6bb5\u53d6\u5f97\u4fe1\u4efb\u2014\u2014\u4ed6\u6740\u6b7b\u4e86\u5927\u6279\u539f\u672c\u7684\u6218\u53cb\uff0c\u63d0\u4f9b\u4e86\u9b54\u9b3c\u4eec\u7684\u4f5c\u6218\u8ba1\u5212\uff0c\u5e76\u4e14\u5728\u540e\u6765\u83b7\u5f97\u4e86\u9a8c\u8bc1\u3002</p><p>\u7136\u540e\u62dc\u5c14\u5411\u6076\u9b54\u4eec\u63d0\u51fa\u4e00\u4e2a\u8ba1\u7b56\uff1a\u4ed6\u5047\u610f\u80cc\u53db\u6076\u9b54\uff0c\u518d\u5ea6\u56de\u5f52\u5730\u72f1\u9635\u8425\uff0c\u53d6\u5f97\u4fe1\u4efb\uff0c\u7136\u540e\u5728\u4e0b\u4e00\u573a\u6218\u5f79\u4e2d\u7ee7\u7eed\u4e34\u9635\u5012\u6208\u3002</p><p>\u6076\u9b54\u4eec\u540c\u610f\u4e86\uff0c\u4e8e\u662f\u62dc\u5c14\u5f53\u771f\u53c8\u56de\u5230\u4e86\u9b54\u9b3c\u5f53\u4e2d\uff0c\u4f9d\u65e7\u5728\u624e\u745e\u5c14\u9ebe\u4e0b\u62c5\u4efb\u5c06\u519b\uff0c\u4f9d\u65e7\u7edf\u9886\u519b\u961f\u2014\u2014\u7136\u540e\u4ed6\u679c\u7136\u53c8\u80cc\u53db\u4e86\uff0c\u518d\u6b21\u5012\u5411\u6076\u9b54\u3002</p><p>\u63a5\u8fde\u4e24\u6b21\u5b8c\u7f8e\u7684\u80cc\u53db\uff0c\u5f7b\u5e95\u6253\u6d88\u4e86\u6076\u9b54\u4eec\u7684\u7591\u5fc3\uff0c\u771f\u6b63\u628a\u62dc\u5c14\u89c6\u4e3a\u81ea\u5df1\u4eba\u2014\u2014\u7136\u540e\u62dc\u5c14\u628a\u6076\u9b54\u4eec\u5e26\u8fdb\u4e86\u9b54\u9b3c\u7684\u9677\u9631\uff0c\u4e00\u8d25\u6d82\u5730\uff0c\u4e0d\u4f46\u8f93\u6389\u4e86\u8fd9\u6b21\u6218\u5f79\uff0c\u51e0\u4e4e\u8fde\u4e07\u6e0a\u5e73\u539f\u90fd\u88ab\u5f7b\u5e95\u653b\u9677\uff0c\u8fc7\u4e86\u5f88\u4e45\u4ee5\u540e\u624d\u6162\u6162\u6536\u590d\u56de\u6765\u3002</p><p>\u51ed\u501f\u8fd9\u4e00\u7cfb\u5217\u7684\u7cbe\u5f69\u80cc\u53db\uff0c\u62dc\u5c14\u83b7\u5f97\u4e86\u4e5d\u72f1\u4e4b\u4e3b\u963f\u65af\u8482\u83ab\u65af\u7684\u5609\u5956\uff0c\u5c06\u4ed6\u63d0\u62d4\u4e3a\u963f\u5f17\u7eb3\u65af\u7684\u7edf\u6cbb\u8005\uff0c\u4f4d\u5217\u5730\u72f1\u4e5d\u5927\u9b54\u9b3c\u9886\u4e3b\u4e4b\u4e00\u3002\u81f3\u4e8e\u4ed6\u7684\u524d\u4efb\u624e\u745e\u5c14\uff0c\u5219\u5728\u6b64\u524d\u7684\u4e00\u6b21\u6218\u5f79\u4e2d\u843d\u5165\u6076\u9b54\u7684\u5305\u56f4\u5708\uff0c\u4e0d\u77e5\u6240\u7ec8\uff0c\u5b98\u65b9\u7684\u8bf4\u6cd5\u662f\u5149\u8363\u6218\u6b7b\u4e86\uff0c\u5176\u5b9e\u5927\u5bb6\u90fd\u77e5\u9053\u662f\u62dc\u5c14\u5728\u6363\u9b3c\uff0c\u501f\u5200\u6740\u4eba\u3002\u751a\u81f3\u8fd8\u6709\u4e00\u79cd\u8bf4\u6cd5\u662f\u624e\u745e\u5c14\u5176\u5b9e\u6ca1\u6b7b\uff0c\u5982\u4eca\u6b63\u88ab\u62dc\u5c14\u5173\u62bc\u5728\u67d0\u4e2a\u79d8\u5bc6\u76d1\u7262\u4e2d\uff0c\u4e4b\u6240\u4ee5\u6709\u8fd9\u79cd\u4f20\u8a00\uff0c\u662f\u56e0\u4e3a\u62dc\u5c14\u7d20\u6765\u4e5f\u6709\u597d\u8272\u7684\u540d\u58f0\uff0c\u5c24\u5176\u559c\u6b22\u5411\u5973\u4e0a\u53f8\u4e0b\u624b\u2014\u2014\u5728\u4ed6\u8fc7\u53bb\u51e0\u4e07\u5e74\u7684\u594b\u6597\u5386\u53f2\u4e2d\uff0c\u7c7b\u4f3c\u7684\u4e8b\u60c5\u4ed6\u5df2\u7ecf\u5e72\u8fc7\u5f88\u591a\u6b21\u4e86\uff0c\u800c\u624e\u745e\u5c14\u6b63\u662f\u9b54\u9b3c\u4e2d\u8457\u540d\u7684\u6807\u81f4\u7f8e\u4eba\u513f\uff0c\u53c8\u66fe\u7ecf\u662f\u4ed6\u7684\u9876\u5934\u4e0a\u53f8\u3002</p><p>\u201c\u6df1\u6e0a\u91cc\u751a\u81f3\u6709\u4e00\u53e5\u73a9\u7b11\uff0c\u8bf4\u62dc\u5c14\u5982\u4eca\u7684\u4e0a\u53f8\u963f\u65af\u8482\u83ab\u65af\u5e76\u975e\u5973\u6027\uff0c\u4ee5\u81f3\u4e8e\u62dc\u5c14\u7f3a\u4e4f\u7ee7\u7eed\u524d\u8fdb\u7684\u52a8\u529b\uff0c\u5426\u5219\u7684\u8bdd\uff0c\u4ed6\u65e9\u5c31\u5df2\u7ecf\u6210\u4e3a\u4e5d\u72f1\u4e4b\u4e3b\u4e86\u3002\u201d</p><p>\u201c\u5514\uff0c\u8fd9\u53e5\u8bdd\u2026\u2026\u5176\u5b9e\u4e5f\u672a\u5c1d\u6ca1\u6709\u51e0\u5206\u9053\u7406\u554a\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u54e6\uff0c\u6211\u662f\u5728\u60f3\uff0c\u5018\u82e5\u6709\u4e00\u4e2a\u6f02\u4eae\u7f8e\u8273\u7684\u5973\u4e0a\u53f8\uff0c\u6210\u5929\u5728\u6211\u9762\u524d\u547c\u6765\u559d\u53bb\uff0c\u9890\u6c14\u6307\u4f7f\uff0c\u90a3\u4e48\u6211\u5e94\u8be5\u4e5f\u662f\u5f88\u6709\u53bb\u63a8\u5012\u5f81\u670d\u5979\u7684\u6b32\u671b\u548c\u52a8\u529b\u5427\u3002\u201d</p><p>\u201c\u90a3\u4f60\u73b0\u5728\u7684\u4e0a\u53f8\u662f\u7537\u6027\uff1f\u8fd8\u662f\u8001\u592a\u5a46\uff1f\u201d</p><p>\u743c\u6069\u53f9\u6c14\uff0c\u201c\u7537\u6027\u3002\u201d</p><p>\u4ed6\u6700\u65e9\u7684\u4e0a\u53f8\u662f\u5e03\u96f7\u7eb3\u65af\uff0c\u540e\u6765\u53bb\u70bc\u91d1\u5b66\u9662\uff0c\u4e0a\u53f8\u662f\u739b\u63d0\u514b\u548c\u74e6\u63d0\u514b\uff0c\u518d\u540e\u6765\u5230\u4e86\u7b2c\u4e8c\u8fdc\u5f81\u5e08\uff0c\u4e0a\u53f8\u662f\u96c5\u8fbe\u2014\u2014\u5168\u90fd\u662f\u9634\u9b42\u738b\u5b50\uff0c\u6ca1\u6709\u516c\u4e3b\u3002\u8bf4\u8d77\u6765\u4e5f\u5947\u602a\uff0c\u4e3a\u4ec0\u4e48\u9634\u9b42\u57ce\u4e3b\u6709\u5341\u4e8c\u4e2a\u513f\u5b50\uff0c\u5374\u8fde\u4e00\u4e2a\u5973\u513f\u90fd\u6ca1\u6709\u5462\uff1b\u4e0d\u4f46\u662f\u4ed6\uff0c\u5c31\u8fde\u4ed6\u7684\u5341\u4e8c\u4f4d\u738b\u5b50\uff0c\u4e5f\u540c\u6837\u6ca1\u542c\u8bf4\u6709\u5973\u513f\u2026\u2026\u4e0d\u5bf9\uff0c\u4e0d\u4ec5\u4ec5\u662f\u6ca1\u6709\u5973\u513f\uff0c\u662f\u6839\u672c\u5c31\u6ca1\u6709\u5b50\u55e3\u3002</p><p>\u771f\u9057\u61be\uff0c\u5982\u679c\u548c\u81ea\u5df1\u6253\u4ea4\u9053\u7684\u4e0d\u662f\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\uff0c\u800c\u662f\u5e03\u96f7\u7eb3\u65af\u516c\u4e3b\uff0c\u90a3\u662f\u591a\u4e48\u4ee4\u4eba\u6109\u5feb\u7684\u4e8b\u60c5\u554a\uff0c\u6216\u8005\u4ed6\u751f\u4e2a\u6f02\u4eae\u5973\u513f\u6765\u8ba9\u6211\u63a8\u5012\u4e5f\u4e0d\u9519\u554a\uff0c\u8fd9\u96be\u9053\u4e0d\u662f\u5947\u5e7b\u5c0f\u8bf4\u4e3b\u89d2\u7684\u5e94\u6709\u5f85\u9047\u4e48\u3002\u8fd8\u6709\u90a3\u5bf9\u53cc\u80de\u80ce\u738b\u5b50\uff0c\u739b\u63d0\u514b\u548c\u74e6\u63d0\u514b\uff0c\u70bc\u91d1\u5b66\u9662\u7684\u9662\u957f\uff0c\u4e3a\u4ec0\u4e48\u5c31\u504f\u504f\u4e0d\u662f\u53cc\u80de\u80ce\u516c\u4e3b\u5462\uff0c\u81ea\u5df1\u4e00\u5411\u5bf9\u4e8e\u53cc\u80de\u80ce\u59d0\u59b9\u5f88\u6709\u5174\u8da3\u7684\u5440\u3002</p><p>\u60f3\u8d77\u53cc\u80de\u80ce\u59d0\u59b9\uff0c\u743c\u6069\u4e0d\u7531\u5f97\u53c8\u60f3\u8d77\u8fd8\u7559\u5728\u4f0a\u5361\u6c99\u57ce\u91cc\u7684\u83ab\u5c3c\u5361\u59d0\u59b9\u4e86\uff0c\u8fd9\u6b21\u610f\u5916\u6389\u5230\u6df1\u6e0a\u6765\uff0c\u4e5f\u6709\u597d\u51e0\u65e5\u4e86\uff0c\u8299\u857e\u72c4\u5e94\u8be5\u5df2\u7ecf\u5f97\u77e5\u6d88\u606f\uff0c\u8fd8\u4e0d\u77e5\u9053\u62c5\u5fc3\u6210\u4ec0\u4e48\u6837\u5b50\uff0c\u56de\u53bb\u6709\u5fc5\u8981\u597d\u597d\u5b89\u6170\u2026\u2026\u987a\u4fbf\u628a\u5979\u59d0\u59d0\u8299\u8389\u5a05\u4e5f\u4e00\u8d77\u63a8\u5012\u4e86\u5427\uff0c\u5c31\u5f53\u662f\u5bf9\u591c\u5973\u58eb\u6697\u4e2d\u4e0b\u624b\u7684\u62a5\u590d\uff0c\u8fd9\u4e2a\u7406\u7531\u4f3c\u4e4e\u8db3\u591f\u51a0\u5195\u5802\u7687\u4e86\u3002</p><p>\u201c\u4e3b\u4eba\uff1f\u201d\u9b45\u9b54\u5947\u602a\u5730\u8f7b\u547c\uff0c\u201c\u4f60\u5728\u60f3\u4ec0\u4e48\u5462\uff0c\u600e\u4e48\u597d\u50cf\u7a81\u7136\u8d70\u795e\u4e86\u3002\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\uff0c\u6211\u5728\u953b\u70bc\u4e00\u4e0b\u53d1\u6563\u601d\u7ef4\u3002\u201d</p><p>\u201c\u53d1\u6563\u601d\u7ef4\uff1f\u201d</p><p>\u201c\u4e5f\u5c31\u662f\u8054\u60f3\u4e86\uff0c\u4f60\u77e5\u9053\u7684\uff0c\u5982\u679c\u6ca1\u6709\u8054\u60f3\uff0c\u4e16\u754c\u5c06\u4f1a\u600e\u6837\u2026\u2026\u6211\u4e5f\u4e0d\u77e5\u9053\u4f1a\u600e\u6837\uff0c\u603b\u4e4b\u5f88\u7cdf\u7cd5\u5c31\u662f\u4e86\u2014\u2014\u7ee7\u7eed\u8bb2\u62dc\u5c14\u7684\u4e8b\u60c5\u5427\u3002\u201d</p><p>\u201c\u5c31\u8fd9\u4e9b\u4e86\uff0c\u201d\u9b45\u9b54\u53cc\u624b\u644a\u5f00\uff0c\u201c\u62dc\u5c14\u5c31\u8fd9\u6837\u5f53\u4e0a\u4e86\u963f\u5f17\u7eb3\u65af\u7684\u9886\u4e3b\uff0c\u7136\u540e\u4e00\u76f4\u7a33\u5ea7\u81f3\u4eca\u3002\u4ed6\u624b\u4e0b\u4e0d\u4e4f\u8db3\u591f\u5f3a\u5927\u8db3\u591f\u6709\u529b\u91cf\u7684\u9b54\u9b3c\uff0c\u4f46\u6ca1\u6709\u4e00\u4e2a\u80fd\u6bd4\u4ed6\u66f4\u72e1\u733e\u66f4\u806a\u660e\uff0c\u6240\u4ee5\u8fc4\u4eca\u4e3a\u6b62\uff0c\u65e0\u4eba\u80fd\u591f\u6311\u6218\u4ed6\u7684\u5730\u4f4d\u3002\u800c\u81ea\u4ece\u4ed6\u4e0a\u4efb\u4e4b\u540e\uff0c\u8840\u6218\u7684\u5c40\u52bf\u5c31\u5bf9\u6211\u4eec\u6076\u9b54\u8d8a\u6765\u8d8a\u4e0d\u5229\u4e86\uff0c\u4f60\u77e5\u9053\uff0c\u5728\u8fd9\u79cd\u8d85\u5927\u89c4\u6a21\u7684\u6218\u5f79\u4e2d\uff0c\u4e2a\u4eba\u7684\u529b\u91cf\u5f3a\u5f31\u5176\u5b9e\u538b\u6839\u4e0d\u7b97\u4ec0\u4e48\uff0c\u771f\u6b63\u91cd\u8981\u7684\u662f\u7edf\u5e05\u548c\u7b56\u5212\u80fd\u529b\uff0c\u4f46\u6076\u9b54\u5f53\u4e2d\uff0c\u627e\u4e0d\u51fa\u80fd\u591f\u548c\u62dc\u5c14\u76f8\u5339\u654c\u7684\u4eba\u7269\u3002\u201d</p><p>\u201c\u683c\u62c9\u5179\u7279\u5462\uff1f\u201d\u743c\u6069\u95ee\uff0c\u201c\u4ed6\u603b\u53ef\u4ee5\u5427\u3002\u201d</p><p>\u201c\u4ed6\u5e94\u8be5\u53ef\u4ee5\uff0c\u4f46\u81ea\u4ece\u62dc\u5c14\u4e0a\u4efb\u4e4b\u540e\uff0c\u683c\u62c9\u5179\u7279\u5c31\u518d\u4e5f\u6ca1\u6709\u4e3b\u52a8\u53d1\u8d77\u8fc7\u4e00\u6b21\u6218\u4e89\u4e86\uff0c\u4e5f\u4ece\u6765\u6ca1\u6709\u548c\u62dc\u5c14\u4ea4\u624b\u8fc7\uff0c\u6240\u4ee5\u65e0\u4ece\u9a8c\u8bc1\u3002\u201d</p><p>\u201c\u5514\u3002\u201d</p><p>\u6709\u5173\u62dc\u5c14\u7684\u8d44\u6599\uff0c\u57fa\u672c\u5c31\u662f\u8fd9\u4e9b\uff0c\u867d\u7136\u838e\u73de\u514b\u8bf4\u5f97\u4e0d\u5c11\uff0c\u4f46\u771f\u6b63\u5bf9\u773c\u524d\u6709\u7528\u7684\u4f3c\u4e4e\u4e5f\u4e0d\u591a\u3002\u603b\u7ed3\u8d77\u6765\uff0c\u8fd9\u5c31\u662f\u4e00\u4e2a\u672c\u8eab\u5b9e\u529b\u4e0d\u7b97\u7279\u522b\u5f3a\u5927\uff0c\u4f46\u6781\u5176\u72e1\u8bc8\u591a\u667a\u7684\u9b54\u9b3c\u9886\u4e3b\uff0c\u800c\u4e14\u540e\u53f0\u4f3c\u4e4e\u9887\u786c\uff0c\u662f\u963f\u65af\u8482\u83ab\u65af\u8d4f\u8bc6\u63d0\u62d4\u7684\u2014\u2014\u800c\u4e14\u8fd8\u6709\u559c\u6b22\u63a8\u5012\u5973\u4e0a\u53f8\u7684\u5947\u7279\u7231\u597d\u3002</p><p>\u201c\u81ea\u5df1\u4e0d\u7b97\u5b9e\u529b\u8d85\u5f3a\uff0c\u5c45\u7136\u8fd8\u6562\u5355\u67aa\u5339\u9a6c\u8dd1\u5230\u65ad\u57df\u9547\u6765\uff0c\u5f53\u771f\u662f\u6709\u6043\u65e0\u6050\u2026\u2026\u8fd9\u662f\u4e3a\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u8fd9\u4e2a\u95ee\u9898\u6ca1\u4eba\u80fd\u591f\u89e3\u7b54\uff0c\u743c\u6069\u5728\u8111\u4e2d\u8f6c\u7740\u5ff5\u5934\uff0c\u5df2\u7ecf\u6d6e\u73b0\u597d\u51e0\u79cd\u53ef\u80fd\u7684\u731c\u6d4b\uff0c\u4f46\u90fd\u65e0\u6cd5\u786e\u5b9a\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u6d88\u606f\u4f20\u6765\uff0c\u8bf4\u662f\u8bf7\u4ed6\u4eec\u6d3e\u4ee3\u8868\u53bb\u7ea2\u8272\u5bff\u8863\u7684\u5bab\u90b8\uff0c\u5546\u8c08\u6b64\u6b21\u6bd4\u8d5b\u5956\u54c1\u7684\u95ee\u9898\u3002</p><p>\u203b\u203b\u203b</p><p>\u56e0\u4e3a\u4e8b\u5148\u65e9\u5c31\u5546\u5b9a\uff0c\u5018\u82e5\u5f97\u5230\u7b2c\u4e00\u540d\uff0c\u5370\u7ae0\u5f52\u743c\u6069\u6240\u6709\uff0c\u5982\u4eca\u81ea\u7136\u4e5f\u5c31\u7531\u4ed6\u53bb\u548c\u7ea2\u8272\u5bff\u8863\u53bb\u6253\u4ea4\u9053\u3002\u743c\u6069\u4e3a\u4ea4\u6d89\u65b9\u4fbf\uff0c\u628a\u838e\u73de\u514b\u4e5f\u5e26\u4e0a\u4e86\u3002</p><p>\u5230\u4e86\u5bab\u90b8\uff0c\u7ea2\u8272\u5bff\u8863\u8d77\u8eab\u8fce\u63a5\uff0c\u4f9d\u65e7\u662f\u5148\u5ba2\u5957\u95f2\u8c08\u3002\u82e5\u5728\u5e73\u65f6\uff0c\u743c\u6069\u5012\u4e5f\u5949\u966a\uff0c\u73b0\u5728\u5fc3\u4e2d\u6ee1\u8179\u7591\u56e2\uff0c\u4e0d\u5f97\u5176\u89e3\uff0c\u4fbf\u4e0d\u613f\u591a\u7ed5\u5f2f\u5b50\u6d6a\u8d39\u65f6\u95f4\uff0c\u7d22\u6027\u76f4\u622a\u4e86\u5f53\u5c31\u95ee\u5bf9\u65b9\u8bf7\u81ea\u5df1\u8fc7\u6765\uff0c\u6709\u4f55\u8d50\u6559\u3002\u7ea2\u8272\u5bff\u8863\u5fae\u5fae\u542b\u7b11\uff0c\u4ece\u65c1\u8fb9\u53d6\u8fc7\u51e0\u5f20\u7eb8\u6765\uff0c\u9012\u7ed9\u743c\u6069\u3002</p><p>\u201c\u8fd9\u662f\u6211\u521a\u521a\u6536\u5230\u7684\u3002\u201d</p><p>\u743c\u6069\u7ec6\u770b\uff0c\u89c1\u662f\u4e00\u4e9b\u663e\u7136\u6765\u6e90\u4e0d\u540c\u7684\u60c5\u62a5\uff0c\u5176\u4e2d\u4e00\u4efd\u8bf4\u62dc\u5c14\u593a\u5f97\u5370\u7ae0\uff0c\u5df2\u7ecf\u8fd4\u56de\u9752\u94dc\u57ce\u5821\uff1b\u53e6\u4e00\u4efd\u5219\u8bf4\u6709\u4eba\u5728\u9752\u94dc\u57ce\u5821\u770b\u89c1\u4e86\u8428\u9a6c\u65af\u7279\uff0c\u88ab\u5949\u4e3a\u4e0a\u5bbe\uff0c\u52a0\u4e0a\u5176\u4ed6\u51e0\u4efd\u60c5\u62a5\u76f8\u4e92\u4f50\u8bc1\uff0c\u201c\u62dc\u5c14\u548c\u8428\u9a6c\u65af\u7279\u52fe\u7ed3\uff0c\u6240\u4ee5\u6765\u62a2\u5370\u7ae0\u201d\u8fd9\u4e2a\u7ed3\u8bba\u57fa\u672c\u5df2\u7ecf\u662f\u786e\u5b9a\u65e0\u7591\u4e86\u3002</p><p>\u201c\u8fd9\u4e9b\u60c5\u62a5\u53ef\u9760\u4e48\uff1f\u201d\u743c\u6069\u968f\u53e3\u95ee\u3002</p><p>\u201c\u7edd\u5bf9\u771f\u5b9e\u53ef\u9760\uff0c\u201d\u7ea2\u8272\u5bff\u8863\u8bf4\uff0c\u201c\u5bf9\u4e8e\u8fd9\u70b9\u6211\u53ef\u4ee5\u62c5\u4fdd\u3002\u201d</p><p>\u743c\u6069\u7565\u7565\u6c89\u541f\uff0c\u7ea2\u8272\u5bff\u8863\u7684\u4fe1\u8a89\u4f3c\u4e4e\u8fd8\u4e0d\u5dee\uff0c\u81f3\u5c11\u4e0d\u4f1a\u516c\u7136\u6492\u8c0e\uff0c\u65e2\u7136\u8bf4\u5f97\u8fd9\u4e48\u65a9\u9489\u622a\u94c1\uff0c\u90a3\u5e94\u8be5\u662f\u6709\u5341\u8db3\u628a\u63e1\u4e86\u3002\u62dc\u5c14\u548c\u8428\u9a6c\u65af\u7279\u52fe\u7ed3\uff0c\u8fd9\u4e5f\u5c31\u80fd\u89e3\u91ca\u4ed6\u4e3a\u4ec0\u4e48\u4f1a\u7a81\u7136\u51fa\u73b0\u62a2\u593a\u5370\u7ae0\uff0c\u81f3\u4e8e\u52fe\u7ed3\u7684\u539f\u56e0\uff0c\u90a3\u5012\u66f4\u5bb9\u6613\u7406\u89e3\u3002\u95ee\u9898\u662f\uff0c\u7ea2\u8272\u5bff\u8863\u53c8\u662f\u5728\u6253\u4ec0\u4e48\u4e3b\u610f\u5462\uff1f\u5355\u7eaf\u53ea\u662f\u8981\u7ed9\u62dc\u5c14\u5e2e\u5fd9\uff1f</p><p>\u201c\u838e\u73de\u514b\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u4f20\u8a00\u7ea2\u8272\u5bff\u8863\u548c\u62dc\u5c14\u6697\u4e2d\u6709\u5bc6\u5207\u5173\u7cfb\uff0c\u6211\u5f53\u65f6\u8fd8\u4e0d\u592a\u76f8\u4fe1\uff0c\u6bd5\u7adf\u4e00\u4e2a\u662f\u6076\u9b54\u4e00\u4e2a\u662f\u9b54\u9b3c\u2026\u2026\u83ab\u975e\u771f\u6709\u5978\u60c5\uff1f\u201d</p><p>\u5fc3\u4e2d\u5982\u6b64\u60f3\u7740\uff0c\u773c\u795e\u4fbf\u4e0d\u7531\u81ea\u4e3b\u5730\u7565\u6709\u53d8\u5316\uff0c\u7ea2\u8272\u5bff\u8863\u9605\u4eba\u65e0\u6570\uff0c\u7ecf\u9a8c\u4e30\u5bcc\u81f3\u6781\uff0c\u5982\u4f55\u770b\u4e0d\u51fa\u6765\uff0c\u53ea\u662f\u4f9d\u65e7\u7b11\u541f\u541f\u5730\u5e76\u4e0d\u70b9\u7834\uff0c\u4e5f\u4e0d\u89e3\u91ca\u3002\u743c\u6069\u867d\u7136\u5fc3\u4e2d\u7591\u8651\uff0c\u603b\u4e5f\u4e0d\u80fd\u76f4\u63a5\u8d28\u95ee\u5979\u5230\u5e95\u662f\u4ec0\u4e48\u610f\u601d\uff0c\u6709\u4e9b\u4e8b\u60c5\u53ea\u80fd\u662f\u5927\u5bb6\u5fc3\u7167\u4e0d\u5ba3\uff0c\u5374\u6ca1\u529e\u6cd5\u6446\u5728\u660e\u9762\u4e0a\u6765\u8bf4\u3002\u6b63\u81ea\u8e0c\u8e87\uff0c\u838e\u73de\u514b\u5728\u65c1\u8fb9\u5f00\u53e3\uff0c\u8bf4\u65e2\u7136\u4e8b\u5df2\u81f3\u6b64\uff0c\u539f\u5b9a\u7684\u5956\u54c1\u5df2\u7ecf\u843d\u5165\u62dc\u5c14\u4e4b\u624b\uff0c\u7ea2\u8272\u5bff\u8863\u51b3\u5b9a\u5982\u4f55\u5151\u73b0\u8d5b\u524d\u7684\u627f\u8bfa\u5462\uff1f\u662f\u51c6\u5907\u518d\u6d3e\u4eba\u4ece\u62dc\u5c14\u624b\u4e2d\u593a\u56de\uff0c\u8fd8\u662f\u53e6\u6709\u8865\u507f\uff1f</p><p>\u51fa\u73b0\u8fd9\u79cd\u610f\u5916\uff0c\u7ea2\u8272\u5bff\u8863\u4f5c\u4e3a\u65ad\u57df\u9547\u7684\u4e3b\u4eba\uff0c\u81ea\u7136\u8d1f\u6709\u4e0d\u53ef\u63a8\u5378\u7684\u8d23\u4efb\uff0c\u5979\u4e5f\u5f88\u723d\u5feb\uff0c\u76f4\u63a5\u53c8\u9012\u8fc7\u4e00\u5f20\u7eb8\u6765\uff0c\u662f\u4e00\u4efd\u540d\u5355\uff0c\u743c\u6069\u4e00\u773c\u626b\u53bb\uff0c\u53ea\u89c1\u4e0a\u9762\u5bc6\u5bc6\u9ebb\u9ebb\u8bb0\u5f55\u4e86\u51e0\u5341\u79cd\u5b9d\u7269\u7684\u540d\u79f0\u548c\u8d44\u6599\u3002</p><p>\u201c\u90a3\u679a\u5370\u7ae0\u4e0d\u8fc7\u662f\u4e2a\u73a9\u7269\uff0c\u5e76\u65e0\u5b9e\u9645\u7528\u9014\uff0c\u4e22\u5f03\u4e86\u4e5f\u4e0d\u53ef\u60dc\u3002\u8fd9\u4efd\u540d\u5355\u4e0a\u6240\u5217\u7684\uff0c\u90fd\u662f\u6211\u5e73\u65f6\u6536\u96c6\u7684\u4e00\u4e9b\u9b54\u6cd5\u7269\u54c1\uff0c\u867d\u7136\u8bf4\u4e0d\u4e0a\u591a\u4e48\u73cd\u8d35\uff0c\u4f46\u4e5f\u4e0d\u662f\u5bfb\u5e38\u5f97\u89c1\u4e4b\u7269\uff0c\u201d\u7ea2\u8272\u5bff\u8863\u67d4\u58f0\u8bf4\uff0c\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\u53ef\u4ee5\u4efb\u9009\u5176\u4e00\uff0c\u4ee3\u66ff\u90a3\u679a\u5370\u7ae0\u4f5c\u4e3a\u5956\u54c1\u3002\u5f53\u7136\uff0c\u6240\u9644\u5e26\u7684\u6240\u6709\u6743\u76ca\u4e5f\u4e00\u6982\u4fdd\u7559\uff0c\u201d\u5979\u7a0d\u987f\u4e86\u987f\uff0c\u671d\u743c\u6069\u770b\u4e86\u4e00\u773c\uff0c\u201c\u5168\u90e8\u4fdd\u7559\uff0c\u4e00\u6837\u4e0d\u7f3a\u3002\u201d</p><p>\u6240\u6709\u6743\u76ca\uff0c\u90a3\u5c31\u662f\u5305\u62ec\u8d38\u6613\u4f18\u60e0\u6743\u3001\u534a\u4ef7\u8d2d\u7269\u6743\u3001\u9547\u5185\u4e89\u6597\u6743\uff0c\u8fd8\u6709\u548c\u7ea2\u8272\u5bff\u8863\u4e00\u591c\u6625\u5bb5\u7684\u673a\u4f1a\u4e86\u3002\u867d\u8bf4\u743c\u6069\u5bf9\u8fd9\u79cd\u4e00\u591c\u60c5\u4e0d\u662f\u5f88\u559c\u6b22\uff0c\u4f46\u6f02\u4eae\u5230\u8fd9\u79cd\u7a0b\u5ea6\u7684\u7f8e\u4eba\uff0c\u4e00\u8f88\u5b50\u4e5f\u672a\u5fc5\u80fd\u518d\u9047\u5230\uff0c\u9519\u8fc7\u4e86\u5b9e\u5728\u592a\u53ef\u60dc\uff0c\u5c31\u5f53\u662f\u6362\u6362\u53e3\u5473\u4e5f\u4e0d\u9519\u3002</p><p>\u8fd9\u4e2a\u6761\u4ef6\u53ef\u7b97\u4f18\u539a\uff0c\u5982\u679c\u6362\u4e86\u5176\u4ed6\u4eba\uff0c\u80af\u5b9a\u6beb\u4e0d\u72b9\u8c6b\u5c31\u7b54\u5e94\u4e86\uff0c\u53cd\u6b63\u4ed6\u4eec\u4e5f\u4e0d\u5728\u4e4e\u90a3\u679a\u5370\u7ae0\u672c\u8eab\uff0c\u53ea\u662f\u4e2a\u8c61\u5f81\u7f62\u4e86\uff0c\u6362\u4e00\u4e2a\u6839\u672c\u65e0\u6240\u8c13\uff0c\u4f55\u51b5\u8fd8\u80fd\u6362\u5f97\u4e00\u4ef6\u73cd\u8d35\u7684\u9b54\u6cd5\u7269\u54c1\u3002\u7136\u800c\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u53c8\u6709\u4e9b\u4e0d\u592a\u4e00\u6837\u3002</p><p>\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\u5bf9\u8fd9\u79cd\u65b9\u6848\u4f3c\u4e4e\u4e0d\u751a\u6ee1\u610f\uff1f\u201d\u7ea2\u8272\u5bff\u8863\u8f7b\u8eab\u8be2\u95ee\u3002</p><p>\u201c\u5514\uff0c\u8fd9\u4e2a\u4e48\uff0c\u5b9e\u4e0d\u76f8\u7792\uff0c\u6211\u4e4b\u6240\u4ee5\u53c2\u52a0\u51b3\u6597\u5927\u8d5b\uff0c\u4e3a\u7684\u5176\u5b9e\u5c31\u662f\u90a3\u679a\u5370\u7ae0\uff0c\u5012\u4e0d\u662f\u5176\u4ed6\u3002\u6211\u4e00\u5411\u55dc\u597d\u6536\u96c6\u8fd9\u4e9b\u53e4\u7269\uff0c\u4e0a\u6b21\u5076\u7136\u5f97\u89c1\uff0c\u68a6\u5bd0\u96be\u5fd8\u2026\u2026\u201d</p><p>\u5176\u5b9e\u743c\u6069\u4e5f\u77e5\u9053\uff0c\u81ea\u5df1\u8fd9\u79cd\u8bdd\u7eaf\u5c5e\u80e1\u626f\uff0c\u4e0d\u4f46\u9a97\u4e0d\u8fc7\u81ea\u5df1\uff0c\u4e5f\u9a97\u4e0d\u8fc7\u7ea2\u8272\u5bff\u8863\u3002\u5bf9\u65b9\u96c4\u8e1e\u4e00\u65b9\uff0c\u6267\u638c\u65ad\u57df\u9547\uff0c\u9664\u975e\u771f\u662f\u80f8\u5927\u65e0\u8111\u7684\u767d\u75f4\uff0c\u5426\u5219\u51b3\u4e0d\u4f1a\u76f8\u4fe1\u8fd9\u79cd\u9b3c\u8bdd\u3002\u4f46\u8fd8\u662f\u90a3\u53e5\u8bdd\uff0c\u4e16\u754c\u4e0a\u5f88\u591a\u4e8b\u60c5\uff0c\u79c1\u4e0b\u90fd\u6e05\u695a\uff0c\u5c31\u662f\u4e0d\u80fd\u6446\u4e0a\u660e\u9762\u6765\u8bf4\uff0c\u53cd\u6b63\u5f7c\u6b64\u5fc3\u7167\u4e0d\u5ba3\uff0c\u4e0d\u4f1a\u62c6\u7a7f\u5c31\u662f\u3002\u743c\u6069\u8bf4\u6765\u8bf4\u53bb\uff0c\u5176\u5b9e\u610f\u601d\u5c31\u662f\u8bf4\u201c\u6211\u975e\u5e38\u770b\u91cd\u90a3\u679a\u5370\u7ae0\u201d\uff0c\u7136\u800c\u73b0\u5728\u5b83\u88ab\u62a2\u8d70\u4e86\uff0c\u7ea2\u8272\u5bff\u8863\u62ff\u5176\u4ed6\u7684\u4e1c\u897f\u6765\u4ee3\u66ff\uff0c\u4ed6\u5c31\u4e0d\u80fd\u7b54\u5e94\u4e86\u3002</p><p>\u7ea2\u8272\u5bff\u8863\u5ae3\u7136\u5fae\u7b11\uff0c\u201c\u5f53\u7136\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u8fd9\u5b8c\u5168\u662f\u6211\u7684\u8d23\u4efb\uff0c\u6240\u4ee5\u6211\u989d\u5916\u53c8\u51c6\u5907\u4e86\u4e24\u5957\u8865\u507f\u65b9\u6848\uff0c\u53ef\u4f9b\u9009\u62e9\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\u610f\u4e0b\u5982\u4f55\uff1f\u201d</p><p>\u743c\u6069\u76b1\u7709\uff0c\u7ea2\u8272\u5bff\u8863\u5df2\u7ecf\u51c6\u5907\u597d\u4e86\u4e24\u5957\u65b9\u6848\u8ba9\u4ed6\u4ece\u4e2d\u9009\u62e9\uff1f\u90a3\u5982\u4f55\u4e24\u5957\u65b9\u6848\u90fd\u4e0d\u6ee1\u610f\u600e\u4e48\u529e\u3002\u201c\u96be\u9053\u4e0d\u662f\u7531\u6211\u81ea\u5df1\u63d0\u51fa\u8865\u507f\u610f\u89c1\u5417\uff1f\u201d\u4ed6\u8bd5\u63a2\u5730\u95ee\u3002</p><p>\u201c\u6839\u636e\u89c4\u5219\uff0c\u8865\u507f\u65b9\u6848\u662f\u7531\u6211\u81ea\u4e3b\u51b3\u5b9a\u7684\uff0c\u201d\u7ea2\u8272\u5bff\u8863\u7b11\u5bb9\u4e0d\u51cf\uff0c\u201c\u5f53\u7136\u6211\u4e00\u5b9a\u4f1a\u5145\u5206\u8003\u8651\u5230\u60a8\u7684\u5229\u76ca\uff0c\u4fdd\u8bc1\u516c\u5e73\u5408\u7406\u3002\u201d</p><p>\u201c\u8fd9\u89c4\u5219\u672a\u514d\u592a\u4e0d\u5408\u7406\u4e86\u5427\u3002\u201d</p><p>\u201c\u54e6\uff0c\u6709\u53ef\u80fd\uff0c\u56e0\u4e3a\u5b83\u662f\u6211\u521a\u521a\u5236\u5b9a\u7684\uff0c\u4ed3\u4fc3\u4e4b\u95f4\uff0c\u53ef\u80fd\u8003\u8651\u4e0d\u5468\u3002\u4ee5\u540e\u4e0d\u59a8\u6162\u6162\u518d\u4fee\u6539\u5b8c\u5584\uff0c\u4e0d\u8fc7\u6682\u65f6\u662f\u4e0d\u65b9\u4fbf\u518d\u6539\u4e86\u3002\u201d</p><p>\u201c\u521a\u5236\u5b9a\u7684\uff1f\u201d</p><p>\u201c\u662f\u554a\uff0c\u56e0\u4e3a\u4ee5\u524d\u4ece\u6ca1\u60f3\u8fc7\u4f1a\u51fa\u73b0\u8fd9\u79cd\u610f\u5916\uff0c\u6240\u4ee5\u4e5f\u6ca1\u5236\u5b9a\u76f8\u5e94\u7684\u89c4\u5219\u3002\u6211\u53ea\u597d\u4e34\u65f6\u5b9a\u4e86\u4e00\u6761\u3002\u201d</p><p>\u201c\u53ef\u662f\u2026\u2026\u8fd9\u5206\u660e\u4e0d\u5408\u4e4e\u6cd5\u7406\u5427\uff1f\u4e8b\u60c5\u5148\u53d1\u751f\uff0c\u7136\u540e\u518d\u5236\u5b9a\u89c4\u5219\uff0c\u8fd9\u8fdd\u53cd\u6cd5\u4e0d\u6eaf\u53ca\u65e2\u5f80\u7684\u9053\u7406\u554a\u3002\u201d</p><p>\u201c\u60a8\u8bf4\u7684\u90a3\u662f\u4eba\u7c7b\u7684\u9053\u7406\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u5bf9\u4e8e\u6211\u4eec\u6076\u9b54\u6765\u8bf4\u662f\u4e0d\u9002\u7528\u7684\u3002\u201d</p><p>\u743c\u6069\u4e00\u65f6\u65e0\u8bed\uff0c\u53ea\u597d\u95ee\u5bf9\u65b9\u5230\u5e95\u51c6\u5907\u4e86\u54ea\u4e24\u5957\u65b9\u6848\uff0c\u5148\u62ff\u6765\u770b\u770b\u518d\u8bf4\u3002</p><p>\u7b2c\u4e00\u5957\u65b9\u6848\uff0c\u662f\u7ea2\u8272\u5bff\u8863\u8bb8\u8bfa\u9001\u743c\u6069\u548c\u4ed6\u7684\u540c\u4f34\u8fd4\u56de\u7269\u8d28\u754c\u3002</p><p>\u201c\u5982\u679c\u6211\u6ca1\u8bb0\u9519\uff0c\u60a8\u4e0a\u6b21\u66fe\u7ecf\u5411\u6211\u63d0\u8d77\u8fc7\uff0c\u8868\u793a\u5e0c\u671b\u56de\u5230\u7269\u8d28\u754c\uff0c\u201d\u7ea2\u8272\u5bff\u8863\u8bf4\uff0c\u201c\u90a3\u4e48\uff0c\u73b0\u5728\u6211\u628a\u8fd9\u4e00\u6761\u52a0\u4e0a\u53bb\u4f5c\u4e3a\u8865\u507f\uff0c\u60a8\u610f\u4e0b\u5982\u4f55\uff1f\u201d</p><p>\u743c\u6069\u6709\u4e9b\u52a8\u5fc3\uff0c\u201c\u90a3\u7b2c\u4e8c\u79cd\u65b9\u6848\u5462\uff1f\u201d</p><p>\u201c\u60a8\u521a\u624d\u8868\u793a\u975e\u5e38\u5728\u610f\u90a3\u679a\u5370\u7ae0\uff0c\u6211\u5bf9\u8fd9\u79cd\u60c5\u51b5\u4e5f\u505a\u4e86\u8003\u8651\uff0c\u6240\u4ee5\u51c6\u5907\u4e86\u7b2c\u4e8c\u79cd\u8865\u507f\u65b9\u6848\uff0c\u201d\u7ea2\u8272\u5bff\u8863\u4e0d\u7d27\u4e0d\u6162\u5730\u8bf4\uff0c\u201c\u5370\u7ae0\u73b0\u5728\u5df2\u7ecf\u843d\u5165\u62dc\u5c14\u624b\u4e2d\uff0c\u4ed6\u5f53\u7136\u4e0d\u4f1a\u4e56\u4e56\u9001\u8fd8\u3002\u5982\u679c\u60a8\u5bf9\u5b83\u771f\u7684\u90a3\u4e48\u559c\u6b22\uff0c\u5fd7\u5728\u5fc5\u5f97\u7684\u8bdd\uff0c\u53ea\u80fd\u53bb\u62a2\u56de\u6765\u4e86\u2014\u2014\u6211\u5bf9\u6b64\u53ef\u4ee5\u63d0\u4f9b\u65b9\u4fbf\u3002\u201d</p><p>\u743c\u6069\u5413\u4e86\u4e00\u8df3\uff0c\u5f00\u73a9\u7b11\uff0c\u8c01\u6562\u8dd1\u53bb\u62dc\u5c14\u7684\u9752\u94dc\u57ce\u5821\u53bb\u62a2\u4ed6\uff0c\u6d3b\u5f97\u4e0d\u8010\u70e6\u4e86\u4e48\uff0c\u5c31\u7b97\u662f\u6b23\u5e03\u8fd9\u79cd\u4eba\u4e5f\u7edd\u65e0\u8fd9\u79cd\u80c6\u91cf\uff0c\u56e0\u4e3a\u8fd9\u7eaf\u7cb9\u662f\u9001\u6b7b\u3002</p><p>\u201c\u5f53\u7136\u4e0d\u4f1a\u662f\u53bb\u9752\u94dc\u5821\u5792\u76f4\u63a5\u62a2\uff0c\u201d\u7ea2\u8272\u5bff\u8863\u89e3\u91ca\uff0c\u201c\u800c\u4e14\u90a3\u91cc\u73b0\u5728\u662f\u9b54\u9b3c\u519b\u961f\u7684\u5927\u672c\u8425\uff0c\u6211\u4e5f\u6ca1\u6709\u8fd9\u4e2a\u80fd\u529b\u5c06\u60a8\u9001\u53bb\u3002\u4e0d\u8fc7\u6211\u53ef\u4ee5\u628a\u60a8\u63a8\u8350\u8fdb\u5965\u5580\u65af\u7684\u7ef4\u62a4\u961f\u3002\u201d</p><p>\u201c\u7ef4\u62a4\u961f\uff1f\u201d</p><p>\u201c\u8fd9\u540d\u5b57\u5f88\u571f\u6c14\u5427\uff0c\u6ca1\u529e\u6cd5\uff0c\u5965\u5580\u65af\u5c31\u8fd9\u4e48\u70b9\u827a\u672f\u7ec6\u80de\uff0c\u4ed6\u624b\u4e0b\u4e5f\u90fd\u4e00\u6837\uff0c\u4e2a\u4e2a\u90fd\u662f\u5446\u5934\u5446\u8111\uff0c\u9664\u4e86\u67d0\u4e2a\u521a\u521a\u53db\u53d8\u8fc7\u53bb\u7684\u5bb6\u4f19\u8fd8\u7b97\u6709\u70b9\u7075\u6c14\u3002\u60a8\u77e5\u9053\uff0c\u8fd9\u6b21\u8840\u6218\u662f\u4ed6\u6311\u8d77\u7684\uff0c\u4ed6\u624b\u4e0b\u4e00\u4f4d\u5c06\u519b\u73b0\u5728\u5c31\u5728\u65ad\u57df\u9547\u91cc\uff0c\u524d\u5929\u6765\u62dc\u8bbf\u6211\uff0c\u542c\u4ed6\u7684\u8a00\u4e0b\u4e4b\u610f\uff0c\u5965\u5580\u65af\u8fd9\u6b21\u9f13\u6363\u51fa\u4e86\u67d0\u79cd\u79d8\u5bc6\u6b66\u5668\u2014\u2014\u6211\u4f30\u8ba1\u4ed6\u5341\u6709\u516b\u4e5d\u53c8\u53d1\u660e\u4e86\u4ec0\u4e48\u65b0\u7684\u4ea1\u7075\u602a\u7269\u2014\u2014\u9700\u8981\u4e00\u6279\u6709\u8db3\u591f\u9020\u8be3\u7684\u5deb\u5e08\u6765\u4f5c\u4e3a\u7ef4\u62a4\u4eba\u5458\uff0c\u5e0c\u671b\u6211\u80fd\u5e2e\u5fd9\u62db\u52df\uff0c\u201d\u7ea2\u8272\u5bff\u8863\u644a\u5f00\u624b\uff0c\u505a\u4e86\u4e2a\u5f88\u65e0\u5948\u7684\u59ff\u52bf\uff0c\u201c\u60a8\u60f3\u5fc5\u4e5f\u6e05\u695a\uff0c\u60f3\u5728\u6076\u9b54\u4e2d\u5bfb\u627e\u5f3a\u608d\u52c7\u731b\u7684\u6218\u58eb\uff0c\u90a3\u662f\u8f7b\u800c\u6613\u4e3e\uff0c\u4f46\u60f3\u627e\u5deb\u5e08\u53ef\u5c31\u4e0d\u90a3\u4e48\u5bb9\u6613\u4e86\u2026\u2026\u4e0d\u8fc7\u6211\u770b\u60a8\u5c31\u5b8c\u5168\u5408\u9002\u3002\u201d</p><p>\u201c\u53ef\u662f\u8fd9\u548c\u6211\u4eec\u5728\u8c08\u8bba\u7684\u8bdd\u9898\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d</p><p>\u201c\u5173\u7cfb\u5f88\u6e05\u695a\u554a\uff0c\u60a8\u5982\u679c\u771f\u60f3\u8981\u90a3\u679a\u5370\u7ae0\uff0c\u76ee\u524d\u6765\u770b\u552f\u4e00\u7684\u529e\u6cd5\u5c31\u662f\u52a0\u5165\u5965\u5580\u65af\u7684\u519b\u961f\uff0c\u6253\u8d25\u62dc\u5c14\uff0c\u4ece\u4ed6\u624b\u91cc\u62a2\u56de\u6765\u3002\u5f53\u7136\u4e86\uff0c\u8840\u6218\u5f88\u5371\u9669\uff0c\u4e0d\u8fc7\u7ef4\u62a4\u961f\u76f8\u5bf9\u5e94\u8be5\u5b89\u5168\u5f88\u591a\u7684\uff0c\u56e0\u4e3a\u4e0d\u9700\u8981\u76f4\u63a5\u53c2\u6218\u3002\u201d</p><p>\u743c\u6069\u76b1\u7740\u7709\u5934\uff0c\u7ea2\u8272\u5bff\u8863\u6240\u8c13\u7684\u7b2c\u4e8c\u79cd\u8865\u507f\u65b9\u6848\u672a\u514d\u6709\u4e9b\u79bb\u8c31\uff0c\u4ed6\u867d\u7136\u6ca1\u771f\u6b63\u7ecf\u5386\u8fc7\u8840\u6218\uff0c\u603b\u4e5f\u542c\u8bf4\u8fc7\u5389\u5bb3\uff0c\u5982\u4f55\u6562\u8f7b\u6613\u5377\u5165\uff0c\u5c31\u7b97\u90a3\u4ec0\u4e48\u7ef4\u62a4\u961f\u5e94\u8be5\u5c5e\u4e8e\u540e\u52e4\u6280\u672f\u4eba\u5458\uff0c\u8eb2\u5728\u540e\u65b9\uff0c\u4e0d\u7528\u76f4\u63a5\u4e0a\u6218\u573a\uff0c\u90a3\u4e5f\u603b\u8fd8\u662f\u6709\u4e00\u5b9a\u98ce\u9669\u7684\u3002\u5f53\u7136\uff0c\u4e16\u754c\u4e0a\u505a\u4ec0\u4e48\u4e8b\u60c5\u90fd\u6709\u98ce\u9669\uff0c\u8fd9\u5012\u8fd8\u7f62\u4e86\uff0c\u4e0d\u662f\u5927\u95ee\u9898\u2014\u2014\u95ee\u9898\u7684\u5173\u952e\u5728\u4e8e\uff0c\u5c31\u7b97\u4e00\u5207\u987a\u5229\uff0c\u6076\u9b54\u519b\u961f\u6253\u8d62\u4e86\uff0c\u743c\u6069\u7b49\u4eba\u4e5f\u5b89\u5168\u65e0\u4e8b\uff0c\u5c31\u4e00\u5b9a\u80fd\u62ff\u5230\u5370\u7ae0\uff1f\u53ef\u884c\u6027\u672a\u514d\u592a\u4f4e\u4e86\u3002</p><p>\u7b2c\u4e00\u79cd\u65b9\u6848\uff0c\u76f8\u5bf9\u6765\u8bf4\u66f4\u503c\u5f97\u8003\u8651\uff0c\u80fd\u591f\u8ba9\u743c\u6069\u548c\u6885\u83f2\u65af\u8fd4\u56de\u7269\u8d28\u754c\uff0c\u65e9\u65e9\u8131\u79bb\u8fd9\u662f\u975e\u4e4b\u5730\u3002\u5f53\u7136\uff0c\u6b23\u5e03\u7b49\u4eba\u53ea\u6015\u662f\u4e0d\u4f1a\u9ad8\u5174\u4e86\uff0c\u5979\u4eec\u8fd9\u79cd\u6b63\u4e49\u4eba\u58eb\uff0c\u65e2\u7136\u77e5\u9053\u8428\u9a6c\u65af\u7279\u6709\u6bc1\u706d\u4e16\u754c\u7684\u8ba1\u5212\uff0c\u53ea\u8981\u6709\u4e00\u7ebf\u5e0c\u671b\u90fd\u8981\u53bb\u963b\u6b62\uff0c\u4e0d\u4f1a\u4e2d\u9014\u653e\u5f03\u2014\u2014\u4f46\u8fd9\u548c\u743c\u6069\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u5979\u4eec\u7231\u5e72\u561b\u5e72\u561b\u53bb\uff0c\u5927\u5bb6\u5404\u4e0d\u76f8\u5e72\uff0c\u4ece\u6b64\u5206\u9053\u626c\u9573\u6b63\u597d\u3002</p><p>\u552f\u4e00\u7684\u95ee\u9898\uff0c\u5c31\u662f\u90a3\u679a\u5370\u7ae0\u662f\u771f\u7684\u8981\u5f7b\u5e95\u653e\u5f03\u4e86\uff0c\u82b1\u4e86\u8fd9\u4e48\u591a\u5fc3\u529b\uff0c\u7ed3\u679c\u8fd8\u662f\u4e00\u573a\u7a7a\uff0c\u4e0d\u514d\u8ba9\u4eba\u6709\u4e9b\u4e0d\u723d\u3002\u4e0d\u8fc7\u4e5f\u6b63\u5982\u6885\u83f2\u65af\u6240\u8bf4\uff0c\u743c\u6069\u60f3\u8981\u90a3\u679a\u5370\u7ae0\uff0c\u66f4\u591a\u662f\u4e00\u79cd\u76f4\u89c9\uff0c\u6bd5\u7adf\u4e0d\u662f\u5df2\u7ecf\u786e\u8bc1\u5b83\u5bf9\u81ea\u5df1\u5c31\u6709\u7528\u5904\u2014\u2014\u8bf4\u4e0d\u5b9a\u81ea\u5df1\u7684\u76f4\u89c9\u9519\u4e86\u5462\uff0c\u8bf4\u4e0d\u5b9a\u81ea\u5df1\u591a\u60f3\u4e86\u5462\u3002\u4e3a\u4e86\u5b83\uff0c\u6253\u51e0\u573a\u6bd4\u8d5b\u5012\u4e5f\u7f62\u4e86\uff0c\u5982\u679c\u8bf4\u8fd8\u8981\u5192\u5377\u5165\u8840\u6218\u7684\u98ce\u9669\uff0c\u4f3c\u4e4e\u5c31\u6709\u4e9b\u4e0d\u592a\u503c\u5f97\u5427\u3002\u6309\u7167\u7ecf\u6d4e\u5b66\u7684\u8bf4\u6cd5\uff0c\u524d\u671f\u6295\u5165\u4e86\u4e00\u4e9b\u6210\u672c\uff0c\u4f46\u5982\u4eca\u5df2\u7ecf\u201c\u6c89\u6ca1\u201d\u4e86\uff0c\u90a3\u5c31\u4e0d\u7528\u518d\u60cb\u60dc\uff0c\u65e9\u65e9\u62bd\u8eab\u653e\u5f03\u5f97\u4e86\u3002</p><p>\u7565\u4f5c\u76d8\u7b97\uff0c\u743c\u6069\u5c31\u51b3\u5b9a\u63a5\u53d7\u7b2c\u4e00\u4e2a\u65b9\u6848\uff0c\u62ac\u8d77\u5934\u6765\u6b63\u8981\u7b54\u5e94\uff0c\u731b\u7136\u770b\u89c1\u5750\u5728\u5bf9\u9762\u7684\u9b45\u9b54\u7f8e\u4eba\u76c8\u76c8\u542b\u7b11\u7684\u773c\u4e2d\uff0c\u4f3c\u4e4e\u6709\u4e00\u4e1d\u7279\u522b\u7684\u795e\u8272\u95ea\u8fc7\uff0c\u4ee4\u4eba\u6349\u6478\u4e0d\u900f\u3002\u4ed6\u5fae\u5fae\u4e00\u6014\uff0c\u5fc3\u4e2d\u5ffd\u7136\u5347\u8d77\u4e00\u79cd\u4e0d\u8be6\u9884\u5146\uff0c\u539f\u672c\u8131\u53e3\u800c\u51fa\u7684\u8bdd\u5c31\u8fdf\u7591\u4e86\u4e0b\u6765\u3002</p><p>\u5947\u602a\uff0c\u603b\u89c9\u5f97\u4ec0\u4e48\u5730\u65b9\u4e0d\u592a\u5bf9\u52b2\u2026\u2026\u662f\u6211\u5f04\u9519\u4e86\u4ec0\u4e48\u4e1c\u897f\uff0c\u8fd8\u662f\u9057\u6f0f\u758f\u5ffd\u4e86\u4ec0\u4e48\u7ec6\u8282\uff1f</p><p>\u4ed6\u8e0c\u8e87\u8d77\u6765\uff0c\u628a\u6574\u4e2a\u4e8b\u60c5\u53c8\u4ece\u5934\u5230\u5c3e\u56de\u60f3\u4e86\u4e00\u904d\u3002\u62dc\u5c14\u62a2\u8d70\u5370\u7ae0\uff0c\u81ea\u7136\u662f\u4e3a\u8428\u9a6c\u65af\u7279\uff0c\u8fd9\u5012\u4e0d\u8db3\u4e3a\u5947\uff0c\u8840\u6218\u5373\u5c06\u5f00\u59cb\uff0c\u9b54\u9b3c\u4e00\u65b9\u5982\u679c\u80fd\u6709\u8428\u9a6c\u65af\u7279\u8fd9\u79cd\u5927\u5deb\u5e08\u52a0\u5165\uff0c\u80dc\u7b97\u81ea\u7136\u66f4\u589e\u51e0\u5206\uff0c\u62dc\u5c14\u4e3a\u6b64\u51fa\u529b\u4e5f\u662f\u6b63\u5e38\u3002\u7ea2\u8272\u5bff\u8863\u548c\u62dc\u5c14\u5341\u6709\u516b\u4e5d\u6709\u5978\u60c5\uff0c\u6240\u4ee5\u624d\u4f1a\u5e2e\u5fd9\u653e\u6c34\u2026\u2026\u53ef\u662f\u7ea2\u8272\u5bff\u8863\u6bd5\u7adf\u662f\u6076\u9b54\u554a\uff0c\u8fd9\u6837\u5e2e\u9b54\u9b3c\u7684\u5fd9\uff0c\u5979\u8fd8\u6709\u6ca1\u6709\u57fa\u672c\u7acb\u573a\uff1f</p><p>\u662f\u4e86\uff0c\u8fd9\u6b21\u8840\u6218\u7684\u6076\u9b54\u4e00\u65b9\uff0c\u662f\u5965\u5580\u65af\u7684\u519b\u961f\uff0c\u5965\u5580\u65af\u548c\u683c\u62c9\u5179\u7279\u662f\u6b7b\u654c\uff0c\u7ea2\u8272\u5bff\u8863\u548c\u683c\u62c9\u5179\u7279\u5219\u662f\u76df\u53cb\u2014\u2014\u6240\u4ee5\u7ea2\u8272\u5bff\u8863\u8fd9\u662f\u5728\u501f\u5200\u6740\u4eba\uff0c\u60f3\u501f\u7740\u62dc\u5c14\u4e4b\u624b\u6765\u524a\u5f31\u5965\u5580\u65af\u7684\u5b9e\u529b\uff0c\u7ed9\u683c\u62c9\u5179\u7279\u5e2e\u5fd9\u3002\u8fd9\u4e48\u4e00\u60f3\uff0c\u6574\u4e2a\u4e8b\u60c5\u57fa\u672c\u5c31\u901a\u987a\u8fc7\u6765\uff1a\u7ea2\u8272\u5bff\u8863\u539f\u672c\u4e0d\u77e5\u624b\u4e2d\u5370\u7ae0\u7684\u91cd\u8981\u6027\uff0c\u62ff\u51fa\u6765\u505a\u5956\u54c1\uff0c\u7ed3\u679c\u53d1\u73b0\u5f15\u6765\u4e86\u8428\u9a6c\u65af\u7279\u548c\u6b23\u5e03\u8fd9\u79cd\u5927\u5deb\u5e08\uff0c\u4ee5\u5979\u7684\u8eab\u4efd\uff0c\u5f88\u5bb9\u6613\u67e5\u51fa\u7f18\u7531\uff0c\u81f3\u5c11\u77e5\u9053\u70b9\u7ebf\u7d22\uff0c\u6b63\u597d\u8428\u9a6c\u65af\u7279\u53c8\u88ab\u743c\u6069\u9634\u4e86\u4e00\u9053\uff0c\u65e0\u529b\u53c2\u8d5b\u4e86\uff0c\u4e8e\u662f\u7ea2\u8272\u5bff\u8863\u987a\u6c34\u63a8\u821f\uff0c\u628a\u6d88\u606f\u900f\u9732\u7ed9\u62dc\u5c14\uff0c\u8ba9\u4ed6\u6765\u62ff\u5230\u5370\u7ae0\uff0c\u4fc3\u6210\u62dc\u5c14\u548c\u8428\u9a6c\u65af\u7279\u7684\u5408\u4f5c\uff0c\u5e0c\u671b\u4ed6\u4eec\u5728\u63a5\u4e0b\u6765\u7684\u8840\u6218\u4e2d\u628a\u5965\u5580\u65af\u6253\u5f97\u7070\u5934\u571f\u8138\u2026\u2026\u90a3\u8fd9\u4e48\u8bf4\u7684\u8bdd\uff0c\u7ea2\u8272\u5bff\u8863\u6050\u6015\u4e0d\u662f\u548c\u62dc\u5c14\u6709\u5978\u60c5\uff0c\u800c\u662f\u548c\u683c\u62c9\u5179\u7279\u6709\u4e00\u817f\uff0c\u6216\u8005\u4e24\u8fb9\u90fd\u6709\u2026\u2026</p><p>\u90a3\u4e48\u5979\u7ed9\u6211\u6446\u51fa\u7684\u8fd9\u4e24\u6761\u9009\u62e9\uff0c\u53c8\u662f\u4ec0\u4e48\u610f\u601d\u5462\uff1f</p></article>"}}}]);
