(window.webpackJsonp=window.webpackJsonp||[]).push([[342],{364:function(p){p.exports={chapterTitle:"\u7b2c\u516b\u5377 \u9f99\u72c2\u7bc7",title:"\u5e8f\u7ae0 \u65e0\u8def\u53ef\u9000\uff08\u4e0a\uff09",content:"<article><h1>\u7b2c\u516b\u5377 \u9f99\u72c2\u7bc7</h1><h2>\u5e8f\u7ae0 \u65e0\u8def\u53ef\u9000\uff08\u4e0a\uff09</h2><p>\u8840\u6c14\u67d3\u7ea2\u4e86\u9634\u973e\u7684\u5929\u7a7a\uff0c\u592a\u9633\u88ab\u4e4c\u4e91\u5b8c\u5168\u906e\u63a9\uff0c\u53ea\u80fd\u900f\u4e0b\u5fae\u5f31\u7684\u5149\u8292\u3002\u72c2\u98ce\u5939\u6742\u7740\u7ec6\u7ec6\u7684\u96e8\u73e0\u76d8\u65cb\u547c\u5578\uff0c\u50cf\u76ae\u97ad\u4e00\u822c\u62bd\u6253\u5728\u6bcf\u4e2a\u751f\u7269\u7684\u8eab\u4e0a\uff0c\u968f\u5373\u5316\u4f5c\u4e1d\u4e1d\u70ed\u6c14\u84b8\u817e\u6d88\u5931\u3002\u5de8\u5927\u7684\u706b\u7403\u6beb\u65e0\u9884\u5146\u5730\u4ece\u7a7a\u4e2d\u5760\u4e0b\uff0c\u5c06\u5730\u9762\u7838\u51fa\u65e0\u6570\u6df1\u5751\u3002\u5c16\u9510\u7c97\u7c9d\u7684\u5ca9\u77f3\u94fa\u5728\u9ed1\u8272\u7684\u6ce5\u571f\u4e0a\uff0c\u9752\u84dd\u8272\u7684\u90aa\u708e\u8df3\u8dc3\u5176\u95f4\uff0c\u5ffd\u9690\u5ffd\u73b0\uff0c\u50cf\u6bd2\u86c7\u4e00\u6837\u54ac\u556e\u7740\u8fd0\u6c14\u4e0d\u4f73\u7684\u65c5\u4eba\u3002\u873f\u8712\u7ef5\u957f\u7684\u8840\u4e4b\u6cb3\uff08\u53c8\u79f0\u51a5\u6cb3\uff09\u4e0d\u75be\u4e0d\u5f90\u5730\u6d41\u8fc7\uff0c\u5343\u767e\u5e74\u6765\u5b83\u7684\u901f\u5ea6\u5c31\u4e0d\u66fe\u6709\u8fc7\u4e1d\u6beb\u6539\u53d8\uff0c\u6210\u5343\u4e0a\u4e07\u7684\u9b3c\u9b42\u5728\u6ce2\u6d6a\u4e2d\u6d6e\u6d6e\u6c89\u6c89\uff0c\u5f20\u7740\u5634\uff0c\u53d1\u51fa\u65e0\u58f0\u7684\u51c4\u5389\u547c\u558a\uff0c\u4f46\u968f\u5373\u88ab\u4e0b\u4e00\u4e2a\u6d6a\u5934\u541e\u6ca1\u3002\u62ac\u773c\u671b\u53bb\uff0c\u5929\u8fb9\u9690\u9690\u7ea6\u7ea6\u73b0\u51fa\u4e00\u5ea7\u9ad8\u8038\u5165\u4e91\u7684\u5c16\u5854\uff0c\u6bcf\u4e00\u5757\u7816\u77f3\u90fd\u662f\u7528\u6076\u9b54\u548c\u9b54\u9b3c\u7684\u5934\u9aa8\u5792\u79ef\u780c\u6210\uff0c\u4f3c\u4e4e\u5e76\u4e0d\u5341\u5206\u9065\u8fdc\uff0c\u4f46\u6c38\u8fdc\u4e5f\u65e0\u6cd5\u9760\u8fd1\u3002</p><p>\u4e5d\u5c42\u5730\u72f1\u7684\u7b2c\u4e00\u5c42\uff0c\u963f\u5f17\u7eb3\u65af\u3002</p><p>\u4e00\u5ea7\u96c4\u4f1f\u7684\u57ce\u5e02\u5dcd\u7136\u77d7\u7acb\u5728\u8840\u4e4b\u6cb3\u4fa7\u5cb8\uff0c\u5341\u4e09\u9053\u57ce\u5899\u6784\u6210\u5341\u4e09\u4e2a\u89c4\u5219\u7684\u540c\u5fc3\u5706\uff0c\u5c06\u4e00\u5ea7\u9752\u94dc\u8272\u7684\u5bab\u6bbf\u62a4\u536b\u5176\u4e2d\uff0c\u8fd9\u4fbf\u662f\u9752\u94dc\u57ce\u5821\uff0c\u963f\u5f17\u7eb3\u65af\u7edf\u6cbb\u8005\u7684\u4f4f\u5904\u6240\u5728\uff0c\u5b83\u7684\u524d\u4efb\u4e3b\u4eba\u662f\u624e\u745e\u5c14\u5973\u58eb\uff0c\u73b0\u5728\u5219\u662f\u62dc\u5c14\u516c\u7235\u2014\u2014\u963f\u65af\u8482\u6469\u65af\u4e3a\u4ed6\u7684\u5404\u5927\u9886\u4e3b\u90fd\u6388\u4e88\u4e86\u201c\u516c\u7235\u201d\u79f0\u53f7\uff0c\u867d\u7136\u62dc\u5c14\u66f4\u559c\u6b22\u522b\u4eba\u79f0\u547c\u4ed6\u4e3a\u201c\u5c06\u519b\u201d\u3002</p><p>\u5728\u9752\u94dc\u57ce\u5821\u524d\u65b9\uff0c\u4e00\u671b\u65e0\u57a0\u7684\u8352\u91ce\u4e0a\uff0c\u6076\u9b54\u548c\u9b54\u9b3c\u7684\u519b\u961f\u5df2\u7ecf\u5728\u6b64\u5bf9\u5cd9\u93d6\u6218\u4e86\u4e03\u5929\u516b\u591c\uff0c\u571f\u5730\u88ab\u9c9c\u8840\u6df1\u6df1\u6d78\u6ce1\uff0c\u663e\u51fa\u4e00\u79cd\u8be1\u5f02\u7684\u6697\u7ea2\uff0c\u6de1\u9752\u8272\u7684\u7ec6\u5ae9\u690d\u7269\u4ece\u5ca9\u77f3\u7684\u7f1d\u9699\u4e2d\u751f\u957f\u51fa\u6765\uff0c\u5f00\u7740\u516b\u89d2\u5f62\u7684\u5587\u53ed\u72b6\u5c0f\u82b1\uff0c\u6bb7\u7ea2\u4f3c\u8840\uff0c\u8fce\u98ce\u5fae\u5fae\u98a4\u6296\uff0c\u8fd9\u662f\u8840\u8165\u8349\uff0c\u53ea\u5728\u6218\u573a\u548c\u8840\u4e4b\u6cb3\u4e24\u4fa7\u6cbf\u5cb8\u751f\u957f\u3002</p><p>\u62dc\u5c14\u7ad9\u5728\u9ad8\u8038\u7684\u57ce\u5899\u4e0a\uff0c\u7528\u6781\u5177\u5a01\u4e25\u7684\u76ee\u5149\u5de1\u89c6\u7740\u6218\u573a\uff0c\u4ed6\u6b64\u65f6\u5df2\u7ecf\u4e0d\u518d\u662f\u6df1\u72f1\u70bc\u9b54\u7684\u6a21\u6837\uff0c\u800c\u662f\u53d8\u6210\u4e86\u4e00\u4e2a\u9ed1\u53d1\u9ed1\u773c\u7684\u4eba\u7c7b\uff0c\u952f\u9f7f\u957f\u5200\u60ac\u6302\u5728\u8170\u95f4\uff0c\u4e00\u88ad\u8c61\u5f81\u516c\u7235\u8eab\u4efd\u7684\u9ed1\u8272\u534e\u7f8e\u957f\u888d\u7f69\u4f4f\u4ed6\u7684\u8eaf\u4f53\uff0c\u9ad8\u9ad8\u7ad6\u8d77\u7684\u8863\u9886\u8ba9\u963f\u5f17\u7eb3\u65af\u7684\u7edf\u6cbb\u8005\u611f\u89c9\u6709\u4e9b\u6c14\u95f7\uff0c\u51e0\u6b21\u6709\u51b2\u52a8\u60f3\u628a\u8fd9\u4ef6\u957f\u888d\u626f\u4e0b\u4e22\u5f00\uff0c\u4f46\u6700\u7ec8\u8fd8\u662f\u6309\u637a\u4f4f\u4e86\u3002\u4ed6\u9700\u8981\u5728\u90e8\u4e0b\u9762\u524d\u5c55\u793a\u4fe1\u5fc3\uff0c\u6709\u65f6\u5019\uff0c\u5f62\u5f0f\u4e3b\u4e49\u4e5f\u662f\u6709\u5fc5\u8981\u7684\u3002</p><p>\u9b54\u9b3c\u7684\u519b\u961f\u65e9\u5df2\u7ecf\u5728\u57ce\u4e0b\u5217\u597d\u9635\u52bf\uff0c\u6570\u91cf\u6700\u591a\u7684\u52a3\u9b54\u5b89\u6392\u5728\u6700\u524d\u65b9\uff0c\u5b83\u4eec\u6ca1\u6709\u4efb\u4f55\u76d4\u7532\u548c\u76fe\u724c\uff0c\u751a\u81f3\u6ca1\u6709\u6b66\u5668\uff0c\u5b8c\u5168\u53ea\u80fd\u4f9d\u9760\u5e76\u4e0d\u5341\u5206\u950b\u5229\u722a\u5b50\u53bb\u5e94\u4ed8\u63a5\u4e0b\u6765\u7684\u6218\u6597\u3002\u5168\u526f\u6b66\u88c5\uff0c\u624b\u6301\u5de8\u5251\u7684\u5012\u94a9\u9b54\u7d27\u968f\u5176\u540e\uff0c\u4ed6\u4eec\u662f\u9b54\u9b3c\u519b\u961f\u7684\u4e3b\u529b\uff0c\u72c2\u70ed\u55dc\u8840\uff0c\u597d\u6218\u8f7b\u751f\u3002\u5168\u8eab\u88ab\u5bd2\u94c1\u9501\u94fe\u5305\u88f9\u7684\u94fe\u9b54\u62a4\u4f4f\u4fa7\u7ffc\uff0c\u6570\u4ee5\u4e07\u8ba1\u7684\u9aa8\u9b54\u624b\u6267\u94a2\u77db\uff0c\u5217\u6210\u5982\u6797\u67aa\u9635\uff0c\u5c16\u523a\u9b54\u5219\u57cb\u4f0f\u5176\u540e\uff0c\u968f\u65f6\u51c6\u5907\u8fd0\u7528\u5b83\u4eec\u5929\u8d4b\u7684\u4efb\u610f\u4f20\u9001\u80fd\u529b\u52a0\u5165\u6218\u56e2\uff0c\u6253\u51fb\u654c\u4eba\u7684\u8584\u5f31\u90e8\u4f4d\u3002\u5728\u9635\u52bf\u7684\u6700\u540e\uff0c\u8fd1\u5343\u540d\u51b0\u9b54\u9a91\u5728\u9ed1\u8272\u7684\u68a6\u9b47\u4e0a\uff0c\u706b\u7130\u548c\u95ea\u7535\u6df7\u5408\u4ea4\u7ec7\u5851\u6210\u7684\u957f\u5200\u63e1\u5728\u624b\u4e2d\uff0c\u53ea\u7b49\u4e00\u58f0\u4ee4\u4e0b\u5373\u4fbf\u53d1\u8d77\u7a81\u51fb\u3002</p><p>\u62dc\u5c14\u5fae\u5fae\u70b9\u5934\uff0c\u6b63\u5f85\u8bf4\u8bdd\uff0c\u80cc\u540e\u8f7b\u5fae\u7684\u811a\u6b65\u58f0\u54cd\u8d77\uff0c\u6709\u4eba\u5411\u4ed6\u8d70\u6765\u3002\u8ddd\u79bb\u5feb\u901f\u62c9\u8fd1\uff0c\u5df2\u7ecf\u53d8\u5f97\u975e\u5e38\u5371\u9669\uff0c\u5982\u679c\u5bf9\u65b9\u5fc3\u6000\u6076\u610f\uff0c\u8fd9\u662f\u4e00\u4e2a\u826f\u597d\u7684\u523a\u6740\u673a\u4f1a\uff0c\u4f46\u62dc\u5c14\u6ca1\u6709\u8f6c\u8eab\uff0c\u751a\u81f3\u6ca1\u6709\u4f5c\u51fa\u4e1d\u6beb\u53cd\u5e94\uff0c\u4ed6\u77e5\u9053\u6765\u8005\u662f\u8c01\u3002</p><p>\u201c\u9eef\u4e4b\u7ffc\u5df2\u7ecf\u5168\u4f53\u5c31\u4f4d\u3002\u201d\u6765\u4eba\u8d70\u5230\u62dc\u5c14\u8eab\u540e\uff0c\u8f7b\u58f0\u7980\u62a5\u3002</p><p>\u62dc\u5c14\u62ac\u5934\u671d\u5929\u7a7a\u4e2d\u671b\u53bb\uff0c\u6d53\u5bc6\u7684\u4e4c\u4e91\u6321\u4f4f\u4e86\u89c6\u7ebf\uff0c\u5373\u4fbf\u662f\u4ed6\u8fd9\u6837\u7684\u5927\u9b54\u9b3c\u4e5f\u65e0\u6cd5\u770b\u900f\u3002\u4f46\u7ec6\u82e5\u6e38\u86c7\u822c\u7684\u6697\u7d2b\u8272\u95ea\u7535\u95f4\u6216\u95ea\u8fc7\uff0c\u4f9d\u65e7\u8ba9\u62dc\u5c14\u51c6\u786e\u5730\u53d1\u73b0\u4e86\u76ee\u6807\u7684\u4f4d\u7f6e\u3002\u201c\u5e72\u5f97\u6f02\u4eae\uff0c\u74e6\u5229\u65af\uff0c\u201d\u9b54\u9b3c\u7b80\u5355\u5730\u5938\u5956\u4e86\u4e00\u53e5\uff0c\u201c\u5f25\u8d5b\u4e9a\u5e26\u961f\uff1f\u201d</p><p>\u201c\u662f\u7684\uff0c\u201d\u74e6\u5229\u65af\u56de\u7b54\uff0c\u201c\u6211\u4eec\u73b0\u5728\u4e5f\u53ea\u80fd\u6307\u671b\u5979\u4e86\u3002\u201d</p><p>\u62dc\u5c14\u5bf9\u6b64\u672a\u4f5c\u8bc4\u4ef7\uff0c\u201c\u8ff7\u9501\u5462\u3002\u201d</p><p>\u201c\u8428\u739b\u65af\u7279\u58f0\u79f0\u4ed6\u4f1a\u5168\u529b\u4ee5\u8d74\uff0c\u514b\u5229\u798f\u5fb7\u548c\u74e6\u4f26\u9053\u5c14\u90fd\u88ab\u6293\u53bb\u5e2e\u5fd9\u4e86\uff0c\u201d\u74e6\u5229\u65af\u7a0d\u7a0d\u505c\u987f\uff0c\u201c\u6211\u8ba4\u4e3a\u90a3\u5bb6\u4f19\u4e0d\u53ef\u4fe1\uff0c\u4ed6\u662f\u4e2a\u5deb\u5996\uff0c\u662f\u4e2a\u4ea1\u7075\u3002\u201d</p><p>\u201c\u4f60\u603b\u662f\u8fd9\u4e48\u8bf4\uff0c\u201d\u62dc\u5c14\u5fae\u7b11\uff0c\u201c\u514b\u5229\u798f\u5fb7\u548c\u74e6\u4f26\u9053\u5c14\u540c\u6837\u4e5f\u662f\u5deb\u5996\u3002\u201d</p><p>\u201c\u4ed6\u4eec\u4e24\u4e2a\u4e0d\u4e00\u6837\uff0c\u201d\u74e6\u5229\u65af\u4e89\u8fa9\uff0c\u201c\u4ed6\u4eec\u662f\u540c\u4f34\u3002\u201d</p><p>\u62dc\u5c14\u5fae\u5fae\u4fa7\u8fc7\u8138\uff0c\u770b\u7740\u4ed6\u7684\u5c06\u519b\uff0c\u201c\u6211\u522b\u65e0\u9009\u62e9\uff0c\u74e6\u5229\u65af\uff0c\u201d\u4ed6\u53f9\u606f\uff0c\u201c\u6211\u4eec\u9762\u5bf9\u7684\u654c\u4eba\u6b63\u662f\u4ea1\u7075\u3002\u5982\u679c\u4e0d\u501f\u52a9\u4ea1\u7075\u5e08\u7684\u529b\u91cf\uff0c\u96be\u9053\u6211\u4eec\u8fd8\u8981\u53bb\u5411\u8bf8\u795e\u6c42\u52a9\uff0c\u8bf7\u4ed6\u4eec\u6d3e\u9063\u7267\u5e08\u5e2e\u5fd9\uff1f\u201d</p><p>\u74e6\u5229\u65af\u8d70\u5230\u57ce\u5899\u8fb9\uff0c\u770b\u89c1\u5728\u9b54\u9b3c\u519b\u961f\u7684\u5bf9\u9762\uff0c\u6076\u9b54\u4e5f\u5df2\u7ecf\u52c9\u52c9\u5f3a\u5f3a\u5217\u6210\u4e86\u201c\u9635\u52bf\u201d\uff0c\u4e71\u7cdf\u7cdf\u5730\u6324\u6210\u4e00\u5806\uff0c\u65e2\u65e0\u79e9\u5e8f\uff0c\u53c8\u65e0\u7ae0\u6cd5\uff0c\u5b8c\u5168\u662f\u4e00\u7fa4\u4e4c\u5408\u4e4b\u4f17\u3002\u6570\u91cf\u6700\u591a\u53c8\u6700\u5f31\u5c0f\u7684\u602f\u9b54\u7406\u5f53\u4f4d\u4e8e\u9635\u52bf\u7684\u6700\u524d\u65b9\uff0c\u4ee5\u4fbf\u7528\u4eba\u6d77\u6218\u672f\u53bb\u6d88\u8017\u654c\u4eba\u7684\u529b\u91cf\uff0c\u7136\u800c\u73b0\u5728\u5b83\u4eec\u5374\u8eb2\u5728\u6700\u540e\u9762\uff0c\u539f\u672c\u5e94\u8be5\u201c\u76d1\u7763\u201d\u602f\u9b54\u575a\u5b88\u5c97\u4f4d\u7684\u5df4\u5e03\u9b54\u548c\u72c2\u6218\u9b54\u4eec\u81ea\u5df1\u5df2\u7ecf\u51b2\u5230\u4e86\u524d\u6cbf\uff0c\u9ad8\u58f0\u53eb\u56b7\u7740\uff0c\u6325\u821e\u5175\u5668\uff0c\u8feb\u4e0d\u53ca\u5f85\u5730\u7b49\u5f85\u7740\u8fdb\u653b\u53f7\u4ee4\u3002\u5224\u9b42\u9b54\u548c\u8ff7\u8bf1\u9b54\u9a91\u5728\u72e9\u9b54\u86db\u4e0a\uff0c\u4e0d\u5b89\u5206\u5730\u56db\u5904\u6e38\u8361\uff0c\u5b8c\u5168\u5fd8\u8bb0\u4e86\u519b\u5b98\u7684\u804c\u8d23\u3002\u74e6\u5229\u65af\u53ef\u4ee5\u65ad\u5b9a\uff0c\u5f53\u6218\u6597\u6253\u54cd\u7684\u65f6\u5019\uff0c\u65e0\u9700\u9b54\u9b3c\u653b\u51fb\uff0c\u8fd9\u4e9b\u6076\u9b54\u4eec\u5c31\u4f1a\u56e0\u4e3a\u81ea\u76f8\u8df5\u8e0f\u63a8\u649e\u800c\u5927\u6253\u51fa\u624b\uff0c\u8fc5\u901f\u51cf\u5458\u4e94\u5206\u4e4b\u4e00\u751a\u81f3\u56db\u5206\u4e4b\u4e00\u3002</p><p>\u4ed6\u5c06\u76ee\u5149\u6295\u5411\u8fdc\u5904\uff0c\u770b\u89c1\u5728\u4e71\u7cdf\u7cdf\u7684\u6076\u9b54\u4e4b\u540e\uff0c\u662f\u6210\u5343\u4e0a\u4e07\u7684\u4ea1\u7075\u5927\u519b\uff0c\u50f5\u5c38\u3001\u9ab7\u9ac5\u548c\u6728\u4e43\u4f0a\u6b63\u8e52\u8dda\u6447\u6643\u5730\u7f13\u6162\u524d\u884c\uff0c\u5c38\u5996\u548c\u6ce2\u8fbe\u5c38\u968f\u884c\u5176\u540e\uff0c\u9ab8\u9aa8\u4e9a\u9f99\u5728\u5929\u7a7a\u4e2d\u7ff1\u7fd4\u76d8\u65cb\uff0c\u5b83\u4eec\u7684\u8eaf\u4f53\u65e9\u5df2\u8150\u8d25\u5e72\u67af\u5f97\u53ea\u5269\u767d\u9aa8\uff0c\u4f46\u5728\u8d1f\u80fd\u91cf\u7684\u652f\u6491\u4e0b\u5374\u6bd4\u751f\u524d\u66f4\u52a0\u7075\u6d3b\u3002\u5728\u6240\u6709\u4ea1\u7075\u4e4b\u540e\uff0c\u662f\u6570\u5343\u540d\u6b7b\u4ea1\u9a91\u58eb\u5217\u6210\u7684\u6574\u9f50\u65b9\u9635\uff0c\u4ed6\u4eec\u5168\u8eab\u91cd\u7532\uff0c\u624b\u6267\u957f\u67aa\uff0c\u8170\u60ac\u91cd\u5251\uff0c\u9a91\u7740\u9ab7\u9ac5\u6218\u9a6c\uff0c\u7c07\u62e5\u7740\u4e00\u5ea7\u516b\u89d2\u5f62\u91d1\u5c5e\u5821\u5792\u7f13\u7f13\u524d\u79fb\uff0c\u867d\u7136\u76f8\u9694\u9065\u8fdc\uff0c\u4f46\u5df2\u7ecf\u80fd\u591f\u611f\u89c9\u5230\u51db\u51db\u7684\u5a01\u538b\u3002</p><p>\u201c\u5927\u6982\u4e03\u5343\u4e2a\u6b7b\u4ea1\u9a91\u58eb\uff0c\u5965\u5580\u65af\u8fd9\u6b21\u7b97\u662f\u503e\u5de2\u800c\u51fa\u4e86\u3002\u201d\u62dc\u5c14\u8bc4\u4ef7\u3002</p><p>\u201c\u9eef\u4e4b\u7ffc\u8db3\u4ee5\u5bf9\u4ed8\uff0c\u201d\u74e6\u5229\u65af\u56de\u7b54\uff0c\u201c\u6211\u4eec\u867d\u7136\u5815\u843d\uff0c\u4f46\u4f9d\u65e7\u795e\u5723\u3002\u201d</p><p>\u201c\u4f60\u4eec\u4ece\u672a\u5815\u843d\uff0c\u53ea\u662f\u5728\u8ba4\u6e05\u672c\u8d28\uff0c\u201d\u62dc\u5c14\u7ea0\u6b63\uff0c\u6307\u5411\u6b7b\u4ea1\u9a91\u58eb\u65b9\u9635\u7684\u4e2d\u592e\uff0c\u90a3\u4e2a\u5de8\u5927\u7684\u91d1\u5c5e\u5821\u5792\uff0c\u201c\u6b7b\u4ea1\u9a91\u58eb\u5e76\u4e0d\u8db3\u8651\uff0c\u6211\u771f\u6b63\u62c5\u5fe7\u7684\uff0c\u662f\u90a3\u4e2a\u9b3c\u4e1c\u897f\u3002\u201d</p><p>\u74e6\u5229\u65af\u7684\u8138\u8272\u4e5f\u9634\u6c89\u4e0b\u6765\uff0c\u201c\u90a3\u4e1c\u897f\u5b9e\u5728\u5f88\u96be\u5bf9\u4ed8\uff0c\u201d\u4ed6\u52c9\u5f3a\u627f\u8ba4\uff0c\u201c\u7b80\u76f4\u5c31\u662f\u65e0\u61c8\u53ef\u51fb\u7684\u3002\u201d</p><p>\u201c\u8fd9\u4e16\u754c\u4e0a\u4e0d\u5b58\u5728\u65e0\u61c8\u53ef\u51fb\u7684\u4e1c\u897f\uff0c\u201d\u62dc\u5c14\u6447\u5934\uff0c\u201c\u6211\u4eec\u603b\u80fd\u627e\u5230\u5bf9\u4ed8\u5b83\u7684\u529e\u6cd5\u2014\u2014\u800c\u4e14\u5fc5\u987b\u627e\u5230\uff0c\u56e0\u4e3a\u6211\u4eec\u5df2\u7ecf\u65e0\u8def\u53ef\u9000\u3002\u201d</p><p>\u201c\u65e0\u8def\u53ef\u9000\u3002\u201d\u74e6\u5229\u65af\u8f7b\u58f0\u91cd\u590d\u3002</p><p>\u201c\u65e0\u8def\u53ef\u9000\uff0c\u201d\u62dc\u5c14\u518d\u6b21\u8bf4\uff0c\u4ece\u8170\u95f4\u62d4\u51fa\u957f\u5200\uff0c\u5411\u7a7a\u4e2d\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u53d1\u51fa\u6e05\u4eae\u7684\u94ff\u9535\u5578\u9e23\uff0c\u6240\u6709\u7684\u9b54\u9b3c\u90fd\u671d\u4ed6\u770b\u8fc7\u6765\uff0c\u9ad8\u9ad8\u4e3e\u8d77\u624b\u4e2d\u7684\u6b66\u5668\u3002\u201c\u4eca\u65e5\u4e4b\u6218\uff0c\u6709\u80dc\u65e0\u8d25\uff0c\u201d\u62dc\u5c14\u9ad8\u58f0\u8bf4\uff0c\u73af\u89c6\u90e8\u4e0b\uff0c\u201c\u963f\u5f17\u7eb3\u65af\u867d\u5927\uff0c\u6211\u4eec\u5df2\u7ecf\u65e0\u8def\u53ef\u9000\uff0c\u540e\u9762\u5c31\u662f\u9752\u94dc\u57ce\u5821\uff01\u201d</p><p>\u203b\u203b\u203b</p><p>\u201c\u4eca\u65e5\u4e00\u6218\uff0c\u6709\u80dc\u65e0\u8d25\uff0c\u201d\u6b4c\u560e\u65af\u5c06\u519b\u4f4e\u58f0\u81ea\u8a00\u81ea\u8bed\uff0c\u201c\u653b\u4e0b\u9752\u94dc\u57ce\u5821\uff0c\u5c31\u662f\u771f\u6b63\u5360\u9886\u4e86\u963f\u5f17\u7eb3\u65af\u3002\u201d</p><p>\u56e0\u4e3a\u4e5d\u5c42\u5730\u72f1\u7684\u4f4d\u9762\u89c4\u5219\u9650\u5236\uff0c\u6bcf\u4e00\u5c42\u5730\u72f1\u90fd\u6709\u4e00\u4e2a\u201c\u4e2d\u5fc3\u201d\uff0c\u6076\u9b54\u552f\u6709\u5360\u636e\u4e2d\u5fc3\uff0c\u624d\u7b97\u662f\u771f\u6b63\u5360\u636e\u4e86\u8fd9\u4e00\u5c42\uff0c\u7136\u540e\u624d\u80fd\u8fdb\u5165\u4e0b\u4e00\u5c42\u5730\u72f1\u3002\u5bf9\u4e8e\u963f\u5f17\u7eb3\u65af\u6765\u8bf4\uff0c\u9752\u94dc\u57ce\u5821\u5c31\u662f\u4e2d\u5fc3\uff0c\u5018\u82e5\u6076\u9b54\u519b\u961f\u60f3\u771f\u6b63\u5f81\u670d\u4e5d\u5c42\u5730\u72f1\uff0c\u8fd9\u5c31\u662f\u5fc5\u987b\u8fbe\u6210\u7684\u7b2c\u4e00\u6b65\u3002\u5bf9\u4e8e\u8fd9\u4e00\u70b9\uff0c\u6b4c\u560e\u65af\u5341\u5206\u6e05\u695a\u3002</p><p>\u6b4c\u560e\u65af\u662f\u6076\u9b54\u519b\u961f\u7684\u7edf\u5e05\uff0c\u4ed6\u662f\u4e2a\u7cbe\u7075\u5438\u8840\u9b3c\uff0c\u5728\u6f2b\u957f\u7684\u5c81\u6708\u4e2d\u5df2\u7ecf\u664b\u5347\u5230\u201c\u4eb2\u738b\u201d\u4f4d\u9636\uff0c\u62b5\u8fbe\u8fd9\u4e2a\u65cf\u88d4\u7684\u9876\u7aef\uff0c\u51e0\u5343\u5e74\u6765\u4e00\u76f4\u62c5\u4efb\u5965\u5580\u65af\u7684\u4f8d\u536b\u957f\u3002\u548c\u201c\u80cc\u53db\u201d\u7684\u4e60\u60ef\u5df2\u7ecf\u6d78\u900f\u9aa8\u9ad3\u7684\u6076\u9b54\u4e0d\u540c\uff0c\u6b4c\u560e\u65af\u5bf9\u5965\u5580\u65af\u5fe0\u5fc3\u803f\u803f\uff0c\u5373\u4fbf\u5728\u4ea1\u7075\u541b\u738b\u5931\u8e2a\u7684\u90a3\u51e0\u767e\u5e74\u4e2d\uff0c\u4ed6\u4e5f\u540c\u6837\u6ca1\u6709\u8f6c\u6295\u9635\u8425\uff0c\u800c\u662f\u575a\u5b88\u7740\u8428\u7eb3\u6258\u65af\uff08\u65e0\u5c3d\u6df1\u6e0a\u7b2c\u4e00\u767e\u4e00\u5341\u4e09\u5c42\uff0c\u5965\u5580\u65af\u7684\u56fd\u5ea6\uff09\uff0c\u7387\u9886\u90e8\u4e0b\u6253\u9000\u4e86\u654c\u4eba\u4e00\u6b21\u53c8\u4e00\u6b21\u7684\u4fb5\u7565\u3002\u5965\u5580\u65af\u56de\u5f52\u4e4b\u540e\uff0c\u5bf9\u4ed6\u7684\u5fe0\u8d1e\u8868\u73b0\u5927\u52a0\u8d5e\u8d4f\uff0c\u63d0\u62d4\u4e3a\u5c06\u519b\uff0c\u59d4\u4ee5\u91cd\u4efb\uff0c\u6210\u4e3a\u6b64\u6b21\u8840\u6218\u7684\u6307\u6325\u5b98\u3002</p><p>\u6b64\u65f6\u8fd9\u4f4d\u5438\u8840\u9b3c\u6307\u6325\u5b98\u6b63\u7ad9\u5728\u9634\u5f71\u7ffc\u9f99\u80cc\u4e0a\uff0c\u4ece\u7a7a\u4e2d\u4fef\u89c6\u7740\u4ed6\u7684\u519b\u961f\u3002</p><p>\u5982\u679c\u4ee5\u201c\u6218\u58eb\u201d\u7684\u6807\u51c6\u6765\u8861\u91cf\uff0c\u6b4c\u560e\u65af\u582a\u79f0\u51fa\u7c7b\u62d4\u8403\uff0c\u7cbe\u7075\u548c\u5438\u8840\u9b3c\u8d4b\u4e88\u7684\u53cc\u91cd\u957f\u5bff\uff0c\u8ba9\u4ed6\u5c06\u6b66\u6280\u548c\u9b54\u6cd5\u5b8c\u7f8e\u5730\u878d\u5408\u4e3a\u4e00\u4f53\uff0c\u5373\u4fbf\u662f\u540c\u65f6\u5bf9\u6297\u5341\u540d\u6b7b\u4ea1\u9a91\u58eb\u4ed6\u4e5f\u80fd\u5e94\u4ed8\u6709\u4f59\uff0c\u800c\u540e\u8005\u662f\u5965\u5580\u65af\u7684\u5f97\u610f\u53d1\u660e\u4e4b\u4e00\uff0c\u867d\u7136\u5728\u7269\u8d28\u754c\u77e5\u9053\u7684\u4eba\u4e0d\u591a\uff0c\u4f46\u5728\u4e0b\u5c42\u754c\uff0c\u6076\u9b54\u4eec\u90fd\u662f\u5c06\u6b7b\u4ea1\u9a91\u58eb\u548c\u5deb\u5996\u3001\u5438\u8840\u9b3c\u5e76\u5217\uff0c\u79f0\u4e3a\u4e09\u5927\u6700\u9ad8\u9636\u4ea1\u7075\uff0c\u901a\u5e38\u60c5\u51b5\u4e0b\uff0c\u4e00\u4f4d\u6b7b\u4ea1\u9a91\u58eb\u8db3\u4ee5\u5355\u72ec\u6740\u6b7b\u4e00\u53ea\u516d\u81c2\u86c7\u9b54\u3002</p><p>\u4f46\u5018\u82e5\u4ee5\u201c\u7edf\u5e05\u201d\u7684\u6807\u51c6\u6765\u8861\u91cf\u7684\u8bdd\uff0c\u6b4c\u560e\u65af\u7684\u624d\u534e\u5c31\u4ee4\u4eba\u96be\u4ee5\u606d\u7ef4\uff0c\u751a\u81f3\u8fde\u201c\u4e2d\u89c4\u4e2d\u8ddd\u201d\u90fd\u672a\u5fc5\u7b97\u5f97\u4e0a\uff0c\u65e0\u8bba\u662f\u6218\u7565\u8fd8\u662f\u6218\u672f\u90fd\u975e\u4ed6\u6240\u957f\uff0c\u751a\u81f3\u8fde\u53d1\u8868\u6f14\u8bb2\u9f13\u821e\u58eb\u6c14\u4ed6\u90fd\u4e0d\u4f1a\u2014\u2014\u6076\u9b54\u4eec\u90fd\u77e5\u9053\uff0c\u6b4c\u560e\u65af\u602f\u573a\uff0c\u79c1\u4e0b\u76f8\u805a\u65f6\u4ed6\u80fd\u4f83\u4f83\u800c\u8c08\uff0c\u4eba\u4e00\u591a\u4ed6\u5c31\u8bf4\u90fd\u4e0d\u4f1a\u8bdd\u4e86\u3002\u4ee5\u5e38\u7406\u800c\u8bba\uff0c\u6d3e\u9063\u8fd9\u6837\u7684\u4eba\u6765\u5f53\u6307\u6325\u5b98\uff0c\u9664\u4e86\u8bc1\u660e\u5965\u5580\u65af\u667a\u5546\u4e0d\u8d85\u8fc7\u4e09\u5341\u5916\u522b\u65e0\u5176\u4ed6\u53ef\u80fd\uff0c\u6076\u9b54\u6218\u8d25\u662f\u6ce8\u5b9a\u7684\u2014\u2014\u4f46\u4e8b\u5b9e\u6070\u6070\u76f8\u53cd\uff0c\u6076\u9b54\u519b\u961f\u9664\u4e86\u4e00\u5f00\u59cb\u6709\u4e9b\u53d7\u632b\uff0c\u5f88\u5feb\u5c31\u626d\u8f6c\u5c40\u52bf\uff0c\u4e00\u8def\u51ef\u6b4c\uff0c\u51b2\u51fa\u4e07\u6e0a\u5e73\u539f\uff0c\u8d8a\u8fc7\u8840\u4e4b\u88c2\u9699\uff0c\u6253\u5230\u4e86\u963f\u5f17\u7eb3\u65af\uff0c\u5982\u4eca\u5df2\u7ecf\u5175\u4e34\u9752\u94dc\u57ce\u5821\u4e86\uff0c\u8fd9\u5728\u8840\u6218\u7684\u6570\u4e07\u5e74\u5386\u53f2\u4e0a\u90fd\u662f\u6781\u5176\u7f55\u89c1\u7684\u3002</p><p>\u4e4b\u6240\u4ee5\u6709\u8fd9\u79cd\u8f89\u714c\u6218\u679c\uff0c\u5e76\u975e\u662f\u56e0\u4e3a\u6b4c\u560e\u65af\u4e00\u76f4\u6df1\u85cf\u4e0d\u9732\u6216\u8005\u7a81\u7136\u5f00\u7a8d\uff0c\u800c\u5e94\u8be5\u5f52\u529f\u4e8e\u5965\u5580\u65af\u7684\u77e5\u4eba\u5584\u4efb\u3002</p><p>\u4f17\u6240\u5468\u4e4b\uff0c\u6076\u9b54\u5728\u6570\u91cf\u4e0a\u8fdc\u8fdc\u80dc\u8fc7\u9b54\u9b3c\u51e0\u5341\u500d\uff0c\u5355\u5175\u4f5c\u6218\u80fd\u529b\u4e5f\u6beb\u4e0d\u900a\u8272\uff0c\u751a\u81f3\u7565\u6709\u8fc7\u4e4b\uff0c\u4f46\u8840\u6218\u6301\u7eed\u6570\u4e07\u5e74\uff0c\u5374\u4e00\u76f4\u96be\u5206\u80dc\u8d1f\uff0c\u751a\u81f3\u5728\u8fd1\u51e0\u767e\u5e74\u91cc\u6076\u9b54\u8fd8\u4e00\u76f4\u88ab\u538b\u5236\u3002\u4e4b\u6240\u4ee5\u9020\u6210\u8fd9\u79cd\u7ed3\u679c\uff0c\u4e43\u662f\u7531\u5b83\u4eec\u7684\u672c\u8d28\u51b3\u5b9a\u7684\uff0c\u867d\u7136\u540c\u6837\u90aa\u6076\uff0c\u4f46\u6076\u9b54\u6df7\u4e71\u53db\u9006\uff0c\u4ece\u65e0\u4efb\u4f55\u201c\u79e9\u5e8f\u201d\u7684\u89c2\u5ff5\uff0c\u9b54\u9b3c\u5219\u5b8c\u5168\u76f8\u53cd\uff0c\u8fd9\u79cd\u5dee\u5f02\u53cd\u5e94\u5230\u6218\u573a\u4e0a\uff0c\u5c31\u662f\u4e4c\u5408\u4e4b\u4f17\u548c\u767e\u70bc\u7cbe\u5175\u7684\u533a\u522b\u3002\u9b54\u9b3c\u4eec\u867d\u7136\u5175\u529b\u8fdc\u900a\uff0c\u5374\u62e5\u6709\u7b2c\u4e00\u6d41\u7684\u6218\u7565\u5bb6\uff0c\u7b2c\u4e00\u6d41\u7684\u6307\u6325\u5b98\uff0c\u7b2c\u4e00\u6d41\u7684\u6b66\u5668\u88c5\u5907\u548c\u7b2c\u4e00\u6d41\u7684\u7eaa\u5f8b\uff0c\u800c\u4ed6\u4eec\u7684\u5bf9\u624b\u603b\u662f\u4f1a\u72af\u5404\u79cd\u83ab\u540d\u5176\u5999\u532a\u5937\u6240\u601d\u7684\u9519\u8bef\uff0c\u6700\u7ec8\u5c06\u5e9e\u5927\u7684\u5175\u529b\u4f18\u52bf\u5316\u4e3a\u4e4c\u6709\u3002\u8fd9\u662f\u6076\u9b54\u7684\u5148\u5929\u7f3a\u9677\uff0c\u5c31\u7b97\u518d\u9ad8\u660e\u518d\u6770\u51fa\u7684\u6307\u6325\u5b98\u4e5f\u65e0\u6cd5\u5f25\u8865\uff0c\u54ea\u6015\u8ba9\u683c\u62c9\u5179\u7279\u6765\u6307\u6325\uff0c\u4ed6\u7684\u547d\u4ee4\u4e5f\u4f1a\u5728\u6267\u884c\u8fc7\u7a0b\u4e2d\u8d70\u6837\u5f97\u5343\u5947\u767e\u602a\uff0c\u6700\u7ec8\u53d8\u5f97\u4e00\u584c\u7cca\u6d82\u3002</p><p>\u4f46\u5bf9\u4e8e\u5965\u5580\u65af\u7684\u519b\u961f\u6765\u8bf4\uff0c\u8fd9\u79cd\u95ee\u9898\u867d\u7136\u4f9d\u65e7\u5b58\u5728\uff0c\u5374\u76f8\u5bf9\u8f7b\u5fae\u7f13\u548c\u5f97\u591a\u2014\u2014\u56e0\u4e3a\u4e3b\u529b\u4e0d\u662f\u6076\u9b54\uff0c\u800c\u662f\u4ea1\u7075\u3002</p><p>\u5982\u679c\u8bf4\u8fd9\u4e16\u754c\u4e0a\u6709\u4ec0\u4e48\u519b\u961f\u6bd4\u9b54\u9b3c\u66f4\u6709\u79e9\u5e8f\uff0c\u66f4\u670d\u4ece\u547d\u4ee4\uff0c\u90a3\u6beb\u65e0\u7591\u95ee\u5c31\u662f\u4ea1\u7075\u4e86\uff0c\u5c24\u5176\u662f\u90a3\u4e9b\u6ca1\u6709\u81ea\u4e3b\u610f\u8bc6\u7684\u4f4e\u9636\u4ea1\u7075\u3002\u62dc\u5c14\u5982\u679c\u547d\u4ee4\u4e00\u961f\u9b54\u9b3c\u8df3\u4e0b\u6df1\u4e0d\u89c1\u5e95\u7684\u60ac\u5d16\uff0c\u9b54\u9b3c\u6216\u8bb8\u8fd8\u4f1a\u72b9\u8c6b\u9000\u7f29\uff0c\u4f46\u5982\u679c\u6b4c\u560e\u65af\u5bf9\u4e00\u961f\u9ab7\u9ac5\u50f5\u5c38\u53d1\u51fa\u540c\u6837\u547d\u4ee4\u7684\u8bdd\uff0c\u4ed6\u7684\u6743\u5a01\u4f1a\u5f97\u5230\u767e\u5206\u4e4b\u767e\u7684\u8d2f\u5f7b\u3002\u5373\u4fbf\u662f\u6b7b\u4ea1\u9a91\u58eb\u8fd9\u79cd\u5730\u4f4d\u76f8\u5f53\u4e8e\u5deb\u5996\u3001\u5438\u8840\u9b3c\u7684\u9ad8\u9636\u4ea1\u7075\uff0c\u540c\u6837\u4e5f\u4e0d\u4f1a\u4e1d\u6beb\u8fdd\u62d7\u6b4c\u560e\u65af\u7684\u547d\u4ee4\uff0c\u5965\u5580\u65af\u53f7\u79f0\u4ea1\u7075\u541b\u738b\uff0c\u5e76\u4e0d\u662f\u6d6a\u5f97\u865a\u540d\uff0c\u4ed6\u81ea\u6709\u5404\u79cd\u7cbe\u5999\u7684\u65b9\u6cd5\u63a7\u5236\u4ed6\u7684\u9020\u7269\uff0c\u786e\u4fdd\u4ed6\u4eec\u670d\u4ece\u3002</p><p>\u4ea1\u7075\u4e0d\u996e\u4e0d\u98df\uff0c\u4e0d\u7720\u4e0d\u4f11\uff0c\u53f7\u4ee4\u4e00\u51fa\uff0c\u5982\u81c2\u6307\u4f7f\uff0c\u8fde\u540e\u52e4\u90fd\u57fa\u672c\u65e0\u9700\u8003\u8651\u4e86\u3002\u7edf\u5e05\u8fd9\u6837\u7684\u519b\u961f\uff0c\u5373\u4f7f\u662f\u6b4c\u560e\u65af\u8fd9\u79cd\u5e73\u5eb8\u5c06\u9886\uff0c\u540c\u6837\u4e5f\u662f\u80fd\u591f\u80dc\u4efb\u7684\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\uff0c\u6b63\u56e0\u4e3a\u6b4c\u560e\u65af\u7edf\u5e05\u624d\u5177\u5e73\u5e73\uff0c\u5965\u5580\u65af\u624d\u653e\u5fc3\u8ba9\u4ed6\u6765\u6307\u6325\uff0c\u5018\u82e5\u6362\u4e86\u4e2a\u806a\u660e\u7684\u6076\u9b54\uff0c\u53ea\u6015\u53cd\u800c\u5f04\u5de7\u6210\u62d9\u3002\u6ca1\u6709\u667a\u529b\u7684\u58eb\u5175\uff0c\u6b63\u5e94\u8be5\u914d\u5e73\u5eb8\u7684\u5c06\u9886\u3002</p><p>\u5f53\u7136\uff0c\u5018\u82e5\u4ec5\u4ec5\u5982\u6b64\uff0c\u4e5f\u4e0d\u8fc7\u662f\u5f97\u4e86\u4e00\u4e2a\u201c\u5e73\u6b63\u201d\uff0c\u9519\u8bef\u6bd4\u4ee5\u524d\u76f8\u5bf9\u72af\u5f97\u5c11\uff0c\u5e76\u4e0d\u7b49\u4e8e\u5c31\u80fd\u591f\u80dc\u8fc7\u5bf9\u624b\u3002\u66f4\u4f55\u51b5\uff0c\u62dc\u5c14\u7684\u519b\u4e8b\u6307\u6325\u624d\u80fd\uff0c\u5373\u4fbf\u662f\u5728\u9b54\u9b3c\u4e2d\u90fd\u662f\u9996\u5c48\u4e00\u6307\u7684\uff0c\u8fd9\u4e5f\u662f\u4ed6\u80fd\u591f\u5f97\u5230\u963f\u65af\u8482\u6469\u65af\u770b\u4e2d\uff0c\u88ab\u4efb\u547d\u4e3a\u963f\u5f17\u7eb3\u65af\u9886\u4e3b\uff0c\u751a\u81f3\u5f97\u5230\u516b\u9b54\u5c06\u4e00\u81f4\u770b\u91cd\u7684\u539f\u56e0\u6240\u5728\u3002\u6b4c\u560e\u65af\u80fd\u591f\u51fb\u8d25\u62dc\u5c14\uff0c\u8282\u8282\u80dc\u5229\uff0c\u6700\u91cd\u8981\u7684\u51ed\u501f\u548c\u4f9d\u4ed7\uff0c\u662f\u90a3\u4ef6\u79d8\u5bc6\u6b66\u5668\u3002</p><p>\u4e00\u4ef6\u5728\u6218\u573a\u4e0a\u6240\u5411\u65e0\u654c\uff0c\u5f53\u8005\u62ab\u9761\u7684\u79d8\u5bc6\u6b66\u5668\u3002</p></article>"}}}]);
