(window.webpackJsonp=window.webpackJsonp||[]).push([[342],{365:function(p){p.exports={chapterTitle:null,title:"\u7b2c342\u7ae0 \u8ff7\u5c40",content:"<article><h2>\u7b2c342\u7ae0 \u8ff7\u5c40</h2><p>\u3000\u3000\u5728\u5341\u91cc\u6885\u6797\u5317\u7684\u56fd\u9053\u8fb9\u770b\u5230\u9676\u884c\u5065\uff0c\u5f20\u94c1\u68ee\u8fd8\u771f\u5413\u4e86\u4e00\u8df3\u3002\u8981\u4e0d\u662f\u9676\u884c\u5065\u8282\u5916\u751f\u679d\uff0c\u4ed6\u65e9\u5c31\u5750\u4e0a\u5408\u8d44\u516c\u53f8\u4e2d\u65b9\u7ecf\u7406\u7684\u4f4d\u5b50\uff0c\u5408\u8d44\u65b9\u6848\u88ab\u7701\u91cc\u9a73\u56de\u4e4b\u540e\uff0c\u4ed6\u7acb\u5373\u6307\u4f7f\u4eba\u5c06\u9676\u884c\u5065\u8d76\u51fa\u9999\u96ea\u6d77\u3002\u867d\u7136\u5c06\u9676\u884c\u5065\u8d76\u51fa\u9999\u96ea\u6d77\uff0c\u4f46\u662f\u5408\u8d44\u7684\u4e8b\u60c5\u5374\u4e00\u76f4\u62d6\u5230\u73b0\u5728\u65e0\u6cd5\u89e3\u51b3\uff0c\u8981\u8bf4\u8ba9\u5f20\u94c1\u68ee\u6068\u4e4b\u5165\u9aa8\u7684\u4eba\u4e5f\u591a\uff0c\u56fd\u9053\u4e0a\u5bf9\u4ed6\u7834\u53e3\u5927\u9a82\u7684\u5927\u8d27\u53f8\u673a\u4e5f\u7b97\u4e00\u4e2a\uff0c\u9676\u884c\u5065\u80af\u5b9a\u4e5f\u7b97\u4e00\u4e2a\u3002\u9676\u884c\u5065\u79bb\u5f00\u60e0\u5c71\u6709\u534a\u5e74\u7684\u65f6\u95f4\u4e0d\u77e5\u53bb\u8e2a\uff0c\u5f20\u94c1\u68ee\u60f3\u5173\u6ce8\u4e5f\u5173\u6ce8\u4e0d\u8fc7\u6765\uff0c\u53ea\u662f\u53c8\u5230\u5408\u8d44\u7684\u5173\u952e\u65f6\u523b\uff0c\u518d\u6b21\u770b\u5230\u9676\u884c\u5065\u603b\u8ba9\u4ed6\u5fc3\u91cc\u6709\u4e0d\u8be6\u4e4b\u611f\u3002</p><p>\u3000\u3000\u5f20\u94c1\u68ee\u7422\u78e8\u7740\u90a3\u8f86Vs40\u4e0eAudi A8\u8f66\u4e3b\u7684\u8eab\u4efd\uff0c\u9676\u884c\u5065\u8ddf\u8fd9\u4e9b\u4eba\u4e00\u8d77\u56de\u60e0\u5c71\uff0c\u90a3\u4e9b\u4e0b\u4e09\u6ee5\u7684\u624b\u6bb5\u5c31\u4e0d\u80fd\u7528\u5728\u4ed6\u8eab\u4e0a\u4e86\uff0c\u60f3\u8d77\u53bb\u5e74\u8c22\u5251\u5357\u5728\u60e0\u5c71\u7838\u4eba\u8f66\u7684\u4e8b\u60c5\uff0c\u60f3\u60f3\u4ee5\u8c22\u5bb6\u7684\u5bb6\u52bf\uff0c\u5468\u747e\u745c\u8fd8\u662f\u60e0\u5c71\u5e02\u957f\uff0c\u6700\u7ec8\u90fd\u8981\u8d54\u4eba\u5bb6\u4e00\u8f86\u5954\u9a70\u624d\u5c06\u4e8b\u60c5\u5e73\u606f\u6389\uff0c\u6709\u4e9b\u5f3a\u9f99\u8fc7\u5883\uff0c\u786e\u5b9e\u4e0d\u662f\u5730\u5934\u86c7\u80fd\u60f9\u7684\u3002</p><p>\u3000\u3000\u9676\u884c\u5065\u662f\u60e0\u5c71\u4eba\uff0c\u6625\u6696\u82b1\u5f00\u4e4b\u9645\uff0c\u56de\u8d9f\u60e0\u5c71\u4e5f\u6ca1\u6709\u7279\u522b\u7684\u5730\u65b9\uff0c\u53ea\u662f\u4ed6\u966a\u540c\u7684\u90a3\u4e9b\u4eba\u7684\u8eab\u4efd\u8ba9\u4ed6\u5fc3\u91cc\u4e0d\u8e0f\u5b9e\uff0c\u7422\u78e8\u7740\u627e\u8c01\u6253\u542c\u8fd9\u4e9b\u4eba\u7684\u8eab\u4efd\uff0c\u60f3\u8d77\u6628\u5929\u4e00\u8d77\u6d88\u8c34\u7684\u6c5f\u5929\u96c6\u56e2\u7684\u8001\u603b\u53f6\u6c5f\u5929\u89c1\u591a\u8bc6\u5e7f\uff0c\u8c22\u5bb6\u5341\u5e74\u524d\u624d\u5230\u60e0\u5c71\u7acb\u8db3\u7684\uff0c\u6839\u5e95\u8fd8\u5728\u65b0\u6cf0\u5e02\uff0c\u53f6\u6c5f\u5929\u624d\u80fd\u7b97\u60e0\u5c71\u7684\u8001\u5730\u5934\u86c7\uff0c\u8981\u4e0d\u662f\u8c22\u5bb6\u8fd9\u51e0\u5e74\u5728\u60e0\u5c71\u7684\u52a8\u4f5c\u592a\u5927\uff0c\u53f6\u6c5f\u5929\u4f1a\u4e00\u76f4\u662f\u60e0\u5c71\u5e02\u7684\u9996\u5bcc\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u4eba\u603b\u4e0d\u4f1a\u65e0\u7f18\u65e0\u6545\u7684\u5230\u60e0\u5c71\u6765\uff0c\u8bf4\u4e0d\u5b9a\u53f6\u6c5f\u5929\u77e5\u9053\u4e9b\u6d88\u606f\uff0c\u5f20\u94c1\u68ee\u62ff\u8d77\u6401\u5728\u4eea\u8868\u76d8\u4e0a\u7684\u624b\u673a\uff0c\u62d4\u901a\u53f6\u6c5f\u5929\u7684\u7535\u8bdd\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u7684\u7236\u4eb2\u6c5f\u6d9b\u6709\u9080\u8bf7\u53f6\u6c5f\u5929\u3001\u53f6\u9e3f\u7236\u5b50\u53c2\u52a0\u5e86\u751f\u9152\u5bb4\uff0c\u53ea\u662f\u53f6\u6c5f\u5929\u8fd9\u65f6\u5019\u8fd8\u6ca1\u6709\u4e0e\u5f20\u606a\u4ed6\u4eec\u9047\u4e0a\u3002</p><p>\u3000\u3000\u53f6\u6c5f\u5929\u6253\u5fc3\u773c\u91cc\u770b\u4e0d\u8d77\u5f20\u94c1\u68ee\uff0c\u4f46\u662f\u5f20\u94c1\u68ee\u662f\u5468\u747e\u745c\u7684\u79d8\u4e66\uff0c\u53c8\u662f\u4e09\u661f\u3001\u9999\u96ea\u6d77\u5408\u8d44\u8c08\u5224\u7684\u4e2d\u65b9\u4ee3\u8868\uff0c\u53f6\u6c5f\u5929\u60f3\u627f\u63fd\u5408\u8d44\u5de5\u5382\u7684\u5efa\u5382\u4e1a\u52a1\uff0c\u5f20\u94c1\u68ee\u662f\u4ed6\u5fc5\u9700\u5df4\u7ed3\u7684\u4e00\u4e2a\u4eba\u3002\u53f6\u6c5f\u5929\u4e0d\u77e5\u9053\u4ec0\u4e48A8\u3001Vs40\u8f66\u4e3b\uff0c\u53ea\u5f53\u5f20\u94c1\u68ee\u627e\u501f\u53e3\u62c9\u4ed6\u51fa\u6765\u6d88\u8c34\uff0c\u5fd9\u7b54\u5e94\u4e0e\u5f20\u94c1\u68ee\u5230\u6628\u5929\u900d\u9065\u5feb\u6d3b\u7684\u4f1a\u6240\u78b0\u5934\uff0c\u5750\u4e0a\u8f66\u7b49\u53f8\u673a\u5c06\u8f66\u5f00\u51fa\u505c\u8f66\u573a\uff0c\u4ed6\u624d\u60f3\u8d77\u6c5f\u6d9b\u8bf7\u4ed6\u559d\u5e86\u751f\u9152\u7684\u4e8b\u60c5\u3002\u6c5f\u6d9b\u4e0e\u5f20\u94c1\u68ee\u5b70\u8f7b\u5b70\u91cd\uff0c\u53f6\u6c5f\u5929\u81ea\u7136\u5206\u5f97\u6e05\u695a\uff0c\u4e0d\u8fc7\u4ed6\u8fd8\u662f\u7ed9\u6c5f\u6d9b\u6302\u4e86\u7535\u8bdd\u544a\u7f6a\uff0c\u7535\u8bdd\u91cc\u8bf4\u5b9a\u8ba9\u4ed6\u513f\u5b50\u53f6\u9e3f\u8fc7\u53bb\u559d\u5e86\u751f\u9152\u5e76\u81f4\u6b49\uff0c\u5012\u663e\u51fa\u4ed6\u6ef4\u6c34\u4e0d\u6f0f\u7684\u4e3a\u4eba\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u529e\u9152\u5e2d\u7684\u9152\u5e97\u5c31\u662f\u5c0f\u533a\u9644\u8fd1\uff0c\u6c5f\u6d9b\u592b\u5987\u65e9\u5c31\u53bb\u9152\u5e97\u51c6\u5907\uff0c\u5f20\u606a\u4ed6\u4eec\u4ece\u6885\u6797\u8fc7\u6765\uff0c\u4fbf\u5148\u5230\u6c5f\u9edb\u513f\u5bb6\u53bb\u3002\u9664\u4e86\u8bb8\u7ef4\u3001\u5b59\u9759\u6aac\u5979\u4eec\uff0c\u8fd8\u6709\u98de\u9e3f\u5e7f\u544a\u516c\u53f8\u7684\u8001\u603b\u656c\u98de\u9e3f\u4e0e\u98de\u9e3f\u5e7f\u544a\u516c\u53f8\u7684\u4e00\u4e9b\u540c\u4e8b\uff0c\u6c5f\u9edb\u513f\u5bb6\u7684\u4e00\u4e9b\u4eb2\u621a\u4e5f\u5728\u90a3\u91cc\uff0c\u5f20\u606a\u4ed6\u4eec\u4e5f\u627e\u4e0d\u5230\u7ad9\u811a\u7684\u5730\u65b9\uff0c\u51b3\u5b9a\u5148\u53bb\u9152\u5e97\u7b49\u7740\u3002</p><p>\u3000\u3000\u201c\u6628\u5929\u6c5f\u9edb\u513f\u4e5f\u5c31\u968f\u4fbf\u4e00\u8bf4\uff0c\u4f60\u8fd8\u771f\u6709\u8138\u8fc7\u6765\u559d\u5bff\u9152\uff1f\u201d\u8bb8\u7ef4\u7559\u4e0b\u6765\u966a\u6c5f\u9edb\u513f\u62db\u547c\u6c5f\u5bb6\u7684\u4eb2\u621a\uff0c\u5b59\u9759\u6aac\u8ddf\u5b59\u9759\u9999\u3001\u8bb8\u601d\u3001\u5f20\u606a\u6324\u4e00\u8f66\u91cc\u5148\u53bb\u9152\u5e97\uff0c\u656c\u98de\u9e3f\u4e0e\u5e7f\u544a\u516c\u53f8\u5176\u4ed6\u4eba\u5f00\u53e6\u4e00\u90e8\u8f66\uff0c\u5f20\u606a\u4ed6\u4eec\u8fc7\u6765\u559d\u5bff\u9152\uff0c\u8fd8\u771f\u662f\u6628\u5929\u9047\u5230\u6c5f\u9edb\u513f\u5979\u5988\u5988\u968f\u4fbf\u4e00\u8bf4\uff0c\u6ca1\u60f3\u5230\u5f20\u606a\u8fd8\u771f\u662f\u6709\u7a7a\uff0c\u62c9\u7740\u8bb8\u601d\u4e00\u8d77\u8fc7\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u53c8\u4e0d\u662f\u6ca1\u5305\u793c\uff0c\u96be\u9053\u5c31\u8981\u6bd4\u4f60\u81ea\u5351\u4e0d\u6210\uff1f\u201d</p><p>\u3000\u3000\u6c5f\u5bb6\u5728\u60e0\u5c71\u7684\u4eba\u8109\u4e0d\u9519\uff0c\u5c06\u4e00\u5bb6\u4e09\u661f\u7ea7\u9152\u5e97\u7684\u4e8c\u697c\u5927\u5385\u90fd\u5305\u4e86\u4e0b\u6765\uff0c\u6709\u4e8c\u5341\u516d\u4e03\u684c\u7684\u6837\u5b50\uff0c\u4e00\u4e9b\u91cd\u8981\u7684\u5ba2\u4eba\u90fd\u5236\u4f5c\u4e86\u5ea7\u5e2d\u724c\uff0c\u9646\u9646\u7eed\u7eed\u7684\u6709\u4e9b\u4eba\u5230\u4e86\uff0c\u5f20\u606a\u4ed6\u4eec\u7ed9\u5b89\u6392\u5728\u7d27\u90bb\u5927\u5385\u7684\u5305\u53a2\u91cc\u3002</p><p>\u3000\u3000\u5dee\u4e0d\u591a\u516d\u70b9\u949f\u5de6\u53f3\uff0c\u6c5f\u9edb\u513f\u95ea\u8eab\u8fdb\u6765\uff0c\u5f20\u606a\u89c1\u5979\u614c\u6025\u7684\u6837\u5b50\uff0c\u62ac\u5934\u95ee\uff1a\u201c\u89c1\u9b3c\u4e86\uff0c\u4f60\u8fd9\u6837\u5b50\uff1f\u201d</p><p>\u3000\u3000\u201c\u5dee\u4e0d\u591a\u89c1\u4e86\u9b3c\uff0c\u201d\u6c5f\u9edb\u513f\u6342\u7740\u80f8\u53e3\u5598\u606f\uff0c\u90a3\u53cc\u7edd\u7f8e\u7684\u7738\u5b50\u91cc\u5374\u6ca1\u6709\u614c\u4e71\uff0c\u6e05\u6f88\u660e\u4e3d\uff0c\u6e10\u6e10\u6709\u7740\u5706\u6da6\u81ea\u4fe1\u7684\u6c14\u8d28\uff0c\u4f7f\u5979\u7684\u5bb9\u8c8c\u6108\u52a0\u7684\u51fa\u4f17\uff0c\u7ea2\u5507\u8bf1\u4eba\u7684\u5507\u89d2\u6d6e\u51fa\u4e00\u4e1d\u4fcf\u76ae\u7684\u6d45\u7b11\uff0c\u8bb8\u7ef4\u5728\u540e\u9762\u6324\u8fdb\u6765\uff0c\u56de\u5934\u5f80\u95e8\u5916\u770b\u4e86\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u53f6\u5bb6\u5927\u5c11\u7237\u8ffd\u8fc7\u6765\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u76b1\u7740\u7709\u5934\uff0c\u6765\u60e0\u5c71\u4e4b\u524d\uff0c\u8fd8\u771f\u6ca1\u6709\u8003\u8651\u4f1a\u8ba8\u538c\u770b\u5230\u8c01\uff0c\u73b0\u5728\u60f3\u60f3\uff0c\u5c31\u7b97\u9047\u5230\u8c22\u5251\u5357\uff0c\u4e5f\u6bd4\u9047\u5230\u53f6\u9e3f\u8981\u597d\u3002</p><p>\u3000\u3000\u53f6\u9e3f\u63a8\u95e8\u63a2\u8fc7\u5934\u6765\uff0c\u4e00\u4f8b\u7684\u6cb9\u5934\u7c89\u9762\uff0c\u98d8\u5ffd\u7684\u773c\u5149\u5728\u5305\u53a2\u91cc\u626b\u4e86\u4e00\u5708\uff0c\u8bf4\u4e0d\u51fa\u7684\u8f7b\u6d6e\uff0c\u773c\u5149\u8f6e\u8fc7\u8bb8\u601d\u3001\u5b59\u9759\u9999\u59ca\u59b9\u8138\u4e0a\u65f6\uff0c\u4e5f\u77e5\u9053\u6536\u655b\u4e00\u4e0b\uff0c\u773c\u775b\u770b\u5230\u6c5f\u9edb\u513f\u8138\u4e0a\uff0c\u8868\u60c5\u5374\u53c8\u5341\u5206\u7684\u77eb\u63c9\u9020\u4f5c\uff1a\u201c\u9edb\u513f\uff0c\u4f60\u600e\u4e48\u5750\u5728\u8fd9\u91cc\uff1f\u5916\u9762\u4eba\u8fd9\u4e48\u4eb2\u670b\u597d\u53cb\uff0c\u4f60\u90fd\u8981\u62db\u547c\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u9edb\u513f\u521a\u8fdb\u6765\u6b47\u6b47\uff0c\u8981\u4e0d\u4f60\u4e5f\u8fdb\u6765\u5750\u5750\uff1f\u201d\u5f20\u606a\u624b\u6401\u5728\u6905\u80cc\u4e0a\uff0c\u76ef\u7740\u53f6\u9e3f\u8bf4\u3002</p><p>\u3000\u3000\u53f6\u9e3f\u5012\u662f\u60f3\u8fdb\u53bb\u5750\uff0c\u770b\u5230\u5f20\u606a\u4e0d\u5584\u7684\u773c\u795e\uff0c\u5fc3\u91cc\u4e00\u865a\uff0c\u8fde\u8c22\u5251\u5357\u5728\u4ed6\u624b\u91cc\u90fd\u683d\u4e86\u4e24\u56de\uff0c\u8baa\u8baa\u7684\u7b11\u4e86\u7b11\uff0c\u6ca1\u6709\u518d\u8bf4\u4ec0\u4e48\uff0c\u5c31\u9000\u4e86\u51fa\u53bb\u3002</p><p>\u3000\u3000\u770b\u7740\u53f6\u9e3f\u8d70\u51fa\u53bb\uff0c\u6c5f\u9edb\u513f\u624d\u8212\u4e86\u4e00\u53e3\u6c14\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u90fd\u4e0d\u6653\u5f97\u4ed6\u600e\u4e48\u6709\u4e9b\u5fe4\u5f20\u606a\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u601d\u62bf\u5634\u7b11\u7740\uff0c\u7ea8\u7ed4\u5b50\u5f1f\u81ea\u7136\u5f97\u7531\u7ea8\u7ed4\u5b50\u5f1f\u6765\u6574\u6cbb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8c22\u5251\u5357\u63a5\u5230\u53f6\u9e3f\u7684\u7535\u8bdd\uff0c\u542c\u8bf4\u5f20\u606a\u4e0e\u8bb8\u601d\u5230\u60e0\u5c71\u6765\u53c2\u52a0\u6c5f\u9edb\u513f\u7236\u4eb2\u7684\u5e86\u751f\u9152\uff0c\u4ed6\u5fc3\u91cc\u5341\u5206\u5947\u602a\uff0c\u53ef\u4ee5\u8bf4\u4ed6\u6bd4\u8c01\u90fd\u6e05\u695a\u5f20\u606a\u7684\u8eab\u4efd\u4e0e\u5730\u4f4d\uff0c\u5f20\u606a\u4e0e\u8bb8\u601d\u4e0d\u5927\u53ef\u80fd\u4e13\u7a0b\u62bd\u7a7a\u8fc7\u6765\u53c2\u52a0\u6c5f\u9edb\u513f\u7236\u4eb2\u7684\u5e86\u751f\u9152\u3002</p><p>\u3000\u3000\u5bf9\u8c22\u5251\u5357\u6765\u8bf4\uff0c\u5f20\u606a\u662f\u552f\u4e00\u51fa\u73b0\u5c31\u4f1a\u8ba9\u4ed6\u5fc3\u7eea\u4e0d\u5b81\u7684\u4eba\uff0c\u63a5\u5230\u53f6\u9e3f\u7684\u7535\u8bdd\uff0c\u4ed6\u5c31\u6ca1\u6709\u5b89\u4e0b\u5fc3\u6765\u3002\u5f20\u94c1\u68ee\u6253\u7535\u8bdd\u8fc7\u6765\uff0c\u8c22\u5251\u5357\u770b\u7740\u624b\u673a\u5c4f\u5e55\u4e0a\u7684\u663e\u793a\uff0c\u60f3\u8ba9\u624b\u673a\u5c31\u8fd9\u4e48\u54cd\u7740\uff0c\u8fdf\u7591\u4e86\u4e00\u4f1a\u513f\uff0c\u8fd8\u662f\u63a5\u901a\u7535\u8bdd\u3002</p><p>\u3000\u3000\u53f6\u5bb6\u7684\u76db\u946b\u73af\u7403\u7535\u5668\u516c\u53f8\u662f\u6d77\u6cf0\u7684\u6f5c\u5728\u7ade\u4e89\u5bf9\u624b\uff0c\u8c22\u5251\u5357\u5bf9\u53f6\u5efa\u658c\u7684\u4e86\u89e3\uff0c\u81ea\u7136\u4e0d\u662f\u53f6\u6c5f\u5929\u3001\u5f20\u94c1\u68ee\u4ed6\u4eec\u80fd\u6bd4\u7684\uff0c\u4ed6\u5728\u7535\u8bdd\u542c\u5f20\u94c1\u68ee\u63d0\u8d77\u90a3\u8f86\u5965\u8fea\u4e0eVs40\uff0c\u77ac\u65f6\u5c31\u60f3\u5230\u53f6\u5efa\u658c\u4e0e\u5f20\u606a\uff0c\u4f55\u51b5\u53f6\u9e3f\u5df2\u7ecf\u770b\u5230\u5f20\u606a\u5c31\u5728\u60e0\u5c71\u5e02\u3002</p><p>\u3000\u3000\u9676\u884c\u5065\u662f\u53bb\u5e74\u963b\u6b62\u4e09\u661f\u3001\u9999\u96ea\u6d77\u5408\u8d44\u6848\u7684\u5173\u952e\u4eba\u7269\uff0c\u8c22\u5251\u5357\u6ca1\u6709\u89c1\u8fc7\uff0c\u4f46\u542c\u4ed6\u5988\u591a\u6b21\u63d0\u5230\u8fc7\uff0c\u8fd9\u6837\u4e00\u4e2a\u4eba\u4e0e\u53f6\u5efa\u658c\u3001\u5f20\u606a\u8d70\u5230\u4e00\u8d77\uff0c\u53c8\u51fa\u73b0\u5728\u60e0\u5c71\u5e02\uff0c\u4ee4\u4eba\u5bfb\u5473\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u867d\u7136\u5f88\u4e0d\u559c\u6b22\u5f20\u94c1\u68ee\u8fd9\u4e2a\u4eba\uff0c\u4f46\u4ed6\u66f4\u4e0d\u559c\u6b22\u5f20\u606a\uff0c\u5f20\u606a\u7684\u884c\u8e2a\u592a\u8010\u4eba\u5bfb\u5473\uff0c\u4ed6\u8fd9\u6b21\u5012\u6ca1\u7684\u627e\u501f\u53e3\u63a8\u8f9e\uff0c\u76f4\u63a5\u53bb\u4f1a\u6240\u4e0e\u5f20\u94c1\u68ee\u3001\u53f6\u6c5f\u5929\u78b0\u5934\u3002</p><p>\u3000\u3000\u201c\u4ec0\u4e48\uff0c\u9676\u884c\u5065\u600e\u4e48\u4f1a\u8ddf\u76db\u946b\u4e0e\u7231\u8fbe\u7535\u5b50\u7684\u4eba\u6df7\u5728\u4e00\u8d77\uff1f\u201d\u5f20\u94c1\u68ee\u542c\u8c22\u5251\u5357\u4ecb\u7ecd\u5f20\u606a\u3001\u53f6\u5efa\u658c\u4e24\u4eba\u7684\u80cc\u666f\uff0c\u8fd8\u771f\u662f\u5403\u4e86\u4e00\u60ca\u3002</p><p>\u3000\u3000\u5bf9\u53f6\u6c5f\u5929\u6765\u8bf4\uff0c\u4e8b\u4e0d\u5173\u5df1\uff0c\u5012\u662f\u8f7b\u677e\u4e00\u4e9b\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8bf4\u4e0d\u5b9a\u4eba\u5bb6\u6b63\u597d\u7ecf\u8fc7\u60e0\u5c71\u8ba9\u5f20\u5904\u957f\u770b\u89c1\uff0c\u6ca1\u5fc5\u8981\u98ce\u58f0\u9e64\u5533\u3001\u8349\u6728\u7686\u5175\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u8bf4\u9053\uff1a\u201c\u9676\u884c\u5065\u53bb\u5e74\u5c06\u6750\u6599\u4f20\u5230\u7701\u957f\u5f90\u5b66\u5e73\u624b\u91cc\u7684\u8fc7\u7a0b\u6709\u4e9b\u8e4a\u8df7\uff0c\u8bf4\u662f\u7ecf\u8d38\u59d4\u4e0e\u65b0\u534e\u5206\u793e\u4f1a\u7684\u4e24\u4e2a\u5c0f\u9752\u5e74\u6345\u7bd3\u5b50\uff0c\u624d\u5c06\u6750\u6599\u6345\u5230\u5f90\u5b66\u5e73\u90a3\u91cc\uff0c\u4e0d\u8fc7\u8981\u544a\u8bc9\u4f60\u4eec\u4e00\u58f0\uff0c\u8fd9\u4e2a\u5f20\u606a\u4e0e\u5f90\u5b66\u5e73\u7684\u5173\u7cfb\u53ef\u4e0d\u4e00\u822c\uff0c\u8fd9\u80cc\u540e\u672a\u514d\u592a\u5de7\u5408\u4e86\u3002\u201d\u8c22\u5251\u5357\u4e5f\u4e0d\u786e\u5b9a\u5f20\u606a\u662f\u4e0d\u662f\u85cf\u7740\u522b\u7684\u610f\u56fe\uff0c\u6311\u5506\u5f20\u94c1\u68ee\u4e0e\u5f20\u606a\u4e3a\u654c\u4e5f\u672a\u5fc5\u80fd\u8d77\u4ec0\u4e48\u5927\u4f5c\u7528\uff0c\u53ea\u662f\u4e60\u60ef\u6027\u7684\u4f5c\u51fa\u4e0d\u5229\u5f20\u606a\u7684\u63a8\u65ad\u3002</p><p>\u3000\u3000\u9664\u4e86\u4e24\u6b21\u7838\u8f66\u4e8b\u4ef6\u4ee5\u6765\uff0c\u8fd8\u6709\u5f88\u591a\u7684\u4e8b\u60c5\u8ba9\u8c22\u5251\u5357\u803f\u803f\u4e8e\u6000\u3002\u4e09\u6708\u521d\u5728\u6d77\u5dde\u770b\u5230\u5f20\u606a\u5f00\u7740\u94f6\u7070\u8272\u7684Vs40\u8dd1\u8f66\u51fa\u73b0\u5728\u5e9c\u5929\u5927\u53a6\u80cc\u540e\u7684\u505c\u8f66\u573a\uff0c\u8c22\u5251\u5357\u5c31\u6253\u7535\u8bdd\u8ddf\u8c22\u5b50\u5609\u786e\u8ba4\u5f20\u606a\u5c31\u662f\u9648\u9759\u4e0e\u5b50\u5609\u5728\u9ad8\u901f\u8def\u4e0a\u9047\u5230\u90a3\u4e2a\u4eba\uff0c\u4ed6\u60f3\u4e0d\u660e\u767d\u5f20\u606a\u4e3a\u4ec0\u4e48\u4f1a\u5728\u5b50\u5609\u3001\u9648\u9759\u9762\u524d\u88c5\u804b\u4f5c\u54d1\uff0c\u66f4\u60f3\u4e0d\u660e\u767d\u4e3a\u4ec0\u4e48\u9648\u9759\u5728\u4ed6\u9762\u524d\u4f1a\u88c5\u4f5c\u4e0e\u5f20\u606a\u7d20\u4e0d\u76f8\u8bc6\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u662f\u90a3\u79cd\u6709\u60f3\u660e\u767d\u5374\u7edd\u4e0d\u4f1a\u628a\u4e0d\u660e\u767d\u8868\u9732\u5728\u8138\u4e0a\u7684\u4eba\uff0c\u4f46\u662f\u603b\u4e0d\u53ef\u80fd\u5f53\u4ec0\u4e48\u4e8b\u60c5\u90fd\u6ca1\u6709\u53d1\u751f\u8fc7\u3002</p><p>\u3000\u3000\u8fd9\u5176\u4e2d\u7684\u8fc7\u8282\u8d8a\u7ea0\u7f20\u8d8a\u6df1\u4e86\uff0c\u8c22\u665a\u6674\u65e2\u7136\u5df2\u7ecf\u4e0d\u518d\u628a\u81ea\u5df1\u770b\u6210\u662f\u8c22\u5bb6\u4eba\uff0c\u8c22\u5251\u5357\u624d\u4e0b\u5b9a\u51b3\u5b9a\u652f\u6301\u8c22\u77bb\u5230\u6d77\u5dde\u8d1f\u8d23\u4e0e\u9526\u57ce\u96c6\u56e2\u7684\u5408\u4f5c\uff0c\u4e5f\u7b97\u662f\u5f7b\u5e95\u65ad\u7edd\u4e86\u4e0e\u8c22\u665a\u6674\u7684\u5173\u7cfb\u3002</p><p>\u3000\u3000\u867d\u7136\u8fd9\u4e8b\u60c5\u770b\u4e0a\u53bb\u4e0e\u4ed6\u65e0\u5173\uff0c\u4f46\u662f\u53ea\u8981\u6d89\u53ca\u5f20\u606a\uff0c\u8c22\u5251\u5357\u4fbf\u4f1a\u5f53\u6210\u81ea\u5df1\u7684\u4e8b\u60c5\u6765\u5bf9\u5f85\uff0c\u53f6\u9e3f\u65e2\u7136\u5728\u53c2\u4e0e\u6c5f\u9edb\u513f\u7236\u4eb2\u7684\u5e86\u751f\u9152\u5bb4\uff0c\u8c22\u5251\u5357\u4fbf\u6253\u7535\u8bdd\u7ed9\u4ed6\uff0c\u8ba9\u4ed6\u53bb\u770b\u8fd8\u6709\u8c01\u8ddf\u5f20\u606a\u4e00\u8d77\u5230\u60e0\u5c71\u6765\u3002\u5f97\u77e5\u9676\u884c\u5efa\u4e0e\u53f6\u5efa\u658c\u5e76\u6ca1\u6709\u8ddf\u5f20\u606a\u5728\u4e00\u8d77\uff0c\u800c\u662f\u5b59\u6c0f\u59d0\u59b9\u5728\u573a\uff0c\u53e6\u5916\u6709\u5bb6\u5e7f\u544a\u516c\u53f8\u7684\u4eba\u5012\u663e\u5f97\u65e0\u5173\u7d27\u8981\u3002</p></article>"}}}]);
