(window.webpackJsonp=window.webpackJsonp||[]).push([[343],{365:function(p){p.exports={chapterTitle:null,title:"\u5e8f\u7ae0 \u65e0\u8def\u53ef\u9000\uff08\u4e0b\uff09",content:"<article><h2>\u5e8f\u7ae0 \u65e0\u8def\u53ef\u9000\uff08\u4e0b\uff09</h2><p>\u6b4c\u560e\u65af\u4fef\u89c6\u4e0b\u65b9\uff0c\u89c1\u90a3\u5ea7\u5de8\u5927\u7684\u91d1\u5c5e\u5821\u5792\u6b63\u88ab\u6b7b\u4ea1\u9a91\u58eb\u7262\u7262\u62a4\u536b\uff0c\u7c07\u62e5\u7740\u7f13\u7f13\u524d\u8fdb\uff0c\u516b\u4f4d\u5deb\u5996\u7ad9\u5728\u5821\u5792\u7684\u516b\u4e2a\u51f8\u51fa\u5c16\u89d2\u4e0a\uff0c\u624b\u4e2d\u7684\u9ab7\u9ac5\u6cd5\u6756\u53d1\u51fa\u9ed1\u8272\u7684\u7075\u5149\uff0c\u5411\u4e3b\u5e05\u793a\u610f\u4e00\u5207\u6b63\u5e38\u3002\u6b4c\u560e\u65af\u653e\u4e0b\u5fc3\u6765\uff0c\u5c06\u76ee\u5149\u6295\u5411\u9065\u8fdc\u7684\u524d\u65b9\u3002</p><p>\u4ed6\u6e05\u695a\u5730\u770b\u89c1\u9b54\u9b3c\u7684\u519b\u961f\u5df2\u7ecf\u5217\u9635\u5b8c\u6bd5\uff0c\u6240\u6709\u58eb\u5175\u5404\u5c31\u5404\u4f4d\uff0c\u957f\u67aa\u4f3c\u6797\uff0c\u906e\u5929\u853d\u65e5\uff0c\u94e0\u7532\u5982\u955c\uff0c\u8000\u76ee\u751f\u8f89\uff0c\u4ed6\u770b\u89c1\u62dc\u5c14\u4ee5\u4eba\u7c7b\u5f62\u6001\u7ad9\u5728\u57ce\u5899\u4e0a\uff0c\u6b63\u5728\u53d1\u8868\u6f14\u8bf4\u9f13\u821e\u58eb\u6c14\u3002\u6b4c\u560e\u65af\u77e5\u9053\u56e0\u4e3a\u6709\u963f\u65af\u8482\u6469\u65af\u548c\u516b\u9b54\u5c06\u7684\u652f\u6301\uff0c\u62dc\u5c14\u6709\u6743\u5f81\u53ec\u5730\u72f1\u524d\u516b\u5c42\u5404\u5927\u9b54\u9b3c\u516c\u7235\u7684\u90e8\u961f\uff08\u9664\u4e86\u76f4\u7cfb\u8fd1\u536b\uff09\uff0c\u4ed6\u7684\u6218\u58eb\u6765\u6e90\u4e8e\u5730\u72f1\u4e2d\u6700\u7cbe\u9510\u7684\u9b54\u9b3c\uff0c\u6218\u529b\u975e\u51e1\uff0c\u4f46\u4ed6\u6765\u56de\u626b\u89c6\uff0c\u5fc3\u4e2d\u6cdb\u8d77\u4e00\u4e1d\u7591\u60d1\uff0c\u56e0\u4e3a\u4ed6\u6ca1\u770b\u5230\u771f\u6b63\u9884\u6599\u4e2d\u7684\u5bf9\u624b\u3002</p><p>\u201c\u9eef\u4e4b\u7ffc\u5728\u54ea\u91cc\uff1f\u201d\u4ed6\u4f4e\u58f0\u8be2\u95ee\u81ea\u5df1\u3002</p><p>\u7531\u4e0d\u5f97\u591a\u60f3\uff0c\u773c\u770b\u4ea1\u7075\u519b\u961f\u5df2\u7ecf\u9760\u8fd1\u524d\u7ebf\uff0c\u6b4c\u560e\u65af\u4ece\u9634\u5f71\u7ffc\u9f99\u80cc\u4e0a\u62ff\u8d77\u4e00\u53ea\u5f2f\u66f2\u53f7\u89d2\uff0c\u545c\u545c\u5439\u54cd\u8d77\u6765\u3002</p><p>\u201c\u8fdb\u653b\uff01\u201d\u4ed6\u9ad8\u58f0\u8bf4\u3002</p><p>\u4e0e\u6b64\u540c\u65f6\uff0c\u9752\u94dc\u57ce\u5821\u7684\u57ce\u5934\u4e0a\uff0c\u62dc\u5c14\u6572\u54cd\u4e86\u6218\u9f13\u3002</p><p>\u4e24\u652f\u519b\u961f\u50cf\u4e24\u9053\u6c79\u6d8c\u7684\u6d2a\u6d41\u649e\u51fb\u5728\u4e00\u8d77\uff0c\u5f88\u5feb\u9b54\u9b3c\u4e00\u65b9\u5c31\u5360\u636e\u4e86\u4e0a\u98ce\uff0c\u5728\u8fd9\u79cd\u5927\u89c4\u6a21\u6218\u4e89\u4e2d\uff0c\u4e2a\u4eba\u7684\u529b\u91cf\u5f3a\u5f31\u53d8\u5f97\u5fae\u4e0d\u8db3\u9053\uff0c\u771f\u6b63\u8d77\u51b3\u5b9a\u4f5c\u7528\u7684\u662f\u58eb\u6c14\u3001\u88c5\u5907\u3001\u56e2\u7ed3\u534f\u4f5c\u548c\u968f\u673a\u5e94\u53d8\u7684\u6307\u6325\uff0c\u8fd9\u4e9b\u5168\u90fd\u662f\u9b54\u9b3c\u7684\u5f3a\u9879\uff0c\u800c\u6076\u9b54\u4eec\u552f\u4e00\u7684\u6570\u91cf\u4f18\u52bf\u6b64\u65f6\u4e5f\u4e0d\u5177\u5907\u3002\u4e0d\u5230\u534a\u4e2a\u5c0f\u65f6\uff0c\u6076\u9b54\u519b\u961f\u4fbf\u6e83\u6563\u4e86\uff0c\u56db\u6563\u9003\u8dd1\u3002\u9b54\u9b3c\u6ca1\u6709\u4e58\u80dc\u8ffd\u51fb\uff0c\u800c\u662f\u91cd\u6574\u9635\u52bf\uff0c\u7f13\u7f13\u524d\u8fdb\uff0c\u8fce\u4e0a\u4e86\u6076\u9b54\u540e\u65b9\u7684\u4ea1\u7075\u519b\u56e2\u3002</p><p>\u5012\u94a9\u9b54\u6325\u821e\u5de8\u5251\uff0c\u6467\u67af\u62c9\u673d\u5730\u65a9\u6740\u7740\u50f5\u5c38\u548c\u9ab7\u9ac5\uff0c\u9aa8\u9b54\u67aa\u9635\u62b5\u4f4f\u6ce2\u8fbe\u5c38\u548c\u5c38\u5996\uff0c\u9ab8\u9aa8\u4e9a\u9f99\u51cc\u7a7a\u4e0b\u6251\uff0c\u94fe\u9b54\u6325\u821e\u7740\u94c1\u94fe\u594b\u529b\u8fd8\u51fb\u3002\u51b0\u9b54\u9a91\u7740\u68a6\u9b47\u51b2\u5165\u4ea1\u7075\u5927\u519b\u4e2d\uff0c\u7528\u95ea\u7535\u548c\u70c8\u7130\u4ea4\u7ec7\u6210\u7684\u957f\u5200\u8ba9\u8fd9\u4e9b\u4e0d\u6b7b\u7684\u90aa\u6076\u518d\u5ea6\u6c89\u7720\u3002\u6b7b\u4ea1\u9a91\u58eb\u4f9d\u65e7\u62a4\u536b\u7740\u91d1\u5c5e\u5821\u5792\u7f13\u7f13\u524d\u8fdb\uff0c\u5e76\u4e0d\u53c2\u6218\u3002</p><p>\u4ea1\u7075\u6ee1\u5c71\u904d\u91ce\u800c\u6765\uff0c\u5360\u636e\u4e86\u5175\u529b\u4e0a\u7684\u7edd\u5bf9\u4f18\u52bf\uff0c\u9b54\u9b3c\u51ed\u501f\u9635\u578b\u6b7b\u6b7b\u62b5\u5fa1\uff0c\u6e10\u6e10\u6709\u4e0d\u652f\u7684\u8ff9\u8c61\u3002\u57ce\u5899\u4e0a\u7684\u62dc\u5c14\u89c1\u72b6\uff0c\u957f\u5200\u6325\u52a8\uff0c\u53d1\u51fa\u4e00\u4e2a\u547d\u4ee4\uff0c\u4e00\u961f\u6df1\u72f1\u70bc\u9b54\u51fa\u73b0\u5728\u4ed6\u8eab\u540e\uff0c\u968f\u5373\u6d88\u5931\uff0c\u4e0b\u4e00\u77ac\u95f4\u5df2\u7ecf\u7ad9\u5728\u6218\u573a\u4e0a\u7a7a\u3002\u4e00\u9053\u9053\u71c3\u70e7\u7684\u706b\u5899\u9aa4\u7136\u4ece\u5730\u9762\u5347\u817e\u8d77\u6765\uff0c\u5c06\u4ea1\u7075\u5927\u519b\u5207\u5272\u6210\u51e0\u5341\u5757\uff0c\u6a58\u7ea2\u8272\u7684\u5de8\u5927\u706b\u7403\u4ece\u6df1\u72f1\u70bc\u9b54\u624b\u4e2d\u8fde\u73e0\u53d1\u51fa\uff0c\u8f70\u70b8\u7740\u4e8b\u5148\u770b\u51c6\u7684\u76ee\u6807\u2014\u2014\u5c24\u5176\u662f\u90a3\u4e9b\u6728\u4e43\u4f0a\uff0c\u8fd9\u79cd\u4ea1\u7075\u5f88\u96be\u88ab\u5200\u5251\u67aa\u77db\u4f24\u5bb3\uff0c\u4f46\u975e\u5e38\u754f\u60e7\u706b\u7130\uff0c\u5b83\u4eec\u8eab\u4e0a\u7f20\u6ee1\u7684\u7ef7\u5e26\u6d78\u900f\u5c38\u6cb9\uff0c\u80fd\u591f\u63d0\u4f9b\u4f18\u826f\u7684\u7269\u7406\u9632\u5fa1\u80fd\u529b\uff0c\u540c\u65f6\u5374\u4e5f\u662f\u6700\u4f73\u7684\u5bfc\u706b\u7d22\u3002</p><p>\u70bc\u9b54\u75af\u72c2\u5730\u53d1\u5c04\u706b\u7403\uff0c\u4ed6\u4eec\u5e76\u4e0d\u62c5\u5fc3\u8bef\u4f24\u53cb\u519b\uff0c\u9b54\u9b3c\u4eec\u901a\u5e38\u90fd\u5bf9\u706b\u7130\u6709\u826f\u597d\u7684\u62b5\u6297\u80fd\u529b\uff0c\u53ea\u6709\u8fd9\u4e9b\u4ea1\u7075\u4eec\u624d\u9700\u8981\u62c5\u5fc3\u3002\u5927\u91cf\u7684\u6728\u4e43\u4f0a\u88ab\u70e7\u6b7b\uff0c\u9b54\u9b3c\u519b\u961f\u7684\u538b\u529b\u7a0d\u7a0d\u51cf\u8f7b\uff0c\u4f46\u5e76\u6ca1\u6709\u771f\u6b63\u597d\u8f6c\uff0c\u56e0\u4e3a\u4ea1\u7075\u4f9d\u65e7\u5f88\u591a\u3002\u9ab8\u9aa8\u4e9a\u9f99\u653e\u5f03\u4e86\u539f\u672c\u7684\u76ee\u6807\uff0c\u8f6c\u800c\u653b\u51fb\u6df1\u72f1\u70bc\u9b54\uff0c\u5b83\u4eec\u7684\u6570\u91cf\u4eff\u4f5b\u65e0\u7a77\u65e0\u5c3d\uff0c\u91cd\u91cd\u56f4\u56f0\uff0c\u524d\u8d74\u540e\u7ee7\u3002</p><p>\u6c89\u95f7\u7684\u9a6c\u8e44\u5954\u817e\u58f0\u9707\u64bc\u5927\u5730\uff0c\u6b7b\u4ea1\u9a91\u58eb\u7ec8\u4e8e\u524d\u6765\uff0c\u4ed6\u4eec\u5171\u6709\u4e03\u5343\u4eba\uff0c\u5176\u4e2d\u56db\u5343\u4f9d\u65e7\u7ec4\u6210\u65b9\u9635\uff0c\u5b88\u536b\u7740\u91d1\u5c5e\u5821\u5792\uff0c\u53e6\u5916\u4e09\u5343\u6740\u5165\u6218\u56e2\u3002\u6b7b\u4ea1\u9a91\u58eb\u7684\u52a0\u5165\uff0c\u8ba9\u539f\u672c\u4fbf\u5df2\u7ecf\u4e0d\u5e73\u8861\u7684\u5c40\u52bf\u8fc5\u901f\u503e\u659c\uff0c\u9b54\u9b3c\u7684\u9635\u7ebf\u5728\u51e0\u5206\u949f\u540e\u5b8c\u5168\u5d29\u6e83\u3002</p><p>\u6b4c\u560e\u65af\u6ee1\u610f\u5730\u770b\u5230\u8fd9\u79cd\u6218\u679c\uff0c\u4e00\u5207\u90fd\u5728\u4ed6\u7684\u610f\u6599\u4e4b\u4e2d\uff0c\u9b54\u9b3c\u4eec\u867d\u7136\u62e5\u6709\u5404\u79cd\u6218\u7565\u6218\u672f\u4e0a\u7684\u4f18\u52bf\uff0c\u4f46\u90a3\u53c8\u5982\u4f55\u5462\uff1f\u5728\u7edd\u5bf9\u60ac\u6b8a\u7684\u529b\u91cf\u9762\u524d\uff0c\u4e00\u5207\u90fd\u662f\u5fae\u4e0d\u8db3\u9053\u7684\u3002\u529b\u91cf\uff0c\u552f\u6709\u529b\u91cf\u624d\u662f\u672c\u8d28\uff0c\u552f\u6709\u529b\u91cf\u624d\u662f\u771f\u8c1b\uff0c\u552f\u6709\u529b\u91cf\u624d\u662f\u4e00\u5207\u3002</p><p>\u867d\u7136\u81ea\u5df1\u662f\u5438\u8840\u9b3c\uff0c\u4f46\u5728\u6df1\u6e0a\u4e2d\u751f\u6d3b\u6570\u5343\u5e74\uff0c\u6b4c\u560e\u65af\u65e9\u5c31\u5df2\u7ecf\u63a5\u53d7\u4e86\u6076\u9b54\u7684\u89c2\u5ff5\uff0c\u751a\u81f3\u628a\u81ea\u5df1\u5f53\u4f5c\u6076\u9b54\u7684\u4e00\u5458\u3002\u4ed6\u6253\u5fc3\u773c\u91cc\u770b\u4e0d\u8d77\u90a3\u4e9b\u4ece\u4e5d\u5c42\u5730\u72f1\u91cc\u722c\u51fa\u6765\u7684\u9b54\u9b3c\uff0c\u4f4e\u7ea7\u3001\u80c6\u602f\u3001\u4e0d\u582a\u4e00\u51fb\uff0c\u606a\u5b88\u4e00\u4e9b\u83ab\u540d\u5176\u5999\u7684\u89c4\u77e9\uff0c\u53ea\u4f1a\u8eb2\u5728\u6697\u5904\u73a9\u5f04\u9634\u8c0b\u8be1\u8ba1\uff0c\u6240\u6709\u7684\u8fd9\u4e00\u5207\uff0c\u90fd\u8bc1\u660e\u4e86\u9b54\u9b3c\u4eec\u7684\u5f31\u5c0f\u672c\u8d28\u3002</p><p>\u201c\u6211\u4eec\u8d62\u4e86\u3002\u201d\u4ed6\u60f3\u3002</p><p>\u9b54\u9b3c\u519b\u961f\u8bad\u7ec3\u6709\u7d20\uff0c\u8f6c\u8fdb\u5f97\u98de\u5feb\uff0c\u4ea1\u7075\u5927\u591a\u884c\u52a8\u8fdf\u7f13\uff0c\u8ffd\u4e4b\u4e0d\u53ca\uff0c\u6b7b\u4ea1\u9a91\u58eb\u867d\u7136\u884c\u52a8\u8fc5\u6377\uff0c\u4f46\u88ab\u6ee1\u5c71\u904d\u91ce\u7684\u50f5\u5c38\u9ab7\u9ac5\u6321\u7740\uff0c\u901f\u5ea6\u4e5f\u5b8c\u5168\u53d1\u6325\u4e0d\u51fa\u6765\u3002\u5f53\u4ea1\u7075\u4eec\u8d76\u5230\u9752\u94dc\u57ce\u5821\u7684\u57ce\u5899\u4e0b\u65f6\uff0c\u9b54\u9b3c\u65e9\u5c31\u5df2\u7ecf\u5168\u4f53\u8fdb\u57ce\u4e86\u3002\u5bf9\u4e8e\u8fd9\u79cd\u7ed3\u679c\uff0c\u6b4c\u560e\u65af\u65e9\u6709\u5fc3\u7406\u51c6\u5907\uff0c\u5e76\u4e0d\u5931\u671b\uff0c\u4ed6\u62ff\u8d77\u53f7\u89d2\uff0c\u6b63\u51c6\u5907\u4e0b\u4ee4\u653b\u57ce\uff0c\u9661\u7136\u611f\u89c9\u5934\u9876\u4e0a\u4e00\u6697\u3002</p><p>\u4f5c\u4e3a\u5438\u8840\u9b3c\uff0c\u6b4c\u560e\u65af\u5bf9\u4e8e\u5149\u4eae\u6709\u7740\u8d85\u4e4e\u5bfb\u5e38\u7684\u654f\u611f\uff0c\u4efb\u4f55\u7ec6\u5fae\u7684\u5149\u5f71\u53d8\u5316\u90fd\u9003\u4e0d\u8fc7\u4ed6\u7684\u773c\u775b\u3002\u51e0\u4e4e\u662f\u672c\u80fd\uff0c\u4ed6\u611f\u5e94\u5230\u4e86\u5371\u9669\u7684\u903c\u8fd1\uff0c\u7136\u540e\u773c\u524d\u5149\u534e\u95ea\u70c1\uff0c\u4e00\u652f\u707f\u91d1\u8272\u957f\u7bad\u671d\u4ed6\u6fc0\u5c04\u800c\u6765\u3002</p><p>\u78a7\u706b\u83b9\u83b9\u7684\u957f\u5200\u6325\u51fa\uff0c\u9694\u5f00\u91d1\u7bad\uff0c\u6b4c\u560e\u65af\u4e00\u4e2a\u7ffb\u8eab\u8dc3\u4e0b\u7ffc\u9f99\uff0c\u671d\u5730\u9762\u6025\u901f\u5760\u53bb\u3002\u4ed6\u5e76\u4e0d\u62c5\u5fc3\u4f1a\u6454\u6b7b\uff0c\u4f5c\u4e3a\u201c\u4eb2\u738b\u201d\u4f4d\u9636\u7684\u5438\u8840\u9b3c\uff0c\u5c31\u7b97\u4ece\u4e07\u91cc\u9ad8\u7a7a\u6454\u4e0b\u4e5f\u80fd\u591f\u6beb\u53d1\u65e0\u4f24\u3002\u773c\u770b\u8981\u649e\u4e0a\u5730\u9762\uff0c\u4ed6\u9661\u7136\u5168\u8eab\u6536\u7f29\uff0c\u5316\u4f5c\u4e00\u53ea\u5de8\u5927\u7684\u9ed1\u8272\u8759\u8760\uff0c\u7fe9\u7136\u6ed1\u7fd4\uff0c\u843d\u5230\u6b7b\u4ea1\u9a91\u58eb\u62a4\u536b\u7684\u90a3\u5ea7\u91d1\u5c5e\u5821\u5792\u4e0a\uff0c\u91cd\u65b0\u6062\u590d\u4eba\u5f62\uff0c\u62ac\u5934\u5411\u5929\u4e0a\u770b\u53bb\u3002</p><p>\u8fd1\u767e\u540d\u5815\u5929\u4f7f\u81ea\u4e4c\u4e91\u4e2d\u98de\u51fa\uff0c\u4ed6\u4eec\u7684\u9762\u5bb9\u4fca\u7f8e\uff0c\u795e\u60c5\u5a01\u4e25\uff0c\u6d01\u767d\u7684\u957f\u888d\u4f9d\u65e7\u7cbe\u81f4\u534e\u4e3d\uff0c\u7ea4\u5c18\u4e0d\u67d3\uff0c\u5728\u80f8\u53e3\u7684\u90e8\u4f4d\u90fd\u7ee3\u4e0a\u4e86\u5229\u9f7f\u54ac\u5408\u94a2\u5200\u7684\u5fbd\u8bb0\uff0c\u90a3\u662f\u62dc\u5c14\u7684\u6807\u5fd7\u3002\u56e0\u4e3a\u5815\u843d\uff0c\u8c61\u5f81\u5723\u6d01\u7684\u96ea\u767d\u7fbd\u7ffc\u5df2\u7ecf\u53d8\u5f97\u7070\u9eef\uff0c\u539f\u672c\u91d1\u8272\u7684\u53cc\u773c\u53d8\u5f97\u4e4c\u84dd\uff0c\u4ed6\u4eec\u7684\u5bd2\u51b0\u53cc\u624b\u5251\u4e0a\u4f9d\u65e7\u70c8\u7130\u541e\u5410\uff0c\u96f7\u9706\u73af\u7ed5\uff0c\u4f46\u5df2\u7ecf\u6ca1\u6709\u4e86\u4ee4\u6bcf\u4e2a\u4ea1\u7075\u90fd\u4e0d\u7531\u81ea\u4e3b\u5fc3\u60ca\u80c6\u6218\u7684\u5723\u5149\u3002</p><p>\u5815\u5929\u4f7f\u519b\u56e2\u201c\u9eef\u4e4b\u7ffc\u201d\uff0c\u62dc\u5c14\u771f\u6b63\u7684\u4e09\u5927\u5ae1\u7cfb\u519b\u961f\u4e4b\u4e00\u3002</p><p>\u203b\u203b\u203b</p><p>\u9ab7\u9ac5\u6218\u9a6c\u56db\u8e44\u751f\u706b\uff0c\u8f7d\u7740\u6b7b\u4ea1\u9a91\u58eb\u4eec\u817e\u7a7a\u800c\u8d77\uff0c\u5411\u5815\u5929\u4f7f\u53d1\u52a8\u51b2\u950b\u3002\u6b4c\u560e\u65af\u6025\u4fc3\u5730\u53d1\u51fa\u4e00\u4e2a\u547d\u4ee4\uff0c\u7ad9\u5728\u91d1\u5c5e\u5821\u5792\u516b\u4e2a\u51f8\u89d2\u4e0a\u7684\u5deb\u5996\u4eec\u540c\u65f6\u4e3e\u8d77\u6cd5\u6756\uff0c\u5404\u81ea\u5ff5\u8bf5\u51fa\u4e00\u6bb5\u542b\u6df7\u6666\u6da9\u7684\u5492\u8bed\u3002</p><p>\u5468\u56f4\u7684\u7a7a\u6c14\u6e29\u5ea6\u5239\u90a3\u95f4\u6025\u5267\u964d\u4f4e\uff0c\u4e00\u4e2a\u5e9e\u5927\u65e0\u5339\u7684\u8eab\u5f71\u5728\u91d1\u5c5e\u5821\u5792\u7684\u4e0a\u65b9\u51fa\u73b0\u3002</p><p>\u4e00\u5f00\u59cb\u53ea\u662f\u865a\u5f71\uff0c\u77ed\u77ed\u51e0\u79d2\u79cd\u5185\u4fbf\u5df2\u7ecf\u51dd\u805a\u6210\u4e86\u5b9e\u4f53\u3002\u5982\u679c\u4ec5\u770b\u8f6e\u5ed3\uff0c\u90a3\u662f\u4e00\u4e2a\u6cf0\u5766\u822c\u7684\u5de8\u4eba\uff0c\u5373\u4fbf\u6700\u9ad8\u5927\u7684\u708e\u9b54\u7ad9\u5728\u65c1\u8fb9\u4e5f\u53ea\u80fd\u53ca\u5230\u4ed6\u7684\u819d\u76d6\uff0c\u4f46\u5982\u679c\u4ed4\u7ec6\u89c2\u5bdf\u7684\u8bdd\uff0c\u4fbf\u4f1a\u53d1\u73b0\u4ed6\u662f\u4e2a\u4e11\u964b\u7684\u602a\u7269\uff0c\u4ed6\u7684\u5934\u9885\u5f88\u5c0f\uff0c\u5634\u5374\u975e\u5e38\u5927\uff0c\u53cc\u81c2\u957f\u77ed\u4e0d\u4e00\uff0c\u4e24\u817f\u7c97\u7ec6\u4e0d\u5300\uff0c\u4e0a\u8eab\u957f\u800c\u4e0b\u8eab\u77ed\uff0c\u751a\u81f3\u8fde\u76ae\u80a4\u7684\u989c\u8272\u90fd\u6591\u9a73\u6210\u5757\uff0c\u5404\u4e0d\u76f8\u540c\uff0c\u6574\u4e2a\u4eba\u770b\u8d77\u6765\u5c31\u50cf\u662f\u4e2a\u7f1d\u5408\u602a\uff0c\u53c8\u50cf\u662f\u4e2a\u5236\u4f5c\u4f4e\u52a3\u7684\u8089\u9b54\u50cf\uff0c\u7528\u4e0d\u540c\u7684\u5c38\u5757\u52c9\u5f3a\u62fc\u51d1\u8d77\u6765\u3002</p><p>\u4e8b\u5b9e\u8ddd\u6b64\u4e0d\u8fdc\u3002</p><p>\u5f53\u8fd9\u602a\u7269\u73b0\u8eab\u65f6\uff0c\u6240\u6709\u7684\u4ea1\u7075\u4eff\u4f5b\u90fd\u6536\u5230\u611f\u5e94\u4e00\u822c\uff0c\u9f50\u9f50\u53d1\u51fa\u4e00\u58f0\u4f4e\u6c89\u5636\u54d1\u7684\u53eb\u558a\uff0c\u5373\u4fbf\u662f\u65e0\u610f\u8bc6\u7684\u50f5\u5c38\u9ab7\u9ac5\u4e5f\u4e0d\u4f8b\u5916\u3002\u6bcf\u4e2a\u4ea1\u7075\u7528\u7684\u90fd\u662f\u751f\u524d\u6700\u719f\u6089\u7684\u8bed\u8a00\uff0c\u4f46\u5343\u767e\u79cd\u8bed\u8a00\u5374\u6c47\u6210\u540c\u6837\u7684\u610f\u601d\uff1a\u201c\u795e\u8eaf\u9b54\u50cf\uff01\u201d</p><p>\u8fd9\u5c31\u662f\u5965\u5580\u65af\u7684\u79d8\u5bc6\u6b66\u5668\uff0c\u7528\u6b7b\u4ea1\u7684\u795e\u660e\u8eaf\u4f53\u5236\u6210\u7684\u9b54\u50cf\u3002</p><p>\u795e\u8eaf\u9b54\u50cf\u521a\u521a\u51dd\u805a\u6210\u578b\uff0c\u968f\u5373\u9ad8\u9ad8\u8dc3\u8d77\uff0c\u4e00\u62f3\u7838\u51fa\uff0c\u6b63\u4e2d\u4e00\u540d\u5815\u5929\u4f7f\uff0c\u5de8\u5927\u7684\u529b\u91cf\u5c06\u4ed6\u4e0a\u534a\u8eab\u5f53\u573a\u7c89\u788e\uff0c\u56de\u81c2\u6a2a\u626b\uff0c\u53c8\u5c06\u53e6\u5916\u4e00\u540d\u5815\u5929\u4f7f\u8fdc\u8fdc\u649e\u98de\u51fa\u53bb\uff0c\u76f4\u8dcc\u5165\u9752\u94dc\u57ce\u5821\u4e2d\u3002</p><p>\u7b2c\u4e09\u540d\u5815\u5929\u4f7f\u81ea\u80cc\u540e\u6251\u6765\uff0c\u4e00\u5251\u5288\u4e2d\u9b54\u50cf\uff0c\u96f7\u9706\u70c8\u7130\u4ea4\u76f8\u8f70\u51fa\uff0c\u5374\u53ea\u5c06\u5b83\u7684\u80cc\u540e\u5212\u51fa\u4e00\u9053\u6d45\u6d45\u7684\u4f24\u53e3\u3002\u795e\u8eaf\u9b54\u50cf\u4f4e\u543c\u4e00\u58f0\uff0c\u53cd\u8eab\u6a2a\u635e\uff0c\u5374\u6293\u4e86\u4e2a\u7a7a\uff0c\u5b83\u867d\u7136\u52a8\u4f5c\u4e0d\u6162\uff0c\u5b8c\u5168\u4e0d\u4f3c\u5bfb\u5e38\u9b54\u50cf\u90a3\u822c\u7b28\u62d9\uff0c\u4f46\u6bd4\u8d77\u957f\u7740\u7fc5\u8180\u7684\u5815\u5929\u4f7f\uff0c\u7ec8\u7a76\u8fd8\u662f\u5dee\u4e86\u5f88\u591a\u3002</p><p>\u66f4\u591a\u7684\u5815\u5929\u4f7f\u6251\u4e86\u4e0a\u6765\uff0c\u6b7b\u4ea1\u9a91\u58eb\u963b\u6321\u4e0d\u4f4f\uff0c\u9ab7\u9ac5\u6218\u9a6c\u867d\u7136\u80fd\u591f\u98de\u884c\uff0c\u5230\u5e95\u6bd4\u4e0d\u4e0a\u5929\u7136\u7684\u7fbd\u7ffc\u3002\u795e\u8eaf\u9b54\u50cf\u63a5\u8fde\u88ab\u780d\u4e2d\u51e0\u5251\uff0c\u597d\u5728\u53d7\u521b\u4e0d\u6df1\uff0c\u4f46\u5b83\u7684\u62f3\u5934\u4e5f\u6253\u4e0d\u4e2d\u5815\u5929\u4f7f\uff0c\u8fd9\u6837\u53ea\u6328\u6253\u4e0d\u80fd\u8fd8\u624b\uff0c\u7ec8\u7a76\u4e0d\u662f\u529e\u6cd5\u3002\u91d1\u5c5e\u5821\u5792\u4e0a\u516b\u540d\u5deb\u5996\u89c1\u72b6\uff0c\u518d\u5ea6\u4e3e\u8d77\u6cd5\u6756\uff0c\u5f53\u7a7a\u7ed8\u51fa\u516b\u4e2a\u590d\u6742\u7684\u7ebf\u6761\uff0c\u8fde\u7f00\u6210\u4e00\u4e2a\u9b54\u6cd5\u9635\u3002</p><p>\u795e\u8eaf\u9b54\u50cf\u4eff\u4f5b\u6709\u6240\u611f\u5e94\u4e00\u822c\uff0c\u4e00\u58f0\u7a7a\u6d1e\u800c\u5636\u54d1\u7684\u4f4e\u543c\u58f0\u81ea\u5b83\u5589\u95f4\u4f20\u51fa\uff0c\u83ab\u540d\u7684\u538b\u6291\u3001\u7edd\u671b\u548c\u618e\u6068\u7b3c\u7f69\u4e0a\u4e86\u6218\u573a\u4e0a\u6240\u6709\u53c2\u6218\u8005\u7684\u5fc3\u5934\uff0c\u5373\u4fbf\u662f\u65e0\u5fc3\u667a\u7684\u4ea1\u7075\u4f3c\u4e4e\u4e5f\u53d7\u5230\u4e86\u5f71\u54cd\u3002\u4e4c\u9ed1\u5149\u8292\u81ea\u9b54\u50cf\u5185\u90e8\u900f\u5c04\u51fa\u6765\uff0c\u5b83\u7684\u5f62\u4f53\u4e5f\u6025\u901f\u53d1\u751f\u53d8\u5316\uff0c\u5de8\u5927\u7684\u8eab\u8eaf\u50cf\u4e00\u53ea\u8f6f\u4f53\u52a8\u7269\u822c\u626d\u66f2\u8815\u52a8\u7740\uff0c\u6025\u901f\u53d8\u5316\u6210\u5404\u79cd\u5947\u602a\u7684\u6a21\u6837\u3002\u5343\u4e07\u5f20\u9762\u5b54\u7ffb\u6eda\u7740\u6d6e\u73b0\u51fa\u6765\uff0c\u4e00\u8fb9\u53d1\u51fa\u75db\u82e6\u800c\u66b4\u6012\u7684\u5636\u543c\uff0c\u4e00\u8fb9\u6025\u901f\u5730\u53d8\u5e7b\u7740\u6837\u8c8c\uff0c\u547c\u5524\u7740\u4e00\u4e2a\u540d\u5b57\u3002</p><p>\u8f6c\u77ac\u4e4b\u95f4\uff0c\u539f\u672c\u4e11\u964b\u7684\u7f1d\u5408\u602a\u6d88\u5931\u4e86\uff0c\u53d6\u800c\u4ee3\u4e4b\u7684\u662f\u4e00\u4f4d\u5de8\u5927\u7684\u7537\u6027\u5353\u5c14\uff0c\u6234\u7740\u8759\u8760\u5f62\u72b6\u7684\u4e1d\u7ec7\u9762\u5177\uff0c\u624b\u4e2d\u591a\u51fa\u4e24\u67c4\u5149\u5f71\u53d8\u5e7b\u7684\u9ed1\u8272\u77ed\u5251\u3002\u5b83\u7684\u901f\u5ea6\u5728\u5239\u90a3\u4e4b\u95f4\u9661\u7136\u52a0\u5feb\u4e86\u51e0\u500d\uff0c\u76f4\u523a\u6a2a\u524a\uff0c\u8fdb\u9000\u5982\u98ce\uff0c\u884c\u52a8\u8fc5\u6377\u81f3\u6781\uff0c\u51e0\u4e4e\u6bcf\u4e00\u7167\u9762\u90fd\u4f1a\u51fb\u6740\u4e00\u540d\u5815\u5929\u4f7f\u3002</p><p>\u7247\u523b\u4e4b\u95f4\uff0c\u9eef\u4e4b\u7ffc\u519b\u56e2\u5c31\u5df2\u7ecf\u635f\u5931\u4e86\u8fd1\u4e8c\u5341\u540d\u6210\u5458\uff0c\u4ed6\u4eec\u5f00\u59cb\u9000\u5374\uff0c\u4f46\u5e76\u4e0d\u662f\u671d\u7740\u9752\u94dc\u57ce\u5821\u7684\u65b9\u5411\uff0c\u800c\u662f\u4fa7\u9762\u7684\u4e00\u5904\u4e18\u9675\uff0c\u795e\u8eaf\u9b54\u50cf\u7d27\u8ffd\u4e0d\u820d\u3002\u6b4c\u560e\u65af\u5e76\u4e0d\u5728\u610f\uff0c\u5439\u8d77\u53f7\u89d2\uff0c\u547d\u4ee4\u5176\u4ed6\u7684\u4ea1\u7075\u51c6\u5907\u653b\u57ce\u3002</p><p>\u9ab7\u9ac5\u4eec\u81ea\u52a8\u7ec4\u5408\u8d77\u6765\uff0c\u642d\u6210\u4e86\u4e91\u68af\uff0c\u6ce2\u8fbe\u5c38\u548c\u5c38\u5996\u9c7c\u8d2f\u800c\u4e0a\uff0c\u884c\u52a8\u7f13\u6162\u7684\u50f5\u5c38\u7528\u81ea\u5df1\u7684\u8eab\u4f53\u4e00\u6ce2\u6ce2\u5730\u649e\u51fb\u7740\u57ce\u95e8\uff0c\u9ab8\u9aa8\u4e9a\u9f99\u5728\u6b64\u524d\u548c\u6df1\u72f1\u70bc\u9b54\u7684\u7a7a\u6218\u4e2d\u635f\u5931\u8fc7\u534a\uff0c\u6b64\u65f6\u6b63\u98de\u7fd4\u5728\u6b4c\u560e\u65af\u7684\u4e0a\u7a7a\uff0c\u4fdd\u62a4\u7740\u91d1\u5c5e\u5821\u5792\u3002\u6b7b\u4ea1\u9a91\u58eb\u8fc5\u901f\u96c6\u7ed3\uff0c\u51c6\u5907\u98de\u8d8a\u57ce\u5899\u3002</p><p>\u4fbf\u5728\u6b64\u65f6\uff0c\u5927\u5730\u5f00\u59cb\u5267\u70c8\u9707\u52a8\u3002</p><p>\u795e\u8eaf\u9b54\u50cf\u8ffd\u8d76\u7740\u5815\u5929\u4f7f\u8fdb\u5165\u4e18\u9675\u5730\u5e26\uff0c\u5728\u5b83\u521a\u521a\u8e0f\u5165\u7684\u90a3\u4e00\u77ac\u95f4\uff0c\u51e0\u5341\u9053\u706b\u67f1\u9661\u7136\u4ece\u5730\u5e95\u55b7\u6d8c\u51fa\u6765\uff0c\u76f4\u8d2f\u957f\u7a7a\uff0c\u5c06\u9b54\u50cf\u56e2\u56e2\u56f4\u4f4f\uff0c\u51e0\u5341\u53ea\u5168\u8eab\u70c8\u7130\u7f20\u7ed5\u7684\u5de8\u9f99\u4ece\u706b\u67f1\u4e2d\u51b2\u51fa\u6765\uff0c\u50cf\u7ea2\u773c\u6597\u725b\u4e00\u6837\u649e\u4e0a\u9b54\u50cf\u3002</p><p>\u201c\u70bc\u72f1\u708e\u9f99\uff01\u201d</p><p>\u6b4c\u560e\u65af\u4e00\u773c\u8ba4\u51fa\u4e86\u8fd9\u79cd\u751f\u6d3b\u5728\u5730\u72f1\u7684\u706b\u9f99\uff0c\u4ed6\u5927\u5403\u4e86\u4e00\u60ca\u3002\u70bc\u72f1\u708e\u9f99\u4f20\u8bf4\u662f\u4e94\u8272\u9f99\u795e\u63d0\u4e9a\u739b\u7279\u67d0\u6b21\u90aa\u6076\u8bd5\u9a8c\u7684\u4ea7\u7269\uff0c\u548c\u7269\u8d28\u754c\u7684\u540c\u65cf\u4e00\u6837\u5f3a\u5927\uff0c\u4f46\u66f4\u52a0\u61d2\u6563\uff0c\u5e73\u7d20\u603b\u662f\u8eb2\u5728\u706b\u5c71\u7194\u5ca9\u4e2d\u547c\u547c\u5927\u7761\uff0c\u4ece\u4e0d\u53c2\u4e0e\u8840\u6218\uff0c\u5982\u4eca\u5374\u600e\u4e48\u4f1a\u5728\u8fd9\u91cc\u51fa\u73b0\u3002</p><p>\u5728\u6df1\u6e0a\u91cc\u5f85\u5f97\u592a\u4e45\uff0c\u603b\u662f\u548c\u667a\u529b\u4f4e\u4e0b\u7684\u4ea1\u7075\u6253\u4ea4\u9053\uff0c\u8ba9\u6b4c\u560e\u65af\u7684\u601d\u7ef4\u53cd\u5e94\u4e5f\u53d8\u5f97\u9000\u5316\uff0c\u8fd8\u6ca1\u7b49\u4ed6\u60f3\u660e\u767d\u8fd9\u662f\u600e\u4e48\u4e00\u56de\u4e8b\uff0c\u70bc\u72f1\u708e\u9f99\u5df2\u7ecf\u548c\u795e\u8eaf\u9b54\u50cf\u7f20\u6597\u5728\u4e00\u8d77\u3002\u4eff\u4f5b\u5403\u4e86\u5174\u594b\u5242\u4f3c\u7684\uff0c\u8fd9\u4e9b\u5e73\u7d20\u6e29\u548c\u61d2\u6563\u7684\u708e\u9f99\u53d8\u5f97\u51f6\u66b4\u65e0\u6bd4\uff0c\u4e2a\u4e2a\u594b\u4e0d\u987e\u8eab\uff0c\u62fc\u6b7b\u653b\u51fb\uff0c\u795e\u8eaf\u9b54\u50cf\u867d\u7136\u5f3a\u5927\uff0c\u731b\u7136\u5bf9\u4e0a\u51e0\u5341\u53ea\u53d1\u72c2\u7684\u5de8\u9f99\u4e5f\u62db\u67b6\u4e0d\u6613\uff0c\u867d\u7136\u4e0d\u843d\u4e0b\u98ce\uff0c\u8eab\u4e0a\u4e5f\u8fc5\u901f\u591a\u4e86\u65e0\u6570\u521b\u4f24\u3002</p><p>\u6b4c\u560e\u65af\u987e\u4e0d\u4e0a\u6307\u6325\u653b\u57ce\uff0c\u62a2\u8fc7\u4e00\u5339\u9ab7\u9ac5\u6218\u9a6c\uff0c\u7387\u9886\u9644\u8fd1\u7684\u6b7b\u4ea1\u9a91\u58eb\u98de\u5954\u800c\u6765\u63f4\u52a9\u3002\u8ddd\u79bb\u8fd1\u524d\uff0c\u4ed6\u7ec8\u4e8e\u53d1\u73b0\u4e86\u95ee\u9898\u6240\u5728\uff1a\u6240\u6709\u7684\u708e\u9f99\u8eab\u4e0a\u90fd\u7b3c\u7f69\u7740\u4e00\u5c42\u9b54\u6cd5\u7075\u5149\uff0c\u4eff\u4f5b\u88ab\u540c\u65f6\u52a0\u6301\u4e86\u67d0\u79cd\u55dc\u8840\u6cd5\u672f\u3002\u5438\u8840\u9b3c\u5fc3\u4e2d\u5347\u8d77\u4e00\u80a1\u4e0d\u8be6\u7684\u9884\u611f\uff0c\u4ed6\u62ac\u5934\u671d\u9ad8\u7a7a\u4e2d\u671b\u53bb\uff0c\u4ece\u4e4c\u4e91\u548c\u8840\u8272\u4e4b\u95f4\u627e\u5230\u4e86\u95ea\u70c1\u7684\u76ee\u6807\u3002</p><p>\u201c\u9f99\u72c2\u8ff7\u9501\uff01\u201d</p><p>\u4f5c\u4e3a\u7cbe\u7075\uff0c\u6b4c\u560e\u65af\u5bf9\u4e8e\u672c\u65cf\u7684\u8fd9\u4e2a\u5f97\u610f\u53d1\u660e\u81ea\u7136\u6beb\u4e0d\u964c\u751f\uff0c\u7acb\u523b\u5c31\u8fa8\u8ba4\u51fa\u6765\u3002\u6574\u4e2a\u8fd9\u7247\u4e18\u9675\u5df2\u7ecf\u5b8c\u5168\u88ab\u9f99\u72c2\u8ff7\u9501\u7b3c\u7f69\uff0c\u8fd9\u5c31\u662f\u70bc\u72f1\u708e\u9f99\u7a81\u7136\u72c2\u66b4\u7684\u539f\u56e0\u6240\u5728\u2014\u2014\u4f46\u4e3a\u4ec0\u4e48\u5b83\u4eec\u53ea\u653b\u51fb\u795e\u8eaf\u9b54\u50cf\uff1f\u9f99\u72c2\u8ff7\u9501\u7684\u6548\u679c\u7406\u5f53\u662f\u8ba9\u5de8\u9f99\u53d1\u72c2\uff0c\u80e1\u4e71\u653b\u51fb\u5468\u56f4\u7684\u4e00\u5207\u751f\u7269\u624d\u5bf9\u3002</p><p>\u65e0\u6687\u591a\u60f3\u8fd9\u4e9b\uff0c\u6b4c\u560e\u65af\u957f\u5200\u4e00\u6325\uff0c\u7387\u9886\u5341\u4f59\u540d\u6b7b\u4ea1\u9a91\u58eb\u817e\u7a7a\u800c\u8d77\uff0c\u51b2\u5411\u4e91\u9704\uff0c\u4ed6\u5df2\u7ecf\u53d1\u73b0\u4e86\u8fd9\u4e2a\u8ff7\u9501\u7684\u201c\u6838\u5fc3\u201d\u6240\u5728\uff0c\u53ea\u8981\u5c06\u5b83\u7834\u574f\uff0c\u8ff7\u9501\u81ea\u7136\u5d29\u6e83\u3002\u5f53\u4ed6\u5373\u5c06\u63a5\u8fd1\u76ee\u6807\u7684\u65f6\u5019\uff0c\u9661\u7136\u95f4\u53c8\u662f\u4e00\u9053\u91d1\u7bad\u4eff\u4f5b\u95ea\u7535\u822c\u8fce\u9762\u5c04\u6765\uff0c\u6b4c\u560e\u65af\u4fa7\u8eab\u95ea\u8fc7\uff0c\u8eab\u540e\u7684\u4e24\u540d\u6b7b\u4ea1\u9a91\u58eb\u5374\u6ca1\u80fd\u8eb2\u5f00\uff0c\u88ab\u8fd9\u4e00\u7bad\u8d2f\u7a7f\u80f8\u8179\uff0c\u4e00\u5708\u767d\u5149\u4ece\u4f24\u53e3\u5904\u6025\u901f\u6269\u6563\uff0c\u5c06\u4ed6\u4eec\u77ac\u95f4\u5316\u4f5c\u98de\u7070\u3002</p><p>\u66e6\u5929\u4f7f\u7684\u77ac\u6740\u7bad\u3002</p><p>\u4e00\u9053\u95ea\u7535\u4ece\u4e91\u4e2d\u5288\u4e0b\uff0c\u5728\u534a\u7a7a\u4e2d\u51dd\u805a\u6210\u578b\uff0c\u90a3\u662f\u4e00\u4f4d\u5973\u6027\u5929\u4f7f\uff0c\u707f\u91d1\u957f\u53d1\u3001\u707f\u91d1\u53cc\u773c\u3001\u707f\u91d1\u76ae\u80a4\uff0c\u6d01\u767d\u7684\u7fbd\u7ffc\u4e0a\u5723\u5149\u95ea\u70c1\uff0c\u4e00\u5f20\u91d1\u8272\u957f\u5f13\u5728\u5979\u624b\u4e2d\u5982\u6c34\u6d41\u6ea2\uff0c\u77ac\u95f4\u53d8\u5316\u6210\u4e86\u4e00\u67c4\u5341\u5b57\u5f62\u53cc\u624b\u5de8\u5251\uff0c\u5251\u8eab\u900f\u660e\uff0c\u5149\u534e\u56db\u5c04\u3002</p><p>\u5979\u51b7\u51b7\u5730\u770b\u7740\u4ea1\u7075\uff0c\u7f8e\u4e3d\u7684\u53cc\u773c\u4e2d\u6f20\u65e0\u8868\u60c5\uff0c\u7136\u540e\u4e00\u5251\u5f53\u5934\u5288\u4e0b\uff0c\u91d1\u8272\u5723\u5149\u8f70\u7136\u7efd\u653e\uff0c\u5c06\u6b4c\u560e\u65af\u548c\u4ed6\u7684\u5c5e\u4e0b\u5c3d\u6570\u5305\u88f9\u5176\u4e2d\u3002</p></article>"}}}]);
