(window.webpackJsonp=window.webpackJsonp||[]).push([[348],{371:function(p){p.exports={chapterTitle:null,title:"\u7b2c224\u7ae0 \u547d\u8fd0\u6ca1\u6709\u62d0\u5f2f",content:"<article><h2>\u7b2c224\u7ae0 \u547d\u8fd0\u6ca1\u6709\u62d0\u5f2f</h2><p>\u3000\u3000\u6d88\u606f\u65e2\u7136\u5df2\u7ecf\u5728\u6d77\u5dde\u4e00\u4e2d\u4f20\u5f00\uff0c\u6559\u5e08\u4eec\u7684\u6124\u6012\u81ea\u7136\u53ef\u4ee5\u60f3\u8c61\uff0c\u5f20\u606a\u5750\u5728\u8f66\u91cc\u53d1\u4e86\u4e00\u4f1a\u513f\u5446\uff0c\u51b3\u5b9a\u8fd8\u662f\u5148\u53bb\u627e\u5510\u5a67\u3002\u5510\u5b66\u8c26\u65e9\u65e9\u53bb\u4e86\u5e02\u91cc\uff0c\u5f20\u606a\u8ddf\u5510\u5a67\u8bf4\u5b66\u6821\u4eca\u5929\u505c\u8bfe\uff0c\u5510\u5a67\u5f00\u59cb\u8fd8\u4e0d\u4fe1\uff0c\u6253\u7535\u8bdd\u5230\u5b66\u6821\u95ee\u4e86\u503c\u73ed\u8001\u5e08\uff0c\u624d\u77e5\u9053\u6559\u804c\u5de5\u96c6\u4f53\u7f62\u8bfe\u5230\u5e02\u6559\u80b2\u5c40\u8bf7\u613f\u53bb\u3002</p><p>\u3000\u3000\u201c\u53d1\u751f\u4ec0\u4e48\u5927\u4e8b\u60c5\u4e86\uff0c\u5168\u6821\u8001\u5e08\u505c\u8bfe\uff1f\u201d\u987e\u5efa\u840d\u51c6\u5907\u5148\u8ba9\u5085\u4fca\u5f00\u8f66\u9001\u5979\u53bb\u5355\u4f4d\uff0c\u542c\u5230\u4e00\u4e2d\u4eca\u5929\u7a81\u7136\u505c\u8bfe\uff0c\u6709\u4e9b\u5947\u602a\u3002</p><p>\u3000\u3000\u5f20\u606a\u81ea\u7136\u4e0d\u4f1a\u8ddf\u987e\u5efa\u840d\u3001\u5510\u5a67\u6bcd\u5973\u8bf4\u8fd9\u4e9b\u4e4c\u4e03\u516b\u7cdf\u7684\u4e8b\u60c5\uff0c\u644a\u644a\u624b\uff1a\u201c\u6211\u4e5f\u4e0d\u77e5\u9053\u627e\u8c01\u95ee\uff0c\u5510\u4f2f\u4f2f\u53ef\u80fd\u8fd9\u65f6\u5019\u77e5\u9053\u4e86\u3002\u201d</p><p>\u3000\u3000\u987e\u5efa\u840d\u7684\u597d\u5947\u5fc3\u8fd8\u6ca1\u6709\u5f3a\u70c8\u5230\u76f4\u63a5\u6253\u7535\u8bdd\u7ed9\u4e08\u592b\u5510\u5b66\u8c26\uff0c\u4f46\u662f\u4e8b\u5173\u5510\u5a67\u7684\u5b66\u6821\uff0c\u51b3\u5b9a\u5148\u53bb\u5355\u4f4d\u518d\u627e\u4eba\u6253\u542c\uff0c\u8ba9\u5085\u4fca\u9001\u5979\u53bb\u5355\u4f4d\u3002</p><p>\u3000\u3000\u4e5d\u6708\u521d\u7684\u6691\u6c14\u8fd8\u76db\uff0c\u624d\u4e03\u516b\u70b9\u949f\uff0c\u7ea2\u70ed\u7684\u592a\u9633\u5c31\u60ac\u5728\u7a7a\u4e2d\uff0c\u4eae\u5f97\u6643\u773c\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u505a\u4e9b\u5565\uff1f\u201d\u5f20\u606a\u77a5\u773c\u770b\u7740\u5510\u5a67\u5bb6\u7684\u4fdd\u59c6\u5f00\u59cb\u6536\u62fe\u5c4b\u5b50\uff0c\u4e00\u65f6\u60f3\u4e0d\u51fa\u6b63\u5f53\u7406\u7531\u5c06\u4fdd\u59c6\u637b\u8d70\uff0c\u201c\u8981\u4e0d\u6211\u4eec\u53bb\u5b66\u6821\u770b\u70ed\u95f9\uff1f\u201d</p><p>\u3000\u3000\u201c\u6559\u5e08\u4e0d\u90fd\u53bb\u6559\u80b2\u5c40\u5417\uff1f\u201d\u5510\u5a67\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u53bb\u8c61\u5c71\u9547\u770b\u674e\u8001\u5e08\u5427\uff1f\u201d</p><p>\u3000\u3000\u867d\u8bf4\u6559\u804c\u5de5\u73b0\u5728\u90fd\u53bb\u4e86\u6559\u80b2\u5c40\uff0c\u4f46\u662f\u6700\u6709\u70ed\u95f9\u770b\u7684\u8fd8\u662f\u5728\u5b66\u6821\uff0c\u542c\u675c\u98de\u8bf4\u6d88\u606f\u5728\u5b66\u751f\u4e2d\u95f4\u4f20\u5f00\u4e5f\u5f97\u5230\u4e49\u6124\u586b\u81ba\u7684\u6fc0\u70c8\u54cd\u5e94\uff0c\u65e9\u4e0a\u8d76\u5230\u5b66\u6821\u7684\u5b66\u751f\u5728\u542c\u5230\u505c\u8bfe\u7684\u901a\u77e5\u4e4b\u540e\u5e76\u6ca1\u6709\u79bb\u5f00\u5b66\u6821\uff0c\u597d\u50cf\u915d\u917f\u7740\u4ec0\u4e48\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u674e\u829d\u82b3\u7ed9\u8c03\u5230\u8c61\u5c71\u9547\u521d\u7ea7\u4e2d\u5b66\u7684\u4e8b\u60c5\u6709\u4e9b\u7a81\u7136\uff0c\u5f20\u606a\u63a8\u6d4b\u53ef\u80fd\u662f\u5979\u8ddf\u738b\u708e\u658c\u7684\u4e8b\u60c5\u6f0f\u9732\u5230\u738b\u708e\u658c\u8001\u5a46\u7684\u8033\u6735\u91cc\uff0c\u5f20\u606a\u672c\u4e0d\u60f3\u63d2\u624b\u8fd9\u4ef6\u4e8b\uff0c\u65e2\u7136\u5510\u5a67\u63d0\u51fa\u8981\u53bb\u8c61\u5c71\u521d\u4e2d\u770b\u674e\u829d\u82b3\uff0c\u4ed6\u5f53\u7136\u4e5f\u4e0d\u4f1a\u8bf4\u4e0d\u597d\uff0c\u5173\u952e\u60f3\u7740\u53ef\u4ee5\u8ddf\u5510\u5a67\u4e24\u4e2a\u4eba\u53bb\u9644\u8fd1\u7684\u8c61\u5c71\u68ee\u6797\u516c\u56ed\u6253\u53d1\u4e00\u5929\u7684\u65f6\u95f4\uff0c\u8fd9\u4e00\u70b9\u5f88\u503c\u5f97\u671f\u5f85\u3002</p><p>\u3000\u3000\u5085\u4fca\u7b49\u4f1a\u513f\u8981\u53bb\u63a5\u9a6c\u6d77\u9f99\u5230\u516c\u53f8\uff0c\u6ca1\u6709\u7b49\u5085\u4fca\uff0c\u5f20\u606a\u4e0e\u5510\u5a67\u51fa\u4e86\u5e08\u8303\u5c0f\u533a\u3002</p><p>\u3000\u3000\u521a\u51fa\u5c0f\u533a\u5c31\u63a5\u5230\u675c\u98de\u7684\u7535\u8bdd\uff0c\u675c\u98de\u672c\u60f3\u7559\u5728\u5b66\u6821\u91cc\u6dfb\u6cb9\u52a0\u918b\uff0c\u8ba9\u4e8b\u60c5\u518d\u95f9\u70ed\u4e9b\uff0c\u90a3\u60f3\u5230\u4ed6\u7684\u540d\u58f0\u4e0d\u6bd4\u4e07\u5929\u624d\u3001\u8d75\u53f8\u660e\u597d\u591a\u5c11\uff0c\u8fd9\u65f6\u5019\u6709\u4eba\u5c06\u5e74\u524d\u4ed6\u63cd\u66f9\u5149\u660e\u7684\u4e8b\u60c5\u7ffb\u51fa\u6765\u8bf4\uff0c\u4ed6\u81ea\u7136\u4e0d\u6562\u7559\u5728\u5b66\u6821\uff0c\u6015\u4e07\u4e00\u5176\u4ed6\u4eba\u4f17\u60c5\u6fc0\u6124\uff0c\u5c06\u4ed6\u9971\u63cd\u4e00\u987f\u5c31\u5f88\u4e0d\u723d\u4e86\u3002</p><p>\u3000\u3000\u675c\u98de\u542c\u5230\u5f20\u606a\u4e0e\u5510\u5a67\u8981\u53bb\u770b\u674e\u829d\u82b3\uff0c\u4ed6\u72b9\u8c6b\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u624d\u8bf4\u8981\u4e00\u8d77\u53bb\u3002</p><p>\u3000\u3000\u6253\u8f66\u5230\u8c61\u5c71\u9547\u521d\u7ea7\u4e2d\u5b66\uff0c\u5b66\u6821\u4f4d\u4e8e\u8c61\u5c71\u5317\u9e93\uff0c\u8c61\u5c71\u9547\u9547\u653f\u5e9c\u5357\u9996\uff0c\u4e00\u5ea7\u4e09\u5c42\u7684\u6559\u5b66\u697c\u4e0e\u4e00\u5ea7\u4e24\u5c42\u7684\u529e\u516c\u697c\u5939\u7740\u4e24\u767e\u7c73\u7684\u5c0f\u64cd\u573a\uff0c\u4e1c\u5317\u89d2\u8fd8\u6709\u51e0\u680b\u65e7\u5e73\u623f\uff0c\u6709\u8c61\u533b\u52a1\u5ba4\u3001\u4f53\u80b2\u5668\u6750\u5ba4\u3001\u6559\u804c\u5de5\u5bbf\u820d\u7684\u4e4b\u7c7b\u623f\u95f4\uff0c\u4e0d\u8981\u8bf4\u4e00\u4e2d\u7684\u521d\u4e2d\u90e8\u4e86\uff0c\u6bd4\u8d77\u5e02\u91cc\u666e\u901a\u7684\u521d\u7ea7\u4e2d\u5b66\u6765\uff0c\u8981\u5bd2\u9178\u591a\u4e86\u3002</p><p>\u3000\u3000\u6765\u4e4b\u524d\u53ea\u77e5\u9053\u674e\u829d\u82b3\u8c03\u8c61\u5c71\u9547\u521d\u7ea7\u4e2d\u5b66\u6765\uff0c\u5176\u4ed6\u60c5\u51b5\u4e00\u6982\u4e0d\u77e5\u9053\uff0c\u770b\u7740\u5e73\u623f\u8d70\u5eca\u91cc\u6709\u4e24\u4e2a\u4e2d\u5e74\u5973\u6559\u5e08\u4f9d\u7740\u8d70\u5eca\u91cc\u7684\u5eca\u67f1\u5728\u8bf4\u8bdd\uff0c\u5f20\u606a\u62c9\u7740\u5510\u5a67\u8dd1\u8fc7\u53bb\uff0c\u8fdc\u8fdc\u7684\u5f00\u53e3\u60f3\u95ee\u674e\u829d\u82b3\u7684\u53bb\u5411\uff0c\u5374\u7ed9\u5979\u4eec\u7684\u8c08\u8bdd\u6253\u65ad\u3002</p><p>\u3000\u3000\u201c\u674e\u8001\u5e08\u5728\u6d77\u5dde\u4e00\u4e2d\u53ef\u662f\u62b1\u7740\u7c97\u817f\u5f53\u6559\u52a1\u5904\u4e3b\u4efb\u7684\uff0c\u5973\u4eba\u751f\u4e00\u4ed8\u597d\u8eab\u5b50\uff0c\u8131\u4e86\u88e4\u5b50\u4e0a\u5e8a\uff0c\u9664\u4e86\u81ea\u5df1\u8212\u5766\uff0c\u8fd8\u4ec0\u4e48\u597d\u5904\u90fd\u4eab\u53d7\u5230\u4e86\u3002\u53ef\u60dc\u4e8b\u60c5\u8d25\u9732\u4e86\uff0c\u7ed9\u4eba\u5bb6\u8001\u5a46\u637b\u5230\u6211\u4eec\u8fd9\u91cc\u6765\uff0c\u4e5f\u96be\u602a\u5979\uff0c\u957f\u8fd9\u4e48\u6f02\u4eae\uff0c\u6362\u7740\u6211\u662f\u7537\u4eba\uff0c\u8fd8\u4e0d\u5343\u65b9\u767e\u8ba1\u7684\u5f04\u5e8a\u4e0a\u53bb\uff1f\u201d</p><p>\u3000\u3000\u201c\u53ef\u4e0d\u662f\uff0c\u5979\u6765\u624d\u51e0\u5929\uff0c\u6559\u80b2\u5c40\u7684\u738b\u5c40\u957f\u5c31\u5230\u6211\u4eec\u5b66\u6821\u6307\u5bfc\u5de5\u4f5c\u4e24\u4e09\u6b21\uff0c\u4e4b\u524d\u4e5f\u6ca1\u89c1\u4ed6\u6765\u6307\u5bfc\u6211\u4eec\u5de5\u4f5c\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u56bc\u4e0d\u5b8c\u7684\u820c\u6839\uff0c\u5f20\u606a\u51b7\u51b7\u4e00\u7b11\uff0c\u770b\u4e86\u675c\u98de\u4e00\u773c\uff0c\u5c31\u6015\u8fd9\u6d51\u5c0f\u5b50\u51b2\u4e0a\u53bb\u6247\u8fd9\u4e24\u4e2a\u53ef\u6076\u7684\u5a46\u5a18\u51e0\u5df4\u638c\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u8d70\uff01\u201d\u5510\u5a67\u54ea\u91cc\u60f3\u5230\u5973\u6559\u5e08\u4e5f\u4f1a\u8fd9\u4e48\u6ca1\u7d20\u8d28\u7684\u5728\u80cc\u540e\u56bc\u4eba\u820c\u6839\uff0c\u53c8\u7f9e\u53c8\u607c\uff0c\u62c9\u7740\u5f20\u606a\u7684\u624b\u5c31\u8981\u8d70\uff0c\u5b81\u53ef\u4e00\u95f4\u6559\u5ba4\u4e00\u95f4\u6559\u5ba4\u7684\u627e\uff0c\u4e5f\u4e0d\u60f3\u95ee\u8fd9\u4e24\u4e2a\u8ba9\u4eba\u8ba8\u538c\u7684\u5973\u4eba\u3002</p><p>\u3000\u3000\u5f20\u606a\u8f7b\u8f7b\u8e22\u4e86\u675c\u98de\u4e00\u811a\uff0c\u8ba9\u4ed6\u4e0d\u8981\u505a\u50bb\u4e8b\uff0c\u4ed6\u8981\u771f\u52a8\u624b\uff0c\u53ea\u6015\u674e\u829d\u82b3\u4ee5\u540e\u5728\u6559\u80b2\u7cfb\u7edf\u6ca1\u6709\u5b89\u8eab\u4e4b\u5730\uff0c\u53ea\u662f\u5f88\u5947\u602a\uff0c\u662f\u8c01\u628a\u8fd9\u4e9b\u4e8b\u60c5\u6f0f\u9732\u51fa\u6765\u4e86\uff0c\u738b\u708e\u658c\u4ed6\u8001\u5a46\u5fc3\u91cc\u6068\u5f52\u6068\uff0c\u8fd8\u6ca1\u6709\u50bb\u5230\u8ba9\u8fd9\u4e9b\u4e8b\u4f20\u5f97\u4eba\u6240\u7686\u77e5\u3002</p><p>\u3000\u3000\u66f9\u5149\u660e\uff1f</p><p>\u3000\u3000\u5f20\u606a\u60f3\u8d77\u90a3\u4e2a\u7ed9\u675c\u98de\u51e0\u62f3\u7838\u65ad\u9f3b\u6881\u9aa8\u7684\u66f9\u5149\u660e\u3002</p><p>\u3000\u3000\u675c\u98de\u62f3\u5934\u634f\u7d27\uff0c\u6307\u5173\u8282\u53d1\u767d\uff0c\u5f3a\u5fcd\u7740\u5fc3\u91cc\u7684\u6012\u706b\uff0c\u4ed6\u5fc3\u91cc\u77e5\u9053\u51b2\u52a8\u89e3\u51b3\u4e0d\u4e86\u4ec0\u4e48\u95ee\u9898\uff0c\u65cb\u8e35\u8981\u8ddf\u5f20\u606a\u8d70\u5f00\uff0c\u5374\u542c\u89c1\u8eab\u540e\u4f20\u6765\u4e24\u58f0\u60ca\u547c\u3002</p><p>\u3000\u3000\u201c\u5468\u6653\u7490\uff0c\u4f60\u505a\u4ec0\u4e48\uff1f\u4e0a\u8bfe\u65f6\u95f4\uff0c\u4f60\u600e\u4e48\u5728\u6559\u5ba4\u91cc\uff1f\u201d\u4e00\u540d\u4e2d\u5e74\u5973\u6559\u5e08\u6293\u72c2\u7684\u5c16\u53eb\u8d77\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u8f6c\u8eab\u770b\u89c1\u4e00\u4e2a\u5341\u4e8c\u4e09\u5c81\u7684\u5c0f\u5973\u5b69\u624b\u7aef\u7740\u8138\u76c6\u7ad9\u5728\u623f\u95e8\u53e3\uff0c\u6740\u6c14\u817e\u817e\u7684\u76ef\u7740\u4e24\u4e2a\u56bc\u820c\u6839\u7684\u5973\u6559\u5e08\uff0c\u800c\u4e24\u540d\u5973\u6559\u5e08\u5374\u7ed9\u6c34\u6d47\u5f97\u901a\u4f53\u900f\u6e7f\uff0c\u8863\u670d\u5355\u8584\uff0c\u6e7f\u4e86\u5c31\u7d27\u8d34\u5728\u8eab\u4e0a\uff0c\u8ddf\u534a\u88f8\u4f3c\u7684\uff0c\u53ef\u60dc\u4eba\u5230\u4e2d\u5e74\uff0c\u53c8\u59ff\u8272\u5e73\u5e73\uff0c\u5b9e\u5728\u6ca1\u4ec0\u4e48\u770b\u5934\u3002\u5012\u662f\u5c0f\u5973\u5b69\u5507\u7ea2\u9f7f\u767d\uff0c\u53ef\u76ee\u6012\u7741\uff0c\u5341\u5206\u6f02\u4eae\uff0c\u53ef\u60dc\u5e74\u9f84\u592a\u5c0f\uff0c\u7626\u7626\u7684\u8eab\u6750\u8fd8\u6ca1\u6709\u957f\u5f00\u3002</p><p>\u3000\u3000\u5f20\u606a\u542c\u5230\u90a3\u5973\u6559\u5e08\u5524\u8fd9\u5c0f\u5973\u5b69\u5b50\u7684\u540d\u5b57\uff0c\u5fc3\u91cc\u4e00\u60ca\uff1a\u5468\u6653\u7490\uff1f\u4ec5\u4ec5\u662f\u540d\u5b57\u6070\u5de7\u76f8\u540c\u5417\uff1f</p><p>\u3000\u3000\u4e00\u540d\u5973\u6559\u5e08\u8981\u4e0a\u524d\u6559\u8bad\u5c0f\u5973\u5b69\uff0c\u53e6\u4e00\u4e2a\u5973\u6559\u5e08\u770b\u89c1\u5f20\u606a\u4ed6\u4eec\uff0c\u5fc3\u865a\u7684\u62c9\u4e86\u62c9\u540c\u4f34\uff1a\u201c\u7b97\u4e86\uff0c\u4e0d\u8981\u8ddf\u5c0f\u5b69\u5b50\u4e00\u822c\u89c1\u8bc6\uff0c\u9a6c\u4e0a\u5c31\u8981\u4e0b\u8bfe\u4e86\uff0c\u5feb\u627e\u5730\u65b9\u628a\u8863\u670d\u6362\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u4e24\u540d\u5973\u6559\u5e08\u7070\u6e9c\u6e9c\u7684\u8d70\u8fdb\u6700\u4e1c\u5934\u7684\u623f\u95f4\u91cc\uff0c\u5510\u5a67\u770b\u7740\u90a3\u4e2a\u5c0f\u5973\u5b69\u5b50\uff0c\u4eb2\u5207\u7684\u95ee\u5979\uff1a\u201c\u5c0f\u540c\u5b66\uff0c\u4f60\u77e5\u9053\u521a\u521a\u8c03\u8fc7\u6765\u7684\u674e\u829d\u82b3\u8001\u5e08\u6559\u54ea\u4e2a\u73ed\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u627e\u6211\u5988\u5988\u4ec0\u4e48\u4e8b\uff1f\u201d\u5c0f\u5973\u5b69\u5b50\u8b66\u60d5\u7684\u770b\u7740\u5510\u5a67\u3001\u5f20\u606a\u3001\u675c\u98de\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u5f20\u606a\u5dee\u70b9\u60f3\u626d\u5934\u9003\u8d70\uff1b\u5386\u53f2\u4e0d\u53d1\u751f\u6539\u53d8\uff0c\u674e\u829d\u82b3\u53ea\u4f1a\u662f\u675c\u98de\u7684\u73ed\u4e3b\u4efb\uff0c\u9664\u4e86\u90a3\u4ef6\u6349\u5978\u7684\u4e8b\u60c5\uff0c\u5f20\u606a\u672c\u4eba\u5bf9\u674e\u829d\u82b3\u7684\u5370\u8c61\u5e76\u4e0d\u6df1\u523b\uff0c\u4e5f\u4e0d\u4f1a\u770b\u51fa\u6210\u5e74\u540e\u7684\u5468\u6653\u7490\u8ddf\u674e\u829d\u82b3\u6709\u54ea\u70b9\u50cf\u3002\u5f20\u606a\u77e5\u9053\u4e3a\u4ec0\u4e48\u675c\u98de\u5728\u4e0e\u5468\u6653\u7490\u53d1\u751f\u4e00\u591c\u60c5\u3001\u77e5\u9053\u5979\u662f\u88d5\u4e30\u96c6\u56e2\u8001\u603b\u7684\u60c5\u4eba\u4e4b\u540e\uff0c\u4ecd\u7136\u5bf9\u5979\u52a8\u4e86\u60c5\uff0c\u5927\u6982\u5c31\u662f\u56e0\u4e3a\u5468\u6653\u7490\u4e0e\u5979\u5988\u5988\u674e\u829d\u82b3\u957f\u5f97\u5f88\u50cf\u7684\u7f18\u6545\u3002</p><p>\u3000\u3000\u8fd8\u672a\u6210\u5e74\u7684\u5468\u6653\u7490\u5c31\u7ad9\u5728\u8eab\u524d\uff0c\u90a3\u5728\u51fa\u8f66\u7978\u524d\u4e0e\u81ea\u5df1\u901a\u7535\u8bdd\u7684\u5f20\u5a67\u5728\u54ea\u91cc\uff1f\u90a3\u4e2a\u5c0f\u7f8e\u5973\u6210\u5e74\u4e4b\u540e\u8ddf\u5510\u5a67\u957f\u5f97\u795e\u4f3c\uff0c\u4e24\u53ea\u773c\u775b\u53c8\u957f\u53c8\u5a9a\uff0c\u73b0\u5728\u9876\u591a\u53ea\u6709\u5341\u4e00\u4e8c\u5c81\u3002</p><p>\u3000\u3000\uff08\u5468\u6653\u7490\u3001\u5f20\u5a67\u7684\u8eab\u4efd\u8bf7\u53c2\u8003\u8651\u8d44\u6599\u7bc7\u300a\u6d88\u901d\u7684\u6954\u5b50\u300b\u4e00\u7ae0\uff09</p><p>\u3000\u3000\u201c\u674e\u8001\u5e08\u662f\u4f60\u7684\u5988\u5988\u554a\uff1f\u201d\u5510\u5a67\u60ca\u559c\u7684\u8bf4\uff0c\u201c\u6211\u4eec\u662f\u674e\u8001\u5e08\u7684\u5b66\u751f\uff0c\u8d76\u7740\u5b66\u6821\u4eca\u5929\u505c\u8bfe\uff0c\u4eca\u5929\u8fc7\u6765\u770b\u770b\u674e\u8001\u5e08\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u5988\u5988\u5728\u4e0a\u8bfe\uff0c\u9a6c\u4e0a\u5c31\u8981\u4e0b\u8bfe\u4e86\u3002\u201d\u5468\u6653\u7490\u6536\u8d77\u773c\u775b\u91cc\u7684\u654c\u610f\uff0c\u4e0d\u597d\u610f\u601d\u7684\u5c06\u8138\u76c6\u6536\u5230\u8eab\u540e\uff0c\u5927\u6982\u5979\u4e5f\u89c9\u5f97\u7aef\u7740\u51c9\u4eba\u6cfc\u4eba\u7684\u505a\u6cd5\u592a\u5f6a\u608d\u3002</p><p>\u3000\u3000\u5468\u6653\u7490\u8ddf\u674e\u829d\u82b3\u5230\u8c61\u5c71\u521d\u7ea7\u4e2d\u5b66\u6765\u8bfb\u521d\u4e00\uff0c\u5979\u65e9\u6668\u6709\u4e9b\u611f\u5192\uff0c\u5c31\u7559\u5728\u674e\u829d\u82b3\u7684\u5bbf\u820d\u91cc\u4f11\u606f\uff0c\u6ca1\u6709\u53bb\u4e0a\u8bfe\uff0c\u542c\u5230\u90a3\u4e24\u4e2a\u5973\u5e08\u5728\u623f\u524d\u56bc\u820c\u5934\uff0c\u5979\u4e00\u51b2\u52a8\uff0c\u5c31\u7aef\u4e86\u4e00\u76c6\u51c9\u6c34\u6d47\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u5468\u6653\u7490\u5f88\u6709\u793c\u8c8c\u7684\u8bf7\u5f20\u606a\u4ed6\u4eec\u8fdb\u5c4b\uff0c\u5355\u4eba\u6559\u5e08\u5bbf\u820d\u7684\u7a7a\u95f4\u4e0d\u5927\uff0c\u5f20\u606a\u8d77\u521d\u4ee5\u4e3a\u662f\u674e\u829d\u82b3\u7684\u4e34\u65f6\u4f4f\u5904\uff0c\u8fdb\u53bb\u4e00\u770b\uff0c\u604d\u7136\u4ee5\u4e3a\u674e\u829d\u82b3\u5c06\u5bb6\u642c\u4e86\u8fc7\u6765\uff0c\u867d\u7136\u4e0d\u77e5\u9053\u674e\u829d\u82b3\u7231\u4eba\u7684\u5de5\u4f5c\u60c5\u51b5\uff0c\u4f46\u662f\u674e\u829d\u82b3\u5728\u4e00\u4e2d\u5f53\u6559\u52a1\u5904\u4e3b\u4efb\u6709\u4e24\u4e09\u5e74\u7684\u65f6\u95f4\uff0c\u4e00\u4e2d\u6559\u804c\u5de5\u53bb\u5e74\u521a\u521a\u5206\u8fc7\u4e00\u6279\u4f4f\u623f\uff0c\u53ef\u80fd\u666e\u901a\u8001\u5e08\u6ca1\u6709\u4efd\uff0c\u4f46\u7edd\u4e0d\u4f1a\u628a\u674e\u829d\u82b3\u7ed9\u6f0f\u4e86\uff0c\u96be\u9053\u8bf4\u674e\u829d\u82b3\u7684\u623f\u5b50\u7ed9\u6536\u56de\u53bb\u4e86\uff1f</p><p>\u3000\u3000\u674e\u829d\u82b3\u4e0a\u8bfe\u56de\u6765\uff0c\u770b\u5230\u5510\u5a67\u3001\u5f20\u606a\u3001\u675c\u98de\u4ed6\u4eec\uff0c\u53c8\u60ca\u53c8\u559c\uff0c\u81ea\u7136\u6ca1\u4eba\u63d0\u5468\u6653\u7490\u521a\u624d\u5f6a\u608d\u7684\u4e3e\u52a8\u3002</p><p>\u3000\u3000\u674e\u829d\u82b3\u95ee\u5f20\u606a\u4ed6\u4eec\uff1a\u201c\u4eca\u5929\u600e\u4e48\u4f1a\u6709\u7a7a\u8fc7\u6765\uff0c\u5b66\u6821\u4e0d\u4e0a\u8bfe\u5417\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c06\u4e8b\u60c5\u7b80\u8981\u7684\u8bf4\u4e86\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u600e\u4e48\u4f1a\u53d1\u751f\u8fd9\u79cd\u4e8b\uff1f\u201d\u5510\u5a67\u7a81\u7136\u77aa\u773c\u770b\u7740\u5f20\u606a\uff0c\u201c\u65e9\u4e0a\u4f60\u5728\u6211\u5bb6\uff0c\u600e\u4e48\u8bf4\u4f60\u4e0d\u77e5\u9053\uff1f\u201d</p><p>\u3000\u3000\u201c\u563f\u563f\uff0c\u8fd9\u4e8b\u538b\u6839\u5c31\u662f\u5f20\u606a\u6345\u7834\u7684\uff0c\u8981\u4e0d\u662f\u5f20\u606a\uff0c\u8bf4\u4e0d\u5b9a\u5c0f\u5d14\u8001\u5e08\u4eca\u5929\u65e9\u4e0a\u4f1a\u5728\u54ea\u4e2a\u8352\u90ca\u91ce\u5916\u7ed9\u53d1\u751f\u2026\u2026\u201d\u505a\u4e86\u8fd9\u4e48\u5927\u7684\u4e8b\u60c5\uff0c\u5374\u4e0d\u80fd\u8ddf\u4eba\u8bf4\uff0c\u597d\u6bd4\u7a7f\u4e86\u4e00\u8eab\u597d\u8863\u670d\u53ea\u80fd\u5728\u81ea\u5df1\u7684\u623f\u95f4\u91cc\u8f6c\u60a0\u4e00\u6837\u4e0d\u723d\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u5510\u5a67\u77aa\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u663e\u7136\u5979\u60f3\u5230\u6628\u5929\u5403\u8fc7\u665a\u996d\u5f20\u606a\u8ddf\u5979\u7238\u8eb2\u4e66\u623f\u91cc\u53ef\u80fd\u5c31\u662f\u8bf4\u8fd9\u4e8b\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u4e5f\u4e0d\u6015\u674e\u829d\u82b3\u4f1a\u5c06\u4e8b\u60c5\u8bf4\u51fa\u53bb\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u8ddf\u675c\u98de\u662f\u6709\u540d\u7684\u574f\u5b66\u751f\uff0c\u8fd9\u65f6\u5019\u53ef\u4e0d\u6562\u53bb\u5b66\u6821\uff0c\u514d\u5f97\u7ed9\u6b83\u53ca\u6c60\u9c7c\u3002\u201d</p><p>\u3000\u3000\u674e\u829d\u82b3\u6447\u4e86\u6447\u5934\uff0c\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u6211\u4ee5\u524d\u5bf9\u4f60\u6709\u4e9b\u8bef\u89e3\uff0c\u4f60\u867d\u7136\u8ddf\u666e\u901a\u5b66\u751f\u4e0d\u540c\uff0c\u8ddf\u675c\u98de\u3001\u5510\u5a67\u90fd\u4e0d\u540c\uff0c\u4f46\u4f60\u7edd\u5bf9\u4e0d\u662f\u4ec0\u4e48\u574f\u5b66\u751f\u3002\u201d</p></article>"}}}]);
