(window.webpackJsonp=window.webpackJsonp||[]).push([[350],{372:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e03\u8282 \u518d\u6218",content:"<article><h2>\u7b2c\u4e03\u8282 \u518d\u6218</h2><p>\u5f53\u963f\u5f17\u7eb3\u65af\u7684\u592a\u9633\u4ece\u897f\u65b9\u7684\u5929\u7a7a\u5189\u5189\u5347\u8d77\u65f6\uff0c\u9b54\u9b3c\u5927\u519b\u51c6\u5907\u53d1\u52a8\u65b0\u4e00\u8f6e\u653b\u51fb\u3002</p><p>\u62dc\u5c14\u7ad9\u5728\u540e\u65b9\u7684\u4e00\u5904\u571f\u4e18\u4e0a\uff0c\u4f9d\u65e7\u662f\u9ed1\u53d1\u9ed1\u773c\u7684\u4eba\u7c7b\u5f62\u6001\uff0c\u4e00\u88ad\u534e\u4e3d\u957f\u888d\uff0c\u952f\u9f7f\u94a2\u5200\u60ac\u6302\u5728\u8170\u95f4\u3002\u4ed6\u4ef0\u671b\u7740\u8fdc\u5904\u7684\u9ed1\u8272\u57ce\u5899\uff0c\u5634\u5507\u62bf\u5f97\u7d27\u7d27\uff0c\u4e00\u8a00\u4e0d\u53d1\u3002</p><p>\u81ea\u4ece\u5760\u5165\u5730\u72f1\uff0c\u53d8\u6210\u9b54\u9b3c\u4ee5\u6765\uff0c\u62dc\u5c14\u4e0d\u77e5\u9053\u5df2\u7ecf\u7ecf\u5386\u4e86\u591a\u5c11\u6b21\u8840\u6218\u3002\u4ece\u51b2\u5728\u6700\u524d\u9762\u7684\u70ae\u7070\uff0c\u6162\u6162\u6210\u957f\u4e3a\u5f3a\u58ee\u7684\u6218\u58eb\uff0c\u6210\u4e3a\u4f4e\u7ea7\u519b\u5b98\uff0c\u6210\u4e3a\u9ad8\u7ea7\u5c06\u9886\uff0c\u6210\u4e3a\u4e00\u57ce\u4e4b\u4e3b\uff0c\u6700\u7ec8\u5750\u4e0a\u5927\u516c\u7235\u7684\u5b9d\u5ea7\uff0c\u4ed6\u6240\u6709\u7684\u4e00\u5207\uff0c\u90fd\u662f\u4ece\u8840\u706b\u6218\u573a\u4e0a\u62fc\u6740\u51fa\u6765\u7684\u3002\u81ea\u53e4\u4ee5\u6765\uff0c\u5730\u72f1\u4e5d\u5927\u9886\u4e3b\uff0c\u65e0\u4e0d\u662f\u8fdb\u5316\u5230\u7ec8\u6781\u5f62\u6001\u7684\u5927\u9b54\u9b3c\u624d\u80fd\u62c5\u4efb\uff0c\u62dc\u5c14\u8fd8\u53ea\u662f\u4e00\u4e2a\u6df1\u72f1\u70bc\u9b54\uff0c\u8fde\u8d50\u4e88\u4fe1\u5f92\u795e\u672f\u7684\u80fd\u529b\u90fd\u6ca1\u6709\uff0c\u5374\u80fd\u591f\u63a5\u66ff\u624e\u745e\u5c14\u62c5\u4efb\u963f\u5f17\u7eb3\u65af\u9886\u4e3b\uff0c\u7edf\u9886\u7740\u4e5d\u5c42\u5730\u72f1\u4ec5\u6b21\u4e8e\u963f\u65af\u8482\u6469\u65af\u7684\u519b\u961f\uff0c\u6b63\u662f\u770b\u91cd\u4ed6\u7684\u519b\u4e8b\u624d\u80fd\uff0c\u56e0\u4e3a\u4ed6\u603b\u80fd\u591f\u7387\u9886\u9b54\u9b3c\u5728\u8840\u6218\u4e2d\u83b7\u80dc\u3002</p><p>\u5728\u624e\u745e\u5c14\u65f6\u4ee3\uff0c\u9b54\u9b3c\u56e0\u4e3a\u5175\u529b\u4e0a\u7684\u7edd\u5bf9\u52a3\u52bf\uff0c\u7eb5\u7136\u503e\u5c3d\u5168\u529b\uff0c\u5728\u8840\u6218\u4e2d\u7684\u5927\u591a\u6570\u65f6\u95f4\u91cc\u53ea\u80fd\u5904\u4e8e\u9632\u5b88\u5730\u4f4d\u3002\u6076\u9b54\u5927\u519b\u4e00\u6b21\u6b21\u5730\u653b\u5165\u963f\u5f17\u7eb3\u65af\uff0c\u867d\u7136\u53c8\u4e00\u6b21\u6b21\u88ab\u51fb\u9000\uff0c\u4f46\u6bcf\u6b21\u90fd\u7ed9\u5730\u72f1\u9020\u6210\u4e86\u4e25\u91cd\u7684\u7834\u574f\u3002\u81ea\u4ece\u4e09\u767e\u5e74\u524d\u62dc\u5c14\u4e0a\u53f0\u4e4b\u540e\uff0c\u60c5\u5f62\u4fbf\u5f00\u59cb\u53d1\u751f\u53d8\u5316\uff0c\u6076\u9b54\u653b\u5165\u963f\u5f17\u7eb3\u65af\u7684\u6b21\u6570\u8d8a\u6765\u8d8a\u5c11\uff0c\u800c\u9b54\u9b3c\u53cd\u51fb\u7684\u673a\u4f1a\u8d8a\u6765\u8d8a\u591a\u4e86\u3002</p><p>\u4e00\u6b21\u6b21\u7684\u8f89\u714c\u6218\u7ee9\uff0c\u79ef\u7d2f\u8d77\u4e86\u5d07\u9ad8\u7684\u58f0\u671b\uff0c\u8ba9\u62dc\u5c14\u6210\u4e3a\u9b54\u9b3c\u5fc3\u76ee\u4e2d\u4e0d\u8d25\u7684\u795e\u8bdd\u3002\u53ea\u8981\u8fd9\u79cd\u60c5\u5f62\u80fd\u591f\u7ee7\u7eed\u4e0b\u53bb\uff0c\u90a3\u4e48\u4ed6\u5c31\u80fd\u7a33\u7a33\u5750\u5728\u8fd9\u4e2a\u5b9d\u5ea7\u4e0a\uff0c\u76f4\u5230\u66f4\u8fdb\u4e00\u6b65\uff08\u5982\u679c\u53ef\u80fd\u7684\u8bdd\uff09\u3002\u62dc\u5c14\u5e76\u4e0d\u662f\u4e2a\u6ca1\u6709\u91ce\u5fc3\u7684\u4eba\uff0c\u800c\u4ed6\u559c\u6b22\u7a33\u624e\u7a33\u6253\u5730\u83b7\u53d6\u3002</p><p>\u4f46\u8fd9\u4e00\u6b21\uff0c\u62dc\u5c14\u5bf9\u81ea\u5df1\u7684\u4fe1\u5fc3\u9690\u9690\u53d1\u751f\u52a8\u6447\u3002\u539f\u672c\u4ee5\u4e3a\u5df2\u7ecf\u770b\u89c1\u7684\u66d9\u5149\uff0c\u4f3c\u4e4e\u53c8\u53d8\u56de\u6c89\u6c89\u9ed1\u6697\u3002</p><p>\u81ea\u4ece\u770b\u5230\u795e\u8eaf\u9b54\u50cf\u7684\u90a3\u4e00\u523b\u8d77\uff0c\u62dc\u5c14\u5c31\u5df2\u7ecf\u610f\u8bc6\u5230\u4e86\u5f3a\u70c8\u7684\u5371\u673a\u3002\u5f53\u9b54\u9b3c\u548c\u6076\u9b54\u5927\u519b\u8fd8\u5728\u6df1\u6e0a\u7ea0\u7f20\u7684\u65f6\u5019\uff0c\u4ed6\u4fbf\u79d8\u5bc6\u6d3e\u9063\u519b\u961f\u5f00\u59cb\u5728\u81ea\u5df1\u5bb6\u95e8\u53e3\u6316\u5751\u57cb\u96f7\u3002\u5766\u767d\u5730\u8bf4\uff0c\u62dc\u5c14\u770b\u4e0d\u8d77\u5965\u5580\u65af\uff0c\u5c31\u50cf\u4ed6\u4ece\u6765\u770b\u4e0d\u8d77\u6076\u9b54\u4e00\u6837\uff0c\u89c9\u5f97\u4ed6\u4eec\u53ea\u4f1a\u86ee\u5e72\uff0c\u6253\u8d77\u6218\u6765\u6beb\u65e0\u6280\u672f\u542b\u91cf\uff0c\u5b8c\u5168\u7f3a\u4e4f\u7f8e\u611f\u2014\u2014\u4f46\u8fd9\u6b21\u7684\u95ee\u9898\u5728\u4e8e\uff0c\u5bf9\u65b9\u5df2\u7ecf\u86ee\u5e72\u5230\u4e86\u4e00\u5b9a\u7a0b\u5ea6\u4e86\u3002</p><p>\u6ca1\u6709\u667a\u529b\u7684\u4ea1\u7075\u58eb\u5175\uff0c\u5e73\u5eb8\u7684\u7edf\u5e05\u5c06\u5b98\uff0c\u4ee5\u53ca\u5f3a\u608d\u65e0\u654c\u7684\u79d8\u5bc6\u6b66\u5668\uff0c\u8fd9\u4e00\u5207\u7ec4\u5408\u8d77\u6765\uff0c\u5c31\u903c\u5f97\u62dc\u5c14\u4e0d\u5f97\u4e0d\u4e00\u8def\u540e\u9000\u3002\u5982\u679c\u9047\u4e0a\u9ad8\u660e\u7684\u7edf\u5e05\uff0c\u4ed6\u8fd8\u53ef\u4ee5\u548c\u5bf9\u65b9\u6597\u667a\u6597\u529b\uff0c\u73a9\u5404\u79cd\u8ba1\u8c0b\u8be1\u8bc8\uff0c\u4f46\u78b0\u5230\u6b4c\u560e\u65af\u8fd9\u79cd\u7b28\u86cb\u5bb6\u4f19\uff0c\u5f80\u5f80\u5f88\u591a\u5de7\u5999\u7684\u6218\u672f\u53d8\u5f97\u65e0\u6cd5\u5b9e\u65bd\uff0c\u4f60\u5728\u90a3\u91cc\u4e0d\u52a8\u58f0\u8272\u5730\u8bbe\u8ba1\u5f15\u8bf1\uff0c\u4ed6\u53ea\u5f53\u4f5c\u832b\u7136\u672a\u77e5\uff0c\u8fd9\u5b9e\u5728\u662f\u8ba9\u4eba\u5f88\u90c1\u95f7\u7684\u4e8b\u60c5\u3002</p><p>\u597d\u5728\u5bf9\u65b9\u6709\u79d8\u5bc6\u6b66\u5668\uff0c\u81ea\u5df1\u4e5f\u4e0d\u7b49\u4e8e\u5c31\u6ca1\u6709\u85cf\u7740\u51e0\u624b\u3002\u901a\u8fc7\u683c\u62c9\u5179\u7279\u7684\u5e2e\u5fd9\uff0c\u62dc\u5c14\u83b7\u5f97\u4e86\u8428\u739b\u65af\u7279\u7684\u76f8\u52a9\uff0c\u53c8\u51e0\u4e4e\u628a\u5730\u72f1\u91cc\u6240\u6709\u7684\u70bc\u72f1\u708e\u9f99\u90fd\u7ed9\u6293\u6765\uff0c\u5728\u6700\u540e\u5173\u5934\u5b8c\u6210\u4e86\u9f99\u72c2\u8ff7\u9501\uff0c\u654c\u4f4f\u4e86\u795e\u8eaf\u9b54\u50cf\u3002\u53c8\u501f\u52a9\u706b\u836f\u8fd9\u79cd\u65b0\u5f0f\u6b66\u5668\uff0c\u7ec8\u4e8e\u5728\u9752\u94dc\u57ce\u5821\u4e4b\u4e0b\u4e00\u6218\u7834\u654c\uff0c\u6210\u529f\u9006\u8f6c\u5c40\u52bf\u3002</p><p>\u6076\u9b54\u6e83\u8d25\uff0c\u62dc\u5c14\u7387\u519b\u4e00\u8def\u8ffd\u6740\uff0c\u4e24\u65e5\u4e4b\u540e\u5175\u4e34\u9ed1\u77f3\u57ce\u4e0b\u3002\u4ed6\u5f53\u7136\u4e0d\u4f1a\u653e\u8fc7\u8fd9\u4e2a\u96be\u5f97\u7684\u673a\u4f1a\uff0c\u6253\u7b97\u4e58\u80dc\u4e00\u4e3e\u7834\u654c\uff0c\u5c06\u6076\u9b54\u519b\u5f7b\u5e95\u6253\u57ae\u3002</p><p>\u4f46\u4e8b\u60c5\u5374\u6ca1\u6709\u9884\u6599\u5f97\u90a3\u4e48\u7b80\u5355\u3002</p><p>\u6076\u9b54\u519b\u867d\u7136\u5728\u9752\u94dc\u57ce\u5821\u4e0b\u4e00\u6218\u6e83\u8d25\uff0c\u4f46\u5e76\u672a\u635f\u4f24\u6839\u672c\u3002\u4ece\u524d\u7ebf\u9003\u56de\u7684\u6b8b\u5175\uff0c\u52a0\u4e0a\u9ed1\u77f3\u57ce\u4e2d\u539f\u672c\u9a7b\u5b88\u7684\u519b\u961f\uff0c\u4f9d\u65e7\u8fd8\u662f\u6709\u6570\u4e07\u4e4b\u4f17\uff1b\u6700\u7cbe\u9510\u7684\u6218\u529b\u6b7b\u4ea1\u9a91\u58eb\u5c1a\u5b58\u4e94\u5343\u4f59\u540d\u3002\u706b\u836f\u70e7\u6b7b\u4e86\u5927\u91cf\u7684\u4ea1\u7075\uff0c\u9540\u94f6\u5f39\u7247\u5e72\u6389\u4e86\u4e0d\u5c11\u4f4e\u9636\u7684\u5438\u8840\u9b3c\uff0c\u4f46\u9ad8\u9636\u519b\u5b98\u4eec\u5927\u591a\u9003\u751f\uff0c\u6574\u4e2a\u6307\u6325\u7cfb\u7edf\u5e76\u672a\u762b\u75ea\uff0c\u5f88\u5feb\u53c8\u91cd\u5efa\u8d77\u6765\u3002</p><p>\u6b4c\u560e\u65af\u5148\u88ab\u66e6\u5929\u4f7f\u7684\u77ac\u6740\u7bad\u6bc1\u6389\u4e00\u53ea\u80f3\u818a\uff0c\u63a5\u7740\u4e2d\u4e86\u9540\u94f6\u5f39\u7247\uff0c\u5728\u8d25\u9000\u65f6\u53c8\u8fde\u906d\u8ffd\u6740\uff0c\u867d\u7136\u6027\u547d\u65e0\u5fe7\uff0c\u5374\u662f\u91cd\u4f24\u6655\u8ff7\uff0c\u4e0d\u80fd\u89c6\u4e8b\u4e86\u3002\u8fd9\u539f\u672c\u662f\u62dc\u5c14\u7684\u4e00\u7740\u5999\u68cb\uff0c\u6b4c\u560e\u65af\u867d\u7136\u662f\u4e2a\u4e0d\u79f0\u804c\u7684\u4e3b\u5e05\uff0c\u4f46\u4ed6\u6709\u5965\u5580\u65af\u505a\u540e\u53f0\uff0c\u53ea\u8981\u4ed6\u5728\uff0c\u4ea1\u7075\u5927\u519b\u5c31\u662f\u4e2a\u7edf\u4e00\u7684\u6574\u4f53\uff0c\u53f7\u4ee4\u901a\u7545\uff0c\u6307\u6325\u5982\u610f\u3002\u5982\u4eca\u4ed6\u4e0d\u80fd\u7406\u4e8b\uff0c\u624b\u4e0b\u7684\u51e0\u4e2a\u9ad8\u7ea7\u5c06\u9886\u4eec\u5f7c\u6b64\u5730\u4f4d\u76f8\u5f53\uff0c\u80fd\u529b\u76f8\u82e5\uff0c\u81ea\u7136\u8c01\u4e5f\u4e0d\u670d\u8c01\uff0c\u5c31\u7b97\u4e0d\u4e92\u76f8\u62c6\u53f0\uff0c\u5c31\u6b64\u5206\u88c2\uff0c\u6218\u6597\u529b\u4e5f\u4f1a\u5927\u6253\u6298\u6263\u3002</p><p>\u4e5d\u72f1\u4e4b\u4e3b\u963f\u65af\u8482\u6469\u65af\u965b\u4e0b\u5236\u5b9a\u7684\u201c\u9b54\u9b3c\u5341\u4e00\u94c1\u5f8b\u201d\uff0c\u5f00\u5b97\u660e\u4e49\u7684\u7b2c\u4e00\u6761\u5c31\u662f\u201c\u529b\u91cf\u5728\u4e8e\u56e2\u7ed3\u201d\u3002\u552f\u6709\u56e2\u7ed3\u624d\u6709\u529b\u91cf\uff0c\u552f\u6709\u56e2\u7ed3\u624d\u80fd\u6253\u8d25\u5f3a\u654c\uff0c\u8fd9\u662f\u94ed\u523b\u5728\u6240\u6709\u9b54\u9b3c\u7075\u9b42\u91cc\u7684\u70d9\u5370\u3002\u8840\u6218\u6301\u7eed\u5343\u4e07\u5e74\uff0c\u9b54\u9b3c\u80fd\u591f\u4ee5\u7edd\u5bf9\u52a3\u52bf\u7684\u5175\u529b\u548c\u6076\u9b54\u76f8\u6297\uff0c\u9760\u7684\u4e0d\u4ec5\u4ec5\u662f\u88c5\u5907\uff0c\u662f\u58eb\u6c14\uff0c\u662f\u7edf\u5e05\u548c\u6307\u6325\u80fd\u529b\uff0c\u6700\u91cd\u8981\u7684\u662f\u56e2\u7ed3\u3002\u800c\u6076\u9b54\u4e5f\u6b63\u56e0\u4e3a\u4e0d\u56e2\u7ed3\uff0c\u6240\u4ee5\u5c61\u5c61\u53d7\u632b\uff0c\u5f80\u5f80\u5728\u4f18\u52bf\u5c40\u9762\u4e4b\u4e0b\u7ffb\u76d8\uff0c\u5426\u5219\u65e9\u5c31\u6253\u5230\u5730\u72f1\u7b2c\u4e5d\u5c42\u8010\u745f\u65af\u4e86\u3002</p><p>\u5982\u4eca\u6076\u9b54\u519b\u7684\u9ad8\u7ea7\u519b\u5b98\u4eec\u867d\u7136\u90fd\u662f\u4ea1\u7075\uff0c\u4f46\u5374\u662f\u5965\u5580\u65af\u8fd9\u4e2a\u6076\u9b54\u9886\u4e3b\u624b\u4e0b\u7684\u4ea1\u7075\uff0c\u4ed6\u4eec\u5728\u6df1\u6e0a\u4e2d\u751f\u6d3b\u4e86\u5343\u767e\u5e74\uff0c\u53ea\u8981\u6709\u667a\u6167\u6709\u610f\u8bc6\uff0c\u6f5c\u79fb\u9ed8\u5316\u95f4\u5fc5\u7136\u4f1a\u88ab\u6df7\u4e71\u7684\u4f4d\u9762\u89c4\u5219\u6240\u5f71\u54cd\uff0c\u8fd1\u4f3c\u6076\u9b54\u7684\u601d\u7ef4\uff0c\u4fb5\u67d3\u6076\u9b54\u7684\u4e60\u6027\u3002\u5c31\u50cf\u628a\u4e00\u4e2a\u4eba\u7c7b\u6254\u8fdb\u5730\u72f1\u5446\u4e0a\u51e0\u5341\u5e74\u4e0a\u767e\u5e74\uff0c\u5982\u679c\u80fd\u6d3b\u4e0b\u6765\uff0c\u4ed6\u867d\u7136\u8eaf\u4f53\u4e0a\u8fd8\u662f\u4e2a\u4eba\u7c7b\uff0c\u9aa8\u5b50\u91cc\u548c\u9b54\u9b3c\u4e5f\u5dee\u4e0d\u591a\u4e86\u2014\u2014\u5bf9\u4e8e\u8fd9\u4e00\u70b9\uff0c\u62dc\u5c14\u786e\u4fe1\u4e0d\u7591\uff0c\u56e0\u4e3a\u8fd9\u662f\u7ecf\u9a8c\u4e4b\u8c08\u3002</p><p>\u201c\u5e72\u6389\u6b4c\u560e\u65af\uff0c\u6076\u9b54\u519b\u7684\u6218\u6597\u529b\u81f3\u5c11\u4f1a\u6253\u4e24\u4e2a\u6298\u6263\u3002\u201d\u9752\u94dc\u57ce\u5821\u6218\u524d\uff0c\u62dc\u5c14\u5982\u6b64\u5bf9\u5c5e\u4e0b\u8bf4\u3002</p><p>\u867d\u7136\u66e6\u5929\u4f7f\u5f25\u8d5b\u4e9a\u6700\u7ec8\u6ca1\u80fd\u6210\u529f\u51fb\u6740\u6b4c\u560e\u65af\uff0c\u80fd\u8ba9\u4ed6\u91cd\u4f24\u6655\u8ff7\uff0c\u6548\u679c\u4e5f\u5dee\u4e0d\u591a\u4e86\u3002\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u6076\u9b54\u519b\u5374\u5e76\u6ca1\u6709\u51fa\u73b0\u4ec0\u4e48\u5927\u7684\u52a8\u8361\uff0c\u5f88\u5feb\u5c31\u4ece\u6218\u8d25\u7684\u9634\u5f71\u4e2d\u91cd\u65b0\u7a33\u5b9a\u4e0b\u6765\uff0c\u5f00\u59cb\u636e\u57ce\u800c\u5b88\u3002\u4e2d\u95f4\u5230\u5e95\u53d1\u751f\u4e86\u4ec0\u4e48\uff0c\u62dc\u5c14\u5e76\u4e0d\u6e05\u695a\uff0c\u552f\u4e00\u53ef\u4ee5\u80af\u5b9a\u7684\u662f\uff0c\u73b0\u5728\u4ea1\u7075\u519b\u7684\u6700\u9ad8\u7edf\u5e05\u662f\u8fea\u5185\u7279\u3002</p><p>\u5185\u4e71\u5e73\u606f\uff0c\u518d\u5fa1\u5916\u654c\uff0c\u4ea1\u7075\u4eec\u80cc\u57ce\u624e\u8425\uff0c\u8fce\u51fb\u8fdc\u9053\u800c\u6765\u7684\u9b54\u9b3c\u5927\u519b\u3002\u6fc0\u6218\u5df2\u7ecf\u6574\u6574\u6301\u7eed\u4e86\u4e09\u5929\u4e09\u591c\uff0c\u62dc\u5c14\u5c06\u4ed6\u7684\u6307\u6325\u827a\u672f\u53d1\u6325\u5230\u4e86\u6dcb\u6f13\u5c3d\u81f4\u7684\u5730\u6b65\uff0c\u57cb\u4f0f\u3001\u5939\u51fb\u3001\u4f6f\u653b\u3001\u7591\u5175\uff0c\u6240\u6709\u8fd9\u4e9b\u90fd\u88ab\u4ed6\u7528\u7684\u51fa\u795e\u5165\u5316\u3002\u6076\u9b54\u519b\u5904\u5904\u6328\u6253\uff0c\u6b65\u6b65\u53d7\u632b\uff0c\u603b\u662f\u843d\u5165\u5c42\u51fa\u4e0d\u7a77\u7684\u9677\u9631\u4e4b\u4e2d\u2014\u2014\u4f46\u5374\u5e76\u672a\u88ab\u771f\u6b63\u51fb\u8d25\uff0c\u56e0\u4e3a\u5b83\u4eec\u6709\u795e\u8eaf\u9b54\u50cf\u3002</p><p>\u62dc\u5c14\u7684\u53f3\u624b\u7528\u529b\u63e1\u7740\u5200\u67c4\uff0c\u624b\u80cc\u4e0a\u7684\u7b4b\u8109\u8f7b\u5fae\u8df3\u52a8\u7740\uff0c\u201c\u76d6\u4f26\u51fa\u53d1\u4e86\u5417\uff1f\u201d</p><p>\u4e00\u56e2\u626d\u66f2\u7684\u9ed1\u5f71\u5728\u62dc\u5c14\u8eab\u540e\u7684\u7a7a\u6c14\u4e2d\u51fa\u73b0\uff0c\u7136\u540e\u51dd\u805a\u6210\u578b\uff0c\u53d8\u6210\u4e00\u4f4d\u80e1\u987b\u82b1\u767d\u7684\u9ed1\u888d\u8001\u4eba\uff0c\u4ed6\u662f\u74e6\u4f26\u9053\u5c14\uff0c\u62dc\u5c14\u7684\u9996\u5e2d\u5e55\u50da\uff0c\u540c\u65f6\u4e5f\u662f\u4e00\u4f4d\u9020\u8be3\u9ad8\u6df1\u7684\u5deb\u5e08\u3002\u4e8b\u5b9e\u4e0a\uff0c\u4ed6\u5df2\u7ecf\u662f\u4e2a\u5deb\u5996\uff0c\u5168\u8eab\u53ea\u5269\u4e00\u526f\u9ab7\u9ac5\u9aa8\u67b6\uff0c\u4f46\u4ed6\u603b\u559c\u6b22\u4ee5\u6d3b\u7740\u65f6\u7684\u9762\u8c8c\u51fa\u73b0\u3002\u201c\u76d6\u4f26\u5df2\u7ecf\u51fa\u53d1\uff0c\u201d\u74e6\u4f26\u9053\u5c14\u8bf4\uff0c\u201c\u4ed6\u5e26\u8d70\u4e86\u9690\u5f62\u95ea\u7535\u548c\u5168\u90e8\u7684\u5c0f\u9b54\u9b3c\u3002\u201d</p><p>\u201c\u55ef\u3002\u201d</p><p>\u62dc\u5c14\u6709\u4e9b\u75b2\u5026\u5730\u70b9\u70b9\u5934\uff0c\u793a\u610f\u81ea\u5df1\u77e5\u9053\u3002\u4ed6\u5fc5\u987b\u4ee5\u6700\u5feb\u901f\u5ea6\u51fb\u6e83\u5bf9\u624b\uff0c\u6467\u6bc1\u6389\u90a3\u4e2a\u795e\u8eaf\u9b54\u50cf\uff0c\u5426\u5219\u7b49\u6076\u9b54\u7684\u63f4\u5175\u62b5\u8fbe\uff0c\u597d\u4e0d\u5bb9\u6613\u8425\u9020\u8d77\u6765\u7684\u4f18\u52bf\u5c40\u9762\u53c8\u8361\u7136\u65e0\u5b58\u3002\u683c\u62c9\u5179\u7279\u662f\u4ed6\u7684\u76df\u53cb\uff0c\u4f1a\u5e2e\u5fd9\u5ef6\u6ede\u5965\u5580\u65af\u7684\u63f4\u519b\uff0c\u4f46\u4ed6\u8981\u4fdd\u5b88\u7ea2\u8272\u5bff\u8863\u7684\u8eab\u4efd\u79d8\u5bc6\uff0c\u4e0d\u53ef\u80fd\u505a\u5f97\u592a\u8fc7\u706b\uff1b\u6b4c\u560e\u65af\u4e00\u8def\u6253\u4e0b\u7684\u524d\u9762\u5341\u51e0\u5ea7\u57ce\u5821\uff0c\u540c\u6837\u4e5f\u9a7b\u624e\u4e86\u4e0d\u5c11\u5175\u529b\uff0c\u5f88\u53ef\u80fd\u8fc7\u6765\u6551\u63f4\uff0c\u76d6\u4f26\u7684\u4efb\u52a1\uff0c\u5c31\u662f\u5c3d\u4e00\u5207\u53ef\u80fd\u53bb\u62d6\u5ef6\u4ed6\u4eec\uff0c\u4e89\u53d6\u65f6\u95f4\u3002</p><p>\u5c3d\u7ba1\u5982\u6b64\uff0c\u5269\u4e0b\u7684\u65f6\u95f4\u4e5f\u5df2\u7ecf\u4e0d\u591a\u4e86\u3002</p><p>\u201c\u5982\u679c\u4eca\u5929\u8fd8\u4e0d\u80fd\u6210\u529f\u2026\u2026\u90a3\u6050\u6015\uff0c\u5c31\u53ea\u80fd\u64a4\u9000\u4e86\u3002\u201d</p><p>\u62dc\u5c14\u5728\u5fc3\u4e2d\u4f4e\u4f4e\u559f\u53f9\u7740\uff0c\u5982\u679c\u64a4\u9000\uff0c\u7b49\u654c\u4eba\u4f11\u6574\u6062\u590d\uff0c\u518d\u6765\u8fdb\u653b\u7684\u8bdd\uff0c\u9752\u94dc\u57ce\u5821\u5fc5\u5b9a\u5b88\u5fa1\u4e0d\u4f4f\u3002\u800c\u4e00\u65e6\u4e22\u5931\u9752\u94dc\u57ce\u5821\uff0c\u7eb5\u7136\u4ed6\u672c\u4eba\u65e0\u6059\uff0c\u90e8\u5c5e\u4ecd\u5728\uff0c\u4ed6\u7684\u5927\u516c\u7235\u8eab\u4efd\u4e5f\u5c31\u6b64\u81ea\u52a8\u5265\u593a\u3002\u54ea\u6015\u4ed6\u4ee5\u540e\u80fd\u591f\u91cd\u65b0\u628a\u8fd9\u5ea7\u57ce\u5e02\u593a\u56de\u6765\uff0c\u8fd9\u4e2a\u5b9d\u5ea7\u4e5f\u5750\u4e0d\u4e86\u591a\u4e45\u4e86\uff0c\u524d\u4efb\u963f\u5f17\u7eb3\u65af\u5927\u516c\u7235\u624e\u745e\u5c14\uff0c\u4e0d\u5c31\u662f\u88ab\u5929\u4f7f\u519b\u56e2\u653b\u9677\u4e86\u9752\u94dc\u57ce\u5821\uff0c\u56e0\u800c\u4e27\u5931\u963f\u65af\u8482\u6469\u65af\u965b\u4e0b\u7684\u652f\u6301\uff0c\u88ab\u4ed6\u6697\u4e2d\u63a8\u7ffb\u7684\u4e48\u3002</p><p>\u62dc\u5c14\u53ef\u4e0d\u60f3\u843d\u5230\u90a3\u79cd\u5730\u6b65\u3002</p><p>\u4ed6\u6df1\u5438\u4e00\u53e3\u6c14\uff0c\u62d4\u51fa\u957f\u5200\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u201c\u8fdb\u653b\uff01\u201d\u4ed6\u5389\u58f0\u9ad8\u559d\uff0c\u671d\u7740\u524d\u65b9\u9ed1\u8272\u7684\u57ce\u5899\u9065\u9065\u6307\u53bb\u3002</p><p>\u6210\u5343\u4e0a\u4e07\u7684\u9b54\u9b3c\u5954\u8dd1\u8d77\u6765\uff0c\u6c47\u805a\u6210\u6c79\u6d8c\u7684\u6d77\u6d0b\u3002</p><p>\u203b\u203b\u203b</p><p>\u8fea\u5185\u7279\u8e0c\u8e87\u6ee1\u5fd7\u5730\u7ad9\u5728\u57ce\u5934\uff0c\u4fef\u89c6\u7740\u4e0b\u65b9\u7684\u4ea1\u7075\u548c\u9b54\u9b3c\u5927\u519b\uff0c\u707c\u70ed\u7684\u70c8\u98ce\u4ece\u8fdc\u5904\u5439\u6765\uff0c\u5e26\u7740\u6d53\u6d53\u7684\u8840\u8165\u6c14\uff0c\u4ee4\u5438\u8840\u9b3c\u66f4\u52a0\u7cbe\u795e\u4e00\u632f\u3002</p><p>\u201c\u73b0\u5728\u8fd9\u662f\u6211\u7684\u6218\u4e89\u4e86\uff01\u201d</p><p>\u6b63\u5982\u62dc\u5c14\u6240\u6599\uff0c\u6b4c\u560e\u65af\u4e00\u5012\u4e0b\uff0c\u4ed6\u624b\u4e0b\u7684\u51e0\u5927\u5c06\u9886\u7acb\u523b\u5f00\u59cb\u4e89\u593a\u7edf\u5e05\u5b9d\u5ea7\uff0c\u773c\u770b\u6076\u9b54\u519b\u5c31\u8981\u56db\u5206\u4e94\u88c2\uff0c\u91cd\u590d\u4ee5\u524d\u91cd\u590d\u4e86\u5343\u767e\u6b21\u7684\u5931\u8d25\u7ed3\u5c40\u3002\u4f46\u5c31\u5728\u6b64\u65f6\uff0c\u8fea\u5185\u7279\u767b\u573a\u4e86\u3002</p><p>\u4ed6\u662f\u6b4c\u560e\u65af\u7684\u8001\u90e8\u4e0b\uff0c\u5949\u547d\u9a7b\u5b88\u9ed1\u77f3\u57ce\uff0c\u501f\u7740\u8fd9\u5730\u4e3b\u4e4b\u5229\uff0c\u5728\u8d25\u5175\u521a\u5165\u57ce\u65f6\u9080\u8bf7\u4f17\u4f4d\u5c06\u9886\u96c6\u4f1a\uff0c\u6697\u4e2d\u4f0f\u4e0b\u91cd\u5175\uff0c\u731d\u7136\u53d1\u96be\uff0c\u4e00\u4e3e\u65a9\u6740\u4e86\u6240\u6709\u8db3\u591f\u5206\u91cf\u7684\u7ade\u4e89\u5bf9\u624b\uff0c\u4ece\u6b64\u987a\u5229\u63a5\u638c\u5175\u6743\uff0c\u4e00\u573a\u773c\u770b\u8981\u7206\u53d1\u7684\u5185\u4e71\u5c31\u6b64\u5e73\u606f\u3002\u81f3\u4e8e\u6b4c\u560e\u65af\uff0c\u8fea\u5185\u7279\u5012\u6ca1\u6709\u4e0b\u624b\uff0c\u4e00\u65b9\u9762\u662f\u4e0d\u6562\uff0c\u6bd5\u7adf\u5bf9\u65b9\u662f\u5965\u5580\u65af\u7684\u4eb2\u4fe1\uff0c\u53e6\u4e00\u65b9\u9762\u662f\u6ca1\u6709\u5fc5\u8981\u3002</p><p>\u53ea\u8981\u81ea\u5df1\u6253\u8d62\u4e86\u8fd9\u4e00\u6218\uff0c\u51fb\u8d25\u62dc\u5c14\uff0c\u653b\u9677\u9752\u94dc\u57ce\u5821\uff0c\u5730\u4f4d\u81ea\u7136\u5c31\u80fd\u66f4\u4e0a\u4e00\u5c42\uff0c\u4fbf\u662f\u6210\u4e3a\u6b64\u5730\u7684\u9886\u4e3b\u4e5f\u672a\u5fc5\u4e0d\u80fd\u3002</p><p>\u8fea\u5185\u7279\u4ece\u4e0d\u7f3a\u4e4f\u91ce\u5fc3\uff0c\u4ed6\u5728\u5fc3\u91cc\u4e5f\u4ece\u6765\u5c31\u770b\u4e0d\u8d77\u6b4c\u560e\u65af\uff0c\u8ba4\u4e3a\u5bf9\u65b9\u662f\u4e2a\u7b28\u86cb\u3002\u8bba\u53ca\u5438\u8840\u9b3c\u4f4d\u9636\uff0c\u4ed6\u4eec\u90fd\u662f\u4eb2\u738b\uff0c\u867d\u7136\u6b4c\u560e\u65af\u786e\u5b9e\u5728\u6b66\u6280\u4e0a\u662f\u80dc\u8fc7\u4e00\u7b79\uff0c\u4f46\u8981\u8bf4\u8111\u5b50\uff0c\u8fea\u5185\u7279\u81ea\u8ba4\u4e3a\u53ef\u5f3a\u591a\u4e86\uff0c\u4ed6\u624d\u5e94\u8be5\u662f\u8fd9\u652f\u519b\u961f\u7684\u771f\u6b63\u7edf\u5e05\u624d\u5bf9\u3002\u5f53\u7136\uff0c\u8fea\u5185\u7279\u6709\u81ea\u77e5\u4e4b\u660e\uff0c\u6e05\u695a\u81ea\u5df1\u5728\u519b\u4e8b\u6307\u6325\u4e0a\u5e76\u975e\u62dc\u5c14\u7684\u5bf9\u624b\uff0c\u4f46\u8fd9\u6709\u4ec0\u4e48\u5173\u7cfb\u5462\uff1f\u8840\u6218\u6301\u7eed\u4e07\u5e74\uff0c\u6076\u9b54\u4ece\u6765\u5c31\u5728\u6218\u672f\u4e0a\u6ca1\u6709\u4efb\u4f55\u957f\u8fdb\uff0c\u4f46\u90a3\u4e9b\u559c\u6b22\u73a9\u5f04\u5404\u79cd\u5351\u9119\u82b1\u62db\u7684\u9b54\u9b3c\u4eec\uff0c\u53c8\u53d6\u5f97\u4e86\u591a\u5c11\u80dc\u5229\u5462\uff0c\u4ed6\u4eec\u8fde\u4e07\u6e0a\u5e73\u539f\u90fd\u6ca1\u80fd\u5360\u636e\u8fc7\u5427\u3002</p><p>\u5728\u7edd\u5bf9\u7684\u529b\u91cf\u9762\u524d\uff0c\u4efb\u4f55\u9634\u8c0b\u8be1\u8ba1\u90fd\u662f\u6ca1\u7528\u7684\u3002\u800c\u8fea\u5185\u7279\u624b\u4e2d\uff0c\u73b0\u5728\u5c31\u638c\u63e1\u7740\u8fd9\u7edd\u5bf9\u7684\u529b\u91cf\uff1a\u795e\u8eaf\u9b54\u50cf\u3002</p><p>\u9752\u94dc\u57ce\u5821\u4e00\u6218\uff0c\u795e\u8eaf\u9b54\u50cf\u5728\u70bc\u72f1\u708e\u9f99\u548c\u5815\u5929\u4f7f\u7684\u56f4\u653b\u4e4b\u4e0b\u53d7\u5230\u91cd\u521b\uff0c\u4f46\u56de\u57ce\u4e4b\u540e\u4fbf\u7acb\u523b\u9001\u8fdb\u5730\u4e0b\u5bab\u6bbf\u7ef4\u4fee\u6062\u590d\uff0c\u53ea\u8fc7\u4e86\u4e00\u65e5\u4fbf\u53c8\u795e\u5a01\u51db\u51db\u5730\u51fa\u73b0\u5728\u6218\u573a\u4e0a\u3002\u6bcf\u5f53\u5c40\u52bf\u4e0d\u5229\u7684\u65f6\u5019\uff0c\u5b83\u4fbf\u4f1a\u633a\u8eab\u800c\u51fa\uff0c\u529b\u633d\u72c2\u6f9c\uff1b\u6bcf\u5f53\u843d\u5165\u56f0\u5883\u7684\u65f6\u5019\uff0c\u5b83\u90fd\u80fd\u52c7\u731b\u5f53\u5148\uff0c\u6740\u5f00\u8840\u8def\u3002\u65e0\u8bba\u62dc\u5c14\u6709\u5343\u79cd\u5999\u8ba1\uff0c\u4e07\u822c\u795e\u7b97\uff0c\u8bbe\u8ba1\u4e86\u73af\u73af\u76f8\u6263\u7cbe\u5de7\u65e0\u6bd4\u7684\u9677\u9631\uff0c\u5230\u5934\u6765\u603b\u662f\u529f\u4e8f\u4e00\u7bd1\uff0c\u786c\u751f\u751f\u88ab\u8fd9\u795e\u8eaf\u9b54\u50cf\u7ed9\u6405\u5c40\u4e86\u3002</p><p>\u4ee5\u529b\u7834\u5de7\uff0c\u83ab\u8fc7\u4e8e\u6b64\u3002</p><p>\u9b54\u9b3c\u4eec\u50cf\u6d77\u6d6a\u4e00\u822c\u6c79\u6d8c\u649e\u4e0a\u4ea1\u7075\u7684\u9635\u5730\uff0c\u4e0b\u4e00\u77ac\u95f4\u4fbf\u662f\u60e8\u70c8\u7684\u53ae\u6740\u3002\u4f4e\u9636\u7684\u70ae\u7070\u4eec\u5df2\u7ecf\u5728\u6b64\u524d\u7684\u6218\u6597\u4e2d\u635f\u8017\u6b86\u5c3d\uff0c\u5982\u4eca\u8fd8\u80fd\u751f\u5b58\u4e0b\u6765\u7684\u5168\u90fd\u662f\u7cbe\u9510\u3002\u5c3d\u7ba1\u5982\u6b64\uff0c\u4ed6\u4eec\u7684\u751f\u547d\u4f9d\u7136\u8106\u5f31\u5982\u540c\u8584\u7eb8\uff0c\u6bcf\u4e00\u77ac\u95f4\u90fd\u6709\u4eba\u5012\u4e0b\uff0c\u6bcf\u4e00\u77ac\u95f4\u90fd\u6709\u751f\u547d\u6d88\u6563\uff0c\u518d\u9ad8\u660e\u7684\u6b66\u6280\uff0c\u518d\u575a\u56fa\u7684\u94e0\u7532\uff0c\u518d\u950b\u9510\u7684\u5200\u5251\uff0c\u4e5f\u4e0d\u8fc7\u80fd\u591a\u4e89\u53d6\u51e0\u79d2\u949f\u7684\u547c\u5438\u800c\u5df2\u3002\u5728\u9b54\u9b3c\u51f6\u731b\u7684\u653b\u52bf\u4e4b\u4e0b\uff0c\u4ea1\u7075\u4eec\u5f00\u59cb\u62b5\u6321\u4e0d\u4f4f\uff0c\u6b65\u6b65\u540e\u64a4\uff0c\u5df2\u7ecf\u9000\u5230\u62a4\u57ce\u6cb3\u7684\u8fb9\u7f18\u3002</p><p>\u8fea\u5185\u7279\u4e0d\u52a8\u58f0\u8272\u5730\u5ba1\u89c6\u7740\u6218\u573a\uff0c\u4ed6\u7684\u5fc3\u810f\u65e9\u5df2\u51b0\u51b7\uff0c\u4e0d\u4f1a\u4e3a\u4efb\u4f55\u6b7b\u4ea1\u800c\u98a4\u52a8\u3002\u201c\u6b7b\u4ea1\u9a91\u58eb\u5df2\u7ecf\u4e0d\u8db3\u4e24\u5343\uff0c\u201d\u4ed6\u6697\u81ea\u8ba1\u7b97\u7740\uff0c\u201c\u4f46\u5bf9\u65b9\u7684\u89d2\u9b54\u548c\u51b0\u9b54\u4e5f\u8fd1\u4e4e\u4f24\u4ea1\u6b86\u5c3d\uff0c\u9eef\u4e4b\u7ffc\u7684\u5815\u5929\u4f7f\u5df2\u7ecf\u53ea\u5269\u56db\u5341\u4f59\u540d\u2026\u2026\u201d</p><p>\u5c31\u5175\u529b\u4e0a\u800c\u8a00\uff0c\u4ea1\u7075\u5904\u4e8e\u52a3\u52bf\uff0c\u4f46\u8fd9\u662f\u6ca1\u6709\u628a\u795e\u8eaf\u9b54\u50cf\u8ba1\u7b97\u5728\u5185\u3002\u53ea\u8981\u628a\u5b83\u52a0\u8fdb\u6765\uff0c\u539f\u672c\u5927\u4f53\u5747\u8861\u7684\u5929\u5e73\u7acb\u523b\u5c31\u4f1a\u4e25\u91cd\u503e\u659c\u3002</p><p>\u201c\u8be5\u8ba9\u5b83\u51fa\u52a8\u4e86\u3002\u201d\u8fea\u5185\u7279\u60f3\u3002</p><p>\u4eff\u4f5b\u4e3a\u4e86\u6620\u8bc1\u4ed6\u7684\u60f3\u6cd5\uff0c\u4e00\u9635\u4f4e\u6c89\u7684\u8f70\u9e23\u58f0\u4ece\u80cc\u540e\u9690\u9690\u4f20\u6765\uff0c\u516b\u9053\u7c97\u5927\u7684\u5149\u67f1\u81ea\u9ed1\u77f3\u57ce\u7684\u5893\u5730\u4e0b\u65b9\u53d1\u51fa\uff0c\u5728\u9ad8\u7a7a\u4e2d\u6c47\u805a\uff0c\u5316\u6210\u4e00\u4e2a\u5e9e\u5927\u65e0\u5339\u7684\u865a\u5f71\uff0c\u7d27\u63a5\u7740\u5728\u51e0\u79d2\u949f\u5185\u51dd\u805a\u6210\u5b9e\u4f53\u3002\u8fd1\u4f3c\u7f1d\u5408\u602a\u7269\u7684\u795e\u8eaf\u9b54\u50cf\u518d\u4e00\u6b21\u73b0\u8eab\uff0c\u968f\u5373\u8dc3\u5165\u6218\u573a\u3002</p></article>"}}}]);
