(window.webpackJsonp=window.webpackJsonp||[]).push([[352],{374:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e5d\u8282 \u5408\u4f5c",content:"<article><h2>\u7b2c\u4e5d\u8282 \u5408\u4f5c</h2><p>\u5c31\u4f0a\u84d3\u4e1d\u800c\u8a00\uff0c\u5979\u73b0\u5728\u662f\u9636\u4e0b\u56da\uff0c\u8eab\u9677\u654c\u65b9\u7684\u57ce\u6c60\u4e2d\uff0c\u53c8\u88ab\u9b54\u6cd5\u9879\u5708\u675f\u7f1a\u538b\u5236\uff0c\u4e27\u5931\u6240\u6709\u80fd\u529b\uff0c\u81ea\u7136\u671f\u671b\u80fd\u591f\u548c\u743c\u6069\u5408\u4f5c\uff0c\u6216\u8005\u8bf4\u8fbe\u6210\u4ea4\u6613\uff0c\u8fd9\u662f\u5979\u552f\u4e00\u7684\u51fa\u8def\u3002\u5728\u8fd9\u51e0\u5929\u91cc\uff0c\u5979\u4f7f\u51fa\u6d51\u8eab\u89e3\u6570\uff0c\u4e0d\u65ad\u529d\u8bf4\u743c\u6069\u914d\u5408\u9b54\u9b3c\u53cd\u6208\u4e00\u51fb\uff0c\u8bb8\u8bfa\u79cd\u79cd\u597d\u5904\u3002\u800c\u5bf9\u4e8e\u743c\u6069\u6765\u8bf4\uff0c\u4ed6\u5bf9\u6076\u9b54\u4e5f\u4ece\u65e0\u534a\u70b9\u5fe0\u8bda\u5ea6\u53ef\u8a00\uff0c\u5c31\u4e2a\u4eba\u504f\u597d\u800c\u8a00\u8fd8\u66f4\u503e\u5411\u4e8e\u9b54\u9b3c\uff0c\u5982\u679c\u80fd\u591f\u5e2e\u52a9\u62dc\u5c14\u626d\u8f6c\u6218\u5c40\uff0c\u6253\u8d62\u8fd9\u4e00\u4ed7\uff0c\u81ea\u7136\u80fd\u635e\u5230\u66f4\u591a\u7684\u597d\u5904\u3002\u6240\u4ee5\u5c31\u8fd9\u70b9\u6765\u8bf4\uff0c\u53cc\u65b9\u90fd\u662f\u5f88\u6709\u5408\u4f5c\u610f\u5411\u7684\uff0c\u7136\u800c\u5177\u4f53\u7684\u7ec6\u8282\uff0c\u53cc\u65b9\u5374\u4e00\u76f4\u8c08\u4e0d\u62e2\u3002</p><p>\u743c\u6069\u575a\u6301\u8981\u6c42\u4f0a\u84d3\u4e1d\u5148\u628a\u5979\u4ece\u8fea\u5185\u7279\u90a3\u91cc\u7a83\u53d6\u6765\u7684\u60c5\u62a5\u516c\u5f00\u51fa\u6765\uff0c\u7136\u540e\u518d\u51b3\u5b9a\u662f\u5426\u8fdb\u4e00\u6b65\u5408\u4f5c\u3002\u5c31\u4ed6\u7684\u7acb\u573a\u800c\u8a00\uff0c\u8fd9\u662f\u60c5\u7406\u4e4b\u4e2d\u7684\u4e8b\u60c5\u3002\u4f0a\u84d3\u4e1d\u867d\u7136\u8bf4\u662f\u5f04\u5230\u4e86\u67d0\u4e9b\u60c5\u62a5\uff0c\u53ef\u4ee5\u5e2e\u52a9\u62dc\u5c14\u6253\u8d25\u9b54\u50cf\uff0c\u4f46\u95ee\u9898\u662f\u8c01\u77e5\u9053\u771f\u5047\u5462\uff1f\u60c5\u62a5\u6216\u8bb8\u662f\u9519\u8bef\u7684\uff0c\u6216\u8bb8\u5df2\u7ecf\u8fc7\u65f6\u4e86\uff0c\u6216\u8bb8\u867d\u7136\u6709\u7528\uff0c\u4f46\u6267\u884c\u8d77\u6765\u96be\u5ea6\u592a\u9ad8\uff0c\u6210\u529f\u53ef\u80fd\u6027\u592a\u4f4e\uff0c\u8fd9\u90fd\u662f\u9700\u8981\u8003\u8651\u7684\u95ee\u9898\u3002\u743c\u6069\u4e0d\u53ef\u80fd\u5728\u5bf9\u8fd9\u4e9b\u90fd\u4e00\u65e0\u6240\u77e5\u7684\u60c5\u51b5\u4e0b\u5c31\u7b54\u5e94\u548c\u62dc\u5c14\u5408\u4f5c\uff0c\u5c31\u7b97\u662f\u8981\u5192\u9669\uff0c\u603b\u4e5f\u5f97\u5148\u6382\u91cf\u6382\u91cf\u6709\u51e0\u6210\u628a\u63e1\u3002\u518d\u8bf4\u4e86\uff0c\u81ea\u5df1\u5982\u679c\u4e0d\u77e5\u9053\u60c5\u62a5\u7684\u5177\u4f53\u5185\u5bb9\uff0c\u90a3\u53c8\u600e\u4e48\u4f30\u7b97\u5b83\u7684\u4ef7\u503c\uff0c\u600e\u4e48\u53bb\u548c\u62dc\u5c14\u8c08\u5224\uff0c\u83b7\u53d6\u6700\u591a\u7684\u597d\u5904\u3002\u8bf4\u5f97\u96be\u542c\u70b9\uff0c\u54ea\u6015\u662f\u52ab\u532a\u7ed1\u7968\uff0c\u603b\u4e5f\u5f97\u77e5\u9053\u81ea\u5df1\u624b\u4e0a\u4eba\u8d28\u7684\u8eab\u4efd\u5730\u4f4d\uff0c\u4ef7\u503c\u51e0\u4f55\uff0c\u7136\u540e\u624d\u80fd\u53bb\u5c3d\u53ef\u80fd\u7d22\u8981\u6700\u591a\u7684\u8d4e\u91d1\u5427\u3002</p><p>\u4f46\u5bf9\u4e8e\u4f0a\u84d3\u4e1d\u800c\u8a00\uff0c\u743c\u6069\u7684\u8fd9\u79cd\u8981\u6c42\u663e\u7136\u5c31\u592a\u8fc7\u5206\u4e86\u3002\u4ed6\u8981\u6c42\u62ff\u5230\u6240\u6709\u60c5\u62a5\uff0c\u5374\u4ec0\u4e48\u90fd\u4e0d\u505a\u4fdd\u8bc1\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u7b49\u4e8e\u4e3b\u52a8\u6743\u5b8c\u5168\u638c\u63e1\u5728\u4ed6\u7684\u624b\u4e2d\uff0c\u4f0a\u84d3\u4e1d\u5219\u5904\u4e8e\u5f7b\u5e95\u7684\u88ab\u52a8\u5730\u4f4d\uff0c\u4e27\u5931\u6700\u540e\u4e00\u70b9\u7b79\u7801\u3002\u8fd9\u79cd\u65e0\u7406\u8981\u6c42\u5979\u53c8\u600e\u4e48\u53ef\u80fd\u7b54\u5e94\uff0c\u518d\u52a0\u4e0a\u62dc\u5c14\u5728\u9752\u94dc\u57ce\u4e0b\u4e00\u6218\u83b7\u80dc\uff0c\u987a\u52bf\u53cd\u51fb\uff0c\u8fd9\u51e0\u5929\u91cc\u56f4\u653b\u751a\u6025\uff0c\u5927\u6709\u4e00\u4e3e\u7834\u57ce\u7684\u52bf\u5934\uff0c\u4f0a\u84d3\u4e1d\u5e95\u6c14\u53d8\u8db3\uff0c\u6001\u5ea6\u4e5f\u76f8\u5e94\u5730\u5f3a\u786c\u8d77\u6765\u3002</p><p>\u7136\u800c\u5979\u73b0\u5728\u5df2\u7ecf\u65e0\u6cd5\u518d\u5f3a\u786c\u4e0b\u53bb\u4e86\u3002</p><p>\u201c\u6218\u51b5\u4f60\u5df2\u7ecf\u770b\u5f97\u5f88\u6e05\u695a\uff0c\u5c0f\u59d0\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u62dc\u5c14\u5c06\u519b\u653b\u57ce\u4e0d\u5229\uff0c\u773c\u4e0b\u5df2\u7ecf\u5230\u4e86\u5931\u8d25\u7684\u8fb9\u7f18\uff0c\u5982\u679c\u60f3\u7ffb\u76d8\u7684\u8bdd\uff0c\u73b0\u5728\u662f\u6700\u540e\u7684\u673a\u4f1a\u3002\u201d</p><p>\u6b32\u9b54\u51b7\u51b7\u5730\u770b\u7740\u4ed6\uff0c\u201c\u90a3\u6211\u8be5\u600e\u4e48\u505a\uff1f\u201d</p><p>\u201c\u5c31\u50cf\u6211\u8bf4\u7684\u90a3\u6837\uff0c\u4f60\u544a\u8bc9\u6211\u4f60\u6240\u83b7\u5f97\u7684\u60c5\u62a5\u5185\u5bb9\uff0c\u544a\u8bc9\u6211\u4f60\u7684\u8bbe\u60f3\u6784\u601d\u2014\u2014\u6211\u77e5\u9053\u4f60\u4e00\u5b9a\u6709\u3002\u5982\u679c\u786e\u5b9e\u53ef\u884c\u7684\u8bdd\uff0c\u90a3\u4e48\u6211\u4f1a\u5f88\u4e50\u610f\u914d\u5408\u3002\u201d</p><p>\u201c\u5982\u679c\u4f60\u89c9\u5f97\u4e0d\u884c\u5462\uff1f\u201d</p><p>\u201c\u90a3\u5c31\u53ea\u597d\u62b1\u6b49\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u4e0d\u516c\u5e73\uff01\u201d</p><p>\u743c\u6069\u53f9\u6c14\uff0c\u201c\u662f\u4e0d\u516c\u5e73\uff0c\u5c0f\u59d0\uff0c\u4f46\u6211\u603b\u5f97\u4e3a\u6211\u81ea\u5df1\u548c\u6211\u7684\u540c\u4f34\u4eec\u591a\u8003\u8651\u4e00\u70b9\u3002\u5f53\u7136\u8fd9\u4e0d\u662f\u95ee\u9898\u7684\u5173\u952e\u2014\u2014\u5173\u952e\u5728\u4e8e\uff0c\u4f60\u73b0\u5728\u8fd8\u6709\u522b\u7684\u9009\u62e9\u5417\uff1f\u201d</p><p>\u4f0a\u84d3\u4e1d\u6ca1\u6709\u3002</p><p>\u201c\u4e3a\u4e86\u5bf9\u4ed8\u8fd9\u4e2a\u9b54\u50cf\uff0c\u62dc\u5c14\u5927\u4eba\u505a\u4e86\u591a\u79cd\u9884\u6848\uff0c\u201d\u6c89\u9ed8\u534a\u664c\uff0c\u6b32\u9b54\u6162\u6162\u5f00\u53e3\uff0c\u201c\u5176\u4e2d\u6709\u4e00\u79cd\u65b9\u6848\uff0c\u662f\u5148\u5728\u6218\u573a\u4e0a\u5c06\u9b54\u50cf\u51fb\u4f24\uff0c\u7136\u540e\u8d81\u5b83\u4fee\u590d\u7684\u65f6\u5019\uff0c\u6f5c\u5165\u7ef4\u4fee\u57fa\u5730\u7834\u574f\uff0c\u5c06\u63a7\u5236\u9b54\u50cf\u7684\u5deb\u5996\u4eec\u6740\u6b7b\uff0c\u4ece\u800c\u627c\u6740\u9b54\u50cf\uff0c\u8ba9\u5b83\u4e0d\u80fd\u542f\u52a8\u3002\u201d</p><p>\u201c\u7136\u540e\u5462\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u65b9\u6848\u7684\u96be\u5904\uff0c\u9996\u5148\u5728\u4e8e\u65e0\u6cd5\u786e\u5b9a\u9b54\u50cf\u4e34\u65f6\u542f\u52a8\u6240\u9700\u8981\u7684\u65f6\u95f4\uff0c\u5176\u6b21\u5728\u4e8e\u65e0\u6cd5\u786e\u5b9a\u9b54\u50cf\u7ef4\u4fee\u65f6\u6240\u5728\u7684\u771f\u6b63\u4f4d\u7f6e\uff0c\u6240\u4ee5\u53ea\u6709\u7c97\u7565\u7684\u6784\u60f3\uff0c\u6ca1\u6709\u7ec6\u8282\uff0c\u65e0\u6cd5\u5b9e\u65bd\uff0c\u5f88\u5feb\u88ab\u5ffd\u7565\u4e86\u3002\u201d</p><p>\u201c\u8fd9\u4e48\u8bf4\uff0c\u73b0\u5728\u4f60\u5df2\u7ecf\u628a\u8fd9\u4e9b\u7ec6\u8282\u90fd\u5f04\u6e05\u695a\u4e86\uff1f\u201d</p><p>\u201c\u9b54\u50cf\u5982\u679c\u53d7\u521b\uff0c\u5728\u4fee\u590d\u671f\u95f4\u4e34\u65f6\u542f\u52a8\u7684\u8bdd\uff0c\u9700\u8981\u5341\u5206\u949f\uff0c\u201d\u6b32\u9b54\u8bf4\uff0c\u201c\u9b54\u50cf\u7684\u4fee\u590d\u57fa\u5730\u662f\u5893\u56ed\u533a\u7684\u5730\u4e0b\u5bab\u6bbf\uff0c\u73b0\u5728\u5206\u6210\u4e09\u5757\u533a\u57df\uff1a\u5916\u56f4\u3001\u5185\u5c42\u548c\u6838\u5fc3\uff0c\u9b54\u50cf\u5c31\u5728\u6838\u5fc3\u3002\u201d</p><p>\u5979\u8981\u6765\u7eb8\u7b14\uff0c\u5feb\u901f\u753b\u4e86\u4e00\u5f20\u5730\u56fe\uff0c\u201c\u8fd9\u662f\u5730\u4e0b\u5bab\u6bbf\u7684\u7ed3\u6784\u793a\u610f\u56fe\uff0c\u73b0\u5728\u662f\u9b54\u50cf\u7684\u4fee\u590d\u57fa\u5730\u201d\u5979\u5728\u5176\u4e2d\u4e00\u4e2a\u5730\u65b9\u70b9\u4e86\u4e00\u70b9\uff0c\u201c\u9b54\u50cf\u5c31\u5728\u8fd9\u91cc\u8fdb\u884c\u4fee\u590d\uff0c\u7b49\u5f85\u542f\u52a8\uff0c\u7531\u516b\u4f4d\u5deb\u5996\u770b\u5b88\u3002\u201d</p><p>\u743c\u6069\u6447\u5934\uff0c\u201c\u8fd9\u91cc\u65e9\u5c31\u5df2\u7ecf\u88ab\u5deb\u5996\u4eec\u5e03\u4e0b\u4e86\u6700\u5f3a\u7684\u8b66\u6212\u7ed3\u754c\uff0c\u5916\u4eba\u4e00\u8e0f\u5165\u5c31\u4f1a\u88ab\u53d1\u89c9\uff0c\u4ed6\u4eec\u4f1a\u7acb\u523b\u542f\u52a8\u9b54\u50cf\u7684\u3002\u201d</p><p>\u201c\u9b54\u50cf\u542f\u52a8\u9700\u8981\u5341\u5206\u949f\uff0c\u201d\u4f0a\u84d3\u4e1d\u53cd\u9a73\uff0c\u201c\u53ea\u8981\u62a2\u5728\u8fd9\u4e4b\u524d\uff0c\u80fd\u5728\u5341\u5206\u949f\u4e4b\u5185\u95ef\u8fdb\u6838\u5fc3\uff0c\u6740\u6389\u5deb\u5996\uff0c\u9b54\u50cf\u5c31\u6ca1\u6cd5\u542f\u52a8\u4e86\u3002\u201d</p><p>\u743c\u6069\u7aef\u8be6\u4e86\u4e00\u4f1a\u5730\u56fe\uff0c\u201c\u4ece\u5730\u4e0b\u5bab\u6bbf\u7684\u5165\u53e3\uff0c\u4e00\u8def\u6740\u5230\u6838\u5fc3\uff0c\u8fd9\u4e2d\u95f4\u8def\u7a0b\u53ef\u4e0d\u77ed\uff0c\u5730\u4e0b\u5bab\u6bbf\u91cc\u53ef\u4e5f\u6709\u4e0d\u5c11\u5b88\u536b\u3002\u201d</p><p>\u201c\u4f60\u522b\u5fd8\u4e86\uff0c\u8fd9\u91cc\u539f\u672c\u662f\u6211\u4eec\u7684\u57ce\u5e02\uff0c\u8fd9\u5ea7\u5730\u4e0b\u5bab\u6bbf\u662f\u6211\u4eec\u5efa\u7684\uff0c\u201d\u4f0a\u84d3\u4e1d\u8bf4\uff0c\u201c\u5bf9\u4e8e\u5b83\u7684\u7ed3\u6784\uff0c\u6211\u4eec\u4e86\u5982\u6307\u638c\uff0c\u8fd8\u6709\u5f88\u591a\u6697\u95e8\u5bc6\u9053\u53ef\u4ee5\u5229\u7528\u3002\u6211\u4ed4\u7ec6\u5730\u8ba1\u7b97\u8fc7\uff0c\u6210\u529f\u7684\u53ef\u80fd\u6027\u975e\u5e38\u9ad8\u3002\u201d</p><p>\u201c\u6709\u591a\u9ad8\uff1f\u201d</p><p>\u201c\u516b\u6210\u3002\u201d</p><p>\u743c\u6069\u7b11\u8d77\u6765\uff0c\u201c\u8bf4\u8c0e\u662f\u4e0d\u597d\u7684\uff0c\u5c0f\u59d0\uff0c\u800c\u4e14\u4e5f\u6ca1\u6709\u610f\u4e49\u3002\u201d</p><p>\u4f0a\u84d3\u4e1d\u54ac\u4e86\u54ac\u5634\u5507\uff0c\u201c\u4e94\u6210\u628a\u63e1\uff0c\u201d\u5979\u627f\u8ba4\uff0c\u201c\u81f3\u5c11\u6709\u4e94\u6210\u628a\u63e1\u3002\u201d</p><p>\u201c\u4f60\u786e\u5b9a\uff1f\u201d</p><p>\u201c\u6211\u662f\u9ed1\u73ab\u7470\u7684\u526f\u519b\u56e2\u957f\uff0c\u4e5f\u662f\u6574\u4e2a\u60c5\u62a5\u673a\u6784\u7684\u6700\u9ad8\u8d1f\u8d23\u4eba\uff0c\u591a\u5e74\u6765\u80fd\u591f\u5f97\u5230\u62dc\u5c14\u5927\u4eba\u7684\u4fe1\u4efb\u501a\u91cd\uff0c\u6b63\u662f\u56e0\u4e3a\u6211\u7684\u8ba1\u7b97\u80fd\u529b\u65e0\u4eba\u80fd\u6bd4\u3002\u6211\u8bf4\u6709\u4e94\u6210\u628a\u63e1\uff0c\u90a3\u4e48\u5c31\u4e00\u5b9a\u6709\u3002\u201d</p><p>\u743c\u6069\u601d\u5fd6\u7740\uff0c\u518d\u53bb\u770b\u5730\u56fe\uff0c\u201c\u4f60\u4ece\u8fea\u5185\u7279\u90a3\u91cc\u9003\u8131\uff0c\u4ed6\u4f1a\u4e0d\u4f1a\u56e0\u6b64\u8d77\u7591\uff0c\u628a\u9b54\u50cf\u8f6c\u79fb\u5730\u65b9\u5462\uff1f\u201d</p><p>\u201c\u4e0d\u4f1a\uff0c\u4ed6\u5e76\u4e0d\u77e5\u9053\u6211\u5077\u770b\u4e86\u90a3\u4e9b\u673a\u5bc6\u8d44\u6599\uff0c\u6211\u505a\u5f97\u5f88\u5de7\u5999\uff0c\u51b3\u4e0d\u4f1a\u7559\u534a\u70b9\u75d5\u8ff9\u3002\u4ed6\u53ea\u8ba4\u4e3a\u6211\u662f\u627e\u5230\u673a\u4f1a\u9003\u8dd1\uff0c\u6240\u4ee5\u6d3e\u6b7b\u4ea1\u9a91\u58eb\u6765\u8ffd\u6355\u7f62\u4e86\u3002\u201d</p><p>\u201c\u6709\u4e00\u4e2a\u5173\u952e\u95ee\u9898\u88ab\u5ffd\u7565\u4e86\uff0c\u201d\u6885\u83f2\u65af\u5728\u65c1\u8fb9\u9759\u9759\u63d0\u9192\uff0c\u201c\u8ba1\u5212\u6216\u8bb8\u4e0d\u9519\uff0c\u4f46\u8c01\u6765\u6267\u884c\u5462\uff1f\u201d</p><p>\u8fd9\u79cd\u5371\u9669\u7684\u5de5\u4f5c\uff0c\u743c\u6069\u7b49\u4eba\u81ea\u7136\u662f\u4e0d\u4f1a\u53bb\u505a\uff0c\u90a3\u5c31\u53ea\u53ef\u80fd\u662f\u57ce\u5916\u7684\u9b54\u9b3c\u4eec\u4e86\u3002\u4f46\u9ed1\u77f3\u57ce\u5982\u4eca\u6212\u5907\u68ee\u4e25\uff0c\u5deb\u5996\u4eec\u5e03\u4e0b\u4e86\u4e25\u5bc6\u7684\u9b54\u6cd5\u5c4f\u969c\uff0c\u4e00\u7cfb\u5217\u7684\u6b21\u5143\u951a\u5f7c\u6b64\u8054\u7ed3\uff0c\u5b8c\u5168\u963b\u9694\u4e86\u4ece\u57ce\u5916\u5f80\u57ce\u5185\u7684\u4f20\u9001\uff0c\u4ea1\u7075\u4eec\u53c8\u4e0d\u7720\u4e0d\u4f11\uff0c\u663c\u591c\u770b\u5b88\uff0c\u5929\u7a7a\u4e2d\u90fd\u6709\u9ab8\u9aa8\u4e9a\u9f99\u76d8\u65cb\u8b66\u6212\uff0c\u9b54\u9b3c\u4eec\u60f3\u5077\u5077\u6f5c\u5165\u8fdb\u6765\u641e\u7834\u574f\u53ef\u5f88\u96be\u3002</p><p>\u201c\u74e6\u5229\u65af\u5c06\u519b\u548c\u5f25\u8d5b\u4e9a\u5c0f\u59d0\uff0c\u201d\u6b32\u9b54\u8bf4\uff0c\u201c\u4ed6\u4eec\u4e24\u4f4d\u80fd\u591f\u8fdb\u6765\u3002\u201d</p><p>\u5176\u5b9e\u62dc\u5c14\u4e5f\u53ef\u4ee5\u6f5c\u5165\u8fdb\u6765\uff0c\u4ed6\u7684\u529b\u91cf\u6bd4\u74e6\u5229\u65af\u8fd8\u8981\u80dc\u4e0a\u4e00\u7b79\u3002\u53ea\u662f\u4f0a\u84d3\u4e1d\u7684\u8fd9\u4e2a\u8ba1\u5212\u91cc\uff0c\u5e76\u6ca1\u8003\u8651\u9000\u8def\u7684\u95ee\u9898\uff0c\u5c31\u7b97\u6210\u529f\u51fb\u6740\u5deb\u5996\uff0c\u7834\u574f\u9b54\u50cf\uff0c\u4e5f\u4f1a\u88ab\u57ce\u4e2d\u7684\u5b88\u536b\u5305\u56f4\u3002\u5f53\u7136\uff0c\u4ee5\u74e6\u5229\u65af\u548c\u5f25\u8d5b\u4e9a\u7684\u5b9e\u529b\uff0c\u6740\u5f00\u4e00\u6761\u8840\u8def\u9003\u8131\uff0c\u8fd9\u79cd\u53ef\u80fd\u6027\u8fd8\u662f\u4e0d\u4f4e\u7684\u3002\u4f46\u8fd9\u79cd\u5b64\u8eab\u6d89\u9669\u7684\u4e8b\u60c5\uff0c\u4f0a\u84d3\u4e1d\u4f5c\u4e3a\u90e8\u5c5e\uff0c\u65e0\u8bba\u5982\u4f55\u4e5f\u4e0d\u6562\u628a\u4e3b\u541b\u8ba1\u7b97\u5728\u5185\u3002</p><p>\u201c\u542c\u8d77\u6765\u4f3c\u4e4e\u6709\u70b9\u53ef\u884c\uff0c\u201d\u743c\u6069\u6c89\u541f\u7247\u523b\uff0c\u6162\u6162\u8bf4\uff0c\u201c\u4f46\u53ea\u6709\u4e94\u6210\u628a\u63e1\uff0c\u8fd8\u662f\u592a\u5192\u9669\u4e86\u3002\u201d</p><p>\u201c\u5982\u679c\u5355\u9760\u74e6\u5229\u65af\u548c\u5f25\u8d5b\u4e9a\u4e24\u4f4d\uff0c\u90a3\u4e48\u53ea\u6709\u4e94\u6210\u628a\u63e1\uff0c\u4f46\u5982\u679c\u52a0\u4e0a\u4f60\u4eec\u5462\uff1f\u201d\u4f0a\u84d3\u4e1d\u8bf4\uff0c\u201c\u6211\u867d\u7136\u529b\u91cf\u53d7\u5236\uff0c\u4f46\u773c\u5149\u5374\u8fd8\u6ca1\u4e22\u5931\uff0c\u51e0\u4f4d\u7684\u5b9e\u529b\u90fd\u4e0d\u4fd7\uff0c\u5982\u679c\u6709\u4f60\u4eec\u5e2e\u52a9\u7684\u8bdd\uff0c\u628a\u63e1\u81f3\u5c11\u80fd\u591f\u518d\u63d0\u5347\u4e00\u6210\u3002\u516d\u6210\u628a\u63e1\uff0c\u5df2\u7ecf\u8db3\u591f\u4e86\u3002\u201d</p><p>\u201c\u5514\uff0c\u8fd8\u662f\u592a\u5192\u9669\u4e86\u70b9\u554a\u3002\u201d</p><p>\u201c\u90a3\u4f60\u4eec\u53ef\u4ee5\u7f6e\u8eab\u4e8b\u5916\uff0c\u53ea\u8981\u628a\u8fd9\u4e2a\u60c5\u62a5\u9001\u7ed9\u62dc\u5c14\u5927\u4eba\uff0c\u4ed6\u81ea\u7136\u4f1a\u7ed9\u4f60\u4eec\u6ee1\u610f\u7684\u62a5\u916c\u3002\u201d</p><p>\u743c\u6069\u8f6c\u8fc7\u8138\u770b\u7740\u540c\u4f34\u3002</p><p>\u201c\u542c\u4f60\u7684\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u51db\u8038\u8038\u80a9\uff0c\u201c\u6211\u542c\u827e\u5f25\u8587\u7684\u3002\u201d</p><p>\u838e\u73de\u514b\u81ea\u7136\u66f4\u4e0d\u4f1a\u6709\u610f\u89c1\u3002</p><p>\u201c\u90a3\u4e48\u6211\u4eec\u5c31\u53ea\u5269\u4e0b\u6700\u540e\u4e00\u4e2a\u95ee\u9898\u9700\u8981\u8ba8\u8bba\u4e86\uff0c\u201d\u743c\u6069\u8f6c\u8fc7\u8138\u5bf9\u6b32\u9b54\u8bf4\uff0c\u201c\u600e\u6837\u624d\u80fd\u548c\u62dc\u5c14\u5c06\u519b\u53d6\u5f97\u8054\u7cfb\u5462\uff1f\u201d</p><p>\u201c\u4f60\u4eec\u628a\u90a3\u679a\u5047\u773c\u8fd8\u7ed9\u6211\uff0c\u6211\u53ec\u5524\u4e00\u53ea\u89d2\u9b54\uff0c\u4f60\u9001\u5b83\u51fa\u57ce\u53bb\u89c1\u62dc\u5c14\u5927\u4eba\u5c31\u662f\u3002\u201d</p><p>\u743c\u6069\u5fae\u5fae\u70b9\u5934\uff0c\u793a\u610f\u838e\u73de\u514b\u628a\u4f0a\u84d3\u4e1d\u7684\u5047\u773c\u8fd8\u7ed9\u5979\uff0c\u4f46\u5374\u5236\u6b62\u5979\u7acb\u523b\u53ec\u5524\u89d2\u9b54\uff0c\u201c\u7a0d\u7b49\u3002\u201d\u5deb\u5e08\u8bf4\uff0c\u53d6\u8fc7\u4e00\u5f20\u7f8a\u76ae\u7eb8\uff0c\u5237\u5237\u5730\u4e66\u5199\u8d77\u6765\u3002</p><p>\u51db\u597d\u5947\u5730\u51d1\u8fc7\u6765\u770b\uff0c\u89c1\u743c\u6069\u662f\u628a\u4f0a\u84d3\u4e1d\u6240\u8bf4\u7684\u60c5\u62a5\u4ee5\u53ca\u5206\u6790\u4f30\u7b97\uff0c\u4e00\u70b9\u4e0d\u6f0f\u5730\u90fd\u5199\u5728\u4e86\u7eb8\u4e0a\u3002\u201c\u73b0\u5728\u53ec\u5524\u89d2\u9b54\u5427\uff0c\u5c0f\u59d0\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u987a\u4fbf\u628a\u8fd9\u5f20\u7eb8\u5e26\u7ed9\u62dc\u5c14\u5c06\u519b\u3002\u201d</p><p>\u201c\u4f60\u53d1\u6655\u4e86\uff1f\u201d\u51db\u5947\u602a\uff0c\u201c\u76f4\u63a5\u628a\u8fd9\u4e9b\u90fd\u544a\u8bc9\u62dc\u5c14\u4e86\uff0c\u90a3\u6211\u4eec\u8fd8\u80fd\u5f97\u5230\u4ec0\u4e48\uff1f\u201d</p><p>\u201c\u8fd9\u4e9b\u60c5\u62a5\u90fd\u662f\u4f0a\u84d3\u4e1d\u5c0f\u59d0\u8f9b\u8f9b\u82e6\u82e6\u5f97\u6765\u7684\uff0c\u6211\u5982\u679c\u7528\u5b83\u53bb\u5411\u62dc\u5c14\u5c06\u519b\u4ea4\u6362\u4ec0\u4e48\u597d\u5904\uff0c\u90a3\u5c82\u4e0d\u6210\u4e86\u52d2\u7d22\u3002\u8fd9\u79cd\u5351\u9119\u7684\u4e8b\u60c5\uff0c\u6211\u600e\u4e48\u4f1a\u505a\u3002\u201d</p><p>\u4f0a\u84d3\u4e1d\u76b1\u7709\uff0c\u201c\u4f60\u5230\u5e95\u4ec0\u4e48\u610f\u601d\u3002\u201d</p><p>\u201c\u610f\u601d\u5f88\u7b80\u5355\uff0c\u5c0f\u59d0\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u5f04\u9519\u4e86\u4e24\u4ef6\u4e8b\u60c5\u3002\u201d</p><p>\u201c\u55ef\uff1f\u201d</p><p>\u201c\u9996\u5148\uff0c\u5bf9\u4e8e\u6211\u6765\u8bf4\uff0c\u516d\u6210\u628a\u63e1\u662f\u4e0d\u591f\u7684\u3002\u6211\u662f\u4e2a\u8c28\u614e\u7684\u4eba\uff0c\u81f3\u5c11\u9700\u8981\u516b\u6210\u628a\u63e1\u624d\u884c\u3002\u201d</p><p>\u201c\u53ef\u662f\u2026\u2026\u201d</p><p>\u743c\u6069\u6446\u6446\u624b\uff0c\u6253\u65ad\u4f0a\u84d3\u4e1d\u7684\u8bdd\uff0c\u201c\u5176\u6b21\uff0c\u4f60\u8ba1\u7b97\u6709\u70b9\u9519\u8bef\u3002\u5982\u679c\u6709\u6211\u4eec\u5e2e\u52a9\u7684\u8bdd\uff0c\u628a\u63e1\u5e76\u4e0d\u662f\u53ea\u63d0\u5347\u4e00\u6210\uff0c\u800c\u662f\u4e09\u6210\u2014\u2014\u4e5f\u5c31\u662f\u8bf4\uff0c\u4f1a\u6709\u516b\u6210\u628a\u63e1\uff0c\u201d\u4ed6\u70b9\u70b9\u5934\uff0c\u201c\u516b\u6210\u628a\u63e1\uff0c\u90a3\u5c31\u5927\u53ef\u4ee5\u8d4c\u4e0a\u4e00\u8d4c\u4e86\u3002\u201d</p><p>\u4ed6\u628a\u7f8a\u76ae\u7eb8\u9012\u7ed9\u4f0a\u84d3\u4e1d\uff0c\u201c\u544a\u8bc9\u62dc\u5c14\u5c06\u519b\uff0c\u5982\u679c\u4ed6\u613f\u610f\u63a5\u53d7\u6211\u7684\u5e2e\u52a9\uff0c\u90a3\u4e48\u5c31\u8bf7\u4ed6\u5f53\u9762\u6765\u8c08\u8c08\u2014\u2014\u5bf9\u4e86\uff0c\u8865\u5145\u4e00\u53e5\uff0c\u5982\u679c\u6709\u4ec0\u4e48\u514b\u5236\u5bf9\u4ed8\u5438\u8840\u9b3c\u7684\u597d\u4e1c\u897f\uff0c\u8bb0\u5f97\u591a\u5e26\u51e0\u4ef6\u3002\u201d</p></article>"}}}]);
