(window.webpackJsonp=window.webpackJsonp||[]).push([[352],{375:function(p){p.exports={chapterTitle:null,title:"\u7b2c228\u7ae0 \u5373\u5c06\u5230\u6765\u7684\u79bb\u522b",content:"<article><h2>\u7b2c228\u7ae0 \u5373\u5c06\u5230\u6765\u7684\u79bb\u522b</h2><p>\u3000\u3000\u5e02\u653f\u5e9c\u91cc\u8bf8\u4e8b\u7f20\u8eab\uff0c\u5510\u5b66\u8c26\u4e0e\u5f20\u77e5\u884c\u603b\u8981\u62d6\u5230\u5929\u5b8c\u5168\u9ed1\u6389\u624d\u8131\u8eab\u8d76\u5230\u897f\u57ce\u996d\u5e97\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u5bf9\u5f20\u77e5\u884c\u7b11\u7740\u8bf4\uff1a\u201c\u5e74\u524d\u6211\u8fd8\u5728\u8bf4\u4f60\u5bb6\u5c0f\u5b50\u672a\u6765\u7684\u524d\u7a0b\u53ef\u9650\u91cf\uff0c\u6211\u770b\u4e5f\u4e0d\u7528\u7b49\u672a\u6765\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5927\u5bb6\u90fd\u5728\u7eb5\u5bb9\u4ed6\uff0c\u201d\u5f20\u77e5\u884c\u7b11\u7740\u8bf4\uff0c\u201c\u675c\u5c0f\u5c71\u4ed6\u513f\u5b50\u8d76\u5230\u5b66\u6821\u53bb\uff0c\u62a2\u5230\u4e00\u4e2a\u8bb0\u5927\u8fc7\u5904\u5206\uff0c\u504f\u504f\u4ed6\u6ca1\u4e8b\uff0c\u4e0d\u80fd\u518d\u5938\u4ed6\u4e86\uff0c\u518d\u5938\u4ed6\u5c31\u8981\u5f97\u610f\u5fd8\u5f62\u4e86\u3002\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u54c8\u54c8\u5927\u7b11\uff0c\u4ed6\u4e00\u76f4\u5173\u6ce8\u7740\u8fd9\u4ef6\u4e8b\uff0c\u51e0\u4e4e\u6bcf\u4e00\u7ec6\u8282\u90fd\u6709\u4eba\u5411\u4ed6\u6c47\u62a5\uff0c\u8fd9\u4e00\u4ed7\u53ef\u8c13\u7cbe\u5f69\u7edd\u4f26\uff0c\u66f4\u5999\u5904\u662f\u5468\u5bcc\u660e\u3001\u4e07\u52c7\u6839\u672c\u4e0d\u6653\u5f97\u8f93\u5728\u54ea\u91cc\uff0c\u770b\u4e0a\u53bb\u53ea\u662f\u5efa\u59d4\u526f\u4e3b\u4efb\u9ec4\u67f1\u5168\u6ca1\u6709\u6309\u5e38\u7406\u5148\u901a\u77e5\u4e07\u52c7\u800c\u662f\u76f4\u63a5\u62a5\u4e86\u6848\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u65e9\u5728\u5305\u53a2\u91cc\u7b49\u5f97\u809a\u5b50\u997f\u762a\uff0c\u624d\u770b\u5230\u5510\u5b66\u8c26\u8ddf\u7238\u7238\u59d7\u59d7\u6765\u8fdf\uff0c\u5510\u5a67\u62b1\u6028\u7740\u4e0d\u505c\uff0c\u53c8\u5174\u594b\u7684\u8bf4\u8d77\u8ddf\u65b9\u5b8f\u6e05\u5bf9\u8d28\u7684\u4e8b\u60c5\uff1a\u201c\u5f53\u7740\u5927\u5bb6\u7684\u9762\u8ddf\u526f\u5e02\u957f\u8bf4\u8bdd\u5462\uff0c\u624b\u5fc3\u91cc\u90fd\u634f\u7740\u6c57\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6ca1\u51fa\u606f\uff0c\u8fd9\u70b9\u7834\u4e8b\u90fd\u8bf4\u4e86\u591a\u5c11\u904d\u4e86\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u6539\u5929\u628a\u5e02\u957f\u62ce\u5230\u8bb2\u53f0\u597d\u597d\u8bad\u4e00\u987f\uff0c\u4fdd\u7ba1\u4f60\u624b\u5fc3\u4e00\u70b9\u6c57\u90fd\u4e0d\u6d41\u2026\u2026\u201d</p><p>\u3000\u3000\u5927\u5bb6\u54c8\u54c8\u5927\u7b11\uff0c\u90fd\u8bf4\u54ea\u6709\u8ddf\u81ea\u5df1\u7238\u7238\u8bf4\u8bdd\u8fd8\u6709\u7d27\u5f20\u5f97\u624b\u5fc3\u6d41\u6c57\u7684\u3002</p><p>\u3000\u3000\u201c\u4eca\u5929\u53c8\u662f\u4f60\u5728\u80cc\u540e\u4f7f\u9b3c\uff1f\u201d\u5510\u5b66\u8c26\u624b\u6309\u7740\u5f20\u606a\u7684\u80a9\u8180\uff0c\u201c\u542c\u8bf4\u4f60\u65e9\u4e0a\u53bb\u627e\u5510\u5a67\uff0c\u8f6c\u773c\u5374\u6ca1\u89c1\u4f60\u8ddf\u5510\u5a67\u56de\u5b66\u6821\uff0c\u675c\u98de\u6328\u4e86\u4e00\u4e2a\u5904\u5206\uff0c\u4f60\u5012\u662f\u6ed1\u5934\u5f97\u5f88\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eca\u5929\u8981\u966a\u6211\u5927\u8205\u901b\u6d77\u5dde\uff0c\u54ea\u6709\u65f6\u95f4\u53bb\u51d1\u70ed\u95f9\uff1f\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u8ddf\u6881\u56fd\u5174\u63e1\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u542c\u77e5\u884c\u8bf4\u4f60\u60f3\u8f6c\u4e1a\u652f\u63f4\u5730\u65b9\u5efa\u8bbe\uff0c\u6211\u8c28\u4ee3\u8868\u6211\u4e2a\u4eba\u6b22\u8fce\u4f60\u2026\u2026\u201d\u5927\u5bb6\u53c8\u662f\u4e00\u7b11\u3002</p><p>\u3000\u3000\u6881\u56fd\u5174\u8fd9\u624d\u770b\u5230\u59b9\u592b\u5f20\u77e5\u884c\u5728\u6d77\u5dde\u5e02\u91cc\u7684\u6839\u57fa\u7edd\u4e0d\u540c\u4e00\u822c\u7684\u5904\u7ea7\u5e72\u90e8\uff0c\u9152\u684c\u4e0a\u6ca1\u6709\u63d0\u8f6c\u4e1a\u7684\u4e8b\u60c5\uff0c\u4ed6\u5fc3\u91cc\u5df2\u7ecf\u4e0b\u4e86\u51b3\u5fc3\uff0c\u4ed6\u7684\u5de5\u4f5c\u8c03\u52a8\u6025\u4e0d\u6765\uff0c\u51b3\u5b9a\u8ba9\u513f\u5b50\u6881\u519b\u5148\u628a\u624b\u5934\u7684\u5de5\u4f5c\u4e22\u6389\uff0c\u5e26\u7740\u5973\u670b\u53cb\u5148\u8fc7\u6765\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5403\u8fc7\u665a\u996d\uff0c\u5510\u5b66\u8c26\u4e00\u5bb6\u4eba\u5148\u8d70\uff1b\u5927\u8205\u3001\u5927\u8205\u5988\u3001\u8868\u59d0\u8fd8\u8981\u4f4f\u4ed6\u5bb6\uff0c\u5f20\u606a\u4e0d\u60f3\u518d\u7761\u6c99\u53d1\uff0c\u5c31\u94bb\u8fdb\u5c0f\u53d4\u7684\u8f66\u91cc\u3002</p><p>\u3000\u3000\u665a\u996d\u524d\uff0c\u5085\u4fca\u8fc7\u6765\u5c06\u9a6c\u6d77\u9f99\u63a5\u8d70\uff0c\u5728\u5bb6\u4eba\u8ddf\u5510\u5b66\u8c26\u4e00\u5bb6\u9762\u524d\uff0c\u5f20\u606a\u89c9\u5f97\u8fd8\u662f\u4fdd\u6301\u4f4e\u8c03\u7684\u597d\u3002</p><p>\u3000\u3000\u201c\u8981\u8bf4\u5b89\u7f6e\u623f\u7684\u4e8b\u60c5\uff1f\u201d\u5f20\u77e5\u975e\u7591\u60d1\u7684\u770b\u7740\u5f20\u606a\u94bb\u8fdb\u4ed6\u7684\u8f66\u91cc\u6765\u3002</p><p>\u3000\u3000\u201c\u662f\u6d77\u88d5\u6253\u7b97\u5165\u8d44\u5b89\u7f6e\u623f\u9879\u76ee\uff0c\u6211\u64cd\u8fd9\u4efd\u95f2\u5fc3\u505a\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u62b1\u5934\u8737\u5728\u8f66\u5ea7\uff0c\u4e8b\u4e0d\u5173\u5df1\u7684\u8f7b\u677e\u8bf4\u9053\u3002</p><p>\u3000\u3000\u6d77\u6cf018\uff05\u7684\u80a1\u6743\u8d76\u7740\u8fd9\u4e24\u5929\u5df2\u7ecf\u4ea4\u5272\u6389\uff0c\u65e2\u7136\u8c22\u5bb6\u5bf9\u9526\u6e56\u4e5f\u8d77\u4e86\u89ca\u89ce\u4e4b\u5fc3\uff0c\u5f20\u606a\u4e0e\u8c22\u665a\u6674\u4fbf\u51b3\u5b9a\u5bf9\u5b8f\u8fdc\u7684\u5165\u8d44\u4e0d\u518d\u4ee5\u9526\u6e56\u7684\u540d\u4e49\uff0c\u514d\u5f97\u4ee5\u540e\u518d\u8282\u5916\u751f\u679d\uff0c\u800c\u662f\u4ee5\u6d77\u88d5\u516c\u53f8\u7684\u540d\u4e49\u5bf9\u5b89\u7f6e\u623f\u9879\u76ee\u6295\u8d44\u4e00\u5343\u4e07\uff0c\u800c\u4e0d\u662f\u5bf9\u5b8f\u8fdc\u516c\u53f8\u76f4\u63a5\u5165\u8d44\u3002</p><p>\u3000\u3000\u57ce\u89c4\u8bbe\u8ba1\u9662\u4e0e\u6d77\u5efa\u516c\u53f8\u7b49\u56fd\u6709\u4f01\u4e8b\u4e1a\u5355\u4f4d\u7684\u9000\u51fa\uff0c\u8ba9\u5b8f\u8fdc\u6210\u4e3a\u771f\u6b63\u7684\u79c1\u8425\u4f01\u4e1a\uff0c\u6709\u8fc7\u53bb\u4e00\u5e74\u7684\u7f13\u51b2\uff0c\u5b8f\u8fdc\u5728\u6d77\u5dde\u5e02\u5df2\u7ecf\u624e\u4e0b\u6839\u57fa\uff0c\u5f53\u521d\u4ece\u8fd9\u4e9b\u4f01\u4e8b\u4e1a\u5355\u4f4d\u8fdb\u5165\u5b8f\u8fdc\u516c\u53f8\u7684\u6280\u672f\u3001\u7ba1\u7406\u4eba\u5458\uff0c\u76f8\u5f53\u4e00\u90e8\u5206\u56e0\u4e3a\u5b8f\u8fdc\u652f\u4ed8\u7684\u4e30\u539a\u85aa\u916c\u7559\u4e86\u4e0b\u6765\u3002\u867d\u7136\u5728\u5229\u76ca\u4e0a\u6709\u4e0d\u4e00\u81f4\u7684\u5730\u65b9\uff0c\u4f46\u6bd5\u7adf\u5f20\u77e5\u975e\u80cc\u540e\u7ad9\u7740\u5e02\u957f\u5510\u5b66\u8c26\uff0c\u800c\u4e14\u5f20\u77e5\u975e\u662f\u94c1\u5b9a\u5fc3\u534f\u52a9\u5510\u5b66\u8c26\u505a\u5b89\u7f6e\u623f\u9879\u76ee\uff0c\u57ce\u89c4\u8bbe\u8ba1\u9662\u7b49\u4f01\u4e8b\u4e1a\u5355\u4f4d\u9000\u51fa\u65f6\uff0c\u5e76\u6ca1\u6709\u62c6\u5f20\u77e5\u975e\u7684\u53f0\uff0c\u5f20\u77e5\u975e\u62c6\u5e8a\u5356\u67dc\u3001\u53c8\u627e\u4eba\u501f\u4e86\u4e00\u767e\u591a\u4e07\u5c06\u57ce\u89c4\u8bbe\u8ba1\u9662\u7b49\u4f01\u4e8b\u4e1a\u5355\u4f4d\u624b\u91cc\u7684\u80a1\u6743\u4e70\u4e0b\u6765\uff0c\u4e00\u4e3e\u638c\u63e1\u5b8f\u8fdc\u8fd180\uff05\u7684\u63a7\u80a1\u6743\uff0c\u6210\u4e3a\u5b8f\u8fdc\u7684\u771f\u6b63\u8001\u677f\u3002</p><p>\u3000\u3000\u60f3\u60f3\u4e00\u5e74\u4e4b\u524d\uff0c\u5f20\u77e5\u975e\u8fd8\u7a9d\u5728\u4e1c\u793e\u53bf\uff0c\u867d\u7136\u624b\u91cc\u6709\u5bb6\u5efa\u7b51\u516c\u53f8\uff0c\u8bf4\u5f20\u77e5\u975e\u662f\u5927\u5de5\u5934\u66f4\u6070\u5f53\u4e9b\uff0c\u8f6c\u8fdb\u6d77\u5dde\u5e02\u624d\u77ed\u77ed\u4e00\u5e74\u7684\u65f6\u95f4\uff0c\u624b\u91cc\u5c31\u638c\u63e1\u4e00\u5bb6\u8d44\u4ea7\u8d85\u5343\u4e07\u7684\u516c\u53f8\uff0c\u8fd9\u534e\u4e3d\u7684\u8f6c\u8eab\uff0c\u60f3\u60f3\u5c31\u8ba9\u4eba\u5174\u594b\u554a\u3002</p><p>\u3000\u3000\u5f20\u77e5\u975e\u77e5\u9053\u8fd9\u5176\u4e2d\u6709\u5f20\u606a\u8bb8\u591a\u7684\u529f\u52b3\uff0c\u4ed6\u5f88\u5e0c\u671b\u5f20\u606a\u4e5f\u80fd\u53c2\u4e0e\u5b8f\u8fdc\u516c\u53f8\u7684\u7ecf\u8425\uff0c\u4fa7\u5934\u770b\u7740\u5f20\u606a\u6d51\u8eab\u4e0a\u4e0b\u65e0\u4e00\u5904\u4e0d\u8212\u5766\u7684\u6837\u5b50\uff0c\u8bf4\u9053\uff1a\u201c\u9526\u6e56\u4ece\u6d77\u6cf0\u64a4\u80a1\uff0c\u62ff\u7684\u94b1\u4f60\u4e0d\u5f97\u520630\uff05\uff1f\u4f60\u8fd9\u94b1\u7559\u5728\u624b\u91cc\u505a\u4ec0\u4e48\uff0c\u6295\u5230\u5b8f\u8fdc\u6765\uff0c\u80a1\u6743\u6211\u6253\u6298\u5356\u7ed9\u4f60\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u4e1d\u6beb\u4e0d\u4e3a\u5fc3\u52a8\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8001\u5b50\u4e00\u65e5\u5728\u6d77\u5dde\u5f53\u5b98\uff0c\u6211\u4e00\u65e5\u4e0d\u6cbe\u5de5\u7a0b\u3001\u571f\u5730\u4e0a\u7684\u4e8b\uff0c\u8fd9\u53ef\u4e0d\u662f\u4e00\u4e2a\u6e05\u8005\u81ea\u6e05\u7684\u793e\u4f1a\uff0c\u4f55\u51b5\u6211\u8fd9\u94b1\u6709\u522b\u7684\u7528\u5904\u3002\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u7528\u5904\uff0c\u201d\u5f20\u77e5\u975e\u4fa7\u8fc7\u8eab\u5b50\u6765\uff0c\u95ee\u9053\uff0c\u201c\u7231\u8fbe\u90a3\u91cc\u662f\u7f3a\u94b1\uff0c\u4f46\u662f\u8fd9\u70b9\u94b1\u7838\u8fdb\u53bb\uff0c\u8fd8\u57ab\u4e0d\u4e86\u5e95\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7231\u8fbe\u6e34\u6c42\u7684\u8d44\u91d1\u91cf\u662f\u5927\uff0c\u7231\u8fbe\u6240\u9700\u8981\u7684\u5de8\u91cf\u8d44\u91d1\u6682\u65f6\u53ea\u80fd\u4ece\u5e02\u573a\u7ecf\u9500\u4f53\u7cfb\u91cc\u62bd\u53d6\uff0c\u8fd9\u70b9\u5c0f\u94b1\uff0c\u6211\u6709\u522b\u7684\u7528\u5904\u3002\u201d\u5f20\u606a\u95ed\u76ee\u517b\u795e\uff0c\u4e5f\u4e0d\u7ec6\u8bf4\u8fd9\u94b1\u7684\u7528\u5904\u3002</p><p>\u3000\u3000\u8bb8\u601d\u8981\u53bb\u9999\u6e2f\u7b79\u5efa\u65b0\u516c\u53f8\uff0c\u6ca1\u6709\u8d44\u91d1\u5c31\u592a\u8f9b\u82e6\u4e86\uff0c\u6d77\u6cf0\u7684\u80a1\u6743\u4ea4\u5272\u7ed3\u675f\uff0c\u5f20\u606a\u624b\u91cc\u4e5f\u591a\u4e86\u56db\u4e94\u767e\u4e07\u8d44\u91d1\uff0c\u4ed6\u6253\u7b97\u90fd\u8ba9\u8bb8\u601d\u5e26\u5230\u9999\u6e2f\u53bb\u8d2d\u7f6e\u7269\u4e1a\u3002</p><p>\u3000\u3000\u719f\u6089\u7ecf\u6d4e\u7684\u4eba\u90fd\u77e5\u9053\uff0c1985\u52301997\u5e74\u662f\u9999\u6e2f\u623f\u5730\u4ea7\u4e1a\u6700\u540e\u7684\u4e00\u4e2a\u7e41\u8363\u671f\uff0c\u4f46\u8fd9\u4e2a\u8fc7\u7a0b\u4e5f\u662f\u4e00\u6ce2\u4e09\u6298\u300284\u5e74\u4e2d\u82f1\u4e24\u56fd\u7b7e\u7f72\u300a\u4e2d\u82f1\u8054\u5408\u58f0\u660e\u300b\uff0c\u660e\u786e\u9999\u6e2f\u7684\u653f\u6cbb\u524d\u9014\uff0c\u540c\u65f6\u53d7\u5230\u9999\u6e2f\u7ecf\u6d4e\u7ed3\u6784\u8f6c\u578b\u4e0e\u670d\u52a1\u4e1a\u5feb\u901f\u53d1\u5c55\u7684\u5f71\u54cd\uff0c\u5199\u5b57\u697c\u4f9b\u5e94\u4e25\u91cd\u4e0d\u8db3\uff0c\u5bfc\u81f4\u623f\u4ef7\u3001\u79df\u91d1\u6025\u5347\uff0c\u79df\u91d1\u6c34\u5e73\u4f4d\u5217\u5168\u7403\u7b2c\u4e09\u4f4d\uff0c\u4e00\u76f4\u523094\u5e74\uff0c\u9999\u6e2f\u7684\u623f\u5730\u4ea7\u4e1a\u51fa\u73b0\u4e00\u4e2a\u660e\u663e\u7684\u9ad8\u5cf0\uff0c\u793e\u4f1a\u8206\u8bba\u8981\u6c42\u6e2f\u5e9c\u51fa\u9762\u5e72\u9884\uff0c\u52a0\u4e0a\u5728\u6b64\u4e4b\u524d\uff0c\u4e2d\u82f1\u7b2c\u5341\u4e03\u8f6e\u8c08\u5224\u7684\u7834\u88c2\uff0c\u6210\u4e3a\u9999\u6e2f\u56de\u5f52\u9014\u4e2d\u6700\u5927\u7684\u653f\u6cbb\u98ce\u6ce2\uff0c\u5bfc\u81f4\u9999\u6e2f\u5e02\u6c11\u4e0e\u6295\u8d44\u8005\u5bf9\u9999\u6e2f\u56de\u5f52\u4ea7\u751f\u65b0\u7684\u6050\u60e7\uff0c\u79cd\u79cd\u56e0\u7d20\u4f5c\u7528\u4e0b\uff0c\u81f4\u4f7f\u9999\u6e2f\u4f4f\u5b85\u4ef7\u683c\u5e73\u5747\u4e0b\u8dcc30\uff05\u4e4b\u591a\u3002</p><p>\u3000\u3000\u5230\u4eca\u5e74\u51ac\u5929\uff0c\u9999\u6e2f\u7684\u653f\u6cbb\u524d\u9014\u5c06\u8fdb\u4e00\u6b65\u660e\u6717\uff0c\u9999\u6e2f\u5e02\u6c11\u548c\u6295\u8d44\u8005\u5bf9\u56de\u5f52\u7684\u6050\u60e7\u8f6c\u4e3a\u5bf9\u672a\u6765\u7684\u61a7\u61ac\uff0c\u540c\u65f6\u6d77\u5916\u4e0e\u5185\u5730\u8d44\u91d1\u5927\u91cf\u6d8c\u5165\u9999\u6e2f\uff0c\u63a8\u52a8\u9999\u6e2f\u623f\u5730\u4ea7\u573a\u8fdb\u51fa\u6700\u540e\u7684\u5feb\u901f\u4e0a\u5347\u671f\uff0c\u4e00\u76f4\u5230\u4e5d\u4e03\u5e74\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u7a81\u7136\u7206\u53d1\uff0c\u624d\u8ba9\u9999\u6e2f\u623f\u5730\u4ea7\u5e02\u573a\u5f7b\u5e95\u7684\u8fdb\u5165\u5341\u5e74\u4e25\u51ac\u671f\u3002</p><p>\u3000\u3000\u8bb8\u601d\u4f1a\u4ee5\u8d44\u672c\u6295\u8d44\u4eba\u7684\u540d\u4e49\u7533\u8bf7\u9999\u6e2f\u5c45\u7559\u6743\uff0c\u80fd\u5728\u4e24\u4e09\u4e2a\u6708\u7684\u65f6\u95f4\u5c06\u5165\u7c4d\u4e0e\u7b79\u5efa\u516c\u53f8\u7684\u4e8b\u60c5\u529e\u59a5\u5df2\u7ecf\u4e0d\u9519\uff0c\u80fd\u8d76\u5230\u5165\u51ac\u4e4b\u524d\u8d2d\u7f6e\u7269\u4e1a\uff0c\u4e5f\u7b97\u642d\u4e0a\u9999\u6e2f\u623f\u5730\u4e1a\u9ec4\u91d1\u5341\u5e74\u6700\u540e\u4e24\u5e74\u7684\u8f89\u714c\u73ed\u8f66\uff0c\u53ea\u662f\u80fd\u591f\u52a8\u7528\u7684\u8d44\u91d1\u5341\u5206\u6709\u9650\uff0c\u5f53\u7136\uff0c\u771f\u6b63\u7684\u6295\u8d44\u673a\u4f1a\u8fd8\u662f\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u7684\u6765\u88ad\u3002</p><p>\u3000\u3000\u5f20\u606a\u94bb\u8fdb\u5c0f\u53d4\u7684\u8f66\u91cc\uff0c\u53ea\u662f\u7b49\u4ed6\u7238\u5988\u8ddf\u5927\u8205\u4e00\u5bb6\u4eba\u6253\u8f66\u79bb\u5f00\uff0c\u5750\u5728\u8f66\u4e0a\u53d1\u4e86\u4e00\u4f1a\u6123\uff0c\u624d\u7741\u5f00\u773c\u775b\uff1a\u201c\u554a\uff0c\u6211\u60f3\u8d77\u6765\u8fd8\u6709\u5176\u4ed6\u4e8b\u60c5\u2026\u2026\u201d\u5c31\u63a8\u95e8\u4e0b\u8f66\uff0c\u6325\u4e86\u6325\u8ba9\u5c0f\u53d4\u5148\u8d70\u3002</p><p>\u3000\u3000\u5f20\u77e5\u975e\u65e0\u5948\u7684\u7b11\u4e86\u7b11\uff0c\u4ed6\u867d\u7136\u4e3a\u81ea\u5df1\u7684\u5b8f\u8fdc\u516c\u53f8\u81ea\u8c6a\uff0c\u4f46\u6bd4\u8d77\u5f20\u606a\u8fd9\u4e00\u5e74\u6765\u6240\u505a\u7684\u4e8b\u60c5\uff0c\u4ed6\u5c31\u592a\u81ea\u6127\u4e0d\u5982\u4e86\uff0c\u5f20\u606a\u4ec5\u524d\u540e\u4e24\u6b21\u4ece\u6d77\u6cf0\u516c\u53f8\u5957\u53d6\u7684\u73b0\u91d1\u5c31\u9ad8\u8fbe\u4e03\u767e\u591a\u4e07\uff0c\u6b64\u65f6\u8fd8\u5360\u6709\u9526\u6e5630\uff05\u7684\u80a1\u6743\uff0c\u7231\u8fbe\u7684\u4e8b\u60c5\uff0c\u5f20\u606a\u6ca1\u6709\u8ddf\u4ed6\u7ec6\u8bf4\uff0c\u53ea\u77e5\u9053\u4e01\u69d0\u3001\u82cf\u6d25\u4e1c\u4e00\u6837\u662f\u7231\u8fbe\u7684\u80a1\u4e1c\uff0c\u4f46\u662f\u4e01\u69d0\u4e0e\u82cf\u6d25\u4e1c\u662f\u5f20\u606a\u8bf7\u5230\u6d77\u5dde\u6765\u7684\uff0c\u800c\u4e14\u5927\u4e8b\u90fd\u51b3\u4e8e\u5f20\u606a\uff0c\u778e\u773c\u4eba\u90fd\u77e5\u9053\u5f20\u606a\u624d\u7231\u8fbe\u771f\u6b63\u7684\u63a7\u5236\u4eba\u3002</p><p>\u3000\u3000\u5f20\u77e5\u975e\u6447\u4e86\u6447\u5934\uff0c\u5fc3\u60f3\u5b8f\u8fdc\u7684\u4e8b\u4e1a\u8fd8\u65e0\u6cd5\u6253\u52a8\u4ed6\u8fd9\u4e2a\u8ba9\u4eba\u770b\u4e0d\u900f\u7684\u4f84\u5b50\u3002</p><p>\u3000\u3000\u770b\u7740\u5c0f\u53d4\u5c06\u8f66\u5f00\u8d70\uff0c\u5f20\u606a\u7ed9\u8bb8\u601d\u6253\u7535\u8bdd\u3002\u867d\u7136\u5728\u6d77\u5dde\uff0c\u4f46\u662f\u5728\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u52a0\u4e0a\u5404\u81ea\u7684\u4e8b\u60c5\u4e5f\u591a\uff0c\u4e0d\u65b9\u4fbf\u4e0e\u8bb8\u601d\u9891\u7e41\u7684\u5355\u72ec\u76f8\u5904\uff0c\u4f46\u662f\u8ba9\u8bb8\u601d\u53bb\u9999\u6e2f\uff0c\u5206\u5f00\u4e24\u5730\uff0c\u53c8\u8ba9\u4eba\u5341\u5206\u4e0d\u820d\uff1b\u90a3\u603b\u662f\u4e00\u79cd\u5206\u5f00\u4e24\u5730\u7684\u79bb\u6101\u522b\u7eea\u3002</p><p>\u3000\u3000\u8bb8\u601d\u5bf9\u5bb6\u91cc\u53ea\u8bf4\u53bb\u9999\u6e2f\u51fa\u5dee\uff0c\u4e5d\u4e03\u5e74\u4e4b\u524d\uff0c\u4e24\u5730\u7684\u4ea4\u6d41\u8fd8\u4e0d\u5145\u5206\uff0c\u53bb\u9999\u6e2f\u516c\u5e72\uff0c\u4e0e\u51fa\u56fd\u65e0\u5f02\uff0c\u90a3\u65f6\u5019\u51fa\u56fd\u65c5\u6e38\u8fd8\u6ca1\u6709\u5174\u8d77\uff0c\u52a0\u4e0a\u8bb8\u601d\u8fd9\u6b21\u53bb\u9999\u6e2f\u7684\u65f6\u95f4\u6bd4\u8f83\u957f\uff0c\u8bb8\u601d\u5bb6\u91cc\u5bf9\u8fd9\u4e8b\u5f88\u91cd\u89c6\u3002\u8bb8\u7ef4\u4e5f\u8d81\u7740\u5927\u5b66\u5f00\u5b66\u524d\u4e24\u5929\u7684\u7a7a\u5f53\u56de\u4e86\u6d77\u5dde\u3002</p><p>\u3000\u3000\u8bb8\u601d\u7684\u58f0\u97f3\u523b\u610f\u7684\u5e73\u6de1\uff0c\u5f20\u606a\u77e5\u9053\u5979\u8ddf\u8bb8\u7ef4\u5728\u4e00\u4e2a\u623f\u95f4\u91cc\uff0c\u60f3\u7740\u4e0a\u4e00\u6b21\u5728\u60e0\u5c71\uff0c\u4ed6\u8ddf\u8bb8\u601d\u7684\u5173\u7cfb\u5dee\u70b9\u8ba9\u7530\u6d77\u971e\u8bf4\u7834\uff0c\u5c31\u6015\u8bb8\u7ef4\u5bf9\u8fd9\u4e8b\u8d77\u4e86\u5fc3\uff0c\u4e0d\u6562\u591a\u8bf4\u4ec0\u4e48\u3002\u8bb8\u601d\u8bf4\u5979\u5bb6\u91cc\u660e\u91cc\u8981\u4e3a\u5979\u53bb\u9999\u6e2f\u7684\u4e8b\u5728\u5bb6\u91cc\u529e\u684c\u9152\u996f\u884c\uff0c\u8ba9\u4ed6\u660e\u5929\u4e5f\u8fc7\u53bb\u5403\u996d\uff0c\u5f20\u606a\u53ea\u662f\u70b9\u5934\u7b54\u5e94\u3002</p><p>\u3000\u3000\u65e0\u6cd5\u627e\u8bb8\u601d\u51fa\u6765\uff0c\u5f20\u606a\u4fbf\u53bb\u5bcc\u8d35\u56ed\u5c0f\u533a\u7684\u623f\u5b50\u7761\u89c9\uff0c\u8fd9\u4e2a\u6691\u5047\u4e00\u76f4\u5728\u5916\u9762\u5fd9\u788c\uff0c\u8fd9\u5957\u4e00\u5ba4\u6237\u7684\u623f\u95f4\uff0c\u5df2\u7ecf\u5f88\u65e9\u6ca1\u8fc7\u6765\u7761\u4e86\uff0c\u6536\u62fe\u5f97\u6574\u6574\u9f50\u9f50\uff0c\u4e0d\u6653\u5f97\u662f\u5988\u5988\u8fd8\u662f\u5510\u5a67\uff0c\u5979\u4eec\u90fd\u6709\u94a5\u5319\uff0c\u4f46\u662f\u5730\u9762\u6709\u4e9b\u6f6e\u6e7f\uff0c\u5f88\u591a\u868a\u866b\uff0c\u5f20\u606a\u61d2\u5f97\u53bb\u4e70\u868a\u9999\uff0c\u5f00\u4e86\u7a7a\u8c03\uff0c\u62ff\u88ab\u5355\u8499\u7740\u5934\u51d1\u5408\u7740\u7761\u4e86\u4e00\u89c9\u3002\u7761\u7720\u8d28\u91cf\u5f88\u4e0d\u597d\uff0c\u5f20\u606a\u5fc3\u60f3\u7740\u72e1\u5154\u4e09\u7a9f\uff0c\u662f\u4e0d\u662f\u5728\u6d77\u5dde\u518d\u51c6\u5907\u4e24\u5904\u843d\u811a\u7684\u5730\u65b9\uff1f\u8fd9\u91cc\u7684\u6761\u4ef6\u4e5f\u592a\u5dee\u4e86\u4e9b\uff0c\u5173\u952e\u8ba9\u8001\u5988\u77e5\u9053\u4e86\u3002</p></article>"}}}]);
