(window.webpackJsonp=window.webpackJsonp||[]).push([[353],{376:function(p){p.exports={chapterTitle:null,title:"\u7b2c353\u7ae0 \u5c0f\u9b3c\u96be\u7f20",content:"<article><h2>\u7b2c353\u7ae0 \u5c0f\u9b3c\u96be\u7f20</h2><p>\u3000\u3000\u79bb\u5f00\u5f90\u5b66\u5e73\u5bb6\uff0c\u5f20\u606a\u4e0e\u7236\u4eb2\u7684\u5fc3\u60c5\u90fd\u4e0d\u8f7b\u677e\uff0c\u65c1\u4eba\u53ea\u5f53\u5f90\u5b66\u5e73\u8eab\u5c45\u9ad8\u4f4d\uff0c\u4e0d\u5982\u610f\u65f6\u5c11\uff0c\u5176\u5b9e\u5927\u8c2c\u3002\u5728\u6574\u4e2a\u4f53\u7cfb\u90fd\u4e3a\u5229\u76ca\u7684\u7f18\u6545\u7d27\u5bc6\u7684\u7ed3\u5408\u5728\u4e00\u8d77\uff0c\u8981\u53bb\u6253\u7834\u9648\u89c4\u964b\u4e60\uff0c\u8981\u53bb\u89e6\u72af\u522b\u4eba\u7684\u65e2\u5f97\u5229\u76ca\uff0c\u5373\u4f7f\u662f\u8eab\u5c45\u9ad8\u4f4d\u8005\uff0c\u4e5f\u662f\u8981\u4ed8\u51fa\u4ee3\u4ef7\u7684\u3002</p><p>\u3000\u3000\u9664\u4e86\u5218\u534e\u5c71\u6beb\u65e0\u635f\u4f24\u7684\u8c03\u53bb\u90bb\u7701\u67d0\u5e02\u5f53\u5e02\u59d4\u4e66\u8bb0\u4e4b\u5916\uff0c\u5f90\u5b66\u5e73\u6b32\u8ba9\u6c34\u5229\u90e8\u95e8\u7ec4\u7ec7\u8c03\u67e5\u7ec4\u5bf9\u5c0f\u6c5f\u6d41\u57df\u8fd1\u51e0\u5e74\u6765\u7684\u5824\u9632\u5efa\u8bbe\u60c5\u51b5\u8fdb\u884c\u5168\u9762\u7684\u68c0\u67e5\uff0c\u4e5f\u56e0\u963b\u529b\u592a\u5927\uff08\u6d89\u53ca\u5230\u6c34\u5229\u90e8\uff09\uff0c\u6700\u7ec8\u4f5c\u7f62\u3002\u8ddf\u5404\u90e8\u95e8\u626f\u4e86\u534a\u4e2a\u591a\u6708\u7684\u76ae\uff0c\u6298\u4e2d\u65b9\u6848\u53ea\u662f\u7531\u7701\u91cc\u589e\u52a0\u4eca\u5e74\u7684\u5824\u9632\u5efa\u8bbe\u9884\u7b97\uff1b\u8fd9\u4e2a\u5012\u662f\u5927\u5bb6\u4e50\u4e8e\u89c1\u5230\u7684\u7ed3\u679c\u3002</p><p>\u3000\u3000\u8fd9\u79cd\u79cd\u4e8b\uff0c\u90fd\u8ba9\u5f90\u5b66\u5e73\u6709\u5fc3\u75b2\u529b\u7aed\u4e4b\u611f\u3002</p><p>\u3000\u3000\u201c\u5ba6\u9014\u8270\u9669\uff0c\u4e0d\u968f\u6ce2\u9010\u6d41\u662f\u5f88\u96be\u7684\u4e00\u4ef6\u4e8b\u2026\u2026\u201d\u5f20\u77e5\u884c\u5fcd\u4e0d\u4f4f\u5728\u8f66\u4e0a\u5c31\u53d1\u51fa\u8fd9\u6837\u7684\u611f\u6168\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u5f20\u606a\u8f7b\u8f7b\u5e94\u4e86\u4e00\u58f0\uff0c\u632f\u4f5c\u8d77\u7cbe\u795e\uff0c\u5728\u6697\u6627\u7684\u8f66\u53a2\u91cc\u81ea\u987e\u81ea\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u54ea\u91cc\u6709\u7b80\u5355\u7684\u4e8b\u60c5\uff1f\u8981\u662f\u4e0d\u8d1f\u8d23\u4efb\u7684\u53bb\u505a\u5b98\uff0c\u5012\u662f\u8f7b\u677e\u5feb\u6d3b\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u5fae\u5fae\u4e00\u7b11\uff0c\u6ce8\u89c6\u7740\u8f66\u7a97\u5916\u7684\u8f66\u6d41\u706f\u6cb3\uff0c\u4e3a\u5b98\u4e00\u4efb\uff0c\u4fbf\u662f\u6c42\u80fd\u6709\u5efa\u6811\uff0c\u4e0d\u8d1f\u8d23\u4efb\u7684\u53bb\u505a\u5b98\uff0c\u5bf9\u67d0\u4e9b\u4eba\u5bb9\u6613\uff0c\u5bf9\u67d0\u4e9b\u4eba\u4e5f\u4e0d\u662f\u4e00\u4ef6\u7b80\u5355\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u6682\u65f6\u8fd8\u501f\u4f4f\u5728\u8c22\u665a\u6674\u5728\u9752\u5c71\u516c\u5bd3\u7684\u90a3\u5957\u623f\u5b50\uff0c\u51b3\u5b9a\u5c06\u6c5f\u8fb9\u7684\u623f\u5b50\u4e70\u4e0b\u6765\uff0c\u5c31\u6253\u7b97\u5c3d\u5feb\u642c\u8fdb\u53bb\u4f4f\uff0c\u623f\u5b50\u90fd\u5df2\u7ecf\u7cbe\u88c5\u4fee\u8fc7\uff0c\u6dfb\u7f6e\u4e9b\u7b80\u5355\u7684\u5bb6\u4ff1\u80fd\u591f\u5c45\u4f4f\u5c31\u53ef\u4ee5\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u56db\u6708\u4e8c\u5341\u4e8c\u65e5\uff0c\u97e9\u56fd\u4e09\u661f\u4f01\u4e1a\u4e0e\u9a6c\u978d\u5c71\u5236\u51b7\u8bbe\u5907\u6709\u9650\u516c\u53f8\u79d8\u5bc6\u5546\u8c08\u7ec4\u5efa\u5408\u8d44\u5de5\u5382\u7684\u6d88\u606f\u7531\u5efa\u90ba\u6668\u62a5\u4e88\u4ee5\u6279\u9732\uff0c\u60e0\u5c71\u5e02\u653f\u5e9c\u7ed9\u72e0\u72e0\u7684\u523a\u6fc0\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u5468\u747e\u745c\u4e5f\u4e0d\u5f97\u4e0d\u4eb2\u81ea\u6307\u793a\u8c08\u5224\u7ec4\u4ee5\u4e0e\u5609\u4fe1\u5b9e\u4e1a\u63a5\u89e6\u8c08\u5224\u4e3a\u65e5\u540e\u7684\u91cd\u70b9\uff0c\u4ee5\u5f25\u8865\u60e0\u5c71\u5e02\u653f\u5e9c\u4e4b\u524d\u7684\u8fc7\u5931\u3002</p><p>\u3000\u3000\u5609\u4fe1\u5b9e\u4e1a\u63d0\u51fa\u7684\u5408\u8d44\u65b9\u6848\uff0c\u662f\u76f8\u5bf9\u6210\u719f\u7684\u65b9\u6848\u3002\u4eca\u540e\u5341\u5e74\uff0c\u662f\u767d\u8272\u5bb6\u7535\u5927\u53d1\u5c55\u7684\u5341\u5e74\uff0c\u9752\u5c9b\u6d77\u5c14\u4ec5\u51b0\u7bb1\u4e00\u7c7b\u7684\u9500\u552e\u989d\uff0c\u4ece\u4e5d\u4e94\u5e74\u5230\u96f6\u4e94\u5e74\uff0c\u5c31\u589e\u6da8\u4e86\u516d\u4e03\u5341\u500d\u3002\u65f6\u95f4\u6bd4\u4e00\u65f6\u7684\u5229\u76ca\u5f97\u5931\u8981\u4e3a\u91cd\u8981\uff0c\u9664\u4e86\u63a7\u80a1\u6743\uff0c\u5f20\u606a\u4ee5\u4e3a\u5609\u4fe1\u5b9e\u4e1a\u4e0d\u9700\u8981\u592a\u62d8\u6ce5\u7ec6\u8282\u4e0a\u7684\u5229\u76ca\uff0c\u4ee5\u81f4\u8c08\u5224\u7684\u65f6\u95f4\u8fc7\u5ea6\u62d6\u957f\u5ef6\u8bef\u53d1\u5c55\u7684\u6700\u597d\u65f6\u673a\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u3001\u845b\u660e\u5fb7\u90fd\u6301\u4e0e\u5f20\u606a\u76f8\u8fd1\u7684\u610f\u89c1\uff0c\u5e0c\u671b\u5f20\u606a\u518d\u5230\u60e0\u5c71\u8d70\u4e00\u8d9f\uff0c\u4e3b\u6301\u6700\u540e\u7684\u5173\u952e\u8c08\u5224\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u7701\u57ce\u4f4f\u4e86\u4e09\u4e2a\u665a\u4e0a\uff0c\u5c31\u53c8\u8d76\u5230\u60e0\u5c71\u5e02\uff0c\u53ef\u60dc\u8fd9\u6b21\u6ca1\u6709\u8bb8\u601d\u966a\u540c\uff1b\u8bb8\u601d\u5728\u6d77\u5dde\u6b47\u4e86\u4e24\u5929\uff0c\u5c31\u56de\u9999\u6e2f\u4e86\uff0c\u867d\u7136\u4e5f\u662f\u4ece\u7701\u57ce\u5efa\u90ba\u5e02\u56fd\u9645\u673a\u573a\u642d\u4e58\u7684\u98de\u673a\uff0c\u53ea\u662f\u90a3\u5929\uff0c\u5f20\u606a\u7ed9\u4ed6\u5988\u5988\u6293\u4f4f\u53bb\u9009\u8d2d\u5bb6\u4ff1\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d76\u5230\u60e0\u5c71\uff0c\u5c31\u76f4\u63a5\u53c2\u4e0e\u4e86\u8c08\u5224\uff0c\u5728\u56fa\u5b9a\u8d44\u4ea7\u3001\u65e0\u5f62\u8d44\u4ea7\u8bc4\u4f30\u65b9\u9762\uff0c\u90fd\u6ca1\u6709\u4f5c\u592a\u5927\u7684\u575a\u6301\uff0c\u5728\u5de5\u4f1a\u8bbe\u7acb\u3001\u5458\u5de5\u5f85\u9047\u7b49\u7ec6\u8282\u65b9\u9762\uff0c\u90fd\u5c3d\u53ef\u80fd\u7ed9\u9999\u96ea\u6d77\u6700\u5927\u7684\u4f18\u5f85\uff0c\u8c08\u5224\u7684\u91cd\u70b9\u76f4\u63a5\u5c31\u5954\u5230\u5408\u8d44\u4f01\u4e1a\u653f\u7b56\u4f18\u60e0\u4e0e\u7ba1\u7406\u5c42\u914d\u5236\u65b9\u9762\u3002</p><p>\u3000\u3000\u8c08\u5224\u7ec4\u63d0\u51fa\u8bbe\u7acb\u4e2d\u65b9\u603b\u7ecf\u7406\uff0c\u89c1\u5f20\u94c1\u68ee\u4f3c\u4e4e\u8fd8\u6709\u4e9b\u641e\u4e0d\u6e05\u72b6\u51b5\uff0c\u5f20\u606a\u6beb\u4e0d\u7559\u60c5\u9762\u7684\u5bf9\u4ed6\u8bf4\u9053\uff1a\u201c\u9999\u6e2f\u660e\u5e74\u5c31\u8981\u56de\u5f52\u4e86\uff0c\u4ec0\u4e48\u6e2f\u65b9\u3001\u4e2d\u65b9\uff0c\u8fd9\u65f6\u5019\u518d\u8fd9\u4e48\u8bf4\uff0c\u8fd8\u6709\u4ec0\u4e48\u610f\u4e49\uff1f\u5c31\u7ba1\u7406\u4eba\u5458\uff0c\u5609\u4fe1\u5b9e\u4e1a\u9664\u4e86\u6d3e\u51fa\u603b\u7ecf\u7406\u3001\u5e02\u573a\u603b\u76d1\u3001\u8d22\u52a1\u603b\u76d1\u4e09\u4e2a\u4eba\u4e4b\u5916\uff0c\u5176\u4ed6\u7684\u91cd\u8981\u5c97\u4f4d\u90fd\u4f1a\u7ee7\u7eed\u8058\u7528\u9999\u96ea\u6d77\u7535\u51b0\u7bb1\u5382\u7684\u539f\u7ba1\u7406\u4eba\u5458\uff0c\u6211\u60f3\u4e0d\u901a\u8c08\u5224\u7ec4\u5728\u8fd9\u4e0a\u9762\u8fd8\u6709\u4ec0\u4e48\u4e89\u6267\u7684\uff1f\u83ab\u975e\u5f20\u79d8\u4e66\u8fd8\u6709\u5fc3\u5230\u5408\u8d44\u516c\u53f8\u6765\u5f53\u8fd9\u4e2a\u4e2d\u65b9\u603b\u7ecf\u7406\uff1f\u201d</p><p>\u3000\u3000\u5f20\u94c1\u68ee\u6b64\u524d\u672a\u66fe\u4e0e\u5f20\u606a\u6709\u8fc7\u63a5\u89e6\uff0c\u542c\u8c22\u5251\u5357\u63cf\u8ff0\uff0c\u518d\u770b\u5f20\u606a\u4eca\u5929\u5f97\u52bf\u4e0d\u7ed5\u4eba\u7684\u950b\u5229\u8a00\u8f9e\uff0c\u8fd8\u771f\u662f\u56a3\u5f20\u8dcb\u6248\u7684\u4eba\uff0c\u7ed9\u4ed6\u4e00\u901a\u8bdd\u523a\u5f97\u9762\u76ee\u9752\u7ea2\u3002</p><p>\u3000\u3000\u90fd\u8bf4\u5b81\u53ef\u5f97\u7f6a\u541b\u5b50\uff0c\u4e0d\u53ef\u5f97\u7f6a\u5c0f\u5b50\uff0c\u53ea\u662f\u4e0e\u5468\u8c22\u4e24\u5bb6\u5df2\u6ca1\u6709\u53ef\u8f6c\u571c\u7684\u4f59\u5730\uff0c\u5f20\u606a\u53ef\u4e0d\u5728\u4e4e\u591a\u5f97\u7f6a\u5f20\u94c1\u68ee\u8fd9\u53f7\u4eba\u7269\uff0c\u54ea\u91cc\u9700\u8981\u7ed9\u4ed6\u597d\u8138\u8272\uff1f</p><p>\u3000\u3000\u5c31\u8fde\u5468\u747e\u745c\u5bf9\u5408\u8d44\u7684\u4e8b\u60c5\u90fd\u8f6c\u53d8\u4e86\u6001\u5ea6\uff0c\u8d75\u9633\u66f4\u662f\u5f3a\u8c03\u8981\u6c42\u8fd1\u671f\u5c31\u51fa\u6210\u679c\uff0c\u5c31\u51ed\u5f20\u94c1\u68ee\u8fd9\u53f7\u4eba\u7269\u8fd8\u4e0d\u80fd\u6380\u8d77\u4ec0\u4e48\u98ce\u6d6a\u3002</p><p>\u3000\u3000\u4e00\u5929\u7684\u8c08\u5224\u7ed3\u675f\u4e86\uff0c\u73b0\u5728\u4e5f\u4e0d\u9002\u5408\u79c1\u4e0b\u91cc\u8bf7\u90ed\u5efa\u3001\u9648\u6cab\u4e00\u8d77\u5403\u996d\uff0c\u5f20\u606a\u8ba9\u9676\u884c\u5065\u9009\u5403\u996d\u7684\u5730\u65b9\uff0c\u4ed6\u662f\u60e0\u5c71\u4eba\u3002</p><p>\u3000\u3000\u5230\u505c\u8f66\u573a\uff0c\u5750\u8fdb\u8f66\u91cc\uff0c\u770b\u89c1\u5f20\u94c1\u68ee\u501f\u7528\u9999\u96ea\u6d77\u7684\u7ea2\u65d7\u8f66\u8fd8\u505c\u5728\u90a3\u91cc\uff0c\u4ed6\u4eec\u8fd9\u8fb9\u8f66\u521a\u53d1\u52a8\uff0c\u7ea2\u65d7\u8f66\u5c31\u9760\u4e86\u8fc7\u6765\uff0c\u770b\u89c1\u5f20\u94c1\u68ee\u9732\u51fa\u5934\u6765\uff0c\u5f20\u606a\u8be7\u5f02\u7684\u6309\u4e0b\u8f66\u7a97\uff0c\u4ee5\u4e3a\u4ed6\u6709\u4ec0\u4e48\u4e8b\u8981\u8bf4\u3002</p><p>\u3000\u3000\u201c\u5c31\u7b97\u5408\u8d44\u6210\u529f\u53c8\u600e\u4e48\u6837\uff1f\u4f60\u4eec\u4e0d\u8981\u5fd8\u4e86\uff0c\u8fd9\u8fd8\u662f\u5728\u60e0\u5c71\u7684\u5730\u76d8\u4e0a\uff0c\u4f60\u4eec\u8981\u80fd\u4ece\u60e0\u5c71\u635e\u4e0d\u5230\u4e00\u5206\u94b1\uff0c\u6211\u5c31\u8ddf\u4f60\u59d3\u2026\u2026\u201d\u5f20\u94c1\u68ee\u645e\u8fd9\u53e5\u72e0\u8bdd\u5c31\u626c\u957f\u800c\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ed9\u5f20\u94c1\u68ee\u7684\u8fd9\u53e5\u8bdd\u641e\u5f97\u6709\u4e9b\u6478\u4e0d\u7740\u5934\u8111\uff0c\u5f20\u94c1\u68ee\u8d64\u88f8\u88f8\u7684\u5a01\u80c1\u6ca1\u6709\u8ba9\u4ed6\u751f\u6c14\uff0c\u5374\u89c9\u5f97\u6709\u4e9b\u597d\u7b11\uff0c\u4ed6\u6709\u4ec0\u4e48\u8d44\u683c\u8bf4\u8fd9\u79cd\u8bdd\uff1f</p><p>\u3000\u3000\u201c\u8ddf\u6211\u59d3\uff0c\u4e5f\u662f\u59d3\u5f20\u2026\u2026\u201d\u5f20\u606a\u5f88\u65e0\u8c13\u7684\u8bf4\u4e86\u4e00\u53e5\uff0c\u4e0d\u8fc7\u7ea2\u65d7\u8f66\u5df2\u7ecf\u7edd\u5c18\u51fa\u4e86\uff0c\u5f20\u606a\u4fa7\u8fc7\u5934\u5bf9\u9676\u884c\u5065\u8bf4\uff0c\u201c\u8fd9\u8f66\u662f\u9999\u96ea\u6d77\u7684\u8d44\u4ea7\uff0c\u660e\u5929\u8c08\u5224\u65f6\u63d0\u51fa\u6765\uff0c\u51ed\u4ec0\u4e48\u501f\u7ed9\u4ed6\u7528\uff1f\u201d</p><p>\u3000\u3000\u5085\u5bb6\u4fca\u5012\u662f\u6709\u4e9b\u5fe7\u5fc3\uff0c\u4ed6\u8bf4\u9053\uff1a\u201c\u4f01\u4e1a\u8ddf\u5730\u65b9\u653f\u5e9c\u5173\u7cfb\u641e\u50f5\uff0c\u7684\u786e\u5bf8\u6b65\u96be\u884c\uff0c\u7279\u522b\u662f\u5927\u795e\u6613\u6321\uff0c\u5c0f\u9b3c\u96be\u7f20\u3002\u6211\u6709\u4e2a\u670b\u53cb\u5230\u5185\u5730\u6765\u5f00\u9152\u5e97\uff0c\u4ed6\u4eba\u813e\u6c14\u786c\uff0c\u4ed7\u7740\u81ea\u5df1\u662f\u6e2f\u5546\u7684\u8eab\u4efd\uff0c\u5c31\u6ca1\u6709\u5230\u5904\u5b5d\u656c\uff0c\u5f00\u4e1a\u540e\uff0c\u4eca\u5929\u6765\u67e5\u536b\u751f\uff0c\u660e\u5929\u6765\u67e5\u6d88\u9632\uff0c\u51e0\u4e4e\u5929\u5929\u6709\u4e3e\u62a5\u4ed6\u9152\u5e97\u6709\u5356\u6deb\u5ad6\u5a3c\u7684\uff0c\u7ed9\u6298\u817e\u4e86\u4e24\u4e2a\u6708\uff0c\u53c8\u4e0d\u7518\u5fc3\u4f4e\u5934\uff0c\u53ea\u5f97\u4f4e\u4ef7\u8f6c\u624b\u64a4\u4e86\u51fa\u6765\uff0c\u4ed6\u540e\u6765\u5f97\u77e5\uff0c\u8fd8\u53ea\u662f\u5f53\u5730\u4e00\u540d\u526f\u533a\u957f\u5bf9\u4ed6\u6709\u7279\u522b\u5927\u7684\u610f\u89c1\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fae\u5fae\u6447\u4e86\u6447\u5934\uff0c\u6362\u6210\u8c22\u5251\u5357\u3001\u5468\u747e\u745c\u5c31\u4e0d\u4f1a\u8bf4\u8fd9\u4e48\u8822\u611a\u7684\u8bdd\uff0c\u6b63\u6cf0\u96c6\u56e2\u53c8\u4e0d\u4f1a\u9f9f\u7f29\u5728\u60e0\u5c71\u4e0d\u51fa\u53bb\u53d1\u5c55\uff0c\u7a0d\u6709\u4e9b\u601d\u667a\u7684\u4eba\u90fd\u4e0d\u4f1a\u73a9\u9634\u624b\uff0c\u4eca\u5929\u4f60\u73a9\u522b\u4eba\uff0c\u660e\u5929\u5c31\u5f97\u7ed9\u522b\u4eba\u73a9\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u94c1\u68ee\u5f00\u7740\u7ea2\u65d7\u9a76\u51fa\u9999\u96ea\u6d77\u7535\u51b0\u7bb1\u5382\u7684\u5382\u533a\uff0c\u624d\u7ed9\u8c22\u5251\u5357\u6253\u7535\u8bdd\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u90a3\u73ed\u72d7\u65e5\u7684\uff0c\u4ed6\u4eec\u5012\u4ee5\u4e3a\u62ff\u51fa\u4e00\u53e0\u770b\u8d77\u6765\u4e0d\u9519\u7684\u5e9f\u7eb8\u5c31\u80fd\u6765\u60e0\u5c71\u6405\u5c40\u4e86\uff0c\u6211\u8fd8\u6015\u4ed6\u4eec\u9000\u7f29\u5462\uff0c\u7559\u5230\u4ee5\u540e\u6162\u6162\u73a9\u6b7b\u4ed6\u4eec\u624d\u6709\u610f\u601d\u3002\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u5546\u4e0d\u8ddf\u5b98\u6597\uff0c\u81ea\u53e4\u4ee5\u6765\u5c31\u662f\u8fd9\u4e2a\u9053\u7406\uff0c\u518d\u8bf4\u4e86\uff0c\u5f3a\u9f99\u4e0d\u538b\u5730\u5934\u86c7\uff0c\u5f20\u606a\u8fd9\u5bb6\u4f19\u806a\u660e\u8fd8\u662f\u6709\u4e9b\u806a\u660e\u7684\uff0c\u53ef\u60dc\u5bf9\u8fd9\u4e2a\u95ee\u9898\u8fd8\u662f\u8ba4\u8bc6\u4e0d\u900f\u2026\u2026\u201d\u8c22\u5251\u5357\u7684\u7b11\u58f0\u5f88\u5e72\uff0c\u4e0d\u8fc7\u8fd8\u5f88\u52aa\u529b\u7684\u5728\u7b11\u7740\uff0c\u201c\u4ed6\u4eec\u4ee5\u4e3a\u8d70\u4e0a\u5c42\u8def\u7ebf\u5c31\u4e07\u4e8b\u5927\u5409\u4e86\uff0c\u4e8b\u60c5\u8fd8\u4e0d\u90fd\u662f\u4e0b\u9762\u529e\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u5475\uff0c\u771f\u60f3\u628a\u90a3\u5c0f\u5b50\u5f53\u65f6\u7684\u6837\u5b50\u62cd\u7ed9\u4f60\u770b\uff0c\u201d\u5f20\u94c1\u68ee\u5fc3\u91cc\u5341\u5206\u7545\u5feb\uff0c\u201c\u4f60\u554a\u4e5f\u771f\u662f\u7684\uff0c\u600e\u4e48\u4e24\u6b21\u90fd\u8ba9\u4ed6\u5360\u4e86\u4fbf\u5b9c\uff0c\u5c31\u7b97\u5728\u6d77\u5dde\u7684\u5730\u9762\uff0c\u4e5f\u4e0d\u80fd\u8ba9\u90a3\u5c0f\u5b50\u800d\u6d41\u6c13\u554a\uff0c\u771f\u662f\u66ff\u60e0\u5c71\u4eba\u6c11\u4e22\u4eba\u3002\u597d\u4e86\uff0c\u73b0\u5728\u5230\u60e0\u5c71\u4e0a\u7684\u5730\u9762\u4e0a\u7684\uff0c\u6211\u4eec\u4e5f\u8981\u5b66\u4f1a\u5229\u7528\u5408\u6cd5\u7684\u624b\u6bb5\u3002\u4e0d\u8bf4\u4f60\u4e86\uff0c\u665a\u4e0a\u51fa\u6765\u5403\u996d\uff1f\u201d</p><p>\u3000\u3000\u201c\u4eca\u5929\u665a\u4e0a\u53ef\u80fd\u4e0d\u884c\uff0c\u6211\u8981\u8d76\u7740\u53bb\u7701\u57ce\uff0c\u53ef\u80fd\u8fc7\u597d\u51e0\u5929\u624d\u80fd\u56de\u60e0\u5c71\uff0c\u56de\u6765\u6211\u8bf7\u4f60\u5403\u996d\u5427\uff0c\u201d\u8c22\u5251\u5357\u5728\u7535\u8bdd\u91cc\u8bf4\u9053\uff0c\u201c\u6211\u8bf4\u5f20\u54e5\uff0c\u4f60\u4e3a\u4ec0\u4e48\u975e\u8981\u60f3\u7740\u53bb\u5f53\u4ec0\u4e48\u4e2d\u65b9\u603b\u7ecf\u7406\uff0c\u4ee5\u4f60\u7684\u624d\u534e\uff0c\u8981\u4e0d\u8fc7\u6765\u8ddf\u6211\u4e00\u8d77\u505a\u6574\u6d77\u6cf0\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u5c31\u4e3a\u4e86\u4e89\u4e00\u53e3\u6c14\uff1f\u73b0\u5728\u60f3\u60f3\u7b97\u4e86\uff0c\u7ecf\u5546\u7684\u641e\u4e0d\u8fc7\u641e\u884c\u653f\u7684\uff0c\u641e\u884c\u653f\u7684\u641e\u4e0d\u8fc7\u641e\u653f\u6cbb\u7684\uff0c\u6211\u5934\u660f\u6389\u4e86\uff0c\u4ee5\u524d\u7adf\u7136\u60f3\u7740\u8981\u53bb\u5f53\u4ec0\u4e48\u603b\u7ecf\u7406\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u5012\u662f\uff0c\u6211\u8fd8\u4e0d\u662f\u501f\u7740\u6211\u5988\u7684\u540d\u4e49\u5728\u5916\u9762\u62db\u6447\u649e\u9a97\uff0c\u8981\u4e0d\u7136\u9047\u5230\u5f20\u54e5\uff0c\u8fd8\u4e0d\u5f97\u8ddf\u7740\u88c5\u5b59\u5b50\uff1f\u201d\u8c22\u5251\u5357\u6302\u4e86\u7535\u8bdd\uff0c\u5fc3\u91cc\u60f3\u7740\u5f20\u606a\u4f1a\u4e0d\u4f1a\u8ba4\u8bc6\u4e00\u4e0b\u5c0f\u9b3c\u96be\u7f20\uff0c\u8fd8\u662f\u4f1a\u6709\u522b\u7684\u52a8\u4f5c\uff1f</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u6e05\u695a\u5f20\u94c1\u68ee\u4e3a\u4ec0\u4e48\u8981\u8bf4\u8fd9\u6837\u7684\u8bdd\uff0c\u65e2\u7136\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u5c31\u4e0d\u80fd\u7f6e\u4e4b\u4e0d\u7406\uff0c\u771f\u8981\u7ed9\u8fd9\u4e9b\u5c0f\u9b3c\u7f20\u8eab\uff0c\u4e09\u5929\u4e24\u5934\u7ed9\u4f60\u641e\u4e2a\u7a81\u51fb\u68c0\u67e5\uff0c\u5728\u98df\u5802\u770b\u5230\u53ea\u82cd\u8747\u5c31\u7f5a\u4f60\u5343\u513f\u516b\u767e\u7684\uff0c\u8fd9\u5382\u5b50\u8fd8\u771f\u96be\u5f00\u4e0b\u53bb\u3002\u5f20\u606a\u4e5f\u76f8\u4fe1\u4ee5\u5f20\u94c1\u68ee\u5728\u60e0\u5c71\u5e02\u7684\u80fd\u8010\u80fd\u505a\u5230\u8fd9\u4e00\u70b9\uff0c\u4f46\u662f\u5f20\u94c1\u68ee\u51ed\u4ec0\u4e48\u8ba4\u4e3a\u5728\u4ed6\u505a\u8fd9\u4e9b\u5c0f\u52a8\u4f5c\u4e4b\u524d\u4e0d\u4f1a\u7ed9\u637b\u6b7b\u6389\uff1f</p><p>\u3000\u3000\u8981\u6ca1\u6709\u5468\u747e\u745c\u5728\u80cc\u540e\u6491\u8170\uff0c\u5f20\u94c1\u68ee\u662f\u5403\u4e86\u8c79\u5b50\u80c6\u4e86\uff1f\u8fd9\u4e48\u5927\u5e74\u7eaa\u90fd\u957f\u72d7\u8eab\u4e0a\u53bb\u4e86\uff1f</p><p>\u3000\u3000\u8981\u6ca1\u6709\u5468\u747e\u745c\u5728\u80cc\u540e\u6491\u8170\uff0c\u637b\u6b7b\u5f20\u94c1\u68ee\u8ddf\u53ea\u8682\u8681\u4f3c\u7684\u3002</p><p>\u3000\u3000\u8fd9\u4e8b\u8fd8\u771f\u662f\u8e4a\u8df7\u4e86\u3002</p><p>\u3000\u3000\u5468\u747e\u745c\u4f1a\u652f\u6301\u4ed6\u641e\u8fd9\u4e9b\u5c0f\u52a8\u4f5c\uff1f</p><p>\u3000\u3000\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u5468\u747e\u745c\u4f1a\u5728\u8fd9\u79cd\u4e8b\u4e0a\u786c\u78d5\uff0c\u90a3\u771f\u662f\u6ca1\u4ec0\u4e48\u597d\u62c5\u5fe7\u7684\u4e86\u3002\u8fd8\u6709\u4e00\u79cd\u53ef\u80fd\uff0c\u5f20\u94c1\u68ee\u7eaf\u7cb9\u5c31\u662f\u4e00\u70ae\u7070\uff0c\u88ab\u63a8\u51fa\u6765\u6076\u5fc3\u4eba\u7684\u3002</p></article>"}}}]);
