(window.webpackJsonp=window.webpackJsonp||[]).push([[356],{378:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e09\u8282 \u5fc5\u6b7b\u65e0\u7591",content:"<article><h2>\u7b2c\u5341\u4e09\u8282 \u5fc5\u6b7b\u65e0\u7591</h2><p>\u5f53\u795e\u8eaf\u9b54\u50cf\u7684\u5e9e\u5927\u8eab\u5f71\u5728\u534a\u7a7a\u4e2d\u51fa\u73b0\u65f6\uff0c\u4e0d\u4ec5\u6b32\u9b54\u4f0a\u84d3\u4e1d\u7684\u8138\u8272\u5239\u90a3\u95f4\u53d8\u5f97\u96be\u770b\u81f3\u6781\uff0c\u5c31\u8fde\u743c\u6069\u7b49\u4eba\u90fd\u9762\u9762\u76f8\u89d1\uff0c\u4e00\u65f6\u95f4\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002</p><p>\u62dc\u5c14\u548c\u5f25\u8d5b\u4e9a\u8981\u505a\u7684\uff0c\u662f\u62a2\u5728\u9b54\u50cf\u542f\u52a8\u4e4b\u524d\u53bb\u6740\u6389\u5deb\u5996\uff0c\u628a\u8fd9\u602a\u7269\u76f4\u63a5\u627c\u6740\u5728\u6447\u7bee\u91cc\u3002\u5982\u4eca\u9b54\u50cf\u65e2\u7136\u73b0\u8eab\uff0c\u90a3\u663e\u7136\u662f\u8ba1\u5212\u5931\u8d25\u4e86\uff0c\u8fd9\u53ef\u4e0d\u662f\u4ec0\u4e48\u597d\u6d88\u606f\u3002\u6bd5\u7adf\u73b0\u5728\u5927\u5bb6\u7b97\u662f\u540c\u4e00\u9635\u7ebf\u7684\uff0c\u5982\u679c\u9b54\u9b3c\u4e00\u65b9\u6218\u80dc\uff0c\u743c\u6069\u7b49\u4eba\u5c31\u662f\u529f\u81e3\uff0c\u62dc\u5c14\u8bb8\u8bfa\u7684\u597d\u5904\u81ea\u7136\u4e00\u6837\u4e0d\u4f1a\u5c11\uff0c\u5927\u5bb6\u5408\u4f5c\u6109\u5feb\uff0c\u7686\u5927\u6b22\u559c\uff1b\u5982\u679c\u662f\u6076\u9b54\u4e00\u65b9\u6218\u80dc\uff0c\u90a3\u5c31\u53ea\u6709\u8d76\u5feb\u8dd1\u8def\u5f00\u6e9c\u4e86\uff0c\u867d\u8bf4\u4e5f\u6ca1\u635f\u5931\u4ec0\u4e48\uff0c\u7ec8\u7a76\u8fd8\u662f\u8ba9\u4eba\u6bd4\u8f83\u4e0d\u723d\u3002</p><p>\u4fbf\u5728\u6b64\u65f6\uff0c\u4e00\u9053\u91d1\u5149\u4ece\u5730\u4e0b\u51b2\u8d77\uff0c\u5728\u534a\u7a7a\u4e2d\u5316\u4f5c\u4eba\u5f62\uff0c\u6b63\u662f\u66e6\u5929\u4f7f\u5f25\u8d5b\u4e9a\uff0c\u624b\u6301\u5bd2\u51b0\u5251\u548c\u795e\u8eaf\u9b54\u50cf\u7f20\u6597\u8d77\u6765\u3002\u56e0\u4e3a\u8ddd\u79bb\u592a\u8fdc\uff0c\u53ea\u80fd\u6a21\u6a21\u7cca\u7cca\u770b\u5230\u4e2a\u5927\u6982\u8f6e\u5ed3\uff0c\u743c\u6069\u6b63\u5f85\u53d6\u8fc7\u671b\u8fdc\u955c\u6765\u7ec6\u770b\uff0c\u5c31\u89c1\u66e6\u5929\u4f7f\u5f20\u5f00\u7fbd\u7ffc\uff0c\u671d\u7740\u9ed1\u77f3\u57ce\u7684\u5357\u90e8\u98de\u53bb\uff0c\u901f\u5ea6\u5e76\u4e0d\u5feb\uff0c\u795e\u8eaf\u9b54\u50cf\u5728\u540e\u9762\u7d27\u8ffd\u4e0d\u820d\u3002</p><p>\u201c\u62dc\u5c14\u5462\uff1f\u201d</p><p>\u795e\u8eaf\u9b54\u50cf\u548c\u5f25\u8d5b\u4e9a\u5148\u540e\u73b0\u8eab\uff0c\u62dc\u5c14\u5374\u6ca1\u8e2a\u5f71\u2026\u2026\u5982\u679c\u5f80\u574f\u7684\u65b9\u9762\u60f3\uff0c\u83ab\u975e\u8fd9\u5bb6\u4f19\u51f6\u591a\u5409\u5c11\u4e86\uff1f</p><p>\u743c\u6069\u6c89\u541f\u7247\u523b\uff0c\u201c\u6211\u8fc7\u53bb\u770b\u770b\u5230\u5e95\u53d1\u751f\u4ec0\u4e48\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u4f60\u4eec\u5728\u8fd9\u91cc\u7b49\u6211\u56de\u6765\u3002\u201d</p><p>\u4e0d\u7b49\u5176\u4ed6\u4eba\u56de\u7b54\uff0c\u4ed6\u5df2\u7ecf\u53d1\u52a8\u4e86\u4f20\u9001\u672f\uff0c\u77ac\u95f4\u4fbf\u5230\u4e86\u5893\u5730\u4e0a\u7a7a\u3002\u743c\u6069\u7528\u80f8\u9488\u7ef4\u6301\u7740\u6d6e\u7a7a\u72b6\u6001\uff0c\u7f13\u7f13\u4e0b\u964d\u5230\u4e00\u5b9a\u9ad8\u5ea6\uff0c\u4e0d\u6562\u7ee7\u7eed\u5f80\u4e0b\u843d\uff0c\u5426\u5219\u5c31\u8fdb\u5165\u7a7a\u95f4\u67b7\u9501\u7684\u8303\u56f4\uff0c\u4e00\u65e6\u6709\u5371\u9669\u4fbf\u65e0\u6cd5\u7acb\u523b\u9003\u79bb\u4e86\u3002\u4f4e\u5934\u5f80\u4e0b\u770b\uff0c\u53d1\u73b0\u6574\u4e2a\u5893\u5730\u90fd\u5df2\u7ecf\u5b8c\u5168\u5d29\u584c\u4e86\uff0c\u65e0\u6570\u7834\u788e\u7684\u5c38\u4f53\u5012\u5728\u65ad\u58c1\u6b8b\u6853\u95f4\uff0c\u4e0d\u4ec5\u6709\u4ea1\u7075\uff0c\u800c\u4e14\u8fd8\u6709\u4e0d\u5c11\u662f\u6076\u9b54\u3002\u5f25\u6f2b\u5347\u817e\u7684\u70df\u5c18\u88ab\u72c2\u98ce\u5439\u8fc7\uff0c\u5f88\u5feb\u5c31\u6d88\u5931\u5f97\u65e0\u5f71\u65e0\u8e2a\u3002</p><p>\u743c\u6069\u66fe\u7ecf\u591a\u6b21\u8fdb\u51fa\uff0c\u53c8\u770b\u8fc7\u6b32\u9b54\u753b\u7684\u5730\u56fe\uff0c\u81ea\u7136\u5f88\u6e05\u695a\uff0c\u8fd9\u5730\u65b9\u8868\u9762\u4e0a\u770b\u662f\u8352\u51c9\u5893\u5730\uff0c\u5176\u5b9e\u6839\u672c\u5c31\u662f\u4f2a\u88c5\uff0c\u4e0b\u9762\u53ef\u662f\u6709\u4e00\u5ea7\u5730\u4e0b\u5bab\u6bbf\u7684\uff0c\u5f00\u59cb\u65f6\u662f\u4f5c\u4e3a\u57ce\u4e3b\u7684\u5b98\u90b8\uff0c\u5b89\u5168\u6027\u81ea\u7136\u8981\u6709\u4fdd\u8bc1\uff0c\u5899\u58c1\u3001\u5730\u677f\u548c\u5929\u82b1\u4ee5\u53ca\u652f\u67f1\uff0c\u5168\u90fd\u662f\u5ca9\u77f3\u780c\u6210\uff0c\u94c1\u6c34\u6d47\u6ce8\uff0c\u7ed3\u6784\u7a33\u5b9a\u65e0\u6bd4\u3002\u5982\u4eca\u5c45\u7136\u5d29\u584c\u4e86\uff0c\u5012\u662f\u9887\u6709\u4e9b\u51fa\u4e4e\u610f\u6599\uff0c\u4e00\u65f6\u90fd\u4e0d\u660e\u767d\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\u3002</p><p>\u201c\u521a\u624d\u603b\u5171\u53ea\u542c\u5230\u4e00\u6b21\u9707\u54cd\uff0c\u518d\u6ca1\u5176\u4ed6\uff0c\u5c45\u7136\u5c31\u9020\u6210\u4e86\u8fd9\u4e48\u5927\u7684\u7834\u574f\uff0c\u5c31\u7b97\u662f\u6d41\u661f\u7206\u6216\u8005\u5730\u9707\u672f\u4e5f\u529e\u4e0d\u5230\u554a\u3002\u795e\u8eaf\u9b54\u50cf\u81ea\u7136\u80fd\u7834\u571f\u800c\u51fa\uff0c\u4f46\u6700\u591a\u628a\u5730\u9762\u7a7f\u4e2a\u6d1e\uff0c\u4e5f\u6ca1\u9053\u7406\u628a\u6574\u4e2a\u5893\u5730\u90fd\u5f04\u584c\u4e86\u2026\u2026\u5514\uff0c\u8fd9\u79cd\u523a\u9f3b\u7684\u6c14\u5473\u2026\u2026\u201d</p><p>\u743c\u6069\u82e5\u6709\u6240\u609f\uff0c\u518d\u60f3\u8d77\u62dc\u5c14\u5728\u9752\u94dc\u57ce\u4e0b\u6240\u4e3a\uff0c\u5927\u7ea6\u4e5f\u5c31\u731c\u5230\u4e86\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4ed6\u4ed4\u7ec6\u89c2\u5bdf\u56db\u5468\uff0c\u786e\u8ba4\u6ca1\u4ec0\u4e48\u5371\u9669\uff0c\u795e\u8eaf\u9b54\u50cf\u4e00\u8def\u8ffd\u9010\u66e6\u5929\u4f7f\uff0c\u5df2\u7ecf\u53bb\u5f97\u8fdc\u4e86\u3002\u5deb\u5e08\u60f3\u4e86\u4e00\u60f3\uff0c\u542f\u52a8\u4e86\u6cd5\u672f\u9006\u8f6c\u6212\u6307\uff0c\u7136\u540e\u62bd\u51fa\u6cd5\u6756\uff0c\u7f13\u7f13\u4e0b\u964d\uff0c\u843d\u5230\u5730\u9762\u3002</p><p>\u201c\u4e0b\u5348\u597d\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\u3002\u201d</p><p>\u743c\u6069\u811a\u8fd8\u6ca1\u843d\u5730\u7ad9\u5b9a\uff0c\u80cc\u540e\u5c31\u4f20\u6765\u4e00\u58f0\u62db\u547c\uff0c\u4ed6\u6b64\u65f6\u7cbe\u795e\u9ad8\u5ea6\u7d27\u5f20\uff0c\u5012\u662f\u88ab\u5413\u4e86\u4e00\u8df3\u3002\u56de\u5934\u770b\u53bb\uff0c\u53d1\u73b0\u5c45\u7136\u662f\u62dc\u5c14\u3002\u8fd9\u5bb6\u4f19\u6b63\u534a\u5750\u534a\u501a\u5728\u4e00\u5757\u5de8\u5927\u7684\u77f3\u5934\u65c1\u8fb9\uff0c\u6a21\u6837\u72fc\u72c8\u4e0d\u582a\uff0c\u5168\u8eab\u843d\u6ee1\u4e86\u5c18\u571f\uff0c\u5934\u53d1\u4e0a\u3001\u8138\u4e0a\u3001\u957f\u888d\u4e0a\u539a\u539a\u7684\u7070\u8499\u8499\u4e00\u7247\uff0c\u4e5f\u4e0d\u52a8\u5f39\uff0c\u4e0d\u6ce8\u610f\u770b\u6839\u672c\u5206\u8fa8\u4e0d\u51fa\uff0c\u8fd8\u4ee5\u4e3a\u662f\u4e2a\u7834\u65e7\u77f3\u96d5\u3002\u743c\u6069\u8be7\u5f02\uff0c\u5fc3\u60f3\u8fd9\u79cd\u65f6\u5019\u4ed6\u5c45\u7136\u8fd8\u597d\u6574\u4ee5\u6687\u5730\u5750\u5728\u8fd9\u91cc\u4e0d\u52a8\uff0c\u8fd1\u524d\u4e00\u770b\u624d\u660e\u767d\u8fc7\u6765\u3002\u62dc\u5c14\u7684\u80f8\u8179\u90e8\u4f4d\u88ab\u9510\u5668\u91cd\u91cd\u780d\u4e86\u4e00\u5200\uff0c\u4f24\u53e3\u6781\u6df1\uff0c\u76ae\u8089\u7efd\u5f00\uff0c\u5df2\u7ecf\u53ef\u4ee5\u6e05\u695a\u770b\u89c1\u808b\u9aa8\uff0c\u5de6\u817f\u81ea\u819d\u76d6\u4ee5\u4e0b\u5168\u90fd\u88ab\u77f3\u5757\u7ed9\u6b7b\u6b7b\u538b\u4f4f\u4e86\uff0c\u96be\u602a\u7ad9\u4e0d\u8d77\u8eab\u6765\uff0c\u90a3\u628a\u957f\u5200\u4e5f\u4e0d\u77e5\u9053\u4e22\u5230\u54ea\u91cc\u53bb\u4e86\u3002\u5012\u662f\u795e\u8272\u4f9d\u65e7\u4ece\u5bb9\u81ea\u82e5\uff0c\u8138\u4e0a\u5fae\u7b11\u4e0d\u51cf\uff0c\u4eff\u4f5b\u53d7\u4f24\u7684\u90a3\u4e2a\u4eba\u4e0d\u662f\u4ed6\u81ea\u5df1\u4f3c\u7684\u3002</p><p>\u201c\u5514\uff0c\u62dc\u5c14\u5c06\u519b\uff0c\u4f60\u8fd8\u6ca1\u6b7b\u5462\u3002\u201d</p><p>\u201c\u597d\u50cf\u8fd8\u6709\u53e3\u6c14\u5427\u3002\u201d</p><p>\u6ca1\u6b7b\u5c31\u597d\uff0c\u8fd9\u91cc\u662f\u4e5d\u5c42\u5730\u72f1\uff0c\u56e0\u4e3a\u4f4d\u9762\u89c4\u5219\u7684\u7167\u987e\uff0c\u9b54\u9b3c\u4f1a\u62e5\u6709\u8d85\u5f3a\u7684\u81ea\u6108\u80fd\u529b\uff0c\u867d\u7136\u6bd4\u4e0d\u4e0a\u5438\u8840\u9b3c\u90a3\u4e48\u53d8\u6001\uff0c\u4f46\u4e5f\u4e0d\u5dee\u592a\u591a\uff0c\u62dc\u5c14\u80f8\u8179\u4e0a\u7684\u4f24\u52bf\u867d\u7136\u4e25\u91cd\uff0c\u4f46\u4f30\u8ba1\u8fc7\u4e0a\u4e00\u523b\u949f\u534a\u5c0f\u65f6\u7684\uff0c\u4e5f\u5c31\u6062\u590d\u8fc7\u6765\u4e86\uff0c\u5012\u662f\u8fd9\u817f\u600e\u4e48\u529e\u2026\u2026</p><p>\u538b\u7740\u62dc\u5c14\u5de6\u817f\u7684\u5de8\u5927\u77f3\u5757\uff0c\u6700\u4fdd\u5b88\u7684\u4f30\u8ba1\u4e5f\u8d85\u8fc7\u4e24\u5343\u78c5\u91cd\uff0c\u743c\u6069\u81ea\u5ea6\u81ea\u5df1\u662f\u7edd\u5bf9\u79fb\u4e0d\u5f00\u642c\u4e0d\u52a8\u7684\uff0c\u5c31\u7b97\u56de\u53bb\u628a\u6885\u83f2\u65af\u5979\u4eec\u5168\u90fd\u53eb\u6765\u4e5f\u662f\u65e0\u7528\uff0c\u81f3\u4e8e\u9b54\u6cd5\u7684\u8bdd\uff0c\u89e3\u79bb\u672f\u6216\u8005\u7f29\u7269\u672f\u4e5f\u90fd\u5bf9\u4ed8\u4e0d\u4e86\u8fd9\u79cd\u5e9e\u7136\u5927\u7269\uff0c\u5316\u77f3\u4e3a\u6ce5\u5012\u53ef\u4ee5\u8bd5\u8bd5\u770b\uff0c\u53ef\u60dc\u743c\u6069\u4eca\u5929\u53c8\u6ca1\u51c6\u5907\u8fd9\u6cd5\u672f\u3002\u5018\u82e5\u62dc\u5c14\u662f\u6b4c\u560e\u65af\u6216\u8005\u8fea\u5185\u7279\u90a3\u79cd\u201c\u4eb2\u738b\u201d\uff0c\u4e8b\u60c5\u5c31\u597d\u529e\u4e86\uff0c\u76f4\u63a5\u4e00\u5200\u628a\u817f\u780d\u65ad\uff0c\u8fc7\u4e0a\u5341\u5206\u949f\u5c31\u91cd\u65b0\u957f\u51fa\u6765\uff0c\u7136\u800c\u4ed6\u662f\u9b54\u9b3c\u4e0d\u662f\u5438\u8840\u9b3c\uff0c\u8fd9\u79cd\u65ad\u80a2\u91cd\u751f\u7684\u672c\u4e8b\u8fd8\u6ca1\u6709\u3002</p><p>\u4e00\u65f6\u95f4\u6ca1\u4ec0\u4e48\u4e3b\u610f\uff0c\u743c\u6069\u7d22\u6027\u53ec\u5524\u4e86\u56db\u53ea\u5c0f\u578b\u98ce\u5143\u7d20\uff0c\u547d\u4ee4\u5b83\u4eec\u56db\u5468\u8b66\u6212\u5bdf\u770b\uff0c\u81ea\u5df1\u5728\u62dc\u5c14\u65c1\u8fb9\u4e00\u6839\u65ad\u88c2\u77f3\u67f1\u4e0a\u5750\u4e0b\uff0c\u8be2\u95ee\u5230\u5e95\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\u3002\u867d\u8bf4\u8fd9\u4e16\u754c\u4e0a\u7684\u8ba1\u5212\uff0c\u6ca1\u6709\u767e\u5206\u4e4b\u767e\u80af\u5b9a\u6210\u529f\u7684\u9053\u7406\uff0c\u4f46\u4e8b\u5148\u9884\u6599\u6709\u516b\u6210\u7684\u628a\u63e1\uff0c\u62dc\u5c14\u548c\u5f25\u8d5b\u4e9a\u4e24\u4eba\u4eb2\u81ea\u51fa\u52a8\uff0c\u5c45\u7136\u8fd8\u4f1a\u628a\u4e8b\u60c5\u641e\u7838\uff0c\u81ea\u5df1\u90fd\u53d7\u4e86\u8fd9\u6837\u91cd\u7684\u4f24\uff0c\u786e\u5b9e\u5f88\u6709\u4e9b\u51fa\u4e4e\u743c\u6069\u610f\u6599\u3002</p><p>\u201c\u6ca1\u529e\u6cd5\uff0c\u4eca\u5929\u8fd0\u6c14\u7cdf\u7cd5\uff0c\u201d\u62dc\u5c14\u53f9\u6c14\uff0c\u201c\u4e00\u5207\u90fd\u8ba1\u7b97\u65e0\u8bef\uff0c\u5c31\u662f\u6700\u540e\u6f0f\u7b97\u4e86\u4e00\u4e2a\u4eba\u3002\u201d</p><p>\u201c\u8c01\uff1f\u201d</p><p>\u201c\u6b4c\u560e\u65af\u3002\u201d</p><p>\u201c\u554a\u3002\u201d</p><p>\u743c\u6069\u5fae\u5fae\u4e00\u60ca\uff0c\u8ffd\u95ee\u600e\u4e48\u56de\u4e8b\u3002\u539f\u6765\u62dc\u5c14\u548c\u5f25\u8d5b\u4e9a\u6309\u7167\u9884\u5148\u8ba1\u5212\u95ef\u5165\u5730\u4e0b\u5bab\u6bbf\uff0c\u4ed6\u4eec\u672c\u5c31\u719f\u6089\u5730\u5f62\uff0c\u53c8\u6709\u5965\u5580\u65af\u7684\u90aa\u5fbd\u5728\u624b\uff0c\u4e8b\u5148\u66f4\u5f97\u743c\u6069\u63d0\u9192\uff0c\u51c6\u5907\u4e86\u5927\u91cf\u4e13\u95e8\u9488\u5bf9\u5438\u8840\u9b3c\u7684\u5b9d\u7269\uff0c\u4e00\u8def\u8fc7\u5173\u65a9\u5c06\uff0c\u5982\u5165\u65e0\u4eba\u4e4b\u5883\uff0c\u53ea\u7528\u56db\u5206\u949f\u5c31\u95ef\u8fdb\u4e86\u9b54\u50cf\u6240\u5728\u7684\u6838\u5fc3\u5ba4\u3002</p><p>\u62dc\u5c14\u867d\u7136\u4e0d\u4ee5\u4e2a\u4eba\u529b\u91cf\u8457\u79f0\uff0c\u5230\u5e95\u4e5f\u662f\u5730\u72f1\u4e5d\u5927\u9b54\u9b3c\u9886\u4e3b\u4e4b\u4e00\uff0c\u5f25\u8d5b\u4e9a\u66f4\u662f\u66e6\u5929\u4f7f\uff0c\u5723\u5149\u5bf9\u4e8e\u4ea1\u7075\u6709\u5929\u7136\u7684\u514b\u5236\u6548\u679c\u3002\u4e24\u4eba\u6709\u5907\u800c\u6765\uff0c\u539f\u672c\u662f\u5f88\u6709\u628a\u63e1\u5728\u51e0\u5206\u949f\u91cc\u5e72\u6389\u90a3\u516b\u4e2a\u5deb\u5996\uff0c\u7136\u800c\u5f53\u65f6\u5728\u6838\u5fc3\u5ba4\u91cc\u7684\uff0c\u8fd8\u6709\u53e6\u5916\u4e00\u4e2a\u4eba\u2014\u2014\u6216\u8005\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u4e00\u4e2a\u5438\u8840\u9b3c\uff0c\u6076\u9b54\u519b\u7684\u7edf\u5e05\u6b4c\u560e\u65af\u3002</p><p>\u9752\u94dc\u57ce\u4e0b\u4e00\u6218\uff0c\u6b4c\u560e\u65af\u5148\u88ab\u5f25\u8d5b\u4e9a\u5c04\u6bc1\u4e00\u81c2\uff0c\u63a5\u7740\u88ab\u9540\u94f6\u5f39\u7247\u70b8\u4f24\uff0c\u9000\u519b\u9014\u4e2d\u53c8\u8fde\u906d\u8ffd\u6740\uff0c\u6700\u7ec8\u91cd\u4f24\u6655\u8ff7\uff0c\u56de\u5230\u9ed1\u77f3\u57ce\u4e4b\u540e\u4fbf\u88ab\u5b89\u6392\u5728\u8fd9\u5730\u4e0b\u5bab\u6bbf\u4e2d\u7597\u517b\u3002\u90a3\u51e0\u4e2a\u5deb\u5996\u90fd\u662f\u9020\u8be3\u9ad8\u6df1\u7684\u4ea1\u7075\u5deb\u5e08\uff0c\u786e\u5b9e\u6709\u51e0\u628a\u5237\u5b50\uff0c\u5728\u7ef4\u62a4\u64cd\u63a7\u9b54\u50cf\u7684\u95f2\u6687\u4e4b\u4f59\uff0c\u5f53\u771f\u628a\u6b4c\u560e\u65af\u4f53\u5185\u6240\u4e2d\u7684\u94f6\u6bd2\u9a71\u9664\uff0c\u5c06\u624b\u81c2\u4f24\u53e3\u5904\u6b8b\u7559\u7684\u5723\u5149\u6d88\u89e3\uff0c\u5438\u8840\u9b3c\u7684\u4f24\u52bf\u867d\u7136\u8fd8\u6ca1\u80fd\u5b8c\u5168\u75ca\u6108\uff0c\u4f46\u5b9e\u529b\u4e5f\u5df2\u7ecf\u6062\u590d\u5927\u534a\uff0c\u8fde\u6bc1\u6389\u7684\u90a3\u53ea\u80f3\u818a\u90fd\u91cd\u65b0\u957f\u4e86\u51fa\u6765\u3002</p><p>\u5bf9\u4e8e\u4ea1\u7075\u4eec\u6765\u8bf4\uff0c\u8fd9\u6216\u8bb8\u662f\u4e2a\u597d\u6d88\u606f\uff0c\u4f46\u5bf9\u4e8e\u62dc\u5c14\u800c\u8a00\uff0c\u8fd9\u5c31\u7cdf\u7cd5\u900f\u9876\u3002</p><p>\u5c3d\u7ba1\u5728\u6307\u6325\u5b98\u8fd9\u4e2a\u4f4d\u7f6e\u4e0a\u5e72\u5f97\u5f88\u4e0d\u79f0\u804c\uff0c\u4f46\u6b4c\u560e\u65af\u786e\u5b9e\u662f\u7b2c\u4e00\u6d41\u7684\u6b66\u8005\uff0c\u4e0a\u6b21\u548c\u5f25\u6492\u4e9a\u4ea4\u624b\uff0c\u4e00\u4e2a\u7167\u9762\u5c31\u91cd\u4f24\u8d25\u9000\uff0c\u4eff\u4f5b\u5f92\u6709\u865a\u540d\uff0c\u90a3\u5176\u5b9e\u662f\u4e0a\u4e86\u62dc\u5c14\u7684\u5f53\u3002\u5438\u8840\u9b3c\u672c\u8eab\u662f\u4e0d\u4f1a\u98de\u7fd4\u7684\uff0c\u9664\u975e\u53d8\u6210\u8759\u8760\uff0c\u6b4c\u560e\u65af\u88ab\u5f15\u5230\u9ad8\u7a7a\uff0c\u4e27\u5931\u4e86\u5728\u5730\u9762\u4e0a\u8f97\u8f6c\u817e\u632a\u7684\u901f\u5ea6\u4f18\u52bf\uff0c\u5b9e\u529b\u81f3\u5c11\u8981\u6253\u4e2a\u4e94\u6298\uff0c\u53cd\u8fc7\u6765\u5f25\u8d5b\u4e9a\u5374\u662f\u66e6\u5929\u4f7f\uff0c\u80cc\u540e\u7684\u4e00\u5bf9\u7fbd\u7ffc\u53ef\u4e0d\u662f\u6446\u8bbe\uff0c\u6700\u64c5\u957f\u7684\u5c31\u662f\u7a7a\u6218\uff0c\u6b64\u6d88\u5f7c\u957f\u4e4b\u4e0b\uff0c\u6b4c\u560e\u65af\u5018\u82e5\u4e0d\u5927\u8d25\u800c\u5f52\u624d\u662f\u602a\u4e8b\u3002\u5982\u4eca\u662f\u5728\u5730\u4e0b\u5bab\u6bbf\u91cc\u906d\u9047\uff0c\u5730\u7406\u5f62\u52bf\u7684\u4f18\u52a3\u987f\u65f6\u5b8c\u5168\u9006\u8f6c\u8fc7\u6765\u3002</p><p>\u4e00\u573a\u5267\u6218\uff0c\u62dc\u5c14\u91cd\u521b\uff0c\u5f25\u8d5b\u4e9a\u4e5f\u53d7\u4f24\u4e0d\u8f7b\uff0c\u516b\u4e2a\u5deb\u5996\u548c\u6b4c\u560e\u65af\u5c3d\u6570\u88ab\u51fb\u6740\uff0c\u4f46\u795e\u8eaf\u9b54\u50cf\u8fd8\u662f\u5728\u6700\u540e\u5173\u5934\u88ab\u6210\u529f\u542f\u52a8\u3002\u66e6\u5929\u4f7f\u65e0\u5948\u4e4b\u4e0b\u53ea\u5f97\u5148\u628a\u9b54\u50cf\u5f15\u8d70\uff0c\u907f\u514d\u5b83\u653b\u51fb\u62dc\u5c14\u3002</p><p>\u201c\u90a3\u8fd9\u5730\u65b9\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u4f60\u4eec\u51e0\u4e2a\u6253\u67b6\u4e0d\u81f3\u4e8e\u628a\u6574\u4e2a\u5bab\u6bbf\u90fd\u62c6\u6bc1\u4e86\u5427\u3002\u201d</p><p>\u201c\u54e6\uff0c\u4f60\u770b\u6211\u53d7\u4e86\u91cd\u4f24\uff0c\u8dd1\u53c8\u8dd1\u4e0d\u5feb\uff0c\u8fd9\u5730\u65b9\u88ab\u7a7a\u95f4\u67b7\u9501\u5c01\u95ed\u4e86\uff0c\u60f3\u4f20\u9001\u9003\u8dd1\u90fd\u4e0d\u6210\u3002\u9b54\u50cf\u867d\u7136\u662f\u88ab\u5f15\u8d70\u4e86\uff0c\u53ef\u662f\u8fd8\u6709\u5f88\u591a\u5b88\u536b\u8dd1\u8fc7\u6765\u60f3\u6740\u6211\uff0c\u53ea\u80fd\u60f3\u70b9\u529e\u6cd5\u81ea\u4fdd\u4e86\u3002\u201d</p><p>\u201c\u60f3\u70b9\u529e\u6cd5\u81ea\u4fdd\uff1f\u6211\u4e3a\u4ec0\u4e48\u4f1a\u95fb\u5230\u8fd9\u4e48\u91cd\u7684\u706b\u836f\u5473\uff1f\u201d</p><p>\u201c\u5f88\u7b80\u5355\uff0c\u56e0\u4e3a\u6211\u65e9\u5c31\u5728\u8fd9\u91cc\u57cb\u4e86\u4e00\u5806\u706b\u836f\u554a\u3002\u201d</p><p>\u62dc\u5c14\u653e\u5f03\u9ed1\u77f3\u57ce\uff0c\u4e5f\u731c\u5230\u6076\u9b54\u519b\u5e94\u8be5\u4f1a\u62ff\u8fd9\u5ea7\u5730\u4e0b\u5bab\u6bbf\u505a\u9b54\u50cf\u4fee\u590d\u7684\u57fa\u5730\uff0c\u6b63\u597d\u5e93\u5b58\u7684\u706b\u836f\u70b8\u5f39\u8fd8\u6709\u4e00\u4e9b\uff0c\u7d22\u6027\u5c3d\u6570\u90fd\u57cb\u8fdb\u8fd9\u91cc\uff0c\u539f\u672c\u5c31\u662f\u9884\u5907\u7740\u8981\u641e\u7834\u574f\u3002\u8fd9\u5893\u5730\u4e0b\u9762\u662f\u5b8c\u5168\u4e2d\u7a7a\u7684\uff0c\u53ea\u8981\u628a\u51e0\u4e2a\u5173\u952e\u7684\u652f\u67f1\u70b8\u6bc1\uff0c\u6574\u4e2a\u5bab\u6bbf\u987f\u65f6\u5c3d\u6570\u5d29\u584c\u3002\u867d\u7136\u5a01\u529b\u6709\u4e9b\u4e0d\u5c3d\u5982\u4eba\u610f\uff0c\u6ca1\u80fd\u771f\u6b63\u4f24\u5bb3\u5230\u795e\u8eaf\u9b54\u50cf\uff0c\u4f46\u5bf9\u4ed8\u90a3\u4e9b\u4ea1\u7075\u5b88\u536b\u8fd8\u662f\u7ef0\u7ef0\u6709\u4f59\uff0c\u8fde\u5e26\u88ab\u56da\u7981\u5728\u6b64\u5904\u5f53\u201c\u6750\u6599\u201d\u7684\u6076\u9b54\u4eec\u4e5f\u90fd\u7eb7\u7eb7\u906d\u6b83\uff0c\u4e00\u547d\u545c\u547c\u3002\u81f3\u4e8e\u62dc\u5c14\u81ea\u5df1\uff0c\u4ed6\u662f\u59cb\u4f5c\u4fd1\u8005\uff0c\u81ea\u7136\u63d0\u524d\u5c31\u627e\u597d\u5b89\u5168\u4f4d\u7f6e\uff0c\u4e0d\u4f1a\u88ab\u538b\u6b7b\u3002</p><p>\u201c\u786e\u5b9e\u6ca1\u88ab\u538b\u6b7b\uff0c\u4f46\u4f60\u8fd9\u817f\u2026\u2026\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u8bd5\u7528\u65b0\u5f0f\u6b66\u5668\uff0c\u96be\u514d\u4f1a\u6709\u610f\u5916\u561b\uff0c\u201d\u62dc\u5c14\u54c8\u54c8\u5927\u7b11\uff0c\u201c\u4e0d\u7528\u5728\u610f\uff0c\u4e0d\u7528\u5728\u610f\u3002\u201d</p><p>\u706b\u836f\u8fd9\u79cd\u4e1c\u897f\uff0c\u5c31\u8fde\u5728\u7269\u8d28\u754c\u90fd\u8fd8\u662f\u534a\u79d8\u5bc6\u72b6\u6001\uff0c\u77e5\u6653\u7684\u4eba\u5be5\u5be5\u65e0\u51e0\uff0c\u66f4\u522b\u8bf4\u662f\u4e0b\u5c42\u754c\u3002\u6076\u9b54\u4eec\u81ea\u7136\u4e00\u65e0\u6240\u77e5\uff0c\u6240\u4ee5\u624d\u4f1a\u5403\u4e86\u5927\u4e8f\uff0c\u4f46\u9b54\u9b3c\u5176\u5b9e\u4e5f\u672a\u89c1\u5f97\u5c31\u591a\u4e48\u7cbe\u901a\u4e86\u3002\u81f3\u5c11\u5bf9\u4e8e\u62dc\u5c14\u6765\u8bf4\uff0c\u7528\u706b\u836f\u6765\u70b8\u623f\u5b50\uff0c\u8fd9\u4e8b\u60c5\u8fd8\u662f\u7b2c\u4e00\u6b21\u505a\uff0c\u96be\u514d\u4f1a\u51fa\u5dee\u9519\u3002\u7ed3\u679c\u5c31\u662f\u4ed6\u8eb2\u5728\u81ea\u4ee5\u4e3a\u5b89\u5168\u7684\u5730\u65b9\uff0c\u5934\u9876\u5374\u6389\u4e0b\u4e00\u5757\u5343\u65a4\u5de8\u77f3\u6765\u2026\u2026</p><p>\u201c\u603b\u7b97\u6211\u8eb2\u95ea\u5f97\u5feb\uff0c\u5426\u5219\u5c31\u4e0d\u662f\u534a\u6761\u817f\uff0c\u800c\u662f\u6574\u4e2a\u4eba\u90fd\u53d8\u6210\u8089\u997c\u4e86\u3002\u4ee5\u540e\u4e00\u5b9a\u8981\u53bb\u641c\u7f57\u4e00\u4e9b\u5efa\u7b51\u5b66\u65b9\u9762\u7684\u4eba\u624d\u6765\uff0c\u8fd9\u79cd\u5b9a\u70b9\u7206\u7834\u7684\u4e8b\u60c5\uff0c\u8fd8\u662f\u5e94\u8be5\u8ba9\u4e13\u4e1a\u4eba\u58eb\u6765\u505a\u624d\u5bf9\u3002\u201d</p><p>\u62dc\u5c14\u7684\u611f\u53f9\uff0c\u4e0d\u80fd\u8bf4\u6ca1\u6709\u9053\u7406\uff0c\u7136\u800c\u743c\u6069\u6b64\u65f6\u5374\u6ca1\u5fc3\u601d\u542c\u4ed6\u603b\u7ed3\u7ecf\u9a8c\u6559\u8bad\u3002\u201c\u5c06\u519b\uff0c\u6211\u5b9e\u5728\u662f\u5f88\u4f69\u670d\u4f60\u554a\uff0c\u5230\u8fd9\u79cd\u65f6\u5019\uff0c\u4f60\u5c45\u7136\u8fd8\u80fd\u7b11\u5f97\u8fd9\u4e48\u5f00\u5fc3\u3002\u201d</p><p>\u201c\u55ef\uff1f\u6211\u6b7b\u91cc\u9003\u751f\uff0c\u6027\u547d\u65e0\u5fe7\uff0c\u96be\u9053\u4e0d\u5e94\u8be5\u5f00\u5fc3\u5417\uff1f\u201d</p><p>\u201c\u6027\u547d\u65e0\u5fe7\uff1f\u201d\u743c\u6069\u51b7\u7b11\uff0c\u201c\u90a3\u9b54\u50cf\u53ef\u5df2\u7ecf\u542f\u52a8\u4e86\uff0c\u4f60\u5982\u4eca\u624b\u4e0b\u7cbe\u9510\u5c3d\u5931\uff0c\u81ea\u5df1\u90fd\u88ab\u538b\u5728\u8fd9\u91cc\u4e0d\u80fd\u52a8\u5f39\uff0c\u8fd8\u62ff\u4ec0\u4e48\u53bb\u62b5\u6321\uff1f\u201d</p><p>\u201c\u653e\u5fc3\uff0c\u6211\u4eec\u867d\u7136\u6ca1\u80fd\u963b\u6b62\u9b54\u50cf\u542f\u52a8\uff0c\u4f46\u4e5f\u4e0d\u662f\u4ec0\u4e48\u4e8b\u90fd\u6ca1\u505a\u554a\uff0c\u81f3\u5c11\u90a3\u516b\u4e2a\u5deb\u5996\u662f\u5168\u90fd\u5e72\u6389\u4e86\u3002\u201d</p><p>\u62dc\u5c14\u63a5\u7740\u89e3\u91ca\uff0c\u795e\u8eaf\u9b54\u50cf\u4e4b\u6240\u4ee5\u96be\u4ee5\u5bf9\u6297\uff0c\u9996\u5148\u662f\u56e0\u4e3a\u5b83\u80fd\u591f\u53d8\u5e7b\u5404\u79cd\u795e\u76f8\uff0c\u5176\u6b21\u662f\u56e0\u4e3a\u5b83\u80fd\u4e0d\u65ad\u4fee\u590d\uff0c\u4f46\u8fd9\u4e9b\u5168\u90fd\u662f\u5fc5\u987b\u8981\u5deb\u5996\u6765\u64cd\u7eb5\u63a7\u5236\u7684\u3002\u5982\u4eca\u516b\u4e2a\u5deb\u5996\u90fd\u88ab\u51fb\u6740\uff0c\u9664\u975e\u5965\u5580\u65af\u53e6\u5916\u6d3e\u4eba\u6765\uff0c\u5426\u5219\u9b54\u50cf\u4e0d\u80fd\u53d8\u5e7b\u795e\u76f8\uff0c\u5a01\u529b\u81ea\u7136\u5927\u51cf\uff0c\u53d7\u521b\u4e4b\u540e\u66f4\u6ca1\u6cd5\u4fee\u590d\uff0c\u5bf9\u4ed8\u8d77\u6765\u5c31\u5bb9\u6613\u591a\u4e86\u3002</p><p>\u201c\u800c\u4e14\u4eca\u5929\u65e9\u4e0a\uff0c\u6211\u4eec\u91cd\u521b\u4e86\u9b54\u50cf\uff0c\u73b0\u5728\u624d\u53ea\u8fc7\u4e86\u4e0d\u5230\u516b\u4e2a\u5c0f\u65f6\u3002\u8fd9\u602a\u7269\u867d\u7136\u7ecf\u8fc7\u4e00\u5b9a\u7a0b\u5ea6\u7684\u4fee\u590d\uff0c\u4f46\u5e76\u4e0d\u662f\u5b8c\u5168\u72b6\u6001\uff0c\u4f60\u6ca1\u53d1\u89c9\u5b83\u884c\u52a8\u4e4b\u95f4\u90fd\u8fdf\u7f13\u4e86\u5f88\u591a\u5417\u3002\u201d</p><p>\u743c\u6069\u671d\u7740\u62dc\u5c14\u6240\u6307\u7684\u65b9\u5411\u770b\u53bb\uff0c\u795e\u8eaf\u9b54\u50cf\u548c\u66e6\u5929\u4f7f\u8fd8\u5728\u7f20\u6597\u8ffd\u9010\uff0c\u770b\u8d77\u6765\u4e0d\u50cf\u662f\u751f\u6b7b\u6fc0\u6218\uff0c\u53cd\u800c\u662f\u5728\u6e38\u620f\u4f3c\u7684\u3002\u9b54\u50cf\u6bcf\u4e00\u62f3\u51fb\u51fa\uff0c\u4f9d\u65e7\u662f\u6709\u5343\u94a7\u4e4b\u529b\uff0c\u65e0\u53ef\u62b5\u5fa1\uff0c\u4f46\u52a8\u4f5c\u5374\u6bd4\u4ee5\u524d\u660e\u663e\u7f13\u6162\u4e86\u8bb8\u591a\uff0c\u6240\u6709\u7684\u653b\u51fb\u5c3d\u6570\u843d\u7a7a\uff0c\u66e6\u5929\u4f7f\u5c55\u5f00\u7fbd\u7ffc\uff0c\u51ed\u501f\u5353\u8d8a\u7684\u98de\u7fd4\u80fd\u529b\u5728\u7a7a\u4e2d\u6e38\u8d70\u95ea\u907f\uff0c\u4e0d\u65f6\u5728\u9b54\u50cf\u8eab\u4e0a\u780d\u4e0a\u4e00\u5251\uff0c\u867d\u7136\u90fd\u9020\u4e0d\u6210\u591a\u91cd\u7684\u521b\u4f24\uff0c\u4f46\u8fd9\u6837\u6301\u7eed\u4e0b\u53bb\uff0c\u7ec8\u7a76\u6709\u80dc\u5229\u7684\u65f6\u5019\u2014\u2014\u5f53\u7136\uff0c\u524d\u63d0\u662f\u66e6\u5929\u4f7f\u4e0d\u4f1a\u5148\u81ea\u5df1\u7d2f\u57ae\u3002</p><p>\u201c\u81f3\u4e8e\u6211\u81ea\u5df1\u4e48\uff0c\u5012\u662f\u6709\u70b9\u9ebb\u70e6\uff0c\u201d\u62dc\u5c14\u82e6\u7b11\uff0c\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u80fd\u5426\u5e2e\u6211\u901a\u77e5\u5c5e\u4e0b\uff0c\u8ba9\u4ed6\u4eec\u5e26\u70b9\u5de5\u5177\u628a\u6211\u5f04\u51fa\u6765\u3002\u201d</p><p>\u8fd9\u79cd\u4e3e\u624b\u4e4b\u52b3\uff0c\u743c\u6069\u81ea\u7136\u6ca1\u7406\u7531\u62d2\u7edd\uff0c\u7136\u800c\u6b63\u5f53\u4ed6\u7ad9\u8d77\u8eab\uff0c\u51c6\u5907\u542f\u52a8\u98de\u884c\u672f\u65f6\uff0c\u9661\u7136\u6014\u4e86\u6014\u3002\u201c\u62dc\u5c14\u5c06\u519b\uff0c\u4f60\u521a\u624d\u597d\u50cf\u8bf4\uff0c\u4f60\u4eec\u628a\u6b4c\u560e\u65af\u6740\u4e86\uff1f\u201d</p><p>\u201c\u662f\u554a\u3002\u201d</p><p>\u201c\u786e\u5b9a\u6b7b\u4e86\uff1f\u201d</p><p>\u201c\u786e\u5b9a\uff0c\u4ed6\u88ab\u5f25\u8d5b\u4e9a\u4e00\u8bb0\u77ac\u6740\u7bad\u5c04\u7a7f\u4e86\u5fc3\u810f\uff0c\u5fc5\u6b7b\u65e0\u7591\u3002\u201d</p><p>\u743c\u6069\u70b9\u70b9\u5934\uff0c\u4f38\u624b\u4e00\u6307\uff0c\u201c\u90a3\u4e48\u4ed6\u662f\u8c01\uff1f\u201d</p><p>\u62dc\u5c14\u83ab\u540d\u5176\u5999\u5730\u987a\u7740\u4ed6\u6240\u6307\u65b9\u5411\u770b\u53bb\uff0c\u4e00\u4e2a\u7626\u524a\u7684\u4eba\u5f71\u6b63\u4ece\u8fdc\u5904\u7684\u5e9f\u589f\u4e2d\u6162\u6162\u722c\u8d77\uff0c\u4e0d\u662f\u522b\u4eba\uff0c\u6b63\u662f\u4ed6\u521a\u521a\u8bf4\u201c\u5fc5\u6b7b\u65e0\u7591\u201d\u7684\u6b4c\u560e\u65af\u3002</p></article>"}}}]);
