(window.webpackJsonp=window.webpackJsonp||[]).push([[356],{379:function(p){p.exports={chapterTitle:null,title:"\u7b2c231\u7ae0 \u8c22\u6c49\u9756\u7684\u610f\u5fd7",content:"<article><h2>\u7b2c231\u7ae0 \u8c22\u6c49\u9756\u7684\u610f\u5fd7</h2><p>\u3000\u3000\u6b63\u6cf0\u96c6\u56e2\u7ba1\u7406\u5c42\u96c6\u4f53\u7684\u610f\u5fd7\u6bd5\u7adf\u8fd8\u4e0d\u662f\u8c22\u6c49\u660e\u3001\u8c22\u5251\u5357\u7236\u5b50\u6240\u80fd\u5de6\u53f3\u7684\uff0c\u5bf9\u4e8e\u56fd\u5185\u73af\u5883\u653f\u7b56\u5bf9\u9020\u7eb8\u4ea7\u4e1a\u7684\u6301\u7eed\u5f71\u54cd\uff0c\u4f1a\u4f7f\u9020\u7eb8\u4ea7\u4e1a\u957f\u671f\u5904\u4e8e\u4e0a\u5347\u671f\uff0c\u57fa\u4e8e\u8fd9\u6837\u7684\u5224\u65ad\uff0c\u6709\u673a\u4f1a\u8fdb\u5165\u4e00\u4e2a\u5189\u5189\u4e0a\u5347\u7684\u671d\u9633\u4ea7\u4e1a\uff0c\u6b63\u6cf0\u96c6\u56e2\u5f53\u7136\u4e0d\u4f1a\u9519\u8fc7\uff0c\u8fd9\u662f\u8c22\u6c49\u9756\u6700\u7ec8\u51b3\u5b9a\u4ece\u6d77\u88d5\u624b\u91cc\u6536\u8d2d30\uff05\u9526\u6e56\u80a1\u4efd\u7684\u521d\u8877\u3002</p><p>\u3000\u3000\u4e0e\u8c22\u5251\u5357\u5fc3\u6000\u6028\u6068\u4e0d\u540c\uff0c\u8c22\u6c49\u9756\u5e0c\u671b\u5728\u8fdb\u5165\u9526\u6e56\u4e4b\u521d\uff0c\u5c31\u901a\u8fc7\u8c08\u5224\u89e3\u51b3\u6389\u65e5\u540e\u53ef\u80fd\u4ea7\u751f\u4e89\u6267\u7684\u9690\u60a3\uff0c\u8d8a\u79c0\u516c\u53f8\u624b\u91cc\u5bf9\u9020\u7eb8\u5382\u7684\u5e76\u8d2d\u6743\u4ee5\u53ca\u65b0\u5149\u5546\u6807\uff0c\u662f\u5b9e\u65bd\u5e76\u8d2d\u524d\u5fc5\u987b\u8981\u89e3\u51b3\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5c06\u624b\u91cc\u7684\u80a1\u4efd\u5957\u73b0\u4ece\u6d77\u6cf0\u516c\u53f8\u9010\u6b65\u9000\u51fa\uff0c\u5f20\u606a\u6709\u4ed6\u7684\u8003\u8651\u3002\u867d\u7136\u6e20\u9053\u8d44\u6e90\u5728\u4e5d\u5341\u5e74\u4ee3\u4e2d\u671f\u5341\u5206\u91cd\u8981\uff0c\u4f46\u662f\u968f\u7740\u5927\u578b\u8fde\u9501\u5356\u573a\u7684\u51fa\u73b0\uff0c\u6700\u7ec8\u5f62\u6210\u7684\u5e9e\u5927\u7684\u5546\u4e1a\u8d44\u672c\u52bf\u529b\uff0c\u624d\u662f\u5546\u54c1\u6d41\u901a\u9886\u57df\u6700\u5f3a\u52bf\u7684\u529b\u91cf\uff0c\u6e20\u9053\u5546\u867d\u7136\u4f1a\u4e00\u76f4\u5b58\u5728\uff0c\u4f46\u662f\u53d1\u5c55\u7a7a\u95f4\u5374\u8d8a\u6765\u8d8a\u5c0f\u3002\u53ca\u65f6\u4ece\u53d1\u5c55\u524d\u666f\u8d8a\u6765\u8d8a\u72ed\u9698\u7684\u884c\u4e1a\u9000\u51fa\uff0c\u624d\u8ba9\u8ba9\u6536\u76ca\u6700\u5927\u5316\uff0c\u8fd9\u662f\u5f20\u606a\u4e0d\u73cd\u60dc\u6d77\u6cf0\u80a1\u6743\u7684\u521d\u8877\u3002\u4f46\u662f\u9020\u7eb8\u4ea7\u4e1a\uff0c\u5f20\u606a\u786e\u77e5\u4e00\u76f4\u5230\u5341\u51e0\u5e74\u540e\u90fd\u662f\u503c\u5f97\u5927\u89c4\u6a21\u6295\u8d44\u7684\u671d\u9633\u4ea7\u4e1a\uff0c\u5f53\u7136\u4e0d\u4f1a\u8f7b\u6613\u9000\u51fa\u3002</p><p>\u3000\u3000\u6536\u8d2d\u90e8\u5206\u80a1\u6743\u65e0\u6cd5\u907f\u514d\uff0c\u5173\u952e\u662f\u6536\u8d2d\u5b9a\u4ef7\u7684\u9ad8\u4f4e\u3002</p><p>\u3000\u3000\u9526\u6e56\u624b\u91cc\u6700\u6709\u4ef7\u503c\u7684\u8d44\u6e90\u5c31\u662f\u516d\u5bb6\u9020\u7eb8\u5382\u7684\u7ecf\u8425\u6743\uff0c\u5176\u4e2d\u4ee5\u65b0\u5149\u5382\u7684\u8d44\u4ea7\u6574\u5408\u6700\u4e3a\u5145\u5206\uff0c\u5176\u76c8\u5229\u524d\u666f\u4e5f\u6700\u4e3a\u660e\u6717\uff0c\u4e0d\u4ec5\u65b0\u5382\u6709\u7740\u7a33\u5b9a\u7684\u76c8\u5229\uff0c\u65e7\u5382\u7684\u5730\u79df\u6bcf\u5e74\u81f3\u5c11\u4e5f\u5728\u516d\u767e\u4e07\u4e4b\u4e0a\uff1b\u5176\u4ed6\u4e94\u5bb6\u9020\u7eb8\u5382\uff0c\u4e4b\u524d\u901a\u8fc7\u5404\u79cd\u5173\u7cfb\uff0c\u4ece\u56fd\u5bb6\u8ba1\u59d4\u7533\u8bf7\u5230\u4e00\u4ebf\u5143\u7684\u6280\u6539\u9879\u76ee\u8d44\u91d1\u8d37\u6b3e\uff0c\u6b63\u7b79\u5907\u7d27\u6328\u7740\u65b0\u5149\u5382\u65b0\u5382\u65c1\u8fb9\u6295\u8d44\u5efa\u8bbe\u4e00\u5ea7\u5927\u578b\u7684\u9020\u7eb8\u57fa\u5730\uff0c\u8d44\u4ea7\u7684\u6574\u5408\u8fd8\u521a\u521a\u5f00\u59cb\uff0c\u6548\u76ca\u8fd8\u6ca1\u6709\u663e\u73b0\u51fa\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u9884\u8ba1\u5230\u5bf9\u516d\u5bb6\u9020\u7eb8\u5382\u7ecf\u8425\u6743\u7684\u4f30\u4ef7\u5c06\u8c08\u5224\u7684\u96be\u70b9\u3002</p><p>\u3000\u3000\u7ad9\u5728\u522b\u5885\u7684\u540e\u5ead\u56ed\u91cc\uff0c\u4e34\u6e56\u6709\u6761\u6728\u642d\u5efa\u7684\u5165\u6c34\u53f0\u9636\uff0c\u5f20\u606a\u5750\u5728\u5165\u6c34\u53f0\u9636\u4e0a\uff0c\u5bf9\u665a\u775b\u8bf4\uff1a\u201c\u6b63\u6cf0\u96c6\u56e2\u8fd9\u6b21\u4f1a\u6d3e\u8c01\u6765\uff1f\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u901a\u8fc7\u6d77\u88d5\u516c\u53f8\u5bf9\u9526\u6e56\u63a7\u80a170\uff05\uff0c\u6b63\u6cf0\u8fd9\u6b21\u662f\u4ece\u6d77\u88d5\u516c\u53f8\u624b\u91cc\u6536\u8d2d30\uff05\u9526\u6e56\u7684\u80a1\u6743\uff0c\u4f46\u662f\u6536\u8d2d\u65f6\uff0c\u53c8\u6d89\u53ca\u5230\u5bf9\u516d\u5bb6\u9020\u7eb8\u5382\u7684\u5e76\u8d2d\u6743\u3001\u65b0\u5149\u5546\u6807\u6240\u6709\u6743\u5c5e\u7684\u95ee\u9898\uff0c\u5f20\u606a\u51b3\u5b9a\u4eb2\u81ea\u53c2\u4e0e\u8c08\u5224\uff0c\u4e5f\u7b97\u662f\u8ddf\u6b63\u6cf0\u96c6\u56e2\u7684\u9996\u6b21\u4ea4\u950b\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u6e29\u5a49\u7684\u7b11\u4e86\u7b11\uff1a\u201c\u4ed6\u4eec\u662f\u628a\u6211\u5b8c\u5168\u5f53\u6210\u5916\u4eba\u4e86\uff0c\u8fd9\u6b21\u8c01\u8fc7\u6765\uff0c\u6211\u4e5f\u4e0d\u77e5\u9053\u7684\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u60f3\u4e00\u672c\u6b63\u7ecf\u7684\u5728\u65b0\u6d77\u901a\u5927\u53a6\u7684\u4f1a\u8bae\u5ba4\u91cc\u8fdb\u884c\u8c08\u5224\uff0c\u800c\u9009\u62e9\u5728\u5c0f\u9526\u6e56\u7554\u7684\u522b\u5885\u91cc\u606d\u5019\u6b63\u6cf0\u96c6\u56e2\u7684\u4ee3\u8868\u3002</p><p>\u3000\u3000\u82b7\u5f64\u4e0e\u60dc\u5bb9\u3001\u60dc\u7fbd\u59d0\u59b9\u5728\u524d\u9762\u7684\u5ba2\u5385\u91cc\u7684\u4e71\u4e32\uff0c\u4e0d\u65f6\u7684\u53d1\u51fa\u94f6\u94c3\u822c\u7684\u8c08\u58f0\uff0c\u8521\u7eef\u5a1f\u53ca\u52a9\u624b\u5728\u4f1a\u5ba2\u5ba4\u91cc\u51c6\u5907\u6750\u6599\uff0c\u5085\u4fca\u773c\u775b\u76ef\u7740\u4e09\u4e2a\u5c0f\u4e2b\u5934\uff0c\u4ee5\u514d\u78b0\u5230\u649e\u5230\u54ea\u91cc\uff0c\u9a6c\u6d77\u9f99\u8ddf\u968f\u5f20\u606a\u624d\u534a\u4e2a\u591a\u6708\u7684\u65f6\u95f4\uff0c\u4e0d\u8fc7\u5df2\u7ecf\u9002\u5e94\u8fc7\u6765\uff0c\u4ecd\u662f\u5f20\u606a\u6ca1\u6709\u592a\u591a\u8981\u6c42\u7684\u7f18\u6545\u3002</p><p>\u3000\u3000\u542c\u7740\u524d\u9662\u6c7d\u8f66\u5587\u53ed\u58f0\u54cd\uff0c\u5f20\u606a\u4ece\u53f0\u9636\u4e0a\u7ad9\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6765\u4e86\uff1f\u201d\u770b\u89c1\u9a6c\u6d77\u9f99\u6253\u5f00\u540e\u95e8\u9732\u51fa\u534a\u7247\u8eab\u5b50\u6765\uff0c\u77e5\u9053\u4eba\u6765\u4e86\uff0c\u6413\u6413\u624b\uff0c\u62db\u547c\u665a\u6674\u4e00\u8d77\u56de\u5c4b\u91cc\u53bb\u3002\u4eba\u7ad9\u5728\u5ba2\u5385\u91cc\uff0c\u770b\u89c1\u524d\u5ead\u9662\u91cc\u505c\u7740\u4e00\u90e8\u516b\u6210\u65b0\u7684\u6851\u5854\u7eb3\uff0c\u540e\u9762\u8ddf\u7740\u4e00\u8f86\u7d2b\u7405\u9762\u5305\u8f66\uff0c\u5f20\u606a\u6709\u4e9b\u61c8\u6c14\uff0c\u5374\u89c1\u8c22\u665a\u6674\u7684\u795e\u8272\u6709\u4e9b\u8bb6\u7136\u3002</p><p>\u3000\u3000\u201c\u4e8c\u53d4\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u6c49\u9756\uff0c\u6b63\u6cf0\u96c6\u56e2\u7684\u8463\u4e8b\u957f\u3001\u603b\u88c1\uff1f\u5374\u4e0d\u77e5\u9053\u6b63\u6cf0\u96c6\u56e2\u7684\u638c\u95e8\u4eba\u7684\u5750\u9a7e\u4f1a\u662f\u4e00\u8f86\u666e\u901a\u7684\u6851\u5854\u7eb3\u3002\u5f20\u606a\u770b\u7740\u56db\u5341\u4e94\u516d\u5c81\u7684\u4e2d\u5e74\u4eba\u4ece\u6851\u5854\u7eb3\u8f7f\u8f66\u91cc\u94bb\u51fa\u6765\uff0c\u867d\u7136\u6ca1\u6709\u9762\u5bf9\u9762\u89c1\u8fc7\uff0c\u4f46\u5728\u7535\u89c6\u91cc\u3001\u6742\u5fd7\u91cc\u770b\u8fc7\u4ed6\u7684\u6a21\u6837\uff0c\u4e4b\u540e\u662f\u8c22\u5251\u5357\uff0c\u8fd8\u6709\u4e00\u4e2a\u4e2d\u5e74\u4eba\uff0c\u5f88\u9762\u751f\uff0c\u4f46\u4ed6\u8ddf\u8c22\u6c49\u9756\u3001\u8c22\u5251\u5357\u90fd\u5f88\u76f8\u50cf\uff0c\u5e74\u7eaa\u8ddf\u8c22\u6c49\u9756\u76f8\u4eff\uff0c\u8c22\u5bb6\u8fd9\u4e00\u4ee3\u5802\u5144\u5f1f\u4f17\u591a\uff0c\u636e\u8bf4\u5f53\u521d\u8fd8\u662f\u4ed7\u5802\u5144\u5f1f\u4f17\u591a\uff0c\u627f\u5305\u9547\u4e0a\u7684\u91c7\u7802\u573a\u800c\u6398\u5230\u7b2c\u4e00\u6876\u91d1\u7684\u3002</p><p>\u3000\u3000\u4ece\u9762\u5305\u8f66\u91cc\u94bb\u51fa\u4e00\u4e9b\u4eba\uff0c\u5927\u6982\u662f\u8c08\u5224\u7528\u7684\u4e13\u4e1a\u4eba\u58eb\u3002</p><p>\u3000\u3000\u201c\u90a3\u662f\u6211\u4e09\u53d4\u2026\u2026\u201d\u665a\u6674\u6307\u7740\u8c22\u6c49\u9756\u65c1\u7684\u8c22\u6c49\u660e\u4ecb\u7ecd\u8bf4\u3002</p><p>\u3000\u3000\u6b63\u6cf0\u96c6\u56e2\u5bb6\u5927\u4e1a\u5927\uff0c\u6570\u5341\u4ebf\u7684\u8d44\u4ea7\uff0c\u8c22\u5bb6\u4e2d\u9752\u4e24\u4ee3\u6700\u91cd\u8981\u7684\u4e09\u4e2a\u4eba\u4e3a\u9526\u6e5630\uff05\u7684\u80a1\u6743\u6536\u8d2d\u6848\u9f50\u805a\u6d77\u5dde\uff0c\u5f20\u606a\u53ef\u6ca1\u6709\u53d7\u5ba0\u82e5\u60ca\u7684\u611f\u89c9\u3002\u5f20\u606a\u62cd\u62cd\u8111\u888b\uff0c\u6487\u6487\u5634\uff0c\u7ad9\u5728\u5ba2\u5385\u91cc\uff0c\u4e5f\u6ca1\u6709\u8981\u51fa\u53bb\u8fce\u63a5\u7684\u610f\u601d\u3002</p><p>\u3000\u3000\u9762\u5bf9\u5bb6\u91cc\u7684\u957f\u8f88\uff0c\u5c31\u7b97\u6ca1\u4ec0\u4e48\u60c5\u8c0a\uff0c\u8c22\u665a\u6674\u5374\u65e0\u6cd5\u505a\u5f97\u592a\u51b7\u6f20\uff0c\u8dd1\u51fa\u5ba2\u5385\u63a5\u8c22\u6c49\u9756\u3001\u8c22\u6c49\u660e\u4ed6\u4eec\u8fdb\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u6709\u610f\u65e0\u610f\u7684\u773c\u795e\u626b\u8fc7\u8c22\u5251\u5357\u7684\u8138\uff0c\u5634\u89d2\u6302\u7740\u6d45\u6d45\u7684\u7b11\uff0c\u4ee5\u81f4\u8c22\u5251\u5357\u5dee\u70b9\u6ca1\u6709\u52c7\u6c14\u8d70\u4e0a\u53f0\u9636\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4f4d\u662f\u2026\u2026\u201d\u8c22\u6c49\u9756\u76ee\u5149\u626b\u8fc7\u5ba2\u5385\u91cc\u7684\u8bf8\u4eba\uff0c\u6700\u540e\u8fd8\u662f\u5b9a\u775b\u770b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u6211\u662f\u5f20\u606a\uff0c\u8c22\u5148\u751f\u6765\u4e4b\u524d\u4e00\u5b9a\u5f88\u60f3\u77e5\u9053\u8d8a\u79c0\u516c\u53f8\u7684\u4ee3\u8868\u4f1a\u662f\u8c01\uff0c\u201d\u5f20\u606a\u6de1\u6de1\u7684\u7b11\u7740\uff0c\u201c\u770b\u5230\u6211\u4f1a\u4e0d\u4f1a\u5947\u602a\uff1f\u201d</p><p>\u3000\u3000\u4ece\u5bb9\u4e0d\u8feb\u7684\u8138\u8272\u770b\u4e0d\u51fa\u4ed6\u5185\u5fc3\u7684\u52a8\u8361\uff0c\u8c22\u6c49\u9756\u53ea\u662f\u56de\u5934\u770b\u4e86\u8c22\u5251\u5357\u4e00\u773c\uff0c\u76f8\u5f53\u5927\u5ea6\u7684\u671d\u5f20\u606a\u4f38\u51fa\u624b\u63e1\u4e86\u63e1\uff0c\u5e76\u4e0d\u4ecb\u610f\u4ed6\u7684\u5e74\u8f7b\u3001\u8d44\u5386\u6d45\u3002</p><p>\u3000\u3000\u4ed6\u8fd9\u6b21\u4eb2\u81ea\u8fc7\u6765\u6700\u4e3b\u8981\u7684\u7528\u610f\uff0c\u662f\u8003\u8651\u5230\u6700\u8fd1\u5728\u4e1c\u6d77\u7701\u540d\u58f0\u9e4a\u8d77\u7684\u7231\u8fbe\u7535\u5b50\u7684\u6295\u8d44\u65b9\uff0c\u53ef\u80fd\u5c31\u662f\u9526\u6e56\u7684\u7b2c\u4e8c\u5927\u80a1\u4e1c\u8d8a\u79c0\u516c\u53f8\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f53\u521d\u5728\u6d77\u6cf0\u5360\u80a1\u7528\u7684\u662f\u8bb8\u601d\u7684\u540d\u4e49\uff0c\u8d8a\u79c0\u7684\u6cd5\u5b9a\u4ee3\u8868\u4eba\u4e5f\u662f\u8bb8\u601d\uff0c\u8c22\u5251\u5357\u80fd\u6709\u9488\u5bf9\u6027\u7684\u67e5\u5230\u8bb8\u601d\u4fbf\u662f\u6d89\u53ca\u4e01\u5411\u5c71\u6848\u7684\u8bb8\u601d\uff0c\u800c\u5bf9\u4e8e\u8c22\u6c49\u9756\u6216\u8005\u8c22\u5251\u5357\u7684\u7236\u4eb2\u8c22\u6c49\u660e\u800c\u8a00\uff0c\u4ed6\u4eec\u751a\u81f3\u4e0d\u77e5\u9053\u8bb8\u601d\u662f\u7537\u662f\u5973\u3002\u65e0\u8bba\u662f\u6d77\u6cf0\u3001\u9526\u6e56\u3001\u8fd8\u662f\u7231\u8fbe\u7535\u5b50\uff0c\u6240\u73b0\u7684\u5e02\u573a\u8fd0\u4f5c\u6c34\u51c6\u3001\u8d44\u4ea7\u6574\u5408\u624b\u6bb5\uff0c\u90fd\u662f\u8c22\u6c49\u9756\u6240\u7f55\u89c1\u7684\uff0c\u4ed6\u8fd9\u6b21\u60f3\u501f\u8fd9\u6b21\u8c08\u5224\u7684\u673a\u4f1a\u89c1\u4e00\u89c1\u5e55\u540e\u7684\u90a3\u4e2a\u4eba\u3002</p><p>\u3000\u3000\u8fd8\u6709\u4e00\u4e2a\u76ee\u7684\uff0c\u8c22\u665a\u6674\u7236\u6bcd\u65e9\u901d\uff0c\u5979\u53c8\u8ddf\u5979\u540c\u7236\u5f02\u6bcd\u7684\u5144\u957f\u8c22\u665a\u5c71\u5173\u7cfb\u5f88\u50f5\uff0c\u5979\u65e9\u5e74\u5728\u56fd\u5916\u7559\u5b66\uff0c\u8ddf\u8c22\u5bb6\u5176\u4ed6\u4eba\u7684\u5173\u7cfb\u90fd\u5f88\u6de1\uff0c\u867d\u5c31\u7ecf\u8fc7\u5468\u747e\u745c\u7684\u5173\u7cfb\uff0c\u8ba4\u8bc6\u540c\u5728\u6d77\u5916\u7559\u5b66\u7684\u5f90\u5fd7\u660e\uff0c\u4f46\u8ddf\u8c22\u5bb6\u4eba\u7684\u5173\u7cfb\u4e00\u76f4\u6ca1\u6709\u4eb2\u70ed\u8d77\u6765\u3002</p><p>\u3000\u3000\u56e0\u4e3a\u5468\u747e\u745c\u4e0e\u5468\u53d4\u60e0\u6709\u8fdc\u8868\u4eb2\u7684\u5173\u7cfb\uff0c\u5468\u747e\u745c\u53c8\u662f\u5979\u8ddf\u5f90\u5fd7\u660e\u4e4b\u95f4\u7684\u4ecb\u7ecd\u4eba\uff0c\u8c22\u665a\u6674\u4e4b\u524d\u4e0e\u8c22\u6c49\u660e\u3001\u8c22\u5251\u5357\u4e00\u5bb6\u4eb2\u70ed\u4e9b\uff0c\u5f90\u5fd7\u660e\u56de\u56fd\u521b\u529e\u6d77\u88d5\u8d38\u6613\u516c\u53f8\uff0c\u800c\u540e\u5f90\u5b66\u5e73\u8c03\u5230\u7701\u91cc\u5f53\u653f\u6cd5\u59d4\u4e66\u8bb0\uff0c\u5f90\u5fd7\u660e\u9000\u51fa\u6d77\u88d5\u516c\u53f8\u7684\u7ecf\u8425\uff0c\u8c22\u77bb\u4ee3\u638c\u6d77\u88d5\u8fd8\u662f\u8c22\u6c49\u660e\u63a8\u8350\u7684\u3002\u540e\u6765\u8ddf\u8c22\u77bb\u5f04\u5f97\u4e0d\u6b22\u800c\u6563\uff0c\u5373\u4f7f\u8fd9\u79cd\u60c5\u51b5\uff0c\u8c22\u665a\u6674\u5728\u9047\u5230\u56f0\u96be\u65f6\uff0c\u8fd8\u662f\u9996\u5148\u60f3\u5230\u5411\u6b63\u6cf0\u96c6\u56e2\u6551\u63f4\u3002</p><p>\u3000\u3000\u662f\u6d77\u6cf0\u7b79\u5efa\u5e76\u9010\u6e10\u53d1\u5c55\u7684\u8fc7\u7a0b\u4e2d\uff0c\u8c22\u665a\u6674\u9010\u6e10\u8fdc\u79bb\u4e86\u5bb6\u65cf\uff0c\u5230\u4e86\u8c22\u5251\u5357\u5728\u6d77\u5dde\u88ab\u7838\u8f66\uff0c\u5979\u5df2\u7ecf\u8868\u73b0\u51fa\u660e\u663e\u7684\u6001\u5ea6\u3002</p><p>\u3000\u3000\u5bf9\u8c22\u665a\u6674\u5fc3\u601d\u7684\u8f6c\u53d8\uff0c\u8c22\u6c49\u9756\u5fc3\u91cc\u4e5f\u6e05\u695a\uff0c\u4ed6\u603b\u8981\u5c1d\u8bd5\u7740\u80fd\u4e0d\u80fd\u633d\u56de\u4e9b\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u867d\u7136\u4ece\u6d77\u6cf0\u5230\u9526\u6e56\uff0c\u5e55\u540e\u7684\u90a3\u4e2a\u4eba\u8d77\u5230\u76f8\u5f53\u91cd\u8981\u7684\u4f5c\u7528\uff0c\u4f46\u662f\u8c22\u665a\u6674\u5728\u516c\u53f8\u7ecf\u8425\u4e0a\u7684\u8868\u73b0\u4e5f\u662f\u4f17\u76ee\u6240\u7779\uff0c\u7279\u522b\u8c22\u665a\u6674\u4e0e\u5f90\u5bb6\u7684\u5173\u7cfb\u5e76\u6ca1\u6709\u56e0\u4e3a\u5979\u7684\u4e08\u592b\u5f90\u5fd7\u660e\u53bb\u4e16\u800c\u7a0d\u6de1\uff0c\u4e0d\u7ba1\u4ece\u54ea\u65b9\u9762\u6765\u8bf4\uff0c\u80fd\u5c06\u5979\u91cd\u65b0\u62c9\u56de\u8c22\u5bb6\uff0c\u90a3\u662f\u518d\u597d\u4e0d\u8fc7\u7684\u3002</p><p>\u3000\u3000\u8c22\u6c49\u9756\u4e00\u8fdb\u95e8\u5c31\u8868\u73b0\u51fa\u6e29\u60c5\u8109\u8109\u7684\u4e00\u9762\uff0c\u5f20\u606a\u6487\u5634\u51b7\u7b11\u7740\uff0c\u6b63\u6cf0\u96c6\u56e2\u63d0\u51fa\u73b0\u91d1\u56de\u8d2d\u9526\u6e56\u7684\u80a1\u6743\uff0c\u800c\u975e\u62ff\u6b63\u6cf0\u96c6\u56e2\u7684\u80a1\u4efd\u7b49\u4ef7\u5bf9\u6362\uff0c\u53ef\u89c1\u5728\u6b63\u6cf0\u96c6\u56e2\u7684\u6574\u4e2a\u7ba1\u7406\u5c42\u90fd\u5df2\u7ecf\u5c06\u665a\u6674\u9a71\u9010\u51fa\u8c22\u6c0f\u5bb6\u65cf\uff0c\u8c22\u5251\u5357\u3001\u8c22\u6c49\u660e\u7b49\u4eba\u7684\u51b7\u6de1\uff0c\u66f4\u5c06\u8fd9\u70b9\u8868\u9732\u65e0\u7591\u3002</p><p>\u3000\u3000\u4eba\u751f\u8bfb\u6863\u524d\uff0c\u5f20\u606a\u8bfb\u8fc7\u4e00\u4e9b\u5173\u4e8e\u8c22\u6c49\u9756\u7684\u7c7b\u4f3c\u4f20\u8bb0\u7684\u4e13\u9898\u62a5\u9053\uff0c\u8c22\u6c49\u9756\u4e0d\u6b62\u4e00\u6b21\u7684\u5766\u767d\u4ed6\u7684\u5fc3\u58f0\uff1a\u201c\u96c6\u56e2\u8981\u53d1\u5c55\u3001\u5bb6\u65cf\u8981\u58ee\u5927\uff0c\u5fc5\u987b\u629b\u5f03\u4e2a\u4eba\u7684\u60c5\u611f\uff0c\u800c\u8981\u4ee5\u4e00\u79cd\u575a\u5b9a\u4e0d\u5c48\u7684\u610f\u5fd7\u53bb\u5e2d\u5377\u5929\u4e0b\u95f4\u7684\u8d22\u5bcc\u3002\u201d\u4ed6\u624d\u662f\u8c22\u6c0f\u5bb6\u65cf\u7684\u7075\u9b42\u4eba\u7269\uff0c\u8c22\u5251\u5357\u8fd8\u6ca1\u6709\u5230\u80fd\u63a5\u4ed6\u73ed\u7684\u65f6\u5019\u3002</p><p>\u3000\u3000\u8981\u4e0d\u662f\u5229\u76ca\u7ea0\u845b\uff0c\u5f20\u606a\u5012\u5f88\u60f3\u8ddf\u8fd9\u4f4d\u6c11\u8425\u8d44\u672c\u7684\u5148\u884c\u8005\u8ba8\u8bba\u8ba8\u8bba\u53d1\u5c55\u6c11\u65cf\u8d44\u672c\u529b\u91cf\u7684\u95ee\u9898\uff0c\u4e00\u65e6\u6709\u4e86\u5229\u76ca\u7ea0\u845b\uff0c\u5c31\u8981\u4f60\u7b97\u8ba1\u4f60\u7684\u5229\u76ca\uff0c\u6211\u7b97\u8ba1\u6211\u7684\u5229\u76ca\uff0c\u4f55\u51b5\u8c22\u6c49\u9756\u5728\u5546\u754c\u5e76\u4e0d\u7ed9\u4eba\u5f88\u5bbd\u5bb9\u5927\u5ea6\u7684\u5370\u8c61\uff0c\u6b63\u6cf0\u96c6\u56e2\u8fd9\u79cd\u63a0\u98df\u6027\u7684\u53d1\u5c55\u6218\u7565\uff0c\u4e5f\u66f4\u591a\u7684\u662f\u4ed6\u4e2a\u4eba\u610f\u5fd7\u7684\u4f53\u73b0\u3002</p><p>\u3000\u3000\u5f20\u606a\u8868\u660e\u4ed6\u4e3a\u8d8a\u79c0\u516c\u53f8\u8c08\u5224\u4ee3\u8868\u7684\u8eab\u4efd\uff0c\u8c22\u6c49\u9756\u3001\u8c22\u6c49\u660e\u3001\u8c22\u5251\u5357\u5fc3\u91cc\u90fd\u5f02\u5e38\u7684\u8be7\u5f02\uff0c\u8c22\u6c49\u9756\u3001\u8c22\u6c49\u660e\u8fd9\u662f\u7b2c\u4e00\u6b21\u89c1\u5f20\u606a\uff0c\u4f46\u542c\u5f20\u606a\u81ea\u62a5\u5bb6\u95e8\uff0c\u4fbf\u77e5\u9053\u4ed6\u662f\u8ddf\u5251\u5357\u6709\u7740\u6069\u6028\u7684\u90a3\u4e2a\u4eba\uff0c\u6d77\u5dde\u5e02\u653f\u5e9c\u79d8\u4e66\u957f\u5f20\u77e5\u884c\u7684\u513f\u5b50\uff0c\u542c\u8bf4\u5728\u6d77\u5dde\u4e00\u4e2d\u8bfb\u9ad8\u4e8c\uff0c\u4ed6\u4eec\u4e09\u4e2a\u5fc3\u91cc\u90fd\u95ea\u8fc7\u4e00\u4e2a\u5ff5\u5934\uff1a\u4ed6\u5c31\u662f\u5e55\u540e\u7684\u90a3\u4e2a\u4eba\uff1f\u8c22\u6c49\u660e\u3001\u8c22\u5251\u5357\u662f\u65e9\u6709\u731c\u6d4b\uff0c\u6bd5\u7adf\u5f20\u606a\u5728\u6d77\u6cf0\u7b79\u5efa\u7684\u521d\u671f\u5f88\u6d3b\u8dc3\uff0c\u4f46\u662f\u8fdb\u4e00\u6b65\u63a5\u8fd1\u4e8b\u5b9e\uff0c\u5374\u53c8\u4e0d\u6562\u76f8\u4fe1\u4e86\u3002</p></article>"}}}]);
