(window.webpackJsonp=window.webpackJsonp||[]).push([[363],{385:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u8282 \u5f52\u9014",content:"<article><h2>\u7b2c\u4e8c\u5341\u8282 \u5f52\u9014</h2><p>\u6570\u5343\u540d\u4ec6\u9b54\u4ece\u57ce\u4e2d\u6d8c\u51fa\uff0c\u5728\u8fdc\u79bb\u6218\u573a\u7684\u7a7a\u5730\u4e0a\u96c6\u5408\u3002\u8fd9\u79cd\u9b54\u9b3c\u8eab\u5f3a\u4f53\u58ee\uff0c\u4e2a\u4e2a\u90fd\u662f\u5e9e\u7136\u5927\u7269\uff0c\u5374\u6ca1\u4ec0\u4e48\u6218\u6597\u529b\uff0c\u6240\u4ee5\u5730\u4f4d\u5f88\u4f4e\uff0c\u5728\u5730\u72f1\u91cc\u5145\u5f53\u52b3\u5de5\u548c\u82e6\u529b\u3002\u82e5\u5728\u5e73\u65f6\uff0c\u4e0a\u9635\u4f5c\u6218\u6839\u672c\u8f6e\u4e0d\u5230\u5b83\u4eec\uff0c\u4f46\u5982\u4eca\u8fd9\u4e00\u6b21\u4fbf\u6b63\u7528\u5f97\u7740\u4e86\u3002</p><p>\u201c\u53ea\u8981\u591f\u91cd\uff0c\u591f\u6c89\uff0c\u591f\u5206\u91cf\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u5176\u4ed6\u4e00\u6982\u65e0\u6240\u8c13\u3002\u201d</p><p>\u79c9\u627f\u7740\u8fd9\u79cd\u8981\u6c42\uff0c\u51e0\u5343\u526f\u5168\u8eab\u94e0\u7532\u4ece\u4ed3\u5e93\u91cc\u88ab\u7d27\u6025\u7ffb\u68c0\u51fa\u6765\uff0c\u5168\u90fd\u662f\u7528\u6700\u6c89\u91cd\u7684\u5bd2\u94c1\u953b\u9020\u800c\u6210\u3002\u5bd2\u94c1\u662f\u5730\u72f1\u91cc\u7684\u4e00\u79cd\u7279\u4ea7\u91d1\u5c5e\uff0c\u5728\u7269\u8d28\u754c\u662f\u6ca1\u6709\u7684\uff0c\u8bba\u575a\u56fa\u5b83\u4e0d\u5982\u7cbe\u91d1\uff0c\u8bba\u8f7b\u5de7\u4e0d\u5982\u79d8\u94f6\uff0c\u6240\u4ee5\u4ef7\u503c\u4e0d\u9ad8\u2014\u2014\u4f46\u5b83\u591f\u91cd\u591f\u6c89\uff0c\u8fd9\u5c31\u8db3\u591f\u4e86\u3002</p><p>\u4ec6\u9b54\u4eec\u7a7f\u4e0a\u94e0\u7532\uff0c\u53d8\u6210\u51e0\u5343\u4e2a\u5bc6\u4e0d\u900f\u98ce\u7684\u94c1\u7f50\u5934\uff0c\u5728\u80cc\u540e\u7684\u7fc5\u8180\u548c\u98de\u884c\u836f\u6c34\u7684\u8f85\u52a9\u4e0b\uff0c\u6447\u6447\u6643\u6643\u98de\u4e86\u8d77\u6765\uff0c\u5728\u9ad8\u7a7a\u4e2d\u805a\u96c6\u3002\u5c0f\u9b54\u9b3c\u4eec\u6325\u821e\u7740\u94a2\u53c9\uff0c\u50cf\u7267\u7f8a\u4e00\u822c\u547c\u559d\u9a71\u8d76\u7740\uff0c\u8ba9\u5b83\u4eec\u5c3d\u53ef\u80fd\u5730\u5f7c\u6b64\u9760\u8fd1\uff0c\u4e0d\u7559\u7a7a\u9699\u3002\u51e0\u767e\u53ea\u9ed1\u8272\u7684\u68a6\u9b47\u56db\u8e44\u751f\u706b\uff0c\u5fa1\u98ce\u817e\u4e91\uff0c\u81ea\u7a7a\u6c14\u4e2d\u5954\u51fa\uff0c\u5b83\u4eec\u540c\u6837\u4e5f\u90fd\u62ab\u6302\u7740\u91cd\u7532\uff0c\u8eab\u540e\u65e0\u4e00\u4f8b\u5916\u90fd\u62c9\u7740\u5927\u8f66\uff0c\u4e0a\u9762\u662f\u5806\u79ef\u5982\u5c71\u7684\u94e0\u7532\u3001\u6b66\u5668\u3001\u9b54\u5076\u3001\u96d5\u5851\u7b49\u7b49\u4e00\u5207\u6c89\u91cd\u7684\u91d1\u5c5e\u7269\u4f53\uff0c\u751a\u81f3\u5305\u62ec\u524d\u51e0\u65e5\u5728\u9ed1\u77f3\u57ce\u51fb\u6740\u7684\u90a3\u51e0\u5343\u540d\u6b7b\u4ea1\u9a91\u58eb\u7684\u5c38\u4f53\uff0c\u56e0\u4e3a\u5168\u8eab\u91cd\u94e0\uff0c\u4e5f\u88ab\u76f4\u63a5\u88c5\u4e0a\u62c9\u4e86\u8fc7\u6765\u3002</p><p>\u201c\u4f60\u786e\u5b9a\u8fd9\u771f\u6709\u7528\uff1f\u201d\u62dc\u5c14\u95ee\u3002</p><p>\u201c\u786e\u5b9a\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u63e1\u7d27\u4e86\u624b\u4e2d\u7684\u7d2b\u6c34\u6676\u6cd5\u6756\uff0c\u201c\u6211\u505a\u8fc7\u8bd5\u9a8c\u3002\u201d</p><p>\u62dc\u5c14\u4e0d\u518d\u591a\u8bf4\uff0c\u62ac\u624b\u53d1\u51fa\u4fe1\u53f7\uff0c\u4e00\u9053\u9ed1\u8272\u7130\u706b\u7830\u7136\u5f39\u8d77\uff0c\u5728\u534a\u7a7a\u4e2d\u70b8\u5f00\uff0c\u5316\u4f5c\u5229\u9f7f\u54ac\u5408\u94a2\u5200\u7684\u5fbd\u8bb0\u3002\u6b63\u5728\u56f4\u653b\u9b54\u50cf\u7684\u5815\u5929\u4f7f\u548c\u70bc\u9b54\u7acb\u523b\u53d1\u52a8\u7cbe\u786e\u4f20\u9001\uff0c\u8fdc\u8fdc\u9003\u5f00\uff0c\u9ad8\u7a7a\u4e2d\u7684\u4ec6\u9b54\u4eec\u539f\u672c\u5df2\u7ecf\u98de\u5f97\u8270\u96be\uff0c\u773c\u770b\u652f\u6301\u4e0d\u4f4f\uff0c\u89c1\u72b6\u5982\u91ca\u91cd\u8d1f\uff0c\u4f4e\u5934\u4fbf\u671d\u795e\u8eaf\u9b54\u50cf\u4fef\u51b2\u800c\u6765\u3002</p><p>\u6570\u5343\u53ea\u5168\u526f\u6b66\u88c5\u7684\u9b54\u9b3c\u540c\u65f6\u4fef\u51b2\uff0c\u8fdc\u770b\u5c31\u50cf\u4e00\u56e2\u5de8\u5927\u7684\u8702\u7fa4\u7838\u8fc7\u6765\u4e00\u822c\uff0c\u6c14\u52bf\u7740\u5b9e\u60ca\u4eba\u3002\u6362\u4e86\u5176\u4ed6\u5bf9\u624b\uff0c\u53ea\u6015\u65e9\u5c31\u88ab\u7838\u6241\u4e86\uff0c\u7136\u800c\u8981\u60f3\u5bf9\u4ed8\u795e\u8eaf\u9b54\u50cf\uff0c\u8fd9\u79cd\u7a0b\u5ea6\u8fd8\u8fdc\u8fdc\u4e0d\u591f\u3002</p><p>\u201c\u78c1\u5316\u672f\uff01\u201d</p><p>\u4e00\u9053\u8000\u773c\u7684\u84dd\u8272\u5149\u7ebf\u4ece\u743c\u6069\u7684\u6cd5\u6756\u9876\u7aef\u53d1\u51fa\uff0c\u9065\u9065\u6b63\u4e2d\u51b2\u5728\u6700\u524d\u9762\u7684\u4e00\u53ea\u4ec6\u9b54\uff0c\u5b83\u7684\u8eab\u4f53\u4e0a\u9661\u7136\u6d6e\u73b0\u4e00\u5c42\u660e\u6697\u4e0d\u5b9a\u7684\u5e7d\u5149\u3002\u78c1\u5316\u672f\u77ac\u95f4\u53d1\u6325\u4f5c\u7528\uff0c\u5c06\u9b54\u9b3c\u53d8\u6210\u4e86\u4e00\u5757\u78c1\u77f3\uff0c\u5e9e\u5927\u7684\u4f53\u79ef\u3001\u6c89\u91cd\u7684\u5206\u91cf\uff0c\u52a0\u4e0a\u8eab\u4e0a\u94c1\u7f50\u5934\u822c\u7684\u5bd2\u94c1\u94e0\u7532\uff0c\u78c1\u573a\u77ac\u95f4\u5f3a\u5316\u5230\u60ca\u4eba\u7684\u7a0b\u5ea6\uff0c\u5468\u56f4\u7684\u9b54\u9b3c\u4eec\u8eab\u4e0d\u7531\u5df1\uff0c\u53ea\u542c\u5f97\u201c\u7830\u7830\u7830\u7830\u201d\u8fde\u54cd\uff0c\u5168\u90fd\u88ab\u5438\u4e86\u8fc7\u6765\u3002</p><p>\u201c\u53d8\u5de8\uff01\u201d</p><p>\u201c\u53d8\u5de8\uff01\u201d</p><p>\u201c\u53d8\u5de8\uff01\u201d</p><p>\u201c\u53d8\u5de8\uff01\u201d</p><p>\u2026\u2026</p><p>\u90aa\u9b54\u5927\u591a\u4e0d\u64c5\u6cd5\u672f\uff0c\u62dc\u5c14\u624b\u4e0b\u7684\u5deb\u5e08\u4e5f\u4e0d\u591a\uff0c\u4f46\u53d8\u5de8\u672f\u8fd9\u79cd\u6700\u521d\u7ea7\u7684\u5165\u95e8\u9b54\u6cd5\uff0c\u8fd8\u662f\u6709\u4e0d\u5c11\u4eba\u4f1a\u7684\uff0c\u81f3\u5c11\u80fd\u591f\u7528\u5377\u8f74\u65bd\u5c55\u3002\u4e0a\u767e\u540d\u9b54\u9b3c\u796d\u53f8\u548c\u5b66\u5f92\u7784\u51c6\u9ad8\u7a7a\u4e2d\u7684\u76ee\u6807\u8fde\u8fde\u65bd\u6cd5\uff0c\u4ec6\u9b54\u4eec\u63a5\u4e8c\u8fde\u4e09\u5730\u5439\u6c14\u7403\u822c\u9f13\u80c0\u8d77\u6765\uff0c\u78c1\u573a\u7684\u5a01\u529b\u8fdb\u4e00\u6b65\u6269\u5927\uff0c\u540e\u9762\u7684\u9b54\u9b3c\u6b63\u5728\u4fef\u51b2\uff0c\u751a\u81f3\u6839\u672c\u4e0d\u9700\u8981\u5438\u529b\u5c31\u81ea\u5df1\u649e\u4e0a\u3002\u4eff\u4f5b\u6eda\u96ea\u7403\u4e00\u822c\uff0c\u4e0d\u8fc7\u4e00\u4e24\u79d2\u949f\uff0c\u51e0\u5343\u540d\u4ec6\u9b54\u5c31\u88ab\u7262\u7262\u5438\u5728\u4e00\u8d77\uff0c\u51dd\u805a\u6210\u5de8\u5927\u7684\u7403\u4f53\uff0c\u8f70\u7136\u671d\u7740\u9b54\u50cf\u7838\u8fc7\u6765\u3002</p><p>\u82e5\u5728\u5e73\u65f6\uff0c\u51e0\u5343\u4e2a\u4ec6\u9b54\u4fef\u51b2\u800c\u4e0b\uff0c\u5bf9\u4e8e\u795e\u8eaf\u9b54\u50cf\u800c\u8a00\u4e5f\u4e0d\u8fc7\u5c31\u76f8\u5f53\u4e8e\u4e00\u9635\u731b\u70c8\u788e\u7802\uff0c\u5e76\u65e0\u5927\u788d\u3002\u4f46\u5982\u4eca\u8fd9\u51e0\u5343\u5757\u201c\u788e\u7802\u201d\u88ab\u78c1\u5316\u672f\u51dd\u805a\u6210\u4e86\u4e00\u5757\u201c\u5de8\u77f3\u201d\uff0c\u5a01\u529b\u5c31\u76f8\u5f53\u53ef\u89c2\u4e86\u3002\u7830\u5730\u4e00\u58f0\uff0c\u9b54\u50cf\u88ab\u8fd9\u98de\u6765\u5de8\u77f3\u8fce\u9762\u7838\u4e2d\uff0c\u7acb\u8db3\u4e0d\u7a33\u7ffb\u8eab\u6454\u5012\u3002\u5b83\u529b\u6c14\u867d\u5927\uff0c\u4f46\u5230\u5e95\u662f\u51e0\u5343\u4e2a\u5168\u526f\u6b66\u88c5\u7684\u4ec6\u9b54\uff0c\u5206\u91cf\u53e0\u52a0\u8d77\u6765\u53c8\u5c82\u540c\u5bfb\u5e38\uff0c\u4e00\u65f6\u95f4\u6323\u624e\u4e0d\u8d77\u3002</p><p>\u4e0e\u6b64\u540c\u65f6\uff0c\u743c\u6069\u770b\u51c6\u9b54\u50cf\u5012\u4e0b\u7684\u4f4d\u7f6e\uff0c\u5feb\u901f\u8bfb\u5b8c\u6b23\u5e03\u7559\u7ed9\u4ed6\u7684\u5377\u8f74\u3002\u4e00\u9053\u9ed1\u8272\u6b21\u5143\u88c2\u9699\u5728\u5730\u9762\u4e0a\u5ef6\u4f38\u5f00\u6765\uff0c\u9b54\u50cf\u672c\u80fd\u5730\u610f\u8bc6\u5230\u4e86\u5371\u9669\u7684\u903c\u8fd1\uff0c\u5b83\u8fde\u8fde\u6012\u543c\u7740\uff0c\u60f3\u8981\u5c06\u8eab\u4e0a\u7684\u91cd\u538b\u63a8\u5f00\uff0c\u9003\u79bb\u6b64\u5904\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u53ea\u542c\u5f97\u4e00\u58f0\u547c\u5578\uff0c\u6210\u7fa4\u7ed3\u961f\u7684\u68a6\u9b47\u8e0f\u4e91\u4e58\u98ce\uff0c\u62c9\u7740\u5927\u8f66\u4ece\u56db\u9762\u516b\u65b9\u9ad8\u901f\u51b2\u6765\u3002</p><p>\u68a6\u9b47\u8eab\u4e0a\u62ab\u7740\u91cd\u7532\uff0c\u5b83\u901f\u5ea6\u539f\u672c\u5c31\u5feb\uff0c\u6240\u62c9\u7684\u8f66\u4e0a\u649e\u5728\u7684\u53c8\u5168\u662f\u91d1\u5c5e\u91cd\u7269\uff0c\u62dc\u5c14\u4e3a\u6c42\u4e00\u51fb\u5f97\u624b\uff0c\u53ef\u4ee5\u8bf4\u662f\u503e\u5bb6\u8361\u4ea7\uff0c\u8fde\u4ed6\u81ea\u5df1\u5b98\u90b8\u91cc\u7684\u5341\u51e0\u4e2a\u5b88\u536b\u94c1\u9b54\u50cf\u90fd\u8d21\u732e\u51fa\u6765\u3002\u4e0d\u8fc7\u77ac\u606f\u4e4b\u95f4\uff0c\u51e0\u767e\u8f86\u5927\u8f66\u5c31\u5c3d\u6570\u649e\u4e0a\uff0c\u5806\u79ef\u6210\u5c71\uff0c\u5c06\u795e\u8eaf\u9b54\u50cf\u6b7b\u6b7b\u9547\u538b\u5728\u4e0b\u9762\u3002</p><p>\u9b54\u50cf\u8fde\u8fde\u6323\u624e\uff0c\u53ea\u662f\u9003\u8131\u4e0d\u51fa\u3002\u62dc\u5c14\u624b\u4e0b\u7684\u4e24\u4e2a\u5deb\u5996\u7387\u9886\u5b66\u5f92\u5c55\u5f00\u4e00\u5f20\u5f20\u5377\u8f74\uff0c\u4e0d\u505c\u65bd\u5c55\u53d8\u5de8\u672f\uff0c\u5c06\u8fd9\u4e00\u5ea7\u201c\u9b54\u9b3c\u5c71\u201d\u53d8\u5f97\u8d8a\u53d1\u5e9e\u5927\u6c89\u91cd\u3002\u743c\u6069\u542f\u52a8\u5965\u672f\u89c6\u89c9\uff0c\u900f\u8fc7\u5806\u79ef\u5982\u5c71\u7684\u9b54\u9b3c\u4eec\u770b\u89c1\u5730\u9762\u4e0a\u7684\u6b21\u5143\u88c2\u9699\u5df2\u7ecf\u5f62\u6210\u9ed1\u8272\u7684\u6f29\u6da1\uff0c\u6b63\u5728\u5168\u529b\u65cb\u8f6c\uff0c\u6025\u901f\u6269\u5927\uff0c\u518d\u8fc7\u56db\u79d2\u949f\u5c31\u80fd\u5b8c\u5168\u7a33\u5b9a\u6210\u578b\uff0c\u5c06\u9b54\u50cf\u9001\u5f80\u9884\u5148\u8bbe\u5b9a\u597d\u7684\u76ee\u7684\u5730\u3002</p><p>\u4e09\u79d2\u949f\uff01\u4e24\u79d2\u949f\uff01\u4e00\u79d2\u949f\uff01</p><p>\u773c\u770b\u5c31\u8981\u5927\u529f\u544a\u6210\uff0c\u8bf8\u4eba\u4e0d\u7531\u5f97\u90fd\u9762\u9732\u559c\u8272\uff0c\u7136\u800c\u90a3\u795e\u8eaf\u9b54\u50cf\u4e5f\u77e5\u9053\u60c5\u52bf\u5371\u6025\uff0c\u5df2\u7ecf\u5230\u4e86\u6700\u540e\u751f\u6b7b\u5b58\u4ea1\u7684\u5173\u5934\uff0c\u5f53\u4e0b\u594b\u8d77\u4f59\u529b\uff0c\u6012\u543c\u4e00\u58f0\uff0c\u53cc\u81c2\u731b\u7136\u6323\u5f00\uff0c\u5c45\u7136\u786c\u751f\u751f\u5c06\u8fd9\u5ea7\u78c1\u529b\u8054\u63a5\u4e34\u65f6\u5851\u6210\u7684\u201c\u9b54\u9b3c\u5c71\u201d\u9707\u6210\u5341\u4f59\u5757\u5d29\u6563\u5f00\u6765\uff0c\u56db\u53ea\u767d\u68ee\u68ee\u7684\u9aa8\u7ffc\u81ea\u9b54\u50cf\u80cc\u4e0a\u8212\u5c55\uff0c\u5b83\u4e00\u4e2a\u7ffb\u8eab\u8dc3\u8d77\uff0c\u5f80\u4e0a\u4fbf\u98de\u3002</p><p>\u7a7a\u6c14\u8f7b\u5fae\u6ce2\u6298\uff0c\u62dc\u5c14\u5df2\u7ecf\u51fa\u73b0\u5728\u9b54\u50cf\u4e0a\u7a7a\uff0c\u4ed6\u73b0\u51fa\u4e86\u5e9e\u5927\u7684\u6df1\u72f1\u70bc\u9b54\u539f\u5f62\uff0c\u70c8\u7130\u8426\u7ed5\u9ed1\u4e91\u7f20\u8eab\uff0c\u539f\u672c\u4fee\u957f\u7ea4\u7ec6\u7684\u957f\u5200\u53d8\u6210\u95e8\u677f\u822c\u539a\u91cd\uff0c\u5927\u559d\u4e00\u58f0\u7167\u7740\u9b54\u50cf\u5f53\u5934\u5288\u4e0b\u3002</p><p>\u9b54\u50cf\u4e00\u62f3\u7838\u51fa\uff0c\u6b63\u4e2d\u5200\u5203\uff0c\u6beb\u65e0\u60ac\u5ff5\u5c06\u62dc\u5c14\u8fde\u4eba\u5e26\u5200\u90fd\u8fdc\u8fdc\u9707\u98de\u51fa\u53bb\u3002\u867d\u7136\u5982\u6b64\uff0c\u5b83\u7684\u4e0a\u98de\u4e4b\u52bf\u4e5f\u4e0d\u7531\u5f97\u6ede\u4e86\u4e00\u6ede\u3002\u521a\u521a\u52c9\u5f3a\u6b62\u4f4f\u4e0b\u6c89\u4e4b\u52bf\uff0c\u4e00\u9053\u95ea\u7535\u81ea\u5934\u9876\u5288\u4e0b\uff0c\u51dd\u805a\u6210\u66e6\u5929\u4f7f\u7684\u707f\u70c2\u8eab\u5f71\uff0c\u91d1\u8272\u53cc\u7738\u4e2d\u70c8\u7130\u71c3\u70e7\uff0c\u516d\u5bf9\u5de8\u5927\u7684\u900f\u660e\u5149\u7ffc\u5728\u80cc\u540e\u7ffc\u91cd\u91cd\u8212\u5c55\uff0c\u5bd2\u51b0\u5251\u4e0a\u5723\u5149\u8f70\u7136\u7efd\u653e\uff0c\u8fd1\u4e4e\u65e0\u8fb9\u65e0\u9645\u5730\u8513\u5ef6\u5f00\u6765\uff0c\u77ac\u95f4\u7167\u8000\u534a\u4e2a\u5929\u7a7a\u3002</p><p>\u6d69\u6d69\u8361\u8361\u7684\u91d1\u5149\u6d77\u6d0b\u4e2d\uff0c\u9661\u7136\u53c8\u817e\u8d77\u4e00\u4e2a\u94f6\u8272\u8eab\u5f71\uff0c\u6885\u83f2\u65af\u4e0d\u77e5\u4f55\u65f6\u4e5f\u5df2\u7ecf\u51b2\u5230\u8fd1\u524d\uff0c\u201c\u7737\u604b\u201d\u94f6\u5251\u4e0a\u5149\u534e\u56db\u5c04\uff0c\u8000\u5982\u70c8\u65e5\uff0c\u5c06\u5979\u6574\u4e2a\u4eba\u90fd\u5305\u88f9\u5176\u4e2d\u3002</p><p>\u7834\u90aa\u65a9\uff01</p><p>\u7834\u4e71\u65a9\uff01</p><p>\u4e24\u9053\u795e\u5723\u65a9\u51fb\u6c47\u805a\u4e00\u5904\uff0c\u91d1\u767d\u4e24\u9053\u5149\u8f89\u5f7c\u6b64\u7ede\u7f20\uff0c\u4eff\u4f5b\u9f99\u86c7\u7ffb\u817e\uff0c\u5316\u4f5c\u4e00\u67c4\u5e9e\u5927\u65e0\u6bd4\u7684\u6218\u9524\u5f53\u5934\u7838\u4e0b\u3002\u9b54\u50cf\u53cc\u81c2\u4ea4\u53c9\uff0c\u5168\u529b\u4e0a\u683c\uff0c\u53ea\u662f\u5b83\u539f\u672c\u5c31\u52b2\u52bf\u5df2\u8870\uff0c\u867d\u7136\u5c06\u5de8\u9524\u9707\u6e83\uff0c\u81ea\u5df1\u518d\u4e5f\u7ef4\u6301\u4e0d\u4f4f\u6d6e\u7a7a\u72b6\u6001\uff0c\u7b14\u76f4\u5f80\u4e0b\u5760\u843d\u3002</p><p>\u4f4d\u9762\u901a\u9053\u6b64\u65f6\u6b63\u5df2\u7ecf\u65cb\u8f6c\u6269\u5f20\u5230\u6781\u9650\uff0c\u5730\u9762\u5316\u6210\u76f4\u5f84\u8d85\u8fc7\u516d\u5341\u82f1\u5c3a\u7684\u5de8\u5927\u9ed1\u6d1e\uff0c\u6df1\u4e0d\u89c1\u5e95\uff0c\u5468\u56f4\u7684\u4e00\u5207\u5149\u7ebf\u5c3d\u6570\u88ab\u5438\u7eb3\u5176\u4e2d\u3002\u9b54\u50cf\u81ea\u7a7a\u4e2d\u5760\u843d\uff0c\u53cc\u817f\u521a\u521a\u6ca1\u5165\u9ed1\u6d1e\uff0c\u7d27\u63a5\u7740\u6574\u4e2a\u4eba\u4fbf\u75be\u901f\u6c89\u6ca1\u4e0b\u53bb\uff0c\u7728\u773c\u4e4b\u95f4\uff0c\u90a3\u9876\u5929\u7acb\u5730\u7684\u5de8\u5927\u8eab\u5f71\u4fbf\u4ece\u963f\u5f17\u7eb3\u65af\u7684\u571f\u5730\u4e0a\u6d88\u5931\u4e0d\u89c1\u3002</p><p>\u9b54\u50cf\u867d\u7136\u88ab\u4f20\u9001\u8d70\uff0c\u4f4d\u9762\u901a\u9053\u4e00\u65f6\u5e76\u672a\u6d88\u6563\uff0c\u66e6\u5929\u4f7f\u53cc\u7ffc\u5f20\u5f00\uff0c\u9ad8\u9ad8\u98de\u8d77\uff0c\u6885\u83f2\u65af\u5374\u6b63\u597d\u5760\u843d\u4e0b\u53bb\u3002\u5979\u6b63\u8981\u542f\u52a8\u6d6e\u7a7a\u9774\uff0c\u66e6\u5929\u4f7f\u7684\u624b\u4e2d\u7a81\u7136\u98de\u51fa\u4e00\u70b9\u94f6\u5149\uff0c\u75be\u901f\u6da8\u5927\uff0c\u5316\u4f5c\u4e00\u53ea\u94f6\u5149\u95ea\u95ea\u7684\u72ec\u89d2\u517d\uff0c\u80cc\u751f\u53cc\u7ffc\uff0c\u72b6\u82e5\u5929\u9a6c\u8e0f\u7a7a\u800c\u6765\uff0c\u77ac\u95f4\u98de\u5230\u6885\u83f2\u65af\u8eab\u4e0b\u3002\u5723\u6b66\u58eb\u4f38\u624b\u5728\u72ec\u89d2\u517d\u80cc\u4e0a\u4e00\u6309\uff0c\u501f\u52bf\u817e\u8d77\uff0c\u4eba\u5df2\u7ecf\u5750\u4e86\u4e0a\u53bb\u3002\u72ec\u89d2\u517d\u5f20\u5f00\u53cc\u7ffc\u7fe9\u7136\u6a2a\u63a0\uff0c\u98de\u56de\u7ad9\u5728\u57ce\u5934\u4e0a\u7684\u743c\u6069\u8eab\u8fb9\u3002</p><p>\u6885\u83f2\u65af\u8f7b\u76c8\u5730\u8dc3\u4e0b\u72ec\u89d2\u517d\uff0c\u811a\u4e0b\u7a81\u7136\u4e00\u4e2a\u8e09\u8dc4\uff0c\u7ad9\u7acb\u4e0d\u7a33\uff0c\u743c\u6069\u8fde\u5fd9\u5c06\u5979\u6276\u4f4f\uff0c\u53d1\u89c9\u8138\u8272\u7070\u6697\uff0c\u91d1\u8272\u53d1\u4e1d\u95f4\u6c57\u73e0\u70b9\u70b9\u6ef4\u843d\uff0c\u663e\u7136\u662f\u521a\u624d\u90a3\u4e00\u51fb\u7834\u4e71\u65a9\u8017\u529b\u751a\u5de8\uff0c\u4e00\u65f6\u95f4\u652f\u6301\u4e0d\u4f4f\u3002\u6b63\u8981\u62b1\u5979\u56de\u53bb\u4f11\u606f\uff0c\u90a3\u53ea\u94f6\u5149\u95ea\u95ea\u7684\u72ec\u89d2\u517d\u8d70\u8fc7\u6765\uff0c\u4f38\u51fa\u989d\u5934\u4e0a\u6676\u83b9\u900f\u660e\u7684\u5c16\u89d2\uff0c\u8f7b\u8f7b\u78b0\u89e6\u6885\u83f2\u65af\u7684\u8eab\u4f53\uff0c\u4e00\u5c42\u5c42\u4e73\u767d\u8272\u7684\u5149\u6655\u81ea\u517d\u89d2\u5c16\u7aef\u8513\u5ef6\u5f00\u6765\uff0c\u4eff\u4f5b\u6c34\u6ce2\u8361\u6f3e\uff0c\u4ee4\u4eba\u5fc3\u795e\u5b89\u5b81\uff0c\u4eff\u4f5b\u6e05\u98ce\u62c2\u9762\uff0c\u53c8\u50cf\u548c\u7166\u9633\u5149\u7167\u5728\u8eab\u4e0a\uff0c\u6240\u6709\u7684\u75b2\u52b3\u5026\u7d2f\u987f\u65f6\u4e00\u626b\u800c\u7a7a\u3002\u4e0d\u8fc7\u7247\u523b\uff0c\u6885\u83f2\u65af\u7684\u8138\u8272\u4fbf\u6e10\u6e10\u6062\u590d\u8fc7\u6765\u3002</p><p>\u66e6\u5929\u4f7f\u98de\u56de\u57ce\u5934\uff0c\u6062\u590d\u5e73\u65f6\u7684\u4eba\u7c7b\u5c11\u5973\u5f62\u6001\uff0c\u4f9d\u65e7\u88f9\u5728\u7070\u8272\u6597\u7bf7\u91cc\uff0c\u6162\u6162\u8d70\u8fc7\u6765\u3002\u5979\u7684\u53f3\u624b\u6307\u7f1d\u95f4\u6cc4\u9732\u51fa\u70b9\u70b9\u94f6\u5149\uff0c\u72ec\u89d2\u517d\u5fae\u5fae\u56de\u5934\uff0c\u8eab\u4f53\u9010\u6e10\u53d8\u5f97\u6de1\u5316\u900f\u660e\uff0c\u6700\u7ec8\u6d88\u5931\u5728\u7a7a\u6c14\u4e2d\u3002\u6885\u83f2\u65af\u6b63\u8981\u9053\u8c22\uff0c\u66e6\u5929\u4f7f\u4f38\u51fa\u624b\uff0c\u638c\u5fc3\u6258\u7740\u4e00\u53ea\u7cbe\u81f4\u5c0f\u5de7\u7684\u94f6\u8272\u96d5\u50cf\uff0c\u6b63\u662f\u521a\u624d\u90a3\u53ea\u72ec\u89d2\u517d\u7684\u6a21\u6837\u3002\u201c\u9001\u7ed9\u4f60\u4e86\uff0c\u201d\u5979\u6de1\u6de1\u8bf4\uff0c\u201c\u53cd\u6b63\u6211\u4e5f\u4e0d\u9700\u8981\u3002\u201d</p><p>\u6885\u83f2\u65af\u6ca1\u6709\u63a8\u8f9e\uff0c\u63a5\u8fc7\u96d5\u50cf\u3002</p><p>\u9707\u8033\u6b32\u804b\u7684\u6b22\u547c\u58f0\u54cd\u8d77\uff0c\u9752\u94dc\u57ce\u524d\u987f\u65f6\u6cb8\u817e\u8d77\u6765\uff0c\u6240\u6709\u5e78\u5b58\u7684\u9b54\u9b3c\u4eec\u72c2\u547c\u4e71\u558a\uff0c\u5e86\u795d\u7740\u8fd9\u5f97\u6765\u4e0d\u6613\u7684\u80dc\u5229\u3002\u5728\u4e00\u7247\u55a7\u95f9\u58f0\u4e2d\uff0c\u743c\u6069\u8138\u4e0a\u9732\u51fa\u5fae\u5fae\u82e6\u7b11\u3002</p><p>\u201c\u4e0b\u5c42\u754c\u7684\u5de8\u5934\u4eec\uff0c\u7b97\u662f\u5f97\u7f6a\u4e2a\u904d\uff0c\u4ece\u6b64\u4ee5\u540e\uff0c\u8fd9\u91cc\u662f\u4e0d\u7528\u6765\u4e86\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u501f\u52a9\u6b23\u5e03\u7ed9\u4ed6\u7528\u6765\u9003\u547d\u7684\u5916\u57df\u4f20\u9001\u5377\u8f74\uff0c\u743c\u6069\u76f4\u63a5\u628a\u9b54\u50cf\u9001\u5230\u4e86\u65ad\u57df\u9547\u3002\u90a3\u91cc\u6709\u683c\u62c9\u5179\u7279\u4eb2\u81ea\u5750\u9547\uff0c\u76f8\u4fe1\u4ee5\u4ed6\u7684\u672c\u4e8b\uff0c\u4e0d\u53ef\u80fd\u771f\u5bf9\u4ed8\u4e0d\u4e86\u8fd9\u602a\u7269\uff0c\u4f46\u80af\u5b9a\u4e5f\u4f1a\u635f\u5931\u60e8\u91cd\u3002\u8fd9\u6837\u4e00\u6765\uff0c\u7b97\u662f\u540c\u65f6\u5f97\u7f6a\u4e86\u5965\u5580\u65af\u548c\u683c\u62c9\u5179\u7279\u3002\u800c\u743c\u6069\u501f\u6b64\u673a\u4f1a\u8981\u631f\u62dc\u5c14\u653e\u5f03\u5f25\u8d5b\u4e9a\uff0c\u5c31\u7b97\u6709\u5951\u7ea6\u4fdd\u969c\uff0c\u6682\u65f6\u65e0\u4e8b\uff0c\u5176\u5b9e\u4e5f\u662f\u91cd\u91cd\u635f\u5bb3\u4e86\u4ed6\u7684\u5229\u76ca\u3002\u4e00\u5929\u4e4b\u5185\uff0c\u540c\u65f6\u5f97\u7f6a\u6df1\u6e0a\u4e09\u5de8\u5934\u4e2d\u7684\u4e24\u4f4d\uff0c\u52a0\u4e0a\u5730\u72f1\u7b2c\u4e00\u5c42\u5927\u516c\u7235\uff0c\u4ece\u67d0\u79cd\u610f\u4e49\u4e0a\u8bf4\uff0c\u8fd9\u6210\u7ee9\u7740\u5b9e\u8f89\u714c\u5f97\u5f88\uff0c\u5b8c\u5168\u53ef\u4ee5\u62ff\u53bb\u70ab\u8000\u3002</p><p>\u7136\u800c\u5bf9\u4e8e\u5f53\u4e8b\u4eba\u6765\u8bf4\uff0c\u4ed6\u53ea\u60f3\u8d76\u7d27\u9003\u79bb\u73b0\u573a\uff0c\u8fd4\u56de\u7269\u8d28\u754c\u3002</p><p>\u5bf9\u4e8e\u743c\u6069\u8d81\u706b\u6253\u52ab\u7684\u884c\u4e3a\uff0c\u62dc\u5c14\u4e1d\u6beb\u6ca1\u6709\u8868\u73b0\u51fa\u534a\u70b9\u607c\u6012\uff0c\u76f8\u53cd\u4f3c\u4e4e\u8fd8\u51e0\u5206\u94a6\u4f69\u3002\u4ed6\u9080\u8bf7\u743c\u6069\u7559\u4e0b\u6765\u76f8\u52a9\uff0c\u8bb8\u8bfa\u4e86\u8bf8\u591a\u4e30\u539a\u7684\u6761\u4ef6\uff0c\u4f46\u88ab\u5deb\u5e08\u5a49\u8a00\u62d2\u7edd\u4e86\u3002</p><p>\u201c\u4e00\u70b9\u5c0f\u806a\u660e\u800c\u5df2\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4e0d\u503c\u5f97\u60a8\u8fd9\u6837\u770b\u91cd\u3002\u201d</p><p>\u71c3\u70e7\u7684\u9aa8\u9abc\u8054\u7ed3\u52fe\u5d4c\uff0c\u5851\u6210\u5de8\u5927\u7684\u4e34\u65f6\u4f20\u9001\u95e8\uff0c\u901a\u5f80\u7269\u8d28\u754c\u7684\u6668\u70bc\u57ce\u3002\u743c\u6069\u548c\u540c\u4f34\u4eec\u8d70\u5230\u4f20\u9001\u95e8\u524d\uff0c\u548c\u524d\u6765\u9001\u884c\u7684\u62dc\u5c14\u544a\u522b\u3002</p><p>\u201c\u4e00\u8def\u8d70\u597d\uff0c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u201d\u62dc\u5c14\u4f9d\u65e7\u7b11\u5bb9\u6ee1\u9762\uff0c\u201c\u611f\u8c22\u4f60\u4e00\u76f4\u4ee5\u6765\u7684\u5e2e\u52a9\u3002\u6211\u76f8\u4fe1\uff0c\u6211\u4eec\u8fd8\u4f1a\u518d\u6b21\u9047\u89c1\u7684\u3002\u201d</p><p>\u743c\u6069\u5fae\u5fae\u8eac\u8eab\uff0c\u4f46\u5e76\u6ca1\u6709\u8bf4\u8bdd\u3002</p><p>\u62dc\u5c14\u4e5f\u4e0d\u591a\u8bf4\uff0c\u6325\u4e86\u6325\u624b\u8f6c\u8eab\u79bb\u53bb\uff0c\u743c\u6069\u770b\u7740\u4ed6\u7684\u8eab\u5f71\u5728\u9053\u8def\u5c3d\u5934\u6d88\u5931\uff0c\u56de\u8fc7\u8eab\u6765\uff0c\u201c\u8d70\u5427\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u4eec\u4e5f\u8be5\u56de\u53bb\u4e86\u3002\u201d</p><p>\u201c\u8fd8\u6709\u4ef6\u4e8b\u60c5\u6ca1\u505a\u5b8c\u3002\u201d\u6885\u83f2\u65af\u9759\u9759\u63d0\u9192\u3002</p><p>\u201c\u5514\uff0c\u5dee\u70b9\u5fd8\u4e86\u3002\u201d</p><p>\u743c\u6069\u968f\u624b\u5728\u7a7a\u6c14\u4e2d\u62bd\u51fa\u4e00\u5f20\u95ea\u70c1\u7740\u4e4c\u9ed1\u5149\u8292\u7684\u7f8a\u76ae\u7eb8\u6765\uff0c\u6b63\u662f\u62dc\u5c14\u4ee5\u524d\u548c\u5f25\u8d5b\u4e9a\u7b7e\u8ba2\u7684\u5951\u7ea6\uff0c\u5982\u4eca\u5df2\u7ecf\u9644\u4e0a\u65b0\u7684\u6761\u6b3e\uff0c\u5c06\u62dc\u5c14\u7684\u6240\u6709\u6743\u5229\u8ba9\u6e21\u8fc7\u6765\u3002\u4ed6\u77a5\u4e86\u773c\u5185\u5bb9\uff0c\u5c06\u5b83\u9012\u7ed9\u7ad9\u5728\u4e00\u65c1\u7684\u66e6\u5929\u4f7f\u3002</p><p>\u201c\u4f5c\u5e9f\u5427\u3002\u201d\u5deb\u5e08\u8bf4\u3002</p><p>\u66e6\u5929\u4f7f\u770b\u4e86\u743c\u6069\u4e00\u773c\uff0c\u201c\u4f5c\u5e9f\u3002\u201d\u5979\u8f7b\u58f0\u91cd\u590d\u3002</p><p>\u7f8a\u76ae\u7eb8\u65e0\u58f0\u65e0\u606f\u5730\u71c3\u70e7\u8d77\u6765\uff0c\u5316\u4f5c\u4e00\u56e2\u5e7d\u706b\uff0c\u6e10\u6e10\u7184\u706d\uff0c\u534a\u70b9\u7070\u70ec\u90fd\u4e0d\u5269\u4e0b\uff0c\u4eff\u4f5b\u4ece\u6765\u4e0d\u66fe\u5b58\u5728\u4e8e\u8fd9\u4e2a\u4e16\u754c\u4e0a\u4f3c\u7684\u3002\u66e6\u5929\u4f7f\u6014\u6014\u7ad9\u7acb\u7247\u523b\uff0c\u8138\u4e0a\u795e\u60c5\u53d8\u5e7b\u4e0d\u5b9a\uff0c\u6700\u7ec8\u6062\u590d\u5e73\u7d20\u7684\u6f20\u7136\u3002</p><p>\u201c\u591a\u8c22\u4e86\u3002\u201d\u5979\u8bf4\u3002</p><p>\u900f\u660e\u7684\u98ce\u5728\u811a\u4e0b\u5f62\u6210\u6f29\u6da1\uff0c\u5c06\u5979\u6258\u8d77\uff0c\u5189\u5189\u4e0a\u5347\u3002\u5934\u9876\u9ad8\u7a7a\u4e2d\u9707\u96f7\u9690\u9690\u8f70\u9e23\uff0c\u707f\u70c2\u7684\u7fbd\u72b6\u4e91\u5f69\u5f62\u6210\u4e86\u4e00\u4e2a\u5de8\u5927\u5706\u73af\uff0c\u91d1\u8272\u5149\u8f89\u6cc4\u4e0b\uff0c\u6d12\u5728\u66e6\u5929\u4f7f\u6d01\u767d\u7684\u7fbd\u7ffc\u4e0a\uff0c\u53cd\u5c04\u51fa\u5927\u5927\u5c0f\u5c0f\u7684\u4e03\u5f69\u5149\u73af\uff0c\u7480\u74a8\u7eda\u4e3d\u3002\u65e0\u6570\u540c\u6837\u957f\u7740\u6d01\u767d\u7fbd\u7ffc\u7684\u5929\u4f7f\u4ece\u5706\u73af\u4e2d\u98de\u51fa\uff0c\u5206\u7537\u5973\u7ad9\u5217\u4e24\u65c1\uff0c\u4ed6\u4eec\u5bb9\u8c8c\u4fca\u7f8e\u79c0\u4e3d\uff0c\u957f\u888d\u4e0a\u95ea\u8000\u7740\u661f\u8fb0\u822c\u7480\u74a8\u5149\u8f89\uff0c\u4ee4\u4eba\u4e0d\u6562\u6b63\u89c6\u3002</p><p>\u201c\u6b22\u8fce\u56de\u6765\uff0c\u5f25\u8d5b\u4e9a\uff01\u201d</p><p>\u5929\u4f7f\u4eec\u5f02\u53e3\u540c\u58f0\u5730\u8bf4\uff0c\u5c06\u5f25\u8d5b\u4e9a\u73af\u7ed5\u5f53\u4e2d\uff0c\u53cc\u624b\u5728\u80f8\u524d\u4ea4\u53e0\uff0c\u5531\u9882\u8d77\u5e84\u4e25\u534e\u7f8e\u7684\u5723\u6b4c\u3002\u6700\u7ec8\uff0c\u4ed6\u4eec\u7684\u8eab\u5f71\u6e10\u6e10\u6de1\u5316\uff0c\u6d88\u5931\u4e0d\u89c1\u3002</p><p>\u201c\u5b8c\u4e8b\u4e86\uff0c\u201d\u743c\u6069\u62cd\u62cd\u624b\uff0c\u201c\u8d70\u5427\u3002\u201d</p><p>\u838e\u73de\u514b\u7b2c\u4e00\u4e2a\u8d70\u8fdb\u4f20\u9001\u95e8\uff0c\u63a5\u7740\u662f\u51db\uff0c\u63a5\u7740\u5e94\u8be5\u662f\u6885\u83f2\u65af\uff0c\u5979\u671b\u4e86\u671b\u5929\u7a7a\uff0c\u7a81\u7136\u4fa7\u8fc7\u8138\u6765\u770b\u7740\u743c\u6069\u3002\u201c\u5c31\u8fd9\u4e48\u653e\u5f03\uff0c\u4e0d\u89c9\u5f97\u53ef\u60dc\u5417\uff1f\u201d\u5979\u5fae\u7b11\u7740\uff0c\u201c\u6211\u4ee5\u4e3a\u4f60\u4f1a\u7559\u4e0b\u5979\u5462\u3002\u201d</p><p>\u201c\u6709\u4f60\u5c31\u591f\u4e86\u3002\u201d</p><p>\u201c\u5979\u662f\u5929\u4f7f\u5462\u3002\u201d</p><p>\u743c\u6069\u7b11\u4e86\u8d77\u6765\uff0c\u4f4e\u5934\u4eb2\u543b\u5979\u7684\u79c0\u53d1\u3002\u201c\u4f60\u5c31\u662f\u6211\u7684\u5929\u4f7f\u3002\u201d\u4ed6\u8bf4\uff0c\u63fd\u7740\u5c11\u5973\u7684\u8170\uff0c\u5e76\u80a9\u8d70\u8fc7\u4f20\u9001\u95e8\u3002</p></article>"}}}]);
