(window.webpackJsonp=window.webpackJsonp||[]).push([[363],{386:function(p){p.exports={chapterTitle:null,title:"\u7b2c238\u7ae0 \u7559\u4e00\u70b9\u60ac\u5ff5",content:"<article><h2>\u7b2c238\u7ae0 \u7559\u4e00\u70b9\u60ac\u5ff5</h2><p>\u3000\u3000\u770b\u7740\u5c0f\u53d4\u5f20\u77e5\u975e\u4ece\u697c\u91cc\u8ffd\u51fa\u6765\uff0c\u5f20\u606a\u5e2e\u4ed6\u6253\u5f00\u8f66\u95e8\u3002</p><p>\u3000\u3000\u201c\u6211\u8ba9\u5f20\u5955\u73b0\u5728\u5c31\u56de\u4e1c\u793e\u53bb\uff0c\u4ee5\u4e3a\u5e26\u4ed6\u51fa\u6765\uff0c\u80fd\u957f\u8fdb\u4e00\u4e9b\uff0c\u201d\u5f20\u77e5\u975e\u7b11\u7740\u5bf9\u5f20\u606a\u8bf4\uff0c\u201c\u4e3a\u8fd9\u4e8b\u751f\u8fd9\u4e48\u5927\u6c14\uff0c\u628a\u4f60\u53d4\u90fd\u4e22\u4e0b\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u54ea\u91cc\u4f1a\uff0c\u8138\u8272\u662f\u505a\u7ed9\u5f20\u5955\u770b\u7684\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u53ef\u6ca1\u6709\u91cc\u9762\u65f6\u7684\u4e25\u8083\uff0c\u201c\u6211\u7238\u8fd9\u4eba\u600e\u4e48\u6837\uff1f\u8981\u8bf4\u4ed6\u4e0d\u559c\u6b22\u94b1\uff0c\u90a3\u4e5f\u4e0d\u53ef\u80fd\uff0c\u8c01\u4e0d\u559c\u6b22\u94b1\uff1f\u4f46\u662f\u4ed6\u4ece\u6ca1\u6709\u4f38\u624b\u591a\u62ff\u8fc7\u4e00\u5206\u94b1\uff0c\u6709\u4e9b\u94b1\u4e5f\u4e0d\u597d\u4e0d\u62ff\uff0c\u4ed6\u80c6\u5b50\u5c0f\u554a\uff0c\u6709\u65f6\u5019\u6709\u4e9b\u4e0d\u7406\u89e3\uff0c\u8fd9\u65f6\u5019\u80fd\u660e\u767d\u4e00\u4e9b\uff0c\u505a\u5c11\u6570\u6d3e\uff0c\u66f4\u8981\u5982\u5c65\u8584\u51b0\u2026\u2026\u201d\u5f20\u606a\u624b\u5ff1\u7740\u540e\u8111\u52fa\uff0c\u9760\u5230\u5ea7\u6905\u4e0a\uff0c\u8bf4\u9053\uff0c\u201c\u4e0a\u4e2a\u6708\u90a3\u6863\u5b50\u7325\u4eb5\u6559\u5e08\u6848\uff0c\u6ca1\u8ddf\u4f60\u7ec6\u8bf4\u8fc7\uff0c\u5176\u5b9e\u5728\u6848\u53d1\u4e4b\u524d\uff0c\u6211\u4eec\u5c31\u77e5\u9053\u4e86\uff0c\u5efa\u59d4\u7684\u90a3\u4e2a\u526f\u4e3b\u4efb\u662f\u7ed9\u6211\u4eec\u903c\u7740\u53bb\u62a5\u8b66\u7684\uff0c\u4f60\u4ee5\u4e3a\u80cc\u540e\u7b80\u5355\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff1f\u201d\u5f20\u77e5\u975e\u6123\u4e86\u4e00\u4e0b\uff0c\u4fa7\u8fc7\u8eab\u5b50\u6765\uff0c\u53c8\u5927\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u6211\u5c31\u77e5\u9053\u8fd9\u4e8b\u60c5\u80cc\u540e\u6ca1\u90a3\u4e48\u7b80\u5355\uff0c\u94c1\u5b9a\u6709\u4eba\u5728\u6363\u9b3c\uff0c\u5e02\u91cc\u5f88\u591a\u4eba\u90fd\u5728\u731c\u6d4b\uff0c\u4f46\u5c31\u662f\u60f3\u4e0d\u5230\u662f\u8c01\uff1f\u8c01\u80fd\u77e5\u9053\u662f\u4f60\u5c0f\u5b50\uff01\u4f60\u5feb\u7ed9\u6211\u8bf4\uff0c\u7a76\u7adf\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e07\u52c7\u90fd\u8c03\u5230\u65b0\u6cf0\u4e86\uff0c\u4e8b\u60c5\u90fd\u8fc7\u53bb\u4e86\uff0c\u8fd8\u6709\u4ec0\u4e48\u597d\u8bf4\u7684\uff1f\u201d\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u53ef\u4e0d\u60f3\u5c06\u9648\u829d\u9ebb\u70c2\u8c37\u5b50\u7684\u4e8b\u60c5\u518d\u62ff\u51fa\u6765\u8bf4\u4e00\u904d\uff0c\u201c\u6211\u4eec\u76ef\u7740\u522b\u4eba\uff0c\u522b\u4eba\u4e5f\u76ef\u7740\u6211\u4eec\uff0c\u7279\u522b\u6211\u4eec\u60f3\u4e0e\u522b\u4eba\u4e0d\u540c\uff0c\u5c31\u66f4\u52a0\u8981\u5982\u5c65\u8584\u51b0\uff0c\u4e0d\u7136\u6251\u4e0a\u6765\u54ac\u6211\u4eec\u7684\u4eba\u53ef\u4e0d\u662f\u4e00\u4e2a\u4e24\u4e2a\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u7684\u60f3\u6cd5\u662f\u5bf9\u7684\uff0c\u201d\u5f20\u77e5\u975e\u8f7b\u8f7b\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff1a\u201c\u5f20\u5955\u6ca1\u6709\u4f60\u7684\u5929\u5206\uff0c\u8fd8\u8981\u6bdb\u8e81\u51e0\u5e74\u624d\u80fd\u5b66\u4f1a\u505a\u4eba\u505a\u4e8b\uff0c\u8fd8\u662f\u8ba9\u4ed6\u5728\u4e1c\u793e\u6df7\u7740\u5427\uff0c\u8bf4\u4e0d\u5b9a\u518d\u8fc7\u51e0\u5e74\u4e5f\u4f1a\u6709\u4e9b\u51fa\u606f\uff0c\u8ddf\u4f60\u4e0d\u80fd\u6bd4\uff0c\u5176\u4ed6\u4eba\u8fd8\u4e0d\u90fd\u662f\u8fd9\u4e48\u6df7\u8fc7\u6765\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u5f20\u606a\u77e5\u9053\u5c0f\u53d4\u8fd8\u60f3\u7ed9\u5f20\u5955\u673a\u4f1a\uff0c\u6bd5\u7adf\u662f\u4e00\u5bb6\u4eba\uff0c\u770b\u4ed6\u65e5\u540e\u8868\u73b0\u518d\u8bf4\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u5927\u4f2f\u5728\u4e1c\u793e\u8fd8\u80fd\u5e2e\u4ed6\u906e\u62e6\u4e9b\u9ebb\u70e6\uff0c\u603b\u6bd4\u5728\u6d77\u5dde\u5e02\u505a\u51fa\u4ec0\u4e48\u4e8b\u60c5\u8ba9\u6211\u4eec\u5927\u4e49\u706d\u4eb2\u7684\u5f3a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e0d\u8bf4\u8fd9\u4e8b\u4e86\uff0c\u201d\u5f20\u77e5\u975e\u6362\u4e86\u4e2a\u8bdd\u9898\uff0c\u201c\u4e07\u5411\u524d\u8001\u4e0d\u6b7b\u7684\u53bb\u4e86\u7701\u91cc\uff0c\u4e07\u52c7\u8fd9\u6b21\u4e5f\u662f\u5e73\u8c03\u53bb\u65b0\u6cf0\u5e02\u4efb\u526f\u5e02\u957f\uff0c\u4e07\u5bb6\u5728\u6d77\u5dde\u6ca1\u6709\u5f97\u7f6a\u4eba\u4e5f\u5c31\u7f62\u4e86\uff0c\u8981\u662f\u4ed6\u4e07\u5bb6\u505a\u4e8b\u592a\u8fc7\u5206\uff0c\u4ee5\u524d\u4e0d\u6562\u7ad9\u51fa\u6765\u8bf4\u8bdd\u7684\u4eba\uff0c\u8fd9\u65f6\u5019\u5c31\u4f1a\u5c11\u4e86\u8bb8\u591a\u987e\u8651\uff0c\u4f60\u8bf4\u4ed6\u7684\u65e5\u5b50\u4f1a\u4e0d\u4f1a\u597d\u8fc7\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u5f88\u6ca1\u6709\u6b63\u4e49\u611f\u7684\u8bf4\uff1a\u201c\u53ea\u8981\u4e0d\u5728\u6211\u9762\u524d\u788d\u773c\u5c31\u53ef\u4ee5\u4e86\uff0c\u5176\u4ed6\u4e8b\uff0c\u6211\u7ba1\u4ed6\u7684\uff1f\u201d\u7ecf\u6b64\u4e00\u6298\u817e\uff0c\u8bf4\u4e0d\u5b9a\u4e07\u52c7\u5728\u6d77\u5dde\u8fd8\u5f97\u7f6a\u4e86\u8c01\u4f1a\u5728\u4ed6\u79bb\u5f00\u4e4b\u540e\u8df3\u51fa\u6765\uff0c\u4e07\u52c7\u5728\u4ed5\u9014\u4e0a\u7684\u4e0a\u5347\u901a\u9053\u5dee\u4e0d\u591a\u7ed9\u622a\u65ad\u4e86\uff0c\u4f46\u662f\u4e5f\u5f88\u96be\u8bf4\uff0c\u4e07\u8001\u5934\u6bd5\u7adf\u5728\u7701\u4eba\u5927\u8fd8\u6302\u7740\u526f\u4e3b\u4efb\u7684\u5934\u8854\uff1b\u4e07\u5bb6\u7684\u5bb6\u5e95\u6df1\u539a\uff0c\u53ef\u4ee5\u62ff\u94b1\u51fa\u6765\u6492\uff0c\u4e0e\u9526\u57ce\u4e4b\u95f4\u7684\u5173\u7cfb\u53c8\u4e0d\u4e3a\u5916\u754c\u6240\u77e5\uff1b\u7279\u522b\u8c22\u5bb6\u8fd9\u6b21\u81ea\u544a\u594b\u52c7\u8981\u66ff\u8d75\u9526\u8363\u3001\u4e07\u52c7\u4e0e\u5468\u5bcc\u660e\u4e4b\u95f4\u5f53\u548c\u4e8b\u4f6c\uff0c\u800c\u65b0\u6cf0\u5e02\u53c8\u662f\u8c22\u5bb6\u6b63\u6cf0\u96c6\u56e2\u7684\u8001\u57fa\u5730\uff0c\u8fd9\u80cc\u540e\u7684\u5173\u7cfb\u6709\u6ca1\u6709\u53ef\u80fd\u8fdb\u4e00\u6b65\u590d\u6742\u5316\uff0c\u5916\u4eba\u4e00\u6982\u4e0d\u77e5\uff0c\u4f46\u662f\u81f3\u5c11\u6709\u4e00\u70b9\u662f\u53ef\u4ee5\u80af\u5b9a\u7684\uff0c\u81f3\u5c11\u8fd1\u51e0\u5e74\u5185\uff0c\u4e0d\u9700\u8981\u62c5\u5fc3\u6d77\u5dde\u8fd8\u4f1a\u5192\u51fa\u4e2a\u5f3a\u654c\u51fa\u6765\u3002</p><p>\u3000\u3000\u81f3\u4e8e\u9526\u57ce\u96c6\u56e2\uff0c\u90a3\u8fd8\u8981\u770b\u81ea\u5df1\u6709\u6ca1\u6709\u5174\u8da3\u53bb\u60f9\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u8fd9\u4e48\u60f3\u7740\uff0c\u5f20\u606a\u5fc3\u60c5\u5c31\u6109\u5feb\u591a\uff0c\u60f3\u8d77\u8fc7\u6765\u662f\u8981\u770b\u5b89\u7f6e\u623f\u89c4\u5212\u65b9\u6848\u7684\uff0c\u7b11\u7740\u5bf9\u5c0f\u53d4\u8bf4\uff1a\u201c\u4ec0\u4e48\u65b9\u6848\u4e0d\u65b9\u6848\uff0c\u6211\u4e0d\u7528\u770b\u4e86\uff0c\u5750\u8f66\u5230\u5730\u65b9\u4e0a\u770b\u4e00\u770b\uff0c\u5c0f\u53d4\u8ddf\u6211\u8bf4\u4e00\u8bf4\uff0c\u6211\u80fd\u542c\u660e\u767d\u5c31\u884c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u884c\u2026\u2026\u201d</p><p>\u3000\u3000\u5728\u6539\u9769\u5f00\u653e\u4e09\u5341\u5e74\u95f4\uff0c\u623f\u5730\u4ea7\u5e02\u573a\u6709\u4e24\u6b21\u8f83\u5927\u7684\u53d1\u5c55\u65f6\u671f\uff0c\u7b2c\u4e00\u6b21\u662f\u4e5d\u4e8c\u5e74\u521d\u9093\u5c0f\u5e73\u5357\u5de1\u8bb2\u8bdd\uff0c\u52a0\u5927\u6539\u9769\u5f00\u53d1\u7684\u6b65\u4f10\uff0c\u4e1c\u5357\u6cbf\u6d77\u57ce\u5e02\u7684\u5546\u4e1a\u5730\u4ea7\u6709\u4e9b\u5f88\u5927\u7684\u53d1\u5c55\uff0c\u4f46\u662f\u4e5d\u56db\u5e74\u901a\u8d27\u81a8\u80c0\u7387\u5c45\u9ad8\u4e0d\u4e0b\uff0c\u592e\u884c\u6536\u7f29\u94f6\u6839\uff0c\u9650\u5236\u5546\u4e1a\u5730\u4ea7\u7684\u53d1\u5c55\uff0c\u4f46\u662f\u6d77\u5916\u8d44\u91d1\uff0c\u7279\u522b\u662f\u4e1c\u5357\u4e9a\u8d44\u91d1\u7684\u6d41\u5165\uff0c\u7ee7\u7eed\u652f\u6491\u7740\u5546\u4e1a\u5730\u4ea7\u7684\u53d1\u5c55\uff0c\u5230\u4e5d\u4e03\u5e74\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u7206\u53d1\uff0c\u6d77\u5916\u8d44\u91d1\u6050\u614c\u9003\u79bb\uff0c\u624d\u4f7f\u5f97\u4e1c\u5357\u6cbf\u6d77\u57ce\u5e02\u7684\u5546\u4e1a\u5730\u4ea7\u53d7\u5230\u4e00\u5b9a\u7684\u6253\u51fb\u3002</p><p>\u3000\u3000\u4f46\u662f\u4e00\u76f4\u5230\u4e5d\u516b\u5e74\uff0c\u56fd\u5185\u57ce\u5e02\u4f4f\u623f\u5236\u5ea6\u4e00\u76f4\u5b9e\u884c\u53cc\u8f68\u5236\uff0c\u56fd\u6709\u4f01\u4e8b\u4e1a\u5458\u5de5\u4e0e\u516c\u52a1\u4eba\u5458\u90fd\u4eab\u53d7\u5206\u623f\u7684\u798f\u5229\uff0c\u5f53\u65f6\u66f4\u6ca1\u6709\u6309\u63ed\u8d37\u6b3e\u4e00\u8bf4\uff0c\u6c11\u95f4\u79ef\u7d2f\u8d22\u5bcc\u7a0b\u5ea6\u5c1a\u4e0d\u8db3\u7ed9\u5f00\u53d1\u5546\u4e0e\u67d0\u4e9b\u5229\u76ca\u9636\u5c42\u5265\u593a\uff0c\u5546\u54c1\u623f\u5e02\u573a\u6ca1\u6709\u5f97\u5230\u591a\u5c11\u53d1\u5c55\uff0c\u4efb\u4f55\u5728\u4e5d\u4e03\u3001\u4e5d\u516b\u5e74\uff0c\u67d0\u4e9b\u5f00\u53d1\u6fc0\u8fdb\u7684\u5730\u5e02\uff0c\u51fa\u73b0\u5546\u54c1\u623f\u4ef7\u683c\u5927\u5e45\u4e0b\u964d\u7684\u60c5\u5f62\u3002\u5c31\u5f20\u606a\u6240\u77e5\uff0c\u6d77\u5dde\u5e02\u5f00\u53d1\u7684\u5546\u54c1\u4f4f\u5b85\u66fe\u4e00\u5ea6\u964d\u5230\u4e94\u767e\u6bcf\u5e73\u65b9\u7c73\u7684\u4f4e\u4ef7\u3002</p><p>\u3000\u3000\u623f\u5730\u4ea7\u7684\u771f\u6b63\u673a\u4f1a\u8fd8\u8981\u7b49\u5230\u56db\u4e94\u5e74\u4e4b\u540e\uff0c\u73b0\u5728\u505a\u5b89\u7f6e\u623f\u9879\u76ee\uff0c\u5728\u653f\u5e9c\u7684\u6307\u5bfc\u610f\u89c1\u4e0b\u5b9e\u65bd\uff0c\u5229\u6da6\u867d\u7136\u4e0d\u9ad8\uff0c\u4f46\u662f\u6709\u6240\u4fdd\u8bc1\u3002\u5c06\u4e00\u5343\u4e07\u6295\u5230\u5b89\u7f6e\u623f\u9879\u76ee\u91cc\uff0c\u5229\u6da6\u7387\u751a\u81f3\u6ca1\u6709\u4e5d\u4e94\u5e74\u7684\u94f6\u884c\u8d37\u6b3e\u5229\u7387\u9ad8\uff0c\u4f46\u662f\u6709\u4e9b\u4e8b\u60c5\u5fc5\u987b\u8981\u6709\u53bb\u505a\u3002</p><p>\u3000\u3000\u81f3\u4e8e\u4ec0\u4e48\u65b9\u6848\u4e66\uff0c\u53ea\u8981\u4e0d\u5728\u8d28\u91cf\u4e0a\u505a\u6587\u7ae0\uff0c\u5f20\u606a\u7684\u786e\u6ca1\u6709\u5fc3\u601d\u53bb\u770b\uff0c\u4f46\u662f\u4e0d\u80fd\u9a73\u5c0f\u53d4\u7684\u9762\u5b50\u3002</p><p>\u3000\u3000\u5f20\u606a\u966a\u7740\u5c0f\u53d4\u5230\u5b89\u7f6e\u623f\u9879\u76ee\u9009\u5740\u5904\u8f6c\u4e86\u4e00\u5708\uff0c\u4e5f\u6ca1\u6709\u803d\u6401\u4ec0\u4e48\u65f6\u95f4\uff0c\u5f20\u77e5\u975e\u95ee\u4ed6\u63a5\u4e0b\u53bb\u8981\u53bb\u54ea\u91cc\uff0c\u5f20\u606a\u8bf4\u5230\u5de5\u5730\u53bb\u8d70\u4e00\u8d70\u3002</p><p>\u3000\u3000\u6240\u8c13\u7684\u5de5\u5730\uff0c\u81ea\u7136\u662f\u7231\u8fbe\u7684\u5de5\u5730\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7684\u5de5\u5382\u5efa\u8bbe\u5f53\u7136\u4ea4\u7ed9\u5b8f\u8fdc\u8d1f\u8d23\uff0c\u516b\u6708\u5708\u5730\uff0c\u4e5d\u6708\u5960\u57fa\uff0c\u8fdb\u884c\u5382\u533a\u5185\u7684\u5730\u4e0b\u7ba1\u7f51\u65bd\u5de5\u4e0e\u529e\u516c\u697c\u3001\u7efc\u5408\u697c\u65bd\u5de5\uff0c\u7531\u4e8e\u7b2c\u4e00\u671f\u662f\u7ec4\u88c5\u5de5\u5382\uff0c\u51e0\u4e4e\u8c08\u4e0d\u4e0a\u6280\u672f\u96be\u5ea6\uff0c\u53ea\u8981\u53ca\u65f6\u4f9b\u7ed9\u5efa\u8bbe\u8d44\u91d1\uff0c\u53c8\u8ba9\u5b8f\u8fdc\u6295\u5165\u8db3\u591f\u591a\u7684\u4eba\u624b\uff0c\u5c31\u53ef\u4ee5\u5b9e\u73b0\u6700\u5feb\u7684\u5efa\u8bbe\u901f\u5ea6\uff0c\u884c\u653f\u5ba1\u6279\u4e0a\uff0c\u4e5f\u662f\u6253\u8fb9\u5efa\u8bbe\u8fb9\u5ba1\u6279\u7684\u64e6\u8fb9\u7403\u3002\u73b0\u5728\u90fd\u51c6\u5907\u5f00\u59cb\u5382\u533a\u9053\u8def\u65bd\u5de5\uff0c\u4e00\u5f85\u8fd9\u51e0\u5929\u641e\u5b9a\u8def\u57fa\uff0c\u5382\u533a\u3001\u914d\u5957\u5de5\u7a0b\u5c06\u4e0e\u9053\u8def\u6d47\u7b51\u540c\u65f6\u8fdb\u884c\uff0c\u5f20\u606a\u8981\u6c42\u5143\u65e6\u4e4b\u524d\u4e3b\u4f53\u5de5\u7a0b\u7ae3\u5de5\uff0c\u6625\u8282\u4e4b\u524d\u6295\u5165\u4f7f\u7528\u3002</p><p>\u3000\u3000\u5f20\u606a\u6402\u94b1\u7684\u672c\u4e8b\uff0c\u8ba9\u4eba\u77a0\u76ee\u7ed3\u820c\uff0c\u4f46\u662f\u82b1\u94b1\u7684\u529f\u592b\uff0c\u4e5f\u8ba9\u4eba\u60ca\u53f9\u4e0d\u5df2\uff0c\u4e0d\u4ec5\u65b0\u7684\u7ec4\u88c5\u5de5\u5382\u3001\u539f\u7231\u8fbe\u7535\u5b50\u7684\u5de5\u5382\uff0c\u8fd8\u5bf9\u521a\u6536\u8d2d\u6765\u7684\u65e0\u7ebf\u7535\u5143\u5668\u4ef6\u8fdb\u884c\u5382\u533a\u6539\u5efa\u3002\u5bf9\u65e0\u7ebf\u7535\u4e8c\u5382\u7684\u6539\u5efa\uff0c\u4e0d\u4ec5\u5305\u62ec\u65b0\u5efa\u4e00\u5e62\u7814\u53d1\u5927\u697c\uff0c\u8fd8\u5bf9\u65e7\u5382\u533a\u8fdb\u884c\u7ffb\u65b0\u3002</p><p>\u3000\u3000\u65e0\u7ebf\u7535\u5143\u5668\u4ef6\u4e8c\u5382\u66f4\u540d\u4e3a\u7231\u8fbe\u7535\u5b50\u5143\u5668\u4ef6\u6709\u9650\u516c\u53f8\uff0c\u6210\u4e3a\u7231\u8fbe\u7535\u5b50\u7684\u5168\u8d44\u5b50\u516c\u53f8\uff0c\u4ec5\u6536\u8d2d\u5c31\u8017\u8d44\u4e24\u5343\u4e07\uff0c\u5f20\u606a\u662f\u60f3\u7b49\u76ca\u9686\u9547\u4e1c\u7684\u751f\u4ea7\u57fa\u5730\u5f62\u6210\u89c4\u6a21\u4e4b\u540e\uff0c\u8fd9\u91cc\u80fd\u6210\u4e3a\u7231\u8fbe\u7684\u7814\u53d1\u4e2d\u5fc3\uff0c\u6b64\u756a\u6539\u6269\u5efa\u6beb\u4e0d\u5fc3\u8f6f\u7684\u5c31\u6295\u5165\u8fd1\u5343\u4e07\uff0c\u4ec5\u5b50\u516c\u53f8\u7684\u5927\u95e8\u6539\u5efa\u5c31\u6295\u5165\u4e0a\u4e94\u516d\u5341\u4e07\uff0c\u52a0\u4e0a\u516c\u53f8\u5185\u7684\u9053\u8def\uff0c\u5c31\u6d88\u8017\u8fd1\u4e24\u767e\u4e07\u7684\u8d44\u91d1\u3002</p><p>\u3000\u3000\u5f20\u606a\u82b1\u94b1\u7684\u98ce\u683c\u8ba9\u5f20\u77e5\u975e\u6709\u4e9b\u6478\u4e0d\u7740\u5934\u8111\u3002</p><p>\u3000\u3000\u7231\u8fbe\u8d44\u91d1\u94fe\u5982\u6b64\u8106\u5f31\uff0c\u6709\u5fc5\u8981\u5728\u5206\u516c\u53f8\u7684\u5927\u95e8\u4e0e\u5382\u524d\u533a\u7684\u9053\u8def\u82b1\u8fd9\u4e48\u591a\u94b1\uff0c\u8fd8\u5bf9\u4e34\u8fd1\u4e3b\u5e72\u9053\u7684\u529e\u516c\u697c\u5916\u5c42\u8fdb\u884c\u91cd\u65b0\u88c5\u6f62\uff1f</p><p>\u3000\u3000\u201c\u771f\u662f\u5962\u4f88\uff0c\u8981\u6ca1\u6709\u6d77\u88d5\u518d\u5f80\u7231\u8fbe\u6295\u4e09\u5343\u4e07\uff0c\u7231\u8fbe\u7684\u73b0\u91d1\u80fd\u625b\u5f97\u4f4f\u8fd9\u6837\u7684\u6d88\u8017\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5c31\u7b97\u52c9\u5f3a\u80fd\u6491\u4f4f\uff0c\u4e5f\u4f1a\u5f88\u8f9b\u82e6\uff0c\u4e4b\u524d\u7684\u8d44\u91d1\u7ed3\u7b97\uff0c\u662f\u4ee5\u5341\u5929\u4e3a\u4e00\u6b21\uff0c\u8bf7\u6765\u7684\u8d22\u52a1\u3001\u4f1a\u8ba1\u5dee\u70b9\u90fd\u8981\u7092\u6211\u7684\u9c7f\u9c7c\uff0c\u6709\u4e86\u6d77\u88d5\u7684\u4e09\u5343\u4e07\uff0c\u7b97\u662f\u7f13\u4e86\u4e00\u53e3\u6c14\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u77e5\u975e\u5bf9\u7231\u8fbe\u7684\u8d44\u91d1\u8fd0\u8f6c\u65b9\u5f0f\u4e00\u76f4\u5f88\u6709\u7591\u60d1\uff0c\u60f3\u4e0d\u901a\u5f20\u606a\u5230\u5e95\u6709\u591a\u5f3a\u7684\u4fe1\u5fc3\u652f\u6491\u4ed6\u8fd9\u4e48\u6402\u94b1\u3001\u8fd9\u4e48\u82b1\u94b1\uff1a\u201c\u6309\u8bf4\u53ea\u80fd\u62ff\u7ecf\u9500\u5546\u7f34\u6765\u7684\u4fdd\u8bc1\u91d1\u8fdb\u884c\u5e02\u573a\u5ba3\u4f20\uff0c\u4ed6\u4eec\u7f34\u6765\u7684\u9884\u4ed8\u6b3e\u53ea\u80fd\u7528\u4e8e\u751f\u4ea7\uff0c\u7231\u8fbe\u7adf\u7136\u8981\u4ece\u7ecf\u9500\u5546\u90a3\u91cc\u6402\u94b1\u8fdb\u884c\u56fa\u5b9a\u8d44\u4ea7\u6295\u8d44\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u7231\u8fbe\u4ece\u7ecf\u9500\u5546\u90a3\u91cc\u83b7\u5f97\u7684\u8d44\u91d1\u5fc5\u987b\u4e0d\u65ad\u7684\u6269\u5927\uff0c\u53ea\u80fd\u4ece\u6269\u5927\u7684\u90e8\u5206\u91cc\u62bd\u53d6\u8d44\u91d1\u8fdb\u884c\u56fa\u5b9a\u8d44\u4ea7\u6295\u8d44\uff0c\u5c31\u7b97\u4f60\u6709\u5f88\u5f3a\u7684\u4fe1\u5fc3\uff0c\u4f3c\u4e4e\u94b1\u4e5f\u53ef\u4ee5\u8282\u7ea6\u4e00\u4e9b\u82b1\uff1f\u201d</p><p>\u3000\u3000\u201c\u8282\u7ea6\u4e00\u4e9b\u82b1\uff1f\u201d\u5f20\u606a\u4e0d\u6653\u5f97\u5c0f\u53d4\u8981\u8bf4\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u201c\u5c31\u662f\u8fd9\u4e2a\u2026\u2026\u201d\u5f20\u77e5\u975e\u671d\u7231\u8fbe\u5b50\u516c\u53f8\u6b63\u5728\u65bd\u5de5\u7684\u95e8\u5ead\u4e0e\u9053\u8def\u5476\u5476\u5634\u3002</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u4f60\u8bf4\u8fd9\u4e2a\u554a\uff0c\u201d\u5f20\u606a\u6487\u5634\u7b11\u4e86\u7b11\uff0c\u201c\u6211\u95ee\u8fc7\u4e8c\u5382\u7684\u5382\u957f\uff0c\u539f\u5148\u7684\u5927\u95e8\u5efa\u6210\u6709\u4e09\u5341\u5e74\u4e86\uff0c\u8fd9\u6761\u8def\u5012\u662f\u524d\u5e74\u91cd\u4fee\u8fc7\uff0c\u4f46\u662f\u5382\u533a\u4e0e\u4e3b\u5e72\u9053\u8fde\u63a5\u7684\u5730\u65b9\u5751\u5751\u6d3c\u6d3c\uff0c\u518d\u8fc7\u4e00\u4e24\u4e2a\u6708\uff0c\u5168\u56fd\u7684\u5a92\u4f53\u90fd\u4f1a\u8702\u62e5\u800c\u81f3\uff0c\u7231\u8fbe\u603b\u8981\u6709\u4e2a\u50cf\u6837\u7684\u5730\u65b9\u7ed9\u4ed6\u4eec\u62cd\u7167\u4e0d\u662f\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\uff1f\u201d\u5f20\u77e5\u975e\u4e0d\u53ef\u7f6e\u4fe1\u7684\u95ee\uff0c\u201c\u600e\u4e48\u53eb\u518d\u8fc7\u4e00\u4e24\u4e2a\u6708\uff0c\u5168\u56fd\u7684\u5a92\u4f53\u5c31\u4f1a\u8702\u62e5\u800c\u81f3\uff1f\u7231\u8fbe\u4eca\u5e74\u518d\u706b\uff0c\u52c9\u5f3a\u6324\u4e0a\u7535\u5b50\u767e\u5f3a\u699c\u5c31\u4e0d\u9519\u4e86\uff0c\u600e\u4e48\u53ef\u80fd\u5438\u5f15\u5168\u56fd\u7684\u5a92\u4f53\u8702\u62e5\u800c\u81f3\uff1f\u201d</p><p>\u3000\u3000\u201c\u8c01\u77e5\u9053\uff0c\u6216\u8bb8\u5a92\u4f53\u96c6\u4f53\u7a81\u7136\u53d1\u98da\u5931\u63a7\u4e5f\u6709\u53ef\u80fd\u2026\u2026\u201d\u5f20\u606a\u80e1\u626f\u9053\u3002</p><p>\u3000\u3000\u201c\u8ddf\u5c0f\u53d4\u4e0d\u80fd\u63d0\u524d\u6f0f\u70b9\u98ce\uff1f\u201d\u5f20\u77e5\u975e\u5fc3\u75d2\u75d2\uff0c\u518d\u7b49\u4e00\u4e24\u4e2a\u6708\u77e5\u9053\u7b54\u6848\uff0c\u8fd8\u4e0d\u6653\u5f97\u4f1a\u75db\u82e6\u6210\u4ec0\u4e48\u6837\u5b50\u3002</p><p>\u3000\u3000\u201c\u8fd8\u662f\u7559\u70b9\u60ac\u5ff5\u597d\u5427\uff1f\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u8fc7\u51e0\u5929\u53bb\u4e0a\u6d77\uff0c\u518d\u7ecf\u4e0a\u6d77\u53bb\u9999\u6e2f\uff0c\u5c0f\u53d4\u6709\u7a7a\u4e00\u8d77\u53bb\u73a9\u73a9\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u65f6\u5019\u624d\u8bf4\uff0c\u8fc7\u51e0\u5929\u662f\u51e0\u5929\uff1f\u62a4\u7167\u529e\u5f97\u4e0b\u6765\uff1f\u201d\u5f20\u77e5\u975e\u6487\u6487\u5634\uff0c\u201c\u600e\u4e48\u597d\u4e45\u6ca1\u5728\u9526\u6e56\u770b\u89c1\u8bb8\u601d\uff0c\u4f60\u5c06\u5979\u85cf\u54ea\u91cc\u53bb\u4e86\uff1f\u201d</p></article>"}}}]);
