(window.webpackJsonp=window.webpackJsonp||[]).push([[364],{387:function(p){p.exports={chapterTitle:null,title:"\u7b2c364\u7ae0 \u5bcc\u8d35\u903c\u4eba",content:"<article><h2>\u7b2c364\u7ae0 \u5bcc\u8d35\u903c\u4eba</h2><p>\u3000\u3000\u5b59\u4e3d\u7236\u6bcd\u662f\u4eca\u5929\u51cc\u6668\u7684\u706b\u8f66\u5230\u6d77\u5dde\u7684\uff0c\u4f4f\u8fdb\u897f\u57ce\u996d\u5e97\uff0c\u7531\u6881\u519b\u966a\u8ddf\u5b59\u4e3d\u966a\u7740\u3002\u5988\u5988\u966a\u5c0f\u8205\u4e00\u5bb6\u4eba\u597d\u597d\u7684\u5728\u6d77\u5dde\u8f6c\u4e00\u8f6c\uff0c\u5f20\u606a\u53ea\u5f97\u5c06\u4e24\u90e8\u8f66\u90fd\u8ba9\u51fa\u6765\uff0c\u4ed6\u4e0e\u5510\u5a67\u7ea6\u597d\u4eca\u5929\u8fd8\u53bb\u5b66\u6821\u590d\u4e60\u529f\u8bfe\uff0c\u5403\u8fc7\u65e9\u996d\u4ed6\u5c31\u9a91\u7740\u81ea\u884c\u8f66\u5148\u53bb\u627e\u5510\u5a67\uff0c\u7136\u540e\u4e00\u8d77\u53bb\u5b66\u6821\u3002\u4e24\u5bb6\u90fd\u642c\u5230\u5c0f\u9526\u6e56\u9644\u8fd1\uff0c\u7a7f\u8fc7\u524d\u95e8\u5546\u52a1\u533a\uff0c\u4e09\u56db\u91cc\u8def\uff0c\u5c31\u80fd\u5230\u5b66\u6821\u3002</p><p>\u3000\u3000\u4e0a\u5348\u6234\u7740\u8033\u673a\uff0c\u5f20\u606a\u72ec\u81ea\u5750\u5728\u6559\u5ba4\u7684\u89d2\u843d\u91cc\u6e29\u4e60\u4e86\u534a\u5929\u7684\u4e66\uff0c\u4e2d\u5348\u63a5\u5230\u4ed6\u5988\u7684\u7535\u8bdd\uff0c\u4ed6\u5988\u4e0e\u5c0f\u8205\u4e00\u5bb6\u4eba\u5728\u56db\u51e4\u6865\uff0c\u8ba9\u4ed6\u8fc7\u53bb\u4e00\u8d77\u5403\u996d\u3002\u4e0a\u5348\u4ed6\u4eec\u53bb\u4e86\u8c61\u5c71\uff0c\u5f20\u606a\u8bb0\u5f97\u53bb\u5e74\u5927\u8205\u4e00\u5bb6\u56de\u6765\u65f6\uff0c\u4e5f\u662f\u5148\u53bb\u4e86\u8c61\u5c71\u3002\u5f20\u606a\u60f3\u7559\u5728\u5b66\u6821\u91cc\u5403\u98df\u5802\u4f46\u4ed6\u8fde\u996d\u76d2\u90fd\u6ca1\u6709\uff0c\u4fbf\u62c9\u7740\u5510\u5a67\u4e00\u8d77\u53bb\u5403\u996d\u3002</p><p>\u3000\u3000\u8d70\u51fa\u6821\u95e8\uff0c\u770b\u89c1\u5802\u5144\u5f20\u5955\u7ad9\u5728\u4e0d\u8fdc\u5904\uff0c\u65c1\u8fb9\u7ad9\u7740\u4e00\u4e2a\u7a7f\u84dd\u7070\u8272\u5957\u88d9\u3001\u76f8\u8c8c\u6e05\u79c0\u7684\u5973\u5b69\u5b50\u3002</p><p>\u3000\u3000\u5f20\u606a\u65e9\u4e0a\u51fa\u95e8\u65f6\uff0c\u8fd8\u7a7f\u4e86\u5939\u514b\uff0c\u770b\u89c1\u5f20\u5955\u8eab\u8fb9\u7684\u5973\u5b69\u5b50\u7a7f\u4e0a\u88d9\u5b50\uff0c\u624d\u53d1\u73b0\u4e2d\u5348\u7684\u5929\u6c14\u771f\u662f\u6709\u4e9b\u70ed\uff0c\u81ea\u5df1\u771f\u662f\u540e\u77e5\u540e\u89c9\u7684\u52a8\u7269\uff0c\u90fd\u6ca1\u6709\u89c9\u5f97\u70ed\u8fc7\u3002</p><p>\u3000\u3000\u5f20\u5955\u770b\u89c1\u5f20\u606a\u51fa\u6821\u95e8\uff0c\u5927\u6b65\u8d70\u8fc7\u6765\uff1a\u201c\u5f20\u606a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u627e\u6211\uff1f\u201d\u5f20\u606a\u597d\u4e00\u9635\u5b50\u6ca1\u770b\u89c1\u8fd9\u4e2a\u5802\u5144\u4e86\u3002\u53bb\u5e74\u5f20\u5955\u7ed9\u5c0f\u53d4\u5f20\u77e5\u975e\u8d76\u51fa\u5b8f\u8fdc\u516c\u53f8\uff0c\u56de\u5230\u4e1c\u793e\u8001\u5bb6\uff0c\u8ba9\u4ed6\u8001\u5b50\u5f20\u77e5\u5fae\u4ece\u53bf\u6c11\u653f\u5c40\u632a\u51fa\u4e00\u7b14\u94b1\u4e0e\u4eba\u5408\u4f19\u5f00\u516c\u53f8\uff0c\u94b1\u6b3e\u7ed9\u5408\u4f19\u4eba\u5377\u8d70\u4e86\uff0c\u4e00\u76f4\u62d6\u5230\u5f20\u77e5\u5fae\u632a\u7528\u516c\u6b3e\u7684\u4e8b\u60c5\u7ed9\u53bf\u6c11\u653f\u5c40\u5185\u90e8\u67e5\u51fa\u6765\uff0c\u624d\u8feb\u4e0d\u5f97\u5df2\u5411\u8fd9\u8fb9\u6551\u63f4\uff0c\u5f20\u606a\u5f53\u65f6\u4e0e\u5c0f\u53d4\u5c06\u4ed6\u4eec\u667e\u4e86\u4e00\u4e2a\u6708\uff0c\u624d\u5c06\u94b1\u6253\u8fc7\u53bb\u3002\u4e8b\u60c5\u867d\u7136\u5f25\u5e73\u4e86\uff0c\u4f46\u662f\u5f20\u77e5\u5fae\u4e5f\u53d7\u5230\u5185\u90e8\u5904\u5206\uff0c\u4ece\u4e1c\u793e\u53bf\u6c11\u653f\u5e9c\u526f\u5c40\u957f\u7684\u4f4d\u5b50\u5f7b\u5e95\u9000\u4e0b\u6765\uff0c\u4e4b\u540e\uff0c\u5f20\u606a\u5c31\u6ca1\u6709\u542c\u8bf4\u8fc7\u5927\u4f2f\u5f20\u77e5\u5fae\u4e0e\u5802\u5144\u5f20\u5955\u7684\u6d88\u606f\uff0c\u5fc3\u60f3\u5c0f\u53d4\u4e0e\u4ed6\u4eec\u5e94\u8be5\u8fd8\u6709\u8054\u7cfb\uff0c\u5927\u6982\u77e5\u9053\u81ea\u5df1\u4e0d\u559c\u6b22\u4ed6\u4eec\u5bb6\uff0c\u5c31\u6ca1\u6709\u5728\u81ea\u5df1\u9762\u524d\u63d0\u8d77\u4e1c\u793e\u8001\u5bb6\u7684\u4e8b\u60c5\u3002\u5f20\u5955\u7a81\u7136\u51fa\u73b0\u6765\u5b66\u6821\u627e\u81ea\u5df1\uff0c\u5f20\u606a\u6709\u4e9b\u8bb6\u5f02\u3002</p><p>\u3000\u3000\u201c\u4e0a\u5348\u53bb\u5e02\u653f\u5e9c\u529e\u4e86\u4e9b\u4e8b\u60c5\uff0c\u9047\u5230\u4e8c\u53d4\u8bf4\u4f60\u5728\u5b66\u6821\u91cc\uff0c\u529e\u5b8c\u4e8b\u5c31\u8fc7\u6765\u770b\u770b\u4f60\uff0c\u4f60\u665a\u4e0a\u4e5f\u8981\u53bb\u559d\u6881\u519b\u8ddf\u5b59\u4e3d\u7684\u8ba2\u5a5a\u9152\uff1f\u201d\u5f20\u77e5\u884c\u5728\u5802\u5144\u5f1f\u91cc\u6392\u884c\u8001\u4e8c\uff0c\u662f\u5f20\u5955\u7684\u4e8c\u53d4\u3002</p><p>\u3000\u3000\u201c\u6881\u519b\u4e5f\u8bf7\u4e86\u4f60\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u4e0a\u5348\u9047\u5230\u4ed6\u8bf4\u4e86\u4e00\u4e0b\uff0c\u4e0b\u5348\u8fd8\u6709\u4e9b\u4e8b\u60c5\uff0c\u529e\u5b8c\u4e8b\uff0c\u6211\u4f1a\u8fc7\u53bb\u7684\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u6881\u519b\u662f\u8868\u5144\u5f1f\uff0c\u4e0e\u5f20\u5955\u662f\u5802\u5144\u5f1f\uff0c\u5f20\u606a\u4ed6\u7238\u7238\u662f\u5f20\u5955\u7684\u5802\u53d4\uff0c\u662f\u6881\u519b\u7684\u59d1\u592b\uff0c\u4f46\u662f\u6881\u519b\u4e0e\u5f20\u5955\u4e4b\u95f4\u6ca1\u6709\u4ec0\u4e48\u76f4\u63a5\u7684\u5173\u7cfb\uff0c\u5f53\u521d\u4e0b\u51b3\u5fc3\u5c06\u5f20\u5955\u8d76\u56de\u4e1c\u793e\uff0c\u539f\u56e0\u5f88\u591a\uff0c\u6700\u76f4\u63a5\u7684\u539f\u56e0\u8fd8\u5c31\u662f\u5f20\u5955\u660e\u77e5\u9053\u5b59\u4e3d\u4e0e\u6881\u519b\u5904\u670b\u53cb\uff0c\u8fd8\u53bb\u7ea0\u7f20\u5b59\u4e3d\u3002\u6881\u519b\u4e0e\u5b59\u4e3d\u8ba2\u5a5a\uff0c\u5c31\u5728\u5434\u5929\u5b9d\u7684\u5efa\u90ba\u9152\u5e97\u6446\u51e0\u684c\u9152\u5bb4\uff0c\u8bf7\u7684\u90fd\u662f\u4eb2\u8fd1\u7684\u4eba\u3002\u4e0d\u6653\u5f97\u5f20\u5955\u5728\u54ea\u91cc\u9047\u5230\u6881\u519b\uff0c\u6881\u519b\u8fd9\u4e48\u8bf4\uff0c\u5e94\u8be5\u53ea\u662f\u5ba2\u5957\u7684\u8bf4\u6cd5\uff0c\u5f20\u5955\u4e0d\u4f1a\u542c\u4e0d\u51fa\u6765\uff0c\u5f20\u606a\u772f\u8d77\u773c\u775b\u770b\u4e86\u5f20\u5955\u4e00\u773c\uff0c\u773c\u795e\u5012\u6bd4\u5927\u534a\u5e74\u524d\u51dd\u655b\u4e86\u4e00\u4e9b\uff0c\u4e5f\u4e0d\u6653\u5f97\u4ed6\u4e0a\u5348\u5230\u5e02\u653f\u5e9c\u505a\u4ec0\u4e48\u4e8b\u53bb\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u4e00\u8d77\u53bb\u5403\u4e2d\u996d\u5427\u2026\u2026\u201d\u65e2\u7136\u662f\u7238\u7238\u544a\u8bc9\u5f20\u5955\u5230\u5b66\u6821\u6765\u627e\u81ea\u5df1\uff0c\u5f20\u606a\u5012\u6ca1\u6709\u7529\u624b\u5c31\u8d70\uff0c\u201c\u6211\u5988\u4ed6\u4eec\u90fd\u5728\u56db\u51e4\u6865\uff0c\u6211\u7238\u80fd\u62bd\u51fa\u8eab\u6765\uff0c\u4e5f\u4f1a\u8fc7\u53bb\u7684\u3002\u201d\u5f20\u606a\u6000\u7591\u4ed6\u7238\u80fd\u4e0d\u80fd\u62bd\u51fa\u8eab\u6765\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u4e2d\u5348\u786e\u5b9e\u7ed9\u5e02\u653f\u5e9c\u529e\u7684\u540c\u50da\u7ed9\u201c\u7ed1\u201d\u5230\u6587\u5c71\u996d\u5e97\u5403\u996d\uff0c\u4ed6\u56db\u6708\u521d\u7a81\u7136\u5c31\u8fdb\u5165\u7701\u59d4\u515a\u6821\u5b66\u4e60\uff0c\u800c\u4e14\u662f\u7834\u4f8b\u8fdb\u4e86\u5385\u5c40\u7ea7\u5e72\u90e8\u57f9\u8bad\u73ed\uff0c\u5373\u4f7f\u4e0d\u6e05\u695a\u5185\u60c5\u7684\u4eba\uff0c\u4e5f\u77e5\u9053\u5f20\u77e5\u884c\u8fd9\u6b21\u57f9\u8bad\u51fa\u6765\uff0c\u80af\u5b9a\u5c11\u4e0d\u4e86\u4e00\u4e2a\u526f\u5e02\uff0c\u6d88\u606f\u7075\u901a\u4e00\u4e9b\u7684\uff0c\u4e5f\u4ece\u5e02\u5e38\u59d4\u6210\u5458\u90a3\u91cc\u77e5\u9053\u5f20\u77e5\u884c\u8fd9\u6b21\u4f1a\u79bb\u5f00\u6d77\u5dde\u3002\u5e02\u653f\u5e9c\u79d8\u4e66\u957f\u7684\u4f4d\u7f6e\u5012\u4e5f\u7f62\u4e86\uff0c\u6d77\u5dde\u63a7\u80a1\u5728\u5f20\u77e5\u884c\u8d70\u540e\u7531\u8c01\u6765\u638c\u63e1\uff0c\u5c31\u6210\u4e3a\u6d77\u5dde\u5e02\u5b98\u573a\u8fd9\u4e24\u4e2a\u6708\u6765\u89d2\u529b\u7684\u6f5c\u6d41\u3002</p><p>\u3000\u3000\u6309\u8bf4\u8c01\u4ee3\u66ff\u5f20\u77e5\u884c\u638c\u63a7\u6d77\u5dde\u63a7\u80a1\u5e94\u8be5\u7531\u5e02\u91cc\u8bf4\u7684\u7b97\uff0c\u4f46\u662f\uff0c\u662f\u5f20\u77e5\u884c\u5c06\u5e02\u91cc\u90a3\u4e9b\u7edd\u5927\u591a\u6570\u7ecf\u8425\u56f0\u96be\u7684\u4f01\u4e1a\u91cd\u7ec4\u5230\u6d77\u5dde\u63a7\u80a1\u7684\u65d7\u4e0b\uff0c\u5728\u77ed\u77ed\u4e00\u5e74\u534a\u7684\u65f6\u95f4\u91cc\uff0c\u4ec5\u4ec5\u662f\u5229\u7528\u8d22\u52a1\u91cd\u7ec4\u3001\u571f\u5730\u7f6e\u6362\u7b49\u624b\u6bb5\uff0c\u4e0d\u4ec5\u8ba9\u7edd\u5927\u591a\u6570\u4f01\u4e1a\u90fd\u6539\u5934\u6362\u9762\uff0c\u8fd8\u989d\u5916\u8ba9\u6d77\u5dde\u63a7\u80a1\u5957\u51fa\u5341\u591a\u4ebf\u7684\u8d44\u91d1\uff0c\u9664\u4e86\u65d7\u4e0b\u90a3\u4e9b\u6539\u5934\u6362\u9762\u7684\u4f01\u4e1a\uff0c\u6d77\u5dde\u63a7\u80a1\u5f00\u53d1\u4e86\u5e76\u62e5\u6709\u4e24\u4e2a\u7efc\u53d1\u6279\u53d1\u5e02\u573a\u4e0e\u56db\u4e2a\u4e13\u4e1a\u6279\u53d1\u5e02\u573a\uff0c\u4ec5\u8fd9\u4e9b\u6279\u53d1\u5e02\u573a\u6bcf\u5e74\u6536\u83b7\u7684\u5730\u79df\uff0c\u6bcf\u5e74\u90fd\u8981\u4e0a\u4ebf\u5143\uff0c\u8fd9\u4e9b\u6210\u7ee9\u90fd\u662f\u5f20\u77e5\u884c\u4ece\u65e0\u5230\u6709\u5e72\u51fa\u6765\u7684\u3002\u771f\u8981\u51b3\u5b9a\u8c01\u6765\u63a5\u7ba1\u6d77\u5dde\u63a7\u80a1\uff0c\u5f20\u77e5\u884c\u8bf4\u8bdd\u6bd4\u4e00\u822c\u7684\u5e38\u59d4\u6210\u5458\u8fd8\u8981\u91cd\u8981\u3002</p><p>\u3000\u3000\u65e0\u6cd5\u8fdb\u4e00\u6b65\u77e5\u9053\u5185\u60c5\u7684\u4eba\u5f88\u5947\u602a\u5f20\u77e5\u884c\u4e3a\u4ec0\u4e48\u4f1a\u653e\u5f03\u6d77\u5dde\u63a7\u80a1\u638c\u95e8\u4eba\u7684\u8eab\u4efd\u800c\u53bb\u5916\u5730\u5f53\u4e2a\u526f\u5e02\u957f\uff0c\u76f4\u63a5\u638c\u63a7\u6d77\u5dde\u63a7\u80a1\uff0c\u6bd4\u5bfb\u5e38\u4e00\u540d\u526f\u5e02\u957f\u4e0d\u5f97\u5dee\u3002</p><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u6709\u4eba\u4e3a\u6d77\u5dde\u63a7\u80a1\u8463\u4e8b\u957f\u7684\u4f4d\u5b50\u89d2\u529b\uff0c\u4ed6\u4eec\u8fd9\u8fb9\u7684\u5b89\u6392\u662f\u8ba9\u5b8b\u57f9\u660e\u517c\u4efb\uff0c\u5177\u4f53\u7684\u7ecf\u8425\u8fd8\u662f\u7531\u674e\u660e\u5b66\u8d1f\u8d23\u3002\u53bb\u5e74\u5b8b\u57f9\u660e\u517c\u4efb\u5e02\u957f\u52a9\u7406\uff0c\u5c31\u4e3a\u4eca\u5929\u505a\u597d\u51c6\u5907\uff0c\u4e5f\u65b9\u4fbf\u5b8b\u57f9\u660e\u4e0b\u4e00\u6b65\u4ece\u57ce\u5357\u533a\u8df3\u5230\u5e02\u91cc\u3002\u6d77\u5dde\u63a7\u80a1\u7684\u653f\u7ee9\u600e\u4e48\u8bf4\u90fd\u8981\u5f52\u5230\u5510\u5b66\u8c26\u7684\u540d\u4e0b\uff0c\u5468\u5bcc\u660e\u8fd8\u4e0d\u81f3\u4e8e\u73b0\u5728\u5c31\u53bb\u4e89\u6d77\u5dde\u63a7\u80a1\u7684\u4eba\u4e8b\u6743\u3002</p><p>\u3000\u3000\u5f20\u5955\u4ecb\u7ecd\u8eab\u8fb9\u76f8\u8c8c\u6e05\u79c0\u7684\u5973\u5b69\u5b50\u662f\u4ed6\u73b0\u5728\u516c\u53f8\u7684\u540c\u4e8b\u5218\u5a77\uff0c\u5f20\u606a\u8fd9\u624d\u77e5\u9053\u5f20\u5955\u73b0\u5728\u5230\u4e1c\u793e\u53bf\u4e00\u5bb6\u5efa\u6750\u8d38\u6613\u516c\u53f8\u5f53\u7ecf\u7406\u52a9\u7406\uff0c\u8981\u4e0d\u8bf4\u5f20\u5955\u7565\u663e\u8f7b\u6d6e\u7684\u6027\u5b50\uff0c\u4ed6\u5012\u662f\u9002\u5408\u8fd9\u4e2a\u804c\u4f4d\u3002</p><p>\u3000\u3000\u867d\u8bf4\u662f\u540c\u4e8b\uff0c\u5f20\u606a\u8bf7\u5f20\u5955\u4e00\u8d77\u8fc7\u53bb\u5403\u996d\uff0c\u5973\u5b69\u5b50\u5012\u6ca1\u6709\u8bf4\u8981\u79bb\u5f00\uff0c\u800c\u662f\u8ddf\u7740\u5750\u8fdb\u51fa\u79df\u8f66\uff0c\u770b\u5f20\u5955\u4e0e\u5979\uff0c\u7684\u786e\u4e5f\u6ca1\u6709\u53d1\u5c55\u5230\u7537\u5973\u670b\u53cb\u7684\u5173\u7cfb\u4e0a\u3002</p><p>\u3000\u3000\u8981\u8bf4\u8d77\u6765\uff0c\u5f20\u5bb6\u4eba\u7684\u957f\u76f8\u90fd\u4e0d\u8d56\uff0c\u5c31\u5f20\u73ab\u957f\u5f97\u50cf\u5c0f\u5a76\u5a76\uff0c\u76ae\u80a4\u6709\u4e9b\u9ed1\uff0c\u4ee5\u81f4\u5f20\u73ab\u957f\u5927\u4e4b\u540e\u4e00\u76f4\u62b1\u6028\u81ea\u5df1\u4e3a\u4ec0\u4e48\u4e0d\u591a\u9057\u4f20\u4e00\u4e9b\u5c0f\u53d4\u7684\u57fa\u56e0\uff0c\u5f20\u5955\u80fd\u8bf4\u4f1a\u9053\uff0c\u4e5f\u7684\u786e\u80fd\u8ba8\u5973\u5b69\u5b50\u559c\u6b22\uff1b\u5f20\u606a\u60f3\u60f3\u81ea\u5df1\u5728\u5510\u5a67\u51fa\u56fd\u540e\u8fd8\u4e0d\u662f\u653e\u8361\u4e0d\u7f81\u5f97\u5f88\uff0c\u5b9e\u5728\u6ca1\u6709\u9053\u7406\u53bb\u6307\u8d23\u5f20\u5955\u5728\u79c1\u751f\u6d3b\u4e0a\u4e0d\u68c0\u70b9\uff0c\u53ea\u662f\u5f20\u5955\u7ea0\u7f20\u5b59\u4e3d\u7684\u884c\u4e3a\u6709\u4e9b\u8fc7\u5206\uff0c\u8ba9\u5f20\u606a\u627e\u5230\u5c06\u4ed6\u8d76\u56de\u4e1c\u793e\u7684\u501f\u53e3\u3002</p><p>\u3000\u3000\u5f20\u5955\u624b\u91cc\u62ff\u7740\u624b\u63d0\u888b\uff0c\u4e0a\u8f66\u540e\uff0c\u5f20\u606a\u65e0\u610f\u770b\u89c1\u624b\u63d0\u888b\u91cc\u88c5\u7740\u51e0\u672c\u591c\u6821\u7684\u6559\u6750\uff0c\u7591\u60d1\u7684\u95ee\u5f20\u5955\uff1a\u201c\u600e\u4e48\u770b\u8d77\u8fd9\u4e2a\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u5728\u8bfb\u4e1c\u793e\u7684\u7535\u5927\uff0c\u4e0d\u7ba1\u80fd\u4e0d\u80fd\u5b66\u5230\u4ec0\u4e48\u4e1c\u897f\uff0c\u4ee5\u540e\u51fa\u6765\u505a\u4e8b\uff0c\u6709\u5f20\u6587\u51ed\u603b\u8981\u597d\u4e00\u4e9b\u3002\u201d</p><p>\u3000\u3000\u5f20\u5955\u9ad8\u4e2d\u6bd5\u4e1a\u5230\u793e\u4f1a\u4e0a\u53ae\u6df7\u4e86\u4e09\u5e74\uff0c\u7ec8\u7a76\u653e\u5f03\u90a3\u4e9b\u597d\u9ad8\u9a9b\u8fdc\u7684\u5ff5\u5934\uff0c\u5f00\u59cb\u8ba4\u771f\u8003\u8651\u4ee5\u540e\u7684\u4eba\u751f\u9053\u8def\uff0c\u770b\u6765\u53bb\u5e74\u53d1\u751f\u7684\u90a3\u4e9b\u4e8b\u60c5\uff0c\u5bf9\u4ed6\u8fd8\u662f\u6709\u5f88\u5927\u7684\u89e6\u52a8\u7684\u3002</p><p>\u3000\u3000\u8d76\u5230\u56db\u51e4\u6865\uff0c\u5c31\u9009\u5728\u5434\u5929\u5b9d\u7684\u5efa\u90ba\u9152\u5e97\u91cc\uff0c\u6240\u6709\u4eba\u90fd\u5728\uff0c\u5c31\u5c0f\u8868\u54e5\u6881\u6587\u6c5f\u7684\u8eab\u5f71\u4e0d\u5728\uff0c\u5f20\u606a\u95ee\u9053\uff1a\u201c\u6587\u6c5f\u8868\u54e5\u4ed6\u4eba\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ed6\u5927\u5b66\u6709\u4e24\u4e2a\u5b66\u957f\u5728\u6d77\u5dde\u5de5\u4f5c\uff0c\u4ed6\u4e2d\u5348\u53bb\u627e\u4ed6\u4eec\u4e86\u2026\u2026\u201d\u6881\u6587\u5c71\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u5f20\u606a\u95ee\u9053\uff0c\u201c\u5e08\u5144\u8fd8\u662f\u5e08\u59d0\uff1f\u6587\u6c5f\u54e5\u7279\u610f\u4ece\u5317\u4eac\u8f6c\u9053\u5f90\u5dde\u8ddf\u4f60\u4eec\u6c47\u5408\u5230\u6d77\u5dde\u6765\uff0c\u5012\u4e0d\u50cf\u662f\u8ddf\u7740\u6765\u63a2\u4eb2\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5e74\u8f7b\u4e00\u4ee3\u5bf9\u5bb6\u65cf\u89c2\u5ff5\u90fd\u5f88\u6de1\uff0c\u4f55\u51b5\u6881\u6587\u6c5f\u8fd8\u5728\u6e05\u534e\u8bfb\u4e66\uff0c\u8fd9\u65f6\u5019\u53c8\u4e0d\u662f\u653e\u5047\uff0c\u5012\u50cf\u662f\u4e13\u7a0b\u6765\u8ddf\u4ed6\u7684\u5b66\u957f\u89c1\u9762\u7684\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u5012\u6ca1\u6709\u95ee\uff0c\u201d\u6881\u6587\u5c71\u7b11\u7740\u8bf4\uff0c\u201c\u4ed6\u5012\u662f\u559c\u6b22\u5e74\u7eaa\u6bd4\u4ed6\u5927\u7684\u5973\u5b69\u5b50\uff0c\u7b49\u4ed6\u56de\u6765\u518d\u95ee\u4ed6\u2026\u2026\u201d\u6881\u6587\u6c5f\u4e0d\u5728\uff0c\u53ef\u4ee5\u968f\u4fbf\u7f16\u6392\u4ed6\u3002</p><p>\u3000\u3000\u5434\u5929\u5b9d\u8fd9\u65f6\u5019\u62ff\u7740\u83dc\u5355\u8fc7\u6765\uff0c\u8ba9\u5f20\u606a\u770b\u4e00\u773c\u5408\u4e0d\u5408\u610f\u3002\u6881\u683c\u73cd\u9886\u7740\u4eba\u8fc7\u6765\u5403\u996d\uff0c\u5434\u5929\u5b9d\u77e5\u9053\u4e86\uff0c\u5c31\u6025\u51b2\u51b2\u4ece\u5916\u9762\u8d76\u56de\u5e97\u91cc\u4eb2\u81ea\u62db\u5f85\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u7b49\u6211\u8fc7\u6765\uff0c\u6211\u809a\u5b50\u90fd\u997f\u762a\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u83dc\u90fd\u51c6\u5907\u597d\u4e86\uff0c\u6709\u4f60\u4e0d\u5408\u610f\u7684\uff0c\u5c31\u4e0d\u7aef\u4e0a\u6765\u4e86\u3002\u201d\u5434\u5929\u5b9d\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u6ca1\u90a3\u4e48\u6311\u5254\uff0c\u51c6\u5907\u597d\u4ec0\u4e48\u5c31\u7aef\u4e0a\u6765\u5427\uff0c\u8fd8\u7b49\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u53c8\u56de\u5934\u95ee\u4ed6\u5988\uff0c\u201c\u6587\u6c5f\u54e5\u4ed6\u4e0b\u5348\u77e5\u9053\u53bb\u4ec0\u4e48\u5730\u65b9\u627e\u4f60\u4eec\uff1f\u201d</p><p>\u3000\u3000\u6881\u683c\u73cd\u8bf4\u9053\uff1a\u201c\u90fd\u8ddf\u4f60\u90a3\u4e48\u6ca1\u8bb0\u6027\uff0c\u544a\u8bc9\u4ed6\u624b\u673a\u53f7\u7801\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u6587\u6c5f\u2026\u2026\u201d\u5305\u53a2\u7684\u95e8\u5f00\u7740\u4e00\u9053\u53e3\u770b\uff0c\u6881\u6587\u5c71\u770b\u89c1\u4ed6\u5f1f\u5f1f\u8ddf\u4e24\u4e2a\u9752\u5e74\u8d70\u8fdb\u6765\uff0c\u5fd9\u7ad9\u8d77\u6765\u6253\u5f00\u5305\u53a2\u7684\u95e8\u62db\u547c\u4ed6\u4eec\uff0c\u201c\u4f60\u8ddf\u4f60\u540c\u5b66\u4e5f\u5230\u8fd9\u5bb6\u5e97\u5403\u996d\uff1f\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u624d\u77e5\u9053\u6881\u6587\u6c5f\u7684\u5b66\u957f\u539f\u6765\u90fd\u662f\u7231\u8fbe\u7535\u5b50\u6280\u672f\u7814\u53d1\u4e2d\u5fc3\u7684\u65bd\u6625\u950b\u4e0e\u4e01\u6625\u5efa\u3002\u5f20\u606a\u4e00\u6b21\u5728\u5143\u5668\u4ef6\u5206\u516c\u53f8\u5f00\u4f1a\u540e\u5230\u5458\u5de5\u5a31\u4e50\u5ba4\u53bb\u53c2\u89c2\uff0c\u770b\u5230\u65bd\u6625\u950b\u5728\u73a9\u81ea\u5df1\u6c49\u5316\u7684\u9b54\u517d\u4e89\u9738\uff0c\u867d\u7136\u662f\u6781\u51fa\u8272\u7684\u6280\u672f\u5f00\u53d1\u4eba\u5458\uff0c\u4f46\u662f\u53d1\u73b0\u65bd\u6625\u950b\u672c\u4eba\u7684\u5174\u8da3\u8fd8\u5728\u6e38\u620f\u7834\u89e3\u4e0e\u5f00\u53d1\u4e0a\uff0c\u5c31\u6697\u4e2d\u63a8\u8350\u7ed9\u76db\u9752\u4ed6\u4eec\u641e\u76d7\u7248\u6e38\u620f\u3002\u6b64\u65f6\u7684\u65bd\u6625\u950b\u5df2\u7ecf\u79bb\u5f00\u7231\u8fbe\u7535\u5b50\uff0c\u76f4\u63a5\u4e3a\u76db\u9752\u3001\u90b5\u81f3\u521a\u4ed6\u4eec\u8d1f\u8d23\u5728\u6d77\u5dde\u6210\u7acb\u7684\u4e00\u95f4\u6e38\u620f\u5de5\u4f5c\u5ba4\uff0c\u5de5\u4f5c\u5ba4\u5c31\u8bbe\u5728\u6c99\u7530\u7684\u65e7\u5efa\u7b51\u7fa4\u91cc\uff0c\u5c31\u662f\u5f20\u606a\u6258\u90b5\u81f3\u521a\u51fa\u9762\u4e70\u4e0b\u7684\u8bb8\u601d\u5bb6\u7684\u90a3\u680b\u9662\u5b50\uff0c\u7ecf\u8fc7\u4fee\u7f2e\uff0c\u7ed9\u5c0f\u578b\u7684\u5de5\u4f5c\u5ba4\u7528\uff0c\u518d\u5408\u9002\u4e0d\u8fc7\u3002\u9664\u4e86\u8fd9\u5bb6\u5de5\u4f5c\u5ba4\uff0c\u76db\u9752\u3001\u90b5\u81f3\u521a\uff0c\u8fd8\u6709\u5c0f\u53d4\u4ed6\u4eec\u4e3a\u76d7\u7248\u5728\u6d77\u5dde\u6240\u6210\u7acb\u7684\u5176\u4ed6\u51e0\u5bb6\u5de5\u4f5c\u5ba4\u90fd\u8bbe\u5728\u6c99\u7530\uff0c\u770b\u52bf\u6001\uff0c\u4ed6\u4eec\u662f\u60f3\u5c06\u6c99\u7530\u7684\u4e1c\u5317\u89d2\u6539\u9020\u6210\u76d7\u7248\u56ed\u533a\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u4e0e\u65bd\u6625\u950b\u76f8\u9047\u4e4b\u540e\u60f3\u5230\u4e5d\u516b\u5e74\u4e4b\u540e\u5e94\u7528\u6781\u5e7f\u7684\u8ba1\u7b97\u673a\u5feb\u901f\u50a8\u5b58\u6280\u672f\u6765\uff0c\u8fd9\u9879\u6280\u672f\u7684\u5e94\u7528\u524d\u666f\u5f88\u5e7f\uff0c\u539f\u60f3\u8ba9\u7814\u53d1\u4e2d\u5fc3\u5f00\u53d1\u6b64\u9879\u6280\u672f\uff0c\u540e\u6765\u4ece\u65bd\u6625\u950b\u90a3\u91cc\u4e86\u89e3\u5230\u6e05\u534e\u5927\u5b66\u5df2\u7ecf\u6709\u5b66\u751f\u5728\u8fdb\u884c\u76f8\u5173\u65b9\u9762\u7684\u7814\u7a76\u3002\u4e01\u6625\u5efa\u5c31\u662f\u90a3\u4e2a\u5728\u8bfb\u7814\u671f\u95f4\u5c31\u5f00\u53d1\u51fa\u95ea\u76d8\u63a7\u5236\u90e8\u4ef6\u7684\u5173\u952e\u6280\u672f\u2014\u2014IDE\u8f6cUSB\u6280\u672f\u7684\u6e05\u534e\u5b66\u751f\uff0c\u5f20\u606a\u7acb\u5373\u6d3e\u4eba\u966a\u65bd\u6625\u950b\u53bb\u5317\u4eac\u627e\u5230\u4e01\u6625\u5efa\uff0c\u5c06\u8fd8\u4e0d\u662f\u5f88\u6210\u719f\u7684\u6280\u672f\u4e70\u4e0b\u6765\uff0c\u8058\u8bf7\u4e01\u6625\u5efa\u5230\u7231\u8fbe\u7814\u53d1\u4e2d\u5fc3\u7ee7\u7eed\u8d1f\u8d23\u6b64\u9879\u6280\u672f\u7684\u5f00\u53d1\u3002\u4e01\u6625\u5efa\u4eca\u5e74\u6625\u5b63\u624d\u4ece\u6e05\u534e\u6bd5\u4e1a\uff0c\u4e4b\u524d\u5df2\u7ecf\u6572\u5b9a\u53bbTI\u7684\u4e2d\u56fd\u516c\u53f8\u5de5\u4f5c\uff0c\u8fd8\u662f\u5f20\u606a\u4e0eTI\u7684\u4e2d\u56fd\u533a\u603b\u88c1\u9648\u4fe1\u751f\u6c9f\u901a\u540e\uff0c\u624d\u8ba9\u4e01\u6625\u5efa\u987a\u5229\u5230\u7231\u8fbe\u7535\u5b50\u6765\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u56fd\u592e\u89c6\u6807\u738b\u800c\u95fb\u540d\uff0c\u5728\u5916\u754c\u773c\u91cc\uff0c\u53ea\u662f\u751f\u4ea7\u5f71\u789f\u673a\u800c\u5df2\uff0c\u4e01\u6625\u5efa\u8d77\u521d\u8fd8\u633a\u770b\u4e0d\u8d77\u7231\u8fbe\u7535\u5b50\uff0c\u81f3\u5c11\u5728\u6280\u672f\u5c42\u9762\u4e0a\uff0c\u7231\u8fbe\u7535\u5b50\u5bf9\u4ed6\u6ca1\u6709\u4e1d\u6beb\u7684\u5438\u5f15\u529b\uff0c\u5230\u7231\u8fbe\u6765\uff0c\u4e00\u662f\u7231\u8fbe\u7535\u5b50\u5f00\u51fa\u76f8\u5f53\u9ad8\u7684\u85aa\u8d44\uff0c\u800c\u4e14\u627f\u8bfa\u4ed6\u4e00\u8fdb\u7231\u8fbe\u7535\u5b50\u5c31\u6709\u9879\u76ee\u7ed9\u4ed6\u8d1f\u8d23\uff0c\u53e6\u5916\u8fd8\u6709\u4e00\u4e2a\u91cd\u8981\u7684\u539f\u56e0\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u4e0eTI\u5408\u8d44\u6210\u7acbTIDA\u7814\u7a76\u6240\u91cd\u70b9\u7814\u53d1DVD\u5149\u789f\u8bfb\u53d6\u6280\u672f\uff0cTI\u6d3e\u5f80\u7814\u7a76\u6240\u7684\u9996\u5e2d\u5de5\u7a0b\u5e08\u4e01\u78ca\u662f\u5f53\u521d\u51b3\u5b9a\u5c06\u4e01\u6625\u5efa\u62db\u5165TI\u4e2d\u56fd\u516c\u53f8\u7684\u4eba\uff0c\u4e01\u78ca\u53bb\u5e74\u51ac\u5c31\u5230\u5408\u8d44\u7684TIDA\u7814\u7a76\u6240\u8d1f\u8d23DVD\u8bfb\u53d6\u6280\u672f\u7684\u8bfe\u9898\uff0c\u4ed6\u63a8\u8350\u4e01\u6625\u5efa\u5230\u7231\u8fbe\u7535\u5b50\u8bd5\u4e00\u8bd5\u3002</p><p>\u3000\u3000\u4e01\u6625\u5efa\u5230\u7231\u8fbe\u7535\u5b50\u4e4b\u540e\uff0c\u624d\u770b\u5230\u7231\u8fbe\u7535\u5b50\u5728\u6570\u5b57\u5a92\u4f53\u6280\u672f\u4e0a\u7684\u52c3\u52c3\u91ce\u5fc3\u3002</p><p>\u3000\u3000\u7231\u8fbe\u7535\u5b50\u7684\u524d\u7a0b\u662f\u5426\u8fdc\u5927\uff0c\u5e02\u573a\u8fd0\u4f5c\u5df2\u7ecf\u4e0d\u662f\u6700\u5173\u952e\u7684\u56e0\u7d20\uff0c\u5f20\u606a\u6e05\u695a\u7684\u77e5\u9053\uff0c\u7231\u8fbe\u7535\u5b50\u7684\u7814\u53d1\u65b9\u5411\u51b3\u5b9a\u7740\u7231\u8fbe\u7535\u5b50\u7684\u524d\u7a0b\uff0c\u4ed6\u59cb\u7ec8\u5c06\u6280\u672f\u7814\u53d1\u4e2d\u5fc3\u770b\u6210\u7231\u8fbe\u7535\u5b50\u6700\u91cd\u8981\u7684\u8d44\u4ea7\uff0c\u5c31\u7b97\u6709\u7740\u5c06\u5f71\u789f\u673a\u4e1a\u52a1\u51fa\u552e\u7ed9\u5609\u4fe1\u5b9e\u4e1a\u7684\u6253\u7b97\uff0c\u9996\u5148\u4e5f\u8981\u5c06\u6280\u672f\u7814\u53d1\u4e2d\u5fc3\u5265\u79bb\u51fa\u6765\uff0c\u6210\u7acb\u65b0\u7684\u79d1\u6280\u516c\u53f8\u3002</p><p>\u3000\u3000\u5e74\u540e\uff0c\u5f20\u606a\u5c31\u5f88\u5c11\u76f4\u63a5\u5e72\u9884\u7231\u8fbe\u7535\u5b50\u5e02\u573a\u90e8\u7684\u4e8b\u60c5\uff0c\u4f46\u662f\u7814\u53d1\u4e2d\u5fc3\u6bcf\u6b21\u786e\u5b9a\u65b0\u7684\u7814\u53d1\u65b9\u5411\u4e0e\u9879\u76ee\uff0c\u4ed6\u53ea\u8981\u5728\u6d77\u5dde\uff0c\u90fd\u4f1a\u62bd\u51fa\u8eab\u53bb\u53c2\u52a0\uff0c\u8981\u8bf4\u7231\u8fbe\u7535\u5b50\u751f\u4ea7\u90e8\u95e8\u8fd8\u6709\u4e00\u4e9b\u4e2d\u5c42\u7ba1\u7406\u4eba\u5458\u4e0d\u77e5\u9053\u5f20\u606a\u5728\u7231\u8fbe\u7684\u5730\u4f4d\uff0c\u7814\u53d1\u4e2d\u5fc3\u7684\u6bcf\u4e00\u540d\u5de5\u7a0b\u5e08\uff0c\u90fd\u77e5\u9053\u201c\u606a\u5c11\u201d\u8fd9\u4e2a\u79f0\u547c\u3002</p><p>\u3000\u3000\u4e01\u6625\u5efa\u4f5c\u4e3a\u4e3b\u4efb\u5de5\u7a0b\u5e08\u4e4b\u4e00\uff0c\u66f4\u77e5\u9053\u5f20\u606a\u8eab\u4e3a\u7231\u8fbe\u7535\u5b50\u4e3b\u8981\u6295\u8d44\u4eba\u7684\u8eab\u4efd\u3002\u6881\u6587\u6c5f\u8fd9\u6b21\u5230\u6d77\u5dde\u6765\u627e\u4ed6\uff0c\u4ed6\u62c9\u6765\u65bd\u6625\u950b\u4f5c\u966a\uff0c\u5728\u4ed6\u770b\u6765\uff0c\u6d77\u5dde\u8457\u540d\u7684\u5403\u98df\u5e97\uff0c\u90fd\u96c6\u4e2d\u5728\u56db\u51e4\u6865\u7684\u9910\u996e\u5e7f\u573a\uff0c\u7814\u53d1\u4e2d\u5fc3\u4e0e\u5143\u5668\u4ef6\u5206\u516c\u53f8\u7684\u98df\u5802\u662f\u5434\u5929\u5b9d\u7684\u5efa\u90ba\u9152\u5e97\u627f\u5305\u7684\uff0c\u4ed6\u4eec\u5230\u56db\u51e4\u6865\u6765\u5403\u996d\uff0c\u9996\u5148\u60f3\u7740\u662f\u5230\u5efa\u90ba\u9152\u5e97\u6765\u3002</p><p>\u3000\u3000\u201c\u5927\u8001\u677f\u2026\u2026\u201d\u5305\u53a2\u91cc\u6709\u4eba\u558a\u6881\u6587\u6c5f\uff0c\u4e01\u6625\u5efa\u4e5f\u56de\u8fc7\u5934\u53bb\u770b\uff0c\u770b\u5230\u5f20\u606a\u5750\u5728\u5305\u53a2\uff0c\u8111\u888b\u4f38\u7740\u5f80\u5916\u770b\uff0c\u4e0b\u610f\u8bc6\u7684\u5c31\u5524\u4e86\u51fa\u6765\u3002</p><p>\u3000\u3000\u6881\u6587\u5c71\u6b63\u5947\u602a\u6587\u6c5f\u600e\u4e48\u6b63\u597d\u8ddf\u540c\u5b66\u4e5f\u5230\u8fd9\u5bb6\u9152\u5e97\u5403\u996d\uff0c\u542c\u5230\u6587\u6c5f\u7684\u540c\u5b66\u8131\u53e3\u5524\u4e86\u8fd9\u4e00\u58f0\uff0c\u4ed6\u8ff7\u7cca\u4e86\u4e00\u4f1a\u513f\uff0c\u770b\u7740\u90a3\u4eba\u5c06\u76ee\u5149\u76ef\u5230\u5f20\u606a\u8138\u4e0a\uff0c\u66f4\u662f\u4e0d\u89e3\uff0c\u4ee5\u4e3a\u542c\u9519\u4e86\u3002</p><p>\u3000\u3000\u6881\u6587\u6c5f\u5012\u662f\u542c\u6e05\u695a\u4e86\uff0c\u5374\u4e0d\u660e\u767d\u542c\u4e01\u6625\u5efa\u3001\u65bd\u6625\u950b\u5439\u5618\u4e86\u534a\u5929\u3001\u94a6\u670d\u5f02\u5e38\u7684\u201c\u606a\u5c11\u201d\u771f\u662f\u4ed6\u90a3\u4e2a\u8fde\u4f1a\u8003\u90fd\u8981\u627e\u4eba\u8865\u4e60\u7684\u5c0f\u8868\u5f1f\uff0c\u800c\u4e0d\u662f\u540c\u540d\u540c\u59d3\uff1b\u65e9\u6668\u4ed6\u7238\u6881\u56fd\u76db\u8fd8\u8ba9\u4ed6\u5728\u6d77\u5dde\u8fd9\u51e0\u5929\u5e2e\u8fd9\u4e2a\u8868\u5f1f\u590d\u4e60\u4e00\u4e0b\u529f\u8bfe\u3002</p><p>\u3000\u3000\u5f20\u5955\u4e5f\u542c\u6e05\u695a\u4e86\uff0c\u4e5f\u786e\u5207\u660e\u767d\u4e09\u53d4\u5f20\u77e5\u975e\u8ddf\u4ed6\u8bf4\u7684\u8bdd\uff1a\u201c\u4f60\u8981\u60f3\u7740\u518d\u56de\u6d77\u5dde\u5e02\u91cc\uff0c\u8981\u9760\u4f60\u81ea\u5df1\u52aa\u529b\uff0c\u6211\u4e5f\u662f\u4e00\u6b65\u4e00\u6b65\u8d70\u5230\u73b0\u5728\u7684\uff0c\u4e5f\u8981\u5f97\u5230\u5f20\u606a\u7684\u8ba4\u540c\u2026\u2026\u201d\u4ed6\u4e5f\u662f\u53bb\u5e74\u7ed9\u5f20\u77e5\u975e\u8d76\u51fa\u5b8f\u8fdc\u516c\u53f8\u56de\u5230\u4e1c\u793e\u4e4b\u540e\uff0c\u624d\u9010\u6e10\u7422\u78e8\u51fa\u5f20\u606a\u7684\u5730\u4f4d\u662f\u5f02\u5e38\u7684\u91cd\u8981\u3002\u5f20\u77e5\u975e\u867d\u7136\u4e5f\u60f3\u7740\u7ed9\u4ed6\u5927\u54e5\u5f20\u77e5\u5fae\u4e9b\u6559\u8bad\uff0c\u4f46\u662f\u7ec8\u4e0d\u4f1a\u5bf9\u81ea\u5df1\u7684\u4eb2\u4f84\u5b50\u6492\u624b\u4e0d\u7ba1\uff0c\u7279\u610f\u56de\u8fc7\u51e0\u6b21\u4e1c\u793e\u8001\u5bb6\uff0c\u4e0e\u5f20\u5955\u957f\u8c08\u4e86\u51e0\u6b21\uff0c\u9646\u9646\u7eed\u7eed\u7684\u544a\u8bc9\u4ed6\u4e00\u4e9b\u4e8b\u60c5\uff0c\u53ea\u5dee\u6ca1\u6709\u70b9\u660e\u5f20\u606a\u6b64\u65f6\u7684\u5730\u4f4d\uff0c\u5f20\u5955\u6b64\u6b21\u542c\u4e01\u6625\u5efa\u8fd9\u58f0\u5524\uff0c\u7b97\u662f\u786e\u5207\u7684\u660e\u767d\u8fc7\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u62db\u624b\u8ba9\u4e01\u6625\u5efa\u4ed6\u4eec\u8fdb\u6765\uff0c\u62db\u5934\u5bf9\u5434\u5929\u5b9d\u8bf4\uff1a\u201c\u5e2e\u6211\u4eec\u6dfb\u4e24\u5f20\u6905\u5b50\u3002\u201d\u672c\u6765\u5750\u7740\u4e5d\u4e2a\u4eba\uff0c\u65bd\u6625\u950b\u3001\u4e01\u6625\u5efa\u3001\u6881\u6587\u6c5f\u8fdb\u6765\uff0c\u6dfb\u4e24\u5f20\u6905\u5b50\u6324\u4e00\u6324\u4e00\u8d77\u7528\u9910\u4fbf\u5f97\uff0c\u6307\u7740\u6881\u6587\u6c5f\u5bf9\u4e01\u6625\u5efa\u8bf4\uff0c\u201c\u4ed6\u662f\u6211\u8868\u54e5\uff0c\u4ed6\u73b0\u5728\u624d\u5728\u6e05\u534e\u8bfb\u5927\u4e09\uff0c\u4f60\u90fd\u7814\u7a76\u751f\u6bd5\u4e1a\u4e86\uff0c\u600e\u4e48\u53ef\u80fd\u8ba4\u8bc6\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u4e01\u6625\u5efa\u6123\u4e86\u534a\u54cd\uff0c\u4ed6\u54ea\u91cc\u80fd\u60f3\u5230\u5230\u6d77\u5dde\u6765\u627e\u4ed6\u7684\u5b66\u5f1f\u7adf\u662f\u5927\u8001\u677f\u7684\u8868\u54e5\uff0c\u5c82\u4e0d\u662f\u8bf4\u5927\u8001\u677f\u6bd4\u6881\u6587\u6c5f\u8fd8\u8981\u5e74\u8f7b\uff1f</p><p>\u3000\u3000\u65bd\u6625\u950b\u6bd5\u7adf\u79bb\u5f00\u7231\u8fbe\u7535\u5b50\u4e86\uff0c\u5f20\u606a\u540e\u6765\u8fd8\u627e\u4ed6\u501f\u9b54\u517d\u4e89\u9738\u7684\u6c49\u5316\u7248\u6e38\u620f\u5149\u76d8\uff0c\u4e4d\u770b\u5230\u5f20\u606a\u5728\u573a\uff0c\u518d\u52a0\u4e0a\u672c\u8eab\u5c31\u662f\u751f\u6027\u6563\u6de1\u7684\u4eba\uff0c\u4e5f\u6ca1\u6709\u592a\u62d8\u675f\uff0c\u5f20\u606a\u95ee\u4e01\u6625\u5efa\u8bdd\uff0c\u4e01\u6625\u5efa\u8be7\u5f02\u4e4b\u4e0b\u5fd8\u4e86\u56de\u7b54\uff0c\u4ed6\u66ff\u4e01\u6625\u5efa\u8bf4\u9053\uff1a\u201c\u6e05\u534e\u8ba1\u7b97\u673a\u7cfb\uff0c\u53ea\u8981\u6709\u7279\u522b\u51fa\u8272\u7684\u5b66\u751f\uff0c\u4e0d\u7ba1\u662f\u672c\u79d1\u751f\u8fd8\u662f\u4ec0\u4e48\uff0c\u4e5f\u4e0d\u7ba1\u662f\u4e0d\u662f\u521a\u5165\u5b66\uff0c\u90fd\u6709\u53ef\u80fd\u7ed9\u5bfc\u5e08\u62c9\u8fc7\u53bb\u8ddf\u7740\u505a\u9879\u76ee\uff0c\u6881\u6587\u6c5f\u5927\u4e00\u7684\u65f6\u5019\u8fd8\u5e2e\u8fc7\u6211\u7684\u5fd9\uff0c\u4e0d\u8fc7\u6700\u5e38\u7ed9\u4e01\u6625\u5efa\u7684\u5bfc\u5e08\u62c9\u8fc7\u53bb\u5f53\u725b\u505a\u9a6c\uff0c\u6240\u4ee5\u8ba4\u8bc6\u3002\u53ea\u662f\u2026\u2026\u6881\u6587\u6c5f\u771f\u662f\u4f60\u7684\u8868\u54e5\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0a\u5c0f\u5b66\u65f6\u53bb\u8fc7\u897f\u5b89\uff0c\u5230\u5c0f\u8205\u5355\u4f4d\u91cc\u53bb\u73a9\uff0c\u770b\u5230\u4ed6\u8fd9\u8f88\u5b50\u4ee5\u6765\u770b\u5230\u7684\u6700\u53e4\u8001\u7684\u8ba1\u7b97\u673a\uff0c\u7f16\u7a0b\u7adf\u662f\u7528\u6253\u5b54\u5668\u5728\u7eb8\u7247\u4e0a\u6253\u5b54\uff0c\u7136\u540e\u7528\u8bfb\u5361\u5668\u624d\u80fd\u8fd0\u884c\u7a0b\u5e8f\uff0c\u6881\u6587\u5c71\u3001\u6881\u6587\u6c5f\u5012\u662f\u4ece\u5c0f\u5c31\u5b66\u8fc7\u8fd9\u79cd\u53e4\u8001\u7684\u8ba1\u7b97\u673a\uff0c\u8ba1\u7b97\u673a\u7684\u57fa\u7840\u5f88\u662f\u6df1\u539a\u3002\u5f20\u606a\u4ece\u5c0f\u5c31\u77e5\u9053\u6881\u6587\u6c5f\u662f\u505a\u5b66\u95ee\u7684\u6599\uff0c\u5012\u6ca1\u6709\u60f3\u5230\u4ed6\u5728\u6e05\u534e\u8ba1\u7b97\u7cfb\u86ee\u6709\u540d\u6c14\u7684\uff0c\u53ea\u662f\u4ed6\u540e\u6765\u53bb\u7f8e\u56fd\u7559\u5b66\u3001\u5b9a\u5c45\uff0c\u5de5\u4f5c\u3001\u751f\u6d3b\u7686\u4e0d\u9519\uff0c\u5728\u5b66\u6811\u4e0a\u5012\u6ca1\u6709\u7279\u522b\u8000\u773c\u7684\u5efa\u6811\u3002\u4e0d\u8fc7\u8bdd\u4e5f\u8bf4\u56de\u6765\uff0c\u673a\u7f18\u771f\u7684\u662f\u5f88\u91cd\u8981\u7684\uff0c\u62ff\u4e01\u6625\u5efa\u6765\u8bf4\uff0c\u8981\u4e0d\u662f\u673a\u7f18\u5de7\u5408\uff0c\u4ed6\u7814\u7a76\u7684IDE\u8f6cUSB\u6280\u672f\u5c31\u4f1a\u4f5c\u4e3a\u6bd5\u4e1a\u8bba\u4e1a\u62ff\u5230\u4e13\u4e1a\u6742\u5fd7\u4e0a\u516c\u5f00\u53d1\u8868\uff0c\u6210\u4e3a\u96f6\u96f6\u5e74\u4e4b\u540e\u4f18\u76d8\u4e13\u5229\u4e4b\u4e89\u7684\u5bfc\u706b\u7d22\uff0c\u800c\u5c31\u4e01\u6625\u5efa\u672c\u4eba\u65e5\u540e\u4f3c\u4e4e\u4e0e\u95ea\u76d8\u4e13\u5229\u4e4b\u4e89\u6beb\u65e0\u74dc\u845b\u3002</p><p>\u3000\u3000\u6b64\u65f6IDE\u8f6cUSE\u6280\u672f\u4e0e\u5176\u4ed6\u7231\u8fbe\u7535\u5b50\u7814\u53d1\u51fa\u6765\u7684\u6280\u672f\u4e00\u8d77\u7531\u7231\u8fbe\u7535\u5b50\u6cd5\u52a1\u90e8\u5411\u5168\u7403\u4e3b\u8981\u56fd\u5bb6\u7533\u8bf7\u4e13\u5229\u800c\u6210\u4e3a\u7231\u8fbe\u7535\u5b50\u7684\u4e13\u5229\u6280\u672f\u3002\u5373\u4f7f\u7231\u8fbe\u7535\u5b50\u624b\u91cc\u6ca1\u6709\u4f18\u76d8\u53e6\u4e00\u79cd\u5173\u952e\u6280\u672f\uff08\u95ea\u5b58\u4e13\u5229\uff09\uff0c\u4f46\u662f\u62e5\u6709\u63a7\u5236\u90e8\u4ef6\u7684\u4e13\u5229\u6280\u672f\u4ee5\u53ca\u5176\u4ed6\u4e00\u4e9b\u5916\u5f62\u3001\u529f\u80fd\u8bbe\u8ba1\u7b49\u6b21\u8981\u4e13\u5229\u6280\u672f\uff0c\u4e5f\u8db3\u4ee5\u5728\u65e5\u540e\u5feb\u901f\u50a8\u5b58\u5668\u5e02\u573a\u53d1\u5c55\u8d77\u6765\u4e4b\u540e\u5360\u636e\u5f88\u5927\u7684\u4e3b\u52a8\u3002</p><p>\u3000\u3000\u201c\u662f\u554a\uff01\u201d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u770b\u7740\u5434\u5929\u5b9d\u4eb2\u81ea\u4ece\u5916\u9762\u642c\u6905\u5b50\u8fc7\u6765\uff0c\u7ad9\u8d77\u6765\u5e2e\u4ed6\u7684\u5fd9\uff0c\u201c\u86ee\u5de7\u7684\uff0c\u90a3\u5c31\u4e00\u8d77\u5403\u996d\u5427\u3002\u201d</p><p>\u3000\u3000\u4e01\u6625\u5efa\u3001\u65bd\u6625\u950b\u81ea\u7136\u4e0d\u4f1a\u62d2\u7edd\u5f20\u606a\u7684\u9080\u8bf7\uff0c\u53ea\u662f\u6881\u6587\u6c5f\u6709\u4e9b\u53d1\u6123\uff0c\u8fd9\u4e48\u4e45\u8fd8\u6ca1\u6709\u6d88\u5316\u6389\u8fd9\u4e2a\u8ba9\u4eba\u5403\u60ca\u7684\u6d88\u606f\u3002</p><p>\u3000\u3000\u201c\u4ed6\u521a\u521a\u79f0\u5c0f\u606a\u4ec0\u4e48\uff1f\u4ec0\u4e48\u5927\u8001\u677f\uff1f\u201d\u6881\u56fd\u76db\u521a\u624d\u4e5f\u542c\u89c1\u4e86\uff0c\u53ea\u5f53\u542c\u9519\u4e86\uff0c\u542c\u7740\u5f20\u606a\u4e0e\u4e01\u6625\u5efa\u3001\u65bd\u6625\u950b\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u53c8\u7591\u60d1\u8d77\u6765\uff0c\u4f4e\u5934\u95ee\u4ed6\u59b9\u59b9\u6881\u683c\u73cd\u3002</p><p>\u3000\u3000\u201c\u5c0f\u8205\u2026\u2026\u201d\u5f20\u606a\u542c\u5230\u4ed6\u5c0f\u8205\u7684\u8bdd\uff0c\u5e2e\u4ed6\u4eec\u4ecb\u7ecd\u4e01\u6625\u5efa\u3001\u65bd\u6625\u950b\uff0c\u201c\u4e01\u6625\u5efa\u3001\u65bd\u6625\u950b\uff0c\u4ed6\u4eec\u662f\u6587\u6c5f\u8868\u54e5\u7684\u6e05\u534e\u540c\u5b66\uff0c\u6070\u597d\u4e01\u6625\u5efa\u5de5\u4f5c\u7684\u516c\u53f8\uff0c\u5f53\u5e74\u6211\u6709\u53c2\u52a0\u521b\u529e\uff0c\u4e5f\u7b97\u662f\u6295\u8d44\u4eba\u4e4b\u4e00\uff0c\u65bd\u6625\u950b\u4e4b\u524d\u4e5f\u5728\u90a3\u5bb6\u516c\u53f8\u5de5\u4f5c\uff0c\u73b0\u5728\u5230\u522b\u5bb6\u516c\u53f8\u53bb\u4e86\uff0c\u56e0\u4e3a\u6211\u7238\u7684\u5173\u7cfb\uff0c\u8fd9\u4e9b\u4e8b\u60c5\u5c31\u6ca1\u6709\u5411\u5916\u754c\u516c\u5f00\u8fc7\u2026\u2026\u201d</p><p>\u3000\u3000\u7ed9\u5f20\u606a\u8fd9\u4e48\u4ecb\u7ecd\uff0c\u65bd\u6625\u950b\u4e5f\u6ca1\u6709\u8df3\u69fd\u540e\u9047\u5230\u539f\u8001\u677f\u7684\u5c34\u5c2c\uff0c\u5f00\u59cb\u7ed9\u90b5\u81f3\u521a\u3001\u76db\u9752\u3001\u5f20\u77e5\u975e\u6316\u8d70\uff0c\u4ed6\u8fd8\u6709\u4e9b\u5185\u759a\u6765\u7740\uff0c\u540e\u6765\u53c8\u4e0e\u5f20\u606a\u63a5\u89e6\u8fc7\u51e0\u6b21\uff0c\u89c1\u5f20\u606a\u4e0e\u90b5\u81f3\u521a\u3001\u76db\u9752\u5173\u7cfb\u8fd9\u4e48\u597d\uff0c\u5f20\u77e5\u975e\u662f\u5f20\u606a\u7684\u5802\u53d4\uff0c\u6881\u519b\u662f\u5f20\u606a\u7684\u8868\u5144\uff0c\u624d\u77e5\u9053\u4ed6\u662f\u7ed9\u5f20\u606a\u6697\u4e2d\u5356\u51fa\u53bb\u7684\u3002</p><p>\u3000\u3000\u6881\u56fd\u76db\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u4e5f\u5341\u5206\u60ca\u8bb6\uff0c\u4e0d\u8fc7\u4e5f\u6ca1\u6709\u8ddf\u7231\u8fbe\u7535\u5b50\u8fd9\u6837\u7684\u5927\u516c\u53f8\u8054\u7cfb\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u606a\u8fd9\u4e48\u70b9\u5e74\u7eaa\u5c31\u5f00\u516c\u53f8\u4e86\uff0c\u5c0f\u8205\u8fd8\u62c5\u5fc3\u4f60\u5b66\u4e60\u6210\u7ee9\u4e0d\u597d\u4ee5\u540e\u600e\u4e48\u529e\u5462\u2026\u2026\u54c8\u54c8\u2026\u2026\u201d\u81ea\u987e\u81ea\u7684\u5148\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u6881\u6587\u5c71\u5230\u6d77\u5dde\u6765\u5c31\u6709\u5f88\u591a\u7684\u7591\u60d1\uff0c\u5230\u73b0\u5728\u624d\u91ca\u7591\u3002\u5728\u4ed6\u773c\u91cc\uff0c\u5c0f\u59d1\u5bb6\u7684\u6392\u573a\uff0c\u5df2\u7ecf\u8fdc\u8fdc\u8d85\u8fc7\u5c0f\u59d1\u592b\u5e02\u653f\u5e9c\u79d8\u4e66\u957f\u90a3\u4e00\u7ea7\u7684\u6863\u6b21\uff0c\u4e34\u6e56\u5c0f\u697c\uff0c\u4e24\u90e8\u8f66\uff0c\u4e00\u90e8\u94f6\u7070\u8272VOLVO\u8dd1\u8f66\uff0c\u4e00\u90e8\u5965\u8fea\uff0c\u5965\u8fea\u8f66\u662f\u519b\u533a\u7684\u519b\u724c\uff0cVOLVO\u5219\u662f\u6302\u7740\u201c\u6d77F12345\u201d\u8fd9\u6837\u7684\u7279\u6b8a\u53f7\u724c\uff1b\u4ed6\u4eec\u6628\u5929\u53ea\u662f\u591a\u559d\u4e86\u4e00\u4e9b\u9152\uff0c\u5c31\u8ba9\u533b\u751f\u62ff\u6602\u8d35\u7684\u8fdb\u53e3\u836f\u7269\u8fc7\u6765\u7ed9\u4ed6\u4eec\u6ce8\u5c04\u9192\u9152\uff0c\u867d\u7136\u6ca1\u6709\u523b\u610f\u7684\u5f20\u626c\uff0c\u4f46\u662f\u65e0\u5904\u4e0d\u900f\u9732\u7740\u903c\u4eba\u7684\u5bcc\u8d35\u3002</p></article>"}}}]);
