(window.webpackJsonp=window.webpackJsonp||[]).push([[367],{389:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u56db\u8282 \u5fc3\u6001\u95ee\u9898",content:"<article><h2>\u7b2c\u4e8c\u5341\u56db\u8282 \u5fc3\u6001\u95ee\u9898</h2><p>\u51db\u51c6\u5907\u9b54\u6cd5\u7684\u65b9\u5f0f\u9999\u8273\u6deb\u7cdc\uff0c\u8bf1\u4eba\u81f3\u6781\uff0c\u5982\u679c\u518d\u52a0\u4e0a\u6885\u83f2\u65af\u7684\u8bdd\u2026\u2026\u90a3\u526f\u753b\u9762\u53ea\u8981\u5728\u8111\u5b50\u91cc\u60f3\u60f3\u5c31\u8ba9\u4eba\u6fc0\u52a8\uff0c\u611f\u89c9\u5fc3\u8df3\u90fd\u8981\u52a0\u901f\u51e0\u500d\u3002\u53ef\u60dc\u743c\u6069\u7684\u53c2\u89c2\u7533\u8bf7\u88ab\u51db\u6beb\u4e0d\u72b9\u8c6b\u5730\u6253\u56de\u6765\uff0c\u53ea\u80fd\u770b\u7740\u4e24\u4f4d\u7f8e\u5c11\u5973\u624b\u62c9\u624b\u8fdb\u4e86\u623f\u95f4\uff0c\u81ea\u5df1\u7559\u4e0b\u6765\u6536\u62fe\u68cb\u5c40\u3002</p><p>\u201c\u592a\u8fc7\u5206\u4e86\uff0c\u4ee5\u524d\u662f\u4e0a\u6211\u7684\u5e8a\uff0c\u73b0\u5728\u76f4\u63a5\u628a\u6211\u4e22\u5230\u4e00\u8fb9\u4e86\u2026\u2026\u7531\u6b64\u53ef\u89c1\uff0c\u767e\u5408\u7edd\u5bf9\u662f\u4e16\u754c\u4e0a\u6700\u5927\u7684\u539f\u7f6a\u554a\uff01\u201d</p><p>\u505a\u7740\u6beb\u65e0\u610f\u4e49\u7684\u62b1\u6028\uff0c\u743c\u6069\u5c06\u5341\u516b\u679a\u68cb\u5b50\u4e00\u4e00\u62e3\u8d77\uff0c\u88c5\u8fdb\u4e2d\u7a7a\u7684\u9ed1\u6728\u68cb\u76d8\u5185\u90e8\u3002\u6885\u83f2\u65af\u7684\u90a3\u53e5\u6697\u793a\uff0c\u4ed6\u5176\u5b9e\u662f\u542c\u5230\u4e86\uff0c\u53ea\u662f\u4f9d\u65e7\u6709\u4e9b\u8ff7\u60d1\u3002\u51db\u5e76\u4e0d\u662f\u53ea\u4f1a\u7528\u706b\u7130\u9b54\u6cd5\u2014\u2014\u8fd9\u4e00\u70b9\u743c\u6069\u65e9\u5c31\u77e5\u9053\u4e86\uff0c\u51db\u662f\u7279\u522b\u7231\u7528\u706b\u7130\u7c7b\u578b\u7684\u9b54\u6cd5\u653b\u51fb\u4eba\uff0c\u4f46\u4e5f\u4f1a\u5728\u9b54\u6cd5\u523a\u9752\u91cc\u50a8\u5b58\u4e00\u4e9b\u7528\u4e8e\u8f85\u52a9\u548c\u9632\u5fa1\u7684\u6cd5\u672f\uff0c\u5c31\u50cf\u5728\u585e\u5c14\u7684\u65f6\u5019\uff0c\u5979\u66fe\u7ecf\u7528\u72c2\u98ce\u672f\u63a8\u52a8\u706b\u5899\u70e7\u6b7b\u5bf9\u624b\u7684\u53ec\u5524\u602a\u7269\uff0c\u4fbf\u662f\u5178\u578b\u7684\u4f8b\u5b50\u3002\u53ea\u662f\u4ec5\u4ec5\u5982\u6b64\u7684\u8bdd\uff0c\u4f3c\u4e4e\u4e5f\u5e76\u6ca1\u6709\u4ec0\u4e48\u9700\u8981\u7279\u522b\u5f3a\u8c03\u7684\u5427\u3002\u8fd8\u662f\u8bf4\uff0c\u51db\u660e\u5929\u4f1a\u6539\u53d8\u98ce\u683c\uff0c\u51c6\u5907\u5176\u4ed6\u7c7b\u578b\u7684\u653b\u51fb\u6cd5\u672f\uff1f\u8fd9\u4e0d\u592a\u53ef\u80fd\u3002\u51db\u662f\u4e2a\u5851\u80fd\u5e08\uff0c\u504f\u79d1\u975e\u5e38\u4e25\u91cd\uff0c\u5bf9\u4e8e\u5176\u4ed6\u5b66\u6d3e\u90fd\u4e0d\u751a\u4e86\u89e3\uff0c\u5373\u4fbf\u662f\u5851\u80fd\u5b66\u6d3e\u7684\u516d\u5927\u5206\u652f\uff0c\u5979\u4e5f\u53ea\u7cbe\u901a\u201c\u706b\u7130\u201d\u4e00\u7cfb\uff0c\u8fd9\u70b9\u743c\u6069\u5f88\u6e05\u695a\u3002\u5982\u679c\u653e\u5f03\u81ea\u5df1\u7684\u64c5\u957f\u9886\u57df\uff0c\u4e34\u65f6\u6539\u7528\u5176\u4ed6\u6cd5\u672f\uff0c\u5e76\u4e0d\u662f\u4ec0\u4e48\u660e\u667a\u4e4b\u4e3e\u3002</p><p>\u7b97\u4e86\uff0c\u4e0d\u60f3\u4e86\uff0c\u53cd\u6b63\u660e\u5929\u5c31\u89c1\u5206\u6653\u3002</p><p>\u6536\u62fe\u5b8c\u6bd5\uff0c\u56de\u5230\u81ea\u5df1\u623f\u95f4\u4f11\u606f\u3002\u6d1b\u65af\u517d\u76ae\u5236\u6210\u7684\u5e8a\u8925\u6e29\u6696\u67d4\u8f6f\uff0c\u743c\u6069\u8eba\u5728\u4e0a\u9762\u5374\u9887\u6709\u4e9b\u4e0d\u81ea\u5728\uff0c\u5e73\u8eba\u7740\u3001\u4fa7\u5367\u7740\u3001\u751a\u81f3\u5e72\u8106\u8db4\u7740\uff0c\u4e0d\u77e5\u9053\u4e3a\u4ec0\u4e48\uff0c\u603b\u89c9\u5f97\u54ea\u5730\u65b9\u4e0d\u592a\u5bf9\u52b2\u4f3c\u7684\uff0c\u6362\u4e86\u5404\u79cd\u7761\u59ff\uff0c\u7136\u800c\u7ffb\u6765\u8986\u53bb\u5c31\u662f\u7761\u4e0d\u7740\u3002\u60f3\u4e86\u534a\u5929\uff0c\u603b\u7b97\u660e\u767d\u8fc7\u6765\uff0c\u539f\u6765\u662f\u56e0\u4e3a\u6000\u91cc\u7a7a\u8361\u8361\u7684\uff0c\u5c11\u4e86\u4ec0\u4e48\u3002</p><p>\u8981\u8bf4\u8d77\u6765\uff0c\u743c\u6069\u8fd8\u662f\u5f88\u5e78\u798f\u7684\uff0c\u8eab\u8fb9\u603b\u6709\u7f8e\u5973\u966a\u4f34\uff0c\u5c0f\u65f6\u5019\u6709\u59d0\u59d0\u73ca\u5609\uff0c\u5728\u5deb\u5e08\u5b66\u6821\u91cc\u6709\u8299\u857e\u72c4\uff0c\u540e\u6765\u6709\u6885\u83f2\u65af\uff0c\u5728\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u65f6\u5019\u867d\u7136\u77ed\u6682\u548c\u6885\u83f2\u65af\u5206\u5f00\uff0c\u4f46\u8fd9\u4e2a\u7a7a\u6863\u4e5f\u6709\u8299\u857e\u72c4\u586b\u8865\u3002\u4e0d\u77e5\u4ece\u4ec0\u4e48\u65f6\u5019\u8d77\uff0c\u81ea\u5df1\u5df2\u7ecf\u4e60\u60ef\u4e86\u8fd9\u79cd\u751f\u6d3b\uff0c\u4e60\u60ef\u4e86\u6bcf\u5929\u665a\u4e0a\u7761\u89c9\u65f6\u62b1\u7740\u5973\u5b69\u5b50\u7684\u67d4\u8f6f\u7389\u4f53\uff0c\u7a81\u7136\u5931\u53bb\u7684\u8bdd\uff0c\u987f\u65f6\u5c31\u89c9\u5f97\u4e0d\u9002\u5e94\u8d77\u6765\u3002</p><p>\u597d\u5427\uff0c\u90a3\u5c31\u627e\u4e2a\u5973\u4eba\u53bb\u3002</p><p>\u8fd9\u5012\u5e76\u4e0d\u662f\u4ec0\u4e48\u8270\u5de8\u7684\u4efb\u52a1\u3002\u6668\u70bc\u57ce\u91cc\u5982\u4eca\u6027\u522b\u6bd4\u4f8b\u4e25\u91cd\u5931\u8c03\uff0c\u5973\u6027\u5360\u4e86\u603b\u4eba\u6570\u7684\u516b\u6210\u4ee5\u4e0a\uff0c\u800c\u4e14\u4e0d\u662f\u5353\u5c14\u5c31\u662f\u7cbe\u7075\uff0c\u90fd\u662f\u4ee5\u79c0\u4e3d\u7f8e\u8c8c\u8457\u79f0\u7684\u79cd\u65cf\uff0c\u81f3\u5c11\u4ee5\u4eba\u7c7b\u7684\u5ba1\u7f8e\u89c2\u800c\u8a00\u662f\u5982\u6b64\u3002\u7cbe\u7075\u5148\u4e0d\u8bf4\uff0c\u5353\u5c14\u5374\u662f\u5929\u6027\u6deb\u4e71\u653e\u8361\u7684\u79cd\u65cf\uff0c\u65e0\u8bba\u7f57\u4e1d\u7684\u4fe1\u5f92\u8fd8\u662f\u4f0a\u8389\u4e1d\u7fe0\u7684\u4fe1\u5f92\uff0c\u5728\u8fd9\u70b9\u4e0a\u90fd\u6ca1\u591a\u5c11\u5dee\u522b\uff0c\u5982\u4eca\u7537\u5c11\u5973\u591a\uff0c\u6b63\u5904\u4e8e\u6b32\u6c42\u4e0d\u6ee1\u7684\u9965\u6e34\u72b6\u6001\u3002\u5fb7\u53e4\u62c9\u7684\u6218\u7ee9\u4e4b\u6240\u4ee5\u8f89\u714c\uff0c\u9664\u4e86\u4e2a\u4eba\u9b45\u529b\u4e4b\u5916\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e5f\u6709\u73af\u5883\u7684\u56e0\u7d20\u5728\u5185\u3002\u743c\u6069\u5982\u679c\u613f\u610f\u7684\u8bdd\uff0c\u627e\u4e2a\u4e34\u65f6\u5e8a\u4f34\u5e76\u4e0d\u7b97\u4ec0\u4e48\u96be\u4e8b\uff0c\u53ea\u662f\u8fd9\u6837\u611f\u89c9\u592a\u5dee\uff0c\u5c31\u597d\u50cf\u662f\u53bb\u9152\u5427\u52fe\u642d\u5c0f\u59d0\u4f3c\u7684\uff0c\u6ca1\u4e0a\u7684\u65f6\u5019\u60f3\u4e0a\uff0c\u4e0a\u5b8c\u4e86\u53c8\u89c9\u5f97\u7d22\u7136\u65e0\u5473\uff0c\u53cd\u800c\u4f1a\u8ba9\u4eba\u5fc3\u60c5\u53d8\u7cdf\uff0c\u60f3\u60f3\u8fd8\u662f\u7b97\u4e86\u3002</p><p>\u61d2\u5f97\u5728\u5916\u9762\u627e\uff0c\u90a3\u5c31\u53ea\u80fd\u5bf9\u8eab\u8fb9\u7684\u4eba\u4e0b\u624b\uff0c\u6885\u83f2\u65af\u88ab\u51db\u62c9\u8d70\uff0c\u5269\u4e0b\u7684\u552f\u4e00\u9009\u62e9\uff0c\u4f3c\u4e4e\u4e5f\u5c31\u662f\u838e\u73de\u514b\u4e86\u3002\u4ee5\u524d\u5728\u70db\u5821\u90a3\u6b21\u4e0d\u7b97\uff0c\u81ea\u4ece\u6df1\u6e0a\u91cc\u91cd\u9022\u4ee5\u6765\uff0c\u4e5f\u5df2\u7ecf\u6709\u51e0\u4e2a\u6708\u4e86\uff0c\u5929\u5929\u90fd\u5728\u773c\u524d\u6643\u6765\u6643\u53bb\uff0c\u743c\u6069\u5374\u8fd8\u6ca1\u771f\u6b63\u63a8\u5012\u8fc7\u8fd9\u4e2a\u9b45\u9b54\uff0c\u5012\u662f\u7ecf\u5e38\u5582\u5979\u559d\u725b\u5976\uff0c\u8bf4\u8d77\u6765\u771f\u662f\u5947\u602a\uff0c\u81ea\u5df1\u90fd\u4e0d\u77e5\u9053\u81ea\u5df1\u5fc3\u91cc\u662f\u600e\u4e48\u60f3\u7684\u3002</p><p>\u201c\u96be\u4e0d\u6210\u662f\u6f02\u4eae\u7f8e\u4eba\u770b\u5f97\u592a\u591a\uff0c\u5df2\u7ecf\u5fc3\u5982\u6b62\u6c34\u4e86\uff1f\u5e72\uff0c\u4e0d\u81f3\u4e8e\u5427\uff0c\u6211\u8fd8\u6b63\u5e74\u8f7b\u554a\u3002\u201d</p><p>\u6447\u6447\u5934\uff0c\u628a\u8fd9\u4e9b\u4e71\u4e03\u516b\u7cdf\u7684\u601d\u7eea\u6254\u5230\u4e00\u8fb9\uff0c\u743c\u6069\u51b3\u5b9a\u4eca\u665a\u53bb\u5728\u9b45\u9b54\u8eab\u4e0a\u8bc1\u660e\u4e00\u4e0b\u81ea\u5df1\u7684\u9752\u6625\u6d3b\u529b\u3002\u4ed6\u8d77\u8eab\u4e0b\u5e8a\uff0c\u62ab\u4e0a\u5916\u5957\uff0c\u7a7f\u8fc7\u957f\u5eca\u8d70\u5230\u5c3d\u5934\uff0c\u5c48\u8d77\u624b\u6307\u8f7b\u8f7b\u6572\u4e86\u6572\u95e8\uff0c\u7136\u540e\u542c\u89c1\u4e86\u9b45\u9b54\u7684\u58f0\u97f3\u3002</p><p>\u201c\u8bf7\u8fdb\u3002\u201d\u5979\u8bf4\u3002</p><p>\u63a8\u95e8\u8fdb\u5165\uff0c\u9b45\u9b54\u6b63\u61d2\u61d2\u6563\u6563\u5730\u9760\u5728\u5e8a\u5934\uff0c\u817f\u4e0a\u653e\u7740\u4e00\u526f\u9752\u84dd\u8272\u7684\u76ae\u7532\uff0c\u89c1\u743c\u6069\u8fdb\u6765\uff0c\u5fae\u5fae\u70b9\u5934\u793a\u610f\uff0c\u5374\u6ca1\u8bf4\u8bdd\u3002\u8d70\u5230\u8fd1\u524d\uff0c\u53d1\u73b0\u5979\u8138\u8272\u6709\u4e9b\u9eef\u6de1\uff0c\u50cf\u662f\u5f88\u75b2\u5026\u7684\u6a21\u6837\uff0c\u5de6\u624b\u62ff\u7740\u4e00\u67c4\u5c0f\u5200\uff0c\u53f3\u81c2\u4e0a\u6709\u4e03\u516b\u9053\u89e6\u76ee\u60ca\u5fc3\u7684\u4f24\u75d5\uff0c\u9c9c\u8840\u6dcb\u6f13\uff0c\u663e\u7136\u662f\u521a\u521a\u5272\u5f00\u7684\u3002</p><p>\u7c7b\u4f3c\u7684\u60c5\u5f62\u743c\u6069\u89c1\u8fc7\u591a\u6b21\uff0c\u5df2\u7ecf\u4e60\u4ee5\u4e3a\u5e38\uff0c\u5e76\u4e0d\u60ca\u8bb6\uff0c\u53ea\u662f\u9759\u9759\u65c1\u89c2\u3002\u8fc7\u4e86\u7247\u523b\uff0c\u838e\u73de\u514b\u4e3e\u5200\u5728\u81ea\u5df1\u7684\u53f3\u624b\u8155\u90e8\u53c8\u8f7b\u8f7b\u5212\u4e86\u4e00\u5200\uff0c\u53e3\u4e2d\u5ff5\u8bf5\u6076\u9b54\u7684\u5492\u6587\u3002\u8840\u4ece\u4f24\u53e3\u5904\u6ea2\u51fa\uff0c\u5374\u4e0d\u662f\u70b9\u70b9\u6ef4\u843d\uff0c\u800c\u662f\u51dd\u6210\u4e00\u9053\u7ec6\u957f\u7684\u8840\u7ebf\uff0c\u4eff\u4f5b\u6709\u751f\u547d\u7684\u86db\u4e1d\u4e00\u822c\u6e38\u52a8\u5230\u76ae\u7532\u4e0a\uff0c\u873f\u8712\u76d8\u7ed5\uff0c\u6e17\u900f\u5176\u4e2d\u3002\u968f\u7740\u9c9c\u8840\u7684\u4e0d\u65ad\u6d8c\u51fa\uff0c\u838e\u73de\u514b\u7684\u8138\u8272\u4e5f\u8d8a\u6765\u8d8a\u82cd\u767d\uff0c\u7d2b\u8272\u523a\u9752\u81ea\u53cc\u81c2\u4e0a\u6e05\u6670\u6d6e\u73b0\uff0c\u4eff\u4f5b\u8109\u7edc\u4e00\u822c\u3002\u6574\u4e2a\u8fc7\u7a0b\u5927\u7ea6\u6301\u7eed\u4e86\u4e03\u516b\u5206\u949f\uff0c\u6700\u7ec8\u9b45\u9b54\u624b\u8155\u4f24\u53e3\u5904\u505c\u6b62\u6d41\u8840\uff0c\u4e00\u526f\u72f0\u72de\u7684\u8840\u7ea2\u8272\u7f8a\u89d2\u9ab7\u9ac5\u5934\u9aa8\u5728\u76ae\u7532\u7684\u80f8\u53e3\u90e8\u4f4d\u6162\u6162\u6d6e\u73b0\u51fa\u6765\uff0c\u505c\u7559\u4e86\u51e0\u79d2\u949f\u540e\u53c8\u518d\u5ea6\u6d88\u5931\u3002</p><p>\u201c\u5b8c\u6210\u4e86\uff1f\u201d\u743c\u6069\u95ee\u3002</p><p>\u201c\u7b97\u662f\u5427\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u81f3\u5c11\u5341\u5929\u534a\u6708\u662f\u6ca1\u95ee\u9898\u4e86\u3002\u201d</p><p>\u8fd9\u4ef6\u9752\u84dd\u8272\u7684\u76ae\u7532\u662f\u838e\u73de\u514b\u5728\u65ad\u57df\u9547\u7684\u65f6\u5019\u82b1\u91cd\u91d1\u8d2d\u5f97\uff0c\u51fa\u81ea\u65e0\u5c3d\u6df1\u6e0a\u7b2c\u4e09\u767e\u56db\u5341\u516b\u5c42\u9886\u4e3b\u5854\u82ac\u4e4b\u624b\u3002\u5854\u82ac\u662f\u4e2a\u5f3a\u5927\u7684\u6076\u9b54\u9886\u4e3b\uff0c\u7cbe\u901a\u5404\u79cd\u90aa\u6076\u7684\u201c\u827a\u672f\u201d\uff0c\u6700\u559c\u6b22\u505a\u7684\u4e8b\u60c5\u4fbf\u662f\u5c06\u654c\u4eba\u7684\u76ae\u80a4\u751f\u751f\u5265\u4e0b\uff0c\u5236\u6210\u7cbe\u7f8e\u7684\u76ae\u7532\uff0c\u5927\u90e8\u4efd\u7559\u4f5c\u81ea\u5df1\u6536\u85cf\uff0c\u6709\u65f6\u5019\u4e5f\u4f1a\u5bf9\u5916\u51fa\u552e\u6216\u8005\u8d60\u9001\u3002\u838e\u73de\u514b\u624b\u91cc\u7684\u8fd9\u526f\u662f\u7528\u5df4\u6d1b\u708e\u9b54\u3001\u5224\u9b42\u9b54\u548c\u5df4\u5e03\u9b54\u7684\u76ae\u6df7\u5408\u5236\u6210\uff0c\u867d\u7136\u7b97\u4e0d\u4e0a\u6700\u9876\u7ea7\u4f18\u79c0\u7684\u4f5c\u54c1\uff0c\u4f46\u4e5f\u5df2\u7ecf\u4e0d\u5dee\uff0c\u4e0d\u4f46\u575a\u56fa\u7ed3\u5b9e\uff0c\u800c\u4e14\u53ef\u4ee5\u5927\u5e45\u5ea6\u524a\u51cf\u95ea\u7535\u3001\u706b\u7130\u3001\u5bd2\u51b0\u7c7b\u578b\u7684\u4f24\u5bb3\uff0c\u5bf9\u9b54\u6cd5\u4e5f\u6709\u4e00\u5b9a\u7684\u62b5\u5fa1\u6548\u679c\u3002\u4f46\u5b83\u7ec8\u7a76\u662f\u4e2a\u90aa\u7269\uff0c\u5fc5\u987b\u5b9a\u671f\u4ee5\u7cbe\u8840\u5582\u9972\uff0c\u5426\u5219\u5c31\u4f1a\u9010\u6e10\u7834\u8d25\uff0c\u6700\u7ec8\u5b8c\u5168\u4e27\u5931\u6548\u529b\u3002\u6076\u9b54\u7684\u7269\u54c1\u5927\u591a\u90fd\u5982\u6b64\uff0c\u8fd9\u4e5f\u7b97\u662f\u5e38\u6001\u4e86\u3002</p><p>\u56e0\u4e3a\u73b0\u5728\u662f\u5728\u7269\u8d28\u754c\uff0c\u6076\u9b54\u7684\u80fd\u529b\u666e\u904d\u6536\u5230\u538b\u5236\uff0c\u838e\u73de\u514b\u624b\u81c2\u4e0a\u7684\u4f24\u53e3\u8fdf\u8fdf\u4e0d\u80fd\u6108\u5408\uff0c\u743c\u6069\u6478\u51fa\u8428\u74e6\u68cb\uff0c\u63b7\u4e0b\u4e00\u679a\u201c\u5973\u796d\u53f8\u201d\uff0c\u8ba9\u5979\u65bd\u5c55\u4e86\u4e24\u4e2a\u6cbb\u7597\u672f\uff0c\u77ac\u95f4\u4fbf\u6062\u590d\u5982\u521d\uff0c\u808c\u80a4\u5149\u6d01\u7ec6\u817b\uff0c\u4eff\u4f5b\u6b64\u524d\u7684\u5200\u75d5\u5168\u662f\u5e7b\u89c9\u3002\u5f53\u7136\uff0c\u6cbb\u7597\u672f\u5e76\u4e0d\u662f\u4e07\u80fd\u7684\uff0c\u5b83\u80fd\u8ba9\u4f24\u53e3\u75ca\u6108\uff0c\u5374\u4e0d\u53ef\u80fd\u8ba9\u5931\u53bb\u7684\u8840\u91cd\u65b0\u6d41\u56de\uff0c\u66f4\u4e0d\u53ef\u80fd\u77ac\u95f4\u518d\u751f\u51fa\u6765\uff0c\u6240\u4ee5\u838e\u73de\u514b\u7684\u8138\u8272\u4f9d\u65e7\u82cd\u767d\uff0c\u989d\u5934\u4e0a\u6c57\u73e0\u6ef4\u6ef4\u6eda\u843d\uff0c\u975e\u5e38\u865a\u5f31\u3002\u743c\u6069\u4ece\u65c1\u8fb9\u62ff\u8fc7\u4e00\u6761\u6bdb\u5dfe\uff0c\u66ff\u5979\u8f7b\u8f7b\u64e6\u62ed\u3002</p><p>\u201c\u65e9\u70b9\u4f11\u606f\u5427\u3002\u201d\u4ed6\u8bf4\uff0c\u6536\u56de\u68cb\u5b50\u51c6\u5907\u79bb\u5f00\u3002</p><p>\u201c\u4f60\u603b\u4e0d\u4f1a\u662f\u4e13\u7a0b\u6765\u770b\u671b\u6211\u7684\u5427\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u201c\u65e2\u7136\u6765\u4e86\uff0c\u53c8\u4f55\u5fc5\u8d70\uff0c\u201d\u5979\u62cd\u4e86\u62cd\u8eab\u8fb9\u7684\u5e8a\u5355\uff0c\u201c\u966a\u6211\u5750\u4f1a\u597d\u4e48\u3002\u201d</p><p>\u743c\u6069\u5fc3\u60f3\u53cd\u6b63\u56de\u53bb\u4e5f\u662f\u7761\u4e0d\u7740\uff0c\u804a\u804a\u5929\u4e5f\u597d\uff0c\u70b9\u4e86\u70b9\u5934\uff0c\u5728\u65c1\u8fb9\u5750\u4e86\u4e0b\u6765\u3002\u838e\u73de\u514b\u6536\u8d77\u76ae\u7532\uff0c\u4fa7\u8138\u671d\u4ed6\u770b\u4e86\u770b\uff0c\u201c\u4f60\u90a3\u4e24\u4f4d\u5c0f\u60c5\u4eba\u628a\u4f60\u8d76\u51fa\u6765\u4e86\uff1f\u201d</p><p>\u201c\u5443\uff0c\u867d\u7136\u4e0d\u60f3\u627f\u8ba4\uff0c\u4e0d\u8fc7\u7b97\u662f\u5427\u3002\u201d</p><p>\u201c\u6211\u8fd8\u4ee5\u4e3a\u4f60\u65e9\u5df2\u7ecf\u89e3\u51b3\u8fd9\u4e2a\u9ebb\u70e6\u4e86\u3002\u201d</p><p>\u201c\u6211\u4e5f\u66fe\u7ecf\u8fd9\u4e48\u4ee5\u4e3a\uff0c\u4f46\u73b0\u5728\u770b\u8d77\u6765\u5e76\u975e\u5982\u6b64\u3002\u201d</p><p>\u201c\u4e3a\u4ec0\u4e48\u4f60\u603b\u662f\u8fd9\u4e48\u4e0d\u5e78\u5462\u3002\u201d</p><p>\u201c\u8fd9\u4e2a\u95ee\u9898\u2026\u2026\u6211\u4e5f\u6b63\u5728\u601d\u8003\u3002\u201d</p><p>\u838e\u73de\u514b\u8f7b\u8f7b\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u81ea\u627e\u7684\u3002\u201d\u5979\u4e0b\u65ad\u8a00\u3002</p><p>\u201c\u8fd9\u4e48\u8bf4\u6709\u70b9\u8fc7\u5206\u5427\u3002\u201d</p><p>\u201c\u8fc7\u5206\u5417\uff0c\u6211\u4e0d\u89c9\u5f97\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u201c\u56e0\u4e3a\u4f60\u5b8c\u5168\u53ef\u4ee5\u62d2\u7edd\u51db\u7684\uff0c\u5bf9\u5427\u3002\u4f60\u6709\u8fd9\u4e2a\u6743\u5229\uff0c\u800c\u4e14\u6211\u4eec\u90fd\u6e05\u695a\uff0c\u5982\u679c\u4f60\u575a\u6301\u7684\u8bdd\uff0c\u6885\u83f2\u65af\u6700\u7ec8\u662f\u4f1a\u542c\u4f60\u7684\uff0c\u8fd9\u70b9\u6ca1\u9519\u5427\u3002\u201d</p><p>\u201c\u5e94\u8be5\u662f\u6ca1\u9519\u3002\u201d</p><p>\u201c\u90a3\u5c31\u662f\u4e86\u3002\u6240\u4ee5\u8bf4\uff0c\u5176\u5b9e\u4f60\u5b8c\u5168\u53ef\u4ee5\u907f\u514d\u8fd9\u79cd\u5c40\u9762\uff0c\u4f60\u6709\u8fd9\u4e2a\u6743\u5229\u548c\u80fd\u529b\uff0c\u4f46\u5374\u81ea\u5df1\u653e\u5f03\u4e86\u2014\u2014\u8fd9\u4e0d\u662f\u81ea\u627e\u7684\uff0c\u53c8\u662f\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\u4e48\uff0c\u600e\u4e48\u8bf4\u5462\u3002\u53cd\u6b63\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5927\u4e8b\uff0c\u65e2\u7136\u51db\u63d0\u51fa\u6765\u4e86\uff0c\u827e\u5f25\u8587\u663e\u7136\u4e5f\u540c\u610f\u4e86\uff0c\u90a3\u4e48\u6211\u53c8\u4f55\u5fc5\u5f3a\u786c\u53cd\u5bf9\uff0c\u7ed3\u679c\u4e0d\u662f\u4f1a\u5f04\u5f97\u5927\u5bb6\u90fd\u4e0d\u6109\u5feb\u4e48\u3002\u201d</p><p>\u838e\u73de\u514b\u8f7b\u58f0\u4f4e\u7b11\uff0c\u201c\u6ca1\u9519\uff0c\u8fd9\u5c31\u662f\u95ee\u9898\u7684\u5173\u952e\u6240\u5728\u2014\u2014\u4f60\u5e72\u561b\u8981\u7ba1\u5979\u4eec\u9ad8\u5174\u4e0d\u9ad8\u5174\u5462\uff1f\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u9b45\u9b54\u8fd9\u53e5\u8bdd\u5927\u6982\u95ee\u5f97\u592a\u8fc7\u7406\u6240\u5f53\u7136\uff0c\u4ee5\u81f3\u4e8e\u743c\u6069\u4e00\u65f6\u95f4\u90fd\u7ed9\u6014\u4f4f\u4e86\u3002\u201c\u4f60\u5230\u5e95\u4ec0\u4e48\u610f\u601d\uff1f\u201d\u4ed6\u53cd\u95ee\uff0c\u201c\u6211\u4e0d\u592a\u660e\u767d\u3002\u201d</p><p>\u201c\u6ca1\u4ec0\u4e48\u610f\u601d\uff0c\u6211\u53ea\u662f\u60f3\u8bf4\uff1a\u987e\u53ca\u4ed6\u4eba\u7684\u611f\u53d7\uff0c\u5bb9\u5fcd\u3001\u9000\u8ba9\u3001\u59a5\u534f\uff0c\u8fd9\u662f\u666e\u901a\u4eba\u7684\u5fc3\u6001\uff0c\u5f88\u6b63\u5e38\uff0c\u5f88\u5408\u7406\uff0c\u65e0\u53ef\u975e\u8bae\u3002\u53ef\u662f\u4e3b\u4eba\uff0c\u4f60\u5df2\u7ecf\u4e0d\u662f\u4e00\u4e2a\u666e\u901a\u4eba\u4e86\uff0c\u201d\u838e\u73de\u514b\u8bf4\uff0c\u201c\u4f60\u662f\u4e2a\u4f18\u79c0\u7684\u5deb\u5e08\uff0c\u7ad9\u5728\u8fd9\u4e2a\u4e16\u754c\u7684\u9876\u7aef\uff0c\u4f60\u6709\u529b\u91cf\uff0c\u6709\u5730\u4f4d\uff0c\u6709\u6743\u529b\uff0c\u4f46\u5374\u4f3c\u4e4e\u8fd8\u6ca1\u6709\u4e0e\u4e4b\u76f8\u79f0\u7684\u5fc3\u6001\u3002\u8fd9\u4e00\u70b9\u5f88\u5371\u9669\uff0c\u5982\u679c\u4e0d\u505a\u6539\u53d8\u7684\u8bdd\uff0c\u4e3b\u4eba\uff0c\u6211\u60f3\u4f60\u6ce8\u5b9a\u4e00\u4e8b\u65e0\u6210\u3002\u201d</p><p>\u201c\u8bf4\u5f97\u672a\u514d\u592a\u4e25\u91cd\u4e86\u5427\uff0c\u201d\u743c\u6069\u7b11\u7b11\uff0c\u6709\u4e9b\u4e0d\u4ee5\u4e3a\u7136\uff0c\u201c\u4e00\u70b9\u5c0f\u4e8b\uff0c\u7528\u4e0d\u7740\u8fd9\u4e48\u4e0a\u7eb2\u4e0a\u7ebf\u3002\u201d</p><p>\u201c\u89c1\u5fae\u77e5\u8457\u561b\uff0c\u201d\u838e\u73de\u514b\u683c\u683c\u5a07\u7b11\uff0c\u201c\u5f53\u7136\u4e86\uff0c\u4f60\u53ef\u4ee5\u8ba4\u4e3a\u8fd9\u662f\u5728\u5371\u8a00\u8038\u542c\uff0c\u6bd5\u7adf\uff0c\u6211\u662f\u4e2a\u6076\u9b54\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u548c\u5f80\u5e38\u4e00\u6837\uff0c\u743c\u6069\u5728\u6f5c\u610f\u8bc6\u91cc\u62d2\u7edd\u76f8\u4fe1\u838e\u73de\u514b\uff0c\u672c\u80fd\u5730\u89c9\u5f97\u662f\u6076\u9b54\u5728\u86ca\u60d1\u4eba\u5fc3\uff0c\u6311\u62e8\u79bb\u95f4\u3002\u4f46\u4e0d\u5f97\u4e0d\u627f\u8ba4\u7684\u662f\uff0c\u5979\u6bcf\u6b21\u8bf4\u7684\u8bdd\uff0c\u603b\u662f\u8ba9\u4eba\u5f88\u96be\u8f7b\u6613\u5fd8\u8bb0\uff0c\u5c31\u50cf\u9634\u5f71\u4e00\u6837\uff0c\u65f6\u4e0d\u65f6\u4ece\u8111\u4e2d\u95ea\u8fc7\uff0c\u9759\u6084\u6084\u6d6e\u4e0a\u5fc3\u5934\u3002</p><p>\u6487\u5f00\u8fd9\u70b9\u4e0d\u8bba\uff0c\u9b45\u9b54\u786e\u5b9e\u662f\u7406\u60f3\u7684\u5e8a\u4f34\uff0c\u4ee5\u5bb9\u8c8c\u800c\u8bba\uff0c\u5979\u6bd4\u6885\u83f2\u65af\u548c\u51db\u900a\u8272\u4e00\u4e9b\uff0c\u4f46\u8eab\u6750\u5374\u582a\u79f0\u662f\u5b8c\u7f8e\uff0c\u524d\u51f8\u540e\u7fd8\uff0c\u4e30\u8174\u8089\u611f\uff0c\u62b1\u5728\u6000\u4e2d\u5c31\u50cf\u4e00\u56e2\u6e29\u9999\u8f6f\u7389\uff0c\u5904\u5904\u5408\u624b\uff0c\u5341\u5206\u8212\u9002\uff0c\u534a\u70b9\u820d\u4e0d\u5f97\u653e\u5f00\u3002\u5373\u4fbf\u4ec0\u4e48\u90fd\u4e0d\u505a\uff0c\u53ea\u662f\u9759\u9759\u62b1\u7740\uff0c\u611f\u89c9\u5c31\u5df2\u7ecf\u975e\u5e38\u597d\u4e86\u3002</p><p>\u4e00\u591c\u65e0\u8bdd\uff0c\u5f53\u743c\u6069\u518d\u6b21\u9192\u6765\u65f6\uff0c\u53ea\u89c9\u5f97\u795e\u6e05\u6c14\u723d\uff0c\u524d\u6bb5\u65f6\u95f4\u79ef\u7d2f\u4e0b\u6765\u7684\u75b2\u5026\u4e00\u626b\u800c\u7a7a\u3002\u770b\u770b\u6c99\u6f0f\uff0c\u5df2\u7ecf\u662f\u7b2c\u4e8c\u5929\u65e9\u4e0a\u516b\u70b9\u949f\uff0c\u8eab\u65c1\u7684\u838e\u73de\u514b\u8fd8\u5728\u719f\u7761\uff0c\u743c\u6069\u4e5f\u4e0d\u60ca\u52a8\uff0c\u6084\u6084\u8d77\u8eab\u4e0b\u5e8a\u7a7f\u8863\uff0c\u8d70\u51fa\u95e8\u53bb\u3002</p><p>\u8d70\u8fc7\u51db\u7684\u623f\u95f4\uff0c\u53d1\u73b0\u623f\u95e8\u7d27\u95ed\uff0c\u91cc\u9762\u6084\u7136\u65e0\u58f0\uff0c\u743c\u6069\u539f\u672c\u4ee5\u4e3a\u4e24\u4f4d\u5c11\u5973\u8fd8\u672a\u8d77\u5e8a\uff0c\u8d70\u5230\u5ba2\u5385\uff0c\u53d1\u73b0\u6885\u83f2\u65af\u548c\u51db\u5df2\u7ecf\u5750\u5728\u684c\u8fb9\u5bf9\u4ed8\u65e9\u9910\u3002\u770b\u89c1\u4ed6\u8d70\u8fc7\u6765\uff0c\u51db\u628a\u53e3\u4e2d\u7684\u534a\u5757\u9762\u5305\u541e\u4e0b\uff0c\u542b\u542b\u7cca\u7cca\u6253\u4e86\u4e2a\u62db\u547c\u3002</p><p>\u201c\u54a6\uff0c\u51db\uff0c\u4f60\u4eca\u5929\u600e\u4e48\u8d77\u5f97\u8fd9\u4e48\u65e9\uff0c\u201d\u743c\u6069\u6709\u4e9b\u8be7\u5f02\uff0c\u201c\u5e73\u5e38\u8fd9\u65f6\u5019\uff0c\u5e94\u8be5\u90fd\u8fd8\u5728\u7761\u61d2\u89c9\u5427\u3002\u201d</p><p>\u201c\u56e0\u4e3a\u6211\u4e00\u60f3\u8d77\u4eca\u5929\u53ef\u4ee5\u597d\u597d\u6559\u8bad\u4f60\uff0c\u5c31\u5174\u594b\u5f97\u7761\u4e0d\u7740\u4e86\u5440\u3002\u201d</p><p>\u201c\u8fd9\u4e48\u6709\u81ea\u4fe1\uff1f\u201d</p><p>\u201c\u5f53\u7136\uff01\u201d</p><p>\u743c\u6069\u7b11\u7b11\uff0c\u5728\u5979\u65c1\u8fb9\u5750\u4e86\u4e0b\u6765\uff0c\u5f00\u59cb\u5bf9\u4ed8\u81ea\u5df1\u7684\u90a3\u4efd\u65e9\u9910\u3002</p><p>\u6668\u70bc\u57ce\u4e2d\u6709\u51b3\u6597\u573a\uff0c\u662f\u4ee5\u524d\u7684\u7070\u77ee\u4eba\u6240\u5efa\uff0c\u867d\u7136\u4e0d\u662f\u5f88\u9002\u5408\u7528\u6765\u5deb\u5e08\u51b3\u6597\uff0c\u4f46\u4e5f\u52c9\u5f3a\u5c06\u5c31\u4e86\u3002\u743c\u6069\u548c\u51db\u8fdb\u5165\u573a\u5730\uff0c\u5404\u5360\u4e00\u8fb9\uff0c\u6885\u83f2\u65af\u8fdc\u8fdc\u7ad9\u5728\u770b\u53f0\u4e0a\uff0c\u62cd\u4e86\u62cd\u624b\uff0c\u201c\u5f00\u59cb\u5427\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u90fd\u5c0f\u5fc3\u70b9\u3002\u201d</p><p>\u201c\u55ef\u55ef\uff0c\u201d\u51db\u8bf4\uff0c\u201c\u6211\u4f1a\u624b\u4e0b\u7559\u60c5\u7684\uff0c\u653e\u5fc3\u597d\u4e86\u827e\u5f25\u8587\u3002\u201d</p><p>\u201c\u4e0a\u6b21\u4f60\u597d\u50cf\u4e5f\u662f\u8fd9\u4e48\u8bf4\u7684\u3002\u201d\u743c\u6069\u8ba5\u8bbd\u3002</p><p>\u201c\u662f\u5417\uff0c\u6211\u5fd8\u4e86\u3002\u201d</p><p>\u201c\u2026\u2026\u201d</p><p>\u51db\u563b\u563b\u5a07\u7b11\uff0c\u4ece\u8170\u95f4\u53e3\u888b\u91cc\u53d6\u51fa\u4e00\u9897\u7ea2\u5b9d\u77f3\uff0c\u6296\u624b\u5f80\u5730\u9762\u4e0a\u7838\u53bb\u3002\u53ea\u542c\u5f97\u7830\u5730\u4e00\u58f0\uff0c\u5b9d\u77f3\u788e\u88c2\u6210\u5343\u767e\u7ec6\u5c0f\u6676\u7247\uff0c\u706b\u7130\u4ece\u4e2d\u817e\u817e\u5192\u51fa\uff0c\u51db\u4f38\u624b\u4e00\u5f15\uff0c\u4e09\u679a\u706b\u7130\u7bad\u51dd\u805a\u6210\u578b\uff0c\u5448\u4e09\u89d2\u5f62\u5f80\u524d\u98de\u51fa\u3002</p><p>\u201c\u9006\u8f6c\u7206\u70b8\uff01\u201d</p><p>\u743c\u6069\u4e0d\u614c\u4e0d\u5fd9\u5730\u9ed8\u8bf5\u5492\u6587\uff0c\u5b8c\u6210\u4e86\u65e9\u5c31\u51c6\u5907\u597d\u7684\u6cd5\u672f\uff0c\u4e00\u5c42\u6c34\u6ce2\u72b6\u7684\u900f\u660e\u6d41\u5149\u77ac\u95f4\u8986\u76d6\u5168\u8eab\u3002\u8fd9\u4e2a\u6cd5\u672f\u8fd8\u662f\u5f53\u65e5\u5728\u585e\u5c14\u7684\u65f6\u5019\uff0c\u4ece\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u90a3\u91cc\u5b66\u6765\u7684\uff0c\u636e\u8bf4\u53d1\u660e\u8005\u662f\u8d39\u6797\u9b54\u8475\uff0c\u80fd\u591f\u53cd\u5f39\u5404\u79cd\u706b\u7130\u7c7b\u578b\u7684\u9b54\u6cd5\u653b\u51fb\uff0c\u867d\u7136\u529f\u80fd\u662f\u592a\u5355\u4e00\u4e86\u70b9\uff0c\u4f46\u7528\u6765\u5bf9\u4ed8\u51db\u5374\u662f\u6700\u5408\u9002\u4e0d\u8fc7\u3002</p><p>\u706b\u7130\u7bad\u6765\u52bf\u6781\u5feb\uff0c\u743c\u6069\u4e0d\u95ea\u4e0d\u907f\uff0c\u8111\u4e2d\u6e05\u6670\u6d6e\u73b0\u51fa\u53d8\u5f62\u672f\u7684\u5492\u6587\uff0c\u51c6\u5907\u53d1\u51fa\u4e0b\u4e00\u51fb\u3002\u4fbf\u5728\u6b64\u65f6\uff0c\u9661\u7136\u8033\u4e2d\u542c\u89c1\u51db\u4f4e\u559d\u4e00\u58f0\uff1a\u201c\u51b0\uff01\u201d</p><p>\u5239\u90a3\u65f6\u51b0\u534e\u8000\u76ee\uff0c\u4e09\u679a\u8d64\u7ea2\u8272\u7684\u706b\u7130\u7bad\u5728\u534a\u7a7a\u4e2d\u5fae\u5fae\u4e00\u6ede\uff0c\u4e0b\u4e00\u77ac\u95f4\u5df2\u7ecf\u53d8\u6210\u4e86\u901a\u4f53\u900f\u660e\u7684\u4e09\u652f\u51b0\u7bad\uff0c\u671d\u7740\u743c\u6069\u5f53\u80f8\u6fc0\u5c04\u800c\u6765\u3002</p></article>"}}}]);
