(window.webpackJsonp=window.webpackJsonp||[]).push([[368],{390:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e94\u8282 \u518d\u6b21\u51b3\u6597",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e94\u8282 \u518d\u6b21\u51b3\u6597</h2><p>\u4e09\u652f\u51b0\u7bad\u7834\u7a7a\u52b2\u5c04\u800c\u6765\uff0c\u707f\u70c2\u7684\u6676\u534e\u5728\u9ed1\u6697\u4e2d\u523a\u5f97\u4eba\u773c\u775b\u53d1\u75bc\u3002\u743c\u6069\u8fd9\u4e00\u60ca\u975e\u540c\u5c0f\u53ef\uff0c\u8fde\u80cc\u540e\u90fd\u5192\u51fa\u51b7\u6c57\u6765\uff0c\u5306\u5fd9\u95f4\u5f85\u8981\u95ea\u907f\uff0c\u7136\u800c\u8ddd\u79bb\u5df2\u7ecf\u5982\u6b64\u4e4b\u8fd1\uff0c\u4ed6\u53c8\u4e0d\u662f\u90a3\u79cd\u53cd\u5c04\u795e\u7ecf\u8d85\u5feb\u7684\u6b66\u8005\uff0c\u4ed3\u4fc3\u95f4\u54ea\u91cc\u8eb2\u5f97\u5f00\u3002\u603b\u7b97\u4ed6\u8c28\u614e\uff0c\u63d0\u524d\u5728\u8eab\u4e0a\u7684\u9b54\u6cd5\u523a\u9752\u91cc\u50a8\u5b58\u4e86\u51e0\u4e2a\u9632\u5fa1\u6cd5\u672f\uff0c\u539f\u672c\u662f\u60f3\u7740\u6709\u5907\u65e0\u60a3\uff0c\u6b64\u65f6\u4e0d\u6687\u601d\u7d22\uff0c\u4e0b\u610f\u8bc6\u5730\u6fc0\u53d1\u51fa\u6765\u3002</p><p>\u9632\u62a4\u80fd\u91cf\u4f24\u5bb3\u2014\u2014\u5bd2\u51b0\uff01</p><p>\u968f\u7740\u65bd\u6cd5\u8005\u6307\u5b9a\u6cd5\u672f\u7684\u9632\u5fa1\u7c7b\u578b\uff0c\u65e0\u5f62\u7684\u9b54\u6cd5\u80fd\u91cf\u77ac\u95f4\u5728\u865a\u7a7a\u4e2d\u51dd\u805a\uff0c\u5851\u6210\u4e00\u9762\u6de1\u84dd\u8272\u7684\u900f\u660e\u76fe\u724c\u6321\u5728\u743c\u6069\u8eab\u524d\uff0c\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u582a\u582a\u62b5\u4f4f\u4e86\u4e09\u652f\u51b0\u7bad\u3002\u4e00\u649e\u4e4b\u4e0b\uff0c\u201c\u567c\u556a\u201d\u8fde\u58f0\u8106\u54cd\uff0c\u6de1\u84dd\u8272\u76fe\u724c\u6beb\u53d1\u65e0\u635f\uff0c\u53cd\u800c\u662f\u51b0\u7bad\u5bf8\u5bf8\u6298\u65ad\uff0c\u5316\u4f5c\u4e1d\u4e1d\u5bd2\u6c14\u6d88\u6563\u7a7a\u4e2d\u3002</p><p>\u201c\u600e\u4e48\u8fd9\u4e48\u8106\u5f31\uff1f\u201d</p><p>\u743c\u6069\u7565\u7565\u60ca\u8bb6\uff0c\u4ed6\u662f\u53d8\u5316\u5e08\uff0c\u867d\u7136\u5b66\u8fc7\u57fa\u7840\u5e38\u7528\u7684\u9632\u62a4\u6cd5\u672f\uff0c\u4f46\u5e76\u4e0d\u5341\u5206\u7cbe\u901a\uff0c\u800c\u51db\u7684\u6cd5\u672f\u7ecf\u8fc7\u5b9d\u77f3\u7684\u5f3a\u5316\u589e\u5e45\uff0c\u5a01\u529b\u5927\u5f97\u60ca\u4eba\uff0c\u8fde\u7cbe\u7814\u9632\u62a4\u7684\u5deb\u5e08\u62b5\u6321\u8d77\u6765\u90fd\u5f88\u8d39\u529b\u3002\u6240\u4ee5\u4ed6\u5176\u5b9e\u6839\u672c\u5c31\u6ca1\u6307\u671b\u80fd\u591f\u5b8c\u5168\u5c01\u4f4f\u8fd9\u4e00\u51fb\uff0c\u53ea\u671f\u671b\u80fd\u591f\u8d77\u5230\u524a\u5f31\u4f5c\u7528\uff0c\u8ba9\u81ea\u5df1\u4e0d\u81f3\u4e8e\u4f24\u5f97\u592a\u5389\u5bb3\u5c31\u597d\uff0c\u51fa\u73b0\u8fd9\u79cd\u7ed3\u679c\uff0c\u5012\u662f\u9887\u4e3a\u51fa\u4e4e\u4ed6\u7684\u610f\u6599\u4e4b\u5916\u3002</p><p>\u51db\u7b11\u772f\u772f\u5728\u5730\u8fdc\u5904\u4e3e\u624b\u793a\u610f\u3002</p><p>\u201c\u600e\u4e48\u6837\uff0c\u743c\u6069\uff0c\u201d\u5979\u8bf4\uff0c\u201c\u8fd9\u7b97\u662f\u6253\u4e2a\u62db\u547c\uff0c\u63a5\u4e0b\u6765\u6211\u53ef\u5c31\u4e0d\u5ba2\u6c14\u4e86\u5462\u3002\u201d</p><p>\u743c\u6069\u6ca1\u6709\u56de\u7b54\uff0c\u4ed6\u5728\u601d\u7d22\uff0c\u6216\u8005\u66f4\u51c6\u786e\u5730\u8bf4\u662f\u9707\u60ca\u3002\u51db\u521a\u624d\u6240\u7528\u7684\u8fd9\u4e00\u62db\u4ed6\u662f\u77e5\u9053\u7684\uff0c\u5143\u7d20\u638c\u63a7\uff0c\u53c8\u540d\u5851\u80fd\u8f6c\u5316\uff0c\u80fd\u591f\u5c06\u4e00\u79cd\u5f62\u5f0f\u7684\u5851\u80fd\u9b54\u6cd5\u4e34\u65f6\u8f6c\u6362\u6210\u53e6\u5916\u4e00\u79cd\u5f62\u5f0f\uff0c\u8fd9\u662f\u552f\u6709\u628a\u5851\u80fd\u6cd5\u672f\u953b\u70bc\u5230\u6781\u81f4\u7684\u5927\u5deb\u5e08\u624d\u53ef\u80fd\u62e5\u6709\u7684\u6280\u827a\u3002\u5728\u963f\u683c\u62c9\u9686\u7684\u65f6\u5019\uff0c\u743c\u6069\u66fe\u7ecf\u89c1\u6b23\u5e03\u7528\u8fc7\u4e00\u6b21\uff0c\u5f53\u65f6\u5979\u5bf9\u4ed8\u7ea2\u888d\u5deb\u5e08\u4f1a\u7684\u9644\u9b54\u5b66\u6d3e\u9996\u5e2d\uff0c\u5148\u662f\u53d1\u52a8\u6d41\u661f\u7206\u53ec\u6765\u70c8\u7130\u9668\u77f3\uff0c\u5728\u534a\u9014\u5c06\u5b83\u4eec\u8f6c\u5316\u6210\u95ea\u7535\u7403\uff0c\u5c06\u5bf9\u624b\u6253\u4e86\u4e00\u4e2a\u63aa\u624b\u4e0d\u53ca\uff0c\u51e0\u4e4e\u5f53\u573a\u4e27\u547d\u3002</p><p>\u7136\u800c\u2026\u2026\u51db\u7edd\u4e0d\u53ef\u80fd\u6709\u8fd9\u79cd\u9020\u8be3\u554a\u3002</p><p>\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u4e8b\u60c5\u53d8\u5f97\u51fa\u4e4e\u610f\u6599\u5730\u68d8\u624b\u8d77\u6765\uff0c\u5982\u679c\u51db\u8fd8\u50cf\u4ee5\u524d\u90a3\u6837\uff0c\u5355\u7eaf\u53ea\u64c5\u957f\u706b\u7130\u9b54\u6cd5\u653b\u51fb\uff0c\u90a3\u5c31\u7b97\u5a01\u529b\u518d\u5f3a\u743c\u6069\u4e5f\u80fd\u5e94\u4ed8\uff1b\u5c31\u7b97\u51db\u7a81\u7136\u5f00\u7a8d\uff0c\u4e0d\u518d\u504f\u79d1\uff0c\u80fd\u591f\u50cf\u5176\u4ed6\u5851\u80fd\u5e08\u90a3\u6837\u535a\u5b66\u517c\u901a\uff0c\u743c\u6069\u4e5f\u4e0d\u754f\u60e7\u3002\u95ee\u9898\u662f\u73b0\u5728\u8fd9\u79cd\u60c5\u51b5\uff0c\u5979\u53d1\u5c04\u7684\u660e\u660e\u662f\u706b\u7130\u7bad\uff0c\u51fb\u4e2d\u65f6\u5374\u6709\u53ef\u80fd\u53d8\u6210\u4e86\u5bd2\u51b0\u9525\uff1b\u6253\u51fa\u7684\u660e\u660e\u662f\u95ea\u7535\u675f\uff0c\u51fb\u4e2d\u662f\u5374\u53ef\u80fd\u53d8\u6210\u4e86\u97f3\u6ce2\u77db\u2014\u2014\u8fd9\u5bfc\u81f4\u7684\u7ed3\u679c\uff0c\u5c31\u662f\u5bf9\u624b\u6839\u672c\u65e0\u6240\u9002\u4ece\uff0c\u65e0\u6cd5\u9009\u62e9\u76f8\u5e94\u7684\u6cd5\u672f\u5bf9\u6297\uff0c\u8c01\u80fd\u53d7\u5f97\u4e86\u3002</p><p>\u5bb9\u4e0d\u5f97\u743c\u6069\u591a\u60f3\uff0c\u51db\u518d\u5ea6\u53d1\u8d77\u4e86\u653b\u51fb\uff0c\u5979\u7684\u53f3\u81c2\u8f7b\u8f7b\u62ac\u8d77\uff0c\u4e09\u679a\u706b\u86cb\u767d\u77f3\u4f9d\u6b21\u5f39\u51fa\uff0c\u5316\u4f5c\u4e09\u56e2\u706b\u7403\u5148\u540e\u98de\u5c04\u3002\u743c\u6069\u8fd9\u6b21\u5438\u53d6\u4e86\u6559\u8bad\uff0c\u53cc\u624b\u540c\u65f6\u5f80\u4e0a\u865a\u63d0\uff0c\u4ece\u5730\u9762\u4e0a\u786c\u751f\u751f\u62c9\u626f\u8d77\u4e00\u9053\u77f3\u5899\u6765\uff0c\u6321\u5728\u8eab\u524d\u3002\u4e09\u56e2\u5de8\u5927\u7684\u706b\u7403\u5728\u534a\u9014\u4e2d\u9661\u7136\u5411\u5185\u584c\u9677\uff0c\u51dd\u6210\u4e09\u679a\u62f3\u5934\u5927\u5c0f\u7684\u7403\u5f62\u95ea\u7535\uff0c\u5288\u556a\u4f5c\u54cd\u7740\u8f70\u51fb\u800c\u6765\uff0c\u5728\u77f3\u5899\u7684\u8868\u9762\u7ffb\u6eda\u98de\u6e85\uff0c\u70b8\u5f00\u4e00\u6e9c\u4e32\u7684\u5c0f\u5751\uff0c\u4f46\u5e76\u672a\u7a7f\u900f\u3002</p><p>\u201c\u53d8\u5f62\uff01\u201d</p><p>\u743c\u6069\u5feb\u6b65\u540e\u9000\uff0c\u62c9\u5f00\u8ddd\u79bb\uff0c\u5de6\u624b\u6084\u65e0\u58f0\u606f\u5730\u505a\u4e86\u4e2a\u590d\u6742\u7684\u624b\u52bf\uff0c\u9065\u9065\u671d\u7740\u51db\u4e00\u6307\u3002\u5492\u8bed\u5177\u73b0\u6210\u4e3a\u538b\u529b\uff0c\u77ac\u95f4\u7b3c\u7f69\u5305\u88f9\u4f4f\u5c0f\u5973\u5deb\u7684\u8eab\u4f53\uff0c\u8bd5\u56fe\u5c06\u5979\u626d\u66f2\u53d8\u5316\u6210\u4e00\u53ea\u677e\u9f20\u3002\u51db\u8eab\u4f53\u6643\u52a8\uff0c\u8e09\u8dc4\u8dcc\u9000\u4e24\u6b65\uff0c\u773c\u770b\u5c31\u8981\u6454\u5012\uff0c\u4f46\u968f\u5373\u53c8\u91cd\u65b0\u7ad9\u7a33\uff0c\u5de8\u9f99\u7684\u8840\u8109\u8d4b\u4e88\u5979\u5f3a\u5065\u7684\u4f53\u8d28\u548c\u4f18\u79c0\u7684\u6297\u9b54\u80fd\u529b\uff0c\u8fc5\u901f\u903c\u9000\u4e86\u53d8\u5f62\u672f\u7684\u4fb5\u88ad\u3002</p><p>\u743c\u6069\u8d81\u6b64\u673a\u4f1a\u518d\u5ea6\u91ca\u653e\u51fa\u4e00\u4e2a\u8fdf\u7f13\u672f\uff0c\u4f46\u5374\u6ca1\u6709\u5bf9\u51db\u9020\u6210\u4efb\u4f55\u5f71\u54cd\u3002\u5c0f\u5973\u5deb\u556a\u5730\u6253\u4e86\u4e2a\u54cd\u6307\uff0c\u4e00\u56e2\u706b\u7130\u5728\u534a\u7a7a\u4e2d\u7efd\u653e\u51fa\u6765\uff0c\u51dd\u805a\u6210\u957f\u77db\u5f62\u72b6\uff0c\u5979\u4e00\u8dc3\u800c\u8d77\uff0c\u4f38\u624b\u7ef0\u8fc7\u70c8\u7130\u77db\uff0c\u7ea4\u8170\u53cd\u5f13\uff0c\u201c\u563f\u201d\u5730\u4e00\u58f0\uff0c\u4eff\u4f5b\u63b7\u6807\u67aa\u822c\u7528\u529b\u5c04\u51fa\u3002</p><p>\u201c\u97f3\u6ce2\uff01\u201d\u5979\u7528\u9f99\u8bed\u5389\u58f0\u4f4e\u559d\u3002</p><p>\u706b\u7ea2\u8272\u7684\u957f\u77db\u9661\u7136\u4ece\u89c6\u91ce\u4e2d\u6d88\u5931\uff0c\u53ea\u5728\u7a7a\u6c14\u91cc\u6b8b\u4f59\u4e00\u4e1d\u8fd1\u4e4e\u900f\u660e\u7684\u8f6e\u5ed3\uff0c\u4f34\u968f\u7740\u6c89\u95f7\u8f70\u9e23\u58f0\u9ad8\u901f\u63a5\u8fd1\uff0c\u97f3\u6ce2\u77db\u649e\u4e0a\u4e86\u77f3\u5899\u3002\u4f5c\u4e3a\u5851\u80fd\u5b66\u6d3e\u516d\u5927\u5206\u652f\u4e2d\u96be\u5ea6\u6700\u9ad8\u7684\u4e24\u7cfb\u4e4b\u4e00\uff0c\u97f3\u6ce2\u6cd5\u672f\u6700\u64c5\u957f\u7684\u5c31\u662f\u6467\u7834\u575a\u786c\u56fa\u4f53\uff0c\u8fd9\u4e00\u51fb\u97f3\u6ce2\u77db\u5982\u679c\u8f70\u5728\u6c34\u9762\u4e0a\uff0c\u6700\u591a\u6cdb\u8d77\u51e0\u5708\u6d9f\u6f2a\uff0c\u4f46\u73b0\u5728\u7838\u5728\u5ca9\u77f3\u4e0a\uff0c\u65e0\u5f62\u7684\u9707\u8361\u6ce2\u6025\u901f\u6e17\u900f\u6269\u6563\uff0c\u539a\u91cd\u7684\u77f3\u5899\u8f70\u7136\u5012\u584c\uff0c\u788e\u77f3\u6563\u843d\u6ee1\u5730\u3002</p><p>\u743c\u6069\u53cc\u624b\u865a\u62ac\uff0c\u811a\u4e0b\u7684\u5ca9\u77f3\u5730\u9762\u4eff\u4f5b\u6cbc\u6cfd\u822c\u7ffb\u6eda\u626d\u52a8\uff0c\u4e00\u9762\u65b0\u7684\u77f3\u5899\u5feb\u901f\u5347\u8d77\u3002</p><p>\u51db\u518d\u5ea6\u63b7\u51fa\u97f3\u6ce2\u77db\uff0c\u51fb\u6bc1\u4e86\u77f3\u5899\uff0c\u7d27\u63a5\u7740\u743c\u6069\u53c8\u91cd\u65b0\u5851\u8d77\uff0c\u8fd9\u79cd\u5355\u8c03\u7684\u653b\u9632\u6218\u8fdb\u884c\u5230\u7b2c\u4e09\u56de\u5408\uff0c\u5c0f\u5973\u5deb\u7ec8\u4e8e\u5931\u53bb\u4e86\u8010\u5fc3\u3002</p><p>\u201c\u6211\u5012\u8981\u770b\u770b\u4f60\u5230\u5e95\u51c6\u5907\u4e86\u51e0\u4e2a\u77f3\u5899\u672f\uff01\u201d\u5979\u6c14\u9f13\u9f13\u5730\u8bf4\uff0c\u4ece\u8170\u95f4\u7684\u53e3\u888b\u91cc\u6478\u51fa\u4e00\u679a\u83f1\u5f62\u5b9d\u77f3\uff0c\u624b\u6307\u7528\u529b\u556a\u5730\u4e00\u58f0\u634f\u788e\u3002</p><p>\u8d64\u7ea2\u8272\u7684\u706b\u7130\u4ece\u5c11\u5973\u7684\u8863\u88d9\u4e2d\u70c8\u70c8\u817e\u8d77\uff0c\u5728\u5979\u7684\u64cd\u7eb5\u4e0b\u51dd\u6210\u4e09\u652f\u957f\u77db\uff0c\u7136\u540e\u4f9d\u6b21\u98de\u5c04\u51fa\u53bb\u3002\u7b2c\u4e00\u652f\u957f\u77db\u5728\u534a\u9014\u5316\u4f5c\u97f3\u6ce2\uff0c\u4e00\u51fb\u9707\u788e\u4e86\u77f3\u5899\uff0c\u77f3\u5c51\u98de\u6e85\u7070\u5c18\u5f25\u6f2b\u4e4b\u4e2d\u73b0\u51fa\u743c\u6069\u7684\u8eab\u5f71\u3002\u7b2c\u4e8c\u652f\u957f\u77db\u4f9d\u65e7\u662f\u706b\u7130\uff0c\u7b2c\u4e09\u652f\u957f\u77db\u5374\u51dd\u6210\u5bd2\u51b0\uff0c\u4e00\u524d\u4e00\u540e\u4ea4\u9519\u5c04\u6765\uff0c\u5b83\u4eec\u7cbe\u786e\u5730\u547d\u4e2d\u4e86\u76ee\u6807\u2014\u2014\u7136\u540e\u5f84\u76f4\u4ece\u8eab\u4f53\u91cc\u7a7f\u900f\u8fc7\u53bb\uff0c\u7838\u5728\u540e\u9762\u7684\u77f3\u58c1\u4e0a\u3002\u539f\u672c\u9634\u51b7\u6f6e\u6e7f\u7684\u77f3\u58c1\u77ac\u95f4\u534a\u4fa7\u9677\u5165\u706b\u6d77\uff0c\u534a\u4fa7\u88ab\u51b0\u51bb\u8986\u76d6\uff0c\u666f\u8c61\u5341\u5206\u8be1\u5f02\u3002</p><p>\u5047\u8c61\u672f\uff08Mislead\uff09\uff1f</p><p>\u51db\u660e\u767d\u4e86\u600e\u4e48\u56de\u4e8b\uff0c\u8fd9\u662f\u4e00\u79cd\u5f88\u9ad8\u660e\u7684\u5e7b\u672f\uff0c\u521b\u9020\u4e00\u4e2a\u903c\u771f\u7684\u5e7b\u5f71\u5192\u5145\u81ea\u5df1\uff0c\u5438\u5f15\u6ce8\u610f\u529b\uff0c\u800c\u771f\u6b63\u7684\u5deb\u5e08\u672c\u4eba\u5219\u9690\u5f62\u4e86\u8d77\u6765\u3002\u8981\u5e94\u4ed8\u8fd9\u79cd\u5c40\u9762\u4e5f\u4e0d\u96be\uff0c\u9884\u8a00\u5b66\u6d3e\u6709\u5404\u79cd\u7834\u9690\u5f62\u6cd5\u672f\uff0c\u7136\u800c\u51db\u534a\u4e2a\u90fd\u6ca1\u51c6\u5907\u2026\u2026</p><p>\u5f53\u7136\uff0c\u8fd9\u96be\u4e0d\u5012\u5979\u3002</p><p>\u5c0f\u5973\u5deb\u53cc\u81c2\u5f20\u5f00\uff0c\u4eff\u4f5b\u821e\u8e48\u4e00\u822c\u5728\u539f\u5730\u505a\u4e86\u4e2a\u8f7b\u76c8\u4f18\u7f8e\u7684\u65cb\u8eab\uff0c\u4e4c\u9ed1\u957f\u53d1\u98de\u626c\u4e2d\uff0c\u4e00\u9053\u706b\u5708\u5728\u5979\u8eab\u5468\u6d6e\u73b0\uff0c\u5f80\u5916\u75be\u901f\u6269\u6563\u51fa\u53bb\u3002\u56e0\u4e3a\u592a\u8fc7\u5206\u6563\uff0c\u706b\u7130\u5df2\u7ecf\u6ca1\u4ec0\u4e48\u7834\u574f\u529b\uff0c\u4f46\u51db\u7684\u76ee\u7684\u539f\u672c\u4e5f\u5c31\u4e0d\u5728\u4e8e\u4f24\u4eba\uff0c\u800c\u662f\u627e\u51fa\u9690\u5f62\u8005\u3002</p><p>\u201c\u5657\uff01\u201d</p><p>\u5728\u8ddd\u79bb\u51db\u53f3\u4fa7\u5927\u7ea6\u4e8c\u5341\u82f1\u5c3a\u7684\u5730\u65b9\uff0c\u7a7a\u6c14\u8f7b\u5fae\u6ce2\u6298\uff0c\u743c\u6069\u7684\u8eab\u5f71\u663e\u73b0\u51fa\u6765\uff0c\u4ed6\u7684\u957f\u888d\u4e0a\u88ab\u706b\u7130\u707c\u70e7\u5f00\u51e0\u4e2a\u7834\u6d1e\u3002\u51db\u5f97\u610f\u5730\u7b11\u4e86\u8d77\u6765\uff0c\u53f3\u624b\u98df\u6307\u5df2\u7ecf\u9065\u9065\u6307\u4e2d\u76ee\u6807\uff0c\u9b54\u6cd5\u80fd\u91cf\u98de\u901f\u5728\u6307\u5c16\u6c47\u805a\uff0c\u51dd\u805a\u6210\u4e94\u679a\u5f39\u73e0\u5927\u5c0f\u7684\u900f\u660e\u98de\u5f39\uff0c\u5728\u4e0b\u4e00\u77ac\u95f4\u5373\u5c06\u6fc0\u53d1\u3002</p><p>\u7136\u540e\u5979\u773c\u524d\u9661\u7136\u4e00\u82b1\u3002</p><p>\u5149\u5f71\u9519\u4e71\u4e4b\u4e2d\uff0c\u743c\u6069\u5df2\u7ecf\u4e00\u5206\u4e3a\u4e8c\uff0c\u4e8c\u5206\u4e3a\u56db\uff0c\u5c42\u5c42\u8fed\u8fed\uff0c\u4e00\u5171\u5316\u51fa\u516b\u4e2a\u955c\u5f71\u5206\u8eab\u6765\uff0c\u8fde\u771f\u5b9e\u7684\u672c\u4f53\u5728\u5185\u5171\u6709\u4e5d\u4eba\u3002\u51db\u7a0d\u7a0d\u8fdf\u7591\uff0c\u4e94\u679a\u9b54\u6cd5\u98de\u5f39\u5206\u6563\u53d1\u5c04\u4e86\u51fa\u53bb\uff0c\u6bcf\u9897\u98de\u5f39\u90fd\u51c6\u786e\u51fb\u4e2d\u4e86\u4e00\u4e2a\u201c\u743c\u6069\u201d\uff0c\u7136\u540e\u4eba\u5f71\u6d88\u5931\uff0c\u5b83\u4eec\u5168\u662f\u955c\u50cf\u3002</p><p>\u201c\u5207\uff0c\u8fd0\u6c14\u771f\u5dee\uff01\u201d</p><p>\u51db\u5600\u5495\u7740\uff0c\u4f46\u8fd9\u4e2a\u6cd5\u672f\u5e76\u672a\u5b8c\u5168\u6d6a\u8d39\uff0c\u81f3\u5c11\u5b83\u6210\u529f\u5c06\u76ee\u6807\u7684\u6570\u91cf\u524a\u51cf\u4e86\u5927\u534a\u3002\u5979\u6478\u51fa\u4e00\u679a\u5b9d\u77f3\uff0c\u6b63\u8981\u63b7\u51fa\uff0c\u9661\u7136\u89c9\u5f97\u811a\u4e0b\u5fae\u5fae\u4e00\u6c89\uff0c\u539f\u672c\u575a\u5b9e\u65e0\u6bd4\u7684\u5ca9\u77f3\u5730\u9762\u4e0d\u77e5\u4f55\u65f6\u9010\u6e10\u53d8\u5f97\u67d4\u8f6f\uff0c\u773c\u770b\u5c31\u8981\u5316\u4f5c\u5b8c\u5168\u7684\u6ce5\u6d46\u3002</p><p>\u65e0\u58f0\u65e0\u606f\u4e4b\u95f4\uff0c\u743c\u6069\u6084\u7136\u53d1\u52a8\u4e86\u5316\u77f3\u4e3a\u6ce5\u3002</p><p>\u62dc\u5965\u6c83\u4f20\u6388\u7684\u6cd5\u672f\u9ed8\u53d1\u6280\u5de7\u6240\u8d50\uff0c\u4ed6\u65bd\u6cd5\u65f6\u65e0\u9700\u50cf\u5176\u4ed6\u5deb\u5e08\u90a3\u6837\u8bf5\u5492\uff0c\u9690\u853d\u6027\u5927\u5927\u589e\u52a0\uff0c\u4ee5\u81f3\u4e8e\u51db\u90fd\u6ca1\u6709\u5bdf\u89c9\u3002\u4f46\u5c0f\u5973\u5deb\u786e\u5b9e\u53cd\u5e94\u795e\u901f\uff0c\u521a\u4e00\u5bdf\u89c9\u811a\u4e0b\u6709\u5f02\u6837\uff0c\u4e0d\u5047\u601d\u7d22\u5730\u5c31\u8df3\u4e86\u8d77\u6765\u3002\u4e00\u5c42\u5fae\u5149\u5728\u51db\u7684\u80cc\u540e\u6cdb\u8d77\uff0c\u9b54\u6cd5\u523a\u9752\u4e2d\u50a8\u5b58\u7684\u6cd5\u672f\u77ac\u95f4\u542f\u52a8\uff0c\u8ba9\u5979\u60ac\u6d6e\u5728\u7a7a\u4e2d\uff0c\u6ca1\u6709\u5760\u843d\u4e0b\u53bb\u3002\u7136\u800c\u5c31\u5728\u6b64\u65f6\uff0c\u5730\u9762\u4e0a\u9661\u7136\u7a9c\u51fa\u4e24\u53ea\u5ca9\u77f3\u624b\u81c2\uff0c\u4e00\u5de6\u4e00\u53f3\u817e\u7a7a\u800c\u8d77\uff0c\u500f\u5ffd\u5982\u7535\uff0c\u671d\u7740\u51db\u6293\u8fc7\u6765\u3002</p><p>\u82e5\u662f\u5728\u5e73\u65f6\uff0c\u4ee5\u51db\u7684\u8eab\u624b\uff0c\u8fd9\u77f3\u722a\u672f\u672a\u5fc5\u6709\u7528\uff0c\u4f46\u5982\u4eca\u5979\u60ac\u6d6e\u5728\u7a7a\u4e2d\uff0c\u6839\u672c\u65e0\u4ece\u95ea\u907f\uff0c\u8f6c\u77ac\u95f4\u53cc\u8db3\u8db3\u8155\u5df2\u7ecf\u88ab\u7d27\u7d27\u6263\u4f4f\u3002\u77f3\u722a\u575a\u82e5\u7cbe\u91d1\uff0c\u51db\u6323\u4e86\u4e00\u6323\uff0c\u53d1\u89c9\u65e0\u6cd5\u6446\u8131\uff0c\u968f\u5373\u5de6\u81c2\u4e0a\u5149\u8292\u4e00\u95ea\uff0c\u6574\u4e2a\u4eba\u9661\u7136\u6d88\u5931\u3002</p><p>\u4e0b\u4e00\u77ac\u95f4\uff0c\u51db\u51fa\u73b0\u5728\u743c\u6069\u8eab\u524d\u3002\u5979\u4e00\u4e2a\u5feb\u901f\u6ed1\u6b65\uff0c\u8eab\u5f62\u5fae\u4fa7\uff0c\u4ee5\u5de6\u8db3\u652f\u5730\uff0c\u53f3\u817f\u62ac\u8d77\uff0c\u5728\u7a7a\u4e2d\u5316\u51fa\u4e00\u9053\u5f27\u7ebf\uff0c\u901f\u5ea6\u60ca\u4eba\u5730\u671d\u7740\u743c\u6069\u80a9\u90e8\u6a2a\u8e22\u8fc7\u6765\u3002</p><p>\u5979\u7389\u817f\u4fee\u957f\uff0c\u539f\u672c\u5c31\u6781\u7f8e\uff0c\u53c8\u7a7f\u7740\u8d85\u77ed\u88d9\uff0c\u8fd9\u4e00\u8bb0\u4fa7\u8e22\u62ac\u817f\u6781\u9ad8\uff0c\u987f\u65f6\u8fde\u7c89\u7ea2\u8272\u7684\u4e1d\u7ec7\u5185\u88e4\u90fd\u9732\u4e86\u51fa\u6765\uff0c\u751a\u81f3\u9690\u9690\u7ea6\u7ea6\u663e\u51fa\u82b1\u623f\u8f6e\u5ed3\u3002\u53ea\u662f\u8fd9\u65d6\u65ce\u98ce\u5149\u4e2d\u5374\u8574\u542b\u7740\u51cc\u5389\u7684\u51f6\u9669\u6740\u610f\uff0c\u5018\u82e5\u743c\u6069\u771f\u88ab\u8e22\u4e2d\u4e86\uff0c\u6027\u547d\u662f\u65e0\u5fe7\uff0c\u9aa8\u6298\u5927\u6709\u53ef\u80fd\uff0c\u8fd9\u8fd8\u662f\u51db\u624b\u4e0b\u7559\u60c5\uff0c\u8d77\u817f\u65f6\u7a0d\u7a0d\u538b\u4f4e\u4e86\u534a\u5c3a\uff0c\u6ca1\u6709\u7167\u7740\u5934\u90e8\u8e22\u3002</p><p>\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u743c\u6069\u672c\u80fd\u5730\u62ac\u8d77\u5de6\u81c2\u4e00\u683c\uff0c\u5168\u8eab\u5149\u8292\u66b4\u8d77\uff0c\u5269\u4f59\u4e09\u4e2a\u9b54\u6cd5\u523a\u9752\u4e2d\u50a8\u5b58\u7684\u6cd5\u672f\u540c\u65f6\u53d1\u52a8\u3002</p><p>\u201c\u5580\u5693\uff01\u201d</p><p>\u968f\u7740\u4e00\u58f0\u9aa8\u9abc\u788e\u88c2\u7684\u8106\u54cd\uff0c\u4e24\u9053\u4eba\u5f71\u5206\u5f00\uff0c\u743c\u6069\u8fde\u9000\u4e86\u51e0\u6b65\u624d\u52c9\u5f3a\u7ad9\u7a33\uff0c\u5374\u663e\u7136\u6ca1\u53d7\u4ec0\u4e48\u4f24\uff1b\u51db\u5374\u8dcc\u5750\u5728\u5730\uff0c\u6012\u89c6\u7740\u743c\u6069\uff0c\u63a5\u7740\u5c0f\u5634\u4e00\u6241\uff0c\u54c7\u5730\u54ed\u4e86\u51fa\u6765\u3002</p></article>"}}}]);
