(window.webpackJsonp=window.webpackJsonp||[]).push([[37],{60:function(p){p.exports={chapterTitle:null,title:"\u7b2c1027\u7ae0 \u591a\u5c11\u4eba\u7684\u5fc3\u601d\u88ab\u7275\u52a8",content:"<article><h2>\u7b2c1027\u7ae0 \u591a\u5c11\u4eba\u7684\u5fc3\u601d\u88ab\u7275\u52a8</h2><p>\u3000\u3000\u5b59\u9759\u9999\u5728\u73c0\u65af\u5728\u505c\u7559\u4e86\u4e09\u5929\uff0c\u8d76\u4e0a\u6709\u56de\u9999\u6e2f\u7684\u76f4\u822a\u822a\u73ed\u5c31\u76f4\u63a5\u56de\u9999\u6e2f\u4e86\uff0c\u5f20\u606a\u3001\u53f6\u5efa\u658c\u8fd8\u6709\u7fdf\u4e39\u9752\u5728\u73c0\u65af\u5446\u4e86\u4e00\u5468\u7684\u65f6\u95f4\uff0c\u4e0e\u73c0\u65af\u5404\u754c\u4eba\u58eb\u63a5\u89e6\u3002</p><p>\u3000\u3000\u9526\u6e56\u629b\u51fa\u5341\u4e94\u4ebf\u7f8e\u5143\u7684\u6295\u8d44\u8ba1\u5212\uff0c\u8fde\u66f4\u8be6\u7ec6\u7684\u65b9\u6848\u90fd\u6ca1\u6709\uff0c\u9876\u591a\u7b97\u662f\u5728\u7a7a\u6c14\u91cc\u753b\u51fa\u4e00\u4e2a\u5927\u997c\uff0c\u4f46\u662f\u5bf9\u51b7\u5bc2\u3001\u65e0\u4eba\u7406\u4f1a\u7684\u9a7b\u73c0\u65af\u603b\u9886\u4e8b\u9986\u6765\u8bf4\uff0c\u5374\u50cf\u4f53\u5185\u7ed9\u6ce8\u5165\u4e00\u9488\u5f3a\u5fc3\u5242\uff0c\u9a7b\u5916\u673a\u6784\u652f\u6301\u6c11\u8425\u4f01\u4e1a\u5728\u6d77\u5916\u7684\u53d1\u5c55\u4e5f\u65e0\u53ef\u539a\u975e\u3002</p><p>\u3000\u3000\u957f\u65f6\u95f4\u4ee5\u6765\uff0c\u4e2d\u51b6\u6fb3\u6d32\u516c\u53f8\u5728\u897f\u6fb3\u6d32\u5b64\u7acb\u65e0\u63f4\uff0c\u800c\u4e4b\u524d\u9526\u6e56\u4e0e\u4e2d\u51b6\u603b\u516c\u53f8\u5728\u56fd\u5185\u7a00\u571f\u4ea7\u4e1a\u4e0a\u7684\u6f02\u4eae\u5408\u4f5c\uff0c\u4e5f\u65e0\u7591\u8ba9\u4e2d\u51b6\u6fb3\u6d32\u5206\u516c\u53f8\u5bf9\u9526\u6e56\u5728\u897f\u6fb3\u6d32\u7684\u8868\u73b0\u66f4\u52a0\u671f\u5f85\u2014\u2014\u8d3e\u81f3\u6bc5\u4ee5\u4e3a\u9526\u6e56\u7684\u8ba1\u5212\u81f3\u5c11\u80fd\u4fc3\u8fdb\u603b\u516c\u53f8\u4e0b\u5b9a\u51b3\u5fc3\u5c3d\u65e9\u5c06\u6070\u90a3\u94c1\u77ff\u7684\u5e74\u4ea7\u91cf\u63d0\u9ad8\u5230\u4e00\u5343\u4e07\u5428\u3002</p><p>\u3000\u3000\u90a3\u4e9b\u4e2d\u5c0f\u94c1\u77ff\u77f3\u8d38\u6613\u5546\u4ece\u6765\u90fd\u662f\u4f9d\u8d56\u5927\u77ff\u5546\u751f\u5b58\uff0c\u7531\u4e8e\u56fd\u5185\u5bf9\u897f\u6fb3\u6d32\u7684\u94c1\u77ff\u77f3\u4f9d\u8d56\u7a0b\u5ea6\u4e0d\u9ad8\uff0c\u6240\u4ee5\u4ed6\u4eec\u8fd9\u4e9b\u6765\u81ea\u4e2d\u56fd\u7684\u94c1\u77ff\u77f3\u8d38\u6613\u5546\u5728\u73c0\u65af\u4e0d\u5927\u53d7\u91cd\u89c6\u3002\u6070\u90a3\u94c1\u77ff\u5c06\u5e74\u4ea7\u91cf\u63d0\u9ad8\u5230\u4e00\u5343\u4e07\u77ff\u3001\u9526\u6e56\u518d\u5728\u897f\u6fb3\u6d32\u6295\u8d44\u4e00\u5343\u4e07\u5428\u7684\u94c1\u77ff\uff0c\u4e5f\u5c31\u662f\u4ece\u897f\u6fb3\u6d32\u8fd0\u5f80\u56fd\u5185\u7684\u94c1\u77ff\u77f3\u8d38\u6613\u91cf\u5c06\u6fc0\u589e\u8fd1\u4e03\u500d\u3002\u867d\u7136\u9526\u6e56\u660e\u786e\u8868\u793a\u5176\u6240\u6295\u8d44\u7684\u94c1\u77ff\u5c06\u4f9b\u5e94\u8054\u5408\u94a2\u94c1\uff0c\u4f46\u662f\u8d38\u6613\u91cf\u7684\u6fc0\u589e\uff0c\u53c8\u6709\u8054\u5408\u94a2\u94c1\u7684\u793a\u8303\u6548\u5e94\uff0c\u4f1a\u5f15\u8d77\u56fd\u5185\u94a2\u94c1\u4ea7\u4e1a\u5bf9\u897f\u6fb3\u6d32\u94c1\u77ff\u77f3\u8d44\u6e90\u7684\u6574\u4f53\u91cd\u89c6\uff0c\u5728\u73c0\u65af\u7684\u94c1\u77ff\u77f3\u8d38\u6613\u5546\u7684\u5730\u4f4d\u5c31\u5c06\u4e0d\u540c\u4ee5\u5f80\u3002\u518d\u8bf4\u4e86\uff0c\u9526\u6e56\u5546\u4e8b\u6b64\u65f6\u624b\u91cc\u7684\u8fdb\u53e3\u6743\u8fd8\u4e0d\u5230\u4e24\u767e\u4e07\u5428\uff0c\u8981\u662f\u56fd\u5bb6\u5728\u8fdb\u53e3\u6743\u9650\u4e0a\u5361\u9526\u6e56\u5546\u4e8b\u7684\u8116\u5b50\uff0c\u9526\u6e56\u5546\u4e8b\u5728\u897f\u6fb3\u6d32\u751f\u4ea7\u591a\u4f59\u7684\u94c1\u77ff\u77f3\u5c31\u53ea\u80fd\u901a\u8fc7\u4e2d\u51b6\u603b\u516c\u53f8\u6216\u8005\u4ed6\u4eec\u8f93\u5165\u56fd\u5185\u5e02\u573a\u3002</p><p>\u3000\u3000\u5bf9\u94c1\u77ff\u77f3\u8d44\u6e90\u8f93\u51fa\u7ed9\u529b\u62d3\u3001\u5fc5\u548c\u5fc5\u62d3\u4e25\u5bc6\u63a7\u5236\u4ee5\u81f4\u4e25\u91cd\u5f71\u54cd\u8be5\u5730\u533a\u8d44\u6e90\u8fdb\u4e00\u6b65\u5f00\u53d1\u7684\u73b0\u72b6\uff0c\u897f\u6fb3\u6d32\u653f\u5e9c\u4e0e\u516c\u4f17\u662f\u6709\u6781\u5927\u610f\u89c1\u7684\u3002</p><p>\u3000\u3000\u897f\u6fb3\u6d32\u5bf9\u77ff\u4ea7\u4e1a\u7684\u4f9d\u8d56\u6027\u6781\u9ad8\uff0c\u7ecf\u6d4e\u53d1\u5c55\u3001\u589e\u52a0\u5c31\u4e1a\u51e0\u4e4e\u90fd\u4f9d\u8d56\u77ff\u4ea7\u4ea7\u4e1a\u7684\u7e41\u8363\uff0c\u5728\u5f53\u524d\u5168\u7403\u94a2\u94c1\u5e02\u573a\u9700\u6c42\u75b2\u8f6f\uff0c\u7279\u522b\u662f\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u4f7f\u5f97\u4e4b\u524d\u5feb\u901f\u589e\u6da8\u7684\u4e9a\u6d32\u94a2\u94c1\u6d88\u8d39\u5e02\u573a\u4e5f\u9677\u5165\u6c89\u5bc2\u800c\u4e0d\u77e5\u51e0\u65f6\u4f1a\u771f\u6b63\u7684\u590d\u82cf\uff0c\u5168\u7403\u4ea7\u4e1a\u8d44\u672c\u5bf9\u6295\u8d44\u94c1\u77ff\u7684\u70ed\u60c5\u6301\u7eed\u964d\u4f4e\uff0c\u53ef\u4ee5\u8bf4\u662f\u5230\u8fbe\u8fd1\u5341\u5e74\u6765\u7684\u4e00\u4e2a\u4f4e\u70b9\uff0c\u9526\u6e56\u9ad8\u8fbe\u5341\u4e94\u4ebf\u7f8e\u5143\u7684\u6295\u8d44\u8ba1\u5212\uff0c\u5bf9\u603b\u4eba\u53e3\u624d\u4e24\u767e\u591a\u4e07\u7684\u897f\u6fb3\u6d32\u653f\u5e9c\u4e0e\u516c\u4f17\u662f\u6781\u6709\u8bf1\u60d1\u529b\u7684\u3002</p><p>\u3000\u3000\u5f53\u9526\u6e56\u5728\u4e2d\u56fd\u9a7b\u73c0\u65af\u603b\u9886\u4e8b\u9986\u5ba3\u4f20\u5341\u4e94\u4ebf\u7f8e\u5143\u3001\u4e00\u5343\u4e07\u5428\u7684\u94c1\u77ff\u77f3\u6295\u8d44\u8ba1\u5212\u4e4b\u540e\uff0c\u5728\u73c0\u65af\u8fd9\u5ea7\u5c0f\u57ce\u6240\u5f15\u8d77\u7684\u8f70\u52a8\u662f\u76f8\u5f53\u70ed\u70c8\u7684\uff0c\u4fdd\u6301\u6c89\u9ed8\u7684\u53ea\u662f\u529b\u62d3\u3001\u5fc5\u548c\u5fc5\u62d3\u4ee5\u53ca\u4e09\u4e95\u3001\u4e09\u83f1\u3001\u4f0a\u6ed5\u5fe0\u7b49\u5728\u73c0\u65af\u7684\u529e\u4e8b\u673a\u6784\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6c60\u4f50\u79c0\u85cf\u516d\u6708\u4e8c\u65e5\u5728\u5317\u4eac\u624d\u77e5\u9053\u9526\u6e56\u5546\u4e8b\u8ba1\u5212\u5728\u897f\u6fb3\u6d32\u6295\u8d44\u94c1\u77ff\u7684\u6d88\u606f\uff0c\u8fd9\u662f\u4ed6\u8fd9\u4e2a\u6708\u6765\u8fde\u7eed\u542c\u5230\u7684\u4e24\u6b21\u5669\u8017\u3002</p><p>\u3000\u3000\u53d7\u4e2d\u6d77\u77f3\u6cb9\u53ef\u80fd\u5728\u6e24\u6d77\u6e7e\u5916\u6d77\u53e3\u53ef\u80fd\u6709\u91cd\u5927\u5730\u8d28\u53d1\u73b0\u7684\u5185\u5e55\u6d88\u606f\u5f71\u54cd\uff0c\u4e4b\u524d\u4e09\u4e95\u7269\u4ea7\u901a\u8fc7\u79cd\u79cd\u6e20\u9053\u7ed9\u4e2d\u6d77\u77f3\u6cb9\u5728\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u4e0a\u5e02\u878d\u8d44\u8ba1\u5212\u65bd\u52a0\u7684\u538b\u529b\u80fd\u53d1\u6325\u7684\u4f5c\u7528\u5df2\u7ecf\u76f8\u5f53\u6709\u9650\u7684\u3002</p><p>\u3000\u3000\u8fd9\u5012\u4e5f\u7f62\u4e86\uff0c\u6c60\u4f50\u79c0\u85cf\u4e5f\u4e0d\u662f\u5355\u7eaf\u7684\u519b\u56fd\u4e3b\u4e49\u8005\uff0c\u4e00\u5b9a\u8981\u5bf9\u4e2d\u56fd\u7684\u5d1b\u8d77\u65bd\u52a0\u5f3a\u529b\u7684\u904f\u5236\uff0c\u7ecf\u6d4e\u6e17\u900f\u624d\u662f\u4e3b\u5bfc\u4e09\u4e95\u5bf9\u534e\u6295\u8d44\u7684\u4e3b\u6d41\u601d\u6f6e\uff0c\u4f46\u662f\u6bcf\u65e6\u60f3\u8d77\u674e\u99a8\u4e88\u8863\u886b\u4e0d\u6574\u4e0e\u5f20\u606a\u540c\u5904\u4e00\u5ba4\u7684\u5efa\u90ba\u6e05\u6668\uff0c\u6c60\u4f50\u79c0\u85cf\u5c31\u6012\u4e0d\u53ef\u904f\u3002</p><p>\u3000\u3000\u201c\u7830\uff01\u201d\u6c60\u4f50\u79c0\u85cf\u4e00\u62f3\u91cd\u91cd\u7684\u6253\u5728\u529e\u516c\u684c\u4e0a\uff0c\u8336\u676f\u7ed9\u9707\u5012\u6eda\u843d\u5230\u5730\u6bef\u4e0a\uff0c\u5f04\u5f97\u5230\u5904\u90fd\u662f\u8336\u6c34\u6e0d\uff0c\u4ed6\u4e5f\u6ca1\u6709\u5fc3\u60c5\u6536\u62fe\u3002</p><p>\u3000\u3000\u5f53\u4ed6\u8d39\u5c3d\u5fc3\u673a\u7684\u53bb\u6276\u6301\u79d1\u738b\u60f3\u8981\u538b\u5236\u9526\u6e56\u6d88\u8d39\u7535\u5b50\u4ea7\u4e1a\u7684\u53d1\u5c55\uff0c\u9526\u6e56\u5374\u5728\u94a2\u94c1\u3001\u6e2f\u53e3\u4ea7\u4e1a\u5f02\u519b\u7a81\u8d77\uff0c\u5982\u4eca\u53c8\u5c06\u89e6\u624b\u4f38\u5411\u4e0a\u6e38\u7684\u94c1\u77ff\u77f3\u8d44\u6e90\uff0c\u800c\u4e14\u4e00\u4e0b\u5b50\u5c31\u8981\u94fa\u5f00\u5341\u4e94\u4ebf\u7f8e\u5143\u7684\u644a\u5b50\uff1b\u4ed6\u4e5f\u5df2\u7ecf\u77e5\u9053\u9526\u6e56\u5546\u4e8b\u662f\u4e2d\u6d77\u77f3\u6cb9\u9999\u6e2f\u4e0a\u5e02\u7684\u6700\u5927\u6697\u76d8\u6295\u8d44\u4eba\u3002</p><p>\u3000\u3000\u4e09\u4e95\u7269\u4ea7\u516d\u5341\u5e74\u4ee3\u4e2d\u53f6\u5c31\u8054\u5408\u4e09\u83f1\u3001\u4f0a\u6ed5\u5fe0\u8fdb\u5165\u6fb3\u5927\u5229\u4e9a\u897f\u6fb3\u5730\u533a\u8fdb\u884c\u94c1\u77ff\u77f3\u8d44\u6e90\u7684\u4ea7\u4e1a\u5e03\u5c40\uff0c\u9526\u6e56\u641e\u8fd9\u4e48\u5927\u7684\u52a8\u4f5c\uff0c\u7adf\u7136\u5c31\u662f\u60f3\u4ece\u4e09\u4e95\u5634\u91cc\u593a\u98df\u3002</p><p>\u3000\u3000\u9526\u6e56\u770b\u4f3c\u5f31\u5c0f\uff0c\u6839\u672c\u4e0d\u8db3\u4ee5\u8ba9\u4e09\u4e95\u4e3a\u654c\uff0c\u4f46\u662f\u9526\u6e56\u5374\u5728\u53bb\u5e74\u516d\u6708\u4e4b\u540e\u5947\u8ff9\u822c\u7684\u83b7\u5f97\u4e1c\u5357\u4e9a\u8bf8\u591a\u534e\u5546\u8d22\u56e2\u7684\u9ad8\u5ea6\u4fe1\u4efb\uff0c\u5728\u53bb\u5e74\u534e\u4eba\u8d44\u672c\u9003\u79bb\u5370\u5c3c\u7684\u671f\u95f4\uff0c\u9526\u6e56\u76f4\u63a5\u5438\u7eb3\u8d44\u672c\u5c31\u9ad8\u8fbe\u5341\u56db\u4ebf\u5230\u5341\u516b\u4ebf\u7f8e\u5143\u4e4b\u591a\uff0c\u8fd8\u4e0e\u5357\u6d0b\u6d77\u8fd0\uff08\u65b0\u52a0\u5761\uff09\u3001\u4e07\u4e1a\u96c6\u56e2\uff08\u8fc1\u5f80\u65b0\u52a0\u5761\uff09\u3001\u4e91\u6e90\u96c6\u56e2\uff08\u9a6c\u4e9a\u897f\u6765\uff09\u7b49\u534e\u5546\u8d22\u56e2\u4ea4\u53c9\u6301\u80a1\uff0c\u5f62\u6210\u5bc6\u5207\u7684\u6218\u7565\u540c\u76df\u5173\u7cfb\u3002</p><p>\u3000\u3000\u8fd9\u662f\u9526\u6e56\u8fd9\u4e00\u5e74\u6765\u4ee5\u9526\u6e56\u5546\u4e8b\u7684\u540d\u4e49\u80fd\u5728\u56fd\u5185\u3001\u56fd\u5916\u8fde\u7eed\u5927\u6269\u5f20\u7684\u57fa\u7840\u3002</p><p>\u3000\u3000\u66f4\u4ee4\u4eba\u5fc3\u60b8\u7684\uff0c\u5728\u53bb\u5e74\u516d\u6708\u5230\u5341\u4e8c\u6708\u95f4\uff0c\u603b\u5171\u6709\u516d\u5341\u4ebf\u5230\u516b\u5341\u4ebf\u7f8e\u5143\u7684\u5370\u5c3c\u534e\u4eba\u8d44\u672c\u88ab\u9526\u6e56\u76f4\u63a5\u5f15\u5bfc\u6d41\u5411\u4e1c\u6d77\u3001\u6c5f\u5357\u4e24\u7701\uff0c\u8fd9\u4e9b\u8d44\u672c\u7531\u4e8e\u4e0e\u9526\u6e56\u7684\u5bc6\u5207\u5173\u7cfb\uff0c\u968f\u65f6\u90fd\u53ef\u80fd\u6210\u4e3a\u9526\u6e56\u5546\u4e8b\u5728\u6d77\u5916\u6218\u7565\u6269\u5f20\u7684\u65b0\u751f\u529b\u91cf\u3002</p><p>\u3000\u3000\u6b64\u65f6\u7684\u9526\u6e56\uff0c\u5df2\u7ecf\u4e0d\u662f\u4e00\u5e74\u524d\u770b\u4e0a\u53bb\u7684\u90a3\u4e48\u5f31\u4e0d\u7981\u98ce\u4e86\u3002</p><p>\u3000\u3000\u6c60\u4f50\u79c0\u85cf\u7709\u5934\u7d27\u9501\u7740\uff0c\u6b64\u65f6\u5bf9\u9526\u6e56\u7684\u904f\u5236\uff0c\u5df2\u7ecf\u4e0d\u662f\u4ed6\u7684\u80fd\u529b\u8303\u56f4\u4e86\uff0c\u4ed6\u7684\u804c\u6743\u662f\u534f\u8c03\u4e09\u4e95\u5728\u534e\u7684\u4e1a\u52a1\uff0c\u5f53\u9526\u6e56\u7684\u53d1\u5c55\u5df2\u7ecf\u8fdc\u8fdc\u4e0d\u518d\u5c40\u9650\u4e8e\u4e2d\u56fd\u56fd\u5185\uff0c\u90a3\u5c31\u5e94\u8be5\u662f\u8d22\u9600\u603b\u90e8\u8981\u5f15\u8d77\u8db3\u591f\u7684\u91cd\u89c6\u3002</p><p>\u3000\u3000\u4ed6\u770b\u7740\u684c\u4e0a\u7684\u4fe1\u7eb8\uff0c\u659f\u914c\u7740\u7ed9\u8d22\u9600\u9012\u4ea4\u7684\u62a5\u544a\u5e94\u8be5\u600e\u4e48\u7528\u8bcd\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u516d\u6708\u4e8c\u65e5\u674e\u5728\u6d19\u5728\u5efa\u90ba\uff0c\u4ed6\u7279\u610f\u8dd1\u5230\u4e1c\u6d77\u5927\u5b66\u4e0e\u97f3\u4e50\u5b66\u9662\u4e4b\u95f4\u7684\u7231\u8fbe\u56fe\u4e66\u9986\u770b\u4e86\u4e00\u770b\u3002</p><p>\u3000\u3000\u5efa\u8bbe\u7684\u6b65\u4f10\u76f8\u5f53\u5feb\u554a\uff0c\u4f3c\u4e4e\u4ece\u4f20\u51fa\u6d88\u606f\u6765\u90fd\u6ca1\u6709\u8fc7\u53bb\u51e0\u4e2a\u6708\uff0c\u8bfa\u5927\u7684\u7acb\u4f53\u51e0\u4f55\u9020\u578b\u7684\u516d\u5c42\u5efa\u7b51\u5c31\u8038\u7acb\u5728\u773c\u524d\uff0c\u674e\u5728\u6d19\u8bb0\u5f97\u4e4b\u524d\u8fd9\u91cc\u662f\u7b80\u964b\u7684\u6c11\u623f\uff0c\u8def\u8fb9\u7684\u4eba\u884c\u9053\u4e5f\u5751\u5751\u6d3c\u6d3c\uff0c\u6ca1\u60f3\u5230\u8fd9\u4e48\u77ed\u7684\u65f6\u95f4\u91cc\u5c31\u6536\u62fe\u4e00\u65b0\u3002</p><p>\u3000\u3000\u4ece\u4ed6\u7ad9\u7684\u89d2\u5ea6\u53ea\u80fd\u770b\u5230\u56fe\u4e66\u9986\u5185\u90e8\u6b63\u5728\u8fdb\u884c\u7d27\u5f20\u7684\u88c5\u6f62\u5de5\u7a0b\uff0c\u4e0d\u80fd\u65e0\u6545\u7684\u8d70\u8fdb\u53bb\u53c2\u89c2\uff0c\u9526\u6e56\u4ee5\u7231\u8fbe\u96c6\u56e2\u540d\u4e49\u8fdb\u884c\u8fd9\u9879\u6350\u8d60\uff0c\u4e3a\u7231\u8fbe\u96c6\u56e2\u8d62\u6765\u5f88\u9ad8\u7684\u58f0\u8a89\uff0c\u4e2d\u56fd\u5ba3\u4f20\u90e8\u95e8\u4f3c\u4e4e\u4e5f\u4e50\u610f\u7ed9\u9526\u6e56\u8fd9\u79cd\u5bf9\u6587\u5316\u4e8b\u4e1a\u8fdb\u884c\u6350\u52a9\u7684\u884c\u4e3a\u8fdb\u884c\u5927\u89c4\u6a21\u7684\u5ba3\u4f20\uff0c\u8fd9\u4e9b\u90fd\u65e0\u6cd5\u80fd\u7a33\u56fa\u7231\u8fbe\u5728\u4e2d\u56fd\u6d88\u8d39\u7535\u5b50\u4ea7\u54c1\u5e02\u573a\u4e0a\u7684\u5730\u4f4d\u3002</p><p>\u3000\u3000\u4e09\u661f\u96c6\u56e2\u867d\u7136\u4e5f\u8d70\u7efc\u5408\u5546\u793e\u7684\u53d1\u5c55\u6a21\u5f0f\uff0c\u4f46\u662f\u7ecf\u8fc7\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u7684\u51b2\u51fb\uff0c\u6269\u5f20\u7684\u6b65\u4f10\u6709\u6240\u6536\u655b\uff0c\u53bb\u5e74\u751a\u81f3\u679c\u65ad\u7684\u780d\u6389\u4e00\u4e9b\u679d\u53f6\uff0c\u5c06\u7cbe\u529b\u653e\u5728\u4e09\u661f\u7535\u5b50\u7684\u53d1\u5c55\u4e0a\u6765\u3002</p><p>\u3000\u3000\u5bf9\u674e\u5728\u6d19\u6765\u8bf4\uff0c\u4ed6\u66f4\u5173\u5fc3\u4e0e\u9526\u6e56\u65d7\u4e0b\u7231\u8fbe\u96c6\u56e2\u5728\u4e2d\u56fd\u6d88\u8d39\u7535\u5b50\u4ea7\u54c1\u5e02\u573a\u7684\u7ade\u4e89\uff0c\u9526\u6e56\u5728\u897f\u6fb3\u6d32\u7684\u52a8\u4f5c\uff0c\u5e94\u8be5\u7531\u6d66\u9879\u5236\u94c1\u53bb\u5934\u75bc\u3002</p><p>\u3000\u3000\u6bd4\u8d77\u9526\u6e56\u5728\u897f\u6fb3\u6d32\u7684\u52a8\u4f5c\uff0c\u674e\u5728\u6d19\u66f4\u5173\u5fc3\u6606\u817e\u5728\u7ebf\u4eca\u5929\u5728\u7f8e\u56fd\u7eb3\u65af\u8fbe\u514b\u7684\u4e0a\u5e02\u6d88\u606f\u3002\u6bd5\u7adf\u7231\u8fbe\u96c6\u56e2\u65d7\u4e0b\u7684\u6a61\u6811\u56ed\u521b\u4e1a\u6295\u8d44\u57fa\u91d1\u662f\u6606\u817e\u5728\u7ebf\u6700\u4e3b\u8981\u7684\u6295\u8d44\u8005\uff0c\u6606\u817e\u5728\u7ebf\u7684\u6210\u529f\u4e0a\u5e02\uff0c\u610f\u5473\u7740\u9526\u6e56\u80fd\u4ece\u7eb3\u65af\u8fbe\u514b\u5957\u51fa\u66f4\u591a\u7684\u8d44\u91d1\u7528\u4e8e\u5fae\u7535\u5b50\u6280\u672f\u4e0e\u4ea7\u4e1a\u7684\u53d1\u5c55\u3002</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u7684\u80a1\u6743\u51e0\u7ecf\u8c03\u6574\uff0c\u4e0a\u5e02\u524d\u603b\u80a1\u672c\u4e3a5000\u4e07\u80a1\uff0c\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u7b49\u7ba1\u7406\u5c42\u6301\u80a1\u7ea6800\u4e07\u80a1\uff0c\u6a61\u6811\u56ed\u521b\u6295\u76f4\u63a5\u6301\u80a1\u7ea6700\u4e07\u80a1\uff0c\u9ad8\u76db\u65d7\u4e0b\u4ee5\u53ca\u6700\u65e9\u5411\u6606\u817e\u5728\u7ebf\u6295\u8d44\u7684\u534e\u767b\u7cfb\u7b49\u98ce\u6295\u57fa\u91d1\u6301\u80a118000\u4e07\u80a1\uff0c\u8fd8\u67091700\u4e07\u80a1\u5728\u6700\u540e\u4e00\u6b21\u80a1\u6743\u8c03\u6574\u4e2d\u7ed9\u6a61\u6811\u56ed\u521b\u6295\u4ee5\u4e09\u5343\u516b\u767e\u4e07\u7f8e\u5143\u7684\u4ef7\u683c\u8f6c\u8ba9\u7ed9ESS\u65d7\u4e0b\u7684\u57fa\u91d1\u7ec4\u7ec7\u2014\u2014ESS\u4f5c\u4e3a\u7231\u8fbe\u96c6\u56e2\u5728\u7f8e\u56fd\u7684\u91cd\u578b\u5b9e\u9a8c\u57fa\u5730\uff0c\u674e\u5728\u6d19\u662f\u518d\u6e05\u695a\u4e0d\u8fc7\u4e86\u3002</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u6b64\u6b21\u5728\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\u62df\u516c\u5f00\u53d1\u884c750\u4e07\u65b0\u80a1\uff0c\u516c\u5f00\u53d1\u884c\u4ef7\u4e3a3.4\u7f8e\u5143\uff0c\u52df\u96c6\u4e24\u5343\u4e94\u767e\u591a\u4e07\u7f8e\u5143\u7684\u8d44\u91d1\u3002</p><p>\u3000\u3000\u7ecf\u8fc7\u51e0\u6b21\u98ce\u6295\u52df\u8d44\uff0c\u6606\u817e\u5728\u7ebf\u5df2\u7ecf\u5438\u8db3\u4e86\u53d1\u5c55\u8d44\u91d1\uff0c\u6700\u540e\u4e00\u6b21\u4e0a\u5e02\u524d\u80a1\u6743\u8c03\u6574\uff0c\u5c31\u4ee53\u7f8e\u5143\u6bcf\u80a1\u7684\u4ef7\u683c\u5411\u9ad8\u76db\u65d7\u4e0b\u98ce\u6295\u57fa\u91d1\u589e\u53d1\u7ea6400\u4e07\u7684\u65b0\u80a1\uff0c\u4ec5\u6b64\u4e00\u9879\u5c31\u52df\u96c6\u4e86\u4e00\u5343\u4e24\u767e\u4e07\u7f8e\u5143\uff0c\u6606\u817e\u5728\u7ebf\u662f\u4e0d\u7f3a\u4e4f\u53d1\u5c55\u8d44\u91d1\u7684\uff0c\u516c\u5f00\u4e0a\u5e02\u53ea\u662f\u7ed9\u4e4b\u524d\u8fdb\u5165\u7684\u98ce\u6295\u4ece\u6606\u817e\u5728\u7ebf\u76c8\u5229\u64a4\u51fa\u7684\u673a\u4f1a\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u770b\u4e86\u770b\u8155\u8868\uff0c\u73b0\u5728\u624d\u662f\u5317\u4eac\u65f6\u95f4\u4e0b\u5348\u56db\u70b9\u949f\uff0c\u79bb\u7f8e\u56fd\u7eb3\u65af\u8fbe\u514b\u4e0a\u5348\u7684\u5f00\u76d8\u65f6\u95f4\u8fd8\u65e9\uff0c\u4ed6\u5fc3\u60f3\u9526\u6e56\u7684\u9ad8\u5c42\u5927\u6982\u90fd\u5168\u805a\u7f8e\u56fd\u7b49\u7740\u5e86\u795d\u6606\u817e\u5728\u7ebf\u4e0a\u5e02\u6210\u529f\uff0c\u4e0d\u8fc7\u636e\u516c\u5f00\u7684\u6d88\u606f\uff0c\u9664\u4e86\u7231\u8fbe\u96c6\u56e2\u526f\u603b\u88c1\u82cf\u6d25\u4e1c\u4e0e\u6a61\u6811\u56ed\u521b\u6295\u603b\u88c1\u8d75\u6893\u7433\u4e0e\u6606\u817e\u5728\u7ebf\u7684\u4e3b\u8981\u7ba1\u7406\u5c42\u7387\u4e0e\u4e2d\u56fd\u5a92\u4f53\u56e2\u961f\u7ed3\u961f\u524d\u5f80\u7f8e\u56fd\u4e4b\u5916\uff0c\u5012\u6ca1\u6709\u542c\u8bf4\u8fd8\u6709\u9526\u6e56\u54ea\u4e2a\u9ad8\u5c42\u524d\u5f80\u7f8e\u56fd\u7684\u3002</p><p>\u3000\u3000\u60f3\u60f3\u4e5f\u5bf9\uff0c\u5c31\u7b97\u65b0\u79d1\u6280\u6d6a\u6f6e\u8fd8\u4f1a\u6301\u7eed\u6c79\u6d8c\u4e0b\u53bb\uff0c\u7b49\u516d\u4e2a\u6708\u7684\u7981\u552e\u671f\u8fc7\u53bb\uff0c\u6606\u817e\u5728\u7ebf\u80a1\u4ef7\u8ddd\u53d1\u884c\u4ef7\u518d\u6da8\u4e00\u500d\uff0c\u7231\u8fbe\u96c6\u56e2\u6240\u6301\u7684\u80a1\u7968\u5e02\u503c\u603b\u989d\u4e5f\u4e0d\u8fc7\u4e00\u4ebf\u4e94\u5343\u4e07\u7f8e\u5143\uff0c\u5bf9\u4e8e\u5df2\u6709\u5982\u6b64\u89c4\u6a21\u7684\u9526\u6e56\u6765\u8bf4\uff0c\u7684\u786e\u6ca1\u6709\u5fc5\u8981\u592a\u6fc0\u52a8\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u6536\u56de\u601d\u7eea\uff0c\u6253\u7b97\u5750\u8fdb\u8f66\u91cc\u8fd4\u56de\u9152\u5e97\uff0c\u99a8\u4e88\u4e5f\u501f\u53e3\u5b66\u6821\u91cc\u6709\u8bfe\u8981\u4e0a\uff0c\u4e0d\u613f\u8ddf\u4ed6\u89c1\u9762\uff0c\u4ed6\u4e5f\u4e0d\u77e5\u9053\u99a8\u4e88\u8ddf\u5f20\u606a\u7a76\u7adf\u53d1\u5c55\u5230\u54ea\u4e00\u6b65\uff0c\u662f\u5076\u5c14\u7684\u5bfb\u6b22\u4f5c\u4e50\uff0c\u8fd8\u662f\u7518\u613f\u6210\u4e3a\u5f20\u606a\u80cc\u540e\u7684\u4e00\u4eba\u5973\u4eba\uff1f\u4f20\u51fa\u53bb\u771f\u662f\u5bb6\u65cf\u7684\u803b\u8fb1\uff0c\u4f46\u662f\u8fd9\u6837\u82e5\u80fd\u5f15\u8d77\u6c60\u4f50\u79c0\u85cf\u5bf9\u9526\u6e56\u7684\u5f3a\u70c8\u654c\u89c6\u4e5f\u7b97\u662f\u503c\u5f97\u7684\uff0c\u674e\u5728\u6d19\u51b3\u5b9a\u7ee7\u7eed\u5bf9\u5bb6\u65cf\u9690\u7792\u8fd9\u4e9b\u6d88\u606f\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u521a\u94bb\u8fdb\u8f66\u91cc\u5173\u4e0a\u8f66\u95e8\uff0c\u770b\u89c1\u4e00\u8f86\u5954\u9a70\u4ece\u8857\u89d2\u62d0\u6765\uff0c\u674e\u5728\u6d19\u8ba9\u53f8\u673a\u7a0d\u7f13\u4e00\u4e0b\uff0c\u770b\u5230\u5954\u9a70\u8f66\u505c\u5728\u56fe\u4e66\u9986\u524d\u5f20\u606a\u4ece\u91cc\u9762\u94bb\u51fa\u6765\uff0c\u4ed6\u5fae\u5fae\u4e00\u6014\uff0c\u5fc3\u60f3\uff1a\u5f20\u606a\u4ec0\u4e48\u65f6\u5019\u56de\u56fd\u4e86\uff1f</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u770b\u5230\u674e\u5728\u6d19\u5750\u7684\u8f66\uff0c\u4ed6\u521a\u521a\u73c0\u65af\u7ecf\u9999\u6e2f\u8f6c\u673a\u56de\u5efa\u90ba\uff0c\u53f6\u5efa\u658c\u5c31\u5728\u9999\u6e2f\u4e0b\u4e86\u98de\u673a\uff0c\u4ed6\u8fd8\u8981\u4eb2\u81ea\u76ef\u7740\u4e2d\u6d77\u6cb9\u4e0a\u5e02\u4e00\u4e8b\u2014\u2014\u7fdf\u4e39\u9752\u8ddf\u5f20\u606a\u4e00\u8d77\u8fd4\u56de\u5efa\u90ba\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u6ca1\u6709\u5f20\u606a\u8fd9\u4e48\u60a0\u95f2\uff0c\u4ece\u673a\u573a\u56de\u6765\u5c31\u6765\u770b\u56fe\u4e66\u9986\u7684\u5efa\u8bbe\u8fdb\u5ea6\uff0c\u7fdf\u4e39\u9752\u8981\u4e3a\u5979\u518d\u6b21\u524d\u5f80\u73c0\u65af\u4e3b\u6301\u5927\u5c40\u505a\u51c6\u5907\u3002</p><p>\u3000\u3000\u5efa\u90ba\u7684\u5de5\u4f5c\u8981\u4e0e\u53f6\u5c0f\u6850\u4ea4\u63a5\uff0c\u8981\u4ece\u9526\u6e56\u5404\u516c\u53f8\u62bd\u8c03\u7cbe\u82f1\u7ec4\u6210\u9a7b\u73c0\u65af\u529e\u4e8b\u673a\u6784\u7684\u7cbe\u82f1\u56e2\u961f\uff0c\u8fd8\u8981\u4e0e\u4e2d\u51b6\u603b\u516c\u53f8\u4ee5\u53ca\u51b6\u91d1\u5de5\u4e1a\u5c40\u7684\u5b98\u5458\u63a5\u89e6\u2014\u2014\u9526\u6e56\u8981\u5728\u6d77\u5916\u8fdb\u884c\u77ff\u4ea7\u8d44\u6e90\u6269\u5f20\uff0c\u79bb\u4e0d\u5f00\u56fd\u5185\u51b6\u91d1\u7cfb\u7edf\u7684\u652f\u6301\u3002</p><p>\u3000\u3000\u9526\u6e56\u53d7\u56fd\u5185\u4ea7\u4e1a\u653f\u7b56\u7684\u8282\u7ea6\uff0c\u65e0\u6cd5\u5927\u4e3e\u5728\u56fd\u5185\u5927\u4e3e\u8fdb\u519b\u57fa\u7840\u8d44\u6e90\u4ea7\u4e1a\u9886\u57df\uff0c\u4f46\u662f\u5728\u6d77\u5916\u7684\u6269\u5f20\u5374\u662f\u4e0d\u53d7\u56fd\u5185\u653f\u7b56\u9650\u5236\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u772f\u7740\u773c\uff0c\u770b\u7740\u8fdc\u5904\u697c\u9876\u7684\u5915\u9633\uff0c\u8fd9\u6b21\u79bb\u5f00\u5efa\u90ba\u53c8\u662f\u5c06\u8fd1\u4e00\u4e2a\u6708\u65f6\u95f4\uff0c\u8fd9\u6b21\u4e0d\u6653\u5f97\u80fd\u5728\u5efa\u90ba\u505c\u7559\u591a\u4e45\uff0c\u770b\u56fe\u4e66\u9986\u7684\u5efa\u8bbe\u8fdb\u5ea6\uff0c\u5728\u79cb\u5b63\u5f00\u5b66\u4e4b\u524d\u5efa\u6210\u6295\u5165\u4f7f\u7528\uff0c\u5e94\u8be5\u4e0d\u5b58\u5728\u592a\u5927\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u5f20\u606a\u772f\u773c\u770b\u4e86\u4e00\u4f1a\u513f\uff0c\u8f6c\u8eab\u5f80\u5b66\u5e9c\u5df7\u8d70\u53bb\uff0c\u7fdf\u4e39\u9752\u8981\u53bb\u73c0\u65af\uff0c\u9759\u6aac\u4e5f\u8981\u53bb\u65b0\u52a0\u5761\u7559\u5b66\uff0c\u70ed\u70ed\u95f9\u95f9\u7684\u5efa\u90ba\u53c8\u8981\u7a7a\u65f7\u8d77\u6765\u4e86\uff0c\u8d70\u5230\u5b66\u5e9c\u5df7\u5185\u5df7\u62d0\u89d2\uff0c\u63a5\u5230\u53f6\u5efa\u658c\u6253\u6765\u7684\u7535\u8bdd\u3002</p><p>\u3000\u3000\u201c\u624d\u5206\u5f00\u4e0d\u5230\u56db\u4e2a\u5c0f\u65f6\uff0c\u5c31\u53c8\u60f3\u8d77\u6211\u6765\u4e86\uff1f\u201d\u5f20\u606a\u8ddf\u53f6\u5efa\u658c\u5f00\u73a9\u7b11\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u8c01\u6ca1\u4e8b\u4f1a\u60f3\u4f60\u554a\uff0c\u201d\u53f6\u5efa\u658c\u8bf4\u9053\uff0c\u201c\u6211\u521a\u521a\u8ddf\u4e2d\u6d77\u77f3\u6cb9\u7684\u5218\u6210\u536b\u89c1\u8fc7\u9762\uff0c\u65b0\u6c82\u6cb9\u7530\u7684\u95ee\u9898\u8fd8\u662f\u51fa\u4e86\u5185\u9b3c\uff0c\u8d1f\u8d23\u4e0e\u8096\u5eb7\u6069\u77f3\u6cb9\u516c\u53f8\u8fdb\u884c\u5408\u4f5c\u52d8\u63a2\u7684\u4e2d\u65b9\u8d1f\u8d23\u4eba\u88ab\u62d6\u4e0b\u4e86\u6c34\uff0c\u5df2\u7ecf\u7ed9\u53cc\u89c4\u4e86\u3002\u7ecf\u8d38\u59d4\u4ee5\u53ca\u4e2d\u6d77\u77f3\u6cb9\u8fd8\u662f\u6ca1\u6709\u8ddf\u8096\u5eb7\u6069\u77f3\u6cb9\u516c\u53f8\u6495\u7834\u8138\u76ae\u7684\u610f\u601d\uff0c\u6b64\u4e8b\u53ea\u4f1a\u505a\u5185\u90e8\u5904\u7406\uff0c\u4e0d\u8fc7\u4f1a\u5229\u7528\u6b64\u4e8b\u8ba9\u8096\u5eb7\u6069\u77f3\u6cb9\u516c\u53f8\u5728\u5408\u4f5c\u4e2d\u505a\u51fa\u8ba9\u6b65\u2014\u2014\u5bf9\u4e0a\u5e02\u5e94\u8be5\u6ca1\u6709\u5927\u7684\u5f71\u54cd\u3002\u201d</p><p>\u3000\u3000\u201c\u5546\u4eba\u91cd\u5229\uff0c\u6211\u4eec\u4e5f\u662f\u5546\u4eba\uff0c\u201d\u5f20\u606a\u5fae\u5fae\u4e00\u7b11\uff0c\u5728\u7535\u8bdd\u8ddf\u53f6\u5efa\u658c\u8bf4\u9053\uff0c\u201c\u8fd9\u4ef6\u4e8b\u6345\u51fa\u53bb\uff0c\u8096\u5eb7\u6069\u77f3\u6cb9\u516c\u53f8\u4f1a\u5728\u7f8e\u56fd\u56fd\u5185\u53d7\u5230\u91cd\u7f5a\uff0c\u5bf9\u6211\u4eec\u4f3c\u4e4e\u4e5f\u6ca1\u6709\u7279\u522b\u7684\u597d\u5904\uff0c\u8fd8\u8981\u803d\u6401\u4e2d\u6d77\u77f3\u6cb9\u4e0a\u5e02\u878d\u8d44\u8ba1\u5212\uff0c\u5185\u90e8\u5904\u7406\u5c31\u5185\u90e8\u5904\u7406\u597d\u4e86\uff0c\u53ea\u8981\u4e0d\u653e\u8fc7\u5185\u9b3c\u5c31\u53ef\u4ee5\u4e86\u2014\u2014\u201d</p><p>\u3000\u3000\u5982\u6b64\u4e00\u6765\uff0c\u5012\u662f\u80fd\u786e\u8ba4\u65b0\u6c82\u6d77\u57df\u5b58\u5728\u5927\u91cf\u7684\u6cb9\u6c14\u8d44\u6e90\uff0c\u4e2d\u6d77\u77f3\u6cb9\u5728\u65b0\u4ead\u5efa\u70bc\u5316\u57fa\u5730\uff0c\u5012\u4e0d\u7528\u62c5\u5fc3\u539f\u6cb9\u6765\u6e90\u4e86\uff0c\u4e2d\u6d77\u77f3\u6cb9\u5f00\u53d1\u65b0\u6c82\u6d77\u57df\u53ca\u5176\u4ed6\u4e1c\u6d77\u6d77\u57df\u7684\u6cb9\u6c14\u8d44\u6e90\uff0c\u751f\u4ea7\u6210\u719f\u4e4b\u540e\uff0c\u5dee\u4e0d\u591a\u6bcf\u5e74\u80fd\u589e\u52a0\u4e00\u4ebf\u6876\u539f\u6cb9\u4ea7\u91cf\uff0c\u8db3\u4ee5\u652f\u6491\u5343\u4e07\u5428\u7ea7\u7684\u70bc\u6cb9\u5382\u539f\u6599\u4f9b\u5e94\u3002</p><p>\u3000\u3000\u5c31\u4e2d\u6d77\u77f3\u6cb9\u4eca\u540e\u51e0\u5e74\u7684\u53d1\u5c55\u89c4\u6a21\uff0c\u8fd9\u6b21\u5c06\u4ece\u9999\u6e2f\u8d44\u672c\u5e02\u573a\u52df\u96c6\u7684\u5341\u4ebf\u7f8e\u5143\u8d44\u91d1\u4e5f\u662f\u8fdc\u8fdc\u4e0d\u591f\u7684\uff0c\u4e0d\u8fc7\u996d\u603b\u8981\u4e00\u53e3\u4e00\u53e3\u7684\u5403\uff0c\u4e2d\u6d77\u77f3\u6cb9\u80fd\u5728\u6210\u54c1\u6cb9\u9886\u57df\u53d1\u5c55\u58ee\u5927\uff0c\u81f3\u5c11\u80fd\u9650\u5236\u4e00\u4e0b\u4e2d\u77f3\u6cb9\u3001\u4e2d\u77f3\u5316\u5bf9\u56fd\u5185\u5e02\u573a\u7684\u8d2a\u5fc3\uff0c\u8fd8\u80fd\u52a0\u901f\u4e2d\u56fd\u5bf9\u6d77\u5916\u77f3\u6cb9\u80fd\u6e90\u4ea7\u4e1a\u7684\u5e03\u5c40\u3002</p><p>\u3000\u3000\u4e0e\u53f6\u5efa\u658c\u8bf4\u7740\u8bdd\uff0c\u5c31\u5230\u4e861978\u95e8\u5916\uff0c\u5f20\u606a\u63a2\u5934\u5f80\u91cc\u770b\u4e86\u770b\uff0c\u6ca1\u770b\u5230\u5b59\u9759\u6aac\u7684\u8eab\u5f71\uff1a\u8fd9\u59ae\u5b50\u4e00\u58f0\u4e0d\u542d\u7684\u5c31\u8ba1\u5212\u53bb\u65b0\u52a0\u5761\u7559\u5b66\u3002</p><p>\u3000\u3000\u201c\u53c8\u662f\u597d\u4e45\u6ca1\u89c1\uff0c\u201d\u7ad9\u5728\u5427\u53f0\u91cc\u7684\u4f55\u5f26\u770b\u5230\u5f20\u606a\u5728\u95e8\u53e3\u63a2\u5934\u63a2\u8111\u7684\uff0c\u8ddf\u4ed6\u6253\u62db\u547c\u9053\uff0c\u201c\u4f60\u627e\u54ea\u4f4d\u4f73\u4eba\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u6709\u597d\u4e45\u6ca1\u89c1\u5417\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u600e\u4e48\u611f\u89c9\u6628\u5929\u624d\u89c1\u8fc7\u4f60\u4f3c\u7684\uff1f\u201d</p><p>\u3000\u3000\u4f55\u5f26\u77e5\u9053\u8981\u6597\u5634\u4e0d\u662f\u5f20\u606a\u7684\u5bf9\u624b\uff0c\u5ae3\u7136\u7b11\u7740\u95ee\u5f20\u606a\u559d\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u4e1c\u5927\u4e1c\u95e8\u5230\u556c\u56ed\u8def\u8fd9\u6bb5\u5185\u5df7\u5dee\u4e0d\u591a\u7ed9\u9152\u5427\u5360\u6ee1\uff0c\u4e0b\u5348\u4f1a\u8425\u4e1a\u7684\u9152\u5427\u6ca1\u6709\u51e0\u5bb6\uff0c\u5f20\u606a\u5f53\u7136\u4e0d\u4f1a\u5728\u9ec4\u660f\u91cc\u72ec\u81ea\u5750\u5728\u5427\u524d\u559d\u9152\uff0c\u8ba9\u4f55\u5f26\u5e2e\u4ed6\u51b2\u4e86\u4e00\u676f\u5496\u5561\u3002\u8fd9\u65f6\u95f4\uff0c1978\u91cc\u4e5f\u6ca1\u6709\u591a\u5c11\u4eba\uff0c\u5f20\u606a\u5750\u5728\u5427\u53f0\u524d\uff0c\u8fb9\u559d\u5496\u5561\u8fb9\u8ddf\u4f55\u5f26\u804a\u5929\uff0c\u4e00\u676f\u5496\u5561\u559d\u5b8c\u8fd8\u8981\u7eed\u676f\u65f6\uff0c\u5b59\u9759\u6aac\u8d70\u4e86\u8fdb\u6765\u3002</p><p>\u3000\u3000\u201c\u51b3\u5b9a\u4e86\u53bb\u65b0\u52a0\u5761\uff1f\u201d\u5f20\u606a\u4ece\u5427\u53f0\u4e0a\u53c8\u62ff\u4e86\u4e00\u53ea\u676f\u5b50\u4e0b\u6765\uff0c\u5206\u4e86\u534a\u676f\u5496\u5561\u7ed9\u9759\u6aac\u3002</p><p>\u3000\u3000\u201c\u55ef\uff0c\u201d\u5b59\u9759\u6aac\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff0c\u201c\u6765\u5efa\u90ba\u8bfb\u4e66\u4e4b\u524d\u5176\u5b9e\u5c31\u51b3\u5b9a\u53bb\u65b0\u52a0\u5761\u5b66\u821e\u8e48\u7684\uff0c\u56db\u5e74\u524d\u6ca1\u80fd\u53bb\u6210\uff0c\u8ddf\u6211\u59d0\u5230\u5efa\u90ba\u6765\u2014\u2014\u8fd9\u6b21\u53bb\u65b0\u52a0\u5761\u5012\u4e0d\u80fd\u518d\u5b66\u821e\u8e48\u4e86\uff0c\u4eba\u90fd\u8001\u4e86\u2014\u2014\u5b66\u4e9b\u4e1c\u897f\uff0c\u53ef\u4ee5\u53bb\u5e2e\u6211\u59d0\u59d0\uff0c\u6211\u53ef\u4e0d\u60f3\u5f53\u4e00\u8f88\u5b50\u7684\u86c0\u866b\u3002\u201d</p><p>\u3000\u3000\u201c\u5475\u5475\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u5b59\u9759\u6aac\u5c31\u6bd4\u4ed6\u5927\u516b\u4e2a\u6708\uff0c\u518d\u8bf4\u5b59\u5c1a\u4e49\u592b\u5987\u8f6c\u5230\u5b59\u9759\u9999\u3001\u5b59\u9759\u6aac\u4e24\u59d0\u59b9\u540d\u4e0b\u7684\u80a1\u4efd\u53ef\u4ee5\u4f9b\u5979\u4eec\u5f53\u516b\u8f88\u5b50\u7684\u86c0\u866b\u4e86\uff0c\u95ee\u9053\uff0c\u201c\u4f60\u8d70\u4e86\uff0c1978\u600e\u4e48\u529e\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u77e5\u9053\u554a\uff0c\u201d\u5b59\u9759\u6aac\u62bf\u7740\u5634\u5507\uff0c\u8bf4\u9053\uff0c\u201c\u675c\u98de\u90a3\u5c0f\u5b50\u4e00\u8eab\u7684\u94dc\u81ed\u5473\uff0c1978\u603b\u4e0d\u80fd\u8f6c\u7ed9\u521b\u57df\uff0c\u6216\u8bb8\u5c31\u5173\u6389\u5427\u3002\u201d</p><p>\u3000\u3000\u201c\u5173\u6389\u592a\u53ef\u60dc\u4e86\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u6211\u5176\u5b9e\u5f88\u7a7a\u95f2\u7684\uff0c\u6211\u5e2e\u4f60\u7167\u6599\u5427\u3002\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u6ca1\u6709\u611f\u52a8\u5230\u6cea\u773c\u5a46\u5a11\uff0c\u53cd\u800c\u72d0\u7591\u7684\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u95ee\u9053\uff1a\u201c\u4e0d\u4f1a\u501f\u673a\u60f3\u52fe\u642d\u522b\u7684\u5973\u5b69\u5b50\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u65e0\u5948\u7684\u82e6\u7b11\u7740\uff0c\u8bf4\u9053\uff0c\u201c\u5efa\u90ba\u5230\u65b0\u52a0\u5761\u7684\u76f4\u822a\u4e0b\u534a\u5e74\u53ef\u80fd\u4e5f\u4f1a\u5f00\u901a\uff0c\u4f60\u7ecf\u5e38\u56de\u5efa\u90ba\u770b\u770b\u5c31\u77e5\u9053\u3002\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u89c1\u5f20\u606a\u4e5f\u5173\u5fc3\u7740\u5efa\u90ba\u4e0e\u65b0\u52a0\u5761\u4e4b\u95f4\u7684\u76f4\u822a\u6d88\u606f\uff0c\u5b89\u9759\u7684\u6ca1\u6709\u518d\u8ddf\u4ed6\u6597\u5634\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u8bf4\u9053\uff1a\u201c\u6709\u4f60\u7167\u65991978\u4e5f\u662f\u597d\u7684\uff0c\u6211\u4e5f\u4e0d\u820d\u5f97\u5c31\u8fd9\u6837\u5173\u6389\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6253\u7b97\u4e0e\u5b59\u9759\u6aac\u5355\u72ec\u627e\u5730\u65b9\u5403\u996d\u7684\uff0c\u53ef\u60dc\u675c\u98de\u7684\u55c5\u89c9\u7075\u654f\uff0c\u76f4\u63a5\u52301978\u5c06\u4ed6\u4eec\u5835\u4e86\u4e2a\u6b63\u7740\u3002</p><p>\u3000\u3000\u201c\u6606\u817e\u5728\u7ebf\u4eca\u5929\u591c\u91cc\u5728\u7f8e\u56fd\u4e0a\u5e02\uff0c\u4f60\u77e5\u9053\u7275\u52a8\u56fd\u5185\u591a\u5c11\u4eba\u7684\u5fc3\u601d\uff0c\u4f60\u5012\u662f\u4e00\u70b9\u90fd\u4e0d\u5728\u4e4e\u554a\uff1f\u201d\u675c\u98de\u8d70\u8fdb\u6765\uff0c\u770b\u7740\u5f20\u606a\u8ddf\u5b59\u9759\u6aac\u3001\u4f55\u5f26\u56f4\u7740\u5427\u53f0\u60a0\u95f2\u7684\u559d\u7740\u5496\u5561\u3002</p><p>\u3000\u3000\u201c\u6709\u4ec0\u4e48\u597d\u5728\u4e4e\u7684\uff1f\u201d\u5f20\u606a\u7b11\u7740\u95ee\uff0c\u9526\u6e56\u624b\u91cc\u6240\u6301\u7684\u6606\u817e\u5728\u7ebf\u80a1\u7968\u6700\u5feb\u4e5f\u8981\u5230\u516d\u4e2a\u6708\u540e\u624d\u8fc7\u7981\u552e\u671f\uff0c\u4eca\u5929\u591c\u91cc\u7684\u5f00\u76d8\u4ef7\u3001\u6536\u76d8\u4ef7\uff0c\u6ca1\u6709\u4ec0\u4e48\u597d\u5173\u5fc3\u7684\uff0c\u4e0d\u8fc7\u5bf9\u56fd\u5185\u4e92\u8054\u7f51\u4ea7\u4e1a\u6765\u8bf4\uff0c\u4eca\u5929\u5374\u662f\u4e00\u4e2a\u6ce8\u5b9a\u8981\u5f7b\u591c\u96be\u7720\u7684\u591c\u665a\u3002</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u662f\u56fd\u5185\u7b2c\u4e00\u5bb6\u5230\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\u7684\u4e92\u8054\u7f51\u4f01\u4e1a\uff0c\u6606\u817e\u5728\u7ebf\u4eca\u591c\u80fd\u5426\u83b7\u5f97\u6210\u529f\uff0c\u5c06\u76f4\u63a5\u4f53\u73b0\u7f8e\u56fd\u7684\u8d44\u672c\u5e02\u573a\u5bf9\u6253\u4e0a\u4e2d\u56fd\u6807\u8bc6\u7684\u65b0\u79d1\u6280\u4f01\u4e1a\u7684\u53cb\u597d\u7a0b\u5ea6\u3002</p><p>\u3000\u3000\u4e00\u65e6\u6606\u817e\u5728\u7ebf\u5927\u83b7\u6210\u529f\uff0c\u610f\u5473\u7740\u5c06\u6709\u66f4\u591a\u7684\u98ce\u9669\u8d44\u672c\u5230\u4e2d\u56fd\u4e92\u8054\u7f51\u4ea7\u4e1a\u6765\u8fdb\u884c\u6dd8\u91d1\uff0c\u4e5f\u610f\u5473\u7740\u56fd\u5185\u7684\u4e92\u8054\u7f51\u4ea7\u4e1a\u5c06\u83b7\u5f97\u66f4\u591a\u7684\u53d1\u5c55\u8d44\u91d1\uff0c\u4f46\u662f\u540c\u65f6\u4e5f\u4f1a\u5c06\u6b27\u7f8e\u8d44\u672c\u5e02\u573a\u7684\u65b0\u79d1\u6280\u6ce1\u6cab\u98ce\u6f6e\u66f4\u52a0\u76f4\u63a5\u7684\u4f20\u5bfc\u56de\u56fd\u5185\u7684\u8bc1\u5238\u5e02\u573a\u3002</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u82e5\u662f\u4e0a\u5e02\u5373\u53d7\u91cd\u632b\uff0c\u90a3\u4e5f\u610f\u5473\u7740\u56fd\u5185\u4e92\u8054\u7f51\u4ea7\u4e1a\u7684\u4fe1\u5fc3\u5c06\u53d7\u5230\u4e00\u6b21\u4e25\u91cd\u7684\u632b\u4f24\u3002</p><p>\u3000\u3000\u5f20\u606a\u63c9\u4e86\u63c9\u592a\u9633\u7a74\uff0c\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u65b0\u7684\u82f1\u96c4\u4eba\u7269\u53c8\u8981\u4ea7\u751f\u4e86\uff0c\u4eca\u591c\u6216\u8bb8\u4f1a\u6bd4\u8f83\u70ed\u95f9\u3002\u201d</p><p>\u3000\u3000\u4ed6\u77e5\u9053\u6606\u817e\u5728\u7ebf\u4eca\u591c\u4f1a\u6210\u529f\u4e86\uff0c\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u4ed6\u4eec\u5c06\u6210\u4e3a\u4e2d\u56fd\u4e92\u8054\u7f51\u4ea7\u4e1a\u7684\u82f1\u96c4\u5f0f\u4eba\u7269\u53d7\u5230\u5a92\u4f53\u7684\u70ed\u6367\uff0c\u4ed6\u77e5\u9053\u56fd\u5185\u8ddf\u968f\u524d\u5f80\u7f8e\u56fd\u8ffd\u8e2a\u62a5\u9053\u7684\u5a92\u4f53\u89c4\u6a21\u6bd4\u4efb\u4f55\u4e00\u6b21\u56fd\u5185\u4f01\u4e1a\u6d77\u5916\u4e0a\u5e02\u90fd\u8981\u5e9e\u5927\u5f97\u591a\u3002</p><p>\u3000\u3000\u5efa\u90ba\u7684\u4e92\u8054\u7f51\u4ea7\u4e1a\u96c6\u7fa4\u89c4\u6a21\u8f83\u5c0f\uff0c\u4f46\u662f\u6a61\u6811\u56ed\u521b\u6295\u662f\u6606\u817e\u5728\u7ebf\u7684\u76f4\u63a5\u6295\u8d44\u8005\uff0c\u6606\u817e\u5728\u7ebf\u7684\u6210\u529f\u4f1a\u4e0d\u80fd\u523a\u6fc0\u5efa\u90ba\u4e92\u8054\u7f51\u4ea7\u4e1a\u8df3\u8dc3\u5f0f\u53d1\u5c55\uff1f</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u6606\u817e\u5728\u7ebf\u4eca\u591c\u5728\u7f8e\u56fd\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\u4e00\u4e8b\u6ca1\u6709\u4ec0\u4e48\u7275\u6302\uff0c\u5403\u8fc7\u665a\u996d\u8fd8\u6709\u8bb8\u591a\u79ef\u7d2f\u4e0b\u6765\u7684\u8d44\u6599\u8981\u770b\u3001\u8981\u5904\u7406\u3002\u591c\u91cc\u4e5d\u70b9\u949f\u65f6\uff0c\u5728\u592a\u5e73\u6d0b\u5bf9\u5cb8\u7684\u7f8e\u56fd\u80a1\u5e02\u5373\u5c06\u5f00\u76d8\u4e4b\u524d\uff0c\u675c\u98de\u53c8\u6253\u8fc7\u7535\u8bdd\uff0c\u5f20\u606a\u4fbf\u653e\u4e0b\u624b\u91cc\u7684\u4e8b\u60c5\uff0c\u8dd1\u5230\u521b\u57df\u7f51\u5427\u4e09\u697c\u7684\u866b\u4ff1\u4e50\u90e8\u5de5\u4f5c\u5ba4\u51d1\u70ed\u95f9\u3002</p><p>\u3000\u3000\u866b\u4ff1\u4e50\u90e8\u5de5\u4f5c\u5ba4\u7684\u9aa8\u5e72\u6210\u5458\u90fd\u5728\uff0c\u9a6c\u529b\u3001\u6768\u6bc5\u8fd9\u4e9b\u5353\u57df\u7f51\u7684\u7ba1\u7406\u5c42\u4e5f\u90fd\u5728\uff0c\u4ee4\u5c0f\u71d5\u3001\u79e6\u521a\u4ed6\u4eec\u90fd\u5728\uff0c\u675c\u98de\u6b63\u5728\u63a5\u7535\u8bdd\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u5927\u5bb6\u90fd\u8fd9\u4e48\u70ed\u60c5\u7684\u5e2e\u6606\u817e\u5728\u7ebf\u6512\u52b2\u505a\u4ec0\u4e48\uff1f\u8981\u5e2e\u522b\u4eba\u6570\u94b1\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u6697\u76d8\u4ef7\u662f\u591a\u5c11\uff1f\u201d\u65f6\u5b66\u658c\u95ee\u9053\uff1a\u201c\u6a61\u6811\u56ed\u521b\u6295\u5e94\u8be5\u6e05\u695a\u6697\u76d8\u5f00\u4ef7\u4e86\u5427\uff01\u201d</p><p>\u3000\u3000\u867d\u7136\u6ca1\u6709\u51e0\u4e2a\u4eba\u77e5\u9053\u5f20\u606a\u771f\u6b63\u7684\u8eab\u4efd\uff0c\u4f46\u662f\u5f20\u606a\u4e0e\u7231\u8fbe\u96c6\u56e2\u3001\u4e0e\u6a61\u6811\u56ed\u7d27\u5bc6\u5230\u4f7f\u4e4b\u5982\u81c2\u7684\u5173\u7cfb\uff0c\u65f6\u5b66\u658c\u3001\u65bd\u65b0\u98de\u3001\u8463\u8dc3\u534e\u8fd9\u4e9b\u521b\u57df\u9aa8\u5e72\u8fd8\u662f\u5f88\u6e05\u695a\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u516c\u5f00\u53d1\u884c\u4ef7\u662f3.2\u7f8e\u5143\uff0c\u81f3\u4e8e\u80fd\u5f00\u51fa\u591a\u5c11\uff0c\u6211\u8fd9\u5c31\u544a\u8bc9\u4f60\u6697\u76d8\u4ef7\uff0c\u4e0d\u662f\u5c11\u4e86\u8bb8\u591a\u60ac\u5ff5\uff1f\u201d\u4e5f\u4e0d\u770b\u663e\u793a\u7f8e\u56fd\u7eb3\u65af\u8fbe\u514b\u80a1\u5e02\u4ea4\u6613\u60c5\u51b5\u7684\u7f51\u9875\uff0c\u62d6\u5230\u4e00\u5f20\u6ed1\u8f6e\u6905\u5750\u5230\u4e00\u8fb9\u3002</p><p>\u3000\u3000\u675c\u98de\u63a5\u8fc7\u7535\u8bdd\uff0c\u8ddf\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u4e1c\u5927\u79d1\u603b\u7684\u9648\u603b\u6253\u6765\u7684\u7535\u8bdd\uff0c\u4ed6\u4e5f\u5fcd\u4e0d\u4f4f\u4f1a\u63d0\u524d\u77e5\u9053\u6606\u817e\u5728\u7ebf\u7684\u6697\u76d8\u4ef7\u3002\u201d</p><p>\u3000\u3000\u4e1c\u5927\u79d1\u6280\u5f00\u53d1\u603b\u516c\u53f8\u9664\u4e86\u6295\u8d44\u5353\u57df\u7f51\u4e4b\u5916\uff0c\u8fd8\u6295\u4e86\u4e24\u5bb6\u7531\u4e1c\u5927\u6559\u5e08\u4e0e\u5b66\u751f\u521b\u5efa\u7684\u4e92\u8054\u7f51\u7f51\u7ad9\uff0c\u81ea\u7136\u4e5f\u6781\u5173\u5fc3\u6606\u817e\u5728\u7ebf\u7684\u4e0a\u5e02\u60c5\u51b5\uff0c\u5f20\u606a\u671d\u675c\u98de\u6325\u4e86\u6325\u624b\uff0c\u793a\u610f\u4e0d\u8981\u7406\u4f1a\u90a3\u8fb9\u3002</p><p>\u3000\u3000\u80fd\u8d76\u5728\u65b0\u79d1\u6280\u6d6a\u6f6e\u6ce1\u6cab\u4e4b\u524d\u5230\u7f8e\u56fd\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\u7684\u56fd\u5185\u4e92\u8054\u7f51\u4f01\u4e1a\u6ca1\u6709\u51e0\u5bb6\uff0c\u4e0d\u8fc7\u80fd\u591a\u9a97\u6765\u6d77\u5916\u98ce\u6295\u8fc7\u6765\uff0c\u5012\u4e5f\u662f\u4e0d\u9519\u7684\u9009\u62e9\u3002\u5f20\u606a\u53c8\u60f3\u5230\u5b8f\u4fe1\u4e0e\u6d77\u7c9f\u79d1\u6280\u4e5f\u6295\u8d448818\u7f51\u4e0a\u5546\u57ce\uff0c\u4e25\u6587\u4ecb\u4e0e\u6797\u96ea\u3001\u738b\u6d77\u7c9f\u8fd9\u4e9b\u4eba\u5927\u6982\u76ef\u7740\u6606\u817e\u5728\u7ebf\u7684\u4e00\u4e3e\u4e00\u52a8\u5427\uff0c\u6606\u817e\u5728\u7ebf\u8981\u662f\u80fd\u5927\u83b7\u6210\u529f\uff0c\u4ed6\u4eec\u7684\u91ce\u5fc3\u4f1a\u66f4\u81a8\u80c0\u5427\uff1f</p></article>"}}}]);
