(window.webpackJsonp=window.webpackJsonp||[]).push([[370],{393:function(p){p.exports={chapterTitle:null,title:"\u7b2c244\u7ae0 \u6c5f\u9edb\u513f\u7684\u751f\u65e5",content:"<article><h2>\u7b2c244\u7ae0 \u6c5f\u9edb\u513f\u7684\u751f\u65e5</h2><p>\u3000\u3000\u5341\u4e00\u6708\u521d\u7684\u9999\u6e2f\u3001\u6df1\u5733\uff0c\u751a\u81f3\u8fde\u79cb\u610f\u90fd\u4e0d\u6d53\u51bd\uff0c\u56de\u5230\u5185\u5730\uff0c\u4eba\u5728\u5ba4\u5916\uff0c\u8ba9\u98ce\u5439\u7740\uff0c\u897f\u88c5\u5916\u9762\u751a\u81f3\u8981\u7a7f\u98ce\u8863\u624d\u89c9\u5f97\u6696\u548c\u3002\u7701\u57ce\u57ce\u5357\u5c71\u5ce6\u7ef5\u5ef6\uff0c\u897f\u5317\u98ce\u6beb\u65e0\u906e\u6321\u7684\u5728\u57ce\u5e02\u91cc\u7684\u8086\u8650\uff0c\u7701\u57ce\u662f\u4e00\u5ea7\u56db\u5b63\u4e0d\u5206\u660e\u7684\u57ce\u5e02\uff0c\u51e0\u4e4e\u611f\u89c9\u4e0d\u5230\u6625\u79cb\u4e24\u5b63\u7684\u5b58\u5728\uff0c\u597d\u50cf\u590f\u5929\u521a\u8fc7\uff0c\u4e00\u573a\u897f\u5317\u98ce\u5439\u6765\uff0c\u5c31\u5df2\u7ecf\u611f\u89c9\u5230\u51ac\u5929\u7684\u5bd2\u610f\u4e86\u3002</p><p>\u3000\u3000\u5218\u660e\u8f89\u63fd\u4e0b\u5e7f\u5dde\u90a3\u8fb9\u7684\u6d3b\uff0c\u8fd9\u6b21\u53bb\u5317\u4eac\uff0c\u5f20\u606a\u51b3\u5b9a\u8ba9\u5468\u6e38\u9876\u5728\u524d\u9762\uff1b\u5468\u6e38\u897f\u670d\u5916\u8fd8\u7a7f\u7740\u77ed\u98ce\u8863\uff0c\u5f20\u606a\u53ea\u7a7f\u7740\u6d45\u7070\u8272\u7684\u5de5\u88c5\u5939\u514b\u3001\u725b\u4ed4\u88e4\uff0c\u5f39\u529b\u8fd0\u52a8\u978b\uff0c\u5341\u8db3\u7684\u5b66\u751f\u6837\uff0c\u597d\u50cf\u662f\u8981\u53bb\u5317\u4eac\u65c5\u6e38\u3002</p><p>\u3000\u3000\u5728\u897f\u57ce\u5356\u573a\u63a8\u51fa\u4e4b\u540e\u7684\u4e24\u4e2a\u6708\u95f4\uff0c\u76db\u946b\u540c\u65f6\u5728\u7701\u57ce\u3001\u4e0a\u6d77\u7b79\u5907\u4e24\u5bb6\u89c4\u6a21\u66f4\u5927\u7684\u5bb6\u7535\u8fde\u9501\u5356\u573a\uff0c\u7701\u57ce\u4e3a\u76db\u946b\u72ec\u8425\uff0c\u4e0a\u6d77\u7684\u5bb6\u7535\u5356\u573a\u4e3a\u76db\u946b\u4e0e\u6c38\u57ce\u5408\u8d44\uff0c\u4f46\u662f\u76db\u946b\u63a7\u5236\u7740\u7edd\u5bf9\u80a1\u6743\uff0c\u4eba\u5230\u7701\u57ce\uff0c\u603b\u514d\u4e0d\u4e86\u8981\u7ed9\u53f6\u5efa\u658c\u7ea0\u7f20\uff0c\u5f20\u606a\u5c06\u5468\u6e38\u63a8\u51fa\u53bb\uff0c\u8ba9\u5085\u4fca\u5f00\u8f66\u8f7d\u4ed6\u5148\u5e94\u4ed8\u53f6\u5efa\u658c\uff0c\u8ba9\u4ed6\u634e\u8bdd\u7ed9\u53f6\u5efa\u658c\uff0c\u665a\u4e0a\u53ef\u4ee5\u966a\u4ed6\u53bb\u5b66\u5e9c\u5df7\u559d\u9152\u3002</p><p>\u3000\u3000\u5f20\u606a\u5219\u53bb\u627e\u8bb8\u7ef4\u5979\u4eec\u3002</p><p>\u3000\u3000\u5728\u6d77\u5dde\u65f6\u4e0e\u8bb8\u7ef4\u6709\u8fc7\u8054\u7cfb\uff0c\u4eca\u5929\u6070\u597d\u662f\u6c5f\u9edb\u513f\u751f\u65e5\uff0c\u5f53\u65f6\u4ee4\u5c0f\u71d5\u4e5f\u5728\u7535\u8bdd\u65c1\uff0c\u8bf4\u4ed6\u4f5c\u4e3a\u8001\u677f\u81ea\u7136\u8981\u8868\u793a\u4e00\u4e0b\uff0c\u5979\u4eec\u6682\u65f6\u5c31\u6ca1\u8ba9\u6c5f\u9edb\u513f\u77e5\u9053\u5f20\u606a\u8fd9\u6b21\u8981\u53bb\u7701\u57ce\u3002</p><p>\u3000\u3000\u5f20\u606a\u5148\u53bb\u4e1c\u6d77\u5927\u5b66\u627e\u5230\u8bb8\u7ef4\uff0c\u4ee4\u5c0f\u71d5\u3001\u6797\u51b0\u90fd\u5728\u5bbf\u820d\u91cc\u5019\u4ed6\uff0c\u89c1\u5230\u5f20\u606a\uff0c\u5c31\u95ee\u4ed6\uff1a\u201c\u4f60\u6253\u7b97\u600e\u4e48\u5e2e\u9edb\u513f\u5e86\u795d\u751f\u65e5\uff1f\u201d</p><p>\u3000\u3000\u201c\u5927\u4e0d\u4e86\u8bf7\u4f60\u4eec\u5403\u987f\u996d\uff0c\u201d\u5f20\u606a\u6d51\u4e0d\u5728\u610f\u7684\u8bf4\uff0c\u201c\u5168\u5e02\u7684\u996d\u5e97\u4efb\u9009\uff0c\u6211\u4fdd\u8bc1\u4e0d\u4f1a\u7728\u773c\u3002\u201d</p><p>\u3000\u3000\u201c\u5207\uff0c\u5b8c\u5168\u4e0d\u503c\u5f97\u671f\u5f85\uff0c\u201d\u4ee4\u5c0f\u71d5\u3001\u6797\u51b0\u5927\u5931\u6240\u671b\uff0c\u201c\u5b8c\u5168\u4f53\u73b0\u4e0d\u51fa\u82b1\u82b1\u516c\u5b50\u7684\u6c34\u51c6\uff0c\u8981\u77e5\u9053\u8fd9\u4e2a\u751f\u65e5\uff0c\u5bf9\u9edb\u513f\u662f\u5f88\u91cd\u8981\u7684\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8038\u8038\u80a9\uff0c\u8868\u793a\u5bf9\u6b64\u65e0\u80fd\u4e3a\u529b\u3002\u5357\u65b9\u6709\u5e86\u4e5d\u4e0d\u5e86\u5341\u7684\u8bf4\u6cd5\uff0c\u6309\u865a\u5c81\u7b97\uff0c\u6c5f\u9edb\u513f\u4eca\u5929\u6ee1\u4e8c\u5341\u5c81\uff0c\u5e94\u8be5\u7b97\u5341\u5e74\u4e2d\u6700\u91cd\u8981\u7684\u4e00\u4e2a\u751f\u65e5\u3002</p><p>\u3000\u3000\u4e0d\u8fc7\u5bb6\u91cc\u4e00\u5411\u4e0d\u91cd\u89c6\u8fc7\u751f\u65e5\uff0c\u7279\u522b\u662f\u7238\u7238\u8fdb\u5165\u5e02\u91cc\u4e4b\u540e\uff0c\u5bb6\u91cc\u5c31\u6ca1\u6709\u4eba\u5e86\u795d\u8fc7\u751f\u65e5\uff0c\u5bf9\u6b64\uff0c\u5988\u5988\u662f\u6ee1\u809a\u5b50\u7684\u610f\u89c1\uff1b\u5f20\u606a\u5bf9\u751f\u65e5\u4e4b\u7c7b\u7684\u65e5\u5b50\uff0c\u7f3a\u4e4f\u654f\u611f\u7684\u70ed\u60c5\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u4e0d\u6653\u5f97\u5f20\u606a\u4eca\u5929\u8981\u8fc7\u6765\uff0c\u867d\u7136\u662f\u5468\u672b\uff0c\u5979\u4eba\u8fd8\u5728\u97f3\u4e50\u5b66\u9662\u7684\u821e\u8e48\u6559\u5ba4\u91cc\uff0c\u5f20\u606a\u966a\u8bb8\u7ef4\u3001\u4ee4\u5c0f\u71d5\u3001\u6797\u51b0\u9a91\u8f66\u53bb\u97f3\u4e50\u5b66\u9662\u7684\u821e\u8e48\u6559\u5ba4\u53bb\u627e\u6c5f\u9edb\u513f\u3002</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u8d76\u5230\u821e\u8e48\u6559\u5ba4\u91cc\uff0c\u6c5f\u9edb\u513f\u6b63\u9a91\u5750\u5728\u6a61\u6728\u5730\u677f\u4e0a\u4fef\u8eab\u538b\u8170\uff0c\u6c5f\u9edb\u513f\u7a7f\u7740\u7d27\u8eabT\u6064\u3001\u5f39\u529b\u88e4\uff0c\u8170\u95f4\u7cfb\u7740\u957f\u8896\u8863\uff0c\u5979\u4fef\u8eab\u8d34\u7740\u5730\u677f\uff0c\u4ece\u5706\u9886\u53e3\u9732\u51fa\u4e00\u5c0f\u89d2\u96ea\u767d\u7684\u4e73\u808c\uff0c\u7ea4\u817f\u4fee\u957f\uff0c\u8ba9\u5f39\u529b\u88e4\u7ef7\u7d27\uff0c\u6d51\u8eab\u6563\u53d1\u51fa\u9752\u6625\u660e\u8273\u7684\u9b45\u529b\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u4f60\u4eec\u600e\u4e48\u8fc7\u6765\u4e86\uff1f\u201d\u6c5f\u9edb\u513f\u624b\u638c\u8f7b\u8f7b\u6491\u5730\uff0c\u8eab\u4f53\u8f7b\u76c8\u76c8\u7684\u5c31\u7ad9\u4e86\u8d77\u6765\uff0c\u5145\u6ee1\u97f5\u5f8b\u7684\u7f8e\u611f\uff0c\u5f20\u606a\u53ef\u662f\u8bb0\u5f97\u6c5f\u9edb\u513f\u5728\u590f\u5929\u4e4b\u524d\uff0c\u8fd8\u6ca1\u6709\u821e\u8e48\u7684\u57fa\u7840\uff0c\u5fc3\u60f3\u5979\u8fd9\u4e24\u4e2a\u6708\uff0c\u771f\u662f\u4e0d\u5bb9\u6613\u3002</p><p>\u3000\u3000\u201c\u4eca\u5929\u4f60\u751f\u65e5\uff0c\u5f20\u606a\u7279\u610f\u4ece\u6d77\u5dde\u8d76\u8fc7\u6765\u7ed9\u4f60\u60ca\u559c\u2026\u2026\u201d\u4ee4\u5c0f\u71d5\u62ff\u5f20\u606a\u5f00\u73a9\u7b11\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u5012\u662f\u6709\u4e9b\u60ca\u559c\uff0c\u8138\u988a\u5fae\u7ea2\uff0c\u4f38\u624b\u62b9\u6389\u989d\u5934\u4e0a\u7684\u7ec6\u6c57\uff0c\u65e0\u6cd5\u7f6e\u4fe1\u7684\u770b\u7740\u5f20\u606a\uff1a\u201c\u771f\u7684\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u521a\u60f3\u80e1\u626f\uff0c\u770b\u89c1\u6c5f\u9edb\u513f\u5979\u5988\u674e\u6708\u8339\u624b\u91cc\u62b1\u7740\u4e00\u4ef6\u7f69\u8863\u4ece\u89d2\u843d\u91cc\u7684\u957f\u51f3\u4e0a\u8d70\u8fc7\u6765\uff0c\u4fbf\u6ca1\u6709\u5fc3\u601d\u80e1\u626f\uff0c\u76f4\u63a5\u89e3\u91ca\u8fd9\u6b21\u6765\u627e\u6c5f\u9edb\u513f\u7684\u7528\u610f\uff1a\u201c\u8fd9\u6b21\u516c\u53f8\u4f1a\u6709\u4eba\u53bb\u5317\u4eac\uff0c\u8ddf\u4e2d\u592e\u7535\u89c6\u53f0\u6709\u63a5\u89e6\uff0c\u5e0c\u671b\u9edb\u513f\u80fd\u5411\u5b66\u6821\u8bf7\u51e0\u5929\u5047\uff0c\u914d\u5408\u4e00\u4e0b\u516c\u53f8\u7684\u5ba3\u4f20\u6d3b\u52a8\u3002\u65e2\u7136\u4eca\u5929\u662f\u9edb\u513f\u751f\u65e5\uff0c\u6211\u4ee3\u8868\u516c\u53f8\u8bf7\u5927\u5bb6\u5403\u987f\u996d\u3002\u201d\u8fd9\u8bdd\u8bf4\u5f97\u5ba2\u5ba2\u6c14\u6c14\uff0c\u6ef4\u6c34\u4e0d\u6f0f\u3002</p><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\uff0c\u4e2d\u592e\u7535\u89c6\u53f0\u8fd8\u7b3c\u7f69\u7740\u795e\u79d8\u7684\u6c14\u606f\uff0c\u674e\u6708\u8339\u542c\u5230\u5973\u513f\u8fd9\u6b21\u80fd\u8ddf\u7740\u76f4\u63a5\u8ddf\u4e2d\u592e\u7535\u89c6\u53f0\u7684\u4eba\u63a5\u89e6\uff0c\u81ea\u7136\u5f88\u9ad8\u5174\u3002\u5012\u662f\u6c5f\u9edb\u513f\u542c\u5230\u5f20\u606a\u4e0d\u662f\u4e13\u7a0b\u6765\u7ed9\u5979\u5e86\u795d\u751f\u65e5\uff0c\u5fc3\u91cc\u6709\u4e9b\u7740\u5c0f\u5c0f\u7684\u5931\u843d\u3002</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u5979\u4eec\u62ff\u5f20\u606a\u4e0e\u6c5f\u9edb\u513f\u8bf4\u4e8b\uff0c\u66f4\u591a\u662f\u5f00\u73a9\u7b11\uff0c\u8c01\u8ba9\u5f20\u606a\u5df2\u7ecf\u770b\u8fc7\u6c5f\u9edb\u513f\u7684\u534a\u88f8\u8eab\u4f53\uff1b\u53ea\u8981\u5f20\u606a\u7b54\u5e94\u8bf7\u5403\u665a\u996d\uff0c\u5979\u4eec\u4e5f\u6ca1\u6709\u7ea0\u7f20\u7740\u4ed6\u4e0d\u653e\u3002</p><p>\u3000\u3000\u201c\u6211\u4eca\u5929\u8fd8\u6709\u51e0\u4e2a\u52a8\u4f5c\u7ec3\u4e60\u6ca1\u6709\u5b8c\u6210\u2026\u2026\u201d\u6c5f\u9edb\u513f\u6709\u4e9b\u4e0d\u597d\u610f\u601d\u8ba9\u5f20\u606a\u7b49\uff0c\u8bf4\u8bdd\u90fd\u7ec6\u58f0\u7ec6\u6c14\u7684\u3002</p><p>\u3000\u3000\u8fd9\u4e48\u5b8c\u7f8e\u7684\u4e00\u5177\u8eab\u4f53\u5e26\u7740\u8fd9\u4e48\u5a07\u7f8e\u7684\u9762\u5bb9\u5728\u9762\u524d\u8df3\u821e\uff0c\u54ea\u6015\u5c31\u6446\u51e0\u4e2a\u59ff\u5f0f\uff0c\u5f20\u606a\u54ea\u91cc\u4f1a\u6709\u4e00\u4e01\u70b9\u7684\u4e0d\u8010\u70e6\uff1f\u5f20\u606a\u8ddf\u8bb8\u7ef4\u3001\u4ee4\u5c0f\u71d5\u3001\u6797\u51b0\u5979\u4eec\u5750\u4e86\u4e00\u8fb9\u53bb\uff0c\u624b\u6258\u7740\u4e0b\u5df4\uff0c\u5b9a\u6674\u7684\u770b\u7740\u6c5f\u9edb\u513f\u90a3\u91cc\u7ec3\u4e60\u821e\u8e48\u52a8\u4f5c\uff1b\u6709\u674e\u660e\u8339\u5728\u65c1\u8fb9\u76ef\u7740\uff0c\u6ca1\u6562\u592a\u5165\u795e\u3002</p><p>\u3000\u3000\u51e0\u4e2a\u5973\u5b69\u5b50\u7a7f\u7740\u8fd0\u52a8\u886b\u7b11\u7b11\u95f9\u95f9\u7684\u8d70\u8fdb\u6765\uff0c\u5979\u4eec\u770b\u5230\u5f20\u606a\u90a3\u8fb9\u5750\u7740\u4eba\uff0c\u5c31\u5230\u5bf9\u9762\u7684\u957f\u51f3\u4e0a\u8131\u6389\u5916\u9762\u7684\u8fd0\u52a8\u886b\uff0c\u9732\u51fa\u91cc\u9762\u7684\u7d27\u8eab\u8863\u3002\u8981\u4e0d\u662f\u5b59\u9759\u6aac\u7684\u7b11\u58f0\u6709\u79cd\u72ec\u7279\u7684\u8f7b\u7075\uff0c\u5f20\u606a\u8fd8\u65e0\u6cd5\u8ba4\u51fa\u5979\u5378\u6389\u6d53\u5986\u4e4b\u540e\u7684\u6e05\u7eaf\u6a21\u6837\u3002</p><p>\u3000\u3000\u60f3\u8d77\u4e0a\u56de\u6df1\u591c\u9001\u5b59\u9759\u6aac\u56de\u5bbf\u820d\u7684\u6050\u6016\u6a21\u6837\uff0c\u5f20\u606a\u5c06\u8863\u9886\u7ad6\u8d77\u6765\uff0c\u751f\u6015\u5b59\u9759\u6aac\u4f1a\u8ba4\u51fa\u81ea\u5df1\uff0c\u6bd5\u7adf\u91cd\u65b0\u56de\u5230\u4e5d\u56db\u5e74\uff0c\u8fd8\u662f\u6709\u4eba\u662f\u81ea\u5df1\u60f9\u4e0d\u8d77\u7684\uff0c\u773c\u524d\u7684\u5b59\u9759\u6aac\u5c31\u662f\u4e00\u4f4d\u3002\u60f9\u4e0d\u8d77\uff0c\u8fd8\u8eb2\u4e0d\u8d77\u5417\uff1f</p><p>\u3000\u3000\u8fd9\u8fb9\u6761\u51f3\u5750\u7740\u4e09\u4e2a\u5973\u5b69\u5b50\u3001\u4e00\u4e2a\u4e2d\u5e74\u5987\u5973\uff0c\u5f20\u606a\u4e00\u4e2a\u5c11\u7537\u5939\u5728\u91cc\u9762\u5c31\u989d\u5916\u7684\u9192\u76ee\uff0c\u5b59\u9759\u6aac\u90a3\u8fb9\u51e0\u4e2a\u5973\u5b69\u5b50\u65e9\u5c31\u6ce8\u610f\u5230\u4ed6\uff0c\u8fd8\u4e0d\u505c\u5f80\u4ed6\u8138\u4e0a\u7784\uff0c\u65f6\u4e0d\u65f6\u5077\u5077\u6307\u7740\u8fd9\u8fb9\u54af\u54af\u4e71\u7b11\uff0c\u90fd\u731c\u4e0d\u5230\u5979\u4eec\u5728\u8ba8\u8bba\u4ec0\u4e48\uff0c\u7b11\u58f0\u542c\u8d77\u6765\u5f88\u66a7\u6627\u3002</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u5482\u5482\u5634\uff0c\u5728\u4ed6\u8033\u6735\u4f4e\u58f0\u8bf4\uff1a\u201c\u5c0f\u5e05\u54e5\u5c31\u662f\u5403\u9999\uff0c\u4f60\u8981\u8ba9\u4f60\u5bb6\u53f8\u673a\u9001\u4f60\u8fc7\u6765\uff0c\u81f3\u5c11\u80fd\u5f81\u670d\u8fd9\u91cc\u4e00\u534a\u7684\u5973\u5b69\u5b50\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u6ca1\u6709\u5e94\u5979\u7684\u8bdd\uff0c\u4e09\u6240\u5b66\u6821\u5c31\u6328\u5728\u4e00\u8d77\uff0c\u5176\u4ed6\u4e24\u6240\u5b66\u6821\u7684\u5973\u5b69\u5bf9\u97f3\u4e50\u5b66\u9662\u7684\u5973\u5b69\u9887\u591a\u5fae\u8f9e\uff0c\u5f53\u7136\uff0c\u8ddf\u97f3\u4e50\u5b66\u9662\u6bcf\u5230\u5468\u672b\u5c31\u6709\u65e0\u6570\u540d\u8f66\u63a5\u5973\u5b69\u5b50\u4eec\u51fa\u6821\u8fc7\u5468\u672b\u7684\u98ce\u6c14\u6709\u5173\uff0c\u4f46\u4e5f\u4e0d\u662f\u6bcf\u4e2a\u5973\u5b69\u5b50\u90fd\u662f\u8fd9\u6837\u3002</p><p>\u3000\u3000\u4e0d\u8fc7\u4ece\u5b59\u9759\u6aac\u7684\u8868\u60c5\u770b\uff0c\u5979\u6ca1\u6709\u8ba4\u51fa\u81ea\u5df1\uff0c\u8fd9\u8ba9\u5f20\u606a\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u5927\u6982\u4eca\u5929\u7684\u6253\u626e\uff0c\u4e0e\u4e0a\u56de\u89c1\u5979\u65f6\u5dee\u8ddd\u8f83\u5927\uff0c\u5f20\u606a\u8fd8\u662f\u4e0d\u6562\u677e\u61c8\u7684\u62ff\u8863\u9886\u906e\u4f4f\u5c0f\u534a\u7247\u8138\uff0c\u76f4\u5230\u6c5f\u9edb\u513f\u7ec3\u4e60\u5b8c\uff0c\u624d\u5306\u5fd9\u9003\u79bb\u73b0\u573a\u3002</p><p>\u3000\u3000\u5f20\u606a\u5306\u5306\u8d70\u51fa\u821e\u8e48\u6559\u5ba4\uff0c\u770b\u89c1\u6c5f\u9edb\u513f\u8d70\u5230\u6559\u5ba4\u95e8\u53e3\uff0c\u5b59\u9759\u6aac\u558a\u4f4f\u5979\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u7b49\u6c5f\u9edb\u513f\u51fa\u6765\uff0c\u95ee\u5979\uff1a\u201c\u4f60\u8ba4\u8bc6\u90a3\u4e2a\u5973\u5b69\u5b50\uff1f\u201d</p><p>\u3000\u3000\u201c\u5b59\u9759\u6aac\u554a\uff0c\u4e5d\u56db\u5c4a\u821e\u8e48\u7f16\u5bfc\u7684\u5927\u59d0\u5927\u5462\uff0c\u4e0d\u8fc7\u5979\u7684\u6c11\u65cf\u821e\u975e\u5e38\u7684\u5389\u5bb3\uff0c\u53ea\u8981\u8fdb\u8fd9\u95f4\u6559\u5ba4\u7684\u5973\u5b69\u5b50\uff0c\u5dee\u4e0d\u591a\u90fd\u8ba4\u8bc6\u5979\uff0c\u53ea\u662f\u5979\u4e0d\u5927\u7406\u4eba\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u771f\u4e0d\u6653\u5f97\u5b59\u9759\u6aac\u662f\u5b66\u7f16\u821e\u7684\uff0c\u60f3\u8d77\u5979\u6d53\u5986\u8273\u62b9\u7684\u6837\u5b50\uff0c\u5374\u6709\u4e9b\u4e0d\u50cf\u3002</p><p>\u3000\u3000\u5403\u996d\u5c31\u9009\u5728\u5b66\u5e9c\u5df7\u7684\u996d\u5e97\uff0c\u7d27\u6328\u77401978\u9152\u5427\uff0c\u8fd8\u662f\u4ee4\u5c0f\u71d5\u7684\u7537\u670b\u53cb\u6731\u5c0f\u519b\u8d76\u8fc7\u6765\u65f6\uff0c\u624b\u91cc\u63d0\u7740\u4e00\u53ea\u5927\u86cb\u7cd5\uff0c\u539f\u6765\u8bb8\u7ef4\u3001\u4ee4\u5c0f\u71d5\u3001\u6797\u51b0\u4e09\u4e2a\u5973\u5b69\u5b50\u65e9\u5c31\u7ed9\u6c5f\u9edb\u513f\u9884\u8ba2\u4e86\u751f\u65e5\u86cb\u7cd5\u3002\u6731\u5c0f\u519b\u4ece\u5355\u4f4d\u76f4\u63a5\u8d76\u8fc7\u6765\uff0c\u9664\u4e86\u516c\u4e8b\u5305\uff0c\u8fd8\u5939\u7740\u6587\u6863\u888b\u3002</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u5947\u602a\u7684\u95ee\uff1a\u201c\u4ec0\u4e48\u65f6\u95f4\u7ecf\u8d38\u59d4\u7684\u5de5\u4f5c\u591a\u5f97\u505a\u4e0d\u5b8c\u8981\u665a\u4e0a\u5e26\u56de\u5bb6\u53bb\u505a\uff1f\u201d</p><p>\u3000\u3000\u6731\u5c0f\u519b\u516b\u6708\u4efd\u6bd5\u4e1a\u8fdb\u5165\u7701\u7ecf\u8d38\u59d4\u5de5\u4f5c\uff0c\u8fd9\u662f\u4e00\u4efd\u4f18\u6e25\u800c\u6709\u95f2\u7684\u5de5\u4f5c\uff0c\u4ed6\u542c\u7740\u4ee4\u5c0f\u71d5\u5632\u7b11\u4ed6\uff0c\u7701\u7ecf\u8d38\u59d4\u5f88\u591a\u662f\u4ece\u90e8\u961f\u8f6c\u4e1a\u7684\u5e72\u90e8\uff0c\u867d\u7136\u8fd9\u4e9b\u8f6c\u4e1a\u5e72\u90e8\u5728\u4e13\u4e1a\u4e0a\u5f88\u6b20\u7f3a\uff0c\u4f46\u662f\u5f88\u591a\u521a\u53bb\u7684\u5927\u5b66\u751f\uff0c\u53ea\u4f1a\u7ed9\u5b89\u6392\u505a\u4e00\u4e9b\u6253\u626b\u529e\u516c\u5ba4\u536b\u751f\u4e4b\u7c7b\u7684\u65e0\u804a\u5de5\u4f5c\uff0c\u6731\u5c0f\u519b\u521a\u53bb\u4e24\u4e09\u4e2a\u6708\uff0c\u8fd8\u6ca1\u6709\u5165\u95e8\uff0c\u66f4\u6ca1\u6709\u8d44\u683c\u62ff\u6587\u4ef6\u56de\u5bb6\u53bb\u5904\u7406\uff0c\u4ed6\u7b11\u7740\u89e3\u91ca\u9053\uff1a\u201c\u60e0\u5c71\u5e02\u7684\u4e00\u5bb6\u4f01\u4e1a\uff0c\u51b3\u5b9a\u8981\u4e09\u661f\u5408\u8d44\uff0c\u5408\u8d44\u7684\u4e8b\u60c5\uff0c\u60e0\u5c71\u5e02\u91cc\u5df2\u7ecf\u8ddf\u4e09\u661f\u8c08\u59a5\u5408\u4f5c\u7684\u6761\u4ef6\uff0c\u610f\u5411\u4e66\u65e9\u5c31\u7b7e\u4e0b\u6765\uff0c\u5c31\u7b49\u7740\u7701\u91cc\u7ed9\u6279\u590d\uff0c\u4eca\u5929\u6211\u4eec\u5904\u957f\u4e0b\u73ed\uff0c\u7ed9\u8fd9\u5bb6\u5382\u7684\u4e00\u540d\u526f\u5382\u957f\u622a\u4f4f\uff0c\u786c\u7ed9\u585e\u4e86\u8fd9\u4efd\u6750\u6599\u3002\u5185\u90e8\u8981\u771f\u6709\u4e0d\u540c\u7684\u610f\u89c1\uff0c\u66f4\u5e94\u8be5\u76f4\u63a5\u53bb\u627e\u5916\u7ecf\u8d38\u59d4\uff0c\u8ddf\u6211\u4eec\u7ecf\u8d38\u59d4\u6709\u4ec0\u4e48\u5173\u7cfb\uff1f\u6211\u4eec\u5904\u957f\u770b\u5230\u6211\u5728\u65c1\u8fb9\uff0c\u5c31\u786c\u585e\u7ed9\u6211\u4e86\uff0c\u8ba9\u6211\u968f\u4fbf\u6253\u53d1\u6389\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u60e0\u5c71\u5e02\u54ea\u5bb6\u4f01\u4e1a\uff0c\u201d\u4ee4\u5c0f\u71d5\u5947\u602a\u7684\u95ee\uff0c\u201c\u8ddf\u4e09\u661f\u5408\u8d44\uff1f\u201d</p><p>\u3000\u3000\u6731\u5c0f\u519b\u6ca1\u56de\u7b54\uff0c\u6c5f\u9edb\u513f\u5979\u5988\u674e\u6708\u8339\u8bf4\u9053\uff1a\u201c\u9999\u96ea\u6d77\u51b0\u7bb1\u5382\u8981\u8ddf\u4e09\u661f\u5408\u8d44\uff0c\u60e0\u5c71\u5e02\u90fd\u4f20\u904d\u4e86\uff0c\u597d\u50cf\u4e09\u661f\u8981\u5f80\u60e0\u5c71\u6295\u597d\u51e0\u4e2a\u4ebf\u4e0e\u9999\u96ea\u6d77\u5efa\u4ec0\u4e48\u51b0\u7bb1\u751f\u4ea7\u57fa\u5730\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u5230\u8fd9\u91cc\uff0c\u5f20\u606a\u60f3\u8d77\u8fd9\u4e2a\u4e5d\u5341\u5e74\u4ee3\u8fd8\u4eab\u8a89\u56fd\u5185\u7684\u9a70\u540d\u51b0\u7bb1\u54c1\u724c\u5728\u5408\u8d44\u540e\u672a\u6765\u5341\u51e0\u5e74\u7684\u51c4\u60e8\u906d\u9047\uff0c\u770b\u7740\u6731\u5c0f\u519b\uff0c\u8bf4\u9053\uff1a\u201c\u9999\u96ea\u6d77\u7684\u8fd9\u4e2a\u5382\u957f\u662f\u53cd\u5bf9\u5408\u8d44\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u8bf4\u4ec0\u4e48\u5408\u8d44\u65f6\u6ca1\u6709\u8ba1\u7b97\u9999\u96ea\u6d77\u54c1\u724c\u7684\u4ef7\u503c\uff0c\u8fd8\u8981\u6c42\u5408\u8d44\u540e\u4e09\u5e74\u65f6\u95f4\u91cc\u4e0d\u4f7f\u7528\u9999\u96ea\u6d77\u8fd9\u4e2a\u54c1\u724c\u2026\u2026\u4eba\u5bb6\u4e09\u661f\u53ea\u8981\u6c42\u4e09\u5e74\u4e0d\u4f7f\u7528\u9999\u96ea\u6d77\u54c1\u724c\uff0c\u53c8\u4e0d\u662f\u5c06\u8fd9\u4e2a\u54c1\u724c\u7f6e\u5165\u5408\u8d44\u4f01\u4e1a\uff0c\u600e\u4e48\u8981\u8ba1\u7b97\u54c1\u724c\u7684\u4ef7\u503c\uff1f\u53c8\u4e0d\u662f\u8bf4\u8fd9\u4e2a\u54c1\u724c\u4e09\u5e74\u4e0d\u80fd\u518d\u4f7f\u7528\uff0c\u603b\u4e4b\u8bf4\u4e86\u5f88\u591a\u4e0d\u597d\u7684\u5730\u65b9\uff0c\u5b8c\u5168\u6ca1\u770b\u5230\u5f15\u8fdb\u5916\u8d44\u3001\u5f15\u8fdb\u97e9\u56fd\u5148\u8fdb\u751f\u4ea7\u6280\u672f\u3001\u7ba1\u7406\u6c34\u5e73\u7684\u597d\u5904\uff0c\u6211\u4eec\u5904\u957f\u8bf4\uff0c\u6bcf\u4ef6\u4e8b\uff0c\u603b\u4f1a\u9047\u5230\u8fd9\u6837\u7684\u523a\u5934\u2026\u2026\u201d</p><p>\u3000\u3000\u6731\u5c0f\u519b\u8fd8\u521a\u8d70\u4e0a\u793e\u4f1a\uff0c\u5bf9\u8fd9\u79cd\u590d\u6742\u7684\u4e8b\u60c5\u7f3a\u4e4f\u72ec\u7acb\u7684\u5224\u65ad\uff0c\u5f20\u606a\u5bf9\u4ed6\u7684\u8bdd\u6beb\u4e0d\u4ecb\u610f\uff0c\u5fc3\u91cc\u60f3\uff1a\u97e9\u56fd\u68d2\u5b50\u4f1a\u5b89\u4ec0\u4e48\u597d\u5fc3\uff1f\u4e00\u4e2a\u54c1\u724c\u505c\u7528\u4e09\u5e74\uff0c\u5c31\u8db3\u4ee5\u8ba9\u5e02\u573a\u5f7b\u5e95\u7684\u9057\u5fd8\uff0c\u4e09\u661f\u501f\u5408\u8d44\u4ece\u6839\u672c\u4e0a\u62b9\u6740\u9999\u96ea\u6d77\u8fd9\u4e00\u4e2a\u5f3a\u52bf\u7684\u6c11\u65cf\u54c1\u724c\uff0c\u964d\u4f4e\u4ed6\u4eec\u8fdb\u5165\u56fd\u5185\u5e02\u573a\u7684\u963b\u529b\uff0c\u540c\u65f6\u8ba9\u9999\u96ea\u6d77\u9644\u5eb8\u6210\u4ed6\u4eec\u7684\u751f\u4ea7\u8f66\u95f4\uff0c\u4f46\u662f\u56fd\u5185\u7684\u5b98\u5458\u5f15\u8fdb\u5916\u8d44\u90fd\u70e7\u7ea2\u4e86\u773c\u2026\u2026</p></article>"}}}]);
