(window.webpackJsonp=window.webpackJsonp||[]).push([[372],{395:function(p){p.exports={chapterTitle:null,title:"\u7b2c372\u7ae0 \u4e89\u98ce\u5403\u918b",content:"<article><h2>\u7b2c372\u7ae0 \u4e89\u98ce\u5403\u918b</h2><p>\u3000\u3000\u4e0a\u4e86\u6c5f\u5824\uff0c\u53c8\u9a7e\u8f66\u6cbf\u7740\u8fd9\u6bb5\u7ea6\u5341\u516c\u91cc\u7684\u6c5f\u5824\u5927\u9053\u8d70\u4e86\u4e2a\u6765\u56de\uff0c\u60c5\u51b5\u90fd\u662f\u5982\u6b64\uff0c\u9760\u8fd1\u73af\u57ce\u4e1c\u8def\u7684\u5730\u6bb5\uff0c\u8fd8\u6709\u4e00\u5904\u584c\u9677\uff0c\u542c\u536b\u5170\u6beb\u65e0\u5fc3\u673a\u7684\u8bed\u6c14\uff0c\u57ce\u9632\u6c5f\u5824\u662f\u8c46\u8150\u6e23\u5de5\u7a0b\u7684\u4e8b\u5b9e\uff0c\u65b0\u829c\u5e02\u6c11\u5927\u591a\u662f\u77e5\u9053\u7684\uff0c\u4e5f\u96be\u602a\uff0c\u6c5f\u5824\u5927\u9053\u6446\u5728\u8fd9\u91cc\uff0c\u80fd\u7792\u8fc7\u5929\u4e0b\u4eba\u7684\u773c\u775b\uff1f\u627f\u5305\u57ce\u9632\u6c5f\u5824\u5de5\u7a0b\u7684\u9f99\u534e\u5efa\u5de5\u7684\u8001\u603b\u9f9a\u81ea\u7acb\u4e0e\u5e38\u52a1\u526f\u5e02\u957f\u7f57\u5f52\u6e90\u4e4b\u95f4\u7684\u201c\u4eb2\u5bc6\u5173\u7cfb\u201d\uff0c\u5728\u65b0\u829c\u968f\u4fbf\u62c9\u4e00\u4e2a\u4eba\uff0c\u591a\u534a\u4e5f\u662f\u77e5\u9053\u7684\uff0c\u4f46\u662f\u53ea\u6015\u5f88\u5c11\u4eba\u77e5\u9053\u4fee\u8fd9\u6c5f\u5824\u7528\u6781\u5dee\u7684\u6c34\u6ce5\u4e0d\u7b97\uff0c\u6c5f\u5824\u91cc\u8bbe\u8ba1\u91cc\u7684\u5927\u5c0f\u94a2\u7b4b\u5374\u5927\u91cf\u7684\u7ed9\u7a7a\u5fc3\u7684\u82a6\u82c7\u4ee3\u66ff\u3002</p><p>\u3000\u3000\u4e5d\u516b\u5e74\u590f\u5929\u5c0f\u6c5f\u6d41\u57df\u4e00\u5171\u5f62\u6210\u516b\u6b21\u6d2a\u5cf0\uff0c\u8fd9\u91cc\u8fde\u7b2c\u4e00\u6b21\u90fd\u6ca1\u6709\u62b5\u6321\u4f4f\u3002</p><p>\u3000\u3000\u6574\u4e2a\u4e0a\u5348\u5c31\u5728\u8fd9\u4e8c\u5341\u591a\u91cc\u957f\u7684\u6c5f\u5824\u5927\u9053\u4e0a\uff0c\u505c\u505c\u770b\u770b\uff0c\u76f4\u5230\u4e34\u8fd1\u4e2d\u5348\u65f6\u8bb8\u745e\u5e73\u5f00\u8f66\u5230\u65b0\u829c\u540e\u6253\u6765\u7535\u8bdd\u8054\u7cfb\uff0c\u5f20\u606a\u624d\u6536\u8d77\u201c\u73a9\u5174\u201d\u56de\u53bb\u3002</p><p>\u3000\u3000\u536b\u5170\u5fc3\u673a\u5355\u7eaf\uff0c\u80fd\u8d56\u5728\u8f66\u91cc\u5219\u8d56\u5728\u8f66\u91cc\uff0c\u4e0d\u613f\u4e0e\u5f20\u606a\u591a\u63a5\u89e6\uff1b\u7fdf\u4e39\u9752\u5012\u662f\u770b\u51fa\u4e9b\u5f02\u5e38\uff0c\u5f20\u606a\u4f3c\u4e4e\u5bf9\u5979\u4eec\u7684\u5174\u8da3\u5e76\u4e0d\u5927\uff0c\u8ba9\u5979\u51c6\u5907\u7684\u4e00\u4e9b\u6258\u8bcd\u90fd\u6ca1\u6709\u673a\u4f1a\u8bf4\u51fa\u53e3\uff0c\u53ea\u662f\u5728\u4ed6\u8eab\u4e0a\u627e\u4e0d\u5230\u5341\u4e03\u516b\u5c81\u5c11\u5e74\u7684\u7a1a\u6c14\uff0c\u592a\u591a\u7684\u8001\u6210\u5706\u6ed1\uff0c\u624d\u5bb9\u6613\u8ba9\u4eba\u751f\u51fa\u6212\u5fc3\uff1b\u518d\u8bf4\u8fd9\u5824\u5916\u7684\u6c5f\u6c34\uff0c\u5915\u9633\u4e0b\u8fd8\u6709\u4e9b\u770b\u5934\uff0c\u8fd9\u5927\u70ed\u5929\u7684\uff0c\u6765\u6765\u56de\u56de\u5c0f\u534a\u5929\u7684\uff0c\u90fd\u51fa\u4e86\u597d\u51e0\u8eab\u6c57\uff0c\u518d\u597d\u7684\u5174\u81f4\u4e5f\u6ca1\u6709\u4e86\uff0c\u8d76\u7740\u5bbe\u9986\u91cc\u6709\u51e0\u4ef6\u4e8b\u627e\u5979\uff0c\u5979\u53c8\u6000\u7591\u5f20\u606a\u8fd9\u4e48\u62d6\u7740\u662f\u4e0d\u662f\u60f3\u5979\u5148\u56de\u53bb\uff0c\u53c8\u4e0d\u80fd\u5c06\u536b\u5170\u4e00\u8d77\u5e26\u56de\u53bb\uff0c\u8fd8\u771f\u6709\u4e9b\u4e0d\u653e\u5fc3\u3002\u597d\u4e0d\u5bb9\u6613\u7b49\u5230\u5f20\u606a\u60f3\u52a8\u8eab\u56de\u53bb\uff0c\u5979\u53c8\u63a5\u4e86\u4e2a\u7535\u8bdd\uff0c\u6709\u4eba\u8ddf\u5979\u901a\u98ce\u62a5\u4fe1\u8bf4\u6709\u4e00\u4e2a\u5979\u8ddf\u536b\u5170\u4e0d\u5e0c\u671b\u770b\u5230\u7684\u4eba\u5728\u5bbe\u9986\u91cc\uff0c\u5979\u53c8\u4e0d\u60f3\u8fd9\u65f6\u5019\u56de\u53bb\u4e86\u3002</p><p>\u3000\u3000\u201c\u4f60\u5230\u65b0\u829c\u6765\uff0c\u4e00\u5b9a\u8981\u5c1d\u5c1d\u65b0\u829c\u7684\u9f99\u867e\u8ddf\u7530\u87ba\uff0c\u6211\u5e26\u4f60\u4eec\u53bb\u4e2a\u5730\u65b9\u5305\u4f60\u4eec\u6ee1\u610f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7fdf\u59d0\u521a\u624d\u4e0d\u662f\u4e00\u76f4\u7126\u6025\u7740\u56de\u53bb\uff1f\u201d\u5f20\u606a\u5947\u602a\u7fdf\u4e39\u9752\u63a5\u4e86\u4e2a\u7535\u8bdd\u5c31\u8f6c\u53d8\u6001\u5ea6\u4e86\uff0c\u201c\u9f99\u867e\u8ddf\u7530\u87ba\uff0c\u7b49\u4e0b\u56de\u5427\uff0c\u6709\u4eba\u5230\u5bbe\u9986\u627e\u6211\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u7fdf\u4e39\u9752\u627e\u4e0d\u5230\u501f\u53e3\uff0c\u53ea\u5f97\u4e0e\u536b\u5170\u968f\u8f66\u56de\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e00\u8def\u4e0e\u8bb8\u745e\u5e73\u901a\u7535\u8bdd\uff0c\u6307\u70b9\u4ed6\u600e\u4e48\u5f00\u8f66\u5230\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u8ddf\u4ed6\u6c47\u5408\uff0c\u4e24\u8f66\u65f6\u95f4\u6390\u5f97\u5f88\u51c6\uff0c\u6070\u5de7\u5728\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u524d\u78b0\u5934\u3002</p><p>\u3000\u3000\u5c06\u8f66\u505c\u8fdb\u5bbe\u9986\u5317\u89d2\u7684\u505c\u8f66\u573a\uff0c\u8bb8\u745e\u5e73\u770b\u7740\u59ff\u8272\u51fa\u4f17\u7684\u536b\u5170\u3001\u7fdf\u4e39\u9752\u4e00\u5de6\u4e00\u53f3\u4ece\u5f20\u606a\u8f66\u540e\u5ea7\u94bb\u51fa\u6765\uff0c\u7b49\u5f20\u606a\u4ece\u8f66\u540e\u5ea7\u94bb\u51fa\u6765\uff0c\u5f00\u73a9\u7b11\u8bf4\uff1a\u201c\u606a\u5c11\u771f\u662f\u5230\u54ea\u91cc\u90fd\u4e0d\u7518\u5bc2\u5bde\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u606a\u5c11\u201d\u8fd9\u4e2a\u79f0\u8c13\uff0c\u7ea8\u7ed4\u6c14\u66f4\u91cd\u4e00\u4e9b\uff0c\u7fdf\u4e39\u9752\u542c\u8bb8\u745e\u5e73\u8fd9\u4e48\u8bf4\u8bdd\uff0c\u8f7b\u8f7b\u7684\u5482\u4e86\u5482\u5634\uff0c\u4e0e\u536b\u5170\u53ea\u4f1a\u52a0\u91cd\u5bf9\u5f20\u606a\u662f\u4e2a\u7ea8\u7ed4\u5b50\u5f1f\u7684\u5370\u8c61\u3002</p><p>\u3000\u3000\u201c\u8fd8\u4ee5\u4e3a\u4f60\u4e0b\u5348\u624d\u80fd\u5230\u5462\uff0c\u6211\u90fd\u4e0d\u6653\u5f97\u6211\u7238\u4e2d\u5348\u4f1a\u4e0d\u4f1a\u56de\u6765\u5403\u996d\u3002\u201d\u65b0\u829c\u4e0e\u6d77\u5dde\u4e4b\u95f4\u7684\u9ad8\u901f\u516c\u8def\u8981\u7b49\u5230\u96f6\u4e09\u5e74\u624d\u5efa\u6210\u901a\u8f66\uff0c\u8bb8\u745e\u5e73\u9a7e\u8f66\u8fc7\u6765\uff0c\u8981\u4ece\u60e0\u5c71\u8f6c\uff0c\u7136\u540e\u8d70\u56fd\u9053\uff0c\u603b\u8981\u4e03\u516b\u4e2a\u5c0f\u65f6\u624d\u80fd\u5230\uff0c\u770b\u6765\u8bb8\u745e\u5e73\u8d77\u4e86\u5927\u65e9\u3002\u5f20\u606a\u4f38\u8fc7\u624b\u6765\u62cd\u62cd\u8bb8\u745e\u5e73\u7684\u80a9\u8180\uff0c\u8ba9\u4ed6\u4e00\u8d77\u5f80\u4ed6\u4f4f\u7684\u90a3\u680b\u697c\u8d70\u53bb\uff0c\u770b\u7740\u7fdf\u4e39\u9752\u4e0e\u536b\u5170\u5f80\u524d\u697c\u8d70\u8fc7\u53bb\uff0c\u8bf4\u9053\uff0c\u201c\u600e\u4e48\u4e0d\u8ddf\u6211\u4eec\u4e00\u8d77\u5403\u996d\uff1f\u201d</p><p>\u3000\u3000\u201c\u536b\u5170\u8fd8\u6ca1\u6709\u56de\u5bb6\u5462\uff0c\u6211\u8fd8\u6709\u4e9b\u4e8b\uff0c\u6539\u5929\u5427\u2026\u2026\u201d\u7fdf\u4e39\u9752\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u670d\u52a1\u53f0\u540e\u9762\u8d34\u7684\u662f\u53bb\u5e74\u4e03\u6708\u4efd\u7684\u503c\u73ed\u8868\uff1f\u201d\u5f20\u606a\u6487\u4e86\u6487\u5634\uff0c\u4e5f\u4e0d\u7ba1\u7fdf\u4e39\u9752\u7ed9\u6233\u7834\u8c0e\u8a00\u7684\u8138\u8272\u662f\u4e0d\u662f\u96be\u770b\uff0c\u4e0e\u8bb8\u745e\u5e73\u3001\u5085\u4fca\u5c31\u8981\u5f80\u56de\u8d70\u3002</p><p>\u3000\u3000\u201c\u536b\u5170\u2026\u2026\u201d</p><p>\u3000\u3000\u8fce\u9762\u8d70\u6765\u4e00\u4e2a\u9752\u5e74\uff0c\u6325\u624b\u671d\u536b\u5170\u7684\u80cc\u5f71\u62db\u547c\u3002</p><p>\u3000\u3000\u5f20\u606a\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u536b\u5170\u88c5\u4f5c\u6ca1\u770b\u89c1\uff0c\u52a0\u7d27\u811a\u6b65\u5f80\u524d\u8d70\uff0c\u539f\u6765\u8fd8\u6709\u4e00\u4e2a\u4ee4\u5979\u8ba8\u538c\u7684\u5bb6\u4f19\u5728\u8fd9\u91cc\u5019\u5979\u3002</p><p>\u3000\u3000\u201c\u536b\u5170\uff0c\u536b\u5170\u2026\u2026\u4f60\u53c8\u6545\u610f\u88c5\u542c\u4e0d\u89c1\u4e86\uff0c\u7fdf\u59d0\uff01\u201d</p><p>\u3000\u3000\u90a3\u9752\u5e74\u7684\u58f0\u97f3\u51e0\u4e4e\u8981\u628a\u6574\u4e2a\u9662\u5b50\u91cc\u7684\u4eba\u90fd\u5524\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u7f57\u6587\uff0c\u4f60\u53eb\u6211\uff1f\u201d\u536b\u5170\u4e07\u822c\u65e0\u5948\u7684\u8f6c\u8fc7\u5934\u6765\uff0c\u7c89\u5ae9\u7684\u5c0f\u8138\u9634\u6c89\u5f97\u8981\u6ef4\u51fa\u6c34\u6765\u3002\u7fdf\u4e39\u9752\u8f6c\u8fc7\u8138\uff0c\u4e5f\u662f\u4e00\u8138\u7684\u5c34\u5c2c\u3002</p><p>\u3000\u3000\u5f20\u606a\u54c2\u7136\u4e00\u7b11\uff0c\u5f80\u8fb9\u4e0a\u8ba9\u4e86\u8ba9\uff0c\u7ed9\u90a3\u9752\u5e74\u8fc7\u53bb\uff0c\u6ca1\u60f3\u5230\u90a3\u9752\u5e74\u8d70\u5230\u8ddf\u524d\u5c31\u505c\u4e86\u4e0b\u6765\uff0c\u8fd8\u7b11\u7740\u4f38\u51fa\u624b\uff1a\u201c\u4f60\u5c31\u662f\u5f20\u526f\u5e02\u957f\u7684\u513f\u5b50\u5f20\u606a\u5427\uff0c\u6211\u53eb\u7f57\u6587\uff0c\u6211\u7238\u7238\u662f\u526f\u5e02\u957f\u7f57\u5f52\u6e90\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u770b\u7f57\u6587\u4f38\u51fa\u7684\u624b\uff0c\u53c8\u770b\u4e86\u770b\u7f57\u6587\u7684\u8138\uff0c\u4e8c\u5341\u4e09\u56db\u5c81\u5de6\u53f3\uff0c\u60f3\u5fc5\u5df2\u7ecf\u4ece\u5176\u4ed6\u670d\u52a1\u5458\u90a3\u91cc\u77e5\u9053\u662f\u81ea\u5df1\u5e26\u536b\u5170\u51fa\u53bb\u7684\uff0c\u5fc3\u91cc\u660e\u660e\u4e0d\u723d\uff0c\u5374\u88c5\u51fa\u7b11\u8138\uff0c\u5f88\u96be\u4e3a\u4ed6\u3002\u5f20\u606a\u7709\u5934\u6311\u4e86\u6311\uff0c\u4f38\u51fa\u624b\uff0c\u5374\u4e0d\u662f\u53bb\u8ddf\u7f57\u6587\u63e1\u624b\uff0c\u800c\u662f\u671d\u536b\u5170\u6325\u4e86\u6325\uff1a\u201c\u536b\u5170\uff0c\u4f60\u5feb\u53bb\u62ff\u4e1c\u897f\uff0c\u522b\u78e8\u8e6d\u4e86\uff1b\u6211\u809a\u5b50\u90fd\u997f\u762a\u4e86\uff0c\u7b49\u7740\u4f60\u8fc7\u6765\u4e00\u8d77\u5403\u996d\u5462\u3002\u201d\u63a5\u7740\u773c\u775b\u53c8\u89c6\u82e5\u65e0\u7269\u7684\u626b\u8fc7\u7f57\u6587\uff0c\u4e0e\u8bb8\u745e\u5e73\u3001\u5085\u4fca\u5f80\u4ed6\u4f4f\u7684\u90a3\u680b\u697c\u8d70\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u7684\u76ee\u4e2d\u65e0\u4eba\u4e0e\u56a3\u5f20\uff0c\u5373\u4f7f\u50cf\u7fdf\u4e39\u9752\u8fd9\u79cd\u89c1\u60ef\u7ea8\u7ed4\u5b50\u5f1f\u7684\u5973\u4eba\u4e5f\u53f9\u4e3a\u89c2\u6b62\u3002\u9ad8\u5e72\u5b50\u5f1f\u56a3\u5f20\u4e5f\u7f62\u3001\u72c2\u5984\u4e5f\u7f62\uff0c\u591a\u534a\u662f\u9488\u5bf9\u5f31\u52bf\u7fa4\u4f53\u7684\uff0c\u5bf9\u4e8e\u666e\u901a\u4eba\u4ed6\u4eec\u4f1a\u6709\u9ad8\u9ad8\u5728\u4e0a\u7684\u5fc3\u7406\u4f18\u52bf\uff0c\u4f46\u662f\u7ea8\u7ed4\u5b50\u5f1f\u4e4b\u95f4\uff0c\u591a\u534a\u8fd8\u80fd\u4fdd\u6301\u5f6c\u5f6c\u6709\u793c\u7684\u59ff\u6001\uff0c\u5373\u4f7f\u6709\u6240\u4e0d\u5408\uff0c\u591a\u534a\u4e5f\u4e0d\u4f1a\u4e00\u89c1\u9762\u5c31\u524a\u4eba\u9762\u5b50\u7684\u3002</p><p>\u3000\u3000\u7f57\u6587\u60f3\u60f3\u81ea\u5df1\u5728\u65b0\u829c\u591f\u56a3\u5f20\u4e86\uff0c\u6ca1\u60f3\u5230\u8fc7\u6765\u4e00\u4e2a\u6bd4\u4ed6\u8fd8\u56a3\u5f20\u7684\uff0c\u4e00\u65f6\u6ca1\u53cd\u5e94\u8fc7\u6765\uff0c\u7ad9\u5728\u90a3\u91cc\u6709\u4e9b\u53d1\u6123\u3002</p><p>\u3000\u3000\u6bd4\u8d77\u5728\u7fdf\u4e39\u9752\u9762\u524d\u5c31\u6562\u6478\u81ea\u5df1\u5927\u817f\u7684\u7f57\u6587\uff0c\u536b\u5170\u89c9\u5f97\u5f20\u606a\u8fd8\u4e0d\u662f\u90a3\u4e48\u8ba8\u538c\uff0c\u867d\u7136\u4e24\u4e2a\u4eba\u90fd\u8ba9\u5979\u4e0d\u559c\u6b22\uff0c\u4f46\u662f\u5fc5\u987b\u9009\u4e00\u4e2a\u7684\u8bdd\uff0c\u5979\u53ea\u80fd\u9009\u62e9\u5f20\u606a\u3002\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u5979\u7ad9\u5728\u90a3\u91cc\u6743\u8861\u4e86\u4e00\u4e0b\uff0c\u5c31\u8bf4\u9053\uff1a\u201c\u4e1c\u897f\u6211\u4e0d\u62ff\u4e86\uff0c\u6211\u8ddf\u4f60\u4e00\u8d77\u8d70\u2026\u2026\u201d\u4e5f\u4e0d\u6562\u770b\u7f57\u6587\uff0c\u671d\u5f20\u606a\u8ffd\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u5634\u89d2\u6d6e\u51fa\u4e00\u4e1d\u6d45\u7b11\uff0c\u505c\u4e0b\u6765\u7b49\u536b\u5170\uff0c\u4ed6\u559c\u6b22\u770b\u5973\u5b69\u5b50\u7a7f\u725b\u4ed4\u88e4\u7684\u6837\u5b50\uff0c\u7279\u522b\u50cf\u536b\u5170\u8fd9\u79cd\u6709\u7740\u4fee\u957f\u6d51\u5706\u5927\u817f\u7684\u5973\u5b69\u5b50\uff0c\u5c06\u725b\u4ed4\u88e4\u7ef7\u5f97\u7d27\u7d27\u7684\uff0c\u6709\u7740\u5f02\u6027\u7684\u6027\u611f\uff0c\u4ed6\u8086\u65e0\u5fcc\u60ee\u7684\u8fd9\u6837\u770b\u4e86\u51e0\u773c\uff0c\u751a\u81f3\u8fd8\u4fa7\u8fc7\u5934\u6765\u770b\u536b\u5170\u7ed9\u725b\u4ed4\u88e4\u5305\u7d27\u7684\u81c0\u90e8\uff0c\u624d\u56de\u5934\u95ee\u7fdf\u4e39\u9752\uff1a\u201c\u7fdf\u59d0\u4f60\u5462\uff0c\u4e5f\u4e00\u8d77\u5403\u4e2d\u996d\uff1f\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u89c1\u7f57\u6587\u8138\u8272\u5f88\u5dee\uff0c\u6015\u4ed6\u5f53\u573a\u53d1\u4f5c\uff0c\u5fc3\u91cc\u5927\u558a\u5012\u9709\uff0c\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e2d\u5348\u8fd8\u6709\u4e8b\u60c5\uff0c\u5c31\u4e0d\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u4e0d\u53bb\u7ba1\u7fdf\u4e39\u9752\uff0c\u60f3\u53bb\u62c9\u536b\u5170\u7684\u624b\uff0c\u7ed9\u5979\u5de7\u5999\u7684\u8ba9\u5f00\uff0c\u4e5f\u4fbf\u7b97\u4e86\uff0c\u8d70\u8fdb\u697c\u91cc\uff0c\u624d\u5c06\u536b\u5170\u652f\u5f00\uff1a\u201c\u65b0\u829c\u7684\u9f99\u867e\u4e0e\u7530\u87ba\u5f88\u51fa\u540d\u7684\uff0c\u7fdf\u59d0\u8fd8\u4e0d\u662f\u8981\u4ecb\u7ecd\u4e00\u5bb6\u5e97\uff1f\u4f60\u5e2e\u6211\u6253\u7535\u8bdd\u95ee\u4e00\u4e0b\uff0c\u90a3\u5bb6\u5e97\u80fd\u4e0d\u80fd\u9001\u5916\u5356\uff0c\u94b1\u4e0d\u662f\u95ee\u9898\u3002\u201d</p><p>\u3000\u3000\u536b\u5170\u5fc3\u91cc\u6709\u7740\u521a\u8131\u72fc\u7a9d\u53c8\u5165\u864e\u53e3\u7684\u5fd0\u5fd1\uff0c\u7fdf\u4e39\u9752\u6ca1\u6709\u8ddf\u8fc7\u6765\u5e2e\u5979\u5e94\u4ed8\uff0c\u5979\u4e5f\u4e0d\u6562\u591a\u8bf4\u4ec0\u4e48\uff0c\u5c31\u8dd1\u5230\u670d\u52a1\u53f0\u540e\u9762\u6253\u7535\u8bdd\u53bb\u4e86\u3002</p><p>\u3000\u3000\u8fdb\u4e86\u623f\u95f4\uff0c\u8bb8\u745e\u5e73\u5947\u602a\u7684\u95ee\uff1a\u201c\u600e\u4e48\u8dd1\u5230\u65b0\u829c\u4e89\u98ce\u5403\u918b\u6765\u4e86\uff0c\u4f60\u4e0d\u6015\u7ed9\u5510\u5a67\u77e5\u9053\u53d1\u98da\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u5c31\u4e0d\u8981\u8ba9\u5979\u77e5\u9053\u5c31\u5f97\u4e86\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8981\u5728\u5c3d\u5feb\u65b0\u829c\u5e02\u533a\u9009\u4e00\u5904\u5408\u9002\u7684\u4f4f\u5b85\uff0c\u5979\u5988\u4e2d\u5348\u4e0d\u4f1a\u56de\u5bbe\u9986\uff0c\u5bf9\u5085\u4fca\u8bf4\uff0c\u201c\u65e9\u4e0a\u8ba9\u4f60\u6536\u7740\u7684\u62a5\u7eb8\uff0c\u4f60\u7ed9\u8001\u8bb8\u770b\u770b\u3002\u201d</p><p>\u3000\u3000\u8bb8\u745e\u5e73\u770b\u8fc7\u90a3\u7bc7\u62a5\u9053\u65b0\u829c\u57ce\u9632\u6c5f\u5824\u7684\u957f\u7bc7\u901a\u8baf\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4e09\u5341\u4e07\u65b0\u829c\u5e02\u6c11\u7684\u751f\u547d\u957f\u5824\uff0c\u597d\u6807\u9898\uff0c\u5475\u5475\uff0c\u5b98\u6837\u6587\u7ae0\uff0c\u80fd\u6253\u4e2a\u516b\u6298\u5c31\u7b97\u5b8c\u7f8e\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u4e0d\uff0c\u95ee\u9898\u6bd4\u4f60\u60f3\u50cf\u4e2d\u4e25\u91cd\u591a\u4e86\uff0c\u201d\u5f20\u606a\u4e25\u8083\u7684\u8bf4\u9053\uff0c\u201c\u6211\u4e0a\u5348\u5c31\u8ddf\u5085\u4fca\u53bb\u770b\u8fc7\u4e86\uff0c\u4e0d\u9700\u8981\u4e13\u4e1a\u7684\u77e5\u8bc6\uff0c\u5c31\u80fd\u770b\u51fa\u57ce\u9632\u6c5f\u5824\u4e2d\u5b58\u5728\u4e25\u91cd\u7684\u8d28\u91cf\u95ee\u9898\u2026\u2026\u6807\u51c6\u7684\u8c46\u8150\u6e23\u5de5\u7a0b\uff0c\u6c5f\u5824\u4e0a\u6709\u4e9b\u786c\u5316\u8def\u9762\uff0c\u751a\u81f3\u7528\u624b\u90fd\u80fd\u63b0\u788e\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u770b\u7167\u7247\u50cf\u6c34\u6ce5\u8def\u9762\uff0c\u201d\u8bb8\u745e\u5e73\u6709\u4e9b\u96be\u4ee5\u7f6e\u4fe1\u7684\u95ee\uff0c\u62ff\u8d77\u62a5\u7eb8\u53c8\u4ed4\u7ec6\u770b\u4e86\u770b\u7167\u7247\uff0c\u62a5\u9053\u5c06\u4e00\u4e9b\u8d22\u52a1\u6570\u636e\u4e5f\u5217\u51fa\u6765\uff0c\u201c\u9020\u4ef7\u5012\u662f\u4e0d\u4f4e\uff0c\u4f60\u786e\u5b9a\u90a3\u662f\u6c34\u6ce5\u8def\u9762\uff1f\u201d</p><p>\u3000\u3000\u201c\u786c\u5ea6\u751a\u81f3\u6bd4\u4e0d\u4e0a\u4e09\u5408\u571f\uff0c\u8fd9\u8fd8\u53ea\u662f\u770b\u5f97\u5230\u7684\u90e8\u5206\uff0c\u770b\u4e0d\u5230\u6c5f\u5824\u5185\u90e8\u85cf\u7740\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u5916\u9762\u90a3\u4e2a\u7f57\u6587\u662f\u526f\u5e02\u957f\u7f57\u5f52\u6e90\u7684\u513f\u5b50\uff1f\u201d\u8bb8\u745e\u5e73\u521a\u624d\u6709\u4e9b\u604d\u7136\uff0c\u4e0d\u786e\u5b9a\u7f57\u6587\u5728\u8bf4\u4ec0\u4e48\uff0c\u201c\u4f60\u6545\u610f\u627e\u4ed6\u7684\u9ebb\u70e6\u3002\u201d</p><p>\u3000\u3000\u201c\u662f\u7684\uff0c\u201d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u201c\u65b0\u829c\u7684\u60c5\u51b5\u6bd4\u60f3\u50cf\u4e2d\u590d\u6742\uff0c\u57ce\u9632\u6c5f\u5824\u5b58\u5728\u7684\u95ee\u9898\uff0c\u5373\u4f7f\u666e\u901a\u5e02\u6c11\u90fd\u5728\u5927\u8086\u7684\u8c08\u8bba\uff0c\u4e3a\u4f55\u6574\u4e2a\u65b0\u829c\u5e02\u91cc\u5374\u5145\u8033\u4e0d\u95fb\uff1f\u5e02\u653f\u5e9c\u5728\u6c5f\u5824\u5efa\u6210\u624d\u534a\u5e74\uff0c\u5c31\u53c8\u540c\u610f\u62d4\u6b3e\u91cd\u4fee\u6c5f\u5824\u5927\u9053\uff0c\u91cd\u4fee\u540e\u7684\u6c5f\u5824\u5927\u9053\u4f9d\u65e7\u95ee\u9898\u91cd\u91cd\u3002\u6211\u770b\u65b0\u829c\u5e02\u5b58\u5728\u6d3e\u7cfb\u503e\u8f67\u90a3\u662f\u80af\u5b9a\u7684\uff0c\u4f46\u662f\u5728\u67d0\u4e9b\u5b98\u5458\u5f88\u53ef\u80fd\u6d89\u53ca\u4e25\u91cd\u7684\u7ecf\u6d4e\u95ee\u9898\u65f6\uff0c\u53c8\u4e00\u81f4\u7684\u91c7\u53d6\u5b98\u5b98\u76f8\u62a4\u7684\u6001\u5ea6\uff0c\u76f8\u4e92\u5305\u5e87\u3001\u88c5\u804b\u4f5c\u54d1\uff0c\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff1f\u6211\u4eec\u8fd9\u65f6\u5019\u516c\u6b63\u65e0\u79c1\u7684\u5c06\u8fd9\u95ee\u9898\u6345\u51fa\u6765\uff0c\u4f1a\u6709\u4ec0\u4e48\u7ed3\u679c\uff1f\u5f88\u53ef\u80fd\u662f\u7ad9\u5230\u5927\u90e8\u5206\u65b0\u829c\u5b98\u5458\u7684\u5bf9\u7acb\u9762\u53bb\uff0c\u6211\u8001\u5b50\u8fd8\u8981\u5728\u8fd9\u4e2a\u5b98\u573a\u6df7\u4e0b\u53bb\uff0c\u5f97\u7f6a\u8fd9\u4e2a\u4f53\u7cfb\u91cc\u7684\u7edd\u5927\u591a\u6570\u4eba\u53ef\u4e0d\u884c\u3002\u8fd9\u4ef6\u4e8b\u80fd\u4e0d\u80fd\u8896\u624b\u4e0d\u7ba1\uff1f\u6211\u8bf4\u670d\u4e0d\u4e86\u81ea\u5df1\uff0c\u6709\u4e9b\u4e8b\u60c5\u65e0\u80fd\u4e3a\u529b\uff0c\u4f46\u603b\u4e0d\u80fd\u89c6\u800c\u4e0d\u89c1\u3002\u5de6\u53f3\u6743\u8861\uff0c\u6211\u8981\u8ba9\u6574\u4ef6\u4e8b\u770b\u8d77\u6765\u50cf\u5185\u90e8\u77db\u76fe\uff0c\u53ea\u662f\u4e89\u98ce\u5403\u918b\u800c\u5df2\uff1a\u7f57\u6587\u5f97\u7f6a\u4e86\u6211\uff0c\u6211\u5c31\u8981\u6574\u4ed6\u8001\u5b50\u3002\u201d</p></article>"}}}]);
