(window.webpackJsonp=window.webpackJsonp||[]).push([[376],{398:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u4e09\u8282 \u8bef\u8ba4",content:"<article><h2>\u7b2c\u4e09\u5341\u4e09\u8282 \u8bef\u8ba4</h2><p>\u51db\u4e4b\u6240\u4ee5\u542c\u8fc7\u8fd9\u4e2a\u4e0d\u7eaf\u6d01\u7684\u8bcd\uff0c\u662f\u4e0a\u6b21\u953b\u70bc\u9b54\u6cd5\u7684\u65f6\u5019\uff0c\u5979\u4ea4\u9519\u4f7f\u7528\u70c8\u7130\u5bd2\u51b0\uff0c\u628a\u743c\u6069\u6253\u5f97\u72fc\u72c8\u4e0d\u582a\u3002\u7ecf\u8fc7\u8fd9\u51e0\u5929\u7684\u8bad\u7ec3\uff0c\u51db\u5bf9\u7709\u5fc3\u9b54\u6cd5\u9635\u7684\u638c\u63a7\u8d8a\u52a0\u7eaf\u719f\uff0c\u8fd0\u8f6c\u53d8\u5316\u4e4b\u95f4\u518d\u65e0\u534a\u70b9\u6ede\u788d\uff0c\u743c\u6069\u62b5\u5fa1\u8d77\u6765\u4e5f\u5c31\u8d8a\u53d1\u8270\u96be\uff0c\u518d\u52a0\u4e0a\u4ed6\u64c5\u957f\u7684\u662f\u53d8\u5316\u9b54\u6cd5\uff0c\u6709\u5f88\u591a\u6cd5\u672f\u4e0d\u80fd\u65bd\u5c55\uff0c\u4fbf\u5982\u89e3\u79bb\u672f\uff0c\u5a01\u529b\u5927\u5219\u5927\u77e3\uff0c\u4f46\u5deb\u5e08\u81ea\u5df1\u5374\u6ca1\u6cd5\u63a7\u5236\uff0c\u4e00\u65e6\u6cd5\u672f\u6210\u529f\u5c31\u76f4\u63a5\u628a\u4eba\u5316\u4f5c\u7c89\u672b\uff0c\u4ed6\u5982\u4f55\u6562\u5bf9\u51db\u7528\u3002</p><p>\u88ab\u51db\u7528\u706b\u7130\u7bad\u548c\u5bd2\u51b0\u9525\u8f70\u70b8\u4e4b\u540e\uff0c\u743c\u6069\u7070\u5934\u571f\u8138\u5730\u9003\u51fa\u6765\uff0c\u968f\u53e3\u611f\u6168\uff0c\u8bf4\u51db\u4f60\u8fd9\u771f\u662f\u540d\u526f\u5176\u5b9e\u7684\u201c\u51b0\u706b\u4e5d\u91cd\u5929\u201d\u4e86\u3002\u51db\u597d\u5947\u5fc3\u53d1\u4f5c\uff0c\u8be2\u95ee\u8fd9\u4e2a\u8bcd\u7684\u6765\u5386\uff0c\u743c\u6069\u4e5f\u662f\u4e0d\u6000\u597d\u610f\uff0c\u4fbf\u5411\u5979\u63cf\u8ff0\u4e86\u4e00\u904d\uff0c\u62c9\u5979\u5c1d\u8bd5\u3002\u51db\u5374\u4e0d\u4e0a\u5f53\uff0c\u575a\u51b3\u4e0d\u80af\uff0c\u743c\u6069\u4e5f\u53ea\u5f97\u7f62\u4e86\uff0c\u4e8b\u60c5\u8fc7\u53bb\u4fbf\u5fd8\u5728\u8111\u540e\uff0c\u6ca1\u60f3\u5230\u51db\u4eca\u5929\u53c8\u4e3b\u52a8\u63d0\u8d77\u6765\u3002</p><p>\u201c\u4e0d\uff0c\u4e0d\u4f1a\u5427\uff0c\u6700\u8fd1\u600e\u4e48\u4f1a\u8fd9\u4e48\u5e78\u798f\uff0c\u96be\u9053\u662f\u89c9\u5f97\u6211\u5728\u4e0b\u5c42\u754c\u5f85\u5f97\u592a\u8fc7\u90c1\u95f7\uff0c\u65f6\u6765\u8fd0\u8f6c\u4e86\uff1f\u8fd8\u662f\u8bf4\u6211\u7684\u4e3b\u89d2\u6a21\u677f\u59d7\u59d7\u6765\u8fdf\uff0c\u73b0\u5728\u7ec8\u4e8e\u7206\u53d1\u4e86\uff1f\u201d</p><p>\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u8fd9\u771f\u662f\u4ee4\u4eba\u5fc3\u60c5\u6109\u5feb\u7684\u4e8b\u60c5\uff0c\u743c\u6069\u6beb\u4e0d\u72b9\u8c6b\u5730\u5c31\u628a\u51db\u6dfb\u52a0\u8fdb\u4e86\u968f\u884c\u540d\u5355\u91cc\u3002\u5982\u6b64\u4e00\u6765\uff0c\u53bb\u4f0a\u5361\u6c99\u57ce\u7684\u8def\u4e0a\u5c31\u6709\u4e24\u4f4d\u7f8e\u5973\u966a\u4f34\u5de6\u53f3\u4e86\u2014\u2014\u53e6\u5916\u4e00\u4e2a\u662f\u9b45\u9b54\u838e\u73de\u514b\u3002</p><p>\u838e\u73de\u514b\u7684\u4f5c\u7528\u5f88\u591a\uff0c\u9996\u5148\u5979\u662f\u6740\u624b\uff0c\u8033\u76ee\u806a\u660e\uff0c\u9690\u9041\u8eab\u5f62\u6f5c\u85cf\u5f62\u8ff9\u7684\u80fd\u529b\u4e00\u6d41\uff0c\u8d70\u8def\u7684\u65f6\u5019\u53ef\u4ee5\u5728\u524d\u9762\u5f15\u5bfc\uff0c\u907f\u514d\u5192\u5192\u5931\u5931\u649e\u8fdb\u4ec0\u4e48\u9677\u9631\u6216\u8005\u731b\u517d\u7a9d\u91cc\uff1b\u5176\u6b21\u5979\u662f\u9b45\u9b54\uff0c\u5bb9\u8c8c\u79c0\u4e3d\u8eab\u6750\u5b8c\u7f8e\uff0c\u4f8d\u5949\u7537\u4eba\u7684\u6280\u672f\u4e00\u6d41\uff0c\u5a36\u6765\u5f53\u59bb\u5b50\u4f1a\u6709\u95ee\u9898\uff0c\u62ff\u6765\u5f53\u5e8a\u4f34\u5374\u662f\u6700\u5408\u9002\u4e0d\u8fc7\uff0c\u53ef\u4ee5\u6709\u6548\u6d3e\u9063\u65c5\u9014\u5bc2\u5bde\u3002\u800c\u4e14\u743c\u6069\u6b64\u6b21\u524d\u53bb\u4f0a\u5361\u6c99\u57ce\uff0c\u6709\u5f88\u591a\u9700\u8981\u626f\u76ae\u4ea4\u6d89\u7684\u5730\u65b9\uff0c\u8fd9\u65b9\u9762\u6885\u83f2\u65af\u5e76\u4e0d\u64c5\u957f\uff0c\u5979\u592a\u521a\u786c\uff0c\u51db\u4e5f\u4e0d\u592a\u64c5\u957f\uff0c\u838e\u73de\u514b\u5374\u5f88\u5728\u884c\u3002</p><p>\u6885\u83f2\u65af\u751f\u65e5\u4e4b\u540e\u7684\u7b2c\u56db\u5929\uff0c\u743c\u6069\u7ec8\u4e8e\u4ece\u8d85\u8d1f\u8377\u5267\u70c8\u8fd0\u52a8\u7684\u540e\u9057\u75c7\u4e2d\u6062\u590d\u8fc7\u6765\uff0c\u81f3\u5c11\u5168\u8eab\u808c\u8089\u4e0d\u90a3\u4e48\u9178\u75db\u96be\u5fcd\u4e86\u3002\u5e26\u7740\u51db\u548c\u838e\u73de\u514b\uff0c\u542f\u7a0b\u524d\u5f80\u4f0a\u5361\u6c99\u57ce\u3002</p><p>\u4ece\u6668\u70bc\u5230\u4f0a\u5361\u6c99\uff0c\u5927\u7ea6\u4e24\u5230\u4e09\u65e5\u7684\u8def\u7a0b\uff0c\u8fd9\u5f53\u7136\u662f\u6307\u6b65\u884c\u901f\u5ea6\uff0c\u4f46\u5176\u5b9e\u6709\u5750\u9a91\u4ee3\u6b65\u7684\u8bdd\u4e5f\u4e0d\u4f1a\u5feb\u591a\u5c11\uff0c\u56e0\u4e3a\u5730\u4e0b\u9053\u8def\u66f2\u6298\u590d\u6742\uff0c\u6839\u672c\u4e0d\u80fd\u50cf\u5728\u5730\u8868\u4e00\u6837\u5feb\u901f\u5954\u9a70\uff0c\u65e0\u975e\u662f\u6bd4\u6b65\u884c\u8282\u7701\u529b\u6c14\u800c\u5df2\u3002\u743c\u6069\u548c\u838e\u73de\u514b\u90fd\u9a91\u7740\u516d\u722a\u8725\u8734\uff0c\u51db\u5374\u9a91\u7740\u4e00\u53ea\u94f6\u8272\u72ec\u89d2\u517d\uff0c\u662f\u5979\u4ece\u6885\u83f2\u65af\u90a3\u91cc\u501f\u6765\u7684\u3002</p><p>\u5728\u9752\u94dc\u57ce\u5821\u7684\u65f6\u5019\uff0c\u66e6\u5929\u4f7f\u5f25\u8d5b\u4e9a\u9001\u7ed9\u6885\u83f2\u65af\u4e00\u5ea7\u5c0f\u96d5\u50cf\uff0c\u53ef\u4ee5\u53ec\u5524\u51fa\u4e00\u53ea\u957f\u7740\u53cc\u7ffc\u7684\u94f6\u8272\u72ec\u89d2\u517d\u3002\u636e\u6885\u83f2\u65af\u8bf4\uff0c\u90a3\u4e0d\u662f\u4e00\u53ea\u666e\u901a\u7684\u72ec\u89d2\u517d\uff0c\u800c\u662f\u5929\u754c\u72ec\u89d2\u517d\uff0c\u5e76\u975e\u7269\u8d28\u754c\u7684\u751f\u7269\uff0c\u800c\u662f\u8bde\u751f\u4e8e\u81ea\u7136\u5723\u6bbf\uff08\u81ea\u7136\u4e4b\u795e\u897f\u51e1\u7eb3\u65af\u3001\u5927\u5730\u6bcd\u795e\u88f3\u63d0\u963f\u3001\u68ee\u6797\u5973\u795e\u6885\u4e3d\u51ef\u7b49\u81ea\u7136\u8bf8\u795e\u5c45\u4f4f\u4f4d\u9762\uff09\u7684\u72ec\u89d2\u517d\u4e1b\u6797\u4e2d\uff0c\u5c5e\u4e8e\u795e\u5723\u7684\u9b54\u6cd5\u751f\u7269\u2014\u2014\u5c31\u56e0\u4e3a\u592a\u795e\u5723\u4e86\uff0c\u4ee5\u81f3\u4e8e\u53ea\u6709\u5f25\u8d5b\u4e9a\u8fd9\u79cd\u66e6\u5929\u4f7f\u6216\u8005\u6885\u83f2\u65af\u8fd9\u79cd\u5723\u6b66\u58eb\u624d\u80fd\u9a7e\u9a6d\uff0c\u743c\u6069\u522b\u8bf4\u60f3\u9a91\u5b83\uff0c\u5c31\u7b97\u9760\u8fd1\u6478\u6478\u90fd\u4e0d\u6210\uff0c\u76f4\u63a5\u62ff\u957f\u89d2\u5c31\u9876\u8fc7\u6765\u3002</p><p>\u72ec\u89d2\u517d\u7684\u89d2\u662f\u5149\u660e\u5723\u6d01\u4e4b\u7269\uff0c\u53ef\u4ee5\u51c0\u5316\u6bd2\u7d20\u3001\u9a71\u9664\u8bc5\u5492\u3001\u6062\u590d\u75b2\u52b3\u3001\u6cbb\u7597\u521b\u4f24\uff0c\u8fd8\u662f\u591a\u79cd\u5ef6\u5bff\u957f\u751f\u836f\u5242\u7684\u5fc5\u5907\u6750\u6599\u3002\u4f46\u53ea\u8981\u5b83\u4eec\u613f\u610f\uff0c\u4e5f\u662f\u5b8c\u5168\u53ef\u4ee5\u62ff\u6765\u5f53\u957f\u67aa\u624e\u4eba\u7684\uff0c\u800c\u4e14\u5a01\u529b\u5f3a\u5f97\u5f88\u3002\u743c\u6069\u4e00\u5f00\u59cb\u4e0d\u77e5\u9053\uff0c\u60f3\u53bb\u6478\u6478\u5b83\u7684\u76ae\u6bdb\uff0c\u8868\u793a\u53cb\u5584\uff0c\u7ed3\u679c\u72ec\u89d2\u517d\u6c14\u52bf\u6c79\u6c79\u5c31\u50cf\u9a91\u58eb\u4e00\u6837\u51b2\u950b\u8fc7\u6765\uff0c\u5018\u82e5\u4e0d\u662f\u6885\u83f2\u65af\u53ca\u65f6\u559d\u6b62\u7684\u8bdd\uff0c\u53ea\u6015\u8eab\u4e0a\u5c31\u8981\u591a\u4e2a\u7a9f\u7abf\u4e86\u3002</p><p>\u72ec\u89d2\u517d\u662f\u7eaf\u6d01\u7684\u8c61\u5f81\uff0c\u800c\u743c\u6069\u81ea\u5ea6\u8ddd\u79bb\u8fd9\u4e2a\u8bcd\u5dee\u8ddd\u5341\u4e07\u516b\u5343\u91cc\uff0c\u867d\u7136\u88ab\u9119\u89c6\u4e86\uff0c\u5374\u4e5f\u6ca1\u4ec0\u4e48\u53ef\u62b1\u6028\u7684\uff0c\u7eaf\u5c5e\u81ea\u627e\u6ca1\u8da3\u3002\u4f46\u6709\u4e00\u70b9\u4ee4\u4ed6\u5f88\u662f\u6124\u6168\uff0c\u5c31\u662f\u5b83\u5bf9\u51db\u5341\u5206\u53cb\u5584\uff0c\u4e0d\u4f46\u5141\u8bb8\u5979\u9760\u8fd1\uff0c\u5141\u8bb8\u5979\u629a\u6478\uff0c\u8fd8\u5141\u8bb8\u5979\u9a91\u5728\u80cc\u4e0a\uff0c\u4eb2\u8fd1\u5f97\u5f88\u3002</p><p>\u201c\u51ed\u4ec0\u4e48\u5979\u5c31\u53ef\u4ee5\u9a91\uff0c\u6211\u5c31\u8fde\u78b0\u90fd\u4e0d\u80fd\u78b0\uff1f\u201d\u743c\u6069\u5411\u6885\u83f2\u65af\u62b1\u6028\u3002</p><p>\u201c\u8fd9\u4e2a\u95ee\u9898\u2026\u2026\u6211\u89c9\u5f97\u4f60\u5e94\u8be5\u81ea\u6211\u53cd\u7701\u624d\u5bf9\u3002\u201d</p><p>\u201c\u51db\u4e5f\u4e0d\u662f\u5904\u5973\u554a\uff0c\u4e0d\u662f\u8bf4\u5904\u5973\u624d\u80fd\u9a91\u4e0a\u72ec\u89d2\u517d\u4e48\u3002\u201d</p><p>\u201c\u65e9\u5c31\u544a\u8bc9\u8fc7\u4f60\u90a3\u662f\u8c23\u8a00\u4e86\u3002\u53ea\u8981\u5fc3\u5730\u7eaf\u6d01\u5584\u826f\u7684\u4eba\uff0c\u90fd\u80fd\u591f\u88ab\u72ec\u89d2\u517d\u6240\u63a5\u53d7\uff0c\u6211\u4e5f\u4e0d\u662f\u5904\u5973\u554a\u3002\u201d</p><p>\u201c\u4f60\u4ece\u4e25\u683c\u610f\u4e49\u4e0a\u8bf4\u8fd8\u662f\u5904\u5973\u2014\u2014\u800c\u4e14\u4f60\u662f\u5723\u6b66\u58eb\uff0c\u8bf4\u5fc3\u5730\u7eaf\u6d01\u5584\u826f\u6211\u76f8\u4fe1\uff0c\u51db\u54ea\u91cc\u7eaf\u6d01\u5584\u826f\u4e86\uff1f\u5979\u5206\u660e\u5c31\u662f\u4e2a\u5c0f\u6076\u9b54\u597d\u4e0d\u597d\u3002\u201d</p><p>\u6885\u83f2\u65af\u644a\u624b\uff0c\u201c\u662f\u5426\u7eaf\u6d01\u5584\u826f\uff0c\u8fd9\u662f\u5224\u65ad\u6807\u51c6\u7684\u4e0d\u540c\u3002\u5982\u679c\u4f60\u5bf9\u6b64\u6709\u610f\u89c1\u7684\u8bdd\uff0c\u76f4\u63a5\u627e\u9732\u4e1d\u96c5\uff08Lucciola\uff09\u53bb\u63a2\u8ba8\u5427\u3002\u201d</p><p>\u9732\u4e1d\u96c5\u5c31\u662f\u8fd9\u53ea\u94f6\u8272\u5929\u754c\u72ec\u89d2\u517d\u7684\u540d\u5b57\uff0c\u636e\u8bf4\u5728\u53e4\u7cbe\u7075\u8bed\u4e2d\u662f\u201c\u8424\u5149\u201d\u7684\u610f\u601d\uff0c\u6781\u6709\u68a6\u5e7b\u7f25\u6e3a\u4e4b\u7f8e\u611f\uff0c\u5b83\u662f\u96cc\u6027\uff0c\u53eb\u8fd9\u4e2a\u540d\u5b57\u5012\u4e5f\u6ca1\u4ec0\u4e48\u4e0d\u59a5\u3002</p><p>\u743c\u6069\u81ea\u7136\u6ca1\u5174\u8da3\u771f\u53bb\u548c\u4e00\u4e2a\u72ec\u89d2\u517d\u63a2\u8ba8\u5584\u6076\u95ee\u9898\uff0c\u62b1\u6028\u4e24\u53e5\u4e5f\u5c31\u7f62\u4e86\u3002\u8fd9\u6b21\u53bb\u4f0a\u5361\u6c99\u57ce\uff0c\u51db\u4e0d\u613f\u610f\u9a91\u8725\u8734\uff0c\u4fbf\u628a\u9732\u4e1d\u96c5\u501f\u6765\u4ee3\u6b65\u3002</p><p>\u5979\u6ca1\u6709\u7a7f\u5e73\u65f6\u90a3\u5957\u8d85\u77ed\u88d9\u88c5\uff0c\u800c\u662f\u7c73\u767d\u8272\u886c\u886b\u3001\u7d27\u7a84\u7684\u9ed1\u8272\u957f\u88e4\u3001\u94f6\u7070\u8272\u77ed\u9774\uff0c\u5916\u9762\u518d\u5957\u4e00\u4ef6\u6df1\u7ea2\u8272\u5927\u8863\uff0c\u770b\u8d77\u6765\u6574\u6574\u9f50\u9f50\uff0c\u5341\u5206\u5e05\u6c14\u3002\u72ec\u89d2\u517d\u5168\u8eab\u94f6\u5149\u95ea\u95ea\uff0c\u53cc\u7ffc\u6536\u8d77\uff0c\u4e0d\u77e5\u9053\u85cf\u5230\u54ea\u91cc\u53bb\u4e86\uff0c\u989d\u4e0a\u72ec\u89d2\u7b14\u633a\u4fee\u957f\uff0c\u6676\u83b9\u900f\u660e\uff0c\u5728\u9ed1\u6697\u4e2d\u653e\u51fa\u768e\u6d01\u5149\u8292\uff0c\u4eff\u4f5b\u6e05\u4e3d\u6708\u534e\u3002\u4e00\u4eba\u4e00\u517d\u76f8\u4e92\u6620\u886c\uff0c\u4fca\u7f8e\u65e0\u6bd4\u3002\u76f8\u6bd4\u8d77\u6765\uff0c\u743c\u6069\u9a91\u5728\u5c16\u5934\u957f\u5c3e\u9ed1\u4e0d\u6e9c\u79cb\u7684\u516d\u722a\u8725\u8734\u4e0a\uff0c\u987f\u65f6\u5c31\u663e\u5f97\u8d8a\u53d1\u9eef\u6de1\u3002</p><p>\u201c\u600e\u4e48\u6837\uff0c\u743c\u6069\uff0c\u201d\u51db\u5f97\u610f\u6d0b\u6d0b\u5730\u70ab\u8000\uff0c\u201c\u6211\u548c\u827e\u5f25\u8587\u90fd\u53ef\u4ee5\u9a91\u9732\u4e1d\u96c5\uff0c\u4f60\u5c31\u4e0d\u884c\u3002\u201d</p><p>\u201c\u65e0\u6240\u8c13\uff0c\u6211\u4e0d\u5728\u4e4e\u3002\u201d</p><p>\u201c\u771f\u7684\uff1f\u201d</p><p>\u201c\u771f\u7684\u3002\u201d\u743c\u6069\u8bf4\uff0c\u4ed6\u5728\u5fc3\u91cc\u63a5\u7740\u8865\u5145\u4e86\u4e00\u53e5\uff1a\u81f3\u5c11\u6211\u80fd\u9a91\u4f60\u4eec\u3002</p><p>\u201c\u5bf9\u4e86\uff0c\u51db\uff0c\u4f60\u4eca\u5929\u600e\u4e48\u4e0d\u7a7f\u88d9\u5b50\u4e86\uff1f\u201d</p><p>\u201c\u5e72\u561b\u8981\u7a7f\u88d9\u5b50\uff1f\u201d</p><p>\u201c\u56e0\u4e3a\u4f60\u7684\u817f\u975e\u5e38\u6f02\u4eae\u554a\uff0c\u4e0d\u9732\u51fa\u6765\u5b9e\u5728\u592a\u53ef\u60dc\u4e86\u3002\u201d</p><p>\u51db\u70b9\u70b9\u5934\uff0c\u663e\u7136\u5bf9\u743c\u6069\u7684\u8fd9\u4e2a\u56de\u7b54\u611f\u5230\u5341\u5206\u6ee1\u610f\uff0c\u201c\u5e7d\u6697\u5730\u57df\u91cc\u592a\u9634\u51b7\u4e86\uff0c\u201d\u5979\u62b1\u6028\uff0c\u201c\u6240\u4ee5\u6211\u51b3\u5b9a\u591a\u7a7f\u70b9\u3002\u201d</p><p>\u201c\u4f60\u53c8\u4e0d\u6015\u51b7\uff0c\u4f60\u662f\u51b7\u8840\u2026\u2026\u51b7\u7f8e\u4eba\u3002\u201d</p><p>\u51db\u4e0d\u9ad8\u5174\u5730\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u201c\u6211\u662f\u4e0d\u6015\u51b7\uff0c\u53ef\u662f\u8fd9\u79cd\u6f6e\u6e7f\u7684\u73af\u5883\u4f1a\u635f\u574f\u6211\u7684\u76ae\u80a4\uff0c\u800c\u4e14\u4f1a\u8ba9\u5173\u8282\u5f97\u75c5\u7684\u3002\u201d</p><p>\u201c\u8fd9\u5012\u4e5f\u662f\u3002\u201d</p><p>\u6709\u4e24\u4f4d\u7f8e\u4eba\u966a\u4f34\u8eab\u8fb9\u95f2\u8c08\u8bf4\u7b11\uff0c\u65c5\u9014\u81ea\u7136\u4e5f\u4e0d\u5bc2\u5bde\uff0c\u4f11\u606f\u5bbf\u8425\u65f6\u6000\u91cc\u66f4\u4e0d\u4f1a\u7a7a\u865a\uff0c\u552f\u4e00\u53ef\u60dc\u7684\u662f\u51db\u548c\u838e\u73de\u514b\u5173\u7cfb\u4e0d\u7b97\u5f88\u597d\uff0c\u6240\u4ee5\u743c\u6069\u5de6\u62e5\u53f3\u62b1\u7684\u7f8e\u68a6\u6ca1\u80fd\u5b9e\u73b0\u3002\u4e00\u8def\u98ce\u5e73\u6d6a\u9759\uff0c\u53ea\u9047\u5230\u4e24\u53ea\u6050\u722a\u602a\uff0c\u88ab\u51db\u5e72\u8106\u5229\u843d\u5730\u89e3\u51b3\u4e86\uff0c\u6b63\u597d\u5c31\u5730\u70e7\u70e4\uff0c\u4eab\u53d7\u4e00\u987f\u7f8e\u9910\u3002</p><p>\u7b2c\u4e8c\u5929\u4e0b\u5348\uff0c\u4e09\u4eba\u62b5\u8fbe\u76ee\u7684\u5730\uff0c\u53d1\u73b0\u4f0a\u5361\u6c99\u57ce\u4f3c\u4e4e\u6b63\u5728\u4e3e\u884c\u4ec0\u4e48\u96c6\u4f1a\uff0c\u8857\u9053\u4e0a\u4eba\u6765\u4eba\u5916\u70ed\u95f9\u975e\u51e1\uff0c\u4e0d\u4ec5\u4ec5\u6709\u77ee\u4eba\uff0c\u6709\u5730\u5e95\u4f8f\u5112\uff0c\u8fd8\u6709\u4e0a\u534a\u8eab\u662f\u4eba\uff0c\u4e0b\u534a\u8eab\u662f\u5de8\u5927\u874e\u5b50\u6a21\u6837\u7684\u874e\u8eab\u4eba\uff0c\u6709\u5934\u5982\u87fe\u870d\uff0c\u8eab\u8eaf\u77ee\u80d6\u7c97\u58ee\uff0c\u56db\u80a2\u5374\u7ea4\u7ec6\u7626\u5f31\uff0c\u5168\u8eab\u8986\u76d6\u9c9c\u8273\u989c\u8272\u9cde\u7247\u7684\u5bc7\u6d9b\u9c7c\u4eba\uff0c\u6709\u5168\u8eab\u4eff\u4f5b\u9540\u7740\u4e00\u5c42\u7070\u767d\u5927\u7406\u77f3\uff0c\u53cc\u81c2\u4e0b\u5782\u80fd\u642d\u819d\u76d6\u7684\u77f3\u5de8\u4eba\uff0c\u6709\u625b\u7740\u6218\u65a7\u7a7f\u7740\u9f3b\u73af\u957f\u7740\u8e44\u5b50\u7684\u725b\u5934\u602a\uff0c\u751a\u81f3\u8fd8\u6709\u743c\u6069\u5728\u65ad\u57df\u9547\u51b3\u6597\u5927\u8d5b\u4e0a\u89c1\u8fc7\u7684\u5409\u65af\u6d0b\u57fa\u4eba\uff0c\u7b49\u7b49\u7b49\u7b49\u3002\u8fd9\u4e48\u591a\u79cd\u65cf\u5018\u82e5\u5728\u91ce\u5916\u9047\u89c1\uff0c\u76f8\u4fe1\u4e00\u5b9a\u4f1a\u6253\u8d77\u6765\uff0c\u7136\u800c\u73b0\u5728\u5927\u5bb6\u5374\u5b89\u5b9a\u548c\u8c10\u5730\u5171\u805a\u4e00\u57ce\uff0c\u771f\u662f\u4ee4\u4eba\u5927\u5f00\u773c\u754c\u3002</p><p>\u201c\u743c\u6069\uff0c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u51db\u770b\u8d77\u6765\u4e5f\u5f88\u9707\u60ca\uff0c\u4e0d\u8fc7\u770b\u5979\u7684\u795e\u60c5\uff0c\u6050\u6015\u66f4\u591a\u662f\u89c9\u5f97\u6709\u8da3\uff0c\u201c\u77ee\u4eba\u4eec\u8fd9\u4e48\u70ed\u60c5\u597d\u5ba2\u5417\uff1f\u201d</p><p>\u201c\u8fd9\u4e2a\uff0c\u201d\u743c\u6069\u63c9\u63c9\u989d\u5934\uff0c\u4e5f\u662f\u5927\u60d1\u4e0d\u89e3\uff0c\u201c\u603b\u4e0d\u4f1a\u662f\u4f0a\u5361\u6c99\u57ce\u5728\u4e3e\u884c\u5e7d\u6697\u5730\u57df\u7b2c\u4e00\u5c4a\u5965\u6797\u5339\u514b\u8fd0\u52a8\u4f1a\u5427\u2026\u2026\u5514\uff0c\u53ef\u80fd\u662f\u4ec0\u4e48\u8282\u65e5\uff1f\u201d</p><p>\u201c\u4ec0\u4e48\u8282\u65e5\uff1f\u201d</p><p>\u201c\u4e0d\u77e5\u9053\u3002\u201d</p><p>\u743c\u6069\u867d\u7136\u548c\u77ee\u4eba\u4e5f\u6253\u8fc7\u4e0d\u5c11\u4ea4\u9053\uff0c\u4f46\u5bf9\u4ed6\u4eec\u7684\u4eba\u6587\u98ce\u4fd7\u5e76\u6ca1\u6709\u591a\u4e48\u6df1\u5165\u7684\u4e86\u89e3\uff0c\u5c24\u5176\u8282\u65e5\u5e86\u5178\u8fd9\u90e8\u5206\u66f4\u662f\u8bb0\u4e0d\u4f4f\u2014\u2014\u56e0\u4e3a\u77ee\u4eba\u7684\u8282\u65e5\u5b9e\u5728\u592a\u591a\u4e86\u3002\u5728\u5927\u591a\u6570\u5730\u533a\uff0c\u4eba\u7c7b\u4e00\u5e74\u53ea\u6709\u4e94\u4e2a\u4e3b\u8981\u8282\u65e5\uff1a\u4ef2\u51ac\u8282\u3001\u7eff\u8349\u8282\u3001\u4ef2\u590f\u8282\u3001\u4e30\u6536\u8282\u3001\u6708\u4eae\u8282\uff0c\u6b64\u5916\u6bcf\u9694\u56db\u5e74\u4f1a\u591a\u51fa\u4e00\u4e2a\u201c\u76fe\u4f1a\u201d\u3002\u4f46\u77ee\u4eba\u5219\u4e0d\u540c\uff0c\u4e00\u5e74\u4e0b\u6765\u91cd\u8981\u7684\u201c\u5927\u8282\u65e5\u201d\u5c31\u6709\u4e03\u516b\u4e2a\uff0c\u8fd8\u6709\u5c06\u8fd1\u4e8c\u5341\u4e2a\u5de6\u53f3\u7684\u201c\u5c0f\u8282\u65e5\u201d\u2014\u2014\u7528\u743c\u6069\u7684\u8bdd\u8bf4\uff0c\u5c31\u662f\u8fd9\u5e2e\u5bb6\u4f19\u5728\u627e\u5404\u79cd\u7406\u7531\u5404\u79cd\u501f\u53e3\u996e\u9152\u72c2\u6b22\u3002</p><p>\u4f46\u6298\u817e\u5230\u8fd9\u79cd\u5730\u6b65\uff0c\u672a\u514d\u4e5f\u532a\u5937\u6240\u601d\u4e86\u70b9\u5427\u3002</p><p>\u743c\u6069\u62c9\u8fc7\u4e00\u4e2a\u77ee\u4eba\u8be2\u95ee\uff0c\u624d\u77e5\u9053\u539f\u6765\u4eca\u5929\u662f\u201c\u8d27\u5e01\u5e86\u5178\u201d\uff0c\u987e\u540d\u601d\u4e49\u5c31\u662f\u4e3e\u884c\u5927\u578b\u5546\u4e1a\u6d3b\u52a8\u7684\u597d\u65e5\u5b50\uff0c\u6bcf\u9694\u516b\u5e74\u624d\u4f1a\u4e3e\u884c\u4e00\u6b21\uff0c\u6bcf\u6b21\u6301\u7eed\u5341\u5929\uff0c\u662f\u77ee\u4eba\u6700\u91cd\u8981\u7684\u8282\u65e5\u4e4b\u4e00\u3002\u4e0d\u77e5\u9053\u4ece\u4ec0\u4e48\u65f6\u5019\u8d77\uff0c\u8fd9\u4e00\u533a\u57df\u5f62\u6210\u4e86\u4e00\u4e2a\u4f20\u7edf\uff1a\u6bcf\u5230\u8d27\u5e01\u5e86\u5178\uff0c\u5468\u56f4\u7684\u8fd9\u4e9b\u79cd\u65cf\u90fd\u4f1a\u805a\u96c6\u5230\u4f0a\u5361\u6c99\u57ce\u6765\u8fdb\u884c\u8d38\u6613\u2014\u2014\u901a\u4fd7\u5730\u8bf4\u5c31\u662f\u8d76\u96c6\u3002\u4f0a\u5361\u6c99\u57ce\u4ee5\u953b\u9020\u5de5\u827a\u8457\u79f0\uff0c\u751f\u4ea7\u51fa\u6765\u7684\u6b66\u5668\u76d4\u7532\u8fdc\u8fd1\u95fb\u540d\uff0c\u4ef7\u683c\u540c\u6837\u9ad8\u6602\u7684\u60ca\u4eba\uff0c\u4f46\u5728\u8d27\u5e01\u5e86\u5178\u4e2d\uff0c\u77ee\u4eba\u4eec\u4e00\u5207\u5546\u54c1\u90fd\u662f\u534a\u4ef7\u9500\u552e\uff0c\u8fd9\u4e5f\u5438\u5f15\u4e86\u5927\u6279\u5ba2\u6237\u524d\u6765\u3002</p><p>\u743c\u6069\u5bf9\u6b64\u5174\u8da3\u4e0d\u5927\uff0c\u4e8b\u5b9e\u4e0a\u4ed6\u5f88\u6015\u5435\u95f9\uff0c\u4e00\u770b\u5230\u8fd9\u4e48\u591a\u4eba\u5c31\u5934\u75bc\u3002\u4e5f\u5c31\u56e0\u4e3a\u8fd9\u4e2a\u7f18\u6545\uff0c\u4ed6\u5411\u6765\u5f88\u4e0d\u559c\u6b22\u966a\u5973\u5b69\u5b50\u901b\u8857\uff0c\u4e0d\u662f\u56e0\u4e3a\u89c9\u5f97\u7d2f\uff0c\u800c\u662f\u8ba8\u538c\u7199\u7199\u6518\u6518\u7684\u62e5\u6324\u55a7\u95f9\u3002\u5982\u679c\u6885\u83f2\u65af\u5728\u8eab\u8fb9\uff0c\u4e00\u5b9a\u4f1a\u8d5e\u540c\u4ed6\u7684\u770b\u6cd5\uff0c\u7136\u800c\u5bf9\u4e8e\u51db\u6765\u8bf4\u8fd9\u4e9b\u5168\u4e0d\u662f\u95ee\u9898\uff0c\u5979\u662f\u90a3\u79cd\u6ca1\u6709\u65b0\u9c9c\u523a\u6fc0\u5c31\u6d3b\u4e0d\u4e0b\u53bb\u7684\u4eba\uff0c\u4e4b\u6240\u4ee5\u975e\u8981\u7f20\u7740\u743c\u6069\u5e26\u4ed6\u6765\u8fd9\u91cc\uff0c\u4e0d\u5c31\u662f\u89c9\u5f97\u6668\u70bc\u592a\u8fc7\u6b7b\u5bc2\uff0c\u6ca1\u6709\u610f\u601d\u4e48\uff0c\u5982\u4eca\u8d76\u4e0a\u8fd9\u4e48\u96be\u5f97\u4e00\u89c1\u7684\u573a\u9762\uff0c\u600e\u4e48\u80fd\u9519\u8fc7\uff0c\u5f53\u4e0b\u62ff\u96d5\u50cf\u6536\u56de\u72ec\u89d2\u517d\uff0c\u5174\u9ad8\u91c7\u70c8\u5c31\u51b2\u8fdb\u4eba\u7fa4\u91cc\u53bb\u4e86\u3002\u743c\u6069\u65e0\u5948\u5730\u770b\u4e86\u838e\u73de\u514b\u4e00\u773c\uff0c\u793a\u610f\u5979\u8ddf\u4e0a\u51db\uff0c\u514d\u5f97\u51fa\u4ec0\u4e48\u4e8b\u60c5\u3002</p><p>\u5c06\u81ea\u5df1\u5728\u57ce\u91cc\u7684\u4f4f\u5904\u65b9\u4f4d\u5bf9\u9b45\u9b54\u8bf4\u4e86\u4e00\u4e0b\uff0c\u518d\u5c06\u4ee3\u8868\u8bae\u5458\u8eab\u4efd\u7684\u5fbd\u7ae0\u7559\u7ed9\u5979\uff0c\u743c\u6069\u6682\u65f6\u4e22\u4e0b\u4e24\u4f4d\u5c11\u5973\uff0c\u5148\u53bb\u62dc\u8bbf\u77ee\u4eba\u56fd\u738b\u9c81\u6587\u3002</p><p>\u5f53\u65e5\u4f0a\u5361\u6c99\u57ce\u7684\u524d\u4efb\u56fd\u738b\uff0c\u94f6\u9f99\u7c73\u65af\u5df4\u62c9\u514b\u6c89\u7761\u4e0d\u9192\uff0c\u77ee\u4eba\u4eec\u675f\u624b\u65e0\u7b56\uff0c\u5f53\u65f6\u8fd8\u662f\u8bae\u5458\u7684\u9c81\u6587\u6bc5\u7136\u5317\u4e0a\u6c42\u52a9\uff0c\u6700\u7ec8\u5e26\u56de\u4e86\u743c\u6069\u7b49\u4eba\u3002\u4e00\u573a\u7b79\u5212\u4e4b\u540e\uff0c\u94f6\u9f99\u56fd\u738b\u88ab\u201c\u5524\u9192\u201d\uff0c\u867d\u7136\u5f88\u5feb\u53c8\u201c\u9a7e\u5d29\u201d\uff0c\u4f46\u4ed6\u53d1\u52a8\u4e86\u8ff7\u9501\uff0c\u5927\u8d25\u74dc\u7406\u5fb7\u65af\u57ce\u7684\u5353\u5c14\u4fb5\u7565\u8005\uff0c\u5e76\u5728\u201c\u4e34\u7ec8\u201d\u6307\u5b9a\u9c81\u6587\u7ee7\u627f\u738b\u4f4d\u3002\u4ece\u8fd9\u70b9\u4e0a\u8bf4\uff0c\u9c81\u6587\u80fd\u591f\u987a\u5229\u7ee7\u4efb\u56fd\u738b\uff0c\u5f88\u5927\u7a0b\u5ea6\u4e0a\u662f\u62dc\u743c\u6069\u6240\u8d50\u3002\u6709\u8fd9\u5c42\u6e0a\u6e90\u5728\uff0c\u4e24\u4eba\u7684\u79c1\u4ea4\u4e00\u76f4\u90fd\u4e0d\u9519\u3002\u8475\u9732\u4ece\u6df1\u6e0a\u8fd4\u56de\u7269\u8d28\u754c\u4e4b\u524d\uff0c\u743c\u6069\u5199\u4e86\u51e0\u5c01\u4fe1\u6258\u5979\u5e26\u56de\uff0c\u5176\u4e2d\u6709\u4e00\u5c01\u5c31\u662f\u7ed9\u4f0a\u5361\u6c99\u57ce\u7684\u77ee\u4eba\u56fd\u738b\u9c81\u6587\u3002\u6b63\u662f\u501f\u7740\u4ed6\u7684\u4ecb\u7ecd\uff0c\u8475\u9732\u624d\u80fd\u8fd9\u4e48\u5feb\u548c\u77ee\u4eba\u4eec\u642d\u4e0a\u7ebf\uff0c\u83b7\u5f97\u4fe1\u4efb\u548c\u5e2e\u52a9\uff0c\u8fc5\u901f\u7ad9\u7a33\u811a\u8ddf\u3002\u6240\u4ee5\u5bf9\u4e8e\u743c\u6069\u53bb\u4e86\u6df1\u6e0a\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u9c81\u6587\u4e5f\u662f\u77e5\u9053\u7684\uff0c\u53ea\u662f\u4e0d\u660e\u8be6\u60c5\u7531\u6765\u7f62\u4e86\u3002</p><p>\u4eca\u5929\u662f\u8d27\u5e01\u5e86\u5178\u7684\u7b2c\u4e00\u65e5\uff0c\u9c81\u6587\u5728\u738b\u5bab\u4e2d\u8bbe\u5bb4\u62db\u5f85\u5bbe\u5ba2\u4e2d\u7684\u91cd\u8981\u4eba\u7269\uff0c\u6b63\u662f\u9152\u9163\u8033\u70ed\u4e4b\u9645\uff0c\u743c\u6069\u6b64\u65f6\u5230\u6765\uff0c\u4fbf\u662f\u81ea\u6295\u7f57\u7f51\uff0c\u8fd8\u6ca1\u53cd\u5e94\u8fc7\u6765\u600e\u4e48\u56de\u4e8b\u5c31\u5df2\u7ecf\u88ab\u704c\u4e86\u4e09\u5927\u676f\u9152\u3002\u77ee\u4eba\u7684\u917f\u9152\u6280\u672f\u548c\u4ed6\u4eec\u953b\u9020\u5de5\u827a\u540c\u6837\u8d6b\u8d6b\u6709\u540d\uff0c\u751a\u81f3\u72b9\u6709\u8fc7\u4e4b\uff0c\u8fd9\u53c8\u662f\u91cd\u8981\u5bb4\u4f1a\uff0c\u62ff\u51fa\u6765\u7684\u90fd\u662f\u738b\u5bab\u73cd\u85cf\uff0c\u70c8\u5ea6\u6781\u9ad8\uff0c\u4e09\u676f\u4e0b\u53bb\u743c\u6069\u987f\u65f6\u5934\u91cd\u811a\u8f7b\uff0c\u773c\u524d\u51fa\u73b0\u91cd\u5f71\uff0c\u52c9\u5f3a\u53c8\u652f\u6301\u4e00\u4f1a\uff0c\u8d76\u7d27\u7785\u4e2a\u673a\u4f1a\u9003\u4e86\u51fa\u6765\u3002</p><p>\u4e00\u8def\u6447\u6447\u6643\u6643\u56de\u5230\u4f4f\u5904\uff0c\u62ff\u51b7\u6c34\u6d17\u6d17\u8138\uff0c\u603b\u7b97\u6e05\u9192\u4e86\u4e9b\uff0c\u4f46\u4f9d\u65e7\u89c9\u5f97\u5341\u5206\u96be\u53d7\u3002\u56db\u5468\u9759\u6084\u6084\u7684\uff0c\u51db\u548c\u838e\u73de\u514b\u663e\u7136\u8fd8\u6ca1\u56de\u6765\uff0c\u743c\u6069\u811a\u6b65\u4e0d\u7a33\u5730\u4e0a\u4e86\u697c\uff0c\u770b\u89c1\u8299\u857e\u72c4\u5367\u5ba4\u7684\u95e8\u662f\u865a\u63a9\u7740\u7684\uff0c\u4e0d\u5047\u601d\u7d22\u4fbf\u63a8\u95e8\u8fdb\u5165\u3002\u5c0f\u5973\u5b69\u7a7f\u7740\u6d45\u7d2b\u8272\u7684\u7761\u8863\uff0c\u6b63\u9759\u9759\u7ad9\u5728\u7a97\u524d\uff0c\u542c\u5230\u63a8\u95e8\u7684\u54cd\u52a8\u8f6c\u56de\u8eab\u6765\uff0c\u770b\u89c1\u662f\u743c\u6069\uff0c\u8138\u4e0a\u5fae\u5fae\u9732\u51fa\u8bb6\u5f02\u7684\u8868\u60c5\u3002</p><p>\u201c\u4f60\u2026\u2026\u201d</p><p>\u82e5\u5728\u5e73\u65f6\uff0c\u743c\u6069\u7acb\u523b\u5c31\u80fd\u5bdf\u89c9\u5f02\u6837\uff0c\u8299\u857e\u72c4\u770b\u89c1\u4ed6\u56de\u6765\uff0c\u80af\u5b9a\u662f\u76f4\u63a5\u6251\u5230\u6000\u4e2d\uff0c\u4e0d\u53ef\u80fd\u662f\u8fd9\u79cd\u795e\u6001\u53cd\u5e94\u3002\u4f46\u73b0\u5728\u4ed6\u8111\u4e2d\u5df2\u7ecf\u53ea\u5269\u4e09\u5206\u6e05\u9192\uff0c\u601d\u7ef4\u90fd\u8fdf\u949d\u4e86\u51e0\u500d\uff0c\u6839\u672c\u5c31\u6ca1\u6ce8\u610f\u5230\u8fd9\u4e9b\uff0c\u53cd\u800c\u5192\u51fa\u4e00\u4e2a\u83ab\u540d\u5176\u5999\u7684\u5ff5\u5934\uff1a\u201c\u8fd9\u4ef6\u7d2b\u8272\u7761\u8863\u4e0d\u9519\uff0c\u886c\u5f97\u76ae\u80a4\u597d\u767d\u2026\u2026\u201d</p><p>\u743c\u6069\u5f20\u5f00\u53cc\u81c2\u60f3\u53bb\u62b1\u5979\uff0c\u5bf9\u65b9\u4e00\u4e2a\u9000\u6b65\uff0c\u5de7\u5999\u5730\u907f\u8fc7\u4e86\u3002\u201c\u5170\u5c3c\u65af\u7279\u5148\u751f\uff0c\u8bf7\u4f60\u6e05\u9192\u70b9\uff0c\u201d\u5979\u51b7\u51b7\u8bf4\uff0c\u201c\u6211\u4e0d\u662f\u8299\u857e\u72c4\uff0c\u6211\u662f\u8299\u8389\u5a05\u3002\u201d</p><p>\u201c\u8299\u8389\u5a05\uff1f\u201d\u743c\u6069\u6014\u4e86\u4e00\u4e0b\u624d\u53cd\u5e94\u8fc7\u6765\uff0c\u201c\u5514\uff0c\u8299\u857e\u72c4\u7684\u59d0\u59d0\u2026\u2026\u201d</p><p>\u4e00\u9635\u9152\u52b2\u4e0a\u6d8c\uff0c\u4ed6\u53ea\u89c9\u7ad9\u7acb\u4e0d\u7a33\uff0c\u987a\u52bf\u5c31\u5f80\u5730\u6bef\u4e0a\u5012\u4e86\u4e0b\u53bb\u3002\u8299\u8389\u5a05\u89c1\u4ed6\u6ee1\u8eab\u9152\u6c14\uff0c\u773c\u795e\u6726\u80e7\uff0c\u660e\u663e\u662f\u559d\u9189\u4e86\uff0c\u4e0d\u7531\u5f97\u76b1\u4e86\u76b1\u7709\u3002\u539f\u672c\u6253\u7b97\u76f4\u63a5\u79bb\u5f00\u623f\u95f4\uff0c\u4e0d\u60f3\u7ba1\u4ed6\uff0c\u4f46\u72b9\u8c6b\u7247\u523b\uff0c\u6700\u7ec8\u8fd8\u662f\u52aa\u529b\u5c06\u4ed6\u6276\u5230\u5e8a\u4e0a\uff0c\u76d6\u4e0a\u6bdb\u6bef\u3002\u743c\u6069\u662f\u4e2a\u6210\u5e74\u7537\u5b50\uff0c\u8eab\u8eaf\u9887\u4e3a\u6c89\u91cd\uff0c\u8299\u8389\u5a05\u529b\u6c14\u53c8\u5c0f\uff0c\u505a\u5b8c\u8fd9\u4e9b\u5df2\u7ecf\u7d2f\u5f97\u4e0d\u884c\uff0c\u6b63\u81ea\u5750\u5728\u5e8a\u8fb9\u5598\u606f\uff0c\u7a81\u7136\u611f\u89c9\u8170\u4e0a\u4e00\u7d27\uff0c\u8eab\u4f53\u4e0d\u7531\u81ea\u4e3b\u540e\u4ef0\uff0c\u5374\u662f\u743c\u6069\u8ff7\u8ff7\u7cca\u7cca\u95f4\u5728\u62b1\u5979\u3002</p><p>\u743c\u6069\u867d\u7136\u521a\u624d\u542c\u89c1\u5979\u8bf4\u81ea\u5df1\u662f\u8299\u8389\u5a05\uff0c\u4f46\u5176\u5b9e\u8111\u5b50\u91cc\u5e76\u6ca1\u6709\u5f53\u771f\u53cd\u5e94\u8fc7\u6765\uff0c\u4f9d\u65e7\u8fd8\u5f53\u5979\u662f\u8299\u857e\u72c4\uff0c\u60f3\u8981\u5979\u8fc7\u6765\u966a\u81ea\u5df1\u4f11\u606f\u3002\u4ed6\u867d\u7136\u9152\u9189\uff0c\u795e\u667a\u4e0d\u6e05\uff0c\u529b\u6c14\u5374\u53cd\u800c\u6bd4\u5e73\u65f6\u66f4\u5927\u4e86\u51e0\u5206\uff0c\u8299\u8389\u5a05\u54ea\u91cc\u80fd\u591f\u6323\u8131\uff0c\u4e00\u65f6\u95f4\u88ab\u62b1\u5728\u4e86\u6000\u4e2d\uff0c\u53cc\u81c2\u88ab\u7b8d\u4f4f\uff0c\u60f3\u63a8\u5f00\u90fd\u662f\u4e0d\u884c\u3002\u5979\u5728\u5bb6\u4e2d\u95f2\u6687\u65e0\u4e8b\uff0c\u5348\u7761\u65b9\u8d77\uff0c\u8eab\u4e0a\u53ea\u6709\u4e00\u4ef6\u8f7b\u8584\u7761\u8863\uff0c\u8fde\u5934\u53d1\u90fd\u6ca1\u6765\u5f97\u53ca\u68b3\uff0c\u838e\u5c14\u5723\u5fbd\u4e5f\u6ca1\u5e26\u7740\uff0c\u5426\u5219\u8fd8\u53ef\u4ee5\u7528\u6cd5\u672f\u628a\u8fd9\u5bb6\u4f19\u51fb\u5f00\u3002\u597d\u5728\u743c\u6069\u786e\u5b9e\u9189\u4e86\uff0c\u4ed6\u662f\u4e60\u60ef\u4e86\u7761\u89c9\u65f6\u6000\u91cc\u6709\u4e2a\u5973\u4eba\uff0c\u5426\u5219\u4fbf\u89c9\u5f97\u7a7a\u8361\u8361\u7684\u4e0d\u8212\u670d\uff0c\u73b0\u5728\u5c06\u8299\u8389\u5a05\u62b1\u4f4f\u4e86\uff0c\u4e5f\u5c31\u6ca1\u6709\u8fdb\u4e00\u6b65\u52a8\u4f5c\uff0c\u81ea\u987e\u81ea\u5730\u6c89\u6c89\u7761\u53bb\u3002</p><p>\u8299\u8389\u5a05\u539f\u672c\u53c8\u7f9e\u53c8\u6025\uff0c\u4ee5\u4e3a\u4ed6\u8981\u4fb5\u72af\u81ea\u5df1\uff0c\u5982\u4eca\u89c1\u4ed6\u5f53\u771f\u7761\u8fc7\u53bb\uff0c\u65b9\u624d\u7565\u7565\u653e\u5fc3\u3002\u53ea\u662f\u743c\u6069\u7761\u662f\u7761\u4e86\uff0c\u53cc\u81c2\u8fd8\u662f\u62b1\u5f97\u5f88\u7d27\uff0c\u8299\u8389\u5a05\u4f9d\u65e7\u6323\u8131\u4e0d\u5f00\uff0c\u53ea\u5f97\u6682\u65f6\u653e\u5f03\u3002\u4e24\u4eba\u5173\u7cfb\u7b97\u4e0d\u4e0a\u597d\uff0c\u8fd8\u4ece\u672a\u5982\u6b64\u63a5\u8fd1\u8fc7\uff0c\u8299\u8389\u5a05\u770b\u7740\u90a3\u5f20\u8fd1\u5728\u54ab\u5c3a\u7684\u8138\uff0c\u8fde\u90a3\u5e26\u7740\u9152\u6c14\u7684\u547c\u5438\u90fd\u6e05\u695a\u611f\u53d7\u5230\uff0c\u6068\u4e0d\u5f97\u80fd\u4e00\u62f3\u6253\u8fc7\u53bb\u3002</p><p>\u201c\u4e0d\u8fc7\uff0c\u957f\u5f97\u786e\u5b9e\u8fd8\u633a\u6f02\u4eae\u7684\u3002\u201d</p><p>\u743c\u6069\u548c\u59d0\u59d0\u73ca\u5609\u957f\u5f97\u9887\u50cf\uff0c\u4e0d\u77e5\u5185\u60c5\u7684\u5f80\u5f80\u90fd\u4f1a\u5f53\u4f5c\u662f\u53cc\u80de\u80ce\uff0c\u4f46\u4ed6\u4eec\u53c8\u6027\u522b\u4e0d\u540c\uff0c\u8fd9\u5bfc\u81f4\u7684\u7ed3\u679c\uff0c\u662f\u4ee5\u7537\u6027\u7684\u6807\u51c6\u6765\u770b\uff0c\u743c\u6069\u7684\u8138\u6709\u4e9b\u504f\u79c0\u6c14\u3002\u4e0d\u8fc7\u5bf9\u4e8e\u5973\u5b69\u5b50\u800c\u8a00\uff0c\u4f3c\u4e4e\u5c0f\u767d\u8138\u6c38\u8fdc\u90fd\u6bd4\u866c\u9aef\u5927\u53d4\u66f4\u53d7\u6b22\u8fce\u2026\u2026</p><p>\u8299\u8389\u5a05\u548c\u8299\u857e\u72c4\u662f\u53cc\u80de\u80ce\u59d0\u59b9\uff0c\u540c\u6837\u7684\u9057\u4f20\uff0c\u540c\u6837\u7684\u5bb6\u5ead\u80cc\u666f\uff0c\u540c\u6837\u7684\u751f\u6d3b\u73af\u5883\uff0c\u5bfc\u81f4\u5979\u4eec\u7684\u5ba1\u7f8e\u504f\u597d\u662f\u4e00\u81f4\u7684\u3002\u8299\u857e\u72c4\u4f1a\u8ff7\u604b\u743c\u6069\uff0c\u867d\u7136\u4e0d\u53ea\u662f\u5355\u7eaf\u56e0\u4e3a\u4ed6\u957f\u5f97\u5e05\uff0c\u4f46\u81f3\u5c11\u7b2c\u4e00\u5370\u8c61\u611f\u89c9\u4e0d\u9519\uff0c\u8299\u8389\u5a05\u5176\u5b9e\u4e5f\u5dee\u4e0d\u591a\uff0c\u53ea\u662f\u5634\u4e0a\u4e0d\u80af\u627f\u8ba4\u7f62\u4e86\u3002</p><p>\u6f02\u4eae\u5f52\u6f02\u4eae\uff0c\u8299\u8389\u5a05\u5bf9\u743c\u6069\u7684\u5370\u8c61\u5e76\u4e0d\u4f1a\u56e0\u6b64\u800c\u597d\u8f6c\uff0c\u4f11\u606f\u7247\u523b\uff0c\u773c\u770b\u6062\u590d\u4e86\u70b9\u529b\u6c14\uff0c\u6b63\u8981\u518d\u5ea6\u6323\u624e\uff0c\u95e8\u53e3\u7a81\u7136\u4f20\u6765\u8f7b\u8f7b\u6572\u95e8\u58f0\uff0c\u5979\u62ac\u773c\u770b\u53bb\uff0c\u89c1\u4e00\u4e2a\u8eab\u6750\u706b\u8fa3\u6027\u611f\u7684\u8910\u53d1\u5973\u5b50\u8d70\u4e86\u8fdb\u6765\u3002</p><p>\u201c\u5537\uff0c\u8fd9\u4e48\u65e9\u5c31\u4f11\u606f\u4e86\uff0c\u201d\u5979\u7b11\u541f\u541f\u5730\u8bf4\uff0c\u770b\u4e86\u770b\u6c89\u7761\u4e2d\u7684\u743c\u6069\uff0c\u518d\u770b\u770b\u88ab\u4ed6\u62b1\u5728\u6000\u91cc\u52aa\u529b\u60f3\u8981\u6323\u8131\u7684\u8299\u8389\u5a05\uff0c\u201c\u5982\u679c\u6211\u6ca1\u5f04\u9519\uff0c\u8fd9\u4f4d\u662f\u83ab\u5c3c\u5361\u5c0f\u59d0\u5427\uff0c\u9700\u8981\u6211\u5e2e\u5fd9\u5417\uff1f\u201d</p></article>"}}}]);
