(window.webpackJsonp=window.webpackJsonp||[]).push([[378],{401:function(p){p.exports={chapterTitle:null,title:"\u7b2c378\u7ae0 \u4eba\u6c11\u4e13\u653f\u7684\u5a01\u529b",content:"<article><h2>\u7b2c378\u7ae0 \u4eba\u6c11\u4e13\u653f\u7684\u5a01\u529b</h2><p>\u3000\u3000\u4eff\u4f5b\u4ece\u6b7b\u4e2d\u6d3b\u8fc7\u6765\uff0c\u6709\u7740\u5c06\u8981\u7a92\u606f\u7684\u611f\u89c9\uff0c\u5f20\u606a\u624d\u4ece\u6c34\u91cc\u4f38\u51fa\u5934\u6765\uff0c\u5927\u53e3\u7684\u5598\u606f\u7740\uff0c\u867d\u7136\u8eab\u4f53\u91cc\u6709\u7740\u65e0\u7a77\u65e0\u5c3d\u7684\u7cbe\u75b2\u529b\u7aed\uff0c\u8fd8\u662f\u8981\u632f\u4f5c\u8d77\u6765\u3002</p><p>\u3000\u3000\u4ece\u6d74\u76c6\u91cc\u7ad9\u8d77\u6765\uff0c\u770b\u7740\u955c\u4e2d\u7684\u81ea\u5df1\uff0c\u4e00\u591c\u7684\u5de5\u592b\uff0c\u7adf\u6709\u4e9b\u524a\u7626\u4e86\uff0c\u5c06\u5934\u53d1\u64e6\u4e86\u534a\u5e72\uff0c\u7a7f\u597d\u8863\u670d\uff0c\u6df1\u6df1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u544a\u8beb\u81ea\u5df1\uff1a\u5df2\u7ecf\u770b\u4e86\u4e00\u773c\uff0c\u8be5\u56de\u5230\u81ea\u5df1\u7684\u751f\u6d3b\u4e2d\u6765\u4e86\u3002</p><p>\u3000\u3000\u63a8\u5f00\u95e8\uff0c\u5085\u4fca\u3001\u9a6c\u6d77\u9f99\u4ed6\u4eec\u8fd8\u5b88\u5728\u5916\u9762\uff0c\u5f20\u606a\u62bf\u4e86\u62bf\u5634\uff0c\u6709\u4e9b\u4e8b\u60c5\u6c38\u8fdc\u90fd\u65e0\u6cd5\u89e3\u91ca\u7684\uff0c\u53ea\u662f\u6de1\u6de1\u7684\u8bf4\u9053\uff1a\u201c\u5076\u5c14\u90fd\u4f1a\u6709\u4e9b\u60c5\u7eea\uff0c\u4e0d\u4f1a\u5927\u60ca\u5c0f\u602a\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u4f1a\uff0c\u201d\u5085\u4fca\u7b11\u7740\u8bf4\uff0c\u201c\u8fd8\u89c9\u5f97\u606a\u5c11\u5e73\u65e5\u592a\u6ca1\u60c5\u7eea\u5462\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u6487\u5634\u60f3\u7b11\u4e86\u4e00\u4e0b\uff0c\u6ca1\u7b11\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u997f\u4e86\uff0c\u770b\u5bbe\u9986\u8fd9\u65f6\u5019\u8fd8\u80fd\u5f04\u4e9b\u4ec0\u4e48\u5403\u7684\u4e1c\u897f\u6765\u3002\u201d</p><p>\u3000\u3000\u89c1\u5f20\u606a\u6062\u590d\u6b63\u5e38\uff0c\u5085\u4fca\u4e0e\u9a6c\u6d77\u9f99\u4ed6\u4eec\u9000\u51fa\u623f\u95f4\uff0c\u8ba9\u4eba\u4e0b\u697c\u5230\u670d\u52a1\u53f0\u627e\u670d\u52a1\u5458\u51c6\u5907\u4e9b\u5403\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u5728\u5c0f\u5ba2\u5385\u91cc\u6253\u5f00\u7535\u89c6\u770b\u4e86\u4e00\u4f1a\u513f\u65b0\u95fb\uff0c\u662f\u6709\u5173\u4e8e\u65b0\u829c\u5e02\u59d4\u7b79\u5907\u53ec\u5f00\u5168\u4f53\u6269\u5927\u4f1a\u8bae\u7684\u6d88\u606f\uff0c\u5fc3\u91cc\u60f3\u7740\u65b0\u829c\u7684\u4f1a\u8bae\u8fd8\u771f\u591f\u9891\u7e41\u7684\uff0c\u6709\u8fd9\u6837\u7684\u4f1a\u8bae\u4e5f\u65b9\u4fbf\u7236\u4eb2\u5728\u65b0\u829c\u5e02\u7684\u5927\u5c0f\u5b98\u5458\u524d\u4eae\u76f8\u3002\u623f\u95e8\u6253\u5f00\uff0c\u5f20\u606a\u626d\u5934\u770b\u8fc7\u53bb\uff0c\u7fdf\u4e39\u9752\u4e0e\u536b\u5170\u63d0\u7740\u51e0\u53ea\u88c5\u996d\u83dc\u7684\u5851\u6599\u76d2\u8fdb\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bb6\u5f02\u7684\u8bf4\uff1a\u201c\u8fd8\u4ee5\u4e3a\u5728\u8fd9\u91cc\u518d\u4e5f\u770b\u4e0d\u5230\u4e8c\u4f4d\uff0c\u8fd9\u4f1a\u513f\u600e\u4e48\u6562\u52b3\u4f60\u4eec\u7684\u5927\u9a7e\uff1f\u201d</p><p>\u3000\u3000\u201c\u6c5f\u4e66\u8bb0\u53ef\u662f\u4eb2\u81ea\u5429\u5490\u6211\u4eec\u8981\u7167\u987e\u597d\u4f60\u7684\u751f\u6d3b\uff0c\u6211\u4eec\u600e\u4e48\u6562\u6020\u6162\uff1f\u201d\u7fdf\u4e39\u9752\u5de7\u7b11\u5ae3\u7136\uff0c\u5750\u5230\u5f20\u606a\u8eab\u8fb9\uff0c\u5c06\u88c5\u996d\u83dc\u7684\u5851\u6599\u76d2\u4e00\u4e00\u6253\u5f00\uff0c\u653e\u5728\u73bb\u7483\u51e0\u4e0a\uff0c\u201c\u606a\u5c11\u5148\u5c06\u5c31\u4e9b\u5403\uff0c\u6211\u8ba9\u5927\u53a8\u51c6\u5907\u51e0\u4e2a\u5c0f\u83dc\uff0c\u4e00\u4f1a\u513f\u5c31\u80fd\u4e0a\u6765\u3002\u201d</p><p>\u3000\u3000\u201c\u6765\uff0c\u6765\uff0c\u6765\uff0c\u81ea\u5df1\u642c\u51f3\u5b50\uff0c\u201d\u5f20\u606a\u62db\u547c\u5085\u4fca\u4ed6\u4eec\u4e00\u8d77\u5750\u8fc7\u6765\u5403\u996d\uff0c\u201c\u6709\u732a\u5934\u8089\u5c31\u597d\uff0c\u5176\u4ed6\u7684\u5c31\u4e0d\u7528\u51c6\u5907\u4e86\u3002\u201d\u51e0\u4e2a\u4eba\u90fd\u997f\u614c\u4e86\uff0c\u72fc\u541e\u864e\u54bd\uff0c\u7247\u523b\u95f4\u5c31\u5c06\u51e0\u5851\u6599\u76d2\u7684\u996d\u83dc\u90fd\u5377\u5165\u8179\u4e2d\uff0c\u624d\u516d\u4e03\u6210\u9971\uff0c\u7fdf\u4e39\u9752\u51c6\u5907\u6253\u7535\u8bdd\u901a\u77e5\u4e0b\u9762\u518d\u51c6\u5907\u4e9b\u9001\u4e0a\u6765\uff0c\u5f20\u606a\u62e6\u4f4f\u5979\uff1a\u201c\u591f\u4e86\uff0c\u518d\u5403\u4f1a\u6491\uff0c\u201d\u5bf9\u5085\u4fca\u4ed6\u4eec\u8bf4\uff0c\u201c\u8c01\u611f\u89c9\u4e0d\u591f\u5148\u559d\u4e9b\u6c34\u57ab\u4e00\u57ab\uff0c\u809a\u5b50\u7559\u5230\u665a\u4e0a\u518d\u5403\u5927\u9910\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u63c9\u4e86\u63c9\u809a\u5b50\uff0c\u89c1\u7fdf\u4e39\u9752\u8ddf\u6628\u5929\u6bd4\u6709\u4e9b\u5f02\u5e38\uff0c\u95ee\u9053\uff1a\u201c\u7fdf\u59d0\u77e5\u9053\u6628\u5929\u53d1\u751f\u7684\u4e8b\u4e86\uff1f\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u5f53\u7136\u4e0d\u4f1a\u627f\u8ba4\u5979\u4e0e\u536b\u5170\u5c31\u5728\u73b0\u573a\uff0c\u5a07\u7b11\u7740\u8bf4\uff1a\u201c\u606a\u5c11\u540d\u9707\u65b0\u829c\uff0c\u6d88\u606f\u6628\u5929\u665a\u4e0a\u5c31\u4f20\u5f00\u4e86\uff0c\u6211\u8fd9\u91cc\u4e5f\u4e0d\u7b97\u5341\u5206\u7684\u5b64\u964b\u5be1\u95fb\uff0c\u600e\u4e48\u53ef\u80fd\u6ca1\u542c\u8bf4\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u7fdf\u59d0\u4e5f\u77e5\u9053\u5bf9\u65b9\u662f\u8c01\u55bd\uff1f\u201d\u5f20\u606a\u76ef\u7740\u7fdf\u4e39\u9752\u7684\u773c\u775b\uff0c\u8fd9\u5973\u4eba\u773c\u89d2\u7709\u68a2\u90fd\u5e26\u7740\u51e0\u5206\u98ce\u6d41\uff0c\u76c8\u76c8\u6c34\u6cfd\uff0c\u8fd9\u6837\u7684\u773c\u775b\u8fc7\u4e8e\u8273\u4e3d\u4e86\uff0c\u201c\u6574\u51fa\u90a3\u4e48\u5927\u7684\u52a8\u9759\uff0c\u6211\u5012\u4e5f\u6ca1\u6709\u60f3\u5230\uff1f\u201d</p><p>\u3000\u3000\u201c\u52a8\u9759\u662f\u591f\u5927\u7684\uff0c\u534a\u4e2a\u65b0\u829c\u7684\u6df7\u6df7\u90fd\u60ca\u52a8\u4e86\uff0c\u201d\u7fdf\u4e39\u9752\u7b11\u7740\u8bf4\uff0c\u201c\u6123\u662f\u6ca1\u4eba\u6562\u52a8\u606a\u5c11\u4e00\u6839\u6c57\u6bdb\uff0c\u606a\u5c11\u771f\u7684\u5f88\u5a01\u98ce\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fae\u5fae\u76b1\u7740\u7709\u5934\uff0c\u7fdf\u4e39\u9752\u6c5f\u6e56\u6c14\u592a\u91cd\u7684\u8bdd\uff0c\u8ba9\u4ed6\u542c\u8d77\u6765\u4e0d\u662f\u5f88\u8212\u670d\uff0c\u7fdf\u4e39\u9752\u4f5c\u4e3a\u4e00\u4e2a\u5e74\u7eaa\u8f7b\u8f7b\u7684\u6f02\u4eae\u5973\u4eba\u62c5\u4efb\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u7684\u603b\u7ecf\u7406\uff0c\u603b\u4f1a\u6709\u4e9b\u8fc7\u4eba\u7684\u624b\u6bb5\uff0c\u6d88\u606f\u4e5f\u8981\u6bd4\u4e00\u822c\u7684\u4eba\u7075\u901a\uff0c\u6709\u4e9b\u6d88\u606f\uff0c\u5979\u80fd\u900f\u9732\u90a3\u518d\u597d\u4e0d\u8fc7\u4e86\u3002\u5f20\u606a\u5e73\u9759\u7684\u770b\u7740\u7fdf\u4e39\u9752\uff0c\u793a\u610f\u5979\u7ee7\u7eed\u8bf4\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u773c\u7738\u5b50\u5fae\u5fae\u655b\u7740\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u4e8b\u60c5\u95f9\u8fd9\u4e48\u5927\uff0c\u6216\u8bb8\u5f20\u526f\u5e02\u957f\u5df2\u7ecf\u77e5\u9053\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u8bf4\u6211\u7238\uff0c\u6216\u8bb8\u77e5\u9053\uff0c\u6216\u8bb8\u4e0d\u77e5\u9053\u3002\u201d\u5f20\u606a\u8138\u4e0a\u6ca1\u6709\u592a\u591a\u7684\u8868\u60c5\uff0c\u4e0d\u60f3\u7fdf\u4e39\u9752\u4ece\u4ed6\u8fd9\u91cc\u770b\u5230\u592a\u591a\u7684\u6d88\u606f\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u5f53\u7136\u60f3\u77e5\u9053\u5f20\u77e5\u884c\u5bf9\u6628\u5929\u90a3\u4e8b\u7684\u6001\u5ea6\uff0c\u4e8b\u60c5\u867d\u7136\u6ca1\u6709\u95f9\u5927\uff0c\u4f46\u662f\u6570\u767e\u540d\u6df7\u6df7\u805a\u96c6\u8d77\u6765\u5a01\u80c1\u65b0\u4efb\u526f\u5e02\u957f\u7684\u513f\u5b50\uff0c\u8fd9\u6027\u8d28\u672c\u8eab\u5c31\u6781\u7aef\u7684\u6076\u52a3\u3002</p><p>\u3000\u3000\u5218\u658c\u6628\u5929\u77e5\u9053\u6d88\u606f\uff0c\u5982\u4e34\u5927\u7978\uff0c\u6025\u51b2\u51b2\u4ece\u5929\u4e91\u5c71\u8d76\u6765\uff0c\u4f46\u662f\u65b0\u95f8\u5c0f\u533a\u95e8\u53e3\u7684\u597d\u620f\u5df2\u7ecf\u6563\u573a\uff0c\u5f20\u606a\u4eba\u8f66\u90fd\u4e0d\u77e5\u53bb\u5411\u3002\u5728\u5218\u658c\u7684\u5fc3\u91cc\uff0c\u653f\u5e9c\u5b98\u5458\u5fc3\u72e0\u624b\u8fa3\u8d77\u6765\uff0c\u5c31\u4f1a\u62ff\u4eba\u6c11\u4e13\u653f\u7684\u90a3\u4e00\u5957\u5bf9\u4ed8\u4f60\uff0c\u9ed1\u793e\u4f1a\uff0c\u4e00\u767e\u4e2a\u9ed1\u793e\u4f1a\u90fd\u662f\u6ed3\uff0c\u9664\u4e86\u4f60\u6709\u6bd4\u4ed6\u66f4\u5f3a\u786c\u7684\u5b98\u9762\u5173\u7cfb\u3002\u504f\u504f\u4ed6\u4e5f\u542c\u8bf4\u8fd9\u4e2a\u65b0\u6765\u7684\u526f\u5e02\u957f\u6765\u5386\u4e0d\u51e1\uff0c\u53c8\u542c\u5230\u624b\u4e0b\u4eba\u8bb2\u8d77\u5f20\u606a\u7684\u6446\u573a\uff0c\u800c\u5f20\u606a\u6628\u5929\u7684\u8868\u73b0\u53c8\u7edd\u5bf9\u662f\u89c1\u8fc7\u5927\u573a\u9762\u7684\u4eba\uff0c\u54ea\u5bb9\u4ed6\u4e0d\u5fc3\u60ca\u8089\u8df3\uff1f\u5c31\u6015\u7a81\u7136\u4ece\u5916\u5e02\u8c03\u51e0\u5343\u540d\u6b66\u8b66\u8fdb\u57ce\u628a\u4ed6\u4eec\u5f53\u6210\u771f\u6b63\u7684\u9ed1\u793e\u4f1a\u7ed9\u4e13\u653f\u4e86\u3002\u4ed6\u72e0\u7740\u5fc3\u5f53\u7740\u4f17\u4eba\u7684\u9762\u72e0\u72e0\u6247\u4e86\u4ed6\u90a3\u9ec4\u6bdb\u513f\u5b50\u4e00\u5df4\u638c\uff0c\u6247\u51fa\u4e00\u9897\u69fd\u7259\u6765\uff0c\u636e\u76ee\u51fb\u8005\u79f0\uff0c\u7edd\u5bf9\u6bd4\u5f20\u606a\u6628\u5929\u4e0b\u5348\u52a8\u624b\u8fd8\u72e0\uff0c\u8fd9\u5f53\u7136\u662f\u505a\u7ed9\u5916\u4eba\u770b\u7684\uff0c\u4e5f\u5e0c\u671b\u80fd\u4f20\u5230\u5f20\u606a\u7684\u8033\u6735\uff1b\u5218\u658c\u53c8\u8fde\u591c\u5c06\u4ed6\u513f\u5b50\u9001\u51fa\u65b0\u829c\u5230\u7701\u57ce\u53bb\uff0c\u4ed6\u5fc3\u91cc\u603b\u5f52\u662f\u6709\u62a4\u728a\u7684\u5fc3\u601d\uff0c\u7fdf\u4e39\u9752\u5fc3\u60f3\u90a3\u9ec4\u6bdb\u8fd9\u65f6\u5019\u5e94\u8be5\u5750\u4e0a\u53bb\u6fb3\u6d32\u7684\u98de\u673a\u4e86\u5427\uff1f\u9664\u6b64\u4e4b\u5916\uff0c\u5218\u658c\u53c8\u8ba9\u6628\u5929\u53c2\u4e0e\u8fd9\u4e8b\u7684\u8857\u9053\u8001\u5927\u4eec\u90fd\u8fde\u591c\u5230\u5916\u5730\u8eb2\u4e00\u8eb2\u98ce\u58f0\u3002\u56e0\u4e3a\u7fdf\u4e39\u9752\u6628\u5929\u5728\u573a\uff0c\u800c\u5f20\u606a\u53c8\u4f4f\u5728\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u91cc\uff0c\u5218\u658c\u53c8\u627e\u5230\u7fdf\u4e39\u9752\u5e0c\u671b\u5979\u80fd\u4ece\u4e2d\u8c03\u89e3\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u4e5f\u6697\u81ea\u597d\u7b11\uff0c\u5218\u658c\u6d3e\u4eba\u627e\u5f20\u606a\u4e00\u5bbf\uff0c\u5374\u4e0d\u77e5\u9053\u5f20\u606a\u4ed6\u4eec\u51e0\u4e2a\u4eba\u5728\u6c5f\u5824\u4e0a\u7ad9\u4e86\u4e00\u5bbf\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u770b\u5230\u5f20\u606a\u72fc\u541e\u864e\u54bd\u7684\u5403\u4e1c\u897f\uff0c\u771f\u6000\u7591\u4ed6\u6709\u591a\u5c11\u7a77\u51f6\u6781\u6076\uff0c\u4f1a\u4e0d\u4f1a\u5218\u658c\u53cd\u5e94\u592a\u654f\u611f\u4e86\u4e00\u4e9b\uff1f\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u5f20\u606a\u5012\u662f\u5728\u65b0\u829c\u4e00\u591c\u6210\u540d\uff0c\u767d\u9053\u4e14\u4e0d\u53bb\u8bf4\uff0c\u9ed1\u9053\u4e0a\u65e0\u4eba\u4e0d\u6653\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u5fc3\u60f3\u5f20\u606a\u6628\u5929\u5c31\u6ca1\u6709\u5403\u4ec0\u4e48\u4e8f\uff0c\u5fc3\u91cc\u5e94\u8be5\u6ca1\u6709\u4ec0\u4e48\u6028\u6068\uff0c\u4e0d\u8fc7\u6362\u6210\u526f\u5e02\u957f\u7f57\u5f52\u6e90\u7684\u513f\u5b50\u7f57\u6587\u5c31\u96be\u8bf4\u4e86\uff0c\u6709\u8fd9\u4e48\u597d\u7684\u673a\u4f1a\uff0c\u54ea\u6015\u5fc3\u91cc\u53ea\u6709\u4e00\u4e01\u70b9\u7684\u6028\u6068\uff0c\u7f57\u6587\u591a\u534a\u4f1a\u5c06\u4ec7\u5bb6\u6574\u5f97\u5bb6\u7834\u4eba\u4ea1\u3002</p><p>\u3000\u3000\u5f20\u606a\u6628\u5929\u7684\u5f02\u6837\uff0c\u4efb\u4f55\u4e00\u4e2a\u654f\u611f\u7684\u5973\u4eba\u5fc3\u90fd\u4f1a\u7ed9\u89e6\u52a8\uff0c\u867d\u7136\u4e0d\u77e5\u9053\u539f\u56e0\uff0c\u7fdf\u4e39\u9752\u5927\u6982\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u5fd8\u8bb0\u5f20\u606a\u6628\u5929\u5728\u96e8\u4e2d\u62bf\u7740\u5634\u7684\u8868\u60c5\uff0c\u53ea\u662f\u73b0\u5728\u5f20\u606a\u5df2\u7ecf\u6062\u590d\u6b63\u5e38\uff0c\u6a21\u68f1\u4e24\u53ef\u7684\u56de\u7b54\uff0c\u53c8\u5411\u81ea\u5df1\u6253\u63a2\u6628\u5929\u751f\u4e8b\u8005\u7684\u5e95\u7ec6\uff0c\u8fd8\u771f\u7422\u78e8\u4e0d\u6e05\u4ed6\u5bf9\u8fd9\u4e8b\u7684\u6001\u5ea6\uff1a\u5230\u5e95\u662f\u5fc3\u91cc\u6709\u6028\u6068\u5462\uff0c\u8fd8\u662f\u53ea\u5f53\u6210\u65e5\u5e38\u751f\u6d3b\u7684\u5c0f\u4e8b\u4ef6\uff1f</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u662f\u8c01\uff1f\u201d\u5f20\u606a\u89c1\u7fdf\u4e39\u9752\u6709\u4e9b\u8d70\u795e\uff0c\u5355\u5200\u76f4\u5165\u7684\u95ee\u9053\uff0c\u201c\u6574\u4e86\u4e00\u8857\u7684\u6df7\u6df7\uff0c\u6027\u8d28\u5f88\u4e25\u91cd\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u80fd\u6709\u591a\u4e25\u91cd\uff0c\u8fd8\u4e0d\u662f\u7ed9\u606a\u5c11\u4f60\u957f\u5a01\u98ce\uff1f\u201d\u7fdf\u4e39\u9752\u8f7b\u63cf\u6de1\u5199\u7684\u8bf4\u9053\uff0c\u201c\u662f\u5927\u5174\u5de5\u8d38\u516c\u53f8\u603b\u7ecf\u7406\u5218\u658c\u7684\u513f\u5b50\u5218\u6b23\u51b2\u649e\u4e86\u4f60\uff0c\u5927\u5174\u5efa\u7b51\u5728\u4e1c\u7247\u6709\u4e9b\u5f71\u54cd\u529b\uff0c\u5218\u658c\u6628\u5929\u53bb\u5929\u53f0\u5c71\u70e7\u9999\u53bb\u4e86\uff0c\u8fd9\u4e9b\u5bb6\u4f19\uff0c\u4e00\u8eab\u7684\u94dc\u81ed\u5473\uff0c\u4e5f\u4e0d\u6015\u53bb\u548c\u5c1a\u5e99\u7ed9\u718f\u81ed\u4e86\uff1f\u4e5f\u662f\u5218\u6b23\u7ed9\u4f60\u4fee\u7406\u5f97\u591f\u60e8\uff0c\u624d\u62ff\u7740\u4ed6\u8001\u5b50\u7684\u540d\u4e49\u9080\u4eba\uff0c\u5f88\u591a\u4eba\u90fd\u662f\u53bb\u770b\u70ed\u95f9\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u542c\u8d77\u6765\u50cf\u6b63\u7ecf\u7684\u4f01\u4e1a\u5bb6\uff0c\u201d\u5f20\u606a\u6487\u5634\u7b11\u4e86\u7b11\uff0c\u201c\u6211\u4e5f\u5f88\u60f3\u89c1\u89c1\u65b0\u829c\u5f53\u5730\u7684\u4f01\u4e1a\u5bb6\u3002\u201d</p><p>\u3000\u3000\u867d\u7136\u8bf4\u5f20\u606a\u5728\u7b11\u7740\uff0c\u7fdf\u4e39\u9752\u5374\u611f\u89c9\u4e0d\u5230\u5f20\u606a\u7684\u7b11\u610f\uff0c\u5f20\u606a\u7ed9\u5979\u7684\u611f\u89c9\uff0c\u4e0e\u6628\u5929\u8fe5\u5f02\uff0c\u4eff\u4f5b\u4e00\u67c4\u51fa\u9798\u7684\u5229\u5251\u7ed9\u4eba\u5fc3\u5934\u5fae\u51c9\u7684\u611f\u89c9\uff0c\u53ea\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u6709\u8fd9\u4e2a\u610f\u601d\u5c31\u597d\uff0c\u5218\u603b\u4e5f\u60f3\u8bf7\u4f60\u8fc7\u53bb\u53d9\u4e00\u53d9\uff1f\u201d</p><p>\u3000\u3000\u201c\u5218\u658c\u901a\u8fc7\u4f60\u9080\u8bf7\u6211\uff1f\u201d\u5f20\u606a\u5fae\u8bb6\u7684\u770b\u7740\u7fdf\u4e39\u9752\uff0c\u4ed6\u5f53\u7136\u4e0d\u4fe1\u5218\u658c\u662f\u4e2a\u6b63\u7ecf\u7684\u5546\u4eba\uff0c\u5012\u6ca1\u6709\u60f3\u5230\u7fdf\u4e39\u9752\u662f\u4e2a\u9ed1\u767d\u901a\u5403\u7684\u4eba\u7269\uff0c\u6487\u5634\u4e00\u7b11\uff0c\u201c\u4ed6\u8fd9\u4e48\u5feb\u5c31\u77e5\u9053\u6211\u4f4f\u8fd9\u91cc\uff0c\u54ea\u91cc\u4f1a\u662f\u6b63\u7ecf\u7684\u5546\u4eba\uff0c\u4e0d\u89c1\u3002\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u54ea\u91cc\u60f3\u5230\u5f20\u606a\u503e\u523b\u95f4\u8bf4\u53d8\u8138\u5c31\u53d8\u8138\uff0c\u8baa\u7136\u7684\u5750\u5728\u90a3\u91cc\uff0c\u89c1\u5f20\u606a\u677f\u7740\u8138\uff0c\u6709\u4e9b\u8bdd\u5c31\u662f\u8146\u7740\u8138\u4e5f\u8bf4\u4e0d\u51fa\u53e3\uff0c\u89c1\u536b\u5170\u5728\u90a3\u91cc\u5c06\u5851\u6599\u98df\u76d2\u6536\u62fe\u505c\u5f53\uff0c\u4e5f\u6ca1\u6709\u7406\u7531\u7ee7\u7eed\u8017\u5728\u91cc\u9762\uff0c\u95ee\u5f20\u606a\u665a\u4e0a\u60f3\u5403\u4ec0\u4e48\uff0c\u5979\u8ba9\u5bbe\u9986\u5927\u53a8\u51c6\u5907\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4f1a\u51fa\u53bb\u5403\u3002\u201d</p><p>\u3000\u3000\u5f85\u7fdf\u4e39\u9752\u4e0e\u536b\u5170\u79bb\u5f00\uff0c\u5f20\u606a\u5bf9\u5085\u4fca\u7b11\u7740\u8bf4\uff1a\u201c\u5979\u8fd8\u4ee5\u4e3a\u6211\u4f1a\u5728\u65b0\u829c\u4ea4\u7ed3\u9f99\u86c7\u725f\u53d6\u597d\u5904\u5462\uff0c\u8001\u8bb8\u8fd9\u4e24\u5929\u6709\u4ec0\u4e48\u8fdb\u5c55\u6ca1\u6709\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u745e\u5e73\u76f8\u8c8c\u666e\u901a\uff0c\u4ed6\u7684\u74f7\u5668\u5382\u4e0e\u65b0\u829c\u5e02\u76f8\u5173\u5355\u4f4d\u6709\u4e1a\u52a1\u5173\u7cfb\uff0c\u7531\u4ed6\u51fa\u53bb\u6253\u63a2\u6d88\u606f\u518d\u5408\u9002\u4e0d\u8fc7\u3002</p><p>\u3000\u3000\u201c\u6c5f\u5824\u7684\u4e8b\u60c5\u8fd8\u6ca1\u6709\u8fdb\u5c55\uff0c\u4e0d\u8fc7\u8fd9\u4e2a\u5218\u658c\u771f\u662f\u4e0d\u7b80\u5355\uff0c\u53ef\u4ee5\u8bf4\u662f\u624b\u906e\u65b0\u829c\u534a\u5929\u7247\u7684\u5927\u54e5\u4eba\u7269\uff0c\u9664\u4e86\u7fdf\u4e39\u9752\u8bf4\u7684\u5de5\u8d38\u516c\u53f8\uff0c\u4ed6\u8fd8\u5728\u57ce\u4e1c\u6709\u5ea7\u7687\u540e\u5a31\u4e50\u573a\uff0c\u542c\u8bf4\u65b0\u829c\u7684\u6709\u94b1\u4eba\u90fd\u559c\u6b22\u5230\u90a3\u91cc\u53bb\u6d88\u9063\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u8fd9\u4e48\u8bf4\u6211\u4eec\u665a\u4e0a\u6709\u7740\u843d\u4e86\uff1f\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u536b\u5170\u62ff\u7740\u5851\u6599\u98df\u76d2\u8ddf\u7fdf\u4e39\u9752\u51fa\u4e86\u623f\u95f4\uff0c\u5c0f\u58f0\u7684\u95ee\u5979\uff1a\u201c\u7fdf\u59d0\uff0c\u4f60\u8bf4\u4ed6\u5a01\u98ce\u4e86\u5c31\u662f\u6307\u8fd9\u4e2a\u3002\u201d</p><p>\u3000\u3000\u201c\u6b7b\u59ae\u5b50\u4e0d\u662f\u4e0d\u60f3\u542c\u7684\u5417\uff1f\u201d\u7fdf\u4e39\u9752\u7b11\u7740\u8981\u53bb\u6390\u5979\uff0c\u201c\u658c\u8001\u5927\u60f3\u7740\u8ddf\u4ed6\u4f4e\u5934\uff0c\u4ed6\u90fd\u4e0d\u7231\u642d\u7406\uff0c\u8fd8\u4e0d\u591f\u4ed6\u5a01\u98ce\u7684\u554a\uff1f\u201d</p></article>"}}}]);
