(window.webpackJsonp=window.webpackJsonp||[]).push([[379],{401:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e09\u5341\u516d\u8282 \u9634\u5f71\u5170",content:"<article><h2>\u7b2c\u4e09\u5341\u516d\u8282 \u9634\u5f71\u5170</h2><p>\u50f5\u6301\u7247\u523b\uff0c\u8299\u8389\u5a05\u6700\u7ec8\u8fd8\u662f\u59a5\u534f\uff0c\u5728\u65c1\u8fb9\u5750\u4e0b\u3002\u743c\u6069\u8bf7\u57ce\u4e2d\u5e74\u957f\u7684\u77ee\u4eba\u8fc7\u6765\uff0c\u4ed4\u7ec6\u8be2\u95ee\u6709\u5173\u90a3\u5757\u707e\u5384\u4e4b\u5730\u7684\u4e00\u5207\u4f20\u95fb\u4fe1\u606f\uff0c\u53c8\u4ece\u56fe\u4e66\u9986\u8c03\u96c6\u5b97\u5377\u67e5\u9605\u3002\u4ed6\u5f00\u59cb\u542c\u5230\u8299\u857e\u72c4\u51fa\u4e8b\u65f6\u8fd8\u6709\u4e9b\u60ca\u614c\uff0c\u73b0\u5728\u5374\u5df2\u7ecf\u5b8c\u5168\u9547\u5b9a\u4e0b\u6765\uff0c\u8138\u4e0a\u534a\u70b9\u58f0\u8272\u4e0d\u52a8\u3002\u8299\u8389\u5a05\u5374\u6ca1\u4ed6\u8fd9\u4e48\u6c89\u5f97\u4f4f\u6c14\uff0c\u60f3\u7740\u59b9\u59b9\u8bef\u5165\u9669\u5730\uff0c\u751f\u6b7b\u672a\u535c\uff0c\u53ea\u89c9\u6bcf\u4e00\u5206\u6bcf\u4e00\u79d2\u90fd\u6f2b\u957f\u65e0\u6bd4\uff0c\u743c\u6069\u5374\u8fd8\u4e0d\u65f6\u8ba9\u5979\u5728\u65c1\u8fb9\u505a\u8bb0\u5f55\u3002\u8db3\u8db3\u8fc7\u4e86\u56db\u4e2a\u5c0f\u65f6\uff0c\u743c\u6069\u65b9\u624d\u8d77\u8eab\uff0c\u5bf9\u8299\u8389\u5a05\u8bf4\uff0c\u201c\u6211\u4eec\u56de\u53bb\u3002\u201d</p><p>\u5230\u4e86\u4f4f\u5904\uff0c\u51db\u548c\u838e\u73de\u514b\u4e5f\u5df2\u7ecf\u56de\u6765\uff0c\u743c\u6069\u628a\u4eba\u53ec\u96c6\u4e00\u5904\uff0c\u5c06\u4e8b\u60c5\u8bf4\u4e86\u4e00\u904d\u3002\u8fd9\u56db\u4eba\u4e4b\u4e2d\uff0c\u51db\u5e74\u5c81\u6700\u957f\uff0c\u4f46\u5fc3\u601d\u5176\u5b9e\u6700\u5355\u7eaf\uff0c\u6216\u8005\u8bf4\u7387\u6027\u5766\u8361\uff0c\u6ca1\u90a3\u4e48\u591a\u5f2f\u7ed5\u60f3\u6cd5\uff0c\u542c\u4e86\u4fbf\u8bf4\uff1a\u201c\u90a3\u8fd8\u7b49\u4ec0\u4e48\uff0c\u5f53\u7136\u662f\u8d76\u5feb\u53bb\u627e\u5979\u554a\u3002\u201d\u838e\u73de\u514b\u5374\u8c28\u614e\u4e9b\uff0c\u77a5\u89c1\u743c\u6069\u795e\u60c5\uff0c\u8bd5\u63a2\u5730\u95ee\uff1a\u201c\u90a3\u5730\u65b9\u96be\u9053\u6f5c\u4f0f\u7740\u4ec0\u4e48\u51f6\u9669\u602a\u7269\uff1f\u201d</p><p>\u743c\u6069\u6447\u6447\u5934\uff0c\u201c\u6211\u4e5f\u4e0d\u6e05\u695a\uff0c\u4f46\u53ea\u6015\u6ca1\u8fd9\u4e48\u7b80\u5355\u3002\u201d</p><p>\u4ed6\u5c06\u8299\u8389\u5a05\u8bb0\u5f55\u7684\u90a3\u4e9b\u8d44\u6599\u53d6\u8fc7\u6765\uff0c\u9012\u7ed9\u51db\u548c\u838e\u73de\u514b\u4f20\u770b\u3002\u201c\u90a3\u5757\u533a\u57df\u65b9\u5706\u5927\u7ea6\u56db\u5341\u82f1\u91cc\uff0c\u5468\u56f4\u4eff\u4f5b\u6709\u4e00\u5708\u65e0\u5f62\u7684\u754c\u7ebf\uff0c\u53ea\u8981\u8d8a\u8fc7\u4e86\uff0c\u54ea\u6015\u662f\u8d70\u8fdb\u534a\u6b65\uff0c\u4e5f\u4f1a\u7acb\u523b\u50cf\u8ff7\u5931\u4e86\u5fc3\u667a\u4e00\u822c\uff0c\u53ea\u7ba1\u4e00\u8def\u5f80\u91cc\u8d70\uff0c\u518d\u4e0d\u56de\u5934\uff0c\u65e0\u8bba\u600e\u4e48\u53eb\u90fd\u53eb\u4e0d\u56de\u6765\u3002\u4e4b\u6240\u4ee5\u6240\u6709\u4eba\u90fd\u6709\u8fdb\u65e0\u51fa\uff0c\u539f\u56e0\u5168\u5728\u4e8e\u6b64\uff0c\u5c31\u5982\u540c\u4e00\u4e2a\u5927\u6f29\u6da1\u4f3c\u7684\uff0c\u9760\u8fd1\u7684\u4eba\u90fd\u88ab\u5377\u4e86\u8fdb\u53bb\u3002\u201d</p><p>\u51db\u7c97\u7565\u7ffb\u4e86\u7ffb\uff0c\u79c0\u7709\u5fae\u76b1\uff0c\u201c\u5e7b\u672f\uff0c\u8fd8\u662f\u9644\u9b54\uff1f\u201d</p><p>\u5979\u662f\u5deb\u5e08\uff0c\u4e00\u65e6\u9047\u5230\u5f02\u5e38\u60c5\u5f62\uff0c\u4e60\u60ef\u6027\u5730\u5c31\u60f3\u5230\u7528\u9b54\u6cd5\u6765\u89e3\u91ca\u3002\u5c31\u743c\u6069\u521a\u624d\u6240\u8bf4\uff0c\u8fd9\u5757\u707e\u5384\u4e4b\u5730\u5206\u660e\u662f\u88ab\u4e00\u80a1\u5f3a\u5927\u7684\u795e\u79d8\u529b\u91cf\u7ed9\u7b3c\u7f69\u4f4f\u4e86\uff0c\u4e00\u65e6\u6709\u4eba\u8e0f\u5165\u5176\u4e2d\u4fbf\u88ab\u5f71\u54cd\uff0c\u6709\u53ef\u80fd\u662f\u4e2d\u4e86\u9644\u9b54\u672f\uff0c\u8ff7\u5931\u5fc3\u667a\uff0c\u6d51\u6d51\u5669\u5669\uff0c\u4efb\u4eba\u6446\u5e03\uff1b\u4e5f\u6709\u53ef\u80fd\u662f\u88ab\u5e7b\u50cf\u8499\u853d\uff0c\u795e\u667a\u867d\u7136\u672a\u5931\uff0c\u4f46\u773c\u4e2d\u6240\u89c1\u3001\u8033\u4e2d\u6240\u95fb\u5168\u90fd\u662f\u5047\u7684\uff0c\u81ea\u7136\u4e5f\u5c31\u4e3b\u52a8\u843d\u5165\u9677\u9631\u3002\u5177\u4f53\u662f\u54ea\u4e00\u79cd\uff0c\u90a3\u5c31\u8981\u518d\u8fdb\u4e00\u6b65\u5206\u6790\u4e86\uff0c\u4e5f\u6709\u53ef\u80fd\u662f\u4e24\u79cd\u7cc5\u5408\uff0c\u4ea4\u4e92\u4f5c\u7528\u7684\u7ed3\u679c\u3002</p><p>\u201c\u90fd\u6709\u53ef\u80fd\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f46\u53c8\u90fd\u4e0d\u592a\u50cf\u3002\u201d</p><p>\u9644\u9b54\u672f\u662f\u76f4\u63a5\u4fb5\u88ad\u7cbe\u795e\uff0c\u5f71\u54cd\u610f\u8bc6\uff0c\u867d\u7136\u5f3a\u529b\uff0c\u5374\u5f88\u96be\u4fdd\u8bc1\u767e\u5206\u4e4b\u767e\u7684\u6210\u529f\u7387\uff0c\u9047\u4e0a\u5fc3\u667a\u575a\u5b9a\u4e4b\u8f88\u5c31\u5f88\u5bb9\u6613\u5931\u6548\uff0c\u4f55\u51b5\u77ee\u4eba\u548c\u5353\u5c14\u8fd8\u90fd\u6709\u5929\u8d4b\u7684\u6297\u9b54\u80fd\u529b\uff0c\u6ca1\u90a3\u4e48\u5bb9\u6613\u88ab\u63a7\u5236\uff0c\u603b\u8be5\u6709\u4eba\u80fd\u9003\u51fa\u6765\u624d\u5bf9\u3002\u5e7b\u672f\u662f\u6b3a\u9a97\u8499\u853d\u611f\u5b98\u4e94\u8bc6\uff0c\u8ba9\u4eba\u201c\u5fc3\u7518\u60c5\u613f\u201d\u5730\u4e0a\u5f53\u53d7\u9a97\uff0c\u9ad8\u660e\u7684\u5e7b\u672f\u5e08\u53ef\u4ee5\u5c06\u67d0\u5757\u533a\u57df\u6574\u4e2a\u7b3c\u7f69\u5728\u5e7b\u50cf\u4e4b\u4e2d\uff0c\u6f14\u5316\u51fa\u6d77\u5e02\u8703\u697c\u822c\u7684\u6548\u679c\uff0c\u65e0\u8bba\u5149\u5f71\u3001\u8272\u5f69\u3001\u58f0\u54cd\u3001\u6c14\u5473\u751a\u81f3\u89e6\u611f\u90fd\u80fd\u6a21\u62df\u5f97\u6829\u6829\u5982\u771f\uff0c\u6beb\u65e0\u7834\u7efd\u3002\u4f46\u95ee\u9898\u662f\u8fd9\u5757\u707e\u5384\u4e4b\u5730\u5b9e\u5728\u592a\u5927\u4e86\uff0c\u65b9\u5706\u56db\u5341\u82f1\u91cc\uff0c\u518d\u9ad8\u660e\u7684\u5deb\u5e08\u4e5f\u4e0d\u53ef\u80fd\u521b\u9020\u51fa\u5982\u6b64\u5e9e\u5927\uff0c\u800c\u4e14\u662f\u65f6\u65f6\u523b\u523b\u6c38\u6052\u6709\u6548\u7684\u5e7b\u50cf\u3002</p><p>\u201c\u4f1a\u4e0d\u4f1a\u5176\u5b9e\u662f\u4e2a\u534a\u4f4d\u9762\uff1f\u201d\u838e\u73de\u514b\u4e5f\u63d0\u51fa\u81ea\u5df1\u7684\u770b\u6cd5\u3002</p><p>\u201c\u5982\u679c\u662f\u534a\u4f4d\u9762\uff0c\u90a3\u8fdb\u5165\u7684\u4eba\u5c31\u5e94\u8be5\u77ac\u95f4\u6d88\u5931\u624d\u5bf9\uff0c\u4f46\u6839\u636e\u8d44\u6599\u5e76\u975e\u5982\u6b64\u3002\u201d</p><p>\u4f17\u4eba\u90fd\u4e0d\u5f97\u5176\u89e3\uff0c\u623f\u95f4\u91cc\u6c14\u6c1b\u4e00\u65f6\u6709\u4e9b\u6c89\u95f7\uff0c\u743c\u6069\u5176\u5b9e\u4e5f\u5e76\u6ca1\u6307\u671b\u5c31\u51ed\u8fd9\u70b9\u96f6\u788e\u8d44\u6599\u4fbf\u80fd\u63a8\u6572\u51fa\u707e\u5384\u4e4b\u5730\u7684\u771f\u76f8\u6765\uff0c\u5018\u82e5\u4e8b\u60c5\u5982\u6b64\u5bb9\u6613\uff0c\u90a3\u77ee\u4eba\u5f53\u4e2d\u4e5f\u6709\u7267\u5e08\uff0c\u5353\u5c14\u66f4\u4e0d\u7f3a\u9ad8\u660e\u7684\u5deb\u5e08\uff0c\u54ea\u91cc\u8fd8\u8981\u7b49\u4ed6\u6765\u505a\u3002\u4e4b\u6240\u4ee5\u8981\u5148\u4ea4\u4ee3\u8fd9\u4e9b\uff0c\u66f4\u591a\u8fd8\u662f\u56e0\u4e3a\u8fd9\u4e00\u6b21\u7684\u9ebb\u70e6\u786e\u5b9e\u6709\u4e9b\u68d8\u624b\uff0c\u987b\u5f97\u5148\u628a\u8bdd\u8bf4\u6e05\u695a\u4e86\u3002</p><p>\u8299\u857e\u72c4\u9047\u9669\uff0c\u743c\u6069\u81ea\u7136\u8981\u53bb\u6551\uff0c\u8fd9\u662f\u6839\u672c\u65e0\u9700\u8003\u8651\u7684\u4e8b\u60c5\uff0c\u4f46\u5c31\u73b0\u5728\u770b\u6765\uff0c\u8fd9\u707e\u5384\u4e4b\u5730\u5e76\u975e\u8c23\u4f20\uff0c\u8fd8\u771f\u662f\u6709\u4e9b\u53e4\u602a\u3002\u4ed6\u4e5f\u7b97\u662f\u5927\u98ce\u5927\u6d6a\u89c1\u5f97\u591a\u4e86\uff0c\u6df1\u6e0a\u5730\u72f1\u90fd\u53bb\u8fc7\uff0c\u9762\u5bf9\u795e\u7947\u6076\u9b54\u9b54\u9b3c\u4e5f\u80fd\u4fdd\u5f97\u6027\u547d\uff0c\u80c6\u6c14\u65e9\u5c31\u953b\u70bc\u51fa\u6765\uff0c\u81ea\u7136\u8c08\u4e0d\u4e0a\u60e7\u6015\u9000\u7f29\uff0c\u4f46\u8981\u8bf4\u5355\u51ed\u81ea\u5df1\u4e00\u4eba\u5c31\u80fd\u641e\u5b9a\uff0c\u8fd9\u4fe1\u5fc3\u4ed6\u5374\u8fd8\u6ca1\u6709\u3002\u5c31\u76ee\u524d\u60c5\u5f62\u6765\u770b\uff0c\u8fd9\u4e9b\u77ee\u4eba\u662f\u80af\u5b9a\u5e2e\u4e0d\u4e0a\u5fd9\u4e86\uff0c\u534a\u70b9\u4e0d\u7528\u6307\u671b\uff0c\u6668\u70bc\u90a3\u8fb9\u867d\u7136\u6709\u4e9b\u4eba\u9a6c\uff0c\u4f46\u8ddd\u79bb\u592a\u8fdc\uff0c\u4e5f\u662f\u6765\u4e0d\u53ca\u3002\u76ee\u524d\u552f\u4e00\u80fd\u591f\u4f5c\u4e3a\u52a9\u529b\u7684\uff0c\u5c31\u662f\u8eab\u8fb9\u7684\u8fd9\u4e09\u4e2a\u5973\u5b69\u5b50\u3002\u8299\u8389\u5a05\u81ea\u7136\u4e0d\u7528\u8bf4\uff0c\u838e\u73de\u514b\u7684\u610f\u613f\u4e5f\u53ef\u4ee5\u5927\u81f4\u5ffd\u7565\u6389\uff0c\u4f46\u51db\u5219\u4e0d\u540c\uff0c\u5979\u548c\u743c\u6069\u7684\u5173\u7cfb\u9887\u4e3a\u5fae\u5999\uff0c\u4ecb\u4e4e\u4e8e\u60c5\u4eba\u548c\u670b\u53cb\u4e4b\u95f4\uff0c\u548c\u8299\u857e\u72c4\u4e5f\u6ca1\u6709\u534a\u70b9\u4ea4\u60c5\u53ef\u8a00\uff0c\u751a\u81f3\u8fd8\u7b97\u662f\u60c5\u654c\u3002\u743c\u6069\u8981\u53bb\u6551\u8299\u857e\u72c4\uff0c\u90a3\u662f\u8d23\u65e0\u65c1\u8d37\uff0c\u4f46\u5018\u82e5\u62c9\u7740\u51db\u4e00\u540c\u8d74\u9669\uff0c\u90a3\u5c31\u5f97\u770b\u5c0f\u5973\u5deb\u4e50\u610f\u4e0d\u4e50\u610f\uff0c\u60c5\u613f\u4e0d\u60c5\u613f\u4e86\u3002</p><p>\u4e0d\u8fc7\uff0c\u743c\u6069\u660e\u663e\u662f\u591a\u8651\u4e86\uff0c\u56e0\u4e3a\u51db\u4f3c\u4e4e\u538b\u6839\u5c31\u6ca1\u60f3\u8fd9\u4e48\u591a\u3002</p><p>\u201c\u7ba1\u5b83\u662f\u4ec0\u4e48\u5462\uff0c\u8fdb\u53bb\u770b\u770b\u4e0d\u5c31\u77e5\u9053\u4e86\uff0c\u5728\u8fd9\u91cc\u4e71\u731c\u6709\u4ec0\u4e48\u610f\u601d\uff0c\u201d\u5979\u8f7b\u76c8\u5730\u4ece\u6905\u5b50\u4e0a\u8df3\u8d77\u6765\uff0c\u62cd\u62cd\u88d9\u89d2\uff0c\u201c\u6211\u4eec\u73b0\u5728\u5c31\u51fa\u53d1\u5427\u3002\u201d</p><p>\u743c\u6069\u6df1\u6df1\u770b\u4e86\u5979\u4e00\u773c\uff0c\u201c\u51db\uff0c\u591a\u8c22\u4e86\u3002\u201d</p><p>\u201c\u5207\uff0c\u8bf4\u4ec0\u4e48\u83ab\u540d\u5176\u5999\u7684\u8bdd\uff0c\u201d\u51db\u6241\u6241\u5634\uff0c\u201c\u6211\u548c\u4f60\u4e00\u8d77\u51fa\u6765\uff0c\u81ea\u7136\u5c31\u8981\u8d1f\u8d23\u4fdd\u62a4\u4f60\uff0c\u628a\u4f60\u5b89\u7136\u65e0\u6059\u5730\u5e26\u56de\u53bb\uff0c\u5426\u5219\u7684\u8bdd\uff0c\u827e\u5f25\u8587\u4f1a\u627e\u6211\u7b97\u8d26\u7684\u3002\u201d</p><p>\u201c\u2026\u2026\u4f60\u4e0d\u8981\u8bf4\u5f97\u6211\u597d\u50cf\u5f88\u5dee\u52b2\u4f3c\u7684\u3002\u201d</p><p>\u201c\u672c\u6765\u5c31\u662f\u561b\uff0c\u201d\u51db\u563b\u563b\u7b11\u7740\uff0c\u201c\u8d70\u5566\u8d70\u5566\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u707e\u5384\u4e4b\u5730\u8ddd\u79bb\u4f0a\u5361\u6c99\u57ce\u5e76\u4e0d\u5f88\u8fdc\uff0c\u743c\u6069\u7b49\u4eba\u5168\u901f\u8d76\u8def\uff0c\u4e24\u65e5\u4e4b\u540e\u4e5f\u5c31\u62b5\u8fbe\uff0c\u5c06\u9886\u8def\u7684\u77ee\u4eba\u5411\u5bfc\u6253\u53d1\u56de\u53bb\uff0c\u4e00\u884c\u4eba\u5f84\u76f4\u524d\u884c\u3002</p><p>\u4e3a\u4e86\u9632\u6b62\u8bef\u5165\uff0c\u77ee\u4eba\u4eec\u5728\u8fb9\u754c\u7ebf\u4e0a\u7acb\u8d77\u4e86\u4e00\u6392\u8bb0\u53f7\u4f5c\u4e3a\u8b66\u793a\uff0c\u867d\u7136\u6ca1\u6709\u536b\u5175\u770b\u5b88\u4e5f\u6ca1\u6709\u94c1\u4e1d\u7f51\u9ad8\u538b\u7535\uff0c\u4f46\u6bd4\u4ec0\u4e48\u94dc\u5899\u94c1\u58c1\u90fd\u6709\u6548\uff0c\u51e1\u662f\u8def\u8fc7\u8005\u65e0\u4e0d\u8fdc\u8fdc\u907f\u5f00\uff0c\u751f\u6015\u88ab\u6b83\u53ca\u3002\u743c\u6069\u7ad9\u5728\u5916\u9762\u5f80\u91cc\u770b\uff0c\u89c2\u5bdf\u534a\u5929\u4e5f\u6ca1\u53d1\u89c9\u6709\u4ec0\u4e48\u5f02\u6837\uff0c\u542f\u52a8\u4e86\u5965\u672f\u89c6\u89c9\uff0c\u5374\u8fde\u9b54\u6cd5\u7075\u5149\u90fd\u6ca1\u770b\u89c1\u534a\u70b9\uff0c\u4e00\u5207\u90fd\u666e\u901a\u5f97\u4e0d\u80fd\u518d\u666e\u901a\uff0c\u5e73\u5e38\u5f97\u4e0d\u80fd\u518d\u5e73\u5e38\uff0c\u4ee5\u81f3\u4e8e\u8ba9\u4ed6\u90fd\u6709\u4e9b\u5fd0\u5fd1\u4e0d\u5b89\u8d77\u6765\u3002</p><p>\u4ed6\u8bd5\u7740\u53ec\u5524\u4e86\u4e00\u53ea\u571f\u5143\u7d20\uff0c\u6d3e\u5b83\u5148\u53bb\u63a2\u8def\uff0c\u770b\u770b\u60c5\u51b5\uff0c\u7ed3\u679c\u571f\u5143\u7d20\u521a\u521a\u8e0f\u8fc7\u754c\u7ebf\uff0c\u743c\u6069\u548c\u5b83\u4e4b\u95f4\u7684\u7cbe\u795e\u8054\u7ed3\u987f\u65f6\u4fbf\u81ea\u52a8\u6d88\u5931\u4e86\u3002\u5931\u53bb\u4e86\u5deb\u5e08\u7684\u6307\u4ee4\uff0c\u571f\u5143\u7d20\u5148\u662f\u832b\u7136\u5446\u7acb\uff0c\u4e0d\u77e5\u6240\u63aa\uff0c\u63a5\u7740\u5e9e\u5927\u7684\u8eab\u8eaf\u6162\u6162\u878d\u5316\u6210\u4e00\u5806\u7c98\u571f\uff0c\u6e17\u900f\u8fdb\u5730\u4e0b\uff0c\u5c31\u6b64\u6d88\u5931\u3002</p><p>\u743c\u6069\u548c\u51db\u9762\u9762\u76f8\u89d1\uff0c\u4ed6\u4eec\u90fd\u662f\u5deb\u5e08\uff0c\u4f46\u90fd\u6ca1\u529e\u6cd5\u89e3\u91ca\u8fd9\u79cd\u60c5\u5f62\u3002\u4ece\u7406\u8bba\u4e0a\u6765\u8bf4\uff0c\u65e2\u7136\u6210\u529f\u53ec\u5524\u4e86\u5143\u7d20\uff0c\u53ea\u8981\u5728\u89c6\u91ce\u4e4b\u5185\uff0c\u5c31\u80fd\u6d3e\u9063\u6307\u6325\u5b83\u4efb\u610f\u884c\u52a8\uff0c\u9664\u975e\u53d7\u5230\u5916\u529b\u7684\u5f3a\u5927\u5e72\u6d89\uff0c\u5426\u5219\u53ec\u5524\u8005\u548c\u5143\u7d20\u4e4b\u95f4\u7684\u7cbe\u795e\u8054\u7ed3\u662f\u4e0d\u4f1a\u81ea\u5df1\u7a81\u7136\u65ad\u5f00\u7684\u3002</p><p>\u201c\u96be\u9053\u8fd9\u91cc\u9762\u662f\u6b7b\u9b54\u6cd5\u533a\uff1f\u201d\u51db\u731c\u6d4b\u3002</p><p>\u743c\u6069\u6447\u5934\u5426\u5b9a\u4e86\u8fd9\u4e2a\u731c\u6d4b\uff0c\u201c\u6b7b\u9b54\u6cd5\u533a\u600e\u4e48\u4f1a\u5f71\u54cd\u5230\u77ee\u4eba\uff1f\u201d</p><p>\u8299\u8389\u5a05\u53d6\u51fa\u94f6\u5fbd\u548c\u5723\u76d8\uff0c\u5c1d\u8bd5\u7528\u751f\u7269\u5b9a\u4f4d\u672f\u6765\u63a2\u6d4b\u8299\u857e\u72c4\u7684\u4e0b\u843d\uff0c\u7ed3\u679c\u662f\u6beb\u65e0\u60ac\u5ff5\u5730\u5931\u8d25\u3002\u201c\u7b97\u4e86\uff0c\u4e0d\u7528\u8bd5\u4e86\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u53ea\u80fd\u8fdb\u53bb\u8d70\u4e00\u8d9f\u3002\u201d</p><p>\u8725\u8734\u5750\u9a91\u4eff\u4f5b\u611f\u5e94\u5230\u4ec0\u4e48\u4f3c\u7684\uff0c\u600e\u4e48\u9a71\u8d76\u90fd\u4e0d\u80af\u518d\u524d\u8fdb\u534a\u6b65\uff0c\u53ea\u5f97\u653e\u5230\u4e00\u8fb9\uff0c\u6b65\u884c\u8fdb\u5165\u3002\u743c\u6069\u8d70\u5728\u6700\u524d\u9762\uff0c\u534a\u4e2a\u8eab\u4f53\u521a\u521a\u8d8a\u8fc7\u754c\u7ebf\uff0c\u9661\u7136\u95f4\u4fbf\u662f\u773c\u524d\u4e00\u82b1\uff0c\u7b49\u518d\u56de\u8fc7\u795e\u65f6\uff0c\u53d1\u73b0\u9762\u524d\u7684\u666f\u8c61\u4e00\u5207\u5982\u65e7\uff0c\u4f46\u9690\u9690\u7ea6\u7ea6\u95f4\u6709\u4e86\u4e9b\u7ec6\u5fae\u53d8\u5316\u2014\u2014\u5230\u5e95\u662f\u4ec0\u4e48\u53d8\u5316\uff0c\u5374\u53c8\u6ca1\u529e\u6cd5\u5f62\u5bb9\uff0c\u52c9\u5f3a\u63cf\u8ff0\u7684\u8bdd\uff0c\u5c31\u50cf\u662f\u7a7a\u6c14\u4e2d\u7a81\u7136\u98d8\u8d77\u4e00\u5c42\u7a00\u8584\u7684\u7070\u6697\u96fe\u6c14\uff0c\u7f25\u7f25\u6e3a\u6e3a\uff0c\u82e5\u6709\u82e5\u65e0\uff0c\u7a79\u9876\u3001\u77f3\u58c1\u3001\u5730\u9762\u2026\u2026\u6240\u6709\u7684\u7269\u4f53\u8868\u9762\u90fd\u5f25\u6f2b\u7740\u4e00\u5c42\u6de1\u5230\u6781\u70b9\u7684\u9634\u5f71\uff0c\u5373\u4fbf\u662f\u9644\u7740\u4e0d\u706d\u660e\u7130\u7684\u706b\u628a\u4e5f\u65e0\u6cd5\u9a71\u9000\u3002\u5ca9\u77f3\u539f\u672c\u575a\u786c\u7684\u5149\u6cfd\u53d8\u5f97\u9eef\u6de1\uff0c\u8fde\u9510\u5229\u7684\u68f1\u89d2\u4eff\u4f5b\u90fd\u67d4\u548c\u4e86\u51e0\u5206\uff0c\u743c\u6069\u8be7\u5f02\u4e4b\u4e0b\uff0c\u56de\u8eab\u5f80\u540e\u770b\u53bb\uff0c\u5374\u53ea\u770b\u5230\u4e00\u7247\u6d53\u5bc6\u96fe\u6c14\uff0c\u6765\u65f6\u7684\u9053\u8def\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u5b8c\u5168\u6d88\u5931\u4e86\u3002</p><p>\u201c\u5e7b\u672f\uff1f\u201d</p><p>\u51fa\u53d1\u4e4b\u524d\u4f17\u4eba\u4e5f\u5546\u8bae\u8fc7\uff0c\u6000\u7591\u662f\u5e7b\u672f\u4f5c\u795f\uff0c\u9884\u5148\u505a\u4e86\u4e00\u4e9b\u76f8\u5e94\u7684\u51c6\u5907\u3002\u743c\u6069\u4ece\u6000\u4e2d\u6478\u51fa\u4e00\u679a\u692d\u5706\u5f62\u7684\u6d45\u8272\u86cb\u767d\u77f3\uff0c\u6b63\u8981\u671d\u5730\u9762\u7838\u53bb\uff0c\u8ff7\u96fe\u4e4b\u4e2d\u53c8\u51b2\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u6b63\u662f\u51db\u3002</p><p>\u838e\u73de\u514b\u548c\u8299\u8389\u5a05\u4e5f\u7d27\u968f\u5176\u540e\u8d70\u51fa\uff0c\u56db\u4eba\u91cd\u65b0\u805a\u9f50\uff0c\u770b\u7740\u8eab\u540e\u7684\u9053\u8def\u7a81\u7136\u88ab\u91cd\u91cd\u8ff7\u96fe\u6df9\u6ca1\uff0c\u4e00\u65f6\u90fd\u6709\u4e9b\u53d1\u6014\u3002\u743c\u6069\u7838\u788e\u4e86\u6d45\u86cb\u767d\u77f3\uff0c\u4e00\u9053\u767d\u5149\u4ece\u4e2d\u7206\u51fa\uff0c\u6ce2\u6d6a\u822c\u5f80\u56db\u9762\u516b\u65b9\u51b2\u51fb\u6269\u6563\u5f00\u53bb\u3002</p><p>\u8fd9\u662f\u771f\u77e5\u672f\uff0c\u9884\u8a00\u5b66\u6d3e\u7684\u62db\u724c\u6cd5\u672f\u4e4b\u4e00\uff0c\u4e13\u95e8\u7834\u5404\u79cd\u5e7b\u5f71\u3001\u5047\u8c61\u3001\u9690\u5f62\u3001\u9b54\u6cd5\u9ed1\u6697\uff0c\u751a\u81f3\u80fd\u591f\u770b\u7834\u53d8\u5f62\u4f2a\u88c5\uff0c\u5b9e\u5728\u662f\u5c45\u5bb6\u65c5\u884c\u6740\u4eba\u706d\u53e3\u7684\u5fc5\u5907\u6280\u80fd\uff0c\u743c\u6069\u5bf9\u9884\u8a00\u672f\u6d89\u730e\u4e0d\u591a\uff0c\u4f46\u8fd9\u79cd\u7ecf\u5178\u7684\u62db\u6570\u4e5f\u662f\u4f1a\u7684\u3002\u53ef\u60dc\u4e0d\u77e5\u9053\u662f\u4ed6\u5b66\u827a\u4e0d\u7cbe\u672c\u4e8b\u4e0d\u591f\uff0c\u8fd8\u662f\u8fd9\u5e7b\u672f\u592a\u8fc7\u9ad8\u660e\uff0c\u771f\u77e5\u672f\u660e\u660e\u6210\u529f\u91ca\u653e\u51fa\u6765\uff0c\u8ff7\u96fe\u5374\u5168\u65e0\u534a\u70b9\u6d88\u6563\u75d5\u8ff9\uff0c\u53cd\u800c\u66f4\u52a0\u6d53\u5bc6\u4e86\u51e0\u5206\u3002</p><p>\u201c\u6211\u4eec\u8d70\u8fdb\u53bb\u770b\u770b\uff1f\u201d\u51db\u63d0\u8bae\u3002</p><p>\u5176\u4ed6\u4eba\u4e5f\u90fd\u8d5e\u6210\uff0c\u4e3a\u4e86\u9632\u6b62\u5931\u6563\uff0c\u8fd9\u6b21\u6ca1\u6709\u5206\u6279\uff0c\u800c\u662f\u624b\u62c9\u7740\u624b\u9c7c\u8d2f\u800c\u5165\u3002\u8fd9\u7247\u7a81\u5982\u5176\u6765\u7684\u8ff7\u96fe\u6d53\u539a\u65e0\u6bd4\uff0c\u4e00\u65e6\u8d70\u8fdb\u53bb\u4fbf\u8fde\u8fd1\u5728\u54ab\u5c3a\u7684\u4eba\u90fd\u770b\u4e0d\u5230\uff0c\u4e0a\u4e0b\u524d\u540e\u5de6\u53f3\u5168\u90fd\u662f\u7070\u8499\u8499\u4e00\u7247\uff0c\u811a\u4e0b\u90fd\u662f\u8f7b\u98d8\u98d8\u7684\uff0c\u6574\u4e2a\u4eba\u4eff\u4f5b\u8d70\u5728\u865a\u7a7a\u4e2d\u4e00\u822c\u3002\u597d\u5728\u5b9e\u9645\u7684\u7684\u7b3c\u7f69\u8303\u56f4\u5f88\u5c0f\uff0c\u624d\u8d70\u4e86\u5341\u51e0\u6b65\uff0c\u773c\u524d\u4fbf\u8c41\u7136\u5f00\u6717\uff0c\u53c8\u91cd\u65b0\u56de\u5230\u4e86\u719f\u6089\u7684\u5730\u65b9\u2014\u2014\u786e\u5b9e\u5f88\u719f\u6089\uff0c\u56e0\u4e3a\u5c31\u662f\u4ed6\u4eec\u8d70\u8fdb\u8ff7\u96fe\u524d\u6240\u7ad9\u4e4b\u5904\u3002</p><p>\u201c\u5947\u602a\uff0c\u600e\u4e48\u53c8\u8f6c\u56de\u6765\u4e86\uff1f\u201d\u51db\u4e0d\u89e3\uff0c\u201c\u6211\u4eec\u5e94\u8be5\u662f\u7b14\u76f4\u5f80\u524d\u8d70\u7684\u554a\u3002\u201d</p><p>\u8fd9\u786e\u5b9e\u5f88\u53e4\u602a\uff0c\u5c31\u50cf\u662f\u7a7a\u95f4\u88ab\u5bf9\u6298\u4e86\u4e00\u822c\uff0c\u660e\u660e\u662f\u5f80\u524d\u8d70\uff0c\u8d70\u7740\u8d70\u7740\u5374\u53d1\u73b0\u7ed3\u679c\u662f\u5f80\u540e\u9000\u3002\u53ea\u8981\u8d8a\u8fc7\u90a3\u6761\u754c\u7ebf\uff0c\u540e\u8def\u4fbf\u76f4\u63a5\u88ab\u8ff7\u96fe\u5207\u65ad\uff0c\u5c31\u7b97\u7acb\u523b\u8f6c\u8eab\u8fd4\u56de\u4e5f\u662f\u6ca1\u7528\u4e86\u3002\u8981\u60f3\u56de\u53bb\uff0c\u6050\u6015\u5f97\u628a\u8fd9\u8ff7\u96fe\u6d88\u9664\u6389\u624d\u884c\uff0c\u4f46\u521a\u624d\u8fde\u771f\u77e5\u672f\u90fd\u8bd5\u8fc7\u4e86\uff0c\u5168\u7136\u65e0\u7528\u3002</p><p>\u4e8b\u5230\u5982\u4eca\uff0c\u4e5f\u53ea\u80fd\u7ee7\u7eed\u5f80\u91cc\u8d70\u4e86\u3002</p><p>\u9053\u8def\u5e76\u4e0d\u590d\u6742\uff0c\u4e5f\u6ca1\u9047\u4e0a\u4ec0\u4e48\u5371\u9669\uff0c\u5e73\u9759\u5230\u4e86\u53cd\u5e38\u7684\u7a0b\u5ea6\uff0c\u8fde\u5e7d\u6697\u5730\u57df\u91cc\u6700\u5e38\u89c1\u7684\u5ca9\u9f20\u90fd\u6ca1\u770b\u89c1\u534a\u53ea\uff0c\u6b7b\u5bc2\u5f97\u5413\u4eba\u3002\u8fd9\u56db\u4e2a\u4eba\u4e0d\u662f\u5deb\u5e08\u5c31\u662f\u7267\u5e08\uff0c\u8fd8\u6709\u4e00\u4e2a\u6076\u9b54\uff0c\u603b\u4e4b\u90fd\u4e0d\u662f\u666e\u901a\u4eba\uff0c\u5404\u79cd\u8d85\u51fa\u5e38\u6001\u7684\u201c\u795e\u79d8\u201d\u89c1\u5f97\u591a\u4e86\uff0c\u5c31\u7b97\u73b0\u5728\u773c\u524d\u7a81\u7136\u8df3\u51fa\u4e00\u53ea\u9f99\u6765\u4e5f\u672a\u5fc5\u4f1a\u60ca\u8bb6\uff0c\u4f46\u8d8a\u662f\u8fd9\u6837\u5e73\u9759\uff0c\u53cd\u800c\u8d8a\u8ba9\u4eba\u5fc3\u91cc\u6ca1\u5e95\u3002</p><p>\u81ea\u4ece\u8fdb\u5165\u8fd9\u707e\u5384\u4e4b\u5730\uff0c\u8299\u8389\u5a05\u4fbf\u518d\u6ca1\u8bf4\u8bdd\uff0c\u53ea\u662f\u51dd\u805a\u5fc3\u795e\uff0c\u8bd5\u56fe\u548c\u59b9\u59b9\u53d6\u5f97\u8054\u7cfb\u3002\u5979\u4eec\u59d0\u59b9\u95f4\u7279\u6b8a\u7684\u5fc3\u7075\u611f\u5e94\uff0c\u5728\u8ddd\u79bb\u4e0d\u8fdc\u65f6\u80fd\u76f8\u4e92\u4f20\u9012\u7b80\u5355\u7684\u8baf\u606f\uff0c\u7b80\u76f4\u53ef\u4ee5\u5f53\u4f5c\u6b65\u8bdd\u673a\u4f7f\u7528\uff0c\u800c\u4e14\u56e0\u4e3a\u4e0d\u662f\u9b54\u6cd5\uff0c\u4e0d\u9700\u8981\u501f\u52a9\u9b54\u7f51\uff0c\u6240\u4ee5\u4e5f\u4e0d\u4f1a\u88ab\u5730\u8109\u8f90\u5c04\u5f71\u54cd\u3002\u7136\u800c\u8fc7\u4e86\u534a\u5929\uff0c\u7ed3\u679c\u4e5f\u662f\u4e00\u65e0\u6240\u83b7\uff0c\u6ca1\u6709\u534a\u70b9\u53cd\u9988\u4fe1\u606f\u3002</p><p>\u9664\u6b64\u4e4b\u5916\uff0c\u66f4\u52a0\u8be1\u5f02\u7684\u662f\uff0c\u743c\u6069\u5c45\u7136\u6709\u4e00\u79cd\u83ab\u540d\u5176\u5999\u7684\u719f\u6089\u611f\u2026\u2026</p><p>\u201c\u6211\u600e\u4e48\u603b\u89c9\u5f97\u8fd9\u5730\u65b9\u6211\u6765\u8fc7\u4f3c\u7684\uff1f\u201d</p><p>\u4ed6\u4f4e\u58f0\u5600\u5495\u7740\uff0c\u838e\u73de\u514b\u8fdc\u8fdc\u8d70\u5728\u524d\u9762\u63a2\u8def\uff0c\u8299\u8389\u5a05\u6b63\u4e13\u5fc3\u81f4\u5fd7\u5730\u7ed9\u59b9\u59b9\u53d1\u77ed\u4fe1\uff0c\u53ea\u6709\u51db\u5728\u65c1\u8fb9\u542c\u89c1\u4e86\uff0c\u201c\u4f60\u4ee5\u524d\u6765\u8fc7\uff1f\u201d</p><p>\u201c\u5f53\u7136\u4e0d\u53ef\u80fd\uff0c\u6211\u6628\u5929\u624d\u77e5\u9053\u6709\u8fd9\u4e48\u4e2a\u5730\u65b9\u2026\u2026\u53ef\u662f\u771f\u7684\u6709\u79cd\u5f88\u773c\u719f\u7684\u611f\u89c9\uff0c\u96be\u4e0d\u6210\u6211\u68a6\u91cc\u6765\u8fc7\uff1f\u201d</p><p>\u201c\u90a3\u4e5f\u6ca1\u4ec0\u4e48\u5947\u602a\u7684\uff0c\u8fd9\u5730\u5e95\u4e0b\u90fd\u5dee\u4e0d\u591a\uff0c\u65e0\u975e\u662f\u77f3\u5934\u6ce5\u571f\uff0c\u8d70\u5230\u54ea\u91cc\u90fd\u4e00\u6837\uff0c\u81ea\u7136\u773c\u719f\u3002\u201d</p><p>\u201c\u4e0d\uff0c\u201d\u743c\u6069\u5426\u5b9a\uff0c\u201c\u4e0d\u662f\u8fd9\u56de\u4e8b\uff0c\u786e\u5b9e\u662f\u6709\u4ec0\u4e48\u5730\u65b9\u4e0d\u540c\uff0c\u4f46\u6211\u4e00\u65f6\u53c8\u8bf4\u4e0d\u4e0a\u6765\u3002\u201d</p><p>\u6b63\u81ea\u6c89\u541f\uff0c\u8d70\u5728\u524d\u9762\u7684\u9b45\u9b54\u7a81\u7136\u505c\u4e0b\u811a\u6b65\uff0c\u62ac\u5934\u5f80\u4e0a\u770b\uff0c\u743c\u6069\u4e0d\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\uff0c\u987a\u7740\u65b9\u5411\u671b\u53bb\uff0c\u53ea\u89c1\u77f3\u58c1\u5939\u7f1d\u91cc\u751f\u957f\u7740\u4e00\u682a\u690d\u7269\uff0c\u6b63\u8fce\u98ce\u6447\u66f3\u3002\u838e\u73de\u514b\u8f7b\u76c8\u8dc3\u8d77\uff0c\u5728\u7a7a\u4e2d\u6298\u8eab\u505c\u6ede\u534a\u79d2\uff0c\u5c06\u690d\u7269\u6458\u5728\u624b\u4e2d\u8d70\u56de\u6765\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u201c\u8fd9\u4e1c\u897f\u6837\u5b50\u6709\u70b9\u5947\u602a\uff0c\u201d\u9b45\u9b54\u8bf4\uff0c\u201c\u4ece\u6ca1\u89c1\u8fc7\u3002\u201d</p><p>\u6837\u5b50\u5947\u602a\uff1f</p><p>\u743c\u6069\u63a5\u8fc7\u6765\u770b\uff0c\u7ec6\u957f\u7684\u53f6\u5b50\uff0c\u6de1\u767d\u8272\u82b1\u74e3\uff0c\u4e00\u80a1\u6de1\u96c5\u7684\u6e05\u9999\u6c81\u4eba\u5fc3\u813e\uff0c\u5f88\u50cf\u5170\u82b1\uff0c\u53ea\u662f\u989c\u8272\u4f1a\u6709\u4e9b\u7070\u8499\u8499\u7684\uff0c\u50cf\u662f\u67d3\u4e0a\u4e86\u4e00\u5c42\u9634\u5f71\uff0c\u53f6\u9762\u4e0a\u8fd8\u6709\u4e9b\u7a00\u7a00\u758f\u758f\u7684\u692d\u5706\u5f62\u6591\u70b9\u3002\u786e\u5b9e\u662f\u79cd\u7f55\u89c1\u7684\u690d\u7269\uff0c\u4e0d\u8fc7\u743c\u6069\u6070\u597d\u8ba4\u8bc6\u3002</p><p>\u201c\u5514\uff0c\u9634\u5f71\u5170\uff0c\u201d\u4ed6\u4e0d\u4ee5\u4e3a\u610f\uff0c\u201c\u6211\u5c0f\u65f6\u5019\u548c\u59d0\u59d0\u7ecf\u5e38\u2026\u2026\u201d</p><p>\u8bdd\u8bf4\u5230\u4e00\u534a\u505c\u4f4f\u4e86\uff0c\u56e0\u4e3a\u4ed6\u7a81\u7136\u60f3\u8d77\u4e00\u4ef6\u4e8b\uff1a\u9634\u5f71\u5170\u2026\u2026\u662f\u53ea\u80fd\u751f\u957f\u5728\u5e7d\u5f71\u754c\u7684\u3002</p></article>"}}}]);
