(window.webpackJsonp=window.webpackJsonp||[]).push([[379],{402:function(p){p.exports={chapterTitle:null,title:"\u7b2c379\u7ae0 \u501f\u52bf\u7acb\u5a01",content:"<article><h2>\u7b2c379\u7ae0 \u501f\u52bf\u7acb\u5a01</h2><p>\u3000\u3000\u8fd9\u680b\u697c\u7684\u8fc7\u9053\u6b63\u5bf9\u7740\u5915\u9633\u5782\u843d\u7684\u65b9\u5411\uff0c\u536b\u5170\u5750\u5728\u670d\u52a1\u53f0\u524d\uff0c\u51dd\u89c6\u7740\u73bb\u7483\u95e8\u5916\u3001\u6797\u68a2\u4e4b\u4e0a\u91d1\u7ea2\u8272\u7684\u5915\u9633\u3002</p><p>\u3000\u3000\u201c\u4ed6\u8fd8\u6ca1\u6709\u4e0b\u697c\uff1f\u201d\u7fdf\u4e39\u9752\u63a8\u95e8\u8d70\u8fdb\u6765\uff0c\u8eab\u4e0a\u62ab\u7740\u91d1\u7ea2\u8272\u7684\u5915\u9633\u5149\u8292\uff0c\u7709\u5b87\u95f4\u538b\u7740\u4e9b\u5fe7\u8651\uff0c\u4e30\u8174\u800c\u5996\u5a9a\u7684\u8138\u5e9e\u591a\u5c11\u6709\u4e9b\u51dd\u91cd\u3002</p><p>\u3000\u3000\u201c\u55ef\uff0c\u4ed6\u4e0e\u5085\u4fca\u4e00\u76f4\u6ca1\u6709\u4e0b\u8fc7\u697c\uff0c\u5176\u4ed6\u4e09\u4e2a\u4eba\u51fa\u53bb\u5374\u4e00\u76f4\u6ca1\u6709\u56de\u6765\u8fc7\u3002\u201d\u536b\u5170\u867d\u7136\u4e0d\u662f\u7a7a\u767d\u7eb8\u4e00\u822c\u7684\u65e0\u77e5\u5c11\u5973\uff0c\u4f46\u4e5f\u662f\u4e00\u4e2a\u5f88\u503e\u5411\u4e8e\u76f8\u4fe1\u81ea\u5df1\u76f4\u89c9\u7684\u5973\u5b69\u5b50\uff0c\u6240\u4ee5\u4e00\u76f4\u6ca1\u6709\u4e8b\u60c5\u770b\u5f97\u591a\u4e25\u91cd\uff0c\u201c\u5916\u9762\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\u4e86\uff1f\u201d</p><p>\u3000\u3000\u536b\u5170\u4e0b\u5348\u7559\u5728\u5bbe\u9986\u91cc\u503c\u73ed\uff0c\u5f20\u606a\u4e00\u76f4\u6ca1\u6709\u4e0b\u8fc7\u697c\uff0c\u5012\u662f\u4ed6\u8eab\u8fb9\u7684\u90a3\u51e0\u4e2a\u4eba\u51fa\u5165\u9891\u7e41\uff0c\u597d\u50cf\u5728\u8c0b\u5212\u4ec0\u4e48\u4e8b\u60c5\u3002\u7fdf\u4e39\u9752\u6709\u4e9b\u5fe7\u8651\uff0c\u544a\u8bc9\u5979\u4e1c\u533a\u7684\u658c\u8001\u5927\u60f3\u8ddf\u5f20\u606a\u4f4e\u5934\uff0c\u5948\u4f55\u5f20\u606a\u6839\u672c\u6ca1\u6709\u8981\u642d\u7406\u7684\u610f\u601d\u3002</p><p>\u3000\u3000\u201c\u7701\u516c\u5b89\u5385\u7ed9\u5e02\u5c40\u6253\u7535\u8bdd\u8be2\u95ee\u6628\u5929\u53d1\u751f\u7684\u4e8b\u4e86\uff0c\u504f\u504f\u65b0\u6765\u7684\u5f20\u526f\u5e02\u957f\u597d\u50cf\u5f53\u4ec0\u4e48\u4e8b\u60c5\u90fd\u6ca1\u53d1\u751f\u8fc7\uff0c\u4f60\u8bf4\u5916\u9762\u53d1\u751f\u4ec0\u4e48\u4e8b\u4e86\uff1f\u201d\u7fdf\u4e39\u9752\u8d70\u5230\u670d\u52a1\u53f0\u540e\uff0c\u62c9\u4e86\u5f20\u6905\u5b50\u5750\u4e0b\u6765\uff0c\u201c\u8fd8\u6709\u4eba\u8bf4\u770b\u89c1\u4e24\u90e8\u6302\u2018\u6d77A\u2019\u8f66\u724c\u7684\u6851\u5854\u7eb3\u91cc\u5750\u7740\u7a7f\u8b66\u670d\u7684\u4eba\u8fdb\u5e02\u91cc\uff0c\u770b\u6765\u4ed6\u6839\u672c\u5c31\u4e0d\u60f3\u901a\u8fc7\u5e02\u91cc\u89e3\u51b3\u8fd9\u4e8b\uff0c\u8fde\u627e\u4eba\u8bf4\u8bdd\u7684\u673a\u4f1a\u90fd\u6ca1\u6709\uff0c\u5916\u9762\u5c71\u96e8\u6b32\u6765\uff0c\u8fd9\u91cc\u5012\u65e0\u58f0\u65e0\u606f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u4e0d\u80fd\u8bf4\u660e\u8ddf\u4ed6\u6709\u5173\u554a\u2026\u2026\u201d\u536b\u5170\u5012\u89c9\u5f97\u5916\u9762\u662f\u4e0d\u662f\u8fc7\u4e8e\u7d27\u5f20\u4e86\u3002</p><p>\u3000\u3000\u201c\u5f97\uff0c\u4f60\u4e2a\u6b7b\u59ae\u5b50\u2026\u2026\u201d\u7fdf\u9752\u4e39\u6ca1\u597d\u6c14\u7684\u8bf4\uff0c\u201c\u80cc\u5730\u91cc\u7684\u4e00\u4e9b\u4e8b\u60c5\u90fd\u4e0d\u6653\u5f97\u600e\u4e48\u8ddf\u4f60\u8bb2\uff0c\u658c\u8001\u5927\u867d\u7136\u4e5f\u7b97\u4e0d\u4e0a\u4ec0\u4e48\u597d\u4eba\uff0c\u603b\u6bd4\u9f99\u534e\u7684\u90a3\u7fa4\u6df7\u86cb\u597d\u4e00\u4e9b\uff0c\u5f20\u606a\u8fd9\u6b21\u8981\u6574\u658c\u8001\u5927\uff0c\u5c82\u4e0d\u662f\u8ba9\u9f99\u534e\u7684\u90a3\u7fa4\u6df7\u86cb\u9ad8\u5174\uff1f\u4f60\u8fd8\u60f3\u57ce\u9632\u6c5f\u5824\u8fd9\u6837\u7684\u8c46\u8150\u6e23\u5de5\u7a0b\u4fee\u7684\u6ee1\u57ce\u90fd\u662f\uff0c\u4f60\u8fd8\u5acc\u7f57\u5f52\u6e90\u8170\u5305\u4e0d\u591f\u9f13\uff1f\u201d</p><p>\u3000\u3000\u79e6\u88d5\u7434\u4e0e\u53e6\u4e00\u4e2a\u5973\u5b69\u5b50\u624b\u633d\u7740\u624b\u8bf4\u8bf4\u7b11\u7b11\u7684\u8fdb\u6765\uff0c\u5979\u4eec\u662f\u6765\u63a5\u73ed\u7684\uff0c\u770b\u89c1\u7fdf\u4e39\u9752\u4fcf\u8138\u542b\u5a01\u7684\u5750\u5728\u670d\u52a1\u53f0\u540e\uff0c\u5fc3\u91cc\u6709\u4e9b\u53d1\u5fe4\uff0c\u7d27\u5f20\u7684\u770b\u4e86\u4e00\u773c\u5899\u58c1\u4e0a\u7684\u6302\u949f\uff0c\u8fd8\u597d\u6ca1\u6709\u8fdf\u5230\uff0c\u5fc3\u602f\u7684\u5524\u4e86\u4e00\u58f0\uff1a\u201c\u7fdf\u603b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5bb6\u91cc\u6b7b\u4eba\u4e86\uff1f\u770b\u5230\u6211\u90fd\u6ca1\u4e00\u4e2a\u7b11\u8138\uff0c\u770b\u5230\u5ba2\u4eba\uff0c\u8138\u8fd8\u4e0d\u662f\u62c9\u5f97\u66f4\u957f\u3002\u201d\u7fdf\u4e39\u9752\u53ea\u5f53\u79e6\u88d5\u7434\u5356\u9a9a\u8ddf\u5f20\u606a\u63d0\u8d77\u536b\u5170\u5bb6\u7684\u4f4f\u5740\uff0c\u624b\u4e0b\u4eba\u4e0d\u53d7\u63a7\u5236\uff0c\u662f\u5979\u6700\u5fcc\u8bb3\u7684\u4e8b\u60c5\uff0c\u7ba1\u5979\u8868\u73b0\u662f\u4e0d\u662f\u606d\u987a\uff0c\u902e\u5230\u673a\u4f1a\u5c31\u9a82\uff0c\u4e0d\u7136\u65e0\u6cd5\u89e3\u6c14\u3002</p><p>\u3000\u3000\u79e6\u88d5\u7434\u4e0d\u77e5\u9053\u54ea\u91cc\u5f97\u7f6a\u7fdf\u4e39\u9752\u4e86\uff0c\u7ad9\u5728\u90a3\u91cc\u4e0d\u6562\u56de\u5634\uff0c\u60f3\u6324\u51fa\u7b11\u8138\uff0c\u5374\u53c8\u60f3\u54ed\u2026\u2026</p><p>\u3000\u3000\u201c\u7fdf\u59d0\u8bad\u4eba\u771f\u662f\u597d\u5a01\u98ce\u2026\u2026\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u8fd8\u60f3\u518d\u9a82\u4e24\u58f0\u89e3\u89e3\u6c14\uff0c\u9aa4\u7136\u770b\u89c1\u5f20\u606a\u4e0e\u5085\u4fca\u4ece\u697c\u68af\u62d0\u89d2\u5904\u8d70\u51fa\u6765\uff0c\u90fd\u6ca1\u6709\u542c\u89c1\u4ed6\u4eec\u4e0b\u697c\u7684\u58f0\u97f3\uff0c\u8fd8\u662f\u8bf4\u4ed6\u4eec\u65e9\u5c31\u7ad9\u5728\u697c\u68af\u62d0\u89d2\u90a3\uff1f\u7fdf\u4e39\u9752\u5fc3\u91cc\u4e00\u60ca\uff0c\u5979\u4e2d\u5348\u4e0e\u5f20\u606a\u8bf4\u8bdd\u65f6\u5c31\u6709\u628a\u658c\u8001\u5b50\u8bf4\u60c5\u7684\u610f\u601d\uff0c\u521a\u624d\u8ddf\u536b\u5170\u8bf4\u8bdd\u65f6\uff0c\u81ea\u7136\u4e5f\u662f\u504f\u8892\u658c\u8001\u5927\uff0c\u7279\u522b\u4e00\u5f00\u59cb\u8be2\u95ee\u5f20\u606a\u4ed6\u4eec\u884c\u8e2a\u7684\u95ee\u8bdd\uff0c\u8981\u662f\u5f20\u606a\u7406\u4f1a\u9519\u4e86\uff0c\u8fd8\u4ee5\u4e3a\u81ea\u5df1\u8ba9\u536b\u5170\u5728\u8fd9\u91cc\u76d1\u89c6\u4ed6\u4eec\uff0c\u771f\u4e0d\u6653\u5f97\u4ed6\u4f1a\u600e\u4e48\u60f3\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u8fdb\u697c\u65f6\uff0c\u5f20\u606a\u4e0e\u5085\u4fca\u521a\u5de7\u4e0b\u697c\uff0c\u542c\u89c1\u7fdf\u4e39\u9752\u95ee\u536b\u5170\u4ed6\u4eec\u7684\u884c\u8e2a\uff0c\u5c31\u653e\u8f7b\u811a\u6b65\u7ad9\u5728\u697c\u68af\u62d0\u89d2\u5904\u5077\u542c\uff0c\u6ca1\u60f3\u5230\u7fdf\u4e39\u9752\u4e0e\u5218\u658c\u8fd9\u4e9b\u4eba\u5173\u7cfb\u8fd8\u771f\u4e0d\u4e00\u822c\uff0c\u4e00\u4e9b\u654f\u611f\u7684\u6d88\u606f\u90fd\u80fd\u53ca\u65f6\u77e5\u9053\uff0c\u5f85\u79e6\u88d5\u7434\u4e0e\u53e6\u5916\u4e00\u4e2a\u5973\u5b69\u5b50\u51fa\u73b0\uff0c\u4e5f\u5c31\u5077\u542c\u4e0d\u5230\u4ec0\u4e48\uff0c\u5f20\u606a\u4e0d\u63a9\u9970\u7684\u76f4\u63a5\u7ad9\u51fa\u6765\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u4e5f\u7b97\u9605\u4eba\u65e0\u6570\uff0c\u5904\u4e16\u5706\u6ed1\uff0c\u504f\u504f\u9762\u5bf9\u5f20\u606a\u5904\u5904\u6709\u7f29\u624b\u7f29\u811a\u4f38\u5c55\u4e0d\u5f00\u7684\u675f\u7f1a\u611f\uff0c\u5927\u6982\u4e5f\u662f\u5bf9\u4ed6\u8086\u65e0\u5fcc\u60ee\u7684\u7ea8\u7ed4\u6027\u5b50\u6ca1\u6709\u7279\u522b\u6709\u6548\u7684\u529e\u6cd5\uff0c\u89c1\u5f20\u606a\u5c31\u8fd9\u4e48\u7a81\u5140\u7684\u7ad9\u51fa\u6765\u5e2e\u79e6\u88d5\u7434\u8bf4\u8bdd\uff0c\u5a07\u7b11\u4e86\u4e00\u58f0\uff0c\u8bf4\u9053\uff1a\u201c\u8ba9\u606a\u5c11\u770b\u7b11\u8bdd\u4e86\uff0c\u5bf9\u5979\u4eec\u4e25\u683c\u4e9b\uff0c\u8fd8\u4e0d\u662f\u4e3a\u4e86\u8ba9\u606a\u5c11\u4f4f\u5728\u8fd9\u91cc\u66f4\u8212\u5fc3\u4e00\u4e9b\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u6577\u884d\u5979\u7684\u8bdd\uff0c\u8d70\u5230\u697c\u68af\u624d\u8bf4\u9053\uff1a\u201c\u6211\u665a\u4e0a\u51c6\u5907\u51fa\u53bb\u73a9\uff0c\u7ea6\u597d\u5c0f\u79e6\u5f53\u5411\u5bfc\uff0c\u6b63\u597d\u7fdf\u59d0\u4eba\u5728\u8fd9\u91cc\uff0c\u90a3\u6211\u5c31\u5e2e\u5979\u8bf7\u4e00\u4e0b\u5047\u2026\u2026\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u6b63\u5947\u602a\u5f20\u606a\u6ca1\u6709\u7ea0\u7f20\u536b\u5170\u3002</p><p>\u3000\u3000\u201c\u4e5d\u70b9\u949f\u6211\u8ba9\u8f66\u6765\u63a5\u4f60\u53bb\u7687\u540eKTV\u2026\u2026\u201d\u5f20\u606a\u5bf9\u79e6\u88d5\u7434\u4e22\u4e0b\u8fd9\u53e5\u8bdd\uff0c\u5c31\u4e0e\u5085\u4fca\u8d70\u51fa\u697c\u53bb\uff0c\u5012\u4e5f\u6ca1\u7ba1\u7fdf\u4e39\u9752\u7b54\u4e0d\u7b54\u5e94\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4eba\u771f\u662f\u7684\uff0c\u6628\u5929\u8bf4\u7684\u597d\u597d\u7684\u4e5f\u4e0d\u51fa\u73b0\uff0c\u4eca\u5929\u8fd8\u4e0d\u77e5\u9053\u4f1a\u4e0d\u4f1a\u7ee7\u7eed\u653e\u4eba\u5bb6\u7684\u9e3d\u5b50\u2026\u2026\u201d\u79e6\u88d5\u7434\u53c8\u559c\u53c8\u6028\uff0c\u4fcf\u8138\u6d6e\u8d77\u7b11\u610f\uff0c\u53ea\u662f\u4f4e\u7740\u5934\u4e0d\u6562\u770b\u7fdf\u4e39\u9752\u7684\u8138\u8272\uff0c\u5374\u53c8\u5fcd\u4e0d\u4f4f\u5f97\u610f\u7684\u62ff\u4f59\u5149\u53bb\u779f\u7fdf\u4e39\u9752\u4e0e\u536b\u5170\uff0c\u5f20\u606a\u8fd9\u6b21\u8fde\u536b\u5170\u63d0\u90fd\u6ca1\u63d0\uff0c\u53ea\u662f\u62b1\u6028\u5f20\u606a\u73b0\u5728\u4e0d\u5e26\u5979\u51fa\u53bb\uff0c\u8fd8\u8981\u7b49\u5230\u4e5d\u70b9\u949f\u624d\u6d3e\u8f66\u6765\u63a5\u5979\u3002</p><p>\u3000\u3000\u7687\u540eKTV\u662f\u5218\u658c\u5728\u4e1c\u7247\u6700\u5927\u6700\u8c6a\u534e\u7684\u573a\u5b50\uff0c\u7fdf\u4e39\u9752\u542c\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u5c31\u77e5\u9053\u5f20\u606a\u867d\u7136\u8db3\u4e0d\u51fa\u6237\uff0c\u5374\u5df2\u7ecf\u5c06\u5218\u658c\u7684\u5e95\u7ec6\u6478\u5f97\u5dee\u4e0d\u591a\u4e86\uff0c\u4e0d\u7ea0\u7f20\u536b\u5170\u4e00\u8d77\u8fc7\u53bb\uff0c\u5927\u6982\u662f\u6000\u7591\u5979\u4eec\u4e0e\u658c\u8001\u5927\u6709\u5173\u7cfb\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u61d2\u5f97\u518d\u8bad\u79e6\u88d5\u7434\uff0c\u514d\u5f97\u8fd9\u5c0f\u9a9a\u8e44\u5b50\u5728\u5f20\u606a\u9762\u524d\u5f04\u820c\u628a\u5979\u8fd9\u8fb9\u4e0e\u5f20\u606a\u7684\u5173\u7cfb\u5f7b\u5e95\u641e\u50f5\uff0c\u6325\u6325\u624b\u8ba9\u79e6\u88d5\u7434\u4e0e\u53e6\u5916\u4e00\u4e2a\u5973\u5b69\u5b50\u53bb\u623f\u95f4\u6362\u5236\u670d\uff1a\u201c\u4e5d\u70b9\u949f\u4e4b\u524d\uff0c\u4f60\u8fd8\u8981\u503c\u73ed\u7684\u3002\u201d\u62ff\u8d77\u670d\u52a1\u53f0\u4e0a\u7684\u7535\u8bdd\uff0c\u62e8\u4e86\u4e00\u4e2a\u53f7\u7801\uff0c\u8bf4\u9053\uff0c\u201c\u658c\u8001\u5927\uff0c\u4e0d\u662f\u8001\u5a18\u4e0d\u5e2e\u4f60\u8bf4\u8bdd\uff0c\u8fd9\u592a\u5b50\u7237\u7684\u813e\u6c14\u53ef\u4e0d\u662f\u6211\u4eec\u8fd9\u4e9b\u5c0f\u5973\u4eba\u80fd\u7422\u78e8\u7684\uff0c\u5f20\u606a\u4e5d\u70b9\u591a\u4f1a\u53bb\u7687\u540e\uff0c\u4f60\u597d\u81ea\u4e3a\u4e4b\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5f20\u606a\u771f\u8981\u53bb\u658c\u8001\u5927\u7684\u5730\u76d8\u95f9\u4e8b\uff1f\u201d\u536b\u5170\u5173\u5fc3\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u8c01\u77e5\u9053\uff1f\u201d\u7fdf\u4e39\u9752\u6302\u4e0b\u7535\u8bdd\uff0c\u201c\u4e0d\u80af\u63a5\u53d7\u658c\u8001\u5927\u7684\u9080\u8bf7\uff0c\u5374\u53c8\u504f\u504f\u53bb\u4eba\u5bb6\u7684\u5730\u76d8\uff0c\u6211\u7fdf\u4e39\u9752\u731c\u7537\u4eba\u7684\u5fc3\u601d\u4e00\u5411\u5f88\u5dee\u7684\u3002\u8fd9\u4e8b\u8ddf\u6211\u4eec\u53c8\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u7b97\u4e86\uff0c\u4e0d\u8981\u7ba1\u4e86\uff0c\u4f60\u4e0b\u73ed\u56de\u53bb\u5427\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u65b0\u829c\u5e02\u533a\u8857\u9053\u591a\u4e3a\u56db\u4e94\u5c42\u9ad8\u7684\u697c\u623f\uff0c\u6781\u5c11\u6709\u73b0\u4ee3\u5316\u8272\u5f69\u660e\u663e\u7684\u9ad8\u697c\u5927\u53a6\uff0c\u4e0d\u8fc7\u5728\u5e02\u533a\u4e1c\u7247\u6709\u4e00\u5ea7\u4e0d\u591a\u89c1\u7684\u5341\u4e8c\u5c42\u9ad8\u697c\uff0c\u73bb\u7483\u5e55\u5899\u5728\u5915\u9633\u5149\u7684\u7167\u8000\u4e0b\u6d41\u5149\u6ea2\u5f69\uff0c\u8fd9\u5728\u65b0\u829c\u81f3\u5c11\u7b97\u5f97\u4e0a\u76f8\u5f53\u9192\u76ee\u7684\u5efa\u7b51\uff0c\u5218\u658c\u7684\u5927\u5174\u5de5\u8d38\u516c\u53f8\u4e0e\u7687\u540eKTV\u90fd\u5728\u90a3\u680b\u697c\u91cc\u3002</p><p>\u3000\u3000\u5927\u53a6\u7684\u5341\u4e8c\u697c\uff0c\u4e00\u4e2a\u4e2d\u7b49\u8eab\u6750\u7684\u4e2d\u5e74\u4eba\u7ad9\u5728\u7a97\u524d\uff0c\u770b\u7740\u8def\u4e0a\u7684\u8f66\u6765\u8f66\u5f80\uff0c\u7709\u5934\u7d27\u76b1\u7740\uff0c\u4eff\u4f5b\u5fc3\u5934\u627f\u53d7\u7740\u65e0\u5f62\u7684\u538b\u529b\u3002\u4ed6\u4fbf\u662f\u5927\u5174\u5de5\u8d38\u516c\u53f8\u603b\u7ecf\u7406\u5218\u658c\uff0c\u4e5f\u662f\u65b0\u829c\u6700\u5927\u5a31\u4e50\u573a\u7687\u540e\u7684\u5e55\u540e\u8001\u677f\u3002</p><p>\u3000\u3000\u4e00\u4e2a\u5e74\u7eaa\u7a0d\u5927\u4e00\u4e9b\u7684\u7537\u5b50\u63a8\u95e8\u8fdb\u6765\uff0c\u4ed6\u662f\u5218\u658c\u7684\u59d0\u592b\u9676\u5efa\u8f9b\uff1a\u201c\u6613\u90e8\u957f\u6253\u7535\u8bdd\u6765\uff0c\u8bf4\u65b0\u6765\u7684\u5f20\u526f\u5e02\u957f\u597d\u50cf\u5f53\u4ec0\u4e48\u4e8b\u60c5\u90fd\u6ca1\u6709\u53d1\u751f\u8fc7\uff0c\u4ed6\u4e5f\u4e0d\u597d\u8bf4\u8bdd\u3002\u6d77\u5dde\u90a3\u8fb9\u8fd8\u6ca1\u6709\u8054\u7cfb\u4e0a\u4eba\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u77e5\u9053\u4e86\uff0c\u4f60\u591a\u8054\u7cfb\u6d77\u5dde\u90a3\u8fb9\u51e0\u6b21\uff0c\u5f20\u77e5\u884c\u6bd5\u7adf\u662f\u6d77\u5dde\u571f\u751f\u571f\u957f\u7684\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u80fd\u642d\u4e0a\u5173\u7cfb\uff0c\u6709\u4e2a\u4e2d\u95f4\u4eba\u5c31\u597d\u8bf4\u8bdd\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u77e5\u9053\uff0c\u4f1a\u591a\u8054\u7cfb\u51e0\u6b21\u7684\uff0c\u4e0d\u8fc7\u770b\u4ed6\u4eec\u8fd9\u6b21\u6765\u52bf\u4e0d\u5c11\u554a\uff0c\u201d\u9676\u5efa\u8bf4\u9053\uff0c\u201c\u4f1a\u4e0d\u4f1a\u662f\u8fd9\u4e2a\u65b0\u6765\u7684\u5f20\u526f\u5e02\u957f\u60f3\u62ff\u6211\u4eec\u5f00\u5200\u7acb\u5a01\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e5f\u6709\u53ef\u80fd\uff0c\u201d\u5218\u658c\u76b1\u7740\u7709\u5934\u8bf4\uff0c\u201c\u5f20\u77e5\u884c\u521a\u6765\u65b0\u829c\uff0c\u5728\u5e02\u91cc\u6ca1\u6709\u4ec0\u4e48\u57fa\u7840\uff0c\u4ed6\u8981\u662f\u76f4\u63a5\u51fa\u9762\u65bd\u538b\uff0c\u5012\u597d\u89e3\u51b3\u4e86\uff0c\u504f\u504f\u7ed5\u8fc7\u5e02\u91cc\uff0c\u76f4\u63a5\u52a8\u7528\u7701\u516c\u5b89\u5385\u7684\u5173\u7cfb\uff0c\u8fd9\u4e9b\u5b98\u554a\uff0c\u5403\u4eba\u603b\u662f\u4e0d\u5410\u9aa8\u5934\u7684\uff0c\u6bd4\u6211\u4eec\u53ef\u9ed1\u591a\u4e86\uff0c\u635e\u94b1\u627e\u6211\u4eec\uff0c\u7acb\u5a01\u8fd8\u627e\u6211\u4eec\u3002\u201d\u5218\u658c\u90fd\u6709\u4e9b\u54ed\u7b11\u4e0d\u5f97\uff0c\u201c\u6211\u521a\u63a5\u5230\u7fdf\u4e39\u9752\u7684\u7535\u8bdd\uff0c\u4eba\u5bb6\u665a\u4e0a\u8981\u5230\u7687\u540e\u6765\uff0c\u4f60\u8ba9\u4e0b\u9762\u5c31\u5f53\u4ec0\u4e48\u4e8b\u60c5\u6ca1\u6709\u53d1\u751f\u8fc7\uff0c\u968f\u4ed6\u600e\u4e48\u95f9\uff0c\u4f46\u613f\u95f9\u4e00\u95f9\u80fd\u8fc7\u53bb\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u665a\u4e0a\u4eb2\u81ea\u5728\u4e0b\u9762\u76ef\u7740\uff0c\u4f1a\u8ba9\u5927\u5bb6\u5c06\u8840\u6027\u90fd\u6536\u8d77\u6765\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u5218\u6b23\u5230\u6fb3\u6d32\u4e86\uff1f\u201d\u5218\u658c\u53c8\u95ee\u4e86\u4e00\u53e5\uff0c\u201c\u4ed6\u4ec0\u4e48\u4eba\u4e0d\u597d\u60f9\uff0c\u504f\u504f\u53bb\u60f9\u8fd9\u4e9b\u715e\u661f\u3002\u5012\u4e0d\u662f\u6211\u8981\u62b1\u6028\u4f60\u59b9\u59b9\uff0c\u4f60\u770b\u5979\u628a\u5218\u6b23\u60ef\u6210\u4ec0\u4e48\u6837\u5b50\uff0c\u5929\u591a\u9ad8\u3001\u5730\u591a\u539a\u90fd\u4e0d\u77e5\u9053\uff0c\u4ee5\u4e3a\u65b0\u829c\u7684\u5929\u5c31\u5927\u8fc7\u6240\u6709\u7684\u5929\uff0c\u4ed6\u600e\u4e48\u4e0d\u7ed9\u6211\u53bb\u60f9\u7f57\u5f52\u6e90\u7684\u513f\u5b50\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e5f\u4e0d\u80fd\u602a\u5218\u6b23\uff0c\u662f\u90a3\u5c0f\u715e\u661f\u5148\u52a8\u624b\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u4e0d\u8981\u4ee5\u4e3a\u6211\u4ec0\u4e48\u4e8b\u60c5\u90fd\u4e0d\u77e5\u9053\uff0c\u5c31\u51b2\u4ed6\u90a3\u7834\u5fb7\u6027\uff0c\u6211\u5728\u573a\uff0c\u4e5f\u4f1a\u4e00\u811a\u8e39\u7ffb\u4ed6\u3002\u7b49\u8fd9\u4e8b\u60c5\u8fc7\u4e86\uff0c\u4f60\u8ddf\u4f60\u59b9\u59b9\u8bf4\uff0c\u8ba9\u5979\u7ed9\u6211\u56de\u6765\uff0c\u4e0d\u8981\u7559\u5728\u90a3\u91cc\u4f3a\u5019\u90a3\u4e2a\u5c0f\u7956\u5b97\uff0c\u751f\u6d3b\u8d39\u4e5f\u7ed9\u6211\u63a7\u5236\u4e25\u4e86\uff0c\u7ba1\u4ed6\u5728\u5916\u9762\u5403\u591a\u5c11\u82e6\uff0c\u5148\u628a\u813e\u6c14\u7ed9\u6211\u634b\u987a\u4e86\u3002\u5728\u5916\u9762\u7559\u5b66\uff0c\u7559\u4e2a\u5c41\u5b66\uff0c\u8fde\u53e5\u9e1f\u8bed\u90fd\u8bf4\u4e0d\u6e9c\uff01\u201d\u4e2d\u5e74\u4eba\u8f7b\u8f7b\u53f9\u4e86\u4e00\u53e3\u6c14\u3002</p><p>\u3000\u3000\u697c\u4e0b\uff0c\u94f6\u7070\u8272\u7684VOLVO\u7f13\u7f13\u7684\u9a76\u8fc7\uff0c\u5f20\u606a\u5750\u5728\u8f66\u91cc\uff0c\u770b\u7740\u8f66\u7a97\u5916\u7687\u540eKTV\u7684\u8c6a\u534e\u5916\u666f\uff0c\u4ece\u5916\u9762\u770b\u4e0d\u51fa\u4e00\u4e1d\u4e1d\u7684\u5f02\u5e38\uff0c\u5bf9\u5085\u4fca\u7b11\u7740\u8bf4\uff1a\u201c\u5218\u658c\u3001\u5f20\u800c\u7acb\uff0c\u53ef\u60dc\u5218\u658c\u4e0d\u6bd4\u9f99\u534e\u7684\u5f20\u800c\u7acb\u90a3\u4e48\u5584\u94bb\u8425\uff0c\u597d\u50cf\u5728\u65b0\u829c\u627f\u5305\u4e0d\u5230\u4ec0\u4e48\u5de5\u7a0b\u554a\u3002\u201d\u4ed6\u4e0e\u5085\u4fca\u6b63\u8d76\u56de\u65b0\u829c\u7684\u65b0\u5c45\u53bb\u5403\u665a\u996d\u3002</p></article>"}}}]);
