(window.webpackJsonp=window.webpackJsonp||[]).push([[383],{406:function(p){p.exports={chapterTitle:null,title:"\u7b2c256\u7ae0 \u4e00\u4e07\u68f5\u73ab\u7470\u82b1",content:"<article><h2>\u7b2c256\u7ae0 \u4e00\u4e07\u68f5\u73ab\u7470\u82b1</h2><p>\u3000\u3000\u4e5d\u5341\u5e74\u4ee3\u5c11\u5973\u4eec\u7684\u6027\u6559\u80b2\u554a\uff0c\u8fd8\u771f\u662f\u4e00\u4ef6\u4efb\u91cd\u800c\u9053\u8fdc\u7684\u4efb\u52a1\uff0c\u867d\u7136\u6027\u610f\u8bc6\u5f00\u59cb\u89c9\u9192\uff0c\u4f46\u662f\u5bf9\u6027\u7684\u8ba4\u77e5\u66f4\u591a\u7684\u662f\u4e0d\u786e\u5b9a\u6027\uff0c\u4e1d\u6beb\u63a5\u89e6\u4e0d\u5230\u907f\u5b55\u5e38\u8bc6\uff0c\u5076\u5c14\u4f1a\u6709\u5077\u5c1d\u9752\u6da9\u7981\u679c\u7684\u5c11\u7537\u5c11\u5973\uff0c\u4f46\u662f\u4eab\u53d7\u4e0d\u4e86\u771f\u6b63\u7684\u6b22\u6109\uff0c\u63a5\u4e0b\u6765\u5bf9\u6000\u5b55\u7684\u6050\u60e7\u6298\u78e8\u7740\u4ed6\u4eec\u3002\u4e07\u4e00\u4e0d\u5e78\u771f\u7684\u6000\u5b55\uff0c\u4e5f\u5f88\u5c11\u6709\u4eba\u80fd\u51b7\u9759\u7684\u5904\u7f6e\uff0c\u4ee5\u81f4\u8bb8\u591a\u79bb\u5f00\u5bb6\u95e8\u5230\u5f02\u5730\u8bfb\u4e66\u7684\u5973\u5b69\u5b50\u9891\u9891\u5c06\u5c0f\u5b69\u751f\u5230\u5395\u6240\u91cc\u3002</p><p>\u3000\u3000\u5510\u5a67\u7684\u8eab\u4f53\u4fee\u76f4\u633a\u62d4\uff0c\u6e10\u6e10\u957f\u5f00\uff0c\u5a07\u6ef4\u6ef4\u7684\u751a\u662f\u8bf1\u4eba\uff0c\u5373\u4f7f\u6709\u65f6\u5510\u5a67\u52a8\u4e86\u60c5\u6b32\u4e00\u4ed8\u4efb\u541b\u91c7\u9889\u7684\u6a21\u6837\uff0c\u5f20\u606a\u4e5f\u4e0d\u5f97\u4e0d\u6291\u5236\u4f4f\u5bf9\u5979\u8eab\u4f53\u7684\u6e34\u671b\uff0c\u4e0d\u6562\u5bf9\u5979\u6709\u8fdb\u4e00\u6b65\u7684\u884c\u52a8\uff1b\u5979\u8fd8\u6ca1\u6709\u505a\u597d\u51c6\u5907\u3002</p><p>\u3000\u3000\u5f20\u606a\u54ea\u60f3\u5230\u8fd9\u5c34\u5c2c\u7684\u4e00\u5e55\u8ba9\u665a\u6674\u770b\u5230\uff0c\u7a98\u8feb\u7684\u7b11\u4e86\u7b11\uff0c\u4ed6\u4e0b\u8eab\u8fd8\u4e0d\u5b89\u5206\u7684\u9686\u8d77\u6765\uff0c\u4e0d\u65b9\u4fbf\u7ad9\u8d77\u6765\uff0c\u665a\u6674\u66a7\u6627\u7684\u7b11\u4e86\u7b11\uff0c\u7275\u7740\u82b7\u5f64\u7684\u5c0f\u624b\u5148\u5f80\u996d\u5e97\u91cc\u8d70\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u4e0b\u8f66\u5427\u3002\u201d\u5f20\u606a\u7b49\u8eab\u4f53\u6062\u590d\u6b63\u5e38\uff0c\u8f7b\u8f7b\u7684\u62e8\u4e86\u62e8\u5510\u5a67\u63a9\u8138\u7684\u5c0f\u624b\uff0c\u201c\u665a\u6674\u59d0\u65e9\u8fdb\u53bb\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u4e0d\u4e0b\u8f66\uff0c\u201d\u5510\u5a67\u6b7b\u6d3b\u4e0d\u80af\u4e0b\u8f66\uff0c\u7c89\u8138\u7ea2\u6655\u65e0\u6cd5\u6d88\u9000\uff0c\u201c\u7b49\u4f1a\u513f\u4e00\u5b9a\u4f1a\u7ed9\u665a\u6674\u59d0\u53d6\u7b11\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u5c31\u8ba9\u5979\u7b11\u597d\u4e86\uff0c\u8c01\u8ba9\u4f60\u5360\u6211\u8fd9\u4e48\u5927\u4fbf\u5b9c\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u8bf4\uff01\u201d\u5510\u5a67\u7684\u7738\u5b50\u4f3c\u8499\u4e86\u4e00\u5c42\u96fe\u4f3c\u7684\u77aa\u7740\u5f20\u606a\uff0c\u5c06\u96ea\u5ae9\u5c0f\u624b\u4f38\u51fa\u5f20\u606a\u7684\u6000\u91cc\uff0c\u8f7b\u8f7b\u7684\u6390\u7740\uff0c\u4f4e\u58f0\u8bf4\uff0c\u201c\u4f60\u662f\u4e2a\u6d41\u6c13\uff01\u201d</p><p>\u3000\u3000\u201c\u6211\u662f\u6d41\u6c13\uff01\u201d\u5f20\u606a\u7ed5\u8fc7\u53bb\u5c06\u5979\u5a07\u8f6f\u7684\u8eab\u4f53\u6a2a\u62b1\u4e0b\u6765\u3002</p><p>\u3000\u3000\u201c\u4f60\u5c06\u6211\u62b1\u8fdb\u53bb\u2026\u2026\u201d\u5510\u5a67\u624b\u81c2\u7f20\u7740\u5f20\u606a\u7684\u8116\u5b50\uff0c\u4e0d\u80af\u4e0b\u6765\u3002</p><p>\u3000\u3000\u505c\u8f66\u573a\u91cc\u7684\u706f\u5149\u5f88\u660f\u6697\uff0c\u5510\u5a67\u5a07\u7f8e\u767d\u7699\u7684\u8138\u86cb\u5728\u706b\u7ea2\u8272\u7684\u6ed1\u96ea\u886b\u6620\u886c\u5a07\u7f8e\u59a9\u7709\uff0c\u5c24\u5176\u662f\u90a3\u53cc\u8499\u4e86\u4e00\u5c42\u96fe\u4f3c\u7684\u773c\u7738\u5b50\u53c8\u957f\u53c8\u5a9a\uff0c\u53ea\u8981\u5510\u5a67\u6709\u8fd9\u4e2a\u80c6\u5b50\uff0c\u5f20\u606a\u5f53\u7136\u613f\u610f\u5c06\u5979\u62b1\u8fdb\u5305\u53a2\u91cc\u53bb\u3002</p><p>\u3000\u3000\u4e00\u79bb\u5f00\u505c\u8f66\u573a\uff0c\u5230\u4e86\u4eae\u5802\u7684\u5730\u65b9\uff0c\u5510\u5a67\u5c31\u6323\u624e\u7740\u4e0b\u6765\uff0c\u4e0d\u8fc7\u624b\u8fd8\u7d27\u7d27\u633d\u7740\u5f20\u606a\u7684\u80f3\u818a\uff0c\u6e29\u99a8\u751c\u871c\u7684\u611f\u89c9\u8ba9\u5510\u5a67\u4ee5\u4e3a\u662f\u68a6\u5e7b\u3002</p><p>\u3000\u3000\u201c\u5f20\u606a\u8ddf\u5a67\u5a67\u8d70\u4e00\u8d77\u8fd8\u771f\u767b\u5bf9\uff0c\u5b9b\u82e5\u74a7\u4eba\uff0c\u5510\u5e02\u957f\u53ea\u6015\u4e0d\u820d\u5f97\u5c06\u5973\u513f\u4fbf\u5b9c\u8fd9\u5c0f\u5b50\u5427\u2026\u2026\u201d\u5b8b\u57f9\u660e\u770b\u7740\u5510\u5a67\u504e\u4f9d\u7740\u5f20\u606a\u8d70\u8fdb\u6765\uff0c\u7b11\u7740\u5bf9\u5510\u5b66\u8c26\u8bf4\u3002</p><p>\u3000\u3000\u201c\u4e0d\u60f3\u4fbf\u5b9c\u4ed6\u53c8\u6709\u4ec0\u4e48\u529e\u6cd5\uff0c\u5f20\u606a\u628a\u5510\u5a67\u90fd\u5a07\u7eb5\u574f\u4e86\uff0c\u73b0\u5728\u56de\u5bb6\u9876\u5634\uff0c\u90fd\u662f\u5f20\u606a\u8bf4\u600e\u4e48\u600e\u4e48\u7684\uff0c\u5979\u4e5f\u4e0d\u60f3\u60f3\uff0c\u597d\u6b79\u6211\u8fd9\u4e2a\u505a\u7238\u7238\u7684\u5c06\u5979\u597d\u4e0d\u5bb9\u6613\u517b\u5230\u5341\u516d\u5c81\uff0c\u8fd8\u5f97\u63a5\u7740\u517b\u5979\u5230\u5927\u5b66\u6bd5\u4e1a\u2026\u2026\u201d\u5510\u5b66\u8c26\u65e0\u5948\u7684\u82e6\u7b11\u7740\u8bf4\u8bdd\uff0c\u60f9\u5f97\u5927\u5bb6\u54c4\u5802\u800c\u7b11\uff0c\u5510\u5a67\u66f4\u662f\u5c06\u5934\u6df1\u57cb\u7740\u4e0d\u6562\u89c1\u4eba\u3002</p><p>\u3000\u3000\u82b7\u5f64\u5174\u594b\u7684\u62db\u624b\u8ba9\u5979\u5750\u8fc7\u53bb\uff0c\u5510\u5a67\u65e0\u5948\uff0c\u53ea\u80fd\u626d\u634f\u7740\u5750\u665a\u6674\u7684\u8eab\u8fb9\uff0c\u89c1\u5979\u5634\u89d2\u6709\u610f\u65e0\u610f\u7684\u6d45\u7b11\uff0c\u8138\u66f4\u70e7\u5f97\u5389\u5bb3\u3002</p><p>\u3000\u3000\u987e\u5efa\u840d\u7b11\u7740\u8bf4\uff1a\u201c\u771f\u4e8f\u5f20\u606a\u80fd\u60f3\u8d77\u6765\uff0c\u7adf\u7136\u62ff\u5510\u5a67\u7684\u751f\u65e5\u4f5c\u6807\u989d\u3002\u6211\u4eec\u90fd\u6ca1\u6709\u770b\u65b0\u95fb\uff0c\u8001\u5510\u56de\u5bb6\u8fd8\u8bf4\u90a3\u4e2a\u6570\u5b57\u600e\u4e48\u611f\u89c9\u8fd9\u4e48\u5947\u602a\u5462\uff0c\u8fd9\u75af\u4e2b\u5934\u4e00\u542c\u5230\u8fd9\u6570\u5b57\uff0c\u53c8\u54ed\u53c8\u7b11\u7684\uff0c\u7740\u4e86\u9b54\u4e00\u6837\uff0c\u6bd4\u6211\u4eec\u9001\u5979\u4e00\u4e07\u4ef6\u793c\u7269\u90fd\u9ad8\u5174\uff0c\u53ea\u662f\u6295\u6807\u989d\u6bd4\u7b2c\u4e8c\u540d\u7684\u90a3\u5bb6\u4f01\u4e1a\u9ad8\u4e86\u90a3\u4e48\u591a\uff0c\u4f1a\u4e0d\u4f1a\u592a\u5403\u4e8f\u4e86\u4e9b\uff1f\u665a\u6674\u600e\u4e48\u4e5f\u8ba9\u5f20\u606a\u80e1\u6765\uff1f\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u3001\u987e\u5efa\u840d\u4e00\u5f00\u59cb\u53ea\u6653\u5f97\u5f20\u606a\u6709\u53c2\u4e0e\u4e8e\u7231\u8fbe\u7535\u5b50\u7684\u4e8b\u60c5\uff0c\u8fd8\u662f\u7b49\u5230\u5f20\u606a\u5c06\u5510\u5a67\u7684\u751f\u65e5\u5f53\u6210\u592e\u89c6\u6807\u738b\u7684\u6295\u6807\u989d\u516c\u5e03\u4e8e\u4f17\uff0c\u624d\u77e5\u9053\u5f20\u606a\u5728\u7231\u8fbe\u7535\u5b50\u7684\u5206\u91cf\u662f\u90a3\u4e48\u7684\u91cd\u8981\u3002</p><p>\u3000\u3000\u5305\u53a2\u91cc\u5403\u996d\u7684\u90fd\u4e0d\u662f\u5916\u4eba\u4e86\uff0c\u5510\u5b66\u8c26\u4e00\u5bb6\u3001\u5f20\u77e5\u884c\u4e00\u5bb6\uff0c\u6b64\u65f6\u7684\u5f20\u77e5\u975e\u3001\u5b8b\u57f9\u660e\u90fd\u4e0d\u80fd\u7b97\u5916\u4eba\u4e86\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u7b11\u7740\u8bf4\uff1a\u201c\u8fd9\u90fd\u662f\u5f20\u606a\u80fd\u51b3\u5b9a\u7684\u4e8b\uff0c\u518d\u8bf4\u6807\u989d\u662f\u592e\u89c6\u5411\u5916\u5ba3\u4f20\u7684\u6570\u5b57\uff0c\u53e6\u5916\u4f1a\u6709\u4e00\u4e9b\u5e7f\u544a\u65f6\u6bb5\u4e0a\u7684\u8865\u507f\u2026\u2026\u201d</p><p>\u3000\u3000\u6295\u6807\u989d\u8ddf\u79e6\u6c60\u9ad8\u4e86\u6709\u4e00\u5343\u4e24\u767e\u591a\u4e07\uff0c\u60f3\u60f3\u90fd\u8ba9\u4eba\u89c9\u5f97\u60cb\u60dc\uff0c\u987e\u5efa\u840d\u542c\u665a\u6674\u8fd9\u4e00\u89e3\u91ca\uff0c\u624d\u91ca\u6000\uff1a\u201c\u54e6\uff0c\u539f\u6765\u662f\u8fd9\u6837\u5b50\uff0c\u5bb3\u6211\u767d\u62c5\u5fc3\u4e86\u4e00\u573a\uff0c\u6211\u5c31\u89c9\u5f97\u5947\u602a\u4e86\uff0c\u5f20\u606a\u600e\u4e48\u7a81\u7136\u5c31\u80fd\u6574\u51fa\u8fd9\u4e48\u5927\u52a8\u9759\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e5f\u4e0d\u7b97\u7a81\u7136\uff0c\u5f00\u53d1\u8fd9\u4e2a\u4ea7\u54c1\u524d\u540e\u5c31\u82b1\u4e86\u534a\u5e74\u7684\u65f6\u95f4\uff0c\u73b0\u5728\u5c31\u662f\u8981\u628a\u8fd9\u4e2a\u4ea7\u54c1\u4e00\u70ae\u6253\u54cd\uff0c\u524d\u540e\u6258\u5b8b\u4e66\u8bb0\u5e2e\u4e86\u5f88\u5927\u7684\u5fd9\uff0c\u5012\u662f\u6211\u6700\u540e\u624d\u8fc7\u6765\u5206\u8fd9\u6000\u7fb9\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u505a\u751f\u610f\uff0c\u6709\u4eba\u5929\u751f\u5c31\u4f1a\uff0c\u5c31\u50cf\u5f20\u606a\uff1b\u6211\u5c31\u5b8c\u5168\u7741\u773c\u778e\uff0c\u6211\u5c31\u60f3\uff0c\u80fd\u6709\u516b\u5343\u4e07\uff0c\u8fd8\u8981\u53bb\u505a\u4ec0\u4e48\u751f\u610f\uff1f\u201d\u987e\u5efa\u840d\u7b11\u7740\u8bf4\uff0c\u201c\u4e00\u5e74\u516b\u5343\u4e07\uff0c\u4e0d\u5f97\u6bcf\u5929\u5f80\u4e2d\u592e\u7535\u89c6\u53f0\u9001\u4e00\u8f86\u6851\u5854\u7eb3\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e00\u8f86\u6851\u5854\u7eb3\u4e5f\u4e0d\u7528\u4e8c\u5341\u591a\u4e07\u3002\u201d\u5b8b\u57f9\u660e\u63a5\u8bdd\u8bf4\u3002</p><p>\u3000\u3000\u201c\u8981\u6709\u516b\u5343\u4e07\u5c31\u597d\u4e86\uff0c\u201d\u5f20\u606a\u4f38\u4e86\u4f38\u61d2\u8170\uff0c\u201c\u73b0\u5728\u662f\u60f3\u6bcf\u5929\u5f80\u4e2d\u592e\u7535\u89c6\u53f0\u9001\u4e00\u8f86\u6851\u5854\u7eb3\uff0c\u5f00\u4e00\u8f86\u5965\u8fea\u51fa\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u683c\u73cd\u8fd8\u6574\u5929\u62c5\u5fc3\u5f20\u606a\u5c31\u8fd9\u5b66\u4e60\u6210\u7ee9\u8003\u4e0d\u4e0a\u5927\u5b66\u600e\u4e48\u529e\uff1f\u201d\u987e\u5efa\u840d\u4fa7\u5934\u770b\u7740\u6881\u683c\u73cd\uff0c\u201c\u4f60\u5c31\u597d\u597d\u7684\u62c5\u5fc3\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u5927\u5bb6\u7b11\u7740\uff0c\u6881\u683c\u73cd\u5012\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u5728\u5979\u7684\u773c\u91cc\uff0c\u513f\u5b50\u6c38\u8fdc\u90fd\u662f\u513f\u5b50\uff0c\u522b\u4eba\u4e0d\u65ad\u7684\u80af\u5b9a\u5f20\u606a\u7684\u80fd\u529b\uff0c\u5979\u6c38\u8fdc\u90fd\u4f1a\u62c5\u5fc3\u8fd9\u62c5\u5fc3\u90a3\u3002\u5979\u73b0\u5728\u624d\u7684\u786e\u611f\u5230\u4e0e\u987e\u5efa\u840d\u7684\u5730\u4f4d\u7b97\u662f\u5e73\u7b49\u4e86\uff0c\u5373\u4f7f\u5728\u5916\u4eba\u7684\u773c\u91cc\uff0c\u987e\u5efa\u840d\u662f\u5e02\u957f\u592b\u4eba\uff0c\u5979\u4e08\u592b\u53ea\u662f\u5e02\u653f\u5e9c\u79d8\u4e66\u957f\uff0c\u4f46\u662f\u5979\u6709\u7740\u5e74\u7eaa\u8f7b\u8f7b\u5c31\u6709\u51fa\u606f\u7684\u513f\u5b50\uff0c\u8ba9\u987e\u5efa\u840d\u4e5f\u5f02\u5e38\u7684\u7fa1\u6155\u3002</p><p>\u3000\u3000\u5403\u8fc7\u665a\u996d\uff0c\u5f20\u606a\u8981\u53bb\u8c22\u665a\u6674\u4f4f\u7684\u522b\u5885\u91cc\u8bf4\u4e8b\uff0c\u5510\u5a67\u8981\u8ddf\u7740\u53bb\u73a9\uff0c\u987e\u5efa\u840d\u4e5f\u6ca1\u6709\u8bf4\u65f6\u95f4\u665a\u4e86\u8ba9\u5979\u8ddf\u7740\u5979\u4eec\u56de\u53bb\uff0c\u53ea\u8bf4\u73a9\u5f97\u592a\u665a\uff0c\u4e0d\u4e00\u5b9a\u8981\u56de\u53bb\u3002\u5510\u5a67\u5174\u594b\u5927\u53eb\uff0c\u5979\u5e73\u65f6\u53ea\u6709\u7b2c\u4e8c\u5929\u4e0d\u4e0a\u5b66\uff0c\u624d\u6709\u673a\u4f1a\u4f4f\u665a\u6674\u90a3\u91cc\u3002</p><p>\u3000\u3000\u665a\u6674\u665a\u4e0a\u559d\u4e86\u4e9b\u7ea2\u9152\uff0c\u5f20\u606a\u4ece\u665a\u6674\u624b\u91cc\u63a5\u8fc7\u8f66\u94a5\u5319\uff0c\u82b7\u5f64\u7f20\u7740\u8981\u5750\u524d\u9762\uff0c\u8ba9\u4ed6\u62b1\u7740\u4e22\u665a\u6674\u4e0e\u5510\u5a67\u4e24\u4eba\u4e2d\u95f4\u53bb\uff0c\u5bb3\u5f97\u82b7\u5f64\u7c89\u5ae9\u7684\u62f3\u5934\u634f\u5b50\u64c2\u4e86\u4ed6\u540e\u80cc\u51e0\u4e0b\u624d\u89e3\u6068\u3002\u6881\u683c\u73cd\u4e0d\u653e\u5fc3\u5f20\u606a\u5f00\u8f66\uff0c\u8fd8\u5f97\u610f\u8dd1\u8fc7\u6765\u5429\u5490\u4e00\u58f0\uff1a\u201c\u4f60\u4f1a\u4e0d\u4f1a\u5f00\u8f66\uff0c\u8981\u4e0d\u8ba9\u996d\u5e97\u6d3e\u4e2a\u53f8\u673a\uff1f\u201d</p><p>\u3000\u3000\u201c\u7edd\u5bf9\u4e0d\u8d85\u8fc7\u4e09\u5341\u8fc8\uff01\u201d\u5f20\u606a\u4fdd\u8bc1\u9053\uff0c\u201c\u5988\uff0c\u4f60\u4ec0\u4e48\u65f6\u5019\u80fd\u5c06\u8f66\u5b66\u4f1a\u4e86\uff0c\u9001\u8f86\u8f66\u7ed9\u4f60\u5f53\u751f\u65e5\u793c\u7269\uff1f\u201d</p><p>\u3000\u3000\u201c\u7a00\u7f55\u4f60\u7684\u4e1c\u897f\uff1f\u4e70\u4e86\u8f66\uff0c\u6709\u5730\u65b9\u6446\uff1f\u201d\u6881\u683c\u73cd\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u53c8\u5429\u5490\u51e0\u53e5\u624d\u79bb\u5f00\u3002</p><p>\u3000\u3000\u8c22\u665a\u6674\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u4f60\u5bb6\u80fd\u6362\u4e2a\u5730\u65b9\uff0c\u50cf\u4eca\u5929\u7684\u996d\uff0c\u5e94\u8be5\u5728\u5bb6\u91cc\u5403\u66f4\u6709\u5473\u9053\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u56de\u5934\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u7238\u8981\u60f3\u7ee7\u7eed\u5f80\u4e0a\u8d70\uff0c\u8fd9\u4e00\u4e24\u5e74\u5c31\u8981\u8c03\u51fa\u53bb\uff0c\u6362\u4e2a\u4f4f\u7684\u5730\u65b9\uff0c\u6211\u5988\u5979\u8f9b\u8f9b\u82e6\u82e6\u7684\u6574\u7406\u597d\uff0c\u5c31\u6015\u5979\u4e0d\u820d\u5f97\u79bb\u5f00\u6d77\u5dde\u4e86\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8d76\u5230\u5c0f\u9526\u6e56\u7554\u7684\u522b\u5885\uff0c\u5927\u5bb6\u90fd\u6709\u4e9b\u5174\u594b\uff0c\u5c31\u5230\u4e34\u6e56\u7684\u540e\u5ead\u5b50\u91cc\u5750\u7740\u3002\u5510\u5a67\u3001\u8c22\u665a\u6674\u5939\u7740\u82b7\u5f64\u5750\u5728\u79cb\u5343\u6447\u6905\u4e0a\uff0c\u5f20\u606a\u82e6\u547d\u7684\u5728\u540e\u9762\u5e2e\u5979\u4eec\u5c06\u6447\u6905\u8361\u8d77\u6765\u3002</p><p>\u3000\u3000\u521d\u51ac\u7684\u5bd2\u591c\uff0c\u6e56\u9762\u8499\u7740\u4e00\u5c42\u8584\u96fe\uff0c\u9690\u9690\u7ea6\u7ea6\u7684\u900f\u7740\u5bf9\u5cb8\u7684\u706f\u5149\uff0c\u5e02\u533a\u91cc\u5927\u6982\u627e\u4e0d\u5230\u8fd9\u4e48\u4e00\u5904\u9759\u8c27\u7684\u591c\u3002</p><p>\u3000\u3000\u5510\u5a67\u56de\u5934\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u73ab\u7470\u56ed\u65c1\u8fb9\u6709\u5ea7\u5c0f\u6e56\u624d\u884c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u884c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u73ab\u7470\u56ed\uff1f\u201d\u8c22\u665a\u6674\u56de\u5934\u5947\u602a\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u5f20\u606a\u8bf4\u8981\u79cd\u4e00\u4e07\u68f5\u73ab\u7470\u9001\u7ed9\u6211\u2026\u2026\u201d\u5510\u5a67\u5fae\u6602\u7740\u5934\uff0c\u795e\u60c5\u95f4\u6709\u4e9b\u9a84\u50b2\uff0c\u53c8\u6709\u4e9b\u7f9e\u6da9\u3002</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u8c22\u665a\u6674\u8f7b\u8f7b\u7684\u611f\u6168\u4e86\u4e00\u58f0\uff0c\u795e\u60c5\u6709\u4e9b\u9eef\u7136\uff0c\u6709\u4e9b\u5fe7\u4f24\u7684\u6ce8\u89c6\u7740\u4e0d\u8fdc\u5904\u7684\u6e56\u9762\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e86\uff0c\u665a\u6674\u59d0\uff1f\u201d\u5510\u5a67\u4e0d\u6653\u5f97\u54ea\u91cc\u89e6\u52a8\u5979\u7684\u5fe7\u4f24\uff0c\u62c5\u5fc3\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u6ca1\u4ec0\u4e48\uff0c\u201d\u665a\u6674\u52c9\u5f3a\u7b11\u4e86\u7b11\uff0c\u201c\u82b7\u5f64\u5979\u7238\u7238\u66fe\u8bf4\u8fc7\u7b49\u516c\u53f8\u8d5a\u4e86\u94b1\u5efa\u4e00\u5ea7\u73ab\u7470\u56ed\u4e4b\u7c7b\u7684\u82b1\u2026\u2026\u201d</p><p>\u3000\u3000\u5f90\u5fd7\u660e\u4e27\u8eab\u8f66\u7978\u65f6\uff0c\u6d77\u88d5\u516c\u53f8\u7684\u72b6\u51b5\u5e76\u4e0d\u597d\uff0c\u60f3\u5fc5\u73ab\u7470\u56ed\u4e4b\u7c7b\u7684\u8bb8\u8bfa\u8fd8\u6ca1\u6709\u5b9e\u73b0\uff0c\u5510\u5a67\u4f38\u624b\u633d\u8fc7\u665a\u6674\u7684\u624b\u81c2\uff0c\u5c06\u82b7\u5f64\u6402\u5728\u4e24\u4eba\u7684\u4e2d\u95f4\uff0c\u5c11\u5973\u654f\u611f\u7684\u5fc3\u601d\u66ff\u665a\u6674\u611f\u5230\u5fe7\u4f24\uff0c\u7a81\u7136\u53c8\u8bf4\u9053\uff1a\u201c\u8ba9\u5f20\u606a\u79cd\u4e24\u5ea7\u73ab\u7470\u56ed\uff0c\u9001\u4e00\u5904\u7ed9\u665a\u6674\u59d0\uff1f\u201d</p><p>\u3000\u3000\u665a\u6674\u7b11\u7740\u8bf4\uff1a\u201c\u50bb\u4e2b\u5934\uff0c\u8fd9\u53ef\u662f\u4f60\u4eec\u5c0f\u60c5\u4eba\u95f4\u7684\u751c\u871c\uff0c\u6211\u600e\u4e48\u53ef\u80fd\u8981\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u53ef\u4ee5\u7684\uff0c\u53cd\u6b63\u5f20\u606a\u662f\u4e2a\u82b1\u5fc3\u5927\u841d\u535c\uff0c\u4ed6\u968f\u968f\u4fbf\u4fbf\u80fd\u9001\u7ed9\u6c5f\u9edb\u513f\u90a3\u4e48\u591a\u73ab\u7470\u82b1\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u80fd\u9001\u7ed9\u665a\u6674\u59d0\uff1f\u201d\u5510\u5a67\u54af\u54af\u7b11\u7740\u8bf4\uff0c\u56de\u5934\u5bf9\u5f20\u606a\u8bf4\uff0c\u201c\u8bb0\u5f97\u7684\uff0c\u73b0\u5728\u4f60\u8981\u79cd\u4e24\u4e07\u68f5\u73ab\u7470\u4e86\uff0c\u4e00\u4e07\u68f5\u9001\u7ed9\u665a\u6674\u59d0\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90fd\u8ddf\u4f60\u89e3\u91ca\u597d\u591a\u904d\u4e86\uff0c\u90a3\u662f\u516c\u53f8\u5ba3\u4f20\u7684\u9700\u8981\uff1f\u201d\u5f20\u606a\u82e6\u7b11\u7740\u8138\u8bf4\u3002</p><p>\u3000\u3000\u201c\u8c01\u77e5\u9053\u4f60\u6709\u6ca1\u6709\u5047\u516c\u6d4e\u79c1\uff1f\u201d\u5510\u5a67\u88c5\u51fa\u86ee\u6a2a\u7684\u6837\u5b50\uff0c\u77aa\u7740\u5f20\u606a\uff0c\u201c\u96be\u9053\u4f60\u4e0d\u60f3\u9001\u7ed9\u665a\u6674\u59d0\uff1f\u201d\u53c8\u8f6c\u8fc7\u8eab\u6765\u6402\u7740\u665a\u6674\u7684\u80f3\u818a\uff0c\u201c\u6211\u4eec\u90fd\u8981\u5b9a\u4e86\uff0c\u8c01\u8ba9\u4ed6\u4e00\u4e0b\u5b50\u8fd9\u4e48\u6709\u94b1\uff0c\u4ed6\u4e0d\u9001\u8c01\u9001\uff1f\u201d\u82b7\u5f64\u5174\u594b\u7684\u5728\u6447\u6905\u4e0a\u7ad9\u8d77\u6765\uff0c\u76f4\u547c\uff1a\u201c\u6211\u4e5f\u8981\uff0c\u6211\u4e5f\u8981\u2026\u2026\u201d</p><p>\u3000\u3000\u665a\u6674\u614c\u5fd9\u6276\u4f4f\u82b7\u5f64\u7684\u8eab\u4f53\uff0c\u6015\u5979\u4e0d\u5c0f\u5fc3\u8dcc\u4e0b\u53bb\uff0c\u5374\u653e\u4efb\u7740\u81ea\u5df1\u4e0d\u53bb\u963b\u6b62\u5510\u5a67\u7684\u4efb\u6027\uff0c\u5185\u5fc3\u6df1\u5904\u5c31\u6709\u9690\u5bc6\u7684\u559c\u60a6\uff0c\u8138\u988a\u5fae\u7ea2\uff0c\u5374\u7ed9\u8ff7\u79bb\u7684\u591c\u8272\u63a9\u4f4f\u3002</p></article>"}}}]);
