(window.webpackJsonp=window.webpackJsonp||[]).push([[386],{409:function(p){p.exports={chapterTitle:null,title:"\u7b2c386\u7ae0 \u5c0f\u4eba\u7269\u7684\u60b2\u54c0",content:"<article><h2>\u7b2c386\u7ae0 \u5c0f\u4eba\u7269\u7684\u60b2\u54c0</h2><p>\u3000\u3000\u5f20\u606a\u5728\u8f66\u4e0a\u5c06\u4e8b\u60c5\u7684\u7ecf\u8fc7\u8ddf\u4ed6\u7238\u8bf4\u4e86\u4e00\u4e0b\uff0c\u5750\u8f66\u8d76\u5230\u533b\u9662\uff0c\u624d\u77e5\u9053\u536b\u5170\u7236\u6bcd\u5df2\u7ecf\u8fc7\u6765\u4e86\u3002\u536b\u5170\u5df2\u7ecf\u5b8c\u5168\u6e05\u9192\u8fc7\u6765\uff0c\u8eab\u4f53\u68c0\u67e5\u6ca1\u4ec0\u4e48\u4e0d\u9002\u5e94\uff0c\u5df2\u7ecf\u8f6c\u79fb\u5230\u666e\u901a\u75c5\u623f\u3002</p><p>\u3000\u3000\u518d\u770b\u5230\u5de6\u817f\u77ed\u534a\u622a\u7684\u5987\u4eba\uff0c\u5f20\u606a\u624d\u604d\u7136\u89c9\u5f97\u4e16\u754c\u6709\u65f6\u5019\u771f\u7684\u5f88\u5c0f\uff0c\u539f\u6765\u536b\u5170\u5bb6\u4e5f\u4f4f\u5728\u65b0\u95f8\u5c0f\u533a\u3002\u536b\u5170\u7684\u7236\u4eb2\u662f\u4e2a\u8eab\u6750\u7626\u5c0f\u7684\u4e2d\u5e74\u4eba\uff0c\u8001\u5b9e\u5df4\u4ea4\u7684\u6837\u5b50\uff0c\u770b\u4ed6\u4eec\u592b\u5987\uff0c\u5b9e\u96be\u60f3\u50cf\u80fd\u751f\u51fa\u536b\u5170\u8fd9\u822c\u59ff\u8272\u51fa\u4f17\u7684\u5973\u513f\u6765\u3002\u592b\u5987\u4fe9\u6b63\u7ad9\u5728\u5e8a\u524d\u8ddf\u8eba\u5728\u75c5\u5e8a\u4e0a\u4e00\u52a8\u4e0d\u52a8\u7684\u536b\u5170\u8bf4\u7740\u4ec0\u4e48\uff0c\u611f\u89c9\u6709\u4eba\u8fc7\u6765\uff0c\u5c31\u4f4f\u5634\u4e0d\u8bf4\uff0c\u8f6c\u5934\u770b\u8fc7\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u8138\u4e0a\u7ed9\u7fdf\u4e39\u9752\u6293\u51fa\u51e0\u9053\u8840\u75d5\uff0c\u6709\u4e9b\u9762\u76ee\u5168\u975e\u7684\u611f\u89c9\uff0c\u536b\u5170\u6bcd\u4eb2\u6ca1\u6709\u7acb\u5373\u5c31\u8ba4\u51fa\u5f20\u606a\u6765\uff0c\u770b\u5230\u4ed6\u8138\u8fd9\u6837\u5b50\uff0c\u60f3\u5f53\u7136\u4ee5\u4e3a\u662f\u536b\u5170\u6323\u624e\u65f6\u6293\u4f4f\u7684\uff0c\u201c\u4f60\u4eec\u8fd9\u4e9b\u4eba\u6e23\uff01\u201d\u51b2\u8fc7\u6765\u4e86\u8981\u6293\u4ed6\u7684\u8138\uff0c\u54ac\u7259\u5207\u9f7f\u7684\u6837\u5b50\u60f3\u5c06\u5f20\u606a\u6574\u4e2a\u4eba\u5403\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u6b21\u624b\u91cc\u6ca1\u6709\u62b1\u4eba\uff0c\u770b\u7740\u536b\u5170\u5979\u5988\u5f20\u7259\u821e\u722a\u7684\u51b2\u6765\uff0c\u5c31\u60f3\u7740\u8981\u95ea\u5f00\uff1b\u536b\u5170\u5979\u5988\u817f\u7638\uff0c\u6025\u7740\u8df3\u8d77\u6765\u6253\u4eba\uff0c\u6574\u4e2a\u4eba\u5c31\u51b2\u8fc7\u6765\uff0c\u60f3\u7740\u81ea\u5df1\u8981\u662f\u95ea\u5f00\uff0c\u536b\u5170\u5979\u5988\u6574\u4e2a\u4eba\u975e\u76f4\u63a5\u649e\u5899\u4e0a\u53bb\u4e0d\u53ef\uff0c\u5f20\u606a\u6ca1\u6cd5\u8ba9\uff0c\u53ea\u662f\u5c06\u8138\u95ea\u5230\u4e00\u8fb9\uff0c\u80f8\u53e3\u8ba9\u536b\u5170\u5979\u5988\u7626\u5f31\u7684\u8eab\u5b50\u649e\u5f97\u751f\u75bc\uff0c\u5e78\u597d\u7fdf\u4e39\u9752\u5728\u65c1\u8fb9\u773c\u660e\u624b\u5feb\uff0c\u62b1\u4f4f\u536b\u5170\u5979\u5988\uff0c\u6293\u4f4f\u536b\u5170\u5979\u5988\u6325\u821e\u7684\u624b\u81c2\uff0c\u5374\u662f\u536b\u5170\u8eba\u5728\u5e8a\u4e0a\u62a2\u7740\u5e2e\u5f20\u606a\u89e3\u91ca\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u662f\u4ed6\u2026\u2026\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u90fd\u6ca1\u6709\u8138\u8bf4\u5f20\u606a\u8138\u4e0a\u7684\u8840\u75d5\u662f\u81ea\u5df1\u6293\u7684\uff0c\u536b\u5170\u5979\u5988\u76ef\u7740\u5f20\u606a\u7684\u8138\u770b\u4e86\u4e00\u4f1a\u513f\uff0c\u8fd9\u624d\u8ba4\u51fa\u6765\uff1a\u201c\u4f60\u662f\u524d\u4e9b\u5929\u5c06\u8f66\u505c\u5728\u65b0\u95f8\u8def\u8fb9\u7684\u90a3\u4e2a\u5e74\u8f7b\u4eba\uff1f\u201d\u8fd9\u624d\u786e\u5b9a\u5f20\u606a\u4e0d\u662f\u90a3\u4e2a\u60f3\u4fae\u8fb1\u5979\u5973\u513f\u7684\u4eba\u3002</p><p>\u3000\u3000\u536b\u5170\u7684\u773c\u775b\u5931\u53bb\u5f80\u65e5\u7684\u795e\u5f69\uff0c\u7a7a\u6d1e\u65e0\u5149\uff0c\u8138\u4e0a\u6709\u672a\u5e72\u7684\u6cea\u75d5\uff0c\u4f3c\u4e4e\u5c31\u521a\u624d\u6025\u5207\u5e2e\u5f20\u606a\u89e3\u91ca\u4e86\u4e00\u53e5\u5176\u4ed6\u65f6\u5019\u90fd\u58f0\u97f3\u90fd\u6ca1\u6709\u542d\u4e00\u58f0\u3002\u65e0\u8bba\u662f\u65e2\u9042\u8fd8\u662f\u672a\u9042\uff0c\u53d1\u751f\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u5bf9\u4e00\u4e2a\u5355\u7eaf\u7684\u5973\u5b69\u5b50\u6765\u8bf4\uff0c\u90fd\u662f\u4e00\u573a\u96be\u4ee5\u6cef\u706d\u7684\u5669\u68a6\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u7ed9\u536b\u5170\u7236\u6bcd\u4ecb\u7ecd\u5f20\u77e5\u884c\u7684\u8eab\u4efd\uff0c\u536b\u5170\u7236\u6bcd\u5e76\u6ca1\u6709\u7279\u522b\u7684\u53cd\u5e94\uff0c\u536b\u5170\u7236\u4eb2\uff0c\u90a3\u4e2a\u770b\u4e0a\u53bb\u8001\u5b9e\u5df4\u4ea4\u7684\u4e2d\u5e74\u4eba\uff0c\u751a\u81f3\u6ca1\u6709\u6562\u62ff\u773c\u775b\u770b\u5f20\u77e5\u884c\uff0c\u5634\u91cc\u53ea\u662f\u8bf4\uff1a\u201c\u6ca1\u51fa\u4e8b\u5c31\u597d\uff0c\u6ca1\u51fa\u4e8b\u5c31\u597d\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u8f7b\u8f7b\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u793a\u610f\u7fdf\u4e39\u9752\u51fa\u53bb\u8bf4\u8bdd\uff0c\u4ed6\u4eec\u5148\u8d70\u51fa\u53bb\u53bb\u770b\u675c\u98de\u3002</p><p>\u3000\u3000\u5728\u5bbe\u9986\u65f6\uff0c\u675c\u98de\u5de6\u624b\u81c2\u7ed9\u788e\u9152\u74f6\u624e\u7834\uff0c\u5230\u533b\u9662\u6253\u4e86\u9ebb\u836f\u7f1d\u4e86\u597d\u51e0\u9488\uff0c\u4e5f\u5728\u75c5\u623f\u91cc\u4f11\u606f\uff0c\u5f20\u606a\u8fd9\u65f6\u5019\u60f3\u8d77\u81ea\u5df1\u7684\u8138\u4e5f\u9700\u8981\u5904\u7406\u4e00\u4e0b\uff0c\u4e0d\u60f3\u7559\u4e0b\u4ec0\u4e48\u75a4\u75d5\uff0c\u4ed6\u4e5f\u4e0d\u60f3\u9760\u9762\u76f8\u51f6\u6076\u5413\u552c\u4eba\u3002</p><p>\u3000\u3000\u4ece\u7fdf\u4e39\u9752\u8fd9\u91cc\uff0c\u5f20\u606a\u77e5\u9053\u5728\u5979\u901a\u77e5\u4e4b\u524d\uff0c\u536b\u5170\u7236\u6bcd\u7ed9\u5e02\u516c\u5b89\u5c40\u63a5\u4e86\u8fc7\u53bb\uff0c\u540e\u6765\u8fd8\u662f\u8b66\u8f66\u76f4\u63a5\u9001\u5230\u533b\u9662\u7684\uff0c\u60f3\u5fc5\u5728\u8fd9\u6bb5\u65f6\u95f4\u91cc\u6709\u4eba\u505a\u8fc7\u536b\u5170\u7236\u6bcd\u7684\u5de5\u4f5c\uff0c\u536b\u5170\u8fd8\u6709\u4e24\u4e2a\u5728\u8bfb\u521d\u4e2d\u7684\u53cc\u80de\u80ce\u5f1f\u5f1f\u3002\u751f\u6d3b\u5728\u5e95\u5c42\u7684\u5e02\u6c11\uff0c\u6709\u4e0d\u7518\u53d7\u8fb1\u7684\uff0c\u6709\u61e6\u5f31\u4e0d\u6562\u6323\u624e\u7684\uff0c\u50cf\u536b\u5170\u7236\u6bcd\u53ea\u5e86\u5e78\u6ca1\u6709\u51fa\u4e8b\u3001\u8981\u8003\u8651\u5bb6\u4eba\u4ee5\u540e\u751f\u5b58\u7684\u4e5f\u4e0d\u51fa\u4eba\u610f\u6599\uff0c\u96be\u9053\u8981\u7238\u7238\u8d70\u5230\u4ed6\u4eec\u9762\u524d\u62cd\u7740\u80f8\u812f\u4fdd\u8bc1\u4f1a\u8fd8\u4ed6\u4eec\u7684\u516c\u9053\uff0c\u5173\u952e\u662f\u4ed6\u4eec\u81ea\u5df1\u4e0d\u4e89\u3002</p><p>\u3000\u3000\u5f20\u606a\u5927\u95f9\u5929\u4e91\u9152\u5e97\u65f6\uff0c\u770b\u7f57\u5f52\u6e90\u7684\u53cd\u5e94\uff0c\u4ed6\u5230\u90a3\u65f6\u8fd8\u4e0d\u77e5\u9053\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u770b\u6765\u80cc\u540e\u53e6\u6709\u65c1\u4eba\u5e2e\u7f57\u6587\u8fd9\u4e9b\u7ea8\u7ed4\u5b50\u5f1f\u7b79\u8c0b\u5212\u7b56\u6d88\u5f25\u7f6a\u5b7d\u3002</p><p>\u3000\u3000\u201c\u536b\u5170\u600e\u4e48\u8bf4\uff1f\u201d\u5f20\u77e5\u884c\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u5c31\u521a\u624d\u5979\u624d\u5410\u4e09\u4e2a\u5b57\uff0c\u5176\u4ed6\u65f6\u5019\u90fd\u4e0d\u80af\u5f00\u53e3\u2026\u2026\u201d\u7fdf\u4e39\u9752\u4e00\u5f00\u59cb\u5bf9\u5f20\u606a\u8fd8\u662f\u6709\u6212\u5fc3\u7684\uff0c\u521a\u624d\u63a5\u5230\u7535\u8bdd\u542c\u8bf4\u5f20\u606a\u5927\u95f9\u5929\u4e91\u9152\u5e97\u7684\u4e8b\u60c5\u4e4b\u540e\uff0c\u770b\u5230\u5f20\u606a\u8138\u4e0a\u7684\u8840\u75d5\uff0c\u5c31\u6709\u4e9b\u5185\u759a\u4e86\uff0c\u201c\u8fd9\u4e9b\u6df7\u8d26\u5bb6\u4f19\uff0c\u603b\u8981\u6709\u4eba\u7ed9\u4ed6\u4eec\u4e9b\u6559\u8bad\uff01\u201d</p><p>\u3000\u3000\u201c\u5e02\u516c\u5b89\u5c40\u4f1a\u4ecb\u5165\u8c03\u67e5\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u4ed6\u5bf9\u6b64\u6ca1\u6709\u62b1\u592a\u5927\u7684\u6307\u671b\uff0c\u6c5f\u4e0a\u5143\u867d\u7136\u516c\u5f00\u8fd9\u4e48\u8868\u793a\uff0c\u8bed\u6c14\u5374\u6210\u95ee\u9898\uff0c\u770b\u60c5\u5f62\u5e02\u516c\u5b89\u5c40\u4e0d\u4f1a\u6025\u7740\u5bf9\u6d89\u6848\u7684\u4eba\u5458\u91c7\u53d6\u884c\u52a8\u3002</p><p>\u3000\u3000\u5e02\u59d4\u79d8\u4e66\u957f\u94b1\u6587\u8d35\u968f\u540e\u8d76\u8fc7\u6765\u770b\u671b\uff0c\u4ed6\u7684\u8bed\u6c14\u4e5f\u662f\u201c\u73a9\u7684\u592a\u8fc7\u4efd\uff0c\u8981\u8ba4\u771f\u5904\u7406\u201d\uff0c\u95f4\u63a5\u6d89\u6848\u7684\u4eba\u4e0d\u5c11\uff0c\u94b1\u6587\u8d35\u662f\u65b0\u829c\u4eba\uff0c\u4e0e\u65b0\u829c\u5b98\u573a\u4e0a\u5343\u7f20\u4e07\u626f\uff0c\u8bf4\u8bdd\u4e0d\u4f1a\u516c\u5141\u7684\uff0c\u4e0d\u8fc7\u4ed6\u8fd8\u662f\u8868\u8fbe\u4e86\u4ed6\u7684\u4e2d\u7acb\u6001\u5ea6\uff0c\u4ed6\u5bf9\u8eba\u5728\u75c5\u5e8a\u4e00\u58f0\u4e0d\u542d\u7684\u536b\u5170\u8bf4\uff1a\u201c\u8981\u6709\u4ec0\u4e48\u59d4\u5c48\uff0c\u4e5f\u4e0d\u8981\u4e00\u58f0\u4e0d\u542d\u3002\u201d\u4ed6\u5728\u75c5\u623f\u91cc\u7ad9\u4e86\u4e00\u4f1a\u513f\uff0c\u5c31\u51fa\u6765\u5bf9\u7fdf\u4e39\u9752\u8bf4\uff1a\u201c\u536b\u5170\u662f\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u7684\u5458\u5de5\uff0c\u5728\u5de5\u4f5c\u4e2d\u51fa\u4e86\u610f\u5916\uff0c\u5bbe\u9986\u8981\u8d1f\u8d23\u5230\u5e95\uff0c\u5979\u7236\u6bcd\u63d0\u51fa\u4ec0\u4e48\u6761\u4ef6\u6765\uff0c\u8981\u5c3d\u91cf\u6ee1\u8db3\uff0c\u4f60\u4e0d\u80fd\u505a\u51b3\u5b9a\u7684\uff0c\u76f4\u63a5\u7ed9\u6211\u6253\u7535\u8bdd\u3002\u201d</p><p>\u3000\u3000\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u5f52\u5e02\u59d4\u79d8\u4e66\u957f\u94b1\u6587\u8d35\u5206\u7ba1\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u77aa\u7740\u773c\u775b\uff0c\u5dee\u70b9\u8981\u5c06\u553e\u6cab\u5550\u4ed6\u8138\u4e0a\u3002</p><p>\u3000\u3000\u94b1\u6587\u8d35\u5c31\u8fd9\u4e48\u770b\u4e86\u4e00\u773c\uff0c\u53c8\u8ddf\u5f20\u77e5\u884c\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u5c31\u8d70\u4e86\uff0c\u60f3\u5fc5\u4e5f\u6015\u6cbe\u4e0a\u9ebb\u70e6\u3002</p><p>\u3000\u3000\u7f57\u6587\u9080\u8bf7\u53c2\u4e0e\u4ed6\u751f\u65e5\u5bb4\u8bf7\u7684\u4e5d\u4e2a\u7537\u5973\uff0c\u5176\u4e2d\u516d\u4e2a\u7537\u9752\u5e74\u5bb6\u91cc\u5728\u65b0\u829c\u90fd\u6709\u663e\u8d6b\u7684\u5e95\u5b50\uff0c\u90fd\u8bf4\u4e24\u4e2a\u964c\u751f\u4eba\u4e4b\u95f4\u53ea\u8981\u901a\u8fc7\u516d\u4e03\u4e2a\u4e2d\u95f4\u4eba\u5c31\u80fd\u642d\u4e0a\u5173\u7cfb\uff0c\u516d\u4e2a\u5728\u65b0\u829c\u6709\u52bf\u529b\u7684\u5bb6\u5ead\u8db3\u4ee5\u5728\u6784\u6210\u4e00\u4e2a\u8986\u76d6\u5168\u65b0\u829c\u5e02\u7684\u5173\u7cfb\u7f51\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u8fd9\u4e2a\u5973\u4eba\u5f88\u4e0d\u7b80\u5355\uff0c\u6709\u4e9b\u4e8b\u60c5\uff0c\u5f20\u606a\u6ca1\u6709\u6562\u8ddf\u5979\u7ec6\u8bf4\u3002\u536b\u5170\u4e0d\u80af\u5f00\u53e3\u8bf4\u8bdd\uff0c\u800c\u536b\u5170\u7236\u6bcd\u53c8\u60f3\u9000\u7f29\uff0c\u5f20\u606a\u7b49\u533b\u751f\u5c06\u4ed6\u8138\u4e0a\u7684\u4f24\u75d5\u5904\u7406\u5b8c\uff0c\u7b49\u675c\u98de\u624b\u81c2\u7684\u9ebb\u836f\u52b2\u5b8c\u5168\u8fc7\u53bb\uff0c\u5c06\u81ea\u5df1\u7684\u624b\u673a\u53f7\u7559\u7ed9\u7fdf\u4e39\u9752\uff1a\u201c\u9700\u8981\u5e2e\u52a9\u7684\u8bdd\uff0c\u4f60\u76f4\u63a5\u6253\u6211\u7684\u7535\u8bdd\u3002\u201d</p><p>\u3000\u3000\u201c\u73b0\u5728\u600e\u4e48\u529e\uff1f\u201d\u5750\u5230\u8f66\u91cc\uff0c\u675c\u98de\u6c14\u9f13\u9f13\u7684\u8bf4\uff0c\u4ed6\u8fd9\u79cd\u5e74\u7eaa\u8fd8\u6ca1\u6709\u5b66\u4f1a\u9690\u5fcd\u3002</p><p>\u3000\u3000\u201c\u867d\u8bf4\u76f4\u63a5\u6d89\u6848\u7684\u624d\u4e24\u4eba\uff0c\u4f46\u90a3\u4e9b\u5728\u5ba2\u5385\u91cc\u7684\u6df7\u8d26\u90fd\u4e0d\u662f\u4ec0\u4e48\u597d\u9e1f\uff0c\u8bf4\u4e0d\u5b9a\u8eab\u4e0a\u4e5f\u80cc\u7740\u4ec0\u4e48\u6848\u5b50\uff0c\u8fd9\u4e9b\u4eba\u4e3a\u4ec0\u4e48\u80fd\u8086\u65e0\u5fcc\u60ee\uff0c\u8fd8\u4e0d\u662f\u4ed6\u4eec\u7236\u6bcd\u5728\u80cc\u540e\u6491\u7740\uff1f\u8fd9\u6848\u5b50\uff0c\u8fd9\u4e48\u67e5\u4e0b\u53bb\uff0c\u4e0d\u6653\u5f97\u4f55\u5e74\u4f55\u6708\u624d\u67e5\u5f97\u6e05\uff0c\u5c31\u7b97\u7701\u516c\u5b89\u5385\u76f4\u63a5\u4ecb\u5165\uff0c\u5c06\u7f57\u6587\u6293\u8fdb\u53bb\uff0c\u96be\u9053\u4ed6\u4eec\u5c31\u80fd\u53d7\u5230\u771f\u6b63\u7684\u6559\u8bad\uff1f\u201d\u5f20\u606a\u7528\u4e00\u79cd\u5f88\u65e0\u5948\u7684\u8bed\u6c14\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u5e72\u4ed6\u5a18\u7684\uff0c\u5f53\u65f6\u6293\u4f4f\u4ed6\u76f4\u63a5\u5e9f\u6389\u5c31\u597d\u4e86\u3002\u201d\u675c\u98de\u80f8\u53e3\u95f7\u7740\u4e00\u80a1\u5b50\u6076\u6c14\u4e0d\u5f97\u53d1\u6cc4\u3002</p><p>\u3000\u3000\u201c\u6ca1\u5fc5\u8981\u4e3a\u4e86\u8fd9\u4e9b\u6df7\u86cb\u628a\u81ea\u5df1\u4e5f\u642d\u8fdb\u53bb\u2026\u2026\u201d\u5f20\u606a\u6447\u4e86\u6447\u5934\u3002</p><p>\u3000\u3000\u201c\u90a3\u4f60\u4e3a\u4ec0\u4e48\u8fd8\u51b2\u8fc7\u53bb\u7838\u4eba\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u7b49\u6211\uff0c\u6211\u53f3\u624b\u8fd8\u662f\u597d\u7684\uff1f\u201d\u675c\u98de\u5bf9\u81ea\u5df1\u7ed9\u6487\u4e0b\u8fd9\u4e8b\u5f88\u4e0d\u723d\u3002</p><p>\u3000\u3000\u201c\u6211\u8981\u8ba9\u5168\u65b0\u829c\u7684\u4eba\u90fd\u77e5\u9053\u6211\u5bf9\u8fd9\u5c0f\u5b50\u6068\u4e4b\u5165\u9aa8\u3002\u201d\u5f20\u606a\u8f7b\u8f7b\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u867d\u7136\u8bf4\u8fd9\u65f6\u5019\u6062\u590d\u4e86\u51b7\u9759\uff0c\u7838\u4eba\u65f6\u5374\u771f\u7684\u96be\u6291\u5fc3\u4e2d\u7684\u6012\u706b\u3002\u201c\u7f57\u6587\u80fd\u5728\u65b0\u829c\u80e1\u4f5c\u975e\u4e3a\uff0c\u65e0\u975e\u56e0\u4e3a\u7f57\u5f52\u6e90\u662f\u65b0\u829c\u5e02\u59d4\u526f\u4e66\u8bb0\u3001\u5e38\u52a1\u526f\u5e02\u957f\uff0c\u5f53\u7f57\u5f52\u6e90\u5012\u4e0b\u6765\uff0c\u7f57\u6587\u5c31\u5c41\u4e5f\u4e0d\u662f\uff0c\u5230\u65f6\u5019\u8c01\u4f1a\u5e2e\u4ed6\u63a9\u9970\uff0c\u5e2e\u4ed6\u8bf4\u8bdd\uff1f\u201d</p><p>\u3000\u3000\u675c\u98de\u6bd5\u7adf\u4e5f\u662f\u5728\u8fd9\u4e2a\u73af\u5883\u91cc\u957f\u5927\uff0c\u5f20\u606a\u7a0d\u4e00\u63d0\u9192\u4ed6\u4fbf\u80fd\u660e\u767d\u8fc7\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u770b\u7f57\u5f52\u6e90\u8eab\u4e0a\u4e0d\u6b62\u8fd9\u4e00\u4e2a\u95ee\u9898\uff0c\u524d\u4e9b\u5929\u4f60\u62c9\u6211\u53bb\u57ce\u9632\u6c5f\u5824\uff0c\u778e\u5b50\u90fd\u770b\u5f97\u51fa\u90a3\u6c5f\u5824\u91cc\u6709\u732b\u817b\uff0c\u6211\u7238\u5e38\u8bf4\u63ed\u76d6\u5b50\u4e0d\u662f\u4e00\u4ef6\u5f88\u7b80\u5355\u7684\u4e8b\u60c5\uff0c\u4f60\u8fd9\u6b21\u5f53\u7740\u65b0\u829c\u5e02\u5927\u5c0f\u5b98\u5458\u7684\u9762\u90fd\u8ddf\u7f57\u5f52\u6e90\u7236\u5b50\u6495\u7834\u8138\u4e86\uff0c\u8fd8\u4e0d\u662f\u6709\u8db3\u591f\u7684\u501f\u53e3\u53bb\u63ed\u4ed6\u4eec\u7684\u8001\u5e95\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u6487\u5634\u60f3\u7b11\uff0c\u6ca1\u6709\u7b11\u51fa\u6765\u3002\u4ed6\u539f\u5148\u5c31\u60f3\u501f\u53e3\u4e0e\u7f57\u5f52\u6e90\u7236\u5b50\u7ffb\u8138\uff0c\u597d\u6709\u501f\u53e3\u5c06\u6c5f\u5824\u7684\u4e8b\u60c5\u76f4\u63a5\u6345\u51fa\u6765\uff0c\u514d\u5f97\u62d6\u5230\u4e5d\u516b\u5e74\u6210\u5927\u60a3\uff0c\u800c\u5f90\u5b66\u5e73\u60f3\u6574\u987f\u5c0f\u6c5f\u6d41\u57df\u7684\u5824\u9632\u7cfb\u7edf\u56e0\u4e3a\u963b\u6b62\u592a\u5927\u53ea\u5f97\u91c7\u53d6\u589e\u52a0\u7ecf\u8d39\u52a0\u56fa\u5824\u575d\u7684\u6298\u4e2d\u65b9\u6848\uff0c\u4f46\u662f\u8fd9\u7b14\u94b1\u4e0b\u53bb\u4f1a\u9020\u51fa\u591a\u5c11\u8c46\u8150\u6e23\u5de5\u7a0b\u9020\u6210\u65b0\u7684\u9690\u60a3\u5c31\u4e0d\u5f97\u800c\u77e5\uff0c\u5c06\u65b0\u829c\u57ce\u9632\u6c5f\u5824\u529e\u6210\u5927\u6848\u5c31\u662f\u4e00\u4e2a\u7edd\u4f73\u7684\u9707\u6151\u6e05\u7406\u5168\u7701\u6c34\u5229\u7cfb\u7edf\u7684\u7a81\u7834\u53e3\u3002\u7701\u6797\u4e1a\u7cfb\u7edf\u7684\u4f8b\u5b50\u5c31\u6446\u5728\u773c\u524d\uff0c\u5218\u534e\u5c71\u6beb\u53d1\u65e0\u635f\u7684\u5230\u6c5f\u5357\u7701\u67d0\u5e02\u5f53\u4e00\u628a\u624b\u53bb\u4e86\uff0c\u6c34\u5229\u7cfb\u7edf\u7684\u60c5\u51b5\u66f4\u52a0\u590d\u6742\uff0c\u53c8\u6d89\u53ca\u5230\u56fd\u52a1\u9662\u5206\u7ba1\u6c34\u5229\u7684\u90e8\u95e8\uff0c\u8981\u60f3\u8c03\u67e5\u4e00\u4ef6\u6848\u5b50\u90fd\u4f1a\u963b\u529b\u91cd\u91cd\uff0c\u66f4\u4e0d\u7528\u8bf4\u8981\u5728\u4e5d\u516b\u5e74\u4e4b\u524d\u6574\u987f\u5168\u7701\u6c34\u5229\u7cfb\u7edf\uff0c\u5f90\u6c34\u5e73\u60f3\u4ee5\u4e00\u5df1\u4e4b\u529b\u800c\u529b\u633d\u72c2\u6f9c\u662f\u4e0d\u53ef\u80fd\u7684\uff0c\u60f3\u6210\u4e8b\uff0c\u5c31\u8981\u6709\u7279\u6b8a\u7684\u624b\u6bb5\u3002\u5f20\u606a\u5374\u4e5f\u4e07\u4e07\u6ca1\u6709\u60f3\u5230\u4f1a\u5728\u536b\u5170\u8eab\u4e0a\u53d1\u751f\u8fd9\u79cd\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u4ed6\u7238\u8bf4\u9053\uff1a\u201c\u7238\uff0c\u6211\u770b\u4f60\u662f\u4e0d\u662f\u6682\u65f6\u8bf7\u5047\u79bb\u5f00\u65b0\u829c\u4e24\u4e09\u5929\uff0c\u6211\u4eec\u665a\u4e0a\u5c31\u5148\u53bb\u7701\u57ce\uff1f\u201d</p><p>\u3000\u3000\u5f20\u77e5\u884c\u60f3\u4e86\u60f3\uff0c\u8fd9\u5f53\u513f\u79bb\u5f00\u65b0\u829c\uff0c\u65e0\u7591\u4f1a\u7ed9\u7f57\u5f52\u6e90\u4ed6\u4eec\u5f88\u5927\u7684\u538b\u529b\uff0c\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u597d\uff0c\u665a\u4e0a\u5c31\u8d70\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8054\u7cfb\u4e00\u76f4\u5728\u6697\u4e2d\u8c03\u67e5\u57ce\u9632\u6c5f\u5824\u7684\u8bb8\u745e\u5e73\u8d76\u8fc7\u6765\u4e0e\u4ed6\u6c47\u5408\uff0c\u8ba9\u76db\u9752\u76f4\u63a5\u8ddf\u5218\u658c\u644a\u724c\uff0c\u770b\u5218\u658c\u624b\u91cc\u6709\u6ca1\u6709\u641e\u5012\u9f99\u534e\u96c6\u56e2\u7684\u5173\u952e\u4e1c\u897f\uff0c\u7f57\u5f52\u6e90\u662f\u7701\u7ba1\u5e72\u90e8\uff0c\u5728\u7701\u91cc\u4e5f\u7b97\u4e00\u68f5\u6709\u4e9b\u6839\u57fa\u7684\u5927\u6811\uff0c\u76f4\u63a5\u67e5\u4ed6\u8981\u7701\u7eaa\u59d4\u51fa\u9762\uff0c\u8ba9\u7701\u7eaa\u59d4\u51fa\u9762\u67e5\u7f57\u5f52\u6e90\u4e0d\u662f\u5f90\u5b66\u5e73\u5728\u77ed\u65f6\u95f4\u5185\u5c31\u80fd\u63d2\u624b\u7684\u4e8b\u60c5\uff0c\u6700\u597d\u8fd8\u662f\u4ece\u57ce\u9632\u6c5f\u5824\u627f\u5efa\u5546\u9f99\u534e\u96c6\u56e2\u3001\u4ece\u7f57\u5f52\u6e90\u8eab\u8fb9\u4eba\u3001\u4ece\u57ce\u9632\u6c5f\u5824\u6316\u8d77\u3002\u5c06\u8bf8\u591a\u4e8b\u60c5\u5b89\u6392\u597d\u4e4b\u540e\uff0c\u56de\u53bb\u5c06\u4ed6\u5988\u4e5f\u63a5\u4ed6\uff0c\u4e09\u90e8\u8f66\uff0c\u5e26\u7740\u624b\u81c2\u53d7\u4f24\u7684\u675c\u98de\u76f4\u63a5\u53bb\u7701\u57ce\u627e\u5f90\u5b66\u5e73\u5408\u8ba1\u6b64\u4e8b\u3002</p></article>"}}}]);
