(window.webpackJsonp=window.webpackJsonp||[]).push([[389],{411:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u56db\u5341\u516d\u8282 \u5b9e\u7528\u4e3b\u4e49",content:"<article><h2>\u7b2c\u56db\u5341\u516d\u8282 \u5b9e\u7528\u4e3b\u4e49</h2><p>\u4f0a\u65af\u5854\u548c\u601d\u601d\u5148\u662f\u8d76\u8d70\u4e86\u6717\u8d39\u7f57\u516c\u7235\uff0c\u73b0\u5728\u53c8\u8bbe\u4e0b\u5e7b\u5883\u56f0\u4f4f\u6cd5\u8b66\uff0c\u663e\u7136\u662f\u51c6\u5907\u63d2\u624b\u5230\u5e95\u4e86\u3002\u7136\u800c\u95ee\u9898\u5728\u4e8e\uff0c\u4ed6\u4eec\u8981\u9762\u5bf9\u7684\u4e0d\u662f\u4e09\u4e24\u4e2a\u654c\u4eba\uff0c\u800c\u662f\u6574\u4e2a\u6cf0\u745f\u5c14\uff0c\u8fd9\u4e00\u6ce2\u6253\u9000\u4e86\uff0c\u4e0b\u4e00\u6ce2\u53c8\u4f1a\u524d\u6765\uff0c\u8fd9\u6b21\u662f\u6cd5\u8b66\uff0c\u4e0b\u6b21\u5c31\u4f1a\u662f\u519b\u961f\u3002\u4efb\u4f60\u5929\u5927\u7684\u672c\u4e8b\uff0c\u4e5f\u4e0d\u53ef\u80fd\u548c\u6574\u4e2a\u5e1d\u56fd\u5bf9\u6297\uff0c\u5373\u4fbf\u81ea\u5df1\u80fd\u4e00\u8d70\u4e86\u4e4b\uff0c\u9003\u8131\u8ffd\u6355\uff0c\u5854\u683c\u6751\u7684\u6751\u6c11\u4eec\u4e5f\u4f1a\u906d\u5230\u7275\u8fde\uff0c\u90a3\u5c31\u5b8c\u5168\u5931\u53bb\u4e00\u5f00\u59cb\u6551\u4eba\u7684\u672c\u610f\u4e86\u3002</p><p>\u8fd9\u4e48\u7b80\u5355\u7684\u9053\u7406\uff0c\u5bf9\u65b9\u6ca1\u53ef\u80fd\u60f3\u4e0d\u5230\uff0c\u65e2\u7136\u5982\u6b64\uff0c\u90a3\u603b\u8be5\u6709\u6240\u76d8\u7b97\u624d\u662f\u3002\u6885\u83f2\u65af\u6b63\u662f\u770b\u51fa\u4e86\u8fd9\u4e00\u70b9\uff0c\u6240\u4ee5\u624d\u76f4\u63a5\u8be2\u95ee\uff0c\u60f3\u542c\u542c\u4ed6\u4eec\u662f\u5426\u6709\u4ec0\u4e48\u597d\u529e\u6cd5\u3002</p><p>\u201c\u54e6\uff0c\u8fd9\u4e2a\u5417\uff0c\u5176\u5b9e\u4e5f\u5f88\u7b80\u5355\u5566\uff0c\u201d\u4f0a\u65af\u5854\u8f7b\u58f0\u4e00\u7b11\uff0c\u201c\u6211\u4eec\u662f\u5b89\u5206\u5b88\u5df1\u7684\u5584\u826f\u5e02\u6c11\uff0c\u81ea\u7136\u4e0d\u4f1a\u548c\u6cd5\u5f8b\u76f8\u5bf9\u6297\u3002\u201d</p><p>\u5b89\u5206\u5b88\u5df1\u7684\u5584\u826f\u5e02\u6c11\uff0c\u8fd9\u53e5\u8bdd\u81ea\u7136\u662f\u7eaf\u7cb9\u9b3c\u626f\uff0c\u4f46\u540e\u9762\u4e00\u53e5\u8bdd\uff0c\u90a3\u5c31\u9887\u4e3a\u503c\u5f97\u73a9\u5473\u4e86\u3002\u4e0d\u548c\u6cd5\u5f8b\u76f8\u5bf9\u6297\uff0c\u4e0d\u7b49\u4e8e\u5c31\u4f1a\u9075\u7eaa\u5b88\u6cd5\uff0c\u771f\u6b63\u7684\u542b\u4e49\u53ea\u6015\u662f\u91c7\u7528\u5176\u4ed6\u65b9\u5f0f\u8fc2\u56de\u7ed5\u8fc7\u3002\u8fd9\u5176\u4e2d\u7684\u5dee\u522b\uff0c\u8981\u89e3\u91ca\u8d77\u6765\u6bd4\u8f83\u9ebb\u70e6\uff0c\u4e3e\u4e2a\u4f8b\u5b50\u5c31\u80fd\u660e\u767d\uff0c\u4fbf\u5982\u5f8b\u5e08\uff0c\u8981\u8bf4\u4ed6\u4eec\u76f4\u63a5\u5bf9\u6297\u6cd5\u5f8b\uff0c\u90a3\u662f\u8c08\u4e0d\u4e0a\uff0c\u7f6a\u72af\u624d\u4f1a\u5982\u6b64\uff0c\u4f46\u8981\u8bf4\u4ed6\u4eec\u90fd\u662f\u9075\u7eaa\u5b88\u6cd5\u7684\u5584\u826f\u5e02\u6c11\uff0c\u90a3\u663e\u7136\u4e5f\u4e0d\u53ef\u80fd\uff0c\u7b97\u662f\u4ecb\u4e4e\u4e24\u8005\u4e4b\u95f4\u3002</p><p>\u4f0a\u65af\u5854\u7684\u6253\u7b97\uff0c\u4e43\u662f\u5148\u7528\u5e7b\u5883\u5c06\u6cd5\u8b66\u56f0\u4f4f\uff0c\u62d6\u5ef6\u65f6\u95f4\u3002\u8299\u857e\u72c4\u6839\u636e\u4ece\u7236\u4eb2\u90a3\u91cc\u5f97\u6765\u7684\u8d44\u6599\uff0c\u8bf4\u8fd9\u4e2a\u6cd5\u672f\u5fc5\u987b\u6709\u65bd\u6cd5\u8005\u5c45\u4e2d\u4e3b\u6301\uff0c\u5426\u5219\u5c31\u4f1a\u5d29\u6e83\u2014\u2014\u8fd9\u4e2a\u8bf4\u6cd5\u662f\u6ca1\u9519\uff0c\u4f46\u5e76\u4e0d\u786e\u5207\uff0c\u771f\u5b9e\u7684\u60c5\u51b5\u662f\u53ea\u5728\u6cd5\u672f\u542f\u52a8\u7684\u524d\u4e24\u4e2a\u5c0f\u65f6\u9700\u8981\u4e3b\u6301\uff0c\u7136\u540e\u5c31\u53ef\u4ee5\u81ea\u884c\u8fd0\u8f6c\uff0c\u65bd\u6cd5\u8005\u4fbf\u80fd\u62bd\u8eab\u800c\u51fa\uff0c\u505a\u5176\u4ed6\u4e8b\u60c5\u4e86\u3002\u5230\u65f6\u5019\uff0c\u4f0a\u65af\u5854\u548c\u601d\u601d\u4fbf\u4f1a\u79bb\u5f00\u6b64\u5730\uff0c\u524d\u5f80\u90a3\u4f4d\u6717\u8d39\u7f57\u516c\u7235\u7684\u57ce\u5821\uff0c\u201c\u529d\u8bf4\u201d\u4ed6\u64a4\u56de\u6307\u63a7\uff0c\u653e\u5f03\u8ffd\u7a76\u8fd9\u4ef6\u4e8b\u60c5\u4e2d\u6240\u6709\u76f8\u5173\u4eba\u7b49\u7684\u8d23\u4efb\uff0c\u5e76\u4e14\u5ba3\u5e03\u53d6\u6d88\u521d\u591c\u6743\u3002</p><p>\u6309\u7167\u6cf0\u745f\u5c14\u7684\u6cd5\u5f8b\uff0c\u53ea\u8981\u6717\u8d39\u7f57\u516c\u7235\u64a4\u56de\u6307\u63a7\uff0c\u4e0d\u4e88\u8ffd\u7a76\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u4e5f\u5c31\u5230\u6b64\u4e3a\u6b62\u3002\u6240\u8c13\u6c11\u4e0d\u544a\uff0c\u5b98\u4e0d\u7a76\uff0c\u6cd5\u9662\u4e5f\u4e0d\u613f\u610f\u4e3b\u52a8\u63fd\u9ebb\u70e6\u4e0a\u8eab\u3002\u5927\u5bb6\u4fbf\u80fd\u7686\u5927\u6b22\u559c\uff0c\u4ece\u6b64\u5404\u8d70\u5404\u7684\u8def\u4e86\u3002</p><p>\u8fd9\u786e\u5b9e\u662f\u4e2a\u5f88\u7406\u60f3\u7684\u89e3\u51b3\u65b9\u6848\uff0c\u81f3\u4e8e\u4f0a\u65af\u5854\u548c\u601d\u601d\u8981\u7528\u4ec0\u4e48\u65b9\u6cd5\u201c\u529d\u8bf4\u201d\u6717\u8d39\u7f57\u516c\u7235\uff0c\u5927\u5bb6\u5fc3\u7167\u4e0d\u5ba3\uff0c\u4e5f\u6ca1\u5fc5\u8981\u591a\u95ee\u3002\u867d\u7136\u5982\u6b64\uff0c\u8fd8\u6709\u4e2a\u95ee\u9898\u6ca1\u6709\u89e3\u51b3\u3002</p><p>\u201c\u8fd9\u4e9b\u6cd5\u8b66\uff0c\u4f60\u4eec\u53c8\u51c6\u5907\u600e\u4e48\u529e\u5462\u3002\u201d</p><p>\u6885\u83f2\u65af\u63d0\u51fa\u4e86\u7591\u95ee\uff0c\u56e0\u4e3a\u65e0\u8bba\u600e\u4e48\u8bf4\uff0c\u56da\u7981\u6cd5\u8b66\u90fd\u662f\u91cd\u7f6a\uff0c\u5c31\u7b97\u6717\u8d39\u7f57\u516c\u7235\u64a4\u56de\u6307\u63a7\uff0c\u90a3\u4e5f\u53ea\u610f\u5473\u7740\u524d\u65e5\u5728\u5854\u683c\u6751\u53d1\u751f\u7684\u4e8b\u60c5\u4e0d\u518d\u8ffd\u7a76\u4e86\uff0c\u5374\u4e0d\u4ee3\u8868\u73b0\u5728\u8fd9\u4ef6\u4e8b\u4e5f\u53ef\u4ee5\u5f53\u4f5c\u6ca1\u53d1\u751f\u8fc7\u3002\u8fd9\u4e2a\u95ee\u9898\u4e0d\u89e3\u51b3\u7684\u8bdd\uff0c\u4f0a\u65af\u5854\u548c\u601d\u601d\u4ee5\u540e\u53ea\u6015\u5c31\u5f97\u6210\u4e3a\u901a\u7f09\u72af\u4e86\u3002</p><p>\u4e0d\u8fc7\u5f88\u663e\u7136\uff0c\u5979\u591a\u8651\u4e86\uff0c\u5bf9\u4e8e\u8fd9\u4e00\u70b9\uff0c\u4f0a\u65af\u5854\u65e9\u6709\u51c6\u5907\u3002</p><p>\u201c\u56da\u7981\u6cd5\u8b66\uff1f\u201d\u4ed6\u53cd\u95ee\uff0c\u201c\u54ea\u6709\u8fd9\u56de\u4e8b\uff0c\u4f60\u6709\u8bc1\u636e\u5417\uff1f\u201d</p><p>\u8bc1\u636e\u2026\u2026</p><p>\u201c\u6211\u7684\u670b\u53cb\u662f\u4e00\u4f4d\u5deb\u5e08\uff0c\u5979\u5728\u8fd9\u91cc\u8bd5\u9a8c\u65b0\u5b66\u4f1a\u7684\u9b54\u6cd5\u3002\u8fd9\u91cc\u662f\u8352\u90ca\u91ce\u5916\uff0c\u65e2\u4e0d\u662f\u57ce\u5e02\u53c8\u4e0d\u662f\u6751\u5e84\uff0c\u5e73\u65f6\u6839\u672c\u65e0\u4eba\u6765\u5f80\uff0c\u663e\u7136\u4e0d\u72af\u6cd5\u5427\uff0c\u201d\u4f0a\u65af\u5854\u4e00\u672c\u6b63\u7ecf\u5730\u8bf4\uff0c\u201c\u81f3\u4e8e\u8fd9\u51e0\u4f4d\u6cd5\u8b66\uff0c\u6070\u597d\u649e\u8fdb\u4e86\u6cd5\u672f\uff0c\u800c\u8fd9\u6cd5\u672f\u53c8\u6bd4\u8f83\u9ebb\u70e6\uff0c\u4e00\u65e6\u53d1\u52a8\u5c31\u6ca1\u6cd5\u53d6\u6d88\uff0c\u53ea\u80fd\u7b49\u5b83\u81ea\u5df1\u5931\u6548\uff0c\u6240\u4ee5\u59d4\u5c48\u4ed6\u4eec\u5728\u91cc\u9762\u591a\u5446\u51e0\u5929\u3002\u5bf9\u6b64\u6211\u4eec\u6df1\u8868\u6b49\u610f\uff0c\u5e76\u4e14\u4ee5\u540e\u4f1a\u66f4\u52a0\u5c0f\u5fc3\uff0c\u907f\u514d\u6b64\u7c7b\u4e8b\u60c5\u518d\u5ea6\u53d1\u751f\u2014\u2014\u4f46\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u8fd9\u53ea\u662f\u4e2a\u610f\u5916\u800c\u5df2\uff0c\u548c\u2018\u56da\u7981\u6cd5\u8b66\u2019\u5728\u6027\u8d28\u4e0a\u662f\u6709\u5929\u58e4\u4e4b\u522b\u7684\u5427\u3002\u201d</p><p>\u8c8c\u4f3c\u5408\u60c5\u5408\u7406\u7684\u8bdd\uff0c\u628a\u8d23\u4efb\u63a8\u5f97\u4e00\u5e72\u4e8c\u51c0\uff0c\u8ba9\u5927\u5bb6\u5168\u90fd\u65e0\u8bdd\u53ef\u8bf4\uff0c\u4e8b\u60c5\u5c31\u8fd9\u6837\u5f97\u5230\u4e86\u5706\u6ee1\u89e3\u51b3\u3002\u601d\u601d\u56de\u5230\u5c71\u95e8\u4e2d\u5c06\u6cd5\u672f\u5f7b\u5e95\u5b8c\u6210\uff0c\u5927\u5bb6\u63e1\u624b\u8a00\u548c\uff0c\u8d70\u51fa\u5e7b\u5883\uff0c\u53d1\u73b0\u5929\u8272\u5df2\u665a\uff0c\u591c\u5e55\u964d\u4e34\uff0c\u4fbf\u4e00\u540c\u524d\u5f80\u5854\u683c\u6751\u3002\u743c\u6069\u7b49\u4eba\u662f\u8981\u53bb\u501f\u5bbf\uff0c\u4f0a\u65af\u5854\u548c\u601d\u601d\u5219\u662f\u8981\u53d6\u81ea\u5df1\u7684\u5750\u9a91\uff0c\u8fde\u591c\u8d76\u5f80\u8d39\u90ce\u7f57\u516c\u7235\u7684\u57ce\u5821\uff0c\u53bb\u8fdb\u884c\u201c\u529d\u8bf4\u201d\u4e8b\u5b9c\u3002</p><p>\u8d39\u90ce\u7f57\u57ce\u5821\u8ddd\u79bb\u5854\u683c\u6751\u8ddd\u79bb\u7834\u8fdc\uff0c\u5c31\u7b97\u4f0a\u65af\u5854\u4e24\u4eba\u8fde\u591c\u8d76\u8def\uff0c\u4e2d\u95f4\u4e0d\u505a\u803d\u6401\uff0c\u4e5f\u8981\u4e00\u5929\u4e00\u591c\u624d\u80fd\u8fd4\u56de\u3002\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u6240\u4ee5\u4ed6\u4eec\u624d\u8981\u5148\u628a\u6cd5\u8b66\u56f0\u5728\u5e7b\u5883\u4e2d\uff0c\u514d\u5f97\u6ecb\u6270\u6751\u6c11\u3002\u800c\u743c\u6069\u7b49\u4eba\u53ea\u662f\u5728\u5854\u683c\u6751\u501f\u5bbf\u4e00\u665a\uff0c\u660e\u5929\u4e00\u65e9\u5c31\u5e94\u8be5\u542f\u7a0b\u4e0a\u8def\u3002\u4e5f\u5c31\u662f\u8bf4\uff0c\u6309\u7167\u539f\u5b9a\u7684\u884c\u7a0b\u5b89\u6392\uff0c\u53cc\u65b9\u4ece\u6b64\u522b\u8fc7\uff0c\u6574\u4ef6\u4e8b\u60c5\u5c31\u5f53\u662f\u4e00\u4ef6\u610f\u5916\u7684\u63d2\u66f2\u3002</p><p>\u53ea\u53ef\u60dc\uff0c\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u6c38\u8fdc\u6ca1\u6709\u90a3\u4e48\u7b80\u5355\u3002</p><p>\u5bf9\u4e8e\u201c\u529d\u8bf4\u201d\u8d39\u90ce\u7f57\u516c\u7235\u4e4b\u4e3e\uff0c\u6885\u83f2\u65af\u8868\u793a\u8981\u4e00\u540c\u524d\u5f80\u3002\u5979\u7684\u76ee\u7684\u5927\u5bb6\u81ea\u7136\u4e5f\u90fd\u5f88\u6e05\u695a\uff0c\u4e00\u65b9\u9762\u662f\u5e2e\u5fd9\uff0c\u53e6\u4e00\u65b9\u9762\u4e5f\u5e26\u6709\u76d1\u89c6\u7684\u542b\u4e49\u3002\u6bd5\u7adf\u4ece\u4e00\u5f00\u59cb\u5230\u73b0\u5728\uff0c\u6574\u4e2a\u4e8b\u60c5\u7684\u771f\u76f8\u5982\u4f55\uff0c\u53ea\u662f\u4f0a\u65af\u5854\u4e00\u4e2a\u4eba\u5728\u8bf4\uff0c\u6765\u5230\u5854\u683c\u6751\u4e4b\u540e\uff0c\u4ece\u6751\u6c11\u53e3\u4e2d\u4e5f\u5f97\u5230\u4e86\u8bc1\u5b9e\uff0c\u4f46\u6bd5\u7adf\u8fd8\u662f\u4e00\u9762\u4e4b\u8bcd\u3002\u867d\u7136\u6885\u83f2\u65af\u4e5f\u57fa\u672c\u76f8\u4fe1\u8fd9\u5c31\u662f\u4e8b\u5b9e\uff0c\u4f46\u6bd5\u7adf\u4e0d\u80fd\u5b8c\u5168\u80af\u5b9a\u3002\u5c31\u7b97\u662f\u4e8b\u5b9e\uff0c\u4f0a\u65af\u5854\u201c\u529d\u8bf4\u201d\u8d39\u90ce\u7f57\u516c\u7235\u7684\u8fc7\u7a0b\u4e4b\u4e2d\uff0c\u4e5f\u96be\u4fdd\u4ed6\u4e0d\u91c7\u53d6\u4ec0\u4e48\u8fc7\u6fc0\u624b\u6bb5\uff0c\u60f9\u51fa\u65b0\u7684\u9ebb\u70e6\u51fa\u6765\u3002\u800c\u4e14\u5c31\u7b97\u201c\u529d\u8bf4\u201d\u6210\u529f\uff0c\u8d39\u90ce\u7f57\u516c\u7235\u64a4\u56de\u6307\u63a7\uff0c\u6cd5\u9662\u53ec\u56de\u6cd5\u8b66\uff0c\u4e2d\u95f4\u4e5f\u9700\u8981\u65f6\u95f4\uff0c\u6765\u56de\u5f80\u8fd4\uff0c\u672a\u5fc5\u4e0d\u4f1a\u518d\u53d1\u751f\u65b0\u7684\u6ce2\u6298\u3002\u6885\u83f2\u65af\u65e2\u7136\u649e\u4e0a\u8fd9\u4ef6\u4e8b\uff0c\u5377\u5165\u8fdb\u6765\uff0c\u603b\u5f97\u5584\u59cb\u5584\u7ec8\uff0c\u628a\u5b83\u505a\u5b8c\u3002</p><p>\u201c\u827e\u5f25\u8587\uff0c\u6211\u73b0\u5728\u7ec8\u4e8e\u7406\u89e3\u4f60\u8bf4\u7684\u90a3\u53e5\u8bdd\u4e86\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u8bdd\uff1f\u201d</p><p>\u201c\u6709\u4e00\u6b21\u6211\u95ee\u4f60\uff0c\u600e\u4e48\u6837\u624d\u80fd\u505a\u5723\u6b66\u58eb\u3002\u4f60\u544a\u8bc9\u6211\u8bf4\uff1a\u8981\u505a\u5723\u6b66\u58eb\u5f88\u7b80\u5355\uff0c\u5148\u505a\u4e2a\u597d\u4eba\u3002\u201d</p><p>\u201c\u54e6\uff0c\u6211\u662f\u8bf4\u8fc7\u8fd9\u53e5\u8bdd\u2026\u2026\u600e\u4e48\u4e86\uff0c\u6709\u4ec0\u4e48\u95ee\u9898\u5417\uff1f\u201d</p><p>\u201c\u6ca1\u95ee\u9898\uff0c\u201d\u743c\u6069\u8bf4\uff0c\u201c\u6211\u53ea\u662f\u60f3\u8bf4\uff1a\u505a\u4e2a\u597d\u4eba\u4e5f\u771f\u4e0d\u5bb9\u6613\u3002\u201d</p><p>\u203b\u203b\u203b</p><p>\u505a\u597d\u4eba\u786e\u5b9e\u4e0d\u5bb9\u6613\uff0c\u575a\u6301\u505a\u597d\u4eba\u66f4\u96be\u3002\u743c\u6069\u81ea\u5ea6\u4e0d\u7b97\u574f\u5230\u5bb6\uff0c\u5076\u5c14\u4e5f\u4f1a\u53d1\u53d1\u5584\u5fc3\uff0c\u4f46\u5982\u679c\u8981\u4ed6\u4e00\u76f4\u5982\u6b64\uff0c\u90a3\u4e0d\u5982\u6740\u4e86\u4ed6\u7b97\u4e86\u3002</p><p>\u201c\u6ca1\u529e\u6cd5\uff0c\u559c\u6b22\u4e00\u4e2a\u4eba\uff0c\u603b\u5f97\u8fc1\u5c31\u8fc1\u5c31\uff0c\u53cd\u6b63\u4e5f\u4e0d\u662f\u4ec0\u4e48\u5927\u4e8b\u3002\u201d</p><p>\u743c\u6069\u539f\u672c\u662f\u60f3\u548c\u6885\u83f2\u65af\u540c\u53bb\uff0c\u4f46\u5374\u53d1\u73b0\u6ca1\u6709\u5750\u9a91\u3002\u4f0a\u65af\u5854\u548c\u601d\u601d\u4f3c\u4e4e\u662f\u60c5\u4fa3\uff0c\u4e24\u4eba\u9a91\u7740\u4e00\u5339\u4fca\u9a6c\uff0c\u81ea\u5df1\u8fd9\u8fb9\u5012\u662f\u4e5f\u6709\u4e00\u5339\u72ec\u89d2\u517d\uff0c\u7136\u800c\u5979\u6839\u672c\u5c31\u4e0d\u8ba9\u743c\u6069\u9760\u8fd1\uff0c\u53ea\u5f97\u7f62\u4e86\u3002</p><p>\u7b2c\u4e8c\u5929\u508d\u665a\u65f6\u5206\uff0c\u4f0a\u65af\u5854\u3001\u601d\u601d\u548c\u6885\u83f2\u65af\u8fd4\u56de\u5854\u683c\u6751\uff0c\u770b\u795e\u60c5\u5c31\u77e5\u9053\u529d\u8bf4\u5de5\u4f5c\u8fdb\u884c\u5f97\u5f88\u987a\u5229\uff0c\u8d39\u90ce\u7f57\u516c\u7235\u5df2\u7ecf\u6d3e\u4eba\u53bb\u8fbe\u52b3\u739b\u57ce\u6cd5\u9662\u9500\u6848\uff0c\u8ba1\u7b97\u6765\u56de\u65f6\u95f4\uff0c\u5927\u6982\u9700\u8981\u4e09\u5929\u624d\u4f1a\u6709\u6700\u7ec8\u7ed3\u679c\u2014\u2014\u8fd9\u4e5f\u5c31\u662f\u8bf4\uff0c\u743c\u6069\u7b49\u4eba\u8fd8\u5f97\u5728\u8fd9\u4e2a\u5c0f\u6751\u5e84\u91cc\u591a\u7559\u51e0\u65e5\u3002</p><p>\u6682\u4f4f\u5012\u65e0\u59a8\uff0c\u6751\u6c11\u4eec\u6df3\u6734\u70ed\u60c5\uff0c\u5468\u56f4\u7684\u98ce\u666f\u4e5f\u9887\u79c0\u4e3d\uff0c\u521a\u4ece\u5e7d\u6697\u5730\u57df\u90a3\u79cd\u9b3c\u5730\u65b9\u51fa\u6765\uff0c\u80fd\u770b\u5230\u8fd9\u6837\u5b89\u5b81\u7965\u548c\u7684\u4e61\u6751\u7530\u56ed\u98ce\u5149\uff0c\u7b80\u76f4\u53ef\u4ee5\u7b97\u662f\u5929\u5802\u4e86\u3002\u53cd\u6b63\u9634\u9b42\u57ce\u53c8\u6ca1\u7ed9\u4ed6\u9650\u5b9a\u671f\u9650\uff0c\u7740\u4ec0\u4e48\u6025\uff0c\u5c31\u5f53\u662f\u65c5\u6e38\u5ea6\u5047\u597d\u4e86\u2026\u2026\u8bdd\u8bf4\u56de\u6765\uff0c\u8eab\u8fb9\u5e26\u7740\u8fd9\u4e48\u4e00\u5927\u7fa4\u6f02\u4eae\u5973\u5b69\u5b50\u51fa\u6765\u65c5\u6e38\uff0c\u4f3c\u4e4e\u592a\u62db\u6447\u4e86\u70b9\uff0c\u4e0d\u7b26\u5408\u81ea\u5df1\u4e00\u8d2f\u7684\u4f4e\u8c03\u4f5c\u98ce\u3002</p><p>\u201c\u73b0\u5728\u89c9\u5f97\u62db\u6447\u4e86\uff1f\u201d\u6885\u83f2\u65af\u8ba5\u8bbd\uff0c\u201c\u90a3\u4f60\u52fe\u642d\u5973\u5b69\u5b50\u7684\u65f6\u5019\uff0c\u600e\u4e48\u5c31\u6ca1\u60f3\u8fc7\u8981\u6536\u655b\u70b9\u5462\uff1f\u201d</p><p>\u8fd9\u771f\u662f\u4e2a\u597d\u95ee\u9898\uff0c\u743c\u6069\u65e0\u8a00\u4ee5\u5bf9\uff0c\u65e2\u7136\u81ea\u5df1\u514b\u5236\u4e0d\u4f4f\u6b32\u671b\uff0c\u90a3\u4e5f\u5c31\u53ea\u80fd\u5fcd\u53d7\u595a\u843d\u5632\u8bbd\uff0c\u8fd9\u662f\u7406\u6240\u5f53\u7136\u7684\u4e8b\u60c5\u3002\u6216\u8005\u8bf4\uff0c\u8fd9\u5df2\u7ecf\u8db3\u591f\u5e78\u8fd0\uff0c\u6bd5\u7adf\u6885\u83f2\u65af\u6ca1\u6709\u6325\u5251\u780d\u4eba\u3002</p><p>\u201c\u5443\uff0c\u8fd9\u4e2a\u4e48\uff0c\u201d\u743c\u6069\u542b\u7cca\u7740\uff0c\u51c6\u5907\u8f6c\u79fb\u8bdd\u9898\uff0c\u201c\u5bf9\u4e86\uff0c\u827e\u5f25\u8587\uff0c\u5173\u4e8e\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u4f60\u96be\u9053\u6ca1\u6709\u4ec0\u4e48\u611f\u60f3\u5417\uff1f\u201d</p><p>\u201c\u4f1a\u6709\u4ec0\u4e48\u611f\u60f3\uff1f\u201d</p><p>\u201c\u4e0d\u4f1a\u5417\uff1f\u4f60\u770b\uff0c\u6574\u4e2a\u4e8b\u60c5\u91cc\uff0c\u5176\u5b9e\u5f88\u96be\u8bf4\u6709\u54ea\u4e00\u65b9\u505a\u9519\u4e86\u4ec0\u4e48\u3002\u5973\u7687\u5609\u5956\u529f\u81e3\uff0c\u7ed9\u4e88\u7279\u6743\uff0c\u8fd9\u662f\u653f\u6cbb\u4e0a\u7684\u59a5\u534f\uff0c\u8c08\u4e0d\u4e0a\u4ec0\u4e48\u5bf9\u9519\uff1b\u90a3\u4e2a\u516c\u7235\u5728\u884c\u4f7f\u4ed6\u7684\u6743\u5229\uff0c\u8fd9\u4e0d\u80fd\u7b97\u9519\u5427\uff1b\u906d\u5230\u963b\u62e6\uff0c\u6cd5\u9662\u6d3e\u9063\u6cd5\u8b66\u6765\u6293\u4eba\uff0c\u8fd9\u4e5f\u6ca1\u95ee\u9898\u5427\uff1b\u7136\u800c\u6751\u6c11\u53cd\u6297\uff0c\u8fd9\u4e5f\u5f88\u6b63\u5f53\u5427\uff1b\u6211\u4eec\u65b0\u8ba4\u8bc6\u7684\u90a3\u4e24\u4f4d\u670b\u53cb\u8def\u89c1\u4e0d\u5e73\uff0c\u62d4\u5200\u76f8\u52a9\uff0c\u8fd9\u603b\u4e5f\u4e0d\u80fd\u8bf4\u6709\u9519\u5427\u2014\u2014\u5927\u5bb6\u90fd\u6ca1\u505a\u9519\uff0c\u7ed3\u679c\u5374\u8fd8\u662f\u51b2\u7a81\u8d77\u6765\uff0c\u4f60\u4e0d\u89c9\u5f97\u8fd9\u5f88\u5947\u602a\u5417\uff1f\u201d</p><p>\u201c\u8fd9\u6709\u4ec0\u4e48\u5947\u602a\u7684\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\u672c\u6765\u5c31\u8fd9\u6837\uff0c\u5e76\u4e0d\u662f\u8bf4\u4f60\u662f\u5bf9\u7684\uff0c\u6211\u5c31\u662f\u9519\u7684\uff0c\u5f80\u5f80\u662f\u5927\u5bb6\u90fd\u6ca1\u9519\uff0c\u4f46\u5c31\u662f\u4f1a\u6253\u8d77\u6765\uff0c\u5f88\u6b63\u5e38\u554a\u3002\u201d</p><p>\u201c\u2026\u2026\u597d\u5427\uff0c\u5c31\u7b97\u8fd9\u662f\u5f88\u6b63\u5e38\uff0c\u53ef\u662f\u5bf9\u4e8e\u4f60\u800c\u8a00\uff0c\u6216\u8005\u8bf4\u5bf9\u4e8e\u4f60\u4eec\u5723\u6b66\u58eb\u800c\u8a00\uff0c\u9047\u5230\u8fd9\u79cd\u60c5\u5f62\uff0c\u90a3\u53c8\u7ad9\u5728\u54ea\u4e00\u8fb9\u5462\uff1f\u53c8\u5982\u4f55\u5224\u65ad\u662f\u975e\u5bf9\u9519\u5462\uff1f\u201d\u743c\u6069\u8bf4\uff0c\u201c\u4f60\u7684\u795e\u7947\u662f\u6b63\u4e49\u4e4b\u795e\uff0c\u4f46\u6b63\u4e49\u8fd9\u4e1c\u897f\u5230\u5e95\u600e\u4e48\u5224\u65ad\u5462\uff1f\u5728\u54ea\u91cc\u5462\uff1f\u5c31\u50cf\u8fd9\u4ef6\u4e8b\uff0c\u73b0\u5728\u662f\u5de7\u5999\u5730\u89e3\u51b3\u4e86\uff0c\u4f46\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u603b\u4e0d\u4f1a\u6b21\u6b21\u90fd\u8fd9\u4e48\u5e78\u8fd0\uff0c\u6b21\u6b21\u90fd\u6709\u529e\u6cd5\u80fd\u5468\u65cb\u8fc7\u53bb\u3002\u6bd4\u5982\u8bf4\u90a3\u4f4d\u6717\u8d39\u7f57\u516c\u7235\uff0c\u4ed6\u575a\u51b3\u5c31\u4e0d\u80af\u64a4\u8bc9\u5462\uff1f\u96be\u9053\u4f60\u8fd8\u80fd\u6740\u4e86\u4ed6\u4e0d\u6210\uff1f\u5982\u679c\u4e00\u8fb9\u662f\u4e0d\u8fd1\u60c5\u7406\u7684\u5f8b\u6cd5\uff0c\u4e00\u8fb9\u662f\u4efb\u4f55\u6b63\u5e38\u4eba\u90fd\u53ef\u4ee5\u6e05\u695a\u5224\u65ad\u7684\u5584\u826f\uff0c\u5b83\u4eec\u51b2\u7a81\u8d77\u6765\uff0c\u65e0\u53ef\u5316\u89e3\uff0c\u90a3\u4e48\u4f60\u5230\u5e95\u7ad9\u5728\u54ea\u4e00\u8fb9\u5462\u3002\u201d</p><p>\u201c\u8fd9\u91cd\u8981\u5417\uff1f\u201d</p><p>\u201c\u554a\uff1f\u201d</p><p>\u201c\u4e16\u4e0a\u4ece\u6765\u5c31\u6ca1\u6709\u5b8c\u7f8e\u7684\u5f8b\u6cd5\uff0c\u8fd9\u79cd\u5e38\u8bc6\u8fd8\u9700\u8981\u4f60\u6765\u544a\u8bc9\u6211\u5417\uff1f\u201d\u6885\u83f2\u65af\u5251\u7709\u4e00\u6311\uff0c\u201c\u5373\u4fbf\u662f\u795e\u8c15\uff0c\u4e5f\u4e0d\u80fd\u8bf4\u7edd\u5bf9\u6b63\u786e\uff0c\u5373\u4fbf\u662f\u6211\u4eec\u6559\u4f1a\u7f16\u64b0\u7684\u300a\u65e0\u5c3d\u6cd5\u5178\u300b\uff0c\u4e5f\u5fc5\u987b\u6bcf\u5e74\u589e\u8865\u4fee\u8ba2\uff0c\u66f4\u4f55\u51b5\u8fd9\u4e2a\u4e16\u754c\u8fd8\u8fdc\u8fdc\u6ca1\u6709\u7edf\u4e00\uff0c\u5404\u4e2a\u5730\u533a\u7684\u5f8b\u6cd5\u53c2\u5dee\u4e0d\u9f50\uff0c\u53d1\u751f\u8fd9\u79cd\u7c7b\u4f3c\u7684\u51b2\u7a81\u662f\u7406\u6240\u5f53\u7136\u7684\u4e8b\u60c5\uff0c\u6709\u4ec0\u4e48\u503c\u5f97\u5927\u60ca\u5c0f\u602a\u7684\u3002\u81f3\u4e8e\u4f60\u95ee\u6211\u5e94\u8be5\u7ad9\u5728\u54ea\u4e00\u8fb9\uff0c\u8fd9\u4e2a\u95ee\u9898\u6839\u672c\u6beb\u65e0\u610f\u4e49\uff0c\u56e0\u4e3a\u5723\u6b66\u58eb\u539f\u672c\u5c31\u4e0d\u662f\u4e3a\u4e86\u505a\u9009\u62e9\u800c\u6765\uff0c\u6211\u4eec\u7684\u804c\u8d23\uff0c\u6b63\u662f\u8981\u5728\u9047\u5230\u8fd9\u79cd\u4e8b\u60c5\u7684\u65f6\u5019\uff0c\u5c3d\u529b\u53bb\u89e3\u51b3\u5b83\uff0c\u6d88\u706d\u5b83\uff0c\u8ba9\u77db\u76fe\u4e0d\u590d\u5b58\u5728\uff0c\u5c31\u50cf\u8fd9\u6b21\u4e00\u6837\u3002\u5177\u4f53\u8981\u600e\u4e48\u505a\uff0c\u5f53\u7136\u662f\u5728\u9047\u5230\u5177\u4f53\u95ee\u9898\u7684\u65f6\u5019\u624d\u77e5\u9053\uff0c\u54ea\u6709\u63d0\u524d\u9884\u8bbe\u7acb\u573a\u7684\u9053\u7406\u3002\u4f5c\u4e3a\u5723\u6b66\u58eb\uff0c\u65e2\u4e0d\u662f\u6cd5\u5f8b\u7684\u76f2\u76ee\u634d\u536b\u8005\uff0c\u4e5f\u4e0d\u662f\u79e9\u5e8f\u7684\u5168\u76d8\u5426\u5b9a\u8005\uff0c\u65e2\u4e0d\u662f\u5b8c\u7f8e\u4e3b\u4e49\u8005\uff0c\u4e5f\u4e0d\u662f\u7406\u60f3\u4e3b\u4e49\u8005\u2014\u2014\u6211\u4eec\u662f\u5b9e\u7528\u4e3b\u4e49\u8005\u3002\u80fd\u505a\u591a\u5c11\uff0c\u5c31\u505a\u591a\u5c11\uff0c\u80fd\u8fdb\u4e00\u6b65\uff0c\u5c31\u8fdb\u4e00\u6b65\uff0c\u4e0d\u5984\u60f3\uff0c\u4e0d\u5962\u6c42\uff0c\u4e0d\u6d88\u6c89\uff0c\u4e0d\u653e\u5f03\uff0c\u4e0d\u754f\u7f29\uff0c\u4e0d\u9000\u907f\uff0c\u5c3d\u529b\u800c\u4e3a\uff0c\u5982\u6b64\u800c\u5df2\u3002\u201d</p><p>\u743c\u6069\u9ed8\u7136\u534a\u664c\uff0c\u8f7b\u8f7b\u70b9\u4e86\u70b9\u5934\uff0c\u201c\u6211\u660e\u767d\u4e86\u3002\u201d</p><p>\u8fd9\u4e2a\u8bdd\u9898\u5230\u6b64\u4e3a\u6b62\uff0c\u53c8\u968f\u53e3\u804a\u4e86\u51e0\u53e5\uff0c\u8c08\u8bdd\u5185\u5bb9\u6e10\u6e10\u8f6c\u5230\u4f0a\u65af\u5854\u548c\u601d\u601d\u8eab\u4e0a\u3002\u5bf9\u4e8e\u65b0\u7ed3\u8bc6\u7684\u8fd9\u4e24\u4e2a\u4eba\uff0c\u743c\u6069\u5012\u662f\u9887\u4e3a\u6709\u5174\u8da3\uff0c\u6216\u8005\u8bf4\u5f88\u597d\u5947\u3002\u9996\u5148\u662f\u76f8\u8c8c\uff0c\u4f0a\u65af\u5854\u662f\u4e2a\u666e\u901a\u7537\u5b50\uff0c\u5e74\u9f84\u4f30\u8ba1\u5728\u4e8c\u5341\u4e94\u5c81\u5230\u4e09\u5341\u5c81\u4e4b\u95f4\uff0c\u9762\u5bb9\u52c9\u5f3a\u80fd\u7b97\u662f\u82f1\u4fca\uff0c\u4f46\u6bd4\u8d77\u743c\u6069\u800c\u8a00\u8fd8\u662f\u5dee\u4e86\u4e00\u7b79\uff0c\u4e0d\u4f1a\u5f15\u8d77\u5ac9\u5992\uff0c\u95ee\u9898\u5728\u4e8e\u4ed6\u7684\u9762\u90e8\u4f3c\u4e4e\u6bd4\u8f83\u201c\u6241\u5e73\u201d\uff0c\u8f6e\u5ed3\u4e0d\u82e5\u5bfb\u5e38\u4eba\u90a3\u6837\u7a81\u51fa\uff0c\u66f4\u7b80\u5355\u70b9\u8bf4\uff0c\u5c31\u662f\u773c\u7736\u4e0d\u591f\u6df1\u9677\uff0c\u9f3b\u6881\u4e0d\u591f\u9ad8\u633a\u2014\u2014\u800c\u4e14\u4ed6\u7684\u80a4\u8272\u662f\u9ec4\u8272\uff0c\u5934\u53d1\u548c\u773c\u73e0\u90fd\u662f\u9ed1\u8272\u3002</p><p>\u9ed1\u53d1\u9ed1\u773c\u5f88\u6b63\u5e38\uff0c\u743c\u6069\u81ea\u5df1\u5c31\u662f\uff0c\u4e0d\u8db3\u4e3a\u5947\u3002\u9ec4\u8272\u76ae\u80a4\u4e5f\u4e0d\u7b97\u4ec0\u4e48\uff0c\u636e\u6885\u83f2\u65af\u8bf4\uff0c\u8d39\u4f26\u5927\u9646\u4e0a\u6709\u4e0d\u5c11\u5730\u533a\u7684\u4eba\u90fd\u662f\u5982\u6b64\uff0c\u7528\u4e0d\u7740\u5927\u60ca\u5c0f\u602a\u3002\u81f3\u4e8e\u9762\u90e8\u8f6e\u5ed3\u4e0d\u591f\u6df1\uff0c\u8fd9\u4e5f\u7b97\u4e0d\u5f97\u4ec0\u4e48\u660e\u663e\u7279\u5f81\u3002\u4f46\u6240\u6709\u8fd9\u4e9b\u56e0\u7d20\u5168\u90fd\u53e0\u52a0\u8d77\u6765\uff0c\u51fa\u73b0\u5728\u540c\u4e00\u4e2a\u4eba\u8eab\u4e0a\uff0c\u518d\u914d\u4e0a\u4ed6\u90a3\u7e41\u590d\u8be1\u5f02\uff0c\u548c\u5e38\u4eba\u98ce\u683c\u8fe5\u5f02\u7684\u957f\u5251\u548c\u5251\u672f\uff0c\u90a3\u95ee\u9898\u6050\u6015\u5c31\u4e0d\u8fd9\u4e48\u7b80\u5355\u4e86\u3002</p><p>\u201c\u81ea\u62a5\u5bb6\u95e8\u7684\u65f6\u5019\uff0c\u4ed6\u8bf4\u8fc7\u4ed6\u6765\u81ea\u4e1c\u65b9\u3002\u201d\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u4e1c\u65b9\u662f\u4e2a\u6a21\u7cca\u6982\u5ff5\uff0c\u743c\u6069\u7b49\u4eba\u73b0\u5728\u5728\u6cf0\u745f\u5c14\uff0c\u8fd9\u662f\u8d39\u4f26\u5927\u9646\u7684\u6700\u897f\u8fb9\uff0c\u5df2\u7ecf\u6bd7\u90bb\u5b9d\u5251\u6d77\u4e86\uff0c\u4e5f\u5c31\u662f\u8bf4\u51e0\u4e4e\u6574\u4e2a\u5927\u9646\u90fd\u53ef\u4ee5\u7b97\u662f\u201c\u4e1c\u65b9\u201d\uff0c\u6bd4\u5982\u963f\u683c\u62c9\u9686\u5c31\u53ef\u4ee5\u7b97\u4e1c\u65b9\uff0c\u585e\u5c14\u4e5f\u80fd\u7b97\u4e1c\u65b9\u3002\u867d\u7136\u5982\u6b64\uff0c\u743c\u6069\u5e76\u4e0d\u8ba4\u4e3a\u5bf9\u65b9\u6240\u6307\u7684\u662f\u8fd9\u79cd\u610f\u601d\uff0c\u4ed6\u6240\u8bf4\u7684\u4e1c\u65b9\uff0c\u6050\u6015\u66f4\u9065\u8fdc\uff0c\u8d8a\u8fc7\u7edd\u5883\u4e1c\u57df\uff0c\u8d8a\u8fc7\u6614\u65e5\u7684\u4f0a\u739b\u65af\u5361\uff0c\u5982\u4eca\u7684\u7a46\u7f55\u745e\u5fb7\u548c\u6069\u745f\u5e1d\u56fd\uff0c\u751a\u81f3\u8d8a\u8fc7\u65e0\u5c3d\u8352\u91ce\u2026\u2026\u90a3\u7247\u4f20\u8bf4\u4e2d\u7684\u4e1c\u65b9\u5927\u9646\u3002</p><p>\u201c\u7fd4\u9f99\u2026\u2026\u201d</p><p>\u4ece\u6b27\u51ef\u53e3\u4e2d\uff0c\u743c\u6069\u77e5\u9053\u4e86\u5f88\u591a\u6709\u5173\u53e4\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u7684\u5f80\u4e8b\u3002\u5982\u679c\u662f\u5355\u7eaf\u7684\u5386\u53f2\uff0c\u743c\u6069\u4f1a\u6709\u5174\u8da3\u542c\u542c\uff0c\u4f46\u4e5f\u4e0d\u4f1a\u592a\u5f53\u56de\u4e8b\uff0c\u7136\u800c\u5982\u679c\u7275\u6d89\u5230\u81ea\u5df1\u7684\u8eab\u4e16\u3001\u6765\u5386\u3001\u8840\u8109\uff0c\u751a\u81f3\u5229\u7528\u4ef7\u503c\uff0c\u90a3\u5c31\u4e0d\u5f97\u4e0d\u6253\u8d77\u5341\u4e8c\u5206\u7684\u7cbe\u795e\u6765\u5bf9\u5f85\u4e86\u3002</p><p>\u81ea\u5df1\u6709\u7fd4\u9f99\u7075\u9b42\u5370\u8bb0\uff0c\u8fd9\u4e00\u70b9\u5f88\u53ef\u80fd\u4fbf\u662f\u88ab\u9634\u9b42\u57ce\u6240\u7279\u522b\u770b\u91cd\u7684\u539f\u56e0\u6240\u5728\uff0c\u5982\u679c\u80fd\u591f\u56f4\u7ed5\u8fd9\u4e2a\u591a\u4e86\u89e3\u4e00\u4e9b\u76f8\u5173\u77e5\u8bc6\uff0c\u90a3\u4e48\u6216\u8bb8\u53ef\u4ee5\u63a8\u6d4b\u51fa\u66f4\u591a\u7684\u4fe1\u606f\u3002\u800c\u636e\u6b27\u51ef\u6240\u8a00\uff0c\u4f0a\u739b\u65af\u5361\u5e1d\u56fd\u5386\u53f2\u4e0a\u7206\u53d1\u60e8\u70c8\u5185\u6218\uff0c\u7fd4\u9f99\u5947\u68b0\u5e08\u6218\u8d25\uff0c\u643a\u5e26\u4e03\u79d8\u5668\u4e4b\u4e09\uff0c\u8fdc\u9041\u4e1c\u65b9\u5927\u9646\uff0c\u5efa\u7acb\u8d77\u4e86\u7fd4\u9f99\u5e1d\u56fd\uff0c\u5ef6\u7eed\u81f3\u4eca\u3002\u5018\u82e5\u8fd9\u4f4d\u540d\u4e3a\u4f0a\u65af\u5854\u7684\u5251\u58eb\u5f53\u771f\u6765\u81ea\u4e1c\u65b9\u5927\u9646\uff0c\u8bf4\u4e0d\u5b9a\u80fd\u4ece\u4ed6\u8eab\u4e0a\u53d1\u73b0\u4ec0\u4e48\u4e1c\u897f\u3002</p><p>\u53ef\u60dc\u5927\u5bb6\u7d20\u6627\u5e73\u751f\uff0c\u6ca1\u4ec0\u4e48\u4ea4\u60c5\uff0c\u800c\u8be2\u95ee\u8eab\u4e16\uff0c\u63a2\u542c\u6765\u5386\u8fd9\u79cd\u4e8b\u60c5\uff0c\u4ece\u6765\u90fd\u662f\u5192\u9669\u8005\u4e2d\u7684\u5927\u5fcc\u8bb3\uff0c\u5173\u7cfb\u4e0d\u5230\u4e00\u5b9a\u7a0b\u5ea6\uff0c\u8fd9\u79cd\u8bdd\u9898\u662f\u7edd\u5bf9\u4e0d\u80fd\u8c08\u7684\u3002\u743c\u6069\u5982\u679c\u8d38\u7136\u5f00\u53e3\u8be2\u95ee\uff0c\u8bf4\u4e0d\u5b9a\u5bf9\u65b9\u4f1a\u5f53\u573a\u7ffb\u8138\uff0c\u62d4\u5251\u5c31\u780d\u3002</p><p>\u8fd9\u4e00\u70b9\u8ba9\u743c\u6069\u5f88\u5934\u75bc\uff0c\u4f46\u8981\u8bf4\u5148\u53bb\u6500\u4ea4\u60c5\uff0c\u62c9\u5173\u7cfb\uff0c\u589e\u8fdb\u53cb\u8c0a\uff0c\u4ed6\u53c8\u4e0d\u613f\u610f\u5e72\u3002\u5982\u679c\u5bf9\u65b9\u662f\u7f8e\u5973\uff0c\u4ed6\u5012\u8fd8\u53ef\u4ee5\u8003\u8651\uff0c\u5bf9\u65b9\u662f\u4e2a\u7537\u4eba\uff0c\u81ea\u5df1\u8fd9\u4e48\u732e\u6bb7\u52e4\uff0c\u4f1a\u4e0d\u4f1a\u88ab\u8ba4\u4e3a\u6709\u67d0\u79cd\u7279\u6b8a\u6027\u53d6\u5411\u3002</p><p>\u8bf4\u5230\u7f8e\u5973\uff0c\u4f0a\u65af\u5854\u8eab\u8fb9\u7684\u90a3\u4f4d\u601d\u601d\u5c0f\u59d0\u5012\u662f\u5f53\u4e4b\u65e0\u6127\uff0c\u5018\u82e5\u662f\u8981\u53bb\u548c\u5979\u642d\u8baa\uff0c\u743c\u6069\u4e00\u5b9a\u5f88\u4e50\u610f\uff0c\u53ef\u60dc\u6ca1\u6709\u4ec0\u4e48\u673a\u4f1a\u3002\u5bf9\u65b9\u663e\u7136\u5df2\u7ecf\u540d\u82b1\u6709\u4e3b\uff0c\u81ea\u5df1\u8981\u60f3\u63d2\u624b\uff0c\u9664\u975e\u5148\u628a\u4f0a\u65af\u5854\u5e72\u6389\u2026\u2026\u8fd9\u4e2a\u96be\u5ea6\u4f3c\u4e4e\u633a\u9ad8\u7684\uff0c\u8fd8\u662f\u4e0d\u4e88\u8003\u8651\u3002</p><p>\u4ee5\u5bb9\u8c8c\u800c\u8bba\uff0c\u601d\u601d\u5176\u5b9e\u4e5f\u7b97\u4e0d\u4e0a\u90a3\u79cd\u60ca\u4e16\u7edd\u8273\u7684\u5927\u7f8e\u4eba\uff0c\u6f02\u4eae\u662f\u53ef\u4ee5\u8bf4\uff0c\u4f46\u8fd8\u6ca1\u5230\u73ca\u5609\u548c\u6885\u83f2\u65af\u90a3\u79cd\u7a0b\u5ea6\uff0c\u4f46\u5979\u8eab\u4e0a\u81ea\u6709\u4e00\u79cd\u72ec\u7279\u6c14\u8d28\uff0c\u4e0d\u597d\u7528\u8bed\u8a00\u6765\u5f62\u5bb9\uff0c\u4f46\u786e\u5b9e\u662f\u743c\u6069\u751f\u5e73\u672a\u9047\uff0c\u4ee4\u4eba\u5370\u8c61\u6df1\u523b\uff0c\u89c1\u8fc7\u4e00\u6b21\u5c31\u518d\u4e5f\u65e0\u6cd5\u5fd8\u8bb0\u3002</p><p>\u800c\u4e14\uff0c\u5979\u7684\u773c\u775b\u5f88\u5947\u602a\u3002</p><p>\u4e16\u754c\u4e0a\u7684\u4eba\uff0c\u773c\u775b\u4ec0\u4e48\u989c\u8272\u7684\u90fd\u6709\uff0c\u9ed1\u8272\u3001\u8910\u8272\u3001\u78a7\u8272\u3001\u7ea2\u8272\u3001\u7d2b\u8272\u3001\u84dd\u8272\u4ec0\u4e48\u7684\uff0c\u743c\u6069\u90fd\u89c1\u8fc7\uff0c\u672c\u6765\u6ca1\u4ec0\u4e48\u597d\u60ca\u8bb6\u7684\u3002\u7136\u800c\u601d\u601d\u7684\u773c\u775b\uff0c\u5374\u662f\u4e24\u79cd\u989c\u8272\u2014\u2014\u5979\u7684\u5de6\u773c\u662f\u6d45\u84dd\u8272\uff0c\u53f3\u773c\u5374\u662f\u6df1\u78a7\u8272\uff0c\u8fd9\u5c31\u5f88\u8ba9\u4eba\u60ca\u8be7\u83ab\u540d\u4e86\uff0c\u4e0d\u77e5\u9053\u4ec0\u4e48\u6837\u7684\u7236\u6bcd\uff0c\u4ec0\u4e48\u6837\u7684\u8840\u7edf\uff0c\u624d\u4f1a\u751f\u51fa\u8fd9\u6837\u7684\u5973\u513f\u3002</p><p>\u201c\u53cc\u773c\u5f02\u8272\uff0c\u8fd9\u79cd\u4e8b\u60c5\u6211\u53ea\u5728\u4e66\u91cc\u8bfb\u5230\u8fc7\uff0c\u4ece\u6ca1\u60f3\u5230\u8fd8\u771f\u6709\u8fd9\u6837\u7684\u4eba\uff0c\u201d\u6885\u83f2\u65af\u6c89\u541f\u7740\uff0c\u201c\u90a3\u4f4d\u601d\u601d\u5c0f\u59d0\uff0c\u53ea\u6015\u5f88\u4e0d\u7b80\u5355\u3002\u201d</p><p>\u201c\u600e\u4e48\u8bf4\uff1f\u201d</p><p>\u201c\u6211\u4ee5\u524d\u8bfb\u8fc7\u7684\u4e00\u4efd\u79d8\u5bc6\u8d44\u6599\uff0c\u4e0a\u9762\u8bf4\u8fd9\u79cd\u60c5\u5f62\u53eb\u505a\u2018\u5996\u77b3\u2019\u3002\u751f\u5177\u5996\u77b3\u7684\u4eba\uff0c\u65e0\u4e00\u4e0d\u662f\u8d85\u51e1\u7edd\u4e16\u4e4b\u624d\u3002\u800c\u4e14\u2026\u2026\u201d\u5979\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u201c\u636e\u8bf4\u5996\u77b3\u8fd8\u6709\u4e00\u5c42\u542b\u4e49\uff0c\u4fbf\u662f\u6b64\u4eba\u7684\u4f53\u5185\uff0c\u5176\u5b9e\u6709\u4e24\u4e2a\u7075\u9b42\u3002\u201d</p></article>"}}}]);
