(window.webpackJsonp=window.webpackJsonp||[]).push([[392],{415:function(p){p.exports={chapterTitle:null,title:"\u7b2c392\u7ae0 \u62db\u5f85\u5bbe\u9986\u5148\u6539\u5236",content:"<article><h2>\u7b2c392\u7ae0 \u62db\u5f85\u5bbe\u9986\u5148\u6539\u5236</h2><p>\u3000\u3000\u6309\u7ea6\u5b9a\u597d\u7684\uff0c\u7b2c\u4e8c\u5929\u4e0a\u5348\u7fdf\u4e39\u9752\u966a\u536b\u5170\u5230\u5e02\u516c\u5c40\u91cd\u65b0\u505a\u4e86\u7b14\u5f55\uff0c\u4e0b\u5348\u5f00\u8f66\u9001\u536b\u5170\u4e0e\u5979\u5988\u5230\u5f20\u77e5\u884c\u5bb6\u8ba4\u8def\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u5728\u73af\u57ce\u6cb3\u5317\u7684\u65b0\u5bb6\u522b\u5885\uff0c\u7fdf\u4e39\u9752\u4e5f\u672a\u66fe\u6709\u673a\u4f1a\u53bb\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u521d\u5230\u65b0\u829c\uff0c\u5c31\u5728\u73af\u57ce\u6cb3\u5317\u522b\u5885\u533a\u8d2d\u7f6e\u7269\u4e1a\uff0c\u4e5f\u7b97\u4e0d\u4e0a\u5f88\u6536\u655b\u7684\u4eba\u3002\u73b0\u5728\u65b0\u829c\u5e02\u91cc\u8bb8\u591a\u4eba\uff0c\u5bf9\u8fd9\u4e2a\u521a\u5230\u65b0\u829c\u6765\u7684\u526f\u5e02\u957f\u770b\u6cd5\u4e0d\u518d\u90a3\u4e48\u7b80\u5355\u3002\u4e8c\u5341\u4e09\u65e5\uff0c\u5f20\u77e5\u884c\u7236\u5b50\u4e0e\u7f57\u5f52\u6e90\u5728\u5929\u4e91\u9152\u5e97\u5f53\u4f17\u7ffb\u8138\uff0c\u5927\u5bb6\u90fd\u4ee5\u4e3a\u8fd9\u53c8\u5c06\u662f\u65b0\u829c\u5b98\u573a\u626f\u4e0d\u5b8c\u7684\u4e00\u672c\u70c2\u5e10\uff0c\u8c01\u66fe\u60f3\u5230\u624d\u8fc7\u4e24\u4e09\u5929\uff0c\u7f57\u5f52\u6e90\u4e00\u70b9\u62b5\u6297\u529b\u90fd\u6ca1\u6709\u7684\u5c31\u53d7\u5230\u8fd9\u4e48\u81f4\u547d\u7684\u4e00\u51fb\u3002\u867d\u7136\u7f57\u5f52\u6e90\u540d\u4e49\u4e0a\u8fd8\u662f\u526f\u5e02\u957f\uff0c\u4f46\u662f\u5927\u5bb6\u90fd\u6653\u5f97\u4ed6\u80fd\u514d\u4e8e\u7262\u72f1\u4e4b\u707e\u5c31\u7b97\u7956\u4e0a\u70e7\u9ad8\u9999\u4e86\u3002\u5f20\u77e5\u884c\u624b\u6bb5\u4e4b\u5e72\u51c0\u5229\u843d\uff0c\u80cc\u666f\u4e4b\u6df1\u539a\u575a\u5b9e\uff0c\u8ba9\u4eba\u77a0\u76ee\u7ed3\u820c\uff0c\u8fd9\u662f\u65b0\u829c\u5e02\u7edd\u5927\u90e8\u95e8\u4e2d\u5c42\u5e72\u90e8\u5185\u5fc3\u7684\u60f3\u6cd5\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u666e\u901a\u5e02\u6c11\u6765\u8bf4\uff0c\u6628\u5929\u65e0\u7591\u662f\u8fc7\u8282\u822c\u7684\u5feb\u4e50\uff0c\u4e0a\u5c42\u7684\u52fe\u5fc3\u6597\u89d2\u4e0e\u4ed6\u4eec\u662f\u65e0\u5173\u7684\uff0c\u9f99\u534e\u96c6\u56e2\u5728\u65b0\u829c\u72b9\u8c6b\u5982\u4e00\u6839\u6bd2\u523a\uff0c\u5341\u51e0\u5e74\u4f55\u66fe\u770b\u5230\u6709\u8c01\u6562\u52a8\u4ed6\u4eec\u4e00\u6839\u6beb\u6bdb\uff1f\u90a3\u4e9b\u5728\u65b0\u829c\u81ed\u540d\u662d\u8457\u7684\u5bb6\u4f19\u6beb\u65e0\u9884\u5146\u7684\u7ed9\u6293\u4e86\u8fdb\u53bb\uff0c\u666e\u901a\u5e02\u6c11\u751a\u81f3\u4e0d\u77e5\u9053\u9f99\u534e\u96c6\u56e2\u662f\u56e0\u4e3a\u57ce\u9632\u6c5f\u5824\u7684\u4e8b\u60c5\u683d\u4e86\u3002</p><p>\u3000\u3000\u65b0\u829c\u65e5\u62a5\u4e8c\u5341\u4e03\u65e5\u4ee5\u5f88\u5c0f\u5e45\u7bc7\u62a5\u9053\u57ce\u9632\u6c5f\u5824\u53ef\u80fd\u5b58\u5728\u91cd\u5927\u7684\u8d28\u91cf\u9690\u60a3\uff0c\u627f\u5efa\u5546\u9f99\u534e\u96c6\u56e2\u76f8\u5173\u8d1f\u8d23\u4eba\u5df2\u7ecf\u88ab\u8b66\u65b9\u63a7\u5236\uff0c\u5728\u53e6\u4e00\u4e2a\u7248\u9762\uff0c\u5728\u4e00\u4e2a\u5f88\u4e0d\u8d77\u773c\u7684\u89d2\u843d\uff0c\u5e02\u653f\u5e9c\u767b\u4e86\u4e00\u5219\u516c\u793a\uff0c\u516c\u793a\u5e02\u653f\u5e9c\u4e3b\u8981\u5206\u7ba1\u9886\u5bfc\u7684\u804c\u80fd\u8c03\u6574\uff0c\u9664\u4e86\u9632\u6c5b\u6307\u6325\u90e8\u5bf9\u5916\u5ba3\u4f20\uff0c\u7f57\u5f52\u6e90\u4e0d\u518d\u5206\u7ba1\u4efb\u4f55\u5de5\u4f5c\u3002\u90a3\u4e9b\u7a0d\u6709\u4e9b\u653f\u6cbb\u654f\u611f\u5ea6\u7684\u5e02\u6c11\u90fd\u9690\u7ea6\u731c\u5230\u5728\u6628\u5929\u65b0\u829c\u6700\u5927\u4e00\u6839\u6bd2\u523a\u7ed9\u62e8\u6389\u4e4b\u540e\uff0c\u6700\u5927\u7684\u4e00\u9897\u6bd2\u7624\u4e5f\u5373\u5c06\u7ed9\u94f2\u9664\uff0c\u5de5\u4f5c\u8c03\u6574\u662f\u4e00\u4e2a\u660e\u663e\u7684\u4fe1\u53f7\u3002</p><p>\u3000\u3000\u627e\u5230\u5f20\u77e5\u884c\u5bb6\u522b\u5885\uff0c\u697c\u524d\u505c\u7740\u4e09\u8f86\u8f7f\u8f66\uff0c\u6709\u8f86\u662f\u5e02\u59d4\u4e66\u8bb0\u6c5f\u4e0a\u5143\u7684\u522b\u514b\u8f66\uff0c\u5fc3\u60f3\u6c5f\u4e0a\u5143\u5728\u8fd9\u91cc\uff0c\u90a3\u4e48\u8bf4\u5f20\u77e5\u884c\u5e94\u8be5\u56de\u65b0\u829c\u4e86\u3002\u95e8\u5385\u91cc\u6709\u5973\u5b69\u5b50\u7684\u5a07\u7b11\u58f0\uff0c\u542c\u7740\u6e05\u8106\uff0c\u7fdf\u4e39\u9752\u5fc3\u60f3\u5e94\u8be5\u6628\u5929\u6881\u683c\u73cd\u6240\u8bf4\u7684\u201c\u5510\u5a67\u201d\u3002\u7fdf\u4e39\u9752\u5c06\u8f66\u505c\u5728\u697c\u524d\uff0c\u4e0e\u536b\u5170\u6276\u5979\u5988\u4e0b\u8f66\uff0c\u536b\u5170\u5979\u5988\u662f\u6765\u5e94\u5de5\uff0c\u81ea\u7136\u4e0d\u613f\u7ed9\u522b\u4eba\u7167\u987e\uff0c\u4e0d\u8ba9\u536b\u5170\u3001\u7fdf\u4e39\u9752\u6400\u6276\u5979\uff0c\u8fd8\u52aa\u529b\u8d70\u5f97\u5468\u6b63\u3002</p><p>\u3000\u3000\u4e00\u4e2a\u9762\u5bb9\u6e05\u4e3d\u3001\u773c\u7738\u6e05\u6f88\u7684\u5973\u5b69\u5b50\u8d70\u51fa\u6765\uff0c\u7fdf\u4e39\u9752\u95ee\u5979\uff1a\u201c\u8fd9\u91cc\u662f\u5f20\u526f\u5e02\u957f\u5bb6\u5427\uff1f\u201d\u7fdf\u4e39\u9752\u4e4b\u524d\u6709\u6253\u5f20\u606a\u7684\u624b\u673a\uff0c\u5fd9\u97f3\u3002</p><p>\u3000\u3000\u201c\u662f\u7684\uff0c\u6881\u963f\u59e8\u5728\u5c4b\u91cc\uff0c\u6211\u5e2e\u4f60\u4eec\u53bb\u558a\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u5c31\u662f\u5510\u5a67\u5427\uff1f\u201d\u7fdf\u4e39\u9752\u5373\u4f7f\u8eab\u4e3a\u6f02\u4eae\u7684\u5973\u4eba\uff0c\u4e5f\u4e3a\u773c\u524d\u201c\u5510\u5a67\u201d\u7eaf\u51c0\u7684\u7f8e\u611f\u89c9\u4e9b\u5fae\u7684\u538b\u8feb\uff0c\u201c\u6881\u59e8\u6628\u5929\u7ea6\u6211\u4eec\u8fc7\u6765\u7684\uff0c\u8fd8\u63d0\u8d77\u8fc7\u4f60\u8fd9\u4e24\u5929\u4f1a\u5230\u65b0\u829c\u6765\u73a9\uff0c\u6ca1\u60f3\u5230\u4f60\u4eca\u5929\u5c31\u5230\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u554a\uff0c\u4f60\u4eec\u77e5\u9053\u5510\u5a67\u554a\uff0c\u5979\u4e5f\u5728\u5c4b\u91cc\uff0c\u6211\u662f\u5510\u5a67\u7684\u540c\u5b66\u2026\u2026\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u7b11\u4e86\u7b11\uff0c\u4eb2\u5207\u7684\u8bf4\uff1a\u201c\u90a3\u4f60\u4e5f\u662f\u5f20\u606a\u7684\u540c\u5b66\u55bd\uff0c\u5f20\u606a\u6709\u8fd9\u4e48\u6f02\u4eae\u7684\u540c\u5b66\uff0c\u5012\u6ca1\u6709\u542c\u4ed6\u63d0\u8d77\u8fc7\u3002\u201d</p><p>\u3000\u3000\u6881\u683c\u73cd\u542c\u5230\u7fdf\u4e39\u9752\u4e0e\u9648\u5983\u84c9\u7684\u8bf4\u8bdd\u58f0\uff0c\u8d70\u5230\u95e8\u53e3\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u8fc7\u6765\u4e86\uff0c\u8fd9\u662f\u5c0f\u606a\u7684\u540c\u5b66\u9648\u5983\u84c9\uff0c\u4e4b\u524d\u6709\u5e2e\u5c0f\u606a\u8865\u4e60\u529f\u8bfe\u6765\u7740\uff0c\u8ddf\u5510\u5a67\u4e00\u8d77\u6765\u65b0\u829c\u73a9\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u5983\u84c9\u9ad8\u8003\u7ed3\u675f\uff0c\u4e1c\u6d77\u5927\u5b66\u7684\u5f55\u53d6\u901a\u77e5\u4e66\u4e5f\u62ff\u5230\u624b\uff0c\u7ed9\u5510\u5a67\u62c9\u5230\u65b0\u829c\u6765\u73a9\uff0c\u5979\u89c1\u6881\u683c\u73cd\u51fa\u6765\u62db\u547c\uff0c\u5c31\u8f6c\u8eab\u8fdb\u4e86\u5c4b\u5b50\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u8ddf\u7740\u6881\u683c\u73cd\u8fdb\u5c4b\uff0c\u5fc3\u60f3\u7740\u5510\u5a67\u4f1a\u662f\u4ec0\u4e48\u6837\u5b50\uff0c\u8981\u6bd4\u521a\u624d\u90a3\u4e2a\u5973\u5b69\u5b50\u8fd8\u8981\u6f02\u4eae\uff0c\u90a3\u5c31\u592a\u6ca1\u6709\u5929\u7406\u4e86\u3002</p><p>\u3000\u3000\u536b\u5170\u5979\u5988\u8fdb\u4e86\u5c4b\uff0c\u5374\u89c9\u5f97\u5ba4\u5185\u5bcc\u4e3d\u5802\u7687\u5f97\u8000\u773c\uff0c\u6697\u611f\u679c\u7136\u662f\u526f\u5e02\u957f\u7684\u5bb6\uff0c\u5fc3\u865a\u8d77\u6765\uff0c\u4e0d\u6653\u5f97\u5728\u8fd9\u6837\u7684\u4eba\u5bb6\u505a\u4e0d\u505a\u5f97\u6765\u5de5\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u8fc7\u6765\u4e86\u3002\u201d\u5f20\u606a\u8170\u4e0b\u56f4\u88d9\uff0c\u624b\u91cc\u8fd8\u62ff\u7740\u6839\u80e1\u841d\u535c\uff0c\u7ad9\u5728\u53a8\u623f\u95e8\u62db\u547c\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u201c\u8d76\u60c5\u4f60\u6628\u5929\u4e0d\u662f\u5439\u725b\uff1f\u201d\u7fdf\u4e39\u9752\u8be7\u5f02\u7684\u8bf4\uff0c\u8fd8\u771f\u60f3\u50cf\u4e0d\u51fa\u4e00\u540d\u5341\u516b\u4e5d\u5c81\u7684\u9ad8\u5e72\u5b50\u5f1f\u8fd8\u7cbe\u901a\u53a8\u827a\u3002</p><p>\u3000\u3000\u201c\u6211\u7684\u624b\u827a\u62ff\u51fa\u6765\u5f85\u5ba2\u5c31\u6709\u6b20\u4e86\u2026\u2026\u201d\u6881\u683c\u73cd\u5728\u65c1\u8fb9\u7b11\u7740\u8bf4\uff0c\u201c\u8fd9\u5c0f\u5b50\u90fd\u8bf4\u53d7\u4e0d\u4e86\u6211\u7684\u53a8\u827a\uff0c\u624d\u81ea\u5b66\u6210\u624d\uff0c\u4e0d\u8fc7\u505a\u7684\u83dc\u8fd8\u7b97\u5730\u9053\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u4f60\u4eec\u5148\u5750\u7740\u5427\uff0c\u505a\u5565\u90fd\u53ef\u4ee5\uff0c\u665a\u996d\u8fd8\u65e9\u7740\u5462\uff0c\u6211\u4e00\u4eba\u52a8\u624b\u8fd8\u5feb\u4e00\u4e9b\uff0c\u5173\u952e\u91cc\u9762\u8fd8\u6709\u4e00\u4e2a\u6dfb\u4e71\u7684\u3002\u201d\u5f20\u606a\u8eab\u4f53\u5f80\u65c1\u8fb9\u8ba9\u4e86\u8ba9\uff0c\u5e2e\u91cc\u9762\u6b63\u62ff\u7740\u4e00\u6839\u7af9\u7b77\u5b50\u5228\u5c71\u836f\u7684\u5510\u5a67\u4ecb\u7ecd\u7fdf\u4e39\u9752\u8ddf\u536b\u5170\u5979\u4eec\uff0c\u5510\u5a67\u6b63\u5728\u540e\u9762\u62ff\u811a\u8e22\u4ed6\uff0c\u201c\u8fd9\u4f4d\u662f\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u7684\u7fdf\u603b\u8ddf\u536b\u59d0\uff0c\u6211\u90fd\u4e0d\u77e5\u9053\u65b0\u829c\u6709\u54ea\u4e9b\u597d\u73a9\u7684\u5730\u65b9\uff0c\u4f60\u8ddf\u9648\u5983\u84c9\u8981\u5728\u65b0\u829c\u73a9\u5f00\u5fc3\uff0c\u8fd8\u5f97\u6c42\u7fdf\u603b\u4e0e\u536b\u59d0\u6307\u70b9\u3002\u201d</p><p>\u3000\u3000\u201c\u7fdf\u59d0\u3001\u536b\u59d0\u2026\u2026\u201d\u5510\u5a67\u4e56\u5de7\u7684\u62db\u547c\u7740\u3002</p><p>\u3000\u3000\u4e0e\u9648\u5983\u84c9\u7684\u6e05\u8273\u660e\u4e3d\u6bd4\u8d77\u6765\uff0c\u5510\u5a67\u6709\u7740\u53e6\u4e00\u79cd\u5a07\u7f8e\u7eaf\u771f\u7684\u5473\u9053\uff0c\u90fd\u662f\u4e00\u6837\u7684\u660e\u8273\uff0c\u7cbe\u81f4\u65e0\u7455\u7684\u9e45\u86cb\u8138\uff0c\u4e4d\u770b\u4e0a\u53bb\u8ddf\u4fe9\u59d0\u59b9\u4e00\u6837\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u671d\u536b\u5170\u644a\u644a\u624b\uff0c\u90a3\u610f\u601d\u662f\u8bf4\uff1a\u60f3\u52fe\u5f15\u4e5f\u6ca1\u529e\u6cd5\uff0c\u8c01\u66fe\u60f3\u5230\u8fd9\u5c0f\u5b50\u6709\u8fd9\u4e48\u6f02\u4eae\u7684\u5c0f\u5973\u670b\u53cb\uff1b\u770b\u7740\u7fdf\u4e39\u9752\u65e0\u5948\u7684\u8868\u60c5\uff0c\u536b\u5170\u5dee\u70b9\u5c31\u7b11\u51fa\u6765\u3002</p><p>\u3000\u3000\u536b\u5170\u5979\u5988\u8981\u5e2e\u5fd9\uff0c\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u536b\u59e8\u4eca\u5929\u8fd8\u662f\u5ba2\u4eba\uff0c\u603b\u6ca1\u6709\u8ba9\u5ba2\u4eba\u52a8\u624b\u7684\u9053\u7406\uff0c\u8981\u5bf9\u6211\u7684\u624b\u827a\u6709\u4fe1\u5fc3\uff0c\u5510\u5a67\u53ea\u4f1a\u6dfb\u4e71\u4e0d\u5047\uff0c\u9648\u5983\u84c9\u5012\u4e5f\u80fd\u5e2e\u4e0a\u5f88\u591a\u5fd9\uff0c\u5f88\u5feb\u5c31\u597d\u4e86\u3002\u201d</p><p>\u3000\u3000\u8fd9\u8fb9\u7684\u4e8b\u60c5\u5dee\u4e0d\u591a\u5e73\u606f\u4e86\uff0c\u9664\u4e86\u5085\u4fca\u8fd8\u7559\u5728\u65b0\u829c\uff0c\u9a6c\u6d77\u9f99\u4ed6\u4eec\u4e0a\u5348\u5f00\u8f66\u5c31\u56de\u6d77\u5dde\u4e86\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u542c\u5230\u6c5f\u4e0a\u5143\u5728\u697c\u4e0a\u7684\u8bf4\u8bdd\u58f0\uff0c\u5927\u6982\u8fc7\u4e86\u5c0f\u534a\u5c0f\u65f6\uff0c\u770b\u89c1\u6c5f\u4e0a\u5143\u4e0e\u5f20\u77e5\u884c\u4e0b\u697c\u6765\uff1a\u201c\u5c0f\u7fdf\u4e5f\u5230\u5f20\u526f\u5e02\u957f\u5bb6\u6765\u505a\u5ba2\u554a\uff1f\u201d\u8d70\u8fc7\u6765\u5bf9\u536b\u5170\u8bf4\uff0c\u201c\u5c0f\u536b\uff0c\u4f60\u53d7\u7684\u59d4\u5c48\uff0c\u5e02\u59d4\u4f1a\u5e2e\u4f60\u4e3b\u6301\u516c\u9053\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u542c\u6c5f\u4e0a\u5143\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5fc3\u91cc\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8fd9\u6c14\u6c1b\u672c\u6765\u597d\u597d\u7684\uff0c\u8ba9\u4ed6\u8fd9\u4e48\u4e00\u8bf4\uff0c\u4e0d\u662f\u7ed9\u4eba\u5fc3\u91cc\u6dfb\u5835\u5417\uff1f\u8fd8\u597d\u7fdf\u4e39\u9752\u5f88\u5feb\u5c94\u5f00\u8bdd\u9898\u3002</p><p>\u3000\u3000\u5f20\u606a\u96be\u5f97\u7a7a\u95f2\u4e0b\u6765\u51c6\u5907\u4e00\u684c\u83dc\uff0c\u4f17\u4eba\u5403\u4e86\u81ea\u7136\u662f\u8d5e\u4e0d\u7edd\u53e3\u3002\u542c\u8bf4\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u7279\u5730\u5230\u65b0\u829c\u6765\u73a9\uff0c\u6c5f\u4e0a\u5143\u8ba9\u7fdf\u4e39\u9752\u6682\u65f6\u5c06\u624b\u5934\u7684\u4e8b\u60c5\u653e\u4e00\u653e\uff0c\u966a\u7740\u4e24\u4e2a\u5973\u5b69\u5b50\u597d\u597d\u5728\u65b0\u829c\u73a9\u4e00\u73a9\uff0c\u4e5f\u8ba9\u536b\u5170\u4e00\u8d77\u8ddf\u7740\u6563\u6563\u5fc3\u3002</p><p>\u3000\u3000\u62db\u5f85\u5bbe\u9986\u53d1\u751f\u4e8c\u5341\u4e09\u65e5\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u7fdf\u4e39\u9752\u77e5\u9053\u81ea\u5df1\u662f\u8981\u5e2e\u7740\u5e02\u59d4\u627f\u62c5\u8d23\u4efb\u7684\uff0c\u4e0d\u7136\u4f1a\u6709\u4eba\u5c06\u77db\u5934\u6307\u5411\u94b1\u6587\u8d35\u7684\uff0c\u6c5f\u4e0a\u5143\u8bf4\u7684\u59d4\u5a49\uff0c\u5979\u5fc3\u91cc\u5374\u662f\u660e\u767d\uff0c\u5fc3\u60f3\u966a\u536b\u5170\u6563\u6563\u5fc3\u4e5f\u597d\u3002</p><p>\u3000\u3000\u5403\u8fc7\u665a\u996d\uff0c\u6c5f\u4e0a\u5143\u6ca1\u6709\u505c\u7559\uff0c\u6709\u4e8b\u5148\u79bb\u5f00\u4e86\uff0c\u7fdf\u4e39\u9752\u4e0e\u536b\u5170\u5e2e\u7740\u5c06\u7897\u789f\u6536\u62fe\u6d17\u51c0\u4e86\uff0c\u4e5f\u6253\u7b97\u544a\u8f9e\u79bb\u5f00\uff0c\u5f20\u77e5\u884c\u5524\u4f4f\u5979\uff1a\u201c\u5e02\u91cc\u641e\u4f01\u4e1a\u6539\u5236\u8bd5\u70b9\uff0c\u6211\u6253\u7b97\u4ece\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u5f00\u59cb\uff0c\u6211\u5df2\u7ecf\u8ddf\u94b1\u79d8\u4e66\u957f\u6253\u8fc7\u62db\u547c\u4e86\uff0c\u6c5f\u4e66\u8bb0\u7ed9\u4f60\u653e\u51e0\u5929\u5047\uff0c\u6211\u5e0c\u671b\u4f60\u5229\u7528\u8fd9\u51e0\u5929\u8ba4\u771f\u7684\u8003\u8651\u4e00\u4e0b\uff0c\u6709\u4ec0\u4e48\u597d\u7684\u5efa\u8bae\uff0c\u53ef\u4ee5\u968f\u65f6\u8ddf\u5e02\u91cc\u6c47\u62a5\u3002\u201d</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u6709\u4e9b\u7a81\u7136\uff0c\u90fd\u5728\u4f20\u53ef\u80fd\u662f\u5e02\u5efa\u516c\u53f8\u7b2c\u4e00\u5bb6\u8bd5\u70b9\u6539\u5236\uff0c\u4ece\u6ca1\u6709\u60f3\u5230\u4f1a\u662f\u62db\u5f85\u5bbe\u9986\uff0c\u7fdf\u4e39\u9752\u53d1\u4e86\u4e00\u4f1a\u6123\uff0c\u624d\u56de\u8fc7\u795e\u6765\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u4f1a\u8ba4\u771f\u8003\u8651\u7684\u3002\u201d\u5fc3\u91cc\u53c8\u60f3\uff1a\u6c5f\u4e0a\u5143\u5728\u5f20\u77e5\u884c\u5bb6\u4e00\u4e0b\u5348\uff0c\u96be\u9053\u662f\u8ba8\u8bba\u4f01\u4e1a\u6539\u5236\u7684\u4e8b\u60c5\uff1f\u5f20\u77e5\u884c\u8fd9\u4e0b\u5b50\u771f\u6b63\u6210\u4e3a\u65b0\u829c\u5e02\u5b9e\u6743\u4eba\u7269\u4e86\u3002</p><p>\u3000\u3000\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u4e00\u76f4\u7ed9\u5e02\u91cc\u4ee5\u53ca\u4e0b\u9762\u533a\u53bf\u515a\u653f\u9886\u5bfc\u5f53\u6210\u4f53\u73b0\u4ed6\u4eec\u7279\u6743\u7684\u6240\u5728\uff0c\u7531\u4e8e\u515a\u653f\u9886\u5bfc\u5174\u8da3\u591a\u6709\u5947\u5f02\u7684\u5730\u65b9\uff0c\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u85cf\u6c61\u7eb3\u57a2\u4e5f\u5728\u6240\u96be\u514d\uff0c\u5373\u4f7f\u53d1\u751f\u4e8c\u5341\u4e09\u65e5\u7684\u4e8b\u60c5\uff0c\u5728\u666e\u901a\u5e02\u6c11\u773c\u91cc\uff0c\u4e5f\u89c6\u82e5\u6b63\u5e38\uff0c\u65e2\u7136\u662f\u4f53\u73b0\u7279\u6743\u7684\u6240\u5728\uff0c\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u4ece\u6210\u7acb\u4e4b\u521d\u7684\u76ee\u7684\u5c31\u662f\u4e0d\u4e3a\u4e86\u76c8\u5229\uff0c\u5bbe\u9986\u5165\u4f4f\u7387\u8fdc\u4e0d\u53ca\u57ce\u90ca\u90a3\u4e9b\u4e0d\u5165\u6d41\u7684\u5bbe\u9986\uff0c\u6bcf\u5e74\u81ea\u7136\u4f1a\u5728\u5e02\u8d22\u653f\u4e0a\u4f1a\u5f62\u6210\u4e00\u9053\u5f88\u5927\u7684\u53e3\u5b50\uff0c\u4f46\u662f\u5e02\u91cc\u90fd\u6e05\u695a\u8fd9\u4e9b\u53e3\u5b50\u662f\u600e\u4e48\u5f62\u6210\u7684\uff0c\u6240\u4ee5\u5e02\u91cc\u62e8\u6b3e\u90fd\u662f\u76f8\u5f53\u7684\u53ca\u65f6\uff0c\u7fdf\u4e39\u9752\u5f88\u5947\u602a\u6c5f\u4e0a\u5143\u600e\u4e48\u5c31\u540c\u610f\u8ba9\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u5148\u884c\u6539\u5236\uff1f\u5947\u602a\u7684\u540c\u65f6\uff0c\u5979\u5185\u5fc3\u91cc\u5374\u677e\u4e86\u4e00\u53e3\u6c14\u3002</p><p>\u3000\u3000\u6c5f\u4e0a\u5143\u4e4b\u6240\u4ee5\u540c\u610f\u5148\u62ff\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u51fa\u6765\u6539\u5236\uff0c\u5f20\u606a\u6628\u5929\u508d\u665a\u4e0e\u4ed6\u8bf4\u7684\u90a3\u756a\u8bdd\uff0c\u4ed6\u6709\u601d\u91cf\u8fc7\uff0c\u8fd8\u6709\u4e00\u70b9\u5f88\u5173\u952e\uff0c\u57ce\u9632\u6c5f\u5824\u8fc7\u4e86\u8fd9\u4e2a\u6c5b\u671f\u8981\u7acb\u5373\u52a8\u624b\u6574\u56fa\uff0c\u8017\u7528\u7684\u8d44\u91d1\u4e0d\u662f\u5c0f\u6570\u76ee\uff0c\u7701\u91cc\u53ef\u80fd\u4f1a\u62e8\u4e00\u4e9b\u2014\u2014\u8fd9\u4e2a\u4e5f\u662f\u672a\u77e5\u6570\u2014\u2014\u6709\u62e8\u6b3e\u6709\u9650\uff0c\u4e00\u5207\u8fd8\u662f\u8981\u5e02\u91cc\u81ea\u7b79\u3002\u65b0\u829c\u5e02\u53bb\u5e74\u7684\u8d22\u653f\u6536\u5165\u4e0d\u5230\u516d\u4e2a\u4ebf\uff0c\u94b1\u8be5\u600e\u4e48\u7528\uff0c\u5e74\u521d\u5c31\u6709\u5b89\u6392\uff0c\u8fd8\u5230\u5904\u90fd\u662f\u7a9f\u7abf\uff0c\u5230\u5e74\u4e2d\u8981\u62ff\u51fa\u51e0\u5343\u4e07\u6765\u6574\u56fa\u6c5f\u5824\uff0c\u5e02\u8d22\u653f\u4f1a\u683c\u5916\u7684\u5403\u7d27\uff0c\u8981\u662f\u5b89\u6392\u660e\u5e74\uff0c\u660e\u5e74\u6c5b\u671f\u4e4b\u524d\u6765\u4e0d\u53ca\u52a8\u5de5\uff0c\u4eca\u5929\u6c5b\u60c5\u8f7b\u5fae\uff0c\u5927\u5bb6\u538b\u529b\u8fd8\u5c0f\u4e00\u4e9b\uff0c\u660e\u5e74\u8981\u662f\u5c0f\u6c5f\u4e0a\u6e38\u5f62\u6210\u5927\u7684\u6d2a\u5cf0\uff0c\u4e07\u4e00\u73b0\u5728\u7684\u6c5f\u5824\u625b\u4e0d\u4f4f\uff0c\u8fd9\u803d\u6401\u7684\u8d23\u4efb\u8c01\u6765\u62c5\uff1f\u5c06\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u62ff\u51fa\u6765\u6539\u5236\uff0c\u9996\u5148\u8ba1\u5212\u53bb\u8865\u62db\u5f85\u5bbe\u9986\u7f3a\u53e3\u7684\u94b1\u53ef\u4ee5\u8282\u7ea6\u4e0b\u6765\u3002\u867d\u7136\u62db\u5f85\u5bbe\u9986\u4e0d\u76c8\u5229\u3001\u6bcf\u5e74\u90fd\u8981\u5f80\u91cc\u9762\u586b\u94b1\uff0c\u4f46\u662f\u6c5f\u4e0a\u5143\u77e5\u9053\u662f\u4ec0\u4e48\u539f\u56e0\u9020\u6210\u7684\uff0c\u5e76\u4e0d\u662f\u8bf4\u62db\u5f85\u5bbe\u9986\u6ca1\u6709\u76c8\u5229\u7684\u57fa\u7840\uff0c\u8981\u8ba4\u771f\u8bf4\u8d77\u6765\uff0c\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u5e94\u8be5\u662f\u5e02\u91cc\u5c11\u6709\u7684\u4f18\u8d28\u8d44\u4ea7\uff0c\u53ea\u662f\u4f53\u5236\u4e0d\u6539\u4e0d\u884c\uff0c\u4e0d\u6539\u90a3\u91cc\u53ea\u6210\u4e3a\u5c11\u6570\u4eba\u4eab\u53d7\u7279\u6743\u7684\u5730\u65b9\u3002</p></article>"}}}]);
