(window.webpackJsonp=window.webpackJsonp||[]).push([[392],{415:function(p){p.exports={chapterTitle:null,title:"\u7b2c264\u7ae0 \u5e72\u7ffb\u6389",content:"<article><h2>\u7b2c264\u7ae0 \u5e72\u7ffb\u6389</h2><p>\u3000\u3000\u542c\u8c22\u665a\u6674\u6253\u7b97\u660e\u5929\u966a\u82b7\u5f64\u53bb\u770b\u7535\u5f71\uff0c\u5f20\u606a\u6765\u4e86\u5174\u8da3\uff0c\u4ed6\u90fd\u4e0d\u6653\u5f97\u4e5d\u4e94\u5e74\u7535\u5f71\u9662\u6709\u4ec0\u4e48\u7535\u5f71\u80fd\u9002\u5408\u56db\u5c81\u7684\u5c0f\u5973\u5b69\u5b50\uff0c\u770b\u7740\u540e\u89c6\u955c\u91cc\u7684\u665a\u6674\u71a0\u71a0\u751f\u8f89\u7684\u773c\u7738\u5b50\uff0c\u89c9\u5f97\u5979\u4eca\u665a\u7684\u773c\u7738\u683c\u5916\u7684\u660e\u4eae\uff0c\u95ee\u5979\uff1a\u201c\u4ec0\u4e48\u7535\u5f71\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u90fd\u4e0d\u6e05\u695a\u554a\uff0c\u7535\u5f71\u9662\u660e\u5929\u653e\u300a\u72ee\u5b50\u738b\u300b\u2026\u2026\u201d\u7ec8\u4e8e\u627e\u5230\u4e00\u4e2a\u8bdd\u9898\u6765\u7f13\u89e3\u5fc3\u91cc\u7684\u7126\u707c\uff0c\u8c22\u665a\u6674\u5c06\u8033\u9b13\u7684\u53d1\u4e1d\u634b\u5230\u8033\u6839\u540e\uff0c\u201c\u5e7c\u513f\u56ed\u6709\u5c0f\u670b\u53cb\u770b\u8fc7\u4e86\uff0c\u82b7\u5f64\u56b7\u7740\u4e5f\u8981\u53bb\u770b\uff0c\u672c\u6765\u62dc\u6258\u674e\u660e\u745c\u5e26\u7740\u4e09\u4e2a\u5c0f\u4e2b\u5934\u53bb\u770b\u7684\uff0c\u65e2\u7136\u660e\u5929\u80fd\u4f11\u606f\uff0c\u6211\u4e5f\u53bb\u770b\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5f20\u606a\u5012\u60f3\u8d77\u6765\u4e86\uff0c\u8fd9\u90e8\u7535\u5f71\u662f\u4ece\u838e\u58eb\u6bd4\u4e9a\u7684\u300a\u738b\u5b50\u590d\u4ec7\u8bb0\u300b\u91cc\u83b7\u5f97\u7f16\u5267\u7075\u611f\uff0c\u53f7\u79f0\u52a8\u7269\u754c\u7684\u54c8\u59c6\u96f7\u7269\uff0c\u5f71\u7247\u5229\u7528\u5f53\u65f6\u6700\u5148\u8fdb\u76842D\u52a8\u753b\u6280\u672f\uff0c\u914d\u4e0a\u5b8f\u4f1f\u7684\u4ea4\u54cd\u4e50\uff0c\u8fd8\u6709\u975e\u6d32\u5f53\u5730\u7684\u539f\u59cb\u97f3\u4e50\uff0c\u5e94\u8be5\u662f\u6691\u5047\u5f15\u8fdb\u7684\u7535\u5f71\uff0c\u6d77\u5dde\u8fd9\u8fb9\u5927\u6982\u662f\u7b2c\u4e8c\u8f6e\u64ad\u6620\uff0c\u4e5f\u8bb0\u4e0d\u5927\u6e05\u695a\uff0c\u603b\u4e4b\u662f\u4e00\u90e8\u76f8\u5f53\u4e0d\u9519\u7684\u5f71\u7247\uff0c\u597d\u50cf\u53f7\u79f0\u6210\u4e3a\u7535\u5f71\u53f2\u4e0a\u6700\u6210\u529f\u7684\u5361\u901a\u5f71\u7247\u3002\u5230\u4e5d\u4e94\u5e74\u4e3a\u6b62\u5012\u6709\u51e0\u90e8\u5927\u5236\u4f5c\u7684\u7535\u5f71\u9002\u5408\u56db\u4e94\u5c81\u7684\u513f\u7ae5\u89c2\u770b\uff0c\u9664\u4e86\u300a\u72ee\u5b50\u738b\u300b\u4e4b\u5916\uff0c\u300a\u73a9\u5177\u603b\u52a8\u5458\u300b\u597d\u50cf\u4e5f\u662f\u4e5d\u4e94\u5e74\u5f15\u8fdb\u7684\u7535\u5f71\uff0c\u300a\u4f8f\u7f57\u7eaa\u516c\u56ed\u300b\u867d\u7136\u6ca1\u6709\u4f5c\u4e3a\u5927\u7247\u516c\u5f00\u5f15\u8fdb\uff0c\u76db\u9752\u90a3\u91cc\u5e94\u8be5\u51fa\u76d7\u7248\u789f\u4e86\u3002</p><p>\u3000\u3000\u201c\u5e94\u8be5\u5f88\u706b\u7206\uff0c\u4e09\u4e2a\u5c0f\u4e2b\u5934\u7247\u5b50\uff0c\u4f60\u4eec\u4e24\u4e2a\u600e\u4e48\u5e94\u4ed8\u5f97\u8fc7\u6765\uff1f\u201d\u6d77\u5dde\u5e02\u7684\u51e0\u4e2a\u7535\u5f71\u9662\u90fd\u662f\u4e03\u5341\u5e74\u4ee3\u5efa\u9020\u7684\u5efa\u7b51\uff0c\u97f3\u50cf\u8bbe\u65bd\u76f8\u5bf9\u843d\u540e\uff0c\u53c8\u6ca1\u6709\u591a\u5385\u64ad\u653e\u6216\u8005VIP\u5385\uff0c\u5f20\u606a\u8bb0\u5f97\u300a\u72ee\u5b50\u738b\u300b\u7b49\u56fd\u5916\u5927\u7247\u5f15\u8fdb\u6765\uff0c\u90fd\u76f8\u5f53\u7684\u706b\u7206\uff0c\u4e09\u4e2a\u5c0f\u4e2b\u5934\uff0c\u82b7\u5f64\u8fd8\u597d\u4e00\u4e9b\uff0c\u60dc\u5bb9\u60dc\u7fbd\u5c31\u6709\u4e9b\u76ae\uff0c\u770b\u5230\u4eba\u7fa4\u5c31\u5174\u594b\uff0c\u8fd8\u9ad8\u5174\u5f80\u4eba\u7fa4\u91cc\u94bb\uff0c\u76f8\u4fe1\u4e00\u5b9a\u4f1a\u8ba9\u665a\u6674\u4e0e\u674e\u660e\u745c\u63aa\u624b\u4e0d\u53ca\u7684\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u660e\u5929\u7ed9\u5085\u4fca\u653e\u5047\uff0c\u8ba9\u4ed6\u4e5f\u966a\u4ed6\u4e24\u4e2a\u5b9d\u8d1d\u5973\u513f\u770b\u573a\u7535\u5f71\uff0c\u4e0d\u8981\u8ba9\u522b\u4eba\u8ba4\u4e3a\u6211\u8fd9\u4e2a\u8001\u677f\u592a\u82db\u523b\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u4e5f\u62c5\u5fc3\u2026\u2026\u201d\u665a\u6674\u5634\u89d2\u6302\u7740\u6d45\u7b11\u770b\u7740\u540e\u89c6\u955c\u91cc\u7684\u5f20\u606a\uff0c\u4ed6\u4e13\u6ce8\u7684\u6ce8\u89c6\u524d\u7684\u8def\uff0c\u665a\u6674\u53ef\u4ee5\u7a0d\u7a0d\u53d1\u8086\u7684\u770b\u7740\u4ed6\u7684\u773c\u775b\u4e0e\u9686\u8d77\u7684\u7709\u9aa8\u3001\u8d77\u4e86\u80e1\u5b50\u832c\u7684\u4e0b\u9894\uff0c\u5f85\u4ed6\u89c6\u7ebf\u8f6c\u5230\u540e\u89c6\u955c\u4e0a\uff0c\u665a\u6674\u7684\u773c\u7738\u5b50\u4fbf\u4f1a\u8f7b\u8f7b\u7684\u95ea\u8eb2\u5f00\uff0c\u5f88\u5174\u594b\u7684\u804a\u8d77\u660e\u5929\u8981\u770b\u7684\u7535\u5f71\u3002</p><p>\u3000\u3000\u5230\u5c0f\u9526\u6e56\u522b\u5885\uff0c\u4f9d\u5f80\u5e38\u7684\u4e60\u60ef\uff0c\u665a\u6674\u90fd\u4f1a\u8ba9\u5f20\u606a\u4e0a\u697c\u5750\u4e00\u5750\uff0c\u4eca\u665a\u6000\u7740\u5f02\u6837\u7684\u5fc3\u7eea\uff0c\u6709\u4e9b\u4e0d\u6562\u8ddf\u5f20\u606a\u76f8\u5904\uff0c\u4e0b\u4e86\u8f66\u5c31\u5bf9\u5f20\u606a\u8bf4\uff1a\u201c\u4f60\u8205\u8205\u5bb6\u4eca\u5929\u4e0d\u662f\u5728\u56db\u51e4\u6865\u8bf7\u5ba2\u5403\u996d\u5417\uff1f\u65f6\u95f4\u8fd8\u65e9\uff0c\u4f60\u8fd8\u80fd\u8d76\u8fc7\u53bb\uff0c\u9152\u4e0d\u8981\u518d\u559d\u4e86\uff0c\u8def\u4e0a\u5f00\u8f66\u5c0f\u5fc3\u4e00\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8d76\u8fc7\u6765\uff0c\u4e5f\u53ea\u5269\u4e0b\u6b8b\u7fb9\u51b7\u7099\u2026\u2026\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u6325\u4e86\u6325\u624b\uff0c\u5c06\u8f66\u5012\u4e86\u51fa\u53bb\u3002\u770b\u7740\u8f7f\u8f66\u6d88\u5931\u5728\u6797\u836b\u9053\uff0c\u62d0\u5165\u4e3b\u5e72\u9053\uff0c\u665a\u6674\u5fc3\u865a\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u5fc3\u91cc\u53c8\u662f\u65e0\u6bd4\u7684\u5931\u843d\uff0c\u672a\u5c1d\u4e0d\u6e34\u671b\u4e0e\u4ed6\u72ec\u5904\u4e00\u4f1a\u513f\uff0c\u521a\u624d\u5374\u6b8b\u5fcd\u7684\u8ba9\u4ed6\u79bb\u5f00\u3002\u7ad9\u5728\u5ead\u9662\u7684\u6728\u6805\u680f\u5904\uff0c\u665a\u6674\u5ba1\u89c6\u7740\u60a3\u5f97\u60a3\u5931\u7684\u5fc3\u60c5\uff0c\u6709\u4e9b\u5bb3\u6015\u5fc3\u91cc\u7684\u60c5\u611f\u65e0\u6cd5\u6291\u5236\uff0c\u4e4b\u524d\u77e5\u9053\u8bb8\u601d\u4e0e\u5f20\u606a\u7684\u4e8b\u60c5\uff0c\u8fd8\u89c9\u5f97\u8bb8\u601d\u7684\u5883\u9047\u6709\u4e9b\u60b2\u54c0\uff0c\u6b64\u65f6\u5374\u7fa1\u6155\u5979\u6765\uff0c\u54ea\u6015\u5f97\u5230\u4e00\u523b\u7684\u6170\u85c9\uff0c\u90fd\u80fd\u8ba9\u4eba\u7559\u604b\u7684\u56de\u5473\u8bb8\u4e45\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4eca\u5929\u662f\u5927\u8205\u529e\u4e54\u8fc1\u9152\uff0c\u897f\u57ce\u996d\u5e97\u6863\u6b21\u592a\u9ad8\u4e86\u4e00\u4e9b\uff0c\u6ca1\u5fc5\u8981\u592a\u5962\u4f88\u3001\u592a\u8bb2\u6392\u573a\uff0c\u6881\u519b\u5230\u6d77\u5dde\u4e5f\u8fd1\u4e09\u4e2a\u6708\u4e86\uff0c\u8ddf\u7740\u5c0f\u53d4\u5f20\u77e5\u975e\uff0c\u8ddf\u90b5\u81f3\u521a\u3001\u5434\u5929\u5b9d\u3001\u76db\u9752\u4e5f\u7b97\u8ba4\u8bc6\uff0c\u4e54\u8fc1\u9152\u5c31\u9009\u5728\u5434\u5929\u5b9d\u7684\u9152\u5e97\u91cc\u3002</p><p>\u3000\u3000\u8f66\u5b50\u9a76\u4e0a\u56db\u51e4\u6865\uff0c\u4eea\u8868\u76d8\u4e0a\u7684\u624b\u673a\u4fe1\u53f7\u706f\u95ea\u70c1\u8d77\u6765\uff0c\u6ca1\u7b49\u94c3\u58f0\u54cd\u8d77\u6765\uff0c\u5f20\u606a\u4f38\u624b\u6293\u8d77\u624b\u673a\uff0c\u4e00\u53ea\u624b\u628a\u7740\u65b9\u5411\u76d8\uff0c\u8ba9\u8f66\u6162\u6162\u7684\u6ed1\u4e0b\u56db\u51e4\u6865\uff0c\u62d0\u5165\u9910\u996e\u5e7f\u573a\u524d\u9762\u7684\u505c\u8f66\u573a\uff0c\u53f3\u624b\u62c7\u6307\u6309\u7740\u901a\u8bdd\u952e\uff0c\u5374\u542c\u89c1\u82b7\u5f64\u5976\u58f0\u5976\u6c14\u7684\u58f0\u97f3\uff1a\u201c\u662f\u5f20\u606a\u5728\u542c\u7535\u8bdd\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u662f\u5f20\u606a\u5728\u542c\u4f60\u7684\u7535\u8bdd\u554a\u2026\u2026\u201d\u5f20\u606a\u5634\u89d2\u542b\u7740\u6d45\u7b11\uff0c\u82b7\u5f64\u53ef\u4ece\u6765\u90fd\u4e0d\u4f1a\u5728\u4ed6\u7684\u540d\u5b57\u52a0\u540e\u7f00\uff0c\u8ba9\u60dc\u7fbd\u3001\u60dc\u5bb9\u4e24\u59d0\u59b9\u90fd\u5b66\u7740\u76f4\u547c\u4ed6\u7684\u540d\u5b57\u3002</p><p>\u3000\u3000\u201c\u5f20\u606a\uff0c\u660e\u5929\u966a\u6211\u8ddf\u5988\u5988\u53bb\u770b\u7535\u5f71\u597d\u4e0d\u597d\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u597d\u554a\uff01\u201d\u5f20\u606a\u60f3\u60f3\u660e\u5929\u4e5f\u6ca1\u6709\u7279\u522b\u8981\u505a\u7684\u4e8b\u60c5\uff0c\u8bf4\u8d77\u6765\uff0c\u300a\u72ee\u5b50\u738b\u300b\u540d\u5934\u867d\u7136\u5f88\u54cd\uff0c\u5f20\u606a\u5374\u4e00\u76f4\u6ca1\u6709\u63d0\u8d77\u5174\u8da3\u53bb\u770b\u8fc7\uff0c\u4f55\u51b5\u82b7\u5f64\u63d0\u51fa\u7684\u8981\u6c42\u54ea\u5bb9\u6613\u5bb9\u4f60\u8f7b\u6613\u62d2\u7edd\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u624b\u673a\u5939\u5728\u5934\u9888\u4e4b\u95f4\uff0c\u5c06\u8f66\u95e8\u5173\u597d\uff0c\u4e00\u8fb9\u8ddf\u82b7\u5f64\u8bf4\u8bdd\uff0c\u4e00\u8fb9\u5f80\u5434\u5929\u5b9d\u7684\u4e16\u7eaa\u5efa\u90ba\u9152\u5e97\u8d70\u8fc7\u53bb\uff0c\u65f6\u95f4\u8fd8\u6ca1\u6709\u5230\u4e5d\u70b9\uff0c\u4ed6\u966a\u9648\u4fe1\u751f\u3001\u5f20\u4e9a\u5e73\u3001\u827e\u9ed8\u4ed6\u4eec\u559d\u9152\uff0c\u90a3\u91cc\u6d45\u5c1d\u5219\u6b62\uff0c\u90fd\u4e0d\u662f\u55dc\u9152\u7684\u4eba\uff0c\u5c31\u7b97\u55dc\u9152\uff0c\u4e5f\u4e0d\u662f\u5728\u8fd9\u79cd\u573a\u5408\u8f7b\u6613\u7684\u8868\u73b0\u51fa\u6765\uff0c\u8be5\u804a\u7684\u8bdd\uff0c\u524d\u51e0\u5929\u5df2\u7ecf\u72e0\u72e0\u7684\u804a\u8fc7\u4e86\uff0c\u6240\u4ee5\u9152\u5bb4\u5f88\u65e9\u5c31\u7ed3\u675f\u4e86\u3002\u5927\u8205\u6881\u56fd\u5174\u529e\u4e54\u8fc1\u5bb4\u5219\u4e0d\u540c\uff0c\u4ed6\u521a\u5230\u52b3\u52a8\u5c40\u5de5\u4f5c\u624d\u534a\u4e2a\u6708\u4e0d\u5230\u7684\u65f6\u95f4\uff0c\u4e0e\u52b3\u52a8\u5c40\u7684\u5176\u4ed6\u5934\u5934\u8111\u8111\u6b63\u5904\u4e8e\u76f8\u4e92\u8bd5\u63a2\u7684\u9636\u6bb5\uff0c\u518d\u52a0\u4e0a\u673a\u5173\u996e\u9152\u7684\u98ce\u6c14\uff0c\u8fd9\u987f\u9152\u5bb4\u6ca1\u90a3\u4e48\u5bb9\u6613\u7ed3\u675f\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u624b\u673a\u88c5\u8fdb\u8863\u515c\u91cc\uff0c\u6b63\u770b\u89c1\u5927\u8205\u5728\u9152\u684c\u4e0a\u5927\u6740\u516b\u65b9\uff0c\u519b\u4eba\u7684\u9152\u91cf\u666e\u904d\u60ca\u4eba\uff0c\u5927\u8205\u4ed6\u4e5f\u4e0d\u4f8b\u5916\uff0c\u9762\u7ea2\u8033\u8d64\u3001\u9152\u6c14\u51b2\u5929\uff0c\u5374\u6ca1\u6709\u534a\u70b9\u9189\u6001\uff0c\u5f20\u606a\u8d81\u7740\u5927\u5bb6\u4e0d\u6ce8\u610f\uff0c\u6084\u6084\u7684\u8d70\u5230\u5988\u5988\u90a3\u4e00\u684c\u3002</p><p>\u3000\u3000\u201c\u4e0d\u662f\u8bf4\u8ba9\u4f60\u65e9\u70b9\u8fc7\u6765\u5417\uff0c\u600e\u4e48\u8fd8\u8fd9\u4e48\u665a\uff1f\u201d\u6881\u683c\u73cd\u55d4\u602a\u7684\u770b\u7740\u513f\u5b50\u3002</p><p>\u3000\u3000\u201c\u5475\u5475\u3002\u201d\u5f20\u606a\u563f\u7136\u7b11\u4e86\u7b11\uff0c\u6b20\u7740\u8eab\u5b50\u8ddf\u540c\u684c\u7684\u8205\u5988\u3001\u8868\u59d0\uff0c\u8fd8\u6709\u5b59\u4e3d\u6253\u62db\u547c\uff0c\u540c\u684c\u8fd8\u6709\u5176\u4ed6\u4e00\u4e9b\u8001\u4eb2\uff0c\u5f20\u606a\u90fd\u4e0d\u5927\u8ba4\u8bc6\uff0c\u53ea\u662f\u70b9\u5934\u6253\u62db\u547c\uff0c\u53c8\u8ddf\u65c1\u684c\u7684\u90b5\u81f3\u521a\u3001\u5434\u5929\u5b9d\u3001\u76db\u9752\u6253\u62db\u547c\uff0c\u8868\u54e5\u6881\u519b\u5728\u4ed6\u4eec\u4e00\u684c\uff0c\u800c\u5c0f\u53d4\u3001\u5927\u8205\u8ddf\u7238\u7238\u8ddf\u52b3\u52a8\u5c40\u5176\u4ed6\u6b63\u526f\u5c40\u957f\u4eec\u5750\u4e3b\u684c\u3002</p><p>\u3000\u3000\u201c\u5f20\u606a\uff0c\u5750\u8fc7\u6765\uff01\u201d\u6881\u56fd\u5174\u8fd9\u624d\u8f6c\u8eab\u770b\u89c1\u5f20\u606a\uff0c\u624b\u4e3e\u7740\u9152\u74f6\uff0c\u62db\u547c\u5f20\u606a\u5750\u8fc7\u53bb\uff0c\u201c\u5e2e\u8205\u8205\u6211\u559d\u676f\u9152\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u60f3\u518d\u559d\u9152\uff0c\u53ef\u662f\u6ca1\u6709\u529e\u6cd5\uff0c\u770b\u7740\u5c0f\u53d4\u5df2\u7ecf\u5e2e\u5fd9\u62ff\u8d77\u56db\u4e24\u88c5\u7684\u73bb\u7483\u91cc\u8ddf\u5b54\u5e9c\u5bb4\u9152\uff0c\u5f20\u606a\u771f\u60f3\u544a\u8bc9\u4ed6\u4eec\u5b54\u5e9c\u5bb4\u9152\u6bd4\u6d77\u5dde\u767d\u9152\u5382\u51fa\u7684\u94f6\u4ead\u9152\u8fd8\u96be\u559d\uff0c\u7eaf\u7cb9\u662f\u9760\u592e\u89c6\u6807\u738b\u7684\u5149\u73af\u786c\u6491\u8d77\u6765\u7684\u540d\u6c14\uff0c\u4e0d\u8fc7\u8fd9\u65f6\u5019\u8fd8\u4e0d\u80fd\u5c06\u592e\u89c6\u6807\u738b\u7684\u5149\u73af\u6233\u7834\uff0c\u8fd9\u4e0d\u662f\u81ea\u5df1\u6253\u81ea\u5df1\u7684\u8138\u5417\uff1f</p><p>\u3000\u3000\u5373\u4f7f\u4e0d\u77e5\u9053\u66f4\u591a\u7684\u4e8b\u60c5\uff0c\u5e02\u76f4\u5c40\u91cc\u7684\u5934\u5934\u8111\u8111\u90fd\u5f88\u5c11\u6709\u4e0d\u77e5\u9053\u5f20\u77e5\u884c\u8fd9\u4e2a\u513f\u5b50\u7684\uff0c\u4e0d\u4ec5\u4ec5\u6709\u4e4b\u524d\u7684\u8bb8\u591a\u4e8b\uff0c\u6559\u5e08\u7325\u4eb5\u6848\u8ba9\u4eca\u5e74\u5f02\u5e38\u5f3a\u52bf\u7684\u526f\u5e02\u957f\u4e07\u52c7\u9eef\u7136\u79bb\u5f00\u6d77\u5dde\uff0c\u5404\u5e02\u76f4\u5c40\u81ea\u7136\u4e5f\u4f1a\u5173\u6ce8\u8fd9\u4e9b\u4e8b\uff0c\u81ea\u7136\u4f1a\u5173\u6ce8\u5230\u675c\u5c0f\u5c71\u4e0e\u5f20\u77e5\u884c\u7684\u513f\u5b50\u3002\u4ed6\u4eec\u90fd\u6000\u7591\u8fd9\u6b21\u5b8b\u57f9\u660e\u517c\u4e0a\u5e02\u957f\u52a9\u7406\uff0c\u800c\u5f20\u77e5\u884c\u539f\u5730\u4e0d\u52a8\uff0c\u4e0e\u6b64\u4e8b\u4e0d\u65e0\u5173\u7cfb\uff0c\u5c31\u7b97\u80cc\u540e\u7ad9\u7740\u7701\u957f\u5f90\u5b66\u5e73\uff0c\u5728\u654f\u611f\u65f6\u523b\uff0c\u90fd\u662f\u9700\u8981\u907f\u5acc\u7684\u3002</p><p>\u3000\u3000\u5b8b\u57f9\u660e\u5df2\u7ecf\u662f\u57ce\u5357\u533a\u59d4\u4e66\u8bb0\uff0c\u5e02\u91cc\u9664\u4e86\u5e38\u59d4\uff0c\u5176\u4ed6\u4ec0\u4e48\u804c\u52a1\uff0c\u5bf9\u4ed6\u90fd\u4e0d\u4f1a\u6709\u592a\u5927\u7684\u5438\u5f15\u529b\uff0c\u4f46\u662f\u4ed6\u7684\u8d44\u5386\u8fd8\u6d45\u4e86\u4e00\u4e9b\uff0c\u5e02\u957f\u52a9\u7406\u867d\u7136\u770b\u8d77\u6765\u662f\u5e02\u91cc\u6700\u65e0\u8db3\u8f7b\u91cd\u7684\u526f\u5385\uff0c\u5374\u53ef\u4ee5\u5e2e\u4ed6\u8f7b\u8f7b\u7684\u8fc8\u8fc7\u4ed5\u9014\u4e0a\u5347\u9014\u4e2d\u7684\u4e00\u4e2a\u574e\uff0c\u8fd9\u6837\u5b8b\u57f9\u660e\u5c31\u53ef\u4ee5\u7ee7\u7eed\u5728\u4ed6\u7684\u57ce\u5357\u533a\u59d4\u4e66\u8bb0\u4e0a\u7ee7\u7eed\u5e72\u7740\uff0c\u518d\u79ef\u7d2f\u4e00\u4e9b\u653f\u7ee9\u5c31\u53ef\u4ee5\u8fdb\u5e38\u59d4\u4e86\u5427\uff1f</p><p>\u3000\u3000\u8bb8\u591a\u4eba\u90fd\u8ba4\u4e3a\u5b8b\u57f9\u660e\u8fd9\u6b21\u5403\u4e86\u72d7\u5c4e\u5c41\uff0c\u592e\u89c6\u6807\u738b\u4f01\u4e1a\u521a\u597d\u51fa\u5728\u4ed6\u7684\u57ce\u5357\u533a\uff0c\u8ba9\u5b8b\u57f9\u660e\u51fa\u591f\u4e86\u98ce\u5934\uff0c\u4ee5\u81f4\u5510\u5b66\u8c26\u63d0\u8bae\u4ed6\u517c\u4efb\u5e02\u957f\u52a9\u7406\uff0c\u5e38\u59d4\u90fd\u4e00\u81f4\u901a\u8fc7\u4e0a\u62a5\u7701\u91cc\u3002\u7701\u7ec4\u7ec7\u90e8\u4e5f\u8d70\u5f62\u52bf\u7684\u8fc7\u6765\u8003\u5bdf\u4e86\u4e00\u4e0b\uff0c\u4efb\u547d\u5f88\u5feb\u5c31\u4e0b\u53d1\u4e86\u3002\u8fd9\u4e9b\u4eba\u611f\u6168\u5b8b\u57f9\u660e\u7684\u597d\u8fd0\u6c14\u65f6\uff0c\u5374\u6ca1\u6709\u60f3\u5230\u6ca1\u6709\u5b8b\u57f9\u660e\u62ff\u81ea\u5df1\u7684\u653f\u6cbb\u524d\u9014\u5192\u9669\u3001\u575a\u5b9a\u4e0d\u79fb\u7684\u652f\u6301\uff0c\u7231\u8fbe\u7535\u5b50\u5f88\u96be\u8fd9\u4e48\u987a\u5229\u7684\u8d70\u5230\u8fd9\u4e00\u6b65\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u5927\u5bb6\u90fd\u4ee5\u4e3a\u5f20\u77e5\u884c\u4f1a\u5bf9\u5b8b\u57f9\u660e\u5f53\u4e0a\u5e02\u957f\u52a9\u7406\u7684\u4e8b\u4e0d\u6109\u5feb\uff0c\u81ea\u7136\u4e0d\u4f1a\u4e0d\u8ba8\u597d\u7684\u5728\u9152\u684c\u4e0a\u63d0\u8d77\u8fd9\u4e8b\uff0c\u5f20\u77e5\u884c\u5728\u4ed5\u9014\u5c0f\u505c\u987f\u4e00\u4e0b\uff0c\u4e5f\u662f\u6b63\u5e38\u7684\uff0c\u6bd5\u7adf\u5f20\u77e5\u884c\u63d0\u4e0a\u6b63\u5904\u624d\u4e00\u5e74\u591a\u7684\u65f6\u95f4\uff0c\u80cc\u540e\u7ad9\u7740\u7701\u957f\u5f90\u5b66\u5e73\uff0c\u8fd8\u7528\u62c5\u5fc3\u524d\u9014\u5417\uff1f\u66f4\u4f55\u51b5\u624b\u91cc\u63e1\u7740\u6d77\u5dde\u63a7\u80a1\u8fd9\u4e48\u5927\u7684\u516c\u53f8\uff0c\u5bfb\u5e38\u4eba\u7684\u4eba\u751f\u5230\u4e86\u8fd9\u4e00\u6b65\u5df2\u7ecf\u4e0d\u5e94\u8be5\u518d\u6709\u9057\u61be\u4e86\u3002</p><p>\u3000\u3000\u52b3\u52a8\u5c40\u7684\u5934\u5934\u8111\u8111\u60f3\u627e\u4e9b\u8bdd\u5938\u5938\u5f20\u606a\u501f\u673a\u8ba8\u597d\u5f20\u77e5\u884c\uff0c\u5374\u53d1\u73b0\u627e\u4e0d\u5230\u5938\u4ed6\u7684\u8bdd\uff0c\u4e5f\u6ca1\u6709\u4eba\u4f1a\u50bb\u4e4e\u4e4e\u95ee\u5f20\u606a\u7684\u5b66\u4e60\u6210\u7ee9\uff0c\u5012\u662f\u5f20\u606a\u8981\u7ed9\u5927\u8205\u7684\u9762\u5b50\uff0c\u6328\u4e2a\u656c\u52b3\u52a8\u5c40\u7684\u9886\u5bfc\uff0c\u8ba9\u6c14\u6c1b\u4e0d\u81f3\u4e8e\u51b7\u843d\u4e0b\u6765\u3002</p><p>\u3000\u3000\u76db\u9752\u5728\u65c1\u684c\u770b\u4e86\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8ba9\u606a\u5c11\u53bb\u5e94\u4ed8\u52b3\u52a8\u5c40\u8fd9\u4e9b\u5bb6\u4f19\uff0c\u771f\u662f\u59d4\u5c48\u4ed6\u4e86\u2026\u2026\u201d\u62cd\u4e86\u62cd\u90b5\u81f3\u521a\u3001\u5434\u5929\u5b9d\u7684\u80a9\u8180\uff0c\u8bf4\u9053\uff0c\u201c\u8d70\uff0c\u628a\u52b3\u52a8\u5c40\u7684\u8fd9\u4e9b\u5bb6\u4f19\u5e72\u7ffb\u6389\uff0c\u514d\u5f97\u4ed6\u4eec\u6765\u4e86\u5174\u81f4\u529d\u606a\u5c11\u559d\u592a\u591a\u9152\u3002\u201d</p><p>\u3000\u3000\u6881\u519b\u5728\u65c1\u8fb9\u542c\u4e86\u5947\u602a\uff0c\u8fd8\u5f53\u76db\u9752\u73a9\u7b11\uff0c\u5374\u89c1\u90b5\u81f3\u521a\u3001\u5434\u5929\u5b9d\u542c\u4e86\u771f\u7ad9\u8d77\u6765\uff0c\u7aef\u7740\u9152\u676f\u8d70\u8fc7\u53bb\u3002</p></article>"}}}]);
