(window.webpackJsonp=window.webpackJsonp||[]).push([[395],{418:function(p){p.exports={chapterTitle:null,title:"\u7b2c267\u7ae0",content:"<article><h2>\u7b2c267\u7ae0</h2><p>\u3000\u300012\u67087\u65e5\uff0c\u7231\u8fbe\u7535\u5b50\u7ecf\u9500\u5546\u5927\u4f1a\u7684\u6700\u540e\u4e00\u5929\uff0c\u9646\u6ea2\u5149\u526f\u4e66\u8bb0\u4e0e\u65b9\u5b8f\u6e05\u526f\u5e02\u957f\u4ee3\u8868\u5e02\u91cc\u5bb4\u8bf7\u6700\u7ec8\u83b7\u5f97\u7231\u8fbe\u7535\u5b50\u533a\u57df\u4ee3\u7406\u6743\u7684\u8fd1\u767e\u5bb6\u7ecf\u9500\u5355\u4f4d\u7684\u4ee3\u8868\uff0c\u5728\u6587\u5c71\u996d\u5e972\u53f7\u697c\u6446\u4e86\u4e09\u5341\u684c\uff0c\u6d77\u5dde\u5e02\u7535\u89c6\u673a\u5382\u3001\u6d77\u5dde\u5e02\u6c34\u4ed9\u6d17\u8863\u673a\u5382\u7684\u9886\u5bfc\u4e5f\u8ddf\u7740\u9646\u6ea2\u5149\u3001\u65b9\u5b8f\u6e05\u6df7\u8ff9\u5176\u4e2d\uff0c\u751a\u81f3\u5b89\u6392\u4ed6\u4eec\u5728\u5bb4\u5e2d\u5f00\u59cb\u4e4b\u524d\u53d1\u8a00\uff0c\u5bf9\u4e8e\u5e02\u91cc\u7684\u5939\u79c1\u884c\u4e3a\uff0c\u5468\u6e38\u4e5f\u9887\u4e3a\u65e0\u5948\uff0c\u5c31\u7b97\u662f\u7ed9\u6d77\u5dde\u7ecf\u6d4e\u53d1\u5c55\u505a\u8d21\u732e\u5427\u3002</p><p>\u3000\u3000\u5728\u6587\u5c71\u996d\u5e97\u76848\u53f7\u697c\uff0c\u5e02\u59d4\u4e66\u8bb0\u5468\u5bcc\u660e\u3001\u5e02\u59d4\u79d8\u4e66\u957f\u5f20\u6653\u5065\u5355\u72ec\u5bb4\u8bf7\u9648\u4fe1\u751f\u3001\u5f20\u4e9a\u5e73\u3001\u827e\u9ed8\u53ca\u4ed6\u4eec\u7684\u968f\u884c\u5de5\u4f5c\u4eba\u5458\uff0c\u5e2d\u95f4\u8868\u793a\u6b22\u8fceTI\u3001\u98de\u5229\u6d66\u3001\u65af\u9ad8\u67cf\u8fd9\u4e9b\u6d77\u5916\u5236\u9020\u5de8\u5934\u5230\u6d77\u5dde\u6765\u6295\u8d44\uff0c\u7231\u8fbe\u7535\u5b50\u7531\u82cf\u6d25\u4e1c\u966a\u540c\uff0c\u53e6\u5916\u8fd8\u6709\u5e02\u62db\u5546\u65c5\u6e38\u5c40\u7684\u9886\u5bfc\u3002\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u7531\u5e02\u957f\u5510\u5b66\u8c26\u51fa\u9762\u6216\u8bb8\u66f4\u6070\u5f53\u4e00\u4e9b\uff0c\u5468\u5bcc\u660e\u5982\u6b64\u79ef\u6781\u4e3b\u52a8\uff0c\u8ba9\u9648\u4fe1\u751f\u3001\u5f20\u4e9a\u5e73\u3001\u827e\u9ed8\u7b49\u4eba\u9519\u4ee5\u4e3a\u5468\u5bcc\u660e\u662f\u4f4d\u76f8\u5f53\u6709\u653f\u6cbb\u62b1\u8d1f\u7684\u5e02\u59d4\u4e66\u8bb0\u3002</p><p>\u3000\u3000\u76db\u9752\u3001\u90b5\u81f3\u521a\u4ed6\u4eec\u5f53\u673a\u7acb\u65ad\u7684\u63a5\u53d7\u5f20\u606a\u7684\u610f\u89c1\uff0c\u5c06\u4e4b\u524d\u76f4\u63a5\u5c31\u8868\u793a\u5bf9\u751f\u4ea7\u76d7\u7248\u789f\u66f4\u611f\u5174\u8da3\u7684\u7ecf\u9500\u5546\u4eec\uff0c\u7528\u4e00\u8f86\u5927\u5ba2\u8f66\u63a5\u5230\u4e1c\u793e\u7684\u5de5\u5382\u91cc\u53c2\u89c2\uff0c\u8868\u793a\u53ef\u4ee5\u63d0\u4f9b\u673a\u5668\u3001\u6210\u5957\u7684\u6280\u672f\u4ee5\u53ca\u590d\u5236\u5149\u789f\u6240\u9700\u7684\u6750\u6599\u4e0e\u6bcd\u76d8\u3002</p><p>\u3000\u3000\u53ea\u6709\u5f62\u6210\u4e00\u5b9a\u7684\u4ea7\u4e1a\u89c4\u6a21\uff0c\u624d\u8db3\u4ee5\u652f\u6491\u904d\u53ca\u5168\u56fd\u7684\u9500\u552e\u7f51\u7edc\uff0c\u4ed6\u4eec\u624d\u80fd\u7ad9\u5728\u6574\u4e2a\u4ea7\u4e1a\u94fe\u7684\u9876\u7aef\u7b11\u50b2\u98ce\u4e91\u3002</p><p>\u3000\u3000\u5f53\u65f6VCD\u76d7\u7248\u789f\u7247\u7684\u751f\u4ea7\u8bbe\u5907\uff0c\u5927\u591a\u662f\u6d77\u5916\u5236\u9020\u5546\u6dd8\u6c70\u4e0b\u6765\u7684CD\u3001LD\u789f\u7247\u751f\u4ea7\u7ebf\u7a0d\u52a0\u6539\u88c5\uff0c\u76db\u9752\u3001\u90b5\u81f3\u521a\u4ece\u6700\u521d\u7684\u56db\u53f0\u9010\u6e10\u589e\u52a0\u5230\u4e8c\u5341\u56db\u53f0\uff0c\u53c8\u6253\u7b97\u65b0\u8d2d\u4e8c\u5341\u591a\u53f0\u5efa\u4e00\u5bb6\u5206\u5382\uff0c\u5bf9\u5176\u4e2d\u7684\u95e8\u8def\u5df2\u7ecf\u638c\u63e1\u5f97\u95e8\u6e05\uff0c\u539f\u6765\u7684\u8ba1\u5212\u53d6\u6d88\uff0c\u4ec5\u5c06\u8fd9\u6279\u8bbe\u5907\u8f6c\u624b\u5c31\u80fd\u8ba9\u4ed6\u4eec\u8d5a\u4e00\u5927\u7b14\u3002</p><p>\u3000\u3000\u5f53\u65f6\u7684\u5730\u65b9\u653f\u5e9c\uff0c\u5730\u65b9\u4fdd\u62a4\u4e3b\u4e49\u8272\u5f69\u6d53\u91cd\u4e00\u4e9b\u7684\uff0c\u751a\u81f3\u5bf9\u6b64\u7c7b\u4f01\u4e1a\u91c7\u53d6\u7eb5\u5bb9\u7684\u6001\u5ea6\uff0c\u6210\u7acb\u4e00\u5bb6\u6709\u4e24\u5230\u56db\u53f0\u538b\u789f\u673a\u89c4\u6a21\u7684\u5de5\u5382\uff0c\u770b\u4e0a\u53bb\u4f3c\u4e4e\u4e0d\u8981\u627f\u62c5\u4e00\u70b9\u98ce\u9669\uff0c\u5f53\u65f6\u5c31\u6709\u8bb8\u591a\u4eba\u63d0\u51fa\u5411\u4ed6\u4eec\u8ba2\u8d2d\u8bbe\u5907\u3001\u8d2d\u4e70\u751f\u4ea7\u6280\u672f\u3002\u6709\u4e9b\u4eba\u613f\u610f\u5c06\u5382\u5b50\u5efa\u5728\u4e1c\u793e\u53bf\u6216\u9644\u8fd1\uff0c\u66f4\u591a\u7684\u4eba\u5e0c\u671b\u5c06\u5382\u5b50\u5efa\u5230\u81ea\u5df1\u7684\u5730\u76d8\u4e0a\uff0c\u770b\u6765\u4e0d\u9700\u8981\u591a\u4e45\uff0c\u4e00\u5f20\u5168\u56fd\u6027\u7684\u76d7\u7248\u789f\u751f\u4ea7\u3001\u9500\u552e\u7f51\u7edc\u5c31\u4f1a\u5f62\u6210\u2026\u2026</p><p>\u3000\u3000\u663e\u7136\uff0c\u5f20\u606a\u7684\u610f\u89c1\u66f4\u52a0\u52a1\u5b9e\u3002</p><p>\u3000\u3000\u770b\u7740\u5927\u5ba2\u8f66\u5c06\u5ba2\u4eba\u9001\u8d70\uff0c\u5434\u5929\u5b9d\u8ddf\u7740\u5750\u8f66\u5148\u56de\u5e02\u91cc\uff0c\u76db\u9752\u3001\u90b5\u81f3\u521a\u3001\u5f20\u77e5\u975e\u4e09\u4eba\u6ca1\u6709\u7acb\u5373\u8ddf\u7740\u56de\u5e02\u91cc\uff0c\u4e5f\u6ca1\u6709\u56de\u5382\u5b50\u91cc\u53bb\uff0c\u5c31\u7ad9\u5728\u5382\u5b50\u5916\u7684\u704c\u6e89\u6e20\u4e0a\u62bd\u70df\uff0c\u56db\u5468\u90fd\u662f\u7eff\u6cb9\u6cb9\u7684\u5c0f\u9ea6\u5730\uff0c\u5c31\u4ed6\u4eec\u4e00\u5bb6\u5de5\u5382\u600e\u4e48\u770b\u600e\u4e48\u7a81\u5140\u3002</p><p>\u3000\u3000\u76ee\u524d\u66ff\u4ed6\u4eec\u8d1f\u8d23\u8fd9\u5bb6\u5de5\u5382\u7684\u5382\u957f\u8fd8\u662f\u5f20\u606a\u4ecb\u7ecd\u7684\uff0c\u662f\u539f\u58f0\u8fbe\u7535\u5b50\u5382\u7684\u5382\u957f\u9648\u5f3a\uff0c\u9648\u5f3a\u672c\u4eba\u5e76\u4e0d\u6653\u5f97\u4ed6\u662f\u7ed9\u5f20\u606a\u5356\u5230\u8fd9\u91cc\u6765\u7684\uff0c\u53ea\u662f\u4ed6\u7ed9\u76ca\u9686\u9547\u653f\u5e9c\u9001\u5230\u9547\u7ecf\u6d4e\u7ad9\u91cc\u517b\u8001\uff0c\u54ea\u6015\u4e00\u4e2a\u526f\u7ad9\u957f\u7684\u804c\u52a1\u90fd\u6ca1\u6709\u7ed9\u4ed6\uff0c\u60f3\u60f3\u8fd9\u4e9b\u5e74\u6765\uff0c\u4ed6\u5728\u58f0\u8fbe\u7535\u5b50\u5382\u5192\u724c\u751f\u4ea7\u71d5\u821e\u7684\u6536\u5f55\u673a\uff0c\u8fd8\u4e0d\u662f\u60f3\u5c06\u5382\u5b50\u7684\u6548\u76ca\u641e\u8d77\u6765\uff1f\u5382\u5b50\u6709\u6548\u76ca\u65f6\uff0c\u9547\u91cc\u4eba\u90fd\u8fc7\u6765\u5403\u559d\u73a9\u4e50\uff0c\u51fa\u4e86\u95ee\u9898\uff0c\u5374\u8981\u4ed6\u4e00\u4eba\u515c\u7740\uff0c\u4ed6\u6ee1\u809a\u5b50\u7684\u6028\u6c14\uff0c\u76db\u9752\u8fc7\u6765\u8bf7\u4ed6\uff0c\u4ed6\u90fd\u6ca1\u6709\u72b9\u8c6b\uff0c\u5c31\u5728\u9547\u91cc\u529e\u4e86\u505c\u85aa\u7559\u804c\uff0c\u8ddf\u76db\u9752\u3001\u90b5\u81f3\u521a\u5230\u4e1c\u793e\u53bf\u4e61\u4e0b\u4e70\u4e86\u51e0\u95f4\u79bb\u516c\u8def\u4e0d\u662f\u592a\u8fdc\uff0c\u4f46\u662f\u7ad9\u5728\u516c\u8def\u4e0a\u53c8\u770b\u4e0d\u5230\u7684\u4ed3\u5e93\u4f5c\u5382\u623f\u641e\u8d77\u76d7\u7248\u789f\u751f\u4ea7\u3002</p><p>\u3000\u3000\u4ed6\u4eec\u8d76\u5bf9\u4e86\u65f6\u673a\uff0c\u673a\u5668\u5230\u5bb6\uff0c\u5e02\u9762\u4e0a\u5bf9\u76d7\u7248\u789f\u7684\u9700\u6c42\u5c31\u6781\u5ea6\u7684\u65fa\u76db\uff0c\u9664\u4e86\u76db\u9752\u3001\u90b5\u81f3\u521a\u3001\u5f20\u77e5\u975e\u9646\u7eed\u6295\u5165\u7684\u4e09\u767e\u4e07\uff0c\u76ee\u524d\u4e8c\u5341\u56db\u53f0\u673a\u5b50\uff0c\u76f8\u5f53\u4e00\u90e8\u5206\u90fd\u662f\u62ff\u8d5a\u7684\u94b1\u4e70\u7684\uff0c\u6240\u4ee5\u4ed6\u4eec\u4e4b\u524d\u624d\u8feb\u4e0d\u53ca\u5f85\u7684\u60f3\u7b79\u94b1\u518d\u8fdb\u4e00\u6279\u8bbe\u5907\u3002</p><p>\u3000\u3000\u5728\u5de5\u5382\u4e0e\u516c\u8def\u4e4b\u95f4\uff0c\u683d\u7740\u51e0\u6392\u9ad8\u5927\u7684\u6c34\u6749\u6811\uff0c\u6321\u4f4f\u516c\u8def\u671b\u5411\u5de5\u5382\u7684\u89c6\u91ce\u3002\u76db\u9752\u8e72\u7740\u811a\u6709\u4e9b\u53d1\u9ebb\uff0c\u7ad9\u8d77\u6765\u8e22\u8e22\u811a\uff0c\u8bf4\u9053\uff1a\u201c\u90b5\u54e5\u8bf4\u2018\u5c0f\u5bcc\u52e4\u52b3\u51fa\u3001\u5927\u5bcc\u5929\u6210\u5c31\u2019\uff0c\u73b0\u5728\u60f3\u60f3\u8fd8\u771f\u662f\u90a3\u56de\u4e8b\u3002\u6211\u4eec\u6295\u4e00\u5343\u4e07\u4e0b\u53bb\uff0c\u6765\u5e74\u80fd\u8d5a\u4e00\u500d\uff0c\u773c\u775b\u90fd\u5feb\u5192\u7eff\u5149\u4e86\uff0c\u5f20\u606a\u8fd9\u5c0f\u5b50\u968f\u4fbf\u63d0\u70b9\u4e00\u4e0b\uff0c\u8fd9\u6c34\u51c6\u63d0\u9ad8\u7684\u4e0d\u662f\u4e00\u70b9\u534a\u70b9\uff0c\u4f60\u8bf4\u4eba\u600e\u4e48\u5c31\u8fd9\u4e48\u5927\u7684\u5dee\u8ddd\u5462\uff1f\u8d5a\u94b1\u8fd8\u662f\u5176\u6b21\uff0c\u5173\u952e\u662f\u6863\u6b21\u4e0e\u6c5f\u6e56\u5730\u4f4d\u7684\u95ee\u9898\u2026\u2026\u201d\u62ff\u811a\u8e22\u4e86\u8e22\u5f20\u77e5\u975e\uff0c\u95ee\u4ed6\uff0c\u201c\u4f60\u4eec\u5f20\u5bb6\u600e\u4e48\u5c31\u751f\u4e86\u4e2a\u8fd9\u4e48\u4f1a\u8d5a\u94b1\u7684\u4e3b\uff1f\u201d</p><p>\u3000\u3000\u5f20\u77e5\u975e\u62ff\u70df\u53bb\u70eb\u76db\u9752\u8e22\u6765\u7684\u817f\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6709\u94b1\u8d5a\uff0c\u4f60\u8fd8\u5431\u5431\u6b6a\u6b6a\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u7b11\u7740\u62bd\u70df\uff0c\u5f20\u606a\u7684\u773c\u754c\uff0c\u603b\u8981\u597d\u597d\u7684\u60f3\u4e00\u60f3\uff0c\u624d\u80fd\u4f53\u5473\u5230\u5999\u5904\u3002\u5c06\u76d7\u7248\u789f\u7684\u751f\u4ea7\u5206\u6563\u5230\u6574\u4e2a\u7f51\u7edc\u91cc\uff0c\u4e0d\u4ec5\u5c06\u6cd5\u5f8b\u7684\u98ce\u9669\u89e3\u6563\u6389\uff0c\u8fd8\u80fd\u8ba9\u6574\u4e2a\u6e20\u9053\u66f4\u52a0\u7684\u7a33\u5b9a\uff0c\u4ed6\u4eec\u53ea\u9700\u6293\u4f4f\u8fd9\u4e2a\u7070\u8272\u4ea7\u4e1a\u94fe\u6700\u9876\u7aef\u7684\u90a3\u4e00\u73af\u5c31\u8db3\u591f\u4e86\u3002\u90b5\u81f3\u521a\u5c06\u70df\u5934\u637b\u706d\uff0c\u8bf4\u9053\uff1a\u201c\u6682\u65f6\u6211\u4eec\u53ef\u4ee5\u5230\u9999\u6e2f\u76f4\u63a5\u62ff\u6bcd\u76d8\u4f9b\u5e94\u7ed9\u4e0b\u9762\u7684\u5de5\u5382\uff0c\u4f46\u4e0d\u662f\u957f\u4e45\u4e4b\u8ba1\uff0c\u6211\u4eec\u4e0d\u80fd\u63a7\u5236\u8fd9\u6761\u8def\u5b50\uff0c\u5c31\u7b97\u4e0b\u9762\u7684\u5de5\u5382\u89c4\u6a21\u4e0d\u8db3\u4ee5\u76f4\u63a5\u5411\u9999\u6e2f\u62ff\u6bcd\u76d8\uff0c\u6df1\u5733\u3001\u5e7f\u5dde\u4e00\u5e26\u7684\u76d7\u7248\u5546\u4e5f\u4f1a\u8df3\u51fa\u6765\u8ddf\u6211\u4eec\u4e89\u4e0b\u9762\u7684\u5de5\u5382\uff0c\u6211\u4eec\u53ea\u6709\u638c\u63e1\u8db3\u591f\u591a\u7684\u8d44\u6e90\uff0c\u8d28\u91cf\u53c8\u8db3\u591f\u7684\u7a33\u5b9a\uff0c\u4ef7\u94b1\u53c8\u8db3\u591f\u7684\u516c\u9053\uff0c\u624d\u80fd\u957f\u4e45\u9738\u5360\u6574\u4e2a\u6e20\u9053\u3002\u7231\u8fbe\u7535\u5b50\u8981\u662f\u80fd\u6bcf\u5e74\u53ec\u5f00\u4e00\u6b21\u7ecf\u9500\u5546\u5927\u4f1a\uff0c\u90fd\u662f\u6211\u4eec\u6269\u5f20\u8fd9\u4e2a\u6e20\u9053\u7684\u597d\u65f6\u673a\uff0c\u6240\u4ee5\uff0c\u6211\u4eec\u524d\u671f\u7684\u8d77\u70b9\u4e00\u5b9a\u8981\u9ad8\uff0c\u5149\u76d8\u5382\u53ef\u4ee5\u8bbe\u5728\u6d77\u5dde\uff0c\u6bcd\u76d8\u5236\u4f5c\u7684\u516c\u53f8\uff0c\u4e00\u5b9a\u8981\u653e\u5728\u6df1\u5733\uff0c\u6216\u8005\u76f4\u63a5\u653e\u5728\u9999\u6e2f\u2026\u2026\u201d\u5b9a\u775b\u770b\u7740\u5f20\u77e5\u975e\uff0c\u201c\u5f20\u606a\u5e38\u53bb\u9999\u6e2f\uff0c\u80fd\u4e0d\u80fd\u8bf7\u4ed6\u5728\u9999\u6e2f\u6216\u8005\u6df1\u5733\u7ed9\u6211\u4eec\u4e00\u4e9b\u7167\u5e94\uff1f\u201d</p><p>\u3000\u3000\u76db\u9752\u4ed6\u4eec\u63a5\u4e0b\u6765\u8feb\u5207\u8981\u89e3\u51b3\u7684\u95ee\u9898\u5c31\u662f\u638c\u63e1\u6bcd\u76d8\u5236\u4f5c\u7684\u6280\u672f\u5e76\u53ca\u65f6\u83b7\u53d6\u5927\u91cf\u7684\u5236\u4f5c\u7d20\u6750\uff0c\u53cd\u800c\u6210\u7acb\u4e00\u5bb6\u5149\u76d8\u5382\u76f8\u5bf9\u7b80\u5355\u8bb8\u591a\u3002</p><p>\u3000\u3000\u6bcd\u76d8\u5236\u4f5c\u7684\u6280\u672f\u53ef\u4ee5\u4ece\u7231\u8fbe\u7535\u5b50\u90a3\u91cc\u62ff\uff0c\u4f46\u662f\u5236\u4f5c\u7d20\u6750\uff0c\u9700\u8981\u4e0e\u5168\u7403\u97f3\u50cf\u4fe1\u606f\u540c\u6b65\uff0c\u5bf9\u56fd\u5185\u6765\u8bf4\uff0c\u9999\u6e2f\u6216\u8005\u6df1\u5733\uff0c\u65e0\u7591\u662f\u6700\u80fd\u53ca\u65f6\u83b7\u5f97\u5168\u7403\u97f3\u50cf\u4fe1\u606f\u7684\u5730\u65b9\u3002</p><p>\u3000\u3000\u5f20\u77e5\u975e\u7b11\u7740\u8bf4\uff1a\u201c\u4ed6\u4e5f\u4e0d\u5e38\u53bb\uff0c\u8fd9\u4e8b\uff0c\u6211\u8ddf\u4ed6\u8bf4\u8bf4\u2026\u2026\u201d\u5f20\u77e5\u975e\u77e5\u9053\u8bb8\u601d\u5728\u9999\u6e2f\uff0c\u4f46\u5f20\u606a\u5177\u4f53\u5728\u9999\u6e2f\u6709\u4ec0\u4e48\u52a8\u4f5c\uff0c\u4ed6\u6ca1\u6709\u7ec6\u95ee\uff0c\u4e5f\u4e0d\u65b9\u4fbf\u8ddf\u90b5\u81f3\u521a\u3001\u76db\u9752\u4ed6\u4eec\u8bf4\u8d77\u6765\uff0c\u53c8\u95ee\u9053\uff0c\u201c\u5c31\u7b97\u5728\u6df1\u5733\u6216\u9999\u6e2f\u6210\u7acb\u516c\u53f8\uff0c\u6211\u4eec\u6d3e\u8c01\u53bb\u8d1f\u8d23\uff1f\u201d\u4ed6\u4eec\u4e09\u4eba\u5728\u6d77\u5dde\u5404\u6709\u4e00\u644a\u5b50\u4e8b\uff0c\u800c\u4e14\u90fd\u662f\u4ed6\u4eec\u7684\u4e3b\u4e1a\uff0c\u4e0d\u80fd\u968f\u968f\u4fbf\u4fbf\u5c31\u4e22\u4e0b\u6765\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u8bf4\u9053\uff1a\u201c\u6881\u519b\u867d\u7136\u521a\u51fa\u6765\u505a\u4e8b\uff0c\u4f46\u662f\u86ee\u7075\u5149\u7684\uff0c\u5148\u8ba9\u4ed6\u8bd5\u8bd5\u770b\uff0c\u4e0d\u884c\u7684\u8bdd\uff0c\u6211\u4eec\u4e09\u4e2a\u6293\u9604\u2026\u2026\u201d</p><p>\u3000\u3000\u76db\u9752\u563f\u7136\u4e00\u7b11\uff0c\u5f20\u606a\u8981\u77e5\u9053\u4ed6\u4eec\u4e09\u4e2a\u8fd9\u65f6\u5019\u60f3\u7740\u5c06\u6881\u519b\u62d6\u4e0b\u6c34\uff0c\u6253\u6b7b\u90fd\u4e0d\u4f1a\u5e2e\u4ed6\u4eec\u51fa\u4e3b\u610f\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5916\u9762\u8fd8\u5728\u4e3a\u7ecf\u9500\u5546\u5927\u4f1a\u5fd9\u5f97\u56e2\u56e2\u8f6c\uff0c\u5bf9\u5f20\u606a\u6765\u8bf4\uff0c\u4ed6\u7684\u4e8b\u60c5\u5df2\u7ecf\u7ed3\u675f\u6389\u4e86\uff0c\u4ed6\u4e0a\u5348\u5728\u9526\u6e56\u4ed6\u7684\u529e\u516c\u5ba4\u91cc\u5750\u4e86\u534a\u5929\uff0c\u4e0b\u5348\u5403\u8fc7\u996d\uff0c\u5c31\u966a\u82b7\u5f64\u5979\u4eec\u53bb\u770b\u7535\u5f71\uff0c\u6628\u5929\u591c\u91cc\u5728\u7535\u8bdd\u91cc\u7b54\u5e94\u82b7\u5f64\u7684\u3002\u7531\u4e8e\u63d0\u524d\u674e\u660e\u745c\u53ea\u4e70\u4e86\u4e94\u5f20\u7968\uff0c\u8ba1\u5212\u662f\u5979\u8ddf\u4e00\u540d\u4fdd\u59c6\u9886\u7740\u4e09\u4e2a\u5c0f\u5b69\u5b50\u6765\u770b\u7684\uff0c\u8c22\u665a\u6674\u6709\u7a7a\u95f2\u90fd\u5f88\u610f\u5916\uff0c\u6ca1\u60f3\u5230\u5f20\u606a\u62c9\u7740\u4ed6\u4e08\u592b\u5085\u4fca\u4e5f\u6765\u770b\uff0c\u53ea\u5f97\u53e6\u5916\u8865\u4e86\u4e09\u5f20\u7968\u3002</p><p>\u3000\u3000\u82b7\u5f64\u72b9\u8c6b\u7684\u534a\u5929\uff0c\u5979\u5f88\u60f3\u5979\u8ddf\u60dc\u7fbd\u3001\u60dc\u5bb9\u5750\u4e00\u8d77\uff0c\u4e5f\u5e0c\u671b\u8ddf\u5f20\u606a\u3001\u5988\u5988\u5750\u4e00\u8d77\uff0c\u4f46\u662f\u5f20\u606a\u4e0d\u6562\u8ba9\u4e09\u4e2a\u4e2b\u5934\u7247\u5b50\u6324\u4e00\u8d77\uff0c\u90a3\u7535\u5f71\u6839\u672c\u5c31\u6ca1\u529e\u6cd5\u770b\uff0c\u8fd8\u5f88\u60f9\u4eba\u5acc\uff0c\u62c9\u7740\u82b7\u5f64\u4e0e\u665a\u6674\u5750\u540e\u6392\uff0c\u8ba9\u5085\u4fca\u4e00\u5bb6\u56db\u53e3\u8ddf\u4fdd\u59c6\u5750\u524d\u9762\uff0c\u8fd9\u6837\u5b89\u6392\u81f3\u5c11\u80fd\u5b89\u5b89\u9759\u9759\u7684\u770b\u4e00\u573a\u7535\u5f71\uff0c\u8981\u95f9\uff0c\u8fd8\u662f\u7b49\u7740\u770b\u5b8c\u7535\u5f71\u53bb\u5403\u80af\u5fb7\u57fa\u518d\u8bf4\u5427\u3002</p><p>\u3000\u3000\u5f20\u606a\u672c\u60f3\u62c9\u5510\u5a67\u4e00\u8d77\u6765\u770b\u7684\uff0c\u4f46\u662f\u4e00\u4e2d\u6574\u987f\u5b66\u98ce\u4ee5\u6765\uff0c\u5bf9\u5b66\u751f\u7684\u7ba1\u7406\u66f4\u52a0\u4e25\u683c\u4e86\uff0c\u9ad8\u4e00\u3001\u9ad8\u4e8c\u53ea\u6709\u661f\u671f\u5929\u4e0b\u5348\u4f11\u606f\u534a\u5929\uff0c\u6bcf\u4e2a\u6708\u624d\u6709\u4e00\u6b21\u4e24\u5929\u7684\u5927\u4f11\uff0c\u8ba9\u5bc4\u5bbf\u751f\u56de\u8d9f\u5bb6\u3002\u5f20\u606a\u60f3\u9f13\u52a8\u5510\u5a67\u9003\u8bfe\u6765\u7740\uff0c\u4f46\u5510\u5a67\u662f\u73ed\u957f\uff0c\u800c\u4e14\u975e\u5e38\u6709\u505a\u73ed\u957f\u7684\u81ea\u89c9\uff0c\u5f20\u606a\u4fbf\u60f3\u7740\u8fd8\u662f\u7b49\u5230\u300a\u6cf0\u5766\u5c3c\u514b\u53f7\u300b\u64ad\u653e\u65f6\u518d\u8bf4\u5427\u3002\u867d\u8bf4\u5f20\u606a\u6bcf\u6b21\u770b\u300a\u6cf0\u5766\u5c3c\u514b\u53f7\u300b\uff0c\u6bcf\u6b21\u7761\u7740\u7684\u65f6\u95f4\u90fd\u4f1a\u63d0\u524d\u90a3\u4e48\u4e00\u4f1a\u513f\uff0c\u4f46\u662f\u4e0d\u53ef\u5426\u8ba4\uff0c\u8be5\u7247\u662f\u53f2\u4e0a\u6700\u6210\u529f\u7684\u50ac\u60c5\u5f71\u7247\u4e4b\u4e00\uff0c\u4e0e\u60c5\u4eba\u8282\u7684\u4f5c\u7528\u7c7b\u4f3c\uff0c\u5927\u6279\u7684\u5904\u5973\u5728\u8be5\u7247\u64ad\u653e\u540e\u6ca6\u9677\u3002\u300a\u6cf0\u5766\u5c3c\u514b\u53f7\u300b98\u5e74\u6625\u5929\u5728\u56fd\u5185\u7684\u516c\u6620\uff0c\u90a3\u65f6\u5927\u5bb6\u90fd\u8bfb\u5927\u4e00\u4e86\uff0c\u7684\u786e\u662f\u770b\u8fd9\u7247\u5b50\u7684\u597d\u65f6\u673a\uff0c\u60f3\u7740\u4ee5\u524d\u966a\u8fc7\u4e09\u4e2a\u5973\u5b69\u5b50\u770b\u8fc7\u300a\u6cf0\u5766\u5c3c\u514b\u53f7\u300b\uff0c\u53ef\u60dc\u771f\u6b63\u7eaf\u60c5\u7684\u6ca1\u6709\u4e00\u4e2a\uff0c\u5bf9\u6027\u4e8b\u5a34\u719f\u7a0b\u5ea6\u8ba9\u5f20\u606a\u4e8b\u540e\u60c5\u4e0d\u81ea\u6797\u7981\u7684\u6000\u7591\u81ea\u5df1\u662f\u4e0d\u662f\u7ed9\u90a3\u4e09\u4e2a\u5973\u5b69\u5b50\u5229\u7528\u4e86\u3002\u8fd9\u4e00\u6b21\u4f1a\u966a\u51e0\u4e2a\u5973\u5b69\u5b50\u53bb\u770b\uff1f\u5f20\u606a\u7f8e\u7f8e\u7684\u60f3\u7684\uff0c\u53ea\u662f98\u5e74\u6625\u5929\u8fd8\u6709\u4e9b\u9065\u8fdc\u3002</p></article>"}}}]);
