(window.webpackJsonp=window.webpackJsonp||[]).push([[396],{419:function(p){p.exports={chapterTitle:null,title:"\u7b2c396\u7ae0 \u5929\u4e91\u6c60\u9152",content:"<article><h2>\u7b2c396\u7ae0 \u5929\u4e91\u6c60\u9152</h2><p>\u3000\u3000\u773c\u4e0b\u65b0\u829c\u6700\u5438\u5f15\u4eba\u76ee\u5149\u7684\uff0c\u65e0\u8fc7\u4e8e\u8c03\u67e5\u57ce\u9632\u6c5f\u5824\u8d28\u91cf\u4e8b\u6545\u3001\u8c03\u67e5\u65b0\u829c\u6700\u5927\u7684\u6c11\u8425\u4f01\u4e1a\u9f99\u534e\u96c6\u56e2\u4e00\u4e8b\uff0c\u6b64\u4e8b\u6ce8\u5b9a\u8981\u7275\u6d89\u5230\u65b0\u829c\u7684\u65b9\u65b9\u9762\u9762\uff0c\u8fd9\u4e9b\u4e8b\u60c5\u5c31\u4ea4\u7ed9\u6c5f\u4e0a\u5143\u3001\u5218\u7235\u4ed6\u4eec\u53bb\u505a\uff0c\u5f20\u606a\u4e5f\u5e0c\u671b\u4ed6\u7238\u80fd\u6682\u65f6\u907f\u5f00\u65b0\u829c\u5e02\u91cc\u7684\u4e3b\u8981\u77db\u76fe\uff0c\u53e6\u8f9f\u8e4a\u5f84\u505a\u4e00\u756a\u5b9e\u7ee9\uff0c\u4eba\u6709\u9650\u7684\u7cbe\u529b\u4e0d\u5e94\u8be5\u90fd\u6d88\u8017\u5728\u65e0\u7a77\u65e0\u5c3d\u7684\u52fe\u5fc3\u6597\u89d2\u4e4b\u4e2d\u3002\u542c\u4ed6\u7238\u4e5f\u662f\u5982\u6b64\u601d\u91cf\uff0c\u5f20\u606a\u81ea\u7136\u5341\u5206\u652f\u6301\u3002</p><p>\u3000\u3000\u65b0\u829c\u5e02\u4e00\u5e74\u8d22\u653f\u6536\u5165\u8fd8\u4e0d\u5230\u516d\u4e2a\u4ebf\uff0c\u8981\u662f\u80fd\u77ed\u65f6\u95f4\u91cc\u5728\u65b0\u829c\u6276\u6301\u4e00\u5230\u4e24\u5bb6\u5411\u5730\u65b9\u8d22\u653f\u4e0a\u7f34\u5229\u7a0e\u8d85\u4ebf\u5143\u7684\u5927\u578b\u4f01\u4e1a\uff0c\u8fd9\u4efd\u5b9e\u7ee9\u81ea\u7136\u5149\u5f69\u8000\u773c\uff0c\u5f20\u606a\u5bf9\u4ed6\u7238\u8bf4\uff1a\u201c\u7ecf\u8425\u4e00\u4e8b\uff0c\u9009\u4eba\u7528\u4eba\u6700\u91cd\u8981\uff0c\u76ee\u524d\u9636\u6bb5\u8981\u60f3\u7ecf\u8425\u4e0d\u53d7\u5e02\u91cc\u7684\u5e72\u6270\uff0c\u53ea\u80fd\u662f\u4f60\u76f4\u63a5\u51fa\u9762\uff0c\u6211\u4e0d\u4f1a\u5728\u65b0\u829c\u76f4\u63a5\u6295\u8d44\uff0c\u5e2e\u4f60\u51fa\u8c0b\u5212\u7b56\u603b\u662f\u53ef\u4ee5\u7684\uff0c\u505a\u51fa\u6210\u7ee9\uff0c\u54b1\u4e5f\u4e0d\u80fd\u8ba9\u7ed9\u522b\u4eba\u3002\u201d\u5fc3\u60f3\u4e24\u4e09\u5e74\u95f4\u5e2e\u65b0\u829c\u5e02\u91cc\u6811\u7acb\u4e00\u4e24\u4e2a\u5229\u7a0e\u8d85\u4ebf\u5143\u7684\u9f99\u5934\u4f01\u4e1a\uff0c\u5e94\u8be5\u80fd\u5c06\u7238\u7238\u5e73\u7a33\u7684\u9001\u4e0a\u5e02\u957f\u7684\u5b9d\u5ea7\u5427\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u8bf4\u9053\uff1a\u201c\u90a3\u884c\uff0c\u6211\u5148\u5c06\u65b0\u829c\u5e02\u51e0\u5bb6\u56fd\u8425\u767d\u9152\u5382\u7684\u6750\u6599\u90fd\u6536\u96c6\u8d77\u6765\uff0c\u51e0\u5bb6\u5382\u7684\u8d1f\u8d23\u4eba\uff0c\u4e5f\u5148\u63a5\u89e6\u8d77\u6765\uff0c\u6211\u5148\u8003\u8651\u7740\uff0c\u5199\u4e2a\u5927\u6982\u7684\u65b9\u6848\uff0c\u4f60\u6709\u65f6\u95f4\u5e2e\u6211\u770b\u4e00\u770b\u2026\u2026\u201d</p><p>\u3000\u3000\u5728\u4e66\u623f\u8c08\u4e86\u8bb8\u4e45\uff0c\u542c\u89c1\u95e8\u524d\u7684\u8f66\u5587\u53ed\u58f0\uff0c\u63a8\u5f00\u7a97\u5b50\u770b\u89c1\u7fdf\u4e39\u9752\u7684\u6377\u8fbe\u8f66\u505c\u5728\u697c\u4e0b\uff0c\u770b\u89c1\u7fdf\u4e39\u9752\u4e0e\u536b\u5170\u4ece\u8f66\u91cc\u94bb\u51fa\u6765\uff0c\u5c45\u9ad8\u4e34\u4e0b\u770b\u8fc7\u53bb\uff0c\u4ece\u9886\u895f\u5904\u770b\u5230\u66f4\u591a\u5ae9\u817b\u5982\u7389\u7684\u767d\u4e73\uff0c\u5f20\u606a\u671d\u4e0b\u9762\u62db\u4e86\u62db\u624b\uff1a\u201c\u600e\u4e48\u8fd9\u4e48\u65e9\u5c31\u8fc7\u6765\uff1f\u201d\u7ea2\u4e91\u8426\u7ed5\u7684\u5929\u9645\u624d\u5410\u51fa\u534a\u9897\u671d\u9633\uff0c\u56de\u5934\u770b\u4e86\u770b\u4e66\u623f\u5899\u58c1\u4e0a\u60ac\u6302\u7684\u77f3\u82f1\u949f\uff0c\u8fd8\u4e0d\u5230\u516d\u70b9\u534a\uff0c\u5510\u5a67\u3001\u9648\u5983\u84c9\u5979\u4eec\u90fd\u6ca1\u6709\u8d77\u5e8a\u3002</p><p>\u3000\u3000\u536b\u5170\u6276\u7740\u5979\u5988\u4ece\u8f66\u91cc\u51fa\u6765\uff0c\u536b\u5170\u5979\u5988\u62ac\u5934\u8bf4\u9053\uff1a\u201c\u8fc7\u6765\u505a\u65e9\u9910\uff0c\u518d\u665a\u5c31\u6015\u803d\u6401\u4f60\u4eec\u7684\u65f6\u95f4\u4e86\uff0c\u6211\u4eec\u4e70\u4e86\u4e9b\u4e1c\u897f\u8fc7\u6765\uff0c\u4e5f\u4e0d\u77e5\u9053\u5408\u4e0d\u5408\u4f60\u4eec\u7684\u610f\uff1f\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5403\u8fc7\u65e9\u9910\uff0c\u5750\u8f66\u53bb\u5929\u4e91\u5c71\u3002</p><p>\u3000\u3000\u6574\u4e2a\u4e1c\u6d77\u7701\u90fd\u662f\u5e73\u539f\u4e18\u9675\u5730\u8c8c\uff0c\u4fbf\u662f\u5c0f\u6c5f\u7684\u4e0a\u6e38\u4e5f\u662f\u4f4e\u77ee\u8d77\u4f0f\u7684\u5c71\u5cad\uff0c\u6d77\u62d4\u8d85\u8fc7\u4e94\u767e\u7c73\u7684\u5c71\u4f53\u90fd\u5f88\u7f55\u89c1\uff0c\u5374\u662f\u5728\u7701\u4e2d\u90e8\u7684\u5730\u5757\u7a81\u5140\u7684\u9686\u8d77\uff0c\u5f62\u6210\u9ad8\u8038\u5165\u4e91\u7684\u534a\u6708\u5f62\u5929\u4e91\u5c71\uff0c\u4f7f\u5f97\u5c0f\u6c5f\u7ecf\u8fc7\u65b0\u829c\u5883\u5185\u62d0\u4e86\u4e2a\u5927\u5f2f\u3002</p><p>\u3000\u3000\u65b0\u829c\u51ac\u6696\u590f\u51c9\u7684\u6e29\u6da6\u6c14\u5019\u4e3b\u8981\u5f97\u76ca\u4e8e\u5ea7\u843d\u5728\u57ce\u80cc\u9762\u7684\u5929\u4e91\u5c71\uff0c\u4e5f\u662f\u7701\u5185\u96be\u5f97\u7684\u65c5\u6e38\u80dc\u5730\uff0c\u53ea\u662f\u4e00\u76f4\u6ca1\u6709\u5f97\u5230\u5f88\u597d\u7684\u89c4\u5212\u4e0e\u5f00\u53d1\uff0c\u53d7\u9650\u4e8e\u4ea4\u901a\u4e0e\u5176\u4ed6\u6761\u4ef6\uff0c\u6ca1\u80fd\u6210\u4e3a\u4e00\u6d41\u7684\u666f\u533a\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u5929\u4e91\u5c71\u4e5f\u4e0d\u7b97\u964c\u751f\uff0c\u6e38\u73a9\u8fc7\u51e0\u6b21\uff0c\u4f46\u5f53\u7136\u4e0d\u53ca\u7fdf\u4e39\u9752\u3001\u536b\u5170\u5979\u4eec\u8fd9\u6837\u7684\u672c\u5730\u4eba\u719f\u6089\u3002</p><p>\u3000\u3000\u542c\u7fdf\u4e39\u9752\u4ecb\u7ecd\uff0c\u65b0\u829c\u917f\u9152\u7684\u5386\u53f2\u4ece\u4e24\u5343\u591a\u5e74\u524d\u897f\u6c49\u65f6\u671f\u5c31\u6709\uff0c\u5929\u4e91\u5c71\u4e0a\u8fd8\u6316\u6398\u51fa\u897f\u6c49\u65f6\u671f\u917f\u9152\u5de5\u574a\u7684\u9057\u8ff9\uff0c\u773c\u4e0b\u5c31\u4f5c\u4e3a\u5929\u4e91\u5c71\u4e0a\u7684\u4e00\u4e2a\u666f\u70b9\uff0c\u800c\u4e14\u65b0\u829c\u5e02\u5386\u53f2\u6700\u60a0\u4e45\u7684\u9152\u5382\u5929\u4e91\u6c60\u9152\u5382\u53ef\u4ee5\u8ffd\u6eaf\u5230\u6e05\u54b8\u4e30\u5e74\u95f4\uff0c\u5929\u4e91\u6c60\u9152\u5382\u751a\u81f3\u8fd8\u4fdd\u7559\u7740\u54b8\u4e30\u5e74\u95f4\u9152\u574a\u7684\u9057\u8ff9\u3002</p><p>\u3000\u3000\u5f20\u606a\u7559\u7740\u5fc3\uff0c\u8ba9\u5085\u4fca\u5f00\u8f66\u7ecf\u8fc7\u5929\u4e91\u6c60\u9152\u5382\uff0c\u770b\u5230\u7684\u53ea\u662f\u89c4\u6a21\u5f88\u5c0f\u7684\u56fd\u8425\u767d\u9152\u5382\uff0c\u542c\u7fdf\u4e39\u9752\u4ecb\u7ecd\uff0c\u9152\u5382\u7684\u5382\u623f\u8fd8\u662f\u65e5\u519b\u4fb5\u534e\u65f6\u671f\u7559\u4e0b\u7684\u9a6c\u68da\uff0c\u53ef\u89c1\u9152\u5382\u4e4b\u7834\u843d\u3001\u9648\u65e7\uff0c\u5012\u662f\u9152\u5382\u540e\u9762\u7684\u9152\u574a\u5efa\u7b51\u5df2\u7ecf\u5217\u4e3a\u5e02\u7ea7\u5386\u53f2\u9057\u8ff9\uff0c\u4fdd\u5b58\u5c1a\u597d\u3002</p><p>\u3000\u3000\u4e2d\u5348\u5728\u5929\u4e91\u5c71\u4e0a\u5403\u996d\u65f6\uff0c\u5f20\u606a\u7279\u610f\u82b1\u5341\u4e8c\u5143\u94b1\u70b9\u4e86\u5929\u4e91\u6c60\u9152\u7cfb\u5217\u6700\u8d35\u7684\u90a3\u79cd\uff0c\u53e3\u611f\u4e0e\u5f53\u524d\u56fd\u5185\u7684\u51e0\u79cd\u9ad8\u6863\u767d\u9152\u6bd4\u8d77\u6765\uff0c\u8fd8\u6709\u76f8\u5f53\u5927\u7684\u5dee\u8ddd\uff0c\u5012\u662f\u4e0e\u5341\u4e8c\u5143\u7684\u8eab\u4ef7\u76f8\u7b26\uff0c\u9500\u552e\u4e5f\u53ea\u9650\u4e8e\u5468\u8fb9\u5730\u533a\u3002</p><p>\u3000\u3000\u76ee\u524d\u767d\u9152\u884c\u4e1a\uff0c\u4ee5\u5ddd\u8d35\u7b49\u5730\u6700\u4f73\uff0c\u4f46\u662f\u5404\u5730\u90fd\u6709\u5927\u91cf\u7684\u767d\u9152\u5382\uff0c\u6240\u4ee5\u5f62\u6210\u4e2d\u4f4e\u6863\u767d\u9152\u8fc7\u5ea6\u7ade\u4e89\uff0c\u5229\u6da6\u6781\u5fae\uff0c\u800c\u5ddd\u8d35\u7b49\u5730\u7684\u54c1\u724c\u767d\u9152\u5360\u636e\u7740\u76c8\u5229\u6700\u4e30\u7684\u9ad8\u7aef\u767d\u9152\u5e02\u573a\uff0c\u7279\u522b\u4e00\u4e9b\u54c1\u724c\u767d\u9152\u5360\u636e\u9ad8\u7aef\u767d\u9152\u5e02\u573a\u4e4b\u540e\u5411\u4e2d\u7aef\u5e02\u573a\u5ef6\u4f38\uff0c\u4f7f\u5f97\u5730\u65b9\u54c1\u724c\u767d\u9152\u5728\u4e2d\u7aef\u5e02\u573a\u7684\u751f\u5b58\u7a7a\u95f4\u4e5f\u5f88\u72ed\u7a84\u3002</p><p>\u3000\u3000\u65b0\u829c\u7684\u767d\u9152\u4e1a\u60f3\u7a81\u51fa\u91cd\u56f4\uff0c\u5012\u662f\u8981\u52a8\u4e9b\u8111\u5b50\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u5929\u4e91\u6c60\u9152\u5382\u5373\u4f7f\u662f\u4ece\u6e05\u9053\u5149\u5e74\u95f4\u4f20\u4e0b\u6765\u7684\u9152\u574a\uff0c\u917f\u9152\u5de5\u827a\u4e5f\u6ca1\u6709\u4ec0\u4e48\u72ec\u9053\u4e4b\u5904\u3002\u90fd\u8bf4\uff1a\u4ea7\u9999\u5728\u53d1\u9175\u3001\u63d0\u9999\u5728\u84b8\u998f\u3001\u6210\u578b\u5728\u52fe\u5151\u3001\u98ce\u683c\u5728\u8c03\u5473\uff0c\u5728\u73b0\u4ee3\uff0c\u52fe\u5151\u6280\u672f\u5df2\u7ecf\u662f\u767d\u9152\u884c\u4e1a\u6700\u91cd\u8981\u7684\u6280\u672f\u3002\u5929\u4e91\u6c60\u9152\u5728\u52fe\u5151\u6280\u672f\u4e0a\u6709\u6240\u6b20\u7f3a\uff0c\u6240\u4ee5\u6324\u4e0d\u8fdb\u9ad8\u6863\u9152\u4e4b\u5217\uff0c\u4f46\u662f\u5929\u4e91\u6c60\u9152\u5473\u9187\u5c3e\u51c0\u3001\u4e0d\u523a\u5589\u3001\u996e\u540e\u4e0d\u4e0a\u5934\u3001\u4e0d\u53e3\u5e72\uff0c\u5374\u8fd8\u4fdd\u7559\u7740\u7eaf\u7cae\u7cbe\u917f\u7684\u539f\u6c41\u539f\u5473\u3002</p><p>\u3000\u3000\u52fe\u5151\u6280\u672f\u662f\u767d\u9152\u884c\u4e1a\u6700\u91cd\u8981\u7684\u673a\u5bc6\u578b\u6280\u672f\u4e0d\u5047\uff0c\u4f46\u662f\u4ee5\u5f53\u524d\u56fd\u5185\u5404\u9152\u4f01\u7684\u4fdd\u5bc6\u610f\u8bc6\uff0c\u60f3\u4ece\u8305\u53f0\u3001\u5251\u5357\u6625\u3001\u4e94\u7cae\u6db2\u7b49\u9152\u5382\u9ad8\u85aa\u6316\u51e0\u540d\u7ecf\u9a8c\u4e30\u5bcc\u7684\u6280\u5e08\u8fc7\u6765\uff0c\u6539\u826f\u52fe\u5151\u5de5\u827a\u90fd\u4e0d\u662f\u592a\u56f0\u96be\u7684\u4e8b\u60c5\uff1b</p><p>\u3000\u3000\u5e02\u573a\u5f62\u8c61\u7684\u5851\u9020\u4e2d\uff0c\u6587\u5316\u56e0\u7d20\u7684\u6ce8\u5165\u662f\u5f88\u5173\u952e\u7684\u4e00\u4e2a\u56e0\u7d20\uff0c\u5929\u4e91\u6c60\u9152\u5382\u6709\u8fd9\u4e2a\u5386\u53f2\uff0c\u65b0\u829c\u7684\u917f\u9152\u4e1a\u6709\u8fd9\u4e2a\u5386\u53f2\uff0c\u6709\u5f88\u503c\u5f97\u6316\u6398\u7684\u5730\u65b9\u3002\u9664\u6b64\u4e4b\u5916\uff0c\u901a\u8fc7\u5e02\u573a\u8425\u9500\u7684\u624b\u6bb5\uff0c\u5c06\u5e02\u573a\u6253\u5f00\u4e4b\u540e\uff0c\u4ea7\u91cf\u8981\u4e00\u4e0b\u5b50\u63d0\u4e0a\u6765\uff0c\u8fd9\u65f6\u5019\u5982\u4f55\u4fdd\u8bc1\u9152\u8d28\u7684\u7a33\u5b9a\uff0c\u624d\u662f\u6210\u8d25\u7684\u5173\u952e\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u5979\u4eec\u90fd\u89c9\u5f97\u5f20\u606a\u4eca\u5929\u7684\u8868\u73b0\u6709\u4e9b\u602a\u5f02\uff0c\u8868\u73b0\u5f97\u5f88\u55dc\u9152\uff0c\u4e0b\u5348\u5c31\u5728\u5929\u4e91\u5c71\u4e0a\u7684\u90a3\u4e2a\u897f\u6c49\u9152\u574a\u9057\u8ff9\u8017\u4e86\u5927\u534a\u5929\uff0c\u5403\u665a\u9910\u65f6\uff0c\u7adf\u7136\u5c06\u65b0\u829c\u5e02\u9762\u4e0a\u6bd4\u8f83\u6709\u540d\u7684\u767d\u9152\u90fd\u70b9\u4e86\u4e0a\u6765\uff0c\u5f00\u74f6\u4e00\u4e00\u54c1\u5c1d\uff0c\u9047\u5230\u9152\u5e97\u8001\u677f\u4e5f\u662f\u8d2a\u676f\u7684\u4eba\uff0c\u7adf\u7136\u8bf7\u522b\u4eba\u5230\u684c\u4e0a\u6765\u5927\u8c08\u65b0\u829c\u5f53\u5730\u7684\u9152\u6587\u5316\u3002</p><p>\u3000\u3000\u9152\u5e97\u7684\u8001\u677f\u81ea\u7136\u9ad8\u5174\uff0c\u8bf7\u4ed6\u559d\u9152\u4e0d\u8bf4\uff0c\u5173\u952e\u5f20\u606a\u8fd9\u4e00\u684c\u56db\u4e2a\u5973\u5b69\u5b50\u90fd\u5a07\u8273\u5f97\u5f88\uff0c\u6210\u719f\u8273\u4e3d\u7684\u7fdf\u4e39\u9752\u5c24\u5176\u80fd\u5438\u5f15\u4e2d\u5e74\u7537\u4eba\u7684\u76ee\u5149\uff0c\u559d\u5b8c\u9152\u79bb\u5f00\uff0c\u9152\u5e97\u8001\u677f\u5927\u624b\u4e00\u6325\u7ed9\u6253\u4e86\u516d\u6298\u3002</p><p>\u3000\u3000\u6bcf\u79cd\u9152\u53ea\u5c1d\u4e00\u4e9b\uff0c\u4f46\u662f\u559d\u4e86\u4e5f\u4e0d\u5c11\uff0c\u5173\u952e\u9152\u591a\u54c1\u6742\uff0c\u5728\u9152\u5e97\u5c31\u6709\u4e0a\u5934\uff0c\u51fa\u95e8\u8ba9\u98ce\u5439\u4e86\u5439\u624d\u597d\u4e9b\u3002</p><p>\u3000\u3000\u591c\u91cc\u98ce\u51c9\uff0c\u5012\u8ba9\u590f\u5929\u6ce1\u6e29\u6cc9\u6210\u4e86\u4e00\u79cd\u4eab\u53d7\uff0c\u8ba9\u7fdf\u4e39\u9752\u9886\u7740\u5230\u4e00\u5bb6\u89c4\u6a21\u4e0d\u7b97\u592a\u5927\u7684\u6e29\u6cc9\u6d74\u573a\uff0c\u770b\u5230\u5218\u658c\u4e0e\u76db\u9752\u7ad9\u5728\u95e8\u53e3\u8fce\u63a5\u4ed6\u4eec\uff0c\u5f20\u606a\u95ee\u4ed6\uff1a\u201c\u8fd9\u4e5f\u662f\u658c\u8001\u5927\u4f60\u7684\u4ea7\u4e1a\uff1f\u201d</p><p>\u3000\u3000\u201c\u5c0f\u672c\u4e70\u5356\uff0c\u8ba9\u606a\u5c11\u89c1\u7b11\u4e86\u2026\u2026\u76db\u603b\u51b3\u5b9a\u6295\u8d44\u8fd9\u91cc\uff0c\u4ee5\u540e\u4ed6\u624d\u662f\u8fd9\u91cc\u7684\u5927\u8001\u677f\u3002\u201d</p><p>\u3000\u3000\u5929\u4e91\u5c71\u6709\u7740\u7701\u5185\u7f55\u89c1\u7684\u6e29\u6cc9\u8d44\u6e90\uff0c\u4f46\u662f\u51e0\u5bb6\u6e29\u6cc9\u6d74\u573a\u4ece\u5916\u9762\u770b\u4e0a\u53bb\u5e76\u4e0d\u592a\u5149\u9c9c\uff0c\u4e3b\u8981\u8fd8\u5c40\u9650\u4e8e\u65b0\u829c\u7684\u65c5\u6e38\u7ecf\u6d4e\u5e76\u4e0d\u53d1\u8fbe\uff0c\u800c\u5e02\u533a\u7684\u6709\u94b1\u6709\u95f2\u4e4b\u4eba\uff0c\u9664\u4e86\u5076\u5c14\u5230\u5929\u4e91\u5c71\u6765\u6ce1\u6e29\u6cc9\uff0c\u5e73\u65e5\u8fd8\u662f\u559c\u6b22\u5230\u5e02\u533a\u91cc\u9762\u7684\u5a31\u4e50\u573a\u6240\u4f11\u95f2\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u76db\u9752\u4e00\u773c\uff0c\u4ed6\u6295\u8d44\u8fd9\u91cc\uff0c\u4e0d\u6653\u5f97\u4ed6\u662f\u770b\u4e2d\u8fd9\u91cc\u7684\u81ea\u7136\u8d44\u6e90\uff0c\u8fd8\u662f\u770b\u4e2d\u8fd9\u91cc\u7684\u7f8e\u5973\u8d44\u6e90\uff0c\u4e5f\u4e0d\u6653\u5f97\u4ed6\u7559\u5728\u65b0\u829c\u8fd9\u4e9b\u5929\u5230\u5e95\u8c08\u59a5\u51e0\u6869\u4e70\u5356\u3002\u5f53\u7136\uff0c\u4ed6\u8981\u95ee\uff0c\u76db\u9752\u81ea\u7136\u4e0d\u4f1a\u7792\u4ed6\uff0c\u4ed6\u4e0d\u95ee\uff0c\u76db\u9752\u4e5f\u4e0d\u597d\u610f\u601d\u62ff\u4e9b\u51e0\u767e\u4e07\u7684\u6295\u8d44\u5230\u4ed6\u9762\u524d\u8bf4\uff0c\u6839\u672c\u6ca1\u4ec0\u4e48\u597d\u70ab\u8000\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u662f\u5f88\u671f\u5f85\u7537\u5973\u6df7\u6d74\uff0c\u7fdf\u4e39\u9752\u7684\u8eab\u6750\uff0c\u5373\u4f7f\u7a7f\u7740\u4fdd\u5b88\u7684\u6cf3\u88c5\uff0c\u4e5f\u5e94\u8be5\u975e\u5e38\u7684\u517b\u773c\uff0c\u5948\u4f55\u5218\u658c\u7ed9\u51c6\u5907\u4e86\u4e24\u4e2a\u5355\u72ec\u7684\u6c60\u5b50\uff0c\u6ce1\u8fc7\u6e29\u6cc9\u6d74\uff0c\u5230\u72ec\u7acb\u7684\u4f11\u606f\u5ba4\u4f11\u606f\uff0c\u5973\u5b69\u5b50\u4eec\u51fa\u6765\u65f6\u90fd\u7a7f\u7740\u6d74\u573a\u91cc\u63d0\u4f9b\u7684\u6d74\u888d\uff0c\u6bd4\u5979\u4eec\u4eca\u5929\u51fa\u95e8\u7a7f\u7684\u670d\u88c5\u8fd8\u4fdd\u5b88\u3002</p><p>\u3000\u3000\u201c\u606a\u5c11\u7684\u5efa\u8bae\uff0c\u6211\u6709\u8ba4\u771f\u7684\u8003\u8651\uff0c\u201d\u7fdf\u4e39\u9752\u5750\u5230\u5f20\u606a\u7684\u65c1\u8fb9\uff0c\u63d0\u8d77\u5f20\u606a\u6628\u665a\u8ddf\u5979\u63d0\u8d77\u7684\u4e8b\u60c5\uff0c\u201c\u4f46\u662f\u6211\u4e0d\u60f3\u63a5\u53d7\u592a\u590d\u6742\u7684\u9644\u52a0\u6761\u4ef6\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5e94\u8be5\u4e0d\u4f1a\u6709\u2026\u2026\u201d\u5f20\u606a\u4fa7\u8fc7\u5934\u95ee\u76db\u9752\uff0c\u201c\u4f60\u5bf9\u7fdf\u59d0\u6709\u6ca1\u6709\u989d\u5916\u7684\u3001\u6bd4\u8f83\u590d\u6742\u7684\u9644\u52a0\u6761\u4ef6\uff1f\u201d</p><p>\u3000\u3000\u76db\u9752\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u662f\u7fdf\u603b\u5141\u8bb8\uff0c\u6211\u5012\u4e0d\u4f1a\u541d\u556c\u591a\u63d0\u51e0\u4e2a\u989d\u5916\u7684\u6761\u4ef6\uff0c\u8981\u662f\u7fdf\u603b\u65e0\u610f\uff0c\u6211\u4eec\u5c31\u4e00\u5207\u4f9d\u7167\u6b63\u89c4\u7684\u516c\u53f8\u501f\u8d37\u7a0b\u5e8f\u529e\uff0c\u6211\u4f1a\u8981\u6c42\u7fdf\u603b\u62ff\u4f60\u65e5\u540e\u83b7\u5f97\u5bbe\u9986\u80a1\u4efd\u4f5c\u62b5\u62bc\uff0c\u8fd9\u5e94\u8be5\u4e0d\u7b97\u989d\u5916\u7684\u592a\u590d\u6742\u7684\u9644\u52a0\u6761\u4ef6\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u76db\u9752\u65e0\u610f\u53c2\u4e0e\u62db\u5f85\u5bbe\u9986\u7684\u6539\u5236\u9879\u76ee\uff0c\u5012\u662f\u613f\u610f\u501f\u94b1\u7ed9\u7fdf\u4e39\u9752\uff0c\u751a\u81f3\u6ca1\u6709\u5173\u5fc3\u7fdf\u4e39\u9752\u9700\u8981\u591a\u5c11\u8d44\u91d1\uff0c\u8fd9\u4e9b\u4e0d\u8fc7\u662f\u56e0\u4e3a\u5f20\u606a\u8bf4\u4e86\u4e00\u53e5\u8bdd\u800c\u5df2\uff0c\u7fdf\u4e39\u9752\u5fc3\u60f3\u5f20\u606a\u8fd9\u4e2a\u5c0f\u7537\u4eba\u8fd8\u662f\u795e\u79d8\u83ab\u6d4b\u3002</p><p>\u3000\u3000\u4eca\u5929\uff0c\u4ed6\u4eec\u5728\u5c71\u4e0a\u6e38\u73a9\uff0c\u5e02\u91cc\u521d\u6b65\u8ba8\u8bba\u5c06\u62db\u5f85\u5bbe\u9986\u62ff\u51fa\u6765\u6539\u5236\u7684\u4e8b\u60c5\uff0c\u7fdf\u4e39\u9752\u4e0b\u5348\u5c31\u63a5\u5230\u7535\u8bdd\u542c\u8bf4\u4f1a\u8bae\u7684\u6709\u5173\u5185\u5bb9\u3002\u6574\u4f53\u51fa\u552e\u7684\u53ef\u80fd\u6027\u5f88\u5927\uff0c\u6bd5\u7adf\u5e02\u91cc\u8981\u7b79\u94b1\u5728\u6c5b\u671f\u8fc7\u540e\u89e3\u51b3\u57ce\u9632\u6c5f\u5824\u7684\u91cd\u5927\u5b89\u5168\u9690\u60a3\u3002</p><p>\u3000\u3000\u5e02\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\u76ee\u524d\u662f\u5e02\u91cc\u5730\u7406\u6761\u4ef6\u6700\u597d\u3001\u8bbe\u65bd\u6700\u5b8c\u5907\u7684\u5bbe\u9986\uff0c\u4f5c\u4e3a\u6574\u4f53\u51fa\u552e\u7684\u6761\u4ef6\uff0c\u5e02\u91cc\u8fd8\u4f1a\u9009\u62e9\u90a3\u91cc\u4f5c\u4e3a\u5e02\u653f\u5e9c\u5b9a\u70b9\u62db\u5f85\u5bbe\u9986\uff0c\u6574\u4f53\u8d44\u4ea7\u5927\u7ea6\u5728\u4e24\u5343\u4e07\u5230\u4e09\u5343\u4e07\u4e4b\u95f4\uff0c\u7fdf\u4e39\u9752\u8981\u53c2\u4e0e\u8fdb\u6765\uff0c\u54ea\u6015\u62ff\u7740\u4e00\u6210\u7684\u80a1\u4efd\uff0c\u4e5f\u8981\u597d\u51e0\u767e\u4e07\uff0c\u518d\u4f4e\u5c31\u6ca1\u6709\u53c2\u4e0e\u7684\u610f\u4e49\u4e86\u3002</p><p>\u3000\u3000\u51e0\u767e\u4e07\uff0c\u5bf9\u5f20\u606a\u6765\u8bf4\uff0c\u4f3c\u4e4e\u662f\u5f88\u8f7b\u63cf\u6de1\u5199\u7684\u4e8b\u60c5\u3002</p></article>"}}}]);
